INSERT INTO [t1714] VALUES (977,'عبد الله بن عمر بن يزيد بن كثير الزهري أخو رستة يكنى أبا محمد , وكان يخضب , له المصنفات الكثيرة , ولد سنة سبع وثمانين ومائة ولي قضاء الكرج , وتوفي بها سنة اثنتين وخمسين ومائتين , كان راوية يحيى , وعبد الرحمن , وحماد بن مسعدة , وروح , ومعاذ بن معاذ ,','2593','818','0');
INSERT INTO [t1714] VALUES (978,'عبد الله بن داود العابد يعرف بسنديلة , مسكنه باغ عيسى , أبو محمد , كان من المتعبدين , راوية الحسين بن حفص , وإبراهيم بن أيوب.','2601','818','0');
INSERT INTO [t1714] VALUES (979,'عبد الله بن محمد الكناني أبو الوليد روى عن أبي معاوية، وابن إدريس وأبي داود، كان كثير الحديث مشهورا بالطلب والكتابة ثم أفصح بموافقة الروافض وأنكر خلافة الصديق فيما حكي عنه، فجمع عبد العزيز بن دلف - وكان والي البلد - مشايخ البلد: أبا مسعود الرازي','2604','818','0');
INSERT INTO [t1714] VALUES (980,'عبد الله بن خالد الكوفي أكره على قضاء أصبهان، قلده المأمون وكان من الفضلاء، روى عنه محمد بن المغيرة، ورستة، وعمرو بن سعيد الجمال، وأسيد بن عاصم، روى عن سفيان بن عيينة وشعيب بن حرب، حدثنا أبو محمد بن حيان، ثنا محمد بن إبراهيم بن شبيب، ثنا محمد بن','2606','818','0');
INSERT INTO [t1714] VALUES (981,'ابن سالم المؤذن مؤذن مسجد الجامع، غير مسمى، وقيل: هو عبد الله','2610','818','0');
INSERT INTO [t1714] VALUES (982,'عبد الله بن محمد بن داود البراد: أبو محمد المديني كان من العباد والزهاد، حدث عن يحيى القطان، ومعاذ بن معاذ، ومحبوب بن الحسن، والحسن بن حبيب بن ندبة.','2612','818','0');
INSERT INTO [t1714] VALUES (983,'عبد الله بن أيوب: أبو جعفر حدث عن سلمة بن الفضل، وأبي زهير ومهران، روى عنه علي بن رستم ومحمد بن عبد الله بن الحسن، ذكره المتأخر.','2616','818','0');
INSERT INTO [t1714] VALUES (984,'عبد الله بن محمد بن يحيى بن أبي بكير الكرماني أبو محمد وقيل: أبو عبد الرحمن قدم أصبهان وحدث بها وكان صدوقا عن أبي بكر بن عياش، وروح بن عبادة، وعمرو بن جرير.','2617','818','0');
INSERT INTO [t1714] VALUES (985,'عبد الله بن عبد الوهاب الخوارزمي قدم أصبهان وحدث بها، في حديثه نكارة.','2622','818','0');
