INSERT INTO [t1714] VALUES (994,'عبد الله بن محمد بن سنان بن سعد: أبو محمد البصري يعرف بالروحي، قدم أصبهان وحدث بها , كثير الوضع، حدث بأحاديث لم يتابع عليها - وبنسخة: لروح بن القاسم لم يتابع عليها - فلذلك سمي الروحي.','2641','818','0');
INSERT INTO [t1714] VALUES (995,'عبد الله بن معروف بن يزيد أخو محمد بن معروف ممولة، أدرك ابن مهدي وسأله.','2644','818','0');
INSERT INTO [t1714] VALUES (996,'عبد الله بن أحمد بن يزيد الشيباني: أبو محمد المؤذن، رأى أبا داود وحدث عن الحسين بن حفص، والحميدي، وبكر بن بكار، توفي سنة تسع وسبعين ومائتين، حدث عنه من المتأخرين أبو علي بن عاصم وأحمد بن محمد بن نصير.','2646','818','0');
INSERT INTO [t1714] VALUES (997,'عبد الله بن الوليد: أبو يحيى القسام من أهل أسفيذان، روى عنه ابنه أبو زكرياء يحيى، يروي عن محمد بن بكير، وعلي بن فرين.','2648','818','0');
INSERT INTO [t1714] VALUES (998,'عبد الله بن محمد بن عبدة: أبو إسماعيل بن عبد الله الضبي روى عنه ابنه إسماعيل، حدث عن أبي داود، والحسين بن حفص.','2649','818','0');
