INSERT INTO [t1714] VALUES (1004,'عبد الله بن أحمد بن سوادة أبو طالب البغدادي قدم أصبهان وحدث بها، توفي بطرسوس سنة خمس وثمانين ومائتين، حدث عنه أحمد بن محمود بن صبيح','2669','818','0');
