INSERT INTO [t1714] VALUES (1008,'عبد الله بن بندار بن إبراهيم بن المحتضر بن عتاب بن خليفة بن إياد بن عبيد الله الضبي، وقال القاضي: الهلالي، يروى عن محمد بن المغيرة، وإسماعيل بن عمرو، وسهل بن عثمان، وموسى بن المساور، كان من الصالحين، توفي سنة أربعة وتسعين ومائتين.','2678','818','0');
INSERT INTO [t1714] VALUES (1009,'عبد الله بن محمد بن زكرياء بن يحيى بن أبي زكرياء، وهو ابن أخي عبد الوهاب بن زكرياء، أبو محمد، مقبول القول من الثقات، له المصنفات الكثيرة، حدث عنه محمد بن يحيى بن منده، توفي سنة ست وثمانين ومائتين، يروي عن سهل بن بكار وأبي الوليد ومحمد بن بكير','2684','818','0');
INSERT INTO [t1714] VALUES (1010,'عبد الله بن زكرياء بن يحيى الأصبهاني التميمي يعرف بالأكفاني سكن الكوفة، يكنى: أبا محمد.','2688','818','0');
INSERT INTO [t1714] VALUES (1011,'عبد الله بن محمد بن العباس بن خالد السهمي أبو محمد، صاحب أصول، توفي سنة ست وتسعين، يروي عن محمد بن المغيرة وسهل بن عثمان، وكان أبوه محمد بن العباس يروي الموطأ عن القعنبي.','2690','818','0');
INSERT INTO [t1714] VALUES (1012,'عبد الله بن محمد بن الوليد بن حازم النفيلي، بصري الأصل، يروي عن العراقيين: علي بن الجعد وكامل بن طلحة، وبسام بن يزيد، توفي سنة إحدى وتسعين ومائتين.','2695','818','0');
INSERT INTO [t1714] VALUES (1013,'عبد الله بن سخت، قيل: إنه قدم أصبهان وحدث بها','2698','818','0');
INSERT INTO [t1714] VALUES (1014,'عبد الله بن الصباح أبو محمد البزار من سكة القصارين، توفي سنة أربع وتسعين ومائتين، صدوق ثقة، يروي عن العراقيين، والمكيين','2701','818','0');
INSERT INTO [t1714] VALUES (1015,'عبد الله بن محمد بن عمران بن أيوب بن عمران بن أبي سليمان أبو محمد وأبو سليمان من أهل خراسان مقبول القول، كان على المسائل رئيسا ووجها، حدث عن الحجازيين: ابن أبي عمر العدني وغيره، توفي سنة أربع وثلاثمائة، وكان أبوه محمد بن عمران له الرئاسة، شهد عند','2706','818','0');
INSERT INTO [t1714] VALUES (1016,'عبد الله بن محمد بن عبد الملك أبو محمد','2708','818','0');
INSERT INTO [t1714] VALUES (1017,'عبد الله بن عبد الملك الطويل أبو محمد','2710','818','0');
INSERT INTO [t1714] VALUES (1018,'عبد الله بن أحمد بن أسيد أبو محمد، أخو إسماعيل بن أحمد، كثير الحديث، صاحب فوائد وغرائب، صنف المسند، توفي سنة عشر وثلاثمائة، روى عن العراقيين، والحجازيين، خرج إلى العراق في آخر أيامه فكتبوا عنه بالعراقين','2712','818','0');
INSERT INTO [t1714] VALUES (1019,'عبد الله بن سعيد بن الوليد بن معدان بن ماهان الضبي أبو محمد ولقب سعيد بسندة، كتب عن الشاميين، كثير الحديث، حدثنا عنه القاضي، وقال: المزني','2718','818','0');
INSERT INTO [t1714] VALUES (1020,'عبد الله بن سليمان بن الأشعث أبو بكر السجستاني قدم أصبهان قديما وكتب عن أحمد بن عصام وغيره، وعاد إليها بعد الثمانين وحدث بها ثم استوطن بغداد، توفي بها سنة ست عشرة وثلاثمائة','2721','818','0');
INSERT INTO [t1714] VALUES (1021,'عبد الله بن محمد بن عيسى أبو عبد الرحمن المقرئ، كثير الحديث حسن المعرفة، توفي سنة ست وثلاثمائة','2726','818','0');
INSERT INTO [t1714] VALUES (1022,'عبد الله بن الحسين بن محمد بن زهير النيسابوري أبو بكر، قدم أصبهان قبل الثلاثمائة سنة ست وتسعين ومائتين، ثم خرج من أصبهان إلى البصرة، يروي عن أحمد بن حفص ومحمد بن عقيل.','2731','818','0');
INSERT INTO [t1714] VALUES (1023,'عبد الله بن محمد بن يزداد الأصبهاني، حدث ببغداد، حدثنا عنه أبو بكر الجعاني','2733','818','0');
INSERT INTO [t1714] VALUES (1024,'عبد الله بن محمد بن إسحاق أبو محمد، كثير الحديث، كتب بمكة، وفارس.','2735','818','0');
INSERT INTO [t1714] VALUES (1025,'عبد الله بن محمد بن الجعد أبو محمد الفرساني، روى عن سهل بن عثمان، وعبد الله بن عمران، وله رؤية من إبراهيم بن أيوب الفرساني صاحب النعمان.','2737','818','0');
INSERT INTO [t1714] VALUES (1026,'عبد الله بن محمد بن عمر بن يزيد ابن أخي رستة، روى عن أبيه وعمه','2739','818','0');
INSERT INTO [t1714] VALUES (1027,'عبد الله بن محمد بن مزيد الأصبهاني سكن البصرة، يروي عن علي بن محمد الطنافسي','2741','818','0');
INSERT INTO [t1714] VALUES (1028,'عبد الله بن عبد السلام بن بندار أبو محمد، كان من الصالحين، توفي بالبادية سنة القرمطي، سنة اثنتي عشرة وثلاثمائة، كان من جيران إبراهيم بن متويه، يروي عن يونس بن عبد الأعلى والمصريين.','2743','818','0');
INSERT INTO [t1714] VALUES (1029,'عبد الله بن محمد بن الحسن بن أسيد أبو محمد الثقفي، مقبول القول كثير الحديث، حدث بأصبهان، وبمدينة الرسول، صلى الله عليه وسلم، توفي سنة عشر وثلاثمائة.','2749','818','0');
INSERT INTO [t1714] VALUES (1030,'عبد الله بن محمد بن يعقوب بن مهران الخزاز، توفي سنة ثلاث عشرة وثلاثمائة في ربيع الأول','2753','818','0');
INSERT INTO [t1714] VALUES (1031,'عبد الله بن محمد بن عبد الرحمن بن الخليل بن الأشقر أبو القاسم، بغدادي، قدم أصبهان وحدث بها، وكان إليه القضاء بالكرج.','2756','818','0');
INSERT INTO [t1714] VALUES (1032,'عبد الله بن مظاهر الحافظ أبو محمد، توفي شابا سنة أربع وثلاثمائة، سمع من يوسف القاضي والمطين وأبي شعيب الحراني وطبقتهم، ارتفع أمره في الحفظ والمعرفة، وفاق الناس بالعراق في الحفظ والمعرفة.','2761','818','0');
INSERT INTO [t1714] VALUES (1033,'عبد الله بن محمد بن يوسف','2764','818','0');
INSERT INTO [t1714] VALUES (1034,'عبد الله بن محمد بن موسى البازيار، يروي عن هارون بن سليمان، والحسن بن عطاء، والأصبهانيين.','2766','818','0');
INSERT INTO [t1714] VALUES (1035,'عبد الله بن محمد بن عبدان العسكري أبو مسعود، يروي عن لوين، وسلمة، توفي سنة خمس عشرة وثلاثمائة.','2770','818','0');
INSERT INTO [t1714] VALUES (1036,'عبد الله بن محمود بن الفرج أبو عبد الرحمن الوذنكاباذي، خال أبي محمد بن حيان، توفي سنة خمس وعشرين وثلاثمائة، حدث عن أبي حاتم، وهلال بن العلاء.','2773','818','0');
INSERT INTO [t1714] VALUES (1037,'عبد الله بن سهل بن كوفة الأصبهاني، سكن مكة، حدث عن أحمد بن عصام.','2777','818','0');
INSERT INTO [t1714] VALUES (1038,'عبد الله بن أبي عمرو بن مهيار البناء يعرف بعبد الله بن فسويه، ثقة، روى عن عبد الله بن عمر، والحسين بن عبد الله بن حمران وغيرهما.','2779','818','0');
INSERT INTO [t1714] VALUES (1039,'عبد الله بن جعفر بن محمد الخشاب أبو بكر، من أهل المدينة، حدث عن الحجازيين والبصريين، روى عن ابن أبي مسرة، وعلي بن عبد العزيز','2781','818','0');
