INSERT INTO [t1714] VALUES (1042,'عبد الله بن محمد بن عبد الله بن منصور الشعار، يروي عن لوين، ومحمد بن مهران الجمال','2788','818','0');
INSERT INTO [t1714] VALUES (1043,'عبد الله بن محمد بن سعيد الأصبهاني، حدث ببغداد واستوطنها، حدث عنه أبو بكر بن الجعابي، يروي عن أسيد بن عاصم','2791','818','0');
INSERT INTO [t1714] VALUES (1044,'عبد الله بن أحمد بن محمد بن سهل التميمي، والد أبي بكر بن ممجة','2792','818','0');
INSERT INTO [t1714] VALUES (1045,'عبد الله بن محمد بن عبد الكريم بن يزيد بن فروخ بن داود، مولى عياش بن مطرف بن عبد الله بن عياش بن أبي ربيعة المخزومي، أبو القاسم ابن أخي أبي زرعة، قدم أصبهان، توفي بها سنة عشرين وثلاثمائة، كثير الحديث، صاحب أصول، ثقة، يروي عن يونس وبحر بن نصر','2794','818','0');
