INSERT INTO [t1714] VALUES (1048,'عبد الله بن جعفر أبو محمد اليزدي، عم القاضي أبي القاسم يروي عن محمد بن بسام، ومحمد بن نصر','2802','818','0');
INSERT INTO [t1714] VALUES (1049,'عبد الله بن محمد بن نصر بن عبدة أبو محمد، ثقة يروي عن إسماعيل بن يزيد القطان مسنده وأخي رستة وغيرهما','2806','818','0');
INSERT INTO [t1714] VALUES (1050,'عبد الله بن محمد بن الحسين بن أحمد بن محمد بن الحسين بن حفص، أخو أبي جعفر، خال أبي علي أحمد بن عبد الرحمن بن محمد بن عمر','2809','818','0');
INSERT INTO [t1714] VALUES (1051,'عبد الله بن محمد بن معروف بن يزيد القرشي أبو محمد، حدث عن أبيه، توفي سنة ثماني عشرة وثلاثمائة، توفي عن ثمان وتسعين سنة، على سن والده وجده، هو والد أبي عمر بن معروف','2811','818','0');
INSERT INTO [t1714] VALUES (1052,'عبد الله بن إسحاق البزاز، حدث عنه أبو إسحاق بن حمزة','2814','818','0');
INSERT INTO [t1714] VALUES (1053,'عبد الله بن أحمد بن محمد بن النضر، حدث عن أبي مسعود، بالجامع، لم يخرج حديثه، ذكره المتأخر','2817','818','0');
INSERT INTO [t1714] VALUES (1054,'عبد الله بن جعفر بن أحمد بن فارس بن الفرج أبو محمد مولده سنة ثمان وأربعين ومائتين، وتوفي سنة ست وأربعين وثلاثمائة في شوال، ذكر المتأخر أنه توفي سنة خمس وأربعين وثلاثمائة في شوال، روى عن أبي مسعود أحمد بن الفرات وهارون بن سليمان الخزاز وأحمد بن عصام','2818','818','0');
INSERT INTO [t1714] VALUES (1055,'عبد الله بن محمد بن الحجاج بن يوسف، فقيه مقبول القول، ثقة، كتب عن المصريين والشاميين','2824','818','0');
INSERT INTO [t1714] VALUES (1056,'عبد الله بن خالد بن محمد بن رستم أبو محمد الرازي، سكن الخان، روى عن ابن أبي مسرة، ومحمد بن إسماعيل الصائغ','2827','818','0');
INSERT INTO [t1714] VALUES (1057,'عبد الله بن القاسم بن مدين أبو بكر، لم يزل يختلف إلى المجالس ويكتب إلى أن مات، وكذلك أخوه، وكان أسن منه، كتب عن أحمد بن مهدي وأبي بكر بن النعمان ولم يحدث','2829','818','0');
INSERT INTO [t1714] VALUES (1058,'عبد الله بن إسحاق بن يوسف الديلماني، محلة من محال جرجان، حدث عنه أبو أحمد، وأبو محمد والجماعة','2831','818','0');
INSERT INTO [t1714] VALUES (1059,'عبد الله بن محمد بن أحمد البناء، يروي عن ابني النعمان، يعرف بعبد الله بن أبي عمرو بن مهيار، شيخ ثقة يروي عن عبد الله بن عمر، وهارون بن طريف، والحسين بن حمران','2834','818','0');
INSERT INTO [t1714] VALUES (1060,'عبد الله بن محمد بن إسحاق أبو محمد، شيخ ثقة، كتب الكثير بمكة وبأصبهان وفارس','2837','818','0');
