INSERT INTO [t1714] VALUES (1062,'عبد الله بن محمد بن سهل بن الصباح أبو محمد','2844','818','0');
INSERT INTO [t1714] VALUES (1063,'عبد الله بن باذان أبو محمد المقرئ، روى عن محمد بن عبد الرحيم، وقرأ عليه، توفي في شعبان سنة ثلاثين','2846','818','0');
INSERT INTO [t1714] VALUES (1064,'عبد الله بن يوسف الرصاص المؤدب الأصبهاني','2849','818','0');
INSERT INTO [t1714] VALUES (1065,'عبد الله بن الحسن بن فورك روى عن عباد بن الوليد','2851','818','0');
INSERT INTO [t1714] VALUES (1066,'عبد الله بن محمد بن نصير بن عبد الله المديني، والد أبي مسلم بن نصير المعدل، حدث عن أحمد بن مهدي، توفي سنة أربع وعشرين وثلاثمائة، حدث عنه ابنه أبو مسلم إجازة','2853','818','0');
INSERT INTO [t1714] VALUES (1067,'عبد الله بن محمد بن بكار الفقيه، يروي عن أحمد بن يونس','2854','818','0');
INSERT INTO [t1714] VALUES (1068,'عبد الله بن موسى، روى عن ابن النعمان','2856','818','0');
INSERT INTO [t1714] VALUES (1069,'عبد الله بن محمد بن ممشاذ العسال، روى عن هارون بن سليمان','2858','818','0');
INSERT INTO [t1714] VALUES (1070,'عبد الله بن محمد بن مهران أبو محمد','2860','818','0');
