INSERT INTO [t1714] VALUES (1191,'عبد الرحمن بن محمد بن الخيبري بن موسى بن سعيد بن الخبيري أبو مسلم العبدي يروي عن الأخرم والطوسي، وابن السكن','3113','818','0');
INSERT INTO [t1714] VALUES (1192,'عبد الرحمن بن محمد بن عمرو بن يحيى بن ششاه أبو محمد القرمطي المؤذن سكن درب مميل، روى عن ابن النعمان، وأبي طالب بن سوادة، توفي سنة تسع وأربعين','3115','818','0');
INSERT INTO [t1714] VALUES (1193,'عبد الرحمن بن أحمد الطبري أبو أحمد القاضي والد عتبة القاضي، يروي عن الحسن بن سفيان','3117','818','0');
INSERT INTO [t1714] VALUES (1194,'عبد الرحمن بن بشير بن نمير بن أشتة أبو مسلم المؤدب شيخ ثقة صاحب أصول، كتب بخراسان وسجستان، حدث عن إسحاق بن إبراهيم بن إسماعيل البستي، ثنا قتيبة بن سعيد','3119','818','0');
INSERT INTO [t1714] VALUES (1195,'عبد الرحمن بن إبراهيم الفارقي حدث عن علي بن يونس روى عنه يونس بن أحمد المغازلي المتأخر','3120','818','0');
INSERT INTO [t1714] VALUES (1196,'عبد الرحمن بن محمد بن أحمد بن سياه أبو مسلم المذكر توفي سلخ رجب سنة ست وأربعين وثلاثمائة','3121','818','0');
INSERT INTO [t1714] VALUES (1197,'عبد الرحمن بن محمد بن جعفر الكسائي أبو بكر، يعرف بابن استرجة يروي عن ابن أبي عاصم توفي سنة أربع وستين','3124','818','0');
INSERT INTO [t1714] VALUES (1198,'عبد الرحمن بن محمد بن جعفر بن حيان أبو مسلم المؤدب أخو أبي الشيخ، توفي فجأة سنة تسع وخمسين وثلاثمائة، روى عن محمد بن زكرياء، وأحمد بن علي الخزاعي، والأخرم، وإبراهيم بن متويه','3126','818','0');
INSERT INTO [t1714] VALUES (1199,'عبد الرحمن بن محمد بن مدين أبو مسلم روى عن أبي بكر بن أبي عاصم، وأبي بكر البزار، وغيرهما، أدركته ولم أرزق السماع منه','3128','818','0');
INSERT INTO [t1714] VALUES (1200,'عبد الرحمن بن محمد بن إبراهيم أبو عمر القطان توفي يوم الأربعاء لخمس خلون من شوال سنة سبعين وثلاثمائة، يروي عن البغوي، وابن أبي داود، وغيرهما','3130','818','0');
INSERT INTO [t1714] VALUES (1201,'عبد الرحمن بن أحمد بن حمدويه أبو سعيد النيسابوري قدم أصبهان مجتازا إلى الحج، حدث عن أبي بكر بن خزيمة','3131','818','0');
INSERT INTO [t1714] VALUES (1202,'عبد الرحمن بن أحمد بن جعفر أبو القاسم القاضي توفي في شوال سنة أربع وسبعين وثلاثمائة','3133','818','0');
INSERT INTO [t1714] VALUES (1203,'عبد الرحمن بن عبد الله بن محمد بن نصير أبو مسلم المعدل المديني توفي في شعبان سنة ثلاث وثمانين، روى عن جده من قبل أمه أبي أسيد فوائده، وغيره','3136','818','0');
INSERT INTO [t1714] VALUES (1204,'عبد الرحمن بن محمد بن عبد الله بن راشد المديني ابن عم أبي عبد الله الراشدي، يكنى بأبي مسلم، روى عن الأخرم','3138','818','0');
INSERT INTO [t1714] VALUES (1205,'عبد الرحمن بن محمد بن بندار القسام المديني أبو الحسن روى عن علي بن سعيد العسكري','3140','818','0');
INSERT INTO [t1714] VALUES (1206,'عبد الرحمن بن إبراهيم بن سعيد أبو مسلم أحد من كتب الكثير، توفي قبل السبعين','3142','818','0');
INSERT INTO [t1714] VALUES (1207,'عبد الرحمن بن محمد بن مزيد أبو بكر الدقاق سمع الكثير توفي بعد السبعين','3144','818','0');
INSERT INTO [t1714] VALUES (1208,'عبد الرحمن بن نجم أبو القاسم مولى محمد بن بكار، وأبوه نجم الفقيه، كان خال أبي مضر، كتب ببغداد عن البغوي، وابن أبي داود، وإبراهيم بن محمد بن العمري حدثنا عبد الرحمن بن نجم أبو القاسم','3146','818','0');
INSERT INTO [t1714] VALUES (1209,'عبد الرحمن بن محمد بن إبراهيم بن شهدل بن مردة العنبري أبو مسلم المديني','3147','818','0');
INSERT INTO [t1714] VALUES (1210,'عبد الرحمن بن محمد الصيدلاني أبو مسلم روى عن عبد الله بن بندار الباطرقاني، حدث عنه أبو بكر بن المقرئ','3149','818','0');
INSERT INTO [t1714] VALUES (1211,'عبد الرحمن بن محمد بن الحصيب بن رستة الضبي أبو علي حدث عن الزبيبي، والداركي، وأبي عمرو بن عقبة، توفي سنة ست أو سبع وثمانين وثلاثمائة، واسم رستة إبراهيم بن الحسن بن يزيد بن مهران الضبي وهو مهران الضبي الكاتب نزيل جرواءان','3151','818','0');
INSERT INTO [t1714] VALUES (1212,'عبد الرحمن بن طلحة بن محمد بن عيسى بن محمد بن عيسى بن صالح بن إبراهيم بن محمد بن طلحة بن عبيد الله أبو عمر الطلحي رحمه الله، يروي عن أبي أسيد، والفضل بن الخصيب، وابن الجارود، وغيرهم توفي بعد الثمانين','3154','818','0');
INSERT INTO [t1714] VALUES (1213,'عبد الرحمن بن جعفر بن يحيى أبو صالح المؤدب روى عنه، أحمد بن محمد بن سندة الخباز','3156','818','0');
INSERT INTO [t1714] VALUES (1214,'باب من اسمه عبد العزيز','3157','818','0');
INSERT INTO [t1714] VALUES (1215,'عبد العزيز بن عبد الله بن أبي سلمة الماجشون أصبهاني الأصل، سكن المدينة، وإليهم تنسب سكة الماجشون، حكى ابن أبي خيثمة قال: كان الماجشون من أهل أصبهان فنزل المدينة ووقع بها، فكان يلقى الناس فيقول لهم: جوني جوني','3158','818','0');
INSERT INTO [t1714] VALUES (1216,'عبد العزيز بن محمد بن عبيد بن أبي عبيدة الدراوردي أبو محمد، من أهل أصبهان، سكن المدينة وتوفي بها','3161','818','0');
INSERT INTO [t1714] VALUES (1217,'عبد العزيز بن صبيح الأصبهاني يروي عن داود بن سليمان الأصبهاني، صاحب علي بن أبي طالب','3165','818','0');
INSERT INTO [t1714] VALUES (1218,'عبد العزيز بن يوسف بن معدان، أخو محمد بن يوسف، حكى عنه رستة، سمع سفيان بن عيينة','3167','818','0');
INSERT INTO [t1714] VALUES (1219,'عبد العزيز بن عمران بن كوشيذ المديني أبو بكر، صنف الشيوخ، قديم الموت، كتب بالشام ومصر والحجاز','3168','818','0');
INSERT INTO [t1714] VALUES (1220,'عبد العزيز بن أحمد بن محمد بن أسيد المعدل المديني أبو بكر بن أبي أسيد، توفي سلخ ذي القعدة سنة ثلاث وستين وثلاثمائة، يروي عن أبي يحيى زكرياء الساجي ومحمد بن نصير وغيرهما','3172','818','0');
INSERT INTO [t1714] VALUES (1221,'عبد العزيز بن محمد بن الحسن بن إبراهيم أبو بكر الخفاف المذكر، ثقة، روى عن البغداديين، والأصبهانيين، مات بعد الثلاثين وثلاثمائة، حدث عن محمد بن نصر الصائغ البغدادي، صاحب إسماعيل بن أبي أويس، وطبقته','3174','818','0');
INSERT INTO [t1714] VALUES (1222,'عبد العزيز بن محمد بن يوسف بن مسلم أبو الحسين بن حفصويه المؤدب، ابن عم همام القاضي، توفي لليلتين بقيتا من رمضان سنة خمس وسبعين، روى عن الأخرم، وأحمد بن الحسن بن عبد الملك، ومحمد بن نصير، ومحمد بن أحمد بن راشد، يرجع إلى تعبد وفضل كثير','3175','818','0');
INSERT INTO [t1714] VALUES (1223,'عبد العزيز بن محمد بن القاسم بن كوفي أبو القاسم الفقيه الصفار، يعرف بالغزال، كان يتفقه على مذهب العراقيين، روى عن البغوي والباغندي وغيرهما، توفي سنة نيف وستين وثلاثمائة','3176','818','0');
INSERT INTO [t1714] VALUES (1224,'عبد العزيز بن محمد بن مقرن أبو القاسم المعدل، توفي يوم الجمعة لليلتين خلتا من المحرم سنة ست وسبعين وثلاثمائة','3180','818','0');
INSERT INTO [t1714] VALUES (1225,'عبد العزيز بن عبد الله بن يزيد أبو بكر، حدث عنه أبو إسحاق السريجاني','3182','818','0');
INSERT INTO [t1714] VALUES (1226,'عبد العزيز بن أحمد بن علي أبو بكر التاجر المعروف بالآدمي، كان من وجوه التجار والأمناء، توفي سنة ست وثمانين وثلاثمائة، روى عن عبد الله بن خالد الرازاني، وعليه كان ينزل إذا قدم، كثير الخير مستقيم الطريقة','3183','818','0');
INSERT INTO [t1714] VALUES (1227,'عبد العزيز بن محمد بن عبد العزيز أبو محمد الكسائي المقرئ توفي سنة ثمان وثمانين، يروي عن محمد بن عمر بن حفص، وعبد الله بن إبراهيم بن الصباح، وأبي عمرو بن حكيم، وأبي علي الصحاف، وغيرهم','3185','818','0');
INSERT INTO [t1714] VALUES (1228,'عبد العزيز بن عبد الواحد بن محمد بن هدة الفقيه المديني أبو بكر، حدث عن العراقيين والمصريين، توفي في جمادى الآخرة سنة تسع وتسعين','3186','818','0');
INSERT INTO [t1714] VALUES (1229,'عبد الرحيم بن العباس المهرياناني سكن قرية مهريانان من موالي المنصور، قديم الموت، شيخ ثقة، يروي عن ابن أبي عمر، وعبد الجبار بن العلاء، ومحمود بن خداش، ولوين، وغيرهم','3187','818','0');
INSERT INTO [t1714] VALUES (1230,'عبد الرحيم بن عبد الله بن محمد بن النعمان روى عن محمد بن النضر الهلالي، حدث عنه أبو سعيد الزعفراني','3189','818','0');
INSERT INTO [t1714] VALUES (1231,'عبد الرحيم بن محمد المجاشعي الأصبهاني سكن الرملة، أبو علي أخو العباس بن محمد، حدث عنه أبو بكر المقرئ، وأبو محمد بن حيان وأبي','3190','818','0');
INSERT INTO [t1714] VALUES (1232,'عبد الرحيم بن محمد بن مسلم بن عبد الرحيم بن أسد المديني أبو علي، يروي عن إبراهيم بن سعدان، وإسماعيل بن أحمد، وإبراهيم بن نائلة، توفي سنة ثلاث وأربعين وثلاثمائة','3193','818','0');
INSERT INTO [t1714] VALUES (1233,'عبد الملك بن حميد بن أبي غنية الكوفي، أصبهاني الأصل، روى عنه أبو نعيم، عن الأعمش وأبي الخطاب الهجري،','3196','818','0');
INSERT INTO [t1714] VALUES (1234,'عبد الملك بن قريب بن عبد الملك بن أصمع أبو سعيد الأصمعي، قدم أصبهان، ذكره ابن منده، توفي سنة اثنتي عشرة ومائتين، حدث عن ابن عون، وشعبة، وسليمان بن المغيرة، ومالك، ويعقوب بن طحلاء،','3200','818','0');
INSERT INTO [t1714] VALUES (1235,'عبد الملك بن مسعود بن خالد بن هامان المقرئ، حدث عنه أبو حامد الملحمي،','3202','818','0');
INSERT INTO [t1714] VALUES (1236,'عبد الملك بن عبد الرحمن بن الأصبهاني، يروي عن خلاد الصفار، وعن أبيه، حديث ابن مسعود في وفاة النبي صلى الله عليه وسلم، حدث عنه عمرو بن محمد العنقري، وأبو نعيم، وعبد العزيز بن أبان','3204','818','0');
INSERT INTO [t1714] VALUES (1237,'عبد الكريم بن هارون المقرئ أبو مالك، وقيل: أبو محمد، كوفي قدم أصبهان، يروي عنه يحيى بن مطرف وسمويه، حدث عن مالك بن أنس، وعبد الله بن المبارك، وعبد الحميد بن سليمان','3206','818','0');
INSERT INTO [t1714] VALUES (1238,'عبد الصمد بن أحمد بن عبد الصمد بن أحمد بن يحيى بن أبان أبو القاسم التميمي المعدل','3209','818','0');
INSERT INTO [t1714] VALUES (1239,'عبد الصمد بن أحمد الحنظلي، أخو مطهر الحنظلي، أبو محمد','3212','818','0');
INSERT INTO [t1714] VALUES (1240,'عبد الصمد بن بحير بن محمد المعدل أبو القاسم، توفي بعد السبعين، روى عن ابن الجارود، وعبد الله بن محمود بن الفرج، حدثنا أبو القاسم عبد الصمد بن بحير بن محمد المعدل،','3213','818','0');
INSERT INTO [t1714] VALUES (1241,'عبد الغفار بن أحمد بن محمد بن عبد الصمد بن حبيب بن عبد الله بن غبار أبو الفوارس الحمصي حدث عنه القاضي، والجماعة، قدم علينا سنة خمس وتسعين ومائتين، ورجع إلى حمص ومات بها','3214','818','0');
INSERT INTO [t1714] VALUES (1242,'عبد الغفور بن عبد الله بن أحمد بن محمد بن أيوب الصالحاني أبو الحسين، يروي عن عبد الله بن جعفر بن أحمد بن فارس،','3218','818','0');
INSERT INTO [t1714] VALUES (1243,'عبد الحميد بن عبد الرحمن بن الحسين النيسابوري الحاكم، قدم أصبهان سنة أربعين وثلاثمائة، يكنى أبا الحسن','3219','818','0');
INSERT INTO [t1714] VALUES (1244,'عبد الحميد بن خالد بن صدقة المكي، قدم أصبهان على أبي مسعود','3221','818','0');
INSERT INTO [t1714] VALUES (1245,'عبد الوهاب بن زكرياء بن أبي زكرياء المعدل أبو سعيد، حدث عنه مطين، يروي عن الحسين بن حفص، وأبي داود، وأزهر، وعبد الله بن بكر السهمي، والقعنبي، وهو عم عبد الله بن محمد بن زكرياء','3223','818','0');
INSERT INTO [t1714] VALUES (1246,'عبد الوهاب بن المندلث الضبي الفقيه، كان من العباد، توفي في مسجد عامر الشونيزي، كان صواما قواما للقرآن طول عمره إلى أن مات','3227','818','0');
INSERT INTO [t1714] VALUES (1247,'عبد الوهاب بن عيسى بن عبد الرحمن بن عيسى أبو العلاء البغدادي قدم علينا بعد السبعين متاجرة','3230','818','0');
INSERT INTO [t1714] VALUES (1248,'عبد الوهاب بن عصام بن الحكم العكبري، قدم أصبهان، وسمع من عبد الله بن عمر، أخي رستة، وإسماعيل بن يزيد، وأبي مسعود، وأسيد بن عاصم','3231','818','0');
INSERT INTO [t1714] VALUES (1249,'عبد الوهاب بن سعيد ٢٦، سمع النعمان','3233','818','0');
INSERT INTO [t1714] VALUES (1250,'عبد الوهاب بن أبي أحمد محمد بن أحمد بن إبراهيم أبو عامر الغسال','3235','818','0');
INSERT INTO [t1714] VALUES (1251,'عبد السلام بن حبيب بن حطيط بن عقبة بن خثيم بن بكر، والد النعمان، كان قدم أصبهان مع أسيد بن عبد الله الخزاعي، وقام بها بالمدينة، وله بها خطة وضيعة، حدث عنه ابنه النعمان','3236','818','0');
INSERT INTO [t1714] VALUES (1252,'عبد المنعم بن عمر بن عبد الله بن حيان أبو بكر الصوفي، توفي قبل الثمانين','3238','818','0');
INSERT INTO [t1714] VALUES (1253,'عبد الرزاق بن بكر المكتب من أهل المدينة، يروي عن بكر، وإبراهيم، وهريم، وإسماعيل بن عمرو، وغيرهم','3242','818','0');
INSERT INTO [t1714] VALUES (1254,'عبد الرزاق بن منصور بن أبان البغدادي قدم أصبهان، لم أر أحدا حدث عنه غير الجمال','3244','818','0');
INSERT INTO [t1714] VALUES (1255,'عبد الرزاق بن عقيل الأصبهاني، سكن بغداد وحدث بها،','3246','818','0');
INSERT INTO [t1714] VALUES (1256,'عبد الرزاق بن أبي محمد أبي الشيخ سمع الكثير، من أبي الصحاف وطبقته، توفي في منصرفه من الحج بالنباج سنة نيف وخمسين','3249','818','0');
INSERT INTO [t1714] VALUES (1257,'عبد الرزاق بن عبد الله بن أحمد المكتنى بأبي أحمد أخي رحمه الله، وقف أربعين وقفة بعرفة، توفي سنة خمس وتسعين منصرفه من الحج بالبادية، سمع من الطبراني والشعار، وسمع بالعراقين، الكوفة والبصرة، وبالحرمين الحديث الكثير، وكتب عنه الغرباء ببغداد وغيره','3251','818','0');
INSERT INTO [t1714] VALUES (1258,'عبد الوارث بن الفردوس بن خاقان الضبي الفقيه أبو عبد الله، يعرف بتراز، كان يخضب، صاحب شروط، روى عن بكر بن بكار','3252','818','0');
INSERT INTO [t1714] VALUES (1259,'عباد بن مشكان القاضي، ولي القضاء بعد أبي هانئ، من أهل الكوفة، سكن جرواءان، كان أيوب بن زياد يبعث بأولاده إلى مجلسه، وله بأصبهان عقب، منهم أبو عمران الصوفي موسى ذكر محمد بن أيوب بن زياد قال: بعثني أبي وبأخي إلى الكوفة أكتب الحديث، فقال لي شريك بن','3255','818','0');
INSERT INTO [t1714] VALUES (1260,'عبيد بن الحسن بن يوسف بن مسلم بن عثمان الأنصاري أبو عبد الله، كان حافظا يذاكر بالأبواب والمسند، يروي عن التبوذكي، والحوضي، وسليمان بن حرب، وعمرو بن مرزوق، سمع من البصريين، وإسماعيل بن عمرو البجلي، وسعدويه، وغيرهم','3256','818','0');
INSERT INTO [t1714] VALUES (1261,'عباد بن العباس بن عباد بن أحمد بن إدريس الطالقاني أبو الحسن، توفي سنة خمس وثلاثين، كان يلي الوزارة للحسن بن بوبه، توفي سنة خمس وثلاثين وثلاثمائة، حدث عن الفريابي والمروزي وأبي خليفة ومحمد بن حيان المازني، كان أبو محمد بن حيان يخرج له الأحكام، وحدث','3261','818','0');
INSERT INTO [t1714] VALUES (1262,'عصام بن يزيد بن عجلان أبو سعيد المعروف بجبر مولى مرة الطبيب سبي عجلان، سباه الديلم من برخوار من قرية بلومية، فحمل إلى الكوفة، فاشتراه مرة الهمذاني، يروي عن الثوري، وشعبة، وهشيم، ومالك، وشريك، وحمزة الزيات، وابن عيينة، ويعقوب القمي، وزائدة،','3264','818','0');
INSERT INTO [t1714] VALUES (1263,'عصام بن سلم بن عبد الله بن أبي مريم أبو سلم بن عصام المديني، توفي وهو شاب سنة إحدى وثلاثين ومائتين، ولم يخرج حديثه، روى عن جويرية بن أسماء','3268','818','0');
INSERT INTO [t1714] VALUES (1264,'عصام بن يحيى أبو عاصم، يستملي إبراهيم بن عمر، حدث عنه أبو حامد الأشعري','3270','818','0');
INSERT INTO [t1714] VALUES (1265,'عصام بن محمد بن أحمد بن يحيى القطري أبو عاصم المديني، توفي سنة خمس وستين، روى عن سلم بن عصام','3271','818','0');
INSERT INTO [t1714] VALUES (1266,'العباس بن يزيد البحراني، يعرف بعباسويه، بصري قدم أصبهان، من الحفاظ، يروي عن يزيد بن زريع، وزياد البكائي، وأبي معاوية','3274','818','0');
INSERT INTO [t1714] VALUES (1267,'العباس بن الوليد بن مرداس الجلكي أبو الفضل، يروي عن القاسم بن الوليد بن مرداس','3278','818','0');
INSERT INTO [t1714] VALUES (1268,'العباس بن إسماعيل الطامذي المقرئ العابد أبو الفضل لزم بيته، توفي بعد الستين والمائتين ولم يحدث، وحفظ عنه الحديث بعد الحديث، روى عن علي بن محمد الطنافسي، روى عنه ابن أبي عاصم، ومحمد بن يحيى بن منده','3280','818','0');
INSERT INTO [t1714] VALUES (1269,'العباس بن حمدان بن محمد بن سلم الحنفي أبو الفضل تحول إلى المدينة ومات بها لأربع خلون من جمادى الآخرة سنة أربع وتسعين ومائتين، ثبت ثقة، كان من عباد الله الصالحين، صنف المسند، يروي عن العراقيين والأصبهانيين','3285','818','0');
INSERT INTO [t1714] VALUES (1270,'العباس بن محمد بن مجاشع أبو الفضل، شيخ ثقة','3289','818','0');
INSERT INTO [t1714] VALUES (1271,'العباس بن حمدان بن العباس بن مافروخ المافروخي المديني، روى عن النضر بن هشام، وإبراهيم بن ناصح، وأحمد بن يونس، وأحمد بن مهدي','3293','818','0');
INSERT INTO [t1714] VALUES (1272,'العباس بن الوليد بن شجاع أبو الفضل، يروي عن أحمد بن منصور، ومحمد بن يحيى النيسابوري، توفي سنة عشرين وثلاثمائة','3295','818','0');
INSERT INTO [t1714] VALUES (1273,'العباس بن إبراهيم بن العباس أبو الفضل الصوفي توفي في شعبان سنة سبعين وثلاثمائة','3299','818','0');
INSERT INTO [t1714] VALUES (1274,'العباس بن محمد أبو يعلى الرخجي، بغدادي قدم أصبهان','3302','818','0');
INSERT INTO [t1714] VALUES (1275,'عنبسة بن أبي حفص الأصبهاني، يروي عن الحسن، وابن سيرين ومجاهد وعكرمة، روى عنه شعبة بن عمران، وعامر بن إبراهيم','3304','818','0');
INSERT INTO [t1714] VALUES (1276,'عقيل بن يحيى بن الأسود أبو صالح الطهراني، يروي عن يحيى بن سعيد القطان، وأبي داود، وابن عيينة، وابن مهدي، توفي في رمضان سنة ثمان وخمسين ومائتين','3307','818','0');
INSERT INTO [t1714] VALUES (1277,'عقيل بن إبراهيم، يقال له: ابن أم هشام هو أخو علي بن المديني الأصبهاني، يروي عن محمد بن بكير، وغيره','3311','818','0');
INSERT INTO [t1714] VALUES (1278,'عيسى بن عبد الله أخو يعقوب القمي، روى عنه أخوه يعقوب، روى عن أبي حمزة الثمالي ١٠','3313','818','0');
INSERT INTO [t1714] VALUES (1279,'عيسى بن هشام المؤذن حدث عن الشاميين والبصريين روى عبد الله بن خالد بن محمد بن رستم، وقد رأيته','3315','818','0');
INSERT INTO [t1714] VALUES (1280,'عيسى بن بهرام مولى قريش سكن الدينور قدم أصبهان فحدث عنه عامر بن إبراهيم','3317','818','0');
INSERT INTO [t1714] VALUES (1281,'عيسى بن إبراهيم بن صالح بن زياد أبو موسى العقيلي يروي عن آدم، وأبي توبة الربيع بن نافع والشاميين، توفي سنة سبعين ومائتين','3319','818','0');
INSERT INTO [t1714] VALUES (1282,'عيسى بن عبد السلام أبو موسى الأصبهاني','3322','818','0');
INSERT INTO [t1714] VALUES (1283,'عيسى بن عبدويه بن جبريل أبو القاسم أصبهاني ثقة يروي عن بكر بن بكار، وأبي عبيدة حاتم بن عبيد الله وغيرهما','3324','818','0');
INSERT INTO [t1714] VALUES (1284,'عيسى بن محمد بن عيسى أبو عقيل المذكر روى عن عبيد الله بن جعفر وغيره','3326','818','0');
INSERT INTO [t1714] VALUES (1285,'عبدة بن صالح الأصبهاني يروي عن داود بن سليمان صاحب علي بن أبي طالب رضي الله عنه','3327','818','0');
INSERT INTO [t1714] VALUES (1286,'عذار بن عبيد الله أبو عبيد الله روى عن أبي روق الهمداني','3328','818','0');
INSERT INTO [t1714] VALUES (1287,'عسكر بن الحصين أبو تراب الصوفي قدم أصبهان قديما صحبه ابن أبي عاصم، كان من أهل نخشب من خراسان، كتب عنه عبد الله بن محمد بن زكرياء ومحمد بن عبد الله بن مصعب','3330','818','0');
INSERT INTO [t1714] VALUES (1288,'عطاء بن السائب بن أبي السائب ويعرف بعطاء الخشك يكنى أبا زيد وجده يكنى أبا جعدبة، قدم عطاء خراسان قائدا من قواد العباسية كان من أهل أصبهان وإليه ينسب دشت عطاء، وباب عطاء، وعقبه بمرو وكانت ابنته خالدة تحت حفص بن الفضل بن يحيى بن ذكوان فولدت له الحسين بن','3332','818','0');
INSERT INTO [t1714] VALUES (1289,'العلاء بن أبي العلاء الأصبهاني المؤذن روى عن جده، مرداس، حدث عنه، عقيل بن يحيى وسمويه','3335','818','0');
INSERT INTO [t1714] VALUES (1290,'العلاء بن إبراهيم أبو سعد الصوفي يحدث عن إبراهيم بن عامر، حدث عنه أبو إسحاق السريجاني','3337','818','0');
INSERT INTO [t1714] VALUES (1291,'عمارة بن أبي حفصة أبو روح الأزدي بصري','3338','818','0');
INSERT INTO [t1714] VALUES (1292,'عتاب بن ورقاء التميمي، قدم أصبهان واليا عليها، كان أحد الأسخياء يهتز للمدائح ويجزل عليها المنائح','3341','818','0');
INSERT INTO [t1714] VALUES (1293,'عبد المتعال بن عبد المنان بن خلف بن طفيل التميمي أبو اليسر النخشبي، قدم أصبهان مجتازا إلى الحج سنة ثمان وخمسين في غيبتي عنها، كتب عنه أحمد بن موسى الحافظ قال','3343','818','0');
INSERT INTO [t1714] VALUES (1294,'باب الغين','3345','0','0');
INSERT INTO [t1714] VALUES (1295,'غالب بن فرقد أبو يحيى وقيل أبو خالد الأصبهاني، يروي عن عمر بن صبح ومبارك بن فضالة وكثير بن سليم، سكن محله خشينان، روى عنه، إسماعيل بن يزيد القطان، وعقيل بن يحيى، وروح بن جبر','3346','1294','0');
INSERT INTO [t1714] VALUES (1296,'غالب بن عبيد الله بن غالب السعدي، بصري قدم أصبهان، حدث عن ابن عيينة، حدث عنه أحمد بن الحسين الأنصاري','3351','1294','0');
INSERT INTO [t1714] VALUES (1297,'غالب بن عبد الله أبو منصور الوراق روى عن الحسن بن عليل العنزي','3354','1294','0');
INSERT INTO [t1714] VALUES (1298,'غياث بن إبراهيم التميمي، كوفي قدم أصبهان، حديثه عند عامر بن إبراهيم، حدث محمد بن إبراهيم بن عامر','3355','1294','0');
INSERT INTO [t1714] VALUES (1299,'غياث بن محمد بن غياث المعدل أبو محمد كثير الحديث، دين ورع من الفقهاء','3357','1294','0');
INSERT INTO [t1714] VALUES (1300,'غانم بن عمر بن محمد بن أحمد بن مسلم الجرواءاني ابن عم همام القاضي، روى عن إبراهيم بن محمد بن الحسن وغيره','3359','1294','0');
INSERT INTO [t1714] VALUES (1301,'غسان بن الفضل بن زيد سأل أبا داود','3361','1294','0');
