INSERT INTO [t1714] VALUES (1631,'محمد بن الفضل بن الخصيب ثنا محمد بن أحمد بن محمد بن علي بن سابور أبو الحسين الأسواري، شيخ ثقة، سمع الرازيين، والعراقيين، والحجازيين: ابن أبي مسرة، وأبا حاتم، والزعفراني، وإبراهيم بن عبد الله العبسي','4235','1361','0');
INSERT INTO [t1714] VALUES (1632,'محمد بن أسباط بن عبد الله بن حماد أبو جعفر','4238','1361','0');
INSERT INTO [t1714] VALUES (1633,'محمد بن عبد الرحمن بن الحسن الباطرقاني أبو بكر روى عن الحارث بن أبي أسامة، والعراقيين، ومحمد بن إبراهيم الجيراني، والأصبهانيين','4240','1361','0');
INSERT INTO [t1714] VALUES (1634,'محمد بن بشر الخشاب المديني','4242','1361','0');
INSERT INTO [t1714] VALUES (1635,'محمد بن القاسم بن محمد بن كوفي الغزال','4244','1361','0');
INSERT INTO [t1714] VALUES (1636,'محمد بن عبيد الله بن أحمد بن محمد بن حفص أبو صالح الجلكي جار شاكر روى عن أحمد بن عصام، ثقة، روى عنه، أحمد بن موسى بن مردويه','4246','1361','0');
INSERT INTO [t1714] VALUES (1637,'محمد بن عبيد الله بن محمد بن الحسن النيسابوري أبو بكر، قدم سنة أربع وأربعين، حدث عنه أحمد بن موسى بن مردويه','4247','1361','0');
INSERT INTO [t1714] VALUES (1638,'محمد بن إسماعيل بن أحمد بن أسيد أبو مسلم توفي في صفر من سنة اثنتين وعشرين','4248','1361','0');
INSERT INTO [t1714] VALUES (1639,'محمد بن عمرو بن شهاب أبو عمر','4252','1361','0');
INSERT INTO [t1714] VALUES (1640,'محمد بن موسى بن خالد بن يحيى العنبري والد أبي بكر الملحمي المعافر','4254','1361','0');
INSERT INTO [t1714] VALUES (1641,'محمد بن عبد العزيز بن بولة أبو بكر المفسر توفي سنة ثمان وعشرين','4256','1361','0');
INSERT INTO [t1714] VALUES (1642,'محمد بن أحمد بن بطة أبو عبد الله المديني توفي سنة خمس وأربعين، خرج إلى نيسابور فأقام بها مدة ثم رجع إلى أصبهان، روى عن إبراهيم بن نائلة','4257','1361','0');
INSERT INTO [t1714] VALUES (1643,'محمد بن إبراهيم بن محمد بن الحسن أبو عبد الله ابن منويه الإمام إمام الجامع، معدل، مقبول القول، توفي سنة أربعين، وأذكر يوم وفاته يوم الجمعة، روى عن ابن النعمان الهروي والطبقة، قدمني إليه والدي رحمه الله في مرضه الذي توفي فيه فمسح رأسي وأعطاني أقطاع','4258','1361','0');
INSERT INTO [t1714] VALUES (1644,'محمد بن عيسى أبو الفوارس الوراق حدث عن أحمد بن المساور وطبقته، حدث عنه أبو سعيد الزعفراني في صحيحه لمسلم','4259','1361','0');
INSERT INTO [t1714] VALUES (1645,'محمد بن أحمد بن الحسن بن عمر بن الفرخان مولى مصعب بن الفضيل الثقفي أبو عبد الله المقرئ الكسائي، كان أصحابنا يختلفون إليه من مجلس ابن كوشيذ ولم أرزق السماع منه، يروي عن العراقيين: أبي خالد القرشي وغيره، توفي سنة سبع وأربعين وثلاثمائة','4260','1361','0');
INSERT INTO [t1714] VALUES (1646,'محمد بن أحمد بن إبراهيم بن سليمان بن محمد بن سليمان بن عبد الله مولى العلاء بن كسيب العنبري العسال، أبو أحمد ولي القضاء، مقبول القول، من كبار الناس في المعرفة والإتقان والحفظ، صنف الشيوخ، والتأريخ، والتفسير، وعامة المسند، مات في رمضان سنة تسع','4262','1361','0');
INSERT INTO [t1714] VALUES (1647,'محمد بن جعفر بن محمد بن ممشاذ أبو جعفر المذكر المعروف بالخياط توفي سنة ثمان وأربعين وثلاثمائة، سمع إبراهيم بن محمد بن الحسن، وابن مصقلة، والخزوري، كان إلى أن مات يسمع الحديث ويختلف معنا إلى مجلس أبي أحمد، وأبي الشيخ، حضرت جنازته وصليت عليه','4266','1361','0');
INSERT INTO [t1714] VALUES (1648,'محمد بن عبد الرحمن بن عمرو أبو محمد القرطمي متعبد صاحب أذان وصلاة، سمع من عبد الله بن محمد بن النعمان، وعبد الله بن أحمد بن سوادة، وأحمد بن عمرو البزاز، توفي في ذي الحجة سنة سبع وأربعين وثلاثمائة','4267','1361','0');
INSERT INTO [t1714] VALUES (1649,'محمد بن عبد الله بن محمد بن الجعد أبو بكر روى عن الأخرم، والجمال، وغيرهما','4269','1361','0');
INSERT INTO [t1714] VALUES (1650,'محمد بن يحيى بن بحرويه أبو عبد الله الشروطي كاتب القضاة، توفي سنة تسع وأربعين','4270','1361','0');
INSERT INTO [t1714] VALUES (1651,'محمد بن أحمد بن الأغلب بن سالم بن عقال بن خفاجة بن عبد الله بن عباد أبو بكر روى عن النيسابوريين: محمد بن إبراهيم البوشنجي، وإبراهيم الذهلي','4271','1361','0');
INSERT INTO [t1714] VALUES (1652,'محمد بن أحمد بن شعيب بن عيسى أبو بكر المذكر توفي سنة ثمان وأربعين وثلاثمائة فيما أقدر، شهدت جنازته','4273','1361','0');
INSERT INTO [t1714] VALUES (1653,'محمد بن إسحاق بن كوشيذ أبو بكر المقرئ توفي سنة ثلاث وخمسين وثلاثمائة، سمع إبراهيم بن سعدان والعراقيين أبا مسلم الكشي','4274','1361','0');
INSERT INTO [t1714] VALUES (1654,'محمد بن معمر بن ناصح أبو مسلم الذهلي الأديب توفي في صفر سنة خمس وخمسين وثلاثمائة، روى عن موسى بن هارون، ويوسف القاضي، وأبي شعيب وطبقتهم، وعن ابن أبي عاصم ببعض مصنفاته','4276','1361','0');
INSERT INTO [t1714] VALUES (1655,'محمد بن جعفر بن محمد بن حفص أبو بكر المغازلي معدل ثقة، صاحب الإطعام والصدقة، روى عن أبي بكر بن النعمان، وأبي طالب بن سوادة، وعلي بن محمد بن سعيد الثقفي، وابن أبي عاصم، والهروي، والأخرم','4281','1361','0');
INSERT INTO [t1714] VALUES (1656,'محمد بن القاسم بن محمد بن سياه أبو بكر العسال كان ثقيل السمع، توفي في سلخ صفر سنة إحدى وخمسين، سمع ابن النعمان، وعبيدا الغزال','4284','1361','0');
INSERT INTO [t1714] VALUES (1657,'محمد بن عبد الله بن ممشاذ بن زيد أبو بكر القارئ يعرف بالقنديل مجاب الدعوة، توفي سنة تسع وأربعين أو خمسين، روى عن أبي بكر بن النعمان، وعبيد الغزال، ومحمود بن الفرج، ومحمد بن إسماعيل سمويه صندوق العلم، وصحب جدي محمد بن يوسف البناء','4287','1361','0');
INSERT INTO [t1714] VALUES (1658,'محمد بن إسحاق بن عمران بن إسماعيل أبو بكر الصيدلاني المتطبب المديني سمعت منه، سمع إبراهيم بن نائلة','4290','1361','0');
INSERT INTO [t1714] VALUES (1659,'محمد بن أحمد بن الحسن بن محمد بن حمزة الهيساني أبو عمر الضبي توفي سنة ثمان وخمسين وثلاثمائة وله من السن ست وثمانون سنة، سمع أبا بكر بن النعمان، وأبا طالب بن سوادة، ومحمد بن عبد الله بن الحسين، وإبراهيم بن نائلة','4292','1361','0');
INSERT INTO [t1714] VALUES (1660,'محمد بن أحمد بن محمد بن يعقوب أبو بكر الشيباني القماط شيخ ثقة، صاحب أصول، توفي بعد الخمسين، روى عن ابن أبي عاصم، وإبراهيم بن نائلة وغيرهما','4294','1361','0');
INSERT INTO [t1714] VALUES (1661,'محمد بن عمر بن محمد بن سلم بن البراء أبو بكر الجعابي الحافظ قدم علينا سنة تسع وأربعين','4296','1361','0');
INSERT INTO [t1714] VALUES (1662,'محمد بن إسحاق بن أيوب أبو بكر الصيدلاني المديني المتطبب توفي سنة خمسين، روى عن إبراهيم بن الحارث','4297','1361','0');
INSERT INTO [t1714] VALUES (1663,'محمد بن أحمد بن المنذر أبو الحسن الصيدلاني المديني توفي سنة إحدى أو اثنتين وخمسين، يروي عن محمد بن نصير، وابن راشد','4299','1361','0');
INSERT INTO [t1714] VALUES (1664,'محمد بن أحمد بن يوسف بن جعفر البصري أبو الطيب المقرئ نزيل بغداد، قدم علينا قبل الخمسين، وسماعي منه سنة تسع وأربعين وثلاثمائة','4301','1361','0');
INSERT INTO [t1714] VALUES (1665,'محمد بن الحسن بن محمد بن دكة المعدل أبو جعفر يروي عن محمد بن عبد الله بن الحسن، وأبي العباس الهروي','4302','1361','0');
INSERT INTO [t1714] VALUES (1666,'محمد بن زريق أبو بكر البغدادي المجدر كان يورق على أبي بكر الجعاني في أيامه','4303','1361','0');
INSERT INTO [t1714] VALUES (1667,'محمد بن إبراهيم بن الفضل أبو الفضل الأستاديراني من قرية أستاديران، روى عن أحمد بن عمرو البزار، ذهب سماعي منه','4305','1361','0');
INSERT INTO [t1714] VALUES (1668,'محمد بن محمد بن يوسف بن مكي الجرجاني أبو أحمد يروي عن العراقيين، والخراسانيين، قدم علينا سنة خمسين، ورأيته ببغداد سنة سبع وخمسين، وسمعنا منه أصل كتاب البخاري عن الفربري عنه','4306','1361','0');
INSERT INTO [t1714] VALUES (1669,'محمد بن أحمد بن عبد الوهاب بن داود بن بهرام السلمي أبو بكر المقرئ الضرير توفي سنة خمس وخمسين وثلاثمائة، روى عن علي بن جبلة، والحسن بن هارون، ومحمد بن إبراهيم بن نصر، ومحمد بن عبد الرحيم بن شبيب، وموسى بن هارون، والطوسي، قرأت عليه القرآن ختمة،','4311','1361','0');
INSERT INTO [t1714] VALUES (1670,'محمد بن نصر أبو بكر مولى ابن رستة، توفي بعد الخمسين، يروي عن محمد بن عبد الله بن الحسن، وعبد الله بن محمد بن زكرياء','4314','1361','0');
INSERT INTO [t1714] VALUES (1671,'محمد بن عبيد الله بن المرزبان الواعظ أبو بكر سمع الكثير، صحب أبا عبد الله الخشوعي وكان من الورعين، روى عن محمد بن يحيى بن منده، وإبراهيم بن محمد بن الحسن، وسمع بالعراق من الكوفيين، والحجازيين، توفي سنة ثلاث وخمسين','4316','1361','0');
INSERT INTO [t1714] VALUES (1672,'محمد بن سفيان بن إبراهيم بن علي بن بطة الضبي أبو عبد الله الرباطي من كبار المتصوفة، توفي سنة اثنتين وخمسين وثلاثمائة، سمع بالعراق من محمد بن الليث الجوهري، وابن ناجية والطبقة','4319','1361','0');
INSERT INTO [t1714] VALUES (1673,'محمد بن سفيان بن هارون بن سفيان بن بشير بن راشد بن أبان أبو عبد الله ابن بنت جعفر الفريابي قدم أصبهان سنة أربعين وثلاثمائة، روى عن جده جعفر الفريابي وغيره، كان يسكن الدينور، وأبوه سفيان كان قاضي الدينور، حدث عنه أبو بكر بن مردويه','4321','1361','0');
INSERT INTO [t1714] VALUES (1674,'محمد بن عبد الله بن سين أبو عبد الله الحاسب المهندس سمع من محمد بن عبد الله مطين، وأبي شعيب الحراني، والحسن بن علي الطوسي. حدثنا أبو عبد الله محمد بن عبد الله بن سين، ثنا محمد بن عبد الله الحضرمي','4322','1361','0');
INSERT INTO [t1714] VALUES (1675,'محمد بن عبد الله بن سعيد بن الحسين أبو علي العسكري سكن أصبهان وتوفي بها سنة ثمان وخمسين، حدث بالمسند عن عبدان بن أحمد، وعن ابن أبي داود، وعن البغداديين، والبصريين','4323','1361','0');
INSERT INTO [t1714] VALUES (1676,'محمد بن عبد الله بن محمد بن منده أبو بكر المقرئ المعروف بالمفتولي، سكن كران','4326','1361','0');
INSERT INTO [t1714] VALUES (1677,'محمد بن جعفر بن محمد بن مهران أبو بكر المؤدب والد أبي عثمان القارئ، يروي عن إبراهيم بن محمد بن الحسن، وابن مصقلة','4328','1361','0');
INSERT INTO [t1714] VALUES (1678,'محمد بن حمدان المقرئ أبو بكر الحبال قرأت عليه القرآن، توفي قبل الخمسين، حدث عن إبراهيم بن محمد بن الحسن، وسلم بن عصام وغيرهما','4330','1361','0');
INSERT INTO [t1714] VALUES (1679,'محمد بن عبد الله بن أحمد الصباح أبو عبد الله المؤدب توفي سنة اثنتين وسبعين، يروي عن أبي خليفة، وابن مكرم','4331','1361','0');
INSERT INTO [t1714] VALUES (1680,'محمد بن محمد بن عبيد الله بن عمرو بن أبي الحسين الجرجاني كتب بالشام، والعراق، وخراسان، قدم أصبهان، قد مات وتوفي بها، من أهل القرآن، والحديث، والأخبار، كان يملي علينا في الجامع، أخرج عنه أبو محمد بن حيان في كتابه، توفي سنة خمس وخمسين','4333','1361','0');
INSERT INTO [t1714] VALUES (1681,'محمد بن جعفر بن زياد بن مهران أبو بكر المؤدب توفي قبل الخمسين، سمع إبراهيم بن محمد بن الحسن، وابن راشد، وسلم بن عصام، كثير الحديث، كان يسمع إلى أن توفي رحمه الله','4335','1361','0');
INSERT INTO [t1714] VALUES (1682,'محمد بن الحسن بن علي بن معاذ أبو عبد الله جارنا، صحب أبا محمد بن حيان أبا الشيخ وخرج معه إلى الري، توفي بعد الخمسين','4338','1361','0');
INSERT INTO [t1714] VALUES (1683,'محمد بن أحمد بن غريب بن طريف أبو المنيب الفقيه الطبري أقام بأصبهان سنين ثم خرج إلى شيراز وتوفي بها بعد الستين، سمع ابن صاعد، وعلي بن عبد الله بن مبشر الواسطي صاحب كتب مقاتل بن سليمان، التفسير وغيره','4340','1361','0');
INSERT INTO [t1714] VALUES (1684,'محمد بن أحمد بن شبويه أبو عبد الله الوراق أحد الحفاظ، كتب بالشام والعراق، كان يسمع الحديث إلى أن مات، توفي في ذي الحجة من سنة ست وستين','4342','1361','0');
INSERT INTO [t1714] VALUES (1685,'محمد بن أحمد بن الحسن الزعفراني أبو عمرو روى عن البغوي','4344','1361','0');
INSERT INTO [t1714] VALUES (1686,'محمد بن عبد الرحمن بن الفضل بن الحسين بن الفضل بن زيد بن ماهان بن بيان بن كوفي أبو بكر الجوهري التميمي الخطيب سمع أبا خليفة، وعبدان، وابن زهير، ومحمودا، والعراقيين، والحجازيين، صاحب التفاسير والقراءات، توفي بعد الستين','4345','1361','0');
INSERT INTO [t1714] VALUES (1687,'محمد بن عبد الرحمن بن سهل بن مخلد أبو عبد الله الغزال توفي في ذي الحجة من سنة تسع وستين وثلاثمائة، رحل إلى الشام، ومصر، والعراق، أحد من يرجع إلى حفظ ومعرفة، له المصنفات والشيوخ','4347','1361','0');
INSERT INTO [t1714] VALUES (1688,'محمد بن عبيد الله بن إبراهيم بن داود أبو عبد الله الكرابيسي المؤدب صاحب أصول، شيخ ثقة، توفي بعد الستين','4350','1361','0');
INSERT INTO [t1714] VALUES (1689,'محمد بن عيسى أبو بكر القصاب وكيل القضاة','4353','1361','0');
INSERT INTO [t1714] VALUES (1690,'محمد بن ممشاذ بن عبد الله بن خالد أبو عبد الله كان يسكن قرية قه جاورسان، يروي عن أبي حامد الملحمي، وطبقته','4355','1361','0');
INSERT INTO [t1714] VALUES (1691,'محمد بن أحمد بن حسنويه أبو بكر من باب باغ سلم، صاحب أصول، توفي بعد الستين، يروي عن البغوي','4356','1361','0');
INSERT INTO [t1714] VALUES (1692,'محمد بن أحمد بن محمد بن حمدان بن النضر أبي هريرة أبو عثمان بن أبي هريرة أحد العباد والأخيار، توفي سنة أربع وستين، سمع بالشام والعراق والري الكثير، صاحب أصول وكتب كثيرة','4358','1361','0');
INSERT INTO [t1714] VALUES (1693,'محمد بن جعفر بن وارة أبو عبد الله الصوفي توفي سنة خمس وستين في ذي الحجة، يروي عن سلم بن عصام، والداركي','4360','1361','0');
INSERT INTO [t1714] VALUES (1694,'محمد بن جعفر بن الحسين بن محمد بن زكرياء أبو بكر غندر الوراق البغدادي قدم علينا قدمتين ثم خرج إلى خراسان بعد الستين وتوفي بها','4362','1361','0');
INSERT INTO [t1714] VALUES (1695,'محمد بن بكر بن إبراهيم أبو مسلم الغزال توفي بعد الخمسين، بقي في داره مقعدا سنين، سمع الأخرم، وإبراهيم بن متويه، والطويل، وحاجبا، وصحب أبا عبد الله الخشوعي محمد بن الحسين وروى عنه','4364','1361','0');
INSERT INTO [t1714] VALUES (1696,'محمد بن إبراهيم بن علي بن عاصم بن زاذان أبو بكر المقرئ محدث كبير ثقة أمين، صاحب مسانيد وأصول، سمع بالعراق، والشام، ومصر ما لا يحصى كثرة، توفي في شوال سنة إحدى وثمانين، وكان من المعمرين، توفي عن ست وتسعين سنة، حدث عنه أبو إسحاق بن حمزة في صحيحه','4366','1361','0');
INSERT INTO [t1714] VALUES (1697,'محمد بن الحسن الأصبهاني أبو جعفر الصوفي سكن نيسابور','4367','1361','0');
INSERT INTO [t1714] VALUES (1698,'محمد بن أحمد بن جعفر بن إسحاق أبو الحسين الأبح المذكر توفي في شعبان سنة سبعين وثلاثمائة، يروي عن محمد بن سهل، وأبي عمرو بن عقبة، والهذيل، ويونس المعارك، والطبقة، كثير الحديث حسن المعرفة به','4369','1361','0');
INSERT INTO [t1714] VALUES (1699,'محمد بن أحمد بن الفضل أبو بكر التاجر الأردستاني ابن أخي علي بن الفضل، توفي سنة سبع وثمانين، روى عن عبد الرحمن بن أبي حاتم','4371','1361','0');
INSERT INTO [t1714] VALUES (1700,'محمد بن إبراهيم بن أحمد بن إسحاق أبو طاهر النفري المحتسب ابن سبط محمد بن يوسف ابن عمي، توفي في شوال سنة أربع وستين، سمع من محمد بن إبراهيم بن المنذر النيسابوري بمكة، ومن الحسين بن يحيى بن عياش، وكتب بالشام والعراق الكثير','4373','1361','0');
INSERT INTO [t1714] VALUES (1701,'محمد بن جعفر بن محمد الأشناني المقرئ أبو عبد الله توفي قبل الستين، حدث عن ابن أبي حاتم','4374','1361','0');
INSERT INTO [t1714] VALUES (1702,'محمد بن جعفر أبو جعفر الصابوني المقرئ يقرئ في مسجد عمرو بن راشد، توفي سلخ رمضان سنة سبع وسبعين','4376','1361','0');
INSERT INTO [t1714] VALUES (1703,'محمد بن إبراهيم بن مقاتل بن تميم أبو مسلم المقرئ توفي قبل الستين','4378','1361','0');
INSERT INTO [t1714] VALUES (1704,'محمد بن أحمد بن جعفر أبو بكر الغزال كان مصلاه مسجد عمرو بن راشد','4379','1361','0');
INSERT INTO [t1714] VALUES (1705,'محمد بن أحمد بن محمد بن إبراهيم أبو منصور الوراق يعرف بابن الخباز توفي قبل الستين، روى عن الداركي، وابن الجارود، والفضل بن الخصيب والطبقة، كثير السماع والحديث','4381','1361','0');
INSERT INTO [t1714] VALUES (1706,'محمد بن عبيد الله بن محمد الغسال أبو الحسن توفي قبل السبعين، سمع الحسن بن محمد بن دكة، والفتح بن إدريس، وأبا مسعود العسكري عبدان، وعبد الله بن محمد بن الخليل، سمع الكثير، صاحب أصول ومعرفة','4384','1361','0');
INSERT INTO [t1714] VALUES (1707,'محمد بن عبد الله بن حامد بن علي بن خريش أبو بكر أخو محمود الدقاق توفي قبل الستين','4385','1361','0');
INSERT INTO [t1714] VALUES (1708,'محمد بن أحمد بن علي بن ممجة أبو الحسن يلقب بمسدد كان يلي الترويج من القضاة، توفي قبل السبعين، روى عن محمد بن يحيى بن عيسى البصري، وعمر بن سهل الدينوري، وابن السكن، وغيرهم','4387','1361','0');
INSERT INTO [t1714] VALUES (1709,'محمد بن أحمد بن محمد بن جشنس أبو بكر المعدل توفي في رمضان سنة أربع وثمانين، سمع من إسحاق بن جميل، ومحمد بن سهل بن الصباح، والحسن بن محمد بن دكة، وبالعراق من أبي محمد بن صاعد، صاحب أصول وكتب كثيرة، ثقة أمين','4388','1361','0');
INSERT INTO [t1714] VALUES (1710,'محمد بن علي بن محمد بن شنبويه الغزال أبو بكر الكوسج سمع من علي بن محمد بن مهرويه القزويني، ومن الرازيين، والأصبهانيين سمع الكثير','4391','1361','0');
INSERT INTO [t1714] VALUES (1711,'محمد بن القاسم بن أحمد بن فاذشاه أبو عبد الله الشافعي يعرف بالنتيف متكلم على مذهب أهل السنة، ينتحل مذهب أبي الحسن الأشعري، عاد إلى أصبهان سنة ثلاث وخمسين وتوفي بها في ربيع الأول سنة إحدى وثمانين، سمع الكثير بالعراق، كثير المصنفات في الأصول والفقه','4393','1361','0');
INSERT INTO [t1714] VALUES (1712,'محمد بن أحمد بن جعفر أبو عبد الله القصار والد أبي بكر الفقيه القصار، توفي بعد الثمانين سمع الكثير، ونسخ الأصول، سمع من ابن مخلد ببغداد ومن ابن المقرئ بمكة','4394','1361','0');
INSERT INTO [t1714] VALUES (1713,'محمد بن أحمد بن عيسى أبو بكر السمسار توفي بعد الثمانين','4395','1361','0');
INSERT INTO [t1714] VALUES (1714,'محمد بن أحمد بن محمد بن حمدان أبو أحمد المروزي النيسابوري المعدل قدم علينا حاجا سنة أربع وسبعين','4397','1361','0');
INSERT INTO [t1714] VALUES (1715,'محمد بن الحسن بن محمد بن سليمان أبو نصر يتفقه على مذهب الشافعي، ثقة صالح','4399','1361','0');
INSERT INTO [t1714] VALUES (1716,'محمد بن إبراهيم النيلي المقرئ إمام الجامع في رمضان في التراويح، توفي في شوال سنة سبع وسبعين وثلاثمائة','4401','1361','0');
INSERT INTO [t1714] VALUES (1717,'محمد بن علي بن أحمد بن محمد بن عمران أبو بكر الخلقاني يعرف بابن كرسبة سمع الكثير بمكة والعراق، توفي بعد الثمانين','4402','1361','0');
INSERT INTO [t1714] VALUES (1718,'محمد بن عبد الله بن أحمد أبو بكر الرصاص من أهل القرآن والفضل، توفي قبل الثمانين، يروي عن محمد بن عمر بن حفص','4404','1361','0');
INSERT INTO [t1714] VALUES (1719,'محمد بن أحمد بن محمد بن مهدي أبو عبد الله الوراق صاحب أصول، كتب الكثير، توفي بعد الثمانين','4405','1361','0');
INSERT INTO [t1714] VALUES (1720,'محمد بن عبد الله بن محمد بن معروف بن يزيد بن عبد الله بن معروف أبو عمر كاتب جعفر اليزدي، توفي في المحرم سنة ست وسبعين وثلاثمائة، سمع الكثير بالعراق وخراسان، صاحب تصانيف كثيرة، حسن الدين والخلق والمروة','4407','1361','0');
INSERT INTO [t1714] VALUES (1721,'محمد بن عبد الله بن أحمد أبو عبد الله الجصاص الوراق كان يحضر معنا إلى أن توفي','4408','1361','0');
INSERT INTO [t1714] VALUES (1722,'محمد بن عبد الله بن محمد بن عمر بن عبد الله بن الحسن أبو الحسين أخو أبي الحسن، توفي في ذي الحجة سنة ثمانين، يروي عن ابن أخي أبي زرعة، وابن الجارود','4410','1361','0');
INSERT INTO [t1714] VALUES (1723,'محمد بن عبيد الله بن الحسن بن محمد بن عبد الله بن الحسن أبو بكر المعدل توفي بقاسان، ورد إلى أصبهان في ذي القعدة سنة خمس وثمانين، روى عن ابن الجارود، وابن أخي أبي زرعة، ومحمد بن عبد الله بن شهاب، وغيرهم','4411','1361','0');
INSERT INTO [t1714] VALUES (1724,'محمد بن الحسن بن عبد الله بن حسنكويه أبو عمر الفقيه انتقل إلى شيراز وكان إليه القضاء ببلد بيضاء، يتفقه على مذهب سفيان الثوري، روى عن عبد الله بن إبراهيم بن الصباح، ومحمد بن عمر بن حفص','4413','1361','0');
INSERT INTO [t1714] VALUES (1725,'محمد بن أحمد بن أيوب أبو بكر القطان الميراثي','4414','1361','0');
INSERT INTO [t1714] VALUES (1726,'محمد بن عبد الله بن غالب بن راشد الأصبهاني سكن الكوفة، وروى عنه الكوفيون','4416','1361','0');
INSERT INTO [t1714] VALUES (1727,'محمد بن عبد الملك يروي عن محمد بن الجهم','4418','1361','0');
INSERT INTO [t1714] VALUES (1728,'محمد بن علي بن يحيى الطبيب القناطري أبو الحسن روى عن الفرقدي، حدث عنه أبو بكر بن مردويه','4420','1361','0');
INSERT INTO [t1714] VALUES (1729,'محمد بن علي بن إبراهيم المؤدب أبو بكر روى عن الأخرم، وإبراهيم بن متويه، حدث عنه ابن مردويه','4421','1361','0');
INSERT INTO [t1714] VALUES (1730,'محمد بن علي بن محمد بن عوف البرجي أبو مسلم الضرير يروي عن ابن أبي حاتم','4422','1361','0');
INSERT INTO [t1714] VALUES (1731,'محمد بن علي بن مهرة القماط أبو بكر يروي عن محمد بن عمر بن حفص، وعبد الله بن إبراهيم بن الصباح','4424','1361','0');
INSERT INTO [t1714] VALUES (1732,'محمد بن علي بن أحمد الحمال أبو بكر الصحاف سمع من الهجيمي، وشعبة البصري، وغيرهما من البصريين','4425','1361','0');
INSERT INTO [t1714] VALUES (1733,'محمد بن علي بن محمد بن عبد الله بن يزيد الصائغ أبو جعفر الطبري كتب عنه ابن مردويه، وقد رأيته','4426','1361','0');
INSERT INTO [t1714] VALUES (1734,'محمد بن الفيرزان المعدل أبو الهيثم يروي عن موسى بن عبد الرحمن بن مهدي، حدث عنه أبو بكر الشيباني القماط','4427','1361','0');
INSERT INTO [t1714] VALUES (1735,'محمد بن محمد الضبي السقطي حدث عنه أبو حامد الأشعري','4428','1361','0');
INSERT INTO [t1714] VALUES (1736,'محمد بن محمد بن إبراهيم الواصلي الطوسي قدم أصبهان','4429','1361','0');
INSERT INTO [t1714] VALUES (1737,'محمد بن حميد بن عبد الرحمن بن يوسف المعداني حدث عنه أبو بكر بن أبي الدنيا','4431','1361','0');
INSERT INTO [t1714] VALUES (1738,'محمد بن منده الأصبهاني حدث بالري، وببغداد، عن الحسين بن حفص، وبكر بن بكار، وهو محمد بن منده بن منصور الأصبهاني. ضعفه بعض الناس بروايته عن الحسين بن حفص، عن شعبة','4433','1361','0');
INSERT INTO [t1714] VALUES (1739,'محمد بن عمران بن أيوب بن عمران والد عبد الله المعدل، له محل وستر. كتب عن قتيبة وعبيد الله بن موسى','4435','1361','0');
INSERT INTO [t1714] VALUES (1740,'محمد بن عثمان بن عمرو بن مرزوق توفي سنة تسع وأربعين ومائتين','4438','1361','0');
INSERT INTO [t1714] VALUES (1741,'محمد بن الحسن بن أبي الحسن يعرف بمتويه والد إبراهيم الإمام أبو عبد الله، كانت أمه ابنة الضحاك بن مزيد بن عجلان، سمع من محمد بن بكير وعبد الله بن عمران وسعدويه','4440','1361','0');
INSERT INTO [t1714] VALUES (1742,'محمد بن محمد بن صخر بن سدوس التيمي أبو جعفر سكن طهران، توفي سنة ثمان وستين ومائتين، حدث عن الحسين بن حفص، والحميدي، والمقرئ، ومحمد بن حرب، وخلاد بن يحيى، وأبي عاصم، وسليمان بن حرب وغيرهم','4441','1361','0');
INSERT INTO [t1714] VALUES (1743,'محمد بن عيسى بن خالد الزجاج إمام الجامع أبو عبد الله ثقة مأمون، حدث عن عبيد الله بن موسى، وأبي نعيم، والناس، وأبي عاصم، والحسين بن حفص، ومحمد بن زياد الأصبهاني، وموسى التبوذكي، ويحيى بن حماد، والفضل بن موفق، أحد الثقات','4445','1361','0');
INSERT INTO [t1714] VALUES (1744,'محمد بن خشنام بن عبد الواحد يروي عن بكر بن بكار، وغيره','4449','1361','0');
INSERT INTO [t1714] VALUES (1745,'محمد بن العباس بن خالد بن يزيد بن ماهان أبو عبد الله مولى بني سليم، أحد العدول الثقات، توفي سنة ست وستين ومائتين، كان من رفقاء عباس الطامذي، وزكرياء بن الصلت، وعبد الرحمن بن علي، يروي عن أبي عاصم، والحسين بن حفص، والمقرئ، وعبيد الله بن موسى،','4451','1361','0');
INSERT INTO [t1714] VALUES (1746,'محمد بن إبراهيم بن حكيم بن أسيد يقال له: ممك، والد إسحاق، وأحمد، حدثا عنه، توفي سنة سبعين ومائتين، روى عن بكر بن بكار، والشاذكوني','4456','1361','0');
INSERT INTO [t1714] VALUES (1747,'محمد بن إبراهيم بن يزيد الضبي أخو أحمد، ويعرف بمحمد بن أبي الخصيب السني، حدث عن بكر بن بكار، حدث عنه إسحاق بن شاذة، ذكره المتأخر ولم يخرج له شيئا حدثنا محمد بن جعفر، ثنا أحمد بن الحسين، ثنا محمد بن إبراهيم بن يزيد، ثنا بكر بن بكار، ثنا عمر بن علي','4458','1361','0');
INSERT INTO [t1714] VALUES (1748,'محمد بن عيسى بن يزيد السعدي أبو بكر الطرسوسي روى عنه أبو مسعود، ثنا محمد بن عيسى، وليس بابن الطباع، روى عن عيسى قالوا: وسليمان بن بنت شرحبيل، ومحمد بن حميد، وسليمان بن داود، وعباس الرقام، قدم أصبهان، وحدث بها، وخرج، منها إلى خراسان','4459','1361','0');
INSERT INTO [t1714] VALUES (1749,'محمد بن إبراهيم بن يزيد الشيباني أبو عبد الله يعرف بالأخوين يروي عن بكر بن بكار، وصالح بن مهران، ومحمد بن المغيرة، وحاتم بن عبيد الله','4463','1361','0');
INSERT INTO [t1714] VALUES (1750,'محمد بن أحمد بن معاذ بن سعد أبو العلاء الموازيني البلخي حدث عن مكي بن إبراهيم، ورجاء بن نوح، وقتيبة بن سعيد روى عنه إسحاق بن شاذة، ذكره المتأخر','4465','1361','0');
INSERT INTO [t1714] VALUES (1751,'محمد بن سليمان بن عبد الله الجرواءاني يعرف بالوكيل أبو عبد الله حدث عنه الناس، لم يخرج له أبو محمد شيئا','4467','1361','0');
INSERT INTO [t1714] VALUES (1752,'محمد بن أيوب الضراب الأصبهاني حدث عن نعيم بن حماد، وموسى بن داود الضبي والشاميين، حدث عنه عمران بن عبد الرحيم، وعبد الله بن محمد بن سلام','4469','1361','0');
INSERT INTO [t1714] VALUES (1753,'محمد بن نصر بن عبدة الخرجاني توفي سنة اثنتين وخمسين ومائتين، حدث عن يحيى بن أبي بكير، وداود بن إبراهيم، حدث عنه محمد بن يحيى بن منده، وابنه عبد الله بن محمد بن نصر، كانا جميعا ثقتين','4471','1361','0');
INSERT INTO [t1714] VALUES (1754,'محمد بن إبراهيم المعداني قرابة محمد بن يوسف المعداني، ويعرف بشنبويه','4475','1361','0');
INSERT INTO [t1714] VALUES (1755,'محمد بن نوح بن محمد الشيباني السمسار سكن باب كوشك، يكنى أبا عبد الله، روى عن شبابة، وإبراهيم بن حميد الطويل، وأبي الوليد، وخلاد، والحسين بن حفص','4477','1361','0');
INSERT INTO [t1714] VALUES (1756,'محمد بن بكار بن الحسن بن عثمان بن زيد بن زياد العنبري الفقيه أبو عبد الله يتفقه على مذهب الكوفيين، توفي سنة خمس وستين ومائتين، لم يخرج له أبو محمد شيئا','4480','1361','0');
INSERT INTO [t1714] VALUES (1757,'محمد بن عبد الله بن غالب بن راشد الأصبهاني نزيل الكوفة، حدث عن الكوفيين','4481','1361','0');
INSERT INTO [t1714] VALUES (1758,'محمد بن الفرج الفقيه أبو جعفر كان خيرا فاضلا، ينظر في كتب الكوفيين، وكان قيم الجامع باليهودية، وكان الفرج من موالي خالد بن يزيد القرني، يروي عن القاسم بن الحكم العرني، حدث عنه محمد بن يحيى بن منده','4483','1361','0');
INSERT INTO [t1714] VALUES (1759,'محمد بن إدريس بن المنذر بن داود بن مهران أبو حاتم الرازي إمام في الحفظ والفهم، توفي سنة سبع وسبعين','4486','1361','0');
INSERT INTO [t1714] VALUES (1760,'محمد بن معاوية بن طالب البصري حدث عن بكر بن بكار ذكره المتأخر، ولم يخرج له شيئا','4489','1361','0');
INSERT INTO [t1714] VALUES (1761,'محمد بن معاذ بن فهد ابن أخي إبراهيم بن فهد، قدم أصبهان مع عمه','4491','1361','0');
INSERT INTO [t1714] VALUES (1762,'محمد بن خليفة الأنصاري أبو ليث قدم أصبهان، حدث عنه عامر بن إبراهيم، يروي عن كثير بن أبي كثير المدني','4493','1361','0');
INSERT INTO [t1714] VALUES (1763,'محمد بن مهدي أبو صالح من الرحالة كتب بمصر، والشام، والعراقين، ومات قديما، حدث عن الحارث بن مسكين، روى عنه محمد بن يحيى بن منده','4495','1361','0');
INSERT INTO [t1714] VALUES (1764,'محمد بن سعيد بن مزيد المقرئ الأصبهاني نزل الري، يكنى أبا جعفر','4498','1361','0');
INSERT INTO [t1714] VALUES (1765,'محمد بن عبد الرحيم المجاشعي الأصبهاني روى عن هشام بن عروة حدث عنه عامر بن إبراهيم','4500','1361','0');
INSERT INTO [t1714] VALUES (1766,'محمد بن راشد بن معدان الثقفي عم أبي بكر محمد بن أحمد بن راشد، يكنى أبا بكر، روى عن حيان بن بشر','4502','1361','0');
INSERT INTO [t1714] VALUES (1767,'محمد بن عبد الرحيم الوراق الأصبهاني يروي عن سلمة بن شبيب، حدث عنه أبو عبد الله الشعار','4504','1361','0');
INSERT INTO [t1714] VALUES (1768,'محمد بن عبد الوهاب الأصبهاني صحب سهل بن عبد الله','4505','1361','0');
INSERT INTO [t1714] VALUES (1769,'محمد بن عبد العزيز بن الفيض بن الفضل بن زيد التميمي الكسائي أبو جعفر والد عبد العزيز بن محمد الكسائي، روى عن محمد بن أحمد بن تميم وغيره','4507','1361','0');
INSERT INTO [t1714] VALUES (1770,'محمد بن إبراهيم بن الرماح قاضي أصبهان خراساني الدار والمنشأ','4508','1361','0');
INSERT INTO [t1714] VALUES (1771,'محمد بن مهران بن كوشاذ الأصبهاني سكن سرمري، حدث عن إبراهيم الهروي','4510','1361','0');
INSERT INTO [t1714] VALUES (1772,'محمد بن عمرو بن يحيى القرمطي يعرف بابن شساه يروي عن إسماعيل بن عمرو البجلي، روى عنه ابنه شيخنا الذي سمعنا منه','4512','1361','0');
INSERT INTO [t1714] VALUES (1773,'محمد بن عبد الله بن المستورد أبو سيار البغدادي الشافعي قدم أصبهان، قال إبراهيم بن أورمة: ما قدم عليكم مثل أبي سيار. حدث عنه عبدان، يروي عن سليمان بن حرب، ومعافى بن سليمان، والحسن بن أحمد بن أبي شعيب','4514','1361','0');
INSERT INTO [t1714] VALUES (1774,'محمد بن عبد الله بن الصباح أبو مسعود أخو جعفر، صنف المسند، وكان من الحفاظ المذاكرين، لم يخرج حديثه','4519','1361','0');
INSERT INTO [t1714] VALUES (1775,'محمد بن إسحاق بن ماهان المسوحي أبو عبد الله ختن رستة، نزل الدينور، وكان من الحفاظ، وحدث بها، وبها مات، قال أبو محمد بن حيان: بلغني أن النعمان حدث عنه حديثا','4521','1361','0');
INSERT INTO [t1714] VALUES (1776,'محمد بن النضر بن سلمة النيسابوري أبو بكر الجارودي قدم أصبهان سنة ثمان وثمانين ومائتين، كان من الحفاظ، يروي عن إسحاق بن راهويه، ومحمد بن رافع، والعراقيين','4524','1361','0');
INSERT INTO [t1714] VALUES (1777,'محمد بن عيسى أبو نصر العقيلي قدم أصبهان، وكان قاضيا بأردبيل','4526','1361','0');
INSERT INTO [t1714] VALUES (1778,'محمد بن خرة العابد، وقيل: محمد بن عبد الله بن خرة يروي عن إبراهيم بن أيوب، ومحمد بن بكير','4528','1361','0');
INSERT INTO [t1714] VALUES (1779,'محمد بن منده بن مهربزد','4529','1361','0');
INSERT INTO [t1714] VALUES (1780,'محمد بن سليمان بن علي سكن سكة القصارين يروي عن بكر، وأبي حذيفة، ومسلم، وكان عنده كتب حماد بن سلمة، عن حجاج بن منهال، لم يخرج له أبو محمد شيئا','4531','1361','0');
INSERT INTO [t1714] VALUES (1781,'محمد بن ممشاذ بن خزيمة أبو عبد الله سكن جوزدان، وانتقل إلى طرسوس، وتوفي بها، جمع القراءات عن أبي حاتم، وكتب الشافعي عن الربيع','4533','1361','0');
INSERT INTO [t1714] VALUES (1782,'محمد بن إبراهيم بن الحسن ولقب محمد: ممويه بن رستة الأصبهاني، يروي عن الأصمعي','4534','1361','0');
INSERT INTO [t1714] VALUES (1783,'محمد بن رستة بن الحسن حدث عن الأصمعي، والأنصاري، ذكره المتأخر، وهو محمد بن إبراهيم بن الحسن المتقدم','4536','1361','0');
INSERT INTO [t1714] VALUES (1784,'محمد بن سمويه','4538','1361','0');
INSERT INTO [t1714] VALUES (1785,'محمد بن هشام بن عجلان حدث عنه محمد بن سهل بن الصباح، وأحمد بن الحسين الأنصاري، والفضل بن الخصيب، روى عن قتيبة بن سعيد، وبشر بن الحكم','4540','1361','0');
INSERT INTO [t1714] VALUES (1786,'محمد بن بكر البرجمي أبو بكر البصري قدم أصبهان، يروي عن عبد الرحمن بن المبارك العيشي، ومحمد بن الطفيل، وعبد الله بن رجاء، حدث عنه محمد بن الحسن بن أيوب النقاش','4543','1361','0');
INSERT INTO [t1714] VALUES (1787,'محمد بن نصر بن القاسم المقرئ المديني يروي عن أبي سفيان صالح بن مهران، توفي سنة إحدى وثمانين','4545','1361','0');
INSERT INTO [t1714] VALUES (1788,'محمد بن مجاشع أبو العباس','4547','1361','0');
INSERT INTO [t1714] VALUES (1789,'محمد بن النضر بن أحمد بن حبيب بن الزبير بن مشكان الهلالي يعرف بممشاذ توفي سنة سبع وسبعين ومائتين، وقيل: خمس وسبعين، يكنى أبا الحسن، يروي عن بكر بن بكار، والحسين بن حفص','4549','1361','0');
INSERT INTO [t1714] VALUES (1790,'محمد بن النضر بن حبيب الزبيري أبو الحسن يروي عن عامر بن إبراهيم، ومحمد بن المغيرة، وغيرهما','4552','1361','0');
INSERT INTO [t1714] VALUES (1791,'محمد بن إبراهيم بن أبان أبو عبد الله الجيراني المكتب أحد الثقات، توفي سنة ثمان وسبعين ومائتين، حدث عن الحسين بن حفص، وبكر بن بكار وحاتم بن عبيد الله','4555','1361','0');
INSERT INTO [t1714] VALUES (1792,'محمد بن عبد الله بن الحسن بن حفص أبو عبد الله الهمذاني توفي سنة خمس وثمانين ومائتين، حدث عن أبي سفيان صالح بن مهران، ومحمد بن بكير الخضرمي والبصريين، والناس، عرض عليه قضاء أصبهان، وورد كتاب المعين بن المتوكل بتولية القضاء له عليها، فهرب منها إلى','4558','1361','0');
INSERT INTO [t1714] VALUES (1793,'محمد بن الحسن بن سعيد الأصبهاني كان بالبصرة، حدث عن حاتم بن عبيد الله، وبكر بن بكار، ومحمد بن بكير','4563','1361','0');
INSERT INTO [t1714] VALUES (1794,'محمد بن إبراهيم الكناني الأصبهاني حدث بهراة، سنة تسع وثمانين ومائتين، يروي عن يونس بن حبيب، روى عنه علي بن محمد الدغولي، ذكره المتأخر','4565','1361','0');
INSERT INTO [t1714] VALUES (1795,'محمد بن أبي سهل واسم أبي سهل: شيرازاذ بن خوشة أبو عبد الله توفي سنة خمس وثمانين ومائتين، حدث عن أحمد بن يونس، والحماني، وأبي بكر بن أبي شيبة، والقعنبي، وسهل بن بكار، والحوضي','4566','1361','0');
INSERT INTO [t1714] VALUES (1796,'محمد بن سعيد بن وهب الأصبهاني أبو بكر روى عن محمد بن بكير الحضرمي','4569','1361','0');
INSERT INTO [t1714] VALUES (1797,'محمد بن يحيى السعيدي سبط سعدويه الأصبهاني، سكن سروشاذران، توفي سنة أربع وثمانين ومائتين','4571','1361','0');
INSERT INTO [t1714] VALUES (1798,'محمد بن الحارث الصيداوي الأسدي توفي بعد ثمانين ومائتين، حدث عن عمرو بن مالك البجلي الأصبهاني','4572','1361','0');
INSERT INTO [t1714] VALUES (1799,'محمد بن إسماعيل بن فضيل بن زيد يقال له: صندوق العلم، أبو جعفر، توفي بعد الثمانين','4574','1361','0');
INSERT INTO [t1714] VALUES (1800,'محمد بن إسماعيل بن بكير ولقب إسماعيل سبة، صاحب خان سبة، يكنى أبا جعفر، سمع الأصمعي، ويحيى بن خلف القاضي، وغيرهما','4576','1361','0');
INSERT INTO [t1714] VALUES (1801,'محمد بن يعقوب بن مهران أبو عبد الله سكن باب دشت، والد عبد الله، وأحمد، لم يخرج حديثه، توفي بعد المائتين، كتب عنه أهل بغداد في اجتيازه إلى الحج، يروي عن محمد بن حميد','4578','1361','0');
INSERT INTO [t1714] VALUES (1802,'محمد بن عصام بن يونس الهذلي الكسائي حدث عن محمد بن بكر، روى عنه إسحاق بن شاذة، ذكره المتأخر','4580','1361','0');
INSERT INTO [t1714] VALUES (1803,'محمد بن الحسن بن حماد بن سليمان حدث عن أبي الربيع الزهراني، حدث إسحاق بن شاذة، ذكره المتأخر','4582','1361','0');
INSERT INTO [t1714] VALUES (1804,'محمد بن الأزمع بن الأشنع بن طلحة بن عبيد بن مالك الأزدي يكنى أبا حامد، وقيل: أبو داود الجرواءاني حدث عن محمد بن بكير','4583','1361','0');
INSERT INTO [t1714] VALUES (1805,'محمد بن سنديلة النحوي يعرف بممشاذ صاحب عربية، من أهل جرواءان، حدث عن محمد بن بكير، وسهل، والشاذكوني','4585','1361','0');
INSERT INTO [t1714] VALUES (1806,'محمد بن نوح الجرواءاني أبو عبد الله حدث عن البصريين','4587','1361','0');
INSERT INTO [t1714] VALUES (1807,'محمد بن خزيمة بن عبد الله المري من قرية مارجية، حدث عنه أبو علي الصحاف','4589','1361','0');
INSERT INTO [t1714] VALUES (1808,'محمد بن خداش روى عن أبي أيوب الشاذكوني','4591','1361','0');
INSERT INTO [t1714] VALUES (1809,'محمد بن حميد بن زياد أبو مسلم السعيدي حدث عنه محمد بن عمر بن حفص وغيره','4593','1361','0');
INSERT INTO [t1714] VALUES (1810,'محمد بن زكرياء بن عبد الله بن محمد أبو جعفر القرشي كتب عنه أبو بكر بن أبي داود السجستاني والشيوخ، قال الجمال: كنا نخرج من مجلس عبد الله بن عمران، ونأتي محمد بن زكرياء، فنسمع منه تفسير أبي حذيفة. صاحب أصول جياد صحاح، سمع البصريين: عثمان بن الهيثم،','4597','1361','0');
INSERT INTO [t1714] VALUES (1811,'محمد بن إبراهيم بن شبيب العسال أبو عبد الله شيخ ثقة، يروي عن إسماعيل بن عمرو، وحيان بن بشر، ومحمد بن المغيرة، وسهل بن عثمان، وعثمان بن عبد الوهاب الثقفي، وغيرهم، توفي سنة اثنتين وتسعين ومائتين','4604','1361','0');
INSERT INTO [t1714] VALUES (1812,'محمد بن هارون الجوزداني أبو عبد الله شيخ دين فاضل، كان يختلف إلى مجلس البزار','4609','1361','0');
INSERT INTO [t1714] VALUES (1813,'محمد بن شهمردان بن حرب أبو عبد الله روى عن الرازيين، وعمرو بن علي','4611','1361','0');
INSERT INTO [t1714] VALUES (1814,'محمد بن مردة بن رستم قديم الموت، كتب عن محمد بن بكير','4613','1361','0');
INSERT INTO [t1714] VALUES (1815,'محمد بن أبي الهيثم الأصبهاني يروي عن بكر بن بكار، حدث عنه أبو العباس بن عقدة','4616','1361','0');
INSERT INTO [t1714] VALUES (1816,'محمد بن أحمد بن سليمان أبو العباس الهروي فقيه، محدث، كثير المصنفات، كتب عنه عامة شيوخنا: جعفر بن أحمد بن فارس، والوليد بن أبان، وإسحاق وأبو عمرو ابنا ممك، خرج من عندنا إلى الجبل سنة ست وثمانين ومائتين ومات بها','4618','1361','0');
INSERT INTO [t1714] VALUES (1817,'محمد بن عبد الله بن مصعب أبو عبيد الله كان من كبار القراء يؤم في الجامع في رمضان، حسن الصوت بالقرآن، كتب عن ابن أبي عمر، وعبد الجبار، والعابدي، توفي سنة إحدى وتسعين','4622','1361','0');
INSERT INTO [t1714] VALUES (1818,'محمد بن سهل بن سليمان أبو عبد الله يروي عن محمد بن بكير','4625','1361','0');
INSERT INTO [t1714] VALUES (1819,'محمد بن يوسف بن معدان بن يزيد بن عبد الرحمن الثقفي البناء الصوفي جد والدي، توفي سنة ست وثمانين، حج سنة خمس وأربعين، وكتب عن عبد الجبار، وسعيد المخزومي، والعابدي، وحسين المروزي، ومحمد بن منصور الخزاز، وإبراهيم بن سلام بن حبيب، ومحمد بن جعفر بن','4628','1361','0');
INSERT INTO [t1714] VALUES (1820,'محمد بن يحيى بن يزيد بن مالك الضبي الخرجاني أبو جعفر توفي سنة إحدى وتسعين ومائتين، شيخ ثقة، حدث عن محمود بن غيلان، والحسين بن حريث، ومحمد بن أبان البلخي، والرازيين','4632','1361','0');
INSERT INTO [t1714] VALUES (1821,'محمد بن إسحاق بن ملة المسوحي أبو عبد الله توفي سنة تسع وتسعين ومائتين، من الثقات، حدث عن لوين، والرازيين، والأصبهانيين','4636','1361','0');
INSERT INTO [t1714] VALUES (1822,'محمد يحيى بن منده بن الوليد بن سندة بن بطة بن أستندار وأستندار سمة للجيش، وأستندار اسمه الفيرزان بن جهار بخت. أسلم وقت الفتح، وكان على بعض أعمال البلد أبو عبد الله العبدي، توفي سنة إحدى وثلاثمائة، واسم منده إبراهيم، منده لقب. قال أبو محمد بن حيان','4642','1361','0');
INSERT INTO [t1714] VALUES (1823,'محمد بن العباس بن أيوب بن سعيد أبو جعفر الأخرم مولى لقريش، توفي سنة إحدى وثلاثمائة، وقطع عن التحديث سنة ست وتسعين لاختلاطه، كان من الحفاظ، مقدما فيهم، شديدا على أهل الزيغ والبدعة، كان ممن يتفقه في الحديث ويفتي به. قال أبو محمد بن حيان: سألني عنه','4650','1361','0');
INSERT INTO [t1714] VALUES (1824,'محمد بن عبد الله بن رستة بن الحسن بن عمر بن زيد الضبي المديني أبو عبد الله كتب مع خاله أبي الحسين بن مخلد بفائدة إبراهيم بن أورمة عن العراقيين، وسمع من الرازيين، والأصبهانيين، وكان الشاذكوني، نازلا عليهم، توفي سنة إحدى وثلاثمائة. حدثنا عنه القاضي،','4656','1361','0');
INSERT INTO [t1714] VALUES (1825,'محمد بن عبد الرحيم بن إبراهيم بن شبيب الأسدي أبو بكر توفي سنة ست وتسعين ومائتين، كان من أئمة القراء، سمع من عثمان بن أبي شيبة، وعبد الله بن عمر بن أبان، وإسحاق بن أبي إسرائيل','4662','1361','0');
INSERT INTO [t1714] VALUES (1826,'محمد بن عبدوس بن مالك بن الأسود بن الصلت أبو الحسن الثقفي الطحان فقيه مناظر، سمع من ابن زغبة، وأبي مصعب، ودخل مصر مع إبراهيم بن متويه، توفي بعد الثلاثمائة','4666','1361','0');
INSERT INTO [t1714] VALUES (1827,'محمد بن أحمد بن البراء البغدادي أبو الحسن قدم مع الموفق، وكان يخطب في الجامع سنة ست وسبعين ومائتين، حدث عن المعافى بن سليمان، وعلي بن المديني','4669','1361','0');
INSERT INTO [t1714] VALUES (1828,'محمد بن الحسين بن إبراهيم بن زياد بن عجلان الأبهري أبو الشيخ سكن بغداد، وتوفي بها سنة ست وثمانين ومائتين','4673','1361','0');
INSERT INTO [t1714] VALUES (1829,'محمد بن عبد الله أبو هشام الملطي قدم قبل الثلاثمائة، يروي عن جعفر بن عبد الواحد، ذكره المتأخر، حدث عنه أبو علي بن عاصم، وعبد الله بن محمود بن كوفي','4676','1361','0');
INSERT INTO [t1714] VALUES (1830,'محمد بن الفضل بن حماد الأصبهاني يروي عن حيان بن بشر','4679','1361','0');
INSERT INTO [t1714] VALUES (1831,'محمد بن عبد الله بن سليمان بن عبيد الله النوفلي البغدادي قدم أصبهان','4681','1361','0');
INSERT INTO [t1714] VALUES (1832,'محمد بن عبد الرحيم الوراق الأصبهاني يروي عن سلمة بن شبيب','4683','1361','0');
INSERT INTO [t1714] VALUES (1833,'محمد بن مسلم بن عبد العزيز الأشعري الأصبهاني يروي عن مجاشع بن عمرو','4685','1361','0');
INSERT INTO [t1714] VALUES (1834,'محمد بن الفيرزان أبو الهيثم المعدل يعرف بممشاذ أصبهاني، ذكره محمد بن أحمد بن يعقوب وكتب عنه','4687','1361','0');
INSERT INTO [t1714] VALUES (1835,'محمد بن عبد الله بن مخلد أبو الحسين خال محمد بن عبد الله بن رستة، يعرف بصاحب الشافعي، وراق الربيع بن سليمان، توفي بمصر قبل التسعين، روى عنه الفضل بن الخصيب، وأبو بكر بن راشد، روى عن قتيبة بن سعيد، والشاميين: كثير بن عبيد، والخبائري وغيرهم، حدث','4689','1361','0');
INSERT INTO [t1714] VALUES (1836,'محمد بن الحسن بن سعيد الأصبهاني قديم الموت، حدث عنه محمد بن الحسين بن مكرم','4694','1361','0');
INSERT INTO [t1714] VALUES (1837,'محمد بن إسحاق بن إبراهيم بن صالح العقيلي أبو بكر الفابزاني توفي سنة ثلاث وثمانين ومائتين، حدث عنه أبو عثمان إسحاق بن إبراهيم، روى عن دحيم، وهشام بن عمار وغيرهما','4697','1361','0');
INSERT INTO [t1714] VALUES (1838,'محمد بن عيسى أبو جعفر صنف الأبواب والكتب، ارتحل إلى اليمن قديما، حدث عنه عبدان','4699','1361','0');
INSERT INTO [t1714] VALUES (1839,'محمد بن عيسى بن سهلويه الأدمي الأصبهاني أبو جعفر','4701','1361','0');
INSERT INTO [t1714] VALUES (1840,'محمد بن إبراهيم بن إسحاق العقيلي حدث بالبصرة','4703','1361','0');
INSERT INTO [t1714] VALUES (1841,'محمد بن أسد بن يزيد أبو عبد الله المديني كان من المعمرين، مستجاب الدعوة، توفي سنة ثلاث وتسعين ومائتين، سمع من أبي داود الطيالسي مجلسا، وسمع من هريم بن عبد الأعلى الأسدي حديثا واحدا، وكان مقعدا','4705','1361','0');
INSERT INTO [t1714] VALUES (1842,'محمد بن عاصم بن يحيى أبو عبد الله كاتب القاضي توفي سنة تسع وتسعين ومائتين، يتفقه للشافعي، صنف كتبا كثيرة، روى عن المصريين والأصبهانيين','4710','1361','0');
INSERT INTO [t1714] VALUES (1843,'محمد بن الفضل أبو هاشم قدم أصبهان، ذكره المتأخر، وهو الملطي، وقد تقدم ذكره','4716','1361','0');
INSERT INTO [t1714] VALUES (1844,'محمد بن أبان بن عبد الله المديني أبو مسلم الفقيه كثير الحديث، ثقة، توفي سنة ثلاث وتسعين ومائتين، يروي عن إسماعيل بن عمرو، وأبي أيوب الشاذكوني والعراقيين، كتب بالعراق بفائدة إبراهيم بن أورمة','4718','1361','0');
INSERT INTO [t1714] VALUES (1845,'محمد بن إبراهيم بن سعيد بن ماونداد الثقفي أبو عبد الله الوشاء شيخ صدوق، يروي عن زيد بن الحريش، وطالوت بن عباد، وعبد الواحد بن غياث، ولوين، توفي سنة تسع وتسعين ومائتين','4722','1361','0');
INSERT INTO [t1714] VALUES (1846,'محمد بن بكر بن إلياس بن بنان أبو جعفر الحافظ الخوارزمي يعرف بمحمد بن أبي علي، ابن أخي كاجويه، قدم أصبهان سنة تسع وتسعين ومائتين، صاحب غرائب، كثير الحديث، كتب عنه أبو إسحاق بن حمزة، وأبو محمد بن حيان بأصبهان وببغداد، وهو ختن عمر بن إبراهيم أبي','4724','1361','0');
INSERT INTO [t1714] VALUES (1847,'محمد بن الحسين أبو عبد الله الخشوعي أستاذ الورعين والقراء، كتب الكثير، توفي قبل الثلاثمائة','4728','1361','0');
INSERT INTO [t1714] VALUES (1848,'محمد بن هارون بن عبد الله أبو بكر','4731','1361','0');
INSERT INTO [t1714] VALUES (1849,'محمد بن مسور بن إشكيب المديني روى عن الزبير بن بكار، وعباس بن يزيد','4733','1361','0');
INSERT INTO [t1714] VALUES (1850,'محمد بن المنذر بن يزيد أبو عبد الله أصبهاني','4736','1361','0');
INSERT INTO [t1714] VALUES (1851,'محمد بن عبد الله بن حيان أبو مسلم حدثنا أبو محمد بن حيان، ثنا أبو مسلم محمد بن عبد الله بن حيان، ثنا أبو القاسم عبد الله بن شعيب الحراني، ثنا عبد الرحمن بن يوسف بن خراش، حدثني إبراهيم بن أورمة الأصبهاني، قال: قرأت في أصل معاذ بن هشام، أخرجه إلي','4738','1361','0');
INSERT INTO [t1714] VALUES (1852,'محمد بن عبد الله بن يوسف أبو عبد الله القصير ابن أخت علي بن جبلة','4739','1361','0');
INSERT INTO [t1714] VALUES (1853,'محمد بن الضحاك بن عمرو بن الضحاك بن مخلد الشيباني البصري قدم أصبهان في أيام عمه أحمد بن عمرو','4741','1361','0');
INSERT INTO [t1714] VALUES (1854,'محمد بن مندويه الغزال','4743','1361','0');
INSERT INTO [t1714] VALUES (1855,'محمد بن مندويه الطويل توفي قبل الثلاثمائة، وكان ممن يختلف إلى البزار','4745','1361','0');
INSERT INTO [t1714] VALUES (1856,'محمد بن مندويه بن الحجاج بن المهاجر أبو عبد الله والد أبي محمد بن مندويه الشروطي','4748','1361','0');
INSERT INTO [t1714] VALUES (1857,'محمد بن عبيدة بن يزيد بن عبيدة الجرواءاني أبو عبد الله أحد الثقات، توفي سنة إحدى وثلاثمائة، يروي عن سليمان بن عمر بن الأقطع، ومؤمل، وابن عسكر، ويوسف القطان وغيرهم','4750','1361','0');
INSERT INTO [t1714] VALUES (1858,'محمد بن إسماعيل بن بطة بن متة الحنظلي الأصبهاني حدث بالكوفة، وكان حاجا','4753','1361','0');
INSERT INTO [t1714] VALUES (1859,'محمد بن إسماعيل الخفاف يعرف بممشاذ بن سمويه، يروي عن سهل بن عثمان، حدث عنه عبد الله بن إبراهيم بن الصباح','4754','1361','0');
INSERT INTO [t1714] VALUES (1860,'محمد بن يعقوب بن أبي يعقوب واسم أبي يعقوب إسحاق، وكنية محمد أبو بكر، روى عن عباد بن يعقوب، وإبراهيم بن سلام المكي، وإبراهيم الصيرفي، وجعدويه','4755','1361','0');
INSERT INTO [t1714] VALUES (1861,'محمد بن جعفر بن حمدان أبو عبد الله البغدادي قدم أصبهان، يروي عن هلال بن العلاء وغيره','4759','1361','0');
INSERT INTO [t1714] VALUES (1862,'محمد بن إبراهيم بن نصر بن شبيب الصفار أبو بكر ثقة، تحول إلى المدينة، توفي سنة خمس وثلاثمائة، يروي عن هارون الحمال مسنده، وكتب أبو ثور عنه، أحد الثقات','4760','1361','0');
INSERT INTO [t1714] VALUES (1863,'محمد بن الحسن بن علي بن يزيد الجوهري الأصبهاني أبو بكر روى عن يونس بن حبيب، ومحمد بن عامر، روى عنه الكوفيون','4763','1361','0');
INSERT INTO [t1714] VALUES (1864,'محمد بن إسماعيل بن سعيد بن عبد الله البزاز أبو جعفر التميمي المديني يروي عن إسماعيل بن عمرو، وعبد الله بن عمران','4764','1361','0');
INSERT INTO [t1714] VALUES (1865,'محمد بن أحمد بن سعيد المكتب أبو مسلم فقيه، روى عن أبي سعيد الأشج، توفي سنة إحدى وثلاثمائة','4766','1361','0');
INSERT INTO [t1714] VALUES (1866,'محمد بن نصير بن عبد الله بن أبان وقيل: أبان جشنس أبو عبد الله القرشي مأمون، توفي في شهر ربيع الأول سنة خمس وثلاثمائة، يروي عن إسماعيل بن عمرو البجلي، وسليمان الشاذكوني، حدثنا عنه القاضي والطبقة','4768','1361','0');
INSERT INTO [t1714] VALUES (1867,'محمد بن علي بن مخلد بن مزيد بن محرز أبو جعفر الفرقدي الداركي توفي سنة سبع وثلاثمائة، سكن قرية دارك، ثقة، آخر من ختم به حديث إسماعيل بن عمرو البجلي','4771','1361','0');
INSERT INTO [t1714] VALUES (1868,'محمد بن موسى بن أحمد بن الزبرقان أبو بكر','4775','1361','0');
INSERT INTO [t1714] VALUES (1869,'محمد بن مسلم بن ميمون بن راشد يروي عن القعنبي، روى عنه أبو حامد','4777','1361','0');
INSERT INTO [t1714] VALUES (1870,'محمد بن مهران بن كوشاذ الأصبهاني سكن سامرة، يروي عن إبراهيم الهروي، حدث عنه عبد الباقي بن قانع','4779','1361','0');
INSERT INTO [t1714] VALUES (1871,'محمد بن منده بن منصور أبو جعفر الأصبهاني حدث ببغداد، وبالري، فيه ضعف، حدث عن الحسين بن حفص، عن شعبة، ويونس بن أبي إسحاق، ولا تعرف للحسن عنهما رواية','4780','1361','0');
INSERT INTO [t1714] VALUES (1872,'محمد بن هارون بن هاشم الأصبهاني حدث عنه محمد بن النضر الجارودي النيسابوري وأثنى عليه، وقال: كان صاحب حديث','4781','1361','0');
INSERT INTO [t1714] VALUES (1873,'محمد بن يعقوب بن شبيب الرازي','4783','1361','0');
INSERT INTO [t1714] VALUES (1874,'محمد بن يعقوب بن موسى الضراب','4785','1361','0');
INSERT INTO [t1714] VALUES (1875,'محمد بن يحيى الباهلي البصري قدم أصبهان','4787','1361','0');
INSERT INTO [t1714] VALUES (1876,'محمد بن يوسف بن محمد الجلاب أبو عبد الله روى عن أحمد بن مهران، ومحمد بن مسلمة، وعبيد بن شريك، والكديمي','4789','1361','0');
INSERT INTO [t1714] VALUES (1877,'محمد بن يحيى بن نجيح المكي قدم أصبهان، يروي عن هشيم، وابن عيينة، وفضيل بن عياض، حدث عنه أبو مسعود الرازي، وعبيد الغزال','4791','1361','0');
INSERT INTO [t1714] VALUES (1878,'محمد بن إسحاق بن محمد بن يحيى بن منده أبو عبد الله توفي سلخ ذي القعدة سنة خمس وتسعين وثلاثمائة، حافظ من أولاد المحدثين، كتب بالشام، ومصر، وخراسان، واختلط في آخر عمره، فحدث عن أبي أسيد، وابن أخي أبي زرعة، وابن الجارود بعد أن سمع منه أن له عنهم','4792','1361','0');
INSERT INTO [t1714] VALUES (1879,'محمد بن عبد الله بن محمد الحنظلي أبو الحسين المعداني، يروي عن أبي عمرو بن حكيم، وغيره','4793','1361','0');
INSERT INTO [t1714] VALUES (1880,'محمد بن عبد الله بن بهرام والد أبي حاتم بن ممجة المقرئ، روى عنه ابنه محمد بن عبد الله أبو عمر الحداد، يروي عن المصاحفي وغيره','4794','1361','0');
INSERT INTO [t1714] VALUES (1881,'محمد بن موسى بن مردويه أبو عبد الله إمام في الفقه والأصول، سمع الكثير من الحديث بالعراق وأصبهان، سمع من أبي الحسن أحمد بن محمد بن عمر اللنباني، وأبي عمرو بن حكيم وطبقتهما، يخرج عليه جماعة في الفقه، مضى رحمة الله عليه حميدا سديدا، توفي سنة ثمان','4795','1361','0');
INSERT INTO [t1714] VALUES (1882,'محمد بن أحمد بن الحسين بن أحمد بن زنجويه أبو بكر المعدل جاور بمكة سنين مع خاله عبد الله بن أبي بكر بن زيدة المعدل، وسمع بمكة والبصرة، كان من الدين والفضل والعقل بمحل، توفي غرة شوال سنة أربع وأربعمائة','4796','1361','0');
INSERT INTO [t1714] VALUES (1883,'محمد بن عبد الله بن أحمد أبو مسعود أخي رحمه الله، سمع من عبد الله بن جعفر بن أحمد بن فارس، ومن بعده من المحدثين بأصبهان، توفي سنة خمس وتسعين وثلاثمائة','4797','1361','0');
INSERT INTO [t1714] VALUES (1884,'محمد بن عبد الله بن محمد بن أيوب القماط أبو بكر المتطيب توفي في المحرم سنة ثمان وأربعمائة، سمع معنا من عبد الله بن الحسن بن بندار، وأبي عمر الهيساني','4798','1361','0');
INSERT INTO [t1714] VALUES (1885,'محمد بن الحسن بن أبي الذبال الأصبهاني نزيل دمشق','4799','1361','0');
INSERT INTO [t1714] VALUES (1886,'محمد بن يحيى بن محمد أبو بكر المصري قدم علينا سنة أربع وثمانين، وكان رفيق ابن منده بالشام ومصر','4801','1361','0');
INSERT INTO [t1714] VALUES (1887,'محمد بن أحمد بن شذرة الخطيب أبو عبد الله روى عن أبي صالح الجلاب المديني صاحب أبي مسعود، كان له العبادة الكثيرة، ملازم لبيته، مقبل على عبادته','4803','1361','0');
INSERT INTO [t1714] VALUES (1888,'محمد بن عبد الرحمن بن الفضل بن محمد بن الفضل بن شاذويه أبو عبد الله المديني سمع من عبد الرحمن بن محمد بن الجارود الرقي، ومن جده الفضل','4804','1361','0');
INSERT INTO [t1714] VALUES (1889,'محمد بن علي بن أحمد الفقيه أبو بكر الأشناني الخطيب، الفقيه، الأديب، سمع بالبصرة، من ابن داسة كتب أبي داود السجستاني، ومن الصفار وطبقته الكثير، وبأصبهان من الخشاب وطبقته، ورحل ثانيا إلى العراق وسمع معنا من أبي بكر بن خلاد، وصحبناه وكل، له الفضل','4805','1361','0');
INSERT INTO [t1714] VALUES (1890,'محمد بن علي بن عمرو النقاش المكتنى بأبي سعيد توفي الثامن من رمضان سنة أربع عشرة وأربعمائة، رحل إلى العراق رحلتين، ورحل إلى المشرق وأقام بنيسابور مدة مديدة، وجمع وكتب الكثير من سائر الفنون، كتب عن الهجيمي والشافعي وطبقتهما، وحدث الكثير إملاء وقراءة','4806','1361','0');
INSERT INTO [t1714] VALUES (1891,'محمد بن عبد الرحمن بن جعفر الخلقاني المعروف بالمضري من المتعبدين، مقرئ، صحب أبا عثمان بن أبي هريرة، توفي سنة خمس عشرة وأربعمائة ٣','4807','1361','0');
INSERT INTO [t1714] VALUES (1892,'محمد بن عبد الرحمن بن الخصيب الجرواءاني أبو بكر روى عن عبد الله بن جعفر بن أحمد بن فارس','4808','1361','0');
INSERT INTO [t1714] VALUES (1893,'محمد بن عبد الواحد أبو الربيع الإستراباذي الوراق صحبنا ببغداد، وقدم أصبهان وتوفي بها، سمع الشافعي، وابن خلاد، والعراقيين','4809','1361','0');
INSERT INTO [t1714] VALUES (1894,'محمد بن عمر بن علكويه التاجر والد أبي منصور الكسائي، سمع من القاضي أحمد بن موسى بن إسحاق، توفي سنة ثمانين وثلاثمائة','4810','1361','0');
INSERT INTO [t1714] VALUES (1895,'محمد بن عبد الله بن خالد أبي نواس روى عن أبي أسيد، وابن أخي أبي زرعة، وغيرهما','4811','1361','0');
INSERT INTO [t1714] VALUES (1896,'محمد بن أحمد بن الحسن الطاهري البغدادي قدم أصبهان، سكن المدينة، روى عن موسى بن محمد الموصلي','4812','1361','0');
INSERT INTO [t1714] VALUES (1897,'محمد بن جعفر بن محمد بن بديل الخزاعي أبو الفضل المقرئ قدم سنة ثمان وثمانين وثلاثمائة، أحد من يحفظ القراءات','4813','1361','0');
INSERT INTO [t1714] VALUES (1898,'محمد بن أحمد بن عمر الطهراني المؤدب أبو جعفر كتب ببغداد عن ابن السماك، والنجاد، والنقاش','4814','1361','0');
INSERT INTO [t1714] VALUES (1899,'محمد بن أحمد بن محمد بن عبد الله أبو بكر البيضاوي قدم علينا، من أهل القرآن والنسك','4816','1361','0');
INSERT INTO [t1714] VALUES (1900,'محمد بن أحمد بن محمد بن عبد الله بن دليل أبو بكر سمع من أبي عمرو بن حكيم، وأبي علي الصحاف، والمظالمي','4818','1361','0');
INSERT INTO [t1714] VALUES (1901,'محمد بن عبد الرحمن بن أحمد الجيراني أبو بكر حدث عن ابن محمويه العسكري','4819','1361','0');
INSERT INTO [t1714] VALUES (1902,'محمد بن عبد العزيز بن محمد أبو منصور الخبيري الطبيب سمع من الخشاب، وعبد الله بن جعفر','4820','1361','0');
INSERT INTO [t1714] VALUES (1903,'محمد بن المغيرة البواني أبو بكر كان من المتعبدين، سمع من المظالمي، والخشاب','4821','1361','0');
INSERT INTO [t1714] VALUES (1904,'محمد بن الحسين بن يوسف بن زريق البغدادي أبو بكر قدم أصبهان، من الكتبة، كتب بالعراق وخراسان','4822','1361','0');
INSERT INTO [t1714] VALUES (1905,'محمد بن أحمد بن شيرويه أبو مسلم الدورقي سمع الكثير من ابن أخي أبي زرعة','4823','1361','0');
INSERT INTO [t1714] VALUES (1906,'محمد بن أحمد بن عبد الرحمن بن محمد بن عمر بن عبد الله بن الحسن بن حفص الذكواني أبو بكر المعدل المحدث، ولد سنة ثلاث وثلاثين وثلاثمائة، وتوفي في غرة شعبان من سنة تسع عشرة وأربعمائة، شهد وحدث ستين سنة، روى عن عبد الله بن جعفر بن أحمد، وأبي عبد الله بن','4824','1361','0');
INSERT INTO [t1714] VALUES (1907,'من اسمه موسى','4825','1361','0');
INSERT INTO [t1714] VALUES (1908,'موسى بن المساور بن موسى بن المساور الضبي أبو الهيثم روى عن سفيان بن عيينة، وعبد الله بن معاذ، ووكيع، وعبد المجيد بن أبي رواد، وعصام بن يزيد جبر، والناس، كان أبوه يتولى للسلطان فزهد في تركته تورعا وتركها لإخوته، وله الآثار المشهورة من الرباطات،','4826','1361','0');
INSERT INTO [t1714] VALUES (1909,'موسى بن عبد الرحمن بن مهدي قدم أصبهان، يروي عن أبيه وعن يحيى بن سعيد القطان، وروح، وحماد بن مسعدة، وموسى بن عبد الرحمن بن خالد الخزاز أبي عبد الرحمن، كان من النساك، روى عن المقرئ، ومسدد، وابن أبي شيبة','4828','1361','0');
INSERT INTO [t1714] VALUES (1910,'موسى بن داود بن سابور الأصبهاني يروي عنه أبو حامد الملحمي، وعنه الحسن بن إسحاق بن إبراهيم','4831','1361','0');
INSERT INTO [t1714] VALUES (1911,'موسى بن عبد الرحمن بن خالد الخزاز أبو عبد الرحمن يعرف بالسراج روى عنه أبو حامد','4833','1361','0');
INSERT INTO [t1714] VALUES (1912,'موسى بن خازم بن سيار الأصبهاني أبو عمران يروي عن محمد بن بكير الحضرمي، وحاتم بن عبيد الله، توفي سنة أربع وتسعين','4835','1361','0');
INSERT INTO [t1714] VALUES (1913,'موسى بن هارون بن سعيد أبو عمران يعرف بالأصم يروي عن سويد بن سعيد، وحامد بن يحيى البلخي، وأبي خيثمة، ومحمد بن بكار، صاحب أصول','4838','1361','0');
INSERT INTO [t1714] VALUES (1914,'موسى بن إبراهيم بن ببويه الصوفي أبو عمران كان من الملازمين للجامع، يجتمع إليه الناس ويعظهم','4841','1361','0');
INSERT INTO [t1714] VALUES (1915,'موسى بن الحسن بن رهام','4844','1361','0');
INSERT INTO [t1714] VALUES (1916,'موسى بن عباد الصوفي أبو عمران حدث عن ابن حساب، ذكره المتأخر','4846','1361','0');
INSERT INTO [t1714] VALUES (1917,'موسى بن عبد الله أبو عمران الطرسوسي صحب أبا يوسف الغسولي، روى عنه أحمد بن علي بن الجارود، وعبد الله بن محمد بن زكرياء','4848','1361','0');
INSERT INTO [t1714] VALUES (1918,'موسى بن محمد المؤدب أبو الهيثم الصائغ المديني يروي عن محمود بن أحمد بن الفرج، وإسماعيل بن أحمد، ومحمد بن يحيى بن منده','4850','1361','0');
