INSERT INTO [t17368] VALUES (110,'المبحث الثالث متى يعذر الشاك في كفر الكافر حتى تقام عليه الحجة، وتبين له الأدلة؟!','382','107','0');
INSERT INTO [t17368] VALUES (111,'المبحث الرابع عدم تكفير المشركين، أو الشك في كفرهم، يوجب الجهاد لأهله','386','107','0');
INSERT INTO [t17368] VALUES (112,'الفصل الثالث العذر بالجهل','388','101','0');
INSERT INTO [t17368] VALUES (113,'المبحث الأول الزمان، زمان فترة','389','112','0');
INSERT INTO [t17368] VALUES (114,'المبحث الثاني حكم من مات في الفترة مشركا ولم تقم عليه حجة البلاغ','390','112','0');
INSERT INTO [t17368] VALUES (115,'المبحث الثالث من عبد غير الله فليس بمسلم، ولو كان جاهلا ولم تقم عليه حجة البلاغ، مع استعراض لأحكام الناس قبل ظهور دعوة الشيخ في الدارين','393','112','0');
INSERT INTO [t17368] VALUES (116,'المبحث الرابع مقتطفات من رسالتي لإمامين من أئمة الدعوة في حكم العذر بالجهل','401','112','0');
INSERT INTO [t17368] VALUES (117,'المبحث الخامس الأدلة الصحيحة الصريحة الدالة على ثبوت وصف الشرك وحكمه لمن وقع في عبادة غير الله، وإن كان جاهلا أو متأولا ولم تقم عليه حجة البلاغ','423','112','0');
INSERT INTO [t17368] VALUES (118,'المبحث السادس حرمة الشرك الأكبر وفاقية التحريم، وإجماعية المنع والتأثيم، ولا توجد شبهة سائغة عليها تدرأ حكم الكفر عن أصحابها، ومن ثم انتفى العذر بالجهل فيها','429','112','0');
INSERT INTO [t17368] VALUES (119,'المبحث السابع العذر بالخطأ في الشرك الأكبر، يلزم منه عدم تكفير طوائف من الكفار والزنادقة، قد أجمعت الأمة على كفرها، وكفر من شك في كفرها، مع بيان أن الحكم بإسلام المشركين الجاهلين ليس عليه دليل إلا مجرد الدعوى المجردة','433','112','0');
INSERT INTO [t17368] VALUES (120,'الفصل الرابع العلاقة بين إقامة الحجة، والكفر، وأحكامه','443','101','0');
INSERT INTO [t17368] VALUES (121,'المبحث الأول عبادة الله وحده لا شريك له، الحجة عليها بلوغ القرآن مع بيان أصناف الذين لا يكفرون حتى تقام عليهم الحجة وكذا الفرق بين قيام الحجة وفهمها','444','120','0');
INSERT INTO [t17368] VALUES (122,'المبحث الثاني العقوبة والمؤاخذة لا تكون إلا بعد إقامة الحجة ويكفي في إقامتها مجرد بيان التوحيد بدليله','448','120','0');
INSERT INTO [t17368] VALUES (123,'الفصل الخامس أنواع الكفر وحكم تكفير المعين','450','101','0');
INSERT INTO [t17368] VALUES (124,'المبحث الأول أنواع الكفر والنفاق الأكبر والأصغر','451','123','0');
INSERT INTO [t17368] VALUES (125,'المبحث الثاني متي يصح التكفير وما هي مقومات الحكم به ومتى لا يصح، مع بيان أن المكفر يدور أمره بين الثواب، والعفو، والإثم الشديد','453','123','0');
INSERT INTO [t17368] VALUES (126,'المبحث الثالث المشرك الجاهل، الذي لم تقم عليه حجة البلاغ لا يكون مسلما، ولو نطق بالشهادتين، واستقبل القبلة، وقام ببعض الفرائض، إلا أنه لا يعين بالكفر المستلزم للعقوبة إلا بعد إقامة الحجة','456','123','0');
INSERT INTO [t17368] VALUES (127,'المبحث الرابع فهم وتأويل أئمة الدعوة لموقف الشيخين ابن تيمية ومحمد بن عبد الوهاب من عدم تكفيرهما للمعين ابتداء، حتى تقام عليه الحجة','465','123','0');
INSERT INTO [t17368] VALUES (128,'المبحث الخامس تكفير لمعينين صادر من أئمة الدعوة رحمهم الله تعالى','469','123','0');
INSERT INTO [t17368] VALUES (129,'الفصل السادس أحكام الديار','475','101','0');
INSERT INTO [t17368] VALUES (130,'المبحث الأول تعريف دار الإسلام، ودار الشرك','476','129','0');
INSERT INTO [t17368] VALUES (131,'المبحث الثاني صفة الدار التي تجب الهجرة منها إلا لمن قدر على إظهار دينه مع تحرير المراد والمتعين من إظهار الدين','478','129','0');
INSERT INTO [t17368] VALUES (132,'المبحث الثالث الأدلة الدالة على وجوب التباعد عن المشركين والرد على محاولة إسقاط فرض الهجرة من ديار المشركين','487','129','0');
INSERT INTO [t17368] VALUES (133,'المبحث الرابع أحكام وأحوال المقيم بين أظهر المشركين','493','129','0');
INSERT INTO [t17368] VALUES (134,'الفصل السابع أحكام القتال ومشروعية الجهاد','496','101','0');
INSERT INTO [t17368] VALUES (135,'المبحث الأول القتال في الإسلام منوط بالإصرار على فعل الشرك ولا ترتفع السيوف المجردة عليه، حتى تتم البراءة منه إلى توحيد الله الخالص','497','134','0');
INSERT INTO [t17368] VALUES (136,'المبحث الثاني مجرد الاعتصام بالإسلام مع عدم الالتزام بشرائعه ليس بمسقط للقتال، بل ويقاتل أهله قتال كفر وردة حتى يكون الدين كله لله','502','134','0');
INSERT INTO [t17368] VALUES (137,'المبحث الثالث بعض الأسباب الموجبة لقتال أهلها','507','134','0');
INSERT INTO [t17368] VALUES (138,'المبحث الرابع المراد من إنكار النبي - صلى الله عليه وسلم - على أحد أصحابه لقتله مشركا بعد ما تلفظ بالشهادتين','511','134','0');
INSERT INTO [t17368] VALUES (139,'المبحث الخامس الجهاد لا يسقط بحال عن الأمة والقيام به شرط في صحة الإمامة','513','134','0');
INSERT INTO [t17368] VALUES (140,'الفصل الثامن نواقض الإسلام وأحكام الردة والمرتدين','516','101','0');
INSERT INTO [t17368] VALUES (141,'المبحث الأول تعريف الردة، وأهمية الإحاطة برءوس مسائلها','517','140','0');
INSERT INTO [t17368] VALUES (142,'المبحث الثاني متعلقات الردة، مع ذكر أهم أحكامهما','522','140','0');
