INSERT INTO [t17368] VALUES (144,'المبحث الأول الرد على شبهة التنقص بمقام الرب سبحانه','530','143','0');
INSERT INTO [t17368] VALUES (145,'المبحث الثاني الرد على شبهة التنقص بحق النبي - صلى الله عليه وسلم - والازدراء بمقام الرسالة','533','143','0');
INSERT INTO [t17368] VALUES (146,'المبحث الثالث الرد على شبهة تكفير المسلمين واعتزالهم','536','143','0');
