INSERT INTO [t17417] VALUES (1,'مسألة: ويجب صيام رمضان على كل مسلم بالغ عاقل قادر على الصوم, ويؤمر به الصبي إذا أطاقه','4','0','0');
INSERT INTO [t17417] VALUES (2,'* فصل: ويستحب لمن رأى الهلال -هلال رمضان أو غيره- أن يدعو','15','1','0');
INSERT INTO [t17417] VALUES (3,'* مسألة: فإن نوى الصوم وجن في بعض اليوم','23','1','0');
INSERT INTO [t17417] VALUES (4,'* فصل: فأما من زال عقله بغير جنون','24','1','0');
INSERT INTO [t17417] VALUES (5,'* فصل: فإن صار من أهل الوجوب في أثاء النهار','30','1','0');
INSERT INTO [t17417] VALUES (6,'* فصل: فأما من يجب عليه القضاء إذا زال عذره في أثناء اليوم','35','1','0');
INSERT INTO [t17417] VALUES (7,'* فصل: فأما إذا وجد سبب الفطر في أثناء النهار','40','1','0');
INSERT INTO [t17417] VALUES (8,'مسألة: ويجب بأحد ثلاثة أشياء: كمال شعبان, ورؤية هلال رمضان, ووجود غيم أو قتر ليلة الثلاثين يحول دونه','53','0','0');
INSERT INTO [t17417] VALUES (9,'* فصل: وإذا أوجبنا صومه','105','8','0');
INSERT INTO [t17417] VALUES (10,'مسألة: وإذا رأى الهلال وحده؛ صام','109','0','0');
INSERT INTO [t17417] VALUES (11,'مسألة: فإن كان عدلا صام الناس بقوله','113','0','0');
INSERT INTO [t17417] VALUES (12,'* فصل: ويقبل فيه شهادة الواحد','123','11','0');
INSERT INTO [t17417] VALUES (13,'مسألة: ولا يفطر إلا بشهادة عدلين','124','0','0');
INSERT INTO [t17417] VALUES (14,'مسألة: (وإذا صاموا بشهادة اثنين وثلاثين يوما؛ أفطروا, وإن كان بغيم أو قول واحد؛ لم يفطروا؛ إلا أن يروه أو يكملوا العدة)','128','0','0');
INSERT INTO [t17417] VALUES (15,'* فصل: وإذا شهد بالرؤية واحد أو اثنان أو أكثر من ذلك عند بعض الناس ولم يثبت عند الإمام','136','14','0');
INSERT INTO [t17417] VALUES (16,'مسألة: وإذا اشتبهت الأشهر على الأسير؛ تحرى وصام؛ فإن وافق الشهر أو بعده أجزأه, وإن وافق قبله لم يجزئه','137','0','0');
INSERT INTO [t17417] VALUES (17,'* فصل: وإذا رأى الهلال بعد زوال الشمس فهو لليلة المقبلة','139','16','0');
INSERT INTO [t17417] VALUES (18,'* فصل: وإذا رأى الهلال أهل بلد؛ لزم سائر البلدان الصوم, وإن لم يروه','148','16','0');
INSERT INTO [t17417] VALUES (19,'* فصل: ولا يصح الصوم إلا بنية كسائر العبادات','153','16','0');
INSERT INTO [t17417] VALUES (20,'* فصل','171','16','0');
INSERT INTO [t17417] VALUES (21,'* فصل: وتصح النية في جميع ليلة الصوم','173','16','0');
INSERT INTO [t17417] VALUES (22,'* فصل: وهل يشترط أن ينوي نية الفريضة؟','175','16','0');
INSERT INTO [t17417] VALUES (23,'* فصل: ولا يجزئ الواجب من الكفارة والقضاء والنذر المطلق إلا بتعيين النية','177','16','0');
INSERT INTO [t17417] VALUES (24,'والنذر المعين','178','16','0');
INSERT INTO [t17417] VALUES (25,'باب في أحكام المفطرين في رمضان','185','0','0');
INSERT INTO [t17417] VALUES (26,'مسألة: ويباح الفطر في رمضان لأربعة أقسام: أحدها: المريض الذي يتضرر به والمسافر الذي له الفطر؛ فالفطر لهما أفضل, وعليهما القضاء, وإن صاما أجزأهما','185','25','0');
INSERT INTO [t17417] VALUES (27,'مسألة: والثاني: الحائض والنفساء يفطران ويقضيان, وإن صامتا لم يجزئهما','222','25','0');
INSERT INTO [t17417] VALUES (28,'مسألة: والثالث: الحامل والمرضع إذا خافتا على ولديهما؛ أفطرتا وقضتا وأطعمتا','222','25','0');
INSERT INTO [t17417] VALUES (29,'عن كل يوم مسكينا, وإن صامتا؛ أجزأهما','223','25','0');
INSERT INTO [t17417] VALUES (30,'مسألة: الرابع: العجز عن الصوم لكبر أو مرض لا يرجى برؤه؛ فإنه يطعم عنه لكل يوم مسكين','232','25','0');
INSERT INTO [t17417] VALUES (31,'* فصل: ولو أحاط العدو ببلد, وكان الصوم المفروض يضعفهم؛ فهل يجوز لهم الفطر؟','232','30','0');
INSERT INTO [t17417] VALUES (32,'* فصل: وإن قوي الشيخ أو العجوز بعد ذلك على القضاء, أو عوفي المريض الميؤوس من برئه, بأن زال عطاشه وزال شبقه ونحو ذلك بعد إخراج الفدية','244','30','0');
INSERT INTO [t17417] VALUES (33,'مسألة: وعلى سائر من أفطر القضاء لا غير؛ إلا من أفطر بجماع في الفرج؛ فإنه يقضي ويعتق رقبة؛ فإن لم يجد؛ فصيام شهرين متتابعين؛ فإن لم يستطع؛ فإطعام ستين مسكينا؛ فإن لم يجد؛ سقطت عنه','245','25','0');
INSERT INTO [t17417] VALUES (34,'* فصل: فإن عجز عن الكفارات الثلاثة','274','33','0');
INSERT INTO [t17417] VALUES (35,'* فصل: ويجب العتق إذا وجد الرقبة أو ثمنها فاضلا عن حوائجه الأصلية','278','33','0');
INSERT INTO [t17417] VALUES (36,'* فصل: ولا تجب الكفارة إلا في شهر رمضان','287','33','0');
INSERT INTO [t17417] VALUES (37,'مسألة: فإن جامع ولم يكفر حتى جامع ثانية؛ فكفارة واحدة. وإن كفر ثم جامع؛ فكفارة ثانية','287','25','0');
INSERT INTO [t17417] VALUES (38,'وكل من لزمه الإمساك في رمضان, فجامع؛ فعليه كفارة','288','25','0');
INSERT INTO [t17417] VALUES (39,'* فصل: ولا فرق في الجماع بين المعذور وغير المعذور','291','38','0');
INSERT INTO [t17417] VALUES (40,'* فصل: وأما المرأة؛ فلا تخلو: إما أن تكون مطاوعة, أو مستكرهة','299','38','0');
INSERT INTO [t17417] VALUES (41,'* فصل: وإن كانت مستكرهة','309','38','0');
INSERT INTO [t17417] VALUES (42,'* فصل: إذا جامع ونزع قبل الفجر, ثم أمنى بذلك بعد طلوع الفجر','314','38','0');
INSERT INTO [t17417] VALUES (43,'* فصل: ولو احتلم الصائم في النهار في المنام','318','38','0');
INSERT INTO [t17417] VALUES (44,'مسألة: ومن أخر القضاء لعذر حتى أدركه رمضان آخر؛ فليس عليه غيره, وإن فرط؛ أطعم مع القضاء لكل يوم مسكينا','319','25','0');
INSERT INTO [t17417] VALUES (45,'* مسألة: وأما إذا أخر القضاء لعذر','332','44','0');
INSERT INTO [t17417] VALUES (46,'* فصل: فإن كان قد أمكنه قضاء بعض ما فاته دون بعض','333','44','0');
INSERT INTO [t17417] VALUES (47,'* مسألة: فإن أخره إلى رمضان ثالث','333','44','0');
INSERT INTO [t17417] VALUES (48,'* فصل: ومن عليه قضاء رمضان, لا يجوز أن يصوم تطوعا','333','44','0');
INSERT INTO [t17417] VALUES (49,'مسألة: وإن ترك القضاء حتى مات لعذر؛ فلا شيء عليه, وإن كان لغير عذر؛ أطعم عنه لكل يوم مسكينا؛ إلا أن يكون الصوم منذورا؛ فإنه يصام عنه, وكذلك كل نذر طاعة','339','25','0');
INSERT INTO [t17417] VALUES (50,'* فصل: فإن فرط حتى أدركه رمضان الثاني قبل أن يصوم, ومات في أثناء ذلك الرمضان أو بعده قبل أن يصوم','345','49','0');
INSERT INTO [t17417] VALUES (51,'* فصل: ويصام النذر عنه','352','49','0');
INSERT INTO [t17417] VALUES (52,'* مسألة: وإن نذر الصوم في حال الكبر واليأس من البرء','355','49','0');
INSERT INTO [t17417] VALUES (53,'* فصل: وإذا صام عنه أكثر من واحد في يوم','355','49','0');
INSERT INTO [t17417] VALUES (54,'باب ما يفسد الصوم','360','0','0');
INSERT INTO [t17417] VALUES (55,'مسألة: ومن أكل أو شرب أو استعط أو وصل إلى جوفه شيئا من أي موضع كان أو استقاء أو استمنى أو قبل أو لمس فأمنى أو أمذى أو كرر النظر حتى أنزل أو حجم أو احتجم عامدا ذاكرا لصومه؛ فسد, وإن فعله ناسيا أو مكرها؛ لم يفسد','360','54','0');
INSERT INTO [t17417] VALUES (56,'* فصل: فإن تجوف جوف في فخذه أو يده أو ظهره أو غير ذلك, وليس بينه وبين البطن منفذ, فوضع فيه شيء','372','55','0');
INSERT INTO [t17417] VALUES (57,'* فصل: ويكره للصائم أن يباشر أو يقبل أو ينظر لشهوة','382','55','0');
INSERT INTO [t17417] VALUES (58,'* فصل: ويفطر بالحجامة في جميع البدن','428','55','0');
INSERT INTO [t17417] VALUES (59,'مسألة: وإن طار إلى حلقه ذباب أو غبار, أو مضمض أو استنشق فوصل إلى حلقه ماء, أو فكر فأنزل, أو قطر في إحليله, أو احتلم, أو ذرعه القيء؛ لم يفسد صومه','442','54','0');
INSERT INTO [t17417] VALUES (60,'* فصل: وما يجتمع في فمه من الريق ونحوه إذا ابتلعه','450','59','0');
INSERT INTO [t17417] VALUES (61,'* فصل: وما يوضع في الفم من طعام أو غيره','455','59','0');
INSERT INTO [t17417] VALUES (62,'* فصل','463','59','0');
INSERT INTO [t17417] VALUES (63,'مسألة: ((ومن أكل يظنه ليلا فبان نهارا؛ أفطر))','467','54','0');
INSERT INTO [t17417] VALUES (64,'مسألة: وإن أكل شاكا في طلوع الفجر؛ لم يفسد صومه, وإن أكل شاكا في غروب الشمس؛ فسد صومه','472','54','0');
INSERT INTO [t17417] VALUES (65,'* فصل: الوقت الذي يجب صيامه','474','64','0');
INSERT INTO [t17417] VALUES (66,'* فصل: والسنة تعجيل الفطور','477','64','0');
INSERT INTO [t17417] VALUES (67,'* فصل: والسحور سنة','491','64','0');
INSERT INTO [t17417] VALUES (68,'* فصل: ويكره الوصال','511','64','0');
INSERT INTO [t17417] VALUES (69,'* فصل: فإن أكل أو شرب ما يرويه وإن قل؛ خرج عن حكم النهي','515','64','0');
INSERT INTO [t17417] VALUES (70,'* فصل: وما كان مكروها أو محرما من الأقوال والأعمال في غير زمن الصوم؛ ففيه أشد تحريما وكراهة','517','64','0');
INSERT INTO [t17417] VALUES (71,'باب صيام التطوع','522','0','0');
INSERT INTO [t17417] VALUES (72,'مسألة: أفضل الصيام صيام داوود عليه السلام, كان يصوم يوما ويفطر يوما','522','71','0');
INSERT INTO [t17417] VALUES (73,'مسألة: وأفضل الصيام بعد شهر رمضان شهر الله المحرم','524','71','0');
INSERT INTO [t17417] VALUES (74,'* فصل: وجاء في صوم الأشهر الحرم مطلقا','525','73','0');
INSERT INTO [t17417] VALUES (75,'* فصل: ويكره إفراد رجب بالصوم','527','73','0');
INSERT INTO [t17417] VALUES (76,'مسألة: وما من أيام العمل الصالح فيهن أحب إلى الله عز وجل من عشر ذي الحجة','530','71','0');
INSERT INTO [t17417] VALUES (77,'مسألة: ومن صام رمضان وأتبعه بست من شوال؛ فكأنما صام الدهر','533','71','0');
INSERT INTO [t17417] VALUES (78,'مسألة: وصوم عاشوراء كفارة سنة, وعرفة كفارة سنتين','537','71','0');
INSERT INTO [t17417] VALUES (79,'فصل: ولا يستحب صومه لمن بعرفة','539','78','0');
INSERT INTO [t17417] VALUES (80,'* فصل: وأما صوم يوم عاشوراء','546','78','0');
INSERT INTO [t17417] VALUES (81,'* فصل: وعاشوراء هو اليوم العاشر من المحرم','556','78','0');
INSERT INTO [t17417] VALUES (82,'والسنة لمن صامه أن يصوم تاسوعا معه','557','78','0');
INSERT INTO [t17417] VALUES (83,'* فصل','562','78','0');
INSERT INTO [t17417] VALUES (84,'مسألة: ويستحب صيام أيام البيض','564','71','0');
INSERT INTO [t17417] VALUES (85,'مسألة: والاثنين والخميس','573','71','0');
INSERT INTO [t17417] VALUES (86,'* فصل','577','85','0');
INSERT INTO [t17417] VALUES (87,'مسألة: والصائم المتطوع أمير نفسه, إن شاء صام, وإن شاء أفطر, ولا قضاء عليه','578','71','0');
INSERT INTO [t17417] VALUES (88,'* فصل: في المواضع التي يكره فيها الفطر أو يستحب أو يباح','608','87','0');
INSERT INTO [t17417] VALUES (89,'* فصل: ومن تلبس بصيام رمضان أو بصلاة في أول وقتها أو بقضاء رمضان أو بقضاء الصلاة أو بصوم نذر أو كفارة؛ لزمه المضي فيه','609','87','0');
INSERT INTO [t17417] VALUES (90,'مسألة: وكذلك سائر التطوع؛ إلا الحج والعمرة؛ فإنه يجب إتمامهما وقضاء ما أفسد منهما','609','71','0');
INSERT INTO [t17417] VALUES (91,'مسألة: ونهى رسول الله صلى الله عليه وسلم عن صوم يومين؛ يوم الفطر ويوم الأضحى','615','71','0');
INSERT INTO [t17417] VALUES (92,'مسألة: ونهى عن صوم أيام التشريق إلا أنه أرخص في صومها للمتمتع إذا لم يجد الهدي','617','71','0');
INSERT INTO [t17417] VALUES (93,'* فصل: وأما المتمتع إذا لم يجد الهدي, ولم يصم الأيام الثلاثة قبل يوم النحر؛ فهل يصوم أيام التشريق؟','620','92','0');
INSERT INTO [t17417] VALUES (94,'* فصل: ويكره صوم يوم الشك في حال الصحو','621','92','0');
INSERT INTO [t17417] VALUES (95,'* فصل: ويكره استقبال رمضان باليوم واليومين','624','92','0');
INSERT INTO [t17417] VALUES (96,'* فصل: ويكره إفراد يوم الجمعة بالصوم','627','92','0');
INSERT INTO [t17417] VALUES (97,'* فصل: ويكره إفراد يوم السبت بالصيام','630','92','0');
INSERT INTO [t17417] VALUES (98,'* فصل','643','92','0');
INSERT INTO [t17417] VALUES (99,'مسألة: وليلة القدر في الوتر من العشر الأواخر من رمضان','644','71','0');
INSERT INTO [t17417] VALUES (100,'* فصل: وعلامتها','674','99','0');
INSERT INTO [t17417] VALUES (101,'* فصل: ويستحب الاجتهاد في العشر مطلقا','676','99','0');
INSERT INTO [t17417] VALUES (102,'باب الاعتكاف','682','0','0');
INSERT INTO [t17417] VALUES (103,'مسألة: وهو لزوم مسجد لطاعة الله فيه','682','102','0');
INSERT INTO [t17417] VALUES (104,'مسألة: وهو سنة لا يجب إلا بالنذر','686','102','0');
INSERT INTO [t17417] VALUES (105,'* فصل: ولا يصح الاعتكاف إلا من مسلم عاقل','695','104','0');
INSERT INTO [t17417] VALUES (106,'مسألة: ويصح من المرأة في كل مسجد, ولا يصح من الرجل إلا في مسجد تقام فيه الجماعة, واعتكافه في مسجد تقام فيه الجمعة أفضل','697','102','0');
INSERT INTO [t17417] VALUES (107,'مسألة: ومن نذر الاعتكاف أو الصلاة في مسجد؛ فله فعل ذلك في غيره؛ إلا المساجد الثلاثة, فإذا نذر ذلك في المسجد الحرام؛ لزمه, وإذا نذره في مسجد المدينة؛ فله فعله في المسجد الحرام وحده, وإن نذره في المسجد الأقصى؛ فله فعله فيهما','744','102','0');
INSERT INTO [t17417] VALUES (108,'* فصل: وإذا نذر المشي إلى بيت المقدس أو إلى مسجد النبي صلى الله عليه وسلم','753','107','0');
INSERT INTO [t17417] VALUES (109,'* فصل: فأما إن نذر الصوم بمكان بعينه','753','107','0');
INSERT INTO [t17417] VALUES (110,'* فصل: فأما الأزمنة','754','107','0');
INSERT INTO [t17417] VALUES (111,'* فصل: وإذا أراد أن يعتكف العشر الأواخر أو شهر رمضان ونحو ذلك؛ فإنه يدخل معتكفه قبل غروب الشمس من أول ليلة','754','107','0');
INSERT INTO [t17417] VALUES (112,'* فصل: ومن نذر اعتكاف ليلة؛ لم يلزمه يومها. وإن نذر اعتكاف يوم؛ لم تلزمه ليلته','757','107','0');
INSERT INTO [t17417] VALUES (113,'* فصل: وإذا نذر اعتكاف شهر مطلق','760','107','0');
INSERT INTO [t17417] VALUES (114,'* فصل: وإذا نذر اعتكاف يوم يقدم فلان','763','107','0');
INSERT INTO [t17417] VALUES (115,'مسألة: ويستحب للمعتكف الاشتغال بالقرب واجتناب ما لا يعنيه من قول أو فعل','764','102','0');
INSERT INTO [t17417] VALUES (116,'* فصل','766','115','0');
INSERT INTO [t17417] VALUES (117,'* فصل','774','115','0');
INSERT INTO [t17417] VALUES (118,'* فصل: ويجوز للمعتكف أن يغسل رأسه ويرجله حال الاعتكاف','775','115','0');
INSERT INTO [t17417] VALUES (119,'مسألة: ولا يخرج من المسجد إلا لما لا بد له منه؛ إلا أن يشترط','777','102','0');
INSERT INTO [t17417] VALUES (120,'* فصل: وأما عيادة المريض وشهود الجنازة','781','119','0');
INSERT INTO [t17417] VALUES (121,'* فصل','786','119','0');
INSERT INTO [t17417] VALUES (122,'* فصل: فإن قال: علي أن أعتكف شهر رمضان إن لم أكن مريضا أو مسافرا','789','119','0');
INSERT INTO [t17417] VALUES (123,'مسألة: ولا يباشر امرأة','789','102','0');
INSERT INTO [t17417] VALUES (124,'* فصل: وإذا أبطل اعتكافا لزمه قضاؤه؛ فهل عليه كفارة؟','792','123','0');
INSERT INTO [t17417] VALUES (125,'* فصل: ويبطل الاعتكاف أيضا بالردة','797','123','0');
INSERT INTO [t17417] VALUES (126,'* فصل: وإذا ترك الاعتكاف بالخروج من المعتكف','798','123','0');
INSERT INTO [t17417] VALUES (127,'* فصل','801','123','0');
INSERT INTO [t17417] VALUES (128,'* فصل: فإن كان الاعتكاف الذي أفسده تطوعا','802','123','0');
INSERT INTO [t17417] VALUES (129,'مسألة: وأن يسأل عن المريض أو غيره في طريقه ولم يعرج عليه','804','102','0');
INSERT INTO [t17417] VALUES (130,'* فصل: في تفصيل الأسباب المبيحة للخروج وأحكامها','804','129','0');
INSERT INTO [t17417] VALUES (131,'* فصل: وأما خروجه للجمعة','808','129','0');
INSERT INTO [t17417] VALUES (132,'* فصل: وإذا جوزنا له الخروج لعيادة المريض وتشييع الجنازة بغير شرط أو كان قد اشترطه؛ فإنه لا يزيد على المسنون','809','129','0');
INSERT INTO [t17417] VALUES (133,'* فصل','809','129','0');
INSERT INTO [t17417] VALUES (134,'* فصل','810','129','0');
INSERT INTO [t17417] VALUES (135,'* فصل: وأما الأكل','811','129','0');
INSERT INTO [t17417] VALUES (136,'* فصل: وأما إذا تعينت عليه شهادة أو أحضره سلطان','813','129','0');
INSERT INTO [t17417] VALUES (137,'* فصل: وإذا حاضت المرأة أو نفست','814','129','0');
INSERT INTO [t17417] VALUES (138,'* فصل: وإذا وجبت عليها عدة وفاة وهي معتكفة','818','129','0');
INSERT INTO [t17417] VALUES (139,'* فصل: وإذا وقعت فتنة خاف منها على نفسه أو ماله أو أهله','819','129','0');
INSERT INTO [t17417] VALUES (140,'* فصل: وإذا تعين عليه الخروج للجهاد','821','129','0');
INSERT INTO [t17417] VALUES (141,'* فصل: ويستحب لمن اعتكف العشر الأواخر من شهر رمضان أن يبيت ليلة العيد في معتكف','823','129','0');
