INSERT INTO [t17712] VALUES (1,'مقدمة الشارح','2','0','0');
INSERT INTO [t17712] VALUES (2,'المصادر النحوية مرتبة هجائيا','6','0','0');
INSERT INTO [t17712] VALUES (3,'الحصيلة النقدية، لقراءة الشواهد النحوية','11','0','0');
INSERT INTO [t17712] VALUES (4,'تمهيد وارائة الحصيلة','11','3','0');
INSERT INTO [t17712] VALUES (5,'١ - البيت ليس وحدة القصيدة العربية القديمة','12','4','0');
INSERT INTO [t17712] VALUES (6,'٢ - ينحصر زمن شعراء الشواهد بين العصر الجاهلي، ونهاية العصر الأموي','15','4','0');
INSERT INTO [t17712] VALUES (7,'٣ - لا يؤخذ التاريخ من القصص الأدبي.','17','4','0');
INSERT INTO [t17712] VALUES (8,'٤ - الشواهد الشعرية لا تشمل القواعد النحوية كلها','19','4','0');
INSERT INTO [t17712] VALUES (9,'٥ - قسم علماء العربية الأوائل، قواعد النحو، إلى قياسية، يصح القياس عليها والنسج على منوالها لكثرة شواهدها عندهم. وإلى سماعية، أو قليلة، أو شاذة، أو ضرورة، ولا يصح القياس عليها لندرة شواهدها عندهم. وتتابع المؤلفون على هذا التقسيم إلى العصر الحديث.','19','4','0');
INSERT INTO [t17712] VALUES (10,'٦ - الموازنة بين رواية الشعر، ورواية الحديث النبوي','20','4','0');
INSERT INTO [t17712] VALUES (11,'٧ - بين البصريين والكوفيين','25','4','0');
INSERT INTO [t17712] VALUES (12,'عمود الشعر العربي','26','3','0');
INSERT INTO [t17712] VALUES (13,'وأما النظرات النقدية','28','3','0');
INSERT INTO [t17712] VALUES (14,'باب الهمزة','63','0','0');
INSERT INTO [t17712] VALUES (15,'١ - سيغنيني الذي أغناك عني ... فلا فقر يدوم ولا غناء','63','14','0');
INSERT INTO [t17712] VALUES (16,'٢ - بعشرتك الكرام تعد منهم ... فلا ترين لغيرهم الوفاء','63','14','0');
INSERT INTO [t17712] VALUES (17,'٣ - إن من يدخل الكنيسة يوما ... يلق فيها جاذرا وظباء','64','14','0');
INSERT INTO [t17712] VALUES (18,'٤ - لما رأيت أبا يزيد مقاتلا ... أدع القتال وأشهد الهيجاء','64','14','0');
INSERT INTO [t17712] VALUES (19,'٥ - إن هند المليحة الحسناء ... وأي من أظهرت لخل وفاء','64','14','0');
INSERT INTO [t17712] VALUES (20,'٦ - غير أني قد أستعين على اله ... م إذا خف بالثوي النجاء','65','14','0');
INSERT INTO [t17712] VALUES (21,'٧ - ملك أضلع البرية لا يو ... جد فيها لما لديه كفاء','65','14','0');
INSERT INTO [t17712] VALUES (22,'٨ - قلت لشيبان ادن من لقائه ... كما تغدي الناس من شوائه','66','14','0');
INSERT INTO [t17712] VALUES (23,'٩ - من لد شولا فإلى إتلائها.','66','14','0');
INSERT INTO [t17712] VALUES (24,'١٠ - إذا أنا لم أؤمن عليك ولم يكن ... لقاؤك إلا من وراء وراء','67','14','0');
INSERT INTO [t17712] VALUES (25,'١١ - ألم أك جاركم ويكون بيني ... وبينكم المودة والإخاء','67','14','0');
INSERT INTO [t17712] VALUES (26,'١٢ - ليس من مات فاستراح بميت ... إنما الميت ميت الأحياء إنما الميت من يعيش كئيبا ... كاسفا باله قليل الرجاء','68','14','0');
INSERT INTO [t17712] VALUES (27,'١٣ - ربما ضربة بسيف صقيل ... بين بصرى، وطعنة نجلاء','68','14','0');
INSERT INTO [t17712] VALUES (28,'١٤ - ولولا يوم يوم ما أردنا ... جزاءك، والقروض لها جزاء','68','14','0');
INSERT INTO [t17712] VALUES (29,'١٥ - لعلك والموعود حق لقاؤه ... بدا لك في تلك القلوص بداء','69','14','0');
INSERT INTO [t17712] VALUES (30,'١٦ - طلبوا صلحنا ولات أوان ... فأجبنا أن ليس حين بقاء','69','14','0');
INSERT INTO [t17712] VALUES (31,'١٧ - وبلد مغبرة أرجاؤه ... كأن لون أرضه سماؤه','70','14','0');
INSERT INTO [t17712] VALUES (32,'١٨ - إذا كان الشتاء فأدفئوني ... فإن الشيخ يهرمه الشتاء','70','14','0');
INSERT INTO [t17712] VALUES (33,'١٩ - وأعلم إن تسليما وتركا ... للامتشابهان ولا سواء','70','14','0');
INSERT INTO [t17712] VALUES (34,'٢٠ - أو منعتم ما تسألون فمن حد ... ثتموه له علينا الولاء؟','70','14','0');
INSERT INTO [t17712] VALUES (35,'٢١ - لا أقعد الجبن عن الهيجاء ... ولو توالت زمر الأعداء','71','14','0');
INSERT INTO [t17712] VALUES (36,'٢٢ - فجاءت به سبط العظام كأنما ... عمامته بين الرجال لواء','71','14','0');
INSERT INTO [t17712] VALUES (37,'٢٣ - يا لك من تمر ومن شيشاء ... ينشب في المسعل واللهاء','71','14','0');
INSERT INTO [t17712] VALUES (38,'٢٤ - دع عنك لومي فإن اللوم إغراء ... وداوني بالتي كانت هي الداء','72','14','0');
INSERT INTO [t17712] VALUES (39,'٢٥ - لوما الإصاخة للوشاة لكان لي ... من بعد سخطك في رضاك رجاء','72','14','0');
INSERT INTO [t17712] VALUES (40,'٢٦ - أمن يهجو رسول الله منكم ... ويمدحه وينصره سواء','72','14','0');
INSERT INTO [t17712] VALUES (41,'٢٧ - كأن خبيئة من بيت رأس ... يكون مزاجها عسل وماء','72','14','0');
INSERT INTO [t17712] VALUES (42,'٢٨ - وما أدري وسوف إخال أدري ... أقوم آل حصن أم نساء','72','14','0');
INSERT INTO [t17712] VALUES (43,'٢٩ - إن سليمى- والله يكلؤها- ... ضنت بشيء ما كان يرزؤها','73','14','0');
INSERT INTO [t17712] VALUES (44,'٣٠ - ولا أراها تزال ظالمة ... تحدث لي قرحة وتنكؤها','73','14','0');
INSERT INTO [t17712] VALUES (45,'٣١ - نعم الفتاة فتاة هند لو بذلت ... رد التحية نطقا أو بإيماء','73','14','0');
INSERT INTO [t17712] VALUES (46,'٣٢ - غافلا تعرض المنية للمر .. ... ء فيدعى ولات حين نداء','74','14','0');
INSERT INTO [t17712] VALUES (47,'٣٣ - متى يأت هذا الموت لا تبق حاجة ... لنفسي إلا قد قضيت قضاءها','74','14','0');
INSERT INTO [t17712] VALUES (48,'٣٤ - إذا عاش الفتى مئتين عاما ... فقد ذهب المسرة والفتاء','74','14','0');
INSERT INTO [t17712] VALUES (49,'٣٥ - والريح تعبث بالغصون وقد جرى ... ذهب الأصيل على لجين الماء','74','14','0');
INSERT INTO [t17712] VALUES (50,'٣٦ - ليت شعري وأين مني ليت ... إن ليتا وإن لوا عناء','74','14','0');
INSERT INTO [t17712] VALUES (51,'٣٧ - فقلتم تعال يا يزي بن مخرم ... فقلت لكم إني حليف صداء','75','14','0');
INSERT INTO [t17712] VALUES (52,'٣٨ - حشى رهط النبي فإن منهم ... بحورا لا تكدرها الدلاء','75','14','0');
INSERT INTO [t17712] VALUES (53,'٣٩ - وجبريل رسول الله فينا ... وروح القدس ليس له كفاء','75','14','0');
INSERT INTO [t17712] VALUES (54,'٤٠ - فتجمع أيمن منا ومنكم ... بمقسمة تمور بها الدماء','75','14','0');
INSERT INTO [t17712] VALUES (55,'٤١ - فلا والله ما يلفى لما بي ... ولا للما بهم أبدا دواء','76','14','0');
INSERT INTO [t17712] VALUES (56,'٤٢ - كيف نومي على الفراش ولما ... تشمل الشام غارة شعواء تذهل الشيخ عن بنيه وتبدي ... عن خدام العقيلة العذراء','76','14','0');
INSERT INTO [t17712] VALUES (57,'٤٣ - فوا كبدا من حب من لا يحبني ... ومن عبرات ما لهن فناء','77','14','0');
INSERT INTO [t17712] VALUES (58,'٤٤ - أيها الناطق المرقش عنا ... عند عمرو وهل لذاك بقاء لا تخلنا على غراتك إنا ... طالما قد وشى بنا الأعداء فبقينا على الشناءة تنمي ... نا جدود وعزة قعساء','77','14','0');
INSERT INTO [t17712] VALUES (59,'٤٥ - فهم بطانتهم وهم وزراؤهم ... وهم الملوك ومنهم الحكماء','77','14','0');
INSERT INTO [t17712] VALUES (60,'٤٦ - فذاك ولم- إذا نحن امترينا ... تكن في الناس يدركك المراء','78','14','0');
INSERT INTO [t17712] VALUES (61,'٤٧ - بادت وغير آيهن من البلى ... إلا رواكد جمرهن هباء ومشجج أما سواء قذاله ... فبدا وغير ساره المعزاء','78','14','0');
INSERT INTO [t17712] VALUES (62,'٤٨ - هيهات قد سفهت أمية رأيها ... فاستجهلت حلماءها سفهاؤها','78','14','0');
INSERT INTO [t17712] VALUES (63,'٤٩ - فكسوت عار لحمة فتركته ... جدلا يسحب ذيله ورداءه','79','14','0');
INSERT INTO [t17712] VALUES (64,'٥٠ - غافلا تعرض المنية للمر ... ء فيدعى ولات حين إباء','79','14','0');
INSERT INTO [t17712] VALUES (65,'٥١ - إن الذي- وهو مثر- لا يجود، حر ... حر بفاقة تعتريه بعد إثراء','79','14','0');
INSERT INTO [t17712] VALUES (66,'٥٢ - لا يني الخب شيمة الخب ما دا ... م فلا تحسبنه ذا إرعواء','79','14','0');
INSERT INTO [t17712] VALUES (67,'٥٣ - قالوا أخفت فقلت: إن وخيفتي ... ما إن تزال منوطة برجائي','79','14','0');
INSERT INTO [t17712] VALUES (68,'٥٤ - فأوه لذكراها إذا ما ذكرتها ... ومن بعد أرض بيننا وسماء','80','14','0');
INSERT INTO [t17712] VALUES (69,'٥٥ - ما إن رأيت ولا أرى في مدتي ... كجواري يلعبن بالصحراء','80','14','0');
INSERT INTO [t17712] VALUES (70,'٥٦ - ألا أبلغ بني بني ربيع ... فأنذال البنين لكم فداء بأني قد كبرت ودق عظمي ... فلا تشغلكم عني النساء','80','14','0');
INSERT INTO [t17712] VALUES (71,'٥٧ - ألا أيهذا النابح السيد إنني ... على نأيها مستبسل من ورائها','80','14','0');
INSERT INTO [t17712] VALUES (72,'باب الباء','82','0','0');
INSERT INTO [t17712] VALUES (73,'١ - له ملك ينادي كل يوم ... لدوا للموت وابنوا للخراب','82','72','0');
INSERT INTO [t17712] VALUES (74,'٢ - سراة بني أبي بكر تسامى ... على كان المسومة العراب','82','72','0');
INSERT INTO [t17712] VALUES (75,'٣ - إن السيوف غدوها ورواحها ... تركت هوازن مثل قرن الأعضب','82','72','0');
INSERT INTO [t17712] VALUES (76,'٤ - لا أشتهي يا قوم إلا كارها ... باب الأمير ولا دفاع الحاجب','82','72','0');
INSERT INTO [t17712] VALUES (77,'٥ - كأنه وجه تركيين إذ غضبا ... مستهدف لطعان غير تذبيب','83','72','0');
INSERT INTO [t17712] VALUES (78,'٦ - إذا كوكب الخرقاء لاح بسحرة ... سهيل أذاعت غزلها في القرائب','85','72','0');
INSERT INTO [t17712] VALUES (79,'٧ - كأن فعلة لم تملأ مواكبها ... ديار بكر ولم تخلع ولم تهب','85','72','0');
INSERT INTO [t17712] VALUES (80,'٨ - فقام أبو ليلى إليه ابن ظالم ... وكان إذا ما يسلل السيف يضرب','85','72','0');
INSERT INTO [t17712] VALUES (81,'٩ - كم دون مية من خرق ومن علم ... كأنه لامع عريان مسلوب','86','72','0');
INSERT INTO [t17712] VALUES (82,'١٠ - معاذ الإله أن تكون كظبية ... ولا دمية ولا عقيلة ربرب','87','72','0');
INSERT INTO [t17712] VALUES (83,'١١ - لقد علم الحي اليمانون أنني ... إذا قلت أما بعد إني خطيبها','87','72','0');
INSERT INTO [t17712] VALUES (84,'١٢ - ويلمها روحة والريح معصفة ... والغيث مرتجز والليل مقترب','88','72','0');
INSERT INTO [t17712] VALUES (85,'١٣ - فبيناه يشري رحله قال قائل ... لمن جمل رخو الملاط نجيب','88','72','0');
INSERT INTO [t17712] VALUES (86,'١٤ - فقلت انجوا عنها نجا الجلد إنه ... سيرضيكما منها سنام وغاربه','89','72','0');
INSERT INTO [t17712] VALUES (87,'١٥ - فهلا أعدوني لمثلي- تفاقدوا- ... إذا الخصم أبزى مائل الرأس أنكب','89','72','0');
INSERT INTO [t17712] VALUES (88,'١٦ - لنا إبلان فيهما ما علمتم ... فعن أية ما شئتم فتنكبوا','90','72','0');
INSERT INTO [t17712] VALUES (89,'١٧ - ولولا دفاعي عن عفاق ومشهدي ... هوت بعفاق عوض عنقاء مغرب','90','72','0');
INSERT INTO [t17712] VALUES (90,'١٨ - ولو ولدت قفيرة جرو كلب ... لسب بذلك الجرو الكلابا','91','72','0');
INSERT INTO [t17712] VALUES (91,'١٩ - دعني فأذهب جانبا ... يوما وأكفك جانبا','91','72','0');
INSERT INTO [t17712] VALUES (92,'٢٠ - أنشأت أسأله ما بال رفقته ... حي الحمول فإن الركب قد ذهبا','92','72','0');
INSERT INTO [t17712] VALUES (93,'٢١ - لا يمنع الناس مني ما أردت ولا ... أعطيهم ما أرادوا حسن ذا أدبا','92','72','0');
INSERT INTO [t17712] VALUES (94,'٢٢ - فأصبحن لا يسألنه عن بما به ... أصعد في علو الهوى أم تصوبا','93','72','0');
INSERT INTO [t17712] VALUES (95,'٢٣ - فيا لرزام رشحوا بي مقدما ... على الحرب خواضا إليها الكرائبا','93','72','0');
INSERT INTO [t17712] VALUES (96,'٢٤ - سيري أمام فإن الأكثرين حصى ... والأكرمين إذا ما ينسبون أبا قوم هم الأنف والأذناب غيرهم ... ومن يسوي بأنف الناقة الذنبا','94','72','0');
INSERT INTO [t17712] VALUES (97,'٢٥ - فأضحى ولو كانت خراسان دونه ... رآها مكان السوق أو هي أقربا','94','72','0');
INSERT INTO [t17712] VALUES (98,'٢٦ - فذاك وخم لا يبالي السبا ... الحزن بابا والعقور كلبا','95','72','0');
INSERT INTO [t17712] VALUES (99,'٢٧ - بأعين منها مليحات النقب ... شكل التجار وحلال المكتسب','96','72','0');
INSERT INTO [t17712] VALUES (100,'٢٨ - بنا تميما يكشف الضباب','96','72','0');
INSERT INTO [t17712] VALUES (101,'٢٩ - إن لها مركنا إرزبا ... كأنه جبهة ذرى حبا','96','72','0');
INSERT INTO [t17712] VALUES (102,'٣٠ - والله ما ليلي بنام صاحبه ... ولا مخالط الليان جانبه','96','72','0');
INSERT INTO [t17712] VALUES (103,'٣١ - يسر المرء ما ذهب الليالي ... وكان ذهابهن له ذهابا','97','72','0');
INSERT INTO [t17712] VALUES (104,'٣٢ - إذن والله نرميهم بحرب ... تشيب الطفل من قبل المشيب','97','72','0');
INSERT INTO [t17712] VALUES (105,'٣٣ - أضحى يمزق أثوابي ويضربني ... أبعد شيبي يبغي عندي الأدبا؟','97','72','0');
INSERT INTO [t17712] VALUES (106,'٣٤ - ألا ليت الشباب يعود يوما ... فأخبره بما فعل المشيب','97','72','0');
INSERT INTO [t17712] VALUES (107,'٣٥ - لكل عيش قد لبست أثوبا.','98','72','0');
INSERT INTO [t17712] VALUES (108,'٣٦ - ومعتد فظ غليظ القلب ... كأن وريديه رشاء خلب غادرته مجدلا كالكلب','98','72','0');
INSERT INTO [t17712] VALUES (109,'٣٧ - وبعض الأخلاء عند البلا ... ء والرزء أروغ من ثعلب وكيف تواصل من أصبحت ... خلالته كأبي مرحب','98','72','0');
INSERT INTO [t17712] VALUES (110,'٣٨ - ولما أن تحمل آل ليلى ... سمعت ببينهم نعب الغرابا','99','72','0');
INSERT INTO [t17712] VALUES (111,'٣٩ - زعمتني شيخا ولست بشيخ ... إنما الشيخ من يدب دبيبا','99','72','0');
INSERT INTO [t17712] VALUES (112,'٤٠ - القوم في أثري ظننت فإن يكن ... ما قد ظننت فقد ظفرت وخابوا','99','72','0');
INSERT INTO [t17712] VALUES (113,'٤١ - وإنما يرضي المنيب ربه ... ما دام معنيا بذكر قلبه','99','72','0');
INSERT INTO [t17712] VALUES (114,'٤٢ - يبكيك ناء بعيد الدار مغترب ... يا للكهول وللشبان للعجب','100','72','0');
INSERT INTO [t17712] VALUES (115,'٤٣ - وكمتا مدماة كأن متونها ... جرى فوقها واستشعرت لون مذهب','100','72','0');
INSERT INTO [t17712] VALUES (116,'٤٤ - ألا يا اسلمي يا ترب أسماء من ترب ... ألا يا اسلمي حييت عني وعن صحبي','101','72','0');
INSERT INTO [t17712] VALUES (117,'٤٥ - وقالت: ألا يا اسمع نعظك بخطة ... فقلت: سميعا، فانطقي وأصيبي','101','72','0');
INSERT INTO [t17712] VALUES (118,'٤٦ - ألا يا قوم للعجب العجيب ... وللغفلات تعرض للأريب','101','72','0');
INSERT INTO [t17712] VALUES (119,'٤٧ - وما لي إلا آل أحمد شيعة ... وما لي، إلا مذهب الحق مذهب','101','72','0');
INSERT INTO [t17712] VALUES (120,'٤٨ - وابأبي أنت وفوك الأشنب ... كأنما ذر عليه الزرنب','102','72','0');
INSERT INTO [t17712] VALUES (121,'٤٩ - وعدت وكان الخلف منك سجية ... مواعيد عرقوب أخاه بيثرب','102','72','0');
INSERT INTO [t17712] VALUES (122,'٥٠ - فإن أهجه يضجر كما ضجر بازل ... من الأدم دبرت صفحتاه وغاربه','102','72','0');
INSERT INTO [t17712] VALUES (123,'٥١ - ولما تعيا بالقلوص ورحلها ... كفى الله كعبا ما تعيا به كعب','103','72','0');
INSERT INTO [t17712] VALUES (124,'٥٢ - إن من لام في بني بنت حسا ... ن ألمه وأعصه في الخطوب','103','72','0');
INSERT INTO [t17712] VALUES (125,'٥٣ - أجدك لست الدهر رائي رامة ... ولا عاقل إلا وأنت جنيب ولا مصعد في المصعدين لمنعج ... ولا هابط ما عشت هضب شطيب','103','72','0');
INSERT INTO [t17712] VALUES (126,'٥٤ - وداوية قفر يحار بها القطا ... أدلة ركبيها بنات النجائب يحايي بها الجلد الذي هو حازم ... بضربة كفيه الملا نفس راكب','104','72','0');
INSERT INTO [t17712] VALUES (127,'٥٥ - يا للرجال ليوم الأربعاء أما ... ينفك يحدث لي بعد النهى طربا إذ لا يزال غزال فيه يفتنني ... يأتي إلى مسجد الأحزاب منتقبا لكنه شاقه أن قيل ذا رجب ... يا ليت عدة حول كله (رجب) رجبا','104','72','0');
INSERT INTO [t17712] VALUES (128,'٥٦ - أيا أخوينا عبد شمس ونوفلا ... أعيذكما بالله أن تحدثا حربا','105','72','0');
INSERT INTO [t17712] VALUES (129,'٥٧ - كأن كبرى وصغرى من فقاقعها ... حصباء در على أرض من الذهب','105','72','0');
INSERT INTO [t17712] VALUES (130,'٥٨ - لم تتلفع بفضل مئزرها ... دعد ولم تسق دعد في العلب','106','72','0');
INSERT INTO [t17712] VALUES (131,'٥٩ - عجب لتلك قضية وإقامتي ... فيكم على تلك القضية أعجب','106','72','0');
INSERT INTO [t17712] VALUES (132,'٦٠ - لك الخير عللنا بها عل ساعة ... تمر وسهواء من الليل يذهب','106','72','0');
INSERT INTO [t17712] VALUES (133,'٦١ - يا ليت أم العمرو كانت صاحبي ... مكان من أشتى على الركائب','107','72','0');
INSERT INTO [t17712] VALUES (134,'٦٢ - فعاجوا فأثنوا بالذي أنت أهله ... ولو سكتوا أثنت عليك الحقائب','107','72','0');
INSERT INTO [t17712] VALUES (135,'٦٣ - إن الشباب الذي مجد عواقبه ... فيه نلذ ولا لذات للشيب','108','72','0');
INSERT INTO [t17712] VALUES (136,'٦٤ - هذا لعمركم الصغار بعينه ... لا أم لي- إن كان ذاك- ولا أب','108','72','0');
INSERT INTO [t17712] VALUES (137,'٦٥ - فإني وقفت اليوم والأمس قبله ... بابك حتى كادت الشمس تغرب','109','72','0');
INSERT INTO [t17712] VALUES (138,'٦٦ - ربه فتية دعوت إلى ما ... يورث المجد دائبا فأجابوا','109','72','0');
INSERT INTO [t17712] VALUES (139,'٦٧ - فأدرك لم يجهد ولم يثن شأوه ... يمر كخذروف الوليد المثقب','110','72','0');
INSERT INTO [t17712] VALUES (140,'٦٨ - نتج الربيع محاسنا ... ألقحنها غر السحائب','110','72','0');
INSERT INTO [t17712] VALUES (141,'٦٩ - كرب القلب من جواه يذوب ... حين قال الوشاة: هند غضوب','111','72','0');
INSERT INTO [t17712] VALUES (142,'٧٠ - لولا توقع معتر فأرضيه ... ما كنت أوثر إترابا على ترب','111','72','0');
INSERT INTO [t17712] VALUES (143,'٧١ - فقلنا لفتيان كرام: ألا انزلوا ... فعالوا علينا فضل ثوب مطنب فلما دخلناه أضفنا ظهورنا ... إلى كل حاري جديد مشطب','111','72','0');
INSERT INTO [t17712] VALUES (144,'٧٢ - يا صاح بلغ ذوي الزوجات كلهم ... أن ليس وصل إذا انحلت عرى الذنب','111','72','0');
INSERT INTO [t17712] VALUES (145,'٧٣ - أمرتك الخير فافعل ما أمرت به ... فقد تركتك ذا مال وذا نشب','112','72','0');
INSERT INTO [t17712] VALUES (146,'٧٤ - أقلي اللوم- عاذل- والعتابن ... وقولي- إن أصبت-: لقد أصابن','112','72','0');
INSERT INTO [t17712] VALUES (147,'٧٥ - فلست بذي نيرب في الصديق ... ومناع خير وسبابها ولا من إذا كان في جانب ... أضاع العشيرة فاغتابها','113','72','0');
INSERT INTO [t17712] VALUES (148,'٧٦ - أبا عرو لا تبعد فكل ابن حرة ... سيدعوه داعي ميتة فيجيب','113','72','0');
INSERT INTO [t17712] VALUES (149,'٧٧ - أرق لأرحام أراها قريبة ... لحار بن كعب لا لجرم وراسب','113','72','0');
INSERT INTO [t17712] VALUES (150,'٧٨ - أبلغ هذيلا وأبلغ من يبلغهم ... عني حديثا وبعض القول تكذيب بأن ذا الكلب عمرا خيرهم حسبا ... ببطن شريان يعوي حوله الذيب','114','72','0');
INSERT INTO [t17712] VALUES (151,'٧٩ - أهابك إجلالا وما بك قدرة ... علي ولكن ملء عين حبيبها','114','72','0');
INSERT INTO [t17712] VALUES (152,'٨٠ - فكن لي شفيعا يوم لا ذو شفاعة ... بمغن فتيلا عن سواد بن قارب','114','72','0');
INSERT INTO [t17712] VALUES (153,'٨١ - كلانا يا معاذ يحب ليلى ... بفي وفيك من ليلى التراب','115','72','0');
INSERT INTO [t17712] VALUES (154,'٨٢ - حتى إذا قملت بطونكم ... ورأيتم أبناءكم شبوا وقلبتم ظهر المجن لنا ... إن اللئيم العاجز الخب','115','72','0');
INSERT INTO [t17712] VALUES (155,'٨٣ - ومصعب حين جد الأم ... ر أكثرها وأطيبها','115','72','0');
INSERT INTO [t17712] VALUES (156,'٨٤ - عسى الكرب الذي أمسيت فيه ... يكون وراءه فرج قريب','116','72','0');
INSERT INTO [t17712] VALUES (157,'٨٥ - فموشكة أرضنا أن تعود ... خلاف الأنيس وحوشا يبابا وتوحش في الأرض بعد الكلام ... ولا تبصر العين فيه كلابا','116','72','0');
INSERT INTO [t17712] VALUES (158,'٨٦ - أم الحليس لعجوز شهربه ... ترضى من اللحم بعظم الرقبه','117','72','0');
INSERT INTO [t17712] VALUES (159,'٨٧ - وربيته حتى إذا ما تركته ... أخا القوم واستغنى عن المسح شاربه','117','72','0');
INSERT INTO [t17712] VALUES (160,'٨٨ - أكنيه حين أناديه لأكرمه ... ولا ألقبه والسوءة اللقب كذاك أدبت حتى صار من خلقي ... إني وجدت ملاك الشيمة الأدب','119','72','0');
INSERT INTO [t17712] VALUES (161,'٨٩ - بأي كتاب أم بأية سنة ... ترى حبهم عارا علي وتحسب','120','72','0');
INSERT INTO [t17712] VALUES (162,'٩٠ - يمرون بالدهنا خفافا عيابهم ... ويرجعن من دارين بجر الحقائب على حين ألهى الناس جل أمورهم ... فندلا- زريق- المال، ندل الثعالب','121','72','0');
INSERT INTO [t17712] VALUES (163,'٩١ - حلفت برب الراكعين لربهم ... خشوعا وفوق الراكعين رقيب لئن كان برد الماء هيمان صاديا ... إلي حبيبا إنها لحبيب وقلت لعراف اليمامة داوني ... فإنك- إن أبرأتني- لطبيب','121','72','0');
INSERT INTO [t17712] VALUES (164,'٩٢ - أتهجر ليلى بالفراق حبيبها؟ ... وما كان نفسا بالفراق تطيب','122','72','0');
INSERT INTO [t17712] VALUES (165,'٩٣ - وداع دعا: يا من يجيب إلى الندى ... فلم يستجبه عند ذاك مجيب فقلت: ادع أخرى وارفع الصوت جهرة ... لعل أبي المغوار منك قريب','122','72','0');
INSERT INTO [t17712] VALUES (166,'٩٤ - واه رأيت وشيكا صدع أعظمه ... وربه عطبا أنقذت من عطبه','123','72','0');
INSERT INTO [t17712] VALUES (167,'٩٥ - خلى الذنابات شمالا كثبا ... وأم أوعال كها أو أقربا','123','72','0');
INSERT INTO [t17712] VALUES (168,'٩٦ - تخيرن من أزمان يوم حليمة ... إلى اليوم قد جربن كل التجارب','124','72','0');
INSERT INTO [t17712] VALUES (169,'٩٧ - وما زال مهري مزجر الكلب منهم ... لدن غدوة حتى دنت لغروب','124','72','0');
INSERT INTO [t17712] VALUES (170,'٩٨ - نجوت وقد بل المرادي سيفه ... من ابن أبي شيخ الأباطح طالب','125','72','0');
INSERT INTO [t17712] VALUES (171,'٩٩ - فقالت لنا: أهلا وسهلا، وزودت ... جنى النحل، بل ما زودت منه أطيب','125','72','0');
INSERT INTO [t17712] VALUES (172,'١٠٠ - وما أدري أغيرهم تناء ... وطول الدهر أم مال أصابوا','126','72','0');
INSERT INTO [t17712] VALUES (173,'١٠١ - فاليوم قربت تهجونا وتشتمنا ... فاذهب فما بك والأيام من عجب','126','72','0');
INSERT INTO [t17712] VALUES (174,'١٠٢ - تبصر خليلي هل ترى من ظعائن ... سوالك نقبا بين حزمي شعبعب','127','72','0');
INSERT INTO [t17712] VALUES (175,'١٠٣ - أنخ فاصطبغ قرصا إذا اعتادك الهوى ... بزيت كما يكفيك فقد الحبائب','127','72','0');
INSERT INTO [t17712] VALUES (176,'١٠٤ - وإني امرؤ من عصبة خندفية ... أبت للأعادي أن تذل رقابها','128','72','0');
INSERT INTO [t17712] VALUES (177,'١٠٥ - فأما القتال لا قتال لديكم ... ولكن سيرا في عراض المواكب','128','72','0');
INSERT INTO [t17712] VALUES (178,'١٠٦ - قلما يبرح اللبيب إلى ما ... يورث المجد داعيا أو مجيبا','129','72','0');
INSERT INTO [t17712] VALUES (179,'١٠٧ - كهز الرديني بين الأكف ... جرى في الأنابيب ثم اضطرب','129','72','0');
INSERT INTO [t17712] VALUES (180,'١٠٨ - رددت بمثل السيد نهد مقلص ... كميش إذا عطفاه ماء تحلبا','129','72','0');
INSERT INTO [t17712] VALUES (181,'١٠٩ - وحديثها كالقطر يسمعه ... راعي السنين تتابعت جدبا فأصاخ يرجو أن يكون حيا ... ويقول من طمع هيا ربا','130','72','0');
INSERT INTO [t17712] VALUES (182,'١١٠ - فيا شوق ما أبقى ويالي من النوى ... ويا دمع ما أجرى ويا قلب ما أصبا','131','72','0');
INSERT INTO [t17712] VALUES (183,'١١١ - وكائن بالأباطح من صديق ... يراني لو أصبت هو المصابا','131','72','0');
INSERT INTO [t17712] VALUES (184,'١١٢ - لن تراها- ولو تأملت- إلا ... ولها في مفارق الرأس طيبا','131','72','0');
INSERT INTO [t17712] VALUES (185,'١١٣ - أرى الدهر إلا منجنونا بأهله ... وما صاحب الحاجات إلا معذبا','132','72','0');
INSERT INTO [t17712] VALUES (186,'١١٤ - إن امرءا رهطه بالشام منزله ... برمل يبرين جار شد ما اغتربا','133','72','0');
INSERT INTO [t17712] VALUES (187,'١١٥ - وللخيل أيام فمن يصطبر لها ... ويعرف لها أيامها الخير تعقب','133','72','0');
INSERT INTO [t17712] VALUES (188,'١١٦ - أرى رجلا منهم أسيفا كأنما ... يضم إلى كشحيه كفا مخصبا','134','72','0');
INSERT INTO [t17712] VALUES (189,'١١٧ - خذي العفو مني تستديمي مودتي ... ولا تنطقي في سورتي حين أغضب فإني رأيت الحب في الصدر والأذى ... إذا اجتمعا لم يلبث الحب يذهب','134','72','0');
INSERT INTO [t17712] VALUES (190,'١١٨ - وأغضي على أشياء منك لترضني ... وأدعى إلى ما سركم فأجيب','134','72','0');
INSERT INTO [t17712] VALUES (191,'١١٩ - كأن هندا ثناياها وبهجتها ... يوم التقينا على أرحال عناب','134','72','0');
INSERT INTO [t17712] VALUES (192,'١٢٠ - ألا إن سرى ليلي فبت كئيبا ... أحاذر أن تنأى النوى بغضوبا','134','72','0');
INSERT INTO [t17712] VALUES (193,'١٢١ - ما الحازم الشهم مقداما ولا بطل ... إن لم يكن للهوى بالحق غلابا','135','72','0');
INSERT INTO [t17712] VALUES (194,'١٢٢ - فإن أهلك فذي حنق لظاه ... علي تكاد تلتهب التهابا','135','72','0');
INSERT INTO [t17712] VALUES (195,'١٢٣ - لدن بهز الكف يعسل متنه ... فيه كما عسل الطريق الثعلب','136','72','0');
INSERT INTO [t17712] VALUES (196,'١٢٤ - أرب يبول الثعلبان برأسه ... لقد هان من بالت عليه الثعالب','136','72','0');
INSERT INTO [t17712] VALUES (197,'١٢٥ - فلا تتركني بالوعيد كأنني ... إلى الناس مطلي به القار أجرب','136','72','0');
INSERT INTO [t17712] VALUES (198,'١٢٦ - وإياك إياك المراء فإنه ... إلى الشر دعاء وللشر جالب','137','72','0');
INSERT INTO [t17712] VALUES (199,'١٢٧ - يرجي المرء ما إن لا يراه ... وتعرض دون أدناه الخطوب','137','72','0');
INSERT INTO [t17712] VALUES (200,'١٢٨ - أتت حتاك تقصد كل فج ... ترجي منك أنها لا تخيب','138','72','0');
INSERT INTO [t17712] VALUES (201,'١٢٩ - هذا سراقة للقرآن يدرسه ... والمرء عند الرشا إن يلقها ذيب','138','72','0');
INSERT INTO [t17712] VALUES (202,'١٣٠ - كتبت أبو جاد وخط مرامر ... وخرقت سربالا ولست بكاتب','139','72','0');
INSERT INTO [t17712] VALUES (203,'١٣١ - أطوف بها لا أرى غيرها ... كما طاف بالبيعة الراهب','139','72','0');
INSERT INTO [t17712] VALUES (204,'١٣٢ - فيا معشر العزاب إن حان شربكم ... فلا تشربوا ما حج لله راكب','139','72','0');
INSERT INTO [t17712] VALUES (205,'١٣٣ - لا تصيب الصديق قارعة التأ ... نيب إلا من الصديق الرغيب غير أن العليل ليس بمذمو ... م على شرح ما به للطبيب لو رأينا التوكيد خطة عجز ... ما شفعنا الأذان بالتثويب','140','72','0');
INSERT INTO [t17712] VALUES (206,'١٣٤ - ومنا لقيط وابنماه وحاجب ... مؤرث نيران المكارم لا المخبي','140','72','0');
INSERT INTO [t17712] VALUES (207,'١٣٥ - لما اتقى بيد عظيم جرمها ... فتركت ضاحي جلدها يتذبذب','140','72','0');
INSERT INTO [t17712] VALUES (208,'١٣٦ - ولو تلتقي أصداؤنا بعد موتنا ... ومن دون رمسينا من الأرض سبسب','140','72','0');
INSERT INTO [t17712] VALUES (209,'١٣٧ - قد أشهد الغارة الشعواء تحملني ... جرداء معروقة اللحيين سرحوب','141','72','0');
INSERT INTO [t17712] VALUES (210,'١٣٨ - منا الذي هو ما إن طر شاربه ... والعانسون ومنا المرد والشيب','141','72','0');
INSERT INTO [t17712] VALUES (211,'١٣٩ - شربت بها والديك يدعو صباحه ... إذا ما بنو نعش دنوا فتصوبوا','142','72','0');
INSERT INTO [t17712] VALUES (212,'١٤٠ - لا بارك الله في الغواني هل ... يصبحن إلا لهن مطلب','143','72','0');
INSERT INTO [t17712] VALUES (213,'١٤١ - ما إن وجدنا للهوى من طب ... ولا عدمنا قهر- وجد- صب','143','72','0');
INSERT INTO [t17712] VALUES (214,'١٤٢ - رأيت بني عمي الأولى يخذلونني ... على حدثان الدهر إذ يتقلب','143','72','0');
INSERT INTO [t17712] VALUES (215,'١٤٣ - فإن تسألوني بالنساء فإنني ... بصير بأدواء النساء طبيب إذا شاب رأس المرء أو قل ماله ... فليس له من ودهن نصيب','143','72','0');
INSERT INTO [t17712] VALUES (216,'١٤٤ - أجارتنا إن الخطوب تنوب ... وإني مقيم ما أقام عسيب','143','72','0');
INSERT INTO [t17712] VALUES (217,'١٤٥ - إذا قيل سيروا إن ليلى لعلها ... جرى دون ليلى مائل القرن أعضب','143','72','0');
INSERT INTO [t17712] VALUES (218,'١٤٦ - فمن يك أمسى بالمدينة رحله ... فإني وقيار بها لغريب','144','72','0');
INSERT INTO [t17712] VALUES (219,'١٤٧ - أين المفر والإله الطالب ... والأشرم المغلوب ليس الغالب','144','72','0');
INSERT INTO [t17712] VALUES (220,'١٤٨ - ولست بنازل إلا ألمت ... برحلي أو خيالتها الكذوب فقد جعلت قلوص بني زياد ... من الأكوار مرتعها قريب','145','72','0');
INSERT INTO [t17712] VALUES (221,'١٤٩ - طربت وما شوقا إلى البيض أطرب ... ولا لعبا مني وذو الشيب يلعب','146','72','0');
INSERT INTO [t17712] VALUES (222,'١٥٠ - فلا تستطل مني بقائي ومدتي ... ولكن يكن للخير منك نصيب','146','72','0');
INSERT INTO [t17712] VALUES (223,'١٥١ - تطاول هذا الليل تسري كواكبه ... وأرقني إذ لا ضجيع ألاعبه فو الله لولا الله تخشى عواقبه ... لزعزع من هذا السرير جوانبه ولكنني أخشى رقيبا موكلا ... بأنفسنا لا يفتر الدهر كاتبه مخافة ربي والحياء يصدني ... وإكرام بعلي أن تنال مراكبه','146','72','0');
INSERT INTO [t17712] VALUES (224,'١٥٢ - أخ ماجد لم يخزني يوم مشهد ... كما سيف عمرو لم تخنه مضاربه','148','72','0');
INSERT INTO [t17712] VALUES (225,'١٥٣ - وما زرت سلمى أن تكون حبيبة ... إلي ولا دين بها أنا طالبه','148','72','0');
INSERT INTO [t17712] VALUES (226,'١٥٤ - وقد طفت من أحوالها وأردتها ... سنين فأخشى بعلها وأهابها ثلاثة أحوال فلما تجرمت ... علينا بهون واستحار شبابها دعاني إليها القلب إني لأمره ... سميع فما أدري أرشد طلابها','149','72','0');
INSERT INTO [t17712] VALUES (227,'١٥٥ - لأنكحن ببه ... جارية خدبة','149','72','0');
INSERT INTO [t17712] VALUES (228,'١٥٦ - صريع غوان راقهن ورقنه ... لدن شب حتى شاب سود الذوائب','150','72','0');
INSERT INTO [t17712] VALUES (229,'١٥٧ - مشائيم ليسوا مصلحين عشيرة ... ولا ناعب إلا ببين غرابها','150','72','0');
INSERT INTO [t17712] VALUES (230,'١٥٨ - في ليلة لا نرى بها أحدا ... يحكي علينا إلا كواكبها','150','72','0');
INSERT INTO [t17712] VALUES (231,'١٥٩ - فما سودتني عامر عن وراثة ... أبى الله أن أسمو بأم ولا أب','151','72','0');
INSERT INTO [t17712] VALUES (232,'١٦٠ - ثم قالوا: تحبها؟ قلت: بهرا ... عدد الرمل والحصى والتراب','151','72','0');
INSERT INTO [t17712] VALUES (233,'١٦١ - إذا ما غدونا قال ولدان أهلنا ... تعالوا إلى أن يأتنا الصيد نحطب','151','72','0');
INSERT INTO [t17712] VALUES (234,'١٦٢ - وكل مصيبات الزمان وجدتها ... سوى فرقة الأحباب- هينة الخطب','151','72','0');
INSERT INTO [t17712] VALUES (235,'١٦٣ - كلاهما حين جد السير بينهما ... قد أقلعا، وكلا أنفيهما رابي','152','72','0');
INSERT INTO [t17712] VALUES (236,'١٦٤ - ألا حبذا، لولا الحياء وربما ... منحت الهوى ما ليس بالمتقارب','152','72','0');
INSERT INTO [t17712] VALUES (237,'١٦٥ - إليك وإلا ما تحث الركائب ... عنك وإلا فالمحدث كاذب','152','72','0');
INSERT INTO [t17712] VALUES (238,'١٦٦ - وقال متى يبخل عليك ويعتلل ... يسؤك وإن يكشف غرامك تدرب','153','72','0');
INSERT INTO [t17712] VALUES (239,'١٦٧ - ولست بنحوي يلوك لسانه ... ولكن سليقي أقول فأعرب','153','72','0');
INSERT INTO [t17712] VALUES (240,'١٦٨ - أفيقوا بني حزن وأهواؤنا معا ... وأرماحنا موصولة لم تقضب','153','72','0');
INSERT INTO [t17712] VALUES (241,'١٦٩ - أما والذي لو شاء لم يخلق النوى ... لئن غبت عن عيني لما غبت عن قلبي','154','72','0');
INSERT INTO [t17712] VALUES (242,'١٧٠ - وما كل ذي لب بمؤتيك نصحه ... وما كل مؤت نصحه بلبيب','154','72','0');
INSERT INTO [t17712] VALUES (243,'١٧١ - وأدفع عن أعراضكم وأعيركم ... لسانا كمقراض الخفاجي ملحبا ثمت لا تجزونني عند ذاكم ... ولكن سيجزيني الإله فيعقبا','154','72','0');
INSERT INTO [t17712] VALUES (244,'١٧٢ - ومن يغترب عن قومه لا يزل يرى ... مصارع مظلوم مجرا ومسحبا وتدفن منه الصالحات وإن يسئ ... يكن ما أساء النار في رأس كبكبا','155','72','0');
INSERT INTO [t17712] VALUES (245,'١٧٣ - تداركن حيا من نمير بن عامر ... أسارى تسام الذل قتلا ومحربا','155','72','0');
INSERT INTO [t17712] VALUES (246,'١٧٤ - تركتني حين لا مال أعيش به ... وحين جن زمان الناس أو كلبا','155','72','0');
INSERT INTO [t17712] VALUES (247,'١٧٥ - عاود هراة وإن معمورها خربا ... وأسعد اليوم مشغوفا إذا طربا','155','72','0');
INSERT INTO [t17712] VALUES (248,'١٧٦ - هيفاء مقبلة عجزاء مدبرة ... محطوطة جدلت شنباء أنيابا','156','72','0');
INSERT INTO [t17712] VALUES (249,'١٧٧ - أثعلبة الفوارس أو رياحا ... عدلت بهم طهية والخشابا','156','72','0');
INSERT INTO [t17712] VALUES (250,'١٧٨ - أعبدا حل في شعبى غريبا ... ألؤما لا أبالك واغترابا','156','72','0');
INSERT INTO [t17712] VALUES (251,'١٧٩ - فما قومي بثعلبة بن سعد ... ولا بفزارة الشعرى رقابا','157','72','0');
INSERT INTO [t17712] VALUES (252,'١٨٠ - رأيت الصدع من كعب وكانوا ... من الشنآن قد صاروا كعابا','157','72','0');
INSERT INTO [t17712] VALUES (253,'١٨١ - ليت هذا الليل شهر ... لا نرى فيه عريبا ليس إياي وإيا ... ك ولا نخشى رقيبا','157','72','0');
INSERT INTO [t17712] VALUES (254,'١٨٢ - وبالسهب، ميمون النقيبة قوله ... لملتمس المعروف: أهل ومرحب','158','72','0');
INSERT INTO [t17712] VALUES (255,'١٨٣ - وجدنا لكم في آل حاميم أية ... تأولها منا تقي ومعرب','158','72','0');
INSERT INTO [t17712] VALUES (256,'١٨٤ - وما حل سعدي غريبا ببلدة ... فينسب إلا الزبرقان له أب','158','72','0');
INSERT INTO [t17712] VALUES (257,'١٨٥ - فدى لبني ذهل بن شيبان ناقتي ... إذا كان يوم ذو كواكب أشهب','159','72','0');
INSERT INTO [t17712] VALUES (258,'١٨٦ - كذبتم وبيت الله لا تنكحونها ... بني شاب قرناها تصر وتحلب','159','72','0');
INSERT INTO [t17712] VALUES (259,'١٨٧ - فلا تجعلي ضيفي ضيف مقرب ... وآخر معزول عن البيت جانب','159','72','0');
INSERT INTO [t17712] VALUES (260,'١٨٨ - فما هو إلا أن أراها فجاءة ... فأبهت حتى ما أكاد أجيب','159','72','0');
INSERT INTO [t17712] VALUES (261,'١٨٩ - بها جيف الحسرى فأما عظامها ... فبيض وأما جلدها فصليب','159','72','0');
INSERT INTO [t17712] VALUES (262,'١٩٠ - وخبر تماني أنما الموت في القرى ... فكيف وهاتا هضبة وقليب','160','72','0');
INSERT INTO [t17712] VALUES (263,'١٩١ - وقفت على ربع لمية ناقتي ... فما زلت أبكي حوله وأخاطبه وأسقيه حتى كاد مما أبثه ... تكلمني أحجاره وملاعبه','160','72','0');
INSERT INTO [t17712] VALUES (264,'١٩٢ - ولكن ديافي أبوه وأمه ... بحوران يعصرن السليط أقاربه','160','72','0');
INSERT INTO [t17712] VALUES (265,'١٩٣ - كأنك لم تذبح لأهلك نعجة ... فيصبح ملقى بالفناء إهابها','161','72','0');
INSERT INTO [t17712] VALUES (266,'١٩٤ - ديار مية إذ مي مساعفة ... ولا يرى مثلها عجم ولا عرب','161','72','0');
INSERT INTO [t17712] VALUES (267,'١٩٥ - تصغي إذا شدها بالرحل جانحة ... حتى إذا ما استوى في غرزها تثب','161','72','0');
INSERT INTO [t17712] VALUES (268,'١٩٦ - اردد حمارك لا تنزع سويته ... إذن يرد وقيد العير مكروب','161','72','0');
INSERT INTO [t17712] VALUES (269,'١٩٧ - عسى الله يغني عن بلاد ابن قادر ... بمنهمر جون الرباب سكوب','162','72','0');
INSERT INTO [t17712] VALUES (270,'١٩٨ - ولقد طعنت أبا عيينة طعنة ... جرمت فزارة بعدها أن يغضبوا','162','72','0');
INSERT INTO [t17712] VALUES (271,'١٩٩ - أقاتل حتى لا أرى لي مقاتلا ... وأنجو إذا غم الجبان من الكرب','162','72','0');
INSERT INTO [t17712] VALUES (272,'٢٠٠ - إذا قصرت أسيافنا كان وصلها ... خطانا إلى أعدائنا فنضارب','162','72','0');
INSERT INTO [t17712] VALUES (273,'٢٠١ - ولا عيب فيهم غير أن سيوفهم ... بهن فلول من قراع الكتائب','163','72','0');
INSERT INTO [t17712] VALUES (274,'٢٠٢ - سألت هذيل رسول الله فاحشة ... ضلت هذيل بما جاءت ولم تصب','163','72','0');
INSERT INTO [t17712] VALUES (275,'٢٠٣ - كم فيهم ملك أغر وسوقة ... حكم بأردية المكارم محتبي','163','72','0');
INSERT INTO [t17712] VALUES (276,'٢٠٤ - حلفت يمينا غير ذي مثنوية ... ولا علم إلا حسن ظن بصاحب','163','72','0');
INSERT INTO [t17712] VALUES (277,'٢٠٥ - فإما تري لمتي بدلت ... فإن الحوادث أودى بها','163','72','0');
INSERT INTO [t17712] VALUES (278,'٢٠٦ - وإذا تصبك خصاصة فارج الغنى ... وإلى الذي يعطي الرغائب فارغب','164','72','0');
INSERT INTO [t17712] VALUES (279,'٢٠٧ - لدوا للموت وابنوا للخراب ... فكلكم يصير إلى ذهاب','164','72','0');
INSERT INTO [t17712] VALUES (280,'٢٠٨ - فها أنا تائب من حب ليلى ... فما لك كلما ذكرت تذوب','164','72','0');
INSERT INTO [t17712] VALUES (281,'٢٠٩ - أحقا عباد الله أن لست صاعدا ... ولا هابطا إلا علي رقيب ولا سالك وحدي ولا في جماعة ... من الناس إلا قيل: أنت مريب','164','72','0');
INSERT INTO [t17712] VALUES (282,'٢١٠ - جارية من قيس بن ثعلبة ... كأنها حلية سيف مذهبة','164','72','0');
INSERT INTO [t17712] VALUES (283,'٢١١ - لو أن قوما- لارتفاع قبيلة ... دخلوا السماء- دخلتها لا أحجب','165','72','0');
INSERT INTO [t17712] VALUES (284,'٢١٢ - أصخ مصيخا لمن أبدى نصيحته ... والزم توقي خلط الجد باللعب','165','72','0');
INSERT INTO [t17712] VALUES (285,'٢١٣ - وهلا أعدوني لمثلي، تفاقدوا ... وفي الأرض مبثوثا شجاع وعقرب','165','72','0');
INSERT INTO [t17712] VALUES (286,'٢١٤ - فمن يك لم ينجب أبوه وأمه ... فإن لنا الأم النجيبة والأب','165','72','0');
INSERT INTO [t17712] VALUES (287,'٢١٥ - ألا ليت شعري كيف جادت بوصلها ... وكيف تراعي وصلة المتغيب','166','72','0');
INSERT INTO [t17712] VALUES (288,'٢١٦ - أستحدث الركب عن أشياعهم خبرا ... أم راجع القلب من أطرابه طرب؟','166','72','0');
INSERT INTO [t17712] VALUES (289,'٢١٧ - قد يعلم الناس أني من خيارهم ... في الدين دينا وفي أحسابهم حسبا لا يمنع الناس مني ما أردت ولا ... أعطيهم ما أرادوا حسن ذا أدبا','166','72','0');
INSERT INTO [t17712] VALUES (290,'٢١٨ - نعم امرأين حاتم وكعب ... كلاهما غيث وسيف عضب','167','72','0');
INSERT INTO [t17712] VALUES (291,'٢١٩ - سموت ولم تكن أهلا لتسمو ... ولكن المضيع قد يصاب','167','72','0');
INSERT INTO [t17712] VALUES (292,'٢٢٠ - أخلاء لو غير الحمام أصابكم ... عتبت ولكن ما على الدهر معتب','168','72','0');
INSERT INTO [t17712] VALUES (293,'٢٢١ - ما بأس لو ردت علينا تحية ... قليل على من يعرف الحق عابها','168','72','0');
INSERT INTO [t17712] VALUES (294,'٢٢٢ - فلئن صرت لا تحير جوابا ... لبما قد ترى وأنت خطيب','168','72','0');
INSERT INTO [t17712] VALUES (295,'٢٢٣ - إذا لم يكن إلا الأسنة مركب ... فلا رأي للمحمول إلا ركوبها','169','72','0');
INSERT INTO [t17712] VALUES (296,'٢٢٤ - يا ليت أم العمرو كانت صاحبي ... مكان من أمسى على الركائب','169','72','0');
INSERT INTO [t17712] VALUES (297,'٢٢٥ - فمن يك لم ينجب أبوه وأمه ... فإن لنا الأم النجيبة والأب','170','72','0');
INSERT INTO [t17712] VALUES (298,'٢٢٦ - يا رب ذي لقح ببابك فاحش ... هلع إذا ما الناس جاع وأجدبوا','170','72','0');
INSERT INTO [t17712] VALUES (299,'٢٢٧ - لا كعبة الله ما هجرتكم ... إلا وفي النفس منكم أرب','170','72','0');
INSERT INTO [t17712] VALUES (300,'٢٢٨ - إني أرقت على المطلى وأشأزني ... برق يضيء أمام البيت أسكوب','170','72','0');
INSERT INTO [t17712] VALUES (301,'٢٢٩ - إليكم ذوي آل النبي تطلعت ... نوازع من قلبي ظماء وألبب','170','72','0');
INSERT INTO [t17712] VALUES (302,'٢٣٠ - تخدي بنا نجب أفنى عرائكها ... خمس وخمس وتأويب وتأويب','171','72','0');
INSERT INTO [t17712] VALUES (303,'٢٣١ - أيا موقدا نارا لغيرك ضوؤها ... ويا حاطبا في غير حبلك تحطب','171','72','0');
INSERT INTO [t17712] VALUES (304,'٢٣٢ - قتلننا بعيون زانها مرض ... وفي المراض لنا شجو وتعذيب','171','72','0');
INSERT INTO [t17712] VALUES (305,'٢٣٣ - على أحوذيين استقلت عليهما ... فما هي إلا لمحة فتغيب','171','72','0');
INSERT INTO [t17712] VALUES (306,'٢٣٤ - فإن السلم زائدة نوالا ... وإن نوى المحارب لا يؤوب','171','72','0');
INSERT INTO [t17712] VALUES (307,'٢٣٥ - طعامهم لئن أكلوا معد ... وما إن لا تحاك لهم ثياب','171','72','0');
INSERT INTO [t17712] VALUES (308,'٢٣٦ - لكل أناس من معد عمارة ... عروض إليها يلجؤون وجانب','172','72','0');
INSERT INTO [t17712] VALUES (309,'٢٣٧ - فو الله ما أدري أسلمى تغولت ... أم النوم أم كل إلي حبيب','172','72','0');
INSERT INTO [t17712] VALUES (310,'٢٣٨ - وفي كل حي قد خبطت بنعمة ... فحق لشأس من نداك ذنوب','172','72','0');
INSERT INTO [t17712] VALUES (311,'٢٣٩ - كحلاء في برج صفراء في نعج ... كأنها فضة قد مسها ذهب','173','72','0');
INSERT INTO [t17712] VALUES (312,'٢٤٠ - لا تعجبنك دنيا أنت تاركها ... كم نالها من أناس ثم قد ذهبوا','173','72','0');
INSERT INTO [t17712] VALUES (313,'٢٤١ - أبلغ هذيلا وأبلغ من يبلغها ... عني رسولا، وبعض القول تكذيب بأن ذا الكلب عمرا خيرهم نسبا ... ببطن شريان يعوي حوله الذيب','173','72','0');
INSERT INTO [t17712] VALUES (314,'٢٤٢ - يأوي إليكم بلا من ولا جحد ... من ساقه السنة الحصاء والذيب','173','72','0');
INSERT INTO [t17712] VALUES (315,'٢٤٣ - وخبرت أن أنما بين بيته ... ونجران أحوى والجناب رطيب','174','72','0');
INSERT INTO [t17712] VALUES (316,'٢٤٤ - يهدي الخميس نجادا في مطالعها ... إما المصاع وإما ضربة رغب','174','72','0');
INSERT INTO [t17712] VALUES (317,'٢٤٥ - كأنها لقوة طلوب ... تيبس في وكرها القلوب باتت على أرم عذوبا ... كأنها شيخة رقوب','174','72','0');
INSERT INTO [t17712] VALUES (318,'٢٤٦ - تراد على دمن الحياض فإن تعف ... فإن المندى رحلة فركوب','174','72','0');
INSERT INTO [t17712] VALUES (319,'٢٤٧ - أنى ومن أين آبك الطرب ... من حيث لا صبوة ولا ريب','175','72','0');
INSERT INTO [t17712] VALUES (320,'٢٤٨ - لمياء في شفتيها حوة لعس ... وفي اللثات وفي أنيابها شنب','175','72','0');
INSERT INTO [t17712] VALUES (321,'٢٤٩ - بكيت أخا اللأواء يحمد يومه ... كريم رؤوس الدارعين ضروب','176','72','0');
INSERT INTO [t17712] VALUES (322,'٢٥٠ - أتاني فلم أسرر به حين جاءني ... كتاب بأعلى القنتين عجيب','176','72','0');
INSERT INTO [t17712] VALUES (323,'٢٥١ - تقول ابنتي لما رأت وشك رحلتي ... كأنك فينا يا أبات غريب','176','72','0');
INSERT INTO [t17712] VALUES (324,'٢٥٢ - وما أنت أم ما ذكرها ربعية ... يخط لها من ثرمداء قليب','176','72','0');
INSERT INTO [t17712] VALUES (325,'٢٥٣ - فراشة الحلم فرعون العذاب وإن ... تطلب نداه، فكلب دونه كلب','176','72','0');
INSERT INTO [t17712] VALUES (326,'٢٥٤ - والعيس ينغضن بكيرانها ... كأنما ينهشهن الكليب','176','72','0');
INSERT INTO [t17712] VALUES (327,'٢٥٥ - تعفق بالأرطى لها وأرادها ... رجال فبذت نبلهم وكليب','176','72','0');
INSERT INTO [t17712] VALUES (328,'٢٥٦ - أفينا تسوم الشاهرية بعد ما ... بدا لك من شهر المليساء كوكب','177','72','0');
INSERT INTO [t17712] VALUES (329,'٢٥٧ - وما لي إلا آل أحمد شيعة ... ومالي إلا مشعب الحق مشعب','177','72','0');
INSERT INTO [t17712] VALUES (330,'٢٥٨ - أخلاء لو غير الحمام أصابكم ... عتبت ولكن ما على الموت معتب','177','72','0');
INSERT INTO [t17712] VALUES (331,'٢٥٩ - وكل من ظن أن الموت مخطئه ... معلل بسواء الحق مكذوب','178','72','0');
INSERT INTO [t17712] VALUES (332,'٢٦٠ - فلا تستطل مني بقائي ومدتي ... ولكن يكن للخير منك نصيب','178','72','0');
INSERT INTO [t17712] VALUES (333,'٢٦١ - ورب أمور لا تضيرك ضيرة ... وللقلب من مخشاتهن وجيب','178','72','0');
INSERT INTO [t17712] VALUES (334,'٢٦٢ - العارفو الحق للمدل به ... والمستقلو كثير ما وهبوا','178','72','0');
INSERT INTO [t17712] VALUES (335,'٢٦٣ - يسر الكريم الحمد لا سيما لدى ... شهادة من في خيره يتقلب','178','72','0');
INSERT INTO [t17712] VALUES (336,'٢٦٤ - رأيت موالي الألى يخذلونني ... على حدثان الدهر إذ يتقلب','178','72','0');
INSERT INTO [t17712] VALUES (337,'٢٦٥ - عوذ وبهثة حاشدون عليهم ... حلق الحديد مضاعفا يتلهب','179','72','0');
INSERT INTO [t17712] VALUES (338,'٢٦٦ - سموت ولم تكن أهلا لتسمو ... ولكن المضيع قد يصاب','179','72','0');
INSERT INTO [t17712] VALUES (339,'٢٦٧ - فلست لإنسي ولكن لملأك ... تنزل من جو السماء يصوب','179','72','0');
INSERT INTO [t17712] VALUES (340,'٢٦٨ - ضحيان شاهقه يرف بشامه ... نديان يقصر دونه اليعقوب','180','72','0');
INSERT INTO [t17712] VALUES (341,'٢٦٩ - وأزور يمطو في بلاد عريضة ... تعاوى به ذؤبانه وثعالبه','180','72','0');
INSERT INTO [t17712] VALUES (342,'٢٧٠ - فإن أستطع أغلب وإن يغلب الهوى ... فمثل الذي لاقيت يغلب صاحبه','180','72','0');
INSERT INTO [t17712] VALUES (343,'٢٧١ - كلا السيف والساق التي ضربت به ... على دهش ألقاه باثنين صاحبه','180','72','0');
INSERT INTO [t17712] VALUES (344,'٢٧٢ - وبالمحض حتى آض جعدا عنطنطا ... إذا قام ساوى غارب الفحل غاربه','181','72','0');
INSERT INTO [t17712] VALUES (345,'٢٧٣ - فصدقته وكذبته ... والمرء ينفعه كذابه','181','72','0');
INSERT INTO [t17712] VALUES (346,'٢٧٤ - فلما جلاها بالأيام تحيزت ... ثباتا عليها ذلها واكتئابها','181','72','0');
INSERT INTO [t17712] VALUES (347,'٢٧٥ - فقلت أيا رباه أول سؤلتي ... لنفسي ليلى ثم أنت حسيبها','182','72','0');
INSERT INTO [t17712] VALUES (348,'٢٧٦ - وجداء لا يرجى بها ذو قرابة ... لعطف ولا يخشى السماة ربيبها','182','72','0');
INSERT INTO [t17712] VALUES (349,'٢٧٧ - ورثت أبي أخلاقه عاجل القرى ... وعبط المهاري كومها وشبوبها','182','72','0');
INSERT INTO [t17712] VALUES (350,'٢٧٨ - لم أر مثل الأقوام في غبن ال ... أيام ينسون ما عواقبها','182','72','0');
INSERT INTO [t17712] VALUES (351,'٢٧٩ - وقد جعلت نفسي تطيب لضغمة ... لضغمهماها يقرع العظم نابها','183','72','0');
INSERT INTO [t17712] VALUES (352,'٢٨٠ - فماله من مجد تليد وماله ... من الريح فضل لا الجنوب ولا الصبا','183','72','0');
INSERT INTO [t17712] VALUES (353,'٢٨١ - ويصغر في عيني تلادي إذا انثنت ... يميني بإدراك الذي كنت طالبا','184','72','0');
INSERT INTO [t17712] VALUES (354,'٢٨٢ - حتى إذا الكلاب قال لها ... كاليوم مطلوبا ولا طلبا','184','72','0');
INSERT INTO [t17712] VALUES (355,'٢٨٣ - في ليلة من جمادى ذات أندية ... ما يبصر الكلب من ظلمائها الطنبا','184','72','0');
INSERT INTO [t17712] VALUES (356,'٢٨٤ - انطق بحق ولو مستخرجا إحنا ... فإن ذا الحق غلاب وإن غلبا','185','72','0');
INSERT INTO [t17712] VALUES (357,'٢٨٥ - يا عمرك الله إلا قلت صادقة ... أصادقا وصف المجنون أو كذبا','185','72','0');
INSERT INTO [t17712] VALUES (358,'٢٨٦ - وهل كنت يا ابن القين في الدهر مالكا ... لغير بعير بله مهرية نجبا','185','72','0');
INSERT INTO [t17712] VALUES (359,'٢٨٧ - يمشي القطوف إذا غنى الحداة به ... مشي الجواد فبله الجلة النجبا','185','72','0');
INSERT INTO [t17712] VALUES (360,'٢٨٨ - تالله لا يحمدن المرء مجتنبا ... فعل الكرام ولو فاق الورى حسبا','185','72','0');
INSERT INTO [t17712] VALUES (361,'٢٨٩ - ثمت لا تجزونني عند ذاكم ... ولكن سيجزيني الإله فيعقبا','186','72','0');
INSERT INTO [t17712] VALUES (362,'٢٩٠ - كذب العتيق وماء شن بارد ... إن كنت سائلتي غبوقا فاذهبي','186','72','0');
INSERT INTO [t17712] VALUES (363,'٢٩١ - أما أقاتل عن ديني على فرسي ... أو هكذا رجلا إلا بأصحاب','186','72','0');
INSERT INTO [t17712] VALUES (364,'٢٩٢ - إن السيوف غدوها ورواحها ... تركت هوازن مثل قرن الأعضب','186','72','0');
INSERT INTO [t17712] VALUES (365,'٢٩٣ - بالله ربك إن دخلت فقل له ... هذا ابن هرمة واقفا بالباب','187','72','0');
INSERT INTO [t17712] VALUES (366,'٢٩٤ - ولقد لحنت لكم لكيما تفقهوا ... واللحن يفهمه ذووا الألباب','187','72','0');
INSERT INTO [t17712] VALUES (367,'٢٩٥ - ولو أرادت لقالت وهي صادقة ... إن الرياضة لا تنصبك للشيب','187','72','0');
INSERT INTO [t17712] VALUES (368,'٢٩٦ - يومان يوم مقامات وأندية ... ويوم سير إلى الأعداء تأويب','187','72','0');
INSERT INTO [t17712] VALUES (369,'٢٩٧ - قديديمة التجريب والحلم إنني ... أرى غفلات العيش قبل التجارب','188','72','0');
INSERT INTO [t17712] VALUES (370,'٢٩٨ - ألا ليت شعري هل يلومن قومه ... زهيرا على ما جر من كل جانب','188','72','0');
INSERT INTO [t17712] VALUES (371,'٢٩٩ - ما إن رأيت ولا سمعت به ... كاليوم هانئ أينق جرب','188','72','0');
INSERT INTO [t17712] VALUES (372,'٣٠٠ - حناني ربنا وله عنونا ... نعاتبه لئن نفع العتاب','189','72','0');
INSERT INTO [t17712] VALUES (373,'٣٠١ - لقد حملت قيس بن عيلان حربها ... على مستقل للنوائب والحرب أخاها إذا كانت عضوضا سمالها ... على كل حال من ذلول ومن صعب','189','72','0');
INSERT INTO [t17712] VALUES (374,'٣٠٢ - بأعين منها مليحات النقب ... شكل التجار وحلال المكتسب','190','72','0');
INSERT INTO [t17712] VALUES (375,'٣٠٣ - وما غرني حوز الرزامي محصنا ... عواشيها بالجو وهو خصيب','190','72','0');
INSERT INTO [t17712] VALUES (376,'٣٠٤ - علي دماء البدن إن لم تفارقي ... أبا حردب ليلا وأصحاب حردب','190','72','0');
INSERT INTO [t17712] VALUES (377,'٣٠٥ - ليس بيني وبين قيس عتاب ... غير طعن الكلى وضرب الرقاب','191','72','0');
INSERT INTO [t17712] VALUES (378,'٣٠٦ - كأنك لم تذبح لأهلك نعجة ... فيصبح ملقى بالفناء إهابها','191','72','0');
INSERT INTO [t17712] VALUES (379,'٣٠٧ - عجبت والدهر كثير عجبه ... من عنزي سبني لم أضربه','191','72','0');
INSERT INTO [t17712] VALUES (380,'٣٠٨ - ديار التي كادت ونحن على منى ... تحل بنا لولا نجاء الركائب','191','72','0');
INSERT INTO [t17712] VALUES (381,'٣٠٩ - ما أنس لا أنساه آخر عيشتي ... ما لاح بالمعزاء ريع سراب','192','72','0');
INSERT INTO [t17712] VALUES (382,'٣١٠ - فوافيناهم منا بجمع ... كأسد الغاب مردان وشيب','192','72','0');
INSERT INTO [t17712] VALUES (383,'٣١١ - باتت فؤادي ذات الخال سالبة ... فالعيش إن حم لي عيش من العجب','192','72','0');
INSERT INTO [t17712] VALUES (384,'٣١٢ - يهولك أن تموت وأنت ملغ ... لما فيه النجاة من العذاب','193','72','0');
INSERT INTO [t17712] VALUES (385,'٣١٣ - صاح هل ريت أو سمعت براع ... رد في الضرع ما قرى في العلاب','193','72','0');
INSERT INTO [t17712] VALUES (386,'٣١٤ - فما أنت باليقظان ناظره إذا ... نسيت بما تهواه ذكر العواقب','193','72','0');
INSERT INTO [t17712] VALUES (387,'٣١٥ - رب حي عرندس ذي شباب ... لا يزالون ضاربين القباب','194','72','0');
INSERT INTO [t17712] VALUES (388,'٣١٦ - فه بالعقود وبالأيمان لا سيما ... عقد وفاء به من أعظم القرب','194','72','0');
INSERT INTO [t17712] VALUES (389,'٣١٧ - وكم ليلة قد بتها غير آثم ... بناحية الحجلين منعمة القلب','194','72','0');
INSERT INTO [t17712] VALUES (390,'٣١٨ - أبلغ أبا دختنوس مألكة ... عن الذي يقال م الكذب','194','72','0');
INSERT INTO [t17712] VALUES (391,'٣١٩ - أحلامكم لسقام الجهل شافية ... كما دماؤكم تشفي من الكلب','195','72','0');
INSERT INTO [t17712] VALUES (392,'٣٢٠ - كأنها من حجار الغيل ألبسها ... مضارب الماء لون الطحلب اللزب','195','72','0');
INSERT INTO [t17712] VALUES (393,'٣٢١ - أصخ مصيخا لمن أبدى نصيحته ... والزم توقي خلط الجد باللعب','195','72','0');
INSERT INTO [t17712] VALUES (394,'٣٢٢ - إذا ما المهارى بلغتنا بلادنا ... فبعد المهارى من حسير ومتعب','195','72','0');
INSERT INTO [t17712] VALUES (395,'٣٢٣ - فو الله ما نلتم وما نيل منكم ... بمعتدل وفق ولا متقارب','195','72','0');
INSERT INTO [t17712] VALUES (396,'٣٢٤ - وقالت متى يبخل عليك ويعتلل ... يسؤك وإن يكشف غرامك تدرب','196','72','0');
INSERT INTO [t17712] VALUES (397,'٣٢٥ - فإن تنأ عنها حقبة لا تلاقها ... فإنك مما أحدثت بالمجرب','196','72','0');
INSERT INTO [t17712] VALUES (398,'٣٢٦ - أحب لحبها السودان حتى ... أحب لحبها سود الكلاب','197','72','0');
INSERT INTO [t17712] VALUES (399,'٣٢٧ - لخطاب ليلى يا لبرثن منكم ... أدل وأمضى من سليك المقانب','197','72','0');
INSERT INTO [t17712] VALUES (400,'٣٢٨ - تدلت على حص ظماء كأنها ... كرات غلام من كساء مؤرنب','197','72','0');
INSERT INTO [t17712] VALUES (401,'٣٢٩ - فذر ذا ولكن هتعين متيما ... على ضوء برق آخر الليل ناصب','197','72','0');
INSERT INTO [t17712] VALUES (402,'٣٣٠ - ما المرء أخوك إن لم تلفه وزرا ... عند الكريهة معوانا على النوب','198','72','0');
INSERT INTO [t17712] VALUES (403,'٣٣١ - وأنت- أراني الله- أمنع عاصم ... وأرأف مستكفى وأسمح واهب','198','72','0');
INSERT INTO [t17712] VALUES (404,'٣٣٢ - لزجرت قلبا لا يريع إلى الصبا ... إن الغوي إذا نها لم يعتب','198','72','0');
INSERT INTO [t17712] VALUES (405,'قافية التاء','199','0','0');
INSERT INTO [t17712] VALUES (406,'١ - فساغ لي الشراب وكنت قبلا ... أكاد أغص بالماء الفرات','199','405','0');
INSERT INTO [t17712] VALUES (407,'٢ - قد كنت أحجو أبا عمرو أخا ثقة ... حتى ألمت بنا يوما ملمات','199','405','0');
INSERT INTO [t17712] VALUES (408,'٣ - وما كنت أدري قبل عزة ما البكا ... ولا موجعات القلب حتى تولت','199','405','0');
INSERT INTO [t17712] VALUES (409,'٤ - هي الخمر لا شك تكنى الطلا ... كما الذئب يكنى أبا جعدة','200','405','0');
INSERT INTO [t17712] VALUES (410,'٥ - فإن الماء ماء أبي وجدي ... وبئري ذو حفرت وذو طويت','200','405','0');
INSERT INTO [t17712] VALUES (411,'٦ - خبير بنو لهب فلا تك ملغيا ... مقالة لهبي إذا الطير مرت','201','405','0');
INSERT INTO [t17712] VALUES (412,'٧ - يا لعن الله بني السعلات ... عمرو بن ميمون شرار النات','201','405','0');
INSERT INTO [t17712] VALUES (413,'٨ - كلف من عنائه وشقوته ... بنت ثماني عشرة من حجته','202','405','0');
INSERT INTO [t17712] VALUES (414,'٩ - يا مر يا ابن واقع يا أنتا ... أنت الذي طلقت عام جعتا حتى إذا اصطبحت واغتبقتا ... أقبلت معتادا لما تركتا قد أحسن الله وقد أسأتا','202','405','0');
INSERT INTO [t17712] VALUES (415,'١٠ - والله أنجاك بكفي مسلمت ... من بعد ما، وبعد ما، وبعدمت كانت نفوس القوم عند الغلصمت ... وكادت الحرة أن تدعى أمت','202','405','0');
INSERT INTO [t17712] VALUES (416,'١١ - فلو أن الأطبا كان حولي ... وكان مع الأطباء الشفاة إذن ما أذهبوا ألما بقلبي ... وإن قيل الشفاة هم الأساة','203','405','0');
INSERT INTO [t17712] VALUES (417,'١٢ - من يك ذا بت فهذا بتي ... مقيظ مصيف مشتي','205','405','0');
INSERT INTO [t17712] VALUES (418,'١٣ - ألا عمر ولى مستطاع رجوعه ... فيرأب ما أثأت يد الغفلات','206','405','0');
INSERT INTO [t17712] VALUES (419,'١٤ - ليت- وهل ينفع شيئا ليت ... ليت شبابا بوع فاشتريت','206','405','0');
INSERT INTO [t17712] VALUES (420,'١٥ - كلا أخي وخليلي واجدي عضدا ... وفي النائبات وإلمام الملمات','206','405','0');
INSERT INTO [t17712] VALUES (421,'١٦ - يا قوم قد حوقلت أو دنوت ... وشر حيقال الرجال الموت','207','405','0');
INSERT INTO [t17712] VALUES (422,'١٧ - ألا رجلا جزاه الله خيرا ... يدل على محصلة تبيت','207','405','0');
INSERT INTO [t17712] VALUES (423,'١٨ - ربما أوفيت في علم ... ترفعن ثوبي شمالات','208','405','0');
INSERT INTO [t17712] VALUES (424,'١٩ - ألا أبلغ أبا إسحق أني ... رأيت البلق دهما مصمتات أري عيني ما لم ترأياه ... كلانا عالم بالترهات كفرت بوحيكم وجعلت نذرا ... علي قتالكم حتى الممات','209','405','0');
INSERT INTO [t17712] VALUES (425,'٢٠ - حنت نوار ولات هنا حنت ... وبدا الذي كانت نوار أجنت','209','405','0');
INSERT INTO [t17712] VALUES (426,'٢١ - وإني- وتهيامي بعزة بعد ما ... تخليت فيما بيننا وتخلت لكالمرتجي ظل الغمامة كلما ... تبوأ منها للمقيل اضمحلت','210','405','0');
INSERT INTO [t17712] VALUES (427,'٢٢ - ألا إن قتلى الطف من آل هاشم ... أذلت رقاب المسلمين فذلت أولئك قوم لم يشيموا سيوفهم ... ولم تكثر القتلى بها حين سلت','210','405','0');
INSERT INTO [t17712] VALUES (428,'٢٣ - وكنت كذي رجلين رجل صحيحة ... ورجل رمى فيها الزمان فشلت','211','405','0');
INSERT INTO [t17712] VALUES (429,'٢٤ - وأي فتى هيجاء أنت وجارها ... إذا ما رجال بالرجال استقلت','212','405','0');
INSERT INTO [t17712] VALUES (430,'٢٥ - علام تقول: الرمح يثقل عاتقي ... إذا أنا لم أطعن إذا الخيل كرت','213','405','0');
INSERT INTO [t17712] VALUES (431,'٢٦ - بعد اللتيا واللتيا والتي ... إذا علتها أنفس تردت','214','405','0');
INSERT INTO [t17712] VALUES (432,'٢٧ - عل صروف الدهر أو دولاتها ... تدلننا اللمة من لماتها فتستريح النفس من زفراتها','214','405','0');
INSERT INTO [t17712] VALUES (433,'٢٨ - شهدت بأن قد خط ما هو كائن ... وأنك تمحو ما تشاء وتثبت','215','405','0');
INSERT INTO [t17712] VALUES (434,'٢٩ - أفي الولائم أولادا لواحدة ... وفي العيادة أولادا لعلات','215','405','0');
INSERT INTO [t17712] VALUES (435,'٣٠ - رحم الله أعظما دفنوها ... بسجستان طلحة الطلحات','215','405','0');
INSERT INTO [t17712] VALUES (436,'٣١ - إذا روح الراعي اللقاح معزبا ... وأمست على آنافها عبراتها','215','405','0');
INSERT INTO [t17712] VALUES (437,'٣٢ - فلست أبالي بعد موت مطرف ... حتوف المنايا أكثرت أو أقلت','216','405','0');
INSERT INTO [t17712] VALUES (438,'٣٣ - أتيت مهاجرين فعلموني ... ثلاثة أحرف متتابعات وخطوا لي أبا جاد وقالوا ... تعلم صعفصا وقريشيات','216','405','0');
INSERT INTO [t17712] VALUES (439,'٣٤ - لحا الله جرما كلما ذر شارق ... وجوه كلاب هارشت فازبأرت','216','405','0');
INSERT INTO [t17712] VALUES (440,'٣٥ - قل لابن قيس أخي الرقيات ... ما أحسن العرف في المصيبات','217','405','0');
INSERT INTO [t17712] VALUES (441,'٣٦ - فمن يك سائلا عني فإني ... بمكة مولدي وبها ربيت وقد ربيت بها الآباء قبلي ... فما شنئت أبي وما شنيت','217','405','0');
INSERT INTO [t17712] VALUES (442,'٣٧ - يا لقوم لزفرة الزفرات ... ولعين كثيرة العبرات','217','405','0');
INSERT INTO [t17712] VALUES (443,'٣٨ - زعمت تماضر أنني إما أمت ... يسدد أبينوها الأصاغر خلتي','217','405','0');
INSERT INTO [t17712] VALUES (444,'٣٩ - ألا يا بيت بالعلياء بيت ... ولولا حب أهلك ما أتيت','218','405','0');
INSERT INTO [t17712] VALUES (445,'٤٠ - ليت شعري وأشعرن إذا ما ... قربوها منشورة ودعيت','218','405','0');
INSERT INTO [t17712] VALUES (446,'٤١ - يا أيها الراكب المزجي مطيته ... سائل بني أسد ما هذه الصوت','218','405','0');
INSERT INTO [t17712] VALUES (447,'٤٢ - ألا يا ليتني والمرء ميت ... وما يغني عن الحدثان ليت','218','405','0');
INSERT INTO [t17712] VALUES (448,'٤٣ - في فتو أنا كالئهم ... في بلايا عورة باتوا','218','405','0');
INSERT INTO [t17712] VALUES (449,'٤٤ - أبلغ أمير المؤمنين ... - أخا العراق- إذا أتيتا أن العراق وأهله ... سلم إليك فهيت هيتا','219','405','0');
INSERT INTO [t17712] VALUES (450,'٤٥ - قلت إني كأنت ثمة لما ... شبت الحرب خضتها وكععتا','219','405','0');
INSERT INTO [t17712] VALUES (451,'٤٦ - وذلك حين لات أوان حلم ... ولكن قبلها اجتنبوا أذاتي','220','405','0');
INSERT INTO [t17712] VALUES (452,'٤٧ - من كان أسرع في تفرق فالج ... فلبونه جربت معا وأغدت إلا كناشرة الذي ضيعتم ... كالغصن في غلوائه المتنبت','220','405','0');
INSERT INTO [t17712] VALUES (453,'٤٨ - ولقد رأبت ثأى العشيرة بينها ... وكفيت جانيها اللتيا والتي','220','405','0');
INSERT INTO [t17712] VALUES (454,'٤٩ - وكأن في العينين حب قرنفل ... أو سنبلا كحلت به فانهلت','221','405','0');
INSERT INTO [t17712] VALUES (455,'٥٠ - كأن بها البدر ابن عشر وأربع ... إذا هبوات الصيف عنها تجلت','221','405','0');
INSERT INTO [t17712] VALUES (456,'٥١ - إن العداوة تستحيل مودة ... بتدارك الهفوات بالحسنات','221','405','0');
INSERT INTO [t17712] VALUES (457,'٥٢ - فرم بيديك هل تسطيع نقلا ... جبالا من تهامة راسيات','221','405','0');
INSERT INTO [t17712] VALUES (458,'٥٣ - وإذا العذارى بالدخان تقنعت ... واستعجلت نصب القدور فملت دارت بأرزاق العفاة مغالق ... بيدي من قمع العشار الجلة','221','405','0');
INSERT INTO [t17712] VALUES (459,'٥٤ - ألا ليت شعري ما يقولن فوارس ... إذا حارب الهام المصيح هامتي','222','405','0');
INSERT INTO [t17712] VALUES (460,'٥٥ - لو صنت طرفك لم ترع بصفاتها ... لما بدت مجلوة وجناتها','222','405','0');
INSERT INTO [t17712] VALUES (461,'قافية الثاء','223','0','0');
INSERT INTO [t17712] VALUES (462,'١ - فعادى بين هاديتين منها ... وأولى أن يزيد على الثلاث','223','461','0');
INSERT INTO [t17712] VALUES (463,'٢ - كم عمة لك يا خليد وخالة ... خضر نواجذها من الكراث','223','461','0');
INSERT INTO [t17712] VALUES (464,'٣ - لروضة من رياض الحزن أو طرف ... من القرية حزن غير محروث أشهى وأحلى لعيني إن مررت به ... من كرخ بغداد ذي الرمان والتوث','223','461','0');
INSERT INTO [t17712] VALUES (465,'٤ - فأنت طلاق والطلاق ألية ... بها المرء ينجو من شباك الطوامث','224','461','0');
INSERT INTO [t17712] VALUES (466,'٥ - متى ما تنكروها تعرفوها ... متى أقطارها علق نفيث','224','461','0');
INSERT INTO [t17712] VALUES (467,'٦ - الحمد لله الذي ... لم يخلق الخلق عبث ولم يخلنا سدى ... من بعد عيسى واكترث أرسل فينا أحمدا ... خير نبي قد بعث صلى عليه الله ما ... حج له ركب وحث','224','461','0');
INSERT INTO [t17712] VALUES (468,'قافية الجيم','225','0','0');
INSERT INTO [t17712] VALUES (469,'١ - متى تأتنا تلمم بنا في ديارنا ... تجد حطبا جزلا ونارا تأججا','225','468','0');
INSERT INTO [t17712] VALUES (470,'٢ - سقى أم عمرو كل آخر ليلة ... حناتم سود ماؤهن ثجيج شربن بماء البحر ثم ترفعت ... متى لجج خضر لهن نئيج','225','468','0');
INSERT INTO [t17712] VALUES (471,'٣ - أومت بعينيها من الهودج ... لولاك في ذا العام لم أحجج أنت إلى مكة أخرجتني ... ولو تركت الحج لم أخرج','226','468','0');
INSERT INTO [t17712] VALUES (472,'٤ - عشية سعدى لو تراءت لراهب ... بدومة تجر دونه وحجيج قلى دينه واهتاج للشوق إنها ... على الشوق إخوان العزاء هيوج','226','468','0');
INSERT INTO [t17712] VALUES (473,'٥ - نحن بنو جعدة أرباب الفلج ... نضرب بالسيف ونرجو بالفرج','227','468','0');
INSERT INTO [t17712] VALUES (474,'٦ - أخيل برقا متى حاب له زجل ... إذا تفتر من توماضه حلجا','227','468','0');
INSERT INTO [t17712] VALUES (475,'٧ - قالت وعيش أبي وحرمة إخوتي ... لأنبهن الحي إن لم تخرج فخرجت خيفة قولها فتبسمت ... فعلمت أن يمينها لم تحرج فلثمت فاها آخذا بقرونها ... شرب النزيف ببرد ماء الحشرج','228','468','0');
INSERT INTO [t17712] VALUES (476,'٨ - إني أتيحت لي يمانية ... إحدى بني الحارث من مذحج تمكث حولا كاملا كله ... لا نلتقي إلا على منهج الحج إن حجت وماذا منى ... وأهله إن هي لم تحجج','228','468','0');
INSERT INTO [t17712] VALUES (477,'٩ - أخلق بذي الصبر أن يحظى بحاجته ... ومدمن القرع للأبواب أن يلجا','229','468','0');
INSERT INTO [t17712] VALUES (478,'١٠ - أضحت ينفرها الولدان من سبأ ... كأنهم تحت دفيها دحاريج','229','468','0');
INSERT INTO [t17712] VALUES (479,'١١ - ودوية قفر تمشي نعامها ... كمشي النصارى في خفاف الأرندج','229','468','0');
INSERT INTO [t17712] VALUES (480,'١٢ - أما النهار ففي قيد وسلسلة ... والليل في قعر منحوت من الساج','230','468','0');
INSERT INTO [t17712] VALUES (481,'١٣ - كأن أصوات- من إيغالهن بنا- ... أواخر الميس أصوات الفراريج','230','468','0');
INSERT INTO [t17712] VALUES (482,'١٤ - وأما قولك الخلفاء منا ... فهم منعوا وريدك من وداج ولولاهم لكنت كحوت بحر ... هوى في مظلم الغمرات داج وكنت أذل من وتد بقاع ... يشجج رأسه بالفهر واجي','230','468','0');
INSERT INTO [t17712] VALUES (483,'١٥ - يحدو ثماني مولعا بلقاحها ... حتى هممن بزيغة الإرتاج','231','468','0');
INSERT INTO [t17712] VALUES (484,'١٦ - ما زال يوقن من يؤمك بالغنى ... وسواك مانع- فضله- المحتاج','231','468','0');
INSERT INTO [t17712] VALUES (485,'١٧ - يا رب بيضاء من العواهج ... أم صبي قد حبا أو دارج','231','468','0');
INSERT INTO [t17712] VALUES (486,'١٨ - فيا ليتي إذا ما كان ذاكم ... ولجت وكنت أولهم ولوجا','231','468','0');
INSERT INTO [t17712] VALUES (487,'١٩ - ألا سبيل إلى خمر فأشربها ... أم لا سبيل إلى نصر بن حجاج','232','468','0');
INSERT INTO [t17712] VALUES (488,'٢٠ - ولم أر شيئا بعد ليلى ألذه ... ولا مشربا أروى به فأعيج','233','468','0');
INSERT INTO [t17712] VALUES (489,'٢١ - يا ليت شعري عن نفسي أزاهقة ... مني ولم أقض ما فيها من الحاج','233','468','0');
INSERT INTO [t17712] VALUES (490,'٢٢ - فتى ليس بالراضي بأدنى معيشة ... ولا في بيوت الحي بالمتولج','233','468','0');
INSERT INTO [t17712] VALUES (491,'٢٣ - قطعت إلى معروفها منكراتها ... إذا خب آل الأمعز المتوهج','233','468','0');
INSERT INTO [t17712] VALUES (492,'٢٤ - كأنما ضربت قدام أعينها ... قطنا بمستحصد الأوتار محلوج','234','468','0');
INSERT INTO [t17712] VALUES (493,'٢٥ - رأى الناس- إلا من رأى مثل رأيه- ... خوارج تراكين قصد المخارج','234','468','0');
INSERT INTO [t17712] VALUES (494,'٢٦ - يا عديا لقلبك المهتاج ... أن عفا رسم منزل بالنباج','234','468','0');
INSERT INTO [t17712] VALUES (495,'٢٧ - نحن- بني جعدة أرباب الفلج ... نضرب بالسيف ونرجو بالفرج','235','468','0');
INSERT INTO [t17712] VALUES (496,'حرف الحاء المهملة','236','0','0');
INSERT INTO [t17712] VALUES (497,'١ - إذا غير النأي المحبين لم يكد ... رسيس الهوى من حب مية يبرح','236','496','0');
INSERT INTO [t17712] VALUES (498,'٢ - أفي أثر الأظعان عينك تلمح ... نعم لات هنا إن قلبك متيح','237','496','0');
INSERT INTO [t17712] VALUES (499,'٣ - كأن لم يمت حي سواك ولم تقم ... على أحد إلا عليك النوائح','237','496','0');
INSERT INTO [t17712] VALUES (500,'٤ - أتى دونها ذب الرياد كأنه ... فتى فارسي في سراويل رامح','238','496','0');
INSERT INTO [t17712] VALUES (501,'٥ - سواء عليك اليوم أنصاعت النوى ... بصيداء أم أنحى لك السيف ذابح','239','496','0');
INSERT INTO [t17712] VALUES (502,'٦ - وإني لأكنو عن قذور بغيرها ... وأعرب أحيانا بها فأصارح','239','496','0');
INSERT INTO [t17712] VALUES (503,'٧ - أخو بيضات رائح متأوب ... رفيق بمسح المنكبين سبوح','239','496','0');
INSERT INTO [t17712] VALUES (504,'٨ - رمى الله في عيني بثينة بالقذى ... وفي الغر من أنيابها بالقوادح','239','496','0');
INSERT INTO [t17712] VALUES (505,'٩ - وانضح جوانب قبره بدمائها ... فلقد يكون أخا دم وذبائح','240','496','0');
INSERT INTO [t17712] VALUES (506,'١٠ - يا لقومي من للعلا والمساعي ... يا لقومي من للندى والسماح يا لعطافنا ويا لرياح ... وأبي الحشرج الفتى النفاح','241','496','0');
INSERT INTO [t17712] VALUES (507,'١١ - وأنت من الغوائل حين ترمى ... ومن ذم الرجال بمنتزاح','241','496','0');
INSERT INTO [t17712] VALUES (508,'١٢ - فتى ما ابن الأغر إذا شتونا ... وحب الزاد في شهري قماح','242','496','0');
INSERT INTO [t17712] VALUES (509,'١٣ - إن السماحة والمروءة ضمنا ... قبرا بمرو على الطريق الواضح','242','496','0');
INSERT INTO [t17712] VALUES (510,'١٤ - أخاك أخاك إن من لا أخا له ... كساع إلى الهيجا بغير سلاح','242','496','0');
INSERT INTO [t17712] VALUES (511,'١٥ - سأترك منزلي لبني تميم ... وألحق بالحجاز فأستريحا','243','496','0');
INSERT INTO [t17712] VALUES (512,'١٦ - يا ناق سيري عنقا فسيحا ... إلى سليمان فنستريحا','243','496','0');
INSERT INTO [t17712] VALUES (513,'١٧ - فكلتاهما قد خط لي في صحيفة ... فلا العيش أهواه ولا الموت أروح','243','496','0');
INSERT INTO [t17712] VALUES (514,'١٨ - بدت مثل قرن الشمس في رونق الضحى ... وصورتها أو أنت في العين أملح','243','496','0');
INSERT INTO [t17712] VALUES (515,'١٩ - ربع عفاه الدهر طولا فانمحى ... قد كاد من طول البلى أن يمصحا','244','496','0');
INSERT INTO [t17712] VALUES (516,'٢٠ - يا ليت بعلك قد غدا ... متقلدا سيفا ورمحا','244','496','0');
INSERT INTO [t17712] VALUES (517,'٢١ - أبت لي عفتي وأبى بلائي ... وأخذي الحمد بالثمن الربيح وإمساكي على المكروه نفسي ... وضربي هامة البطل المشيح وقولي كلما جشأت وجاشت ... مكانك تحمدي أو تستريحي لأدفع عن مآثر صالحات ... وأحمي بعد عن عرض صحيح','244','496','0');
INSERT INTO [t17712] VALUES (518,'٢٢ - نحن اللذون صبحوا الصباحا ... يوم النخيل غارة ملحاحا','245','496','0');
INSERT INTO [t17712] VALUES (519,'٢٣ - وقد كنت تخفي حب سمراء حقبة ... فبح لان منها بالذي أنت بائح','245','496','0');
INSERT INTO [t17712] VALUES (520,'٢٤ - هلا سألت النبيتيين ما حسبي ... عند الشتاء إذا ما هبت الريح ورد جازرهم حزما مصرمة ... في الرأس منها وفي الأصلاء تمليح إذا اللقاح غدت ملقى أصرتها ... ولا كريم من الولدان مصبوح','245','496','0');
INSERT INTO [t17712] VALUES (521,'٢٥ - إذا سايرت أسماء يوما ظعينة ... فأسماء من تلك الظعينة أملح','246','496','0');
INSERT INTO [t17712] VALUES (522,'٢٦ - ولو أن ليلى الأخيلية سلمت ... علي ودوني جندل وصفائح لسلمت تسليم البشاشة أو زقا ... إليها صدى من جانب القبر صائح','246','496','0');
INSERT INTO [t17712] VALUES (523,'٢٧ - الآن بعد لجاجتي تلحونني ... هلا التقدم والقلوب صحاح','247','496','0');
INSERT INTO [t17712] VALUES (524,'٢٨ - وضيف جاءنا والليل داج ... وريح القر تحفز منه روحا فطرت بمنصلي في يعملات ... خفاف الأيد يخبطن السريحا','247','496','0');
INSERT INTO [t17712] VALUES (525,'٢٩ - تغيرت البلاد ومن عليها ... فوجه الأرض مغبر قبيح تغير كل ذي طعم ولون ... وقل بشاشة الوجه المليح','248','496','0');
INSERT INTO [t17712] VALUES (526,'٣٠ - فما حسن أن يمدح المرء نفسه ... ولكن أخلاقا تذم وتمدح','248','496','0');
INSERT INTO [t17712] VALUES (527,'٣١ - دامن سعدك لو رحمت متيما ... لولاك لم يك للصبابة جانحا','248','496','0');
INSERT INTO [t17712] VALUES (528,'٣٢ - لئن كانت الدنيا علي كما أرى ... تباريح من ليلى فللموت أروح','249','496','0');
INSERT INTO [t17712] VALUES (529,'٣٣ - أتقرح أكباد المحبين كالذي ... أرى كبدي من حب مية تقرح','249','496','0');
INSERT INTO [t17712] VALUES (530,'٣٤ - رأيت رجالا يكرهون بناتهم ... وفيهن- لا تكذب- نساء صوالح وفيهن- والأيام يعثرن بالفتى- ... عوائد لا يمللنه ونوائح','249','496','0');
INSERT INTO [t17712] VALUES (531,'٣٥ - فلا، وأبي دهماء، زالت عزيزة ... على قومها ما فتل الزند قادح','250','496','0');
INSERT INTO [t17712] VALUES (532,'٣٦ - لزمنا لدن سالمتمونا وفاقكم ... فلا يك منكم للخلاف جنوح','250','496','0');
INSERT INTO [t17712] VALUES (533,'٣٧ - تركت بنا لوحا ولو شئت جادنا ... بعيد الكرى ثلج بكرمان ناصح','250','496','0');
INSERT INTO [t17712] VALUES (534,'٣٨ - ليبك يزيد ضارع لخصومة ... ومختبط مما تطيح الطوائح','251','496','0');
INSERT INTO [t17712] VALUES (535,'٣٩ - وكان سيان أن لا يسرحوا نعما ... أو يسرحوه بها واغبرت السوح','251','496','0');
INSERT INTO [t17712] VALUES (536,'٤٠ - نهيتك عن طلابك أم عمرو ... بعاقبة وأنت إذ صحيح','252','496','0');
INSERT INTO [t17712] VALUES (537,'٤١ - فقد والله بين لي عنائي ... بوشك فراقهم صرد يصيح','252','496','0');
INSERT INTO [t17712] VALUES (538,'٤٢ - يا بؤس للحرب التي ... وضعت أراهط فاستراحوا','252','496','0');
INSERT INTO [t17712] VALUES (539,'٤٣ - من صد عن نيرانها ... فأنا ابن قيس لا براح','253','496','0');
INSERT INTO [t17712] VALUES (540,'٤٤ - ما باله لاحظته فتضرجت ... وجناته وفؤادي المجروح ورمى وما رمتا يداه فصابني ... سهم يعذب والسهام تريح','253','496','0');
INSERT INTO [t17712] VALUES (541,'٤٥ - وقبل غد يا لهف نفسي من غد ... إذا راح أصحابي ولست برائح','254','496','0');
INSERT INTO [t17712] VALUES (542,'٤٦ - عسى طيئ من طيئ بعد هذه ... ستطفئ غلات الكلى والجوانح','254','496','0');
INSERT INTO [t17712] VALUES (543,'٤٧ - ألستم خير من ركب المطايا ... وأندى العالمين بطون راح','255','496','0');
INSERT INTO [t17712] VALUES (544,'٤٨ - أبحت حمى تهامة بعد نجد ... وما شيء حميت بمستباح','255','496','0');
INSERT INTO [t17712] VALUES (545,'٤٩ - فما أدري وظني كل ظن ... أمسلمني إلى قوم شراح','256','496','0');
INSERT INTO [t17712] VALUES (546,'٥٠ - هم اللاؤون فكوا الغل عني ... بمرو الشاهجان وهم جناحي','256','496','0');
INSERT INTO [t17712] VALUES (547,'٥١ - فإن لا مال أعطيه فإني ... صديق من غدو أو رواح','256','496','0');
INSERT INTO [t17712] VALUES (548,'٥٢ - لو أن حيا مدرك الفلاح ... أدركه ملاعب الرماح','256','496','0');
INSERT INTO [t17712] VALUES (549,'٥٣ - بعيد الغزاة فما إن يزا ... ل مضطمر طرتاه طليحا','257','496','0');
INSERT INTO [t17712] VALUES (550,'٥٤ - دأبت إلى أن ينبت الظل بعد ما ... تقاصر حتى كاد في الآل يمصح وجيف المطايا ثم قلت لصحبتي ... ولم ينزلوا أبردتم فتروحوا','257','496','0');
INSERT INTO [t17712] VALUES (551,'٥٥ - وما الدهر إلا تارتان فمنهما ... أموت وأخرى أبتغي العيش أكدح','257','496','0');
INSERT INTO [t17712] VALUES (552,'٥٦ - إذا لقي الأعداء كان خلاتهم ... وكلب على الأدنين والجار نابح','257','496','0');
INSERT INTO [t17712] VALUES (553,'٥٧ - وعلمي بأسدام المياه فلم تزل ... قلائص تخدي في طريق طلائح وأني إذا ملت ركابي مناخها ... فإني على حظي من الأمر جامح','258','496','0');
INSERT INTO [t17712] VALUES (554,'٥٨ - فإن تمس في قبر برهوة ثاويا ... أنيسك أصداء القبور تصيح','258','496','0');
INSERT INTO [t17712] VALUES (555,'٥٩ - بل هل أريك حمول الحي غادية ... كالنخل زينها ينع وإفضاح','259','496','0');
INSERT INTO [t17712] VALUES (556,'٦٠ - والحرب لا يبقى لجا ... حمها التخيل والمراح إلا الفتى الصبار في الن ... جدات والفرس الوقاح','259','496','0');
INSERT INTO [t17712] VALUES (557,'٦١ - إن ترينا قليلين كما ذي ... دعن المجربين ذود صحاح','259','496','0');
INSERT INTO [t17712] VALUES (558,'٦٢ - ألا رب من قلبي له الله ناصح ... ومن هو عندي في الظباء السوانح','259','496','0');
INSERT INTO [t17712] VALUES (559,'٦٣ - وارتشن حين أردن أن يرميننا ... نبلا بلا ريش ولا بقداح ونظرن من خلل الخدور بأعين ... مرضى مخالطها السقام صحاح','260','496','0');
INSERT INTO [t17712] VALUES (560,'٦٤ - يا لقوم من للعلى والمساعي ... يا لقوم من للندى والسماح','260','496','0');
INSERT INTO [t17712] VALUES (561,'٦٥ - ونبكي على زيد ولا زيد مثله ... بريء من الحمى سليم الجوانح','260','496','0');
INSERT INTO [t17712] VALUES (562,'٦٦ - إن قوما منهم عمير وأشبا ... هـ عمير ومنهم السفاح لجديرون بالوفاء إذا قا ... ل أخو النجدة: السلاح السلاح','261','496','0');
INSERT INTO [t17712] VALUES (563,'٦٧ - لها أذن حشر وذفرى أسيلة ... ووجه كمرآة الغريبة أسجح','261','496','0');
INSERT INTO [t17712] VALUES (564,'٦٨ - لو كان مدحة حي منشرا أحدا ... أحيا أباكن يا ليلى الأماديح','261','496','0');
INSERT INTO [t17712] VALUES (565,'٦٩ - فبينا الفتى في ظل نعماء غضة ... تباكره أفياؤها وتراوح إلى أن رمته الحادثات بنكبة ... يضيق بها منه الرحاب الفسائح','261','496','0');
INSERT INTO [t17712] VALUES (566,'٧٠ - وإن من النسوان من هي روضة ... تهيج الرياض قبلها وتصوح','262','496','0');
INSERT INTO [t17712] VALUES (567,'٧١ - لولا زهير جفاني كنت منتصرا ... ولم أكن جانحا للسلم إن جنحوا','262','496','0');
INSERT INTO [t17712] VALUES (568,'٧٢ - بنا أبدا لا غيرنا يدرك المنى ... وتكشف غماء الخطوب الفوادح','262','496','0');
INSERT INTO [t17712] VALUES (569,'٧٣ - وما أنا من رزء وإن- جل- جازع ... ولا بسرور بعد موتك فارح','262','496','0');
INSERT INTO [t17712] VALUES (570,'٧٤ - فكأنما نظروا إلى قمر ... أو حيث علق قوسه قزح','263','496','0');
INSERT INTO [t17712] VALUES (571,'٧٥ - أقام ببغداد العراق وشوقه ... لأهل دمشق الشام شوق مبرح','263','496','0');
INSERT INTO [t17712] VALUES (572,'٧٦ - لقد كان لي عن ضرتين- عدمتني ... وعما ألاقي منهما متزحزح','263','496','0');
INSERT INTO [t17712] VALUES (573,'٧٧ - ألا إن جيراني العشية رائح ... دعتهم دواع للهوى ومنادح','263','496','0');
INSERT INTO [t17712] VALUES (574,'٧٨ - مرت بنا في نسوة خولة ... والمسك من أردانها نافحه','264','496','0');
INSERT INTO [t17712] VALUES (575,'٧٩ - دان مسف فويق الأرض هيدبه ... يكاد يدفعه من قام بالراح','264','496','0');
INSERT INTO [t17712] VALUES (576,'٨٠ - قلت لقوم في الكنيف تروحوا ... عشية بتنا عند ما وان رزح','264','496','0');
INSERT INTO [t17712] VALUES (577,'٨١ - بينا كذاك رأيتني متلفعا ... بالبرد فوق جلالة سرداح','264','496','0');
INSERT INTO [t17712] VALUES (578,'٨٢ - أريد صلاحها وتريد قتلي ... وشتا بين قتلي والصلاح','264','496','0');
INSERT INTO [t17712] VALUES (579,'٨٣ - جاء شقيق عارضا رمحه ... إن بني عمك فيهم رماح','265','496','0');
INSERT INTO [t17712] VALUES (580,'٨٤ - إنما نحن كشيء فاسد ... فإذا أصلحه الله صلح','265','496','0');
INSERT INTO [t17712] VALUES (581,'قافية الخاء','266','0','0');
INSERT INTO [t17712] VALUES (582,'١ - إذا الرجال شتوا واشتد أكلهم ... فأنت أبيضهم سربال طباخ','266','581','0');
INSERT INTO [t17712] VALUES (583,'٢ - ألا يا غراب البين قد هجت لوعة ... فويحك خبرني بما أنت تصرخ أبا لبين من لبنى؟ فإن كنت صادقا ... فلا زال عظم من جناحك يفضخ ولا زلت من عذب المياه منفرا ... ووكرك مهدوم وبيضك مشدخ ولا زال رام قد أصابك سهمه ... فلا أنت في أمن ولا أنت تفرخ وأبصر','266','581','0');
INSERT INTO [t17712] VALUES (584,'٣ - والله لولا أن تحش الطبخ ... بي الجحيم حين لا مستصرخ','266','581','0');
INSERT INTO [t17712] VALUES (585,'قافية الدال المهملة','268','0','0');
INSERT INTO [t17712] VALUES (586,'١ - وفي كتب الحجاج أنساب معشر ... تعلمها، منا يزيد ومزيدا','268','585','0');
INSERT INTO [t17712] VALUES (587,'٢ - إنما أم خالد يوم جاءت ... بغلة الزينبي من قصر زيدا','268','585','0');
INSERT INTO [t17712] VALUES (588,'٣ - إنا بني تغلب قوم معاقلنا ... بيض السيوف إذا ما أقرع البلد','268','585','0');
INSERT INTO [t17712] VALUES (589,'٤ - فإن لم أصدق ظنهم بتيقن ... فلا سقت الأوصال مني الرواعد ويعلم أعدائي من الناس أنني ... أنا الفارس الحامي الذمار المذاود','268','585','0');
INSERT INTO [t17712] VALUES (590,'٥ - يا قل خير الغواني كيف رغن به ... فشربه وشل فيه وتصريد','268','585','0');
INSERT INTO [t17712] VALUES (591,'٦ - ما أنت لي قائما فتجبرني ... ولا أمير علي مقتلد','269','585','0');
INSERT INTO [t17712] VALUES (592,'٧ - آت الرزق يوم يوم فأجمل ... طلبا وابغ للقيامة زادا','269','585','0');
INSERT INTO [t17712] VALUES (593,'٨ - تباعد مني فطحل إذ سألته ... أمين فزاد الله ما بيننا بعدا','269','585','0');
INSERT INTO [t17712] VALUES (594,'٩ - سعاد التي أضناك حب سعادا ... وإعراضها عنك استمر وزادا','269','585','0');
INSERT INTO [t17712] VALUES (595,'١٠ - ألا أيهذا الزاجري أحضر الوغى ... وأن أشهد اللذات هل أنت مخلدي','270','585','0');
INSERT INTO [t17712] VALUES (596,'١١ - لقد علم الأقوام ما كان داءها ... بثهلان إلا الخزي ممن يقودها','270','585','0');
INSERT INTO [t17712] VALUES (597,'١٢ - فيا ساريا بالليل لا تخش ضلة ... سعيد بن سلم ضوء كل بلاد','270','585','0');
INSERT INTO [t17712] VALUES (598,'١٣ - ألم تر أنا بني دارم ... زرارة فينا أبو معبد','270','585','0');
INSERT INTO [t17712] VALUES (599,'١٤ - كأنه خارجا من جنب صفحته ... سفود شرب نسوه عند مفتأد','270','585','0');
INSERT INTO [t17712] VALUES (600,'١٥ - فإني لآتيكم بشكري ما مضى ... من العرف واستيجاب ما كان في غد','271','585','0');
INSERT INTO [t17712] VALUES (601,'١٦ - كنا ثمانية وكانوا جحفلا ... لجبا فشلوا بالرماح بداد','271','585','0');
INSERT INTO [t17712] VALUES (602,'١٧ - أبت قضاعة أن تعرف لكم نسبا ... وابنا نزار، فأنتم بيضة البلد','271','585','0');
INSERT INTO [t17712] VALUES (603,'١٨ - ثلاث كلهن قتلت عمدا ... فأخزى الله رابعة تعود','271','585','0');
INSERT INTO [t17712] VALUES (604,'١٩ - أرى الحاجات عند أبي خبيب ... نكدن ولا أمية في البلاد','271','585','0');
INSERT INTO [t17712] VALUES (605,'٢٠ - لنا معشر الأنصار مجد مؤثل ... بإرضائنا خير البرية أحمدا','272','585','0');
INSERT INTO [t17712] VALUES (606,'٢١ - جزى الله رب الناس خير جزائه ... رفيقين قالا خيمتي أم معبد هما نزلا بالبر ثم ترحلا ... فأفلح من أمسى رفيق محمد فيا لقصي ما زوى الله عنكم ... به من فعال لا تجازى وسؤدد','272','585','0');
INSERT INTO [t17712] VALUES (607,'٢٢ - كادت النفس أن تفيض عليه ... مذ ثوى حشو ريطة وبرود','273','585','0');
INSERT INTO [t17712] VALUES (608,'٢٣ - أعد نظرا يا عبد قيس لعلما ... أضاءت لك النار الحمار المقيدا','273','585','0');
INSERT INTO [t17712] VALUES (609,'٢٤ - قالت ألا ليتما هذا الحمام لنا ... إلى حمامتنا أو نصفه فقد','273','585','0');
INSERT INTO [t17712] VALUES (610,'٢٥ - ودوية مثل السماء اعتسفتها ... وقد صبغ الليل الحصى بسواد','274','585','0');
INSERT INTO [t17712] VALUES (611,'٢٦ - ولست بحلال التلاع مخافة ... ولكن متى يسترفد القوم أرفد','274','585','0');
INSERT INTO [t17712] VALUES (612,'٢٧ - إذا ما انتسبنا لم تلدني لئيمة ... ولم تجدي من أن تقري بها بدا','274','585','0');
INSERT INTO [t17712] VALUES (613,'٢٨ - دريت الوفي العهد يا عرو فاغتبط ... فإن اغتباطا بالوفاء حميد','275','585','0');
INSERT INTO [t17712] VALUES (614,'٢٩ - تعلم رسول الله أنك مدركي ... وأن وعيدا منك كالأخذ باليد','275','585','0');
INSERT INTO [t17712] VALUES (615,'٣٠ - وسميته يحيى ليحيا فلم يكن ... لأمر قضاه الله في الناس من بد','276','585','0');
INSERT INTO [t17712] VALUES (616,'٣١ - أتاني أنهم مزقون عرضي ... جحاش الكرملين لها فديد','276','585','0');
INSERT INTO [t17712] VALUES (617,'٣٢ - لأن ثواب الله كل موحد ... جنان من الفردوس فيها يخلد','276','585','0');
INSERT INTO [t17712] VALUES (618,'٣٣ - أرجو وأخشى وأدعو الله مبتغيا ... عفوا وعافية في الروح والجسد','276','585','0');
INSERT INTO [t17712] VALUES (619,'٣٤ - إذا كنت ترضيه ويرضيك صاحب ... جهارا فكن في الغيب أحفظ للود','277','585','0');
INSERT INTO [t17712] VALUES (620,'٣٥ - هل تعرفون لباناتي فأرجو أن ... تقضى فيرتد بعض الروح للجسد','277','585','0');
INSERT INTO [t17712] VALUES (621,'٣٦ - ستبدي لك الأيام ما كنت جاهلا ... ويأتيك بالأخبار من لم تزود','277','585','0');
INSERT INTO [t17712] VALUES (622,'٣٧ - ليس على الله بمستنكر ... أن يجمع العالم في واحد','277','585','0');
INSERT INTO [t17712] VALUES (623,'٣٨ - أمست خلاء وأمسى أهلها احتملوا ... أخنى عليها الذي أخنى على لبد','277','585','0');
INSERT INTO [t17712] VALUES (624,'٣٩ - تطاول ليلك بالإثمد ... وبات الخلي ولم ترقد وبات وباتت له ليلة ... كليلة ذي العائر الأرمد وذلك من نبأ جاءني ... وخبرته عن بني الأسود','278','585','0');
INSERT INTO [t17712] VALUES (625,'٤٠ - أزف الترحل غير أن ركابنا ... لما تزل برحالنا وكأن قد','278','585','0');
INSERT INTO [t17712] VALUES (626,'٤١ - رأيت الله أكبر كل شيء ... محاولة وأكثرهم جنودا','278','585','0');
INSERT INTO [t17712] VALUES (627,'٤٢ - يا ابن أمي ويا شقيق نفسي ... أنت خلفتني لدهر شديد','279','585','0');
INSERT INTO [t17712] VALUES (628,'٤٣ - فما كعب ابن مامة وابن أروى ... بأجود منك يا عمر الجوادا','279','585','0');
INSERT INTO [t17712] VALUES (629,'٤٤ - يا لقومي ويا لأمثال قومي ... لأناس عتوهم في ازدياد','279','585','0');
INSERT INTO [t17712] VALUES (630,'٤٥ - تألى ابن أوس حلفة ليردني ... إلى نسوة كأنهن مفائد','280','585','0');
INSERT INTO [t17712] VALUES (631,'٤٦ - لا، لا، أبوح بحب بثنة إنها ... أخذت علي مواثقا وعهودا','280','585','0');
INSERT INTO [t17712] VALUES (632,'٤٧ - وإياك والميتات لا تقربنها ... ولا تعبد الشيطان والله فاعبدا','280','585','0');
INSERT INTO [t17712] VALUES (633,'٤٨ - دعاني من نجد فإن سنينه ... لعبن بنا شيبا وشيبننا مردا','281','585','0');
INSERT INTO [t17712] VALUES (634,'٤٩ - فقلت: أعيراني القدوم لعلني ... أخط بها قبرا لأبيض ماجد','281','585','0');
INSERT INTO [t17712] VALUES (635,'٥٠ - قدني من نصر الخبيبين قدي ... ليس الإمام بالشحيح الملحد','281','585','0');
INSERT INTO [t17712] VALUES (636,'٥١ - رأيت بني غبراء لا ينكرونني ... ولا أهل هذاك الطراف الممدد','282','585','0');
INSERT INTO [t17712] VALUES (637,'٥٢ - من القوم الرسول الله منهم ... لهم دانت رقاب بني معد','282','585','0');
INSERT INTO [t17712] VALUES (638,'٥٣ - قد ثكلت أمه من كنت واجده ... وبات منتشبا في برثن الأسد','283','585','0');
INSERT INTO [t17712] VALUES (639,'٥٤ - بنونا بنو أبنائنا وبناتنا ... بنوهن أبناء الرجال الأباعد','283','585','0');
INSERT INTO [t17712] VALUES (640,'٥٥ - لولا أبوك ولولا قبله عمر ... ألقت إليك معد بالمقاليد','283','585','0');
INSERT INTO [t17712] VALUES (641,'٥٦ - وأبرح ما أدام الله قومي ... بحمد الله منتطقا مجيدا','284','585','0');
INSERT INTO [t17712] VALUES (642,'٥٧ - وما كل من يبدي البشاشة كائنا ... أخاك إذا لم تلفه لك منجدا','284','585','0');
INSERT INTO [t17712] VALUES (643,'٥٨ - قنافذ هداجون حول بيوتهم ... بما كان إياهم عطية عودا','284','585','0');
INSERT INTO [t17712] VALUES (644,'٥٩ - وأنا النذير بحرة مسودة ... تصل الجيوش إليكم أقوادها أبناؤها متكنفون أباهم ... حنقو الصدور وما هم أولادها','285','585','0');
INSERT INTO [t17712] VALUES (645,'٦٠ - أموت أسى يوم الرجام وإنني ... يقينا لرهن بالذي أنا كائد','285','585','0');
INSERT INTO [t17712] VALUES (646,'٦١ - يلومونني في حب ليلى عواذلي ... ولكنني من حبها لعميد','285','585','0');
INSERT INTO [t17712] VALUES (647,'٦٢ - مروا عجالى فقالوا: كيف سيدكم ... فقال من سألوا: أمسى لمجهودا','286','585','0');
INSERT INTO [t17712] VALUES (648,'٦٣ - شلت يمينك إن قتلت لمسلما ... حلت عليك عقوبة المتعمد','286','585','0');
INSERT INTO [t17712] VALUES (649,'٦٤ - رمى الحدثان نسوة آل حرب ... بمقدار سمدن له سمودا فرد شعورهن السود بيضا ... ورد وجوههن البيض سودا','286','585','0');
INSERT INTO [t17712] VALUES (650,'٦٥ - وخبرت سوداء الغميم مريضة ... فأقبلت من أهلي بمصر أعودها','287','585','0');
INSERT INTO [t17712] VALUES (651,'٦٦ - كسا حلمه ذا الحلم أثواب سؤدد ... ورقى نداه ذا الندى في ذرى المجد','287','585','0');
INSERT INTO [t17712] VALUES (652,'٦٧ - لم يعن بالعلياء إلا سيدا ... ولا شفى ذا الغي إلا ذو هدى','287','585','0');
INSERT INTO [t17712] VALUES (653,'٦٨ - إذا كنت ترضيه ويرضيك صاحب ... جهارا فكن في الغيب أحفظ للعهد وألغ أحاديث الوشاة فقلما ... يحاول واش غير هجران ذي ود','288','585','0');
INSERT INTO [t17712] VALUES (654,'٦٩ - لما حططت الرحل عنها واردا ... علفتها تبنا وماء باردا','288','585','0');
INSERT INTO [t17712] VALUES (655,'٧٠ - وقد بعدت بالوصل بيني وبينها ... بلى إن من زار القبور ليبعدا','289','585','0');
INSERT INTO [t17712] VALUES (656,'٧١ - فبت والهم تغشاني طوارقه ... من خوف رحلة بين الظاعنين غدا','289','585','0');
INSERT INTO [t17712] VALUES (657,'٧٢ - حتى إذا أسلكوهم في قتائدة ... شلا كما تطرد الجمالة الشردا','290','585','0');
INSERT INTO [t17712] VALUES (658,'٧٣ - وهذا ثنائي بما أوليت من حسن ... لا زلت عوض قرير العين محسودا','290','585','0');
INSERT INTO [t17712] VALUES (659,'٧٤ - أشلى سلوقية باتت وبات بها ... بوحش إصمت في أصلابها أود','290','585','0');
INSERT INTO [t17712] VALUES (660,'٧٥ - إذا المرء أعيته المروءة ناشئا ... فمطلبها كهلا عليه شديد','291','585','0');
INSERT INTO [t17712] VALUES (661,'٧٦ - بسود نواصيها وحمر أكفها ... وصفر تراقيها وبيض خدودها','291','585','0');
INSERT INTO [t17712] VALUES (662,'٧٧ - حتى استقامت له الآفاق طائعة ... فما يقال له هيد ولا هاد','291','585','0');
INSERT INTO [t17712] VALUES (663,'٧٨ - ولستم فاعلين إخال حتى ... ينال أقاصي الحطب الوقود','292','585','0');
INSERT INTO [t17712] VALUES (664,'٧٩ - يثني عليك وأنت أهل ثنائه ... ولديك إن هو يستزدك مزيد','292','585','0');
INSERT INTO [t17712] VALUES (665,'٨٠ - تريدين كيما تجمعيني وخالدا ... وهل يجمع السيفان ويحك في غمد','293','585','0');
INSERT INTO [t17712] VALUES (666,'٨١ - يقول وقد تر الوظيف وساقها ... ألست ترى أن قد أتيت بمؤيد','293','585','0');
INSERT INTO [t17712] VALUES (667,'٨٢ - رحيب قطاب الجيب منها رفيقة ... لجس الندامى بضة المتجرد','293','585','0');
INSERT INTO [t17712] VALUES (668,'٨٣ - مؤللتان تعرف العتق فيهما ... كسامعتي شاة بحومل مفرد','294','585','0');
INSERT INTO [t17712] VALUES (669,'٨٤ - وإن يلتق الحي الجميع تلاقني ... إلى ذروة البيت الكريم المصمد','294','585','0');
INSERT INTO [t17712] VALUES (670,'٨٥ - فويلم أيام الشباب معيشة ... مع الكثر يعطاه الفتى المتلف الندي','294','585','0');
INSERT INTO [t17712] VALUES (671,'٨٦ - أو حرة عيطل ثبجاء مجفرة ... دعائم الزور نعمت زورق البلد','295','585','0');
INSERT INTO [t17712] VALUES (672,'٨٧ - كأنه خارجا من جنب صفحته ... سفود شرب نسوه عند مفتأد','296','585','0');
INSERT INTO [t17712] VALUES (673,'٨٨ - أضحت خلاء وأضحى أهلها احتملوا ... أخنى عليها الذي أخنى على لبد','296','585','0');
INSERT INTO [t17712] VALUES (674,'٨٩ - فلا لعمر الذي قد زرته حججا ... وما هريق على الأنصاب من جسد والمؤمن العائذات الطير يمسحها ... ركبان مكة بين الغيل والسند','296','585','0');
INSERT INTO [t17712] VALUES (675,'٩٠ - قالت أمامة لما جئت زائرها ... هلا رميت ببعض الأسهم السود لا در درك إني قد رميتهم ... لولا حددت ولا عذرى لمحدود','297','585','0');
INSERT INTO [t17712] VALUES (676,'٩١ - وقتيل مرة أثأرن فإنه ... فرغ وإن أخاهم لم يقصد','297','585','0');
INSERT INTO [t17712] VALUES (677,'٩٢ - ها إن تاعذرة إن لم تكن نفعت ... فإن صاحبها قد تاه في البلد','298','585','0');
INSERT INTO [t17712] VALUES (678,'٩٣ - مهلا فداء لك الأقوام كلهم ... وما أثمر من مال ومن ولد','298','585','0');
INSERT INTO [t17712] VALUES (679,'٩٤ - من يكدني بسيئ كنت منه ... كالشجا بين حلقه والوريد','298','585','0');
INSERT INTO [t17712] VALUES (680,'٩٥ - يديان بيضاوان عند محلم ... قد يمنعانك أن تضام وتضهدا','299','585','0');
INSERT INTO [t17712] VALUES (681,'٩٦ - ما إن جزعت ولا هلع ... ت ولا يرد بكاي زندا','299','585','0');
INSERT INTO [t17712] VALUES (682,'٩٧ - وشق له من اسمه ليجله ... فذو العرش محمود وهذا محمد','299','585','0');
INSERT INTO [t17712] VALUES (683,'٩٨ - فإن تمس مهجور الفناء فربما ... أقام به بعد الوفود وفود','299','585','0');
INSERT INTO [t17712] VALUES (684,'٩٩ - وبالجسم مني بينا لو علمته ... شحوب وإن تستشهدي العين تشهد','299','585','0');
INSERT INTO [t17712] VALUES (685,'١٠٠ - وما لام نفسي مثلها لي لائم ... ولا سد فقري مثل ما ملكت يدي','300','585','0');
INSERT INTO [t17712] VALUES (686,'١٠١ - فلا والله لا يلفي أناس ... فتى حتاك يا ابن أبي زياد','300','585','0');
INSERT INTO [t17712] VALUES (687,'١٠٢ - تزود مثل زاد أبيك فينا ... فنعم الزاد زاد أبيك زادا','300','585','0');
INSERT INTO [t17712] VALUES (688,'١٠٣ - وماذا ترى في عيال قد برمت بهم ... لم أحص عدتهم إلا بعداد كانوا ثمانين أو زادوا ثمانية ... لولا رجاؤك قد قتلت أولادي','301','585','0');
INSERT INTO [t17712] VALUES (689,'١٠٤ - متى تأته تعشو إلى ضوء ناره ... تجد خير نار عندها خير موقد','301','585','0');
INSERT INTO [t17712] VALUES (690,'١٠٥ - من يكدني بسيء كنت منه ... كالشجا بين حلقه والوريد','301','585','0');
INSERT INTO [t17712] VALUES (691,'١٠٦ - رهبان مدين والذين عهدتهم ... يبكون من حذر العذاب قعودا لو يسمعون كما سمعت كلامها ... خروا لعزة ركعا وسجودا','302','585','0');
INSERT INTO [t17712] VALUES (692,'١٠٧ - أبصارهن إلى الشبان مائلة ... وقد أراهن عني غير صداد','302','585','0');
INSERT INTO [t17712] VALUES (693,'١٠٨ - ألم يأتيك والأنباء تنمي ... بما لاقت لبون بني زياد','302','585','0');
INSERT INTO [t17712] VALUES (694,'١٠٩ - قالت أمامة لما جئت زائرها ... هلا رميت ببعض الأسهم السود لا در درك إني قد رميتهم ... لولا حددت ولا عذرى لمحدود','303','585','0');
INSERT INTO [t17712] VALUES (695,'١١٠ - على مثلها أمضي إذا قال صاحبي ... ألا ليتني أفديك منها وأفتدي','303','585','0');
INSERT INTO [t17712] VALUES (696,'١١١ - وقفت فيها أصيلا كي أسائلها ... عيت جوابا وما بالربع من أحد','303','585','0');
INSERT INTO [t17712] VALUES (697,'١١٢ - شدخت غرة السوابق فيهم ... في وجوه إلى اللمام الجعاد','304','585','0');
INSERT INTO [t17712] VALUES (698,'١١٣ - وقفت فيها ... (الشاهد رقم (١١١) من هذا الحرف','304','585','0');
INSERT INTO [t17712] VALUES (699,'١١٤ - ولا أرى فاعلا في الناس يشبهه ... وما أحاشي من الأقوام من أحد','305','585','0');
INSERT INTO [t17712] VALUES (700,'١١٥ - أزمان من يرد الصنيعة يصطنع ... فينا ومن يرد الزهادة يزهد','305','585','0');
INSERT INTO [t17712] VALUES (701,'١١٦ - معاوي إننا بشر فأسجح ... فلسنا بالجبال ولا (الحديدا) أكلتم أرضنا فجرزتموها ... فهل من قائم أو من حصيد أتطمع في الخلود إذا هلكنا ... وليس لنا ولا لك من خلود','305','585','0');
INSERT INTO [t17712] VALUES (702,'١١٧ - ألا حي ندماني عمير بن عامر ... إذا ما تلاقينا من اليوم أو غدا','306','585','0');
INSERT INTO [t17712] VALUES (703,'١١٨ - هذيلية تدعو إذا هي فاخرت ... أبا هذليا من غطارفة نجد','306','585','0');
INSERT INTO [t17712] VALUES (704,'١١٩ - وأخو الغوان متى يشأ يصرمنه ... ويكن أعداء بعيد وداد','306','585','0');
INSERT INTO [t17712] VALUES (705,'١٢٠ - وقائلة ما بال دوسر بعدنا ... صحا قلبه عن آل ليلى وعن هند','306','585','0');
INSERT INTO [t17712] VALUES (706,'١٢١ - يا صاحبي فدت نفسي نفوسكما ... وحيثما كنتما لاقيتما رشدا أن تحملا حاجة لي خف محملها ... وتصنعا نعمة عندي بها ويدا أن تقرآن على أسماء ويحكما ... مني السلام وأن لا تشعرا أحدا','306','585','0');
INSERT INTO [t17712] VALUES (707,'١٢٢ - إنما الفقر والغناء من الله ... فهذا يعطى وهذا يحد','307','585','0');
INSERT INTO [t17712] VALUES (708,'١٢٣ - إذا اسود جنح الليل فلتأت ولتكن ... خطاك خفافا إن حراسنا أسدا','307','585','0');
INSERT INTO [t17712] VALUES (709,'١٢٤ - فيا رب إن لم تقسم الحب بيننا ... سواءين فاجعلني على حبها جلدا','307','585','0');
INSERT INTO [t17712] VALUES (710,'١٢٥ - ألم تغتمض عيناك ليلة أرمدا ... وعادك ما عاد السليم مسهدا وما ذاك من عشق النساء وإنما ... تناسيت قبل اليوم خلة مهددا ولكن أرى الدهر الذي هو خاتر ... إذا أصلحت كفاي عاد فأفسدا شباب وشيب وافتقار ونزوة ... فلله هذا الدهر كيف ترددا','307','585','0');
INSERT INTO [t17712] VALUES (711,'١٢٦ - له نافلات ما يغب نوالها ... وليس عطاء اليوم مانعه غدا','308','585','0');
INSERT INTO [t17712] VALUES (712,'١٢٧ - متى ما تناخي عند باب بن هاشم ... تراحي وتلقي من فواضله ندا','308','585','0');
INSERT INTO [t17712] VALUES (713,'١٢٨ - وما زلت أبغي المال مذ أنا يافع ... وليدا وكهلا حين شبت وأمردا','308','585','0');
INSERT INTO [t17712] VALUES (714,'١٢٩ - ألم تغتمض عيناك ليلة أرمدا ... وبت كما بات السليم مسهدا','308','585','0');
INSERT INTO [t17712] VALUES (715,'١٣٠ - خليلي رفقا ريث أقضي لبانة ... من العرصات المذكرات عهودا','309','585','0');
INSERT INTO [t17712] VALUES (716,'١٣١ - هويت سناء مستطابا مجددا ... فلم تخل من تمهيد مجد وسوددا','309','585','0');
INSERT INTO [t17712] VALUES (717,'١٣٢ - ألمم بزينب إن البين قد أفدا ... قل الثواء لئن كان الرحيل غدا','309','585','0');
INSERT INTO [t17712] VALUES (718,'١٣٣ - آل الزبير سنام المجد قد علمت ... ذاك القبائل والأثرون من عددا','309','585','0');
INSERT INTO [t17712] VALUES (719,'١٣٤ - كأنني، حين أمسي لا تكلمني ... متيم يشتهي ما ليس موجودا','309','585','0');
INSERT INTO [t17712] VALUES (720,'١٣٥ - ما للجمال مشيها وئيدا ... أجندلا يحملن أم حديدا','310','585','0');
INSERT INTO [t17712] VALUES (721,'١٣٦ - فإن يكن الموت أفناهم ... فللموت ما تلد الوالدة','310','585','0');
INSERT INTO [t17712] VALUES (722,'١٣٧ - ورج الفتى للخير ما إن رأيته ... على السن خيرا لا يزال يزيد','310','585','0');
INSERT INTO [t17712] VALUES (723,'١٣٨ - عد النفس نعمى بعد بؤساك ذاكرا ... كذا وكذا لطفا به نسي الجهد','311','585','0');
INSERT INTO [t17712] VALUES (724,'١٣٩ - على الحكم المأتي يوما إذا قضى ... قضيته أن لا يجور ويقصد','311','585','0');
INSERT INTO [t17712] VALUES (725,'١٤٠ - إذا كانت الهيجاء وانشقت العصا ... فحسبك والضحاك سيف مهند','311','585','0');
INSERT INTO [t17712] VALUES (726,'١٤١ - وليل بدت للعين نار كأنها ... سنا كوكب لا يستبين خمودها فقلت عساها نار كأس وعلها ... تشكى فأمضي نحوها فأعودها','311','585','0');
INSERT INTO [t17712] VALUES (727,'١٤٢ - كم ملوك باد ملكهم ... ونعيم سوقة بادوا','312','585','0');
INSERT INTO [t17712] VALUES (728,'١٤٣ - إخوتي لا تبعدوا أبدا ... وبلى والله قد بعدوا كل ما حي وإن أمروا ... واردو الحوض الذي وردوا','312','585','0');
INSERT INTO [t17712] VALUES (729,'١٤٤ - وبالصريمة منها منزل خلق ... عاف تغير إلا النؤي والوتد','313','585','0');
INSERT INTO [t17712] VALUES (730,'١٤٥ - يلقاك مرتديا بأحمر من دم ... ذهبت بخضرته الطلى والأكبد','313','585','0');
INSERT INTO [t17712] VALUES (731,'١٤٦ - نبئت أخوالي بني يزيد ... ظلما علينا لهم فديد','313','585','0');
INSERT INTO [t17712] VALUES (732,'١٤٧ - قفا قليلا بها علي فلا ... أقل من نظرة أزودها','314','585','0');
INSERT INTO [t17712] VALUES (733,'١٤٨ - قل لمن ساد ثم ساد أبوه ... قبله ثم قبل ذلك جده','314','585','0');
INSERT INTO [t17712] VALUES (734,'١٤٩ - ألا إن قرطا على آلة ... ألا إنني كيده لا أكيد','314','585','0');
INSERT INTO [t17712] VALUES (735,'١٥٠ - بكل تداوينا فلم يشف ما بنا ... على أن قرب الدار خير من البعد على أن قرب الدار ليس بنافع ... إذا كان من تهواه ليس بذي ود','314','585','0');
INSERT INTO [t17712] VALUES (736,'١٥١ - وإن الذي حانت بفلج دماؤهم ... هم القوم كل القوم يا أم خالد','315','585','0');
INSERT INTO [t17712] VALUES (737,'١٥٢ - إذا قل مال المرء لانت قناته ... وهان على الأدنى فكيف الأباعد','315','585','0');
INSERT INTO [t17712] VALUES (738,'١٥٣ - إذا ما صنعت الزاد فالتمسي له ... أكيلا فإني لست آكله وحدي','316','585','0');
INSERT INTO [t17712] VALUES (739,'١٥٤ - إن الحق لا يخفى على ذي بصيرة ... وإن هو لم يعدم خلاف معاند','316','585','0');
INSERT INTO [t17712] VALUES (740,'١٥٥ - وما زلت من ليلى لدن أن عرفتها ... لكالهائم المقصى بكل مراد','316','585','0');
INSERT INTO [t17712] VALUES (741,'١٥٦ - فلو كان حمد مخلد الناس لم يمت ... ولكن حمد الناس ليس بمخلد','316','585','0');
INSERT INTO [t17712] VALUES (742,'١٥٧ - تقي نقي لم يكثر غنيمة ... بنهكة ذي قربى ولا بحقلد','317','585','0');
INSERT INTO [t17712] VALUES (743,'١٥٨ - إذا كنت في قوم فصاحب خيارهم ... ولا تصحب الأردى فتردى مع الردي','317','585','0');
INSERT INTO [t17712] VALUES (744,'١٥٩ - فقد ذاقت الأوس القتال وطردت ... وأنت لدى الكنات- كل مطرد تناغي لدى الأبواب حورا نواعما ... وكحل مآقيك الحسان بإثمد','317','585','0');
INSERT INTO [t17712] VALUES (745,'١٦٠ - وعند الذي واللات عدنك إحنة ... عليك فلا يغررك كيد العوائد','318','585','0');
INSERT INTO [t17712] VALUES (746,'١٦١ - وناهدة الثديين قلت لها اتكي ... على الرمل من جنباته لم توسد فقالت على اسم الله أمرك طاعة ... وإن كنت قد كلفت ما لم أعود','318','585','0');
INSERT INTO [t17712] VALUES (747,'١٦٢ - وهل أنا إلا من غزية إن غوت ... غويت إن ترشد غزية أرشد','319','585','0');
INSERT INTO [t17712] VALUES (748,'١٦٣ - ولكنما أهلي بواد أنيسه ... ذئاب تبغى الناس مثنى وموحد','319','585','0');
INSERT INTO [t17712] VALUES (749,'١٦٤ - أنحوي هذا العصر ما هي كلمة ... جرت في لساني جرهم وثمود إذا استعملت في صورة النفي أثبتت: ... وإن أثبتت قامت مقام جحود','320','585','0');
INSERT INTO [t17712] VALUES (750,'١٦٥ - لعمرك ما الفتيان أن تنبت اللحى ... ولكنما الفتيان كل فتى ندي','320','585','0');
INSERT INTO [t17712] VALUES (751,'١٦٦ - ما إن أتيت بشيء أنت تكرهه ... إذن فلا رفعت سوطي إلي يدي','320','585','0');
INSERT INTO [t17712] VALUES (752,'١٦٧ - عممتهم بالندى حتى غواتهم ... فكنت مالك ذي غي وذي رشد','321','585','0');
INSERT INTO [t17712] VALUES (753,'١٦٨ - قد أترك القرن مصفرا أنامله ... كأن أثوابه مجت بفرصاد','321','585','0');
INSERT INTO [t17712] VALUES (754,'١٦٩ - يا عمرو أحسن نماك الله بالرشد ... واقر السلام على الأنقاء والثمد وابكن عيشا تقضى بعد جدته ... طابت أصائله في ذلك البلد','321','585','0');
INSERT INTO [t17712] VALUES (755,'١٧٠ - تركت ضأني تود الذئب راعيها ... وأنها لا تراني آخر الأبد الذئب يطرقها في الدهر واحدة ... وكل يوم تراني مدية بيدي','322','585','0');
INSERT INTO [t17712] VALUES (756,'١٧١ - أحاد أم سداس في أحاد ... لييلتنا المنوطة بالتنادي','322','585','0');
INSERT INTO [t17712] VALUES (757,'١٧٢ - فما جمع ليغلب جمع قومي ... مقاومة ولا فرد لفرد','323','585','0');
INSERT INTO [t17712] VALUES (758,'١٧٣ - أرأيت أي سوالف وخدود ... برزت لنا بين اللوى فزرود','323','585','0');
INSERT INTO [t17712] VALUES (759,'١٧٤ - كنواح ريش حمامة نجدية ... ومسحت باللثتين عصف الإثمد','324','585','0');
INSERT INTO [t17712] VALUES (760,'١٧٥ - إن المنية والحتوف كلاهما ... يوفي المخارم يرقبان سوادي','324','585','0');
INSERT INTO [t17712] VALUES (761,'١٧٦ - إن الرزية لا رزية مثلها ... فقدان مثل محمد ومحمد','325','585','0');
INSERT INTO [t17712] VALUES (762,'١٧٧ - وملكت ما بين العراق ويثرب ... ملكا أجار لمسلم ومعاهد','325','585','0');
INSERT INTO [t17712] VALUES (763,'١٧٨ - نام الخلي فما أحس رقادي ... والهم محتضر لدي وسادي من غير ما سقم ولكن شفني ... هم أراه قد أصاب فؤادي','325','585','0');
INSERT INTO [t17712] VALUES (764,'١٧٩ - فأجبت قائل: كيف أنت ب: صالح ... حتى مللت وملني عوادي','325','585','0');
INSERT INTO [t17712] VALUES (765,'١٨٠ - نعم الفتى المري أنت إذا هم ... حضروا لدى الحجرات نار الموقد','326','585','0');
INSERT INTO [t17712] VALUES (766,'١٨١ - يا من رأى عارضا أسر به ... بين ذراعي وجبهة الأسد','326','585','0');
INSERT INTO [t17712] VALUES (767,'١٨٢ - ما ترى الدهر قد أباد معدا ... وأباد القرون من قوم عاد','326','585','0');
INSERT INTO [t17712] VALUES (768,'١٨٣ - أي يوم سررتني بوصال ... لم ترعني ثلاثة بصدود','326','585','0');
INSERT INTO [t17712] VALUES (769,'١٨٤ - فإن شئت آليت بين المقا ... م والركن والحجر الأسود نسيتك ما دام عقلي معي ... أمد به أمد السرمد','327','585','0');
INSERT INTO [t17712] VALUES (770,'١٨٥ - سقط النصيف ولم ترد إسقاطه ... فتناولته واتقتنا باليد بمخضب رخص كأن بنانه ... عنم يكاد من اللطافة يعقد','327','585','0');
INSERT INTO [t17712] VALUES (771,'١٨٦ - وكان وإياها كحران لم يفق ... عن الماء إذ لاقاه حتى تقددا','327','585','0');
INSERT INTO [t17712] VALUES (772,'١٨٧ - لنا مرفد سبعون ألف مدجج ... فهل في معد فوق ذلك مرفدا','327','585','0');
INSERT INTO [t17712] VALUES (773,'١٨٨ - أتوعدني بقومك يا ابن حجل ... أشابات يخالون العبادا بما جمعت من حضن وعمرو ... وما حضن وعمرو والجيادا','328','585','0');
INSERT INTO [t17712] VALUES (774,'١٨٩ - غلب المساميح الوليد سماحة ... وكفى قريش المعضلات وسادها','328','585','0');
INSERT INTO [t17712] VALUES (775,'١٩٠ - فكيف لنا بالشرب إن لم تكن لنا ... دوانيق عند الحانوي ولا نقد','328','585','0');
INSERT INTO [t17712] VALUES (776,'١٩١ - مفدمة قزا كأن رقابها ... رقاب بنات الماء أفزعها الرعد','328','585','0');
INSERT INTO [t17712] VALUES (777,'١٩٢ - فلاقى ابن أنثى يبتغي مثل ما ابتغى ... من القوم مسقي السمام حدائده','329','585','0');
INSERT INTO [t17712] VALUES (778,'١٩٣ - فلما أتى عامان بعد انفصاله ... عن الضرع واحلولى دماثا يرودها','329','585','0');
INSERT INTO [t17712] VALUES (779,'١٩٤ - سبحانه ثم سبحانا يعود له ... وقبلنا سبح الجودي والجمد','329','585','0');
INSERT INTO [t17712] VALUES (780,'١٩٥ - فلا حسبا فخرت به لتيم ... ولا جدا إذا ازدحم الجدود','329','585','0');
INSERT INTO [t17712] VALUES (781,'١٩٦ - أخالد قد علقتك بعد هند ... فشيبني الخوالد والهنود','330','585','0');
INSERT INTO [t17712] VALUES (782,'١٩٧ - نبئت أن أبا قابوس أوعدني ... ولا قرار على زأر من الأسد','330','585','0');
INSERT INTO [t17712] VALUES (783,'١٩٨ - وكل خليل راءني فهو قائل ... من اجلك: هذا هامة اليوم أو غد','330','585','0');
INSERT INTO [t17712] VALUES (784,'١٩٩ - فلولا رجاء النصر منك ورهبة ... عقابك قد صاروا لنا كالموارد','330','585','0');
INSERT INTO [t17712] VALUES (785,'٢٠٠ - احكم كحكم فتاة الحي إذ نظرت ... إلى حمام شراع وارد الثمد','330','585','0');
INSERT INTO [t17712] VALUES (786,'٢٠١ - مقذوفة بدخيس النحض بازلها ... له صريف صريف القعو بالمسد','331','585','0');
INSERT INTO [t17712] VALUES (787,'٢٠٢ - يا كعب صبرا على ما كان من حدث ... يا كعب لم يبق منا غير أجلاد إلا بقيات أنفاس نحشرجها ... كراحل رائح أو باكر غادي','331','585','0');
INSERT INTO [t17712] VALUES (788,'٢٠٣ - أريد حباءه ويريد قتلي ... عذيرك من خليلك من مراد','331','585','0');
INSERT INTO [t17712] VALUES (789,'٢٠٤ - عمرتك الله الجليل فإنني ... ألوي عليك لو ان لبك يهتدي','332','585','0');
INSERT INTO [t17712] VALUES (790,'٢٠٥ - فصفحت عنهم والأحبة فيهم ... طمعا لهم بعقاب يوم مفسد','332','585','0');
INSERT INTO [t17712] VALUES (791,'٢٠٦ - فلأبغينكم قنا وعوارضا ... ولأقبلن الخيل لابة ضرغد','332','585','0');
INSERT INTO [t17712] VALUES (792,'٢٠٧ - فكأنه لهق السراة كأنه ... ما حاجبيه معين بسواد','333','585','0');
INSERT INTO [t17712] VALUES (793,'٢٠٨ - مستحن بها الرياح فما يج ... تابها في الظلام كل هجود','333','585','0');
INSERT INTO [t17712] VALUES (794,'٢٠٩ - إياك أنت وعبد المسيح ... أن تقربا قبلة المسجد','333','585','0');
INSERT INTO [t17712] VALUES (795,'٢١٠ - لولا رجاء لقاء الظاعنين لما ... أبقت نواهم لنا روحا ولا جسدا','333','585','0');
INSERT INTO [t17712] VALUES (796,'٢١١ - عزمت على إقامة ذي صباح ... لأمر ما يسود من يسود','333','585','0');
INSERT INTO [t17712] VALUES (797,'٢١٢ - فقام يذود الناس عنها بسيفه ... وقال: ألا، لا من سبيل إلى هند','333','585','0');
INSERT INTO [t17712] VALUES (798,'٢١٣ - عسى سائل ذو حاجة إن منعته ... من اليوم سؤلا أن ييسر في غد','334','585','0');
INSERT INTO [t17712] VALUES (799,'٢١٤ - ها إن ذي عذرة إلا تكن نفعت ... فإن صاحبها مشارك النكد','334','585','0');
INSERT INTO [t17712] VALUES (800,'٢١٥ - أبى كرما لا آلفا «جير» أو «نعم» ... بأحسن إيفاء وأنجز موعد','334','585','0');
INSERT INTO [t17712] VALUES (801,'٢١٦ - ما كان أسعد من أجابك آخذا ... بهداك مجتنبا هوى وعنادا','334','585','0');
INSERT INTO [t17712] VALUES (802,'٢١٧ - تجلدت حتى قيل لم يعر قلبه ... من الوجد شيء قلت: بل أعظم الوجد','334','585','0');
INSERT INTO [t17712] VALUES (803,'٢١٨ - وماذا عسى الحجاج يبلغ جهده ... إذا نحن جاوزنا حفير زياد','334','585','0');
INSERT INTO [t17712] VALUES (804,'٢١٩ - يعجبه السخون والعصيد ... والتمر حبا ماله مزيد','335','585','0');
INSERT INTO [t17712] VALUES (805,'٢٢٠ - خمولا وإهمالا؟ وغيرك مولع ... بتثبيت أركان السيادة والمجد','335','585','0');
INSERT INTO [t17712] VALUES (806,'٢٢١ - تسليت طرا عنكم بعد بينكم ... بذكراكم حتى كأنكم عندي','335','585','0');
INSERT INTO [t17712] VALUES (807,'٢٢٢ - سقط النصيف ولم ترد إسقاطه ... فتناولته واتقتنا باليد','335','585','0');
INSERT INTO [t17712] VALUES (808,'٢٢٣ - وما قتل الأحرار كالعفو عنهم ... ومن لك بالحر الذي يحفظ اليدا','335','585','0');
INSERT INTO [t17712] VALUES (809,'٢٢٤ - وقد علتني ذرأة بادي بدي ... ورثية تنهض في تشددي','335','585','0');
INSERT INTO [t17712] VALUES (810,'٢٢٥ - أسقى الإله عدوات الوادي ... وجوفه كل ملث غادي كل أجش حالك السواد','336','585','0');
INSERT INTO [t17712] VALUES (811,'٢٢٦ - على مثلها أمضي إذا قال صاحبي ... ألا ليتني أفديك منها وأفتدي','336','585','0');
INSERT INTO [t17712] VALUES (812,'٢٢٧ - رعيتها أكرم عود عودا ... الصل والصفصل واليعضيدا والخازباز السنم المجودا ... بحيث يدعو عامر مسعودا','336','585','0');
INSERT INTO [t17712] VALUES (813,'٢٢٨ - أودى ابن جلهم عباد بصرمته ... إن ابن جلهم أمسى حية الوادي','337','585','0');
INSERT INTO [t17712] VALUES (814,'٢٢٩ - فزججتها بمزجة ... زج القلوص أبي مزاده','337','585','0');
INSERT INTO [t17712] VALUES (815,'٢٣٠ - في كلت رجليها سلامى واحده ... كلتاهما مقرونة بزائدة','337','585','0');
INSERT INTO [t17712] VALUES (816,'٢٣١ - إذا القعود كر فيها حفدا ... يوما جديدا كله مطردا','338','585','0');
INSERT INTO [t17712] VALUES (817,'٢٣٢ - ولا تجعليني كامرئ ليس همه ... كهمي ولا يغني غنائي ومشهدي','338','585','0');
INSERT INTO [t17712] VALUES (818,'٢٣٣ - هنيئا لسعد ما اقتضى بعد وقعتي ... بناقة سعد والعشية بارد','338','585','0');
INSERT INTO [t17712] VALUES (819,'٢٣٤ - فوقعت بين قتود عنس ضامر ... لحاظة طفل العشي سناد','339','585','0');
INSERT INTO [t17712] VALUES (820,'٢٣٥ - قد جربوه فألفوه المغيث إذا ... ما الروع عم فلا يلوى على أحد','339','585','0');
INSERT INTO [t17712] VALUES (821,'٢٣٦ - أبا وهب جزاك الله خيرا ... نحرناها وأطعمنا الثريدا فعد إن الكريم له معاد ... وظني بابن أروى أن يعودا','339','585','0');
INSERT INTO [t17712] VALUES (822,'٢٣٧ - إخالك- إن لم تغضض الطرف- ذا هوى ... يسومك ما لا يستطاع من الوجد','339','585','0');
INSERT INTO [t17712] VALUES (823,'٢٣٨ - لوجهك في الإحسان بسط وبهجة ... أنا لهماه قفو أكرم والد','340','585','0');
INSERT INTO [t17712] VALUES (824,'٢٣٩ - ربيته حتى إذا تمعددا ... وآض نهدا كالحصان أجردا كان جزائي بالعصا أن أجلدا','340','585','0');
INSERT INTO [t17712] VALUES (825,'٢٤٠ - فما حسن أن يمدح المرء نفسه ... ولكن أخلاقا تذم وتحمد','340','585','0');
INSERT INTO [t17712] VALUES (826,'٢٤١ - دعاني أخي والخيل بيني وبينه ... فلما دعاني لم يجدني بقعدد','341','585','0');
INSERT INTO [t17712] VALUES (827,'٢٤٢ - وكأن برقع والملائك حولها ... سدر تواكله القوائم أجرد','341','585','0');
INSERT INTO [t17712] VALUES (828,'٢٤٣ - وأبغض من وضعت إلي فيه ... لساني معشر عنهم أذود','341','585','0');
INSERT INTO [t17712] VALUES (829,'٢٤٤ - زعم البوارح أن رحلتنا غدا ... وبذاك خبرنا الغداف الأسود','341','585','0');
INSERT INTO [t17712] VALUES (830,'٢٤٥ - يضحي على سوق الجذول كأنه ... خصم أبر على الخصوم يلندد','342','585','0');
INSERT INTO [t17712] VALUES (831,'٢٤٦ - نرضى عن الله إن الناس قد علموا ... أن لا يدانينا من خلقه أحد','342','585','0');
INSERT INTO [t17712] VALUES (832,'٢٤٧ - ألا حبذا هند وأرض بها هند ... وهند أتى من دونها النأي والبعد','342','585','0');
INSERT INTO [t17712] VALUES (833,'٢٤٨ - أأبي لا تبعد وليس بخالد ... حي ومن تصب المنون بعيد','343','585','0');
INSERT INTO [t17712] VALUES (834,'٢٤٩ - نظارة حين تعلو الشمس راكبها ... طرحا بعيني لياح فيه تحديد','343','585','0');
INSERT INTO [t17712] VALUES (835,'٢٥٠ - ترفع لي خندف- والله يرفع لي- ... نارا إذا خمدت نيرانهم تقد','343','585','0');
INSERT INTO [t17712] VALUES (836,'٢٥١ - إذا ما الخبز تأدمه بلحم ... فذاك أمانة الله الثريد','343','585','0');
INSERT INTO [t17712] VALUES (837,'٢٥٢ - فلا حسبا فخرت به لتيم ... ولا جدا إذا ازدحم الجدود','344','585','0');
INSERT INTO [t17712] VALUES (838,'٢٥٣ - متى ما ير الناس الغني وجاره ... فقير يقولوا عاجز وجليد وليس الغنى والفقر من حيلة الفتى ... ولكن أحاظ قسمت وجدود إذا المرء أعيته المروءة ناشئا ... فمطلبها كهلا عليه شديد وكائن رأينا من غني مذمم ... وصعلوك قوم مات وهو حميد','344','585','0');
INSERT INTO [t17712] VALUES (839,'٢٥٤ - ألا يا ليل ويحك نبئينا ... فأما الجود منك فليس جود','344','585','0');
INSERT INTO [t17712] VALUES (840,'٢٥٥ - ورب أسيلة الخدين بكر ... مهفهفة لها فرع وجيد لهوت بها زمانا من شبابي ... وزارتها النجائب والقصيد','345','585','0');
INSERT INTO [t17712] VALUES (841,'٢٥٦ - فذرني أجول في البلاد لعلني ... أسر صديقا أو يساء حسود','345','585','0');
INSERT INTO [t17712] VALUES (842,'٢٥٧ - مستحقبي حلق الماذي يحفزه ... بالمشرفي وغاب فوقه حصد','345','585','0');
INSERT INTO [t17712] VALUES (843,'٢٥٨ - أترضى بأنا لم تجف دماؤنا ... وهذا عروس باليمامة خالد','345','585','0');
INSERT INTO [t17712] VALUES (844,'٢٥٩ - وإن قال مولاهم على جل حادث ... من الدهر ردوا فضل أحلامكم ردوا','346','585','0');
INSERT INTO [t17712] VALUES (845,'٢٦٠ - أقفر من أهله عبيد ... فاليوم لا يبدي ولا يعيد','346','585','0');
INSERT INTO [t17712] VALUES (846,'٢٦١ - فإن تكن الموسى جرت فوق بظرها ... فما اختتنت إلا ومصان قاعد','346','585','0');
INSERT INTO [t17712] VALUES (847,'٢٦٢ - جاءت كبير كما أخفرها ... والقوم صيد كأنهم رمدوا','346','585','0');
INSERT INTO [t17712] VALUES (848,'٢٦٣ - فإنك من حاربته لمحارب ... شقي ومن سالمته لسعيد','346','585','0');
INSERT INTO [t17712] VALUES (849,'٢٦٤ - تستن أعداد قريان تسنمها ... غر الغمام ومرتجاته السود','346','585','0');
INSERT INTO [t17712] VALUES (850,'٢٦٥ - ألا أيهذا المنزل الدارس الذي ... كأنك لم يعهد بك الحي عاهد','347','585','0');
INSERT INTO [t17712] VALUES (851,'٢٦٦ - أبني لبينى لستم بيد ... إلا يدا ليست لها عضد','347','585','0');
INSERT INTO [t17712] VALUES (852,'٢٦٧ - تالله يبقى على الأيام مبتقل ... جون السراة رباع سنه غرد','347','585','0');
INSERT INTO [t17712] VALUES (853,'٢٦٨ - ولقد سئمت من الحياة وطولها ... وسؤال هذا الناس كيف لبيد','347','585','0');
INSERT INTO [t17712] VALUES (854,'٢٦٩ - من رامها حاشا النبي ورهطه ... في الفخر غطمطه هناك المزبد','347','585','0');
INSERT INTO [t17712] VALUES (855,'٢٧٠ - إني لعند أذى المولى لذو حنق ... وإن حلمي إذا أوذيت معتاد','348','585','0');
INSERT INTO [t17712] VALUES (856,'٢٧١ - إذا كانت الهيجاء وانشقت العصا ... فحسبك والضحاك سيف مهند','348','585','0');
INSERT INTO [t17712] VALUES (857,'٢٧٢ - إن الخليط أجدوا البين فانجردوا ... وأخلفوك عدا الأمر الذي وعدوا','348','585','0');
INSERT INTO [t17712] VALUES (858,'٢٧٣ - أماتوا من دمي وتوعدوني ... وكنت ولا ينهنهني الوعيد','348','585','0');
INSERT INTO [t17712] VALUES (859,'٢٧٤ - لحب المؤقدان إلي مؤسى ... وجعدة إذ أضاءهما الوقود','348','585','0');
INSERT INTO [t17712] VALUES (860,'٢٧٥ - متى تؤخذوا قسرا بظنة عامر ... ولا ينج إلا في الصفاد يزيد','349','585','0');
INSERT INTO [t17712] VALUES (861,'٢٧٦ - يبدو وتضمره البلاد كأنه ... سيف على شرف يسل ويغمد','349','585','0');
INSERT INTO [t17712] VALUES (862,'٢٧٧ - فلو أن ما أبقيت مني معلق ... بعود ثمام ما تأود عودها','349','585','0');
INSERT INTO [t17712] VALUES (863,'٢٧٨ - وما هاج هذا الشوق إلا حمامة ... تغنت على خضراء سمر قيودها','350','585','0');
INSERT INTO [t17712] VALUES (864,'٢٧٩ - تناولها كلب بن كلب فأصبحت ... بكف لئيم الوالدين يقودها','350','585','0');
INSERT INTO [t17712] VALUES (865,'٢٨٠ - لقد علم الأقوام ما كان داءها ... بثهلان إلا الخزي ممن يقودها','350','585','0');
INSERT INTO [t17712] VALUES (866,'٢٨١ - ما دام حافظ سري من وثقت به ... فهو الذي لست عنه راغبا أبدا','350','585','0');
INSERT INTO [t17712] VALUES (867,'٢٨٢ - أعني بخوار العنان تخاله ... إذا راح يردي بالمدجج أحردا وأبيض مصقول السطام مهندا ... وذا شطب من نسج داود مسردا','351','585','0');
INSERT INTO [t17712] VALUES (868,'٢٨٣ - وأين ركيب واضعون رحالهم ... إلى أهل نار من أناس بأسودا','351','585','0');
INSERT INTO [t17712] VALUES (869,'٢٨٤ - سرينا إليهم في جموع كأنها ... جبال شرورى لو تعان فتنهدا','351','585','0');
INSERT INTO [t17712] VALUES (870,'٢٨٥ - أتيت حريثا زائرا عن جناية ... وكان حريث في عطائي جاهدا','351','585','0');
INSERT INTO [t17712] VALUES (871,'٢٨٦ - إذا تأوب نوح قامتا معه ... ضربا أليما بسبت يلعج الجلدا','351','585','0');
INSERT INTO [t17712] VALUES (872,'٢٨٧ - رجالي حتى الأقدمون تمالؤوا ... على كل أمر يورث المجد والحمدا','352','585','0');
INSERT INTO [t17712] VALUES (873,'٢٨٨ - ما إن جزعت ولا هلع ... ت ولا يرد بكاي زندا','352','585','0');
INSERT INTO [t17712] VALUES (874,'٢٨٩ - رمى الحدثان نسوة آل حرب ... بمقدار سمدن له سمودا','352','585','0');
INSERT INTO [t17712] VALUES (875,'٢٩٠ - لقد نلت (عبد الله) وابنك غاية ... من المجد من يظفر بها نال سؤددا','352','585','0');
INSERT INTO [t17712] VALUES (876,'٢٩١ - أتوعدني بقومك يا ابن حجل ... أشابات يخالون العبادا','352','585','0');
INSERT INTO [t17712] VALUES (877,'٢٩٢ - تمنى لقائي الجون مغرور نفسه ... فلما رآني ارتاع ثمة عردا','353','585','0');
INSERT INTO [t17712] VALUES (878,'٢٩٣ - لو كنتم منجدي حين استعنتكم ... لم تعدموا ساعدا مني ولا عضدا','353','585','0');
INSERT INTO [t17712] VALUES (879,'٢٩٤ - ما كان أسعد من أجابك آخذا ... بهداك مجتنبا هوى وعنادا','353','585','0');
INSERT INTO [t17712] VALUES (880,'٢٩٥ - أجرى قلائدها وخدد لحمها ... أن لا تذوق مع الشكائم عودا','353','585','0');
INSERT INTO [t17712] VALUES (881,'٢٩٦ - آليت لا أعطيه من أبنائنا ... رهنا فيفسدهم كمن قد أفسدا حتى يفيدك من بنيه رهينة ... نعش، ويرهنك السماك الفرقدا','353','585','0');
INSERT INTO [t17712] VALUES (882,'٢٩٧ - حزق إذا ما القوم أبدوا فكاهة ... تفكر آإياه يعنون أم قردا','354','585','0');
INSERT INTO [t17712] VALUES (883,'٢٩٨ - بربك هل للصب عندك رأفة ... فيرجو بعد اليأس عيشا مجددا','354','585','0');
INSERT INTO [t17712] VALUES (884,'٢٩٩ - سقى الحيا الأرض حتى أمكن عزيت ... لهم فما زال عنها الخير مجدودا','354','585','0');
INSERT INTO [t17712] VALUES (885,'٣٠٠ - أريني جوادا مات هزلا لعلني ... أرى ما ترين أو بخيلا مخلدا','354','585','0');
INSERT INTO [t17712] VALUES (886,'٣٠١ - لنا مرفد سبعون ألف مدجج ... فهل من معد فوق ذلك مرفدا','355','585','0');
INSERT INTO [t17712] VALUES (887,'٣٠٢ - ظننتك إن شبت لظى الحرب صاليا ... فعردت فيمن كان عنها معردا','355','585','0');
INSERT INTO [t17712] VALUES (888,'٣٠٣ - ألا أيهذا السائلي أين يممت ... فإن لها من أهل يثرب موعدا','355','585','0');
INSERT INTO [t17712] VALUES (889,'٣٠٤ - لم يبق إلا المجد والقصائدا ... غيرك يا ابن الأكرمين والدا','355','585','0');
INSERT INTO [t17712] VALUES (890,'٣٠٥ - لئن أمست ربوعهم يبابا ... لقد تدعو الوفود لها الوفودا','356','585','0');
INSERT INTO [t17712] VALUES (891,'٣٠٦ - ومن فعلاتي أنني حسن القرى ... إذا الليلة الشهباء أضحى جليدها','356','585','0');
INSERT INTO [t17712] VALUES (892,'٣٠٧ - متى تأتني أصبحك كأسا روية ... وإن كنت عنها ذا غنى فاغن وازدد','356','585','0');
INSERT INTO [t17712] VALUES (893,'٣٠٨ - وعرق الفرزدق شر العروق ... خبيث الثرى كابي الأزند','356','585','0');
INSERT INTO [t17712] VALUES (894,'٣٠٩ - ملئت رعبا وقوم كنت راجيهم ... لما دهمتك من قومي بآساد','356','585','0');
INSERT INTO [t17712] VALUES (895,'٣١٠ - أسود شرى لاقت أسود خفية ... تساقوا على حرد دماء الأساود','356','585','0');
INSERT INTO [t17712] VALUES (896,'٣١١ - نسيتك ما دام عقلي معي ... أمد به أبد السرمد','357','585','0');
INSERT INTO [t17712] VALUES (897,'٣١٢ - لعل الله يمكنني عليها ... جهارا من زهير أو أسيد','357','585','0');
INSERT INTO [t17712] VALUES (898,'٣١٣ - إلى هادرات صعاب الرؤوس ... قساور للقسور الأصيد','357','585','0');
INSERT INTO [t17712] VALUES (899,'٣١٤ - كيف القرار ببطن مكة بعد ما ... هم الذين تحب بالإنجاد أم كيف صبرك إذ ثويت معالجا ... سقما خلافهم وسقمك باد','358','585','0');
INSERT INTO [t17712] VALUES (900,'٣١٥ - لو اعتصمت بنا لم تعتصم بعدى ... بل أولياء كفاة غير أوغاد','358','585','0');
INSERT INTO [t17712] VALUES (901,'٣١٦ - فأما واحدا فكفاك مثلي ... فمن ليد تطاوحها الأيادي','358','585','0');
INSERT INTO [t17712] VALUES (902,'٣١٧ - فآليت لا أنفك أحدو قصيدة ... تكون وإياها بها مثلا بعدي','358','585','0');
INSERT INTO [t17712] VALUES (903,'٣١٨ - إذا الخمس والخمسين جاوزت فارتقب ... قدوما على الأموات غير بعيد','359','585','0');
INSERT INTO [t17712] VALUES (904,'٣١٩ - يلاعب الريح بالعصرين قصطله ... والوابلون وتهتان التجاويد','359','585','0');
INSERT INTO [t17712] VALUES (905,'٣٢٠ - ردت عليه أقاصيه ولبده ... ضرب الوليدة بالمسحاة في الثأد','359','585','0');
INSERT INTO [t17712] VALUES (906,'٣٢١ - إليه تلجأ الهضاء طرا ... فليس بقائل هجرا لجاد','360','585','0');
INSERT INTO [t17712] VALUES (907,'٣٢٢ - إن اختيارك ما تبغيه ذا ثقة ... بالله مستظهرا بالحزم والجلد','360','585','0');
INSERT INTO [t17712] VALUES (908,'٣٢٣ - كم دون مية موماة يهال لها ... إذا تيممها الخريت ذو الجلد','360','585','0');
INSERT INTO [t17712] VALUES (909,'٣٢٤ - جماد لها جماد ولا تقولن ... طوال الدهر ما ذكرت: حماد','360','585','0');
INSERT INTO [t17712] VALUES (910,'٣٢٥ - فمن نال الغنى فليصطنعه ... صنيعته ويجهد كل جهد','361','585','0');
INSERT INTO [t17712] VALUES (911,'٣٢٦ - دار الفتاة التي كنا نقول لها ... يا ظبية عطلا حسانة الجيد','361','585','0');
INSERT INTO [t17712] VALUES (912,'٣٢٧ - ولو علم الأقوام كيف خلفتهم ... لرب مفد في القبور وحامد','361','585','0');
INSERT INTO [t17712] VALUES (913,'٣٢٨ - وقد أعددت للعذال عندي ... عصا في رأسها منوا حديد','361','585','0');
INSERT INTO [t17712] VALUES (914,'٣٢٩ - أهان دمك فرغا بعد عزته ... يا عمرو بغيك إصرارا على الحسد','361','585','0');
INSERT INTO [t17712] VALUES (915,'٣٣٠ - وأنت الذي يا سعد أبت بمشهد ... كريم وأثواب السيادة والحمد','361','585','0');
INSERT INTO [t17712] VALUES (916,'٣٣١ - أليس أكرم خلق الله قد علموا ... عند الحفاظ بنو عمرو بن حنجود','361','585','0');
INSERT INTO [t17712] VALUES (917,'٣٣٢ - فما سبق القيسي من سوء سيرة ... ولكن طغت علماء غرلة خالد','362','585','0');
INSERT INTO [t17712] VALUES (918,'٣٣٣ - فكنت كالساعي إلى مثعب ... موائلا من سبل الراعد','362','585','0');
INSERT INTO [t17712] VALUES (919,'٣٣٤ - في خمس عشرة من جمادى ليلة ... لا أستطيع على الفراش رقادي','362','585','0');
INSERT INTO [t17712] VALUES (920,'٣٣٥ - إذا ما عد أربعة فسال ... فزوجك خامس وأبوك سادي','362','585','0');
INSERT INTO [t17712] VALUES (921,'٣٣٦ - إلى ردح من الشيزى ملاء ... لباب البر يلبك بالشهاد','363','585','0');
INSERT INTO [t17712] VALUES (922,'٣٣٧ - الضاربون عميرا عن ديارهم ... بالتل يوم عمير ظالم عادي','363','585','0');
INSERT INTO [t17712] VALUES (923,'٣٣٨ - علم القبائل من معد وغيرها ... أن الجواد محمد بن عطارد','363','585','0');
INSERT INTO [t17712] VALUES (924,'٣٣٩ - وفاء يا معية من أبيه ... لمن أوفى بعهد أو بعقد','363','585','0');
INSERT INTO [t17712] VALUES (925,'٣٤٠ - فإنك موشك أن لا تراها ... وتعدو دون غاضرة العوادي','364','585','0');
INSERT INTO [t17712] VALUES (926,'٣٤١ - ومن يتق فإن الله معه ... ورزق الله مؤتاب وغادي','364','585','0');
INSERT INTO [t17712] VALUES (927,'٣٤٢ - قامت به تنشد كل منشد ... فايتصلت بمثل ضوء الفرقد','364','585','0');
INSERT INTO [t17712] VALUES (928,'٣٤٣ - قرنبى يحك قفا مقرف ... لئيم مآثره قعدد','364','585','0');
INSERT INTO [t17712] VALUES (929,'٣٤٤ - ولا يرهب ابن العم مني صولة ... ولا أختتي من صولة المتهدد وإني إن أوعدته أو وعدته ... ليأمن ميعادي ومنجز موعدي','364','585','0');
INSERT INTO [t17712] VALUES (930,'٣٤٥ - أنا الرجل الضرب الذي تعرفونه ... خشاش كرأس الحية المتوقد','365','585','0');
INSERT INTO [t17712] VALUES (931,'٣٤٦ - خمولا وإهمالا وغيرك مولع ... بتثبيت أسباب السيادة والمجد','365','585','0');
INSERT INTO [t17712] VALUES (932,'٣٤٧ - إن قلت خيرا قال شرا غيره ... أو قلت شرا مده بمداد','365','585','0');
INSERT INTO [t17712] VALUES (933,'٣٤٨ - فتركن نهلا عيلا أبناؤها ... وبني كنانة كاللصوت المرد','365','585','0');
INSERT INTO [t17712] VALUES (934,'٣٤٩ - إذا ما دعوا كيسان كانت كهولهم ... إلى الغدر أدنى من شبابهم المرد','365','585','0');
INSERT INTO [t17712] VALUES (935,'٣٥٠ - فقدني وإياهم فإن ألق بعضهم ... يكونوا كتعجيل السنام المسرهد','366','585','0');
INSERT INTO [t17712] VALUES (936,'٣٥١ - ولكن مولاي امرؤ هو خانقي ... على الشكر والتسآل أو أنا مفتدي','366','585','0');
INSERT INTO [t17712] VALUES (937,'٣٥٢ - إن من الحي موجودا خليفته ... وما خليف أبي وهب بموجود','366','585','0');
INSERT INTO [t17712] VALUES (938,'٣٥٣ - لو كان لي وزهير ثالث وردت ... من الحمام عدانا شر مورود','366','585','0');
INSERT INTO [t17712] VALUES (939,'٣٥٤ - إذا رأيت بواد حية ذكرا ... فاذهب ودعني أمارس حية الوادي','367','585','0');
INSERT INTO [t17712] VALUES (940,'٣٥٥ - تجلدت حتى قيل لم يعر قلبه ... من الوجد شيء، قلت: بل أعظم الوجد','367','585','0');
INSERT INTO [t17712] VALUES (941,'٣٥٦ - إخالك إن لم تغضض الطرف ذاهوى ... يسومك ما لا يستطاع من الوجد','367','585','0');
INSERT INTO [t17712] VALUES (942,'٣٥٧ - إذا قلت عل القلب يسلو قيضت ... هواجس لا تنفك تغريه بالوجد','367','585','0');
INSERT INTO [t17712] VALUES (943,'٣٥٨ - أمن بعد رمي الغانيات فؤاده ... بأسهم ألحاظ يلام على الوجد','367','585','0');
INSERT INTO [t17712] VALUES (944,'٣٥٩ - ردوا فو الله لاذدناكم أبدا ... ما دام في مائنا ورد لوراد','367','585','0');
INSERT INTO [t17712] VALUES (945,'٣٦٠ - سقط النصيف ولم ترد إسقاطه ... فتناولته واتقتنا باليد','367','585','0');
INSERT INTO [t17712] VALUES (946,'٣٦١ - ولو عن نثا غيره جاءني ... وجرح اللسان كجرح اليد','368','585','0');
INSERT INTO [t17712] VALUES (947,'٣٦٢ - فقمنا ولما يصح ديكنا ... إلى جونة عند حدادها','368','585','0');
INSERT INTO [t17712] VALUES (948,'٣٦٣ - وجدت إذا اصطلحوا خيرهم ... وزندك أثقب أزنادها','368','585','0');
INSERT INTO [t17712] VALUES (949,'٣٦٤ - وكم دون بيتك من صفصف ... ودكداك رمل وأعقادها ووضع سقاء وإحقابه ... وحل حلوس وإغمادها','368','585','0');
INSERT INTO [t17712] VALUES (950,'٣٦٥ - كل غراء إذا ما برزت ... ترهب العين عليها والحسد','369','585','0');
INSERT INTO [t17712] VALUES (951,'٣٦٦ - عاضها الله غلاما بعد ما ... شابت الأصداغ والضرس نقد','369','585','0');
INSERT INTO [t17712] VALUES (952,'٣٦٧ - فأمطرت لؤلؤا من نرجس وسقت وردا ... وعضت على العناب بالبرد','369','585','0');
INSERT INTO [t17712] VALUES (953,'٣٦٨ - نحن الذين بايعوا محمدا ... على الجهاد ما بقينا أبدا','370','585','0');
INSERT INTO [t17712] VALUES (954,'قافية الذال','371','0','0');
INSERT INTO [t17712] VALUES (955,'١ - ألا حبذا حبذا حبذا ... حبيب تحملت منه الأذى','371','954','0');
INSERT INTO [t17712] VALUES (956,'٢ - سقى الحيا الأرض حتى أمكن عزيت ... لهم فلا زال عنها الخير مجذوذا','371','954','0');
INSERT INTO [t17712] VALUES (957,'٣ - لكل جديد لذة غير أنني ... وجدت جديد الموت غير لذيذ','371','954','0');
INSERT INTO [t17712] VALUES (958,'باب الراء','372','0','0');
INSERT INTO [t17712] VALUES (959,'١ - إلى الحول ثم اسم السلام عليكما ... ومن يبك حولا كاملا فقد اعتذر','372','958','0');
INSERT INTO [t17712] VALUES (960,'٢ - قتلنا ونال القتل منا وربما ... يكون على القوم الكرام لنا الظفر','372','958','0');
INSERT INTO [t17712] VALUES (961,'٣ - أيقنت أني لا محا ... لة حيث صار القوم صائر','372','958','0');
INSERT INTO [t17712] VALUES (962,'٤ - لم يك الحق على أن هاجه ... رسم دار قد تعفى بالسرر','373','958','0');
INSERT INTO [t17712] VALUES (963,'٥ - ففداء لبني قيس على ... ما أصاب الناس من سر وضر ما أقلت قدمي إنهم ... نعم الساعون في الأمر المبر','373','958','0');
INSERT INTO [t17712] VALUES (964,'٦ - لا تفزع الأرنب أهوالها ... ولا ترى الضب بها ينجحر','374','958','0');
INSERT INTO [t17712] VALUES (965,'٧ - إذا أقبلت قلت دباءة ... من الخضر مغموسة في الغدر','374','958','0');
INSERT INTO [t17712] VALUES (966,'٨ - ولم أر قوما مثلنا خير قومهم ... أقل به منا على قومنا فخرا','374','958','0');
INSERT INTO [t17712] VALUES (967,'٩ - ألا هل أتاها- والحوادث جمة ... بأن امرأ القيس بن تملك بيقرا','375','958','0');
INSERT INTO [t17712] VALUES (968,'١٠ - فلما رأى أن ثمر الله ماله ... وأثل موجودا وسد مفاقره','375','958','0');
INSERT INTO [t17712] VALUES (969,'١١ - يا أيها النابح العاوي لشقوته ... إليك أخبرك عما تجهل الخبرا لو لم تكن غطفان لا ذنوب لها ... إذن للام ذوو أحسابها عمرا','376','958','0');
INSERT INTO [t17712] VALUES (970,'١٢ - أو راعيان لبعران شردن لنا ... كي لا يحسان من بعراننا أثرا','376','958','0');
INSERT INTO [t17712] VALUES (971,'١٣ - وما حب الديار شغفن قلبي ... ولكن حب من سكن الديارا','377','958','0');
INSERT INTO [t17712] VALUES (972,'١٤ - بانت لتحزننا عفاره ... يا جارتا ما أنت جاره','377','958','0');
INSERT INTO [t17712] VALUES (973,'١٥ - ولم يستريثوك حتى رمي ... ت فوق الرجال خصالا عشارا','377','958','0');
INSERT INTO [t17712] VALUES (974,'١٦ - أماوي إني رب واحد أمه ... أجرت فلا قتل عليه ولا أسر','378','958','0');
INSERT INTO [t17712] VALUES (975,'١٧ - ولكن أجرا لو فعلت بهين ... وهل ينكر المعروف في الناس والأجر','378','958','0');
INSERT INTO [t17712] VALUES (976,'١٨ - كأن على عرنينه وجبينه ... أقام شعاع الشمس أو طلع البدر','378','958','0');
INSERT INTO [t17712] VALUES (977,'١٩ - لئن كان إياه لقد حال بعدنا ... عن العهد والإنسان قد يتغير','379','958','0');
INSERT INTO [t17712] VALUES (978,'٢٠ - نحابي بها أكفاءنا ونهينها ... ونشرب في أثمانها ونقامر','379','958','0');
INSERT INTO [t17712] VALUES (979,'٢١ - فأجمل وأحسن في أسيرك إنه ... ضعيف ولم يأسر كإياك آسر','379','958','0');
INSERT INTO [t17712] VALUES (980,'٢٢ - حلفت له إن تدلج الليل لا يزل ... أمامك بيت من بيوتي سائر','380','958','0');
INSERT INTO [t17712] VALUES (981,'٢٣ - فإن تك ذا شاء كثير فإنهم ... ذوو جامل لا يهدأ الليل سامره','380','958','0');
INSERT INTO [t17712] VALUES (982,'٢٤ - فأما الصدور لا صدور لجعفر ... ولكن أعجازا شديدا ضريرها','380','958','0');
INSERT INTO [t17712] VALUES (983,'٢٥ - وليل يقول الناس من ظلماته ... سواء صحيحات العيون وعورها كأن لنا منه بيوتا حصينة ... مسوحا أعاليها وساجا كسورها','381','958','0');
INSERT INTO [t17712] VALUES (984,'٢٦ - أخو رغائب يعطيها ويسألها ... يأبى الظلامة منه النوفل الزفر','381','958','0');
INSERT INTO [t17712] VALUES (985,'٢٧ - لنا يوم وللكروان يوم ... تطير البائسات ولا نطير','382','958','0');
INSERT INTO [t17712] VALUES (986,'٢٨ - قد كنت أحسبكم أسود خفية ... فإذا لصاف تبيض فيه الحمر','382','958','0');
INSERT INTO [t17712] VALUES (987,'٢٩ - ولما رأى الرحمن أن ليس فيهم ... رشيد ولا ناه أخاه عن الغدر وصب عليهم تغلب ابنة وائل ... فكانوا عليهم مثل راغية البكر','383','958','0');
INSERT INTO [t17712] VALUES (988,'٣٠ - وكلمتها ثنتين كالثلج منهما ... على اللوح والأخرى أحر من الجمر','383','958','0');
INSERT INTO [t17712] VALUES (989,'٣١ - كأنه وجه تركيين قد غضبا ... مستهدف لطعان غير منحجر','384','958','0');
INSERT INTO [t17712] VALUES (990,'٣٢ - بلى أير الحمار وخصيتاه ... أحب إلى فزارة من فزار','384','958','0');
INSERT INTO [t17712] VALUES (991,'٣٣ - نصف النهار الماء غامره ... ورفيقه بالغيب لا يدري','385','958','0');
INSERT INTO [t17712] VALUES (992,'٣٤ - ورثوا السيادة كابرا عن كابر ... إن الخيار هم بنو الأخيار','386','958','0');
INSERT INTO [t17712] VALUES (993,'٣٥ - متكنفي جنبي عكاظ كليهما ... يدعو وليدهم بها عرعار','387','958','0');
INSERT INTO [t17712] VALUES (994,'٣٦ - وخطرت أيدي الكماة وخطر ... راي إذا أورده الطعن صدر','388','958','0');
INSERT INTO [t17712] VALUES (995,'٣٧ - إذا تخازرت وما بي من خزر','388','958','0');
INSERT INTO [t17712] VALUES (996,'٣٨ - لست بليلي ولكني نهر ... لا أدلج الليل ولكن أبتكر','388','958','0');
INSERT INTO [t17712] VALUES (997,'٣٩ - يا سارق الليلة أهل الدار','389','958','0');
INSERT INTO [t17712] VALUES (998,'٤٠ - أعوذ برب العرش من فئة بغت ... علي فمالي عوض إلاه ناصر','389','958','0');
INSERT INTO [t17712] VALUES (999,'٤١ - وما علينا إذا ما كنت جارتنا ... أن لا يجاورنا إلاك ديار','390','958','0');
INSERT INTO [t17712] VALUES (1000,'٤٢ - إني حلفت ولم أحلف على فند ... فناء بيت من الساعين معمور بالباعث الوارث الأموات قد ضمنت ... إياهم الأرض في دهر الدهارير','390','958','0');
INSERT INTO [t17712] VALUES (1001,'٤٣ - بكيت على سرب القطا إذ مررن بي ... فقلت ومثلي بالبكاء جدير','390','958','0');
INSERT INTO [t17712] VALUES (1002,'٤٤ - ما الله موليك فضل فاحمدنه به ... فما لدى غيره نفع ولا ضرر','391','958','0');
INSERT INTO [t17712] VALUES (1003,'٤٥ - ولقد جنيتك أكمؤا وعساقلا ... ولقد نهيتك عن بنات الأوبر','391','958','0');
INSERT INTO [t17712] VALUES (1004,'٤٦ - رأيتك لما أن عرفت وجوهنا ... صددت وطبت النفس يا قيس عن عمرو','392','958','0');
INSERT INTO [t17712] VALUES (1005,'٤٧ - فأقبلت زحفا على الركبتين ... فثوب لبست وثوب أجر','392','958','0');
INSERT INTO [t17712] VALUES (1006,'٤٨ - كم عمة لك يا جرير وخالة ... فدعاء قد حلبت علي عشاري','393','958','0');
INSERT INTO [t17712] VALUES (1007,'٤٩ - إلى ملك ما أمه من محارب ... أبوه ولا كانت كليب تصاهره','393','958','0');
INSERT INTO [t17712] VALUES (1008,'٥٠ - إني وأسطار سطرن سطرا ... لقائل يا نصر نصرا نصرا','394','958','0');
INSERT INTO [t17712] VALUES (1009,'٥١ - كيف رأيت زبرا ... أأقطا أو تمرا أم قريشا صقرا','394','958','0');
INSERT INTO [t17712] VALUES (1010,'٥٢ - أنعت أعيارا رعين الخنزرا ... أنعتهن آيرا وكمرا','394','958','0');
INSERT INTO [t17712] VALUES (1011,'٥٣ - يقوم تارات ويمشي تيرا','395','958','0');
INSERT INTO [t17712] VALUES (1012,'٥٤ - ألا يا اسلمي يا دارمي على البلى ... ولا زال منهلا بجرعاتك القطر','395','958','0');
INSERT INTO [t17712] VALUES (1013,'٥٥ - ببذل وحلم ساد في قومه الفتى ... وكونك إياه عليك يسير','395','958','0');
INSERT INTO [t17712] VALUES (1014,'٥٦ - فأبت إلى فهم وما كدت آيبا ... وكم مثلها فارقتها وهي تصفر','396','958','0');
INSERT INTO [t17712] VALUES (1015,'٥٧ - عسى فرج يأتي به الله إنه ... له كل يوم في خليقته أمر','396','958','0');
INSERT INTO [t17712] VALUES (1016,'٥٨ - واعلم فعلم المرء ينفعه ... أن سوف يأتي كل ما قدرا','397','958','0');
INSERT INTO [t17712] VALUES (1017,'٥٩ - تعلم شفاء النفس قهر عدوها ... فبالغ بلطف في التحيل والمكر','397','958','0');
INSERT INTO [t17712] VALUES (1018,'٦٠ - نبئت زرعة- والسفاهة كاسمها- ... يهدي إلي غرائب الأشعار','397','958','0');
INSERT INTO [t17712] VALUES (1019,'٦١ - رأين الغواني الشيب لاح بعارضي ... فأعرضن عني بالخدود النواضر','398','958','0');
INSERT INTO [t17712] VALUES (1020,'٦٢ - لما رأى طالبوه مصعبا ذعروا ... وكاد- لو ساعد المقدور- ينتصر','398','958','0');
INSERT INTO [t17712] VALUES (1021,'٦٣ - جزى بنوه أبا الغيلان عن كبر ... وحسن فعل كما يجزى سنمار','398','958','0');
INSERT INTO [t17712] VALUES (1022,'٦٤ - هل الدهر إلا ليلة ونهارها ... وإلا طلوع الشمس ثم غيارها','398','958','0');
INSERT INTO [t17712] VALUES (1023,'٦٥ - وإذا تباع كريمة أو تشترى ... فسواك بائعها وأنت المشتري','399','958','0');
INSERT INTO [t17712] VALUES (1024,'٦٦ - أبحنا حيهم قتلا وأسرا ... عدا الشمطاء والطفل الصغير','399','958','0');
INSERT INTO [t17712] VALUES (1025,'٦٧ - أنا ابن دارة معروفا بها نسبي ... وهل بدارة يا للناس من عار؟','399','958','0');
INSERT INTO [t17712] VALUES (1026,'٦٨ - وإني لتعروني لذكراك هزة ... كما انتفض العصفور بلله القطر','400','958','0');
INSERT INTO [t17712] VALUES (1027,'٦٩ - ربما الجامل المؤبل فيهم ... وعناجيج بينهن المهار','400','958','0');
INSERT INTO [t17712] VALUES (1028,'٧٠ - دعوت لما نابني مسورا ... فلبى، فلبي يدي مسور','400','958','0');
INSERT INTO [t17712] VALUES (1029,'٧١ - تنتهض الرعدة في ظهيري ... من لدن الظهر إلى العصير','401','958','0');
INSERT INTO [t17712] VALUES (1030,'٧٢ - أكل امرئ تحسبين امرأ ... ونار توقد بالليل نارا','401','958','0');
INSERT INTO [t17712] VALUES (1031,'٧٣ - وفاق- كعب- بجير منقذ لك من ... تعجيل تهلكة والخلد في سقر','401','958','0');
INSERT INTO [t17712] VALUES (1032,'٧٤ - إذا صح عون الخالق المرء لم يجد ... عسيرا من الآمال إلا ميسرا','402','958','0');
INSERT INTO [t17712] VALUES (1033,'٧٥ - حذر أمورا لا تضير وآمن ... ما ليس منجيه من الأقدار','402','958','0');
INSERT INTO [t17712] VALUES (1034,'٧٦ - ثم زادوا أنهم في قومهم ... غفر ذنبهم غير فخر','402','958','0');
INSERT INTO [t17712] VALUES (1035,'٧٧ - أرى أم عمرو دمعها قد تحدرا ... بكاء على عمرو وما كان أصبرا','403','958','0');
INSERT INTO [t17712] VALUES (1036,'٧٨ - فذلك إن يلق المنية يلقها ... حميدا وإن يستغن يوما فأجدر','403','958','0');
INSERT INTO [t17712] VALUES (1037,'٧٩ - خليلي ما أحرى بذي اللب أن يرى ... صبورا ولكن لا سبيل إلى الصبر','404','958','0');
INSERT INTO [t17712] VALUES (1038,'٨٠ - تقول عرسي وهي لي في عومره ... بئس امرأ وإنني بئس المره','404','958','0');
INSERT INTO [t17712] VALUES (1039,'٨١ - ولست بالأكثر منهم حصى ... وإنما العزة للكاثر','404','958','0');
INSERT INTO [t17712] VALUES (1040,'٨٢ - أقسم بالله أبو حفص عمر ... ما مسها من نقب ولا دبر فاغفر له اللهم إن كان فجر','405','958','0');
INSERT INTO [t17712] VALUES (1041,'٨٣ - جاء الخلافة أو كانت له قدرا ... كما أتى ربه موسى على قدر','405','958','0');
INSERT INTO [t17712] VALUES (1042,'٨٤ - فألفيته يوما يبير عدوه ... ومجر عطاء يستحق المعابرا','406','958','0');
INSERT INTO [t17712] VALUES (1043,'٨٥ - بات يغشيها بعضب باتر ... يقصد في أسوقها وجائر','406','958','0');
INSERT INTO [t17712] VALUES (1044,'٨٦ - الله يعلم أنا في تلفتنا ... يوم الفراق إلى إخواننا صور وأنني حيثما يثني الهوى بصري ... من حيثما سلكوا أدنو فأنظور','407','958','0');
INSERT INTO [t17712] VALUES (1045,'٨٧ - كأن عذيرهم بجنوب سلى ... نعام قاق في بلد قفار','407','958','0');
INSERT INTO [t17712] VALUES (1046,'٨٨ - إني ضمنت لمن أتاني ما جنى ... وأبى فكنت وكان غير غدور','407','958','0');
INSERT INTO [t17712] VALUES (1047,'٨٩ - ألا يا اسلمي يا هند هند بني بدر ... وإن كان حيانا عدى آخر الدهر','407','958','0');
INSERT INTO [t17712] VALUES (1048,'٩٠ - فيا الغلامان اللذان فرا ... إياكما أن تعقبانا شرا','408','958','0');
INSERT INTO [t17712] VALUES (1049,'٩١ - يا تيم تيم عدي لا أبالكم ... لا يلقينكم في سوأة عمر','408','958','0');
INSERT INTO [t17712] VALUES (1050,'٩٢ - لها بشر مثل الحرير ومنطق ... رخيم الحواشي: لا هراء ولا نزر','409','958','0');
INSERT INTO [t17712] VALUES (1051,'٩٣ - لنعم الفتى تعشو إلى ضوء ناره ... طريف بن مال ليلة الجوع والخصر','410','958','0');
INSERT INTO [t17712] VALUES (1052,'٩٤ - لأستسهلن الصعب أو أدرك المنى ... فما انقادت الآمال إلا لصابر','410','958','0');
INSERT INTO [t17712] VALUES (1053,'٩٥ - مالك عندي غير سهم وحجر ... وغير كبداء شديدة الوتر جادت بكفي كان من أرمى البشر','410','958','0');
INSERT INTO [t17712] VALUES (1054,'٩٦ - ألم يخز التفرق جند كسرى ... ونفخوا في مدائنهم فطاروا','411','958','0');
INSERT INTO [t17712] VALUES (1055,'٩٧ - وأبيض من ماء الحديد كأنه ... شهاب بدا والليل داج عساكره','411','958','0');
INSERT INTO [t17712] VALUES (1056,'٩٨ - بحسبك في القوم أن يعلموا ... بأنك فيهم غني مضر','411','958','0');
INSERT INTO [t17712] VALUES (1057,'٩٩ - إني وقتلي سليكا ثم أعقله ... كالثور يضرب لما عافت البقر','411','958','0');
INSERT INTO [t17712] VALUES (1058,'١٠٠ - أيان نومنك تأمن غيرنا وإذا ... لم تدرك الأمن منا لم تزل حذرا','412','958','0');
INSERT INTO [t17712] VALUES (1059,'١٠١ - يا أبا الأسود لم أسلمتني ... لهموم طارقات وذكر','412','958','0');
INSERT INTO [t17712] VALUES (1060,'١٠٢ - فإياك والأمر الذي إن توسعت ... مداخله ضاقت عليك المصادر فما حسن أن يعذر المرء نفسه ... وليس له من سائر الناس عاذر','413','958','0');
INSERT INTO [t17712] VALUES (1061,'١٠٣ - تربص بها الأيام عل صروفها ... سترمي بها في جاحم متسعر','413','958','0');
INSERT INTO [t17712] VALUES (1062,'١٠٤ - لقد ضجت الأرضون إذقام من بني ... هداد خطيب فوق أعواد منبر','413','958','0');
INSERT INTO [t17712] VALUES (1063,'١٠٥ - متى ما تردن يوما سفار تجد بها ... أديهم يرمي المستجيز المعورا','413','958','0');
INSERT INTO [t17712] VALUES (1064,'١٠٦ - ألم تروا إرما وعادا ... أودى بها الليل والنهار ومر دهر على وبار ... فهلكت جهرة وبار','414','958','0');
INSERT INTO [t17712] VALUES (1065,'١٠٧ - ولما رأيت الخيل تترى أثائجا ... علمت بأن اليوم أحمس فاجر','414','958','0');
INSERT INTO [t17712] VALUES (1066,'١٠٨ - لمن الديار بقنة الحجر ... أقوين من حجح ومن دهر','415','958','0');
INSERT INTO [t17712] VALUES (1067,'١٠٩ - ليس تخفى يسارتي قدر يوم ... ولقد يخف شيمتي إعساري','415','958','0');
INSERT INTO [t17712] VALUES (1068,'١١٠ - إن الذي أغناك يغنيني جير ... والله نفاح اليدين بالخير','415','958','0');
INSERT INTO [t17712] VALUES (1069,'١١١ - تمر على ما تستمر وقد شفت ... غلائل- عبد القيس منها- صدورها','415','958','0');
INSERT INTO [t17712] VALUES (1070,'١١٢ - ونحن قتلنا الأسد، أسد شنوءة ... فما شربوا بعدا على لذة خمرا','416','958','0');
INSERT INTO [t17712] VALUES (1071,'١١٣ - إيه أحاديث نعمان وساكنه ... إن الحديث عن الأحباب أسمار','416','958','0');
INSERT INTO [t17712] VALUES (1072,'١١٤ - استقدر الله خيرا وأرضين به ... فبينما العسر إذ دارت مياسير','416','958','0');
INSERT INTO [t17712] VALUES (1073,'١١٥ - لسلمى بذات الخال دار عرفتها ... وأخرى بذات الجزع آياتها سطر كأنهما ملآن لم يتغيرا ... وقد مر للدارين من بعدنا عصر','417','958','0');
INSERT INTO [t17712] VALUES (1074,'١١٦ - نعم امرأ هرم لم تعر نائبة ... إلا وكان لمرتاع بها وزرا','418','958','0');
INSERT INTO [t17712] VALUES (1075,'١١٧ - أتيح لي من العدا نذيرا ... به وقيت الشر مستطيرا','418','958','0');
INSERT INTO [t17712] VALUES (1076,'١١٨ - تمنى ابنتاي أن يعيش أبوهما ... وهل أنا إلا من ربيعة أو مضر','418','958','0');
INSERT INTO [t17712] VALUES (1077,'١١٩ - إن امرأ غره منكن واحدة ... بعدي وبعدك في الدنيا لمغرور','419','958','0');
INSERT INTO [t17712] VALUES (1078,'١٢٠ - وقد جعلت إذا ما قمت يثقلني ... ثوبي فأنهض نهض الشارب السكر وكنت أمشي على رجلين معتدلا ... فصرت أمشي على أخرى من الشجر','419','958','0');
INSERT INTO [t17712] VALUES (1079,'١٢١ - وقرب جانب الغربي يأدو ... مدب السيل واجتنب الشعارا','419','958','0');
INSERT INTO [t17712] VALUES (1080,'١٢٢ - كلا ثقلينا واثق بغنيمة ... وقد قدر الرحمن ما هو قادر','420','958','0');
INSERT INTO [t17712] VALUES (1081,'١٢٣ - بالله يا ظبيات القاع قلن لنا ... ليلاي منكن أم ليلى من البشر','420','958','0');
INSERT INTO [t17712] VALUES (1082,'١٢٤ - طلب الأزارق بالكتائب إذ هوت ... بشبيب غائلة الثغور غدور','420','958','0');
INSERT INTO [t17712] VALUES (1083,'١٢٥ - جد بعفو فإنني- أيها العبد- ... إلى العفو يا إلهي فقير','421','958','0');
INSERT INTO [t17712] VALUES (1084,'١٢٦ - أراك علقت تظلم من أجرنا ... وظلم الجار إذلال المجير','421','958','0');
INSERT INTO [t17712] VALUES (1085,'١٢٧ - يسلكن في نجد وغورا غائرا ... فواسقا عن قصدها جوائرا','421','958','0');
INSERT INTO [t17712] VALUES (1086,'١٢٨ - وقد زعمت أني تغيرت بعدها ... ومن ذا الذي يا عز لا يتغير','421','958','0');
INSERT INTO [t17712] VALUES (1087,'١٢٩ - وقد علم الأقوام لو أن حاتما ... أراد ثراء المال كان له وفر','422','958','0');
INSERT INTO [t17712] VALUES (1088,'١٣٠ - إذا قال غاو من تنوخ قصيدة ... بها جرب عدت علي بزوبرا','422','958','0');
INSERT INTO [t17712] VALUES (1089,'١٣١ - أؤمل أن أعيش وأن يومي ... بأول أو بأهون أو جبار أو التالي دبار فإن أفته ... فمؤنس أو عروبة أو شيار','422','958','0');
INSERT INTO [t17712] VALUES (1090,'١٣٢ - فأوفضن عنها وهي ترغو حشاشة ... بذي نفسها والسيف عريان أحمر','423','958','0');
INSERT INTO [t17712] VALUES (1091,'١٣٣ - قامت تبكيه على قبره ... من لي من بعدك يا عامر تركتني في الدار ذا غربة ... قد ذل من ليس له ناصر','423','958','0');
INSERT INTO [t17712] VALUES (1092,'١٣٤ - أستغفر الله من عمدي ومن خطئي ... ذنبي وكل امرئ لا شك مؤتزر','423','958','0');
INSERT INTO [t17712] VALUES (1093,'١٣٥ - ضروب بنصل السيف سوق سمانها ... إذا عدموا زادا فإنك عاقر','424','958','0');
INSERT INTO [t17712] VALUES (1094,'١٣٦ - شتان ما يومي على كورها ... ويوم حيان أخي جابر','424','958','0');
INSERT INTO [t17712] VALUES (1095,'١٣٧ - أخشى على ديسم من بعد الثرى ... أبى قضاء الله إلا ما ترى','424','958','0');
INSERT INTO [t17712] VALUES (1096,'١٣٨ - تراه كأن الله يجدع أنفه ... وعينيه إن مولاه ثاب له وفر','424','958','0');
INSERT INTO [t17712] VALUES (1097,'١٣٩ - له زجل كأنه صوت حاد ... إذا طلب الوسيقة أو زمير','425','958','0');
INSERT INTO [t17712] VALUES (1098,'١٤٠ - لعب الرياح بها وغيرها ... بعدي سوافي المور والقطر','425','958','0');
INSERT INTO [t17712] VALUES (1099,'١٤١ - وسمعت حلفتها التي حلفت ... إن كان سمعك غير ذي وقر','425','958','0');
INSERT INTO [t17712] VALUES (1100,'١٤٢ - أبوك حباب سارق الضيف برده ... وجدي يا حجاج فارس شمرا','426','958','0');
INSERT INTO [t17712] VALUES (1101,'١٤٣ - فأصبحت أنى تأتها تلتبس بها ... كلا مركبيها تحت رحلك شاجر','426','958','0');
INSERT INTO [t17712] VALUES (1102,'١٤٤ - كأن لم يكن بين الحجون إلى الصفا ... أنيس ولم يسمر بمكة سامر','426','958','0');
INSERT INTO [t17712] VALUES (1103,'١٤٥ - فلا أب وابنا مثل مروان وابنه ... إذا هو بالمجد ارتدى وتأزرا','427','958','0');
INSERT INTO [t17712] VALUES (1104,'١٤٦ - وحلت بيوتي في يفاع ممنع ... يخال به راعي الحمولة طائرا','428','958','0');
INSERT INTO [t17712] VALUES (1105,'١٤٧ - أبالأراجيز يا بن اللؤم توعدني ... وفي الأراجيز خلت اللؤم والخور','428','958','0');
INSERT INTO [t17712] VALUES (1106,'١٤٨ - قفي فانظري يا أسم هل تعرفينه ... أهذا المغيري الذي كان يذكر؟','429','958','0');
INSERT INTO [t17712] VALUES (1107,'١٤٩ - حملت أمرا عظيما فاصطبرت له ... وقمت فيه بأمر الله يا عمرا','429','958','0');
INSERT INTO [t17712] VALUES (1108,'١٥٠ - في أي يومي من الموت أفر ... أيوم لم يقدر أم يوم قدر','429','958','0');
INSERT INTO [t17712] VALUES (1109,'١٥١ - لكم مسجدا الله المزوران والحصى ... لكم قبصه من بين أثرى وأقترا','429','958','0');
INSERT INTO [t17712] VALUES (1110,'١٥٢ - ألا هلك الشهاب المستنير ... ومدرهنا الكمي إذا نغير وحمال المئين إذا ألمت ... بنا الحدثان والأنف النصور','431','958','0');
INSERT INTO [t17712] VALUES (1111,'١٥٣ - وقائع في مضر تسعة ... وفي وائل كانت العاشره','431','958','0');
INSERT INTO [t17712] VALUES (1112,'١٥٤ - وينمي لها حبها عندنا ... فما قال من كاشح لم يضر','431','958','0');
INSERT INTO [t17712] VALUES (1113,'١٥٥ - حراجيج ما تنفك إلا مناخة ... على الخسف أو يرمى بها بلدا قفرا','432','958','0');
INSERT INTO [t17712] VALUES (1114,'١٥٦ - تقول وقد عاليت بالكور فوقها ... أيسقى فلا يروى إلي ابن أحمرا','432','958','0');
INSERT INTO [t17712] VALUES (1115,'١٥٧ - قهرناكم حتى الكماة فأنتم ... لتخشوننا حتى بنينا الأصاغرا','432','958','0');
INSERT INTO [t17712] VALUES (1116,'١٥٨ - ألا ليس إلا ما قضى الله كائن ... وما يستطيع المرء نفعا ولا ضرا','432','958','0');
INSERT INTO [t17712] VALUES (1117,'١٥٩ - ألا ليت شعري هل إلى أم جعفر ... سبيل فأما الصبر عنها فلا صبرا','432','958','0');
INSERT INTO [t17712] VALUES (1118,'١٦٠ - عليك بأرباب الصدور فمن غدا ... مضافا لأرباب الصدور تصدرا وإياك أن ترضى صحابة ناقص ... فتنحط قدرا من علاك وتحقرا فرفع «أبو من» ثم خفض «مزمل» ... يبين قولي مغريا ومحذرا','433','958','0');
INSERT INTO [t17712] VALUES (1119,'١٦١ - وكنا حسبنا كل بيضاء شحمة ... عشية لاقينا جذام وحميرا','433','958','0');
INSERT INTO [t17712] VALUES (1120,'١٦٢ - فطافت ثلاثا بين يوم وليلة ... وكان النكير: أن تضيف وتجأرا','434','958','0');
INSERT INTO [t17712] VALUES (1121,'١٦٣ - إنني رمت الخطوب فتى ... فوجدت العيش أطوارا ليس يفني عيشه أحد ... لا يلاقي فيه إمعارا من صديق أو أخي ثقة ... أو عدو شاحط دارا','435','958','0');
INSERT INTO [t17712] VALUES (1122,'١٦٤ - إنارة العقل مكسوف بطوع هوى ... وعقل عاصي الهوى يزداد تنويرا','436','958','0');
INSERT INTO [t17712] VALUES (1123,'١٦٥ - قبائلنا سبع وأنتم ثلاثة ... وللسبع خير من ثلاث وأكثر','436','958','0');
INSERT INTO [t17712] VALUES (1124,'١٦٦ - أزيد بن مصبوح فلو غيركم جنى ... غفرنا وكانت من سجيتنا الغفر','436','958','0');
INSERT INTO [t17712] VALUES (1125,'١٦٧ - عهدي بها في الحي قد سربلت ... بيضاء مثل المهرة الضامر','436','958','0');
INSERT INTO [t17712] VALUES (1126,'١٦٨ - تروح من الحي أم تبتكر ... وماذا يضرك لو تنتظر','437','958','0');
INSERT INTO [t17712] VALUES (1127,'١٦٩ - فما تك يا ابن عبد الله فينا ... فلا ظلما نخاف ولا افتقارا','437','958','0');
INSERT INTO [t17712] VALUES (1128,'١٧٠ - أمر على الديار ديار ليلى ... أقبل ذا الجدار وذا الجدارا وما حب الديار شغفن قلبي ... ولكن حب من سكن الديارا','437','958','0');
INSERT INTO [t17712] VALUES (1129,'١٧١ - إذا ما شاء ضروا من أرادوا ... ولا يألو لهم أحد ضرارا','437','958','0');
INSERT INTO [t17712] VALUES (1130,'١٧٢ - قالت سلامة لم يكن لك عادة ... أن تترك الأعداء حتى تعذرا لو كان قتل يا سلام فراحة ... لكن فررت مخافة أن أوسرا','438','958','0');
INSERT INTO [t17712] VALUES (1131,'١٧٣ - ألف الصفون فما يزال كأنه ... مما يقوم على الثلاث كسيرا','438','958','0');
INSERT INTO [t17712] VALUES (1132,'١٧٤ - لا تتركني فيهم شطيرا ... إني إذن أهلك أو أطيرا','438','958','0');
INSERT INTO [t17712] VALUES (1133,'١٧٥ - لذ بقيس حين يأبى غيره ... تلفه بحرا مفيضا خيره','439','958','0');
INSERT INTO [t17712] VALUES (1134,'١٧٦ - أطلب ولا تضجر من مطلب ... فآفة الطالب أن يضجرا أما ترى الحبل بتكراره ... في الصخرة الصماء قد أثرا','439','958','0');
INSERT INTO [t17712] VALUES (1135,'١٧٧ - ولا ألوم البيض ألا تسخرا ... من شمط الشيخ وألا تذعرا','439','958','0');
INSERT INTO [t17712] VALUES (1136,'١٧٨ - في بئر لا حور سرى وما شعر.','439','958','0');
INSERT INTO [t17712] VALUES (1137,'١٧٩ - لقد أنكرتني بعلبك وأهلها ... ولابن جريج كان في حمص أنكرا','440','958','0');
INSERT INTO [t17712] VALUES (1138,'١٨٠ - فالشمس طالعة ليست بكاشفة ... تبكي عليك نجوم الليل والقمرا','440','958','0');
INSERT INTO [t17712] VALUES (1139,'١٨١ - كم ملوك باد ملكهم ... ونعيم سوقة بارا','440','958','0');
INSERT INTO [t17712] VALUES (1140,'١٨٢ - فتى في سبيل الله أصفر وجهه ... ووجهك مما في القوارير أصفرا','440','958','0');
INSERT INTO [t17712] VALUES (1141,'١٨٣ - إن فيها أخيك وابن هشام ... وعليها أخيك والمختارا','440','958','0');
INSERT INTO [t17712] VALUES (1142,'١٨٤ - إلى ملك كاد الجبال لفقده ... تزول وزال الراسيات من الصخر','440','958','0');
INSERT INTO [t17712] VALUES (1143,'١٨٥ - أصبح مني الشباب مبتكرا ... إن ينأ مني فقد ثوى عصرا فارقنا قبل أن نفارقه ... لما قضى من جماعنا وطرا','441','958','0');
INSERT INTO [t17712] VALUES (1144,'١٨٦ - فتولى غلامهم ثم نادى ... أظليما أصيدكم أم حمارا','441','958','0');
INSERT INTO [t17712] VALUES (1145,'١٨٧ - لا أرى الموت يسبق الموت شيء ... نغص الموت ذا الغنى والفقيرا','442','958','0');
INSERT INTO [t17712] VALUES (1146,'١٨٨ - لعمرك إني واردا بعد سبعة ... لأعشى وإني صادرا لبصير','442','958','0');
INSERT INTO [t17712] VALUES (1147,'١٨٩ - إليك إليك عذرة بعد عذرة ... وقد يبلغ الشر السديل المشمر','442','958','0');
INSERT INTO [t17712] VALUES (1148,'١٩٠ - هشام ابن الخلائف قد طوتني ... ببابك سبعة عددا شهور بعيرا واقفان وصاحبيه ... ألما يأن أن يثم البعير','442','958','0');
INSERT INTO [t17712] VALUES (1149,'١٩١ - أحل به الشيب أثقاله ... وما اغتره الشيب إلا اغترارا','442','958','0');
INSERT INTO [t17712] VALUES (1150,'١٩٢ - وتسخن ليلة لا يستطيع ... نباحا بها الكلب إلا هريرا','442','958','0');
INSERT INTO [t17712] VALUES (1151,'١٩٣ - أنفسا تطيب بنيل المنى ... وداعي المنون ينادي جهارا','443','958','0');
INSERT INTO [t17712] VALUES (1152,'١٩٤ - فأمهله حتى إذا أن كأنه ... معاطي يد من لجة الماء غامر','443','958','0');
INSERT INTO [t17712] VALUES (1153,'١٩٥ - إذا قلت: هذا حين أسلو يهيجني ... نسيم الصبا من حيث يطلع الفجر','443','958','0');
INSERT INTO [t17712] VALUES (1154,'١٩٦ - أفي الحق أني مغرم بك هائم ... وأنك لا خل هواك ولا خمر','444','958','0');
INSERT INTO [t17712] VALUES (1155,'١٩٧ - رأت رجلا أيما إذا الشمس عارضت ... فيضحى وأيما بالعشي فيخصر','445','958','0');
INSERT INTO [t17712] VALUES (1156,'١٩٨ - ألم تسمعي أي عبد في رونق الضحى ... بكاء حمامات لهن هدير','446','958','0');
INSERT INTO [t17712] VALUES (1157,'١٩٩ - أيادي سبا يا عز ما كنت بعدكم ... فلن يحل للعينين بعدك منظر','446','958','0');
INSERT INTO [t17712] VALUES (1158,'٢٠٠ - إني إذا ما كان أمر منكر ... وازدحم الورد وضاق المصدر وجدتني أنا الربيس الأكبر','446','958','0');
INSERT INTO [t17712] VALUES (1159,'٢٠١ - وو الله ما أدري وإني لشاكر ... لكثرة ما أوليتني كيف أشكر','447','958','0');
INSERT INTO [t17712] VALUES (1160,'٢٠٢ - طليق الله لم يمنن عليه ... أبو داود وابن أبي كثير ولا الحجاج عيني بنت ماء ... تقلب طرفها حذر الصقور','447','958','0');
INSERT INTO [t17712] VALUES (1161,'٢٠٣ - وطرفك إما جئتنا فاحبسنه ... كما يحسبوا أن الهوى حيث تنظر','447','958','0');
INSERT INTO [t17712] VALUES (1162,'٢٠٤ - ذكرتك والخطي يخطر بيننا ... وقد نهلت منا المثقفة السمر','448','958','0');
INSERT INTO [t17712] VALUES (1163,'٢٠٥ - لقد أذهلتني أم عمرو بكلمة ... أتصبر يوم البين أم لست تصبر','448','958','0');
INSERT INTO [t17712] VALUES (1164,'٢٠٦ - أسكران كان ابن المراغة إذ هجا ... تميما بجو الشام أم متساكر','448','958','0');
INSERT INTO [t17712] VALUES (1165,'٢٠٧ - نبئت نعما على الهجران عاتبة ... سقيا ورعيا لذاك العاتب الزاري','448','958','0');
INSERT INTO [t17712] VALUES (1166,'٢٠٨ - لعمرك ما خشيت على عدي ... سيوف بني مقيدة الحمار','448','958','0');
INSERT INTO [t17712] VALUES (1167,'٢٠٩ - يقولون في حقويك ألفان درهما ... وألفان دينارا فما بك من فقر','449','958','0');
INSERT INTO [t17712] VALUES (1168,'٢١٠ - قتلت فكان تباغيا وتظالما ... إن التظالم في الصديق بوار أفكان أول ما أثبت تهارشت ... أولاد عرج عليك عند وجار','449','958','0');
INSERT INTO [t17712] VALUES (1169,'٢١١ - هما خطتا إما إسار ومنة ... وإما دم والقتل بالحر أجدر','449','958','0');
INSERT INTO [t17712] VALUES (1170,'٢١٢ - تنظرت نصرا والسماكين أيهما ... علي من الغيث استهلت مواطره','450','958','0');
INSERT INTO [t17712] VALUES (1171,'٢١٣ - ومن يك ذا عظم صليب رجا به ... ليكسر عود الدهر فالدهر كاسره','450','958','0');
INSERT INTO [t17712] VALUES (1172,'٢١٤ - وقد زعمت ليلى بأني فاجر ... لنفسي تقاها أو عليها فجورها','450','958','0');
INSERT INTO [t17712] VALUES (1173,'٢١٥ - وتالله ما إن شهلة أم واحد ... بأوجد مني أن يهان صغيرها','451','958','0');
INSERT INTO [t17712] VALUES (1174,'٢١٦ - إذا مات منهم ميت شرف ابنه ... ومن عضة ما ينبتن شكيرها','451','958','0');
INSERT INTO [t17712] VALUES (1175,'٢١٧ - وإني لرام نظرة قبل التي ... لعلي- وإن شطت نواها- أزورها','451','958','0');
INSERT INTO [t17712] VALUES (1176,'٢١٨ - فلا تجزعن من سنة أنت سرتها ... فأول راض سنة من يسيرها','452','958','0');
INSERT INTO [t17712] VALUES (1177,'٢١٩ - إما أقمت وأما أنت مرتحلا ... فالله يكلأ ما تأتي وما تذر','452','958','0');
INSERT INTO [t17712] VALUES (1178,'٢٢٠ - لو كان غيري سليمى الدهر غيره ... وقع الحوادث إلا الصارم الذكر','452','958','0');
INSERT INTO [t17712] VALUES (1179,'٢٢١ - وما أعيد لهم حتى أتيتهم ... أزمان مروان إذ في وحشها غرر فأصبحوا قد أعاد الله نعمتهم ... إذ هم قريش وإذ ما مثلهم بشر','453','958','0');
INSERT INTO [t17712] VALUES (1180,'٢٢٢ - حسب المحبين في الدنيا عذابهم ... تالله لاعذبتهم بعدها سقر','454','958','0');
INSERT INTO [t17712] VALUES (1181,'٢٢٣ - إن ابن ورقاء لا تخشى بوادره ... لكن وقائعه في الحرب تنتظر','455','958','0');
INSERT INTO [t17712] VALUES (1182,'٢٢٤ - ومن تكرمهم في المحل أنهم ... لا يعرف الجار فيهم أنه الجار حتى يكون عزيزا من نفوسهم ... أو أن يبين جميعا وهو مختار','455','958','0');
INSERT INTO [t17712] VALUES (1183,'٢٢٥ - مثل القنافذ هداجون قد بلغت ... نجران أو بلغت سوءاتهم هجر','455','958','0');
INSERT INTO [t17712] VALUES (1184,'٢٢٦ - لقد بدلت أهلا بعد أهل ... فلا عجب بذاك ولا سخار فإنك لا يضيرك بعد عام ... أظبي كان أمك أم حمار','455','958','0');
INSERT INTO [t17712] VALUES (1185,'٢٢٧ - إن يقتلوك فإن قتلك لم يكن ... عارا عليك ورب قتل عار','456','958','0');
INSERT INTO [t17712] VALUES (1186,'٢٢٨ - لهفي عليك للهفة من خائف ... يبغي جوارك حين ليس مجير','456','958','0');
INSERT INTO [t17712] VALUES (1187,'٢٢٩ - قلت لبواب لديه دارها ... تئذن فإني حمؤها وجارها','457','958','0');
INSERT INTO [t17712] VALUES (1188,'٢٣٠ - أرواح مودع أم بكور ... أنت فانظر لأي ذاك تصير','457','958','0');
INSERT INTO [t17712] VALUES (1189,'٢٣١ - هون عليك فإن الأمور ... بكف الإله مقاديرها','457','958','0');
INSERT INTO [t17712] VALUES (1190,'٢٣٢ - لعمرك ما أدري وإن كنت داريا ... شعيث ابن سهم أم شعيث ابن منقر','457','958','0');
INSERT INTO [t17712] VALUES (1191,'٢٣٣ - فقال فريق القوم لما نشدتهم ... نعم وفريق ليمن الله ما ندري','457','958','0');
INSERT INTO [t17712] VALUES (1192,'٢٣٤ - إذا ابن أبي موسى بلالا بلغته ... فقام بفأس بين وصليك جازر','458','958','0');
INSERT INTO [t17712] VALUES (1193,'٢٣٥ - فلو كنت ضبيا عرفت قرابتي ... ولكن زنجي عظيم المشافر','458','958','0');
INSERT INTO [t17712] VALUES (1194,'٢٣٦ - إن العقل في أموالنا لا نضق به ... ذراعا وإن صبرا فنصبر للصبر','458','958','0');
INSERT INTO [t17712] VALUES (1195,'٢٣٧ - أليس أميري في الأمور بأنتما ... بما لستما أهل الخيانة والغدر','459','958','0');
INSERT INTO [t17712] VALUES (1196,'٢٣٨ - فما بال من أسعى لأجبر عظمه ... حفاظا وينوي من سفاهته كسري','459','958','0');
INSERT INTO [t17712] VALUES (1197,'٢٣٩ - وما راعني إلا يسير بشرطة ... وعهدي به قينا يسير بكير','459','958','0');
INSERT INTO [t17712] VALUES (1198,'٢٤٠ - بعيشك- يا سلمى- ارحمي ذا صبابة ... أبى غير ما يرضيك في السر والجهر','460','958','0');
INSERT INTO [t17712] VALUES (1199,'٢٤١ - يا ليتما أمنا شالت نعامتها ... أيما إلى جنة أيما إلى نار','460','958','0');
INSERT INTO [t17712] VALUES (1200,'٢٤٢ - ألا طعان ألا فرسان عادية ... إلا تجشؤكم حول التنانير','460','958','0');
INSERT INTO [t17712] VALUES (1201,'٢٤٣ - صلى على عزة الرحمن وابنتها ... ليلى وصلى على جاراتها الأخر هن الحرائر لا ربات أحمرة ... سود المحاجر لا يقرأن بالسور','461','958','0');
INSERT INTO [t17712] VALUES (1202,'٢٤٤ - كم قد ذكرتك لو أجدى تذكركم ... يا أشبه الناس كل الناس بالقمر','462','958','0');
INSERT INTO [t17712] VALUES (1203,'٢٤٥ - لا أعرفن ربربا حورا مدامعها ... كأن أبكارها نعاج دوار','462','958','0');
INSERT INTO [t17712] VALUES (1204,'٢٤٦ - قوم إذا حاربوا شدوا مآزرهم ... دون النساء ولو باتت بأطهار','462','958','0');
INSERT INTO [t17712] VALUES (1205,'٢٤٧ - لولا فوارس من نعم وأسرتهم ... يوم الصليفاء لم يوفون بالجار','463','958','0');
INSERT INTO [t17712] VALUES (1206,'٢٤٨ - إني وإياك إذ حلت بأرحلنا ... كمن بواديه بعد المحل ممطور','463','958','0');
INSERT INTO [t17712] VALUES (1207,'٢٤٩ - يا لعنة الله والأقوام كلهم ... والصالحين على سمعان من جار','463','958','0');
INSERT INTO [t17712] VALUES (1208,'٢٥٠ - إن امرأ خصني عمدا مودته ... على التنائي لعندي غير مكفور','463','958','0');
INSERT INTO [t17712] VALUES (1209,'٢٥١ - يا ما أمليح غزلانا شدن لنا ... من هؤليائكن الضال والسمر','464','958','0');
INSERT INTO [t17712] VALUES (1210,'٢٥٢ - ولا تهيبني الموماة أركبها ... إذا تجاوبت الأصداء بالسحر','465','958','0');
INSERT INTO [t17712] VALUES (1211,'٢٥٣ - فلو نبش المقابر عن كليب ... فيخبر بالذنائب أي زير بيوم الشعثمين لقر عينا ... وكيف لقاء من تحت القبور','466','958','0');
INSERT INTO [t17712] VALUES (1212,'٢٥٤ - وليس لعيشنا هذا مهاه ... وليست دارنا هاتا بدار','466','958','0');
INSERT INTO [t17712] VALUES (1213,'٢٥٥ - ما زال مذ عقدت يداه إزاره ... فنما فأدرك خمسة الأشبار','466','958','0');
INSERT INTO [t17712] VALUES (1214,'٢٥٦ - يا عاذلاتي لا تردن ملامتي ... إن العواذل لسن لي بأمير','467','958','0');
INSERT INTO [t17712] VALUES (1215,'٢٥٧ - قدر أحلك ذا المجاز وقد أرى ... وأبي مالك ذو المجاز بدار','467','958','0');
INSERT INTO [t17712] VALUES (1216,'٢٥٨ - وقتيل مرة أثأرن فإنه ... فرغ وإن أخاهم لم يثأر','467','958','0');
INSERT INTO [t17712] VALUES (1217,'٢٥٩ - قد سقيت آبالهم بالنار ... والنار قد تشفي من الأوار','468','958','0');
INSERT INTO [t17712] VALUES (1218,'٢٦٠ - إذا تقول «لا» ابنة العجير ... تصدق، لا، إذا تقول جير','468','958','0');
INSERT INTO [t17712] VALUES (1219,'٢٦١ - أنا أبو النجم وشعري شعري ... لله دري ما أجن صدري','468','958','0');
INSERT INTO [t17712] VALUES (1220,'٢٦٢ - باعد أم العمرو من أسيرها ... حراس أبواب على قصورها','469','958','0');
INSERT INTO [t17712] VALUES (1221,'٢٦٣ - لو بغير الماء حلقي شرق ... كنت كالغصان بالماء اعتصاري','469','958','0');
INSERT INTO [t17712] VALUES (1222,'٢٦٤ - اطرد اليأس بالرجا فكأين ... آلما حم يسره بعد عسر','470','958','0');
INSERT INTO [t17712] VALUES (1223,'٢٦٥ - وي كأن من يكن له نشب يح ... بب ومن يفتقر يعش عيش ضر','470','958','0');
INSERT INTO [t17712] VALUES (1224,'٢٦٦ - فغررتني وزعمت أ ... نك لابن بالصيف تامر','470','958','0');
INSERT INTO [t17712] VALUES (1225,'٢٦٧ - تفاقد قومي إذ يبيعون مهجتي ... بجارية بهرا لهم بعدها بهرا','470','958','0');
INSERT INTO [t17712] VALUES (1226,'٢٦٨ - بكى صاحبي لما رأى الدرب دونه ... وأيقن أنا لاحقان بقيصرا فقلت له: لا تبك عينك إنما ... نحاول ملكا أو نموت فنعذرا','471','958','0');
INSERT INTO [t17712] VALUES (1227,'٢٦٩ - إذا ما انتهى علمي تناهيت عنده ... أطال فأملى أو تناهى فأقصرا','471','958','0');
INSERT INTO [t17712] VALUES (1228,'٢٧٠ - أليس أبي بالنضر أم ليس والدي ... لكل نجيب من خزاعة أزهرا','471','958','0');
INSERT INTO [t17712] VALUES (1229,'٢٧١ - وهم أهلات حول قيس بن عاصم ... إذا أدلجوا بالليل يدعون كوثرا','472','958','0');
INSERT INTO [t17712] VALUES (1230,'٢٧٢ - إذا الوحش ضم الوحش في ظللاتها ... سواقط من حر وقد كان أظهرا','472','958','0');
INSERT INTO [t17712] VALUES (1231,'٢٧٣ - فليس بمعروف لنا أن نردها ... صحاحا ولا مستنكر أن تعقرا','472','958','0');
INSERT INTO [t17712] VALUES (1232,'٢٧٤ - هون عليك فإن الأمور ... بكف الإله مقاديرها فليس بآتيك منهيها ... ولا قاصر عنك مأمورها','472','958','0');
INSERT INTO [t17712] VALUES (1233,'٢٧٥ - فمن يك لم يثأر بأعراض قومه ... فإني ورب الراقصات لأثأرا','473','958','0');
INSERT INTO [t17712] VALUES (1234,'٢٧٦ - أو معبر الظهر ينبي عن وليته ... ما حج ربه في الدنيا ولا اعتمرا','473','958','0');
INSERT INTO [t17712] VALUES (1235,'٢٧٧ - يعالج عاقرا أعيت عليه ... ليلقحها فينتجها حوارا','473','958','0');
INSERT INTO [t17712] VALUES (1236,'٢٧٨ - أحار أريك برقا هب وهنا ... كنار مجوس تستعر استعارا','474','958','0');
INSERT INTO [t17712] VALUES (1237,'٢٧٩ - ستعلم أينا خير قديما ... وأعظمنا ببطن حراء نارا','474','958','0');
INSERT INTO [t17712] VALUES (1238,'٢٨٠ - يا دار حسرها البلى تحسيرا ... وسفت عليها الريح بعدك مورا','474','958','0');
INSERT INTO [t17712] VALUES (1239,'٢٨١ - مشق الهواجر لحمهن مع السرى ... حتى ذهبن كلاكلا وصدورا','474','958','0');
INSERT INTO [t17712] VALUES (1240,'٢٨٢ - يا صاحبي دنا الرواح فسيرا ... لا كالعشية زائرا ومزورا','475','958','0');
INSERT INTO [t17712] VALUES (1241,'٢٨٣ - قال العواذل ما لجهلك بعد ما ... شاب المفارق واكتسين قتيرا','475','958','0');
INSERT INTO [t17712] VALUES (1242,'٢٨٤ - ولا نقاتل بالعص ... ي ولا نرامي بالحجارة إلا علالة أو بدا ... هة قارح نهد الجزاره','475','958','0');
INSERT INTO [t17712] VALUES (1243,'٢٨٥ - أصبحت لا أحمل السلاح ولا ... أملك رأس البعير إن نفرا والذئب أخشاه إن مررت به ... وحدي وأخشى الرياح والمطرا','476','958','0');
INSERT INTO [t17712] VALUES (1244,'٢٨٦ - تقول ابنتي حين جد الرحيل ... فأبرحت ربا وأبرحت جارا','476','958','0');
INSERT INTO [t17712] VALUES (1245,'٢٨٧ - خريع دوادي في ملعب ... تأزر طورا وتلقي الإزارا','476','958','0');
INSERT INTO [t17712] VALUES (1246,'٢٨٨ - لها زجل كحفيف الحصا ... د صادف بالليل ريحا دبورا','476','958','0');
INSERT INTO [t17712] VALUES (1247,'٢٨٩ - حمين العراقيب العصا وتركنه ... به نفس عال مخالطه بهر','477','958','0');
INSERT INTO [t17712] VALUES (1248,'٢٩٠ - وأنت امرؤ من أهل نجد وأهلنا ... تهام فما النجدي والمتغور','477','958','0');
INSERT INTO [t17712] VALUES (1249,'٢٩١ - ترى خلقها نصف قناة قويمة ... ونصف نقا يرتج أو يتمرمر','477','958','0');
INSERT INTO [t17712] VALUES (1250,'٢٩٢ - أقام وأقوى ذات يوم وخيبة ... لأول من يلقى وشر ميسر','477','958','0');
INSERT INTO [t17712] VALUES (1251,'٢٩٣ - خذوا حظكم يا آل عكرم واذكروا ... أواصرنا والرحم بالغيب تذكر','477','958','0');
INSERT INTO [t17712] VALUES (1252,'٢٩٤ - فكان مجني دون من كنت أتقي ... ثلاث شخوص كاعبان ومعصر','478','958','0');
INSERT INTO [t17712] VALUES (1253,'٢٩٥ - لعمرك ما معن بتارك حقه ... ولا منسئ معن ولا متيسر','478','958','0');
INSERT INTO [t17712] VALUES (1254,'٢٩٦ - وإن كلابا هذه عشر أبطن ... وأنت بريء من قبائلها العشر','478','958','0');
INSERT INTO [t17712] VALUES (1255,'٢٩٧ - تبكي على لبنى وأنت تركتها ... وكنت عليها بالملا أنت أقدر','479','958','0');
INSERT INTO [t17712] VALUES (1256,'٢٩٨ - وأنت امرؤ من خير قومك فيهم ... وأنت سواهم في معد مخير','479','958','0');
INSERT INTO [t17712] VALUES (1257,'٢٩٩ - وأيقن أن الخيل إن تلتبس به ... يكن لفسيل النخل بعده آبر','479','958','0');
INSERT INTO [t17712] VALUES (1258,'٣٠٠ - وتحت العوالي في القنا مستظلة ... ظباء أعارتها العيون الجآذر','479','958','0');
INSERT INTO [t17712] VALUES (1259,'٣٠١ - فيا مي هل يجزى بكائي بمثله ... مرارا وأنفاسي إليك الزوافر وأني متى أشرف على الجانب الذي ... به أنت من بين الجوانب ناظر','480','958','0');
INSERT INTO [t17712] VALUES (1260,'٣٠٢ - ومثلك رهبى قد تركت رذية ... تقلب عينيها إذا مر طائر','480','958','0');
INSERT INTO [t17712] VALUES (1261,'٣٠٣ - أألحق أن دار الرباب تباعدت ... أو انبت حبل أن قلبك طائر','480','958','0');
INSERT INTO [t17712] VALUES (1262,'٣٠٤ - فلا يدعني قومي صريحا لحرة ... لئن كنت مقتولا ويسلم عامر','481','958','0');
INSERT INTO [t17712] VALUES (1263,'٣٠٥ - فمالي إلا الله لا رب غيره ... ومالي إلا الله غيرك ناصر','481','958','0');
INSERT INTO [t17712] VALUES (1264,'٣٠٦ - تحسب هواس وأقبل أنني ... بها مفتد من واحد لا أغامره فقلت له: فاها لفيك فإنها ... قلوص امرئ قاريك ما أنت حاذره','481','958','0');
INSERT INTO [t17712] VALUES (1265,'٣٠٧ - عذيرك من مولى إذا نمت لم ينم ... يقول الخنا أو تعتريك زنابره','481','958','0');
INSERT INTO [t17712] VALUES (1266,'٣٠٨ - لعلك يا تيسا نزا في مريرة ... معذب ليلى أن تراني أزورها','482','958','0');
INSERT INTO [t17712] VALUES (1267,'٣٠٩ - فقلت تحمل فوق طوقك إنها ... مطبعة من يأتها لا يضيرها','482','958','0');
INSERT INTO [t17712] VALUES (1268,'٣١٠ - ومن يميل أمال السيف ذروته ... حيث التقى من حفافي رأسه الشعر','482','958','0');
INSERT INTO [t17712] VALUES (1269,'٣١١ - دست رسولا بأن القوم إن قدروا ... عليك يشفوا صدورا ذات توغير','483','958','0');
INSERT INTO [t17712] VALUES (1270,'٣١٢ - يا لبكر أنشروا لي كليبا ... يا لبكر أين أين الفرار','483','958','0');
INSERT INTO [t17712] VALUES (1271,'٣١٣ - إلى إمام تغادينا فواضله ... أظفره الله فليهنئ له الظفر','483','958','0');
INSERT INTO [t17712] VALUES (1272,'٣١٤ - نفسي فداء أمير المؤمنين إذا ... أبدى النواجذ يوم باسل ذكر الخائض الغمر والميمون طائره ... خليفة الله يستسقى به المطر','483','958','0');
INSERT INTO [t17712] VALUES (1273,'٣١٥ - كروا إلى حرتيكم تعمرونها ... كما تكر إلى أوطانها البقر','484','958','0');
INSERT INTO [t17712] VALUES (1274,'٣١٦ - خل الطريق لمن يبني المنار به ... وابرز ببرزة حيث اضطرك القدر','484','958','0');
INSERT INTO [t17712] VALUES (1275,'٣١٧ - الناس ألب علينا فيك ليس لنا ... إلا السيوف وأطراف القنا وزر','484','958','0');
INSERT INTO [t17712] VALUES (1276,'٣١٨ - يا أسم صبرا على ما كان من حدث ... إن الحوادث ملقي ومنتظر','484','958','0');
INSERT INTO [t17712] VALUES (1277,'٣١٩ - وبينما المرء في الأحياء مغتبطا ... إذ صار في الرمس تعفوه الأعاصير حتى كأن لم يكن إلا تذكره ... والدهر أيتما حال دهارير','485','958','0');
INSERT INTO [t17712] VALUES (1278,'٣٢٠ - يا أضبعا أكلت آيار أحمرة ... ففي البطون وقد راحت قراقير','485','958','0');
INSERT INTO [t17712] VALUES (1279,'٣٢١ - وجدنا في كتاب بني تميم ... أحق الخيل بالركض المعار','485','958','0');
INSERT INTO [t17712] VALUES (1280,'٣٢٢ - تراها من يبيس الماء شهبا ... مخالط درة منها غرار','486','958','0');
INSERT INTO [t17712] VALUES (1281,'٣٢٣ - فمن يك سائلا عني فإني ... وجروة لا ترود ولا تعار','486','958','0');
INSERT INTO [t17712] VALUES (1282,'٣٢٤ - ألا يا ليل إن خيرت فينا ... بنفسي فانظري أين الخيار','486','958','0');
INSERT INTO [t17712] VALUES (1283,'٣٢٥ - وشيد لي زرارة باذخات ... وعمرو الخير إذ ذكر العمور','486','958','0');
INSERT INTO [t17712] VALUES (1284,'٣٢٦ - يا زبرقان أخابني خلف ... ما أنت ويب أبيك والفخر','486','958','0');
INSERT INTO [t17712] VALUES (1285,'٣٢٧ - إن الخلافة والنبوة فيهم ... والمكرمات وسادة أطهار','487','958','0');
INSERT INTO [t17712] VALUES (1286,'٣٢٨ - تؤم سنانا وكم دونه ... من الأرض محدودبا غارها','487','958','0');
INSERT INTO [t17712] VALUES (1287,'٣٢٩ - كسا اللؤم تيما خضرة في جلودها ... فويلا لتيم من سرابيلها الخضر','487','958','0');
INSERT INTO [t17712] VALUES (1288,'٣٣٠ - فلا ذا جلال هبنه لجلاله ... ولا ذا ضياع هن يتركن للفقر','487','958','0');
INSERT INTO [t17712] VALUES (1289,'٣٣١ - سرت تخبط الظلماء من جانبي قسا ... وحب بها من خابط الليل زائر','488','958','0');
INSERT INTO [t17712] VALUES (1290,'٣٣٢ - فلما لحقنا والجياد عشية ... دعوا يالكلب واعتزينا لعامر','488','958','0');
INSERT INTO [t17712] VALUES (1291,'٣٣٣ - ألا أبلغ الأقياس قيس بن نوفل ... وقيس بن أهبان وقيس بن جابر','488','958','0');
INSERT INTO [t17712] VALUES (1292,'٣٣٤ - عودت قومي إذا ما الضيف نبهني ... عقر العشار على عسري وإيساري إني إذا خفيت نار لمرملة ... ألفى بأرفع تل رافعا ناري ذاك وإني على جاري لذو حدب ... أحنو عليه بما يحنى على الجار','488','958','0');
INSERT INTO [t17712] VALUES (1293,'٣٣٥ - وقال رائدهم أرسوا نزاولها ... فكل حتف امرئ يمضي لمقدار','489','958','0');
INSERT INTO [t17712] VALUES (1294,'٣٣٦ - أما الإماء فلا يدعونني ولدا ... إذا ترامى بنو الإموان بالعار','489','958','0');
INSERT INTO [t17712] VALUES (1295,'٣٣٧ - إذا تغنى الحمام الورق هيجني ... ولو تغربت عنها أم عمار','489','958','0');
INSERT INTO [t17712] VALUES (1296,'٣٣٨ - لا يبعدن قومي الذين هم ... سم العداة وآفة الجزر','489','958','0');
INSERT INTO [t17712] VALUES (1297,'٣٣٩ - ولنعم حشو الدرع أنت إذا ... دعيت نزال ولج في الذعر','490','958','0');
INSERT INTO [t17712] VALUES (1298,'٣٤٠ - أبدا كالفراء فوق ذراها ... حين يطوي المسامع الصرار','490','958','0');
INSERT INTO [t17712] VALUES (1299,'٣٤١ - في فتية جعلوا الصليب إلههم ... حاشاي إني مسلم معذور','490','958','0');
INSERT INTO [t17712] VALUES (1300,'٣٤٢ - قهرت العدا لا مستعينا بعصبة ... ولكن بأنواع الخدائع والمكر','491','958','0');
INSERT INTO [t17712] VALUES (1301,'٣٤٣ - بلى سوف نبكيهم بكل مهند ... ونبكي عميرا بالرماح الخواطر','491','958','0');
INSERT INTO [t17712] VALUES (1302,'٣٤٤ - جاري لا تستنكري عذيري ... سيري وإشفاقي على بعيري','491','958','0');
INSERT INTO [t17712] VALUES (1303,'٣٤٥ - كشحا طوى من بلد مختارا ... من يأسه اليائس أو حذارا','491','958','0');
INSERT INTO [t17712] VALUES (1304,'٣٤٦ - أنعت عيرا من حمير خنزره ... في كل عير مائتان كمره لاقين أم زاحر بالمزربه ... وكمنها مقبلة ومدبره','492','958','0');
INSERT INTO [t17712] VALUES (1305,'٣٤٧ - إذا رأتني سقطت أبصارها ... دأب بكار شايحت بكارها','492','958','0');
INSERT INTO [t17712] VALUES (1306,'٣٤٨ - إن نزارا أصبحت نزارا ... دعوة أبرار دعوا أبرارا','492','958','0');
INSERT INTO [t17712] VALUES (1307,'٣٤٩ - ما الله موليك فضل فاحمدنه به ... فما لدى غيره نفع ولا ضرر','493','958','0');
INSERT INTO [t17712] VALUES (1308,'٣٥٠ - لا تركنن إلى الأمر الذي ركنت ... أبناء يعصر حين اضطرها القدر','493','958','0');
INSERT INTO [t17712] VALUES (1309,'٣٥١ - المطعمين لدى الشتا ... ء سدائفا ملنيب غرا في الجاهلية كان سؤ ... دد وائل فهلم جرا','493','958','0');
INSERT INTO [t17712] VALUES (1310,'٣٥٢ - لطالما جررتكن جرا ... حتى نوى الأعجف واستمرا','494','958','0');
INSERT INTO [t17712] VALUES (1311,'٣٥٣ - باعد أم العمرو من أسيرها ... حراس أبواب على قصورها','494','958','0');
INSERT INTO [t17712] VALUES (1312,'٣٥٤ - فإن جاوزت مقفرة رمت بي ... إلى أخرى كتلك هلم جرا','494','958','0');
INSERT INTO [t17712] VALUES (1313,'٣٥٥ - حتى استكانوا وهم مني على مضض ... والقول ينفذ ما لا ينفذ الإبر','495','958','0');
INSERT INTO [t17712] VALUES (1314,'٣٥٦ - كأنهم أسيف بيض يمانية ... عضب مضاربها باق بها الأثر','495','958','0');
INSERT INTO [t17712] VALUES (1315,'٣٥٧ - ولكن أجرا لو فعلت بهين ... وهل ينكر المعروف في الناس والأجر','495','958','0');
INSERT INTO [t17712] VALUES (1316,'٣٥٨ - لعلهما أن تبغيا لك حيلة ... وأن ترحبا صدرا بما كنت أحصر','495','958','0');
INSERT INTO [t17712] VALUES (1317,'٣٥٩ - ترتع ما رتعت حتى إذا ادكرت ... فإنما هي إقبال وإدبار','496','958','0');
INSERT INTO [t17712] VALUES (1318,'٣٦٠ - أعاذل توشكين بأن تريني ... صريعا لا أزور ولا أزار','496','958','0');
INSERT INTO [t17712] VALUES (1319,'٣٦١ - فعجتها قبل الأخيار منزلة ... والطيبي كل ما التاثت به الأزر','496','958','0');
INSERT INTO [t17712] VALUES (1320,'٣٦٢ - ليس شيء إلا وفيه إذا ما ... قابلته عين البصير اعتبار','497','958','0');
INSERT INTO [t17712] VALUES (1321,'٣٦٣ - وما نفعت أعماله المرء راجيا ... جزاء عليها من سوى من له الأمر','497','958','0');
INSERT INTO [t17712] VALUES (1322,'٣٦٤ - فعليك بالحجاج لا تعدل به ... أحدا إذا نزلت عليك أمور','497','958','0');
INSERT INTO [t17712] VALUES (1323,'٣٦٥ - عنيت قصيرات الحجال ولم أرد ... قصار الخطى شر النساء البحاتر','497','958','0');
INSERT INTO [t17712] VALUES (1324,'٣٦٦ - كل دين يوم القيامة عند الل ... هـ إلا دين الحنيفة بور','497','958','0');
INSERT INTO [t17712] VALUES (1325,'٣٦٧ - والنفس إن دعيت بالعنف آبية ... وهي ما أمرت بالرفق تأتمر','498','958','0');
INSERT INTO [t17712] VALUES (1326,'٣٦٨ - كلا ثقلينا واثق بغنيمة ... وقد قدر الرحمن ما هو قادر','498','958','0');
INSERT INTO [t17712] VALUES (1327,'٣٦٩ - على حين من تلبت عليه ذنوبه ... يجد فقدها إذ في المقام تدابر','498','958','0');
INSERT INTO [t17712] VALUES (1328,'٣٧٠ - وبالزرق أطلال لمية أقفرت ... ثلاثة أحوال تراح وتمطر','499','958','0');
INSERT INTO [t17712] VALUES (1329,'٣٧١ - فما جنة الفردوس أقبلت تبتغي ... ولكن دعاك الخبز أحسب والتمر','499','958','0');
INSERT INTO [t17712] VALUES (1330,'٣٧٢ - أبائنة حبى نعم وتماضر ... لهنا لمقضي علينا التهاجر','499','958','0');
INSERT INTO [t17712] VALUES (1331,'٣٧٣ - فقصرن الشتاء بعد عليه ... وهو للذود أن يقسمن جار','499','958','0');
INSERT INTO [t17712] VALUES (1332,'٣٧٤ - كل سعي سوى الذي يورث الفو ... ز فعقباه حسرة وخسار','500','958','0');
INSERT INTO [t17712] VALUES (1333,'٣٧٥ - وكنت أرى كالموت من بين ليلة ... فكيف ببين كان ميعاده الحشر','500','958','0');
INSERT INTO [t17712] VALUES (1334,'٣٧٦ - على قرماء عالية شواه ... كأن بياض غرته خمار','500','958','0');
INSERT INTO [t17712] VALUES (1335,'٣٧٧ - ثم أضحوا كأنهم ورق جف ... فألوت به الصبا والدبور','501','958','0');
INSERT INTO [t17712] VALUES (1336,'٣٧٨ - لدم ضائع تغيب عنه ... أقربوه إلا الصبا والدبور','501','958','0');
INSERT INTO [t17712] VALUES (1337,'٣٧٩ - ولهت عليه كل معصفة ... هوجاء ليس للبها زبر','501','958','0');
INSERT INTO [t17712] VALUES (1338,'٣٨٠ - قليلا غرار العين حتى يقلصوا ... على كالقطا الجوني أفزعه الزجر','502','958','0');
INSERT INTO [t17712] VALUES (1339,'٣٨١ - فأمسى مقفرا لا حي فيه ... وقد كانوا فأمسى الحي ساروا','502','958','0');
INSERT INTO [t17712] VALUES (1340,'٣٨٢ - إني أتتني لسان لا أسر بها ... من علو لا عجب منها ولا سخر','502','958','0');
INSERT INTO [t17712] VALUES (1341,'٣٨٣ - لا تأمن البازل الكوماء عدوته ... ولا الأمون إذا ما اخروط السفر','502','958','0');
INSERT INTO [t17712] VALUES (1342,'٣٨٤ - ماذا تقول لأفراخ بذي مرخ ... زغب الحواصل لا ماء ولا شجر','503','958','0');
INSERT INTO [t17712] VALUES (1343,'٣٨٥ - وكانوا أناسا ينفحون فأصبحوا ... وأكثر ما يعطونك النظر الشزر','503','958','0');
INSERT INTO [t17712] VALUES (1344,'٣٨٦ - ومن يميل أمال السيف ذروته ... حيث التقى من حفافي رأسه الشعر','503','958','0');
INSERT INTO [t17712] VALUES (1345,'٣٨٧ - رأت إخوتي بعد الجميع تفرقوا ... فلم يبق إلا واحدا منهم شفر','503','958','0');
INSERT INTO [t17712] VALUES (1346,'٣٨٨ - أأترك ليلى ليس بيني وبينها ... سوى ليلة إني إذن لصبور','503','958','0');
INSERT INTO [t17712] VALUES (1347,'٣٨٩ - أماوي ما يغني الثراء عن الفتى ... إذا حشرجت يوما وضاق بها الصدر','504','958','0');
INSERT INTO [t17712] VALUES (1348,'٣٩٠ - فقلنا أسلموا إنا أخوكم ... فقد برئت من الإحن الصدور','504','958','0');
INSERT INTO [t17712] VALUES (1349,'٣٩١ - واذكر غدانة عدانا مزنمة ... من الحبلق تبنى حولها الصير','504','958','0');
INSERT INTO [t17712] VALUES (1350,'٣٩٢ - فهل من خالد إما هلكنا ... وهل بالموت يا للناس عار','504','958','0');
INSERT INTO [t17712] VALUES (1351,'٣٩٣ - فلا يدعني قومي صريحا لحرة ... لئن كنت مقتولا ويسلم عامر','505','958','0');
INSERT INTO [t17712] VALUES (1352,'٣٩٤ - فلم أر بيتا كان أحسن بهجة ... من اللذ له من آل عزة عامر','505','958','0');
INSERT INTO [t17712] VALUES (1353,'٣٩٥ - وكنت هناك أنت كريم قيس ... فما القيسي بعدك والفخار','505','958','0');
INSERT INTO [t17712] VALUES (1354,'٣٩٦ - ولا ينجي من الغمرات إلا ... براكاء القتال أو الفرار','505','958','0');
INSERT INTO [t17712] VALUES (1355,'٣٩٧ - ترك الناس لنا أكنافهم ... وتولوا لات لم يغن الفرار','505','958','0');
INSERT INTO [t17712] VALUES (1356,'٣٩٨ - ذريني للغنى أسعى فإني ... رأيت الناس شرهم الفقير وأحقرهم وأهونهم عليه ... وإن كانا له نسب وخير','506','958','0');
INSERT INTO [t17712] VALUES (1357,'٣٩٩ - وأنت التي حببت كل قصيرة ... إلي وإن لم تدر ذاك القصائر','506','958','0');
INSERT INTO [t17712] VALUES (1358,'٤٠٠ - إنا وجدنا بني جلان كلهم ... كساعد الضب لا طول ولا قصر','506','958','0');
INSERT INTO [t17712] VALUES (1359,'٤٠١ - كحلفة من أبي رياح ... يسمعه لاهه الكبار','507','958','0');
INSERT INTO [t17712] VALUES (1360,'٤٠٢ - تظل مقاليت النساء يطأنه ... يقلن: ألا يلقى على المرء مئزر','507','958','0');
INSERT INTO [t17712] VALUES (1361,'٤٠٣ - أسيلات أبدان دقاق خصورها ... وثيرات ما التفت عليه المآزر','507','958','0');
INSERT INTO [t17712] VALUES (1362,'٤٠٤ - وغبراء يحمي دونها ما وراءها ... ولا يختطيها الدهر إلا المخاطر','507','958','0');
INSERT INTO [t17712] VALUES (1363,'٤٠٥ - وقلت وفي الأحشاء داء مخامر ... ألا حبذا يا عز ذاك التساتر','507','958','0');
INSERT INTO [t17712] VALUES (1364,'٤٠٦ - حتى يكون عزيزا من نفوسهم ... أو أن يبين جميعا وهو مختار','508','958','0');
INSERT INTO [t17712] VALUES (1365,'٤٠٧ - في فتية جعلوا الصليب إلههم ... حاشاي إني مسلم معذور','508','958','0');
INSERT INTO [t17712] VALUES (1366,'٤٠٨ - إن المحب علمت مصطبر ... ولديه ذنب الحب مغتفر','509','958','0');
INSERT INTO [t17712] VALUES (1367,'٤٠٩ - ألا أيهذا الباخع الوجد نفسه ... لشيء نحته عن يديه المقادر','509','958','0');
INSERT INTO [t17712] VALUES (1368,'٤١٠ - قالوا قهرت فقلت جير ليعلمن ... عما قليل أينا المقهور','509','958','0');
INSERT INTO [t17712] VALUES (1369,'٤١١ - حسن الوجه طلقه أنت في السلم ... وفي الحرب كالح مكفهر','509','958','0');
INSERT INTO [t17712] VALUES (1370,'٤١٢ - فلئن تغير ما عهدت وأصبحت ... صدفت فلا بذل ولا ميسور لبما يساعف في اللقاء وليها ... فرح بقرب مزارها مسرور','510','958','0');
INSERT INTO [t17712] VALUES (1371,'٤١٣ - كساك ولم تستكسه فاشكرن له ... أخ لك يعطيك الجزيل وناصر','510','958','0');
INSERT INTO [t17712] VALUES (1372,'٤١٤ - أعوذ برب العرش من فئة بغت ... علي فمالي عوض إلاه ناصر','510','958','0');
INSERT INTO [t17712] VALUES (1373,'٤١٥ - فمالي إلا الله لا رب غيره ... ومالي إلا الله غيرك ناصر','510','958','0');
INSERT INTO [t17712] VALUES (1374,'٤١٦ - وأية بلدة إلا أتينا ... من الأرضين تعلمه نزار','511','958','0');
INSERT INTO [t17712] VALUES (1375,'٤١٧ - عشية فر الحارثيون بعد ما ... قضى نحبه في ملتقى القوم هوبر','511','958','0');
INSERT INTO [t17712] VALUES (1376,'٤١٨ - أمام وخلف المرء من لطف ربه ... كواليء تزوي عنه ما كان يحذر','511','958','0');
INSERT INTO [t17712] VALUES (1377,'٤١٩ - لقد تركتني منجنيق ابن بحدل ... أحيد عن العصفور حين يطير','511','958','0');
INSERT INTO [t17712] VALUES (1378,'٤٢٠ - وسطه كاليراع أو سرج المجدل ... طورا يخبو وطورا ينير','511','958','0');
INSERT INTO [t17712] VALUES (1379,'٤٢١ - وشر المنايا ميت وسط أهله ... كهلك الفتى قد أسلم الحي حاضره','512','958','0');
INSERT INTO [t17712] VALUES (1380,'٤٢٢ - وقلن على الفردوس أول مشرب ... أجل جير إن كانت أبيحت دعاثره','512','958','0');
INSERT INTO [t17712] VALUES (1381,'٤٢٣ - فقلت لها: عيثي جعار وجرري ... بلحم امرئ لم يشهد اليوم ناصره','513','958','0');
INSERT INTO [t17712] VALUES (1382,'٤٢٤ - حمامة بطن الواديين ترنمي ... سقاك من الغر الغوادي مطيرها','513','958','0');
INSERT INTO [t17712] VALUES (1383,'٤٢٥ - فما أفجرت حتى أهب بسحرة ... علاجيم عين ابني صباح يثيرها','513','958','0');
INSERT INTO [t17712] VALUES (1384,'٤٢٦ - إذا ما ستور البيت أرخين لم يكن ... سراج لنا إلا ووجهك نورها (أنور)','513','958','0');
INSERT INTO [t17712] VALUES (1385,'٤٢٧ - ت لي آل زيد واندهم لي جماعة ... وسل آل زيد أي شيء يضيرها','514','958','0');
INSERT INTO [t17712] VALUES (1386,'٤٢٨ - رعته أشهرا وخلا عليها ... فطار الني فيها واستغارا','514','958','0');
INSERT INTO [t17712] VALUES (1387,'٤٢٩ - وننكر يوم الروع ألوان خيلنا ... من الطعن حتى تحسب الجون أشقرا','514','958','0');
INSERT INTO [t17712] VALUES (1388,'٤٣٠ - سقيناهم كأسا سقونا بمثلها ... ولكننا كنا على الموت أصبرا','514','958','0');
INSERT INTO [t17712] VALUES (1389,'٤٣١ - فأيي وأي ابن الحصين وعثعث ... إذا ما التقينا كان بالحلف أغدرا','515','958','0');
INSERT INTO [t17712] VALUES (1390,'٤٣٢ - كأن الحصى من خلفها وأمامها ... إذا نجلته رجلها خذف أعسرا','515','958','0');
INSERT INTO [t17712] VALUES (1391,'٤٣٣ - وكنا حسبناهم فوارس كهمس ... حيوا بعد ما ماتوا من الدهر أعصرا','515','958','0');
INSERT INTO [t17712] VALUES (1392,'٤٣٤ - فما تك يا ابن عبد الله فينا ... فلا ظلما نخاف ولا افتقارا','515','958','0');
INSERT INTO [t17712] VALUES (1393,'٤٣٥ - وكان مضلي من هديت برشده ... فلله مغو عاد بالرشد آمرا','516','958','0');
INSERT INTO [t17712] VALUES (1394,'٤٣٦ - فتاتان أما منهما فشبيهة ... هلالا ولأخرى منهما تشبه البدرا','516','958','0');
INSERT INTO [t17712] VALUES (1395,'٤٣٧ - خلافا لقولي من فيالة رأيه ... كما قيل قبل اليوم خالف تذكرا','516','958','0');
INSERT INTO [t17712] VALUES (1396,'٤٣٨ - مر إني قد امتدحتك مرا ... واثقا أن تثيبني وتسرا مر يا مر مرة بن تليد ... ما وجدناك في الحوادث غرا','516','958','0');
INSERT INTO [t17712] VALUES (1397,'٤٣٩ - متى ما تلقني فردين ترجف ... روانق أليتيك وتستطارا','517','958','0');
INSERT INTO [t17712] VALUES (1398,'٤٤٠ - تسائل بابن أحمر من رآه ... أعارت عينه أم لم تعارا','517','958','0');
INSERT INTO [t17712] VALUES (1399,'٤٤١ - فسر في بلاد الله والتمس الغنى ... تعش ذا يسار أو تموت فتعذرا','518','958','0');
INSERT INTO [t17712] VALUES (1400,'٤٤٢ - فلما قرعنا النبع بالنبع بعضه ... ببعض أبت عيدانه أن تكسرا','518','958','0');
INSERT INTO [t17712] VALUES (1401,'٤٤٣ - ها أنا ذا آمل الخلود وقد ... أدرك عقلي ومولدي حجرا أبا امرئ القيس هل سمعت به ... هيهات هيهات طال ذا عمرا','518','958','0');
INSERT INTO [t17712] VALUES (1402,'٤٤٤ - فما آباؤنا بأمن منه ... علينا اللاء قد مهدوا الحجورا','519','958','0');
INSERT INTO [t17712] VALUES (1403,'٤٤٥ - ستعلم أينا للموت أدنى ... إذا دانيت لي الأسل الحرارا','519','958','0');
INSERT INTO [t17712] VALUES (1404,'٤٤٦ - كأنها درة منعمة ... من نسوة كن قبلها دررا','519','958','0');
INSERT INTO [t17712] VALUES (1405,'٤٤٧ - أخلاي لا تنسوا مواثيق بيننا ... فإني لا والله ما زلت ذاكرا','520','958','0');
INSERT INTO [t17712] VALUES (1406,'٤٤٨ - سفرت فقلت لها هج فتبرقعت ... فذكرت حين تبرقعت ضبارا','520','958','0');
INSERT INTO [t17712] VALUES (1407,'٤٤٩ - حذارا على ألا تصاب مقادتي ... ولا نسوتي حتى يمتن حرائرا','520','958','0');
INSERT INTO [t17712] VALUES (1408,'٤٥٠ - فكيف أنا وانتحالي القوا ... ف بعد المشيب كفى ذاك عارا','520','958','0');
INSERT INTO [t17712] VALUES (1409,'٤٥١ - وأتاها أحيمر كأخي السه ... م بعضب فقال: كوني عقيرا','521','958','0');
INSERT INTO [t17712] VALUES (1410,'٤٥٢ - وكانت من اللا، لا يعيرها ابنها ... إذا ما الغلام الأحمق الأم عيرا','521','958','0');
INSERT INTO [t17712] VALUES (1411,'٤٥٣ - سقى الله أمواها عرفت مكانها ... جرابا وملكوما وبذر والغمرا','521','958','0');
INSERT INTO [t17712] VALUES (1412,'٤٥٤ - وكادت فزارة تشقى بنا ... فأولى فزارة أولى فزارا','521','958','0');
INSERT INTO [t17712] VALUES (1413,'٤٥٥ - لا تعنين بما أسبابه عسرت ... فلا يدي لامرئ إلا بما قدرا','521','958','0');
INSERT INTO [t17712] VALUES (1414,'٤٥٦ - فالشمس طالعة ليست بكاسفة ... تبكي عليك نجوم الليل والقمرا','522','958','0');
INSERT INTO [t17712] VALUES (1415,'٤٥٧ - لقد ظهرت فما تخفى على أحد ... إلا على أحد لا يعرف القمرا','522','958','0');
INSERT INTO [t17712] VALUES (1416,'٤٥٨ - بأي- تراهم- الأرضين حلوا ... أبالدبران أم عسفوا الكفارا؟','523','958','0');
INSERT INTO [t17712] VALUES (1417,'٤٥٩ - بلغت صنع امريء بر إخالكه ... إذ لم تزل لاكتساب الحمد مبتدرا','523','958','0');
INSERT INTO [t17712] VALUES (1418,'٤٦٠ - وإذا ما تشاء تبعث منها ... مغرب الشمس ناشطا مذعورا','523','958','0');
INSERT INTO [t17712] VALUES (1419,'٤٦١ - لقد طرقت رحال الحي ليلى ... فأبعد دار مرتحل مزارا','524','958','0');
INSERT INTO [t17712] VALUES (1420,'٤٦٢ - ودع ذا الهوى قبل القلى ترك ذي الهوى ... متين القوى خير من الصرم مزدرا','524','958','0');
INSERT INTO [t17712] VALUES (1421,'٤٦٣ - بلغنا السماء مجدنا وسناؤنا ... وإنا لنرجو فوق ذلك مظهرا','524','958','0');
INSERT INTO [t17712] VALUES (1422,'٤٦٤ - بنا عاذ عوف وهو بادي ذلة ... لديكم فلم يعدم ولاء ولا نصرا','525','958','0');
INSERT INTO [t17712] VALUES (1423,'٤٦٥ - لم ألق أخبث يا فرزدق منكم ... ليلا وأخبث بالنهار نهارا','525','958','0');
INSERT INTO [t17712] VALUES (1424,'٤٦٦ - منهن أيام صدق قد عرفت بها ... أيام واسط والأيام من هجرا','525','958','0');
INSERT INTO [t17712] VALUES (1425,'٤٦٧ - وتسخن ليلة لا يستطيع ... نباحا بها الكلب إلا هريرا','525','958','0');
INSERT INTO [t17712] VALUES (1426,'٤٦٨ - اطلب ولا تضجر من مطلب ... وآفة الطالب أن يضجرا أما ترى الحبل بتكراره ... في الصخرة الصماء قد أثرا','525','958','0');
INSERT INTO [t17712] VALUES (1427,'٤٦٩ - إن ابن عبد الله نعم أخو ... الندى وابن العشيره','526','958','0');
INSERT INTO [t17712] VALUES (1428,'٤٧٠ - يظل به الحرباء يمثل قائما ... ويكثر فيه من حنين الأباعر','526','958','0');
INSERT INTO [t17712] VALUES (1429,'٤٧١ - وإذا الرجال رأوا يزيد رأيتهم ... خضع الرقاب نواكس الأبصار','526','958','0');
INSERT INTO [t17712] VALUES (1430,'٤٧٢ - فقالوا: ما تشاء؟ فقلت: ألهو ... إلى الإصباح آثر ذي أثير','527','958','0');
INSERT INTO [t17712] VALUES (1431,'٤٧٣ - يا أمة وجدت مالا للا أحد ... إلا لظربى تناست بين أحجار','527','958','0');
INSERT INTO [t17712] VALUES (1432,'٤٧٤ - أجل أن الله قد فضلكم ... فوق من أحكأ صلبا بإزار','527','958','0');
INSERT INTO [t17712] VALUES (1433,'٤٧٥ - ألا أبلغ أبا حفص رسولا ... فدى لك من أخي ثقة إزاري','527','958','0');
INSERT INTO [t17712] VALUES (1434,'٤٧٦ - فقلت: لو باكرت مشمولة ... صهبا كلون الفرس الأشقر','527','958','0');
INSERT INTO [t17712] VALUES (1435,'٤٧٧ - رحت وفي رجليك عقالة ... وقد بدا هنك من المئزر','528','958','0');
INSERT INTO [t17712] VALUES (1436,'٤٧٨ - ومن أنتم إنا نسينا من أنتم ... وريحكم من أي ريح الأعاصر','528','958','0');
INSERT INTO [t17712] VALUES (1437,'٤٧٩ - أعلمت يوم عكاظ حين لقيتني ... تحت الغبار فما خططت غباري أنا اقتسمنا خطتينا بيننا ... فحملت برة واحتملت فجار','528','958','0');
INSERT INTO [t17712] VALUES (1438,'٤٨٠ - أبلغ النعمان عني مألكا ... أنه قد طال حبسي وانتظاري','529','958','0');
INSERT INTO [t17712] VALUES (1439,'٤٨١ - رب في الناس موسر كعديم ... وعديم يخال ذا أيسار','529','958','0');
INSERT INTO [t17712] VALUES (1440,'٤٨٢ - عودت قومي إذا ما الضيف نبهني ... عقر العشار على عسري وإيساري إني إذا خفيت نار لمرملة ... ألفى بأرفع تل رافعا ناري ذاك وإني على جاري لذو حدب ... أحنو عليه بما يحنى على الجار','530','958','0');
INSERT INTO [t17712] VALUES (1441,'٤٨٣ - لعن الإله- وزوجها معها- ... هند الهنود طويلة البظر','530','958','0');
INSERT INTO [t17712] VALUES (1442,'٤٨٤ - دعوا التخاجؤ وامشوا مشية سجحا ... إن الرجال ألو عصب وتذكير','531','958','0');
INSERT INTO [t17712] VALUES (1443,'٤٨٥ - متى تر عيني مالك وجرانه ... وجنبيه تعلم أنه غير ثائر حضجر كأم التوأمين توكأت ... على مرفقيها مستهلة عاشر','531','958','0');
INSERT INTO [t17712] VALUES (1444,'٤٨٦ - ألا أبلغ الأقياس قيس بن نوفل ... وقيس بن أهبان وقيس بن جابر','531','958','0');
INSERT INTO [t17712] VALUES (1445,'٤٨٧ - هي ابنتكم وأختكم زعمتم ... لثعلبة بن نوفل ابن جسر','531','958','0');
INSERT INTO [t17712] VALUES (1446,'٤٨٨ - تسائل عن قرم هجان سميدع ... لدى البأس مغوار الصباح جسور','532','958','0');
INSERT INTO [t17712] VALUES (1447,'٤٨٩ - حار بن كعب ألا أحلام تزجركم ... عنا وأنتم من الجوف الجماخير لا بأس بالقوم من طول ومن عظم ... جسم البغال وأحلام العصافير','532','958','0');
INSERT INTO [t17712] VALUES (1448,'٤٩٠ - أجن كلما ذكرت كليب ... أبيت كأنني أكوى بجمر','532','958','0');
INSERT INTO [t17712] VALUES (1449,'٤٩١ - بعينيك يا سلمى ارحمي ذا صبابة ... أبى غير ما يرضيك في السر والجهر','532','958','0');
INSERT INTO [t17712] VALUES (1450,'٤٩٢ - رهط ابن كوز محقبي أدراعهم ... فيهم ورهط ربيعة بن حذار','532','958','0');
INSERT INTO [t17712] VALUES (1451,'٤٩٣ - دع ذا وعد القول في هرم ... خير البداة وسيد الحضر','533','958','0');
INSERT INTO [t17712] VALUES (1452,'٤٩٤ - ألا قبح الإله بني زياد ... وحي أبيهم قبح الحمار','533','958','0');
INSERT INTO [t17712] VALUES (1453,'٤٩٥ - كسا اللؤم تيما حضرة في جلودها ... فويلا لتيم من سرابيلها الخضر','533','958','0');
INSERT INTO [t17712] VALUES (1454,'٤٩٦ - لهني لأشقى الناس إن كنت غارما ... لعاقبة قتلى خزيمة والخضر','534','958','0');
INSERT INTO [t17712] VALUES (1455,'٤٩٧ - وقربن بالرزق الجمائل بعد ما ... تقوب عن غربان أوراكها الخطر','534','958','0');
INSERT INTO [t17712] VALUES (1456,'٤٩٨ - ولفوك أطيب لو بذلت لنا ... من ماء موهبة على خمر','534','958','0');
INSERT INTO [t17712] VALUES (1457,'٤٩٩ - أن نعم معترك الجياع إذا ... خب السفير وسابئ الخمر','535','958','0');
INSERT INTO [t17712] VALUES (1458,'٥٠٠ - ليست بسوداء ولا عنفص ... تسارق الطرف إلى الداعر','535','958','0');
INSERT INTO [t17712] VALUES (1459,'٥٠١ - يا عين بكي حنيفا رأس حيهم ... الكاسرين القنا في عورة الدبر','535','958','0');
INSERT INTO [t17712] VALUES (1460,'٥٠٢ - يا ليت لي سلوة تشفى النفوس بها ... من بعض ما يعتري قلبي من الدكر','536','958','0');
INSERT INTO [t17712] VALUES (1461,'٥٠٣ - أزور امرأ جما نوال أعده ... لمن أمه مستكفيا أزمة الدهر','536','958','0');
INSERT INTO [t17712] VALUES (1462,'٥٠٤ - سرت تخبط الظلماء من جانبي قسا ... وحب بها من خابط الليل زائر','536','958','0');
INSERT INTO [t17712] VALUES (1463,'٥٠٥ - سقوني الخمر ثم تكنفوني ... عداة الله من كذب وزور','536','958','0');
INSERT INTO [t17712] VALUES (1464,'٥٠٦ - حلفت بمائرات حول عوض ... وأنصاب تركن لدى السعير أجوب الأرض دهرا إثر عمرو ... ولا يلفى بساحته بعيري','537','958','0');
INSERT INTO [t17712] VALUES (1465,'٥٠٧ - جئني بمثل بني بدر لقومهم ... أو مثل أسرة منظور بن سيار','537','958','0');
INSERT INTO [t17712] VALUES (1466,'٥٠٨ - لقد كذبتك نفسك فاكذبيها ... فإن جزعا وإن إجمال صبر','538','958','0');
INSERT INTO [t17712] VALUES (1467,'٥٠٩ - ألا حبذا قوما سليم فإنهم ... وفوا إذ تواصوا بالإعانة والصبر','538','958','0');
INSERT INTO [t17712] VALUES (1468,'٥١٠ - لما أتوها بمصباح ومبزلهم ... سارت إليهم سؤور الأبجل الضاري','539','958','0');
INSERT INTO [t17712] VALUES (1469,'٥١١ - عهدي بها في الحي قد سربلت ... هيفاء مثل المهرة الضامر','539','958','0');
INSERT INTO [t17712] VALUES (1470,'٥١٢ - ونحن تركنا تغلب ابنة وائل ... كمضروبة رجلاه منقطع الظهر','539','958','0');
INSERT INTO [t17712] VALUES (1471,'٥١٣ - سواء عليك النفر أم بت ليلة ... بأهل القباب من عمير بن عامر','539','958','0');
INSERT INTO [t17712] VALUES (1472,'٥١٤ - أنت الجواد الذي ترجى نوافله ... وأبعد الناس كل الناس من عار','539','958','0');
INSERT INTO [t17712] VALUES (1473,'٥١٥ - كلا الضيفن المشنوء والضيف واجد ... لدي المنى والأمن في العسر واليسر','540','958','0');
INSERT INTO [t17712] VALUES (1474,'٥١٦ - ما زلت أفتح أبوابا وأغلقها ... حتى أتيت أبا عمرو بن عمار','540','958','0');
INSERT INTO [t17712] VALUES (1475,'٥١٧ - كم عمة لك يا جرير وخالة ... فدعاء قد حلبت علي عشاري كنا نحاذر أن تضيع لقاحنا ... ولهى إذا سمعت دعاء يسار شغارة تقذ الفصيل برجلها ... فطارة لقوادم الأبكار','540','958','0');
INSERT INTO [t17712] VALUES (1476,'٥١٨ - فلم أجبن ولم أنكل ولكن ... يممت بها أبا صخر بن عمرو','541','958','0');
INSERT INTO [t17712] VALUES (1477,'٥١٩ - سماع الله والعلماء أني ... أعوذ بحقو خالك يا ابن عمرو','541','958','0');
INSERT INTO [t17712] VALUES (1478,'٥٢٠ - رأيتك لما أن عرفت وجوهنا ... صددت وطبت النفس يا قيس عن عمرو','542','958','0');
INSERT INTO [t17712] VALUES (1479,'٥٢١ - وما اهتز عرش الله من أجل هالك ... سمعنا به إلا لسعد أبي عمرو','542','958','0');
INSERT INTO [t17712] VALUES (1480,'٥٢٢ - ونبئت جوابا وسكنا يسبني ... وعمرو بن عفرا لا سلام على عمرو','542','958','0');
INSERT INTO [t17712] VALUES (1481,'٥٢٣ - لولا الحياء وباقي الدين عبتكما ... ببعض ما فيكما إذ عبتما عوري','543','958','0');
INSERT INTO [t17712] VALUES (1482,'٥٢٤ - يدعوه سرا وإعلانا ليرزقه ... شهادة بيدي ملحادة غدر','543','958','0');
INSERT INTO [t17712] VALUES (1483,'٥٢٥ - أتيت بعبد الله في القد موثقا ... فهلا سعيدا ذا الخيانة والغدر','543','958','0');
INSERT INTO [t17712] VALUES (1484,'٥٢٦ - أقول لما جاءني فخره ... سبحان من علقمة الفاخر','543','958','0');
INSERT INTO [t17712] VALUES (1485,'٥٢٧ - كم قد ذكرتك لو أجزى بذكركم ... يا أشبه الناس كل الناس بالقمر','544','958','0');
INSERT INTO [t17712] VALUES (1486,'٥٢٨ - ما المستفز الهوى محمود عاقبة ... ولو أتيح له صفو بلا كدر','545','958','0');
INSERT INTO [t17712] VALUES (1487,'٥٢٩ - وكنت إذا جاري دعا لمضوفة ... أشمر حتى يبلغ الساق مئزري','545','958','0');
INSERT INTO [t17712] VALUES (1488,'٥٣٠ - تربص بها الأيام عل صروفها ... سترمي بها في جاحم متسعر','545','958','0');
INSERT INTO [t17712] VALUES (1489,'٥٣١ - ولقد قتلتهم ثناء وموحدا ... وتركت مرة مثل أمس المدبر','545','958','0');
INSERT INTO [t17712] VALUES (1490,'٥٣٢ - في غرف الجنة العليا التي وجبت ... لهم هناك بسعي كان مشكور','545','958','0');
INSERT INTO [t17712] VALUES (1491,'٥٣٣ - فلم أرقه إن ينج منها وإن يمت ... فطعنة لا غس ولا بمغمر','545','958','0');
INSERT INTO [t17712] VALUES (1492,'٥٣٤ - غمز ابن مرة يا فرزدق كينها ... غمز الطبيب نغانغ المغدور','546','958','0');
INSERT INTO [t17712] VALUES (1493,'٥٣٥ - قهرت العدا لا مستعينا بعصبة ... ولكن بأنواع الخدائع والمكر','546','958','0');
INSERT INTO [t17712] VALUES (1494,'٥٣٦ - أولاك بنو خير وشر كليهما ... جمعيا ومعروف ألم ومنكر','546','958','0');
INSERT INTO [t17712] VALUES (1495,'٥٣٧ - نبئتهم عذبوا بالنار جارهم ... وهل يعذب إلا الله بالنار','546','958','0');
INSERT INTO [t17712] VALUES (1496,'٥٣٨ - وتذكر نعماه لدن أنت يافع ... إلى أنت ذو فودين أبيض كالنسر','546','958','0');
INSERT INTO [t17712] VALUES (1497,'٥٣٩ - أصابهم بلاء كان فيهم ... سوى ما قد أصاب بني النضير','547','958','0');
INSERT INTO [t17712] VALUES (1498,'٥٤٠ - يا قاتل الله صبيانا تجيء بهم ... أم الهنيبر من زند لها واري من كل أعلم مشقوق وتيرته ... لم يوف خمسة أشبار بشبار','547','958','0');
INSERT INTO [t17712] VALUES (1499,'٥٤١ - دعوني فيالبي إذا هدرت لهم ... شقاشق أقوام فأسكتها بدري','547','958','0');
INSERT INTO [t17712] VALUES (1500,'٥٤٢ - لعل التفاتا منك نحوي ميسر ... يمل بك من بعد القساوة لليسر','547','958','0');
INSERT INTO [t17712] VALUES (1501,'٥٤٣ - ولأنت تفري ما خلقت وبع ... ض القوم يخلق ثم لا يفر','548','958','0');
INSERT INTO [t17712] VALUES (1502,'٥٤٤ - رب رام من بني ثعل ... متلج كفيه في قتره','548','958','0');
INSERT INTO [t17712] VALUES (1503,'٥٤٥ - تجاوزت هندا رغبة عن قتاله ... إلى ملك أعشو إلى ضوء ناره','548','958','0');
INSERT INTO [t17712] VALUES (1504,'٥٤٦ - إذا أوقدوا نارا لحرب عدوهم ... فقد خاب من يصلى بها وسعيرها','548','958','0');
INSERT INTO [t17712] VALUES (1505,'٥٤٧ - تمر على ما تستمر وقد شفت ... غلائل عبد القيس منها صدورها','549','958','0');
INSERT INTO [t17712] VALUES (1506,'٥٤٨ - واعلم بأنك والمنية شا ... رب بعقارها','549','958','0');
INSERT INTO [t17712] VALUES (1507,'٥٤٩ - شئز جنبي كأني مهدأ ... جعل القين على الدف الإبر','549','958','0');
INSERT INTO [t17712] VALUES (1508,'٥٥٠ - فإن القوافي يتلجن موالجا ... تضايق عنها أن تولجها الإبر','549','958','0');
INSERT INTO [t17712] VALUES (1509,'٥٥١ - ثم راحوا عبق المسك بهم ... يلحفون الأرض هداب الأزر','549','958','0');
INSERT INTO [t17712] VALUES (1510,'٥٥٢ - لا، وأبيك ابنة العامري ... لا يدعي القوم أني أفر','550','958','0');
INSERT INTO [t17712] VALUES (1511,'٥٥٣ - وقد رابني قولها يا هنا ... هـ ويحك ألحقت شرا بشر','550','958','0');
INSERT INTO [t17712] VALUES (1512,'٥٥٤ - إذا ذقت فاها قلت طعم مدامة ... معتقة مما تجيء به التجر','551','958','0');
INSERT INTO [t17712] VALUES (1513,'٥٥٥ - يفاكهنا سعد ويغدو لجمعنا ... بمثنى الزقاق المترعات وبالجزر','551','958','0');
INSERT INTO [t17712] VALUES (1514,'٥٥٦ - بئس قوم الله قوم طرقوا ... فقروا جارهم لحما وحر','551','958','0');
INSERT INTO [t17712] VALUES (1515,'٥٥٧ - صبحك الله بخير باكر ... بنعم طير وشباب فاخر','551','958','0');
INSERT INTO [t17712] VALUES (1516,'٥٥٨ - كل أنثى وإن بدا لك منها ... آية الحب حبها خيتعور','552','958','0');
INSERT INTO [t17712] VALUES (1517,'٥٥٩ - عن مبرقات بالبرين وتبدو ... بالأكف اللامعات سور','552','958','0');
INSERT INTO [t17712] VALUES (1518,'٥٦٠ - أعمرو بن هند ما ترى رأي صرمة ... لها سبب ترعى به الماء والشجر','552','958','0');
INSERT INTO [t17712] VALUES (1519,'٥٦١ - خالتي والنفس قدما إنهم ... نعم الساعون في القوم الشطر','552','958','0');
INSERT INTO [t17712] VALUES (1520,'٥٦٢ - أيها الفتيان في مجلسنا ... جردوا منها ورادا وشقر','553','958','0');
INSERT INTO [t17712] VALUES (1521,'٥٦٣ - إذا اشتبه الرشد في الحادثا ... ت فارض بأيتها قد قدر','553','958','0');
INSERT INTO [t17712] VALUES (1522,'٥٦٤ - أصحوت اليوم أم شاقتك هر ... ومن الحب جنون مستعر','553','958','0');
INSERT INTO [t17712] VALUES (1523,'٥٦٥ - فأصبحت فيهم آمنا لا كمعشر ... أتوني فقالوا: من ربيعة أو مضر','553','958','0');
INSERT INTO [t17712] VALUES (1524,'٥٦٦ - وأركب في الروع خيفانة ... كسا وجهها سعف منتشر','553','958','0');
INSERT INTO [t17712] VALUES (1525,'٥٦٧ - فيوم علينا ويوم لنا ... ويوم نساء ويوم نسر','553','958','0');
INSERT INTO [t17712] VALUES (1526,'٥٦٨ - وما منهما إلا يسر بنسبة ... تقربه مني وإن كان ذا نفر','554','958','0');
INSERT INTO [t17712] VALUES (1527,'٥٦٩ - لها متنتان خظاتا كما ... أكب على ساعديه النمر','554','958','0');
INSERT INTO [t17712] VALUES (1528,'٥٧٠ - إذا قلت أني آيب أهل بلدة ... وضعت بها عنه الولية بالهجر','554','958','0');
INSERT INTO [t17712] VALUES (1529,'٥٧١ - أحار بن عمرو كأني خمر ... ويعدو على المرء ما يأتمر','554','958','0');
INSERT INTO [t17712] VALUES (1530,'٥٧٢ - نحن في المشتاة ندعو الجفلى ... لا ترى الآدب فينا ينتقر','555','958','0');
INSERT INTO [t17712] VALUES (1531,'قافية الزاي','557','0','0');
INSERT INTO [t17712] VALUES (1532,'١ - كأن لم يكونوا حمى يتقى ... إذ الناس إذ ذاك من عز بزا','557','1531','0');
INSERT INTO [t17712] VALUES (1533,'٢ - وأفنى رجالي فبادوا معا ... فأصبح قلبي بهم مستفزا','557','1531','0');
INSERT INTO [t17712] VALUES (1534,'٣ - وهن وقوف ينتظرن قضاءه ... بضاحي عذاة أمره وهو ضامز','557','1531','0');
INSERT INTO [t17712] VALUES (1535,'٤ - وكل خليل غير هاضم نفسه ... لوصل خليل صارم أو معارز','558','1531','0');
INSERT INTO [t17712] VALUES (1536,'٥ - لا در دري إن أطعمت نازلكم ... قرف الحتي وعندي البر مكنوز','558','1531','0');
INSERT INTO [t17712] VALUES (1537,'٦ - إما تريني اليوم أم حمز ... قاربت بين عنقي وجمزي','559','1531','0');
INSERT INTO [t17712] VALUES (1538,'٧ - يا أيها الجاهل ذو التنزي','559','1531','0');
INSERT INTO [t17712] VALUES (1539,'٨ - برأس دماغ رؤوس العز','559','1531','0');
INSERT INTO [t17712] VALUES (1540,'٩ - مثل الكلاب تهر عند بيوتها ... ورمت لهازمها من الخزباز','559','1531','0');
INSERT INTO [t17712] VALUES (1541,'١٠ - نسيا حاتم وأوس لدن فا ... ضت عطاياك يا بن عبد العزيز','560','1531','0');
INSERT INTO [t17712] VALUES (1542,'قافية السين','561','0','0');
INSERT INTO [t17712] VALUES (1543,'١ - خلا أن العتاق من المطايا ... حسين به فهن إليه شوس','561','1542','0');
INSERT INTO [t17712] VALUES (1544,'٢ - اضرب عنك الهموم طارقها ... ضربك بالسوط قونس الفرس','561','1542','0');
INSERT INTO [t17712] VALUES (1545,'٣ - وبدلت قرحا داميا بعد صحة ... لعل منايانا تحولن أبؤسا','562','1542','0');
INSERT INTO [t17712] VALUES (1546,'٤ - فلم أر مثل الحي حيا مصبحا ... ولا مثلنا يوم التقينا فوارسا أكر وأحمى للحقيقة منهم ... وأضرب منا بالسيوف القوانسا','562','1542','0');
INSERT INTO [t17712] VALUES (1547,'٥ - هذي برزت لنا فهجت رسيسا ... ثم انصرفت وما شفيت نسيسا','563','1542','0');
INSERT INTO [t17712] VALUES (1548,'٦ - قد أصبحت بقرقرى كوانسا ... فلا تلمه أن ينام البائسا','563','1542','0');
INSERT INTO [t17712] VALUES (1549,'٧ - إن سلمى من بعد يأسي همت ... بوصال لو صح لم تبق لي بوسا عينت ليلة فما زلت حتى ... نصفها راجيا فعدت يؤوسا','563','1542','0');
INSERT INTO [t17712] VALUES (1550,'٨ - أقمنا بها يوما ويوما وثالثا ... ويوما له يوم الترحل خامس','564','1542','0');
INSERT INTO [t17712] VALUES (1551,'٩ - آليت حب العراق الدهر أطعمه ... والحب يأكله في القرية السوس','564','1542','0');
INSERT INTO [t17712] VALUES (1552,'١٠ - وأسلمني الزمان كذا ... فلا طرب ولا أنس','565','1542','0');
INSERT INTO [t17712] VALUES (1553,'١١ - وابن اللبون إذا ما لز في قرن ... لم يستطع صولة البزل القناعيس','565','1542','0');
INSERT INTO [t17712] VALUES (1554,'١٢ - أزمعت يأسا مبينا من نوالكم ... ولن ترى طاردا للحر كالياس','565','1542','0');
INSERT INTO [t17712] VALUES (1555,'١٣ - أعلاقة أم الوليد بعد ما ... أفنان رأسك كالثغام المخلس','566','1542','0');
INSERT INTO [t17712] VALUES (1556,'١٤ - عددت قومي كعديد الطيس ... إذ ذهب القوم الكرام ليسي','566','1542','0');
INSERT INTO [t17712] VALUES (1557,'١٥ - فأين إلى أين النجاة ببغلتي ... أتاك أتاك اللاحقون احبس احبس','566','1542','0');
INSERT INTO [t17712] VALUES (1558,'١٦ - أطريفة بن العبد إنك جاهل ... أبساحة الملك الهمام تمرس ألق الصحيفة لا أبالك إنه ... يخشى عليك من الحباء النقرس','567','1542','0');
INSERT INTO [t17712] VALUES (1559,'١٧ - أبا حسن ما زرتكم مذ سنية ... من الدهر إلا والزجاجة تقلس كريم إلى جنب الخوان وزوره ... يحيا بأهلا مرحبا ثم يجلس','567','1542','0');
INSERT INTO [t17712] VALUES (1560,'١٨ - لقد رأيت عجبا مذ أمسا ... عجائزا مثل السعالي خمسا يأكلن ما في رحلهن همسا ... لا ترك الله لهن ضرسا ولا لقين الدهر إلا تعسا','568','1542','0');
INSERT INTO [t17712] VALUES (1561,'١٩ - منع البقاء تقلب الشمس ... وطلوعها من حيث لا تمسي وطلوعها حمراء صافية ... وغروبها صفراء كالورس اليوم أعلم ما يجيء به ... ومضى بفصل قضائه أمس','568','1542','0');
INSERT INTO [t17712] VALUES (1562,'٢٠ - يا صاح يا ذا الضامر العنس ... والرحل ذي الأنساع والحلس','569','1542','0');
INSERT INTO [t17712] VALUES (1563,'٢١ - يا مرو إن مطيتي محبوسة ... ترجو الحباء وربها لم ييأس','569','1542','0');
INSERT INTO [t17712] VALUES (1564,'٢٢ - مرت بنا أول من أموس ... تميس فينا ميسة العروس','569','1542','0');
INSERT INTO [t17712] VALUES (1565,'٢٣ - وبلدة ليس بها أنيس ... إلا اليعافير وإلا العيس','570','1542','0');
INSERT INTO [t17712] VALUES (1566,'٢٤ - ومرة يحميهم إذا ما تبددوا ... ويطعنهم شزرا فأبرحت فارسا','570','1542','0');
INSERT INTO [t17712] VALUES (1567,'٢٥ - أقاتل حتى لا أرى لي مقاتلا ... وأنجو إذا لم ينج إلا المكيس','570','1542','0');
INSERT INTO [t17712] VALUES (1568,'٢٦ - هنيئا لأرباب البيوت بيوتهم ... وللعزب المسكين ما يتلمس','571','1542','0');
INSERT INTO [t17712] VALUES (1569,'٢٧ - إذا شق برد شق بالبرد مثله ... دواليك حتى ليس للبرد لابس','571','1542','0');
INSERT INTO [t17712] VALUES (1570,'٢٨ - لله يبقى على الأيام ذو حيد ... بمشخر به الظيان والآس','571','1542','0');
INSERT INTO [t17712] VALUES (1571,'٢٩ - يا مي إن تفقدي قوما ولدتهم ... أو تخلسيهم فإن الدهر خلاس عمرو وعبد مناف والذي عهدت ... ببطن عرعر آبي الضيم عباس','572','1542','0');
INSERT INTO [t17712] VALUES (1572,'٣٠ - تالله لا يعجز الأيام مبترك ... في حومة الموت رزام وفراس يحمي الصريمة أحدان الرجال له ... صيد ومجترئ بالليل هماس','572','1542','0');
INSERT INTO [t17712] VALUES (1573,'٣١ - إذ ما أتيت على الرسول فقل له ... حقا عليك إذا اطمأن المجلس','573','1542','0');
INSERT INTO [t17712] VALUES (1574,'٣٢ - أحقا بني أبناء سلمى ابن جندل ... تهددكم إياي وسط المجالس','573','1542','0');
INSERT INTO [t17712] VALUES (1575,'٣٣ - سل الهموم بكل معطي رأسه ... ناج مخالط صهبة متعيس مغتال أحبله مبين عنقه ... في منكب زبن المطي عرندس','573','1542','0');
INSERT INTO [t17712] VALUES (1576,'٣٤ - إذا حملت بدني على عدس ... على الذي بين الحمار والفرس فلا أبالي من عدا ومن جلس','574','1542','0');
INSERT INTO [t17712] VALUES (1577,'٣٥ - دع المكارم لا ترحل لبغيتها ... واقعد فإنك أنت الطاعم الكاسي','574','1542','0');
INSERT INTO [t17712] VALUES (1578,'٣٦ - لعمرك ما الإنسان إلا ابن يومه ... على ما تجلى يومه لا ابن أمسه وما الفخر بالعظم الرميم وإنما ... فخار الذي يبغي الفخار بنفسه','574','1542','0');
INSERT INTO [t17712] VALUES (1579,'٣٧ - اعتصم بالرجاء إن عن يأس ... وتناس الذي تضمن أمس','574','1542','0');
INSERT INTO [t17712] VALUES (1580,'٣٨ - في حسب بخ وعز أقعسا','574','1542','0');
INSERT INTO [t17712] VALUES (1581,'٣٩ - فأصبحت بقرقرى كوانسا ... فلا تلمه أن ينام البائسا','575','1542','0');
INSERT INTO [t17712] VALUES (1582,'٤٠ - محتبك ضخم شؤون الرأس','575','1542','0');
INSERT INTO [t17712] VALUES (1583,'٤١ - فمن طلب الأوتار ما حز أنفه ... قصير ورام الموت بالسيف بيهس نعامة لما صرع القوم رهطه ... تبين في أثوابه كيف يلبس','575','1542','0');
INSERT INTO [t17712] VALUES (1584,'٤٢ - بثوب ودينار وشاة ودرهم ... فهل أنت مرفوع بما ها هنا رأس','576','1542','0');
INSERT INTO [t17712] VALUES (1585,'٤٣ - أفي حق مواساتي أخالكم ... بمالي ثم يظلمني السريس','576','1542','0');
INSERT INTO [t17712] VALUES (1586,'٤٤ - من فوقه أنسر سود وأغربة ... وتحته أعنز كلف وأتياس','576','1542','0');
INSERT INTO [t17712] VALUES (1587,'٤٥ - ليث هزبر مدل عند خيسته ... بالرقمتين له أجر وأعراس','576','1542','0');
INSERT INTO [t17712] VALUES (1588,'٤٦ - معاود جرأة وقت الهوادي ... أشم كأنه رجل عبوس','577','1542','0');
INSERT INTO [t17712] VALUES (1589,'٤٧ - تقول: ودقت صدرها بيمينها ... أبعلي هذا بالرحى المتقاعس','578','1542','0');
INSERT INTO [t17712] VALUES (1590,'٤٨ - إذا أرسلوني عند تعذير حاجة ... أمارس فيها كنت نعم الممارس','579','1542','0');
INSERT INTO [t17712] VALUES (1591,'٤٩ - هل من حلوم لأقوام فتنذرهم ... ما جرب الناس من عضي وتضريسي','579','1542','0');
INSERT INTO [t17712] VALUES (1592,'٥٠ - إذا هبطن سماويا موارده ... من نحو دومة خبت قل تعريسي','579','1542','0');
INSERT INTO [t17712] VALUES (1593,'٥١ - مطاعين في الهيجا مطاعيم للقرى ... إذا اصفر آفاق السماء من القرس','579','1542','0');
INSERT INTO [t17712] VALUES (1594,'٥٢ - إما شربت بكأس دار أولها ... على القرون فذاقوا جرعة الكاس','580','1542','0');
INSERT INTO [t17712] VALUES (1595,'٥٣ - كي لتقضيني رقية ما ... وعدتني غير مختلس','580','1542','0');
INSERT INTO [t17712] VALUES (1596,'٥٤ - تنادوا بالرحيل غدا ... وفي ترحالهم نفسي','581','1542','0');
INSERT INTO [t17712] VALUES (1597,'٥٥ - لما تذكرت بالديرين أرقني ... صوت الدجاج وقرع بالنواقيس','581','1542','0');
INSERT INTO [t17712] VALUES (1598,'قافية الشين','582','0','0');
INSERT INTO [t17712] VALUES (1599,'١ - فإن أهلك فسو تجدون فقدي ... وإن أسلم يطب لكم المعاش','582','1598','0');
INSERT INTO [t17712] VALUES (1600,'٢ - وقريش هي التي تسكن البحر ... بها سميت قريش قريشا','582','1598','0');
INSERT INTO [t17712] VALUES (1601,'٣ - تضحك مني أن رأتني أحترش ... ولو حرشت لكشفت عن حرش','582','1598','0');
INSERT INTO [t17712] VALUES (1602,'٤ - أيا أبتي لا زلت فينا فإنما ... لنا أمل في العيش ما دمت عائشا','584','1598','0');
INSERT INTO [t17712] VALUES (1603,'قافية الصاد','585','0','0');
INSERT INTO [t17712] VALUES (1604,'١ - جشأت فقلت اللذ خشيت ليأتين ... وإذا أتاك فلات حين مناص','585','1603','0');
INSERT INTO [t17712] VALUES (1605,'٢ - أكاشره وأعلم أن كلانا ... على ما ساء صاحبه حريص','585','1603','0');
INSERT INTO [t17712] VALUES (1606,'٣ - قد كنت خراجا ولوجا صيرفا ... لم تلتحصني حيص بيص لحاص','585','1603','0');
INSERT INTO [t17712] VALUES (1607,'٤ - كلوا في بعض بطنكم تعفوا ... فإن زمانكم زمن خميص','585','1603','0');
INSERT INTO [t17712] VALUES (1608,'٥ - كلا أخويكم كان فرعا دعامة ... ولكنهم زادوا وأصبحت ناقصا','586','1603','0');
INSERT INTO [t17712] VALUES (1609,'٦ - لدن غدوة حتى ألان بخفها ... بقية منقوص من الظل قالص','586','1603','0');
INSERT INTO [t17712] VALUES (1610,'٧ - أتاني وعيد الحوص من آل جعفر ... فيا عبد عمرو لو نهيت الأحاوصا','586','1603','0');
INSERT INTO [t17712] VALUES (1611,'٨ - فإن تتعدني أتعدك بمثلها ... وسوف أزيد الباقيات القوارصا','587','1603','0');
INSERT INTO [t17712] VALUES (1612,'٩ - يا عبد هل تذكرني ساعة ... في موكب أو رائدا للقنيص','587','1603','0');
INSERT INTO [t17712] VALUES (1613,'١٠ - أأطعمت العراق ورافديه ... فزاريا أحذ يد القميص','587','1603','0');
INSERT INTO [t17712] VALUES (1614,'قافية ضاد العرب','589','0','0');
INSERT INTO [t17712] VALUES (1615,'١ - وليس دين الله بالمعضى','589','1614','0');
INSERT INTO [t17712] VALUES (1616,'٢ - فو الله لا أنسى قتيلا رزئته ... بجانب قوسى ما مشيت على الأرض على أنها تعفو الكلوم وإنما ... يوكل بالأدنى وإن جل ما يمضي','589','1614','0');
INSERT INTO [t17712] VALUES (1617,'٣ - طول الليالي أسرعت في نقضي ... نقضن كلي ونقضن بعضي','590','1614','0');
INSERT INTO [t17712] VALUES (1618,'٤ - لقد أتت في رمضان الماضي ... جارية في درعها الفضفاض تقطع الحديث بالإيماض ... أبيض من أخت بني أباض','590','1614','0');
INSERT INTO [t17712] VALUES (1619,'٥ - أفي كل عام مأتم تبعثونه ... على محمر ثوبتموه وما رضا','591','1614','0');
INSERT INTO [t17712] VALUES (1620,'٦ - أبا منذر أفنيت فاستبق بعضنا ... حنانيك بعض الشر أهون من بعض','591','1614','0');
INSERT INTO [t17712] VALUES (1621,'٧ - هجوم عليها نفسه غير أنه ... متى يرم في عينيه بالشبح ينهض','591','1614','0');
INSERT INTO [t17712] VALUES (1622,'٨ - عذير الحي من عدوا ... ن كانوا حية الأرض','592','1614','0');
INSERT INTO [t17712] VALUES (1623,'٩ - إذا أكلت سمكا وفرضا ... ذهبت طولا وذهبت عرضا','592','1614','0');
INSERT INTO [t17712] VALUES (1624,'١٠ - أمسلم يا اسمع يا بن كل خليفة ... ويا سائس الدنيا ويا جبل الأرض','592','1614','0');
INSERT INTO [t17712] VALUES (1625,'١١ - فقولا لهذا المرء ذو جاء ساعيا ... هلم فإن المشرفي الفرائض','593','1614','0');
INSERT INTO [t17712] VALUES (1626,'١٢ - أظنك دون المال ذو جئت تبتغي ... ستلقاك بيض للنفوس قوابض','593','1614','0');
INSERT INTO [t17712] VALUES (1627,'١٣ - يغادر محض الماء ذو وهو محضه ... على إثره إن كان للماء من محض يروي العروق الهامدات من البلى ... من العرفج النجدي ذو باد والحمض','593','1614','0');
INSERT INTO [t17712] VALUES (1628,'١٤ - ولا أدر من ألقى عليه رداءه ... على أنه قد سل عن ماجد محض','594','1614','0');
INSERT INTO [t17712] VALUES (1629,'١٥ - قضى الله يا أسماء أن لست زائلا ... أحبك حتى يغمض الجفن مغمض','594','1614','0');
INSERT INTO [t17712] VALUES (1630,'١٦ - بتيهاء قفر والمطي كأنها ... قطا الحزن قد كانت فراخا بيوضها','594','1614','0');
INSERT INTO [t17712] VALUES (1631,'١٧ - فق الناس في الخير لا سيما ... ينيلك من ذي الجلال الرضى','595','1614','0');
INSERT INTO [t17712] VALUES (1632,'١٨ - كادت وكدت وتلك خير إرادة ... لو كان من لهو الصبابة ما مضى','595','1614','0');
INSERT INTO [t17712] VALUES (1633,'١٩ - فو الله ما أنسى قتيلا رزيته ... بجانب قوسى ما مشيت على الأرض','595','1614','0');
INSERT INTO [t17712] VALUES (1634,'٢٠ - وممن ولدوا عامر ... ذو الطول وذو العرض','595','1614','0');
INSERT INTO [t17712] VALUES (1635,'٢١ - وسن كسنيق سناء وسنما ... ذعرت بمدلاح الهجير نهوض','596','1614','0');
INSERT INTO [t17712] VALUES (1636,'٢٢ - أرجزا تريد أم قريضا ... أم هكذا بينهما تعريضا كلاهما أجيد مستريضا','597','1614','0');
INSERT INTO [t17712] VALUES (1637,'قافية الطاء','598','0','0');
INSERT INTO [t17712] VALUES (1638,'١ - حتى إذا جن الظلام واختلط ... جاؤا بمذق هل رأيت الذئب قط','598','1637','0');
INSERT INTO [t17712] VALUES (1639,'٢ - فلا والله نادى الحي ضيفي ... هدوا بالمساءة والعلاط','598','1637','0');
INSERT INTO [t17712] VALUES (1640,'٣ - كأني بك تنحط ... إلى اللحد وتنغط وقد أسلمك الرهط ... إلى أضيق من سم','599','1637','0');
INSERT INTO [t17712] VALUES (1641,'٤ - فما أنا والسير في متلف ... يبرح بالذكر الضابط','600','1637','0');
INSERT INTO [t17712] VALUES (1642,'٥ - فإما تعرضن أميم عني ... وينزغك الوشاة أولو النباط فحور قد لهيت بهن عين ... نواعم في المروط وفي الرياط','600','1637','0');
INSERT INTO [t17712] VALUES (1643,'٦ - ومنهل وردته التقاطا ... لم ألق إذ وردته فراطا إلا الحمام الورق والغطاطا','600','1637','0');
INSERT INTO [t17712] VALUES (1644,'٧ - شراب ألبان وتمر وأقط','601','1637','0');
INSERT INTO [t17712] VALUES (1645,'٨ - أبيت على معاري فاخرات ... بهن ملوب كدم العباط','601','1637','0');
INSERT INTO [t17712] VALUES (1646,'٩ - أطلت فراطهم حتى إذا ما ... قتلت سراتهم كانت قطاط','601','1637','0');
INSERT INTO [t17712] VALUES (1647,'قافية الظاء','603','0','0');
INSERT INTO [t17712] VALUES (1648,'١ - ألا من مبلغ حسان عني ... مغلغلة تدب إلى عكاظ','603','1647','0');
INSERT INTO [t17712] VALUES (1649,'٢ - يداك، يد خيرها يرتجى ... وأخرى لأعدائها غائظة','603','1647','0');
INSERT INTO [t17712] VALUES (1650,'٣ - تجلد لا يقل هؤلاء هذا ... بكى لما بكى أسفا وغيظا','603','1647','0');
INSERT INTO [t17712] VALUES (1651,'حرف العين','604','0','0');
INSERT INTO [t17712] VALUES (1652,'١ - لما عصى أصحابه مصعبا ... أدى إليه الكيل صاعا بصاع','604','1651','0');
INSERT INTO [t17712] VALUES (1653,'٢ - فأقسم لو شيء أتانا رسوله ... سواك ولكن لم نجد لك مدفعا','604','1651','0');
INSERT INTO [t17712] VALUES (1654,'٣ - إذا المرء لم يغش الكريهة أوشكت ... حبال الهوينى بالفتى أن تقطعا','605','1651','0');
INSERT INTO [t17712] VALUES (1655,'٤ - قعيدك أن لا تسمعيني ملامة ... ولا تنكئي قرح الفؤاد فييجعا','606','1651','0');
INSERT INTO [t17712] VALUES (1656,'٥ - ألا قالت العصماء يوم لقيتها ... أراك حديثا ناعم البال أفرعا فقلت لها: لا تنكريني فقلما ... يسود الفتى حتى يشيب ويصلعا','606','1651','0');
INSERT INTO [t17712] VALUES (1657,'٦ - لقد عذلتني أم عمرو ولم أكن ... مقالتها- ما كنت حيا- لأسمعا','606','1651','0');
INSERT INTO [t17712] VALUES (1658,'٧ - تعلم أن بعد الغي رشدا ... وأن لهذه الغبر انقشاعا','607','1651','0');
INSERT INTO [t17712] VALUES (1659,'٨ - جزعت حذار البين يوم تحملوا ... وحق لمثلي يا بثينة يجزع','607','1651','0');
INSERT INTO [t17712] VALUES (1660,'٩ - من النفر اللائي الذين إذا اعتزوا ... وهاب الرجال حلقة الباب قعقعوا','607','1651','0');
INSERT INTO [t17712] VALUES (1661,'١٠ - لحافي لحاف الضيف والبرد برده ... ولم يلهني عنه غزال مقنع','607','1651','0');
INSERT INTO [t17712] VALUES (1662,'١١ - هما خيباني كل يوم غنيمة ... وأهلكتهم لو أن ذلك نافع','608','1651','0');
INSERT INTO [t17712] VALUES (1663,'١٢ - لئن تك قد ضاقت عليكم بيوتكم ... ليعلم ربي أن بيتي واسع','608','1651','0');
INSERT INTO [t17712] VALUES (1664,'١٣ - حمال أثقال أهل الود آونة ... أعطيهم الجهد مني بله ما أسع','608','1651','0');
INSERT INTO [t17712] VALUES (1665,'١٤ - أمن ريحانة الداعي السميع ... يؤرقني وأصحابي هجوع','608','1651','0');
INSERT INTO [t17712] VALUES (1666,'١٥ - هجوت زبان ثم جئت معتذرا ... من هجو زبان، لم تهجو ولم تدع','609','1651','0');
INSERT INTO [t17712] VALUES (1667,'١٦ - عبأت له رمحا وألة ... كأن قبس بها حين تشرع','609','1651','0');
INSERT INTO [t17712] VALUES (1668,'١٧ - فلا تكثرا لومي فإن أخاكما ... بذكراه ليلى العامرية مولع','610','1651','0');
INSERT INTO [t17712] VALUES (1669,'١٨ - يا بن الكرام ألا تدنو فتبصر ما ... قد حدثوك فما راء كمن سمعا','610','1651','0');
INSERT INTO [t17712] VALUES (1670,'١٩ - خليلي ما واف بعهدي أنتما ... إذا لم تكونا لي على من أقاطع','610','1651','0');
INSERT INTO [t17712] VALUES (1671,'٢٠ - أبا خراشة أما أنت ذا نفر ... فإن قومي لم تأكلهم الضبع','610','1651','0');
INSERT INTO [t17712] VALUES (1672,'٢١ - سبقوا هوي وأعنقوا لهواهم ... فتخرموا ولكل جنب مصرع','611','1651','0');
INSERT INTO [t17712] VALUES (1673,'٢٢ - لا تجزعي إن منفسا أهلكته ... فإذا هلكت فعند ذلك فاجزعي','611','1651','0');
INSERT INTO [t17712] VALUES (1674,'٢٣ - قد أصبحت أم الخيار تدعي ... علي ذنبا كله لم أصنع من أن رأت رأسي كرأس الأصلع ... يا ابنة عما لا تلومي واهجعي','611','1651','0');
INSERT INTO [t17712] VALUES (1675,'٢٤ - أنا ابن التارك البكري بشر ... عليه الطير ترقبه وقوعا','612','1651','0');
INSERT INTO [t17712] VALUES (1676,'٢٥ - يا سيدا ما أنت من سيد ... موطأ الأكناف رحب الذراع','612','1651','0');
INSERT INTO [t17712] VALUES (1677,'٢٦ - على حين عاتبت المشيب على الصبا ... وقلت ألما أصح والشيب وازع','612','1651','0');
INSERT INTO [t17712] VALUES (1678,'٢٧ - تعز فلا إلفين بالعيش متعا ... ولكن لوراد المنون تتابع','613','1651','0');
INSERT INTO [t17712] VALUES (1679,'٢٨ - لا نسب اليوم ولا خلة ... اتسع الخرق على الراقع','613','1651','0');
INSERT INTO [t17712] VALUES (1680,'٢٩ - أطوف ما أطوف ثم آوي ... إلى بيت قعيدته لكاع','613','1651','0');
INSERT INTO [t17712] VALUES (1681,'٣٠ - كم في بني بكر بن سعد سيد ... ضخم الدسيعة ماجد نفاع','614','1651','0');
INSERT INTO [t17712] VALUES (1682,'٣١ - ليت شعري عن خليلي ما الذي ... غاله في الحب حتى ودعه لا يكن وعدك برقا خلبا ... إن خير البرق ما الغيث معه','614','1651','0');
INSERT INTO [t17712] VALUES (1683,'٣٢ - وقفنا فقلنا: إيه عن أم سالم ... وما بال تكليم الديار البلاقع','615','1651','0');
INSERT INTO [t17712] VALUES (1684,'٣٣ - أما ترى حيث سهيل طالعا ... نجما يضيء كالشهاب لامعا','615','1651','0');
INSERT INTO [t17712] VALUES (1685,'٣٤ - رب من أنضجت غيظا قلبه ... قد تمنى لي موتا لم يطع','616','1651','0');
INSERT INTO [t17712] VALUES (1686,'٣٥ - كيف يرجون سقاطي بعد ما ... لاح في الرأس بياض وصلع ورث البغضة عن آبائه ... حافظ العقل لما كان استمع فسعى مسعاتهم في قومه ... ثم لم يظفر ولا عجزا ودع','616','1651','0');
INSERT INTO [t17712] VALUES (1687,'٣٦ - فما كان حصن ولا حابس ... يفوقان مرداس في مجمع','617','1651','0');
INSERT INTO [t17712] VALUES (1688,'٣٧ - مناعها من إبل مناعها ... أما ترى الموت لدى أرباعها','617','1651','0');
INSERT INTO [t17712] VALUES (1689,'٣٨ - تمل الندامى ما عداني فإنني ... بكل الذي يهوى نديمي مولع','617','1651','0');
INSERT INTO [t17712] VALUES (1690,'٣٩ - ولو سئل الناس التراب لأوشكوا ... إذا قيل هاتوا أن يملوا فيمنعوا','618','1651','0');
INSERT INTO [t17712] VALUES (1691,'٤٠ - مدحت عروقا للندى مصت الثرى ... حديثا فلم تهمم بأن تترعرعا سقاها ذوو الأحلام سجلا على الظما ... وقد كربت أعناقها أن تقطعا','618','1651','0');
INSERT INTO [t17712] VALUES (1692,'٤١ - فقالت: أكل الناس أصبحت مانحا ... لسانك كيما أن تغر وتخدعا','618','1651','0');
INSERT INTO [t17712] VALUES (1693,'٤٢ - لقد عذلتني أم عمرو ولم أكن ... مقالتها ما كنت حيا لأسمعا','619','1651','0');
INSERT INTO [t17712] VALUES (1694,'٤٣ - حميد الذي أمج داره ... أخو الخمر ذو الشيبة الأصلع','619','1651','0');
INSERT INTO [t17712] VALUES (1695,'٤٤ - جازيتموني بالوصال قطيعة ... شتان بين صنيعكم وصنيعي','619','1651','0');
INSERT INTO [t17712] VALUES (1696,'٤٥ - أكفرا بعد رد الموت عني ... وبعد عطائك المائة الرتاعا','620','1651','0');
INSERT INTO [t17712] VALUES (1697,'٤٦ - بعكاظ يعشي الناظري ... ن إذا هم لمحوا شعاعه','620','1651','0');
INSERT INTO [t17712] VALUES (1698,'٤٧ - ذريني إن أمرك لن يطاعا ... وما ألفيتني حلمي مضاعا','620','1651','0');
INSERT INTO [t17712] VALUES (1699,'٤٨ - من لا يزال شاكرا على المعه ... فهو حر بعيشة ذات سعه','621','1651','0');
INSERT INTO [t17712] VALUES (1700,'٤٩ - فإنهم يرجون منه شفاعة ... إذا لم يكن إلا النبيون شافع','621','1651','0');
INSERT INTO [t17712] VALUES (1701,'٥٠ - إذا قيل أي الناس شر قبيلة ... أشارت كليب بالأكف الأصابع','621','1651','0');
INSERT INTO [t17712] VALUES (1702,'٥١ - لقد علمت أولى المغيرة أنني ... كررت فلم أنكل عن الضرب مسمعا','622','1651','0');
INSERT INTO [t17712] VALUES (1703,'٥٢ - يا ليتني كنت صبيا مرضعا ... تحملني الذلفاء حولا أكتعا إذا بكيت قبلتني أربعا ... إذن ظللت الدهر أبكي أجمعا','622','1651','0');
INSERT INTO [t17712] VALUES (1704,'٥٣ - إن علي الله أن تبايعا ... تؤخذ كرها أو تجيء طائعا','622','1651','0');
INSERT INTO [t17712] VALUES (1705,'٥٤ - لا تهين الفقير علك أن ... تركع يوما والدهر قد رفعه','623','1651','0');
INSERT INTO [t17712] VALUES (1706,'٥٥ - يا أقرع بن حابس يا أقرع ... إنك إن يصرع أخوك تصرع','623','1651','0');
INSERT INTO [t17712] VALUES (1707,'٥٦ - تعدون عقر النيب أفضل مجدكم ... بني ضوطرى لولا الكمي المقنعا','623','1651','0');
INSERT INTO [t17712] VALUES (1708,'٥٧ - هم صلبوا العبدي في رأس نخلة ... فلا عطست شيبان إلا بأجدعا','623','1651','0');
INSERT INTO [t17712] VALUES (1709,'٥٨ - فيا رب ليلى أنت في كل موطن ... وأنت الذي في رحمة الله أطمع','624','1651','0');
INSERT INTO [t17712] VALUES (1710,'٥٩ - إذا قلت قدني، قال بالله حلفة ... لتغني عني ذا إنائك أجمعا','624','1651','0');
INSERT INTO [t17712] VALUES (1711,'٦٠ - فلما تفرقنا كأني ومالكا ... لطول اجتماع لم نبت ليلة معا','625','1651','0');
INSERT INTO [t17712] VALUES (1712,'٦١ - لعلك يوما أن تلم ملمة ... عليك من اللائي يدعنك أجدعا','625','1651','0');
INSERT INTO [t17712] VALUES (1713,'٦٢ - يذكرن ذا البث الحزين ببثه ... إذا حنت الأولى سجعن لها معا','625','1651','0');
INSERT INTO [t17712] VALUES (1714,'٦٣ - وإنك مهما تعط بطنك سؤله ... وفرجك نالا منتهى الذم أجمعا','625','1651','0');
INSERT INTO [t17712] VALUES (1715,'٦٤ - فمن نحن نؤمنه يبت وهو آمن ... ومن لا نجره يمس منا مروعا','626','1651','0');
INSERT INTO [t17712] VALUES (1716,'٦٥ - فأدرك إبقاء العرادة ظلعها ... وقد جعلتني من حزيمة إصبعا','626','1651','0');
INSERT INTO [t17712] VALUES (1717,'٦٦ - عندي اصطبار وشكوى عند قاتلتي ... فهل بأعجب من هذا امرؤ سمعا','626','1651','0');
INSERT INTO [t17712] VALUES (1718,'٦٧ - قفي قبل التفرق يا ضباعا ... ولا يك موقف منك الوداعا','626','1651','0');
INSERT INTO [t17712] VALUES (1719,'٦٨ - فلما أن جرى سمن عليها ... كما طينت بالفدن السياعا','627','1651','0');
INSERT INTO [t17712] VALUES (1720,'٦٩ - واستقبلت قمر السماء بوجهها ... فأرتني القمرين في وقت معا','627','1651','0');
INSERT INTO [t17712] VALUES (1721,'٧٠ - أخذنا بآفاق السماء عليكم ... لنا قمراها والنجوم الطوالع','627','1651','0');
INSERT INTO [t17712] VALUES (1722,'٧١ - ما يرتجى وما يخاف جمعا ... فهو الذي كالليث والغيث معا','627','1651','0');
INSERT INTO [t17712] VALUES (1723,'٧٢ - يا ليت أيام الصبا رواجعا ..','628','1651','0');
INSERT INTO [t17712] VALUES (1724,'٧٣ - كنت ويحيى كيدي واحد ... نرمي جميعا ونرامى معا','628','1651','0');
INSERT INTO [t17712] VALUES (1725,'٧٤ - إذا باهلي تحته حنظلية ... له ولد منها فذاك المذرع','628','1651','0');
INSERT INTO [t17712] VALUES (1726,'٧٥ - فوا عجبا حتى كليب تسبني ... كأن أباها نهشل أو مجاشع','629','1651','0');
INSERT INTO [t17712] VALUES (1727,'٧٦ - ولست أبالي بعد فقدي مالكا ... أموتي ناء أم هو الآن واقع','629','1651','0');
INSERT INTO [t17712] VALUES (1728,'٧٧ - يقول الخنى وأبغض العجم ناطقا ... إلى ربنا صوت الحمار اليجدع','629','1651','0');
INSERT INTO [t17712] VALUES (1729,'٧٨ - على عن يميني مرت الطير سنحا ... وكيف سنوح واليمين قطيع','629','1651','0');
INSERT INTO [t17712] VALUES (1730,'٧٩ - إذا أنت لم تنفع فضر فإنما ... يرجى الفتى كيما يضر وينفع','630','1651','0');
INSERT INTO [t17712] VALUES (1731,'٨٠ - أردت لكيما أن تطير بقربتي ... فتتركها شنا ببيداء بلقع','630','1651','0');
INSERT INTO [t17712] VALUES (1732,'٨١ - لعمري وما عمري علي بهين ... لقد نطقت بطلا علي الأقارع','630','1651','0');
INSERT INTO [t17712] VALUES (1733,'٨٢ - أتاني أبيت اللعن أنك لمتني ... وتلك التي تستك منها المسامع مقالة أن قد قلت سوف أناله ... وذلك من تلقاء مثلك رائع','631','1651','0');
INSERT INTO [t17712] VALUES (1734,'٨٣ - فبت كأني ساورتني ضئيلة ... من الرقش في أنيابها السم ناقع','631','1651','0');
INSERT INTO [t17712] VALUES (1735,'٨٤ - مضى زمن والناس يستشفعون بي ... فهل لي إلى ليلى الغداة شفيع','631','1651','0');
INSERT INTO [t17712] VALUES (1736,'٨٥ - وإن يك جثماني بأرض سواكم ... فإن فؤادي عندك الدهر أجمع','631','1651','0');
INSERT INTO [t17712] VALUES (1737,'٨٦ - ونبئت ليلى أرسلت بشفاعة ... إلي فهلا نفس ليلى شفيعها','632','1651','0');
INSERT INTO [t17712] VALUES (1738,'٨٧ - أأكرم من ليلى علي فتبتغي ... به الجاه أم كنت امرأ لا أطيعها','632','1651','0');
INSERT INTO [t17712] VALUES (1739,'٨٨ - فلا تطمع أبيت اللعن فيها ... ومنعكها بشيء مستطاع','632','1651','0');
INSERT INTO [t17712] VALUES (1740,'٨٩ - زعم الفرزدق أن سيقتل مربعا ... أبشر بطول سلامة يا مربع','632','1651','0');
INSERT INTO [t17712] VALUES (1741,'٩٠ - والنفس راغبة إذا رغبتها ... وإذا ترد إلى قليل تقنع','633','1651','0');
INSERT INTO [t17712] VALUES (1742,'٩١ - فغبرت بعدهم بعيش ناصب ... وإخال إني لاحق مستتبع','633','1651','0');
INSERT INTO [t17712] VALUES (1743,'٩٢ - بينا تعانقه لكماة وروغه ... يوما أتيح له جريء سلفع','633','1651','0');
INSERT INTO [t17712] VALUES (1744,'٩٣ - ولقد تركت صبية مرحومة ... لم تدر ما جزع عليك فتجزع','633','1651','0');
INSERT INTO [t17712] VALUES (1745,'٩٤ - يا ليت شعري والمنى لا تنفع ... هل أغدون يوما وأمري مجمع','634','1651','0');
INSERT INTO [t17712] VALUES (1746,'٩٥ - إن كنت قاضي نحبي يوم بينكم ... لو لم تمنوا بوعد غير توديع','634','1651','0');
INSERT INTO [t17712] VALUES (1747,'٩٦ - فبينا نحن نرقبه أتانا ... معلق وفضة وزناد راع','634','1651','0');
INSERT INTO [t17712] VALUES (1748,'٩٧ - قوم إذا سمعوا الصريخ رأيتهم ... ما بين ملجم مهره أو سافع','635','1651','0');
INSERT INTO [t17712] VALUES (1749,'٩٨ - أتبيت ريان الجفون من الكرى ... وأبيت منك بليلة الملسوع','635','1651','0');
INSERT INTO [t17712] VALUES (1750,'٩٩ - قتلت بعبد الله خير لداته ... ذؤابا فلم أفخر بذاك وأجزعا','635','1651','0');
INSERT INTO [t17712] VALUES (1751,'١٠٠ - فلو أن حق اليوم منكم إقامة ... وإن كان سرح قد مضى فتسرعا','636','1651','0');
INSERT INTO [t17712] VALUES (1752,'١٠١ - تمد عليهم من يمين وأشمل ... بحور له من عهد عاد وتبعا','636','1651','0');
INSERT INTO [t17712] VALUES (1753,'١٠٢ - وكائن رددنا عنكم من مدجج ... يجيء أمام الألف يردي مقنعا','636','1651','0');
INSERT INTO [t17712] VALUES (1754,'١٠٣ - نبتم نبات الخيزراني في الثرى ... حديثا متى ما يأتك الخير ينفعا','636','1651','0');
INSERT INTO [t17712] VALUES (1755,'١٠٤ - فمهما تشأ منه فزارة تعطكم ... ومهما تشأ منه فزراة تمنعا','637','1651','0');
INSERT INTO [t17712] VALUES (1756,'١٠٥ - أمرتكم أمري بمنقطع اللوى ... ولا أمر للمعصي إلا مضيعا','637','1651','0');
INSERT INTO [t17712] VALUES (1757,'١٠٦ - فتى الناس لا يخفى عليهم مكانه ... وضرغامة إن هم بالحرب أوقعا','637','1651','0');
INSERT INTO [t17712] VALUES (1758,'١٠٧ - كم بجود مقرف نال العلى ... وكريم بخله قد وضعه','637','1651','0');
INSERT INTO [t17712] VALUES (1759,'١٠٨ - إذ ما تريني اليوم مزجى ظعينتي ... أصعد سيرا في البلاد وأفرع فإني من قوم سواكم وإنما ... رجالي فهم بالحجاز وأشجع','638','1651','0');
INSERT INTO [t17712] VALUES (1760,'١٠٩ - إذا مت كان الناس صنفان: شامت ... وآخر مثن بالذي كنت أصنع','638','1651','0');
INSERT INTO [t17712] VALUES (1761,'١١٠ - وما ذاك أن كان ابن عمي ولا أخي ... ولكن متى ما أملك الضر أنفع','638','1651','0');
INSERT INTO [t17712] VALUES (1762,'١١١ - وقد مات شماخ ومات مزرد ... وأي كريم لا أباك يمتع','638','1651','0');
INSERT INTO [t17712] VALUES (1763,'١١٢ - ونابغة الجعدي بالرمل بيته ... عليه تراب من صفيح موضع','639','1651','0');
INSERT INTO [t17712] VALUES (1764,'١١٣ - أمنزلتي مي سلام عليكما ... هل الأزمن اللائي مضين رواجع','639','1651','0');
INSERT INTO [t17712] VALUES (1765,'١١٤ - يا شاعرا لا شاعر اليوم مثله ... جرير ولكن في كليب تواضع','639','1651','0');
INSERT INTO [t17712] VALUES (1766,'١١٥ - ومنا الذي اختير الرجال سماحة ... وجودا إذا هب الرياح الزعازع','639','1651','0');
INSERT INTO [t17712] VALUES (1767,'١١٦ - وأنت امرؤ منا خلقت لغيرنا ... حياتك لا نفع وموتك فاجع','640','1651','0');
INSERT INTO [t17712] VALUES (1768,'١١٧ - بكت جزعا واسترجعت ثم آذنت ... ركائبها أن لا إلينا رجوعها','640','1651','0');
INSERT INTO [t17712] VALUES (1769,'١١٨ - ولقد علمت إذا الرجال تناهزوا ... أيي وأيكم أعز وأمنع','640','1651','0');
INSERT INTO [t17712] VALUES (1770,'١١٩ - إني رأيت من المكارم حسبكم ... أن تلبسوا حر الثياب وتشبعوا','640','1651','0');
INSERT INTO [t17712] VALUES (1771,'١٢٠ - تكنفني الوشاة فأزعجوني ... فيا للناس للواشي المطاع','641','1651','0');
INSERT INTO [t17712] VALUES (1772,'١٢١ - أتجزع أن نفس أتاها حمامها ... فهلا التي عن بين جنبيك تدفع','641','1651','0');
INSERT INTO [t17712] VALUES (1773,'١٢٢ - تذكرت ليلى فاعترتني صبابة ... وكاد ضمير القلب لا يتقطع','641','1651','0');
INSERT INTO [t17712] VALUES (1774,'١٢٣ - فأرحام شعر يتصلن ببابه ... وأرحام مال لا تني تتقطع','641','1651','0');
INSERT INTO [t17712] VALUES (1775,'١٢٤ - فبكى بناتي شجوهن وزوجتي ... والظاعنون إلي ثم تصدعوا','641','1651','0');
INSERT INTO [t17712] VALUES (1776,'١٢٥ - لئن تك قد ضاقت عليكم بيوتكم ... ليعلم ربي أن بيتي واسع','642','1651','0');
INSERT INTO [t17712] VALUES (1777,'١٢٦ - أقصر فلست بمقصر جزت المدى ... وبلغت حيث النجم تحتك فاربعا','642','1651','0');
INSERT INTO [t17712] VALUES (1778,'١٢٧ - نحن بنو أم البنين الأربعه ... ونحن خير عامر بن صعصعه','642','1651','0');
INSERT INTO [t17712] VALUES (1779,'١٢٨ - قد أصبحت أم الخيار تدعي ... علي ذنبا كله لم أصنع','642','1651','0');
INSERT INTO [t17712] VALUES (1780,'١٢٩ - فقلت لها والله يدري مسافر ... إذا غيبته الأرض ما الله صانع','642','1651','0');
INSERT INTO [t17712] VALUES (1781,'١٣٠ - رعاك ضمان الله يا أم مالك ... والله أن يشفيك أغنى وأوسع يذكرنيك الخير والشر والذي ... أخاف وأرجو والذي أتوقع','643','1651','0');
INSERT INTO [t17712] VALUES (1782,'١٣١ - فحملتها وحفرت عندك قبرها ... جزعا وكنت إخالني لا أجزع','643','1651','0');
INSERT INTO [t17712] VALUES (1783,'١٣٢ - ترى الثور فيها مدخل الظل رأسه ... وسائره باد إلى الشمس أكتع','643','1651','0');
INSERT INTO [t17712] VALUES (1784,'١٣٣ - كلفوني الذي أطيق فإنني ... لست رهنا بفوق ما أستطيع','644','1651','0');
INSERT INTO [t17712] VALUES (1785,'١٣٤ - تباركت إني من عذابك خائف ... وإني إليك نائب النفس باخع','644','1651','0');
INSERT INTO [t17712] VALUES (1786,'١٣٥ - وما الناس إلا كالديار وأهلها ... بها يوم حلوها وغدوا بلاقع','644','1651','0');
INSERT INTO [t17712] VALUES (1787,'١٣٦ - وعليهما مسرودتان قضاهما ... داود أو صنع السوابغ تبع','644','1651','0');
INSERT INTO [t17712] VALUES (1788,'١٣٧ - أتجزع إن نفس أتاها حمامها ... فهلا التي عن بين جنبيك دافع','645','1651','0');
INSERT INTO [t17712] VALUES (1789,'١٣٨ - أتجزع ... تدفع','645','1651','0');
INSERT INTO [t17712] VALUES (1790,'١٣٩ - فالعين بعدهم كأن حداقها ... سملت بشوك فهي عور تدمع','645','1651','0');
INSERT INTO [t17712] VALUES (1791,'١٤٠ - رأيتك يا ابن الحارثية كالتي ... صناعتها أبقت ولا الوهي ترقع','645','1651','0');
INSERT INTO [t17712] VALUES (1792,'١٤١ - فتخالسا نفسيهما بنوافذ ... كنوافذ العبط التي لا ترقع','646','1651','0');
INSERT INTO [t17712] VALUES (1793,'١٤٢ - أودى بني وأعقبوني حسرة ... عند الرقاد وعبرة لا تقلع','646','1651','0');
INSERT INTO [t17712] VALUES (1794,'١٤٣ - إني مقسم ما ملكت فجاعل ... جزءا لآخرتي ودنيا تنفع','646','1651','0');
INSERT INTO [t17712] VALUES (1795,'١٤٤ - طوى النحز والأجراز ما في غروضها ... ما بقيت إلا الضلوع الجراشع','646','1651','0');
INSERT INTO [t17712] VALUES (1796,'١٤٥ - طافت بأعلاقه خود يمانية ... تدعو العرانين من بكر وما جمعوا','647','1651','0');
INSERT INTO [t17712] VALUES (1797,'١٤٦ - لئن نزحت دار لليلى لربما ... غنينا بخير والديار جميع','647','1651','0');
INSERT INTO [t17712] VALUES (1798,'١٤٧ - لما أتى خبر الزبير تواضعت ... سور المدينة والجبال الخشع','647','1651','0');
INSERT INTO [t17712] VALUES (1799,'١٤٨ - توهمت آيات لها فعرفتها ... لستة أعوام وذا العام سابع','648','1651','0');
INSERT INTO [t17712] VALUES (1800,'١٤٩ - وما المرء إلا كالشهاب وضوئه ... يحور رمادا بعد إذ هو ساطع','648','1651','0');
INSERT INTO [t17712] VALUES (1801,'١٥٠ - منا الأناة وبعض القوم يحسبنا ... أنا بطاء وفي إبطائنا سرع','648','1651','0');
INSERT INTO [t17712] VALUES (1802,'١٥١ - فإنك والتأبين عروة بعد ما ... دعاك وأيدينا إليه شوارع','649','1651','0');
INSERT INTO [t17712] VALUES (1803,'١٥٢ - لا يبعد الله إخوانا تركتهم ... لم أدر بعد غداة الأمس ما صنع','649','1651','0');
INSERT INTO [t17712] VALUES (1804,'١٥٣ - يا ليت من يمنع المعروف يمنعه ... حتى يذوق رجال مر ما صنعوا وليت رزق رجال مثل نائلهم ... قوت كقوت ووسع كالذي وسعوا','649','1651','0');
INSERT INTO [t17712] VALUES (1805,'١٥٤ - كأن مجر الرامسات ذيولها ... عليه قضيم نمقته الصوانع','650','1651','0');
INSERT INTO [t17712] VALUES (1806,'١٥٥ - كأن مجر ... ... ... نمقته الأصابع','650','1651','0');
INSERT INTO [t17712] VALUES (1807,'١٥٦ - عليها من قوادم مضرحي ... فتي السن محتلك ضليع','650','1651','0');
INSERT INTO [t17712] VALUES (1808,'١٥٧ - ولم أر مثل الخير يتركه الفتى ... ولا الشر يأتيه امرؤ وهو طائع','650','1651','0');
INSERT INTO [t17712] VALUES (1809,'١٥٨ - خليل أملك مني للذي كسبت ... يدي ومالي فيما يقتني طمع','651','1651','0');
INSERT INTO [t17712] VALUES (1810,'١٥٩ - وأنت امرؤ منا خلقت لغيرنا ... حياتك لا نفع وموتك فاجع','651','1651','0');
INSERT INTO [t17712] VALUES (1811,'١٦٠ - بكل داهية ألقى العداء وقد ... يظن أني في مكري بهم فزع كلا ولكن ما أبديه من فرق ... فكي يغروا فيغريهم بي الطمع','651','1651','0');
INSERT INTO [t17712] VALUES (1812,'١٦١ - بينا كذلك والأعداد وجهتها ... إذ راعها لحفيف خلفها فزع','651','1651','0');
INSERT INTO [t17712] VALUES (1813,'١٦٢ - لو ساوفتنا بسوف من تحيتها ... سوف العيوف لراح الركب قد قنعوا','652','1651','0');
INSERT INTO [t17712] VALUES (1814,'١٦٣ - ليس ينفك ذا غنى واعتزاز ... كل ذي عفة مقل قنوع','652','1651','0');
INSERT INTO [t17712] VALUES (1815,'١٦٤ - أرى ابن نزار قد جفاني وملني ... على هنوات شأنها متتابع','652','1651','0');
INSERT INTO [t17712] VALUES (1816,'١٦٥ - راحت بمسلمة البغال عشية ... فارعي فزارة لا هناك المرتع','652','1651','0');
INSERT INTO [t17712] VALUES (1817,'١٦٦ - ألا يا لقومي كلما حم واقع ... وللطير مجرى والجنوب مصارع','653','1651','0');
INSERT INTO [t17712] VALUES (1818,'١٦٧ - وإذا الأمور تعاظمت وتشابهت ... فهناك يعترفون أين المفزع','653','1651','0');
INSERT INTO [t17712] VALUES (1819,'١٦٨ - أطوف ما أطوف ثم آوي ... إلى أما ويرويني النقيع','653','1651','0');
INSERT INTO [t17712] VALUES (1820,'١٦٩ - ودو ككف المشتري غير أنه ... بساط لأخفاف المراسيل واسع','653','1651','0');
INSERT INTO [t17712] VALUES (1821,'١٧٠ - وخيل قد دلفت لها بخيل ... تحية بينهم ضرب وجيع','654','1651','0');
INSERT INTO [t17712] VALUES (1822,'١٧١ - وما زلت محمولا علي ضغينة ... ومضطلع الأضغان مذ أنا يافع','654','1651','0');
INSERT INTO [t17712] VALUES (1823,'١٧٢ - فوردن والعيوق مقعد رابئ ال ... الضرباء خلف النجم لا يتتلع','654','1651','0');
INSERT INTO [t17712] VALUES (1824,'١٧٣ - فيستخرج اليربوع من نافقائه ... ومن جحره بالشيحة اليتقصع','657','1651','0');
INSERT INTO [t17712] VALUES (1825,'١٧٤ - فو الله ما أدري غريم لويته ... أيشتد إن قاضاك أم يتضرع','658','1651','0');
INSERT INTO [t17712] VALUES (1826,'١٧٥ - أمن المنون وريبها تتوجع ... والدهر ليس بمعتب من يجزع','658','1651','0');
INSERT INTO [t17712] VALUES (1827,'١٧٦ - ألم تر ما لاقيت والدهر أعصر ... ومن يتمل العيش يرأ ويسمع','658','1651','0');
INSERT INTO [t17712] VALUES (1828,'١٧٧ - ما لدى الحازم اللبيب معارا ... فمصون وماله قد يضيع','658','1651','0');
INSERT INTO [t17712] VALUES (1829,'١٧٨ - إذا حارب الحجاج أي منافق ... علاه بسيف كلما هز يقطع','658','1651','0');
INSERT INTO [t17712] VALUES (1830,'١٧٩ - حتى إذا قبضت أولى أظافره ... منها وأوشك ما لم يلقه يقع','659','1651','0');
INSERT INTO [t17712] VALUES (1831,'١٨٠ - قالت أميمة ما لجسمك شاحبا ... منذ ابتذلت ومثل مالك ينفع','659','1651','0');
INSERT INTO [t17712] VALUES (1832,'١٨١ - قصر الحديد إلى بلى ... والعيش في الدنيا انقطاعه','659','1651','0');
INSERT INTO [t17712] VALUES (1833,'١٨٢ - ظننتم بأن يخفى الذي قد صنعتم ... وفينا رسول عنده الوحي واضعه','659','1651','0');
INSERT INTO [t17712] VALUES (1834,'١٨٣ - ضننت بنفسي حقبة ثم أصبحت ... لبنت عطاء بينها وجميعها','659','1651','0');
INSERT INTO [t17712] VALUES (1835,'١٨٤ - تذكرت أياما مضين من الصبا ... فهيهات هيهاتا إليك رجوعها','660','1651','0');
INSERT INTO [t17712] VALUES (1836,'١٨٥ - وخير الأمر ما استقبلت منه ... وليس بأن تتبعه اتباعا','660','1651','0');
INSERT INTO [t17712] VALUES (1837,'١٨٦ - بني أسد هل تعلمون بلاءنا ... إذا كان يوما ذا كواكب أشنعا','660','1651','0');
INSERT INTO [t17712] VALUES (1838,'١٨٧ - كذبتم وبيت الله نرفع عقلها ... عن الحق حتى تضبعوا ثم نضبعا ... ... ولا صلح حتى تضبعونا ونضبعا ... ... ولا صلح حتى تضبعون ونضبعا','661','1651','0');
INSERT INTO [t17712] VALUES (1839,'١٨٨ - إذا كانت الحو الطوال كأنما ... كساها السلاح الأرجوان المضلعا تذود الملوك عنكم وتذودنا ... إلى الموت حتى يضبعوا ثم نضبعا','661','1651','0');
INSERT INTO [t17712] VALUES (1840,'١٨٩ - يبيتهم ذو اللب حتى يراهم ... بسيماهم بيضا لحاهم وأصلعا','661','1651','0');
INSERT INTO [t17712] VALUES (1841,'١٩٠ - لعمري وما دهري بتأبين هالك ... ولا جزع مما أصاب فأوجعا','661','1651','0');
INSERT INTO [t17712] VALUES (1842,'١٩١ - فتى الناس لا يخفى عليهم مكانه ... وضرغامة إن هم بالحرب أوقعا','662','1651','0');
INSERT INTO [t17712] VALUES (1843,'١٩٢ - غدت من عليه تنفض الطل بعد ما ... رأت حاجب الشمس استوى فترفعا','662','1651','0');
INSERT INTO [t17712] VALUES (1844,'١٩٣ - لا تتبعن لوعة إثري ولا هلعا ... ولا تقاسن بعدي الهم والجزعا','662','1651','0');
INSERT INTO [t17712] VALUES (1845,'١٩٤ - ولها بالماطرون إذا ... أكل النمل الذي جمعا','662','1651','0');
INSERT INTO [t17712] VALUES (1846,'١٩٥ - بحي نميري عليه مهابة ... جميع إذا كان اللئام جنادعا','663','1651','0');
INSERT INTO [t17712] VALUES (1847,'١٩٦ - كأن نسوع رحلي حين ضمت ... حوالب غرزا ومعى جياعا','664','1651','0');
INSERT INTO [t17712] VALUES (1848,'١٩٧ - وكنا كالحريق أصاب غابا ... فيخبو ساعة ويهب ساعا','664','1651','0');
INSERT INTO [t17712] VALUES (1849,'١٩٨ - فكرت تبتغيه فوافقته ... على دمه ومصرعه السباعا','664','1651','0');
INSERT INTO [t17712] VALUES (1850,'١٩٩ - قد جربوه فما زادت تجاربهم ... أبا قدامة إلا المجد والفنعا','664','1651','0');
INSERT INTO [t17712] VALUES (1851,'٢٠٠ - وقد أظلكم من شطر ثغركم ... هول له ظلم يغشاكم قطعا','665','1651','0');
INSERT INTO [t17712] VALUES (1852,'٢٠١ - وقالوا لها لا تنكحيه فإنه ... لأول نصل أن يلاقي مجمعا','665','1651','0');
INSERT INTO [t17712] VALUES (1853,'٢٠٢ - فبتنا تحيد الوحش عنا كأننا ... قتيلان لم يعلم لنا الناس مصرعا','665','1651','0');
INSERT INTO [t17712] VALUES (1854,'٢٠٣ - وما وجد أظآر ثلاث روائم ... أصبن مجرا من حوار ومصرعا بأوجد مني يوم قام بمالك ... مناد بصير بالفراق فأسمعا','665','1651','0');
INSERT INTO [t17712] VALUES (1855,'٢٠٤ - إن وجدت الصديق حقا لإياك ... فمرني فلن أزال مطيعا','666','1651','0');
INSERT INTO [t17712] VALUES (1856,'٢٠٥ - حننت إلى ريا ونفسك باعدت ... مزارك من ريا وشعباكما معا','666','1651','0');
INSERT INTO [t17712] VALUES (1857,'٢٠٦ - أكف يدي عن أن ينال التماسها ... أكف صحابي حين حاجاتنا معا','669','1651','0');
INSERT INTO [t17712] VALUES (1858,'٢٠٧ - إذا شئت أن تلهو ببعض حديثها ... رفعن وأنزلن الحديث المقطعا','669','1651','0');
INSERT INTO [t17712] VALUES (1859,'٢٠٨ - فإن يك غثا أو سمينا فإنني ... سأجعل عينيه لنفسه مقنعا','669','1651','0');
INSERT INTO [t17712] VALUES (1860,'٢٠٩ - وزادني كلفا بالحب ما منعت ... وحب شيء إلى الإنسان ما منعا','669','1651','0');
INSERT INTO [t17712] VALUES (1861,'٢١٠ - إذا أنت لم تنفع فضر فإنما ... يرجى الفتى كيما يضر وينفعا','670','1651','0');
INSERT INTO [t17712] VALUES (1862,'٢١١ - ثلاث مئين قد مررن كواملا ... وها أنا هذا أشتهي مر أربع','670','1651','0');
INSERT INTO [t17712] VALUES (1863,'٢١٢ - حميد الذي أمج داره ... أخو الخمر ذو الشيبة الأصلع','670','1651','0');
INSERT INTO [t17712] VALUES (1864,'٢١٣ - وقد كنت في الحرب ذا تدراء ... فلم أعط شيئا ولم أمنع','670','1651','0');
INSERT INTO [t17712] VALUES (1865,'٢١٤ - وما انتميت إلى خور ولا كشف ... ولا لئام غداة الروع أوزاع','670','1651','0');
INSERT INTO [t17712] VALUES (1866,'٢١٥ - ومعرص تغلي المراجل تحته ... عجلت طبيخته لقوم جيع','671','1651','0');
INSERT INTO [t17712] VALUES (1867,'٢١٦ - على جرداء يقطع أبهراها ... حزام السرج في خيل سراع','671','1651','0');
INSERT INTO [t17712] VALUES (1868,'٢١٧ - كرام حين تنكفت الأفاعي ... إلى أجحارهن من الصقيع','671','1651','0');
INSERT INTO [t17712] VALUES (1869,'٢١٨ - وكوني بالمكارم ذكريني ... ودلي دل ماجدة صناع','671','1651','0');
INSERT INTO [t17712] VALUES (1870,'٢١٩ - سقى الأرضين الغيث سهل وحزنها ... فنيطت عرى الآمال بالزرع والضرع','672','1651','0');
INSERT INTO [t17712] VALUES (1871,'٢٢٠ - بالله ربك إلا قلت صادقة ... هل في لقائك للمشغوف من طمع','672','1651','0');
INSERT INTO [t17712] VALUES (1872,'٢٢١ - ليس ينفك ذا غنى واعتزاز ... كل ذي عفة مقل قنوع','672','1651','0');
INSERT INTO [t17712] VALUES (1873,'٢٢٢ - لقد آليت أغدر في جداع ... ولو منيت أمات الرباع لأن الغدر في الأقوام عار ... وإن الحر يجزأ بالكراع','673','1651','0');
INSERT INTO [t17712] VALUES (1874,'٢٢٣ - ألكني إلى سلمى بآية أومأت ... بكف خضيب تحت كفة مدرع','673','1651','0');
INSERT INTO [t17712] VALUES (1875,'٢٢٤ - فصبرا في مجال الموت صبرا ... فما نيل الخلود بمستطاع','674','1651','0');
INSERT INTO [t17712] VALUES (1876,'٢٢٥ - دهم الشتاء ولست أملك عدة ... والصبر في الشتوات غير مطيعي','674','1651','0');
INSERT INTO [t17712] VALUES (1877,'٢٢٦ - بكاللقوة الشغواء جلت فلم أكن ... لأولع إلا بالكمي المقنع','674','1651','0');
INSERT INTO [t17712] VALUES (1878,'٢٢٧ - أتبيت ريان الجفون من الكرى ... وأبيت منك بليلة الملسوع','674','1651','0');
INSERT INTO [t17712] VALUES (1879,'٢٢٨ - وكنت إذا منيت بخصم سوء ... دلفت له فأكويه وقاع','675','1651','0');
INSERT INTO [t17712] VALUES (1880,'٢٢٩ - قوال معروف وفعاله ... عقار مثنى أمهات الرباع','675','1651','0');
INSERT INTO [t17712] VALUES (1881,'٢٣٠ - ويحييني إذا لاقيته ... وإذا يخلو له لحمي رتع','675','1651','0');
INSERT INTO [t17712] VALUES (1882,'٢٣١ - ارحم أصيبيتي الذين كأنهم ... حجلى تدرج في الشربة وقع','676','1651','0');
INSERT INTO [t17712] VALUES (1883,'حرف الغين المعجمة','677','0','0');
INSERT INTO [t17712] VALUES (1884,'١ - أخاك الذي إن تدعه لملمة ... يجبك كما تبغي ويكفك من يبغي وإن تجفه يوما فليس مكافئا ... فيطمع ذو التزوير والوشي أن يصغي','677','1883','0');
INSERT INTO [t17712] VALUES (1885,'٢ - ولكن ببدر سائلوا عن بلائنا ... على الناد والأنباء بالغيب تبلغ','677','1883','0');
INSERT INTO [t17712] VALUES (1886,'حرف الفاء','678','0','0');
INSERT INTO [t17712] VALUES (1887,'١ - فما بالنا أمس أسد العرين ... وما بالنا اليوم شاء النجف','678','1886','0');
INSERT INTO [t17712] VALUES (1888,'٢ - وعض زمان يا ابن مروان لم يدع ... من المال إلا مسحتا أو مجلف','679','1886','0');
INSERT INTO [t17712] VALUES (1889,'٣ - أمن رسم دار مربع ومصيف ... لعينيك من ماء الشؤون وكيف','679','1886','0');
INSERT INTO [t17712] VALUES (1890,'٤ - كفى بالنأي من أسماء كافي ... وليس لنأيها إذ طال شافي','679','1886','0');
INSERT INTO [t17712] VALUES (1891,'٥ - إذا نهي السفيه جرى إليه ... وخالف، والسفيه إلى خلاف','680','1886','0');
INSERT INTO [t17712] VALUES (1892,'٦ - فكلتاهما خرت وأسجد رأسها ... كما سجدت نصرانة لم تحنف','680','1886','0');
INSERT INTO [t17712] VALUES (1893,'٧ - تعلق في مثل السواري سيوفنا ... وما بينها والكعب غوط نفانف','680','1886','0');
INSERT INTO [t17712] VALUES (1894,'٨ - ومن قبل نادى كل مولى قرابة ... فما عطفت مولى عليه العواطف','681','1886','0');
INSERT INTO [t17712] VALUES (1895,'٩ - ولبس عباءة وتقر عيني ... أحب إلي من لبس الشفوف','681','1886','0');
INSERT INTO [t17712] VALUES (1896,'١٠ - بني غدانة ما إن أنتم ذهب ... ولا صريف ولكن أنتم الخزف','682','1886','0');
INSERT INTO [t17712] VALUES (1897,'١١ - تنفي يداها الحصى في كل هاجرة ... نفي الدراهيم تنقاد الصياريف','682','1886','0');
INSERT INTO [t17712] VALUES (1898,'١٢ - وقالوا: تعرفها المنازل من منى ... وما كل من وافى منى أنا عارف','682','1886','0');
INSERT INTO [t17712] VALUES (1899,'١٣ - نحن بما عندنا وأنت بما ... عندك راض والرأي مختلف','682','1886','0');
INSERT INTO [t17712] VALUES (1900,'١٤ - من نثقفن منهم فليس بآيب ... أبدا وقتل بني قتيبة شافي','683','1886','0');
INSERT INTO [t17712] VALUES (1901,'١٥ - أقبلت من عند زياد كالخرف ... تخط رجلاي بخط مختلف تكتبان في الطريق لام الف','683','1886','0');
INSERT INTO [t17712] VALUES (1902,'١٦ - كأن أذنيه إذا تشوفا ... قادمة أو قلما محرفا','683','1886','0');
