INSERT INTO [t17712] VALUES (1924,'٣٨ - وإلى ابن أم أناس أرحل ناقتي ... عمرو فتبلغ حاجتي أو تزحف ملك إذا نزل الوفود ببابه ... عرفوا موارد مزبد لا ينزف','690','1886','0');
