INSERT INTO [t17712] VALUES (2022,'٧١ - قد احتملت مي فهاتيك دارها ... بها السحم تردي والحمام المطوق','722','1951','0');
INSERT INTO [t17712] VALUES (2023,'٧٢ - واعوج عودك من لحو ومن قدم ... لا ينعم الغصن حتى ينعم الورق','723','1951','0');
INSERT INTO [t17712] VALUES (2024,'٧٣ - أدارا بحزوى هجت للعين عبرة ... فماء الهوى يرفض أو يترقرق','723','1951','0');
INSERT INTO [t17712] VALUES (2025,'٧٤ - أرى الربع لا أهلين في عرصاته ... ومن قبل عن أهليه كان يضيق','723','1951','0');
INSERT INTO [t17712] VALUES (2026,'٧٥ - سودت فلم أملك سوادي وتحته ... قميص من القوهي بيض بنائقه','723','1951','0');
INSERT INTO [t17712] VALUES (2027,'٧٦ - وما ضر أثوابي سوادي وتحتها ... لباس من العلياء بيض بنائقه','724','1951','0');
INSERT INTO [t17712] VALUES (2028,'٧٧ - عرضنا فسلمنا فسلم كارها ... علينا وتبريح من الغيظ خانقه','724','1951','0');
INSERT INTO [t17712] VALUES (2029,'٧٨ - حلفت بهدي مشعر بكراته ... يخب بصحراء الغبيط درادقه لئن لم تغير بعض ما قد صنعتم ... لأنتحين العظم ذو أنا عارقه','724','1951','0');
INSERT INTO [t17712] VALUES (2030,'٧٩ - ولم يرتفق والناس محتضرونه ... جميعا وأيدي المعتفين رواهقه','725','1951','0');
INSERT INTO [t17712] VALUES (2031,'٨٠ - يا عجبا للدهر شتى طرائقه ... وللمرء يبلوه بما شاء خالقه','726','1951','0');
INSERT INTO [t17712] VALUES (2032,'٨١ - من لم يمت عبطة يمت هرما ... للموت كأس والمرء ذائقها','726','1951','0');
INSERT INTO [t17712] VALUES (2033,'٨٢ - أإن شمت من نجد بريقا تألقا ... تبيت بليل أم أرمد اعتاد أولقا','726','1951','0');
INSERT INTO [t17712] VALUES (2034,'٨٣ - حذار فقد نبئت إنك للذي ... ستجزى بما تسعى فتسعد أو تشقى','727','1951','0');
INSERT INTO [t17712] VALUES (2035,'٨٤ - فلئن قوم أصابوا غرة ... وأصبنا من زمان رنقا للقد كانوا لدى أزماننا ... لصنيعين لبأس وتقى','727','1951','0');
INSERT INTO [t17712] VALUES (2036,'٨٥ - زحرت بها ليلة كلها ... فجئت بها مؤيدا خنفقيقا','727','1951','0');
INSERT INTO [t17712] VALUES (2037,'٨٦ - حسبتك في الوغى مردى حروب ... إذا خور لديك فقلت سحقا','728','1951','0');
INSERT INTO [t17712] VALUES (2038,'٨٧ - لديك كفيل بالمنى لمؤمل ... وإن سواك من يؤمله يشقى','728','1951','0');
INSERT INTO [t17712] VALUES (2039,'٨٨ - فإنني والذي يحج له الناس ... بجدوى سواك لم أثق','728','1951','0');
INSERT INTO [t17712] VALUES (2040,'٨٩ - يا قر إن أباك حي خويلد ... قد كنت خائفه على الإحماق','728','1951','0');
INSERT INTO [t17712] VALUES (2041,'٩٠ - وكأن حيا قبلكم لم يشربوا ... فيها بأقلبة أجن زعاق','729','1951','0');
INSERT INTO [t17712] VALUES (2042,'٩١ - فمتى واغل يزرهم يحيو ... هـ وتعطف عليه كأس الساقي','730','1951','0');
INSERT INTO [t17712] VALUES (2043,'٩٢ - أيا من رأى لي راي برق شريق ... أسال البحار فانتحى للعقيق','730','1951','0');
INSERT INTO [t17712] VALUES (2044,'٩٣ - ولما رزقت ليأتينك سيبه ... جلبا وليس إليك ما لم ترزق','730','1951','0');
INSERT INTO [t17712] VALUES (2045,'٩٤ - فقلت له صوب ولا تجهدنه ... فيذرك من أعلى القطاة فتزلق','731','1951','0');
INSERT INTO [t17712] VALUES (2046,'٩٥ - فقلت له صوب ولا تجهدنه ... فيدنك من أخرى القطاة فتزلق','731','1951','0');
INSERT INTO [t17712] VALUES (2047,'٩٦ - تزوجتها رامية هرمزية ... بفضل الذي أعطى الأمير من الرزق','731','1951','0');
INSERT INTO [t17712] VALUES (2048,'٩٧ - تعطي الضجيع إذا تنبه موهنا ... كالأقحوان من الرشاش المستقي','731','1951','0');
INSERT INTO [t17712] VALUES (2049,'٩٨ - إذا ما استحمت أرضه من سمائه ... جرى وهو مودوع وواعد مصدق','732','1951','0');
INSERT INTO [t17712] VALUES (2050,'٩٩ - وقد تخذت رجلي لدى جنب غرزها ... نسيفا كأفحوص القطاة المطرق','732','1951','0');
INSERT INTO [t17712] VALUES (2051,'١٠٠ - حبذا أنتما خليلي إن لم ... تعذلاني في دمعي المهراق','733','1951','0');
INSERT INTO [t17712] VALUES (2052,'١٠١ - حمى لا يحل الدهر إلا بإذننا ... ولا تسأل الأقوام عقد المياثق','733','1951','0');
INSERT INTO [t17712] VALUES (2053,'١٠٢ - يا أرط إنك فاعل ما قلته ... والمرء يستحيي إذا لم يصدق','733','1951','0');
INSERT INTO [t17712] VALUES (2054,'١٠٣ - أسعد بن مال ألم تعلموا ... وذو الرأي مهما يقل يصدق','733','1951','0');
INSERT INTO [t17712] VALUES (2055,'١٠٤ - يا خال هلا قلت إذ أعطيتني ... هياك هياك وحنواء العنق أعطيتنيها فانيا أضراسها ... لو تعلف البيض به لم ينفلق','733','1951','0');
INSERT INTO [t17712] VALUES (2056,'١٠٥ - ومنهل ليس له حوازق ... ولضفادي جمه نقانق','734','1951','0');
INSERT INTO [t17712] VALUES (2057,'١٠٦ - ودابق وأين مني دابق','734','1951','0');
INSERT INTO [t17712] VALUES (2058,'١٠٧ - يا عمرويه انطلق الرفاق ... مالك لا تبكي ولا تشتاق','734','1951','0');
INSERT INTO [t17712] VALUES (2059,'١٠٨ - أعز ذات المئزر المنشق ... أخذت خاتامي بغير حق','734','1951','0');
INSERT INTO [t17712] VALUES (2060,'١٠٩ - قد أقبلت عزة من عراقها ... ملصقة السرج بخاق باقها','734','1951','0');
INSERT INTO [t17712] VALUES (2061,'١١٠ - ورحنا بكابن الماء يجنب وسطنا ... تصوب فيه العين طورا وترتقي','734','1951','0');
INSERT INTO [t17712] VALUES (2062,'قافية الكاف','735','0','0');
INSERT INTO [t17712] VALUES (2063,'١ - يا عاذلي دعني من عذلكا ... مثلي لا يقبل من مثلكا','735','2062','0');
INSERT INTO [t17712] VALUES (2064,'٢ - تراكها من إبل تراكها ... أما ترى الموت لدى أوراكها','735','2062','0');
INSERT INTO [t17712] VALUES (2065,'٣ - لن تنفعي ذا حاجة وينفعك ... وتجعلين اللذ معي في اللذ معك','736','2062','0');
INSERT INTO [t17712] VALUES (2066,'٤ - أتتك عنس تقطع الأراكا ... إليك حتى بلغت إياكا','736','2062','0');
INSERT INTO [t17712] VALUES (2067,'٥ - فإن تك خيلي قد أصيب عميدها ... فعمدا على عيني تيممت مالكا أقول له والرمح يأطر متنه ... تأمل خفافا إنني أنا ذلكا','736','2062','0');
INSERT INTO [t17712] VALUES (2068,'٦ - تعلمن ها- لعمر الله- ذا قسما ... فاقدر بذرعك وانظر أين تنسلك','737','2062','0');
INSERT INTO [t17712] VALUES (2069,'٧ - أفي السلم أعيارا جفاء وغلظة ... وفي الحرب أشباه النساء العوارك','737','2062','0');
INSERT INTO [t17712] VALUES (2070,'٨ - سلم على المولى البهاء وصف له ... شوقي إليه وأنني مملوكه أبدا يحركني إليه تشوقي ... جسمي به مشطوره منهوكه لكن نحلت لبعده فكأنني ... ألف وليس بممكن تحريكه','737','2062','0');
INSERT INTO [t17712] VALUES (2071,'٩ - هي الدنيا تقول بملء فيها ... حذار حذار من بطشي وفتكي فلا يغرركم مني ابتسام ... فقولي مضحك والفعل مبكي','738','2062','0');
INSERT INTO [t17712] VALUES (2072,'١٠ - فقلت أجرني أبا خالد ... وإلا فهبني امرأ هالكا','738','2062','0');
INSERT INTO [t17712] VALUES (2073,'١١ - يا أيها المائح دلوي دونكا ... إني رأيت الناس يحمدونكا','738','2062','0');
INSERT INTO [t17712] VALUES (2074,'١٢ - حيكت على نيرين إذ تحاك ... تختبط الشوك ولا تشاك','739','2062','0');
INSERT INTO [t17712] VALUES (2075,'١٣ - خلا الله لا أرجو سواك وإنما ... أعد عيالي شعبة من عيالكا','739','2062','0');
INSERT INTO [t17712] VALUES (2076,'١٤ - فلما خشيت أظافيرهم ... نجوت وأرهنهم مالكا','739','2062','0');
INSERT INTO [t17712] VALUES (2077,'١٥ - يا حكم الوارث عن عبد الملك ... ميراث أحساب وجود منسفك','740','2062','0');
INSERT INTO [t17712] VALUES (2078,'١٦ - تقول بنتي قد أنى إناكا ... يا أبتا علك أو عساكا','740','2062','0');
INSERT INTO [t17712] VALUES (2079,'١٧ - تعيرنا أننا عالة ... ونحن صعاليك أنتم ملوكا','741','2062','0');
INSERT INTO [t17712] VALUES (2080,'١٨ - يا نفس صبرا لعل الخير عقباك ... خانتك من بعد طول الأمن دنياك مرت بنا سحرا طير فقلت لها ... طوباك يا ليتني إياك طوباك إن كان قصدك شوقا بالسلام على ... شاطي الفرات ابلغي إن كان مثواك من موثق بالمنى ما لا فكاك له ... يبكي الدماء على إلف له باكي أظ','741','2062','0');
INSERT INTO [t17712] VALUES (2081,'١٩ - قالت له وهو بعيش ضنك ... لا تكثري لومي وخلي عنك','741','2062','0');
INSERT INTO [t17712] VALUES (2082,'٢٠ - يا خاتم النباء إنك مرسل ... بالحق كل هدى السبيل هداكا','742','2062','0');
INSERT INTO [t17712] VALUES (2083,'٢١ - وأحضرت عذري عليه الشهو ... د إن عاذرا لي وإن تاركا','742','2062','0');
INSERT INTO [t17712] VALUES (2084,'٢٢ - أهوى لها أسفع الخدين مطرق ... ريش القوادم لم تنصب له الشبك','742','2062','0');
INSERT INTO [t17712] VALUES (2085,'٢٣ - رأيت سعودا من شعوب كثيرة ... فلم أر سعدا مثل سعد بن مالك','742','2062','0');
INSERT INTO [t17712] VALUES (2086,'٢٤ - وقلت اجعلي ضوء الفراقد كلها ... يمينا ومهوى النجم من عن شمالك','743','2062','0');
INSERT INTO [t17712] VALUES (2087,'٢٥ - وقد كان منهم حاجب وابن عمه ... أبو جندل والزيد زيد المعارك','743','2062','0');
INSERT INTO [t17712] VALUES (2088,'٢٦ - ثم استمروا وقالوا إن موعدكم ... ماء بشرقي سلمى فيد أوركك','743','2062','0');
INSERT INTO [t17712] VALUES (2089,'٢٧ - أخ مخلص واف صبور محافظ ... على الود والعهد الذي كان مالك','743','2062','0');
INSERT INTO [t17712] VALUES (2090,'٢٨ - يا حار لا أرمين منكم بداهية ... لم يلقها سوقة قبلي ولا ملك','743','2062','0');
INSERT INTO [t17712] VALUES (2091,'٢٩ - إذا الأمهات قبحن الوجوه ... فرجت الظلام بأماتكا','744','2062','0');
INSERT INTO [t17712] VALUES (2092,'٣٠ - أولئك قومي لم يكونوا أشابة ... وهل يعظ الضليل إلا ألالكا','744','2062','0');
INSERT INTO [t17712] VALUES (2093,'٣١ - تجانف عن جو اليمامة ناقتي ... وما عدلت عن أهلها لسوائكا','744','2062','0');
INSERT INTO [t17712] VALUES (2094,'٣٢ - تجلد لا يقل هؤلاء هذا ... بكى لما بكى أسفا عليكا','745','2062','0');
INSERT INTO [t17712] VALUES (2095,'٣٣ - مورثة مالا و- في المجد- رفعة ... لما ضاع فيها من قروء نسائكا','745','2062','0');
INSERT INTO [t17712] VALUES (2096,'٣٤ - وما كان على الجيء ... ولا الهيء امتدا حيكا ولكني على الحب ... وطيب النفس آتيكا','746','2062','0');
INSERT INTO [t17712] VALUES (2097,'٣٥ - يا دار بين النقا والحزن ما صنعت ... يد النوى بالأولى كانوا أهاليك','746','2062','0');
INSERT INTO [t17712] VALUES (2098,'٣٦ - إني لمهد من ثنائي فقاصد ... به لابن عم الصدق شمس بن مالك','746','2062','0');
INSERT INTO [t17712] VALUES (2099,'٣٧ - بئس قرينا يفن هالك ... أم عبيد وأبو مالك','746','2062','0');
INSERT INTO [t17712] VALUES (2100,'٣٨ - فأيقنت أني ثائر ابن مكدم ... غدا تئذ أو هالك في الهوالك','747','2062','0');
INSERT INTO [t17712] VALUES (2101,'٣٩ - وانصر على آل الصليب ... وعابديه اليوم آلك','747','2062','0');
INSERT INTO [t17712] VALUES (2102,'٤٠ - بئس هذا الحي حيا ناصرا ... ليت أحياءهم فيمن هلك','747','2062','0');
INSERT INTO [t17712] VALUES (2103,'٤١ - وإنما الهالك ثم التالك ... ذو حيرة ضاقت به المسالك كيف يكون النوك إلا ذلك','747','2062','0');
INSERT INTO [t17712] VALUES (2104,'٤٢ - أهدموا بيتك لا أبالكا ... وحسبوا أنك لا أخالكا وأنا أمشي الدألى حوالكا','748','2062','0');
INSERT INTO [t17712] VALUES (2105,'٤٣ - أبيت أسري وتبيتي تدلكي ... جلدك بالعنبر والمسك الذكي','748','2062','0');
INSERT INTO [t17712] VALUES (2106,'٤٤ - ليث وليث في محل ضنك ... كلاهما ذو أشر ومحك','748','2062','0');
INSERT INTO [t17712] VALUES (2107,'٤٥ - كأن بين فكها والفك ... فارة مسك ذبحت في سك','750','2062','0');
INSERT INTO [t17712] VALUES (2108,'٤٦ - يا عز كفرانك لا سبحانك ... إني رأيت الله قد أهانك','751','2062','0');
INSERT INTO [t17712] VALUES (2109,'قافية اللام','752','0','0');
INSERT INTO [t17712] VALUES (2110,'١ - لعمرك ما أدري وإني لأوجل ... على أينا تعدو المنية أول','752','2109','0');
INSERT INTO [t17712] VALUES (2111,'٢ - أقول وقد ناحت بقربي حمامة ... أيا جارتا لو تشعرين بحالي معاذ الهوى ما ذقت طارقة النوى ... ولا خطرت منك الهموم ببالي أيا جارتا ما أنصف الدهر بيننا ... تعالي أقاسمك الهموم تعالي','752','2109','0');
INSERT INTO [t17712] VALUES (2112,'٣ - رأيت الوليد بن اليزيد مباركا ... شديدا بأعباء الخلافة كاهله','752','2109','0');
INSERT INTO [t17712] VALUES (2113,'٤ - قفا نبك من ذكرى حبيب ومنزل ... بسقط اللوى بين الدخول فحومل','753','2109','0');
INSERT INTO [t17712] VALUES (2114,'٥ - أغرك مني أن حبك قاتلي ... وأنك مهما تأمري القلب يفعل','753','2109','0');
INSERT INTO [t17712] VALUES (2115,'٦ - إذا النعجة العجفاء كانت بقفرة ... فأيان ما تعدل بها الريح تنزل','753','2109','0');
INSERT INTO [t17712] VALUES (2116,'٧ - وقصيدة تأتي الملوك غريبة ... قد قلتها ليقال: من ذا قالها','753','2109','0');
INSERT INTO [t17712] VALUES (2117,'٨ - سلي إن جهلت الناس عنا وعنهم ... فليس سواء عالم وجهول','754','2109','0');
INSERT INTO [t17712] VALUES (2118,'٩ - لا يأمن الدهر ذو بغي ولو ملكا ... جنوده ضاق عنها السهل والجبل','754','2109','0');
INSERT INTO [t17712] VALUES (2119,'١٠ - علموا أن يؤملون فجادوا ... قبل أن يسألوا بأعظم سؤل','754','2109','0');
INSERT INTO [t17712] VALUES (2120,'١١ - لقد علم الضيف والمرملون ... إذا اغبر أفق وهبت شمالا بأنك ربيع وغيث مريع ... وأنك هناك تكون الثمالا','755','2109','0');
INSERT INTO [t17712] VALUES (2121,'١٢ - لا سابغات ولا جأواء باسلة ... تقي المنون لدى استيفاء آجال','755','2109','0');
INSERT INTO [t17712] VALUES (2122,'١٣ - وإن مدت الأيدي إلى الزاد لم أكن ... بأعجلهم إذ أجشع القوم أعجل','755','2109','0');
INSERT INTO [t17712] VALUES (2123,'١٤ - جفوني ولم أجف الأخلاء إنني ... لغير جميل من خليلي مهمل','755','2109','0');
INSERT INTO [t17712] VALUES (2124,'١٥ - ولو أن ما أسعى لأدنى معيشة ... كفاني- ولم أطلب- قليل من المال','756','2109','0');
INSERT INTO [t17712] VALUES (2125,'١٦ - ألا يا عباد الله قلبي متيم ... بأحسن من صلى وأقبحهم بعلا','756','2109','0');
INSERT INTO [t17712] VALUES (2126,'١٧ - فجئت وقد نضت لنوم ثيابها ... لدى الستر إلا لبسة المتفضل','756','2109','0');
INSERT INTO [t17712] VALUES (2127,'١٨ - فكونوا أنتم وبني أبيكم ... مكان الكليتين من الطحال','757','2109','0');
INSERT INTO [t17712] VALUES (2128,'١٩ - لمية موحشا طلل ... يلوح كأنه خلل','757','2109','0');
INSERT INTO [t17712] VALUES (2129,'٢٠ - ألا كل شيء- ما خلا الله- باطل ... وكل نعيم لا محالة زائل','757','2109','0');
INSERT INTO [t17712] VALUES (2130,'٢١ - فهيهات هيهات العقيق ومن به ... وهيهات خل بالعقيق نواصله','758','2109','0');
INSERT INTO [t17712] VALUES (2131,'٢٢ - إن وجدي بك الشديد أراني ... عاذرا فيك من عهدت عذولا','758','2109','0');
INSERT INTO [t17712] VALUES (2132,'٢٣ - القاتلين الملك الحلاحلا ... خير معد حسبا ونائلا','758','2109','0');
INSERT INTO [t17712] VALUES (2133,'٢٤ - أخا الحرب لباسا إليها جلالها ... وليس بولاج الخوالف أعقلا','759','2109','0');
INSERT INTO [t17712] VALUES (2134,'٢٥ - ما أنت بالحكم الترضى حكومته ... ولا الأصيل ولا ذي الرأي والجدل','759','2109','0');
INSERT INTO [t17712] VALUES (2135,'٢٦ - إذا قلت هاتي نوليني تمايلت ... علي هضيم الكشح ريا المخلخل','760','2109','0');
INSERT INTO [t17712] VALUES (2136,'٢٧ - لا يعجبنك من خطيب خطبة ... حتى يكون مع الكلام أصيلا إن الكلام لفي الفؤاد وإنما ... جعل اللسان على الفؤاد دليلا','760','2109','0');
INSERT INTO [t17712] VALUES (2137,'٢٨ - يذيب الرعب منه كل عضب ... فلولا الغمد يمسكه لسالا','760','2109','0');
INSERT INTO [t17712] VALUES (2138,'٢٩ - ومن لا يصرف الواشين عنه ... صباح مساء يبغوه خبالا','761','2109','0');
INSERT INTO [t17712] VALUES (2139,'٣٠ - يساقط عنه روقه ضارياتها ... سقاط شرار القين أخول أخولا','761','2109','0');
INSERT INTO [t17712] VALUES (2140,'٣١ - ولقد سددت عليك كل ثنية ... وأتيت فوق بني كليب من عل','761','2109','0');
INSERT INTO [t17712] VALUES (2141,'٣٢ - مكر مفر مقبل مدبر معا ... كجلمود صخر حطه السيل من عل','762','2109','0');
INSERT INTO [t17712] VALUES (2142,'٣٣ - لا تضيقن بالأمور فقد تكشف ... غماؤها بغير احتيال ربما تكره النفوس من الأمر ... له فرجة كحل العقال','762','2109','0');
INSERT INTO [t17712] VALUES (2143,'٣٤ - نحن بني ضبة أصحاب الجمل ... ننعى ابن عفان بأطراف الأسل','762','2109','0');
INSERT INTO [t17712] VALUES (2144,'٣٥ - فأخذت أسأل والرسوم تجيبني ... وفي الاعتبار إجابة وسؤال','763','2109','0');
INSERT INTO [t17712] VALUES (2145,'٣٦ - لئن عاد لي عبد العزيز بمثلها ... وأمكنني منها إذن لا أقيلها','763','2109','0');
INSERT INTO [t17712] VALUES (2146,'٣٧ - وليل كموج البحر أرخى سدوله ... علي بأنواع الهموم ليبتلي','763','2109','0');
INSERT INTO [t17712] VALUES (2147,'٣٨ - فمثلك حبلى قد طرقت ومرضع ... فألهيتها عن ذي تمائم محول','763','2109','0');
INSERT INTO [t17712] VALUES (2148,'٣٩ - خليلي أنى تأتياني تأتيا ... أخا غير ما يرضيكما لا يحاول','764','2109','0');
INSERT INTO [t17712] VALUES (2149,'٤٠ - أستغفر الله ذنبا لست محصيه ... رب العباد إليه الوجه والعمل','764','2109','0');
INSERT INTO [t17712] VALUES (2150,'٤١ - وقالوا: نأت فاختر من الصبر والبكى ... فقلت: البكى أشفى إذن لغليلي','765','2109','0');
INSERT INTO [t17712] VALUES (2151,'٤٢ - ضعيف النكاية أعداءه ... يخال الفرار يراخي الأجل','765','2109','0');
INSERT INTO [t17712] VALUES (2152,'٤٣ - كناطح صخرة يوما ليوهنها ... فلم يضرها وأوهى قرنه الوعل','765','2109','0');
INSERT INTO [t17712] VALUES (2153,'٤٤ - ومية أحسن الثقلين جيدا ... وسالفة وأحسنهم قذالا','765','2109','0');
INSERT INTO [t17712] VALUES (2154,'٤٥ - بكم قريش كفينا كل معضلة ... وأم نهج الهدى من كان ضليلا','766','2109','0');
INSERT INTO [t17712] VALUES (2155,'٤٦ - كأن خصييه من التدلدل ... ظرف عجوز فيه ثنتا حنظل','766','2109','0');
INSERT INTO [t17712] VALUES (2156,'٤٧ - تنورتها من أذرعات وأهلها ... بيثرب أدنى دارها نظر عالي','766','2109','0');
INSERT INTO [t17712] VALUES (2157,'٤٨ - كمنية جابر إذ قال ليتي ... أصادفه وأفقد جل مالي','767','2109','0');
INSERT INTO [t17712] VALUES (2158,'٤٩ - وتلك خطوب قد تملت شبابنا ... قديما فتبلينا المنون وما نبلي وتبلي الألى يستلئمون على الألى ... تراهن يوم الروع كالحدإ القبل','767','2109','0');
INSERT INTO [t17712] VALUES (2159,'٥٠ - إذا ما لقيت بني مالك ... فسلم على أيهم أفضل','768','2109','0');
INSERT INTO [t17712] VALUES (2160,'٥١ - فخير نحن عند البأس منكم ... إذا الداعي المثوب قال: يالا','768','2109','0');
INSERT INTO [t17712] VALUES (2161,'٥٢ - فيا رب هل إلا بك النصر يرتجى ... عليهم؟ وهل إلا عليك المعول','768','2109','0');
INSERT INTO [t17712] VALUES (2162,'٥٣ - خالي لأنت ومن تميم خاله ... ينل العلاء ويكرم الأخوالا','769','2109','0');
INSERT INTO [t17712] VALUES (2163,'٥٤ - أنت تكون ماجد نبيل ... إذا تهب شمأل بليل','769','2109','0');
INSERT INTO [t17712] VALUES (2164,'٥٥ - قد قيل ما قيل إن صدقا وإن كذبا ... فما اعتذارك من قول إذا قيلا','769','2109','0');
INSERT INTO [t17712] VALUES (2165,'٥٦ - إن المرء ميتا بانقضاء حياته ... ولكن بأن يبغى عليه فيخذلا','770','2109','0');
INSERT INTO [t17712] VALUES (2166,'٥٧ - فلا تلحني فيها فإن بحبها ... أخاك مصاب القلب جم بلابله','770','2109','0');
INSERT INTO [t17712] VALUES (2167,'٥٨ - ألا اصطبار لليلى أم لها جلد ... إذا ألاقي الذي لاقاه أمثالي','770','2109','0');
INSERT INTO [t17712] VALUES (2168,'٥٩ - علمتك الباذل المعروف فانبعثت ... إليك بي واجفات الشوق والأمل','770','2109','0');
INSERT INTO [t17712] VALUES (2169,'٦٠ - دعاني الغواني عمهن وخلتني ... لي اسم، فلا أدعى به وهو أول','771','2109','0');
INSERT INTO [t17712] VALUES (2170,'٦١ - حسبت التقى والجود خير تجارة ... رباحا إذا ما المرء أصبح ثاقلا','771','2109','0');
INSERT INTO [t17712] VALUES (2171,'٦٢ - فإن تزعميني كنت أجهل فيكم ... فإني شريت الحلم بعدك بالجهل','771','2109','0');
INSERT INTO [t17712] VALUES (2172,'٦٣ - أرجو وآمل أن تدنو مودتها ... وما إخال لدينا منك تنويل','772','2109','0');
INSERT INTO [t17712] VALUES (2173,'٦٤ - يلومونني في اشتراء النخي ... ل أهلي فكلهم يعذل وأهل الذي باع يلحونه ... كما لحي البائع الأول','772','2109','0');
INSERT INTO [t17712] VALUES (2174,'٦٥ - فلا مزنة ودقت ودقها ... ولا أرض أبقل إبقالها','773','2109','0');
INSERT INTO [t17712] VALUES (2175,'٦٦ - مالك من شيخك إلا عمله ... إلا رسيمه وإلا رمله','774','2109','0');
INSERT INTO [t17712] VALUES (2176,'٦٧ - رأيت الناس ما حاشا قريشا ... فإنا نحن أفضلهم فعالا','774','2109','0');
INSERT INTO [t17712] VALUES (2177,'٦٨ - فأرسلها العراك ولم يذدها ... ولم يشفق على نغص الدخال','774','2109','0');
INSERT INTO [t17712] VALUES (2178,'٦٩ - يا صاح هل حم عيش باقيا فترى ... لنفسك العذر في إيعادها الأملا','775','2109','0');
INSERT INTO [t17712] VALUES (2179,'٧٠ - فإن تك أذواد أصبن ونسوة ... فلن يذهبوا فرغا بقتل حبال','775','2109','0');
INSERT INTO [t17712] VALUES (2180,'٧١ - ضيعت حزمي في إبعادي الأملا ... وما ارعويت وشيبا رأسي اشتعلا','775','2109','0');
INSERT INTO [t17712] VALUES (2181,'٧٢ - ولا ترى بعلا ولا حلائلا ... كه ولا كهن إلا حاظلا','776','2109','0');
INSERT INTO [t17712] VALUES (2182,'٧٣ - أتنتهون ولن ينهى ذوي شطط ... كالطعن يذهب فيه الزيت والفتل','776','2109','0');
INSERT INTO [t17712] VALUES (2183,'٧٤ - غدت من عليه بعد ما تم ظمؤها ... تصل وعن قيض بزيزاء مجهل','776','2109','0');
INSERT INTO [t17712] VALUES (2184,'٧٥ - رسم دار وقفت في طلله ... كدت أقضي الحياة من جلله','777','2109','0');
INSERT INTO [t17712] VALUES (2185,'٧٦ - إن للخير وللشر مدى ... وكلا ذلك وجه وقبل','777','2109','0');
INSERT INTO [t17712] VALUES (2186,'٧٧ - أقب من تحت عريض من علي','777','2109','0');
INSERT INTO [t17712] VALUES (2187,'٧٨ - كما خط الكتاب بكف يوما ... يهودي يقارب أو يزيل','778','2109','0');
INSERT INTO [t17712] VALUES (2188,'٧٩ - بضرب بالسيوف رؤوس قوم ... أزلنا هامهن عن المقيل','778','2109','0');
INSERT INTO [t17712] VALUES (2189,'٨٠ - الواهب المائة الهجان وعبدها ... عوذا تزجي بينها أطفالها','779','2109','0');
INSERT INTO [t17712] VALUES (2190,'٨١ - فقلت: اقتلوها عنكم بمزاجها ... وحب بها مقتولة حين تقتل','779','2109','0');
INSERT INTO [t17712] VALUES (2191,'٨٢ - دنوت وقد خلناك كالبدر أجملا ... فظل فؤادي في هواك مضللا','779','2109','0');
INSERT INTO [t17712] VALUES (2192,'٨٣ - ان الذي سمك السماء بنى لنا ... بيتا دعائمه أعز وأطول','779','2109','0');
INSERT INTO [t17712] VALUES (2193,'٨٤ - ولا عيب فيها غير أن سريعها ... قطوف وأن لا شيء منهن أكسل','780','2109','0');
INSERT INTO [t17712] VALUES (2194,'٨٥ - قلت إذ أقبلت وزهر تهادى ... كنعاج الفلا تعسفن رملا','780','2109','0');
INSERT INTO [t17712] VALUES (2195,'٨٦ - ذا ارعواء فليس بعد اشتعال الر ... أس شيبا إلى الصبا من سبيل','780','2109','0');
INSERT INTO [t17712] VALUES (2196,'٨٧ - يا زيد زيد اليعملات الذبل ... تطاول الليل عليك فانزل','781','2109','0');
INSERT INTO [t17712] VALUES (2197,'٨٨ - تدافع الشيب ولم تقتل ... في لجة أمسك فلانا عن فل','781','2109','0');
INSERT INTO [t17712] VALUES (2198,'٨٩ - وضجيع قد تعللت به ... طيب أردانه غير تفل صعدة نابتة في حائر ... أينما الريح تميلها تمل','782','2109','0');
INSERT INTO [t17712] VALUES (2199,'٩٠ - لئن منيت بنا عن غب معركة ... لا تلفنا عن دماء القوم تنتفل','782','2109','0');
INSERT INTO [t17712] VALUES (2200,'٩١ - وكل أناس سوف تدخل بينهم ... دويهية تصفر منها الأنامل','782','2109','0');
INSERT INTO [t17712] VALUES (2201,'٩٢ - ألا تسألان المرء ماذا يحاول ... أنحب فيقضى أم ضلال وباطل','783','2109','0');
INSERT INTO [t17712] VALUES (2202,'٩٣ - إذا لم تجد من دون عدنان والدا ... ودون معد فلتزعك العواذل','783','2109','0');
INSERT INTO [t17712] VALUES (2203,'٩٤ - رأيت ذوي الحاجات حول بيوتهم ... قطينا لهم حتى إذا أنبت البقل','783','2109','0');
INSERT INTO [t17712] VALUES (2204,'٩٥ - كفى ثعلا فخرا بأنك منهم ... ودهر لأن أمسيت من أهله أهل','784','2109','0');
INSERT INTO [t17712] VALUES (2205,'٩٦ - فما زالت القتلى تمج دماءها ... بدجلة حتى ماء دجلة أشكل','784','2109','0');
INSERT INTO [t17712] VALUES (2206,'٩٧ - لنا الفضل في الدنيا وأنفك راغم ... ونحن لكم يوم القيامة أفضل','784','2109','0');
INSERT INTO [t17712] VALUES (2207,'٩٨ - يميد إذا مادت عليه دلاؤهم ... فيصدر عنها كلها وهو ناهل','785','2109','0');
INSERT INTO [t17712] VALUES (2208,'٩٩ - إذا المرء لم يدنس من اللؤم عرضه ... فكل رداء يرتديه جميل','785','2109','0');
INSERT INTO [t17712] VALUES (2209,'١٠٠ - فلا الجارة الدنيا لها تلحينها ... ولا الضيف منها إن أناخ محول','785','2109','0');
INSERT INTO [t17712] VALUES (2210,'١٠١ - وقولي إذا ما أطلقوا عن بعيرهم ... تلاقونه حتى يؤوب المنخل','786','2109','0');
INSERT INTO [t17712] VALUES (2211,'١٠٢ - ولكن من لا يلق أمرا ينوبه ... بعدته ينزل به وهو أعزل','786','2109','0');
INSERT INTO [t17712] VALUES (2212,'١٠٣ - فتلك ولاة السوء قد طال مكثها ... فحتام حتام العناء المطول','786','2109','0');
INSERT INTO [t17712] VALUES (2213,'١٠٤ - وقد أدركتني والحوادث جمة ... أسنة قوم لا ضعاف ولا عزل','786','2109','0');
INSERT INTO [t17712] VALUES (2214,'١٠٥ - ألم تعلمي يا عمرك الله أنني ... كريم على حين الكرام قليل وأني لا أخزى إذا قيل مملق ... سخي وأخزى أن يقال بخيل','787','2109','0');
INSERT INTO [t17712] VALUES (2215,'١٠٦ - وقلن ألا البردي أول مشرب ... أجل جير إن كانت رواء أسافله','787','2109','0');
INSERT INTO [t17712] VALUES (2216,'١٠٧ - إذا ريدة من حيث ما نفحت له ... أتاه برياها خليل يواصله','787','2109','0');
INSERT INTO [t17712] VALUES (2217,'١٠٨ - وابأبي ثغرك ذاك المعسول ... كأن في أنيابه القرنفول','788','2109','0');
INSERT INTO [t17712] VALUES (2218,'١٠٩ - أقول إذ خرت على الكلكال ... يا ناقتا ما جلت من مجال','788','2109','0');
INSERT INTO [t17712] VALUES (2219,'١١٠ - كأني بفتخاء الجناحين لقوة ... على عجل مني أطاطئ شيمالي','788','2109','0');
INSERT INTO [t17712] VALUES (2220,'١١١ - لما نزلنا نصبنا ظل أخبية ... وفار للقوم باللحم المراجيل','789','2109','0');
INSERT INTO [t17712] VALUES (2221,'١١٢ - وما الدنيا بباقية بحزن ... أجل، لا، لا، ولا برخاء بال','789','2109','0');
INSERT INTO [t17712] VALUES (2222,'١١٣ - لا هم إن الحارث بن جبله ... زنى على أبيه ثم قتله وكان في جاراته لا عهد له ... وأي أمر سيئ لا فعله','789','2109','0');
INSERT INTO [t17712] VALUES (2223,'١١٤ - فرد على الفؤاد هوى عميدا ... وسوئل لو يبين لنا السؤالا وقد نغنى بها ونرى عصورا ... بها يقتدننا الخرد الخدالا','790','2109','0');
INSERT INTO [t17712] VALUES (2224,'١١٥ - ثمت قمنا إلى جرد مسومة ... أعرافهن لأيدينا مناديل','791','2109','0');
INSERT INTO [t17712] VALUES (2225,'١١٦ - ما أقدر الله أن يدني على شحط ... من داره الحزن ممن داره صول الله يطوي بساط الأرض بينهما ... حتى يرى الربع فيه وهو مأهول','791','2109','0');
INSERT INTO [t17712] VALUES (2226,'١١٧ - ألا فتى من بني ذبيان يحملني ... وليس حاملني إلا ابن حمال','792','2109','0');
INSERT INTO [t17712] VALUES (2227,'١١٨ - ولقد أغتدي وما صقع الديك ... على أدهم أجش الصهيلا','793','2109','0');
INSERT INTO [t17712] VALUES (2228,'١١٩ - ولما دعاني السمهري أجبته ... بأبيض من ماء الحديد صقيل','793','2109','0');
INSERT INTO [t17712] VALUES (2229,'١٢٠ - فليت دفعت الهم عني ساعة ... فبتنا على ما خيلت ناعمي بال','793','2109','0');
INSERT INTO [t17712] VALUES (2230,'١٢١ - لهنك من عبسية لوسيمة ... على هنوات كاذب من يقولها','794','2109','0');
INSERT INTO [t17712] VALUES (2231,'١٢٢ - دعيني أطوف في البلاد لعلني ... أفيد غنى فيه لذي الحق محمل','794','2109','0');
INSERT INTO [t17712] VALUES (2232,'١٢٣ - وإن كان ما بلغت عني فلامني ... صديقي وشلت من يدي الأنامل وكفنت وحدي منذرا في ردائه ... وصادف حوطا من أعادي قاتل','794','2109','0');
INSERT INTO [t17712] VALUES (2233,'١٢٤ - أزهير إن يشب القذال فإنه ... رب هيضل لجب لففت بهيضل','795','2109','0');
INSERT INTO [t17712] VALUES (2234,'١٢٥ - رددنا لشعثاء الرسول ولا أرى ... كيومئذ شيئا ترد رسائله','795','2109','0');
INSERT INTO [t17712] VALUES (2235,'١٢٦ - لقد خفت حتى لا تزيد مخافتي ... على وعل في ذي المطارة عاقل','796','2109','0');
INSERT INTO [t17712] VALUES (2236,'١٢٧ - أليس قليلا نظرة إن نظرتها ... إليك؟ وكلا ليس منك قليل','796','2109','0');
INSERT INTO [t17712] VALUES (2237,'١٢٨ - فلما أجزنا ساحة الحي وانتحى ... بنا بطن حقف ذي قفاف عقنقل','796','2109','0');
INSERT INTO [t17712] VALUES (2238,'١٢٩ - ورجا الأخيطل من سفاهة رأيه ... ما لم يكن وأب له لينالا','797','2109','0');
INSERT INTO [t17712] VALUES (2239,'١٣٠ - نصروا نبيهم وشدوا أزره ... بحنين يوم تواكل الأبطال','797','2109','0');
INSERT INTO [t17712] VALUES (2240,'١٣١ - قالت أميمة ما لثابت شاخصا ... عاري الأشاجع ناحلا كالمنصل','798','2109','0');
INSERT INTO [t17712] VALUES (2241,'١٣٢ - لي والد شيخ تهضه غيبتي ... وأظن أن نفاد عمره عاجل','798','2109','0');
INSERT INTO [t17712] VALUES (2242,'١٣٣ - لتبعد إذ نأى جدواك عني ... فلا أشقى عليك ولا أبالي','798','2109','0');
INSERT INTO [t17712] VALUES (2243,'١٣٤ - فدعوا نزال فكنت أول نازل ... وعلام أركبه إذا لم أنزل','799','2109','0');
INSERT INTO [t17712] VALUES (2244,'١٣٥ - نعاء أبا ليلى لكل طمرة ... وجرداء مثل القوس سمح حجولها','799','2109','0');
INSERT INTO [t17712] VALUES (2245,'١٣٦ - نعاء ابن ليلى للسماحة والندى ... وأيدي شمال باردات الأنامل','799','2109','0');
INSERT INTO [t17712] VALUES (2246,'١٣٧ - نعاء جذاما غير موت ولا قتل ... ولكن فراقا للدعائم والأصل','800','2109','0');
INSERT INTO [t17712] VALUES (2247,'١٣٨ - اسمع حديثا كما يوما تحدثه ... عن ظهر غيب إذا ما سائل سألا','800','2109','0');
INSERT INTO [t17712] VALUES (2248,'١٣٩ - يقلب عينيه كما لأخافه ... تشاوس رويدا إنني من تأمل','800','2109','0');
INSERT INTO [t17712] VALUES (2249,'١٤٠ - ركاب حسيل أشهر الصيف بدن ... وناقة عمرو ما يحل لها رحل ويزعم حسل أنه فرع قومه ... وما أنت فرع يا حسيل ولا أصل','802','2109','0');
INSERT INTO [t17712] VALUES (2250,'١٤١ - لعمري لأنت البيت أكرم أهله ... وأقعد في أفيائه بالأصائل','802','2109','0');
INSERT INTO [t17712] VALUES (2251,'١٤٢ - أرتني حجلا على ساقها ... فهش الفؤاد لذاك الحجل فقلت ولم أخف عن صاحبي ... ألا بأبي أصل تلك الرجل','803','2109','0');
INSERT INTO [t17712] VALUES (2252,'١٤٣ - علمنا إخواننا بنو عجل ... شرب النبيذ واصطفافا بالرجل','803','2109','0');
INSERT INTO [t17712] VALUES (2253,'١٤٤ - لم نرحب بأن شخصت ولكن ... مرحبا بالرضاء منك وأهلا','803','2109','0');
INSERT INTO [t17712] VALUES (2254,'١٤٥ - حصان رزان ما تزن بريبة ... وتصبح غرثى من لحوم الغوافل','803','2109','0');
INSERT INTO [t17712] VALUES (2255,'١٤٦ - إن الأمور إذا الأحداث دبرها ... دون الشيوخ ترى في بعضها خللا','804','2109','0');
INSERT INTO [t17712] VALUES (2256,'١٤٧ - ويلمه رجلا تأبى به غبنا ... إذا تجرد لا خال ولا بخل','804','2109','0');
INSERT INTO [t17712] VALUES (2257,'١٤٨ - ويلمه مسعر حرب إذا ... ألقي فيها وعليه الشليل','805','2109','0');
INSERT INTO [t17712] VALUES (2258,'١٤٩ - إذا اجتمعوا على ألف وواو ... وياء هاج بينهم جدال','805','2109','0');
INSERT INTO [t17712] VALUES (2259,'١٥٠ - فبيناه يشري رحله قال قائل ... لمن جمل رخو الملاط ذلول','806','2109','0');
INSERT INTO [t17712] VALUES (2260,'١٥١ - قلما عرس حتى هجته ... بالتباشير من الصبح الأول','806','2109','0');
INSERT INTO [t17712] VALUES (2261,'١٥٢ - تزال حبال مبرمات أعدها ... لها ما مشى يوما على خفه جمل','806','2109','0');
INSERT INTO [t17712] VALUES (2262,'١٥٣ - ومتى أهلك فلا أحفله ... بجلي الآن من العيش بجل','807','2109','0');
INSERT INTO [t17712] VALUES (2263,'١٥٤ - يتمارى في الذي قلت له ... ولقد يسمع قولي حيهل','807','2109','0');
INSERT INTO [t17712] VALUES (2264,'١٥٥ - أتعرف أم لا رسم دار معطلا ... من العام يغشاه ومن عام أولا قطار وتارات خريق كأنها ... مضلة بو في رعيل تعجلا','807','2109','0');
INSERT INTO [t17712] VALUES (2265,'١٥٦ - ألا حييا ليلى وقولا لها هلا ... فقد ركبت أمرا أغر محجلا','808','2109','0');
INSERT INTO [t17712] VALUES (2266,'١٥٧ - سمعت الناس ينتجعون غيثا ... فقلت لصيدح انتجعي بلالا','809','2109','0');
INSERT INTO [t17712] VALUES (2267,'١٥٨ - أبو موسى فحسبك نعم جدا ... وشيخ الحي خالك نعم خالا','809','2109','0');
INSERT INTO [t17712] VALUES (2268,'١٥٩ - بدت قمرا ومالت خوط بان ... وفاحت عنبرا ورنت غزالا','809','2109','0');
INSERT INTO [t17712] VALUES (2269,'١٦٠ - وكل أبي باسل غير أنني ... إذا عرضت أولى الطرائد أبسل','809','2109','0');
INSERT INTO [t17712] VALUES (2270,'١٦١ - لعاب الأفاعي القاتلات لعابه ... وأري الجنى اشتارته أيد عواسل','810','2109','0');
INSERT INTO [t17712] VALUES (2271,'١٦٢ - أكرم بها خلة لو أنها صدقت ... موعودها أو لو أن النصح مقبول','810','2109','0');
INSERT INTO [t17712] VALUES (2272,'١٦٣ - أأبكاك بالعرف المنزل ... وما أنت والطلل المحول وما أنت ويك ورسم الديار ... وستوك قد كربت تكمل','810','2109','0');
INSERT INTO [t17712] VALUES (2273,'١٦٤ - كلانا إذا نال شيئا أفاته ... ومن يحترث حرثي وحرثك يهزل','811','2109','0');
INSERT INTO [t17712] VALUES (2274,'١٦٥ - وقد أغتدي والطير في وكناتها ... بمنجرد قيد الأوابد هيكل','811','2109','0');
INSERT INTO [t17712] VALUES (2275,'١٦٦ - كدأبك من أم الحويرث قبلها ... وجارتها أم الرباب بمأسل','812','2109','0');
INSERT INTO [t17712] VALUES (2276,'١٦٧ - فألحقه بالهاديات ودونه ... جواحرها في صرة لم تزيل','812','2109','0');
INSERT INTO [t17712] VALUES (2277,'١٦٨ - كأن ثبيرا في عرانين وبله ... كبير أناس في بجاد مزمل','812','2109','0');
INSERT INTO [t17712] VALUES (2278,'١٦٩ - فو الله لولا أن أجيء بسبة ... تجر على أشياخنا في القبائل','812','2109','0');
INSERT INTO [t17712] VALUES (2279,'١٧٠ - وأهلة ود قد تبريت ودهم ... وأبليتهم في الحمد جهدي ونائلي','813','2109','0');
INSERT INTO [t17712] VALUES (2280,'١٧١ - فلما تنازعنا الحديث وأسمحت ... هصرت بغصن ذي شماريخ ميال فصرنا إلى الحسنى ورق كلامنا ... ورضت (فذلت) صعبة أي إذلال','814','2109','0');
INSERT INTO [t17712] VALUES (2281,'١٧٢ - لله در أنو شروان من رجل ... ما كان أعرفه بالدون والسفل','814','2109','0');
INSERT INTO [t17712] VALUES (2282,'١٧٣ - يسقون من ورد البريص عليهم ... بردى يصفق بالرحيق السلسل','815','2109','0');
INSERT INTO [t17712] VALUES (2283,'١٧٤ - ما بكاء الكبير بالأطلال ... وسؤالي وما يرد سؤالي','815','2109','0');
INSERT INTO [t17712] VALUES (2284,'١٧٥ - وأوقدت ناري كي ليبصر ضوؤها ... وأخرجت كلبي وهو في البيت داخله','819','2109','0');
INSERT INTO [t17712] VALUES (2285,'١٧٦ - أبى جوده «لا» البخل واستعجلت به ... نعم من فتى لا يمنع الجود قاتله','820','2109','0');
INSERT INTO [t17712] VALUES (2286,'١٧٧ - وقائلة تخشى علي أظنه ... سيودي به ترحاله وجعائله','820','2109','0');
INSERT INTO [t17712] VALUES (2287,'١٧٨ - ويوما شهدناه سليما وعامرا ... قليلا سوى الطعن النهال نوافله','821','2109','0');
INSERT INTO [t17712] VALUES (2288,'١٧٩ - وأبيض فياض يداه غمامة ... على معتفيه ما تغب فواضله بكرت عليه بكرة فوجدته ... قعودا لديه بالصريم عواذله','821','2109','0');
INSERT INTO [t17712] VALUES (2289,'١٨٠ - تلم بدار قد تقادم عهدها ... وإما بأموات ألم خيالها','822','2109','0');
INSERT INTO [t17712] VALUES (2290,'١٨١ - كل ابن أنثى وإن طالت سلامته ... يوما على آلة حدباء محمول','822','2109','0');
INSERT INTO [t17712] VALUES (2291,'١٨٢ - لقد أقوم مقاما لو يقوم به ... أرى وأسمع ما لو يسمع الفيل لظل يرعد إلا أن يكون له ... من الرسول بإذن الله تنويل','822','2109','0');
INSERT INTO [t17712] VALUES (2292,'١٨٣ - تجلو عوارض ذي ظلم إذا ابتسمت ... كأنه منهل بالراح معلول شجت بذي شبم من ماء محنية ... صاف بأبطح أضحى وهو مشمول','823','2109','0');
INSERT INTO [t17712] VALUES (2293,'١٨٤ - وما سعاد غداة البين إذ رحلوا ... إلا أغن غضيض الطرف مكحول','824','2109','0');
INSERT INTO [t17712] VALUES (2294,'١٨٥ - كأن أوب ذراعيها إذا عرقت ... وقد تلفع بالقور العساقيل شد النهار ذراعا عيطل نصف ... قامت فجاوبها نكد مثاكيل','825','2109','0');
INSERT INTO [t17712] VALUES (2295,'١٨٦ - وربما فات قوما جل أمرهم ... من التأني وكان الحزم لو عجلوا','826','2109','0');
INSERT INTO [t17712] VALUES (2296,'١٨٧ - هي الشفاء لدائي لو ظفرت بها ... وليس منها شفاء النفس مبذول','826','2109','0');
INSERT INTO [t17712] VALUES (2297,'١٨٨ - أبلغ قريشا وخير القول أصدقه ... والصدق عند ذوي الألباب مقبول أن قد قتلنا بقتلانا سراتكم ... أهل اللواء ففيما يكثر القيل','826','2109','0');
INSERT INTO [t17712] VALUES (2298,'١٨٩ - إما ترينا حفاة لا نعال لنا ... إنا كذلك ما نحفى وننتعل','827','2109','0');
INSERT INTO [t17712] VALUES (2299,'١٩٠ - إن تركبوا فركوب الخيل عادتنا ... أو تنزلون فإنا معشر نزل','827','2109','0');
INSERT INTO [t17712] VALUES (2300,'١٩١ - فاذهب فأي فتى في الناس أحرزه ... من حتفه ظلم دعج ولا جبل','827','2109','0');
INSERT INTO [t17712] VALUES (2301,'١٩٢ - اعتاد قلبك من سلمى عوائده ... وهاج أحزانك المكنونة الطلل ربع قواء أذاع المعصرات به ... وكل حيران سار ماؤه خضل','828','2109','0');
INSERT INTO [t17712] VALUES (2302,'١٩٣ - قليل منك يكفيني ولكن ... قليلك لا يقال له قليل','828','2109','0');
INSERT INTO [t17712] VALUES (2303,'١٩٤ - أما تنفك تركبني بلومى ... لهجت بها كما لهج الفصيل أتنسى- لا هداك الله- سلمى ... وعهد شبابها الحسن الجميل كأن وقد أتى حول كميل ... أثافيها حمامات مثول','828','2109','0');
INSERT INTO [t17712] VALUES (2304,'١٩٥ - ليس العطاء من الفضول سماحة ... حتى تجود وما لديك قليل','829','2109','0');
INSERT INTO [t17712] VALUES (2305,'١٩٦ - ولو ان ما عالجت لين فؤاده ... فقسا استلين به للان الجندل','829','2109','0');
INSERT INTO [t17712] VALUES (2306,'١٩٧ - يا عمرو إنك قد مللت صحابتي ... وصحابتيك- إخال ذاك- قليل','830','2109','0');
INSERT INTO [t17712] VALUES (2307,'١٩٨ - يا رب يوم لي لا أظلله ... أرمض من تحت وأضحى من عله','830','2109','0');
INSERT INTO [t17712] VALUES (2308,'١٩٩ - وجهك البدر لا بل الشمس لو لم ... يقض للشمس كسفة أو أفول','831','2109','0');
INSERT INTO [t17712] VALUES (2309,'٢٠٠ - أفاطم مهلا بعض هذا التدلل ... وإن كنت قد أزمعت صرمي فأجملي','831','2109','0');
INSERT INTO [t17712] VALUES (2310,'٢٠١ - فيا رب يوم قد لهوت وليلة ... بآنسة كأنها خط تمثال','831','2109','0');
INSERT INTO [t17712] VALUES (2311,'٢٠٢ - ألا رب يوم صالح لك منهما ... ولا سيما يوم بدارة جلجل','832','2109','0');
INSERT INTO [t17712] VALUES (2312,'٢٠٣ - دع عنك نهبا صيح في حجراته ... ولكن حديثا ما حديث الرواحل','832','2109','0');
INSERT INTO [t17712] VALUES (2313,'٢٠٤ - ألا عم صباحا أيها الطلل البالي ... وهل يعمن من كان في العصر الخالي وهل يعمن من كان أحدث عهده ... ثلاثين شهرا في ثلاثة أحوال','832','2109','0');
INSERT INTO [t17712] VALUES (2314,'٢٠٥ - حلفت لها بالله حلفة فاجر ... لناموا فما إن من حديث ولا صالي','833','2109','0');
INSERT INTO [t17712] VALUES (2315,'٢٠٦ - ويوم عقرت للعذارى مطيتي ... فيا عجبا من رحلها المتحمل','833','2109','0');
INSERT INTO [t17712] VALUES (2316,'٢٠٧ - فيا لك من ليل كأن نجومه ... بكل مغار الفتل شدت بيذبل','833','2109','0');
INSERT INTO [t17712] VALUES (2317,'٢٠٨ - كأن قلوب الطير رطبا ويابسا ... لدى وكرها العناب والحشف البالي','834','2109','0');
INSERT INTO [t17712] VALUES (2318,'٢٠٩ - كأن دثارا حلقت بلبونه ... عقاب تنوفى لا عقاب القواعل','834','2109','0');
INSERT INTO [t17712] VALUES (2319,'٢١٠ - تجاوزت أحراسا إليها ومعشرا ... علي حراصا لو يشرون مقتلي','834','2109','0');
INSERT INTO [t17712] VALUES (2320,'٢١١ - فتوضح فالمقراة لم يعف رسمها ... لما نسجتها من جنوب وشمأل','834','2109','0');
INSERT INTO [t17712] VALUES (2321,'٢١٢ - ويوم دخلت الخدر خدر عنيزة ... فقالت: لك الويلات إنك مرجلي','834','2109','0');
INSERT INTO [t17712] VALUES (2322,'٢١٣ - وإن شفائي عبرة مهراقة ... وهل عند رسم دارس من معول','835','2109','0');
INSERT INTO [t17712] VALUES (2323,'٢١٤ - فظل طهاة اللحم من بين منضج ... صفيف شواء أو قدير معجل','835','2109','0');
INSERT INTO [t17712] VALUES (2324,'٢١٥ - خرجت بها أمشي تجر وراءنا ... على أثرينا ذيل مرط مرحل','835','2109','0');
INSERT INTO [t17712] VALUES (2325,'٢١٦ - إذا قامتا تضوع المسك منهما ... نسيم الصبا جاءت بريا القرنفل','836','2109','0');
INSERT INTO [t17712] VALUES (2326,'٢١٧ - فقلت يمين الله أبرح قاعدا ... ولو قطعوا رأسي لديك وأوصالي','836','2109','0');
INSERT INTO [t17712] VALUES (2327,'٢١٨ - فقالوا لنا ثنتان لا بد منهما ... صدور رماح أشرعت أو سلاسل','836','2109','0');
INSERT INTO [t17712] VALUES (2328,'٢١٩ - وترمينني بالطرف أي: أنت مذنب ... وتقلينني لكن إياك لا أقلي','836','2109','0');
INSERT INTO [t17712] VALUES (2329,'٢٢٠ - وأبيض يستسقى الغمام بوجهه ... ثمال اليتامى عصمة للأرامل','837','2109','0');
INSERT INTO [t17712] VALUES (2330,'٢٢١ - أريد لأنسى ذكرها فكأنما ... تمثل لي بكل سبيل','837','2109','0');
INSERT INTO [t17712] VALUES (2331,'٢٢٢ - ويلحينني في اللهو أن لا أحبه ... وللهو داع دائب غير غافل','837','2109','0');
INSERT INTO [t17712] VALUES (2332,'٢٢٣ - ألا زعمت أسماء أن لا أحبها ... فقلت بلى لولا ينازعني شغلي','838','2109','0');
INSERT INTO [t17712] VALUES (2333,'٢٢٤ - فأضحت مغانيها قفارا رسومها ... كأن لم- سوى أهل من الوحش- تؤهل','838','2109','0');
INSERT INTO [t17712] VALUES (2334,'٢٢٥ - وإن تعتذر بالمحل من ذي ضروعها ... إلى الضيف يجرح في عراقيبها نصلي','838','2109','0');
INSERT INTO [t17712] VALUES (2335,'٢٢٦ - فقولا لها قولا رفيقا لعلها ... سترحمني من زفرة وعويل','839','2109','0');
INSERT INTO [t17712] VALUES (2336,'٢٢٧ - فليت دفعت الهم عني ساعة ... فنمنا على ما خيلت ناعمي بال','839','2109','0');
INSERT INTO [t17712] VALUES (2337,'٢٢٨ - فلست بآتيه ولا أستطيعه ... ولاك اسقني إن كان ماؤك ذا فضل','839','2109','0');
INSERT INTO [t17712] VALUES (2338,'٢٢٩ - أنا الفارس الحامي الذمار وإنما ... يدافع عن أحسابهم أنا أو مثلي','839','2109','0');
INSERT INTO [t17712] VALUES (2339,'٢٣٠ - ألا أصبحت أسماء جاذمة الحبل ... وضنت علينا والضنين من البخل','839','2109','0');
INSERT INTO [t17712] VALUES (2340,'٢٣١ - أراني- ولا كفران لله أية ... لنفسي- قد طالبت غير منيل','840','2109','0');
INSERT INTO [t17712] VALUES (2341,'٢٣٢ - لعمرك والخطوب مغيرات ... وفي طول المعاشرة التقالي لقد باليت مظعن أم أوفى ... ولكن أم أوفى لا تبالي','840','2109','0');
INSERT INTO [t17712] VALUES (2342,'٢٣٣ - إذا أحسن ابن العم بعد إساءة ... فلست لشري فعله بحمول','840','2109','0');
INSERT INTO [t17712] VALUES (2343,'٢٣٤ - كائن دعيت إلى بأساء داهمة ... فما انبعثت بمزؤود ولا وكل','840','2109','0');
INSERT INTO [t17712] VALUES (2344,'٢٣٥ - وما هجرتك لا، بل زادني شغفا ... هجر وبعد تراخى لا إلى أجل','841','2109','0');
INSERT INTO [t17712] VALUES (2345,'٢٣٦ - لم يمنع الشرب منها غير أن نطقت ... حمامة في غصون ذات أوقال','841','2109','0');
INSERT INTO [t17712] VALUES (2346,'٢٣٧ - وقد جعلت إذا ما قمت يثقلني ... ثوبي فأنهض نهض الشارب الثمل','841','2109','0');
INSERT INTO [t17712] VALUES (2347,'٢٣٨ - ولو نعطى الخيار لما افترقنا ... ولكن لا خيار مع الليالي','841','2109','0');
INSERT INTO [t17712] VALUES (2348,'٢٣٩ - بكيت وما بكا رجل حزين ... على ربعين مسلوب وبال','842','2109','0');
INSERT INTO [t17712] VALUES (2349,'٢٤٠ - أكلت بنيك أكل الضب حتى ... وجدت مرارة الكلأ الوبيل','842','2109','0');
INSERT INTO [t17712] VALUES (2350,'٢٤١ - لما أغفلت شكرك فاصطنعني ... فكيف ومن عطائك جل مالي','842','2109','0');
INSERT INTO [t17712] VALUES (2351,'٢٤٢ - أم لا سبيل إلى الشباب وذكره ... أشهى إلي من الرحيق السلسل','842','2109','0');
INSERT INTO [t17712] VALUES (2352,'٢٤٣ - فأتت به حوش الفؤاد مبطنا ... سهدا إذا ما نام ليل الهوجل','843','2109','0');
INSERT INTO [t17712] VALUES (2353,'٢٤٤ - ممن حملن به وهن عواقد ... حبك النطاق فشب غير مهبل حملت به في ليلة مزؤودة ... كرها وعقد نطاقها لم يحلل','843','2109','0');
INSERT INTO [t17712] VALUES (2354,'٢٤٥ - استغن ما أغناك ربك بالغنى ... وإذا تصبك خصاصة فتجمل','844','2109','0');
INSERT INTO [t17712] VALUES (2355,'٢٤٦ - يغشون حتى ما تهر كلابهم ... لا يسألون عن السواد المقبل','844','2109','0');
INSERT INTO [t17712] VALUES (2356,'٢٤٧ - زعم العواذل أنني في غمرة ... صدقوا ولكن غمرتي لا تنجلي','845','2109','0');
INSERT INTO [t17712] VALUES (2357,'٢٤٨ - ذاك الذي وأبيك تعرف مالك ... والحق يدفع ترهات الباطل','845','2109','0');
INSERT INTO [t17712] VALUES (2358,'٢٤٩ - ومنهل وردته عن منهل ... قفر به الأعطان لم تسهل','845','2109','0');
INSERT INTO [t17712] VALUES (2359,'٢٥٠ - وبدلت والدهر ذو تبدل ... هيفا دبورا بالصبا والشمأل','845','2109','0');
INSERT INTO [t17712] VALUES (2360,'٢٥١ - كل امرئ مصبح في أهله ... والموت أدنى من شراك نعله','846','2109','0');
INSERT INTO [t17712] VALUES (2361,'٢٥٢ - تساور سوارا إلى المجد والعلا ... وفي ذمتي لئن فعلت ليفعلا','846','2109','0');
INSERT INTO [t17712] VALUES (2362,'٢٥٣ - قروم تسامى عند باب دفاعه ... كأن يؤخذ المرء الكريم فيقتلا','846','2109','0');
INSERT INTO [t17712] VALUES (2363,'٢٥٤ - فقال: امكثي حتى يسار لعلنا ... نحج معا قالت: أعاما وقابله','847','2109','0');
INSERT INTO [t17712] VALUES (2364,'٢٥٥ - أتتني سليم قضها بقضيضها ... تمسح حولي بالبقيع سبالها','847','2109','0');
INSERT INTO [t17712] VALUES (2365,'٢٥٦ - كذبتك عينك أم رأيت بواسط ... غلس الظلام من الرباب خيالا','847','2109','0');
INSERT INTO [t17712] VALUES (2366,'٢٥٧ - إن لكم أصل البلاد وفرعها ... فالخير فيكم ثابتا مبذولا','847','2109','0');
INSERT INTO [t17712] VALUES (2367,'٢٥٨ - إن محلا وإن مرتحلا ... وإن في السفر ما مضى مهلا','848','2109','0');
INSERT INTO [t17712] VALUES (2368,'٢٥٩ - على أنني بعد ما قد مضى ... ثلاثون للهجر حولا كميلا يذكرنيك حنين العجول ... ونوح الحمامة تدعو هديلا','848','2109','0');
INSERT INTO [t17712] VALUES (2369,'٢٦٠ - ألام على لو ولو كنت عالما ... بأذناب لو لم تفتني أوائله','848','2109','0');
INSERT INTO [t17712] VALUES (2370,'٢٦١ - فيا لك من دار تحمل أهلها ... أيادي سبا بعدي وطال احتيالها','849','2109','0');
INSERT INTO [t17712] VALUES (2371,'٢٦٢ - في فتية كسيوف الهند قد علموا ... أن هالك كل من يحفى وينتعل','849','2109','0');
INSERT INTO [t17712] VALUES (2372,'٢٦٣ - أأن رأت رجلا أعشى أضر به ... ريب المنون ودهر مفسد خبل','849','2109','0');
INSERT INTO [t17712] VALUES (2373,'٢٦٤ - وما صرفتك حتى قلت معلنة ... لا ناقة لي في هذا ولا جمل','849','2109','0');
INSERT INTO [t17712] VALUES (2374,'٢٦٥ - أملت خيرك هل تأتي قواعده ... فاليوم قصر عن تلقائك الأمل','850','2109','0');
INSERT INTO [t17712] VALUES (2375,'٢٦٦ - كم نالني منهم فضلا على عدم ... إذ لا أكاد من الإقتار أحتمل','850','2109','0');
INSERT INTO [t17712] VALUES (2376,'٢٦٧ - إذ هي أحوى من الربعي حاجبه ... والعين بالإثمد الحاري مكحول','850','2109','0');
INSERT INTO [t17712] VALUES (2377,'٢٦٨ - ولا تشتم المولى وتبلغ أذاته ... فإنك إن تفعل تسفه وتجهل','850','2109','0');
INSERT INTO [t17712] VALUES (2378,'٢٦٩ - ومالكم والفرط لا تقربونه ... وقد خلته أدنى مرد لعاقل','851','2109','0');
INSERT INTO [t17712] VALUES (2379,'٢٧٠ - فمالك والتلدد حول نجد ... وقد غصت تهامة بالرجال','851','2109','0');
INSERT INTO [t17712] VALUES (2380,'٢٧١ - أراني- ولا كفران لله- إنما ... أواخي من الأقوام كل بخيل','851','2109','0');
INSERT INTO [t17712] VALUES (2381,'٢٧٢ - وما أنا للشيء الذي ليس نافعي ... ويغضب منه صاحبي بقؤول','851','2109','0');
INSERT INTO [t17712] VALUES (2382,'٢٧٣ - لما تمكن دنياهم أطاعهم ... في أي نحو يميلوا دينه يمل','852','2109','0');
INSERT INTO [t17712] VALUES (2383,'٢٧٤ - ثلاثة أنفس وثلاث ذود ... لقد جار الزمان على عيالي','852','2109','0');
INSERT INTO [t17712] VALUES (2384,'٢٧٥ - وأنت مكانك من وائل ... مكان القراد من است الجمل','852','2109','0');
INSERT INTO [t17712] VALUES (2385,'٢٧٦ - أنصب للمنية تعتريهم ... رجالي أم هم درج السيول','852','2109','0');
INSERT INTO [t17712] VALUES (2386,'٢٧٧ - إني بحبلك واصل حبلي ... وبريش نبلك رائش نبلي','853','2109','0');
INSERT INTO [t17712] VALUES (2387,'٢٧٨ - إني انصببت من السماء عليكم ... حتى اختطفتك يا فرزدق من عل','853','2109','0');
INSERT INTO [t17712] VALUES (2388,'٢٧٩ - ما إن يمس الأرض إلا منكب ... منه وحرف الساق طي المحمل','853','2109','0');
INSERT INTO [t17712] VALUES (2389,'٢٨٠ - الحرب أول ما تكون فتية ... تسعى ببزتها لكل جهول','854','2109','0');
INSERT INTO [t17712] VALUES (2390,'٢٨١ - ويأوي إلى نسوة عطل ... وشعث مراضيع مثل السعالي','854','2109','0');
INSERT INTO [t17712] VALUES (2391,'٢٨٢ - ألا يا لقوم لطيف الخيال ... أرق من نازح ذي دلال','854','2109','0');
INSERT INTO [t17712] VALUES (2392,'٢٨٣ - وأكذب النفس إذا حدثتها ... إن صدق النفس يزري بالأمل','855','2109','0');
INSERT INTO [t17712] VALUES (2393,'٢٨٤ - حجبت تحيتها فقلت لصاحبي ... ما كان أكثرها لنا وأقلها','855','2109','0');
INSERT INTO [t17712] VALUES (2394,'٢٨٥ - أقيم بدار الحزم ما دام حزمها ... وأحر إذا حالت بأن أتحولا','855','2109','0');
INSERT INTO [t17712] VALUES (2395,'٢٨٦ - فنعم ابن أخت القوم غير مكذب ... زهير حسام مفرد من حمائل','855','2109','0');
INSERT INTO [t17712] VALUES (2396,'٢٨٧ - خليلي ما أذني لأول عاذل ... بصغواء في حق ولا عند باطل','856','2109','0');
INSERT INTO [t17712] VALUES (2397,'٢٨٨ - خليلي إن الرأي ليس بشركة ... ولا نهنه عند الأمور البلابل','857','2109','0');
INSERT INTO [t17712] VALUES (2398,'٢٨٩ - ولما رأيت القوم لاود عندهم ... وقد قطعوا كل العرى والوسائل','857','2109','0');
INSERT INTO [t17712] VALUES (2399,'٢٩٠ - وقد صارحونا بالعداوة والأذى ... وقد طاوعوا أمر العدو المزايل','857','2109','0');
INSERT INTO [t17712] VALUES (2400,'٢٩١ - وقد حالفوا قوما علينا أظنة ... يعضون غيظا خلفنا بالأنامل','857','2109','0');
INSERT INTO [t17712] VALUES (2401,'٢٩٢ - صبرت لهم نفسي بسمراء سمحة ... وأبيض عضب من تراث المقاول','858','2109','0');
INSERT INTO [t17712] VALUES (2402,'٢٩٣ - وأحضرت عند البيت رهطى وإخوتي ... وأمسكت من أثوابه بالوصائل','858','2109','0');
INSERT INTO [t17712] VALUES (2403,'٢٩٤ - قياما معا مستقبلين رتاجه ... لدى حيث يقضي حلفه كل نافل','858','2109','0');
INSERT INTO [t17712] VALUES (2404,'٢٩٥ - أعوذ برب الناس من كل طاعن ... علينا بسوء أو ملح بباطل ومن كاشح يسعى لنا بمعيبة ... ومن ملحق في الدين ما لم نحاول','858','2109','0');
INSERT INTO [t17712] VALUES (2405,'٢٩٦ - وثور ومن أرسى ثبيرا مكانه ... وراق لبر في حراء ونازل','858','2109','0');
INSERT INTO [t17712] VALUES (2406,'٢٩٧ - وبالبيت حق البيت من بطن مكة ... وبالله، إن الله ليس بغافل وبالحجر الأسود إذ يمسحونه ... إذا اكتنفوه بالضحى والأصائل','859','2109','0');
INSERT INTO [t17712] VALUES (2407,'٢٩٨ - وموطئ إبراهيم في الصخر رطبة ... على قدميه حافيا غير ناعل','859','2109','0');
INSERT INTO [t17712] VALUES (2408,'٢٩٩ - وأشواط بين المروتين إلى الصفا ... وما فيهما من صورة وتماثل','859','2109','0');
INSERT INTO [t17712] VALUES (2409,'٣٠٠ - ومن حج بيت الله من كل راكب، ... ومن كل ذي نذر، ومن كل راجل فهل بعد هذا من معاذ لعائذ ... وهل من معيذ يتقي الله عادل','859','2109','0');
INSERT INTO [t17712] VALUES (2410,'٣٠١ - يطاع بنا العدا، وودوا لو أننا ... تسد بنا أبواب ترك وكابل','859','2109','0');
INSERT INTO [t17712] VALUES (2411,'٣٠٢ - كذبتم وبيت الله نترك مكة ... ونظعن إلا أمركم في بلابل','860','2109','0');
INSERT INTO [t17712] VALUES (2412,'٣٠٣ - كذبتم وبيت الله نبزى محمدا ... ولما نطاعن دونه ونناضل','860','2109','0');
INSERT INTO [t17712] VALUES (2413,'٣٠٤ - ونسلمه حتى نصرع حوله ... ونذهل عن أبنائنا والحلائل','860','2109','0');
INSERT INTO [t17712] VALUES (2414,'٣٠٥ - وينهض قوم في الحديد إليكم ... نهوض الروايا تحت ذات الصلاصل','860','2109','0');
INSERT INTO [t17712] VALUES (2415,'٣٠٦ - وحتى نرى ذا الضغن يركب ردعه ... من الطعن فعل الأنكب المتحامل','861','2109','0');
INSERT INTO [t17712] VALUES (2416,'٣٠٧ - وإنا لعمر الله إن جد ما أرى ... لتلتبسن أسيافنا بالأماثل','861','2109','0');
INSERT INTO [t17712] VALUES (2417,'٣٠٨ - بكفي فتى مثل الشهاب سميدع ... أخي ثقة حامي الحقيقة باسل','861','2109','0');
INSERT INTO [t17712] VALUES (2418,'٣٠٩ - وما ترك قوم لا أبا لك سيدا ... يحوط الذمار غير ذرب مواكل','862','2109','0');
INSERT INTO [t17712] VALUES (2419,'٣١٠ - وأبيض يستسقى الغمام بوجهه ... ثمال اليتامى عصمة للأرامل','862','2109','0');
INSERT INTO [t17712] VALUES (2420,'٣١١ - يلوذ به الهلاك من آل هاشم ... فهم عنده في رحمة وفواضل','864','2109','0');
INSERT INTO [t17712] VALUES (2421,'٣١٢ - جزى الله عنا عبد شمس ونوفلا ... عقوبة شر عاجلا غير آجل','865','2109','0');
INSERT INTO [t17712] VALUES (2422,'٣١٣ - بميزان قسط لا يخس شعيرة ... له شاهد من نفسه غير عائل','865','2109','0');
INSERT INTO [t17712] VALUES (2423,'٣١٤ - ونحن الصميم من ذؤابة هاشم ... وآل قصي في الخطوب الأوائل','865','2109','0');
INSERT INTO [t17712] VALUES (2424,'٣١٥ - وكل صديق وابن أخت نعده ... لعمري، وجدنا غبه غير طائل','865','2109','0');
INSERT INTO [t17712] VALUES (2425,'٣١٦ - سوى أن رهطا من كلاب بن مرة ... براء إلينا من معقة خاذل','865','2109','0');
INSERT INTO [t17712] VALUES (2426,'٣١٧ - ونعم ابن أخت القوم غير مكذب ... زهير حساما مفردا من حمائل','866','2109','0');
INSERT INTO [t17712] VALUES (2427,'٣١٨ - أشم، من الشم البهاليل ينتمي ... إلى حسب في حومة المجد فاضل','866','2109','0');
INSERT INTO [t17712] VALUES (2428,'٣١٩ - لعمري، لقد كلفت وجدا بأحمد ... وإخوته دأب المحب المواصل','866','2109','0');
INSERT INTO [t17712] VALUES (2429,'٣٢٠ - فلا زال في الدنيا جمالا لأهلها ... وزينا لمن ولاه ذب المشاكل','867','2109','0');
INSERT INTO [t17712] VALUES (2430,'٣٢١ - فمن مثله في الناس! أي مومل ... إذا قاسه الحكام عند التفاضل!','867','2109','0');
INSERT INTO [t17712] VALUES (2431,'٣٢٢ - حليم رشيد عادل غير طائش ... يوالي إلها ليس عنه بغافل','867','2109','0');
INSERT INTO [t17712] VALUES (2432,'٣٢٣ - فأيده رب العباد بنصره ... وأظهر دينا حقه غير ناصل','867','2109','0');
INSERT INTO [t17712] VALUES (2433,'٣٢٤ - فو الله، لولا أن أجيء بسبة ... تجر على أشياخنا في القبائل لكنا اتبعناه على كل حالة ... من الدهر جدا غير قول التهازل','867','2109','0');
INSERT INTO [t17712] VALUES (2434,'٣٢٥ - لقد علموا أن ابننا لا مكذب ... لدينا ولا يعنى بقول الأباطل','867','2109','0');
INSERT INTO [t17712] VALUES (2435,'٣٢٦ - فأصبح فينا أحمد في أرومة ... يقصر عنها سورة المتطاول','868','2109','0');
INSERT INTO [t17712] VALUES (2436,'٣٢٧ - حدبت بنفسي دونه وحميته ... ودافعت عنه بالذرا والكلاكل','868','2109','0');
INSERT INTO [t17712] VALUES (2437,'٣٢٨ - يمينا لأبغض كل امرئ ... يزخرف قولا ولا يفعل','868','2109','0');
INSERT INTO [t17712] VALUES (2438,'٣٢٩ - أحيا؟ وأيسر ما قاسيت ما قتلا ... والبين جار على ضعفي وما عدلا','868','2109','0');
INSERT INTO [t17712] VALUES (2439,'٣٣٠ - ولبست سربال الشباب أزورها ... ولنعم كان شبيبة المحتال','868','2109','0');
INSERT INTO [t17712] VALUES (2440,'٣٣١ - أتوني فقالوا: يا جميل، تبدلت ... بثينة أبدالا، فقلت: لعلها','868','2109','0');
INSERT INTO [t17712] VALUES (2441,'٣٣٢ - وما زلت سباقا إلى كل غاية ... بها يبتغى في الناس مجد وإجلال','868','2109','0');
INSERT INTO [t17712] VALUES (2442,'٣٣٣ - وبنت كرام قد نكحنا ولم يكن ... لنا خاطب إلا السنان وعامله','869','2109','0');
INSERT INTO [t17712] VALUES (2443,'٣٣٤ - حيتك عزة بعد الهجر وانصرفت ... فحي ويحك، من حياك يا جمل ليت التحية كانت لي فأشكرها ... مكان يا جمل حييت يا رجل','869','2109','0');
INSERT INTO [t17712] VALUES (2444,'٣٣٥ - لو يشأ طار به ذو ميعة ... لاحق الآطال نهد ذو خصل','869','2109','0');
INSERT INTO [t17712] VALUES (2445,'٣٣٦ - إن الكريم، وأبيك يعتمل ... إن لم يجد يوما على من يتكل','869','2109','0');
INSERT INTO [t17712] VALUES (2446,'٣٣٧ - لمتى صلحت ليقضين لك صالح ... ولتجزين إذا جزيت جميلا','870','2109','0');
INSERT INTO [t17712] VALUES (2447,'٣٣٨ - غذوتك مولودا وعلتك يافعا ... تعل بما أدني إليك وتنهل','870','2109','0');
INSERT INTO [t17712] VALUES (2448,'٣٣٩ - وما حالة إلا سيصرف حالها ... إلى حالة أخرى وسوف تزول','870','2109','0');
INSERT INTO [t17712] VALUES (2449,'٣٤٠ - فما مثله فيهم ولا كان قبله ... وليس يكون الدهر ما دام يذبل','870','2109','0');
INSERT INTO [t17712] VALUES (2450,'٣٤١ - هيا أم عمرو هل لي اليوم عندكم ... بغيبة أبصار الوشاة سبيل','870','2109','0');
INSERT INTO [t17712] VALUES (2451,'٣٤٢ - لو كان في قلبي كقدر قلامة ... حبا لغيرك ما أتتك رسائلي','871','2109','0');
INSERT INTO [t17712] VALUES (2452,'٣٤٣ - فإذا وذلك يا كبيشة لم يكن ... إلا كلمة بارق بخيال','871','2109','0');
INSERT INTO [t17712] VALUES (2453,'٣٤٤ - فإذا وذلك ليس إلا ذكره ... وإذا مضى شيء كأن لم يفعل','871','2109','0');
INSERT INTO [t17712] VALUES (2454,'٣٤٥ - ولو كنت تعطي حين تسأل سامحت ... لك النفس واحلولاك كل خليل أجل، لا، ولكن أنت أشأم من مشى ... وأسأل من صماء ذات صليل','871','2109','0');
INSERT INTO [t17712] VALUES (2455,'٣٤٦ - جزى ربه عني عدي بن حاتم ... جزاء الكلاب العاويات وقد فعل','871','2109','0');
INSERT INTO [t17712] VALUES (2456,'٣٤٧ - أيهذان كلا زاديكما ... ودعاني واغلا في من يغل','872','2109','0');
INSERT INTO [t17712] VALUES (2457,'٣٤٨ - محمد تفد نفسك كل نفس ... إذا ما خفت من شيء تبالا','872','2109','0');
INSERT INTO [t17712] VALUES (2458,'٣٤٩ - فاليوم أشرب غير مستحقب ... إثما من الله ولا واغل','872','2109','0');
INSERT INTO [t17712] VALUES (2459,'٣٥٠ - وما حق الذي يعثو نهارا ... ويسرق ليله إلا نكالا','873','2109','0');
INSERT INTO [t17712] VALUES (2460,'٣٥١ - بينما نحن بالأراك معا ... إذ أتى راكب على جمله','873','2109','0');
INSERT INTO [t17712] VALUES (2461,'٣٥٢ - وكل أبي باسل غير أنني ... إذا عرضت أولى الطرائد أبسل','873','2109','0');
INSERT INTO [t17712] VALUES (2462,'٣٥٣ - فإما تريني كابنة الرمل ضاحيا ... على رقة أحفى ولا أتنعل','874','2109','0');
INSERT INTO [t17712] VALUES (2463,'٣٥٤ - بأوشك منه أن يساور قرنه ... إذا شال عن خفض العوالي الأسافل','874','2109','0');
INSERT INTO [t17712] VALUES (2464,'٣٥٥ - فإن تبتئس بالشنفرى أم قسطل ... لما اغتبطت بالشنفرى قبل أطول','874','2109','0');
INSERT INTO [t17712] VALUES (2465,'٣٥٦ - إني لأمنحك الصدود وإنني ... قسما إليك مع الصدود لأميل','874','2109','0');
INSERT INTO [t17712] VALUES (2466,'٣٥٧ - فإن أنت لم ينفعك علمك فانتسب ... لعلك تهديك القرون الأوائل','875','2109','0');
INSERT INTO [t17712] VALUES (2467,'٣٥٨ - فبادرن الديار يزفن فيها ... وبئس من المليحات البديل','875','2109','0');
INSERT INTO [t17712] VALUES (2468,'٣٥٩ - أبلغ يزيد بني شيبان مألكة ... أبا ثبيت أما تنفك تأتكل','875','2109','0');
INSERT INTO [t17712] VALUES (2469,'٣٦٠ - وتشرب أساري القطا الكدر بعد ما ... سرت قربا أحناؤها تتصلصل','875','2109','0');
INSERT INTO [t17712] VALUES (2470,'٣٦١ - فعبت غشاشا ثم مرت كأنها ... مع الصبح ركب من أحاظة مجفل','876','2109','0');
INSERT INTO [t17712] VALUES (2471,'٣٦٢ - زيادتنا نعمان لا تنسينها ... تق الله فينا والكتاب الذي تتلو','876','2109','0');
INSERT INTO [t17712] VALUES (2472,'٣٦٣ - وما حالة إلا سيصرف حالها ... إلى حالة أخرى وسوف تزول','876','2109','0');
INSERT INTO [t17712] VALUES (2473,'٣٦٤ - جوابا به تنجو اعتمد فوربنا ... لعن عمل أسلفت لا غير تسأل','876','2109','0');
INSERT INTO [t17712] VALUES (2474,'٣٦٥ - يا أحسن الناس ما قرنا إلى قدم ... ولا حبال محب واصل تصل','877','2109','0');
INSERT INTO [t17712] VALUES (2475,'٣٦٦ - ماذا- ولا عتب في المقدور- رمت أما ... يكفيك بالنجح أم خسرو تضليل','877','2109','0');
INSERT INTO [t17712] VALUES (2476,'٣٦٧ - فيوما يوافين الهوى غير ماضي ... ويوما ترى منهن غولا تغول','877','2109','0');
INSERT INTO [t17712] VALUES (2477,'٣٦٨ - فإن يك من جن لأبرح طارقا ... وإن يك إنسا ماكها الإنس تفعل','877','2109','0');
INSERT INTO [t17712] VALUES (2478,'٣٦٩ - ولي دونكم أهلون سيد عملس ... وأرقط زهلول وعرفاء جيأل','878','2109','0');
INSERT INTO [t17712] VALUES (2479,'٣٧٠ - وما قصرت بي في التسامي خؤولة ... ولكن عمي الطيب الأصل والخال','880','2109','0');
INSERT INTO [t17712] VALUES (2480,'٣٧١ - إن الكريم لمن ترجوه ذو جدة ... ولو تعذر إيسار وتنويل','880','2109','0');
INSERT INTO [t17712] VALUES (2481,'٣٧٢ - صحا القلب عن سلمى وقد كاد لا يسلو ... وأقفر من سلمى التعانيق والثقل','880','2109','0');
INSERT INTO [t17712] VALUES (2482,'٣٧٣ - أتي الفواحش عندهم معروفة ... ولديهم ترك الجميل جميل','881','2109','0');
INSERT INTO [t17712] VALUES (2483,'٣٧٤ - فما وجد النهدي وجدا وجدته ... ولا وجد العذري قبل جميل','881','2109','0');
INSERT INTO [t17712] VALUES (2484,'٣٧٥ - لقد ألب الواشون ألبا لبينهم ... فترب لأفواه الوشاة وجندل','881','2109','0');
INSERT INTO [t17712] VALUES (2485,'٣٧٦ - لقد لقيت قريظة ما سآها ... وحل بدارها ذل ذليل','881','2109','0');
INSERT INTO [t17712] VALUES (2486,'٣٧٧ - بها العين والآرام لا عد عندها ... ولا كرع إلا المغارات والربل','882','2109','0');
INSERT INTO [t17712] VALUES (2487,'٣٧٨ - علقتها عرضا وعلقت رجلا ... غيري وعلق أخرى غيرها الرجل','882','2109','0');
INSERT INTO [t17712] VALUES (2488,'٣٧٩ - ليت التحية كانت لي فأشكرها ... مكان يا جمل حييت يا رجل','882','2109','0');
INSERT INTO [t17712] VALUES (2489,'٣٨٠ - رباء شماء لا يأوي لقلتها ... إلا السحاب وإلا الأوب والسبل','883','2109','0');
INSERT INTO [t17712] VALUES (2490,'٣٨١ - مشغوفة بك قد شغفت وإنما ... حم الفراق فما إليك سبيل','884','2109','0');
INSERT INTO [t17712] VALUES (2491,'٣٨٢ - مخلفة لا يستطاع ارتقاؤها ... وليس إلى منها النزول سبيل','884','2109','0');
INSERT INTO [t17712] VALUES (2492,'٣٨٣ - وإن هو لم يحمل على النفس ضيمها ... فليس إلى حسن الثناء سبيل','884','2109','0');
INSERT INTO [t17712] VALUES (2493,'٣٨٤ - أنا جدا جدا ولهوك يزدا ... د إذن ما إلى اتفاق سبيل','885','2109','0');
INSERT INTO [t17712] VALUES (2494,'٣٨٥ - فلا وأبيك خير منك إني ... ليؤذيني التحمحم والصهيل','885','2109','0');
INSERT INTO [t17712] VALUES (2495,'٣٨٦ - أهاجيتم حسان عند ذكائه ... فغي لأولاد الحماس طويل','886','2109','0');
INSERT INTO [t17712] VALUES (2496,'٣٨٧ - ألا حبذا عاذري في الهوى ... ولا حبذا الجاهل العاذل','886','2109','0');
INSERT INTO [t17712] VALUES (2497,'٣٨٨ - نحن الفوارس يوم العين ضاحية ... جنبي فطيمة لا ميل ولا عزل','886','2109','0');
INSERT INTO [t17712] VALUES (2498,'٣٨٩ - بكت عيني وحق لها بكاها ... وما يغني البكاء ولا العويل','887','2109','0');
INSERT INTO [t17712] VALUES (2499,'٣٩٠ - فما تدوم على حال تكون بها ... كما تلون في أثوابها الغول','887','2109','0');
INSERT INTO [t17712] VALUES (2500,'٣٩١ - السالك الثغرة اليقظان كالئها ... مشي الهلوك عليها الخيعل الفضل','887','2109','0');
INSERT INTO [t17712] VALUES (2501,'٣٩٢ - فقلت للركب لما أن علا بهم ... من عن يمين الحبيا نظرة قبل','888','2109','0');
INSERT INTO [t17712] VALUES (2502,'٣٩٣ - محا حبها حب الألى كن قبلها ... وحلت مكانا لم يكن حل من قبل','888','2109','0');
INSERT INTO [t17712] VALUES (2503,'٣٩٤ - فإن تبخل سدوس بدرهميها ... فإن الريح طيبة قبول','889','2109','0');
INSERT INTO [t17712] VALUES (2504,'٣٩٥ - أماوي إني رب واحد أمه ... ملكت فلا أسر لدي ولا قتل','889','2109','0');
INSERT INTO [t17712] VALUES (2505,'٣٩٦ - ثلاثة أحباب فحب علاقة ... وحب تملاق وحب هو القتل','889','2109','0');
INSERT INTO [t17712] VALUES (2506,'٣٩٧ - فما كان بين الخير لو جاء سالما ... أبو حجر إلا ليال قلائل','889','2109','0');
INSERT INTO [t17712] VALUES (2507,'٣٩٨ - فلم يجدا إلا مناخ مطية ... تجافى بها زور نبيل وكلكل','890','2109','0');
INSERT INTO [t17712] VALUES (2508,'٣٩٩ - ومفحصها عنها الحصى بجرانها ... ومثنى نواج لم يخنهن مفصل','890','2109','0');
INSERT INTO [t17712] VALUES (2509,'٤٠٠ - وسمر ظماء واترتهن بعد ما ... مضت هجعة عن آخر الليل ذبل','890','2109','0');
INSERT INTO [t17712] VALUES (2510,'٤٠١ - متى ما يفد كسبا يكن كل كسبه ... له مطعم من صدر يوم ومأكل','891','2109','0');
INSERT INTO [t17712] VALUES (2511,'٤٠٢ - ألا قالت أمامة يوم غول ... تقطع يا ابن غلفاء الحبال ذريني إنما خطئي وصوبي ... علي وإن ما أنفقت مال','891','2109','0');
INSERT INTO [t17712] VALUES (2512,'٤٠٣ - لقد بسملت ليلى غداة لقيتها ... ألا حبذا ذاك الحبيب المبسمل','891','2109','0');
INSERT INTO [t17712] VALUES (2513,'٤٠٤ - كما ما امرؤ في معشر غير قومه ... ضعيف الكلام شخصه متضائل','891','2109','0');
INSERT INTO [t17712] VALUES (2514,'٤٠٥ - فلهو أخوف عندي إذ أكلمه ... وقيل إنك منسوب ومسؤول','892','2109','0');
INSERT INTO [t17712] VALUES (2515,'٤٠٦ - نرجو فواضل رب سيبه حسن ... وكل خير لديه فهو مسؤول','892','2109','0');
INSERT INTO [t17712] VALUES (2516,'٤٠٧ - شجت بذي شبم من ماء محنية ... صاف بأبطح أضحى وهو مشمول','892','2109','0');
INSERT INTO [t17712] VALUES (2517,'٤٠٨ - فتلك ولاة السوء قد طال مكثها ... فحتام حتام العناء المطول','892','2109','0');
INSERT INTO [t17712] VALUES (2518,'٤٠٩ - حتى إذا رجب تولى وانقضى ... وجماديان وجاء شهر مقبل','893','2109','0');
INSERT INTO [t17712] VALUES (2519,'٤١٠ - ولى وصرعن من حيث التبسن به ... مضرجات بأجراح ومقتول','893','2109','0');
INSERT INTO [t17712] VALUES (2520,'٤١١ - ثمت قمنا إلى جرد مسومة ... أعرافهن لأيدينا مناديل','894','2109','0');
INSERT INTO [t17712] VALUES (2521,'٤١٢ - سرى بعد ما غار الثريا وبعد ما ... كأن الثريا حلة الغور منخل','894','2109','0');
INSERT INTO [t17712] VALUES (2522,'٤١٣ - عليها أسود ضاريات لبوسهم ... سوابيغ بيض لا يخرقها النبل','894','2109','0');
INSERT INTO [t17712] VALUES (2523,'٤١٤ - وهل ينبت الخطي إلا وشيجه ... وتغرس إلا في منابتها النخل','894','2109','0');
INSERT INTO [t17712] VALUES (2524,'٤١٥ - قد كان في جيف بدجلة حرقت ... أو في الذين على الرحوب شغول وكأن عافية النسور عليهم ... حج بأسفل ذي المجاز نزول','895','2109','0');
INSERT INTO [t17712] VALUES (2525,'٤١٦ - قامت تلوم وبعض اللوم آونة ... مما يضر ولا يبقى له نغل','895','2109','0');
INSERT INTO [t17712] VALUES (2526,'٤١٧ - إذا قلت مهلا غارت العين بالبكا ... غراء ومدتها مدامع نهل','895','2109','0');
INSERT INTO [t17712] VALUES (2527,'٤١٨ - إذا قلت ... ... ... حفل','896','2109','0');
INSERT INTO [t17712] VALUES (2528,'٤١٩ - ألم تسمعي أي عبد في رونق الضحى ... بكاء حمامات لهن هديل','896','2109','0');
INSERT INTO [t17712] VALUES (2529,'٤٢٠ - وقفت بربع الدار قد غير البلى ... معارفها والساريات الهواطل','896','2109','0');
INSERT INTO [t17712] VALUES (2530,'٤٢١ - ولا زال قبر بين تبنى وجاسم ... عليه من الوسمي جود ووابل فينبت حوذانا وعوفا منورا ... سأتبعه من خير ما قال قائل','896','2109','0');
INSERT INTO [t17712] VALUES (2531,'٤٢٢ - فشايع وسط ذودك مستقنا ... لتحسب سيدا ضبعا تنول','897','2109','0');
INSERT INTO [t17712] VALUES (2532,'٤٢٣ - يهز الهرانع عقده عند الخصى ... بأذل حيث يكون من يتذلل','897','2109','0');
INSERT INTO [t17712] VALUES (2533,'٤٢٤ - ولا خالف دارية متغزل ... يروح ويغدو داهنا يتكحل','898','2109','0');
INSERT INTO [t17712] VALUES (2534,'٤٢٥ - وليلة نحس يصطلي القوس ربها ... وأقطعه اللاتي بها يتنبل','899','2109','0');
INSERT INTO [t17712] VALUES (2535,'٤٢٦ - إن يبخلوا أو يجبنوا ... أو يغدروا لا يحفلوا يغدوا عليك مرجلي ... ن كأنهم لم يفعلوا','900','2109','0');
INSERT INTO [t17712] VALUES (2536,'٤٢٧ - فما مثله فيهم ولا كان قبله ... وليس يكون الدهر ما دام يذبل','900','2109','0');
INSERT INTO [t17712] VALUES (2537,'٤٢٨ - غدا طاويا يعارض الريح هافيا ... يخوت بأذناب الشعاب ويعسل','900','2109','0');
INSERT INTO [t17712] VALUES (2538,'٤٢٩ - فهل لك أو من والد لك قبلنا ... يوشح أولاد العشار ويفضل','901','2109','0');
INSERT INTO [t17712] VALUES (2539,'٤٣٠ - بنزوة لص بعد ما مر مصعب ... بأشعث لا يفلى ولا هو يقمل','901','2109','0');
INSERT INTO [t17712] VALUES (2540,'٤٣١ - أردت لكيما لا ترى لي عثرة ... ومن ذا الذي يعطى الكمال فيكمل','901','2109','0');
INSERT INTO [t17712] VALUES (2541,'٤٣٢ - فلئن بان أهله ... لبما كان يؤهل','902','2109','0');
INSERT INTO [t17712] VALUES (2542,'٤٣٣ - أتاني على القعساء عادل وطبه ... بخصي لئيم واست عبد تعادله','902','2109','0');
INSERT INTO [t17712] VALUES (2543,'٤٣٤ - ديار سليمى إذ تصيدك بالمنى ... وإذ حبل سلمى منك دان تواصله','902','2109','0');
INSERT INTO [t17712] VALUES (2544,'٤٣٥ - إذا غاب عنا غاب عنا ربيعنا ... وإن شهد أجدى خيره ونوافله','903','2109','0');
INSERT INTO [t17712] VALUES (2545,'٤٣٦ - إذا غاب عنا غاب عنا فراتنا ... وإن شهد أجري فيضه وجداوله','903','2109','0');
INSERT INTO [t17712] VALUES (2546,'٤٣٧ - يسرك مظلوما ويرضيك ظالما ... وكل الذي حملته فهو حامله','903','2109','0');
INSERT INTO [t17712] VALUES (2547,'٤٣٨ - هممت ولم أفعل وكدت وليتني ... تركت على عثمان تبكي حلائله','903','2109','0');
INSERT INTO [t17712] VALUES (2548,'٤٣٩ - وقائلة تجني علي أظنه ... سيودي به ترحاله وحوائله','903','2109','0');
INSERT INTO [t17712] VALUES (2549,'٤٤٠ - فهيج الحي من كلب فظل لهم ... يوم كثير تناديه وحيهله','904','2109','0');
INSERT INTO [t17712] VALUES (2550,'٤٤١ - إذا قام قوم يسألون مليكهم ... عطاء فدهماء الذي أنا سائله','904','2109','0');
INSERT INTO [t17712] VALUES (2551,'٤٤٢ - ولا تحرم المولى الكريم فإنه ... أخوك ولا تدري لعنك سائله','904','2109','0');
INSERT INTO [t17712] VALUES (2552,'٤٤٣ - ترى النعرات الزرق تحت لبانه ... أحاد ومثنى أضعفتها صواهله','904','2109','0');
INSERT INTO [t17712] VALUES (2553,'٤٤٤ - فأطعمنا من لحمها وسنامها ... شواء وخير الخير ما كان عاجله','905','2109','0');
INSERT INTO [t17712] VALUES (2554,'٤٤٥ - وبنت كرام قد نكحنا ولم يكن ... لنا خاطب إلا السنان وعامله','905','2109','0');
INSERT INTO [t17712] VALUES (2555,'٤٤٦ - فقال: امكثي حتى يسار لعلنا ... نحج معا قالت: أعاما وقابلة','905','2109','0');
INSERT INTO [t17712] VALUES (2556,'٤٤٧ - فقلت تعلم أن للصيد غرة ... وإلا تضيعها فإنك قاتله','905','2109','0');
INSERT INTO [t17712] VALUES (2557,'٤٤٨ - لقد خط رومي ولا زعماته ... لعتبة خطا لم تطبق مفاصله','906','2109','0');
INSERT INTO [t17712] VALUES (2558,'٤٤٩ - فلأيا بلأي ما حملنا غلامنا ... على ظهر محبوك ظماء مفاصله','906','2109','0');
INSERT INTO [t17712] VALUES (2559,'٤٥٠ - فيالك من ذي حاجة حيل دونها ... وما كل ما يهوى امرؤ هو نائله','906','2109','0');
INSERT INTO [t17712] VALUES (2560,'٤٥١ - بيناه في دار صدق قد أقام بها ... حينا يعللنا وما نعلله','907','2109','0');
INSERT INTO [t17712] VALUES (2561,'٤٥٢ - فبيناه يشري رحله قال قائل ... لمن جمل رخو الملاط ذلول','907','2109','0');
INSERT INTO [t17712] VALUES (2562,'٤٥٣ - وهم رجال يشفعوا لي فلم أجد ... شفيعا إليه غير جود يعادله','907','2109','0');
INSERT INTO [t17712] VALUES (2563,'٤٥٤ - وكرار خلف المحجرين جواده ... إذا لم يحام دون أنثى حليلها','907','2109','0');
INSERT INTO [t17712] VALUES (2564,'٤٥٥ - ولسنا إذا عد الحصى بأقلة ... وإن معد اليوم مود ذليلها','908','2109','0');
INSERT INTO [t17712] VALUES (2565,'٤٥٦ - تبين لي أن القماءة ذلة ... وأن أعزاء الرجال طيالها','908','2109','0');
INSERT INTO [t17712] VALUES (2566,'٤٥٧ - وأنتم لهذا الناس كالقبلة التي ... بها أن يضل الناس يهدى ضلالها','908','2109','0');
INSERT INTO [t17712] VALUES (2567,'٤٥٨ - ويها فدى لكم أمي وما ولدت ... حاموا على مجدكم وأكفوا من اتكلا','909','2109','0');
INSERT INTO [t17712] VALUES (2568,'٤٥٩ - أبو حنش يؤرقني وطلق ... وعمار وآونة أثالا أراهم رفقتي حتى إذا ما ... تجافى الليل وانخزل انخزالا إذا أنا كالذي يجري لورد ... إلى آل فلم يدرك بلالا','909','2109','0');
INSERT INTO [t17712] VALUES (2569,'٤٦٠ - ذريني وعلمي بالأمور وشيمتي ... فما طائري يوما عليك بأخيلا','910','2109','0');
INSERT INTO [t17712] VALUES (2570,'٤٦١ - فواعديه سرحتي مالك ... أو الربا بينهما أسهلا','910','2109','0');
INSERT INTO [t17712] VALUES (2571,'٤٦٢ - أبني كليب إن عمي اللذا ... قتلا الملوك وفككا الأغلالا','911','2109','0');
INSERT INTO [t17712] VALUES (2572,'٤٦٣ - أخذوا المخاض من الفصيل غلبة ... ظلما ويكتب للأمير أفيلا','911','2109','0');
INSERT INTO [t17712] VALUES (2573,'٤٦٤ - حتى لحقنا بهم تعدي فوارسنا ... كأننا رعن قف يرفع الآلا','911','2109','0');
INSERT INTO [t17712] VALUES (2574,'٤٦٥ - وليس الموافيني ليرفد خائبا ... فإن له أضعاف ما كان أملا','912','2109','0');
INSERT INTO [t17712] VALUES (2575,'٤٦٦ - علمت بسطك للمعروف خير يد ... فلا أرى فيك إلا باسطا أملا','912','2109','0');
INSERT INTO [t17712] VALUES (2576,'٤٦٧ - لم نرحب بأن شخصت ولكن ... مرحبا بالرضاء منك وأهلا','912','2109','0');
INSERT INTO [t17712] VALUES (2577,'٤٦٨ - لو أن عصم عمايتين ويذبل ... سمعا حديثك أنزلا الأوعالا','913','2109','0');
INSERT INTO [t17712] VALUES (2578,'٤٦٩ - بريذينة بل البراذين ثفرها ... وقد شربت في أول الصيف أيلا','913','2109','0');
INSERT INTO [t17712] VALUES (2579,'٤٧٠ - كن للخليل نصيرا جار أو عدلا ... ولا تشح عليه جاد أو بخلا','914','2109','0');
INSERT INTO [t17712] VALUES (2580,'٤٧١ - ما عاب إلا لئيم فعل ذي كرم ... ولا جفا قط إلا جبأ بطلا','914','2109','0');
INSERT INTO [t17712] VALUES (2581,'٤٧٢ - فأقسم بالله الذي اهتز عرشه ... على فوق سبع لا أعلمه بطلا','914','2109','0');
INSERT INTO [t17712] VALUES (2582,'٤٧٣ - غير أنا لم يأتنا بيقين ... فنرجي ونكثر التأميلا','914','2109','0');
INSERT INTO [t17712] VALUES (2583,'٤٧٤ - كأن قرون الشمس عند ارتفاعها ... وقد صادفت قرنا من النجم أعزلا تردد فيه ضوؤها وشعاعها ... فأحصن وأزين لامرئ إن تسربلا','914','2109','0');
INSERT INTO [t17712] VALUES (2584,'٤٧٥ - فويق جبيل شامخ لن تناله ... بقنته حتى تكل وتعملا','915','2109','0');
INSERT INTO [t17712] VALUES (2585,'٤٧٦ - وكوم تنعم الأضياف عينا ... وتصبح في مباركها ثقالا','915','2109','0');
INSERT INTO [t17712] VALUES (2586,'٤٧٧ - فوربي لسوف يجزى الذي أس ... لفه المرء سيئا أو جميلا','916','2109','0');
INSERT INTO [t17712] VALUES (2587,'٤٧٨ - هل تعرف اليوم رسم الدار والطللا ... كما عرفت بجفن الصيقل الخللا دار لمروة إذ أهلي وأهلهم ... بالكانسية نرعى اللهو والغزلا','916','2109','0');
INSERT INTO [t17712] VALUES (2588,'٤٧٩ - أريت امرأ كنت لم أبله ... أتاني فقال اتخذني خليلا','916','2109','0');
INSERT INTO [t17712] VALUES (2589,'٤٨٠ - أي حين تلم بي تلق ما شئ ... ت من الخير فاتخذني خليلا','916','2109','0');
INSERT INTO [t17712] VALUES (2590,'٤٨١ - فتى هو حقا غير ملغ فريضة ... ولا تتخذ يوما سواه خليلا','916','2109','0');
INSERT INTO [t17712] VALUES (2591,'٤٨٢ - أناو رجالك قتل امرئ ... من العز في حبك اعتاض ذلا','917','2109','0');
INSERT INTO [t17712] VALUES (2592,'٤٨٣ - فكأن ريضها إذا استقبلتها ... كانت معودة الركاب ذلولا','917','2109','0');
INSERT INTO [t17712] VALUES (2593,'٤٨٤ - نصروك قومي فاعتززت بنصرهم ... ولو أنهم خذلوك كنت ذليلا','917','2109','0');
INSERT INTO [t17712] VALUES (2594,'٤٨٥ - ما زلت تحسب كل شيء بعدهم ... خيلا تكر عليكم ورجالا','917','2109','0');
INSERT INTO [t17712] VALUES (2595,'٤٨٦ - لا تحبسنك أثوابي فقد جمعت ... هذا ردائي مطويا وسربالا','918','2109','0');
INSERT INTO [t17712] VALUES (2596,'٤٨٧ - وجدنا الصالحين لهم جزاء ... وجنات وعينا سلسبيلا','918','2109','0');
INSERT INTO [t17712] VALUES (2597,'٤٨٨ - طرن انقطاعة أوتار محظربة ... في أقوس نازعتها أيمن شملا','918','2109','0');
INSERT INTO [t17712] VALUES (2598,'٤٨٩ - ألكني إلى قومي السلام رسالة ... بآية ما كانوا ضعافا ولا عزلا ولا سيئي زي إذا ما تلبسوا ... إلى حاجة يوما مخيسة بزلا','919','2109','0');
INSERT INTO [t17712] VALUES (2599,'٤٩٠ - وقد وسطت مالكا وحنظلا ... صيابها والعدد المجلجلا','919','2109','0');
INSERT INTO [t17712] VALUES (2600,'٤٩١ - فلا ترى بعلا ولا حلائلا ... كه ولا كهن إلا حاظلا','919','2109','0');
INSERT INTO [t17712] VALUES (2601,'٤٩٢ - تظل الشمس كاسفة عليه ... كآبة أنها فقدت عقيلا','920','2109','0');
INSERT INTO [t17712] VALUES (2602,'٤٩٣ - تنصفه البرية وهو سام ... وتلفي العالمون له عيالا','920','2109','0');
INSERT INTO [t17712] VALUES (2603,'٤٩٤ - لو شئت قد نقع الفؤاد بمشرب ... يدع الحوائم لا يجدن غليلا','920','2109','0');
INSERT INTO [t17712] VALUES (2604,'٤٩٥ - سادوا البلاد وأصبحوا في آدم ... بلغوا بها بيض الوجوه فحولا','920','2109','0');
INSERT INTO [t17712] VALUES (2605,'٤٩٦ - بنصركم نحن كنتم واثقين وقد ... أغرى العدى بكم استسلامكم فشلا','921','2109','0');
INSERT INTO [t17712] VALUES (2606,'٤٩٧ - إذا كنت معنيا بمجد وسودد ... فلا تك إلا المجمل القول والفعلا','921','2109','0');
INSERT INTO [t17712] VALUES (2607,'٤٩٨ - دع المغمر لا تسأل بمصرعه ... واسأل بمصقلة البكري ما فعلا','921','2109','0');
INSERT INTO [t17712] VALUES (2608,'٤٩٩ - قالت فطيمة حل شعرك مدحه ... أفبعد كندة تمدحن قبيلا','921','2109','0');
INSERT INTO [t17712] VALUES (2609,'٥٠٠ - لقيتم بالجزيرة خيل قيس ... فقلتم مارسرجس لا قتالا','921','2109','0');
INSERT INTO [t17712] VALUES (2610,'٥٠١ - فألفيته غير مستعتب ... ولا ذاكر الله إلا قليلا','922','2109','0');
INSERT INTO [t17712] VALUES (2611,'٥٠٢ - ولو أنها إياك عضتك مثلها ... جررت على ما شئت نحرا وكلكلا','922','2109','0');
INSERT INTO [t17712] VALUES (2612,'٥٠٣ - إن لكم أصل البلاد وفرعها ... فالخير فيكم ثابتا مبذولا','922','2109','0');
INSERT INTO [t17712] VALUES (2613,'٥٠٤ - إن الألى وصفوا قومي لهم فبهم ... هذا اعتصم تلق من عاداك مخذولا','922','2109','0');
INSERT INTO [t17712] VALUES (2614,'٥٠٥ - عددت قشيرا إذ فخرت فلم أسأ ... بذاك ولم أزعمك عن ذاك معزلا','923','2109','0');
INSERT INTO [t17712] VALUES (2615,'٥٠٦ - حتى إذا لم يتركوا لعظامه ... لحما ولا لفؤاده معقولا','923','2109','0');
INSERT INTO [t17712] VALUES (2616,'٥٠٧ - تحنن علي هداك الملي ... ك فإن لكل مقام مقالا','923','2109','0');
INSERT INTO [t17712] VALUES (2617,'٥٠٨ - بنيت مرافقهن فوق مزلة ... لا يستطيع بها القراد مقيلا','923','2109','0');
INSERT INTO [t17712] VALUES (2618,'٥٠٩ - أزمان قومي والجماعة كالذي ... منع الرحالة أن تميل مميلا','923','2109','0');
INSERT INTO [t17712] VALUES (2619,'٥١٠ - وما شنتا خرقاء واهيتا الكلى ... سقى فيهما ساق ولما تبللا بأضيع من عينيك للدمع كلما ... تعرفت دارا أو توهمت منزلا','924','2109','0');
INSERT INTO [t17712] VALUES (2620,'٥١١ - دعوت امرأ أي امرئ فأجابني ... وكنت وإياه ملاذا وموئلا','924','2109','0');
INSERT INTO [t17712] VALUES (2621,'٥١٢ - عهدت مغيثا مغنيا من أجرته ... فلم أتخذ إلا فناءك موئلا','924','2109','0');
INSERT INTO [t17712] VALUES (2622,'٥١٣ - ما المجد إلا قد تبين أنه ... بندى وحلم لا يزال مؤثلا','924','2109','0');
INSERT INTO [t17712] VALUES (2623,'٥١٤ - أنجب أيام والداه به ... إذ نجلاه فنعم ما نجلا','924','2109','0');
INSERT INTO [t17712] VALUES (2624,'٥١٥ - يوما تراها كشبه أردية ال ... عصب ويوما أديمها نغلا','925','2109','0');
INSERT INTO [t17712] VALUES (2625,'٥١٦ - فأقبل على رهطي ورهطك نبتحث ... مساعينا حتى نرى كيف تفعلا','925','2109','0');
INSERT INTO [t17712] VALUES (2626,'٥١٧ - ألا يا عباد الله قلبي متيم ... بأحسن من صلى وأفضلهم نفلا','925','2109','0');
INSERT INTO [t17712] VALUES (2627,'٥١٨ - خلا أن حيا من قريش تفضلوا ... على الناس أو أن الأكارم نهشلا','925','2109','0');
INSERT INTO [t17712] VALUES (2628,'٥١٩ - الود أنت المستحقة صفوه ... مني وإن لم أرج منك نوالا','926','2109','0');
INSERT INTO [t17712] VALUES (2629,'٥٢٠ - فلم أر مثلها خباسة واحد ... ونهنهت نفسي بعد ما كدت أفعله','926','2109','0');
INSERT INTO [t17712] VALUES (2630,'٥٢١ - مزقوا جيب فتاتهم ... لم يبالوا حرمة الرجله','927','2109','0');
INSERT INTO [t17712] VALUES (2631,'٥٢٢ - أبى الله للشم الألاء كأنهم ... سيوف أجاد القين يوما صقالها','928','2109','0');
INSERT INTO [t17712] VALUES (2632,'٥٢٣ - وداهية من دواهي المنون ... يحسبها الناس لا فالها دفعت سنا برقها إذ بدت ... وكنت على الجهد حمالها','928','2109','0');
INSERT INTO [t17712] VALUES (2633,'٥٢٤ - عتوا إذ أجبناهم إلى السلم رأفة ... فسقناهم سوق البغاث الأجادل','928','2109','0');
INSERT INTO [t17712] VALUES (2634,'٥٢٥ - ألا يا اسقياني قبل غارة سنجال ... وقبل منايا باكرات وآجال','928','2109','0');
INSERT INTO [t17712] VALUES (2635,'٥٢٦ - وما هجرتك، لا، بل زادني شغفا ... هجر وبعد تراخى لا إلى أجل','929','2109','0');
INSERT INTO [t17712] VALUES (2636,'٥٢٧ - وهل يعمن من كان أحدث عهده ... ثلاثين شهرا في ثلاثة أحوال','929','2109','0');
INSERT INTO [t17712] VALUES (2637,'٥٢٨ - فقالت سباك الله إنك فاضحي ... ألست ترى السمار والناس أحوالي','930','2109','0');
INSERT INTO [t17712] VALUES (2638,'٥٢٩ - إذا هي لم تستك بعود أراكة ... تنخل، فاستاكت به عود إسحل','930','2109','0');
INSERT INTO [t17712] VALUES (2639,'٥٣٠ - أغر الثنايا أحم اللثات ... يحسنها سوك الإسحل','931','2109','0');
INSERT INTO [t17712] VALUES (2640,'٥٣١ - أجبيل إن أباك كارب يومه ... فإذا دعيت إلى العظائم فاعجل','931','2109','0');
INSERT INTO [t17712] VALUES (2641,'٥٣٢ - وإنا لنرجو عاجلا منك مثل ما ... رجوناه قدما من ذويك الأفاضل','931','2109','0');
INSERT INTO [t17712] VALUES (2642,'٥٣٣ - رب رفد هرقته ذلك اليو ... م وأسرى من معشر أقيال','931','2109','0');
INSERT INTO [t17712] VALUES (2643,'٥٣٤ - رب رفد هرقته ذلك اليو ... م وأسرى من معشر أقتال','932','2109','0');
INSERT INTO [t17712] VALUES (2644,'٥٣٥ - غير ميل ولا عواوير في الهيجا ... ولا عزل ولا أكفال','932','2109','0');
INSERT INTO [t17712] VALUES (2645,'٥٣٦ - هوينني وهويت الغانيات إلى ... أن شبت فانصرفت عنهن آمالي','932','2109','0');
INSERT INTO [t17712] VALUES (2646,'٥٣٧ - ظني بهم كعسى وهم بتنوفة ... يتنازعون جوائز الأمثال','933','2109','0');
INSERT INTO [t17712] VALUES (2647,'٥٣٨ - ولكنما أسعى لمجد مؤثل ... وقد يدرك المجد المؤثل أمثالي','933','2109','0');
INSERT INTO [t17712] VALUES (2648,'٥٣٩ - لأجهدن فإما درء واقعة ... تخشى وإما بلوغ السؤل والأمل','933','2109','0');
INSERT INTO [t17712] VALUES (2649,'٥٤٠ - إلى ماجد الآباء قرم عثمثم ... إلى عطن رحب المباءة آهل','933','2109','0');
INSERT INTO [t17712] VALUES (2650,'٥٤١ - ولما أبى إلا جماحا فؤاده ... ولم يسل عن ليلى بمال ولا أهل تسلى بأخرى غيرها فإذا التي ... تسلى بها تغري بليلى ولا تسلي','934','2109','0');
INSERT INTO [t17712] VALUES (2651,'٥٤٢ - لات هنا ذكرى جبيرة أم من ... جاء منها بطائف الأهوال','934','2109','0');
INSERT INTO [t17712] VALUES (2652,'٥٤٣ - ملك الخورنق والسدير ودانه ... ما بين حمير أهلها وأوال','935','2109','0');
INSERT INTO [t17712] VALUES (2653,'٥٤٤ - أيا طعنة ما شيخ ... كبير يفن بالي تقيم المأتم الأعلى ... على جهد وإعوال ولولا نبل عوض في ... أعالي وأوصالي لطاعنت صدور الخي ... ل طعنا ليس بالآلي','936','2109','0');
INSERT INTO [t17712] VALUES (2654,'٥٤٥ - لو اعتصمت بنا لم تعتصم بعدا ... بل أولياء كفاة غير أوكال','937','2109','0');
INSERT INTO [t17712] VALUES (2655,'٥٤٦ - وما هو من يأسو الكلوم وتتقى ... به نائبات الدهر كالدائم البخل','937','2109','0');
INSERT INTO [t17712] VALUES (2656,'٥٤٧ - ويوما على ظهر الكثيب تعذرت ... علي وآلت حلفة لم تحلل','937','2109','0');
INSERT INTO [t17712] VALUES (2657,'٥٤٨ - هلا سألت وخبر قوم عندهم ... وشفاء غيك خابرا أن تسألي','938','2109','0');
INSERT INTO [t17712] VALUES (2658,'٥٤٩ - فيا رب يوم قد لهوت وليلة ... بآنسة كأنها خط تمثال','939','2109','0');
INSERT INTO [t17712] VALUES (2659,'٥٥٠ - لن تزالوا كذلكم ثم لا زلت ... لكم خالدا خلود الجبال','939','2109','0');
INSERT INTO [t17712] VALUES (2660,'٥٥١ - حسن فعلا لقاء ذي الثروة الممل ... ق بالبشر والعطاء الجزيل','940','2109','0');
INSERT INTO [t17712] VALUES (2661,'٥٥٢ - ولا يبادر في الشتاء وليدنا ... ألقدر ينزلها بغير جعال','940','2109','0');
INSERT INTO [t17712] VALUES (2662,'٥٥٣ - ألا لا أرى إثنين أحسن شيمة ... على حدثان الدهر مني ومن جمل','940','2109','0');
INSERT INTO [t17712] VALUES (2663,'٥٥٤ - ولن يلبث الجهال أن يتهضموا ... أخا الحلم ما لم يستعن بجهول','940','2109','0');
INSERT INTO [t17712] VALUES (2664,'٥٥٥ - فلا تعجلي يا عز أن تتفهمي ... بنصح أتى الواشون أم بحبول','941','2109','0');
INSERT INTO [t17712] VALUES (2665,'٥٥٦ - إذا قلت يا نومان لم يجهل الذي ... أريد ولم يأخذ بشيء سوى حجلي','941','2109','0');
INSERT INTO [t17712] VALUES (2666,'٥٥٧ - يا خليلي أربعا واستخبرا ال ... منزل الدارس عن حي حلال مثل سحق البرد عفى بعدك ال ... قطر مغناه وتأويب الشمال','941','2109','0');
INSERT INTO [t17712] VALUES (2667,'٥٥٨ - منت لك أن تلاقيني المنايا ... أحاد أحاد في شهر حلال','941','2109','0');
INSERT INTO [t17712] VALUES (2668,'٥٥٩ - خالفاني ولم أخالف خلي ... ي ولا خير في خلاف الخليل','942','2109','0');
INSERT INTO [t17712] VALUES (2669,'٥٦٠ - فإن تك فقعس بانت وبنا ... فنعم ذوو مجاملة الخليل','942','2109','0');
INSERT INTO [t17712] VALUES (2670,'٥٦١ - أو يكن طبك الدلال فلو في ... سالف الدهر والسنين الخوالي','942','2109','0');
INSERT INTO [t17712] VALUES (2671,'٥٦٢ - جاءوا بجيش لو قيس معرسه ... ما كان إلا كمعرس الدئل','942','2109','0');
INSERT INTO [t17712] VALUES (2672,'٥٦٣ - بتنا بتدورة يضيء وجوهنا ... دسم السليط يضيء فوق ذبال','943','2109','0');
INSERT INTO [t17712] VALUES (2673,'٥٦٤ - سيصبح فوقي أقتم الريش واقعا ... بقالي قلا أو من وراء دبيل','943','2109','0');
INSERT INTO [t17712] VALUES (2674,'٥٦٥ - ليس حي على المنون بخال ... فلوى ذروة فجنبي ذيال','943','2109','0');
INSERT INTO [t17712] VALUES (2675,'٥٦٦ - ألا لا بارك الله في سهيل ... إذا ما الله بارك في الرجال','944','2109','0');
INSERT INTO [t17712] VALUES (2676,'٥٦٧ - خناثى يأكلون التمر ليسوا ... بزوجات يلدن ولا رجال','944','2109','0');
INSERT INTO [t17712] VALUES (2677,'٥٦٨ - نصحت بني عوف فلم يتقبلوا ... رسولي ولم تنجح لديهم رسائلي','945','2109','0');
INSERT INTO [t17712] VALUES (2678,'٥٦٩ - فما كنت ضفاطا ولكن راكبا ... أناخ قليلا فوق ظهر سبيل','945','2109','0');
INSERT INTO [t17712] VALUES (2679,'٥٧٠ - لله در أنو شروان من رجل ... ما كان أعرفه بالدون والسفل','945','2109','0');
INSERT INTO [t17712] VALUES (2680,'٥٧١ - أبيتم قبول السلم منا فكدتم ... لدى الحرب أن تغنوا السيوف عن السل','945','2109','0');
INSERT INTO [t17712] VALUES (2681,'٥٧٢ - سيوشك أن تنيخ إلى كريم ... ينالك بالندى قبل السؤال','946','2109','0');
INSERT INTO [t17712] VALUES (2682,'٥٧٣ - فأخذت أسأل والرسوم تجيبني ... إلا اعتبار إجابة وسؤال','946','2109','0');
INSERT INTO [t17712] VALUES (2683,'٥٧٤ - فلو مت في يوم ولم آت عجزة ... يضعفني فيها امرؤ غير عاقل لأكرم بها من ميتة إن لقيتها ... أطاعن فيها كل خرق منازل','946','2109','0');
INSERT INTO [t17712] VALUES (2684,'٥٧٥ - وما لكم والفرط لا تقربونه ... وقد خلته أدنى مرد لعاقل','946','2109','0');
INSERT INTO [t17712] VALUES (2685,'٥٧٦ - فرشني بخير لا أكونن ومدحتي ... كناحت- يوما- صخرة بعسيل','946','2109','0');
INSERT INTO [t17712] VALUES (2686,'٥٧٧ - ندمت على ما فاتني يوم بنتم ... فيا حسرتا أن لا يرين عويلي','947','2109','0');
INSERT INTO [t17712] VALUES (2687,'٥٧٨ - علين بكديون وأبطن كرة ... فهن إضاء صافيات الغلائل','947','2109','0');
INSERT INTO [t17712] VALUES (2688,'٥٧٩ - أما تنفك تركبني بلومى ... لهجت بها كما لهج الفصيل','947','2109','0');
INSERT INTO [t17712] VALUES (2689,'٥٨٠ - وجدنا نهشلا فضلت فقيما ... كفضل ابن المخاض على الفصيل','947','2109','0');
INSERT INTO [t17712] VALUES (2690,'٥٨١ - ألا إنما المستوجبون تفضلا ... بدارا إلى نيل التقدم في الفصل','947','2109','0');
INSERT INTO [t17712] VALUES (2691,'٥٨٢ - أصبح الدهر وقد ألوى بهم ... غير تقوالك من قيل وقال','948','2109','0');
INSERT INTO [t17712] VALUES (2692,'٥٨٣ - جزيتك ضعف الود لما استثبته ... وما إن جزاك الضعف من أحد قبلي','948','2109','0');
INSERT INTO [t17712] VALUES (2693,'٥٨٤ - لقد ظفر الزوار أقفية العدا ... بما جاوز الآمال مل أسر والقتل','948','2109','0');
INSERT INTO [t17712] VALUES (2694,'٥٨٥ - نظرت إليها والنجوم كأنها ... مصابيح رهبان تشب لقفال','948','2109','0');
INSERT INTO [t17712] VALUES (2695,'٥٨٦ - كلما نادى مناد منهم ... يا لتيم الله قلنا يا لمال','949','2109','0');
INSERT INTO [t17712] VALUES (2696,'٥٨٧ - المن للذم داع بالعطاء فلا ... تمنن فتلقى بلا حمد ولا مال','949','2109','0');
INSERT INTO [t17712] VALUES (2697,'٥٨٨ - قعدت له وصحبتي بين ضارج ... وبين العذيب بعد ما متأملي','950','2109','0');
INSERT INTO [t17712] VALUES (2698,'٥٨٩ - كل أمر مباعد أو مدان ... فمنوط بحكمة المتعالي','950','2109','0');
INSERT INTO [t17712] VALUES (2699,'٥٩٠ - هؤلا ثم هاؤلائك ... أعطيت نعالا محذوة بنعال','950','2109','0');
INSERT INTO [t17712] VALUES (2700,'٥٩١ - عدو عينيك وشانيهما ... أصبح مشغول بمشغول','951','2109','0');
INSERT INTO [t17712] VALUES (2701,'٥٩٢ - قومي اللذو بعكاظ طيروا شررا ... من روس قومك ضربا بالمصاقيل','951','2109','0');
INSERT INTO [t17712] VALUES (2702,'٥٩٣ - فرأيتنا ما بيننا من حاجز ... إلا المجن ونصل أبيض مصقل','952','2109','0');
INSERT INTO [t17712] VALUES (2703,'٥٩٤ - فإن يك يومي قد دنا وأخاله ... كواردة يوما إلى ظمء منهل فقبلي مات الخالدان كلاهما ... عميد بني جحوان وابن المضلل','952','2109','0');
INSERT INTO [t17712] VALUES (2704,'٥٩٥ - إن يمس نشوان بمصروفة ... منها بري وعلى مرجل لا تقه الموت وقياته ... خط له ذلك في المحبل','952','2109','0');
INSERT INTO [t17712] VALUES (2705,'٥٩٦ - وشوهاء تعدو بي إلى صارخ الوغى ... بمستلئم مثل الفتيق المرحل','952','2109','0');
INSERT INTO [t17712] VALUES (2706,'٥٩٧ - إذا فاقد خطباء فرخين رجعت ... ذكرت سليمى في الخليط المزايل','952','2109','0');
INSERT INTO [t17712] VALUES (2707,'٥٩٨ - وإن حديثا منك لو تعلمينه ... جنى النحل في ألبان عوذ مطافل','953','2109','0');
INSERT INTO [t17712] VALUES (2708,'٥٩٩ - رحلت إليك من جنفاء حتى ... أنخت فناء بيتك بالمطالي','954','2109','0');
INSERT INTO [t17712] VALUES (2709,'٦٠٠ - تصد وتبدي عن أسيل وتتقي ... بناظرة من وحش وجرة مطفل','954','2109','0');
INSERT INTO [t17712] VALUES (2710,'٦٠١ - حبذا الصبر شيمة لامرئ را ... م مباراة مولع بالمعالي','955','2109','0');
INSERT INTO [t17712] VALUES (2711,'٦٠٢ - بئستم وخلتم أنه ليس ناصر ... فبوئتم من نصرنا خير معقل','955','2109','0');
INSERT INTO [t17712] VALUES (2712,'٦٠٣ - فمثلك بكرا ... ... ... ذي تمائم مغيل','955','2109','0');
INSERT INTO [t17712] VALUES (2713,'٦٠٤ - مطافيل أبكار حديث نتاجها ... يشاب بماء مثل ماء المفاصل','955','2109','0');
INSERT INTO [t17712] VALUES (2714,'٦٠٥ - أتت ذكر عودن أحشاء قلبه ... خفوقا ورفضات الهوى في المفصال','956','2109','0');
INSERT INTO [t17712] VALUES (2715,'٦٠٦ - أبت أجأ أن تسلم العام جارها ... فمن شاء فلينهض لها من مقاتل','956','2109','0');
INSERT INTO [t17712] VALUES (2716,'٦٠٧ - أصاح ترى برقا أريك وميضه ... كلمع اليدين في حبي مكلل','956','2109','0');
INSERT INTO [t17712] VALUES (2717,'٦٠٨ - إما تري رأسي تغير لونه ... شمطا فأصبح كالثغام الممحل','957','2109','0');
INSERT INTO [t17712] VALUES (2718,'٦٠٩ - وما كنت ذا نيرب فيهم ... ولا منمش فيهم منمل','957','2109','0');
INSERT INTO [t17712] VALUES (2719,'٦١٠ - فظلوا ومنهم سابق دمعه له ... وآخر يثني دمعة العين بالمهل','958','2109','0');
INSERT INTO [t17712] VALUES (2720,'٦١١ - وليس بذي رمح فيطعنني به ... وليس بذي سيف وليس بنبال','958','2109','0');
INSERT INTO [t17712] VALUES (2721,'٦١٢ - إني بحبلك واصل حبلي ... وبريش نبلك رائش نبلي','959','2109','0');
INSERT INTO [t17712] VALUES (2722,'٦١٣ - طوى الجديدان ما قد كنت أنشره ... وأنكرتني ذوات الأعين النجل','959','2109','0');
INSERT INTO [t17712] VALUES (2723,'٦١٤ - وإذا الحرب شمرت لم تكن، كي ... حين تدعو الكماة فيها نزال','959','2109','0');
INSERT INTO [t17712] VALUES (2724,'٦١٥ - وقد علمت سلامة أن سيفي ... كريه كلما دعيت نزال','960','2109','0');
INSERT INTO [t17712] VALUES (2725,'٦١٦ - ردوا فو الله لا ذدناكم أبدا ... ما دام في مائنا ورد لنزال','960','2109','0');
INSERT INTO [t17712] VALUES (2726,'٦١٧ - فلما رأونا باديا ركباتنا ... على موطن لا تخلط الجد بالهزل','960','2109','0');
INSERT INTO [t17712] VALUES (2727,'٦١٨ - رأت مر السنين أخذن مني ... كما أخذ السرار من الهلال','960','2109','0');
INSERT INTO [t17712] VALUES (2728,'٦١٩ - أروح ولم أحدث لليلى زيارة ... لبئس إذن راعي المودة والوصل','960','2109','0');
INSERT INTO [t17712] VALUES (2729,'٦٢٠ - ألا هل لهذا الدهر من متعلل ... عن الناس مهما شاء بالناس يفعل وهذا ردائي عنده يستعيره ... ليسلبني عزي أمال بن حنظل','961','2109','0');
INSERT INTO [t17712] VALUES (2730,'٦٢١ - ألا إنني شربت أسود حالكا ... ألا بجلي من الشراب ألا بجل','961','2109','0');
INSERT INTO [t17712] VALUES (2731,'٦٢٢ - وتداعى منخراه بدم ... مثل ما أثمر حماض الجبل','961','2109','0');
INSERT INTO [t17712] VALUES (2732,'٦٢٣ - وسميت كعبا بشر العظام ... وكان أبوك يسمى الجعل','962','2109','0');
INSERT INTO [t17712] VALUES (2733,'٦٢٤ - لقتل بني أسد ربها ... ألا كل شيء سواه جلل','962','2109','0');
INSERT INTO [t17712] VALUES (2734,'٦٢٥ - ثم أضحوا لعب الدهر بهم ... وكذاك الدهر حالا بعد حال','962','2109','0');
INSERT INTO [t17712] VALUES (2735,'٦٢٦ - لم يك الحق سوى أن هاجه ... رسم دار قد تعفت بالطلل','962','2109','0');
INSERT INTO [t17712] VALUES (2736,'٦٢٧ - ذكرت ابن عباس بباب ابن عامر ... وما مر من يومي ذكرت وما فضل','962','2109','0');
INSERT INTO [t17712] VALUES (2737,'٦٢٨ - أميران كانا صاحبي كلاهما ... فكلا جزاه الله عني بما فعل','963','2109','0');
INSERT INTO [t17712] VALUES (2738,'٦٢٩ - يفديك يا زرع أبي وخالي ... قد مر يومان وهذا الثالي وأنت بالهجران لا تبالي','963','2109','0');
INSERT INTO [t17712] VALUES (2739,'قافية الميم','965','0','0');
INSERT INTO [t17712] VALUES (2740,'١ - فما ترك الصنع الذي قد صنعته ... ولا الغيظ مني ليس جلدا وأعظما','965','2739','0');
INSERT INTO [t17712] VALUES (2741,'٢ - فهل لكم فيها إلي فإنني ... طبيب بما أعيا النطاسي حذيما','965','2739','0');
INSERT INTO [t17712] VALUES (2742,'٣ - فذلك إن يلق الكريهة يلقها ... حميدا وإن يستغن يوما فربما','965','2739','0');
INSERT INTO [t17712] VALUES (2743,'٤ - تأخرت أستبقي الحياة فلم أجد ... لنفسي حياة مثل أن أتقدما فلسنا على الأعقاب تدمى كلومنا ... ولكن على أقدامنا تقطر الدما','966','2739','0');
INSERT INTO [t17712] VALUES (2744,'٥ - أما والدماء المائرات تخالها ... على قنة العزى وبالنسر عندما','966','2739','0');
INSERT INTO [t17712] VALUES (2745,'٦ - لأورث بعدي سنة يقتدى بها ... وأجلو عمى ذي شبهة إن توهما','966','2739','0');
INSERT INTO [t17712] VALUES (2746,'٧ - هما إبلان فيهما ما علمتم ... فأدوهما إن شئتم أن نسالما','967','2739','0');
INSERT INTO [t17712] VALUES (2747,'٨ - خليلي هبا طالما قد رقدتما ... أجدكما لا تقضيان كراكما','967','2739','0');
INSERT INTO [t17712] VALUES (2748,'٩ - نودي قم واركبن بأهلك إن ... الله موف للناس ما زعما','967','2739','0');
INSERT INTO [t17712] VALUES (2749,'١٠ - رفوني وقالوا يا خويلد لا ترع ... فقلت- وأنكرت الوجوه- هم هم','967','2739','0');
INSERT INTO [t17712] VALUES (2750,'١١ - عليك بأوساط الأمور فإنها ... طريق إلى نهج الصواب قويم ولا تك فيها مفرطا أو مفرطا ... كلا طرفي قصد الأمور ذميم','968','2739','0');
INSERT INTO [t17712] VALUES (2751,'١٢ - للفتى عقل يعيش به ... حيث تهدي ساقه قدمه','968','2739','0');
INSERT INTO [t17712] VALUES (2752,'١٣ - إن الخليفة إن الله سربله ... لباس ملك به تزجى الخواتيم','968','2739','0');
INSERT INTO [t17712] VALUES (2753,'١٤ - لا ينعش الطرف إلا ما تخونه ... داع يناديه باسم الماء مبغوم','969','2739','0');
INSERT INTO [t17712] VALUES (2754,'١٥ - فإن الكثر أعياني قديما ... ولم أقتر لدن أني غلام','969','2739','0');
INSERT INTO [t17712] VALUES (2755,'١٦ - العاطفون تحين ما من عاطف ... والمسبغون يدا إذا ما أنعموا','969','2739','0');
INSERT INTO [t17712] VALUES (2756,'١٧ - باكرت حاجتها الدجاج بسحرة ... لأعل منها حين هب نيامها','969','2739','0');
INSERT INTO [t17712] VALUES (2757,'١٨ - أقضي اللبانة لا أفرط ريبة ... أو أن يلوم بحاجة لوامها','970','2739','0');
INSERT INTO [t17712] VALUES (2758,'١٩ - فإنا رأينا العرض أحوج ساعة ... إلى الصون من ريط يمان مسهم','970','2739','0');
INSERT INTO [t17712] VALUES (2759,'٢٠ - تمشي بها الدرماء تسحب قصبها ... كأن بطن حبلى ذات أونين متئم','970','2739','0');
INSERT INTO [t17712] VALUES (2760,'٢١ - فكلا أراهم أصبحوا يعقلونه ... صحيحات مال طالعات بمخرم','971','2739','0');
INSERT INTO [t17712] VALUES (2761,'٢٢ - وما الحرب إلا ما علمتم وذقتم ... وما هو عنها بالحديث المرجم','971','2739','0');
INSERT INTO [t17712] VALUES (2762,'٢٣ - يمينا لنعم السيدان وجدتما ... على كل حال من سحيل ومبرم','971','2739','0');
INSERT INTO [t17712] VALUES (2763,'٢٤ - وكان طوى كشحا على مستكنة ... فلا هو أبداها ولم يتقدم','971','2739','0');
INSERT INTO [t17712] VALUES (2764,'٢٥ - ومسكنها بين الفرات إلى اللوى ... إلى شعب ترعى بهن فعيهم','972','2739','0');
INSERT INTO [t17712] VALUES (2765,'٢٦ - وإلا فمن آل المرار فإنهم ... ملوك عظام من كرام أعاظم','972','2739','0');
INSERT INTO [t17712] VALUES (2766,'٢٧ - ثلاث مئين للملوك وفى بها ... ردائي وجلت عن وجوه الأهاتم','972','2739','0');
INSERT INTO [t17712] VALUES (2767,'٢٨ - تداعين باسم الشيب في متثلم ... جوانبه من بصرة وسلام','973','2739','0');
INSERT INTO [t17712] VALUES (2768,'٢٩ - ندمت على لسان كان مني ... فليت بأنه في جوف عكم','973','2739','0');
INSERT INTO [t17712] VALUES (2769,'٣٠ - وساغ لي الشراب وكنت قبلا ... أغص بنقطة الماء الحميم','973','2739','0');
INSERT INTO [t17712] VALUES (2770,'٣١ - نبئت عمرا غير شاكر نعمتي ... والكفر مخبثة لنفس المنعم','974','2739','0');
INSERT INTO [t17712] VALUES (2771,'٣٢ - غادرته جزر السباع ينشنه ... ما بين قلة رأسه والمعصم','974','2739','0');
INSERT INTO [t17712] VALUES (2772,'٣٣ - ذم المنازل بعد منزلة اللوى ... والعيش بعد أولئك الأيام','974','2739','0');
INSERT INTO [t17712] VALUES (2773,'٣٤ - في لجة غمرت أباك بحورها ... في الجاهلية كان والإسلام','974','2739','0');
INSERT INTO [t17712] VALUES (2774,'٣٥ - سيان كسر رغيفه ... أو كسر عظم من عظامه','975','2739','0');
INSERT INTO [t17712] VALUES (2775,'٣٦ - كانت فريضة ما تقول كما ... أن الزناء فريضة الرجم','975','2739','0');
INSERT INTO [t17712] VALUES (2776,'٣٧ - كفاك كف لا تليق درهما ... جودا وأخرى تعط بالسيف الدما','975','2739','0');
INSERT INTO [t17712] VALUES (2777,'٣٨ - فأصبحت بعد (خط) بهجتها ... كأن قفرا رسومها قلما','975','2739','0');
INSERT INTO [t17712] VALUES (2778,'٣٩ - كلا أخوينا ذو رجال كأنهم ... أسود الشرى من كل أغلب ضيغم','976','2739','0');
INSERT INTO [t17712] VALUES (2779,'٤٠ - كلا يومي أمامة يوم صد ... وإن لم نأتها إلا لماما','976','2739','0');
INSERT INTO [t17712] VALUES (2780,'٤١ - إلى الملك القرم وابن الهمام ... وليث الكتيبة في المزدحم وذا الرأي حين تغم الأمور ... بذات الصليل وذات اللجم','976','2739','0');
INSERT INTO [t17712] VALUES (2781,'٤٢ - عرضنا نزال فلم ينزلوا ... وكانت نزال عليهم أطم','976','2739','0');
INSERT INTO [t17712] VALUES (2782,'٤٣ - أولئك قومي إن هجوني هجوتهم ... وأعبد أن تهجى تميم بدارم','977','2739','0');
INSERT INTO [t17712] VALUES (2783,'٤٤ - تمخضت المنون له بيوم ... أنى ولكل حاملة تمام','977','2739','0');
INSERT INTO [t17712] VALUES (2784,'٤٥ - ينباع من ذفرى غضوب جسرة ... زيافة مثل الفنيق المكدم','977','2739','0');
INSERT INTO [t17712] VALUES (2785,'٤٦ - ولكن نصفا لو سببت وسبني ... بنو عبد شمس من مناف وهاشم','978','2739','0');
INSERT INTO [t17712] VALUES (2786,'٤٧ - قضى كل ذي دين فوفى غريمه ... وعزة ممطول معنى غريمها','978','2739','0');
INSERT INTO [t17712] VALUES (2787,'٤٨ - فلولا المزعجات من الليالي ... لما ترك القطا طيب المنام إذا قالت حذام فصدقوها ... فإن القول ما قالت حذام','979','2739','0');
INSERT INTO [t17712] VALUES (2788,'٤٩ - ومهما تكن عند امرئ من خليقة ... وإن خالها تخفى على الناس- تعلم','979','2739','0');
INSERT INTO [t17712] VALUES (2789,'٥٠ - أقول لهم بالشعب إذ يأسرونني ... ألم تيأسوا أني ابن فارس زهدم','980','2739','0');
INSERT INTO [t17712] VALUES (2790,'٥١ - وكنت إذا غمزت قناة قوم ... كسرت كعوبها أو تستقيما','980','2739','0');
INSERT INTO [t17712] VALUES (2791,'٥٢ - لاتنه عن خلق وتأتي مثله ... عار عليك إذا فعلت عظيم','981','2739','0');
INSERT INTO [t17712] VALUES (2792,'٥٣ - نصلي للذي صلت قريش ... ونعبده وإن جحد العموم','982','2739','0');
INSERT INTO [t17712] VALUES (2793,'٥٤ - وإن مولاي ذو يعاتبني ... لا إحنة عنده ولا جرمه ينصرني منك غير معتذر ... يرمي ورائي بامسهم وامسلمه','982','2739','0');
INSERT INTO [t17712] VALUES (2794,'٥٥ - لا طيب للعيش ما دامت منغصة ... لذاته بادكار الموت والهرم','982','2739','0');
INSERT INTO [t17712] VALUES (2795,'٥٦ - لا تقربن الدهر آل مطرف ... إن ظالما أبدا وإن مظلوما','983','2739','0');
INSERT INTO [t17712] VALUES (2796,'٥٧ - ويوما توافينا بوجه مقسم ... كأن ظبية تعطو إلى وارق السلم','983','2739','0');
INSERT INTO [t17712] VALUES (2797,'٥٨ - كأني من أخبار إن ولم يجز ... له أحد في النحو أن يتقدما','983','2739','0');
INSERT INTO [t17712] VALUES (2798,'٥٩ - ولقد علمت لتأتين منيتي ... إن المنايا لا تطيش سهامها','984','2739','0');
INSERT INTO [t17712] VALUES (2799,'٦٠ - تنكرت منا بعد معرفة لمي ... وبعد التصافي والشباب المكرم','984','2739','0');
INSERT INTO [t17712] VALUES (2800,'٦١ - كلت كفيه توالي دائما ... بجيوش من عقاب ونعم','984','2739','0');
INSERT INTO [t17712] VALUES (2801,'٦٢ - أرقني الليلة برق بالتهم ... يالك برقا من يشقه لا يلم','985','2739','0');
INSERT INTO [t17712] VALUES (2802,'٦٣ - وا حر قلباه ممن قلبه شبم ... ومن بجسمي وحالي عنده سقم','985','2739','0');
INSERT INTO [t17712] VALUES (2803,'٦٤ - وتضيء في وجه الظلام منيرة ... كجمانة البحري سل نظامها','986','2739','0');
INSERT INTO [t17712] VALUES (2804,'٦٥ - لعل الله فضلكم علينا ... بشيء أن أمكم شريم','986','2739','0');
INSERT INTO [t17712] VALUES (2805,'٦٦ - إني حلفت برافعين أكفهم ... بين الحطيم وبين حوضي زمزم','987','2739','0');
INSERT INTO [t17712] VALUES (2806,'٦٧ - أتاركة تدللها قطام ... رضينا بالتحية والسلام','987','2739','0');
INSERT INTO [t17712] VALUES (2807,'٦٨ - الليل والخيل والبيداء تعرفني ... والسيف والرمح والقرطاس والقلم','987','2739','0');
INSERT INTO [t17712] VALUES (2808,'٦٩ - أشارت بطرف العين خيفة أهلها ... إشارة محزون ولم تتكلم فأيقنت أن الطرف قد قال مرحبا ... وأهلا وسهلا بالحبيب المتيم','987','2739','0');
INSERT INTO [t17712] VALUES (2809,'٧٠ - تزود منا بين أذناه طعنة ... دعته إلى هابي التراب عقيم','988','2739','0');
INSERT INTO [t17712] VALUES (2810,'٧١ - ثم انقضت تلك السنون وأهلها ... فكأنها وكأنهم أحلام','988','2739','0');
INSERT INTO [t17712] VALUES (2811,'٧٢ - فلا لغو ولا تأثيم فيها ... وما فاهوا به أبدا مقيم','989','2739','0');
INSERT INTO [t17712] VALUES (2812,'٧٣ - سلام الله يا مطر عليها ... وليس عليك يا مطر السلام','989','2739','0');
INSERT INTO [t17712] VALUES (2813,'٧٤ - فغدت كلا الفرجين تحسب أنه ... مولى المخافة خلفها وأمامها','989','2739','0');
INSERT INTO [t17712] VALUES (2814,'٧٥ - تولى قتال المارقين بنفسه ... وقد أسلماه، مبعد وحميم','990','2739','0');
INSERT INTO [t17712] VALUES (2815,'٧٦ - ما برئت من ريبة وذم ... في حربنا إلا بنات العم','990','2739','0');
INSERT INTO [t17712] VALUES (2816,'٧٧ - ندم البغاة ولات ساعة مندم ... والبغي مرتع مبتغيه وخيم','991','2739','0');
INSERT INTO [t17712] VALUES (2817,'٧٨ - وكنت أرى زيدا كما قيل سيدا ... إذا أنه عبد القفا واللهازم','991','2739','0');
INSERT INTO [t17712] VALUES (2818,'٧٩ - على حالة لو أن في القوم حاتما ... على جوده لضن بالماء حاتم','992','2739','0');
INSERT INTO [t17712] VALUES (2819,'٨٠ - فيها اثنتان وأربعون حلوبة ... سودا كخافية الغراب الأسحم','992','2739','0');
INSERT INTO [t17712] VALUES (2820,'٨١ - لا يهولنك اصطلاء لظى الحر ... ب فمحذورها كأن قد ألما','993','2739','0');
INSERT INTO [t17712] VALUES (2821,'٨٢ - بل بلد ملء الفجاج قتمه ... لا يشترى كتانه وجهرمه','993','2739','0');
INSERT INTO [t17712] VALUES (2822,'٨٣ - فطلقها فلست لها بكفء ... وإلا يعل مفرقك الحسام','993','2739','0');
INSERT INTO [t17712] VALUES (2823,'٨٤ - وإن أتاه خليل يوم مسغبة ... يقول: لا غائب مالي ولا حرم','994','2739','0');
INSERT INTO [t17712] VALUES (2824,'٨٥ - ومن يقترب منا ويخضع نؤوه ... ولا يخش ظلما ما أقام ولا هضما','994','2739','0');
INSERT INTO [t17712] VALUES (2825,'٨٦ - ولقد نزلت فلا تظني غيره ... مني بمنزلة المحب المكرم','995','2739','0');
INSERT INTO [t17712] VALUES (2826,'٨٧ - متى تقول القلص الرواسما ... يدنين أم قاسم وقاسما','995','2739','0');
INSERT INTO [t17712] VALUES (2827,'٨٨ - أبعد بعد تقول الدار جامعة ... شملي بهم؟ أم تقول البعد محتوما','996','2739','0');
INSERT INTO [t17712] VALUES (2828,'٨٩ - شتان هذا والعناق والنوم ... والمشرب البارد في ظل الدوم','996','2739','0');
INSERT INTO [t17712] VALUES (2829,'٩٠ - لشتان ما بين اليزيدين في الندى ... يزيد سليم والأغر ابن حاتم','996','2739','0');
INSERT INTO [t17712] VALUES (2830,'٩١ - أظلوم إن مصابكم رجلا ... أهدى السلام تحية ظلم','997','2739','0');
INSERT INTO [t17712] VALUES (2831,'٩٢ - قضى كل ذي دين فوفى غريمه ... وعزة ممطول معنى غريمها','997','2739','0');
INSERT INTO [t17712] VALUES (2832,'٩٣ - أو عدني بالسجن والأداهم ... رجلي فرجلي شثنة المناسم','998','2739','0');
INSERT INTO [t17712] VALUES (2833,'٩٤ - وندمان يزيد الكأس طيبا ... سقيت وقد تغورت النجوم','998','2739','0');
INSERT INTO [t17712] VALUES (2834,'٩٥ - بأبه اقتدى عدي في الكرم ... ومن يشابه أبه فما ظلم','999','2739','0');
INSERT INTO [t17712] VALUES (2835,'٩٦ - غير لاه عداك فاطرح اللهو ... ولا تغترر بعارض سلم','999','2739','0');
INSERT INTO [t17712] VALUES (2836,'٩٧ - ينام بإحدى مقلتيه ويتقي ... بأخرى المنايا فهو يقظان نائم','1000','2739','0');
INSERT INTO [t17712] VALUES (2837,'٩٨ - فكيف إذا مررت بدار قوم ... وجيران لنا كانوا كرام','1000','2739','0');
INSERT INTO [t17712] VALUES (2838,'٩٩ - أكثرت في العذل ملحا دائما ... لا تكثرن إني عسيت صائما','1001','2739','0');
INSERT INTO [t17712] VALUES (2839,'١٠٠ - ما أعطياني ولا سألتهما ... إلا وإني لحاجزي كرمي','1002','2739','0');
INSERT INTO [t17712] VALUES (2840,'١٠١ - ألا ارعواء لمن ولت شبيبته ... وآذنت بمشيب بعده هرم','1002','2739','0');
INSERT INTO [t17712] VALUES (2841,'١٠٢ - فلا تعدد المولى شريكك في الغنى ... ولكنما المولى شريكك في العدم','1003','2739','0');
INSERT INTO [t17712] VALUES (2842,'١٠٣ - فلم يدر إلا الله ماهيجت لنا ... عشية آناء الديار وشامها','1003','2739','0');
INSERT INTO [t17712] VALUES (2843,'١٠٤ - تزودت من ليلى بتكليم ساعة ... فما زاد إلا ضعف ما بي كلامها','1004','2739','0');
INSERT INTO [t17712] VALUES (2844,'١٠٥ - ولو أن مجدا أخلد الدهر واحدا ... من الناس أبقى مجده الدهر مطعما','1004','2739','0');
INSERT INTO [t17712] VALUES (2845,'١٠٦ - تمرون الديار ولم تعوجوا ... كلامكم علي إذا حرام','1005','2739','0');
INSERT INTO [t17712] VALUES (2846,'١٠٧ - وأغفر عوراء الكريم ادخاره ... وأعرض عن شتم اللئيم تكرما','1005','2739','0');
INSERT INTO [t17712] VALUES (2847,'١٠٨ - لا يركنن أحد إلى الإحجام ... يوم الوغى متخوفا لحمام','1006','2739','0');
INSERT INTO [t17712] VALUES (2848,'١٠٩ - ولقد أراني للرماح دريئة ... من عن يميني تارة وأمامي','1006','2739','0');
INSERT INTO [t17712] VALUES (2849,'١١٠ - فإن الحمر من شر المطايا ... كما الحبطات شر بني تميم','1006','2739','0');
INSERT INTO [t17712] VALUES (2850,'١١١ - وأعلم أنني وأبا حميد ... كما النشوان والرجل الحليم','1007','2739','0');
INSERT INTO [t17712] VALUES (2851,'١١٢ - ماوي يا ربتما غارة ... شعواء كاللذعة بالميسم','1008','2739','0');
INSERT INTO [t17712] VALUES (2852,'١١٣ - وننصر مولانا ونعلم أنه ... كما الناس مجروم عليه وجارم','1008','2739','0');
INSERT INTO [t17712] VALUES (2853,'١١٤ - مشين كما اهتزت رماح تسفهت ... أعاليها مر الرياح النواسم','1009','2739','0');
INSERT INTO [t17712] VALUES (2854,'١١٥ - ألا تسألون الناس أيي وأيكم ... غداة التقينا كان خيرا وأكرما','1009','2739','0');
INSERT INTO [t17712] VALUES (2855,'١١٦ - فريشي منكم وهواي معكم ... وإن كانت زيارتكم لماما','1010','2739','0');
INSERT INTO [t17712] VALUES (2856,'١١٧ - ولئن حلفت على يديك لأحلفن ... بيمين- أصدق من يمينك- مقسم','1011','2739','0');
INSERT INTO [t17712] VALUES (2857,'١١٨ - كأن برذون- أبا عصام ... زيد حمار دق باللجام','1011','2739','0');
INSERT INTO [t17712] VALUES (2858,'١١٩ - حتى تهجر في الرواح وهاجها ... طلب المعقب حقه المظلوم','1012','2739','0');
INSERT INTO [t17712] VALUES (2859,'١٢٠ - وكم مالئ عينيه من شيء غيره ... إذا راح نحو الجمرة البيض كالدمى','1012','2739','0');
INSERT INTO [t17712] VALUES (2860,'١٢١ - وقال نبي المسلمين: تقدموا ... وأحبب إلينا أن تكون المقدما','1013','2739','0');
INSERT INTO [t17712] VALUES (2861,'١٢٢ - إني إذا ما حدث ألما ... أقول: يا اللهم يا اللهما','1014','2739','0');
INSERT INTO [t17712] VALUES (2862,'١٢٣ - يحسبه الجاهل ما لم يعلما ... شيخا على كرسيه معمما','1014','2739','0');
INSERT INTO [t17712] VALUES (2863,'١٢٤ - فإن يهلك أبو قابوس يهلك ... ربيع الناس والبلد الحرام ونأخذ بعده بذناب عيش ... أجب الظهر ليس له سنام','1015','2739','0');
INSERT INTO [t17712] VALUES (2864,'١٢٥ - أتوا ناري فقلت منون أنتم ... فقالوا الجن قلت: عموا ظلاما فقلت: إلى الطعام فقال منهم ... زعيم: نحسد الإنس الطعاما','1016','2739','0');
INSERT INTO [t17712] VALUES (2865,'١٢٦ - بني إن البر شيء هين ... المنطق الطيب والطعيم','1016','2739','0');
INSERT INTO [t17712] VALUES (2866,'١٢٧ - لا يبعد الله التلبب وال ... غارات إذ قال الخميس نعم','1016','2739','0');
INSERT INTO [t17712] VALUES (2867,'١٢٨ - وأنت التي حببت شغبا إلى بدا ... إلي وأوطاني بلاد سواهما حللت بهذا حلة ثم حلة ... بهذا فطاب الواديان كلاهما','1017','2739','0');
INSERT INTO [t17712] VALUES (2868,'١٢٩ - ولو أنها عصفورة لحسبتها ... مسومة تدعو عبيدا وأزنما','1017','2739','0');
INSERT INTO [t17712] VALUES (2869,'١٣٠ - أقول له ارحل لا تقيمن عندنا ... وإلا فكن في السر والجهر مسلما','1018','2739','0');
INSERT INTO [t17712] VALUES (2870,'١٣١ - إذا المرء عينا قر بالعيش مثريا ... ولم يعن بالإحسان كان مذمما','1018','2739','0');
INSERT INTO [t17712] VALUES (2871,'١٣٢ - تحلم عن الأدنين واستبق ودهم ... ولن تستطيع الأمر حتى تحلما','1018','2739','0');
INSERT INTO [t17712] VALUES (2872,'١٣٣ - إن الذين قتلتم أمس سيدهم ... لا تحسبوا ليلهم عن ليلكم ناما','1019','2739','0');
INSERT INTO [t17712] VALUES (2873,'١٣٤ - فلا تشلل يد فتكت بعمرو ... فإنك لن تذل ولن تضاما','1019','2739','0');
INSERT INTO [t17712] VALUES (2874,'١٣٥ - بآية يقدمون الخيل شعثا ... كأن على سنابكها مداما','1019','2739','0');
INSERT INTO [t17712] VALUES (2875,'١٣٦ - ألا من مبلغ عني تميما ... بآية ما يحبون الطعاما','1019','2739','0');
INSERT INTO [t17712] VALUES (2876,'١٣٧ - لا يلفك الراجوك إلا مظهرا ... خلق الكرام ولو تكون عديما','1020','2739','0');
INSERT INTO [t17712] VALUES (2877,'١٣٨ - إن تغفر اللهم تغفر جما ... وأي عبد لك لا ألما','1020','2739','0');
INSERT INTO [t17712] VALUES (2878,'١٣٩ - سقتها الرواعد من صيف ... وإن من خريف فلن يعدما','1020','2739','0');
INSERT INTO [t17712] VALUES (2879,'١٤٠ - فإن أنت لاقيت في نجدة ... فلا تتهيبك أن تقدما','1021','2739','0');
INSERT INTO [t17712] VALUES (2880,'١٤١ - فأقسم أن لو التقينا وأنتم ... لكان لكم يوم من الشر مظلم','1021','2739','0');
INSERT INTO [t17712] VALUES (2881,'١٤٢ - فإن ترفقي يا هند فالرفق أيمن ... وإن تخرقي يا هند فالخرق أشأم فأنت طلاق والطلاق عزيمة ... ثلاث ومن يخرق أعق وأظلم فبيني بها أن كنت غير رفيقة ... وما لا مرئ بعد الثلاث مقدم','1021','2739','0');
INSERT INTO [t17712] VALUES (2882,'١٤٣ - أما والذي لا يعلم الغيب غيره ... ومن هو يحيي العظم وهو رميم لقد كنت أطوي البطن والزاد يشتهى ... محافظة من أن يقال لئيم وإني لأستحيي رفيقي ودونه ... ودون يدي داجي الظلام بهيم','1024','2739','0');
INSERT INTO [t17712] VALUES (2883,'١٤٤ - أنيخت فألقت بلدة فوق بلدة ... قليل بها الأصوات إلا بغامها','1024','2739','0');
INSERT INTO [t17712] VALUES (2884,'١٤٥ - إذا هملت عيني لها قال صاحبي ... بمثلك- هذا- لوعة وغرام','1025','2739','0');
INSERT INTO [t17712] VALUES (2885,'١٤٦ - وكائن لنا فضلا عليكم ومنة ... قديما ولا تدرون ما من منعم','1025','2739','0');
INSERT INTO [t17712] VALUES (2886,'١٤٧ - ألا ياسنا برق على قلل الحمى ... لهنك من برق علي كريم','1025','2739','0');
INSERT INTO [t17712] VALUES (2887,'١٤٨ - إذا ما خرجنا من دمشق فلا نعد ... لها أبدا ما دام فيها الجراضم','1025','2739','0');
INSERT INTO [t17712] VALUES (2888,'١٤٩ - إذا غاب عنكم أسود العين كنتم ... كراما وأنتم ما أقام ألائم','1026','2739','0');
INSERT INTO [t17712] VALUES (2889,'١٥٠ - فلا تبدها باللوم قبل سؤالها ... لعل لها عذر وأنت تلوم','1026','2739','0');
INSERT INTO [t17712] VALUES (2890,'١٥١ - صددت فأطولت الصدود وقلما ... وصال على طول الصدود يدوم','1026','2739','0');
INSERT INTO [t17712] VALUES (2891,'١٥٢ - وإن لساني شهدة يشتفى بها ... وهو على من صبه الله علقم','1027','2739','0');
INSERT INTO [t17712] VALUES (2892,'١٥٣ - وأنت الذي أخلفتني ما وعدتني ... وأشمت بي من كان فيك يلوم وأبرزتني للناس حتى تركتني ... لهم غرضا أرمى وأنت سليم فلو أن قولا يكلم الجسم قد بدا ... بجسمي من قول الوشاة كلوم','1027','2739','0');
INSERT INTO [t17712] VALUES (2893,'١٥٤ - لقد كان في حول ثواء ثويته ... تقضى لبانات ويسأم سائم','1028','2739','0');
INSERT INTO [t17712] VALUES (2894,'١٥٥ - وفاؤكما كالربع أشجاه طاسمه ... بأن تسعدا والدمع أشفاه ساجمه','1029','2739','0');
INSERT INTO [t17712] VALUES (2895,'١٥٦ - أيا جبلي نعمان بالله خليا ... نسيم الصبا يخلص إلي نسيمها','1029','2739','0');
INSERT INTO [t17712] VALUES (2896,'١٥٧ - ستعلم ليلى أي دين تداينت ... وأي غريم: في التقاضي غريمها','1029','2739','0');
INSERT INTO [t17712] VALUES (2897,'١٥٨ - زارت رويقة شعثا بعد ما هجعوا ... لدى نواحل في أرساغها الخدم فقمت للطيف مرتاعا فأرقني ... فقلت أهي سرت أم عادني حلم','1030','2739','0');
INSERT INTO [t17712] VALUES (2898,'١٥٩ - وما أصاحب من قوم فأذكرهم ... إلا يزيدهم حبا إلي هم','1030','2739','0');
INSERT INTO [t17712] VALUES (2899,'١٦٠ - أعن ترسمت من خرقاء منزلة ... ماء الصبابة من عينيك مسجوم','1030','2739','0');
INSERT INTO [t17712] VALUES (2900,'١٦١ - ما أطيب العيش لو أن الفتى حجر ... تنبو الحوادث عنه وهو ملموم','1031','2739','0');
INSERT INTO [t17712] VALUES (2901,'١٦٢ - يغضي حياء ويغضى من مهابته ... فما يكلم إلا حين يبتسم','1031','2739','0');
INSERT INTO [t17712] VALUES (2902,'١٦٣ - إن يستغيثوا بنا إن يذعروا يجدوا ... منا معاقل عز زانها كرم','1031','2739','0');
INSERT INTO [t17712] VALUES (2903,'١٦٤ - نطوف ما نطوف ثم نأوي ... ذوو الأموال منا والعديم إلى حفر أسافلهن جوف ... وأعلاهن صفاح مقيم','1032','2739','0');
INSERT INTO [t17712] VALUES (2904,'١٦٥ - فأصبح بطن مكة مقشعرا ... كأن الأرض ليس بها هشام','1032','2739','0');
INSERT INTO [t17712] VALUES (2905,'١٦٦ - ألا يا نخلة من ذات عرق ... عليك ورحمة الله السلام','1033','2739','0');
INSERT INTO [t17712] VALUES (2906,'١٦٧ - فلا وأبي لنأتيها جميعا ... ولو كانت بها عرب وروم','1033','2739','0');
INSERT INTO [t17712] VALUES (2907,'١٦٨ - كضرائر الحسناء قلن لوجهها ... حسدا وبغيا إنه لدميم','1033','2739','0');
INSERT INTO [t17712] VALUES (2908,'١٦٩ - جالت لتصرعني فقلت لها اقصري ... إني امرؤ صرعي عليك حرام','1034','2739','0');
INSERT INTO [t17712] VALUES (2909,'١٧٠ - ولقد علمت لتأتين منيتي ... إن المنايا لا تطيش سهامها','1034','2739','0');
INSERT INTO [t17712] VALUES (2910,'١٧١ - الشعر صعب وطويل سلمه ... إذا ارتقى فيه الذي لا يعلمه','1034','2739','0');
INSERT INTO [t17712] VALUES (2911,'١٧٢ - يا سعد عم الماء ورد يدهمه ... يوم تلاقى شاؤه ونعمه واختلفت أفراسه وقتمه ... فإنما أنت أخ لا نعدمه','1035','2739','0');
INSERT INTO [t17712] VALUES (2912,'١٧٣ - إن من صاد عقعقا لمشوم ... كيف من صاد عقعقان وبوم','1035','2739','0');
INSERT INTO [t17712] VALUES (2913,'١٧٤ - وليس كليبي إذا جن ليله ... إذا لم يجد ريح الأتان بنائم يقول إذا اقلولى عليها واقردت ... ألا هل أخو عيش لذيذ بدائم','1036','2739','0');
INSERT INTO [t17712] VALUES (2914,'١٧٥ - ألم ترني عاهدت ربي وإنني ... لبين رتاج قائما ومقام على حلفة لا أشتم الدهر مسلما ... ولا خارجا من في زور كلام','1036','2739','0');
INSERT INTO [t17712] VALUES (2915,'١٧٦ - فشد ولم تفزع بيوت كثيرة ... لدى حيث ألقت رحلها أم قشعم','1037','2739','0');
INSERT INTO [t17712] VALUES (2916,'١٧٧ - فمن مبلغ الأحلاف عني رسالة ... وذبيان هل أقسمتم كل مقسم','1038','2739','0');
INSERT INTO [t17712] VALUES (2917,'١٧٨ - لعل التفاتا منك نحوي مقدر ... يمل بك من بعد القساوة للرحم','1038','2739','0');
INSERT INTO [t17712] VALUES (2918,'١٧٩ - تناوله بالرمح ثم اتنى له ... فخر صريعا لليدين وللفم','1038','2739','0');
INSERT INTO [t17712] VALUES (2919,'١٨٠ - أقول لعبد الله لما سقاؤنا ... ونحن بوادي عبد شمس وها شم','1039','2739','0');
INSERT INTO [t17712] VALUES (2920,'١٨١ - وإنا لمما نضرب الكبش ضربة ... على رأسه تلقي اللسان من الفم','1039','2739','0');
INSERT INTO [t17712] VALUES (2921,'١٨٢ - ولولا بنوها حولها لخبطتها ... كخبطة عصفور ولم أتلعثم','1039','2739','0');
INSERT INTO [t17712] VALUES (2922,'١٨٣ - وتشرق بالقول الذي قد أذعته ... كما شرقت صدر القناة من الدم','1040','2739','0');
INSERT INTO [t17712] VALUES (2923,'١٨٤ - لأجتذبن منهن قلبي تحلما ... على حين يستصبين كل حليم','1041','2739','0');
INSERT INTO [t17712] VALUES (2924,'١٨٥ - ويرغب أن يبني المعالي خالد ... ويرغب أن يرضى صنيع الألائم','1041','2739','0');
INSERT INTO [t17712] VALUES (2925,'١٨٦ - يا ليت شعري ولا منجى من الهرم ... أم هل على العيش بعد الشيب من ندم','1041','2739','0');
INSERT INTO [t17712] VALUES (2926,'١٨٧ - حتى شآها كليل موهنا عمل ... بانت طرابا وبات الليل لم ينم','1041','2739','0');
INSERT INTO [t17712] VALUES (2927,'١٨٨ - سائل فوارس يربوع بشدتنا ... أهل رأونا بسفح القاع ذي الأكم','1042','2739','0');
INSERT INTO [t17712] VALUES (2928,'١٨٩ - ولا هداك إلى أرض كعالمها ... ولا أعانك في عزم كعزام','1042','2739','0');
INSERT INTO [t17712] VALUES (2929,'١٩٠ - تبلت فؤادك في المنام خريدة ... تسقي الضجيع ببارد بسام','1042','2739','0');
INSERT INTO [t17712] VALUES (2930,'١٩١ - قومي هم قتلوا أميم أخي ... وإذا رميت يصيبني سهمي فلئن عفوت لأعفون جللا ... ولئن سطوت لأوهنن عظمي','1043','2739','0');
INSERT INTO [t17712] VALUES (2931,'١٩٢ - حاشا أبا ثوبان إن به ... ضنا على الملحاة والشتم','1043','2739','0');
INSERT INTO [t17712] VALUES (2932,'١٩٣ - بطل كأن ثيابه في سرحة ... يحذى نعال السبت ليس بتوأم','1043','2739','0');
INSERT INTO [t17712] VALUES (2933,'١٩٤ - جادت عليه كل عين ثرة ... فتركن كل حديقة كالدرهم','1043','2739','0');
INSERT INTO [t17712] VALUES (2934,'١٩٥ - يا شاة من قنص لمن حلت له ... حرمت علي وليتها لم تحرم','1044','2739','0');
INSERT INTO [t17712] VALUES (2935,'١٩٦ - ولقد شفى نفسي وأبرأ سقمها ... قيل الفوارس ويك عنتر أقدم','1044','2739','0');
INSERT INTO [t17712] VALUES (2936,'١٩٧ - يدعون عنتر والرماح كأنها ... أشطان بئر في لبان الأدهم','1044','2739','0');
INSERT INTO [t17712] VALUES (2937,'١٩٨ - لولا الحياء وأن رأسي قد عسا ... فيه المشيب لزرت أم القاسم','1045','2739','0');
INSERT INTO [t17712] VALUES (2938,'١٩٩ - لو غيركم علق الزبير بحبله ... أدى الجوار إلى بني العوام','1045','2739','0');
INSERT INTO [t17712] VALUES (2939,'٢٠٠ - احفظ وديعتك التي استودعتها ... يوم الأعازب إن وصلت وإن لم','1045','2739','0');
INSERT INTO [t17712] VALUES (2940,'٢٠١ - ولا تلمني اليوم يا ابن عمي ... عند أبي الصهباء أقصى همي','1045','2739','0');
INSERT INTO [t17712] VALUES (2941,'٢٠٢ - ولكنني استبقيت أعراض مازن ... وأيامها من مستنير ومظلم أناسا بثغر لا تزال رماحهم ... شوارع من غير العشيرة في الدم','1046','2739','0');
INSERT INTO [t17712] VALUES (2942,'٢٠٣ - ولم أر ليلى بعد يوم تعرضت ... لنا بين أثواب الطراف من الأدم كلابية وبرية حبترية ... نأتك وخانت بالمواعيد والذمم أناسا عدى علقت فيهم وليتني ... طلبت الهوى في رأس ذي زلق أشم','1046','2739','0');
INSERT INTO [t17712] VALUES (2943,'٢٠٤ - وأغفر عوراء الكريم ادخاره ... وأعرض عن شتم اللئيم تكرما','1047','2739','0');
INSERT INTO [t17712] VALUES (2944,'٢٠٥ - لنا الجفنات الغر يلمعن بالضحى ... وأسيافنا يقطرن من نجدة دما','1047','2739','0');
INSERT INTO [t17712] VALUES (2945,'٢٠٦ - ولولا رجال من رزام أعزة ... وآل سبيع أو أسوءك علقما','1048','2739','0');
INSERT INTO [t17712] VALUES (2946,'٢٠٧ - لنا هضبة لا يدخل الذل وسطها ... ويأوي إليها المستجير فيعصما','1048','2739','0');
INSERT INTO [t17712] VALUES (2947,'٢٠٨ - فما كان قيس هلكه هلك واحد ... ولكنه بنيان قوم تهدما','1048','2739','0');
INSERT INTO [t17712] VALUES (2948,'٢٠٩ - هم القائلون الخير والآمرونه ... إذا ما خشوا من محدث الأمر معظما','1049','2739','0');
INSERT INTO [t17712] VALUES (2949,'٢١٠ - هما- أخوا- في الحرب- من لا أخاله ... إذا خاف يوما نبوة فدعاهما','1049','2739','0');
INSERT INTO [t17712] VALUES (2950,'٢١١ - ألم تر إني وابن أسود ليلة ... لنسري إلى نارين يعلو سناهما','1049','2739','0');
INSERT INTO [t17712] VALUES (2951,'٢١٢ - ألا أضحت حبالكم دماما ... وأضحت منك شاسعة أماما','1049','2739','0');
INSERT INTO [t17712] VALUES (2952,'٢١٣ - حدبت علي بطون ضنة كلها ... إن ظالما فيهم وإن مظلوما','1050','2739','0');
INSERT INTO [t17712] VALUES (2953,'٢١٤ - قد سألتني بنت عمرو عن ال ... أرض التي تنكر أعلامها لما رأت «ساتيدما» استعبرت ... لله در اليوم من لامها تذكرت أرضا بها أهلها ... أخوالها فيها وأعمامها','1050','2739','0');
INSERT INTO [t17712] VALUES (2954,'٢١٥ - من سبأ الحاضرين مأرب إذ ... يبنون من دون سيله العرما','1050','2739','0');
INSERT INTO [t17712] VALUES (2955,'٢١٦ - عشية لا تغني الرماح مكانها ... ولا النبل إلا المشرفي المصمم','1051','2739','0');
INSERT INTO [t17712] VALUES (2956,'٢١٧ - فأقسم أن لو التقينا وأنتم ... لكان لكم يوم من الشر مظلم','1051','2739','0');
INSERT INTO [t17712] VALUES (2957,'٢١٨ - تحلل وعالج ذات نفسك وانظرن ... أبا جعل لعلما أنت حالم','1051','2739','0');
INSERT INTO [t17712] VALUES (2958,'٢١٩ - بني ثعل لا تنكعوا العنز شربها ... بني ثعل من ينكع العنز ظالم','1052','2739','0');
INSERT INTO [t17712] VALUES (2959,'٢٢٠ - رأته على شيب القذال وأنها ... تواقع بعلا مرة وتئيم','1052','2739','0');
INSERT INTO [t17712] VALUES (2960,'٢٢١ - صددت فأطولت الصدود وقلما ... وصال على طول الصدود يدوم','1052','2739','0');
INSERT INTO [t17712] VALUES (2961,'٢٢٢ - فرطن فلا رد لما بت وانقضى ... ولكن بغوض أن يقال عديم','1052','2739','0');
INSERT INTO [t17712] VALUES (2962,'٢٢٣ - وكنا ورثناه على عهد تبع ... طويلا (سواريه) شديدا دعائمه','1053','2739','0');
INSERT INTO [t17712] VALUES (2963,'٢٢٤ - وإن بني حرب كما قد علمتم ... مناط الثريا قد تعلت نجومها','1053','2739','0');
INSERT INTO [t17712] VALUES (2964,'٢٢٥ - أهاجتك أطلال تعفت رسومها ... كما كتبت كاف تلوح وميمها','1053','2739','0');
INSERT INTO [t17712] VALUES (2965,'٢٢٦ - نبئت عبد الله بالجو أصبحت ... كراما مواليها لئيما صميمها','1053','2739','0');
INSERT INTO [t17712] VALUES (2966,'٢٢٧ - لا الدار غيرها بعدي الأنيس ولا ... بالدار لو كلمت ذا حاجة صمم','1053','2739','0');
INSERT INTO [t17712] VALUES (2967,'٢٢٨ - هذا الجواد الذي يعطيك نائله ... عفوا ويظلم أحيانا فيطلم','1054','2739','0');
INSERT INTO [t17712] VALUES (2968,'٢٢٩ - هل ما علمت وما استودعت مكتوم ... أم حبلها إذ نأتك اليوم مصروم أم هل كبير بكى لم يقض عبرته ... إثر الأحبة يوم البين مشكوم','1054','2739','0');
INSERT INTO [t17712] VALUES (2969,'٢٣٠ - فأما كيس فنجا ولكن ... عسى يغتر بي حمق لئيم','1054','2739','0');
INSERT INTO [t17712] VALUES (2970,'٢٣١ - أو كلما وردت عكاظ قبيلة ... بعثوا إلي عريفهم يتوسم','1054','2739','0');
INSERT INTO [t17712] VALUES (2971,'٢٣٢ - عهدي بها الحي الجميع وفيهم ... قبل التفرق ميسر وندام','1055','2739','0');
INSERT INTO [t17712] VALUES (2972,'٢٣٣ - ولقد أبيت من الفتاة بمنزل ... فأبيت لا حرج ولا محروم','1055','2739','0');
INSERT INTO [t17712] VALUES (2973,'٢٣٤ - ما أبالي أنب بالحزن تيس ... أم لحاني بظهر غيب لئيم','1055','2739','0');
INSERT INTO [t17712] VALUES (2974,'٢٣٥ - لئن كنت في جب ثمانين قامة ... ورقيت أسباب السماء بسلم ليستدرجنك القول حتى تهره ... وتعلم أني عنك لست بمحرم','1056','2739','0');
INSERT INTO [t17712] VALUES (2975,'٢٣٦ - تنكرت منا بعد معرفة لمي ... وبعد التصابي والشباب المكرم','1056','2739','0');
INSERT INTO [t17712] VALUES (2976,'٢٣٧ - وقدر ككف القرد لا مستعيرها ... يعار ولا من يأتها يتدسم','1056','2739','0');
INSERT INTO [t17712] VALUES (2977,'٢٣٨ - ولكنني أغدو علي مفاضة ... دلاص كأعيان الجراد المنظم','1056','2739','0');
INSERT INTO [t17712] VALUES (2978,'٢٣٩ - إذا لم تزل في كل دار عرفتها ... لها واكف من دمع عينك يسجم','1057','2739','0');
INSERT INTO [t17712] VALUES (2979,'٢٤٠ - فلست بشاوي عليه دمامة ... إذا ما غدا يغدو بقوس وأسهم','1057','2739','0');
INSERT INTO [t17712] VALUES (2980,'٢٤١ - منعت تميما منك أني أنا ابنها ... وشاعرها المعروف عند المواسم','1057','2739','0');
INSERT INTO [t17712] VALUES (2981,'٢٤٢ - أزيد أخا ورقاء إن كنت ثائرا ... فقد عرضت أحناء حق فخاصم','1057','2739','0');
INSERT INTO [t17712] VALUES (2982,'٢٤٣ - عمرتك الله إلا ما ذكرت لنا ... هل كنت جارتنا أيام ذي سلم','1058','2739','0');
INSERT INTO [t17712] VALUES (2983,'٢٤٤ - يا حار لا تجهل على أشياخنا ... إنا ذوو السورات والأحلام','1058','2739','0');
INSERT INTO [t17712] VALUES (2984,'٢٤٥ - فصالحونا جميعا إن بدا لكم ... ولا تقولوا لنا أمثالها عام','1058','2739','0');
INSERT INTO [t17712] VALUES (2985,'٢٤٦ - قالت بنو عامر خالوا بني أسد ... يا بؤس للجهل ضرارا لأقوام','1059','2739','0');
INSERT INTO [t17712] VALUES (2986,'٢٤٧ - أخذت بسجلهم فنفحت فيه ... محافظة لهن إخا الذمام','1059','2739','0');
INSERT INTO [t17712] VALUES (2987,'٢٤٨ - إذا ما المرء كان أبوه عبس ... فحسبك ما تريد إلى الكلام','1059','2739','0');
INSERT INTO [t17712] VALUES (2988,'٢٤٩ - إذا بعض السنين تعرقتنا ... كفى الأيتام فقد أبي اليتيم','1060','2739','0');
INSERT INTO [t17712] VALUES (2989,'٢٥٠ - أبي الإسلام لا أب لي سواه ... إذا افتخروا بقيس أو تميم','1060','2739','0');
INSERT INTO [t17712] VALUES (2990,'٢٥١ - لولا ابن حارثة الأمير لقد ... أغضيت من شتمي على رغم إلا كمعرض المحسر بكره ... عمدا يسببني على الظلم','1060','2739','0');
INSERT INTO [t17712] VALUES (2991,'٢٥٢ - يا ذا المخوفنا بمقتل شيخه ... حجر تمني صاحب الأحلام','1061','2739','0');
INSERT INTO [t17712] VALUES (2992,'٢٥٣ - ولقد خبطن بيوت يشكر خبطة ... أخوالنا وهم بنو الأعمام','1061','2739','0');
INSERT INTO [t17712] VALUES (2993,'٢٥٤ - لنا هضبة لا ينزل الذل وسطها ... ويأوى إليها المستجير ليعصما','1061','2739','0');
INSERT INTO [t17712] VALUES (2994,'٢٥٥ - كذبت وبيت الله لو كنت صادقا ... لما سبقتني بالبكاء الحمائم','1062','2739','0');
INSERT INTO [t17712] VALUES (2995,'٢٥٦ - جزى الله عني، والجزاء بفضله ... ربيعة خيرا ما أعف وأكرما','1062','2739','0');
INSERT INTO [t17712] VALUES (2996,'٢٥٧ - يمينا لنعم السيدان وجدتما ... على كل حال من سحيل ومبرم','1062','2739','0');
INSERT INTO [t17712] VALUES (2997,'٢٥٨ - نعم الفتى فجعت به إخوانه ... يوم البقيع حوادث الأيام','1062','2739','0');
INSERT INTO [t17712] VALUES (2998,'٢٥٩ - تخيره فلم يعدل سواه ... فنعم المرء من رجل تهامي','1063','2739','0');
INSERT INTO [t17712] VALUES (2999,'٢٦٠ - إذا ما غضبنا غضبة مضرية ... هتكنا حجاب الشمس أو قطرت دما','1063','2739','0');
INSERT INTO [t17712] VALUES (3000,'٢٦١ - وكائن ترى من صامت لك معجب ... زيادته أو نقصه في التكلم','1063','2739','0');
INSERT INTO [t17712] VALUES (3001,'٢٦٢ - وكائن لنا فضلا عليكم ومنة ... قديما ولا تدرون ما من منعم','1063','2739','0');
INSERT INTO [t17712] VALUES (3002,'٢٦٣ - ألست بنعم الجار يؤلف بيته ... أخا قلة أو معدم المال مصرما','1063','2739','0');
INSERT INTO [t17712] VALUES (3003,'٢٦٤ - ألا يا اسلمي لا صرم لي اليوم فاطما ... ولا أبدا ما دام وصلك دائما','1064','2739','0');
INSERT INTO [t17712] VALUES (3004,'٢٦٥ - العاطفون تحين ما من عاطف ... والمطعمون زمان أين المطعم','1064','2739','0');
INSERT INTO [t17712] VALUES (3005,'٢٦٦ - وتضيء في وجه الظلام منيرة ... كجمانة البحري سل نظامها','1065','2739','0');
INSERT INTO [t17712] VALUES (3006,'٢٦٧ - فإن المنية من يخشها ... فسوف تصادفه أينما','1065','2739','0');
INSERT INTO [t17712] VALUES (3007,'٢٦٨ - ما برئت من ريبة وذم ... في حربنا إلا بنات العم','1065','2739','0');
INSERT INTO [t17712] VALUES (3008,'٢٦٩ - وكان طوى كشحا على مستكنة ... فلا هو أبداها ولم يتقدم','1065','2739','0');
INSERT INTO [t17712] VALUES (3009,'٢٧٠ - في لجة غمرت أباك بحورها ... في الجاهلية «كان» والإسلام','1066','2739','0');
INSERT INTO [t17712] VALUES (3010,'٢٧١ - فإن لم تك المرآة أبدت وسامة ... فقد أبدت المرآة جبهة ضيغم','1066','2739','0');
INSERT INTO [t17712] VALUES (3011,'٢٧٢ - إذا لم تك الحاجات من همة الفتى ... فليس بمغن عنه عقد الرتائم','1066','2739','0');
INSERT INTO [t17712] VALUES (3012,'٢٧٣ - يا لعنة الله على أهل الرقم ... أهل الحمير والوقير والخزم','1066','2739','0');
INSERT INTO [t17712] VALUES (3013,'٢٧٤ - بحسبك أن قد سدت أخزم كلها ... لكل أناس سادة ودعائم','1067','2739','0');
INSERT INTO [t17712] VALUES (3014,'٢٧٥ - لقد ولد الأخيطل أم سوء ... على باب استها صلب وشام','1067','2739','0');
INSERT INTO [t17712] VALUES (3015,'٢٧٦ - لعن الإله تعلة بن مسافر ... لعنا يشن عليه من قدام','1068','2739','0');
INSERT INTO [t17712] VALUES (3016,'٢٧٧ - فسقى ديارك غير مفسدها ... صوب الربيع وديمة تهمي','1068','2739','0');
INSERT INTO [t17712] VALUES (3017,'٢٧٨ - ولقد خشيت بأن أموت ولم تدر ... للحرب دائرة على ابني ضمضم','1068','2739','0');
INSERT INTO [t17712] VALUES (3018,'٢٧٩ - كأن فتات العهن- في كل منزل ... نزلن به- حب الفنا لم يحطم','1068','2739','0');
INSERT INTO [t17712] VALUES (3019,'٢٨٠ - وخيفاء ألقى الليث فيها ذراعه ... فسرت وساءت كل ماش ومصرم تمشي بها الدرماء تسحب قصبها ... كأن بطن حبلى ذات أونين متئم','1068','2739','0');
INSERT INTO [t17712] VALUES (3020,'٢٨١ - فتعلمي أن قد كلفت بكم ... ثم افعلي ما شئت عن علم','1069','2739','0');
INSERT INTO [t17712] VALUES (3021,'٢٨٢ - ولست بلوام على الأمر بعد ما ... يفوت ولكن عل أن أتقدما','1069','2739','0');
INSERT INTO [t17712] VALUES (3022,'٢٨٣ - ألا يا صاحبي قفا لغنا ... يرى العرصات أو أثر الخيام','1069','2739','0');
INSERT INTO [t17712] VALUES (3023,'٢٨٤ - قد لمتنا يا أم غيلان في السرى ... ونمت وما ليل المطي بنائم','1070','2739','0');
INSERT INTO [t17712] VALUES (3024,'٢٨٥ - أما ودماء مائرات تخالها ... على قنة العزى وبالنسر عند ما وما سبح الرهبان في كل بيعة ... أبيل الأبيلين المسيح ابن مريما لقد ذاق منا عامر يوم لعلع ... حساما إذا ما هز بالكف صمما','1070','2739','0');
INSERT INTO [t17712] VALUES (3025,'٢٨٦ - وما عليك أن تقولي كلما ... صليت أو سبحت يا اللهما أردد علينا شيخنا مسلما','1070','2739','0');
INSERT INTO [t17712] VALUES (3026,'٢٨٧ - أقول وما قولي عليكم بسبة ... إليك ابن سلمى أنت حافر زمزم حفيرة إبراهيم يوم ابن هاجر ... وركضة جبريل على عهد آدم','1072','2739','0');
INSERT INTO [t17712] VALUES (3027,'٢٨٨ - وما خذل قومي فأخضع للعدا ... ولكن إذا أدعوهم فهم هم','1072','2739','0');
INSERT INTO [t17712] VALUES (3028,'٢٨٩ - لا ينسك الأسى تأسيا فما ... ما من حمام أحد معتصما','1072','2739','0');
INSERT INTO [t17712] VALUES (3029,'٢٩٠ - من يعن بالحمد لم ينطق بما سفه ... يحد عن سبيل المجد والكرم','1073','2739','0');
INSERT INTO [t17712] VALUES (3030,'٢٩١ - لو بأبانين جاء يخطبها ... زمل- ما- أنف خاطب بدم','1073','2739','0');
INSERT INTO [t17712] VALUES (3031,'٢٩٢ - تراه وقد فات الرماة كأنه ... أمام الكلاب مصغي الخد أصلم','1074','2739','0');
INSERT INTO [t17712] VALUES (3032,'٢٩٣ - آت الموت تعلمون فلا ير ... هبكم من لظى الحروب اضطرام','1074','2739','0');
INSERT INTO [t17712] VALUES (3033,'٢٩٤ - لا أعد الإقتار عدما ولكن ... فقد من قد رزئته الإعدام','1074','2739','0');
INSERT INTO [t17712] VALUES (3034,'٢٩٥ - يلومونني في اشتراء النخيل ... أهلي فكلهم ألوم','1074','2739','0');
INSERT INTO [t17712] VALUES (3035,'٢٩٦ - وكذاكم مصير كل أناس ... سوف حقا تبليهم الأيام','1075','2739','0');
INSERT INTO [t17712] VALUES (3036,'٢٩٧ - ليت شعري وأين مني ليت ... أعلى العهد يلبن فبرام','1075','2739','0');
INSERT INTO [t17712] VALUES (3037,'٢٩٨ - لئن كان- سلمى- الشيب بالصد مغريا ... لقد هون السلوان عنها التحلم','1075','2739','0');
INSERT INTO [t17712] VALUES (3038,'٢٩٩ - معروريا رمض الرضراض يركضه ... والشمس حيرى لها في الجو تدويم','1075','2739','0');
INSERT INTO [t17712] VALUES (3039,'٣٠٠ - كي تجنحون إلى سلم وما ثئرت ... قتلاكم ولظى الهيجاء تضطرم','1076','2739','0');
INSERT INTO [t17712] VALUES (3040,'٣٠١ - وقالوا أخانا لا تخشع لظالم ... عزيز ولا- ذا حق قومك- تظلم','1076','2739','0');
INSERT INTO [t17712] VALUES (3041,'٣٠٢ - أيها الشاتمي لتحسب مثلي ... إنما أنت في الضلال تهيم','1076','2739','0');
INSERT INTO [t17712] VALUES (3042,'٣٠٣ - لعمري وما عمري علي بهين ... لبئس الفتى المدعو بالليل حاتم','1077','2739','0');
INSERT INTO [t17712] VALUES (3043,'٣٠٤ - لئن كان النكاح أحل شيء ... فإن نكاحها مطر حرام','1077','2739','0');
INSERT INTO [t17712] VALUES (3044,'٣٠٥ - جالت لتصرعني فقلت لها اقصري ... إني امرؤ قتلي عليك حرام','1078','2739','0');
INSERT INTO [t17712] VALUES (3045,'٣٠٦ - نحو الأميلح من سمنان مبتكرا ... بفتية فيهم المرار والحكم','1078','2739','0');
INSERT INTO [t17712] VALUES (3046,'٣٠٧ - كذبت وبيت الله لو كنت عاشقا ... لما سبقتني بالبكاء الحمائم','1079','2739','0');
INSERT INTO [t17712] VALUES (3047,'٣٠٨ - ليت شعري هل ثم هل آتينهم ... أم يحولن دون ذاك حمام','1080','2739','0');
INSERT INTO [t17712] VALUES (3048,'٣٠٩ - ولا أنبأن أن وجهك شانه ... خموش وإن كان الحميم حميم','1081','2739','0');
INSERT INTO [t17712] VALUES (3049,'٣١٠ - كأس عزيز من الأعناب عتقها ... لبعض أحيانها حانية حوم','1081','2739','0');
INSERT INTO [t17712] VALUES (3050,'٣١١ - يقلن حرام ما أحل بربنا ... وتترك أموال عليها الخواتم','1081','2739','0');
INSERT INTO [t17712] VALUES (3051,'٣١٢ - متى كان الخيام بذي طلوح ... سقيت الغيث أيتها الخيامو','1082','2739','0');
INSERT INTO [t17712] VALUES (3052,'٣١٣ - خليلي إن العامري لغارم ... ولولاه ما قلت لدي الدراهم','1082','2739','0');
INSERT INTO [t17712] VALUES (3053,'٣١٤ - سلامك ربنا في كل فجر ... بريئا ما تغنثك الذموم','1082','2739','0');
INSERT INTO [t17712] VALUES (3054,'٣١٥ - صل الذي والتي متا بآصرة ... وإن نأت عن مدى مرماهم الرحم','1083','2739','0');
INSERT INTO [t17712] VALUES (3055,'٣١٦ - أما والذي لا يعلم الغيب غيره ... ومن هو يحيي العظم وهو رميم','1083','2739','0');
INSERT INTO [t17712] VALUES (3056,'٣١٧ - داوية ودجى ليل كأنهما ... يم تراطن في حافاته الروم','1083','2739','0');
INSERT INTO [t17712] VALUES (3057,'٣١٨ - أبا ثابت لا تعلقنك رماحنا ... أبا ثابت فاذهب وعرضك سالم','1083','2739','0');
INSERT INTO [t17712] VALUES (3058,'٣١٩ - لا تحرز المرء أحجاء البلاد ولا ... تبنى له في السموات السلاليم','1083','2739','0');
INSERT INTO [t17712] VALUES (3059,'٣٢٠ - لا الدار غيرها بعد الأنيس ولا ... بالدار لو كلمت ذا حاجة صمم','1084','2739','0');
INSERT INTO [t17712] VALUES (3060,'٣٢١ - وددت وما تغني الودادة أنني ... بما في ضمير الحاجبية عالم','1084','2739','0');
INSERT INTO [t17712] VALUES (3061,'٣٢٢ - إن ابن حارث إن أشتق لرؤيته ... أو أمتدحه فإن الناس قد علموا','1084','2739','0');
