INSERT INTO [t17712] VALUES (3123,'٣٨٤ - فأطرق إطراق الشجاع ولو يرى ... مساغا لناباه الشجاع لصمما','1109','2739','0');
INSERT INTO [t17712] VALUES (3124,'٣٨٥ - أمن دمنتين عرس الركب فيهما ... بحقل الرخامى قد أنى لبلاهما','1110','2739','0');
INSERT INTO [t17712] VALUES (3125,'٣٨٦ - أقامت على ربعيهما جارتا صفا ... كميتا الأعالي جونتا مصطلاهما','1110','2739','0');
