INSERT INTO [t17712] VALUES (3158,'٤١٩ - ما خلتني زلت بعدكم ضمنا ... أشكو إليكم حموة الألم','1121','2739','0');
INSERT INTO [t17712] VALUES (3159,'٤٢٠ - أشارت بطرف العين خيفة أهلها ... إشارة محزون ولم تتكلم','1121','2739','0');
