INSERT INTO [t17712] VALUES (3169,'٤٣٠ - فليت سليمى في الممات ضجيعتي ... هنالك أم في جنة أم جهنم','1124','2739','0');
INSERT INTO [t17712] VALUES (3170,'٤٣١ - عوجا على الطلل القديم لأننا ... نبكي الديار كما بكى ابن خذام','1125','2739','0');
INSERT INTO [t17712] VALUES (3171,'٤٣٢ - بذلنا مارن الخطي فيهم ... وكل مهند ذكر حسام','1125','2739','0');
