INSERT INTO [t17712] VALUES (3180,'٤٤١ - وأربد فارس الهيجا إذا ما ... تقعرت المشاجر بالخيام','1128','2739','0');
INSERT INTO [t17712] VALUES (3181,'٤٤٢ - فلن تستطيعوا أن تزيلوا الذي رسا ... لها عند عال فوق سبعين دائم','1128','2739','0');
INSERT INTO [t17712] VALUES (3182,'٤٤٣ - فعوضني عنها غناي ولم تكن ... تساوي عندي غير خمس دراهم','1128','2739','0');
INSERT INTO [t17712] VALUES (3183,'٤٤٤ - ألا تنتهي عنا ملوك وتتقي ... محارمنا لا يبأء الدم بالدم','1128','2739','0');
INSERT INTO [t17712] VALUES (3184,'٤٤٥ - الشاتمي عرضي ولم أشتمهما ... والناذرين إذا لم ألقهما دمي','1129','2739','0');
INSERT INTO [t17712] VALUES (3185,'٤٤٦ - وتشرق بالقول الذي قد أذعته ... كما شرقت صدر القناة من الدم','1129','2739','0');
INSERT INTO [t17712] VALUES (3186,'٤٤٧ - تركنا أخا بكر ينوء بصدره ... بصفين مخضوب الجبين من الدم','1129','2739','0');
INSERT INTO [t17712] VALUES (3187,'٤٤٨ - أسمعتكم يوم أدعوني مربأة ... لولاكم ساغ لحمي عندها ودمي','1130','2739','0');
INSERT INTO [t17712] VALUES (3188,'٤٤٩ - لو عد قبر وقبر كنت أكرمهم ... ميتا وأبعدهم عن منزل الذام','1130','2739','0');
INSERT INTO [t17712] VALUES (3189,'٤٥٠ - أمن عمل الجراف أمس وظلمه ... وعدوانه أعتبتمونا براسم','1131','2739','0');
INSERT INTO [t17712] VALUES (3190,'٤٥١ - أميري عداء إن حبسنا عليهما ... بهائم مال أوديا بالبهائم','1131','2739','0');
INSERT INTO [t17712] VALUES (3191,'٤٥٢ - فقلت لها أصبت حصاة قلبي ... وربت رمية من غير رامي','1131','2739','0');
INSERT INTO [t17712] VALUES (3192,'٤٥٣ - هما نفثا في في من فمويهما ... على النابح العاوي أشد رجام','1131','2739','0');
INSERT INTO [t17712] VALUES (3193,'٤٥٤ - ليس الأخلاء بالمصغي مسامعهم ... إلى الوشاة ولو كانوا ذوي رحم','1132','2739','0');
INSERT INTO [t17712] VALUES (3194,'٤٥٥ - لولا ابن حارثة الأمير لقد ... أغضيت من شتمي على رغمي','1132','2739','0');
INSERT INTO [t17712] VALUES (3195,'٤٥٦ - إلا كمعرض المحسر بكره ... عمدا يسببني على الظلم','1132','2739','0');
INSERT INTO [t17712] VALUES (3196,'٤٥٧ - أرقت ولم تهجع لعيني هجعة ... وو الله ما دهري بعسر ولا سقم','1132','2739','0');
INSERT INTO [t17712] VALUES (3197,'٤٥٨ - فيه الرماح وفيه كل سابغة ... جدلاء محكمة من نسج سلام','1133','2739','0');
INSERT INTO [t17712] VALUES (3198,'٤٥٩ - هلا تمنن بوعد غير مخلفة ... كما عهدتك في أيام ذي سلم','1133','2739','0');
INSERT INTO [t17712] VALUES (3199,'٤٦٠ - ومن هاب أسباب المنايا ينلنه ... ولو رام أسباب السماء بسلم','1134','2739','0');
INSERT INTO [t17712] VALUES (3200,'٤٦١ - أذلا إذا شب العدا نار حربهم ... وزهوا إذا ما يجنحون إلى السلم','1134','2739','0');
INSERT INTO [t17712] VALUES (3201,'٤٦٢ - وأنا الذي قتلت بكرا بالقنا ... وتركت مرة غير ذات سنام','1135','2739','0');
INSERT INTO [t17712] VALUES (3202,'٤٦٣ - كأنا على أولاد أحقب لاحها ... ورمي السفى أنفاسها بسهام جنوب ذوت عنها التناهي وأنزلت ... بها يوم ذباب السبيب صيام','1135','2739','0');
INSERT INTO [t17712] VALUES (3203,'٤٦٤ - تيممت همدان الذين هم هم ... إذا ناب أمر جنتي وسهامي','1135','2739','0');
INSERT INTO [t17712] VALUES (3204,'٤٦٥ - يا صاح إما تجدني غير ذي جدة ... فما التخلي عن الخلان من شيمي','1137','2739','0');
INSERT INTO [t17712] VALUES (3205,'٤٦٦ - ظللنا بمستن الحرور كأننا ... لدى فرس مستقبل الريح صائم','1138','2739','0');
INSERT INTO [t17712] VALUES (3206,'٤٦٧ - فرت يهود وأسلمت جيرانها ... صمي لما فعلت يهود صمام','1138','2739','0');
INSERT INTO [t17712] VALUES (3207,'٤٦٨ - صدت كما صد عما لا يحل له ... ساقي نصارى قبيل الفصح صوام','1138','2739','0');
INSERT INTO [t17712] VALUES (3208,'٤٦٩ - أرى النيك يجلو الهم والغم والعمى ... ولا سيما (إن نكت) بالمرس الضخم','1138','2739','0');
INSERT INTO [t17712] VALUES (3209,'٤٧٠ - ولقد خشيت بأن أموت ولم تكن ... للحرب دائرة على ابني ضمضم','1139','2739','0');
INSERT INTO [t17712] VALUES (3210,'٤٧١ - نرى أسهما للموت تصمي ولا تنمي ... ولا نرعوي عن نقض- أهواؤنا- العزم','1139','2739','0');
INSERT INTO [t17712] VALUES (3211,'٤٧٢ - تزود منا بين أذناه طعنة ... دعته إلى هابي التراب عقيم','1139','2739','0');
INSERT INTO [t17712] VALUES (3212,'٤٧٣ - وإلا أكن كل الشجاع فإنني ... بضرب الطلى والهام حق عليم','1140','2739','0');
INSERT INTO [t17712] VALUES (3213,'٤٧٤ - ونطعنهم حيث الكلى بعد ضربهم ... ببيض المواضي حيث لي العمائم','1141','2739','0');
INSERT INTO [t17712] VALUES (3214,'٤٧٥ - شغفت بك اللت تيمتك فمثل ما ... بك ما بها من لوعة وغرام','1141','2739','0');
INSERT INTO [t17712] VALUES (3215,'٤٧٦ - قلب من عيل صبره كيف يسلو ... صاليا نار لوعة وغرام','1141','2739','0');
INSERT INTO [t17712] VALUES (3216,'٤٧٧ - فما أنت من قيس فتنبح دونها ... ولا من تميم في اللها والغلاصم','1141','2739','0');
INSERT INTO [t17712] VALUES (3217,'٤٧٨ - وما أنت من قيس فتنبح دونها ... ولا من تميم في الرؤوس الأعاظم','1141','2739','0');
INSERT INTO [t17712] VALUES (3218,'٤٧٩ - قد كنت أحسبني كأغنى واحد ... نزل المدينة عن زراعة فوم','1142','2739','0');
INSERT INTO [t17712] VALUES (3219,'٤٨٠ - لم ألف بالدار ذا نطق سوى طلل ... قد كاد يعفو وما بالعهد من قدم','1142','2739','0');
INSERT INTO [t17712] VALUES (3220,'٤٨١ - فهم مثل الناس الذي يعرفونه ... وأهل الوفا من حادث وقديم','1142','2739','0');
INSERT INTO [t17712] VALUES (3221,'٤٨٢ - أسيد ذو خريطة نهارا ... من المتلقطي قرد القمام','1142','2739','0');
INSERT INTO [t17712] VALUES (3222,'٤٨٣ - دمت الحميد فما تنفك منتصرا ... على العدا في سبيل المجد والكرم','1143','2739','0');
INSERT INTO [t17712] VALUES (3223,'٤٨٤ - نستوقد النبل بالحضيض ونص ... طاد نفوسا بنت على الكرم','1143','2739','0');
INSERT INTO [t17712] VALUES (3224,'٤٨٥ - كيف أصبحت كيف أمسيت مما ... يغرس الود في فؤاد الكريم','1144','2739','0');
INSERT INTO [t17712] VALUES (3225,'٤٨٦ - كأن أخا الكتاب يجد خطا ... بكاف في منازلها ولام','1145','2739','0');
INSERT INTO [t17712] VALUES (3226,'٤٨٧ - ألا أيها الطير المربة بالضحى ... على خالد لقد وقعت على لحم','1145','2739','0');
INSERT INTO [t17712] VALUES (3227,'٤٨٨ - فإما أعش حتى أدب على العصى ... فو الله أنسى ليلتي بالمسالم','1145','2739','0');
INSERT INTO [t17712] VALUES (3228,'٤٨٩ - أتقول إنك بالحياة ممتع ... وقد استبحت دم امرئ مستسلم','1146','2739','0');
INSERT INTO [t17712] VALUES (3229,'٤٩٠ - وليت فلم تقطع لدن أن وليتنا ... قرابة ذي قربى ولا حق مسلم','1146','2739','0');
INSERT INTO [t17712] VALUES (3230,'٤٩١ - هل تبلغني دارها شدنية ... لعنت بمحروم الشراب مصرم','1146','2739','0');
INSERT INTO [t17712] VALUES (3231,'٤٩٢ - ألم ترني عاهدت ربي وإنني ... لبين رتاج قائما ومقام','1146','2739','0');
INSERT INTO [t17712] VALUES (3232,'٤٩٣ - لا ينعش الطرف إلا ما تخونه ... داع يناديه باسم الماء مبغوم','1147','2739','0');
INSERT INTO [t17712] VALUES (3233,'٤٩٤ - وما يشعر الرمح الأصم كعوبه ... بثروة رهط الأعيط المتظلم','1147','2739','0');
INSERT INTO [t17712] VALUES (3234,'٤٩٥ - وإني لأطوي الكشح من دون ما انطوى ... وأقطع بالخرق الهبوع المراجم','1147','2739','0');
INSERT INTO [t17712] VALUES (3235,'٤٩٦ - علقتها عرضا وأقتل قومها ... زعما لعمر أبيك ليس بمزعم','1148','2739','0');
INSERT INTO [t17712] VALUES (3236,'٤٩٧ - إذا ما نعشناه على الرحل ينثني ... مساليه عنه من وراء ومقدم','1148','2739','0');
INSERT INTO [t17712] VALUES (3237,'٤٩٨ - منعت تميما منك أني أنا ابنها ... وشاعرها المعروف عند المواسم','1148','2739','0');
INSERT INTO [t17712] VALUES (3238,'٤٩٩ - وبايعت أقواما وفيت بعهدهم ... وببة قد بايعته غير نادم','1149','2739','0');
INSERT INTO [t17712] VALUES (3239,'٥٠٠ - إلا الإفادة فاستولت ركائبنا ... عند الجبابير بالبأساء والنعم','1149','2739','0');
INSERT INTO [t17712] VALUES (3240,'٥٠١ - كأنما يقع البصري بينهم ... من الطوائف والأعناق بالوذم','1149','2739','0');
INSERT INTO [t17712] VALUES (3241,'٥٠٢ - قد بت أحرسني وحدي ويمنعني ... صوت السباع به يضبحن والهام','1149','2739','0');
INSERT INTO [t17712] VALUES (3242,'٥٠٣ - نزور امرأ أما الإله فيتقي ... وأما بفعل الصالحين فيأتمي','1150','2739','0');
INSERT INTO [t17712] VALUES (3243,'٥٠٤ - ووطئتنا وطئا على حنق ... وطء المقيد نابت الهرم','1150','2739','0');
INSERT INTO [t17712] VALUES (3244,'٥٠٥ - وكان طوى كشحا على مستكنة ... فلا هو أبداها ولم يتجمجم','1151','2739','0');
INSERT INTO [t17712] VALUES (3245,'٥٠٦ - كأن فتات العهن في كل منزل ... نزلن به حب الفنا لم يحطم','1151','2739','0');
INSERT INTO [t17712] VALUES (3246,'٥٠٧ - ومن لا يزل يستحمل الناس نفسه ... ولا يغنها يوما من الدهر يسأم','1152','2739','0');
INSERT INTO [t17712] VALUES (3247,'٥٠٨ - جريء متى يظلم يعاقب بظلمه ... سريعا وإلا يبد بالظلم يظلم','1152','2739','0');
INSERT INTO [t17712] VALUES (3248,'٥٠٩ - فإذا شربت فإنني مستهلك ... مالي وعرضي وافر لم يكلم','1152','2739','0');
INSERT INTO [t17712] VALUES (3249,'٥١٠ - أماوي مهمن يسمعن في صديقه ... أقاويل هذا الناس ماوي يندم','1153','2739','0');
INSERT INTO [t17712] VALUES (3250,'٥١١ - يا دار أقوت بعد أصرامها ... عاما وما يبكيك من عامها','1153','2739','0');
INSERT INTO [t17712] VALUES (3251,'٥١٢ - شم مهاوين أبدان الجزور ... مخاميص العشيات لا خور ولا قزم','1153','2739','0');
INSERT INTO [t17712] VALUES (3252,'٥١٣ - نحن آل الله في بلدتنا ... لم نزل آلا على عهد إرم','1154','2739','0');
INSERT INTO [t17712] VALUES (3253,'٥١٤ - روافده أكرم الرافدات ... بخ لك بخ لبحر خضم','1154','2739','0');
INSERT INTO [t17712] VALUES (3254,'٥١٥ - ترانا إذا أضمرتك البلا ... د نجفى ويقطع منا الرحم','1154','2739','0');
INSERT INTO [t17712] VALUES (3255,'٥١٦ - تقول هلكنا إن هلكت وإنما ... على الله أرزاق العباد كما زعم','1155','2739','0');
INSERT INTO [t17712] VALUES (3256,'٥١٧ - ومكن الضباب طعام العريب ... ولا تشتهيه نفوس العجم','1155','2739','0');
INSERT INTO [t17712] VALUES (3257,'٥١٨ - إلى المرء قيس أطيل السرى ... وآخذ من كل حي عصم','1155','2739','0');
INSERT INTO [t17712] VALUES (3258,'٥١٩ - من خمر بيسان تخيرتها ... ترياقة توشك فتر العظام','1157','2739','0');
INSERT INTO [t17712] VALUES (3259,'٥٢٠ - أولئك إخواني الذين عرفتهم ... وأخدانك اللاءات زين بالكتم','1158','2739','0');
INSERT INTO [t17712] VALUES (3260,'٥٢١ - لا تشتم الناس كما لا تشتم','1158','2739','0');
INSERT INTO [t17712] VALUES (3261,'٥٢٢ - لا تظلموا الناس كما لا تظلموا','1158','2739','0');
INSERT INTO [t17712] VALUES (3262,'٥٢٣ - هما اللتا لو ولدت تميم ... لقيل فخر لهم صميم','1158','2739','0');
INSERT INTO [t17712] VALUES (3263,'٥٢٤ - باسم الذي في كل سورة سمه','1159','2739','0');
INSERT INTO [t17712] VALUES (3264,'٥٢٥ - وعامنا أعجبنا مقدمه ... يدعى أبا السمح وقرضاب سمه','1159','2739','0');
INSERT INTO [t17712] VALUES (3265,'٥٢٦ - كالحوت لا يرويه شيء يلقمه ... يصبح ظمآن وفي البحر فمه','1159','2739','0');
INSERT INTO [t17712] VALUES (3266,'٥٢٧ - أو كتبا بين من حاميما ... قد علمت أبناء إبراهيما','1159','2739','0');
INSERT INTO [t17712] VALUES (3267,'٥٢٨ - أكثرت في العذل ملحا دائما ... لا تكثرن إني عسيت صائما','1159','2739','0');
INSERT INTO [t17712] VALUES (3268,'٥٢٩ - كافا وميمين وسينا طاسما','1160','2739','0');
INSERT INTO [t17712] VALUES (3269,'٥٣٠ - كن لي لا علي يا ابن عما ... نعش عزيزين ونكفى الهما','1160','2739','0');
INSERT INTO [t17712] VALUES (3270,'٥٣١ - قم قائما قم قائما قم قائما ... إنك لا ترجع إلا سالما','1160','2739','0');
INSERT INTO [t17712] VALUES (3271,'٥٣٢ - قد لفها الليل بسواق حطم ... ليس براعي إبل ولا غنم','1161','2739','0');
INSERT INTO [t17712] VALUES (3272,'٥٣٣ - أقبلن من ثهلان أو وادي خيم ... على قلاص مثل خيطان السلم','1161','2739','0');
INSERT INTO [t17712] VALUES (3273,'قافية النون','1162','0','0');
INSERT INTO [t17712] VALUES (3274,'١ - وإذا فلان مات عن أكرومة ... رقعوا معاوز فقده بفلان','1162','3273','0');
INSERT INTO [t17712] VALUES (3275,'٢ - أيها المنكح الثريا سهيلا ... عمرك الله كيف يلتقيان هي شامية إذا ما استقلت ... وسهيل إذا استقل يماني','1162','3273','0');
INSERT INTO [t17712] VALUES (3276,'٣ - لو كنت من مازن لم تستبح إبلي ... بنو اللقيطة من ذهل بن شيبانا إذن لقام بنصري معشر خشن ... عند الحفيظة إن ذو لوثة لانا','1163','3273','0');
INSERT INTO [t17712] VALUES (3277,'٤ - فنعم صاحب قوم لا سلاح لهم ... وصاحب الركب عثمان بن عفانا','1164','3273','0');
INSERT INTO [t17712] VALUES (3278,'٥ - فما وجدت بنات بني نزار ... حلائل أسودين وأحمرينا','1164','3273','0');
INSERT INTO [t17712] VALUES (3279,'٦ - وكان لنا فزارة عم سوء ... وكنت له كشر بني الأخينا','1165','3273','0');
INSERT INTO [t17712] VALUES (3280,'٧ - إليكم يا بني بكر إليكم ... ألما تعرفوا منا اليقينا','1165','3273','0');
INSERT INTO [t17712] VALUES (3281,'٨ - وما إن طبنا جبن ولكن ... منايانا ودولة آخرينا','1165','3273','0');
INSERT INTO [t17712] VALUES (3282,'٩ - فإن أدع اللواتي من أناس ... أضاعوهن لا أدع الذينا','1165','3273','0');
INSERT INTO [t17712] VALUES (3283,'١٠ - وقائلة أسيت فقلت جير ... أسي إنني من ذاك إنه','1166','3273','0');
INSERT INTO [t17712] VALUES (3284,'١١ - إن المنايا يطلعن ... على الأناس الآمنينا','1166','3273','0');
INSERT INTO [t17712] VALUES (3285,'١٢ - تنفك تسمع ما حيي ... ت بهالك حتى تكونه والمرء قد يرجو الحيا ... ة مؤملا والموت دونه','1167','3273','0');
INSERT INTO [t17712] VALUES (3286,'١٣ - أخذت بعين المال حتى نهكته ... وبالدين حتى ما أكاد أدان وحتى سألت القرض عند ذوي الغنى ... ورد فلان حاجتي وفلان','1167','3273','0');
INSERT INTO [t17712] VALUES (3287,'١٤ - وكان لنا أبو حسن علي ... أبا برا ونحن له بنين','1167','3273','0');
INSERT INTO [t17712] VALUES (3288,'١٥ - فليت لنا من ماء زمزم شربة ... مبردة باتت على طهيان','1168','3273','0');
INSERT INTO [t17712] VALUES (3289,'١٦ - علا زيدنا يوم النقا رأس زيدكم ... بأبيض ماضي الشفرتين يمان','1168','3273','0');
INSERT INTO [t17712] VALUES (3290,'١٧ - غير مأسوف على زمن ... ينقضي بالهم والحزن','1169','3273','0');
INSERT INTO [t17712] VALUES (3291,'١٨ - لأصبح الحي أوبادا ولم يجدوا ... عند التفرق في الهيجا جمالين','1169','3273','0');
INSERT INTO [t17712] VALUES (3292,'١٩ - الله أعطاك فضلا من عطيته ... على هن وهن فيما مضى وهن','1170','3273','0');
INSERT INTO [t17712] VALUES (3293,'٢٠ - عرفنا جعفرا وبني أبيه ... وأنكرنا زعانف آخرين','1170','3273','0');
INSERT INTO [t17712] VALUES (3294,'٢١ - وماذا يدري الشعراء مني ... وقد جاوزت حد الأربعين','1171','3273','0');
INSERT INTO [t17712] VALUES (3295,'٢٢ - كلا يومي طوالة وصل أروى ... ظنون، آن مطرح الظنون','1171','3273','0');
INSERT INTO [t17712] VALUES (3296,'٢٣ - أصاب الملوك فأفناهم ... وأخرج من بيته ذا جدن','1172','3273','0');
INSERT INTO [t17712] VALUES (3297,'٢٤ - ألا يا اسلمي قبل الفراق ظعينا ... تحية من أمسى إليك حزينا','1172','3273','0');
INSERT INTO [t17712] VALUES (3298,'٢٥ - امتلأ الحوض وقال قطني ... مهلا، رويدا، قد ملأت بطني','1172','3273','0');
INSERT INTO [t17712] VALUES (3299,'٢٦ - نعمت جزاء المتقين الجنة ... دار الأماني والمنى والمنه','1172','3273','0');
INSERT INTO [t17712] VALUES (3300,'٢٧ - قالوا كلامك هندا وهي مصغية ... يشفيك؟ قلت: صحيح ذاك لو كانا','1173','3273','0');
INSERT INTO [t17712] VALUES (3301,'٢٨ - إن الثمانين- وبلغتها- ... قد أحوجت سمعي إلى ترجمان','1173','3273','0');
INSERT INTO [t17712] VALUES (3302,'٢٩ - نحمي حقيقتنا وبعض القوم يسقط بين بينا','1173','3273','0');
INSERT INTO [t17712] VALUES (3303,'٣٠ - تذكر ما تذكر من سليمى ... على حين التواصل غير دان','1174','3273','0');
INSERT INTO [t17712] VALUES (3304,'٣١ - ألم تريا أني حميت حقيقتي ... وباشرت حد الموت والموت دونها','1174','3273','0');
INSERT INTO [t17712] VALUES (3305,'٣٢ - يحشر الناس لا بنين ولا آباء ... إلا وقد عنتهم شؤون','1175','3273','0');
INSERT INTO [t17712] VALUES (3306,'٣٣ - يا طلحة بن عبيد الله قد وجبت ... لك الجنان وبوئت المها العينا','1175','3273','0');
INSERT INTO [t17712] VALUES (3307,'٣٤ - أقاطن قوم سلمى أم نووا ظعنا ... إن يظعنوا فعجيب عيش من قطنا','1176','3273','0');
INSERT INTO [t17712] VALUES (3308,'٣٥ - أنكرتها بعد أعوام مضين لها ... لا الدار دارا ولا الجيران جيرانا','1176','3273','0');
INSERT INTO [t17712] VALUES (3309,'٣٦ - صددت الكأس عنا أم عمرو ... وكان الكأس مجراها اليمينا','1176','3273','0');
INSERT INTO [t17712] VALUES (3310,'٣٧ - إذا ما الغانيات برزن يوما ... وزججن الحواجب والعيونا','1176','3273','0');
INSERT INTO [t17712] VALUES (3311,'٣٨ - إن يقل هن من بني عبد شمس ... فحرى أن يكون، ذاك، وكانا','1177','3273','0');
INSERT INTO [t17712] VALUES (3312,'٣٩ - لما تبين مين الكاشحين لكم ... أنشأت أعرب عما كان مكنونا','1177','3273','0');
INSERT INTO [t17712] VALUES (3313,'٤٠ - إن هو مستوليا على أحد ... إلا على أضعف المجانين','1177','3273','0');
INSERT INTO [t17712] VALUES (3314,'٤١ - ووجه مشرق اللون ... كأن ثدياه حقان','1178','3273','0');
INSERT INTO [t17712] VALUES (3315,'٤٢ - رب وفقني فلا أعدل عن ... سنن الساعين في خير سنن','1178','3273','0');
INSERT INTO [t17712] VALUES (3316,'٤٣ - ألا رسول لنا منا فيخبرنا ... ما بعد غايتنا من رأس مجرانا','1178','3273','0');
INSERT INTO [t17712] VALUES (3317,'٤٤ - فقلت ادعي وأدعو إن أندى ... لصوت أن ينادي داعيان','1179','3273','0');
INSERT INTO [t17712] VALUES (3318,'٤٥ - أبالموت الذي لا بد أني ... ملاق- لا أباك- تخوفيني','1179','3273','0');
INSERT INTO [t17712] VALUES (3319,'٤٦ - حيثما تستقم يقدر لك الل ... هـ نجاحا في غابر الأزمان','1180','3273','0');
INSERT INTO [t17712] VALUES (3320,'٤٧ - دعتني أخاها أم عمرو ولم أكن ... أخاها، ولم أرضع لها بلبان','1180','3273','0');
INSERT INTO [t17712] VALUES (3321,'٤٨ - أجهالا تقول بني لؤي ... لعمر أبيك أم متجاهلينا','1181','3273','0');
INSERT INTO [t17712] VALUES (3322,'٤٩ - فلا أعني بذلك أسفليكم ... ولكني أريد به الذوينا','1181','3273','0');
INSERT INTO [t17712] VALUES (3323,'٥٠ - ليت شعري مقيم العذر قومي ... لي أم هم في الحب لي عاذلونا','1181','3273','0');
INSERT INTO [t17712] VALUES (3324,'٥١ - ما رأيت امرأ أحب إليه ... البذل منه إليك يا ابن سنان','1182','3273','0');
INSERT INTO [t17712] VALUES (3325,'٥٢ - أنا ابن جلا وطلاع الثنايا ... متى أضع العمامة تعرفوني','1182','3273','0');
INSERT INTO [t17712] VALUES (3326,'٥٣ - صاح شمر، ولا تزل ذاكر المو ... ت فنسيانه ضلال مبين','1184','3273','0');
INSERT INTO [t17712] VALUES (3327,'٥٤ - فو الله ما فارقتكم قاليا لكم ... ولكن ما يقضى فسوف يكون','1184','3273','0');
INSERT INTO [t17712] VALUES (3328,'٥٥ - أنا ابن أباة الضيم من آل مالك ... وإن مالك كانت كرام المعادن','1184','3273','0');
INSERT INTO [t17712] VALUES (3329,'٥٦ - ولست براجع ما فات مني ... بلهف ولا بليت ولا لواني','1184','3273','0');
INSERT INTO [t17712] VALUES (3330,'٥٧ - يا يزيدا لآمل نيل عز ... وغنى بعد فاقة وهوان','1185','3273','0');
INSERT INTO [t17712] VALUES (3331,'٥٨ - ولقد علمت بأن دين محمد ... من خير أديان البرية دينا','1185','3273','0');
INSERT INTO [t17712] VALUES (3332,'٥٩ - هل تذكرون إلى الديرين هجرتكم ... ومسحكم صلبكم رحمان قربانا','1185','3273','0');
INSERT INTO [t17712] VALUES (3333,'٦٠ - أعرف منها الجيد والعينانا ... ومنخرين أشبها ظبيانا','1186','3273','0');
INSERT INTO [t17712] VALUES (3334,'٦١ - أيها السائل عنهم وعني ... لست من قيس ولا قيس مني','1186','3273','0');
INSERT INTO [t17712] VALUES (3335,'٦٢ - قومي ذرا المجد بانوها وقد علمت ... بكنه ذلك عدنان وقحطان','1187','3273','0');
INSERT INTO [t17712] VALUES (3336,'٦٣ - لك العز إن مولاك عز وإن يهن ... فأنت لدى بحبوحة الهون كائن','1187','3273','0');
INSERT INTO [t17712] VALUES (3337,'٦٤ - لولا اصطبار لأودى كل ذي مقة ... لما استقلت مطاياهن للظعن','1187','3273','0');
INSERT INTO [t17712] VALUES (3338,'٦٥ - فأصبحوا والنوى عالي معرسهم ... وليس كل النوى تلقي المساكين','1187','3273','0');
INSERT INTO [t17712] VALUES (3339,'٦٦ - نصرتك إذ لا صاحب غير خاذل ... فبؤئت حصنا بالكماة حصينا','1188','3273','0');
INSERT INTO [t17712] VALUES (3340,'٦٧ - قالت وكنت رجلا فطينا ... هذا لعمر الله إسرائينا','1188','3273','0');
INSERT INTO [t17712] VALUES (3341,'٦٨ - وما عليك إذا أخبرتني دنفا ... وغاب بعلك يوما أن تعوديني','1188','3273','0');
INSERT INTO [t17712] VALUES (3342,'٦٩ - فليت لي بهم قوما إذا ركبوا ... شنوا الإغارة فرسانا وركبانا','1189','3273','0');
INSERT INTO [t17712] VALUES (3343,'٧٠ - ولا ينطق الفحشاء من كان منهم ... إذا جلسوا منا ولا من سوائنا','1189','3273','0');
INSERT INTO [t17712] VALUES (3344,'٧١ - ولم يبق سوى العدوا ... ن دناهم كما دانوا','1190','3273','0');
INSERT INTO [t17712] VALUES (3345,'٧٢ - حاشا قريشا فإن الله فضلهم ... على البرية بالإسلام والدين','1190','3273','0');
INSERT INTO [t17712] VALUES (3346,'٧٣ - نجيت يا رب نوحا واستجبت له ... في فلك ماخر في اليم مشحونا وعاش يدعو بآيات مبينة ... في قومه ألف عام غير خمسينا','1191','3273','0');
INSERT INTO [t17712] VALUES (3347,'٧٤ - أتطمع فينا من أراق دماءنا ... ولولاك لم يعرض لأحسابنا حسن','1191','3273','0');
INSERT INTO [t17712] VALUES (3348,'٧٥ - لاه ابن عمك لا أفضلت في حسب ... عني ولا أنت دياني فتخزوني','1191','3273','0');
INSERT INTO [t17712] VALUES (3349,'٧٦ - إنك لو دعوتني ودوني ... زوراء ذات مترع بيون لقلت لبيه لمن يدعوني','1192','3273','0');
INSERT INTO [t17712] VALUES (3350,'٧٧ - قد كنت داينت بها حسانا ... مخافة الإفلاس والليانا','1192','3273','0');
INSERT INTO [t17712] VALUES (3351,'٧٨ - ولقد أمر على اللئيم يسبني ... فمضيت ثمت قلت لا يعنيني','1192','3273','0');
INSERT INTO [t17712] VALUES (3352,'٧٩ - لعمرك ما أدري وإن كنت داريا ... بسبع رمين الجمر أم بثمان','1193','3273','0');
INSERT INTO [t17712] VALUES (3353,'٨٠ - وحملت زفرات الضحى فأطقتها ... ومالي بزفرات العشي يدان','1193','3273','0');
INSERT INTO [t17712] VALUES (3354,'٨١ - لسان السوء تهديها إلينا ... وحنت وما حسبتك أن تحينا','1193','3273','0');
INSERT INTO [t17712] VALUES (3355,'٨٢ - قالت له بالله يا ذا البردين ... لما غنثت نفسا أو اثنين','1194','3273','0');
INSERT INTO [t17712] VALUES (3356,'٨٣ - قالت بنات العم يا سلمى وإن ... كان فقيرا معدما قالت وإن','1194','3273','0');
INSERT INTO [t17712] VALUES (3357,'٨٤ - هل ترجعن ليال قد مضين لنا ... والعيش منقلب إذ ذاك أفنانا','1194','3273','0');
INSERT INTO [t17712] VALUES (3358,'٨٥ - كانت منازل ألاف عهدتهم ... إذ نحن إذ ذاك دون الناس إخوانا','1195','3273','0');
INSERT INTO [t17712] VALUES (3359,'٨٦ - تامت فؤادك لو يحزنك ما صنعت ... إحدى نساء بني ذهل بن شيبانا','1195','3273','0');
INSERT INTO [t17712] VALUES (3360,'٨٧ - يا خزر تغلب ماذا بال نسوتكم ... لا يستفقن إلى الديرين تحنانا','1195','3273','0');
INSERT INTO [t17712] VALUES (3361,'٨٨ - يا رب غابطنا لو كان يطلبكم ... لاقى مباعدة منكم وحرمانا','1196','3273','0');
INSERT INTO [t17712] VALUES (3362,'٨٩ - يا حبذا جبل الريان من جبل ... وحبذا ساكن الريان من كانا وحبذا نفحات من يمانية ... تأتيك من قبل الريان أحيانا','1196','3273','0');
INSERT INTO [t17712] VALUES (3363,'٩٠ - يا حبذا المال مبذولا بلا سرف ... في أوجه البر إسرارا وإعلانا','1196','3273','0');
INSERT INTO [t17712] VALUES (3364,'٩١ - نزلتم منزل الأضياف منا ... فعجلنا القرى أن تشتمونا','1197','3273','0');
INSERT INTO [t17712] VALUES (3365,'٩٢ - فجئت قبورهم بدءا ولما ... فناديت القبور فلم تجبنه','1197','3273','0');
INSERT INTO [t17712] VALUES (3366,'٩٣ - وقدمت الأديم لراهشيه ... وألفى قولها كذبا ومينا','1197','3273','0');
INSERT INTO [t17712] VALUES (3367,'٩٤ - شجاك أظن ربع الظاعنينا ... ولم تعبأ بعذل العاذلينا','1198','3273','0');
INSERT INTO [t17712] VALUES (3368,'٩٥ - فمن تكن الحضارة أعجبته ... فأي رجال بادية ترانا','1198','3273','0');
INSERT INTO [t17712] VALUES (3369,'٩٦ - نحن الألى فاجمع جمو ... عك ثم جهزهم إلينا','1199','3273','0');
INSERT INTO [t17712] VALUES (3370,'٩٧ - فكفى بنا فضلا على من غيرنا ... حب النبي محمد إيانا','1200','3273','0');
INSERT INTO [t17712] VALUES (3371,'٩٨ - والله لن يصلوا إليك بجمعهم ... حتى أوسد في التراب دفينا','1200','3273','0');
INSERT INTO [t17712] VALUES (3372,'٩٩ - وأتى صواحبها فقلن هذا الذي ... منح المودة غيرنا وجفانا','1200','3273','0');
INSERT INTO [t17712] VALUES (3373,'١٠٠ - ويقلن شيب قد علا ... ك وقد كبرت فقلت: إنه','1201','3273','0');
INSERT INTO [t17712] VALUES (3374,'١٠١ - اللهم لولا أنت ما اهتدينا ... ولا تصدقنا ولا صلينا فاغفر فداء لك ما أبقينا ... وثبت الأقدام إن لاقينا وألقين سكينة علينا ... إنا إذا صيح بنا أتينا إن الذين قد بغوا علينا ... إذا أرادوا فتنة أبينا ونحن عن فضلك ما استغنينا','1201','3273','0');
INSERT INTO [t17712] VALUES (3375,'١٠٢ - رجلان من مكة أخبرانا ... إنا رأينا رجلا عريانا','1202','3273','0');
INSERT INTO [t17712] VALUES (3376,'١٠٣ - قد علمت سلمى وجاراتها ... ما قطر الفارس إلا أنا','1202','3273','0');
INSERT INTO [t17712] VALUES (3377,'١٠٤ - جود يمناك فاض في الخلق حتى ... بائس دان بالإساءة دينا','1202','3273','0');
INSERT INTO [t17712] VALUES (3378,'١٠٥ - لتقم أنت يا ابن خير قريش ... فلتقضي حوائج المسلمينا','1202','3273','0');
INSERT INTO [t17712] VALUES (3379,'١٠٦ - عافت الماء في الشتاء فقلنا ... برديه تصادفيه سخينا','1202','3273','0');
INSERT INTO [t17712] VALUES (3380,'١٠٧ - قول يا للرجال ينهض منا ... مسرعين الكهول والشبانا','1203','3273','0');
INSERT INTO [t17712] VALUES (3381,'١٠٨ - فللموت تغذو الوالدات سخالها ... كما لخراب الدور تبنى المساكن','1203','3273','0');
INSERT INTO [t17712] VALUES (3382,'١٠٩ - ورب السموات العلى وبروجها ... والأرض وما فيها المقدر كائن','1203','3273','0');
INSERT INTO [t17712] VALUES (3383,'١١٠ - بلاد بها كنا وكنا نحبها ... إذ الناس ناس والزمان زمان','1203','3273','0');
INSERT INTO [t17712] VALUES (3384,'١١١ - ما كل ما يتمنى المرء يدركه ... تجري الرياح بما لا تشتهي السفن','1203','3273','0');
INSERT INTO [t17712] VALUES (3385,'١١٢ - إن يسمعوا سبة طاروا بها فرحا ... مني وما سمعوا من صالح دفنوا','1204','3273','0');
INSERT INTO [t17712] VALUES (3386,'١١٣ - علا زيدنا يوم النقا رأس زيدكم ... بأبيض ماضي الشفرتين يمان','1204','3273','0');
INSERT INTO [t17712] VALUES (3387,'١١٤ - سريت بهم حتى تكل مطيهم ... وحتى الجياد ما يقدن بأرسان','1204','3273','0');
INSERT INTO [t17712] VALUES (3388,'١١٥ - قفا نبك من ذكرى حبيب وعرفان ... وربع عفت آثاره منذ أزمان','1205','3273','0');
INSERT INTO [t17712] VALUES (3389,'١١٦ - ألا رب مولود وليس له أب ... وذي ولد لم يلده أبوان وذي شامة غراء في حر وجهه ... مجللة لا تنقضي لأوان ويكمل في تسع وخمس شبابه ... ويهرم في سبع معا وثمان','1205','3273','0');
INSERT INTO [t17712] VALUES (3390,'١١٧ - تحن فتبدي ما بها من صبابة ... وأخفي الذي لولا الأسى لقضاني','1205','3273','0');
INSERT INTO [t17712] VALUES (3391,'١١٨ - وكل رفيقي كل رحل وإن هما ... تعاطى القنا قوماهما أخوان','1206','3273','0');
INSERT INTO [t17712] VALUES (3392,'١١٩ - تعش فإن عاهدتني لا تخونني ... نكن مثل من يا ذئب يصطحبان','1206','3273','0');
INSERT INTO [t17712] VALUES (3393,'١٢٠ - إلى الله أشكو بالمدينة حاجة ... وبالشام أخرى كيف تلتقيان','1206','3273','0');
INSERT INTO [t17712] VALUES (3394,'١٢١ - رويد بني شيبان بعض وعيدكم ... تلاقوا غدا خيلي على سفوان تلاقوا جيادا لا تحيد عن الوغي ... إذا ما غدت في المأزق المتداني تلاقوهم فتعرفوا كيف صبرهم ... على ما جنت فيهم يد الحدثان','1207','3273','0');
INSERT INTO [t17712] VALUES (3395,'١٢٢ - خليلي هل طب فإني وأنتما ... وإن لم تبوحا بالهوى دنفان','1207','3273','0');
INSERT INTO [t17712] VALUES (3396,'١٢٣ - أهم بأمر الحزم لو أستطيعه ... وقد حيل بين العير والنزوان','1207','3273','0');
INSERT INTO [t17712] VALUES (3397,'١٢٤ - أنى جزوا عامرا سوأى بفعلهم ... أم كيف يجزونني السوأى من الحسن أم كيف ينفع ما تعطي العلوق به ... رئمان أنف إذا ماضن باللبن','1208','3273','0');
INSERT INTO [t17712] VALUES (3398,'١٢٥ - من يفعل الحسنات الله يشكرها ... والشر بالشر عند الله مثلان','1209','3273','0');
INSERT INTO [t17712] VALUES (3399,'١٢٦ - كفى بجسمي نحولا أنني رجل ... لولا مخاطبتي إياك لم ترني','1210','3273','0');
INSERT INTO [t17712] VALUES (3400,'١٢٧ - أبلى الهوى أسفا يوم النوى بدني ... وفرق الهجر بين الجفن والوسن','1210','3273','0');
INSERT INTO [t17712] VALUES (3401,'١٢٨ - لو في طهية أحلام لما عرضوا ... دون الذي أنا أرميه ويرميني','1210','3273','0');
INSERT INTO [t17712] VALUES (3402,'١٢٩ - عندي اصطبار وأما أنني جزع ... يوم النوى فلوجد كاد يبريني','1210','3273','0');
INSERT INTO [t17712] VALUES (3403,'١٣٠ - فإما أن تكون أخي بصدق ... فأعرف منك غثي من سميني وإلا فاطرحني واتخذني ... عدوا أتقيك وتتقيني','1210','3273','0');
INSERT INTO [t17712] VALUES (3404,'١٣١ - وكل أخ مفارقه أخوه ... لعمر أبيك إلا الفرقدان','1211','3273','0');
INSERT INTO [t17712] VALUES (3405,'١٣٢ - على حين انحنيت وشاب رأسي ... فأي فتى دعوت وأي حين','1212','3273','0');
INSERT INTO [t17712] VALUES (3406,'١٣٣ - فلو أنا على حجر ذبحنا ... جرى الدميان بالخبر اليقين','1212','3273','0');
INSERT INTO [t17712] VALUES (3407,'١٣٤ - فإن أهلك فرب فتى سيبكي ... علي مهذب رخص البنان','1212','3273','0');
INSERT INTO [t17712] VALUES (3408,'١٣٥ - على ما قام يشتمني لئيم ... كخنزير تمرغ في دمان','1212','3273','0');
INSERT INTO [t17712] VALUES (3409,'١٣٦ - دعي ماذا علمت سأتقيه ... ولكن بالمغيب نبئيني','1212','3273','0');
INSERT INTO [t17712] VALUES (3410,'١٣٧ - أليس الليل يجمع أم عمرو ... وإيانا، فذاك بنا تداني نعم، وأرى الهلال كما تراه ... ويعلوها النهار كما علاني','1213','3273','0');
INSERT INTO [t17712] VALUES (3411,'١٣٨ - مضت سنة لعام ولدت فيه ... وعشر بعد ذاك وحجتان','1213','3273','0');
INSERT INTO [t17712] VALUES (3412,'١٣٩ - ولقد رمقتك في المجالس كلها ... فإذا وأنت تعين من يبغيني','1213','3273','0');
INSERT INTO [t17712] VALUES (3413,'١٤٠ - ما تنقم الحرب العوان مني ... بازل عامين حديث سني لمثل هذا ولدتني أمي','1214','3273','0');
INSERT INTO [t17712] VALUES (3414,'١٤١ - عمدا فعلت ذاك بيد أني ... أخاف لو هلكت لم ترني','1214','3273','0');
INSERT INTO [t17712] VALUES (3415,'١٤٢ - كيف تراني قالبا مجني ... أضرب أمري ظهره لبطن قد قتل الله زيادا عنى','1214','3273','0');
INSERT INTO [t17712] VALUES (3416,'١٤٣ - يا رب من يبغض أذوادنا ... رحن على بغضائه واغتدين','1215','3273','0');
INSERT INTO [t17712] VALUES (3417,'١٤٤ - فهل يمنعني ارتيادي البلا ... د حذر الموت أن يأتين','1215','3273','0');
