INSERT INTO [t17712] VALUES (3522,'٢٤٩ - وكنت كذي رجلين رجل صحيحة ... ورجل رمت فيها يد الحدثان','1253','3273','0');
INSERT INTO [t17712] VALUES (3523,'٢٥٠ - ولا تبلى بسألتهم وإن هم ... صلوا بالحرب حينا بعد حين','1253','3273','0');
INSERT INTO [t17712] VALUES (3524,'٢٥١ - فقلت له: لا والذي حج حاتم ... أخونك عهدا إنني غير خوان','1253','3273','0');
INSERT INTO [t17712] VALUES (3525,'٢٥٢ - قد صرح السير عن كتمان وابتذلت ... وقع المحاجن بالمهرية الذقن','1254','3273','0');
INSERT INTO [t17712] VALUES (3526,'٢٥٣ - رماني بأمر كنت منه ووالدي ... بريئا ومن أجل الطوي رماني','1254','3273','0');
