INSERT INTO [t17712] VALUES (3535,'٢٦٢ - يا لأناس أبوا إلا مثابرة ... على التوغل في بغي وعدوان','1257','3273','0');
INSERT INTO [t17712] VALUES (3536,'٢٦٣ - رأيت بني البكري في حومة الوغي ... كفا غري الأفواه عند عرين','1258','3273','0');
INSERT INTO [t17712] VALUES (3537,'٢٦٤ - لا تظلموا مسورا فإنه لكم ... من الذين وفوا في السر والعلن','1258','3273','0');
INSERT INTO [t17712] VALUES (3538,'٢٦٥ - تالله قد علمت نفس إذا قذفت ... ريح الشتاء بيوت الحي بالعنن','1258','3273','0');
INSERT INTO [t17712] VALUES (3539,'٢٦٦ - إن يغنيا عني المستوطنا عدن ... فإنني لست يوما عنهما بغني','1259','3273','0');
INSERT INTO [t17712] VALUES (3540,'٢٦٧ - ... وكتمانها أكنى بأم فلان','1259','3273','0');
INSERT INTO [t17712] VALUES (3541,'٢٦٨ - تراه كالثغام يعل مسكا ... يسوء الفاليات إذا فليني','1259','3273','0');
INSERT INTO [t17712] VALUES (3542,'٢٦٩ - لا تلمني عتيق حسبي الذي بي ... إن بي يا عتيق ما قد كفاني','1260','3273','0');
INSERT INTO [t17712] VALUES (3543,'٢٧٠ - يطفن بحوزي المراتع لم يرع ... بواديه من قرع القسي الكنائن','1260','3273','0');
INSERT INTO [t17712] VALUES (3544,'٢٧١ - ذعرت به القطا ونفيت عنه ... مقام الذئب كالرجل اللعين','1261','3273','0');
INSERT INTO [t17712] VALUES (3545,'٢٧٢ - ولا يجزون من حسن بسوءى ... ولا يجزون من غلظ بلين','1261','3273','0');
INSERT INTO [t17712] VALUES (3546,'٢٧٣ - كأن حمولهم لما استقلت ... ثلاثة أكلب يتطاردان','1262','3273','0');
INSERT INTO [t17712] VALUES (3547,'٢٧٤ - سقى العلم الفرد الذي في ظلاله ... غزالان مكحولان مختضبان','1262','3273','0');
INSERT INTO [t17712] VALUES (3548,'٢٧٥ - أنا ابن أباة الضيم من آل مالك ... وإن مالك كانت كرام المنازل','1262','3273','0');
INSERT INTO [t17712] VALUES (3549,'٢٧٦ - بثين الزمي، لا، إن، لا، إن لزمته ... على كثرة الواشين أي معون','1262','3273','0');
INSERT INTO [t17712] VALUES (3550,'٢٧٧ - لولا فوارس تغلب ابنة وائل ... ورد العدو عليك كل مكان','1263','3273','0');
INSERT INTO [t17712] VALUES (3551,'٢٧٨ - ونحن منعنا البحر أن تشربوا به ... وقد كان منكم ماؤه بمكان','1263','3273','0');
INSERT INTO [t17712] VALUES (3552,'٢٧٩ - ألا يا ديار الحي بالسبعان ... أمل عليها بالبلى الملوان','1263','3273','0');
INSERT INTO [t17712] VALUES (3553,'٢٨٠ - ألا يا ديار الحي بالسبعان ... عفت حججا بعدي وهن ثماني','1263','3273','0');
INSERT INTO [t17712] VALUES (3554,'٢٨١ - أيها السائل عنهم وعني ... لست من قيس ولا قيس مني','1264','3273','0');
INSERT INTO [t17712] VALUES (3555,'٢٨٢ - ألم تر أن البغل يتبع إلفه ... كما عامر واللؤم مؤتلفان','1264','3273','0');
INSERT INTO [t17712] VALUES (3556,'٢٨٣ - ما سد حي ولا ميت مسدهما ... إلا الخلائف من بعد النبيين','1264','3273','0');
INSERT INTO [t17712] VALUES (3557,'٢٨٤ - وأهجو من هجاني من سواهم ... وأعرض منهم عمن هجاني','1265','3273','0');
INSERT INTO [t17712] VALUES (3558,'٢٨٥ - ربه امرأ بك نال أمنع عزة ... وغنى بعيد خصاصة وهوان','1265','3273','0');
INSERT INTO [t17712] VALUES (3559,'٢٨٦ - جيء ثم حالف وقف بالقوم إنهم ... لمن أجاروا ذوو عز بلا هون','1266','3273','0');
INSERT INTO [t17712] VALUES (3560,'٢٨٧ - أألخير الذي أنا أبتغيه ... أم الشر الذي هو يبتغيني','1266','3273','0');
INSERT INTO [t17712] VALUES (3561,'٢٨٨ - ومن حسد يجور علي قومي ... وأي الدهر ذو لم يحسدوني','1266','3273','0');
INSERT INTO [t17712] VALUES (3562,'٢٨٩ - ألحق عذابك بالقوم الذين طغوا ... وعائذا بك أن يعلوا فيطغوني','1267','3273','0');
INSERT INTO [t17712] VALUES (3563,'٢٩٠ - تخذت غراز إثرهم دليلا ... وفروا في الحجاز ليعجزوني','1267','3273','0');
INSERT INTO [t17712] VALUES (3564,'٢٩١ - تمنوا لي الموت الذي يشعب الفتى ... وكل امرئ والموت يجتمعان','1267','3273','0');
INSERT INTO [t17712] VALUES (3565,'٢٩٢ - محياه يلقى ينال السؤال راجيه ريثما ينثني','1267','3273','0');
INSERT INTO [t17712] VALUES (3566,'٢٩٣ - وظل لنسوة النعمان منا ... على سفوان يوم أروناني','1268','3273','0');
INSERT INTO [t17712] VALUES (3567,'٢٩٤ - وأنبئت قيسا ولم أبله ... كما زعموا خير أهل اليمن','1268','3273','0');
INSERT INTO [t17712] VALUES (3568,'٢٩٥ - لها ثنايا أربع حسان ... وأربع فثغرها ثمان','1268','3273','0');
INSERT INTO [t17712] VALUES (3569,'٢٩٦ - وصاني العجاج فيما وصني ..','1269','3273','0');
INSERT INTO [t17712] VALUES (3570,'٢٩٧ - ألا ليت اللحى كانت حشيشا ... فترعاها خيول المسلمينا','1269','3273','0');
INSERT INTO [t17712] VALUES (3571,'قافية الهاء','1270','0','0');
INSERT INTO [t17712] VALUES (3572,'١ - واها لسلمى ثم واها واها ... يا ليت عيناها لنا وفاها','1270','3571','0');
INSERT INTO [t17712] VALUES (3573,'٢ - ألقى الصحيفة كي يخفف رحله ... والزاد حتى نعله ألقاها','1270','3571','0');
INSERT INTO [t17712] VALUES (3574,'٣ - علفتها تبنا وماء باردا ... حتى غدت همالة عيناها','1271','3571','0');
INSERT INTO [t17712] VALUES (3575,'٤ - إذا رضيت علي بنو قشير ... لعمر الله أعجبني رضاها','1271','3571','0');
INSERT INTO [t17712] VALUES (3576,'٥ - تقول عرسي وهي لي في عومره ... بئس امرأ وإنني بئس المره','1271','3571','0');
INSERT INTO [t17712] VALUES (3577,'٦ - أحجاج لا تعطي العصاة مناهم ... ولا الله يعطي للعصاة مناها','1272','3571','0');
INSERT INTO [t17712] VALUES (3578,'٧ - بربك هل ضممت إليك ليلى ... قبيل الصبح أو قبلت فاها وهل مالت عليك ذؤابتاها ... كمثل الأقحوانة في نداها','1272','3571','0');
INSERT INTO [t17712] VALUES (3579,'٨ - عهدت سعاد ذات هوى معنى ... فزدت وعاد سلوانا هواها','1272','3571','0');
INSERT INTO [t17712] VALUES (3580,'٩ - فما رجعت بخائبة ركاب ... حكيم بن المسيب منتهاها','1272','3571','0');
INSERT INTO [t17712] VALUES (3581,'١٠ - إن أباها وأبا أباها ... قد بلغا في المجد غايتاها','1272','3571','0');
INSERT INTO [t17712] VALUES (3582,'١١ - وكل قوم أطاعوا أمر مرشدهم ... إلا نميرا أطاعت أمر غاويها الظاعنين ولما يظعنوا أحدا ... والقائلون، لمن دار نخليها','1273','3571','0');
INSERT INTO [t17712] VALUES (3583,'١٢ - فأيي ما وأيك كان شرا ... فسيق إلى المقامة لا يراها','1273','3571','0');
INSERT INTO [t17712] VALUES (3584,'١٣ - لعمرك ما إن أبو مالك ... بوان ولا بضعيف قواه','1274','3571','0');
INSERT INTO [t17712] VALUES (3585,'١٤ - إذا ما ترعرع فينا الغلام ... فما إن يقال له: من هوه؟','1274','3571','0');
INSERT INTO [t17712] VALUES (3586,'١٥ - ولقد أرى تغنى به سيفانة ... تصبي الحليم ومثلها أصباه','1275','3571','0');
INSERT INTO [t17712] VALUES (3587,'١٦ - أيا من لست أقلاه ... ولا في البعد أنساه لك الله على ذاك ... لك الله لك الله','1275','3571','0');
INSERT INTO [t17712] VALUES (3588,'١٧ - فلا تصحب أخا الجهل ... وإياك وإياه','1276','3571','0');
INSERT INTO [t17712] VALUES (3589,'١٨ - إنما يعرف ذا الفضل من الناس ذووه','1276','3571','0');
INSERT INTO [t17712] VALUES (3590,'١٩ - ألا يا عمرو عمراه ... وعمرو بن الزبيراه','1276','3571','0');
INSERT INTO [t17712] VALUES (3591,'٢٠ - لها أشارير من لحم تتمره ... من الثعالي ووخز من أرانيها','1277','3571','0');
INSERT INTO [t17712] VALUES (3592,'٢١ - يا باري القوس بريا لست تحسنها ... لا تفسدنها وأعط القوس باريها','1278','3571','0');
INSERT INTO [t17712] VALUES (3593,'٢٢ - بآية الخال منها عند برقعها ... وقول ركبتها قض حين تثنيها','1278','3571','0');
INSERT INTO [t17712] VALUES (3594,'٢٣ - صبحنا الخزرجية مرهفات ... أبار ذوي أرومتها ذووها','1279','3571','0');
INSERT INTO [t17712] VALUES (3595,'٢٤ - أشد على الكتيبة لا أبالي ... أحتفي كان فيها أم سواها','1279','3571','0');
INSERT INTO [t17712] VALUES (3596,'٢٥ - ما بال هم عميد بات يطرقني ... بالواد من هند إذ تعدو عواديها','1280','3571','0');
INSERT INTO [t17712] VALUES (3597,'٢٦ - إنا- بني منقر- قوم ذوو حسب ... فينا سراة بني سعد وناديها','1280','3571','0');
INSERT INTO [t17712] VALUES (3598,'٢٧ - وأشرب الماء ما بي نحوه عطش ... إلا لأن عيونه سيل واديها','1280','3571','0');
INSERT INTO [t17712] VALUES (3599,'٢٨ - إني لأكني بأجبال عن اجبلها ... وباسم أودية عن ذكر واديها','1280','3571','0');
INSERT INTO [t17712] VALUES (3600,'٢٩ - يا دار هند عفت إلا أثافيها ... بين الطوي فصارات فواديها','1280','3571','0');
INSERT INTO [t17712] VALUES (3601,'٣٠ - قبيلة ألأم الأحياء أكرمها ... وأغدر الناس بالجيران وافيها','1281','3571','0');
INSERT INTO [t17712] VALUES (3602,'٣١ - أليس عجيبا بأن الفتى ... يصاب ببعض الذي في يديه','1281','3571','0');
INSERT INTO [t17712] VALUES (3603,'٣٢ - لولا تعوجين يا سلمى على دنف ... فتخمدي نار وجد كاد يفنيه','1281','3571','0');
INSERT INTO [t17712] VALUES (3604,'٣٣ - إذا سدته سدت مطواعة ... ومهما وكلت إليه كفاه','1282','3571','0');
INSERT INTO [t17712] VALUES (3605,'٣٤ - يا با المغيرة رب أمر معضل ... فرجته بالمكر مني والدها','1282','3571','0');
INSERT INTO [t17712] VALUES (3606,'٣٥ - مبارك هو ومن سماه ... على اسمك اللهم يا الله','1282','3571','0');
INSERT INTO [t17712] VALUES (3607,'٣٦ - عدا سليمى وعدا أباها','1282','3571','0');
INSERT INTO [t17712] VALUES (3608,'٣٧ - طاروا علاهن فطر علاها ... واشدد بمثنى حقب حقواها','1282','3571','0');
INSERT INTO [t17712] VALUES (3609,'٣٨ - تمد بالأعناق أو تلويها ... وتشتكي لو أننا نشكيها','1283','3571','0');
INSERT INTO [t17712] VALUES (3610,'٣٩ - في كل يوم ما وكل ليلاه ... حتى يقول من رآه إذ راه يا ويحه من جمل ما أشقاه','1283','3571','0');
INSERT INTO [t17712] VALUES (3611,'قافية الواو، والياء، والألف اللينة','1284','0','0');
INSERT INTO [t17712] VALUES (3612,'١ - وكم موطن لولاي طحت كما هوى ... بأجرامه من قلة النيق منهوي','1284','3611','0');
INSERT INTO [t17712] VALUES (3613,'٢ - فليت كفافا كان خيرك كله ... وشرك عني ما ارتوى الماء مرتوي','1284','3611','0');
INSERT INTO [t17712] VALUES (3614,'٣ - جمعت وفحشا غيبة ونميمة ... ثلاث خلال لست منها بمرعوي','1285','3611','0');
INSERT INTO [t17712] VALUES (3615,'٤ - يطالبني عمي ثمانين ناقة ... ومالي يا عفراء إلا ثمانيا','1286','3611','0');
INSERT INTO [t17712] VALUES (3616,'٥ - وإنك إذ ما تأت ما أنت آمر ... به تلف من إياه تأمر آتيا','1287','3611','0');
INSERT INTO [t17712] VALUES (3617,'٦ - تعز فلا شيء على الأرض باقيا ... ولا وزر مما قضى الله واقيا','1287','3611','0');
INSERT INTO [t17712] VALUES (3618,'٧ - إذا الجود لم يرزق خلاصا من الأذى ... فلا الحمد مكسوبا ولا المال باقيا','1287','3611','0');
INSERT INTO [t17712] VALUES (3619,'٨ - فيا راكبا إما عرضت فبلغا ... نداماي من نجران أن لا تلاقيا','1287','3611','0');
INSERT INTO [t17712] VALUES (3620,'٩ - عميرة ودع إن تجهزت غازيا ... كفى الشيب والإسلام للمرء ناهيا','1288','3611','0');
INSERT INTO [t17712] VALUES (3621,'١٠ - لتقعدن مقعد القصي ... مني ذي القاذورة المقلي أو تحلفي بربك العلي ... أني أبو ذيالك الصبي','1288','3611','0');
INSERT INTO [t17712] VALUES (3622,'١١ - ما حم من موت حمى واقيا ... ولا ترى من أحد باقيا','1289','3611','0');
INSERT INTO [t17712] VALUES (3623,'١٢ - تقول ابنتي: إن انطلاقك واحدا ... إلى الروع يوما تاركي لا أبا ليا','1290','3611','0');
INSERT INTO [t17712] VALUES (3624,'١٣ - ومستبدل من بعد غضيا صريمة ... فأحر به من طول فقر وأحريا','1292','3611','0');
INSERT INTO [t17712] VALUES (3625,'١٤ - ألا حبذا أهل الملا غير أنه ... إذا ذكرت مي فلا حبذا هيا','1292','3611','0');
INSERT INTO [t17712] VALUES (3626,'١٥ - مررت على وادي السباع ولا أرى ... كوادي السباع- حين يظلم- واديا أقل به ركب أتوه تئية ... وأخوف- إلا ما وقى الله- ساريا','1292','3611','0');
INSERT INTO [t17712] VALUES (3627,'١٦ - ألا طال كتماني بثينة حاجة ... من الحاج ما تدري بثينة ما هيا أحاذر أن تعلم بها فتردها ... فتتركها ثقلا علي كما هيا','1293','3611','0');
INSERT INTO [t17712] VALUES (3628,'١٧ - تقول عجوز مدرجي متروحا ... على بابها من عند رحلي وغاديا أذو زوجة بالمصر أم ذو خصومة ... أراك لها بالبصرة العام ثاويا فقلت لها: لا، إن أهلي لجيرة ... لأكثبة الدهنا جميعا وماليا وما كنت مذ أبصرتني في خصومة ... أراجع فيها يا ابنة العم قاضيا','1294','3611','0');
INSERT INTO [t17712] VALUES (3629,'١٨ - كأني وقد خلفت تسعين حجة ... خلعت بها عن منكبي ردائيا','1294','3611','0');
INSERT INTO [t17712] VALUES (3630,'١٩ - أراني إذا أصبحت أصبحت ذا هوى ... فثم إذا أمسيت أمسيت غاديا','1295','3611','0');
INSERT INTO [t17712] VALUES (3631,'٢٠ - وآس سراة الحي حيث لقيتهم ... ولا تك عن حمل الرباعة وانيا','1295','3611','0');
INSERT INTO [t17712] VALUES (3632,'٢١ - أتانا فلم نعدل سواه بغيره ... نبي بدا في ظلمة الليل هاديا','1295','3611','0');
INSERT INTO [t17712] VALUES (3633,'٢٢ - وقائلة خولان فانكح فتاتهم ... وأكرومة الحيين خلو كما هيا','1296','3611','0');
INSERT INTO [t17712] VALUES (3634,'٢٣ - كلانا غني عن أخيه حياته ... ونحن إذا متنا أشد تغانيا','1297','3611','0');
INSERT INTO [t17712] VALUES (3635,'٢٤ - لئن كان ما حدثته اليوم صادقا ... أصم في نهار القيظ، للشمس باديا','1297','3611','0');
INSERT INTO [t17712] VALUES (3636,'٢٥ - وحلت سواد القلب لا أنا باغيا ... سواها ولا في حبها متراخيا','1297','3611','0');
INSERT INTO [t17712] VALUES (3637,'٢٦ - لقيت المرورى والشناخيب دونه ... وجبت هجيرا يترك الماء صاديا','1298','3611','0');
INSERT INTO [t17712] VALUES (3638,'٢٧ - يقولون لا تبعد وهم يدفنونني ... وأين مكان البعد إلا مكانيا','1298','3611','0');
INSERT INTO [t17712] VALUES (3639,'٢٨ - وتضحك مني شيخة عبشمية ... كأن لم ترى قبلي أسيرا يمانيا','1298','3611','0');
INSERT INTO [t17712] VALUES (3640,'٢٩ - ولو أن واش باليمامة داره ... وداري بأعلى حضرموت اهتدى ليا','1299','3611','0');
INSERT INTO [t17712] VALUES (3641,'٣٠ - لما نافع يسعى اللبيب فلا تكن ... لشيء بعيد نفعه الدهر ساعيا','1299','3611','0');
INSERT INTO [t17712] VALUES (3642,'٣١ - فإما كرام موسرون لقيتهم ... فحسبي من ذي عندهم ما كفانيا','1299','3611','0');
INSERT INTO [t17712] VALUES (3643,'٣٢ - ألم تر أني يوم جو سويقة ... بكيت فنادتني هنيدة ماليا','1300','3611','0');
INSERT INTO [t17712] VALUES (3644,'٣٣ - علي إذا ما زرت ليلى بخفية ... زيارة بيت الله رجلان حافيا','1301','3611','0');
INSERT INTO [t17712] VALUES (3645,'٣٤ - ولست مقرا للرجال ظلامة ... أبى ذاك عمي الأكرمان وخاليا','1301','3611','0');
INSERT INTO [t17712] VALUES (3646,'٣٥ - بأهبة حزم لذ وإن كنت آمنا ... فما كل حين من توالي مواليا','1301','3611','0');
INSERT INTO [t17712] VALUES (3647,'٣٦ - فأبلوني بليتكم لعلي ... أصالحكم وأستدرج نويا','1302','3611','0');
INSERT INTO [t17712] VALUES (3648,'٣٧ - يا رب قائلة غدا ... يا لهف أم معاويه','1302','3611','0');
INSERT INTO [t17712] VALUES (3649,'٣٨ - إني إذا ما القوم كانوا أنجيه ... واضطرب القوم اضطراب الأرشيه وشد فوق بعضهم بالأروية ... هناك أوصيني ولا توصي بيه','1303','3611','0');
INSERT INTO [t17712] VALUES (3650,'٣٩ - مهما لي الليلة مهما ليه ... أودى بنعلي وسرباليه','1303','3611','0');
INSERT INTO [t17712] VALUES (3651,'٤٠ - ألفيتا عيناك عن القفا ... أولى فأولى لك ذا واقيه','1304','3611','0');
INSERT INTO [t17712] VALUES (3652,'٤١ - أليس عجيبا بأن الفتى ... يصاب ببعض الذي في يديه','1304','3611','0');
INSERT INTO [t17712] VALUES (3653,'٤٢ - أطربا وأنت قنسري ... والدهر بالإنسان دواري','1304','3611','0');
INSERT INTO [t17712] VALUES (3654,'٤٣ - له ما رأت عين البصير وفوقه ... سماء الإله فوق سبع سمائيا','1305','3611','0');
INSERT INTO [t17712] VALUES (3655,'٤٤ - هي الدار إذ مي لأهلك جيرة ... ليالي لا أمثالهن لياليا','1305','3611','0');
INSERT INTO [t17712] VALUES (3656,'٤٥ - ألا ليت شعري هل يرى الناس ما أرى ... من الأمر أو يبدو لهم ما بدا ليا','1306','3611','0');
INSERT INTO [t17712] VALUES (3657,'٤٦ - فلو كان عبد الله مولى هجوته ... ولكن عبد الله مولى مواليا','1306','3611','0');
INSERT INTO [t17712] VALUES (3658,'٤٧ - ونحن اقتسمنا المال نصفين بيننا ... فقلت لهم: هذا لها هاوذاليا','1306','3611','0');
INSERT INTO [t17712] VALUES (3659,'٤٨ - لها بعد إسناد الكليم وهدئه ... ورنة من يبكي إذا كان باكيا هدير هدير الثور ينفض رأسه ... يذب بروقيه الكلاب الضواريا','1307','3611','0');
INSERT INTO [t17712] VALUES (3660,'٤٩ - وكانت قشير شامتا بصديقها ... وآخر مرزيا وآخر رازيا','1307','3611','0');
INSERT INTO [t17712] VALUES (3661,'٥٠ - فتى كملت خيراته غير أنه ... جواد فلا يبقي من المال باقيا','1307','3611','0');
INSERT INTO [t17712] VALUES (3662,'٥١ - ألا ليت شعري هل تغيرت الرحا ... رحا الحزن أو أضحت بفلج كما هيا','1308','3611','0');
INSERT INTO [t17712] VALUES (3663,'٥٢ - أبلغ الحارث بن ظالم المو ... عد والناذر النذور عليا أنما تقتل النيام ولا تقتل ... يقظان ذا سلاح كميا','1308','3611','0');
INSERT INTO [t17712] VALUES (3664,'٥٣ - كأن العقيليين حين لقيتهم ... فراخ القطا لاقين أجدل بازيا','1308','3611','0');
INSERT INTO [t17712] VALUES (3665,'٥٤ - ماذا على من شم تربة أحمد ... أن لا يشم مدى الزمان غواليا','1308','3611','0');
INSERT INTO [t17712] VALUES (3666,'٥٥ - إذا كان لا يرضيك حتى تردني ... إلى قطري لا إخالك راضيا','1309','3611','0');
INSERT INTO [t17712] VALUES (3667,'٥٦ - لا هيثم الليلة للمطي ... ولا فتى مثل ابن خيبري','1309','3611','0');
INSERT INTO [t17712] VALUES (3668,'٥٧ - متى أنام لا يؤرقني الكري ... ليلا ولا أسمع أجراس المطي','1309','3611','0');
INSERT INTO [t17712] VALUES (3669,'٥٨ - فاومأت إيماء خفيا لحبتر ... فلله عينا حبتر أيما فتى','1309','3611','0');
INSERT INTO [t17712] VALUES (3670,'٥٩ - ويركب يوم الروع منا فوارس ... بصيرون في طعن الأباهر والكلى','1310','3611','0');
INSERT INTO [t17712] VALUES (3671,'٦٠ - فلما تبينا الهدى كان كلنا ... على طاعة الرحمن والحق والتقى','1310','3611','0');
INSERT INTO [t17712] VALUES (3672,'٦١ - على مثل أصحاب البعوضة فاخمشي ... لك الويل حر الوجه أو يبك من بكى','1310','3611','0');
INSERT INTO [t17712] VALUES (3673,'٦٢ - ثمت لاح في الملبين إلى ... حيث تحجى المأزمان ومنى','1310','3611','0');
INSERT INTO [t17712] VALUES (3674,'٦٣ - يا ظبية أشبه شيء بالمها ... ترعى الخزامى بين أشجار النقى','1310','3611','0');
INSERT INTO [t17712] VALUES (3675,'٦٤ - إن امرأ القيس جرى إلى مدى ... فاعتاقه حمامه دون المدى','1311','3611','0');
INSERT INTO [t17712] VALUES (3676,'٦٥ - ينوي التي فضلها رب العلا ... لما دحا تربتها على البنى','1312','3611','0');
INSERT INTO [t17712] VALUES (3677,'٦٦ - فإن عثرت بعدها إن وألت ... نفسي من هاتا، فقولا: لا، لعا','1312','3611','0');
INSERT INTO [t17712] VALUES (3678,'٦٧ - ومن مالئ عينيه من شيء غيره ... إذا راح نحو الجمرة البيض كالدمى','1312','3611','0');
INSERT INTO [t17712] VALUES (3679,'٦٨ - لم يعن بالعلياء إلا سيدا ... ولا شفى ذا الغي إلا ذو هدى','1313','3611','0');
INSERT INTO [t17712] VALUES (3680,'٦٩ - إذا رمت ممن لا يريم متيما ... سلوا فقد أبعدت في رومك المرمى','1313','3611','0');
INSERT INTO [t17712] VALUES (3681,'٧٠ - أطرق كرا أطرق كرا ... إن النعام في القرى','1313','3611','0');
INSERT INTO [t17712] VALUES (3682,'٧١ - يشكو إلي جملي طول السرى ... صبر جميل فكلانا مبتلى','1313','3611','0');
INSERT INTO [t17712] VALUES (3683,'٧٢ - قد وعدتني أم عمرو أن تا ... تدهن رأسي وتفليني وا وتمسح القنفاء حتى تنتا','1313','3611','0');
INSERT INTO [t17712] VALUES (3684,'٧٣ - من اللواتي والتي واللاتي ... زعمن أني كبرت لداتي','1314','3611','0');
INSERT INTO [t17712] VALUES (3685,'٧٤ - أغض ما اسطعت فالكريم الذي ... يألف الحلم إن جفاه بذي','1314','3611','0');
INSERT INTO [t17712] VALUES (3686,'٧٥ - فكأنها بين النساء سبيكة ... تمشي بسدة بيتها فتعي','1314','3611','0');
INSERT INTO [t17712] VALUES (3687,'٧٦ - على أطرقا باليات الخيا ... م إلا الثمام وإلا العصي','1315','3611','0');
INSERT INTO [t17712] VALUES (3688,'٧٧ - وركضك لولا هو لقيت الذي لقوا ... فأصبحت قد جاورت قوما أعاديا','1316','3611','0');
INSERT INTO [t17712] VALUES (3689,'٧٨ - وأركب حمارا بين سرج وفروة ... وأعر من الخاتام صغرى شماليا','1316','3611','0');
INSERT INTO [t17712] VALUES (3690,'٧٩ - من آل أبي موسى ترى الناس حوله ... كأنهم الكروان أبصرن بازيا','1317','3611','0');
INSERT INTO [t17712] VALUES (3691,'٨٠ - ألا هل إلى مي سبيل وساعة ... تكلمني فيها شفاء لما بيا','1317','3611','0');
INSERT INTO [t17712] VALUES (3692,'٨١ - وقد يجمع الله الشتيتين بعد ما ... يظنان كل الظن ألا تلاقيا','1317','3611','0');
INSERT INTO [t17712] VALUES (3693,'٨٢ - ألكني إليها عمرك الله يا فتى ... بآية ما جاءت إلينا تهاديا','1317','3611','0');
INSERT INTO [t17712] VALUES (3694,'٨٣ - وأنت غريم لا أظن قضاءه ... ولا العنزي القارظ الدهر جائيا','1319','3611','0');
INSERT INTO [t17712] VALUES (3695,'٨٤ - رضيت بك اللهم ربا فلن أرى ... أدين إلها غيرك الله راضيا','1319','3611','0');
INSERT INTO [t17712] VALUES (3696,'٨٥ - ألم تعلما أن الملامة نفعها قليل ... وما لومي أخي من شماليا','1320','3611','0');
INSERT INTO [t17712] VALUES (3697,'٨٦ - أبى الهجر أني قد أصابوا كريمتي ... وأن ليس إهداء الخنا من شماليا','1320','3611','0');
INSERT INTO [t17712] VALUES (3698,'٨٧ - لقد أغدوا على أشق ... ر يغتال الصحاريا','1321','3611','0');
INSERT INTO [t17712] VALUES (3699,'٨٨ - دعاهن ردفي فارعوين لصوته ... كما رعت بالجوت الظماء الصواديا','1321','3611','0');
INSERT INTO [t17712] VALUES (3700,'٨٩ - وأوده ردفي فارعوين لصوته ... ... الصواديا','1321','3611','0');
INSERT INTO [t17712] VALUES (3701,'٩٠ - وقد علمت عرسي مليكة أنني ... أنا الليث معديا عليه وعاديا','1322','3611','0');
INSERT INTO [t17712] VALUES (3702,'٩١ - علمتك منانا فلست بآمل ... نداك ولو ظمآن غرثان عاريا','1322','3611','0');
INSERT INTO [t17712] VALUES (3703,'٩٢ - ضاحكا ما قبلتها حين قالوا ... نقضوا صكها وردت عليا','1323','3611','0');
INSERT INTO [t17712] VALUES (3704,'٩٣ - أبلغ الحارث بن ظالم المو ... عد والناذر النذور عليا','1323','3611','0');
INSERT INTO [t17712] VALUES (3705,'٩٤ - أنما تقتل النيام ولا تقت ... ل يقظان ذا السلاح كميا','1323','3611','0');
INSERT INTO [t17712] VALUES (3706,'٩٥ - يا أيها الذكر الذي قد سؤتني ... وفضحتني وطردت أم عياليا','1323','3611','0');
INSERT INTO [t17712] VALUES (3707,'٩٦ - أقاتلي الحجاج إن لم أزر له ... دراب وأترك عند هند فؤاديا','1323','3611','0');
INSERT INTO [t17712] VALUES (3708,'٩٧ - أعان علي الدهر إذ حل بركه ... كفى الدهر لو وكلته بي كافيا','1323','3611','0');
INSERT INTO [t17712] VALUES (3709,'٩٨ - إذا أعجبتك الدهر حال من امرئ ... فدعه وواكل أمره واللياليا','1324','3611','0');
INSERT INTO [t17712] VALUES (3710,'٩٩ - لها بحقيل فالنميرة منزل ... ترى الوحش عوذات به ومتاليا','1324','3611','0');
INSERT INTO [t17712] VALUES (3711,'١٠٠ - وقد شفني أن لا يزال يروعني ... خيالك إما طارقا أو مغاديا','1324','3611','0');
INSERT INTO [t17712] VALUES (3712,'١٠١ - هببت ألوم القلب في طاعة الهوى ... فلج كأني كنت باللوم مغريا','1324','3611','0');
INSERT INTO [t17712] VALUES (3713,'١٠٢ - فما برحت أقدامنا في مقامنا ... ثلاثتنا حتى أزيروا المنائيا','1325','3611','0');
INSERT INTO [t17712] VALUES (3714,'١٠٣ - قعيدكما الله الذي أنتما له ... ألم تسمعا بالبيضتين المناديا','1325','3611','0');
INSERT INTO [t17712] VALUES (3715,'١٠٤ - بأهبة حزم لذ وإن كنت آمنا ... فما كل حين من توالي مواليا','1325','3611','0');
INSERT INTO [t17712] VALUES (3716,'١٠٥ - أقول لصاحبي وقد بدا لي ... معالم منهما وهما نجيا','1325','3611','0');
INSERT INTO [t17712] VALUES (3717,'١٠٦ - لعمر أبي دهماء زالت عزيزة ... علي وإن قللت منها نصيبيا','1326','3611','0');
INSERT INTO [t17712] VALUES (3718,'١٠٧ - فيا ليت شعري هل تغيرت الرحى ... رحى المثل أو أمست بفلج كما هيا','1326','3611','0');
INSERT INTO [t17712] VALUES (3719,'١٠٨ - خليلي ما إن أنتما الصادقا هوى ... إذا خفتما فيه عذولا وواشيا','1326','3611','0');
INSERT INTO [t17712] VALUES (3720,'١٠٩ - فأشهد عند الله أن قد رأيتها ... وعشرون منها إصبعا من ورائيا','1327','3611','0');
INSERT INTO [t17712] VALUES (3721,'١١٠ - ولكل ما نال الفتى ... قد نلته إلا التحيه','1328','3611','0');
INSERT INTO [t17712] VALUES (3722,'١١١ - تبكيهم أسماء معولة ... وتقول سلمى وا رزيتيه','1329','3611','0');
INSERT INTO [t17712] VALUES (3723,'١١٢ - فإياكم وحية بطن واد ... هموز الناب ليس لكم بسي','1329','3611','0');
INSERT INTO [t17712] VALUES (3724,'١١٣ - منعمة تصون إليك منها ... كصونك من رداء شرعبي','1329','3611','0');
INSERT INTO [t17712] VALUES (3725,'١١٤ - وليس المال فاعلمه بمال ... وإن أغناك إلا للذي يريد به العلاء ويصطفيه ... لأقرب أقربيه وللقصي','1329','3611','0');
INSERT INTO [t17712] VALUES (3726,'١١٥ - وبلدة ليس بها طوري ... ولا خلا الجن بها إنسي','1330','3611','0');
INSERT INTO [t17712] VALUES (3727,'١١٦ - يا مرحباه بحمار ناجيه ... إذا أتى قربته للسانيه','1330','3611','0');
INSERT INTO [t17712] VALUES (3728,'١١٧ - لا هيثم الليلة للمطي ....','1330','3611','0');
INSERT INTO [t17712] VALUES (3729,'١١٨ - وهي تنزي دلوها تنزيا ... كما تنزي شهلة صبيا','1331','3611','0');
INSERT INTO [t17712] VALUES (3730,'١١٩ - بنيته بعصبة من ماليا ... أخشى ركيبا أو رجيلا عاديا','1331','3611','0');
INSERT INTO [t17712] VALUES (3731,'١٢٠ - ذكرتني الطعن وكنت ناسيا','1331','3611','0');
INSERT INTO [t17712] VALUES (3732,'١٢١ - حيدة خالي ولقيط وعلي ... وحاتم الطائي وهاب المئي','1331','3611','0');
