INSERT INTO [t17878] VALUES (102,'١٠٢ - [مسألة] الحمد لله أملى القاضي الحنفي المحب ابن الشحنة، أصلح الله أحواله، على جماعته. حديث عبد الله بن سلام في قدوم النبي صلى الله عليه وسلم المدينة، أورده عن البرهان الحلبي، عن الصلاح ابن عمر عن الفخر البخاري. عن ابن طبرزد، عن ابن حصين، عن ابن','385','0','0');
INSERT INTO [t17878] VALUES (103,'١٠٣ - مسألة: روى الترمذي في جامعه في باب ما جاء في التجار وتسمية النبي صلى الله عليه وسلم إياهم من حديث ابن خثيم عن إسماعيل بن عبيد ويقال: عبيد الله بن رفاعة عن أبيه عن جده أنه خرج مع النبي صلى الله عليه وسلم إلى المصلى فرأى الناس يتبايعون فقال: ي','390','0','0');
INSERT INTO [t17878] VALUES (104,'١٠٤ - الحمد لله: التمستم ـ نفعنا الله بركتكم ـ من العبد إيضاح ما أشكل معناه مما أخرجه الحاكم في المستدرك من حديث مروان بن معاوية الفزاري عن أبي المليح الهذلي عن أبي صالح عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم: إن الله ليغضب عن من لا يفعل','393','0','0');
INSERT INTO [t17878] VALUES (105,'١٠٥ - مسألة: هل ثبت أن النبي صلى الله عليه وسلم قال لعلي رضي الله عنه: يا علي قد جعلت إليك هذه السبقة بين الناس، فخرج علي، دعا سراقة بن مالك رضي الله عنه فقال: يا سراقة إني قد جعلت إليك ما جعل النبي صلى الله عليه وسلم في عنقي من هذه السبقة في عنقك،','397','0','0');
INSERT INTO [t17878] VALUES (106,'١٠٦ - الإسعاف بالجواب عن مسألة الأشراف','412','0','0');
INSERT INTO [t17878] VALUES (107,'١٠٧ - مسألة: وقع في الشفاء حديث: لا تفضلوني على يونس بن متى','425','0','0');
INSERT INTO [t17878] VALUES (108,'١٠٨ - سئلت: عن حديث أبي معاوية عن الأعمش عن المنهال بن عمرو عن زاذان أبي عمر عن البراء بن عازب قال: خرجنا مع رسول الله صلى الله عليه وسلم في جنازة رجل من الأنصار، فانتهينا إلى القبر، الحديث بطوله في سؤال الملكين أهو صحيح أم لا؟','428','0','0');
INSERT INTO [t17878] VALUES (109,'١٠٩ - الحمد لله سئلت عن حديث: من ظلم ذميا كان خصمه أو كنت خصمه. ما حكمه ومن المخاصم؟','431','0','0');
INSERT INTO [t17878] VALUES (110,'١١٠ - الحمد لله سئلت: عن حديث: دعوا الناس يرزق الله بعضهم من بعض','434','0','0');
INSERT INTO [t17878] VALUES (111,'١١١ - مسألة','436','0','0');
INSERT INTO [t17878] VALUES (112,'١١٢ - وقع سؤال: عن ليلة الإسراء وتعيين محال الأنبياء من السماوات واختصاص السيد موسى عليه وعليهم الصلاة والسلام من بينهم بالمراجعة، وعن صفة أولاد المسلمين في البعث، ثم في الجبر وعن أطفال المشركين','441','0','0');
INSERT INTO [t17878] VALUES (113,'١١٣ - اشتهر على الألسنة: إن الله لا يعذب بقطع الرزق وقد ورد معناه عند الطبراني في الصغير عن أبي سعيد الخدري عن النبي صلى الله عليه وسلم قال: إن الرزق لا تنقصه المعصية ولا تزيده الحسنة وترك الدعاء معصية','445','0','0');
INSERT INTO [t17878] VALUES (114,'١١٤ - الحمد لله سئلت عن مصر والشام أيهما أفضل؟','448','0','0');
INSERT INTO [t17878] VALUES (115,'١١٥ - الحمد لله سئلت عن الوارد في قراءة (الم تنزيل السجددة) عند النوم','454','0','0');
INSERT INTO [t17878] VALUES (116,'١١٦ - الحمد لله وسألني: الفقيه فخر الدين عثمان المقسي نفع الله به عن القرافة وما فيها من كلام','461','0','0');
INSERT INTO [t17878] VALUES (117,'١١٧ - الحمد لله سئلت عن عامي يروي الحديث النبوي في الجنينة ونحوها من الأماكن المعروفة بما لا يليق؟','469','0','0');
INSERT INTO [t17878] VALUES (118,'١١٨ - الحمد لله وسئلت: عن بوله صلى الله عليه وسلم قائما','469','0','0');
INSERT INTO [t17878] VALUES (119,'١١٩ - [مسألة في التعمير] الحمد لله: وقع الكلام فيما يتداوله التجار ونحوهم من الإخبار بوجود معمر جاز سنة أربعمائة سنة أو نحوها','472','0','0');
INSERT INTO [t17878] VALUES (120,'١٢٠ - سئلت: عن حديث في [صحيح] ابن حبان عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم: لو أن الله يؤاخذني وعيسى بذنوبنا لعذبنا ولا يظلمنا شيئا قال: واشار بالسبابة والتي تليها. ولا شك في عصمة الأنبياء، فما معنى ذلك؟','482','0','0');
INSERT INTO [t17878] VALUES (121,'١٢١ - الحمد لله سئلت: عن الحديث الوارد في إكرام الخبز','485','0','0');
INSERT INTO [t17878] VALUES (122,'١٢٢ - مسألة','499','0','0');
INSERT INTO [t17878] VALUES (123,'١٢٣ - الحمد لله سئلت: عن أبي عبيدة بن الجراح رضي الله عنه أله عقب أم لا؟ فإن بعض من بلده جراح ـ القرية التي بالشرقية من قرى مصر ـ يدعى انتسابه إليه، وإن قولهم الجراحي نسبة إليه فهل ذلك صحيح أم لا؟','504','0','0');
INSERT INTO [t17878] VALUES (124,'١٢٤ - مسألة: وقع الكلام الآن بين يدي السطلان، حفه الله بمزيد نصره، ورد كيد عدوه في نحره، فيمن ترك شعر رأسه بدون حلق حتى يطول ويسترخي من جانبي راسه هل يتعرض لإزالته، أو يترك على هيئته وحالته؟','506','0','0');
INSERT INTO [t17878] VALUES (125,'١٢٥ - ثم وقع السؤال بعد مدة: عن رجل يريد فعل السنة بعدم حلق رأسه إلا في حج أو عمرة، أهل الأفضل في حقه إرسال الشعر أم الضفر، وإذا قلتم بالضفر فهل ينقضه حال الصلاة أم لا؟','512','0','0');
INSERT INTO [t17878] VALUES (126,'١٢٦ - الحمد لله ثم سئلت: هل كان شعره الشريف مسبلا دائما أو في وقت دون وقت، وهل كان ضفائر ظاهرة أو مستترة تحت عمامته الشريفة، وهل كان يلصقها بعمامته يمينا وشمالا أم لا؟ وكم حلق رأسه الشريفة مرة وهل كان يحلق في حجه أم يقصر؟','515','0','0');
INSERT INTO [t17878] VALUES (127,'١٢٧ - [مسألة] الحمد لله: أمرتم من هو قائم لكم بوظيفة الدعاء، مستمر على العبودية والولاء ـ زادكم الله إفضالا، وأسبغ عليكم نعمه ووالى ـ بالنظر فيما نسب لأبي هريرة أنه قال: سال حذيفة بن اليمان رضي الله عنهما النبي صلى الله عليه وسلم: متى الساعة؟ فأطرق','518','0','0');
INSERT INTO [t17878] VALUES (128,'١٢٨ - الحمد لله سأل البدر حسن الكلوتاني الحنفي أحد الفضلاء وأخ لشيخ السبع بالأزهر فيما كتبه إلى بخطه عن عبد الله بن جعفر: كم كان سنة حين مات ابوه بمؤتة، وهل ولد بعد قدومه م الحبشة فإنه قدم والنبي صلى الله عليه وسلم بخيبر وكانت في أول المحرم سنة سبع م','524','0','0');
INSERT INTO [t17878] VALUES (129,'١٢٩ - سئلت: عن شخص دلاصي يوري عن الشاطبي، وكان السائل الشيخ شمس الدين ابن قمر والتمس مني تحقيق ذلك','527','0','0');
INSERT INTO [t17878] VALUES (130,'١٣٠ - سئلت: عن أبي الفضل الحارث بن زياد بن المطلب متى توفي وباي مكان توفي؟','529','0','0');
INSERT INTO [t17878] VALUES (131,'١٣١ - وسئلت: عن قوله صلى الله عليه وسلم لمن قال له: إن امرأتي لا ترد يد لامس: طلقها فقال: إني أحبها قال: أمسكها ما معنى ذلك؟ وهل الحديث صحيح أم غير ذلك؟','531','0','0');
INSERT INTO [t17878] VALUES (132,'١٣٢ - وسئلت: عن السعتر هل ورد في أكله شيء وكذا في إدخاره وشمه؟ وهل ورد في طول عمامته صلى الله عليه وسلم شيء أم لا؟ وهل يكره التعمم جالسا أم لا؟ وهل ورد في إكرام المداح الذين يمدحونه صلى الله عليه وسلم شيء أم لا؟','533','0','0');
INSERT INTO [t17878] VALUES (133,'١٣٣ - سئلت عن معنى الحديث الذي في مقدمة صحيح مسلم: كفى بالمرء كذبا أن يحدث بكل ما سمع','536','0','0');
INSERT INTO [t17878] VALUES (134,'١٣٤ - وسئلت: عن قوله صلى الله عليه وسلم لأبي هريرة: أشكنبدرد','537','0','0');
INSERT INTO [t17878] VALUES (135,'١٣٥ - وسئلت: عن حديث طلق عن أبيه في الدعاء المأثور لمن كان به أسر [البول] وفيه: أنت رب الطيبين هل هذه اللفظة بالتثنية مع فتح المهملة وكسر الموحدة بعدها أو الطيبن بكسر المهملة ثم الموحدة المشددة كما ضبط في بعض النسخ أو الطيبين جمع طيب كما قاله بعض ا','539','0','0');
INSERT INTO [t17878] VALUES (136,'١٣٦ - وسئلت عن الحكمة في قراءة سورة الإخلاص أحد عشر مرة لمن دخل المقابر','545','0','0');
INSERT INTO [t17878] VALUES (137,'١٣٧ - سئلت: عن خذام والد خنساء هل هو بالمعجمة أو المهملة','546','0','0');
INSERT INTO [t17878] VALUES (138,'١٣٨ - وسئلت عن معنى قوله صلى الله عليه وسلم: لو أن الله يؤاخذني وعيسى بذنوبنا لعذبنا ولا يظلمنا شيئا وأشار بالسبابة والتي تليها وفي لفظ: لو يؤاخذني الله وابن مريم بما جنت هاتان ـ يعني الإبهام والتي تليها ـ لعذبنا الله ثم لم يظلمنا؟','548','0','0');
INSERT INTO [t17878] VALUES (139,'١٣٩ - سئلت: عن حديث: أكرموا عماتكم النخل','549','0','0');
INSERT INTO [t17878] VALUES (140,'١٤٠ - وسئلت: عن قول: لولا الوئام لهلك الأنام أهو حديث؟','550','0','0');
INSERT INTO [t17878] VALUES (141,'١٤١ - مسالة','551','0','0');
INSERT INTO [t17878] VALUES (142,'١٤٢ - وسئلت: عن حديث ابن عباس أن علي بن أبي طالب رضي الله عنهم خرج من عند رسول الله صلى الله عليه وسلم في وجعه الذي توفي فيه فقال الناس: يا أبا الحسن: كيف أصبح رسول الله صلى الله عليه وسلم فقال: أصبح بحمد الله بارئا. هل يسمى حديثا أم أثرا؟','555','0','0');
INSERT INTO [t17878] VALUES (143,'١٤٣ - وسئلت: عن قوله صلى الله عليه وسلم لأم سلمة رضي الله عنها حين أرسل يخطبها إن لي بنتا: أما ابنتها فندعو الله بأن يغنيها عنها بماذا حصل الاغتناء؟','556','0','0');
INSERT INTO [t17878] VALUES (144,'١٤٤ - وقع السؤال عما وقع في موضعين متقاربين من الشفا للقاضي عياض رحمه الله عن الحسن رفعه في أحدهما: عمل قليل في سنة خير من عمل كثير في بدعة','557','0','0');
INSERT INTO [t17878] VALUES (145,'١٤٥ - الحمد لله وقع السؤال عما اشتهر على الألسنة: لا أعلم ما وراء جداري','561','0','0');
INSERT INTO [t17878] VALUES (146,'١٤٦ - الحمد لله سئلت عن الحديث الصحيح أن رسول الله صلى الله عليه وسلم قال مخاطبا لأصحابه رضي الله عنهم: ترون قبلتي هاهنا فوالله ما يخفى علي خشوعكم ولا ركوعكم إني لأراكم من وراء ظهري','563','0','0');
INSERT INTO [t17878] VALUES (147,'١٤٧ - الحمد لله حديث: تفترق أمتي','565','0','0');
INSERT INTO [t17878] VALUES (148,'١٤٨ - [مسألة]: عن عبد الرحمن بن عوف رضي الله عنه قال: قال لي رسول الله صلى الله عليه وسلم: يا عبد الرحمن إنك من الأغنياء ولن تدخل الجنة إلا زحفا فأقرض الله يطلق قدميك','573','0','0');
INSERT INTO [t17878] VALUES (149,'١٤٩ - الحمد لله لا يثبت في دخوله رضي الله عنه الجنة زحفا أو حبوا حديث، كما جزم به الحافظ أبو الحسن الهيثمي، وأقره شيخه العراقي','578','0','0');
INSERT INTO [t17878] VALUES (150,'١٥٠ - الحمد لله: روى الترمذي في جامعه من حديث أبي هريرة رضي الله عنه عن النبي صلى الله عليه وسلم قال: السخي قريب من الله قريب من الجنة قريب من الناس بعيد عن النار، والبخيل بعيد من الله بعيد من الجنة بعيد من الناس، قريب من النار، ولجاهل سخي أحب إلى ا','586','0','0');
INSERT INTO [t17878] VALUES (151,'١٥١ - الحمد لله في الغيلانيات','587','0','0');
INSERT INTO [t17878] VALUES (152,'١٥٢ - سئلت: عن حديث اوتيت جوامع الكلم واختصر لي الكلام اختصارا','590','0','0');
INSERT INTO [t17878] VALUES (153,'١٥٣ - سألت: أيدك الله تعالى: هل يكره تسمية الرجل عبده بخير الله وسعدا وسعيدا ومسعودا أم لا؟','594','0','0');
INSERT INTO [t17878] VALUES (154,'١٥٤ - حديث: ثبت عن أنس رضي الله عنه قال: قال الناس: يا رسول الله غلا السعر فسعر لنا فقال: إن الله هو المسعر، القابض، الباسط، الرازق، وإني لأرجو أن ألقى الله وليس أحد منكم يطالبني بمظلمة في دم ولا مال','598','0','0');
INSERT INTO [t17878] VALUES (155,'١٥٥ - الحمد لله سئلت: عما أخرجه الشيخان في صحيحهما عن جابر رضي الله عنه رفعه: أعطيت خمسا لم يعطهن أحد قبلي، فذكرها، وفيها: ونصرت بالرعب مسيرة شهر، هل ورد مسيرة شهرين؟','600','0','0');
INSERT INTO [t17878] VALUES (156,'١٥٦ - وسئلت: عن حديث: كاد الفقر أن يكون كفرا','602','0','0');
INSERT INTO [t17878] VALUES (157,'١٥٧ - ثم سئلت: عن أبيات شاع أنها من نظم شيخنا ونصها: في قص ظفرك يوم السبت أكلة ... تبدو وفيما قلته تذهب البركة وعالم فاضل يبدو بتلوهما ... وإن يكن في الثلاثاء فاحذر الهلكة ويورث السوء في الأخلاق رابعها ... وفي الخميس الغني يأتي لمن سلكه والعلم وا','603','0','0');
INSERT INTO [t17878] VALUES (158,'١٥٨ - رأيت كثيرا ممن يبلغه الكلام في ولاية من لا يصلح، يحتج بولاية الصالحي','604','0','0');
INSERT INTO [t17878] VALUES (159,'١٥٩ - الحمد لله: ورد المرسوم العالي بعد المشافهة بالكشف عن حال عبدالله بن شبرمة الشريكي','605','0','0');
INSERT INTO [t17878] VALUES (160,'١٦٠ - سئلت: عن حديث: لا يعذب الله عبدا بمسألة اختلف فيه العلماء. وما الحكمة في سؤال الأطفال والأنبياء؟ وإذا كان لكافر على مسلم أو كافر حق كي القصاص له يوم القيامة؟ وهل ورد في فعل العذبة شيء، وما حاله وما كيفيتها؟ والحديث الذي في المعجم أوغيره: أ','608','0','0');
INSERT INTO [t17878] VALUES (161,'١٦١ - [حديث: ثلاث من كن فيه]','612','0','0');
INSERT INTO [t17878] VALUES (162,'١٦٢ - حديث: إن يكنه فلن تسلط عليه','617','0','0');
INSERT INTO [t17878] VALUES (163,'١٦٣ - أما حديث: من تعزى بعزاء الجاهلية. فقد أخرجه النسائي في سننه والإمام أحمد والروياني في مسنديهما، والطبراني في معجمه الكبير وغيرهم، وصححه ابن حبان','617','0','0');
INSERT INTO [t17878] VALUES (164,'١٦٤ - [مسألة]','622','0','0');
INSERT INTO [t17878] VALUES (165,'١٦٥ - سئلت عن ضبط حجر بن قيس المدري','626','0','0');
INSERT INTO [t17878] VALUES (166,'١٦٦ - حديث: عليكم بالإثمد فإنه يجلو البصر، وينبت الشعر ويذهب القذاء','626','0','0');
INSERT INTO [t17878] VALUES (167,'١٦٧ -','631','0','0');
INSERT INTO [t17878] VALUES (168,'١٦٨ -','632','0','0');
INSERT INTO [t17878] VALUES (169,'١٦٩ - حديث: من كتاب نبأ المهدي','634','0','0');
INSERT INTO [t17878] VALUES (170,'١٧٠ - حديث: البيهقي في الدعوات: أخبرنا أبو زكريا بن أبي إسحاق أخبرنا أبو محمد بن عبد الله ابن إسحاق الخراساني حدثنا إبراهيم بن الهيثم البلدي حدثني عبد الله بن نافع بن يزيد بن أبي نافع عن عيسى بن يونس السبيعي عن الأعمش عن أبي وائل عن ابن مسعود قال: قا','636','0','0');
INSERT INTO [t17878] VALUES (171,'١٧١ - حديث: اللهم ما أصبح بي من نعمة فمنك','639','0','0');
INSERT INTO [t17878] VALUES (172,'١٧٢ - الحمد لله وسئلت: عن الوارد في فقد البصر','640','0','0');
INSERT INTO [t17878] VALUES (173,'١٧٣ - الحمد لله وسئلت: عن حديث: يساق إلى مصر كل قصير العمر','655','0','0');
INSERT INTO [t17878] VALUES (174,'١٧٤ - الحمد لله سئلت: عن قوله: بخلاء أمتي الخياطون وقوله: أنا حبيب الله والمصلى على حبيبي','656','0','0');
INSERT INTO [t17878] VALUES (175,'١٧٥ - الحمد لله سئلت: عن الوارد في فضل آية الكرسي','661','0','0');
INSERT INTO [t17878] VALUES (176,'١٧٦ - وسئلت: عن حديث: من أصاب مالا من نهاوش أذهبه الله في نهابر','674','0','0');
INSERT INTO [t17878] VALUES (177,'١٧٧ - الحمد لله: حديث أبي هريرة: كل مولود يولد على الفطرة','677','0','0');
INSERT INTO [t17878] VALUES (178,'١٧٨ - سئلت عن كيفية الطواف التي ذكرها العلماء في كتبهم','687','0','0');
INSERT INTO [t17878] VALUES (179,'١٧٩ - الحمد لله وسئلت عن خالد بن سنان الذي أدركت ابنته النبي صلى الله عليه وسلم وآمنت به أكان نبيا أم لا؟ وهل كان بين عيسى ومحمد صلى الله عليه وسلم نبي أم لا؟','691','0','0');
INSERT INTO [t17878] VALUES (180,'١٨٠ - وقع السؤال من القاضي الشافعي بمجلس السلطان في مرضان سنة ست وسبعين كما بلغني: هل كان تجميع اسعد بن زرارة للجمعة بأمر منه صلى الله عليه وسلم أم لا؟ فلم يجبه أحد؟','695','0','0');
INSERT INTO [t17878] VALUES (181,'١٨١ -','702','0','0');
INSERT INTO [t17878] VALUES (182,'١٨٢ - سئلت عن استئصال الشارب، والعنفقة بالحلق، أو النتف هل هو سنة أم لا؟','711','0','0');
INSERT INTO [t17878] VALUES (183,'١٨٣ - سئلت عن حديث: من قال عند مريض لم يحضر أجله سبع مرات: أسال الله العظيم رب العرش العظيم أن يشفيك','714','0','0');
INSERT INTO [t17878] VALUES (184,'١٨٤ - سئلت: عما اعتيد من كثير من الناس فعله من قراءة الفاتحة عقب الصلوات وإهداء ثوابها للمسلمين الأحياء والأموات','717','0','0');
INSERT INTO [t17878] VALUES (185,'١٨٥ - مسألة','717','0','0');
INSERT INTO [t17878] VALUES (186,'١٨٦ - الحمد لله: وقع كما بلغني بمجلس الاستماع عند الشاوي بحضرة صاحبنا القاضي قطب الدين الخيضري الخوض في مسألة رائي النبي صلى الله عليه وسلم بعد موته قبل الدفن، أو بعده هل يكون صحابيا أم لا؟','722','0','0');
INSERT INTO [t17878] VALUES (187,'١٨٧ - سئلت: عن قوله صلى الله عليه وسلم في الحديث الصحيح: نحن معاشر الأنبياء لا نورث، ما تركنا صدقه هل هو عام أم لا؟ وعن قوله صلى الله عليه وسلم مما هو الاعتكاف: من اعتكف فواق ناقة كان كمن أعتق رقبة مؤمنة ما المراد بفواق ناقة؟','724','0','0');
INSERT INTO [t17878] VALUES (188,'١٨٨ - سئلت: عن قول ابن حبان في حديث: من أحيا أرضا ميتة فله فيها أجران الأجر يكون للمسلمين','729','0','0');
INSERT INTO [t17878] VALUES (189,'١٨٩ - وسئلت: عن قول القائل ـ مسالة ـ: إذا وجد شخص ركازا والحال أنه من دفن المسلمين أو أهل الذمة فإنه يكون لقطة اي فيعرفه؟ فقال شارح هذا الكلام: لأن أحاديث اللقطة خاصة بالنسبة إلى أحاديث الركاز، والمقصود تبيين الأحاديث الخاصة، والأحاديث العامة، وبيان','734','0','0');
INSERT INTO [t17878] VALUES (190,'١٩٠ - الحمد لله وسئلت عما نقل في رسالة عن الحسن البصري فيما يجب على المؤمن من الفرائض في اليوم والليلة في الفريضة السابع وهو: أن النبي صلى الله عليه وسلم قال: اللهم من أحبني فارزقه الكفاف ومن ابغضني فأكثر ماله وولده هل هو صحيح أم لا، وما معناه؟','735','0','0');
INSERT INTO [t17878] VALUES (191,'١٩١ - الحمد لله وسئلت عن قول القائل: إذا أحبتموهم فأعلموهم وإذا ابغضتموهم فتجنبوهم أهو حديث أم لا؟ وما يجب على من رواه حديثا؟','742','0','0');
INSERT INTO [t17878] VALUES (192,'١٩٢ - سئلت عن قول القائل: من قصدنا وجب حقه علينا','743','0','0');
INSERT INTO [t17878] VALUES (193,'١٩٣ - فالحمد لله وسئلت عن حديث: اتخذوا عند الفقراء دولة أهو ثابت أم لا؟','744','0','0');
INSERT INTO [t17878] VALUES (194,'١٩٤ - ورد سؤال من المحلة عن قوله في حديث الترهيب في منع الزكاة: له زبيبتان أهو بالنون أو الموحدة أو بهما؟','747','0','0');
INSERT INTO [t17878] VALUES (195,'١٩٥ - الحمد لله اختلف في سن عيسى صلوات الله عليه وسلامه حين رفع','748','0','0');
INSERT INTO [t17878] VALUES (196,'١٩٦ - سئلت عن الحديث الوارد في حبس التهمة','750','0','0');
INSERT INTO [t17878] VALUES (197,'١٩٧ - الحمد لله سئلت: عن المشارق للقاضي عياض والمطالع لابن قرقول أيهما المأخوذ من الآخر؟','755','0','0');
INSERT INTO [t17878] VALUES (198,'١٩٨ - الحمد لله سئلت عن حديث: أحب الناس إلي أسامة ما حاشا فاطمة','757','0','0');
INSERT INTO [t17878] VALUES (199,'١٩٩ - سئلت: عن أطفال المسلمين أيثابون على أعمالهم ويؤاخذون أم لا؟','762','0','0');
INSERT INTO [t17878] VALUES (200,'٢٠٠ - سئلت عما اشتهر على الألسنة إذا حدثت بجبل زال عن مكانه فصدق بخلاف ابن آدم','765','0','0');
INSERT INTO [t17878] VALUES (201,'٢٠١ - وسئلت عن جماعة يجتمعون ويتلون كتاب الله، ويعمل كل منهم شيئا من الطعام عقب القراءة بمكان مدفون فيه سيدي سعدون فأنكر عليهم شخص، وزعم أن هذا المكان، إنما هو للرفاعية فهل الإنكار صحيح أم لا؟ وهل الشيخ سعدون قبل سيدي أحمد بن الرفاعي أم لا؟','768','0','0');
INSERT INTO [t17878] VALUES (202,'٢٠٢ - الحمد لله وسئلت: عن من قرأ حديث: أول طعام أهل الجنة زيادة كبد الحوت بالتنكير في صحيح البخاري هو سوغ ذلك أم لا؟ وهل ثبت فيه أو في مسلم بالتنكير أم لا؟','770','0','0');
INSERT INTO [t17878] VALUES (203,'٢٠٣ - الحمد لله وسئلت عن سؤال الملكين للميت، أهو عام لجميع الأمم الماضية أم خاص بالأمة المحمدية؟','775','0','0');
INSERT INTO [t17878] VALUES (204,'٢٠٤ - الحمد لله سئلت عن قوله صلى الله عليه وسلم: لا أحصي ثناء عليك هل هو في البخاري أم لا؟','776','0','0');
INSERT INTO [t17878] VALUES (205,'٢٠٥ - وسئلت عن حديث: مسكين مسكين مسكين، رجل ليس له امرأة وإن كان كثير المال، ومسكينة مسكينة مسكينة، امرأة ليست لها زوج وإن كانت كثيرة المال، إن حاضت حاض معها وإن تنفست تنفس معها','777','0','0');
INSERT INTO [t17878] VALUES (206,'٢٠٦ - الحمد لله وسئلت عن حديث: ينادي مناد من قبل الله عز وجل يوم القيامة: أين خونة هذه الأمة فيؤتى بثلاثة نر وهم: الصواغ، والحاكة، والنخاسين، أهو صحيح أم لا؟','779','0','0');
INSERT INTO [t17878] VALUES (207,'٢٠٧ - الحمد لله سئلت هل صح في النهي عن تعليم النساء الكتابة شيء؟','783','0','0');
INSERT INTO [t17878] VALUES (208,'٢٠٨ - وسئلت عن حديث: شركم من نزل وحده','788','0','0');
INSERT INTO [t17878] VALUES (209,'٢٠٩ - الحمد لله سئلت عن من زعم انتسابه إلى الزبير بن العوام وميز نفسه بشظفة خضراء كآل النبي صلى الله عليه وسلم، أيمنع أم لا؟','789','0','0');
INSERT INTO [t17878] VALUES (210,'٢١٠ - الحمد لله وسئلت عن حديث: أنه صلى الله عليه وسلم ضحى عن نفسه، وعن آل بيته، ثم عن أمته من شهد لله بالتوحيد وله البلاغ هل هو صح أم لا؟ وعن توجيه معناه؟','794','0','0');
INSERT INTO [t17878] VALUES (211,'٢١١ - الحمد لله وسئلت: عن قولهم: تارك الصلاة بغير عذر شرعي لا يقبل الله توحيده، وقولهم: الصلاة على النبي صلى الله عليه وسلم لا تقبل من تارك الصلاة، أهما حديثان أم لا؟','813','0','0');
INSERT INTO [t17878] VALUES (212,'٢١٢ - وسئلت عن تعيين قبر عمرو بن العاص، وهل لخديجة ابنة اسمها فاطمة قدمت مع زوج لها إلى مصر، ودفنا بمكان واحد خارج القرافة، وهل ثبت أن أحدا من الصحابة المعينين قتلوا ودفنوا أيضا خارج القرافة بالوقعة التي وقعت لعمرو رضي الله عنه خارج القرافة','817','0','0');
INSERT INTO [t17878] VALUES (213,'٢١٣ - الحمد لله: سأل الشمس ابن القاسم عن حديث أم سلمة رضي الله عنها قالت: قلت: يا رسول الله أخبرني عن قول الله عز وجل: (حور عين) قال: حور: بيض عين، ضخام العيون شقر، الحوراء بمنزلة جناح انسر','819','0','0');
INSERT INTO [t17878] VALUES (214,'٢١٤ - الحمد لله سأل السيد علاء الدين ابن السيد عفيف الدين نفع الله بهما عن نصيبين وضبطها وقال: إنه لم يذكرها صاحب النهاية','823','0','0');
INSERT INTO [t17878] VALUES (215,'٢١٥ - الحمد لله وسأل السيد أيضا: هل ورد في لبس النبي صلى الله عليه وسلم السراويل شيء؟','825','0','0');
INSERT INTO [t17878] VALUES (216,'٢١٦ - سئلت: عن حديث: اجعلها عليهم سنين كسني يوسف','833','0','0');
INSERT INTO [t17878] VALUES (217,'٢١٧ - سئلت: عن امرأة قادرة على الحج ولها زوج قادر أيضا فطلبت الحج فمنعها، فهل له ذلك','835','0','0');
INSERT INTO [t17878] VALUES (218,'٢١٨ - سأل الفاضل علم الدين سليمان الزواوي عن قول بعض المستجيزين بعد ذكره المستجاز لهم إجازة خاصة لكل أحد ممن ذكر، وعامة شاملة لكلمن وقف عليه، هل لكل واقف على الاستجازة الرواية عن المجيزين ولو تأخر مولده عن وفاتهم أم لا؟','836','0','0');
INSERT INTO [t17878] VALUES (219,'٢١٩ - وسأل القاضي تقي الدين ابن الزيتوني عن ما اشتهر على الأسلنة بل وفي كتب الفقهاء، والأصوليين من قوله صلى الله عليه وسلم: إنا نحكم بالظاهر والله يتولى السرائر','838','0','0');
INSERT INTO [t17878] VALUES (220,'٢٢٠ - الحمد لله حضر إلي الكمال ابن القاضي معين الدين ابن شرف الدين بن الأشقر، فذكر لي: إن المحب ابن الشحنة ذكر، بل كتب بخطه: إن جد والده كان يهوديا، ثم عمل مكاسا ونسب للبرهان الحلبي في ذلك كلاما يتأيد به، وكذا لشيخنا رحمه ال','840','0','0');
INSERT INTO [t17878] VALUES (221,'٢٢١ - الحمد لله سئلت عن حديث: إنه لم يكن نبي إلا عاش نصف عمر الذي قبله','843','0','0');
INSERT INTO [t17878] VALUES (222,'٢٢٢ - الحمد لله سئلت عن النهي الوارد عن الجلوس بين الظل والشمس','847','0','0');
INSERT INTO [t17878] VALUES (223,'٢٢٣ - وسئلت عمن قال: إن جبريل أنزل على النبي صلى الله عليه وسلم بعمامة صفراء فسأل النبي صلى الله عليه وسلم عن ذلك فقال: يا رسول الله إنها علامة النصر','851','0','0');
INSERT INTO [t17878] VALUES (224,'٢٢٤ - الحمد لله سئلت عن ذي القرنين، وجرجيس، ولقمان، ودانيال، وحزقيل، أهم أنبياء أم لا؟ فقد وجدوا في بعض الأدعية متوسلا بهم','852','0','0');
INSERT INTO [t17878] VALUES (225,'٢٢٥ - سئلت: عن ما اشتهر على الألسنة أنه صلى الله عليه وسلم قال: آية من كتاب الله خير من محمد وآله','856','0','0');
INSERT INTO [t17878] VALUES (226,'٢٢٦ - سئلت عن قول القائل: إن اليهود والنصارى خونة فلا أعان الله من ألبسهم ثوب عز هل ورد أم لا؟','857','0','0');
INSERT INTO [t17878] VALUES (227,'٢٢٧ - حديث: الأرواح جنود مجندة','859','0','0');
INSERT INTO [t17878] VALUES (228,'٢٢٨ - سؤال في حديث أنس رضي الله عنه المرفوع: إن المؤمن يقال له عقب سؤال الملكين عليهما السلام وجوابه: انظر إلى مقعدك من النار قد أبدلك الله تعالى به مقعدا من الجنة فيراهما جميعا','861','0','0');
INSERT INTO [t17878] VALUES (229,'٢٢٩ - [مسألة]','864','0','0');
INSERT INTO [t17878] VALUES (230,'٢٣٠ - وسئلت عن قوله صلى الله عليه وسلم في حديث أم عطية حين توفيت ابنته: اغسلنها ثلاثا أو خمسا أو أكثر من ذلك إن رأيتن ذلك','866','0','0');
INSERT INTO [t17878] VALUES (231,'٢٣١ - الحمد لله سئلت عن الشويك أفتحت صلحا أم عنوة؟','868','0','0');
INSERT INTO [t17878] VALUES (232,'٢٣٢ - وسئلت عن قول ابن السمعاني في ترجمة البسطامي من أنسابه: أن ابا يزيد له حديث واحد لم يصح عنه غيره هل علم الحديث المشار إليه أم لا؟','870','0','0');
INSERT INTO [t17878] VALUES (233,'٢٣٣ - حديث: أنا مدينة العلم وعلي بابها','873','0','0');
INSERT INTO [t17878] VALUES (234,'٢٣٤ - وسئلت عن حديث: من دعا لظالم بالبقاء فقد أحب أن يعصي الله','878','0','0');
INSERT INTO [t17878] VALUES (235,'٢٣٥ - وسئلت عن بشارة بحيرا بالنبي صلى الله عليه وسلم أكانت صادرة عن إيمانه به أم لا؟ وهل مات قبل البعثة؟','880','0','0');
INSERT INTO [t17878] VALUES (236,'٢٣٦ - سئلت عن حديث ما عزل من ولي ابنه','881','0','0');
INSERT INTO [t17878] VALUES (237,'٢٣٧ - مسألة: هل يعد رائي النبي صلى الله عليه وسلم في منامه على غير صفته راء له أم لا؟ وهل في الرائي شرط أم لا؟','886','0','0');
INSERT INTO [t17878] VALUES (238,'٢٣٨ - مسألة أخرى','888','0','0');
INSERT INTO [t17878] VALUES (239,'٢٣٩ - الحمد لله وسئلت بماذا يستفتح به الدعاء عقب الصلاة، أبالتسمية أم بالحمد؟','889','0','0');
INSERT INTO [t17878] VALUES (240,'٢٤٠ - الحمد لله وسئلت: عن ورقة ومن نفى إيمانه أو صحبته متمسكا بقول من لم يشتهر بعلم، وصمم على ذلك، أهو صحيح، وماذا يلزمه؟','892','0','0');
INSERT INTO [t17878] VALUES (241,'٢٤١ - الحمد لله وسئلت عن إمام لغير محصورين يقول في الرفع من ركوع ثانية الصبح: سمع الله لمن حمده، ربنا لك الحمد، فيقول المبلغون: ربنا لك الحمد ويمطوا بها لتبلغ مدى أصواتهم من بعد عنهم، لسعة المساجد، فإذا فرغوا أتى بقنوت الصبح المعروف وذيله بقوله الملح','895','0','0');
INSERT INTO [t17878] VALUES (242,'٢٤٢ - وسئلت عن كيفية ضرب الصراط، أهو ممدود كما يدل له تسميته بالجسر، أو منصوب كارتفاع العمود كما زعمه بعض الطلبة، وأنه وقف على القول به. وما المراد بالدحض والحسك وهل قول أبي سعيد: بلغني أن الصراط أدق من الشعر وأحد من السيف، موقوف أو مرفوع. وإذا كان م','899','0','0');
INSERT INTO [t17878] VALUES (243,'٢٤٣ - وسئلت عن ألفاظ الروايات في جوامع التسبيح','904','0','0');
INSERT INTO [t17878] VALUES (244,'٢٤٤ - الحمد لله وسئلت عما عزاه بعضهم للصحيحين بلفظ: أيما رجل أغلق بابه على امرأة وأرخى أستاره فقد تم صداقها. مما نقل صاحب الغريبين في ضبطه عن شمر: أن الإستارة بالكسر من الستر قال: ولم نسمعه إلا في هذا الحديث وقال بعض أهل التأويل: لو روى أستارة بال','908','0','0');
INSERT INTO [t17878] VALUES (245,'٢٤٥ - سئلت عن حديث من تذكر مصيبته','911','0','0');
INSERT INTO [t17878] VALUES (246,'٢٤٦ - وسئلت عن حديث الفرس فلخصت من الأحاديث المشتهرة الجواب وعن حديث: للسائل حق','911','0','0');
INSERT INTO [t17878] VALUES (247,'٢٤٧ - وعن حديث: من صلى سنة الفجر في بيته يوسع له في رزقه وتقل المنازعة بينه وبين أهله ويختم له بالإيمان','912','0','0');
INSERT INTO [t17878] VALUES (248,'٢٤٨ - وعن حديث: لعن الله سهيلا','912','0','0');
INSERT INTO [t17878] VALUES (249,'٢٤٩ - سئلت عن حديث: لا ينبغي للمؤمن أني ذل نفسه','915','0','0');
INSERT INTO [t17878] VALUES (250,'٢٥٠ - وسئلت عن حديث: لا خير في صحبة من لا يرى لك من الود مثل ما ترى له','916','0','0');
INSERT INTO [t17878] VALUES (251,'٢٥١ - مسألة: قد وقعت من النبي صلى الله عليه وسلم قرائن حالية، وأصولكلية تقتضي أن سيدنا أبا بكر الصديق رضي الله عنه وعن سائر الصحابة أحق بالإمامة، وأولى بالخلافة من غيره','918','0','0');
INSERT INTO [t17878] VALUES (252,'٢٥٢ - [مسألة] الحمد لله يجب امتثال ما قاله صلى الله عليه وسلم شرعا اتفاقا دون ما ذكره صلى الله عليه وسلم من معايش الدنيا على سبيل الرأي لحديث: أنه صلى الله عليه وسلم مر بقوم يلقحون النخل فقال: لو لم تفعلوا لصلح قال: فخرج شيصا ثم مر بهم فقال: ما','922','0','0');
INSERT INTO [t17878] VALUES (253,'٢٥٣ - [مسألة] ما قولكم في قول صاحب العلم المنشور في فضل الأيام والشهور: أولعت فسقة القصاص بأن رسول الله صلى الله عليه وسلم يسمع من يصلي عليه، ثم أبطل ما احتجوا به وفي حديث: ما من أحد يسلم علي ... إلى آخره وهل تعم الصلاة أم لا؟ وهل هو في الحاضر عند','924','0','0');
INSERT INTO [t17878] VALUES (254,'٢٥٤ - [مسألة]','930','0','0');
INSERT INTO [t17878] VALUES (255,'٢٥٥ - الأول: حديث: يؤتي يوم القايمة بالرجل السمين فلا يزن عند الله جناح بعوضة في أي كتاب هذا، ومن روايه، وهو صحيح أم لا؟','932','0','0');
INSERT INTO [t17878] VALUES (256,'٢٥٦ - الثاني: ابن زمل الصحابي رضي الله عنه ما اسمه وترجمته؟','933','0','0');
INSERT INTO [t17878] VALUES (257,'٢٥٧ - الثالث: حديث: أن النبي صلى الله عليه وسلم وعلى إخوانه وآله وصحبه وسلم وبارك [كان] يختم دعائه بسبحان ربك رب العزة إلى آخره، من روايه؟ وفي أي كتاب من الكتب المعتمدة؟','934','0','0');
INSERT INTO [t17878] VALUES (258,'٢٥٨ - الرابع: حديث كعب الأحبار في حديث طويل: يا موسى أكثر من قول سبحان الله والحمد لله إلى الآخر، وأكثر قول سحبان من تعزز القدرة وقهر العباد بالموت من خرجه ومن رواه؟','937','0','0');
INSERT INTO [t17878] VALUES (259,'٢٥٩ -','937','0','0');
INSERT INTO [t17878] VALUES (260,'٢٦٠ - الخامس والسادس: روى الشيخ ابن وهب الدمشقي حديثين عن الحسن أن موسى عليه السلام قال: يا رب يستطيع آدم أن يؤدي شكر ما صنعت عليه خلقته بيدك إلى آخر الحديث','937','0','0');
INSERT INTO [t17878] VALUES (261,'٢٦١ - السابع: حديث رواه صاحب النهاية: من سبق العاطس بالحمد أمن الشوص، واللوص، والعلوص، في أي كتاب من الكتب المعتمدة ومن رواه من الصحابة رضي الله تعالى عنهم وعنكم؟','938','0','0');
INSERT INTO [t17878] VALUES (262,'٢٦٢ - الثامن: الحديث الذي في العوارف في الباب الثلاثين: من عطس أو تجشأ فقال: الحمد لله على كل حال دفع الله عنه سبعين داء أهوانها الجذام. من روايه من الصحابة، ومن أي كتاب من الكتب الحديثية؟','940','0','0');
INSERT INTO [t17878] VALUES (263,'٢٦٣ - التاسع: ما يمنع أحدكم إذا عرف الإجابة من نفسه فشفي من مرض أو قدم من سفر يقدم: الحمد لله الذي بنعمته تتم الصالحات. رواه الحاكم من روايه من الصحابة؟','940','0','0');
INSERT INTO [t17878] VALUES (264,'٢٦٤ - العاشر: روى الشيخ الإمام عبد القادر الجيلي رحمة الله عليه في كتابه الغنية حديثا: أن من رأى بيعة أو كنيسة أو سمع صوت نسر أو ناقوش أو رأى جمعا من المشركين، واليهود، والنصارى يقول: أشهد أن لا إله إلا الله وحده لا شريك له إلها واحدا لا نعبد','941','0','0');
INSERT INTO [t17878] VALUES (265,'٢٦٥ -','941','0','0');
INSERT INTO [t17878] VALUES (266,'٢٦٦ - الثاني عشر: روى الشيخ أبو بكر بن داود الشامي عن علي رضي الله عنه أنه مر بأهل المقابل فقال: السلام على أهل لا إله إلا الله إلى آخر الحديث. هذا الحديث من الذي رواه من أئمة الحديث في كتبهم المشهورة؟','942','0','0');
INSERT INTO [t17878] VALUES (267,'٢٦٧ - الثالث عشر: الحديث الذي في جلاء الأفهام أن يقال بعد صلاة الصبح والمغرب: قال الله تعالى: (إن الله وملائكته يصلون على النبي) الأثر. ثم يقول مائة مرة: اللهم صل عليه. هذا الحديث من يرويه من الصحابة؟ ومن أي كتاب من الكتب المعتمدة؟','942','0','0');
INSERT INTO [t17878] VALUES (268,'٢٦٨ - الرابع عشر: روى الإمام الرافعي في كتاب التدوين في تاريخ قزوين عن البراء رضي الله عنه مرفوعا: قول لا إله إلا الله وحده ... إلى آخره عقب كل صلاة مفروضة من الذي خرجه من أصحاب الكتب المعتمدة، وكيف عبارة الحديث؟','943','0','0');
INSERT INTO [t17878] VALUES (269,'٢٦٩ - الخامس عشر: روي من دخل السوق فاستغفر الله عز وجل غفر له بعدد من في السوق من أي كتاب هذا الحديث، ومن يرويه من الصحابة؟','944','0','0');
INSERT INTO [t17878] VALUES (270,'٢٧٠ - السادس عشر: الحديث القدسي الذي روي: من لم يرض بقضائي وقدري فليلتمس له ربا غيري من أي رواية ومن أي كتاب من الكتب المعتمدة؟','944','0','0');
INSERT INTO [t17878] VALUES (271,'٢٧١ - السابع عشر: في دعاء صلاة الصبح في تشهده مناصحة أهل التوبة وأيضا أناصحك بالتوبة ليس في النهاية، ولا كتب اللغة التي عندي ذكر المناصحة، إنما ذكروا النصح الخلوص، فإن ظفرتم بالمناصحة في شروح الحديث','945','0','0');
INSERT INTO [t17878] VALUES (272,'٢٧٢ - مسألة في النساء: هل يرين الله سبحانه في الآخرة، وهل هن فيها كالرجال؟','946','0','0');
INSERT INTO [t17878] VALUES (273,'٢٧٣ - مسألة: كيف ساغ بريرة مولاة عائشة أم المؤمنين رضي الله عنهما رد شفاعة النبي صلى الله عليه وسلم في زوجها مغيث؟','951','0','0');
INSERT INTO [t17878] VALUES (274,'٢٧٤ - مسألة: في قول القاضي عياض بآخر الشفاء: (ويخصنا بخصيصي زمرة نبينا وجماعته بسكون الياء بصيغة التثنية المحذوفة النون أو بألف التأنيث المقصورة؟','953','0','0');
INSERT INTO [t17878] VALUES (275,'٢٧٥ - مسألة: في أبوي النبي صلى الله عليه وسلم','957','0','0');
INSERT INTO [t17878] VALUES (276,'٢٧٦ - مسألة','973','0','0');
INSERT INTO [t17878] VALUES (277,'٢٧٧ - مسألة: فيما يفعل، أو يقال لمن به صداع أو حمى، أو نحوهما من الأوجاع','977','0','0');
INSERT INTO [t17878] VALUES (278,'٢٧٨ - مسألة: ما يكتب أو يقال لمن يتعسر عليها الولادة','982','0','0');
INSERT INTO [t17878] VALUES (279,'٢٧٩ - مسألة: هل ورد أنه من أراد أن يكون حمل زوجته ذكرا فليضع يده على بطنها وليقل: إن كان هذا الحمل ذكرا سميته محمدا، فإنه يكون؟','985','0','0');
INSERT INTO [t17878] VALUES (280,'٢٨٠ - مسألة: ذم إمرة الصغار','988','0','0');
INSERT INTO [t17878] VALUES (281,'٢٨١ - مسألة: لا يثبت في دخول سيدنا عبد الرحمن بن عوف رضي الله عنه الجنة زحفا، أو حبوا حديث','994','0','0');
INSERT INTO [t17878] VALUES (282,'٢٨٢ - مسألة: قد وقعت من النبي صلى الله عليه وسلم قرائن حالية وأصول كلية تقتضي أن سيدنا أبا بكر الصديق رضي الله عنه وعن سائر الصحابة أحق بالإمامة وأولى بالخلافة من غيره','999','0','0');
INSERT INTO [t17878] VALUES (283,'٢٨٣ - مسألة: تعاهد زيارة القبور، لا سيما من هو بالصلاح مذكور، أمر شهي محبوب، وقدر بهي مطلوب','1001','0','0');
INSERT INTO [t17878] VALUES (284,'٢٨٤ - مسألة: إمام لغير محصورين، يقول في الرفع من ركعو ثانية الصبح: سمع الله لمن حمده، ربنا لك الحمد، فيقول المبلغون: ربنا لك الحمد ويمطونها، ليبلغ مدى أصواتهم، من بعد عنهم، لسعة المسجد، فإذا فرغوا أتى بقنوت الصبح المعروف. وذيله بقوله الملحن: اللهم لا','1004','0','0');
INSERT INTO [t17878] VALUES (285,'٢٨٥ - مسألة: فيمن نفى إيمان ورقة فضلا عن صحبته متمسكا بقول من لم يشتهر بعلم وصمم على ذلك ماذا يلزمه؟','1006','0','0');
INSERT INTO [t17878] VALUES (286,'٢٨٦ - مسألة: بم يستفتح به الدعاء عقب الصلاة، أبالتسمية أم بالحمد؟','1008','0','0');
INSERT INTO [t17878] VALUES (287,'٢٨٧ - مسألة: في هدم المكان الذي أحدثه اليهود ـ لنعهم الله ـ كنيسة ببيت المقدس، استفتي عنه قبل جريان ما جرى؟','1011','0','0');
INSERT INTO [t17878] VALUES (288,'٢٨٨ - مسألة: ما حكم الحديث الوارد في النهي عن كسر سكة المسلمين ومعناه وعلة النهي؟','1029','0','0');
INSERT INTO [t17878] VALUES (289,'٢٨٩ - مسألة: هل ورد تبسمه صلى الله عليه وسلم في الصلاة؟','1032','0','0');
INSERT INTO [t17878] VALUES (290,'٢٩٠ - مسألة: في عمل المسلم للذمي','1033','0','0');
INSERT INTO [t17878] VALUES (291,'٢٩١ - مسألة: هل ورد تعيين أمد للمغفرة للحاج؟','1039','0','0');
INSERT INTO [t17878] VALUES (292,'٢٩٢ - مسألة: هل ورد الإعلام بمجيء زمن يتحاب أهله فيه بالألسنة دون القلوب؟','1041','0','0');
INSERT INTO [t17878] VALUES (293,'٢٩٣ - مسألة: في المنع من الاستغفار عقب الذكر والتسبيح ونحوه','1042','0','0');
INSERT INTO [t17878] VALUES (294,'٢٩٤ - مسألة: هل كان فتح دمياط عنوة، أم صلحا؟','1042','0','0');
INSERT INTO [t17878] VALUES (295,'٢٩٥ - مسألة: في النهي عن تخصيص المرء نفسه بالدعاء','1044','0','0');
INSERT INTO [t17878] VALUES (296,'٢٩٦ - مسألة: هل صح فيما فضل عن الأصابع من الثوب شيء، أم لا؟','1045','0','0');
INSERT INTO [t17878] VALUES (297,'٢٩٧ - مسألة: ما وجه أمره صلى الله عليه وسلم بالقصاص في كسر البيع عمة أنس سن جارية من الأنصار، مع كونه المقرر أن القصاص إنما هو في القلع؟','1047','0','0');
INSERT INTO [t17878] VALUES (298,'٢٩٨ - مسألة: في الحديث الوارد في تشديد أكل درهم ربا على زيادة على ثلاثين زنية','1048','0','0');
INSERT INTO [t17878] VALUES (299,'٢٩٩ - مسألة: في الوارد في القرون','1052','0','0');
INSERT INTO [t17878] VALUES (300,'٣٠٠ - مسألة: في الوارد في المعز والشاة','1054','0','0');
INSERT INTO [t17878] VALUES (301,'٣٠١ - مسألة: فيما وقع في كلام بعض الفقهاء أنه صلى الله عليه وسلم قال: إذا سلم إمامكم ولم يقم فانخسوه وأنه قال: جلوس الإمام بعد سلامه في محرابه جفاء منه وخديعة به، وكأنه قعد على جمرة من النار وأن عليا رضي الله عنه قال: ما من إمام يقعد في مجلسه ب','1061','0','0');
INSERT INTO [t17878] VALUES (302,'٣٠٢ - مسألة: في السنا','1065','0','0');
INSERT INTO [t17878] VALUES (303,'٣٠٣ - مسألة: في مسح الوجه باليدين بعد الدعاء','1067','0','0');
INSERT INTO [t17878] VALUES (304,'٣٠٤ - مسالة','1068','0','0');
INSERT INTO [t17878] VALUES (305,'٣٠٥ - مسألة: في الوعيد على التدافع على الإمامة بلفظ: إذا تجاوز الرجلان في الإمامة بعد الإقامة فهما في النار','1073','0','0');
INSERT INTO [t17878] VALUES (306,'٣٠٦ - مسألة: فيما يقع في كلام بعض الصوفية مما ينسب للحديث: رأيت ربي في المنام في أحسن صورة','1075','0','0');
INSERT INTO [t17878] VALUES (307,'٣٠٧ - مسألة: في الوارد عن النهي عن ضحك المرء من الريح الذي يخرج من جليسه','1077','0','0');
INSERT INTO [t17878] VALUES (308,'٣٠٨ - مسألة: في النهي عن سكنى القرى','1078','0','0');
INSERT INTO [t17878] VALUES (309,'٣٠٩ - مسألة: في الموطن الذي استحيت الملائكة فيه من عثمان رضي الله عنه؟','1081','0','0');
INSERT INTO [t17878] VALUES (310,'٣١٠ - مسألة: روى الدارقطني والبيهقي في سننهما عن عائشة مرفوعا: لا اعتكاف إلا بصيام وسنده ضعيف','1081','0','0');
INSERT INTO [t17878] VALUES (311,'٣١١ - سئلت: كيف ساغ مع قوله صلى الله عليه وسلم: «كل بدعة ضلالة» تقسيمها إلى الأحكام الخمسة التي منها الواجب والمندوب والمباح؟','1085','0','0');
INSERT INTO [t17878] VALUES (312,'٣١٢ - مسألة: الأحاديث الواردة فيمن قعد يذكر الله تعالى بعد صلاة الصبح حتى تطلع الشمس ثم صلى ركعتين التي منها ما للترمذي عن أنس رفعه: من صلى الفجر في جماعة ثم قعد يذكر الله عز وجل حتى تطلع الشمس ثم صلى ركعتين كانت له كأجر حجة وعمرة تامة تامة تامة وق','1086','0','0');
INSERT INTO [t17878] VALUES (313,'٣١٣ - سئلت عما يقال: إن الأمة لا تؤلف تحت الأرض؟','1090','0','0');
INSERT INTO [t17878] VALUES (314,'٣١٤ - سئلت عن حديث: رحم الله والدا أعان ولده على بره','1093','0','0');
INSERT INTO [t17878] VALUES (315,'٣١٥ - سئلت عن رؤية النبي صلى الله عليه وسلم يقظة؟','1096','0','0');
INSERT INTO [t17878] VALUES (316,'٣١٦ - سئلت عن أصل عمل المولد الشريف؟','1112','0','0');
INSERT INTO [t17878] VALUES (317,'٣١٧ - مسألة: قول القائل: إن تأخير السيد يعقوب عليه الصلاة والسلام الاستغفار لبنيه لوقت السحر التماسا للاستجابة أيعترض بأن دعوات الأنبياء في كل وقت مستجابة، أو يجاب بأن قوله صلى الله عليه وسلم: لكل نبي دعوة مستجابة يقتضى تخلف الإجابة، ما تحقيق الأم','1117','0','0');
INSERT INTO [t17878] VALUES (318,'٣١٨ - مسألة: لا شك أنه صلى الله عليه وسلم كان عالما ببراءة عائشة رضي الله عنها بحيث قال: والله ما أعلم على أهلي إلا خيرا ولقد ذكروا لي رجلا والله ما علمت عليه إلا خيرا ولا كان يدخل على أهلي إلا معي','1118','0','0');
INSERT INTO [t17878] VALUES (319,'٣١٩ - سئلت: هل صعد النبي صلى الله عليه وسلم جبل أبي قبيس؟','1120','0','0');
INSERT INTO [t17878] VALUES (320,'٣٢٠ - مسألة: في الترغيب في الوقوف بعرفة إذا كان يوم جمعة','1123','0','0');
INSERT INTO [t17878] VALUES (321,'٣٢١ - سئلت: هل ورد: تكفل الله لطالب العلم برزقه؟','1126','0','0');
INSERT INTO [t17878] VALUES (322,'٣٢٢ - سؤال: في صلاة النبي صلى الله عليه وسلم خلف أبي بكر وعبد الرحمن بن عوف من رواها وهل صلى خلف غيرهما؟','1127','0','0');
INSERT INTO [t17878] VALUES (323,'٣٢٣ - مسألة: في دعاء ابن عباس الذي حفظه عند فراغه صلى الله عليه وسلم من تهجده؟','1134','0','0');
INSERT INTO [t17878] VALUES (324,'٣٢٤ - سئلت: في تحرير الأفضل من فاطمة وخديجة وعائشة رضي الله عنهن وكذا مريم عليها السلام؟','1141','0','0');
INSERT INTO [t17878] VALUES (325,'٣٢٥ - سئلت: هل ورد في توسيع الأكمام شيء؟','1145','0','0');
INSERT INTO [t17878] VALUES (326,'٣٢٦ - مسألة: لم أعلم في تقبيل جدران الكعبة ومسها عن الشارع صلى الله عليه وسلم سوى ما ثبت من تقبيل الحجر الأسود','1151','0','0');
INSERT INTO [t17878] VALUES (327,'٣٢٧ - مسألة: في شق صدره صلى الله عليه وسلم المندرج في معجزاته ووقع وهو صغير عند مرضعته حليمة، ومرة أخرى وهو ابن عشر سنين، كما لعبد الله ابن أحمد في زوائد المسند عن أبي هريرة وهو عند أبي نعيم في الدلائل في قصة له مع عبد المطلب وأنه ابن عشر أو نحوها','1152','0','0');
INSERT INTO [t17878] VALUES (328,'٣٢٨ - مسألة: في رؤيته صلى الله عليه وسلم الأنبياء ليلة الإسراء مع أن أجسادهم مستقرة في قبورهم بالأرض','1153','0','0');
INSERT INTO [t17878] VALUES (329,'٣٢٩ - مسائل في الخضاب وإصلاح اللحية والسراويل','1159','0','0');
INSERT INTO [t17878] VALUES (330,'٣٣٠ - مسألة: عن عمار بن ياسر أنه قال: جلدة ما بين عيني وأنفي، قاله عقب هم شخص بالإشارة إلى جبهته بالعصي ثم قال: فإذا بلغ ذلك من رجل فلم يستبق فاجتنبوه، ما معناه؟','1162','0','0');
INSERT INTO [t17878] VALUES (331,'٣٣١ - سئلت عن حديث: لم تظهر الفاحشة في قوم قط إلا فشا فيهم الطاعون ... الحديث. ما حكمه، ومن أخرجه؟','1163','0','0');
INSERT INTO [t17878] VALUES (332,'٣٣٢ - سئلت عن مصر والشام: أيهما أفضل؟','1166','0','0');
INSERT INTO [t17878] VALUES (333,'٣٣٣ - سئلت: عما يتداوله التجار ونحوهم من الأخبار بوجود معمر زاد سنة على أربعمائة سنة ونحوها','1167','0','0');
