INSERT INTO [t17878] VALUES (335,'٣٣٥ - مسألة: هل أعقب أبو عبيدة بن الجراح','1171','0','0');
INSERT INTO [t17878] VALUES (336,'٣٣٦ - سئلت عن المثل المشهور: كل الصيد في جوف الفرا. هل هو حدث وما معناه؟','1172','0','0');
