INSERT INTO [t17894] VALUES (28,'١ - القاعدة الأولى الماء الجاري؛ هل هو كالراكد، أو كل جرية منه لها حكم الماء المنفرد؟','84','0','0');
INSERT INTO [t17894] VALUES (29,'٢ - القاعدة الثانية شعر الحيوان في حكم المنفصل عنه لا في حكم المتصل، وكذلك الظفر','89','0','0');
INSERT INTO [t17894] VALUES (30,'٣ - القاعدة الثالثة من وجبت عليه عبادة، فأتى بما لو اقتصر على ما دونه لأجزأه؛ هل يوصف الكل بالوجوب، أو قدر الإجزاء منه؟','96','0','0');
INSERT INTO [t17894] VALUES (31,'٤ - القاعدة الرابعة العبادات كلها -سواء كانت بدنية، أو مالية، أو مركبة منهما- لا يجوز تقديمها على سبب وجوبها، ويجوز تقديمها بعد سبب الوجوب وقبل الوجوب، أو قبل شرط الوجوب','103','0','0');
INSERT INTO [t17894] VALUES (32,'٥ - القاعدة الخامسة من عجل عبادة قبل وقت الوجوب، ثم جاء وقت الوجوب وقد تغير الحال بحيث لو فعل المعجل في وقت الوجوب لم يجزئه؛ فهل تجزئه أم لا؟','109','0','0');
INSERT INTO [t17894] VALUES (33,'٦ - القاعدة السادسة إذا فعل عبادة في وقت وجوبها يظن أنها الواجبة عليه، ثم تبين بأخرة أن الواجب كان غيره؛ فإنه يجزئه','115','0','0');
INSERT INTO [t17894] VALUES (34,'٧ - القاعدة السابعة من تلبس بعبادة، ثم وجد قبل فراغها ما لو كان واجدا له قبل الشروع لكان هو الواجب دون ما تلبس به؛ هل يلزمه الانتقال إليه، أم يمضي ويجزئه؟','118','0','0');
INSERT INTO [t17894] VALUES (35,'٨ - القاعدة الثامنة من قدر على بعض العبادة وعجز عن باقيها؛ هل يلزمه الإتيان بما قدر عليه منها أم لا؟','122','0','0');
INSERT INTO [t17894] VALUES (36,'٩ - القاعدة التاسعة [في] العبادات الواقعة على وجه محرم','130','0','0');
INSERT INTO [t17894] VALUES (37,'١٠ - القاعدة العاشرة الألفاظ المعتبرة في العبادات والمعاملات','143','0','0');
INSERT INTO [t17894] VALUES (38,'١١ - القاعدة الحادية عشرة من عليه فرض؛ هل له أن يتنفل قبل أدائه بجنسه أم لا؟','145','0','0');
INSERT INTO [t17894] VALUES (39,'١٢ - القاعدة الثانية عشرة المذهب أن العبادات الواردة على وجوه متعددة يجوز فعلها على جميع تلك الوجوه الواردة فيها من غير كراهة لبعضها','152','0','0');
INSERT INTO [t17894] VALUES (40,'١٣ - القاعدة الثالثة عشرة إذا وجدنا [أثرا] معلولا لعلة، ووجدنا في محله علة صالحة له، ويمكن أن يكون الأثر معلولا لغيرها، لكن لا يتحقق وجود غيرها؛ فهل يحال ذلك الأثر على تلك العلة المعلومة أم لا؟','170','0','0');
INSERT INTO [t17894] VALUES (41,'١٤ - القاعدة الرابعة عشرة إذا وجد سبب إيجاب أو تحريم من أحد رجلين لا يعلم عينه منهما؛ فهل يلحق الحكم بكل [واحد] منهما، أو لا يلحق بواحد منهما شيء؟','189','0','0');
INSERT INTO [t17894] VALUES (42,'١٥ - القاعدة الخامسة عشرة إذا استصحبنا أصلا، [أو] أعملنا ظاهرا في طهارة شيء، أو حله، أو حرمته، وكان لازم ذلك تغير أصل آخر يجب استصحابه، أو ترك العمل بظاهر آخر يجب إعماله؛ لم يلتفت إلى ذلك اللازم على الصحيح','196','0','0');
INSERT INTO [t17894] VALUES (43,'١٦ - القاعدة السادسة عشرة إذا كان للواجب بدل، فتعذر الوصول إلى الأصل حالة الوجوب؛ فهل يتعلق الوجوب بالبدل تعلقا مستقرا بحيث لا يعود إلى الأصل عند وجوده؟','201','0','0');
INSERT INTO [t17894] VALUES (44,'١٧ - القاعدة السابعة عشرة إذا تقابل عملان: أحدهما ذو شرف في نفسه ورفعة وهو واحد، والآخر ذو تعدد في نفسه وكثرة؛ فأيهما يرجح؟','209','0','0');
INSERT INTO [t17894] VALUES (45,'١٨ - القاعدة الثامنة عشرة إذا اجتمعت عبادتان من جنس في وقت واحد ليست إحداهما [مفعولة] على جهة القضاء ولا على طريق التبعية للأخرى في الوقت؛ تداخلت أفعالهما، واكتفي فيهما بفعل واحد.','221','0','0');
INSERT INTO [t17894] VALUES (46,'١٩ - القاعدة التاسعة عشرة إمكان الأداء ليس بشرط في استقرار الواجبات بالشرع في الذمة على ظاهر المذهب','238','0','0');
INSERT INTO [t17894] VALUES (47,'٢٠ - القاعدة العشرون النماء المتولد من العين حكمه حكم الجزء، والمتولد من الكسب بخلافه على الصحيح','243','0','0');
INSERT INTO [t17894] VALUES (48,'٢١ - القاعدة الحادية والعشرون وقد يختص الولد من بين سائر النماء المتولد من العين بأحكام','249','0','0');
INSERT INTO [t17894] VALUES (49,'٢٢ - القاعدة الثانية والعشرون العين المنغمرة في غيرها إذا لم يظهر أثرها؛ فهل هي كالمعدومة حكما أو لا؟','251','0','0');
INSERT INTO [t17894] VALUES (50,'٢٣ - القاعدة الثالثة والعشرون من حرم عليه الامتناع من بذل شيء سئله فامتنع؛ فهل يسقط إذنه بالكلية، أو يعتبر ويجبره الحاكم عليه؟','261','0','0');
INSERT INTO [t17894] VALUES (51,'٢٤ - القاعدة الرابعة والعشرون من تعلق بماله حق واجب عليه، فبادر إلى نقل الملك عنه؛ صح، ثم إن كان الحق متعلقا بالمال نفسه؛ لم يسقط، وإن كان متعلقا بمالكه لمعنى زال بانتقاله عنه؛ سقط، وإن كان لا يزول بانتقاله؛ لم يسقط على الأصح','274','0','0');
INSERT INTO [t17894] VALUES (52,'٢٥ - القاعدة الخامسة والعشرون من ثبت له ملك عين ببينة أو إقرار؛ فهل يتبعها ما يتصل بها، أو [يتولد] منها أم لا؟','278','0','0');
INSERT INTO [t17894] VALUES (53,'٢٦ - القاعدة السادسة والعشرون من أتلف شيئا لدفع أذاه له؛ لم يضمنه، وإن أتلفه لدفع أذاه به؛ ضمنه','285','0','0');
INSERT INTO [t17894] VALUES (54,'٢٧ - القاعدة السابعة والعشرون من أتلف نفسا أو أفسد عبادة لنفع يعود إلى نفسه؛ فلا ضمان عليه، وإن كان النفع يعود إلى غيره؛ فعليه الضمان','288','0','0');
INSERT INTO [t17894] VALUES (55,'٢٨ - القاعدة الثامنة والعشرون إذا حصل التلف من فعلين، أحدهما مأذون فيه والآخر غير مأذون فيه؛ وجب الضمان كاملا على الصحيح، وإن كان من فعلين غير مأذون فيهما؛ فالضمان بينهما نصفين حتى لو كان أحدهما من فعل من لا يجب الضمان عليه؛ لم يجب على الآخر أكثر من ا','291','0','0');
INSERT INTO [t17894] VALUES (56,'٢٩ - القاعدة التاسعة والعشرون من سومح في مقدار يسير فزاد عليه؛ فهل تنتفي المسامحة في الزيادة وحدها أو في الجميع؟','299','0','0');
INSERT INTO [t17894] VALUES (57,'٣٠ - القاعدة الثلاثون إذا [أخرج] عن ملكه [مالا] على وجه العبادة، ثم طرأ ما يمنع إجزاءه [أو] الوجوب؛ فهل يعود إلى ملكه أم لا؟','304','0','0');
INSERT INTO [t17894] VALUES (58,'٣١ - القاعدة الحادية والثلاثون من شرع في عبادة تلزم بالشروع، ثم فسدت؛ فعليه قضاؤها على صفة التي أفسدها، سواء كانت واجبة في الذمة على تلك الصفة أو دونها','306','0','0');
INSERT INTO [t17894] VALUES (59,'٣٢ - القاعدة الثانية والثلاثون يصح عندنا استثناء منفعة العين المنتقل ملكها من ناقلها مدة معلومة','309','0','0');
INSERT INTO [t17894] VALUES (60,'٣٣ - القاعدة الثالثة والثلاثون الاستثناء الحكمي؛ هل هو كالاستثناء اللفظي، أم تغتفر فيه الجهالة بخلاف اللفظي؟','313','0','0');
INSERT INTO [t17894] VALUES (61,'٣٤ - القاعدة الرابعة والثلاثون استحقاق منافع العبد بعقد لازم يمنع من سريان العتق إليها','318','0','0');
INSERT INTO [t17894] VALUES (62,'٣٥ - القاعدة الخامسة والثلاثون من ملك منفعة عين بعقد، ثم ملك العين بسبب آخر؛ هل ينفسخ العقد الأول أم لا؟','323','0','0');
INSERT INTO [t17894] VALUES (63,'٣٦ - القاعدة السادسة والثلاثون من استأجر عينا ممن له ولاية الإيجار، ثم زالت ولايته قبل انقضاء المدة؛ فهل تنفسخ الإجارة؟','329','0','0');
INSERT INTO [t17894] VALUES (64,'٣٧ - القاعدة السابعة والثلاثون في توارد العقود المختلفة بعضها على بعض وتداخل أحكامها','339','0','0');
INSERT INTO [t17894] VALUES (65,'٣٨ - القاعدة الثامنة والثلاثون فيما إذا وصل بألفاظ العقود ما يخرجها عن موضوعها؛ فهل يفسد العقد بذلك، أو يجعل كناية عما يمكن صحته على ذلك الوجه؟','346','0','0');
INSERT INTO [t17894] VALUES (66,'٣٩ - القاعدة التاسعة والثلاثون في انعقاد العقود بالكنايات واختلاف الأصحاب في ذلك','353','0','0');
INSERT INTO [t17894] VALUES (67,'٤٠ - القاعدة الأربعون الأحكام المتعلقة بالأعيان بالنسبة إلى تبدل الأملاك واختلافها','357','0','0');
INSERT INTO [t17894] VALUES (68,'٤١ - القاعدة الحادية والأربعون إذا تعلق بعين حق تعلقا لازما، فأتلفها من يلزمه الضمان؛ فهل يعود الحق إلى البدل المأخوذ من غير عقد آخر؟','364','0','0');
INSERT INTO [t17894] VALUES (69,'٤٢ - القاعدة الثانية والأربعون في أداء الواجبات المالية','366','0','0');
INSERT INTO [t17894] VALUES (70,'٤٣ - القاعدة الثالثة والأربعون فيما يضمن من الأعيان بالعقد أو باليد','373','0','0');
INSERT INTO [t17894] VALUES (71,'٤٤ - القاعدة الرابعة والأربعون في قبول قول الأمناء في الرد والتلف','394','0','0');
INSERT INTO [t17894] VALUES (72,'٤٥ - القاعدة الخامسة والأربعون عقود الأمانات؛ هل تنفسخ بمجرد التعدي فيها أم لا؟','402','0','0');
INSERT INTO [t17894] VALUES (73,'٤٦ - القاعدة السادسة والأربعون في العقود الفاسدة؛ هل هي منعقدة [أو] لا؟','407','0','0');
INSERT INTO [t17894] VALUES (74,'٤٧ - القاعدة السابعة والأربعون في ضمان المقبوض بالعقد الفاسد، كل عقد يجب الضمان في صحيحه يجب الضمان في فاسده، وكل عقد لا يجب الضمان في صحيحه لا يجب [الضمان] في فاسده','413','0','0');
INSERT INTO [t17894] VALUES (75,'٤٨ - القاعدة الثامنة والأربعون كل من ملك شيئا بعوض ملك عليه عوضه في آن واحد','426','0','0');
INSERT INTO [t17894] VALUES (76,'٤٩ - القاعدة التاسعة والأربعون القبض في العقود','432','0','0');
INSERT INTO [t17894] VALUES (77,'٥٠ - القاعدة الخمسون هل يتوقف الملك في العقود القهرية على دفع الثمن، أو يقع بدونه مضمونا في الذمة؟','438','0','0');
INSERT INTO [t17894] VALUES (78,'٥١ - القاعدة الحادية والخمسون فيما يعتبر القبض لدخوله في ضمان مالكه وما لا يعتبر له الملك، يقع تارة بعقد وتارة بغير عقد','442','0','0');
INSERT INTO [t17894] VALUES (79,'٥٢ - القاعدة الثانية والخمسون في التصرف في المملوكات قبل قبضها','454','0','0');
INSERT INTO [t17894] VALUES (80,'٥٣ - القاعدة الثالثة والخمسون من تصرف في عين تعلق بها حق [لله تعالى أو] لآدمي معين؛ إن كان الحق مستقرا فيها بمطالبة من له الحق بحقه أو يأخذه بحقه؛ لم ينفذ التصرف و [إن] لم يوجد سوى تعلق الحق لاستيفائه منها','486','0','0');
INSERT INTO [t17894] VALUES (81,'٥٤ - القاعدة الرابعة والخمسون من ثبت له حق في عين وسقط بتصرف غيره فيها؛ فهل يجوز للمتصرف فيها الإقدام على التصرف المسقط لحق غيره قبل استئذانه أم لا؟','498','0','0');
INSERT INTO [t17894] VALUES (82,'٥٥ - القاعدة الخامسة والخمسون من ثبت له حق التملك بفسخ أو عقد؛ هل يكون تصرفه [تملكا] أم لا؟ وهل ينفذ تصرفه أم لا؟','504','0','0');
INSERT INTO [t17894] VALUES (83,'٥٦ - القاعدة السادسة والخمسون شروط العقود من أهلية العاقد، [و] المعقود له أو عليه إذا وجدت مقترنة بها ولم تتقدم عليها، هل يكتفى بها في صحتها، أم لا بد من سبقها؟','529','0','0');
INSERT INTO [t17894] VALUES (84,'٥٧ - القاعدة السابعة والخمسون إذا تقارن الحكم ووجود المنع منه؛ فهل يثبت الحكم أم لا؟','535','0','0');
INSERT INTO [t17894] VALUES (85,'٥٨ - القاعدة الثامنة والخمسون من تعلق به الامتناع من فعل هو متلبس به، فبادر إلى الإقلاع عنه، هل يكون إقلاعه فعلا للممنوع منه، [أم] تركا له فلا يترتب عليه شيء من أحكامه؟','554','0','0');
INSERT INTO [t17894] VALUES (86,'٥٩ - القاعدة التاسعة والخمسون العقود لا ترد إلا على موجود بالفعل أو بالقوة، وأما الفسوخ؛ فترد على المعدوم حكما واختيارا على الصحيح','569','0','0');
INSERT INTO [t17894] VALUES (87,'٦٠ - القاعدة الستون التفاسخ في العقود الجائزة متى تضمن ضررا على أحد المتعاقدين أو غيرهما ممن له تعلق بالعقد؛ لم يجز ولم ينفذ، إلا أن يمكن استدراك الضرر بضمان أو نحوه؛ فيجوز على ذلك الوجه','578','0','0');
INSERT INTO [t17894] VALUES (88,'٦١ - القاعدة الحادية والستون المتصرف تصرفا عاما على الناس كلهم من غير ولاية أحد معين، وهو الإمام؛ هل يكون تصرفه عليهم بطريق الوكالة لهم، أو بطريق الولاية؟','588','0','0');
INSERT INTO [t17894] VALUES (89,'٦٢ - القاعدة الثانية والستون فيمن ينعزل قبل العلم بالعزل','591','0','0');
INSERT INTO [t17894] VALUES (90,'٦٣ - القاعدة الثالثة والستون وهي أن من لا يعتبر رضاه لفسخ عقد أو حله، لا يعتبر علمه به','595','0','0');
INSERT INTO [t17894] VALUES (91,'٦٤ - القاعدة الرابعة والستون من توقف نفوذ تصرفه أو سقوط الضمان أو الحنث عنه على الإذن، فتصرف قبل العلم به، ثم تبين أن الإذن كان موجودا؛ هل يكون كتصرف المأذون له أو لا؟','599','0','0');
INSERT INTO [t17894] VALUES (92,'٦٥ - القاعدة الخامسة والستون وهي من تصرف في شيء يظن أنه لا يملكه؛ فتبين أنه كان يملكه','601','0','0');
INSERT INTO [t17894] VALUES (93,'٦٦ - القاعدة السادسة والستون ولو تصرف [مستند] إلى سبب، ثم تبين خطؤه فيه، وأن السبب المعتمد غيره وهو موجود','607','0','0');
INSERT INTO [t17894] VALUES (94,'٦٧ - القاعدة السابعة والستون من استحق الرجوع بعين أو دين بفسخ أو غيره، وكان قد رجع إليه ذلك الحق بهبة أو إبراء ممن يستحق عليه الرجوع؛ فهل يستحق الرجوع [عليه] ببدله أم لا؟','609','0','0');
INSERT INTO [t17894] VALUES (95,'٦٨ - القاعدة الثامنة والستون إيقاع العبادات أو العقود أو غيرهما مع الشك في شرط صحتها؛ هل يجعلها كالمعلقة على تحقق ذلك الشرط أم لا؟','613','0','0');
INSERT INTO [t17894] VALUES (96,'٦٩ - القاعدة التاسعة والستون العقد الوارد على عمل معين','625','0','0');
INSERT INTO [t17894] VALUES (97,'٧٠ - القاعدة السبعون الفعل المتعدي إلى مفعول أو المتعلق بظرف أو مجرور إذا كان مفعوله أو متعلقه عاما؛ فهل يدخل الفاعل الخاص في عمومه، أم يكون ذكر الفاعل قرينة مخرجة له من العموم، أو يختلف ذلك بحسب القرائن؟','631','0','0');
INSERT INTO [t17894] VALUES (98,'٧١ - القاعدة الحادية والسبعون فيما يجوز الأكل منه من الأموال بغير إذن مستحقيها','645','0','0');
INSERT INTO [t17894] VALUES (99,'٧٢ - القاعدة الثانية والسبعون اشتراط النفقة والكسوة في العقود','658','0','0');
INSERT INTO [t17894] VALUES (100,'٧٣ - القاعدة الثالثة والسبعون اشتراط نفع أحد المتعاقدين في العقد','664','0','0');
INSERT INTO [t17894] VALUES (101,'٧٤ - القاعدة الرابعة والسبعون فيمن يستحق العوض عن عمله بغير شرط','667','0','0');
INSERT INTO [t17894] VALUES (102,'٧٥ - القاعدة الخامسة والسبعون فيمن يرجع بما أنفق على مال غيره بغير إذنه. وهو نوعان','676','0','0');
INSERT INTO [t17894] VALUES (103,'٧٦ - القاعدة السادسة والسبعون الشريكان في عين مال أو منفعة إذا كانا محتاجين إلى دفع مضرة أو إبقاء منفعة؛ أجبر أحدهما على موافقة الآخر في الصحيح من المذهب','691','0','0');
INSERT INTO [t17894] VALUES (104,'٧٧ - القاعدة السابعة والسبعون من اتصل [بملكه ملك] غيره متميزا عنه وهو تابع له','708','0','0');
INSERT INTO [t17894] VALUES (105,'٧٨ - القاعدة الثامنة والسبعون من أدخل النقص على ملك غيره، لاستصلاح ملكه وتخليصه من ملك غيره','720','0','0');
INSERT INTO [t17894] VALUES (106,'٧٩ - القاعدة التاسعة والسبعون الزرع النابت في أرض الغير بغير إذن صحيح أقسام','725','0','0');
INSERT INTO [t17894] VALUES (107,'٨٠ - القاعدة الثمانون ما يتكرر حمله من أصول البقول والخضراوات؛ هل هو ملحق بالزرع أو بالشجر؟','751','0','0');
INSERT INTO [t17894] VALUES (108,'٨١ - القاعدة الحادية والثمانون النماء المتصل في الأعيان المملوكة العائدة إلى من انتقل الملك عنه بالفسوخ','755','0','0');
INSERT INTO [t17894] VALUES (109,'٨٢ - القاعدة الثانية والثمانون والنماء المنفصل تارة يكون متولدا من عين الذات؛ كالولد والطلع والصوف واللبن والبيض، وتارة يكون متولدا من غيرها، واستحق بسبب العين؛ كالمهر [والأجرة] والأرش','776','0','0');
INSERT INTO [t17894] VALUES (110,'٨٣ - القاعدة الثالثة والثمانون إذا انتقل الملك عن النخل بعقد أو فسخ تتبع فيه الزيادة المتصلة دون المنفصلة أو بانتقال استحقاق','814','0','0');
INSERT INTO [t17894] VALUES (111,'٨٤ - القاعدة الرابعة والثمانون الحمل؛ هل له حكم قبل انفصاله أم لا؟','827','0','0');
INSERT INTO [t17894] VALUES (112,'٨٥ - القاعدة الخامسة والثمانون الحقوق خمسة أنواع','861','0','0');
INSERT INTO [t17894] VALUES (113,'٨٦ - القاعدة السادسة والثمانون الملك أربعة أنواع: ملك عين ومنفعة، وملك عين بلا منفعة، وملك منفعة بلا عين، وملك انتفاع من غير ملك المنفعة','885','0','0');
INSERT INTO [t17894] VALUES (114,'٨٧ - القاعدة السابعة والثمانون فيما يقبل النقل والمعاوضة من الحقوق المالية والأملاك','892','0','0');
INSERT INTO [t17894] VALUES (115,'٨٨ - القاعدة الثامنة والثمانون في الانتفاع وإحداث ما ينتفع به [في] الطرق المسلوكة في الأمصار والقرى وهوائها وقرارها','904','0','0');
INSERT INTO [t17894] VALUES (116,'٨٩ - القاعدة التاسعة والثمانون أسباب الضمان ثلاثة: عقد، ويد، وإتلاف','918','0','0');
INSERT INTO [t17894] VALUES (117,'٩٠ - القاعدة التسعون الأيدي المستولية على مال الغير بغير إذنه ثلاثة','923','0','0');
INSERT INTO [t17894] VALUES (118,'٩١ - القاعدة الحادية والتسعون يضمن بالعقد وباليد الأموال المحضة المنقولة إذا وجد فيها النقل','926','0','0');
INSERT INTO [t17894] VALUES (119,'٩٢ - القاعدة الثانية والتسعون هل تثبت يد الضمان مع ثبوت يد المالك أم لا؟','931','0','0');
INSERT INTO [t17894] VALUES (120,'٩٣ - القاعدة الثالثة والتسعون من قبض مغصوبا من غاصبه، ولم يعلم أنه مغصوب','936','0','0');
INSERT INTO [t17894] VALUES (121,'٩٤ - القاعدة الرابعة والتسعون وقبض مال الغير من يد قابضه بحق بغير إذن مالكه','957','0','0');
INSERT INTO [t17894] VALUES (122,'٩٥ - القاعدة الخامسة والتسعون من أتلف [مال غيره] وهو يظن أنه له، أو تصرف فيه يظن لنفسه ولاية عليه، ثم تبين خطأ ظنه','960','0','0');
INSERT INTO [t17894] VALUES (123,'٩٦ - القاعدة السادسة والتسعون من وجب عليه أداء عين مال، فأداه عنه غيره بغير إذنه؛ هل تقع موقعه وبنتفي الضمان عن المؤدي؟','972','0','0');
INSERT INTO [t17894] VALUES (124,'٩٧ - القاعدة السابعة والتسعون من بيده مال أو في ذمته دين يعرف مالكه، ولكنه غائب يرجى قدومه','980','0','0');
INSERT INTO [t17894] VALUES (125,'٩٨ - القاعدة الثامنة والتسعون من ادعى شيئا وصفه من دفع إليه بالصفة إذا جهل ربه، ولم يثبث عليه يد من جهة مالكه، وإلا؛ فلا','988','0','0');
INSERT INTO [t17894] VALUES (126,'٩٩ - القاعدة التاسعة والتسعون ما تدعو الحاجة إلى الانتفاع به من [الأموال و] الأعيان ولا ضرر في بذله؛ لتيسره وكثرة وجوده، [أو المنافع المحتاج إليها]؛ يجب بذله مجانا بغير عوض [في الأظهر]','990','0','0');
INSERT INTO [t17894] VALUES (127,'١٠٠ - القاعدة المئة [الواجب بالنذر هل يلحق بالواجب بالشرع أو بالمندوب؟','996','0','0');
INSERT INTO [t17894] VALUES (128,'١٠١ - القاعدة الحادية بعد المئة من خير بين شيئين، وأمكنه الإتيان بنصفيهما معا؛ فهل يجزئه أم لا؟','999','0','0');
INSERT INTO [t17894] VALUES (129,'١٠٢ - القاعدة الثانية بعد المئة من أتى بسبب يفيد الملك أو الحل أو يسقط الواجبات] على وجه محرم، وكان مما تدعو النفوس إليه؛ ألغي ذلك الشرط، وصار وجوده كالعدم، ولم يترتب عليه أحكامه','1003','0','0');
INSERT INTO [t17894] VALUES (130,'١٠٣ - القاعدة الثالثة بعد المئة الفعل الواحد يبنى بعضه على بعض مع الاتصال المعتاد، ولا ينقطع يالتفرق اليسير، ولذلك صور','1008','0','0');
INSERT INTO [t17894] VALUES (131,'١٠٤ - القاعدة الرابعة بعد المئة الرضا بالمجهول قدرا أو جنسا أو وصفا؛ هل هو رضا معتبر لازم؟','1015','0','0');
INSERT INTO [t17894] VALUES (132,'١٠٥ - القاعدة الخامسة بعد المئة في إضافة الإنشاءات والإخبارات إلى المبهمات','1020','0','0');
INSERT INTO [t17894] VALUES (133,'١٠٦ - القاعدة السادسة بعد المئة ينزل المجهول منزلة المعدوم؛ وإن كان الأصل بقاءه إذا يئس من الوقوف عليه أو شق اعتباره','1034','0','0');
INSERT INTO [t17894] VALUES (134,'١٠٧ - القاعدة السابعة بعد المئة تمليك المعدوم والإباحة','1038','0','0');
INSERT INTO [t17894] VALUES (135,'١٠٨ - القاعدة الثامنة بعد المئة ما جهل وقوعه مترتبا أو متقارنا؛ هل يحكم عليه بالتقارن أو بالتعاقب؟','1043','0','0');
INSERT INTO [t17894] VALUES (136,'١٠٩ - القاعدة التاسعة بعد المئة المنع من واحد مبهم من أعيان أو معين مشتبه بأعيان يؤثر الاشتباه فيها المنع بمنع التصرف في تلك الأعيان قبل تمييزه','1046','0','0');
INSERT INTO [t17894] VALUES (137,'١١٠ - القاعدة العاشرة بعد المئة من ثبت له أحد أمرين','1059','0','0');
INSERT INTO [t17894] VALUES (138,'١١١ - القاعدة الحادية عشر بعد المئة إذا كان الواجب بسبب واحد أحد شيئين، فقامت حجة يثبت بها أحدهما دون الآخر؛ فهل يثبت به أم لا؟','1062','0','0');
INSERT INTO [t17894] VALUES (139,'١١٢ - القاعدة الثانية عشر بعد المئة إذا اجتمع للمضطر محرمان، كل منهما لا يباح بدون الضرورة؛ وجب تقديم أخفهما مفسدة وأقلهما ضررا؛ لأن الزيادة لا ضرورة إليها؛ فلا تباح؟','1065','0','0');
INSERT INTO [t17894] VALUES (140,'١١٣ - القاعدة الثالثة عشر بعد المئة إذا وجدنا جملة ذات أعداد موزعة على جملة أخرى؛ فهل تتوزع أفراد الجملة الموزعة على أفراد الأخرى، أو كل فرد منها على مجموع الجملة الأولى؟','1072','0','0');
INSERT INTO [t17894] VALUES (141,'١١٤ - القاعدة الرابعة عشر بعد المئة إطلاق الشركة؛ هل يتنزل على المناصفة، [أم] هو مبهم يفتقر إلى تفسير؟','1116','0','0');
INSERT INTO [t17894] VALUES (142,'١١٥ - القاعدة الخامسة عشر بعد المئة الحقوق المشتركة بين اثنين فصاعدا نوعان','1119','0','0');
INSERT INTO [t17894] VALUES (143,'١١٦ - القاعدة السادسة عشر بعد المئة من استند [تملكه] إلى سبب مستقر لا يمكن إبطاله، وتأخر حصول الملك عنه؛ فهل تنعطف أحكام ملكه إلى أول وقت انعقاد السبب وتثبت أحكامه من حينئذ، أم لا يثبت إلا من حين ثبوت الملك؟','1130','0','0');
INSERT INTO [t17894] VALUES (144,'١١٧ - القاعدة السابعة عشر بعد المئة كل عقد معلق يختلف باختلاف حالين إذا وجد تعليقه في أحدهما ووقوعه في الآخر؛ فهل يغلب عليه جانب التعليق أو جانب الوقوع؟','1136','0','0');
INSERT INTO [t17894] VALUES (145,'١١٨ - القاعدة الثامنة عشر بعد المئة تعليق فسخ العقد وإبطاله بوجوده','1140','0','0');
INSERT INTO [t17894] VALUES (146,'١١٩ - القاعدة التاسعة عشر بعد المئة إذا وجدنا لفظا عاما قد خص بعض أفراده بحكم موافق للأول أو مخالف له؛ فهل يقضى بخروج الخاص من العام وانفراده بحكمه المختص به، أو يقضى بدخوله فيه؛ فيتعارضان مع اختلاف الحكم، ويتعدد سبب الاستحقاق مع اتفاقه؟','1144','0','0');
INSERT INTO [t17894] VALUES (147,'١٢٠ - القاعدة العشرون بعد المئة يرجح ذو القرابتين على ذي القرابة الواحدة، وإن لم تكن إحداهما لها مدخل في الاستحقاق','1155','0','0');
INSERT INTO [t17894] VALUES (148,'١٢١ - القاعدة الحادية والعشرون بعد المئة في تخصيص العموم بالعرف','1157','0','0');
INSERT INTO [t17894] VALUES (149,'١٢٢ - القاعدة الثانية والعشرون بعد المئة يخص العموم بالعادة على المنصوص','1168','0','0');
INSERT INTO [t17894] VALUES (150,'١٢٣ - القاعدة الثالثة والعشرون بعد المئة ويخص العموم بالشرع أيضا على الصحيح','1173','0','0');
INSERT INTO [t17894] VALUES (151,'١٢٤ - القاعدة الرابعة والعشرون بعد المئة هل نخص اللفظ العام بسببه الخاص إذا كان السبب هو المقتضي له؟','1175','0','0');
INSERT INTO [t17894] VALUES (152,'١٢٥ - القاعدة الخامسة والعشرون بعد المئة النية تعم الخاص وتخصص العام بغير خلاف [فيهما]، وهل تقيد المطلق [أو تكون] استثناء من النص؟','1181','0','0');
INSERT INTO [t17894] VALUES (153,'١٢٦ - القاعدة السادسة والعشرون بعد المئة الصور التي لا تقصد من العموم عادة؛ إما لندورها، أو لاختصاصها بمانع، لكن يشملها اللفظ مع اعتراف المتكلم بأنه لم يرد إدخالها فيه؛ هل يحكم بدخولها أم لا؟','1193','0','0');
INSERT INTO [t17894] VALUES (154,'١٢٧ - القاعدة السابعة والعشرون بعد المئة إذا استند إتلاف أموال الآدميين ونفوسهم إلى مباشرة وسبب تعلق الضمان بالمباشرة دون السبب؛ إلا أن تكون المباشرة مبنية على السبب وناشئة عنه، سواء كانت ملجئة إليه أو غير ملجئة','1199','0','0');
INSERT INTO [t17894] VALUES (155,'١٢٨ - القاعدة الثامنة والعشرون بعد المئة إذا اختلف حال المضمون في [حالي] الجناية والسراية','1211','0','0');
INSERT INTO [t17894] VALUES (156,'١٢٩ - القاعدة التاسعة والعشرون بعد المئة إذا تغير حال المرمي أو الرامي بين الرمي والإصابة؛ فهل الاعتبار بحالة الإصابة أم بحالة الرمى، أم يفرق بين القود والضمان، أم بين أن يكون الرمي مباحا أو محظورا؟','1220','0','0');
INSERT INTO [t17894] VALUES (157,'١٣٠ - القاعدة الثلاثون بعد المئة المسكن والخادم والمركب المحتاج إليه ليس بمال فاضل يمنع أخذ [الزكوات]، ولا يجب [به] الحج والكفارات، ولا توفى منه الديون والنفقات','1232','0','0');
INSERT INTO [t17894] VALUES (158,'١٣١ - القاعدة الحادية والثلاثون بعد المئة القدرة على اكتساب المال بالبضع ليس بغنى معتبر','1237','0','0');
INSERT INTO [t17894] VALUES (159,'١٣٢ - القاعدة الثانية والثلاثون بعد المئة القدرة على اكتساب المال بالصناعات غنى بالنسبة إلى نففة النفس، ومن تلزم نفقته من زوجة وخادم، وهل هو غنى فاضل عن ذلك؟','1238','0','0');
INSERT INTO [t17894] VALUES (160,'١٣٣ - القاعدة الثالثة والثلاثون بعد المئة يثبت تبعا ما لا يثبت استقلالا','1242','0','0');
INSERT INTO [t17894] VALUES (161,'١٣٤ - القاعدة الرابعة والثلاثون بعد المئة المنع أسهل من الرفع','1250','0','0');
INSERT INTO [t17894] VALUES (162,'١٣٥ - القاعدة الخامسة والثلاثون بعد المئة الملك القاصر من ابتدائه لا يستباح فيه الوطء، بخلاف ما كان القصور طارئا عليه','1253','0','0');
INSERT INTO [t17894] VALUES (163,'١٣٦ - القاعدة السادسة والثلاثون بعد المئة الوطء المحرم لعارض؛ هل يستتبع تحريم مقدماته أم لا؟','1257','0','0');
INSERT INTO [t17894] VALUES (164,'١٣٧ - القاعدة السابعة والثلاثون بعد المئة الواجب بقتل العمد؛ هل هو القود عينا، أو أحد أمرين؛ إما القود أو الدية؟','1259','0','0');
INSERT INTO [t17894] VALUES (165,'١٣٨ - القاعدة الثامنة والثلاثون بعد المئة العين المتعلق بها حق لله تعالى أو لآدمي؛ إما أن تكون مضمونة، أو غير مضمونة','1276','0','0');
INSERT INTO [t17894] VALUES (166,'١٣٩ - القاعدة التاسعة والثلاثون بعد المئة الحقوق الواجبة من جنس إذا كان بعضها مقدرا بالشرع وبعضها غير مقدر به','1285','0','0');
INSERT INTO [t17894] VALUES (167,'١٤٠ - القاعدة الأربعون بعد المئة من سقطت عنه العقوبة بإتلاف نفس أو طرف مع قيام المقتضي له لمانع؛ فإنه يتضاعف عليه الغرم','1290','0','0');
INSERT INTO [t17894] VALUES (168,'١٤١ - القاعدة الحادية والأربعون بعد المئة إذا أتلف عينا تعلق بها حق لله تعالى من يجب عليه حفظها واستيفاؤها إلى مدة معلومة؛ لزمه ضمانها بقيمتها في ذلك الوقت لا يوم تلفها، أو بمثلها على صفاتها في ذلك الوقت، لا يوم تلفها على أصح الوجهين','1293','0','0');
INSERT INTO [t17894] VALUES (169,'١٤٢ - القاعدة الثانية والأربعون بعد المئة ما زال من الأعيان ثم عاد بأصل الخلقة أو بصنع آدمي؛ هل يحكم على العائد بحكم الأول أو لا؟','1295','0','0');
INSERT INTO [t17894] VALUES (170,'١٤٣ - القاعدة الثالثة والأربعون بعد المئة يقوم البدل مقام المبدل ويسد مسده، ويبنى حكمه على [حكمه] في مواضع كثيرة','1300','0','0');
INSERT INTO [t17894] VALUES (171,'١٤٤ - القاعدة الرابعة والأربعون بعد المئة فيما يقوم فيه الورثة مقام موروثهم من الحقوق','1305','0','0');
INSERT INTO [t17894] VALUES (172,'١٤٥ - القاعدة الخامسة والأربعون بعد المئة المعتدة البائن في حكم الزوجات','1315','0','0');
INSERT INTO [t17894] VALUES (173,'١٤٦ - القاعدة السادسة والأربعون بعد المئة تفارق المطلقة الرجعية الزوجات','1318','0','0');
INSERT INTO [t17894] VALUES (174,'١٤٧ - القاعدة السابعة والأربعون بعد المئة أحكام النساء على النصف من أحكام الرجال','1320','0','0');
INSERT INTO [t17894] VALUES (175,'١٤٨ - القاعدة الثامنة والأربعون بعد المئة من أدلى بوارث وقام مقامه في استحقاق إرثه؛ سقط به، وإن أدلى به ولم يرث ميراثه؛ لم يسقط به','1323','0','0');
INSERT INTO [t17894] VALUES (176,'١٤٩ - القاعدة التاسعة والأربعون بعد المئة الحق الثابت لمعين يخالف الثابت لغير معين','1324','0','0');
