INSERT INTO [t18144] VALUES (1351,'من مناقب الشديد على دينه في جاهليته إذا أسلم، ومناقب خيار أهل الجاهلية عند أهل دينه إذا أسلم، وفقهه في الدين، ومثالب ذي الوجهين','12890','1279','0');
