INSERT INTO [t18144] VALUES (1354,'من مناقب أصحابي رسول الله -صلى الله عليه وسلم-، وأن رسول الله -صلى الله عليه وسلم- حالف بينهم وهم: قريش والأنصار، وبيان إبطال الحلف في الإسلام','12907','1279','0');
INSERT INTO [t18144] VALUES (1355,'من مناقب التابعين، [وأتباع التابعين]، وتبعة التابعين والقرن الذين بعدهم','12910','1279','0');
INSERT INTO [t18144] VALUES (1356,'[باب]: بيان المدة؛ التي لم يبق بعدها من كان على عهد النبي -صلى الله عليه وسلم- وحظر سب أصحاب النبي -صلى الله عليه وسلم- والدليل على أن أصحابه الخيار الذين صحبوه في أول الإسلام','12934','1279','0');
INSERT INTO [t18144] VALUES (1357,'من مناقب أويس القرني -رضي الله عنه-.','12949','1279','0');
