INSERT INTO [t18144] VALUES (1359,'من مناقب أسماء بنت أبي بكر، وابنها عبد الله بن الزبير -رضي الله عنهم-، وبيان كذب المختار ومثالب الحجاج','12958','1279','0');
INSERT INTO [t18144] VALUES (1360,'مناقب أهل عمان','12961','1279','0');
INSERT INTO [t18144] VALUES (1361,'مناقب أبناء فارس','12963','1279','0');
INSERT INTO [t18144] VALUES (1362,'باب: الدليل على أن من ينجب من الناس فينتفع به عزيز ومناقب الفقراء','12970','1279','0');
INSERT INTO [t18144] VALUES (1363,'مبتدأ كتاب صلة الأرحام والترغيب فيها وفي التودد والتآلف، والتعاون، ونصرة الأخ أخاه، وزيارته، وعيادته','12979','0','0');
INSERT INTO [t18144] VALUES (1364,'بيان إثبات فرض صلة الأرحام','12980','1363','0');
INSERT INTO [t18144] VALUES (1365,'باب: من يجب حسن صحبته وصلته دون غيرهم والدليل على أن الأم أوجب حقا من الأب','12983','1363','0');
INSERT INTO [t18144] VALUES (1366,'باب: الدليل على أن بر الوالدين بمنزلة الجهاد في سبيل الله ووجوب طاعتهما إذا نهيا ولديهما عن الخروج','12989','1363','0');
INSERT INTO [t18144] VALUES (1367,'باب: بيان الخبر الدال على أن المصلي إذا دعته أمه قطع صلاته وأجاب أمه','12997','1363','0');
INSERT INTO [t18144] VALUES (1368,'باب: بيان الخبر الدال على أن من أدرك أبويه أو أحدهما عند ضعفهما فبرهما أو أحدهما أدخله بره إياهما الجنة','13009','1363','0');
INSERT INTO [t18144] VALUES (1369,'باب: فضيلة الرجل يبر أهل ود أبيه','13011','1363','0');
INSERT INTO [t18144] VALUES (1370,'باب [بيان] الخبر الدال أن الرجل إذا أحسن خلقه. مع أقاربه وأهل ود أبيه وغيرهم فقه بر ووصل، وأن من الإثم ما يضمر في القلب مما يكره أن يطلع عليه','13017','1363','0');
INSERT INTO [t18144] VALUES (1371,'باب: بيان ثواب من وصل رحمه، وعقاب من قطعه وثواب من يصل من قطعه','13021','1363','0');
INSERT INTO [t18144] VALUES (1372,'باب [بيان] حظر القطيعة في التحاسد، والتباغض','13045','1363','0');
INSERT INTO [t18144] VALUES (1373,'باب: بيان عقوبة المشاحن، وأنه لا يعدله شيء من الذنوب','13078','1363','0');
INSERT INTO [t18144] VALUES (1374,'باب: بيان ثواب المتحابين في الله عز وجل','13084','1363','0');
INSERT INTO [t18144] VALUES (1375,'باب: [بيان] ثواب عائد المريض، وإطعام السائل، وسقيه','13089','1363','0');
INSERT INTO [t18144] VALUES (1376,'باب: بيان فضيلة المسقام وصاحب الأوجاع, وثوابها، وأن أفاضل الناس الذي تكون الأوجاع عليهم أشد وأجرهم أكثر','13100','1363','0');
INSERT INTO [t18144] VALUES (1377,'باب: ثواب المؤمن الذي يشاك الشوكة، أو يصيبه هم أو أذى','13110','1363','0');
INSERT INTO [t18144] VALUES (1378,'باب: [بيان] النهي عن سب الحمى وثواب من تصيبه الحمى، وثواب من يصرع فيصبر ولا يتعالج بشيء','13132','1363','0');
INSERT INTO [t18144] VALUES (1379,'باب: تحريم الظلم، وحظر ظلم الناس بعضهم بعضا، والترغيب في قضاء حاجة المسلم، والستر عليه','13136','1363','0');
INSERT INTO [t18144] VALUES (1380,'باب: عقاب الظالم والشاتم المسلم','13156','1363','0');
INSERT INTO [t18144] VALUES (1381,'باب: وجوب نصرة الأخ أخاه مظلوما كان أو ظالما، ونصرته إذا كان ظالما أن يرده عن ظلمه.','13168','1363','0');
INSERT INTO [t18144] VALUES (1382,'باب: وجوب عون المؤمن، وتقويته، والتعفف والترحم عليه، والاغتمام لغمه','13175','1363','0');
INSERT INTO [t18144] VALUES (1383,'باب: [بيان] تفسير الغيبة والبهتان، وحظرهما، وإباحة ذكر الرجل السوء بما فيه من ورائه، والنهي عن القول به في وجهه، والدليل على أن من منعه الحياء أن يصد في وجه المسيء بما يحب فهو من الخير','13192','1363','0');
INSERT INTO [t18144] VALUES (1384,'باب: وجوب الرفق مع الناس والدواب، وعقاب من يحرم الرفق','13201','1363','0');
INSERT INTO [t18144] VALUES (1385,'باب: التشديد فيمن يبدأ بسب المسلم، والإباحة للمسبوب أن يقتص، ولا يتعدى، والدليل على أن إثم ذلك على البادي بالسب ما لم يتعد المسبوب، وبيان ثواب من احتمل ذلك وعفا وتواضع','13214','1363','0');
INSERT INTO [t18144] VALUES (1386,'باب: عقوبة من يلعن دابة وهو راكبها، والتشديد في اللعن، ولا تواضع لله إلا رفعه الله','13222','1363','0');
INSERT INTO [t18144] VALUES (1387,'باب: ذكر أخبار مبينة أن من لعنه النبي -صلى الله عليه وسلم- من المؤمنين والمؤمنات، أو سبه، كان ذلك رحمة له وكفارة وأجرا والدليل على أن ذلك ليس لأحد من أمته، وأن من سبق من أحد من أمته ذلك إلى مؤمن أو مؤمنة استغفر له ودعا له','13239','1363','0');
INSERT INTO [t18144] VALUES (1388,'باب: التشديد في ذي الوجهين، وأنه ليس شيء أكبر منه وفي النميمة، والدليل على أنه من يأتي هؤلاء وهؤلاء حتى يفسد بينهم','13266','1363','0');
INSERT INTO [t18144] VALUES (1389,'باب: بيان الترغيب في الإصلاح بين الناس، والدليل على أنه الذي إذا كان بين اثنين مشاحنة يسعى فيها وقال لكل واحد منهما أحسن ما سمع من صاحبه فيه، ويخرج ما خبث بقلبه حتى يطيب قلب كل واحد منهما عن صاحبه، لا أن يكذب كذبا صراحا على أحد منهما، والتشديد في الكذب','13273','1363','0');
INSERT INTO [t18144] VALUES (1390,'باب [بيان] الترغيب في دفع الغضب، وفضيلته، وضبط نفسه، وكظم غيظه عنه الغضب، وذكر الخبر المبين أن ابن آدم لا يتمالك، ويعجز عن ضبط نفسه عنه الغضب، والترغيب في تقدم ولده وفضيلته','13288','1363','0');
INSERT INTO [t18144] VALUES (1391,'باب: بيان القول الذي إذا قاله الغضبان ذهب غضبه','13297','1363','0');
INSERT INTO [t18144] VALUES (1392,'باب بيان حرمة وجه الإنسان وحرمته، وعقاب من يعذبه أو يؤذيه','13305','1363','0');
INSERT INTO [t18144] VALUES (1393,'باب: [بيان] وجوب أخذ من كان معه سهم بنصله إذا مر به في المسجد، والعلة التي لها أمر، والدليل على أنه يجب على الرجل أن لا يصيب أحدا من المسلمين بشيء يؤذيه بيد، أو غيرها','13319','1363','0');
INSERT INTO [t18144] VALUES (1394,'باب: [بيان] عقوبة من يشير إلى مسلم بحديدة أو سلاح','13332','1363','0');
INSERT INTO [t18144] VALUES (1395,'باب: الترغيب في عزل الأذى عن الطريق، إذا نوى بعزله، أن لا يؤذي الناس، وينوي به الرفق بالمسلمين','13336','1363','0');
INSERT INTO [t18144] VALUES (1396,'باب: التشديد في الإساءة إلى الهر، وفي الكبر والتكبر على الناس وعقوبتهما','13347','1363','0');
INSERT INTO [t18144] VALUES (1397,'باب: عقاب المتألي على الله عز وجل بأنه لا يغفر فلانا، وعقاب من يقول: هلك الناس، والدليل على أنه لا يجوز لأحد أن يقنط الناس من رحمة الله عز وجل، ولا يؤيسهم من رحمته، ويجب أن لا يكون أحد من المسلمين عند نفيسه إلا وهو خير منه وأعلى، حتى لا يحقر أحدا ويزدريه، فلعله به يتحفظ','13354','1363','0');
INSERT INTO [t18144] VALUES (1398,'باب: وجوب تعاهد الجيران والإحسان إليهم وبرهم','13364','1363','0');
INSERT INTO [t18144] VALUES (1399,'باب: الأجر لمن له وصلة إلى ذي سلطان فيشفع لطالب حاجة، أو إلى غير سلطان فيشفع لمن له إليه حاجة، ومثل جليس السوء، ومثل جليس الصالح، والترغيب في مجلسه','13385','1363','0');
INSERT INTO [t18144] VALUES (1400,'باب: ثواب من يعدل بين أولاده في القسمة، والإحسان إلى البنات، وثواب من قدم ثلاثة من الولد، أو اثنين فصبر، وثواب من عال جاريتين','13391','1363','0');
INSERT INTO [t18144] VALUES (1401,'باب: البيان في عوام المسلمين إذا أحبوا الرجل المسلم أنهم يحبونه بحب الله [عز وجل] إياه، وكذلك إذا أبغضوا رجلا وإنه لا يوضع لرجل مسلم القبول في الأرض إلا بعد ما يحبه الله تعالى','13420','1363','0');
INSERT INTO [t18144] VALUES (1402,'باب: ذكر الخبر [المبين] أن الأرواح تأتلف والدليل على أن المرء لا يحب إلا شكله، وينفر قلب المؤمن عن من لا يشاكله، وأن المرء مع من أحب','13430','1363','0');
INSERT INTO [t18144] VALUES (1403,'باب: [بيان] صفة قبول عمل المؤمن في الخير','13467','1363','0');
INSERT INTO [t18144] VALUES (1404,'مبتدأ كتاب القدر','13473','0','0');
INSERT INTO [t18144] VALUES (1405,'باب: إثبات كتاب رزق الإنسان، وأجله، وعمله، وشقاوته إن كان عند الله شقيا، وسعادته إن كان في علمه تعالى سعيدا عند نفخ الروح فيه، وأنه ليس إلى العبد من الاستطاعة شيء في الخير والشر وأن الله يقدرهما عليه','13474','1404','0');
INSERT INTO [t18144] VALUES (1406,'باب: إثبات المقادير وكتابها على الإنسان من الخير والشر وأنه إن جهد أن يتقي الشر فلا يعمله لم يقدر على اجتنابه وكذلك الخير، إلا أن ييسر له ويوفق لة لك، وأن الله يلهمه الخير والشر','13515','1404','0');
INSERT INTO [t18144] VALUES (1407,'باب: البيان في أن الرجل إذا كان من أهل النار لا ينفعه عمله، وإن كان عمله في الظاهر عمل أهل الجنة عامة عمره، وكذلك إذا كان من أهل الجنة لا يضره عمله وإن كان عمله في الظاهر عمل أهل النار عامة عمره. وإن العمل بخواتيمه، والدليل على إبطال قول من ينزل واحدا منهما جنة أو نارا','13528','1404','0');
INSERT INTO [t18144] VALUES (1408,'باب: [البيان] في محاجة آدم وموسى عليهما السلام، وانقياد موسى [عليه السلام] لآدم عند احتجاجه عليه وإن الله قدر عليه ذنبه قبل خلقه','13535','1404','0');
INSERT INTO [t18144] VALUES (1409,'باب: وقت كتابة المقادير، وأن كل شيء من خلق وما فيهم وما يكون منهم بقدر، والدليل على أن الأوهام وما يقع في القلب لا يكون إلا بإذن الله عز وجل','13565','1404','0');
INSERT INTO [t18144] VALUES (1410,'باب: [بيان] التقدير على ابن آدم مما يرتكب من الذنوب وكتابتها عليه، وخط جوار حرمتها، والدليل على أنه مأخوذ بالتمني إذا تمنى ما هو حرام عليه، وأن فعل اللسان نطقه','13575','1404','0');
INSERT INTO [t18144] VALUES (1411,'باب: الأخبار المبينة أن كل مولود يولد على فطرة الإسلام ودين القيم، والدليل على أنهم برآء من الذنوب حتى يدركوا، فيحكم لهم بما تعبر عليهم ألسنتهم','13582','1404','0');
INSERT INTO [t18144] VALUES (1412,'باب: [ذكر] الخبر الدال على أن المولود يحكم له بحكم أبويه','13594','1404','0');
INSERT INTO [t18144] VALUES (1413,'باب: الخبر الدال على أن المولود يولد على الخلقة التي قدره الله عليه في سابق علمه، وإن كان في القدر المقدور سعيد، لا يضره تهويد أبويه ولا تنصيرهم إياه، ويختم الله له بما قدر عليه، وأنه إن كان الله قدر عليه في سابق علمه الشقاء، لم ينفعه إسلام أبويه، وختم له بالشقاء','13596','1404','0');
INSERT INTO [t18144] VALUES (1414,'باب: الأخبار الدالة على أن أطفال المشركين لا ينزلوا جنة ولا نارا، وأنهم في علم الله عز وجل على ما خلقهم له وقدره عليهم من الشقاوة والسعادة','13601','1404','0');
INSERT INTO [t18144] VALUES (1415,'باب: الخبر الناهي عن إنزال أطفال المسلمين جنة ولا نارا، وأن الله عز وجل خلق للجنة أهلها، وللنار أهلها وهم في أصلاب آباءهم','13612','1404','0');
INSERT INTO [t18144] VALUES (1416,'باب: ذكر الأخبار المبينة أن الأجل لا يقدم ولا يؤخر، وأن الأرزاق مقسومة لا يوصل إليها بقوة ولا حيلة، وأن الآثار يبلغها العباد أحبوا أو كرهوا، لا يعجل شيء منها ولا يؤخر، وأنه لا يكون للمسخ نسل','13620','1404','0');
INSERT INTO [t18144] VALUES (1417,'مبتدأ كتاب العلم وذهابه','13625','0','0');
INSERT INTO [t18144] VALUES (1418,'باب: بيان النهي عن اتباع ما تشابه من القرآن واجتناب من اتبع ما تشابه منه، أو احتج به، أو فسره، ومن وفق للرساخة في العلم هو المتبع أحكام الله التي لم تنسخ المحتاج إليها في أمر دينه المستسلم لها، ولا يقول عندها لم، وكيف، ويكل ما يشتبه عليه إلى الله، ويقف عنده ولا يتمادى فيه، ولا يضرب بعضها ببعض، ولا يقيس عندها، ويقر بأن ناسخه ومنسوخه، والخير والشر كله من الله عز وجل، والدليل على أن أحكام القرآن كلها على الظاهر، وأنه لا يجوز لأحد أن يحكم بشيء منها أو يفسر بخلاف الظاهر إلا بخبر فيه عن رسول الله -صلى الله عليه وسلم-، أو ما يجب التسليم له من قول أصحاب النبي -صلى الله عليه وسلم- وإجماعهم عليه','13626','1417','0');
INSERT INTO [t18144] VALUES (1419,'باب: الخبر الدال على حظر الخصومة والمراء في القرآن بغير علم، والقول فيه باختلاف وتفسيره، ووجوب الوقوف عنده وترك المراء فيه، وإباحة القول فيه بالشيء المجمع عليه','13630','1417','0');
INSERT INTO [t18144] VALUES (1420,'باب: الخبر الدال على ذهاب العلماء (......) سنة رسول الله -صلى الله عليه وسلم-: أن من قال بقول قد قيل بخلاف السنة تقليدا ضل وهلك، وبيان الزجر عن التنطع في الدين، وعلى الأئمة، وبيان عقوبته','13640','1417','0');
INSERT INTO [t18144] VALUES (1421,'باب: بيان رفع العلم، وظهور الجهل، وصفة أيامها، والدليل على ذهاب العلم والعلماء والعباد، وترد العمل في الفتن، وعلى أنه ينزع الفهم من أهل العلم في الفتن، ويكثر المال','13645','1417','0');
INSERT INTO [t18144] VALUES (1422,'باب: إباحة تسمية المفتي جاهلا إذا أفتى بغير علم، وأنه ضال مضل، واتخاذهم الناس رؤوسا عند عدم العلماء، والدليل على أن العلم هو الفقه والفهم لا كتابته، وأن الهدى بالفهم','13664','1417','0');
INSERT INTO [t18144] VALUES (1423,'باب: ثواب العالم الداعي إلى الهدى، وعقاب المبتدع إلى ضلالته وبدعته','13698','1417','0');
INSERT INTO [t18144] VALUES (1424,'باب: بيان السنة في الجلوس للناس في الفتيا، والمسائل في المسجد، والتوقيت في الجلوس لهم وتعليمهم','13701','1417','0');
INSERT INTO [t18144] VALUES (1425,'مبتدأ كتاب الدعوات','13703','0','0');
INSERT INTO [t18144] VALUES (1426,'باب: ثواب ذكر الله عز وجل في السر والعلانية، والدليل على أن من أيقن بالإجابة عند الدعاء والذكر لم يخيبه الله، وأن من يذكر الله عند مباشرة الأمور ينصر ويغلب','13704','1425','0');
INSERT INTO [t18144] VALUES (1427,'باب: بيان ثواب من حفظ أسماء الله عز وجل وأحصاها','13715','1425','0');
INSERT INTO [t18144] VALUES (1428,'باب: وجوب العزم في الدعاء والنهي عن قول الداعي في دعائه: إن شئت فأعطني','13723','1425','0');
INSERT INTO [t18144] VALUES (1429,'باب: بيان النهي عن الدعاء بالموت، والعلة التي لها نهي عنه، والإباحة للداعي أن يقول: اللهم توفني إذا كانت الوفاة خيرا لي','13728','1425','0');
INSERT INTO [t18144] VALUES (1430,'باب: بيان الأخبار الدالة على إباحة الدعاء إن كتب الله على العبد لقاءه، ويحببه إليه، والترغيب في التقرب إلى الله عز وجل في الدعاء، وبيان سعة رحمة الله','13743','1425','0');
INSERT INTO [t18144] VALUES (1431,'باب: الترغيب في دعاء المرء {ربنا آتنا في الدنيا حسنة وفي الآخرة حسنة وقنا عذاب النار}','13761','1425','0');
INSERT INTO [t18144] VALUES (1432,'باب: ثواب مجالس الذكر، وثواب من يجلس إلى أهلها وليس هو منهم، والترغيب في سؤال الجنة، والإجارة من النار','13766','1425','0');
INSERT INTO [t18144] VALUES (1433,'باب: ثواب من قال: لا إله إلا الله، وحده لا شريك له، له الملك وله الحمد وهو على كل شيء قدير، مائة مرة، ومن قالها عشر مرات، ومن قال: سبحان الله وبحمده في يوم مائة مرة','13770','1425','0');
INSERT INTO [t18144] VALUES (1434,'باب: بيان فضيلة "سبحان الله وبحمده، سبحان الله العظيم" و"سبحان الله، والحمد لله، ولا إله إلا الله، والله أكبر" وبيان المختار من الدعاء من تعليم رسول الله -صلى الله عليه وسلم-، وثواب التسبيح','13779','1425','0');
INSERT INTO [t18144] VALUES (1435,'باب: بيان الدعاء الذي كان يدعو به النبي -صلى الله عليه وسلم- عند الكرب','13801','1425','0');
INSERT INTO [t18144] VALUES (1436,'باب: الدعاء الذي يجب على من يسلم أن يدعو به','13805','1425','0');
INSERT INTO [t18144] VALUES (1437,'باب الترغيب في الاجتماع في المسجد لذكر الله عز وجل ودرس كتاب الله تعالى','13808','1425','0');
INSERT INTO [t18144] VALUES (1438,'باب فضلية لا حول ولا قوة إلا بالله، وثواب (قايل) والدليل على كراهية رفع الصوت بالدعاء، والترغيب في المخافتة','13822','1425','0');
