INSERT INTO [t18260] VALUES (1,'باب ذكر السنن التي خلافها البدع وذكر الاقتداء والإتباع وشيء من فضل الصحابة ومجانبة أهل البدع','1','0','0');
INSERT INTO [t18260] VALUES (2,'باب مبعث النبي صلى الله عليه وسلم وأيامه وعمره ونسبه','22','0','0');
INSERT INTO [t18260] VALUES (3,'باب في فضل المدينة وذكر القبر والمنبر والمسجد والكعبة، وذكر صدقات النبي صلى الله عليه وسلم وذكر إجلاء اليهود','34','0','0');
INSERT INTO [t18260] VALUES (4,'باب في العلم وهدي العلماء وآدابهم [وذكر الفتيا]','41','0','0');
INSERT INTO [t18260] VALUES (5,'باب في الفتن وفساد الزمان وذكر الأمر بالمعروف والنهي عن المنكر وذكر بعض من امتحن في ذلك وتحليل الظالم، وفي الرجل يطلب العمالة','49','0','0');
INSERT INTO [t18260] VALUES (6,'باب في الدعاء وذكر الله وقراءة القرآن والقراءة بالألحان والقصص والذكر في المساجد والمصاحف ورطانة العجم والسمر بعد العشاء','55','0','0');
INSERT INTO [t18260] VALUES (7,'باب في الصمت والعزلة والتواضع والقصد والحياء وحسن الخلق، وذكر في العبادة وشيء من مواعظ وحكم','65','0','0');
INSERT INTO [t18260] VALUES (8,'باب في التجمل وذكر العجب والرياء والكبر والكذب والغيبة سوء الظن','71','0','0');
INSERT INTO [t18260] VALUES (9,'باب في الورع والمكاسب وطلب الرزق وإصلاح المال، وذكر الصدقة والتعفف عن المسألة وقبول الهدية والإرفاق','77','0','0');
INSERT INTO [t18260] VALUES (10,'باب في رد السلام وما يخرج من الهجرة، والسلام على أهل الذمة وذكر الإخوان في الله [عز وجل] وذكر المكاتبة والاستئذان والمناجاة وتقبيل اليد والمبالغة في البر للزوج والقريب وذي السن أو العلم وبر الوالدين وتشميت العاطس','87','0','0');
INSERT INTO [t18260] VALUES (11,'باب في الفطرة وقص الشارب وحلق العانة والختان ونحوه وذكر السواك والكحل وصبغ الشعر ووصله وذكر الحناء والحجامة ودخول الحمام','97','0','0');
INSERT INTO [t18260] VALUES (12,'باب في ستر العورة وما ينبغي من الستر للنساء والرجال والخلطة في المواكلة والمنام والخلوة بين ذوي المحارم وغيرهم، وسفر المرأة مع غير ذي محرم','106','0','0');
INSERT INTO [t18260] VALUES (13,'باب في الطعام والشراب وغسل اليد والأكل بالشمال، وشرب القائم، وغير ذلك من ذكر الطعام والشراب وإتيان الدعوة والضيافة، وذكر ضيافة أهل الذمة، وذكر جلد الميتة وعظامها','113','0','0');
INSERT INTO [t18260] VALUES (14,'باب في اللباس وذكر الحرير والخز والمصبغات وثياب الصوف وسدل الإزار واشتمال الصماء، وذكر الخاتم والحلي وآنية الذهب والفضة والانتعال، وذكر الصور والتماثيل، وذكر شكل أهل الذمة','120','0','0');
INSERT INTO [t18260] VALUES (15,'باب في الطب والاكتواء والتعالج والرقي والتعوذ وذكر التمائم والطيرة وذكر العين والطاعون وعلاج الجان وذكر النجوم','131','0','0');
INSERT INTO [t18260] VALUES (16,'باب في اتخاذ الكلاب وتعليق الحرز والجرس على الدواب، وفي وسم الدواب، وذكر الخصي والفحلة، وذكر الحيات والذر والنمل ونحوه','140','0','0');
INSERT INTO [t18260] VALUES (17,'باب في الرفق بالمملوك والبهيمة، وذكر في النساء، وفي حفظ الجار، واليتيم، واحتساب المصيبة، وذكر في البنات، وذكر البضع','145','0','0');
INSERT INTO [t18260] VALUES (18,'باب في السفر وسفر المرأة وركوب البحر والتجارة إلى أرض العدو','151','0','0');
INSERT INTO [t18260] VALUES (19,'باب في الأسماء والكني والأنساب، وذكر الرؤيا','154','0','0');
INSERT INTO [t18260] VALUES (20,'باب في ذكر الشعر والغناء واللهو والنرد والشطرنج، وذكر السبق والرمي','157','0','0');
INSERT INTO [t18260] VALUES (21,'باب في الهجرة والمغازي والتاريخ','161','0','0');
