INSERT INTO [t184] VALUES (37,'الفصل العاشر: في أن التأويل شر من التعطيل فإنه يتضمن التشبيه والتعطيل والتلاعب بالنصوص وإساءة الظن بها','187','0','0');
INSERT INTO [t184] VALUES (38,'كشف عورات هؤلاء وبيان فضائحهم وفساد قواعدهم من أفضل الجهاد في سبيل الله','190','37','0');
INSERT INTO [t184] VALUES (39,'الفصل الحادي عشر: في أن قصد المتكلم من المخاطب حمل كلامه على خلاف ظاهره وحقيقته ينافي قصد البيان والإرشاد والهدى، وأن القصدين يتنافيان، وأن تركه بدون ذلك الخطاب خير له وأقرب إلى الهدى','197','0','0');
INSERT INTO [t184] VALUES (40,'إن كان الحق فيما يقوله هؤلاء النفاة لزم من ذلك لوازم باطلة','199','39','0');
INSERT INTO [t184] VALUES (41,'الفصل الثاني عشر: في بيان أنه مع كمال علم المتكلم وفصاحته وبيانه ونصحه يمتنع عليه أن يريد بكلامه خلاف ظاهره وحقيقته وعدم البيان في أهم الأمور وما تشتد الحاجة إلى بيانه','205','0','0');
