INSERT INTO [t184] VALUES (47,'التأويل إذا سلط على أصول الإيمان والإسلام اجتثها وقلعها','235','44','0');
INSERT INTO [t184] VALUES (48,'خروج آدم من الجنة كان بسبب التأويل','241','44','0');
INSERT INTO [t184] VALUES (49,'من جنايات التأويل ما وقع في الإسلام من الحوادث بعد موت رسول الله - صلى الله عليه وسلم - وإلى يومنا هذا','244','44','0');
INSERT INTO [t184] VALUES (50,'الفصل السادس عشر: في بيان ما يقبل التأويل من الكلام وما لا يقبله','247','0','0');
INSERT INTO [t184] VALUES (51,'الفصل السابع عشر: في أن التأويل يفسد العلوم كلها إن سلط عليها ويرفع الثقة بالكلام ولا يمكن أمة من الأمم أن تعيش عليه','258','0','0');
INSERT INTO [t184] VALUES (52,'فصل: في بيان أنه إن سلط على آيات التوحيد القولي العلمي وأخباره لزم تسليطه على آيات التوحيد العملي وأخباره وفسد التوحيد معرفة وقصدا','260','51','0');
