INSERT INTO [t184] VALUES (58,'الصنف الخامس: أصحاب سواء السبيل','277','53','0');
INSERT INTO [t184] VALUES (59,'الفصل التاسع عشر: في الأسباب التي تسهل على النفوس الجاهلة قبول التأويل مع مخالفته للبيان الذي علمه الله الإنسان وفطره على قبوله','285','0','0');
INSERT INTO [t184] VALUES (60,'السبب الأول: أن يأتي به صاحبه مموها مزخرف الألفاظ ملفق المعاني، مكسوا حلة الفصاحة والعبارة الرشيقة','285','59','0');
INSERT INTO [t184] VALUES (61,'السبب الثاني: أن يخرج المعنى الذي يريد إبطاله بالتأويل في صورة مستهجنة، تنفر عنها القلوب، وتنبو عنها الأسماع','287','59','0');
INSERT INTO [t184] VALUES (62,'السبب الثالث: أن يعزو المتأول تأويله وبدعته إلى جليل القدر نبيه الذكر من العقلاء، أو من آل البيت النبوي','289','59','0');
INSERT INTO [t184] VALUES (63,'السبب الرابع: أن يكون ذلك التأويل قد قبله ورضيه مبرز في صناعة من الصناعات أو علم من العلوم','291','59','0');
