INSERT INTO [t184] VALUES (68,'الفصل الحادي والعشرون: في الأسباب الجالبة للتأويل','330','0','0');
INSERT INTO [t184] VALUES (69,'الفصل الثاني والعشرون: في أنواع الاختلاف الناشئة عن التأويل وانقسام الاختلاف إلى محمود ومذموم','339','0','0');
INSERT INTO [t184] VALUES (70,'الاختلاف في كتاب الله نوعان: أحدهما: أن يكون المختلفون كلهم مذمومين','339','69','0');
INSERT INTO [t184] VALUES (71,'والنوع الثاني: اختلاف ينقسم أهله إلى محمود ومذموم','339','69','0');
INSERT INTO [t184] VALUES (72,'وقوع الاختلاف بين الناس أمر ضروري لا بد منه؛ لتفاوت إراداتهم وأفهامهم وقوى إدراكهم','343','69','0');
INSERT INTO [t184] VALUES (73,'الفصل الثالث والعشرون: في أسباب الخلاف الواقع بين الأئمة بعد اتفاقهم على أصل واحد وتحاكمهم إليه وهو كتاب الله وسنة رسوله','344','0','0');
INSERT INTO [t184] VALUES (74,'فصل من كلام أبي محمد بن حزم وهو من أحسن كلامه','344','73','0');
