INSERT INTO [t184] VALUES (76,'أسباب الاختلاف','355','73','0');
INSERT INTO [t184] VALUES (77,'السبب الأول: ألا يكون الحديث قد بلغه','355','76','0');
INSERT INTO [t184] VALUES (78,'السبب الثاني: أن يكون الحديث قد بلغه لكنه لم يثبت عنده','362','76','0');
INSERT INTO [t184] VALUES (79,'السبب الثالث: اعتقاد ضعف الحديث باجتهاد قد خالفه فيه غيره','363','76','0');
INSERT INTO [t184] VALUES (80,'السبب الرابع: اشتراط بعضهم في خبر الواحد العدل شروطا يخالفه فيها غيره','365','76','0');
INSERT INTO [t184] VALUES (81,'السبب الخامس: أن ينسى الحديث أو الآية','365','76','0');
INSERT INTO [t184] VALUES (82,'السبب السادس: عدم معرفته بدلالة الحديث','366','76','0');
INSERT INTO [t184] VALUES (83,'السبب السابع: أن يكون عارفا بدلالة اللفظ وموضوعه ولكن لا يتفطن لدخول هذا الفرد المعين تحت اللفظ','374','76','0');
INSERT INTO [t184] VALUES (84,'السبب الثامن: اعتقاده أن لا دلالة في اللفظ على الحكم المتنازع فيه','375','76','0');
INSERT INTO [t184] VALUES (85,'السبب التاسع: اعتقاده أن تلك الدلالة قد عارضها ما هو مساو لها فيجب التوقف، أو ما هو أقوى منها فيجب تقديمه','377','76','0');
INSERT INTO [t184] VALUES (86,'ولا خلاف بين الأئمة أنه إذا صح الحديث عن رسول الله - صلى الله عليه وسلم - لم يكن عدم العلم بالقائل به مسوغا لمخالفته','379','73','0');
INSERT INTO [t184] VALUES (87,'كثير من أعيان العلماء قد صاروا إلى أقوال متمسكهم فيها عدم العلم بالمخالف، مع قيام الأدلة الظاهرة على خلاف تلك الأقوال','380','73','0');
INSERT INTO [t184] VALUES (88,'من المسائل التي نقض ادعاء الإجماع فيها','382','73','0');
INSERT INTO [t184] VALUES (89,'قول مالك: لا أعلم أحدا أجاز شهادة العبد','382','73','0');
INSERT INTO [t184] VALUES (90,'قول: لا أعلم أحدا أوجب الصلاة على النبي - صلى الله عليه وسلم - في الصلاة','383','73','0');
INSERT INTO [t184] VALUES (91,'قول الشافعي: أجمعوا على أن المعتق بعضه لا يرث','383','73','0');
INSERT INTO [t184] VALUES (92,'والقول بحديث: «أنت ومالك لأبيك».','383','73','0');
INSERT INTO [t184] VALUES (93,'قول الثوري: إذا طلق المدخول بها ثم راجعها ثم طلقها قبل دخول ثان بعد الرجعة، أنها تستأنف العدة، أجمع الفقهاء على هذا','385','73','0');
INSERT INTO [t184] VALUES (94,'الليث بن سعد حكى الإجماع على أن المسافر لا يقصر الصلاة في أقل من يومين','386','73','0');
INSERT INTO [t184] VALUES (95,'ذكر مالك: إن نكل عن اليمين حلف صاحب الحق إن حقه لحق، وثبت حقه على صاحبه','386','73','0');
INSERT INTO [t184] VALUES (96,'أبو عبيد يحكي الإجماع على خلاف قول مالك في القسامة','387','73','0');
INSERT INTO [t184] VALUES (97,'أنكروا عليه قوله: الأمر المجمع عليه عندنا أن يبدأ المدعون بالأيمان في القسامة','389','73','0');
INSERT INTO [t184] VALUES (98,'قول الشافعي: ودل إجماعهم على أن من حلق في الإحرام عمدا أو خطأ أو قتل صيدا عمدا أو خطأ في الكفارة سواء، وعلى أن الحالف بالله وقاتل المؤمن عمدا أو خطأ في الكفارة سواء','390','73','0');
INSERT INTO [t184] VALUES (99,'قول ابن المنذر: أجمع كل من يحفظ عنه من أهل العلم على أن الرجل إذا قال لامرأته: أنت طالق ثلاثا إن دخلت الدار. فطلقها ثلاثا ثم تزوجت بعد ما انقضت عدتها، ثم نكحها الحالف الأول، ثم دخلت الدار أنه لا يقع عليها الطلاق؛ لأن طلاق الملك قد انقضى','391','73','0');
INSERT INTO [t184] VALUES (100,'قول ابن المنذر: أجمع كل من يحفظ عنه من أهل العلم على أن الرجل إذا قال لامرأته: أنت طالق ثلاثا إن شئت. فقالت: قد شئت إن شاء فلان. أنها قد ردت الأمر، ولا يلزمه الطلاق إن شاء فلان','392','73','0');
INSERT INTO [t184] VALUES (101,'قول ابن المنذر: أجمع كل من يحفظ عنه من أهل العلم على أن الرجل إذا قال لامرأته: أنت طالق ثلاثا إلا اثنتين. أنها تطلق واحدة، وإن قال: أنت طالق ثلاثا إلا واحدة. أنها تطلق ثنتين، فإن قال: أنت طالق ثلاثا إلا ثلاثا. طلقت ثلاثا','393','73','0');
INSERT INTO [t184] VALUES (102,'نقل ابن عبد البر الإجماع على أن الاعتكاف يلزم بالشروع','394','73','0');
INSERT INTO [t184] VALUES (103,'حكى صالح بن أحمد عن أبيه أنه قال: لا اختلاف أنه لا يرث المسلم الكافر، ولا الكافر المسلم','394','73','0');
INSERT INTO [t184] VALUES (104,'قول ابن عبد البر: وأما القراءة في الركوع والسجود فجميع العلماء على أن ذلك لا يجوز','394','73','0');
INSERT INTO [t184] VALUES (105,'قول إبراهيم بن مسلم الخوارزمي عن الوضوء من مس الذكر: وهذا منسوخ','395','73','0');
INSERT INTO [t184] VALUES (106,'قول ابن عبد البر: وأما الشهادة على رؤية الهلال فأجمع الفقهاء على أنه لا يقبل في شهادة شوال في الفطر إلا شهادة رجلين عدلين','395','73','0');
INSERT INTO [t184] VALUES (107,'وقول أبي ثور: لا يختلفون أن أقل الطهر خمسة عشر يوما','396','73','0');
INSERT INTO [t184] VALUES (108,'ومن ذلك: أن الحالف بالطلاق والعتاق إذا حنث في يمينه أنه يطلق عليه زوجته، ويعتق عليه عبده أو جاريته','396','73','0');
INSERT INTO [t184] VALUES (109,'وقال ابن المنذر: لم يسبق الشافعي إلى نجاسة الأبوال أحد','405','73','0');
INSERT INTO [t184] VALUES (110,'وقال ابن تيمية: «لم يسبق أحمد بن حنبل إلى الحكم بإسلام أولاد أهل الذمة الصغار بموت آبائهم أحد. ولم يسبقه إلى إقعاد المرأة أول ما ترى الدم يوما وليلة ثم تصلي وهي ترى الدم أحد.','405','73','0');
INSERT INTO [t184] VALUES (111,'غاية هذه الإجماعات أن تفيدنا عدم علم ناقلها بالخلاف، وهذا بمجرده لا يكون عذرا للمجتهد في ترك موجب الدليل','406','73','0');
INSERT INTO [t184] VALUES (112,'ومن ذلك: نقل من نقل الإجماع على أن المتكلم بالطلاق الثلاث في مرة واحدة يقع به الثلاث، وقال بموجب علمه وما بلغه، وإلا فالخلاف في هذه المسألة ثابت من وجوه','406','73','0');
INSERT INTO [t184] VALUES (113,'ومن ذلك: حكاية من حكى الإجماع على وقوع الطلاق في الحيض بحسب ما بلغه، والمسألة مسألة نزاع','413','73','0');
INSERT INTO [t184] VALUES (114,'الفصل الرابع والعشرون: في ذكر الطواغيت الأربع التي هدم بها أصحاب التأويل الباطل معاقل الدين وانتهكوا بها حرمة القرآن ومحوا بها رسوم الإيمان','416','0','0');
INSERT INTO [t184] VALUES (115,'الطاغوت الأول: قولهم: نصوص الوحي أدلة لفظية، وهي لا تفيد اليقين.','417','114','0');
INSERT INTO [t184] VALUES (116,'قال متكلمهم: الدليل اللفظي لا يفيد اليقين إلا عند تيقن أمور عشرة','417','114','0');
INSERT INTO [t184] VALUES (117,'جواب شيخ الإسلام عن هذا من وجوه','417','114','0');
INSERT INTO [t184] VALUES (118,'أحدها: أنا لا نسلم أنه موقوف على هذه المقدمات العشرة','417','117','0');
INSERT INTO [t184] VALUES (119,'الثاني: أن يقال: من المعلوم أن دلالة الأدلة اللفظية لا تختص بالقرآن والسنة؛ بل جميع بني آدم يدل بعضهم بعضا بالأدلة اللفظية.','423','117','0');
INSERT INTO [t184] VALUES (120,'وهذه الطريق يستدل بها من وجوه','423','114','0');
INSERT INTO [t184] VALUES (121,'أحدها: أن هذا المقصود ضروري في حياة بني آدم، فلا بد من وجوده','423','120','0');
INSERT INTO [t184] VALUES (122,'الثاني: جميع الأمم يعرف بعضهم مراد بعض بلفظه ويقطع به ويتيقنه','423','120','0');
INSERT INTO [t184] VALUES (123,'الثالث: معرفة الناس مراد المتكلم منهم بكلامه أعظم من معرفتهم عامة العلوم العقلية','423','120','0');
INSERT INTO [t184] VALUES (124,'الرابع: أن الطفل أول ما يميز يعرف مراد من يربيه بلفظه قبل أن يعرفه شيئا من العلوم الضرورية','424','120','0');
INSERT INTO [t184] VALUES (125,'الخامس: أن كل إنسان يدل غيره بالأدلة اللفظية على ما يعرفه، ويعرف مراد غيره بالأدلة اللفظية','424','120','0');
INSERT INTO [t184] VALUES (126,'السادس: أن التعريف بالأدلة اللفظية أصل للتعريف بالأدلة العقلية','424','120','0');
INSERT INTO [t184] VALUES (127,'السابع: أن الإنسان في فهمه وإفهامه للدليل العقلي محتاج إلى معرفة مراد المخبر به الذاكر له لمن يخاطبه','424','120','0');
INSERT INTO [t184] VALUES (128,'الثامن: أن تعليم الأدلة اللفظية يحسنه كل أحد، وأما تعليم الدلالة العقلية فلا يحسنه كل أحد','425','120','0');
INSERT INTO [t184] VALUES (129,'التاسع: أن الله سبحانه هدى البهائم والطير أن يعرف بعضها بعضا مرادها بأصواتها','425','120','0');
INSERT INTO [t184] VALUES (130,'العاشر: أن أبلد الناس يعلم مراد أكثر من يخاطبه بالكلام الركيك العادم للبلاغة والفصاحة، فكيف لا يعلم أذكى الناس','425','120','0');
INSERT INTO [t184] VALUES (131,'الحادي عشر: أن هذا يستلزم الطعن والقدح في بيان المتكلم وفصاحته، أو في فهم السامع وذهنه، أو فيهما معا','425','120','0');
INSERT INTO [t184] VALUES (132,'الثاني عشر: أنه إذا كان التفاهم والعلم بمراد الحيوان من غيره حاصلا للحيوانات، فما الظن بالإنسان، فما الظن بالعقلاء','426','120','0');
INSERT INTO [t184] VALUES (133,'الثالث عشر: أنا نعلم بالضرورة أن شيوخنا الذين كانوا يخاطبوننا كانوا يعرفونا مرادهم بألفاظهم، وقد عرفنا مرادهم يقينا','426','120','0');
INSERT INTO [t184] VALUES (134,'الرابع عشر: أن دلالة الأدلة اللفظية على مراد المتكلم أقوى من دلالة الأدلة العقلية على الحقائق الثابتة','427','120','0');
INSERT INTO [t184] VALUES (135,'الخامس عشر: أن دلالة قول الرسول على مراده أكمل من دلالة شبهات هؤلاء العقلية على معارضته بما لا نسبة بينهما','427','120','0');
INSERT INTO [t184] VALUES (136,'السادس عشر: أنك إذا تأملت العقليات التي زعموا أنها تفيد اليقين وقدموها على كلام الله ورسوله وجدتها مخالفة لصريح المعقول','427','120','0');
INSERT INTO [t184] VALUES (137,'السابع عشر: أن هذا من أنواع السفسطة، بل هو شر أنواعها','429','120','0');
INSERT INTO [t184] VALUES (138,'الثامن عشر: أن قول القائل: الأدلة اللفظية لا تفيد اليقين؛ إما أن يريد به نفي العموم، أو عموم النفي','430','120','0');
INSERT INTO [t184] VALUES (139,'التاسع عشر: أنا نعلم بالاضطرار أن مصنفي العلوم على اختلاف أنواعها علم الناس مرادهم من ألفاظهم علما يقينا','430','120','0');
INSERT INTO [t184] VALUES (140,'العشرون: أنه من المعلوم أن الصحابة سمعوا القرآن والسنة من النبي - صلى الله عليه وسلم - وقرؤوه، وأقرؤوه من بعدهم','432','120','0');
INSERT INTO [t184] VALUES (141,'الحادي والعشرون: أن كل صنف من العلماء تكفلوا بعلم من العلوم المنقولة عن الرسول، متفقون على أكثر علمهم مسائله ودلائله','433','120','0');
INSERT INTO [t184] VALUES (142,'الثاني والعشرون: أن المخاطبين أولا بالقرآن والسنة لم يتوقف حصول اليقين لهم بمراده على تلك المقدمات العشرة التي ذكروها','434','120','0');
INSERT INTO [t184] VALUES (143,'الثالث والعشرون: أن جميع ما ذكروه من الوجوه العشرة يرجع إلى حرف واحد، وهو احتمال اللفظ لمعنى آخر غير ما يظهر من الكلام','436','120','0');
INSERT INTO [t184] VALUES (144,'الرابع والعشرون: أن قول القائل: «الدليل اللفظي لا يفيد اليقين إلا عند تيقن أمور عشرة» نفي عام، وقضية سالبة كلية','438','120','0');
INSERT INTO [t184] VALUES (145,'الخامس والعشرون: أن الذين لم يحصل لهم اليقين بالأدلة العقلية أضعاف أضعاف الذين حصل لهم اليقين بالأدلة السمعية','441','120','0');
INSERT INTO [t184] VALUES (146,'السادس والعشرون: أن ألفاظ القرآن والسنة ثلاثة أقسام','447','120','0');
INSERT INTO [t184] VALUES (147,'السابع والعشرون: أن الذي حال بين هؤلاء وبين استفادتهم اليقين من كلام الله ورسوله أن كثيرا من ألفاظ القرآن والسنة قد صار لها معان، اصطلح عليها النظار والمتكلمون وغيرهم','448','120','0');
INSERT INTO [t184] VALUES (148,'الثامن والعشرون: أن هؤلاء إما أن يريدوا به نفي اليقين في باب الأسماء والصفات فقط دون باب المعاد والأمر والنهي، أو في باب الصفات وباب المعاد فقط دون الأمر، أو في الجميع','451','120','0');
INSERT INTO [t184] VALUES (149,'التاسع والعشرون: أن دعوى المدعي أن كلام الله ورسوله لا يستفاد منه يقين ولا علم، إما أن يدعيه حيث لا يعارض العقل السمع بل يوافقه، أو حيث يعارضه في زعمه، أو حيث لا يعارضه ولا يوافقه','453','120','0');
INSERT INTO [t184] VALUES (150,'الثلاثون: أن قول القائل: «الأدلة اللفظية موقوفة على هذه المقدمات»؛ أتريد به أن كل دليل منها يقف على مجموع الأمور العشرة، أم تريد به أن جنسها يقف على جنس هذه العشرة؟!','454','120','0');
INSERT INTO [t184] VALUES (151,'الحادي والثلاثون: أن حكمك بتوقف دلالة الدليل على معرفة الإعراب والتصريف خطأ ظاهر','454','120','0');
INSERT INTO [t184] VALUES (152,'الثاني والثلاثون: قولك: «إن ذلك يتوقف على نفي التخصيص والإضمار»؛ فهذا لا يحتاج إليه في فهم معاني الألفاظ المفردة','455','120','0');
INSERT INTO [t184] VALUES (153,'الثالث والثلاثون: أن القدح في دلالة العام باحتمال الخصوص وفي الحقيقة باحتمال المجاز والنقل والاشتراك وسائر ما ذكر؛ يبطل حجج الله على خلقه بآياته، ويبطل أوامره ونواهيه','457','120','0');
INSERT INTO [t184] VALUES (154,'الرابع والثلاثون: أنك تجد عند كثير من المعروفين بالتفسير من رد كثير من ألفاظ القرآن عن العموم إلى الخصوص نظير ما تجده من ذلك عند أرباب التأويلات المستنكرة','464','120','0');
INSERT INTO [t184] VALUES (155,'وقد يقع في كلام السلف تفسير اللفظ العام بصورة خاصة على وجه التمثيل، لا على تفسير معنى اللفظة في اللغة بذلك','473','154','0');
INSERT INTO [t184] VALUES (156,'الخامس والثلاثون: أن ألفاظ القرآن التي وقعت في باب الحمد والذم وقعت بما فيها من الفخامة والجلالة عامة، وكان عمومها من تفخيمها، وجلالة قدرها، وعظمة شأنها','481','120','0');
INSERT INTO [t184] VALUES (157,'السادس والثلاثون: قوله: «وعدم الإضمار». يقال: الإضمار على ثلاثة أنواع','483','120','0');
INSERT INTO [t184] VALUES (158,'السابع والثلاثون: أن الإضمار هو الإخفاء، فهذا إما أن يجعل له عليه دليلا من الخطاب أو لا','485','120','0');
INSERT INTO [t184] VALUES (159,'الثامن والثلاثون: قوله: «وعدم التقديم والتأخير». فهذا من نمط ما قبله','485','120','0');
INSERT INTO [t184] VALUES (160,'أنواع التقديم والتأخير','486','159','0');
INSERT INTO [t184] VALUES (161,'التاسع والثلاثون: قوله: وموقوف على نفي المعارض العقلي؛ لئلا يفضي إلى القدح في العقل الذي يفتقر إليه النقل. جوابه: أنا لا نسلم أن القدح فيما عارض النقل من المعقول قدح فيما يحتاج إليه النقل','492','120','0');
INSERT INTO [t184] VALUES (162,'الأربعون: أن الأدلة القاطعة قد قامت على صدق الرسول صلوات الله وسلامه عليه في كل ما يخبر به، ودلالتها على صدقه أبين وأظهر من دلالة تلك الشبه العقلية على نقيض ما أخبر به عند كافة العقلاء','497','120','0');
INSERT INTO [t184] VALUES (163,'الحادي والأربعون: أن الرسول بين مراده، وقد تبين لنا أكثر مما تبين لنا كثير من دقائق المعقولات الصحيحة','498','120','0');
INSERT INTO [t184] VALUES (164,'الثاني والأربعون: أن المعارضين بين العقل والنقل قد اعترفوا بأن العلم بانتفاء المعارض مطلقا لا سبيل إليه','499','120','0');
INSERT INTO [t184] VALUES (165,'الثالث والأربعون: أن الله قد أخبر أن ما على الرسول البلاغ المبين','499','120','0');
INSERT INTO [t184] VALUES (166,'الرابع والأربعون: أن عقل رسول الله - صلى الله عليه وسلم - أكمل عقول أهل الأرض','500','120','0');
INSERT INTO [t184] VALUES (167,'الخامس والأربعون: أن الله إنما أقام الحجة على خلقه بكتابه ورسله','501','120','0');
INSERT INTO [t184] VALUES (168,'السادس والأربعون: أن الله وصف نفسه بأنه بين لعباده غاية البيان، وأمر رسوله بالبيان، وأخبر أنه أنزل عليه كتابه ليبين للناس','502','120','0');
INSERT INTO [t184] VALUES (169,'السابع والأربعون: أن القائل بأن الدلالة اللفظية لا تفيد اليقين، إما أن يقول: إنها تفيد ظنا، أو لا تفيد علما ولا ظنا','503','120','0');
INSERT INTO [t184] VALUES (170,'الثامن والأربعون: أن الله أخبر أن قلوب المؤمنين مطمئنة بذكره، وهو كتابه الذي هدى به عباده','504','120','0');
INSERT INTO [t184] VALUES (171,'التاسع والأربعون: قوله: «إن العلم بمدلول الأدلة اللفظية موقوف على نقل اللغة» كلام ظاهر البطلان','506','120','0');
INSERT INTO [t184] VALUES (172,'الخمسون: أن السامع متى سمع المتكلم يقول: لبست ثوبا، وركبت فرسا، وأكلت لحما علم مراده قطعا.','507','120','0');
INSERT INTO [t184] VALUES (173,'الحادي والخمسون: أن معرفة مراد المتكلم تعرف باطراد استعماله ذلك اللفظ في ذلك المعنى في مجاري كلامه ومخاطباته','508','120','0');
INSERT INTO [t184] VALUES (174,'الثاني والخمسون: أن من تأمل عامة ألفاظ القرآن وجدها نصوصا صريحة دالة على معناها دلالة لا تحتمل غيرها بوجه من الوجوه','508','120','0');
INSERT INTO [t184] VALUES (175,'الثالث والخمسون: أن القرآن نقل إعرابه كما نقلت ألفاظه ومعانيه','509','120','0');
INSERT INTO [t184] VALUES (176,'الرابع والخمسون: أن عامة ألفاظ القرآن منقول معناها وإعرابها بالتواتر','510','120','0');
INSERT INTO [t184] VALUES (177,'الخامس والخمسون: أن أصحاب هذا القانون قالوا: إن أظهر الألفاظ لفظ الله، وقد اختلف الناس فيه هل هو مشتق أم لا؟ وهل هو مشتق من التأله أو من الوله، أو من لاه إذا احتجب؟ فإذا كان هذا في أظهر الأسماء فما الظن بغيره؟! فتأمل هذا الوهم والإيهام واللبس والتلبيس','510','120','0');
INSERT INTO [t184] VALUES (178,'السادس والخمسون: هذه الوجوه العشرة مضمونها كلها احتمال اللفظ لمعنيين فصاعدا حتى لا يعرف عين مراد المتكلم.','512','120','0');
INSERT INTO [t184] VALUES (179,'السابع والخمسون: أن غاية ما يقال: إن في القرآن ألفاظا استعملت في معان لم تكن تعرفها العرب، وأسماء مجملة لم يرد ظاهرها، وأسماء مشتركة، فهذه الأسماء لا تفيد اليقين بالمراد منها','514','120','0');
INSERT INTO [t184] VALUES (180,'الثامن والخمسون: أن حصول اليقين بمدلول الأدلة السمعية والعلم بمراد المتكلم بها أيسر وأظهر من حصوله بمدلول الأدلة العقلية','516','120','0');
INSERT INTO [t184] VALUES (181,'التاسع والخمسون: ما اتفق عليه أهل الملل أن النبوة خطاب سمعي بوحي يوحيه الملك إلى النبي عن الرب تعالى','517','120','0');
INSERT INTO [t184] VALUES (182,'الستون: أن دلالة الأدلة السمعية على مدلولها من جنس دلالة الآيات المعينة على مدلولها','519','120','0');
INSERT INTO [t184] VALUES (183,'الحادي والستون: أنه من أعظم المحال أن يكون المصنفون في جميع العلوم قد بينوا مرادهم، وعلم الناس مرادهم منها يقينا ويكون الله ورسوله لم يبين مراده بكلامه','522','120','0');
INSERT INTO [t184] VALUES (184,'الثاني والستون: أن يقال لهم: ما تريدون بهذا النفي؟ أتريدون بالأدلة اللفظية جنس كلام بني آدم الدال على مرادهم في الخطاب والتصنيف وغيره أو كلام الله ورسوله؟','525','120','0');
INSERT INTO [t184] VALUES (185,'الثالث والستون: أن مضمون هذا القانون جحد الرسالة في الحقيقة بل مضمونه أن ترك الناس بلا رسول خير من أن يرسل إليهم رسول','526','120','0');
INSERT INTO [t184] VALUES (186,'الرابع والستون: أن أصحاب هذا القانون في قول مختلف','527','120','0');
INSERT INTO [t184] VALUES (187,'الخامس والستون: أنهم جعلوا المحكم ما يدعونه من العقليات وجعلوا القرآن كله مردودا إليه','528','120','0');
INSERT INTO [t184] VALUES (188,'السادس والستون: أنه على قولهم لا سبيل لأحد أن يعرف أن شيئا من القرآن محكم','529','120','0');
INSERT INTO [t184] VALUES (189,'السابع والستون: أنهم لا يمكنهم إنكار أن الأدلة اللفظية تفيد ظنا غالبا، وإن لم تفدهم يقينا','529','120','0');
INSERT INTO [t184] VALUES (190,'الثامن والستون: أن هذا يتضمن القدح في أعظم آيات الرب الدالة على ربوبيته وحكمته، وجحد ما هو من أعظم نعمه على عباده','530','120','0');
INSERT INTO [t184] VALUES (191,'التاسع والستون: أن هذا القول لم يعرف عن طائفة من طوائف بني آدم، لا طوائف المسلمين ولا أحد من أهل الملل','531','120','0');
INSERT INTO [t184] VALUES (192,'السبعون: أن حاصل كلامهم يدور على ثلاث مقدمات الأولى منها صادقة، والأخريان كاذبتان','533','120','0');
INSERT INTO [t184] VALUES (193,'الحادي والسبعون: أنهم مضطربون في العقل الذي يعارض النقل أشد اضطراب','534','120','0');
INSERT INTO [t184] VALUES (194,'الثاني والسبعون: أن الله دعا إلى تدبر كتابه وتعقله وتفهمه، وذم الذين لا يفهمونه ولا يعقلونه','543','120','0');
INSERT INTO [t184] VALUES (195,'الثالث والسبعون: أن أدلة القرآن والسنة نوعان: أحدهما: يدل بمجرد الخبر. والثاني: يدل بطريق التنبيه والإرشاد على الدليل العقلي.','544','120','0');
INSERT INTO [t184] VALUES (196,'فصل: في الطاغوت الثاني وهو قولهم: إن تعارض العقل والنقل وجب تقديم العقل','547','114','0');
INSERT INTO [t184] VALUES (197,'قد أشفى شيخ الإسلام في هذا الباب بما لا مزيد عليه كسر هذا الطاغوت في كتابه الكبير. ونحن نشير إلى كلمات يسيرة هي قطرة من بحره تتضمن كسره ودحضه، وذلك يظهر من وجوه','547','114','0');
INSERT INTO [t184] VALUES (198,'الأول: أن هذا التقسيم باطل من أصله','547','197','0');
INSERT INTO [t184] VALUES (199,'الثاني: أن قوله: «إذا تعارض العقل والنقل» فإما أن يريد به القطعيين فلا نسلم إمكان التعارض','548','197','0');
INSERT INTO [t184] VALUES (200,'الثالث: أنا لا نسلم انحصار القسمة فيما ذكره من الأقسام الأربعة','549','197','0');
INSERT INTO [t184] VALUES (201,'الرابع: قوله: «إن قدمنا النقل لزم الطعن» فحاصله ممنوع','549','197','0');
INSERT INTO [t184] VALUES (202,'الخامس: أن يقال: العقل إما أن يكون عالما بصدق الرسول وثبوت ما أخبر به في نفس الأمر، وإما ألا يكون عالما بذلك','551','197','0');
INSERT INTO [t184] VALUES (203,'السادس: أن المنهي عنه من قبول هذا الخبر وتصديقه فيه هو عين المحذور، فيكون واقعا في المنهي عنه، سواء أطاع أو عصى','552','197','0');
INSERT INTO [t184] VALUES (204,'السابع: أنه إذا قيل له: لا تصدقه في هذا، كان أمرا له بما يناقض ما علم به صدقه، وكان أمرا له بما يوجب ألا يثق بشيء من خبره','552','197','0');
INSERT INTO [t184] VALUES (205,'الثامن: أنه إذا اعتقد في الدليل السمعي أنه ليس بدليل في نفس الأمر بل اعتقاد دلالته على مخالف ما زعمتوه من العقل جهل؛ أمكن أتباع الرسل المصدقين بما جاؤوا به أن يعتقدوا في أدلتكم العقلية أنها ليست بأدلة في نفس الأمر، وأن اعتقاد دلالتها جهل','555','197','0');
INSERT INTO [t184] VALUES (206,'التاسع: أن يقال: لو قدر تعارض الشرع والعقل لوجب تقديم الشرع؛ لأن العقل قد صدق الشرع، ومن ضرورة تصديقه له قبول خبره','556','197','0');
INSERT INTO [t184] VALUES (207,'العاشر: أن العقل مع الوحي كالعامي المقلد مع المفتي العالم، بل ودون ذلك بمراتب كثيرة لا تحصى','556','197','0');
INSERT INTO [t184] VALUES (208,'الحادي عشر: أن الدليل الدال على صحة الشيء أو ثبوته أو عدالته أو قبول قوله لا يجب أن يكون أصلا له، بحيث إذا قدم قول المشهود له والمدلول عليه على قوله يلزم إبطاله','557','197','0');
INSERT INTO [t184] VALUES (209,'الثاني عشر: أن تقديم العقل على الشرع يتضمن القدح في العقل والشرع؛ لأن العقل قد شهد للوحي بأنه أعلم منه','558','197','0');
INSERT INTO [t184] VALUES (210,'الثالث عشر: أن الشرع مأخوذ عن الله بواسطة الرسولين الملكي والبشري بينه وبين عباده','559','197','0');
INSERT INTO [t184] VALUES (211,'الرابع عشر: أن الأمة اختلفت ضروبا من الاختلاف في الأصول والفروع، فما فزعت طائفة من طوائف الأمة في اختلافها إلى منطق ولا فيلسوف، ولا إلى عقل يخالف صريح النقل','565','197','0');
INSERT INTO [t184] VALUES (212,'الخامس عشر: أن التفاوت الذي بين الرسل وبين أرباب هذه المعقولات أعظم بكثير من التفاوت الذي بين هؤلاء وبين أجهل الناس على الإطلاق','567','197','0');
INSERT INTO [t184] VALUES (213,'السادس عشر: تقديم العقول على الأدلة الشرعية ممتنع متناقض، وأما تقديم الأدلة الشرعية فهو ممكن مؤتلف، فوجب الثاني','568','197','0');
INSERT INTO [t184] VALUES (214,'بعض تناقضات العقول','569','213','0');
INSERT INTO [t184] VALUES (215,'السابع عشر: أن الله سبحانه قد تمم الدين بنبيه - صلى الله عليه وسلم -، وأكمله به، ولم يحوجه ولا أمته بعده إلى عقل ولا نقل سواه','571','197','0');
INSERT INTO [t184] VALUES (216,'الثامن عشر: أن ما علم بصريح العقل الذي لا يختلف فيه العقلاء لا يتصور أن يعارضه الشرع البتة، ولا يأتي بخلافه','573','197','0');
INSERT INTO [t184] VALUES (217,'التاسع عشر: أن المسائل التي يقال إنه قد تعارض فيها العقل والسمع ليست من المسائل المعلومة بصريح العقل','574','197','0');
INSERT INTO [t184] VALUES (218,'افتراءات اليهود والنصارى على الله','575','217','0');
INSERT INTO [t184] VALUES (219,'العشرون: أنه لا يعلم آية من كتاب الله ولا نص صحيح عن رسول الله في باب أصول الدين اجتمعت الأمة على خلافه','576','197','0');
INSERT INTO [t184] VALUES (220,'الحادي والعشرون: أن الأدلة السمعية هي الكتاب والسنة والإجماع','577','197','0');
INSERT INTO [t184] VALUES (221,'الثاني والعشرون: أنه إذا قدر تعارض العقل والكتاب، فرد العقل الذي لم يضمن لنا عصمته إلى الكتاب المعلوم العصمة هو الواجب','578','197','0');
INSERT INTO [t184] VALUES (222,'الثالث والعشرون: أن هؤلاء الخائضين في صفات الرب وأفعاله بآرائهم وعقولهم تراهم مختلفين متنازعين حيارى متهوكين','578','197','0');
INSERT INTO [t184] VALUES (223,'الرابع والعشرون: أن كل من أعرض عن السمع لظنه أن العقل يخالفه تجد بينهم من النزاع والتفرق والشهادة من بعضهم على بعض بالضلالة بحسب إعراضهم عن السمع','579','197','0');
INSERT INTO [t184] VALUES (224,'الخامس والعشرون: أن الله لما أهبط الأبوين من الجنة عهد إليهما عهدا تناولهما وتناول ذريتهما إلى يوم القيامة، وضمن لمن تمسك بعهده أنه لا يضل ولا يشقى، ولمن أعرض عنه الضلال والشقاء','584','197','0');
INSERT INTO [t184] VALUES (225,'السادس والعشرون: أن طالب الهدى في غير القرآن والسنة قد شهد الله ورسوله له بالضلال، فكيف يكون عقله مقدما على كتاب الله وسنة رسوله','585','197','0');
INSERT INTO [t184] VALUES (226,'السابع والعشرون: أن هؤلاء قد شهدوا على أنفسهم بالحيرة والشك فيها، وأنهم لم يجزموا فيها بشيء، ولم يظفروا منها بعلم ولا يقين','586','197','0');
INSERT INTO [t184] VALUES (227,'الثامن والعشرون: أن أصحاب القرآن والإيمان قد شهد الله لهم بالعلم واليقين والهدى','587','197','0');
INSERT INTO [t184] VALUES (228,'التاسع والعشرون: إذا تعارض العقل والنقل وجب تقديم النقل','589','197','0');
INSERT INTO [t184] VALUES (229,'الثلاثون: معارضة العقل لما دل العقل على أنه حق دليل على تناقض دلالته، وذلك يوجب فسادها','590','197','0');
INSERT INTO [t184] VALUES (230,'الحادي والثلاثون: أن الآيات والبراهين اليقينية والأدلة القطعية قد دلت على صدق الرسل','591','197','0');
INSERT INTO [t184] VALUES (231,'الثاني والثلاثون: أن الشبهات القادحة في نبوات الأنبياء ووجود الرب أقوى من الشبه التي يدعي النفاة للصفات أنها معقولات خالفت النقل، أو من جنسها، أو قريبة منها','591','197','0');
INSERT INTO [t184] VALUES (232,'الثالث والثلاثون: أن أرباب تلك الشبه إنما استطالوا على النفاة والجهمية بما ساعدوهم عليه من تلك الشبه','593','197','0');
INSERT INTO [t184] VALUES (233,'الرابع والثلاثون: أن الله اقتضت حكمته وعدله أن يفسد على العبد عقله الذي خالف به رسله','595','197','0');
INSERT INTO [t184] VALUES (234,'الخامس والثلاثون: هذه القاعدة تقتضي ألا ينتفع بخبر الأنبياء في باب الصفات والأفعال أحد من الخاصة والعامة','599','197','0');
INSERT INTO [t184] VALUES (235,'السادس والثلاثون: أن الرجل إما أن يكون مقرا بالرسل، أو جاحدا لرسالتهم','600','197','0');
INSERT INTO [t184] VALUES (236,'السابع والثلاثون: أنه إذا جوز أن يكون في العقل ما يعارض ما أخبر به الرسول كان الإيمان الجازم موقوفا على العلم بانتفاء ذلك المعارض','602','197','0');
INSERT INTO [t184] VALUES (237,'الثامن والثلاثون: أن طرق العلم ثلاثة: الحس والعقل والمركب منهما','603','197','0');
INSERT INTO [t184] VALUES (238,'التاسع والثلاثون: أن المعلومات الغائبة التي لا تدرك إلا بالخبر أضعاف أضعاف المعلومات التي تدرك بالحس والعقل','605','197','0');
INSERT INTO [t184] VALUES (239,'الأربعون: أن علوم الأنبياء وما جاؤوا به عن الله لا يمكن أن يدرك بالعقل ولا يكتسب','610','197','0');
INSERT INTO [t184] VALUES (240,'الحادي والأربعون: أن يقال لهم: أخبرونا عن خلق هذا النوع الإنساني من قبضة تراب','612','197','0');
INSERT INTO [t184] VALUES (241,'الثاني والأربعون: أن هؤلاء عكسوا شرعة الله وحكمته','618','197','0');
INSERT INTO [t184] VALUES (242,'الثالث والأربعون: أن العقل تحت حجر الشرع فيما يطلبه ويأمر به','621','197','0');
INSERT INTO [t184] VALUES (243,'الرابع والأربعون: أن القرآن مملوء من ذكر الصفات والعلو على الخلق، وهذا عند النفاة بمنزلة وصفه بالأكل والشرب والجوع والعطش والنوم والموت، كل ذلك مستحيل عليه','623','197','0');
INSERT INTO [t184] VALUES (244,'الخامس والأربعون: أنه لو جاز أن يكون في العقول ما يناقض خبر الرسول لم يتصور الإيمان به البتة','624','197','0');
INSERT INTO [t184] VALUES (245,'السادس والأربعون: أن هذه المعارضة ميراث بالتعصيب من الذين ذمهم الله في كتابه بجدالهم في آياته بغير سلطان وبغير علم','625','197','0');
INSERT INTO [t184] VALUES (246,'السابع والأربعون: أن دلالة السمع على مدلوله متفق عليها بين العقلاء، وإن اختلفوا في جهتها هل هي قطعية أو ظنية','627','197','0');
INSERT INTO [t184] VALUES (247,'الخمسون: كل ما عارض السمع من العقليات ففساده معلوم بالعقل وإن لم يعارض السمع','628','197','0');
INSERT INTO [t184] VALUES (248,'الحادي والخمسون: أن الأمور السمعية التي يقال: إن العقل عارضها هي مما علم بالاضطرار أن الرسول جاء بها، وعلم بالاضطرار صحة نبوته ورسالته','629','197','0');
INSERT INTO [t184] VALUES (249,'الثاني والخمسون: أن دليل العقل هو إخباره عن الذي خلقه وفطره أنه وضع فيه ذلك وعلمه إياه وأرشده إليه','630','197','0');
INSERT INTO [t184] VALUES (250,'الثالث والخمسون: وهو أن الأدلة السمعية نوعان: نوع دل بطريق التنبيه والإرشاد على الدليل العقلي: فهو عقلي سمعي، ونوع دال بمجرد الخبر','631','197','0');
INSERT INTO [t184] VALUES (251,'الرابع والخمسون: أنه ليس في القرآن صفة إلا وقد دل العقل الصريح على إثباتها لله، فقد تواطأ عليها دليل العقل ودليل السمع','632','197','0');
INSERT INTO [t184] VALUES (252,'الخامس والخمسون: أن غاية ما ينتهي إليه من ادعى معارضة العقل للوحي أحد أمور أربعة لا بد له منها','638','197','0');
INSERT INTO [t184] VALUES (253,'السادس والخمسون: أنهم إنما يبنون أمرهم في ذلك على أقوال مشتبهة مجملة تحتمل معاني متعددة','646','197','0');
INSERT INTO [t184] VALUES (254,'السابع والخمسون: أن المعارضة بين العقل ونصوص الوحي لا تتأتى على قواعد المسلمين المؤمنين بالنبوة حقا، ولا على أصول أحد من أهل الملل المصدقين بحقيقة النبوة','672','197','0');
INSERT INTO [t184] VALUES (255,'الثامن والخمسون: أن أمر النبوة وما يخبر به الرسول عن الله هو طور آخر وراء مدارك الحس والعقل والخيال والوهم والمنام والكشف','673','197','0');
INSERT INTO [t184] VALUES (256,'التاسع والخمسون: وهو أنك إذا جعلت العقل ميزانا ووضعت في أحد كفتيه كثيرا من الأمور المشاهدة المحسوسة، ووضعت في الكفة الأخرى الأمور التي أخبرت بها الرسل عن الله وأسمائه وصفاته وملائكته وكتبه ورسله واليوم الآخر، وجدت ترجيحه لهذه الكفة وتصديقه بها فوق ترجيحه للتي قبلها وتصديقه بها أقوى','675','197','0');
INSERT INTO [t184] VALUES (257,'الستون: أن المعارضين بين العقل والوحي لا يمكنهم إثبات الصانع ولا أن العالم مخلوق له، ولا يمكنهم إقامة الدليل على استحالة إلهين','679','0','0');
INSERT INTO [t184] VALUES (258,'الحادي والستون: وهو أن الطرق التي سلكها هؤلاء المعارضون بين الوحي والعقل في إثبات الصانع هي بعينها تنفي وجوده','693','257','0');
INSERT INTO [t184] VALUES (259,'الثاني والستون: أن المعارضين للوحي بعقولهم ارتكبوا أربع عظائم','699','258','0');
INSERT INTO [t184] VALUES (260,'الثالث والستون: أن من عارض بين الوحي والعقل فقد قال بتكافؤ الأدلة','699','258','0');
INSERT INTO [t184] VALUES (261,'الرابع والستون: أن المعارضين للوحي جعلوا أقوالهم هي المحكمة، وجعلوا قول الله ورسوله هو المتشابه','700','258','0');
INSERT INTO [t184] VALUES (262,'الخامس والستون: أنهم قد فارقوا العقل والنقل، فلا عقل ولا نقل','702','258','0');
INSERT INTO [t184] VALUES (263,'السادس والستون: أنهم سلكوا طريقا سحروا بها عقول ضعفاء الناس وبصائرهم، فشبهت عليهم، وخيل إليهم أنها حق','704','258','0');
INSERT INTO [t184] VALUES (264,'السابع والستون: أن الله نهى المؤمنين أن يتقدموا بين يدي رسوله، فأي تقدم أبلغ من تقديم عقله على ما جاء به','706','258','0');
INSERT INTO [t184] VALUES (265,'الثامن والستون: أن معارضة الوحي بالعقل ميراث عن إبليس','707','258','0');
INSERT INTO [t184] VALUES (266,'التاسع والستون: بيان فساد معقول إبليس الذي عارض به الوحي','710','258','0');
INSERT INTO [t184] VALUES (267,'السبعون: أن العقل الذي عارض به هؤلاء السمع هو النفي، والذي دل عليه السمع هو الإثبات','716','258','0');
INSERT INTO [t184] VALUES (268,'الحادي والسبعون: أنه سبحانه وصف نفسه بأنه ليس كمثله شيء، وأنه لا سمي له، ولا كفؤ له','727','258','0');
INSERT INTO [t184] VALUES (269,'الثاني والسبعون: أن الله إنما نفى عن نفسه ما يناقض الإثبات ويضاد ثبوت الصفات والأفعال','730','258','0');
INSERT INTO [t184] VALUES (270,'التاسع والسبعون: أنه سبحانه وصف نفسه بأن له المثل الأعلى','735','258','0');
INSERT INTO [t184] VALUES (271,'الثمانون: أن كل من عارض بين الوحي والعقل ورد نصوص الكتاب والسنة بالرأي لا بد أن يبغض تلك النصوص المخالفة لعقله ويعاديها','741','258','0');
INSERT INTO [t184] VALUES (272,'الحادي والثمانون: أن كل من أبغض شيئا من نصوص الوحي ففيه من عداوة الله ورسوله بحسب ذلك','744','258','0');
INSERT INTO [t184] VALUES (273,'الثاني والثمانون: أن الحكم بين الناس هو الله وحده بما أنزله من الكتاب المفصل','745','258','0');
INSERT INTO [t184] VALUES (274,'الثالث والثمانون: أنه سبحانه أخبر أن كل حكم خالف حكمه الذي أنزله على رسوله فهو من أحكام الهوى، لا من أحكام العقل','748','258','0');
INSERT INTO [t184] VALUES (275,'الرابع والثمانون: أن من عارض نصوص الوحي بالعقل لزمه: إما تكذيبها، وإما كتمانها، وإما تحريفها، وإما تخييلها، وإما تجهيلها','749','258','0');
INSERT INTO [t184] VALUES (276,'الخامس والثمانون: أن المعارضين للوحي بآرائهم خمس طوائف','751','258','0');
INSERT INTO [t184] VALUES (277,'السادس والثمانون: أن الصحابة كانوا يستشكلون بعض النصوص فيه، ولم يكن أحد منهم يورد عليه معقولا يعارض النص البتة','753','258','0');
INSERT INTO [t184] VALUES (278,'السابع والثمانون: أن حقيقة قولهم ألا يحتج بالقرآن والسنة على شيء من المسائل العلمية، ولا يستفاد التصديق الجازم بشيء منها','762','258','0');
INSERT INTO [t184] VALUES (279,'الثامن والثمانون: أن المعقولات ليس لها ضابط يضبطها، ولا هي منحصرة في نوع معين','764','258','0');
INSERT INTO [t184] VALUES (280,'التاسع والثمانون: أنه قد ثبت بالعقل الصريح والنقل الصحيح ثبوت صفات الكمال للرب سبحانه','774','258','0');
INSERT INTO [t184] VALUES (281,'التسعون: أنهم ليس عندهم علم ولا هدى ولا كتاب مبين، فمعارضتهم باطلة','780','258','0');
INSERT INTO [t184] VALUES (282,'الحادي والتسعون: أن العقل ملزوم لعلمنا بالشرع ولازم له','784','258','0');
INSERT INTO [t184] VALUES (283,'الثاني والتسعون: أن المعارضين بين العقل والوحي في الأصل فرقتان: الفلاسفة وجهمية المتكلمين، وكل منهما ينقض حجج الآخر','787','258','0');
INSERT INTO [t184] VALUES (284,'الثالث والتسعون: أن الطريقة التي سلوكها هي بعينها الطريق التي سلكها إخوانهم من الملاحدة في معارضة نصوص المعاد','789','258','0');
INSERT INTO [t184] VALUES (285,'الرابع والتسعون: لا يخلو إما أن يكون الرسول يعرف ما دل عليه العقل بزعمكم أو لم يكن يعرف ذلك','796','258','0');
INSERT INTO [t184] VALUES (286,'الخامس والتسعون: أن الله أنزل كتبه حاكمة بين الناس فيما اختلفوا فيه','798','258','0');
INSERT INTO [t184] VALUES (287,'السادس والتسعون: ما ذكره ابن سينا وأمثاله من أنه لم يرد في القرآن من الإشارة إلى توحيدهم شيء، كلام صحيح','799','258','0');
INSERT INTO [t184] VALUES (288,'السابع والتسعون: أن التوحيد الذي دعا إليه هؤلاء الملاحدة، هو من أعظم الإلحاد في أسماء الرب وصفاته وأفعاله','800','258','0');
INSERT INTO [t184] VALUES (289,'الثامن والتسعون: أنه لو كان الحق فيما يقوله هؤلاء النفاة المعطلون وإخوانهم من الملاحدة لكان قبول الفطر له أعظم من قبولها للإثبات','801','258','0');
INSERT INTO [t184] VALUES (290,'التاسع والتسعون: أنا نعرض على الفطر السليمة والعقول التي لم تفسد بتلقي المقالات الفاسدة وتلقنها خصمين اختصموا في ربهم','802','258','0');
INSERT INTO [t184] VALUES (291,'المائة: انظر رؤوس المثبتة والنفاة وملوكهم وأتباعهم يبين لك حقيقة الأمر','805','258','0');
INSERT INTO [t184] VALUES (292,'الحادي والمائة: أن تجويز معارضة العقل للوحي يوجب وصف الوحي بضد ما وصفه الله به','809','258','0');
INSERT INTO [t184] VALUES (293,'الثاني والمائة: أن الله ضمن الهدى والفلاح لمن اتبع القرآن، والضلال والشقاء لمن أعرض عنه، فكيف بمن عارضه','814','258','0');
INSERT INTO [t184] VALUES (294,'الثالث بعد المائة: أن الله سبحانه ذم المجادلين في آياته بالباطل','815','258','0');
INSERT INTO [t184] VALUES (295,'الرابع والمائة: أن الله وصف المعرضين عن الوحي المعارضين له بعقولهم وآرائهم بالجهل والضلال والحيرة والشك والعمى والريب','816','258','0');
INSERT INTO [t184] VALUES (296,'الخامس والمائة: أنهم لا يمكنهم أن يقولوا: كل واحد من الدليلين المتعارضين يقيني، وأنهما قد تعارضا على وجه لا يمكن الجمع بينهما','820','258','0');
INSERT INTO [t184] VALUES (297,'السادس والمائة: أن هذه المعقولات التي عارضوا بها الوحي لها معقولات تعارضها هي أقوى منها، ومقدماتها أصح من مقدماتها','822','258','0');
INSERT INTO [t184] VALUES (298,'السابع والمائة: أن من خاطب الناس في علم بكلام ذكر أنه بين لهم فيه حقيقة ذلك العلم وأوضح مشكلاته وبين غوامضه ولم يحوجهم بعده إلى كتاب سواه، ولم يكن في ذلك الكتاب بيان ذلك العلم، ولا معرفة ذلك المطلوب = كان هذا المصنف مفرطا في الجهل والضلال، أو في المكر والاحتيال، أو في الكذب والمحال','823','258','0');
INSERT INTO [t184] VALUES (299,'الثامن والمائة: أن هذا يتضمن الصد عن آيات الله وبغيها عوجا','825','258','0');
INSERT INTO [t184] VALUES (300,'التاسع والمائة: أن الرسل لم يسكتوا عن الكلام في هذا الباب، بل تكلموا فيه بغاية الإثبات المناقض لما عليه الجهمية المعطلة','827','258','0');
INSERT INTO [t184] VALUES (301,'العاشر بعد المائة: مثل ما جاءت به الرسل عند النفاة والمعطلة','829','258','0');
INSERT INTO [t184] VALUES (302,'الحادي عشر بعد المائة: أن لوازم هذا القول معلومة البطلان بالضرورة من دين الإسلام','832','258','0');
INSERT INTO [t184] VALUES (303,'الثاني عشر بعد المائة: أن الرسول إذا لم يبين للناس أصول إيمانهم ولا عرفهم ربهم وأسمائه وصفاته وأفعاله وما يجب له ويمتنع عليه، بل إنما بين لهم الأمور العملية، كانت رسالته مقصورة على أدنى المقصودين','838','258','0');
INSERT INTO [t184] VALUES (304,'الثالث عشر بعد المائة: أن أقوال هؤلاء النفاة المعطلة متناقضة مختلفة، وذلك يدل على بطلانها وأنها ليست من عند الله','841','258','0');
INSERT INTO [t184] VALUES (305,'الرابع عشر بعد المائة: أنه يستلزم قولهم ثلاث مقدمات تناقض دعواهم غاية المناقضة','842','258','0');
INSERT INTO [t184] VALUES (306,'الخامس عشر بعد المائة: أن المعارضة ممتنعة على تقدير صحة هذه المعقولات وفسادها','844','258','0');
INSERT INTO [t184] VALUES (307,'السادس عشر بعد المائة: أن تجويز التعارض بين السمع والعقل والإيمان بالله ورسوله لا يمكن اجتماعهما البتة','844','258','0');
INSERT INTO [t184] VALUES (308,'السابع عشر بعد المائة: يقال لهؤلاء المعارضين للوحي بآرائهم: إما أن تردوا هذه النصوص وتكذبوها، وإما أن تصدقوها وتقبلوها','845','258','0');
INSERT INTO [t184] VALUES (309,'الثامن عشر بعد المائة: أنهم لم يبق لهم إلا طريقان: إما طريق النظار، وإما طريق الكشف وكل من هاتين الطريقتين باطله أضعاف حقه','847','258','0');
INSERT INTO [t184] VALUES (310,'التاسع عشر بعد المائة: أن يقال لمن جوز مجيء الرسول بما يخالف صريح العقل: ما تقول إذا سمعت كلامه قبل أن تعلم هل في العقل ما يخالفه أم لا','848','258','0');
INSERT INTO [t184] VALUES (311,'العشرون بعد المائة: أن كل من لم يقر بما جاء به الرسول إلا بعد أن يقوم على صحته عنده دليل منفصل لم يكن مؤمنا به قطعا','849','258','0');
INSERT INTO [t184] VALUES (312,'الحادي والعشرون بعد المائة: أن حال هؤلاء المعارضين بين الوحي والعقل ضد حال أهل الإيمان من كل وجه','849','258','0');
INSERT INTO [t184] VALUES (313,'الثاني والعشرون بعد المائة: أنهم جعلوا كلام الله ورسوله من الطرق الضعيفة المزيفة التي لا يتمسك فيها في العلم واليقين','851','258','0');
INSERT INTO [t184] VALUES (314,'الثالث والعشرون بعد المائة: كل ما أثبته الرسول لله له فهو كمال واجب له، وما نفاه عنه فهو نقص ممتنع عليه','857','258','0');
INSERT INTO [t184] VALUES (315,'الرابع والعشرون بعد المائة: أن هؤلاء يعيبون أهل السنة والحديث بالتقليد، وهم يقلدون المشركين والملاحدة','859','258','0');
INSERT INTO [t184] VALUES (316,'الخامس والعشرون بعد المائة: أن الدين تصديق الرسول فيما أخبر وطاعته فيما أمر','864','258','0');
INSERT INTO [t184] VALUES (317,'السادس والعشرون بعد المائة: أن السمع حجة الله على خلقه، وكذلك العقل','865','258','0');
INSERT INTO [t184] VALUES (318,'السابع والعشرون بعد المائة: أن هذه المعارضة بين الوحي والعقل نتيجة جهلين عظيمين: جهل بالوحي، وجهل بالعقل','881','258','0');
INSERT INTO [t184] VALUES (319,'الثامن والعشرون بعد المائة: أن هؤلاء المعارضين هم صنفان: ملاحدة دهرية، ومعطلة جهمية','882','258','0');
INSERT INTO [t184] VALUES (320,'الوجه التاسع والعشرون بعد المائة: أن الكلام في الدين نوعان: أمر وخبر، فما عارض الأمر كان من باب الهوى، وما عارض الخبر كان من باب الظن والخرص الذي هو أكذب الحديث','884','258','0');
INSERT INTO [t184] VALUES (321,'الثلاثون بعد المائة: أن المعارضين لا يتم لهم ما ادعوه من المعارضة إلا بأربعة أمور: لبس الحق بالباطل وكتمان الحق، والتكذيب به، والتصديق بالباطل','886','258','0');
INSERT INTO [t184] VALUES (322,'الحادي والثلاثون بعد المائة: أنه ما من حق وباطل إلا وبينهما اشتراك من بعض الوجوه','889','258','0');
INSERT INTO [t184] VALUES (323,'الثاني والثلاثون بعد المائة: أنك إذا أخذت لوازم المشترك والمميز، وميزت هذا من هذا صح نظرك ومناظرتك','891','258','0');
INSERT INTO [t184] VALUES (324,'الثالث والثلاثون بعد المائة: أن الأصل الذي قادهم إلى النفي والتعطيل واعتقاد المعارضة بين العقل والوحي أصل واحد، هو الفرار من تعدد صفات الواحد وتكثر أسمائه الدالة على صفاته، وقيام الأمور المتجددة به','893','258','0');
INSERT INTO [t184] VALUES (325,'الرابع والثلاثون بعد المائة: أن من أئمتهم من يقول: إنه ليس في العقل ما يوجب تنزيه الرب عن النقائص، ولم يقم على ذلك دليل عقلي أصلا','899','258','0');
INSERT INTO [t184] VALUES (326,'الخامس والثلاثون بعد المائة: أن يقال لهم: لا يمكنكم تنزيه الرب عن النقائص والعيوب إلا أن تتحيزوا إلى أهل السنة، وإلا فلن يمكنكم على أصولكم تنزيه الرب عن العيوب البتة','901','258','0');
INSERT INTO [t184] VALUES (327,'السادس والثلاثون بعد المائة: أن الله عاب آلهة المشركين بنفس ما وصفتم الإله الحق به','903','258','0');
INSERT INTO [t184] VALUES (328,'السابع والثلاثون بعد المائة: كل يدعي أن العقل دله على مقالته وصحتها، وإذا جاء السمع بخلافها لجأ إلى طاغوت من هذه الطواغيت','904','258','0');
INSERT INTO [t184] VALUES (329,'الثامن والثلاثون بعد المائة: أن اللوازم التي تلزم المعطلة النفاة شر من اللوازم التي تلزم المشبهة المحضة','906','258','0');
INSERT INTO [t184] VALUES (330,'التاسع والثلاثون بعد المائة: إنكم أسأتم القول في العقل غاية الإساءة، وقدحتم فيه أعظم القدح','907','258','0');
INSERT INTO [t184] VALUES (331,'الأربعون بعد المائة: إن الشهادة تعتمد على الشاهد وصدقه، وقد علم كذب العقل المعارض لما جاءت به الرسل قطعا وجملة','907','258','0');
INSERT INTO [t184] VALUES (332,'الحادي والأربعون بعد المائة: أنهم ردوا حكم العقل الصريح المبني على المقدمات الضرورية الفطرية، ونسبوه إلى البطلان، وحينئذ فلا يمكنهم أن يقيموا معقولا صحيحا على خلاف ما دل عليه السمع البتة','908','258','0');
INSERT INTO [t184] VALUES (333,'الثاني والأربعون بعد المائة: أن فحول الكلام وأئمة النظر قد شهدوا لطريقة النفاة المعطلة بمناقضتها للسمع والعقل، وأن السمع والعقل إنما يقتضيان الإثبات','913','258','0');
INSERT INTO [t184] VALUES (334,'الثالث والأربعون بعد المائة: أنهم لم يكفهم أن سدوا على أنفسهم باب الرد على أعداء الإسلام بما وافقوهم فيه من النفي والتعطيل، حتى فتحوا لهم الباب، وطرقوا لهم الطريق إلى محاربة القرآن والسنة','921','258','0');
INSERT INTO [t184] VALUES (335,'الرابع والأربعون بعد المائة: أن يقال لهم: أتدعون هذه المعارضة بين العقل وجميع النقل أو بعضه','923','258','0');
INSERT INTO [t184] VALUES (336,'الخامس والأربعون بعد المائة: إن نهاية أمر هؤلاء المعارضين لنصوص الوحي بالرأي انتهاؤهم إلى الشك والتشكيك والحيرة في أمرهم','925','258','0');
INSERT INTO [t184] VALUES (337,'السادس والأربعون بعد المائة: إن أئمة الإسلام وملوك السنة تنوعوا في ذم طرق المتكلمين، والطعن فيها، وعيب أهلها، والحكم بعقوبتهم، وإشهارهم والتحذير منهم','929','258','0');
INSERT INTO [t184] VALUES (338,'السابع والأربعون بعد المائة: أن الله منح عباده فطرة فطرهم عليها، لا تقبل سوى الحق، ولا تؤثر عليه غيره لو تركت، وأيدها بعقول تفرق بين الحق والباطل، وكملها بشرعة تفصل لها ما هو مستقر في الفطرة وأدركه العقل مجملا','939','258','0');
INSERT INTO [t184] VALUES (339,'الطريق الأول: أنه إذا ثبت بضرورة العقل أنه سبحانه مباين للمخلوقات، وثبت أن العالم كري لزم أن يكون الرب في العلو ضرورة','941','338','0');
INSERT INTO [t184] VALUES (340,'الطريق الثاني: أن يقال: علوه سبحانه على العالم أمر مستقر في فطر العباد، معلوم لهم بالضرورة','943','338','0');
INSERT INTO [t184] VALUES (341,'الطريق الثالث: أنه قد ثبت بصريح العقل أن الأمرين المتقابلين إذا كان أحدهما صفة كمال والآخر صفة نقص؛ فإن الله سبحانه يوصف بالكمال منهما دون النقص','965','338','0');
INSERT INTO [t184] VALUES (342,'الطريق الرابع: أنه إذا كان سبحانه مباينا للعالم فإما أن يكون محيطا به أو لا يكون محيطا به. فإن كان محيطا به لزم علوه عليه قطعا ضرورة','966','338','0');
INSERT INTO [t184] VALUES (343,'الطريق الخامس: ما احتج به الإمام أحمد نفسه على الجهمية','967','338','0');
INSERT INTO [t184] VALUES (344,'الطريق السادس: أن يقال: كل موجودين فإما أن يكون أحدهما قائما بنفسه، أو قائما بالآخر','969','338','0');
INSERT INTO [t184] VALUES (345,'الطريق السابع: أن يقال: الرب إما أن يكون موجودا خارج الأذهان، موجودا في الأعيان أو لا يكون له وجود خارجي','970','338','0');
INSERT INTO [t184] VALUES (346,'الطريق الثامن: إذا ثبت له سبحانه وجود خارج الأذهان فإما أن يكون هو العالم المشهود، أو صفة من صفاته، وعرضا من أعراضه، أو غيره','970','338','0');
INSERT INTO [t184] VALUES (347,'الطريق التاسع: أنا إذا عرضنا على العقل التصديق بموجودين قائمين بأنفسهما، وأحدهما مباين للآخر، مع كونه غير مماثل له ولا هو من جنسه','971','338','0');
INSERT INTO [t184] VALUES (348,'الطريق العاشر: أنه عند المعطلة النفاة كون الله فوق العالم مستويا على عرشه بمنزلة كونه يأكل ويشرب وينام، بل هو بمنزلة إثبات الزوجة والولد له، في كون هذا منافيا لإلهيته وربوبيته وقدمه','972','338','0');
INSERT INTO [t184] VALUES (349,'الطريق الحادي عشر: أن يقال للمعطلة: تنزيهكم له سبحانه عن كونه مباينا لخلقه تنزيه له عن غناه ووجوده، وتنزيهكم له عن استوائه على عرشه تنزيه له عن كماله','972','338','0');
INSERT INTO [t184] VALUES (350,'الطريق الثاني عشر: أن الله جعل بعض مخلوقاته عاليا على بعض، ولم يلزم من ذلك مماثلة العالي للسافل ومشابهته له','973','338','0');
INSERT INTO [t184] VALUES (351,'الطريق الثالث عشر: أن يقال: أخبر الناس بمقالات الفلاسفة قد حكى اتفاق الحكماء على أن الله والملائكة في السماء','975','338','0');
INSERT INTO [t184] VALUES (352,'الطريق الرابع عشر: وهو أنا إذا عرضنا على العقل وجود موجود قائم بنفسه لا في العالم ولا خارجا عنه ولا يشار إليه، وعرضنا عليه وجود موجود يشار إليه فوق العالم ليس بجسم، كان إنكار العقل للأول أعظم','976','338','0');
INSERT INTO [t184] VALUES (353,'الطريق الخامس عشر: أنه سبحانه لو لم يقبل الإشارة الحسية إليه فإما أن يقال: إنه يقبل الإشارة المعنوية فقط، أو لا يقبلها أيضا كما لا يقبل الحسية، فإن لم يقبل هذه ولا هذه فهو عدم محض','976','338','0');
INSERT INTO [t184] VALUES (354,'الطريق السادس عشر: أن من أعجب العجب أن هؤلاء الذين فروا من القول بعلو الله فوق المخلوقات واستوائه على عرشه خشية التشبيه والتجسيم قد اعترفوا بأنهم لا يمكنهم إثبات الصانع إلا بنوع من التشبيه والتمثيل','977','338','0');
INSERT INTO [t184] VALUES (355,'الطريق السابع عشر: أن يقال: هل للرب تعالى ماهية متميزة على سائر الماهيات يختص بها لذاته، أم تقولون: لا ماهية له','979','338','0');
INSERT INTO [t184] VALUES (356,'الطريق الثامن عشر: أن يقال: ذاته سبحانه إما أن تكون قابلة للعلو على العالم، أو لا تكون قابلة.','981','338','0');
INSERT INTO [t184] VALUES (357,'الطريق التاسع عشر: أن الجهمية المعطلة معترفون بوصفه تعالى بعلو القهر وعلو القدر، وأن ذلك كمال لا نقص، فإنه من لوازم ذاته','983','338','0');
INSERT INTO [t184] VALUES (358,'الطريق العشرون: أن الفلاسفة لما أوردوا عليكم هذه الحجة بعينها في نفي الصفات أجبتم عنها','984','338','0');
INSERT INTO [t184] VALUES (359,'الطريق الحادي والعشرون: أن هذه الحجة العقلية القطعية مما ناظر بها الكرامية لأبي إسحاق الإسفراييني، ففر أبو إسحاق إلى كون الرب قائما بنفسه بالمعنى المعقول','985','338','0');
INSERT INTO [t184] VALUES (360,'الطريق الثاني والعشرون: وهو أن القيام بالنفس صفة كمال، فالقائم بنفسه أكمل ممن لا يقوم بنفسه','986','338','0');
INSERT INTO [t184] VALUES (361,'الطريق الثالث والعشرون: أن كل من أقر بوجود رب خالق للعالم مدبر له لزمه الإقرار بمباينته لخلقه وعلوه عليهم، وكل من أنكر مباينته وعلوه لزم إنكاره وتعطيله','987','338','0');
INSERT INTO [t184] VALUES (362,'الطريق الرابع والعشرون: أنه قد دل البرهان الضروري والعقل الصريح على استغنائه سبحانه بنفسه، وأنه الغني بذاته عن كل ما سواه','988','338','0');
INSERT INTO [t184] VALUES (363,'الطريق الخامس والعشرون: أنه قد ثبت بالعقل إمكان رؤيته سبحانه، وبالشرع وقوعها في الدار الآخرة، فاتفق العقل والشرع على إمكان الرؤية ووقوعها','989','338','0');
INSERT INTO [t184] VALUES (364,'الطريق السادس والعشرون: أنه قد ثبت بالعقل والنقل والفطرة أن الله سبحانه سميع بصير، وهو سبحانه يرى كل المرئيات، لا يخفى عليه منها شيء. ورؤيته لخلقه تستلزم مباينته لهم ضرورة','991','338','0');
INSERT INTO [t184] VALUES (365,'الطريق السابع والعشرون: أن كل من أثبت الصفات أو شيئا منها لزمه إثبات المباينة، وإلا تناقض غاية التناقض','992','338','0');
INSERT INTO [t184] VALUES (366,'الطريق الثامن والعشرون: أنهم إذا اعترفوا بقيام الصفات بالذات وأنها زائدة على الذات المجردة، ولم يكن ذلك تجسيما ولا تركيبا يستلزم الحدوث، بطلت كل شبهة لهم تمنع العلو والاستواء على العرش','993','338','0');
INSERT INTO [t184] VALUES (367,'الطريق التاسع والعشرون: أن يقال: ما أثبته هؤلاء المعطلة من المباينة لا يبطل الحلول والاتحاد','994','338','0');
INSERT INTO [t184] VALUES (368,'الطريق الثلاثون: أنه لو لم يكن مباينا للعالم لزم أحد أمور ثلاثة، أحدها: أن يكون هو هذا العالم. والثاني: قول من يقول: بل هو حال في العالم. الثالث: قول من يقول: لا هو العالم ولا هو حال فيه ولا بائن عنه ولا متصل به ولا منفصل عنه','996','338','0');
INSERT INTO [t184] VALUES (369,'فهذه ثلاثون طريقا مضافة إلى الوجه السابع والأربعين بعد المائة في بيان عدم معارضة العقل للنقل وبيان موافقتهما وتطابقهما','997','338','0');
INSERT INTO [t184] VALUES (370,'الثامن والسبعون بعد المائة: إن المعارضين للوحي بآرائهم وعقولهم تتضمن معارضتهم الفرية على الوحي والعقل واللغة والفطرة وإفسادها','997','258','0');
INSERT INTO [t184] VALUES (371,'التاسع والسبعون بعد المائة: أن المعارضين للوحي بعقولهم في الأصل هم أعداء الرسل المكذبون لهم ودونهم طوائف الجهمية المعطلة، وملاحدة الصوفية، وزنادقة الباطنية، وخونة الولاة وظلمتهم','998','258','0');
INSERT INTO [t184] VALUES (372,'الثمانون بعد المائة: أنه من المعلوم عند جميع العقلاء أن الرسل هم أعقل الخلق، وعقولهم أكمل العقول. ولهذا كان ما جاؤوا به فوق عقول البشر، ولهذا حصل على أيديهم من الخير ما لم يحصل على أيدي سواهم','1007','258','0');
INSERT INTO [t184] VALUES (373,'الحادي والثمانون بعد المائة: لو عورض ما جاء به خاتم الرسل بموسى وعيسى كانت هذه المعارضة ضلالا وانسلاخا من الدين بالكلية، فكيف بضلالة قوم اتبعوا كتابا أوحاه الشيطان إلى رؤوس المشركين وأهل الضلال','1008','258','0');
INSERT INTO [t184] VALUES (374,'الثاني والثمانون بعد المائة: أن الله سبحانه أنكر على من لم يكتف بكتابه','1010','258','0');
INSERT INTO [t184] VALUES (375,'الثالث والثمانون بعد المائة: أن هؤلاء الذين لم يكتفوا بكتابه حتى سلكوا ـ بزعمهم ـ طريقة العقل وعارضوه به وقدموه عليه من جنس الذين لم يكتفوا به سبحانه إلها','1010','258','0');
INSERT INTO [t184] VALUES (376,'الرابع والثمانون بعد المائة: أن هؤلاء المعطلة النفاة المعارضين للوحي بآرائهم ومعقولاتهم من الظانين بالله وكتابه ورسوله ظن السوء','1013','258','0');
INSERT INTO [t184] VALUES (377,'الخامس والثمانون بعد المائة: أن هذا نسبة له إلى كونه كذبا في نفسه، فإنه إذا خالف صريح العقل لم يكن مطابقا لمخبره، فيكون المتكلم به قد أخبر بخبر لم يطابق مخبره، وهذا حقيقة الكذب','1014','258','0');
INSERT INTO [t184] VALUES (378,'السادس والثمانون بعد المائة: أن من ادعى معارضة العقل لما جاءت به الرسل من صفاته وأفعاله وحقائق أسمائه لم يقدره حق قدره','1015','258','0');
INSERT INTO [t184] VALUES (379,'السابع والثمانون بعد المائة: أن المعارضين بين الوحي والعقل هم دائما يدلون بنفي التشبيه والتمثيل','1021','258','0');
INSERT INTO [t184] VALUES (380,'الثامن والثمانون بعد المائة: أنه سبحانه فرق بين هذين الاسمين الدالين على علوه وعظمته','1026','258','0');
INSERT INTO [t184] VALUES (381,'التاسع والثمانون بعد المائة: أن العظيم يوصف به الأعيان والكلام والصفات والمعاني','1029','258','0');
INSERT INTO [t184] VALUES (382,'التسعون بعد المائة: أن تعطيل ذاته المقدسة عن وصفها بذلك وجعل ذلك مجرد أمر معنوي يقتضي سلب ذلك عنه بالكلية','1033','258','0');
INSERT INTO [t184] VALUES (383,'الحادي والتسعون بعد المائة: أنه قد علم بالاضطرار أن الله له ذات مخصوصة','1034','258','0');
INSERT INTO [t184] VALUES (384,'الثاني والتسعون بعد المائة: أن كل من عارض الوحي بالرأي والعقل فهو من خصماء الله','1038','258','0');
INSERT INTO [t184] VALUES (385,'الثالث والتسعون بعد المائة: أن هؤلاء النفاة المعطلة إذا غلبوا مع أهل الإثبات، وقامت حجتهم عليهم؛ عدلوا إلى عقوبتهم وإلزامهم بالأخذ بأقوالهم ومذاهبهم','1041','258','0');
INSERT INTO [t184] VALUES (386,'الرابع والتسعون بعد المائة: أن المعارضين للوحي بآرائهم وعقولهم في الأصل صنفان','1044','258','0');
INSERT INTO [t184] VALUES (387,'الخامس والتسعون بعد المائة: أنه كيف يكون النفاة المعطلة أولى بالصواب والحق، وشهداء الله في أرضه من جميع أقطار الأرض يشهدون عليهم بالضلالة والحيرة والكذب على الله ورسوله وكتابه','1047','258','0');
INSERT INTO [t184] VALUES (388,'السادس والتسعون بعد المائة: أن هؤلاء أصلوا أصولا جعلوها أساسا لبنائهم، وسموها قواطع عقلية، وسموا أدلتها براهين يقينية، فجاءت فروع تلك الأصول ولوازمها والبناء الذي ارتفع عليها من أبطل الفروع وأفسد اللوازم وأضعف البناء وأوهاه','1074','258','0');
INSERT INTO [t184] VALUES (389,'السابع والتسعون بعد المائة: أن من تأمل أقوال المعارضين للوحي وجدها قد جمعت أمرين، كل منهما يدل على بطلانها: أحدهما: اختلافها في نفسها واضطرابها وتهافتها. والثاني: أنما مصدرها الخرص والظن والتخمين','1080','258','0');
INSERT INTO [t184] VALUES (390,'الثامن والتسعون بعد المائة: أن هؤلاء النفاة المعطلة لا بد لهم من أصل يقررون به قولهم الذي ابتدعوه، وأصل ينفون به ما أخبرت به الرسل','1083','258','0');
INSERT INTO [t184] VALUES (391,'التاسع والتسعون بعد المائة: أن هؤلاء المعطلة لا يمكن على أصولهم التي أصلوها وقواعدهم التي أسسوها محبة الله، ولا مدحه، ولا حمده وتمجيده والثناء عليه، ولا الرضى به، ولا الابتهاج بقربه ولا الفرح به، ولا اللذة العظمى برؤية وجهه، ولا لذة الآذان والأرواح بسماع كلامه، بل ولا الشوق إليه، ولا الإنابة ولا الطمأنينة به وإليه، ولا الأمن من عذابه لهم بغير جرم أصلا، ومن إبطاله أعمالهم الصالحة بغير سبب','1085','258','0');
INSERT INTO [t184] VALUES (392,'الموفي مائتي وجه: أن هؤلاء وضعوا قانونا يتضمن نفي ما وصف به نفسه من الرأفة والرحمة والمحبة والمودة والحنان والغضب والرضى والفرح والضحك والتعجب. قالوا: لأن هذه الأمور متضمنة للألم واللذة، والله سبحانه منزه عن ذلك','1089','258','0');
INSERT INTO [t184] VALUES (393,'ومضمون هذه الحجة أن جواز ذلك عليه مستلزم لكون الملتذ به حادثا، وكونه متقدما على حدوثه، وكون الشيء متقدما على وجوده محال. وفسادها بين من وجوه','1098','257','0');
INSERT INTO [t184] VALUES (394,'أحدها: النقض والمعارضة بالإرادة والمحبة والرحمة والرضى.','1098','393','0');
INSERT INTO [t184] VALUES (395,'الثاني: أن لفظ اللذة والألم من الألفاظ التي فيها إجمال واشتباه، وليس لها ذكر في الكتاب والسنة بنفي ولا إثبات','1098','393','0');
INSERT INTO [t184] VALUES (396,'الثالث: أن ما وصف الله سبحانه به نفسه من المحبة والرضى والفرح والغضب والبغض والسخط من أعظم صفات الكمال','1100','393','0');
INSERT INTO [t184] VALUES (397,'الرابع: أنه لا كمال في مجرد سلب ذلك عنه','1101','393','0');
INSERT INTO [t184] VALUES (398,'الخامس: أن يقال: ما المانع من أن يكون رضاه ومحبته وفرحه من كماله في نفسه وما هو عليه من الجلال والجمال','1102','393','0');
INSERT INTO [t184] VALUES (399,'السادس: وهو أن اللذة والفرح تابعة للمحبة في الكمال والقوة، والمحبة تابعة لمعرفة المحب بصفات المحبوب وجماله','1105','393','0');
INSERT INTO [t184] VALUES (400,'السابع: أنه سبحانه إذا كان يحب بعض ما خلقه، ويرضى عنه ويفرح به؛ فمحبته لنفسه ورضاه عن نفسه أولى وأحرى وأعظم من محبته لمخلوقه','1106','393','0');
INSERT INTO [t184] VALUES (401,'الثامن: أن الجهمي أبطل هذا بقوله: إن اللذة إدراك الملائم، فيلزم أن يقال: ذات الله ملائمة لذاته، وذلك غير معقول. وهذا الذي قرره باطل من وجوه','1106','393','0');
INSERT INTO [t184] VALUES (402,'التاسع: أن يقال: ما المانع أن يحب ويرضى ويفرح ويضحك بما يكون من الأمور الحادثة الموافقة لمحبته ورضاه','1108','393','0');
INSERT INTO [t184] VALUES (403,'العاشر: أن الجهمي احتج على امتناع ذلك عليه بأن هذا انفعال وتأثير عن العبد، والمخلوق لا يؤثر في الخالق، فلو أغضبه أو فعل ما يفرح به لكان المحدث قد أثر في القديم تلك الكيفيات، وهذا محال. وجواب هذه الشبهة من وجوه','1110','393','0');
INSERT INTO [t184] VALUES (404,'الحادي عشر: أن قولك: «يستحيل أن يخلق الملتذ به في الأزل وألا يخلقه في الأزل» إلى آخره، مبنى الحجة على مقدمتين، ونحن نتكلم على المقدمتين','1112','393','0');
INSERT INTO [t184] VALUES (405,'الثاني عشر: أنا لو فرضنا في حقنا أنه يجب تحصيل المفروح به مع القدرة عليه، فلم قلت: إنه في حق الرب تعالى كذلك','1114','393','0');
INSERT INTO [t184] VALUES (406,'الثالث عشر: أن العبد إنما يجب مع قدرته وداعيته حصول مراده ولذته؛ لأنه يتضرر بعدم حصوله، فإن كماله وصلاحه بحصول ما يحبه ويريده ويلتذ به، وبعدمه يكون متضررا ناقصا! والله لا يلحقه الضرر بوجه ما','1114','393','0');
INSERT INTO [t184] VALUES (407,'الرابع عشر: لم قلت بأن كل ما يحبه الرب سبحانه ويرضاه ويفرح به يمكن وجوده في وقت واحد? فإن ذلك قد يستلزم الجمع بين النقيضين','1115','393','0');
INSERT INTO [t184] VALUES (408,'الخامس عشر: أنه سبحانه إذا كان يحب أمورا، وتلك الأمور المحبوبة لها لوازم يمتنع وجودها بدونها كان وجود تلك الأمور مستلزما للوازمها التي لا توجد بدونها','1115','393','0');
INSERT INTO [t184] VALUES (409,'السادس عشر: أن يقال: اللذة يجب وجودها من القادر إذا كان مستغنيا عنها بلذة أخرى أكمل منها أم مطلقا','1116','393','0');
INSERT INTO [t184] VALUES (410,'السابع عشر: أن يقال: هو لا يحدثها إلا إذا أحبها ورضيها وتضمنت فرحه بها','1116','393','0');
INSERT INTO [t184] VALUES (411,'الثامن عشر: أن يقال: لو صح ما ذكرته كان مستلزما ألا يخلق الرب تعالى شيئا، أو يخلق كل شيء قبل خلقه إياه','1117','393','0');
INSERT INTO [t184] VALUES (412,'التاسع عشر: أن أحدنا إذا كانت إرادته جازمة وقدرته تامة وجب وجود الفعل منه مقترنا بإرادته وقدرته، ولا يتأخر الفعل إلا لعدم كمال القدرة أو لعدم كمال الإرادة','1118','393','0');
INSERT INTO [t184] VALUES (413,'العشرون: أنهم فسروا في مسألة التحسين والتقبيح الحمد والذم بما يستلزم اللذة والألم','1119','393','0');
INSERT INTO [t184] VALUES (414,'الحادي والعشرون: إن هؤلاء الجهمية ينفرون من أحب الأشياء إلى الله وأكرمها عليه وأعظمها عنده، وهو ذكره بأسمائه وصفات كماله ونعوت جلاله، والثناء عليه ومدحه بها وحمده عليها','1137','393','0');
INSERT INTO [t184] VALUES (415,'الثاني والعشرون: وهو أن دعوة الرسل تدور على ثلاثة أمور: تعريف الرب المدعو إليه بأسمائه وصفاته وأفعاله. ومعرفة الطريق الموصلة إليه. وتعريفهم ما لهم بعد الوصول إليه في دار كرامته من النعيم','1138','393','0');
INSERT INTO [t184] VALUES (416,'الثالث والعشرون: في «الصحيح» قيل: يا رسول الله أرأيت الرجل يعمل العمل من الخير ويحمده الناس عليه؟ قال: «تلك عاجل بشرى المؤمن»','1140','393','0');
INSERT INTO [t184] VALUES (417,'الرابع والعشرون: وهو أن الحسن والقبح سواء عرف بالشرع أو بالعقل إنما يعود إلى الملائم والمنافي','1141','393','0');
INSERT INTO [t184] VALUES (418,'الخامس والعشرون: إنه سبحانه كما يبغض هذا الإفك والباطل الذي قاله فيه أعداؤه يفرح بثناء المثني عليه بأوصاف كماله ونعوت جلاله أعظم فرح ويرضى به ويحبه','1143','393','0');
INSERT INTO [t184] VALUES (419,'السادس والعشرون: وهو أن النبي - صلى الله عليه وسلم - جمع بين محبة الرب سبحانه للمدح ومحبته للعذر','1144','393','0');
INSERT INTO [t184] VALUES (420,'السابع والعشرون: أنه إذا كان لا يفرح ولا يرضى بمدحه وحمده والثناء عليه، ولا يغضب ولا يسخط ويبغض شتمه = كان لا فرق عنده بين الحسن والقبيح والمدح والذم، وهذا غاية النقص','1146','393','0');
INSERT INTO [t184] VALUES (421,'الثامن والعشرون: قولكم: إن المدح يستحيل تصوره في حق الله، من أوضح الكفر، وأقبح المعاداة لله، والمناقضة لكتبه ورسله','1151','393','0');
INSERT INTO [t184] VALUES (422,'التاسع والعشرون: إنه من المعلوم أن كونه سبحانه يستحق المدح، والمحامد أبين في الشرع والعقل والفطرة من كونه لا يفرح','1152','393','0');
INSERT INTO [t184] VALUES (423,'الثلاثون بعد المائتين: أن يقال: قولكم: إن المدح والذم لا معنى لهما إلا مجرد الخبر عن استحقاق ما يفرح ويؤلم، ليس كذلك','1152','393','0');
INSERT INTO [t184] VALUES (424,'الحادي والثلاثون بعد المائتين: أن نقول: إذا عارضتم بين المعقول والمنقول فإما أن تكذبوا المنقول وإما أن تصدقوه','1155','393','0');
INSERT INTO [t184] VALUES (425,'الثاني والثلاثون بعد المائتين: أن الأدلة العقلية التي زعمتم أنها تعارض النقل هي بعينها تنفي المعاني التي تأولتم النقل عليها، وصرفتم معناه إليها','1157','393','0');
INSERT INTO [t184] VALUES (426,'الثالث والثلاثون بعد المائتين: أن لازم هذا القول بل حقيقته أن أسماء الرب تعالى إنما تطلق عليه مجازا لا حقيقة','1158','393','0');
INSERT INTO [t184] VALUES (427,'الرابع والثلاثون بعد المائتين: أن الناس في هذه الأسماء التي تقال على الرب وعلى العبد مختلفون على أقوال','1159','393','0');
INSERT INTO [t184] VALUES (428,'الخامس والثلاثون بعد المائتين: أنه قد علم أن المعنى المستعار يكون في المستعار منه أكمل منه في المستعار، وأن المعنى الذي دل عليه اللفظ بطريق الحقيقة أكمل من المعنى الذي دل عليه بطريق المجاز','1162','393','0');
INSERT INTO [t184] VALUES (429,'السادس والثلاثون بعد المائتين: أن أعقل الخلق على الإطلاق الرسل، وأتباعهم بعدهم أعقل الأمم، وأعقل هؤلاء المسلمون، وأعقل المسلمين أصحاب رسول الله - صلى الله عليه وسلم - والتابعون لهم بإحسان، وأهل السنة والحديث أعقل الأمة بعدهم على الإطلاق','1163','393','0');
INSERT INTO [t184] VALUES (430,'السابع والثلاثون بعد المائتين: أنه لو كان ظاهر الكتاب مخالفا لصريح المعقول لكان في الصدور أعظم حرج منه وضيق','1166','393','0');
INSERT INTO [t184] VALUES (431,'الثامن والثلاثون بعد المائتين: أن جماع ما يرد به المبطلون لما ثبت عن رسول الله - صلى الله عليه وسلم - نوعان: أحدهما: منع دلالة ما جاء به على تلك المسألة. والثاني: معارضة الدلالة بما يمنع اتباعها.','1169','393','0');
INSERT INTO [t184] VALUES (432,'التاسع والثلاثون بعد المائتين: أن كل واحد من المنع والمعارضة ينقسم إلى درجات متعددة','1173','393','0');
INSERT INTO [t184] VALUES (433,'الأربعون بعد المائتين: وهو أنه مع التصديق الجازم يمتنع وقوع المعارضة والممانعة، وحيث وجد ذلك فهو ملزوم لانتفاء التصديق','1182','393','0');
INSERT INTO [t184] VALUES (434,'الحادي والأربعون بعد المائتين: أن الله أنزل على عبده ورسوله في أفضل الأيام وأفضل الشهور وأفضل الأماكن ومعه أفضل الخلق {اليوم أكملت لكم دينكم وأتممت عليكم نعمتي ورضيت لكم الإسلام دينا}','1183','393','0');
INSERT INTO [t184] VALUES (435,'الوجه الثاني والأربعون بعد المائتين: أن المعارضة بين العقل والنقل هي أصل كل فساد في العالم، وهي ضد دعوة الرسل من كل وجه','1185','393','0');
INSERT INTO [t184] VALUES (436,'طرق الناس في الأجوبة عن أسولة إبليس الفاسدة','1191','257','0');
INSERT INTO [t184] VALUES (437,'جميع ما يشاهد من مخلوقات الله إذا تأملها صحيح التأمل والنظر وجدها مؤسسة على غاية الحكمة مغشاة بالحكمة','1209','257','0');
INSERT INTO [t184] VALUES (438,'آخر الموجود من الكتاب','1217','0','0');
