INSERT INTO [t18612] VALUES (834,'من رحمته تعالى بالمؤمنين ابتلاؤهم بالأوامر والنواهي، وأن نغص عليهم الدنيا لئلا يسكنوا إليها، وأن حذرهم نفسه لئلا يغتروا به','958','133','0');
INSERT INTO [t18612] VALUES (835,'فصل: ضد الهدى والرحمة: الضلال والغضب، ولذلك أمرنا الله أن نسأله كل يوم مرات الهداية إلى صراط الذين أنعم عليهم وأن يجنبنا طريق المغضوب عليهم والضالين','959','133','0');
INSERT INTO [t18612] VALUES (836,'فصل: «كل حي إنما يعمل لما فيه تنعمه ولذته','960','133','0');
INSERT INTO [t18612] VALUES (837,'الأعمال التي يعملها ابن آدم إما أن يتخذها دينا أو لا، والدين إما حق وإما باطل، والنعيم التام في الدين الحق علما وعملا','960','133','0');
INSERT INTO [t18612] VALUES (838,'ما يصيب كثيرا من المؤمنين من المصائب وكثيرا من الكفار والفساق من الرياسة والمال وغير ذلك','961','133','0');
INSERT INTO [t18612] VALUES (839,'ظن بعض الناس أن ما وعد الله من العزة والنصرة والفلاح للمؤمنين هو في الآخرة فقط','962','133','0');
INSERT INTO [t18612] VALUES (840,'من يعلل ما ينال المؤمن من المصائب في الدنيا ومن لا يعلل','962','133','0');
INSERT INTO [t18612] VALUES (841,'من هؤلاء من يتهم الرب سبحانه بما لا يصدر إلا من عدو','962','133','0');
INSERT INTO [t18612] VALUES (842,'ما كان يقول الجهم بن صفوان مما ينفي به الحكمة والرحمة عن الله','962','133','0');
INSERT INTO [t18612] VALUES (843,'قول بعض كبار الضلال: «ما على الخلق أضر من الخالق»','963','133','0');
INSERT INTO [t18612] VALUES (844,'قولهم: إذا أطعته وتبت إليه نكد علي عيشي','963','133','0');
INSERT INTO [t18612] VALUES (845,'وهذا ناشئ من حسن ظن العبد بنفسه ومن اعتقاد أن الله لا يؤيد صاحب الحق ولا ينصره','963','133','0');
INSERT INTO [t18612] VALUES (846,'العبد وإن آمن بالآخرة لابد له من الدنيا','964','133','0');
INSERT INTO [t18612] VALUES (847,'حديث: «بادروا بالأعمال فتنا كقطع الليل المظلم. . .» الحديث','964','133','0');
INSERT INTO [t18612] VALUES (848,'إذا اعتقد أن الدين الكامل لا يحصل إلا بفساد الدنيا لم يقدم على طلبه','964','133','0');
INSERT INTO [t18612] VALUES (849,'أصل هذه الفتنة ناشئ من جهل حقيقة الدين، وجهل حقيقة النعيم','965','133','0');
INSERT INTO [t18612] VALUES (850,'كمال العبد إنما يحصل بمعرفة النعيم الذي يطلبه والعمل الذي يوصل إليه','965','133','0');
INSERT INTO [t18612] VALUES (851,'ما يكون من جهل العبد بأمر الله ودينه وبوعده ووعيده من الفتنة','966','133','0');
INSERT INTO [t18612] VALUES (852,'كثيرا ما يترك العبد واجبات لتقصيره في العلم','966','133','0');
INSERT INTO [t18612] VALUES (853,'قد يترك واجبات القلوب التي هي آكد من واجبات البدن','966','133','0');
INSERT INTO [t18612] VALUES (854,'ما أكثر من يتعبد الله بترك ما أوجب عليه وهذا من أمقت خلق الله إلى الله','966','133','0');
INSERT INTO [t18612] VALUES (855,'ما أكثر من يتعبد الله بما حرمه عليه ويعتقد أنه طاعة، وهو شر ممن يعتقده معصية ويفعله','967','133','0');
INSERT INTO [t18612] VALUES (856,'ما أكثر من يعتقد أنه مظلوم ومحق من كل وجه، ولا يكون في الحقيقة كذلك','967','133','0');
INSERT INTO [t18612] VALUES (857,'أكثر ديانات الخلق إنما هي عادات أخذوها عن الآباء والأجداد','967','133','0');
INSERT INTO [t18612] VALUES (858,'إنما ضمن الله نصر وليه القائم بدينه علما وعملا، ولم يضمن نصر الباطل وإن اعتقد صاحبه أنه حق','967','133','0');
INSERT INTO [t18612] VALUES (859,'مذهب أهل السنة: أن الإيمان يزيد وينقص','968','133','0');
INSERT INTO [t18612] VALUES (860,'ولاية الله ومعيته الخاصة ونصره الكامل إنما هو لأهل الإيمان الكامل','969','133','0');
INSERT INTO [t18612] VALUES (861,'وبما تقدم يزول الإشكال الوارد في قوله تعالى: {ولن يجعل الله للكافرين على المؤمنين سبيلا}','969','133','0');
INSERT INTO [t18612] VALUES (862,'والتحقيق أن المنفي هو السبيل الكامل عن أهل الإيمان الكامل','969','133','0');
INSERT INTO [t18612] VALUES (863,'فصل: المقام الثاني الذي وقع فيه الغلط ظن كثير من الناس أن أهل الدين والحق يكونون في الدنيا أذلاء، وهذا من عدم الوثوق بوعد الله، ومن سوء الفهم لكتابه','970','133','0');
INSERT INTO [t18612] VALUES (864,'قد بين الله في كتابه أنه ناصر المؤمنين في الدنيا والآخرة','970','133','0');
INSERT INTO [t18612] VALUES (865,'ما أصاب العبد من مصيبة فبذنوبه','970','133','0');
INSERT INTO [t18612] VALUES (866,'قد ذم الله من يطلب النصرة والعزة من غير المؤمنين بقوله في سورة المائدة: {ياأيها الذين آمنوا لا تتخذوا اليهود والنصارى أولياء} الآيات','971','133','0');
INSERT INTO [t18612] VALUES (867,'ونظيره قوله في سورة النساء: {بشر المنافقين بأن لهم عذابا أليما} وما بعدها','971','133','0');
INSERT INTO [t18612] VALUES (868,'قول عبد الله بن أبي المنافق: {لئن رجعنا إلى المدينة. . .} الآية','971','133','0');
INSERT INTO [t18612] VALUES (869,'قوله تعالى في سورة فاطر: {من كان يريد العزة فلله العزة جميعا}','971','133','0');
INSERT INTO [t18612] VALUES (870,'قوله: {هو الذي أرسل رسوله بالهدى ودين الحق. . .} الآية','971','133','0');
INSERT INTO [t18612] VALUES (871,'قوله في سورة الصف: {ياأيها الذين آمنوا هل أدلكم على تجارة تنجيكم من عذاب أليم. . .} الآيات','972','133','0');
INSERT INTO [t18612] VALUES (872,'قوله تعالى للمسيح في سورة آل عمران: {إني متوفيك ورافعك إلي. . .} الآية','972','133','0');
INSERT INTO [t18612] VALUES (873,'لما كان للنصارى نصيب من عيسى كانوا فوق اليهود','972','133','0');
INSERT INTO [t18612] VALUES (874,'قوله تعالى للمؤمنين في سورة الفتح: {ولو قاتلكم الذين كفروا لولوا الأدبار. . .} الآية','972','133','0');
INSERT INTO [t18612] VALUES (875,'قوله: {العاقبة للمتقين}','972','133','0');
INSERT INTO [t18612] VALUES (876,'قوله في سورة آل عمران: {وإن تصبروا وتتقوا}','973','133','0');
INSERT INTO [t18612] VALUES (877,'قوله إخبارا عن يوسف: {إنه من يتق ويصبر. . .} الآية','973','133','0');
INSERT INTO [t18612] VALUES (878,'قوله في سورة الأنفال: {ياأيها الذين آمنوا إن تتقوا الله يجعل لكم فرقانا}','973','133','0');
INSERT INTO [t18612] VALUES (879,'قوله في سورة الطلاق: {ومن يتق الله يجعل له مخرجا (٢) ويرزقه من حيث لا يحتسب. . .}','973','133','0');
INSERT INTO [t18612] VALUES (880,'قول النبي -صلى الله عليه وسلم-: «لو عمل الناس كلهم بهذه الآية لوسعتهم»','974','133','0');
INSERT INTO [t18612] VALUES (881,'الآيات الواردة في المقام الثاني، وهو أن كل مصيبة تصيب العبد بذنوبه','974','133','0');
INSERT INTO [t18612] VALUES (882,'قوله تعالى في قصة أحد في سورة آل عمران: {أولما أصابتكم مصيبة قد أصبتم مثليها} الآية','974','133','0');
INSERT INTO [t18612] VALUES (883,'قوله في سورة آل عمران: {إن الذين تولوا منكم يوم التقى الجمعان}','974','133','0');
INSERT INTO [t18612] VALUES (884,'قوله في سورة الشورى: {وما أصابكم من مصيبة فبما كسبت أيديكم}','974','133','0');
INSERT INTO [t18612] VALUES (885,'قوله في سورة الروم: {ظهر الفساد في البر والبحر بما كسبت أيدي الناس}','974','133','0');
INSERT INTO [t18612] VALUES (886,'قوله في سورة الشورى: {وإنا إذا أذقنا الإنسان منا رحمة فرح بها} الآية','974','133','0');
INSERT INTO [t18612] VALUES (887,'قوله في سورة الروم: {وإذا أذقنا الناس رحمة فرحوا بها} الآية','974','133','0');
INSERT INTO [t18612] VALUES (888,'قوله في سورة الشورى: {أو يوبقهن بما كسبوا} الآية','975','133','0');
INSERT INTO [t18612] VALUES (889,'قوله في سورة النساء: {ما أصابك من حسنة فمن الله وما أصابك من سيئة فمن نفسك}','975','133','0');
INSERT INTO [t18612] VALUES (890,'ولهذا أمر الله رسوله وأتباعه باتباع ما أنزل إليه وطاعته، وهو المقدمة الأولى وأمر بانتظار وعده، وهو المقدمة الثانية وأمر بالاستغفار والصبر','975','133','0');
INSERT INTO [t18612] VALUES (891,'قد ذكر الله قصص أنبيائه وكيف نجاهم بالصبر والطاعة، وجعل فيهم العبرة','975','133','0');
INSERT INTO [t18612] VALUES (892,'فصل: في أصول نافعة يتبين بها هذا المقام','975','133','0');
INSERT INTO [t18612] VALUES (893,'الأصل الأول: الواقع شاهد أن ما يصيب المؤمنين من المحن دون ما يصيب الكفار','975','133','0');
INSERT INTO [t18612] VALUES (894,'الأصل الثاني: ما يصيب المؤمنين مقرون بالرضا والاحتساب، والكفار لا رضا عندهم ولا احتساب','975','133','0');
INSERT INTO [t18612] VALUES (895,'الأصل الثالث: أذى المؤمن محمول عنه بحسب ما في قلبه من حقائق الإيمان','976','133','0');
INSERT INTO [t18612] VALUES (896,'الأصل الرابع: كلما تمكنت المحبة في القلب كان أذى المحب في رضا محبوبه مستحلى','976','133','0');
INSERT INTO [t18612] VALUES (897,'الأصل الخامس: باطن ما ينال الكافر والمنافق من العز والجاه: ذل وهوان','976','133','0');
INSERT INTO [t18612] VALUES (898,'قول الحسن: «إنهم وإن هملجت بهم البغال وطقطقت بهم. . .» إلخ','977','133','0');
INSERT INTO [t18612] VALUES (899,'الأصل السادس: ابتلاء المؤمن كالدواء له','977','133','0');
INSERT INTO [t18612] VALUES (900,'حديث: «لا يقضي الله للمؤمن قضاء إلا كان خيرا له. . .» الحديث','977','133','0');
INSERT INTO [t18612] VALUES (901,'الأصل السابع: ما يصيب المؤمن أمر لابد منه كالحر والبرد لازم للطبيعة والنشأة الإنسانية في هذه الدار حتى للأطفال والبهائم لما اقتضته حكمة أحكم الحاكمين','977','133','0');
INSERT INTO [t18612] VALUES (902,'لو تجرد الخير في هذا العالم عن الشر لكان عالما غير هذا العالم','978','133','0');
INSERT INTO [t18612] VALUES (903,'الأصل الثامن: في ابتلاء المؤمنين بغلبة عدوهم لهم وقهرهم: حكم عظيمة','978','133','0');
INSERT INTO [t18612] VALUES (904,'منها: استخراج عبوديتهم لله بالذل والانكسار والسؤال','978','133','0');
INSERT INTO [t18612] VALUES (905,'ومنها: لو كانوا دائما منصورين لدخل معهم من ليس قصده الدين','979','133','0');
INSERT INTO [t18612] VALUES (906,'ومنها: أن الله يحب من عباده تكميل عبوديتهم على السراء والضراء في العافية والبلاء','979','133','0');
INSERT INTO [t18612] VALUES (907,'ومنها: أن امتحانهم يمحصهم ويهذبهم، كما حصل يوم أحد وما جاء فيها من الآيات (١٣٩ ــ ١٤٤ من سورة آل عمران)','979','133','0');
INSERT INTO [t18612] VALUES (908,'بيان ما في هذه الآيات من مقاصد','980','133','0');
INSERT INTO [t18612] VALUES (909,'الأصل التاسع: إنما خلق الله السموات والأرض والموت والحياة لابتلاء عباده','981','133','0');
INSERT INTO [t18612] VALUES (910,'قوله تعالى في سورة هود: {وهو الذي خلق السماوات والأرض في ستة أيام} إلخ','981','133','0');
INSERT INTO [t18612] VALUES (911,'قوله في سورة الكهف: {لنبلوهم أيهم أحسن عملا}','981','133','0');
INSERT INTO [t18612] VALUES (912,'قوله في سورة الملك: {ليبلوكم أيكم أحسن عملا}','981','133','0');
INSERT INTO [t18612] VALUES (913,'قوله في سورة الأنبياء: {ونبلوكم بالشر والخير فتنة}','981','133','0');
INSERT INTO [t18612] VALUES (914,'قوله في سورة محمد: {ولنبلونكم حتى نعلم المجاهدين منكم والصابرين ونبلو أخباركم}','981','133','0');
INSERT INTO [t18612] VALUES (915,'قوله في سورة العنكبوت: {ولقد فتنا الذين من قبلهم} الآية ومعناها','981','133','0');
INSERT INTO [t18612] VALUES (916,'قوله في سورة الأحزاب: {ولما رأى المؤمنون الأحزاب قالوا هذا ما وعدنا الله ورسوله}','982','133','0');
INSERT INTO [t18612] VALUES (917,'امتحان الكافر في الآخرة بالعذاب','982','133','0');
INSERT INTO [t18612] VALUES (918,'المؤمنون أخف فتنة من الكافر والفاجر','982','133','0');
INSERT INTO [t18612] VALUES (919,'لابد من حصول الألم والمحنة لكل نفس','982','133','0');
INSERT INTO [t18612] VALUES (920,'الأصل العاشر: الإنسان مدني بالطبع لابد له من مخالطة الناس وموافقتهم أو مخالفتهم في أهوائهم واعتقاداتهم، ولابد في ذلك من ألم وعذاب','982','133','0');
INSERT INTO [t18612] VALUES (921,'اعتبر هذا بمن يطلبون موافقته على الظلم والزور','983','133','0');
INSERT INTO [t18612] VALUES (922,'ألم يسير يعقب لذة عظيمة أولى بالاحتمال','983','133','0');
INSERT INTO [t18612] VALUES (923,'الأصل الحادي عشر: البلاء الذي يصيب العبد في الله إما في نفسه أو في ماله، أو في عرضه، أو في أهله ومن يحب','983','133','0');
INSERT INTO [t18612] VALUES (924,'أشد هذه الأقسام: المصيبة في النفس وغاية ذلك الاستشهاد في سبيل الله وتلك أشرف الموتات وأسهلها وأفضلها عقبى','983','133','0');
INSERT INTO [t18612] VALUES (925,'قول الله تعالى: {قل لن ينفعكم الفرار إن فررتم من الموت أو القتل. . .}','984','133','0');
INSERT INTO [t18612] VALUES (926,'{قل من ذا الذي يعصمكم من الله إن أراد بكم سوءا أو أراد بكم رحمة}','984','133','0');
INSERT INTO [t18612] VALUES (927,'إذا كان هذا في مصيبة النفس فمصيبة المال والعرض كذلك','984','133','0');
INSERT INTO [t18612] VALUES (928,'من رفه بدنه وعرضه وآثر راحته على التعب لله أتعبه الله أضعاف ذلك','985','133','0');
INSERT INTO [t18612] VALUES (929,'قول أبي حازم: «لما يلقى العبد الذي لا يتقي الله من معالجة الخلق. . .» إلخ','985','133','0');
INSERT INTO [t18612] VALUES (930,'امتنع إبليس عن ذل سجدة فصار خادما لأهل الفسوق والعصيان','985','133','0');
INSERT INTO [t18612] VALUES (931,'أنف عباد الأصنام أن يعبدوا إلها واحدا ورضوا أن يعبدوا آلهة من الأحجار','985','133','0');
INSERT INTO [t18612] VALUES (932,'كل من امتنع أن يذل لله أو يبذل ماله في مرضاته لابد أن يذل للحقير ويبذل ماله في مرضاته','985','133','0');
INSERT INTO [t18612] VALUES (933,'فصل: محبة الله والأنس به والشوق إلى لقائه والرضى عنه وبه: أصل الدين، كما أن معرفته بأسمائه وصفاته أجل علوم الدين','985','133','0');
INSERT INTO [t18612] VALUES (934,'قول الله لرسوله: {ثم أوحينا إليك أن اتبع ملة إبراهيم حنيفا}','986','133','0');
INSERT INTO [t18612] VALUES (935,'وصية النبي -صلى الله عليه وسلم- أصحابه أن يقولوا عند الصباح: «أصبحنا على فطرة الإسلام. . .» الحديث، وهي حقيقة شهادة أن لا إله إلا الله','986','133','0');
INSERT INTO [t18612] VALUES (936,'محبة الرسول تابعة لمحبة الله، ولا يكون الإيمان إلا بها، فما الظن بمحبة الله','987','133','0');
INSERT INTO [t18612] VALUES (937,'ما خلقت الجن والإنس، ولا أرسلت الرسل، ولا أسست الجنة والنار، إلا لأجل محبته','987','133','0');
INSERT INTO [t18612] VALUES (938,'الله سبحانه كلما خفته أنست به بخلاف المخلوق','987','133','0');
INSERT INTO [t18612] VALUES (939,'محبة المخلوق إذا لم تكن لله فهي عذاب للمحب ووبال','988','133','0');
INSERT INTO [t18612] VALUES (940,'شأن محبة الله غير شأن محبة المخلوق، فمحبته نعيم النفوس وحياة الأرواح','988','133','0');
INSERT INTO [t18612] VALUES (941,'الحلاوة التي يجدها المؤمن بمحبته الله فوق كل حلاوة','988','133','0');
INSERT INTO [t18612] VALUES (942,'قول بعضهم: «إنه ليمر بالقلب أوقات أقول فيها: إن كان أهل الجنة في مثل هذا .. » إلخ','988','133','0');
INSERT INTO [t18612] VALUES (943,'قول آخر: «إنه ليمر بالقلب أوقات يهتز فيها طربا بأنسه بالله»','988','133','0');
INSERT INTO [t18612] VALUES (944,'قول آخر: «مساكين أهل الغفلة خرجوا من الدنيا وما ذاقوا أطيب ما فيها»','989','133','0');
INSERT INTO [t18612] VALUES (945,'قول آخر: «لو علم الملوك وأبناؤهم ما نحن فيه لجالدونا بالسيوف عليه»','989','133','0');
INSERT INTO [t18612] VALUES (946,'وجدان ذلك بحسب قوة المعرفة بالمحبوب وأسمائه وصفاته','989','133','0');
INSERT INTO [t18612] VALUES (947,'القلب لا يفلح ولا ينعم ولا يسكن إلا بعبادة ربه وحده وحبه','989','133','0');
INSERT INTO [t18612] VALUES (948,'في القلب فقر ذاتي إلى ربه من حيث هو معبوده ومحبوبه، ومن حيث هو ربه وخالقه ورازقه','990','133','0');
INSERT INTO [t18612] VALUES (949,'من لم يحقق المحبة لله على أتم معانيها، لم يحقق شهادة أن لا إله إلا الله','990','133','0');
INSERT INTO [t18612] VALUES (950,'من لم يستعن بالله ويتوكل عليه فلا طريق له إلى هذه المحبة','990','133','0');
INSERT INTO [t18612] VALUES (951,'لذة المعصية وشهوتها تستر لذة الحلاوة الإيمانية أو تنقصها أو تذهبها','991','133','0');
