INSERT INTO [t18612] VALUES (967,'فصل: وأما كيده للأبوين فمناهما بالخلود في الجنة، وحلف أنه ناصح، فجرت عليهما المحنة ثم تداركهما الله، فعلمهما: {ربنا ظلمنا أنفسنا وإن لم تغفر لنا وترحمنا لنكونن من الخاسرين}','995','133','0');
INSERT INTO [t18612] VALUES (968,'ظن اللعين أن الله يتخلى عن صفيه وحبيبه','995','133','0');
INSERT INTO [t18612] VALUES (969,'بلي العدو بالذنب فأصر وعارض، ولم يسأل الإقالة ولا ندم. وبلي الحبيب بالذنب فاعترف وندم، وتضرع، وفزع إلى التوحيد والاستغفار','996','133','0');
INSERT INTO [t18612] VALUES (970,'فصل: ثم كاد أحد ولدي آدم حتى قتل أخاه','996','133','0');
INSERT INTO [t18612] VALUES (971,'حديث: «ما من نفس تقتل ظلما إلا كان على ابن آدم الأول كفل من دمها»','996','133','0');
