INSERT INTO [t18612] VALUES (983,'ما روى الكلبي أن أولاد شيث كانوا يأتون جسد آدم في المغارة التي دفنوه فيها من أرض الهند ويعظمونه. وأن رجلا من بني قابيل نحت صنما لبني قابيل','1000','133','0');
