INSERT INTO [t18612] VALUES (985,'كانت هذه الأصنام عملت على عهد يرد بن مهلائيل، ثم بعد القرن الثالث عظمت وعبدت فبعث الله إليهم إدريس فكذبوه','1000','133','0');
