INSERT INTO [t18612] VALUES (1001,'قول الكلبي: إنه لم يكن أحد من ولد إسماعيل يظعن من مكة إلا حمل معه حجرا من الحرم يعظمه ويطوف به حيث كان مع تعظيمهم للبيت وحجه، ثم عبدوا ما استحسنوا من الأوثان ونسوا دين إبراهيم، واستخرجوا ما كان يعبد قوم نوح','1005','133','0');
INSERT INTO [t18612] VALUES (1002,'تلبية نزار: لبيك لا شريك لك إلا شريك هو لك، تملكه وما ملك','1005','133','0');
INSERT INTO [t18612] VALUES (1003,'كان عمرو بن لحي أول من سيب السوائب وبحر البحيرة وحمى الحامي، وهو الذي انتزع الكعبة من جرهم ونفاهم عن مكة','1005','133','0');
