INSERT INTO [t18612] VALUES (1074,'أباح الله لرسوله وأتباعه دماءهم وأموالهم ونساءهم وأبناءهم','1020','1059','0');
INSERT INTO [t18612] VALUES (1075,'فصل: من أسباب عبادة الأصنام: الغلو في المخلوق','1020','133','0');
INSERT INTO [t18612] VALUES (1076,'الله تعالى ينهى أن يجعل غيره ندا له ومثلا، لا أن يشبه هو بغيره','1020','1075','0');
INSERT INTO [t18612] VALUES (1077,'كل مشرك فهو مشبه لإلهه ومعبوده بالله سبحانه، وإن لم يشبهه به من كل وجه','1021','1075','0');
INSERT INTO [t18612] VALUES (1078,'وصف اليهود الله سبحانه بالنقائص والعيوب','1021','1075','0');
INSERT INTO [t18612] VALUES (1079,'قول اليهود: {إن الله فقير} و {يد الله مغلولة}','1021','1075','0');
INSERT INTO [t18612] VALUES (1080,'وصف الله بالاستراحة من خلق العالم وأن له صاحبة وولدا من أبطل الباطل','1021','1075','0');
INSERT INTO [t18612] VALUES (1081,'الذين يقولون من أهل الكلام: إنه لا يقوم دليل عقلي على انتفاء النقائص والعيوب عن الله لا يقدرون على الرد على من اتخذ له الصاحبة والولد، فاستروح بعضهم إلى دليل الإجماع، وأدلته عندهم ظنية','1021','1075','0');
INSERT INTO [t18612] VALUES (1082,'أهل السنة يقولون: إن تنزيهه سبحانه عن النقائص والعيوب واجب لذاته كما أن صفات الحمد والكمال واجبة لذاته','1022','1075','0');
INSERT INTO [t18612] VALUES (1083,'نفى أهل الكلام ما أثبتته الرسل من صفات الله، وزعموا أنه يستلزم التجسيم وجاؤوا إلى ما علم بالفطر والاضطرار العقلي من تنزيه الله عن النقص فقالوا: ليس في أدلة العقل ما ينفيه','1022','1075','0');
INSERT INTO [t18612] VALUES (1084,'لم يكن في الأمم من جعل المخلوق أصلا ثم شبه الله به','1022','1075','0');
INSERT INTO [t18612] VALUES (1085,'أهل الكلام أعرضوا عن بيان أصل عبادة الأصنام وهو تشبيه أوثانهم بالله في الإلهية','1023','1075','0');
