INSERT INTO [t18612] VALUES (1108,'نهي النبي -صلى الله عليه وسلم- أن يسجد أحد لمخلوق أو يحلف به، أو يصلي إلى قبره، أو يتخذ قبره مسجدا، أو يعلق عليه قنديل','1029','133','0');
