INSERT INTO [t18612] VALUES (1168,'كفرهم بأصل الدين الذي جاءت به الرسل، وهما عبادة الله وحده، واتباع رسله فيما جاؤوا به من عند الله','1054','133','0');
INSERT INTO [t18612] VALUES (1169,'رد إمام الحنفاء إبراهيم على الصابئة في عبادة الكواكب ومحاجته لهم','1055','133','0');
