INSERT INTO [t18612] VALUES (1305,'تقديسهم الصليب بمزاعم باطلة والرد عليهم من عدة وجوه','1111','133','0');
INSERT INTO [t18612] VALUES (1306,'وأما تلاعبه بهم في صلاتهم فمن وجوه','1113','133','0');
INSERT INTO [t18612] VALUES (1307,'تغطية المطارنة والأساقفة فساد هذا الدين بما اخترعوا من الحيل والصور في الحيطان بالألوان الجميلة والأعياد، وأنواع الموسيقى، وساعدهم على ترويجه غلظة اليهود وقسوتهم','1114','133','0');
INSERT INTO [t18612] VALUES (1308,'لما رأى النصارى الصحابة وما هم عليه آمن أكثرهم وقالوا: ما الذين صحبوا عيسى بأفضل من هؤلاء','1115','133','0');
INSERT INTO [t18612] VALUES (1309,'فصل: في ذكر تلاعب الشيطان بالأمة الغضبية وهم اليهود','1116','133','0');
INSERT INTO [t18612] VALUES (1310,'الآيات والأحاديث في غضب الله على اليهود','1116','133','0');
INSERT INTO [t18612] VALUES (1311,'حديث: «اليهود مغضوب عليهم والنصارى ضالون»','1116','133','0');
INSERT INTO [t18612] VALUES (1312,'تلاعب الشيطان بهم في حياة موسى إذ قال له: {اجعل لنا إلها كما لهم آلهة} بعد مجاوزتهم البحر وإغراق فرعون وقومه','1116','133','0');
INSERT INTO [t18612] VALUES (1313,'حديث ذات أنواط: وقول النبي -صلى الله عليه وسلم-: «قلتم كما قال قوم موسى لموسى .. » إلخ','1117','133','0');
INSERT INTO [t18612] VALUES (1314,'فصل: ما في عبادتهم العجل من لعب الشيطان بهم بعد أن رأوا ما حل بالمشركين، وما في العجل من المحقرات التي تجعل عابده أحقر خلق الله','1117','133','0');
INSERT INTO [t18612] VALUES (1315,'معنى قول الله في قصة العجل والسامري: {هذا إلهكم وإله موسى فنسي}','1118','133','0');
INSERT INTO [t18612] VALUES (1316,'رواية السدي في اتخاذ العجل وسببه','1118','133','0');
INSERT INTO [t18612] VALUES (1317,'معنى قوله تعالى: {فقبضت قبضة من أثر الرسول}','1120','133','0');
INSERT INTO [t18612] VALUES (1318,'رواية ابن إسحاق في قصة العجل والسامري','1122','133','0');
INSERT INTO [t18612] VALUES (1319,'لم يعتب الله على موسى في إلقاء الألواح لأن الذي حمله عليه الغضب لله','1123','133','0');
INSERT INTO [t18612] VALUES (1320,'فصل: تلاعب الشيطان بهم في قولهم لموسى: {لن نؤمن لك حتى نرى الله جهرة} وتفسير ابن جرير لها','1123','133','0');
INSERT INTO [t18612] VALUES (1321,'رواية ابن إسحاق في هذه القصة','1124','133','0');
INSERT INTO [t18612] VALUES (1322,'معنى قول موسى: {لو شئت أهلكتهم من قبل وإياي} وقوله: {أتهلكنا بما فعل السفهاء منا}','1125','133','0');
INSERT INTO [t18612] VALUES (1323,'فصل: من تلاعبه بهم حين قيل لهم: {وادخلوا الباب سجدا وقولوا حطة}','1127','133','0');
INSERT INTO [t18612] VALUES (1324,'حديث البخاري ومسلم عن أبي هريرة عن النبي -صلى الله عليه وسلم-: «فقدموا فدخلوا يزحفون على أستاههم»','1129','133','0');
INSERT INTO [t18612] VALUES (1325,'الطاعون بالرصد لكل من بدل دين الله','1130','133','0');
INSERT INTO [t18612] VALUES (1326,'فصل: ومن تلاعبه بهم: طلبهم البصل والثوم والعدس، واستبدالهم الذي هو أدنى بالذي هو خير','1130','133','0');
INSERT INTO [t18612] VALUES (1327,'فضل المن والسلوى على غيرهما من الأغذية والأشربة','1131','133','0');
INSERT INTO [t18612] VALUES (1328,'كانوا مع ذلك يتفجر لهم من الحجر اثنا عشر عينا من الماء','1131','133','0');
INSERT INTO [t18612] VALUES (1329,'فصل: ومن تلاعبه بهم: أنهم لم يقبلوا التوراة حتى رفع الجبل فوق رؤوسهم','1131','133','0');
INSERT INTO [t18612] VALUES (1330,'رواية ابن زيد والسدي في هذه القصة','1131','133','0');
INSERT INTO [t18612] VALUES (1331,'فصل: ومن تلاعبه بهم حين أمرهم الله أن يدخلوا القرية التي كتب الله لهم وبشرهم بها قالوا لموسى: {فاذهب أنت وربك فقاتلا إنا هاهنا قاعدون}','1132','133','0');
INSERT INTO [t18612] VALUES (1332,'ما في خطاب موسى لهم من التلطف والتذكير بنعم الله، وما في قولهم من المعصية والامتناع والجبن','1133','133','0');
INSERT INTO [t18612] VALUES (1333,'الرجلان اللذان أنعم الله عليهما، وممن كانا؟ أمن قوم موسى، أم من الجبارين؟','1134','133','0');
INSERT INTO [t18612] VALUES (1334,'قول الأنصار لرسول الله -صلى الله عليه وسلم- في غزوة بدر: «لا نقول لك كما قال قوم موسى لموسى: {فاذهب أنت وربك فقاتلا إنا هاهنا قاعدون}، ولكنا نقاتل عن يمينك وعن شمالك وبين يديك ومن خلفك','1134','133','0');
INSERT INTO [t18612] VALUES (1335,'فصل: ومن تلاعبه بهم قصة القتيل الذي تدارؤوا فيه والبقرة وما في هذه القصة من أنواع العبر','1135','133','0');
INSERT INTO [t18612] VALUES (1336,'لا ينبغي مقابلة أمر الله بالتعنت وكثرة الأسئلة','1136','133','0');
INSERT INTO [t18612] VALUES (1337,'لو أنهم ذبحوا اي بقرة لكانت إياها، ولكن شددوا فشدد عليهم','1136','133','0');
INSERT INTO [t18612] VALUES (1338,'مقابلة أمر الله بالإنكار: نوع من الكفر','1136','133','0');
INSERT INTO [t18612] VALUES (1339,'بحث للإمام ابن جرير فيما يستفاد من قصة البقرة، وحال بني إسرائيل','1136','133','0');
INSERT INTO [t18612] VALUES (1340,'من أقبح ظلمهم وجهلهم قولهم لموسى: {الآن جئت بالحق}','1137','133','0');
INSERT INTO [t18612] VALUES (1341,'فصل: ومن العبر في قصة البقرة الإخبار عن قساوة قلوبهم وغلظها','1137','133','0');
INSERT INTO [t18612] VALUES (1342,'الظاهر أن هذه القصة بعد قصة العجل','1138','133','0');
INSERT INTO [t18612] VALUES (1343,'فصل: ومن تلاعبه بهم ما قص الله من صيد السمك','1138','133','0');
INSERT INTO [t18612] VALUES (1344,'من قصة أصحاب السبت الذين مسخهم قردة لما تحيلوا على استحلال ما حرم الله','1138','133','0');
INSERT INTO [t18612] VALUES (1345,'الحرص على الشيء يوجب الحرمان منه','1138','133','0');
INSERT INTO [t18612] VALUES (1346,'فصل: ومن تلاعبه بهم: إذابتهم الشحوم وبيعها وأكل ثمنها، وقد حرمها الله عليهم','1139','133','0');
INSERT INTO [t18612] VALUES (1347,'اتخاذهم قبور أنبيائهم مساجد، ولعنهم على ذلك','1139','133','0');
INSERT INTO [t18612] VALUES (1348,'كانوا يقتلون الأنبياء ويتخذون أحبارهم أربابا من دون الله','1140','133','0');
INSERT INTO [t18612] VALUES (1349,'حديث عدي بن حاتم في معنى قوله تعالى: {اتخذوا أحبارهم ورهبانهم أربابا من دون الله}','1140','133','0');
INSERT INTO [t18612] VALUES (1350,'قتلهم زكريا ويحيى حتى سلط الله عليهم بختنصر وسنجاريب','1140','133','0');
INSERT INTO [t18612] VALUES (1351,'ما كان منهم في شأن عيسى وأمه ورميهما بالعظائم وهم يعلمون أنه رسول الله، ثم محاولتهم قتله وصلبه','1141','133','0');
INSERT INTO [t18612] VALUES (1352,'لم يزل أمرهم في سفال حتى قطعهم الله في الأرض أمما ومزقهم كل ممزق','1141','133','0');
INSERT INTO [t18612] VALUES (1353,'لما بعث الله محمدا -صلى الله عليه وسلم- كفروا به، فأتم الله عليهم غضبه، وألزمهم الذل والصغار حتى ينزل عيسى آخر الزمان فيطهر الأرض منهم','1141','133','0');
INSERT INTO [t18612] VALUES (1354,'فصل: ومن تلاعب الشيطان بهم: دعواهم أن الله محجور عليه النسخ في الشرائع، وأن يفعل ما يشاء ويحكم ما يريد','1141','133','0');
INSERT INTO [t18612] VALUES (1355,'جعلهم هذه الضلالة ترسا لهم في جحد نبوة محمد -صلى الله عليه وسلم-','1142','133','0');
INSERT INTO [t18612] VALUES (1356,'قد أكذبهم الله في نص التوراة، كما أكذبهم في القرآن','1142','133','0');
INSERT INTO [t18612] VALUES (1357,'آيات {كل الطعام كان حلا لبني إسرائيل} إلخ تضمنت بيان كذبهم صريحا في إبطال النسخ','1142','133','0');
INSERT INTO [t18612] VALUES (1358,'الاستدلال بهذه الآيات على إبطال دعوى اليهود في النسخ لم يحم حوله أكثر المفسرين','1143','133','0');
INSERT INTO [t18612] VALUES (1359,'التوراة نسخت ما قبلها من الشرائع، فما يمنع أن ينسخها غيرها بعدها','1143','133','0');
INSERT INTO [t18612] VALUES (1360,'إلزامهم جواز النسخ ووقوعه بما هم عليه من أحكام في الطهارة والنجاسة خالفوا بها ما كان عليه موسى وخلفاؤه','1144','133','0');
INSERT INTO [t18612] VALUES (1361,'فصل: قالت الأمة الغضبية: لم تأت التوراة بإباحة محظور، والنسخ الذي ننكره هو ما أباح محظورا، وجوابهم على ذلك','1146','133','0');
INSERT INTO [t18612] VALUES (1362,'نسخ التحريم للمصلحة كنسخ التحليل للمصلحة سواء','1146','133','0');
INSERT INTO [t18612] VALUES (1363,'إلزامهم نبوة المسيح ومحمد -صلى الله عليه وسلم-','1147','133','0');
INSERT INTO [t18612] VALUES (1364,'لو كان الشيء يحرم لعينه لحرم على جميع الأنبياء والأمم، وليس السبت ونحوه محرما على نوح وإبراهيم','1148','133','0');
INSERT INTO [t18612] VALUES (1365,'من العجب أن تحجر هذه الأمة الغضبية النسخ على الله، ثم تبيح لأحبارها أن يبطلوا من شرائع التوارة ما يشاؤون','1149','133','0');
INSERT INTO [t18612] VALUES (1366,'أمثلة مما غيره الأحبار من شرائع التوراة في الصلاة والصيام','1149','133','0');
INSERT INTO [t18612] VALUES (1367,'ومن تلاعب الشيطان بهم: زعمهم أن الفقهاء إذا أحلوا الشيء صار حلالا، وإذا حرموه صار حراما','1150','133','0');
INSERT INTO [t18612] VALUES (1368,'فصل: ومن تلاعب الشيطان بهم: ما شددوه على أنفسهم في باب الذبائح وغيرها مما ليس في التوراة','1151','133','0');
INSERT INTO [t18612] VALUES (1369,'كتابا المشنا والتلمود','1151','133','0');
INSERT INTO [t18612] VALUES (1370,'التلمود ألف في عدة عصور من فتاوى الأحبار، وهو مقدار حمل بغل','1152','133','0');
INSERT INTO [t18612] VALUES (1371,'تحريمهم في هذين الكتابين بعض مطاعم غير اليهود وذبائحهم ومناكحتهم حتى لا يختلطوا بالأمم الآخرين','1152','133','0');
INSERT INTO [t18612] VALUES (1372,'اختلاق الأحبار في الذبائح كتابا سموه: «هلكت شحيطا» وما فيه من شروط الذبيحة','1153','133','0');
INSERT INTO [t18612] VALUES (1373,'إن كانت رئة الذبيحة مثقوبة، أو قلبها ملتصقا إلى الظهر أو أحد الجانبين ولو بعرق دقيق كانت عندهم طريفا، أي نجسة','1153','133','0');
INSERT INTO [t18612] VALUES (1374,'الطريفا في التوراة هي ما يفترسه السبع والدليل على ذلك من التوراة','1153','133','0');
INSERT INTO [t18612] VALUES (1375,'سبب تحريم الفريسة على بني إسرائيل','1154','133','0');
INSERT INTO [t18612] VALUES (1376,'تعدي مشايخهم في هذه الطريفا إلى هذيانات تتعلق بالقلب والرئة ونحوها','1154','133','0');
INSERT INTO [t18612] VALUES (1377,'اليهود القراؤون يبرأون من المشنا والتلمود ويصفون مؤلفيهم بأنهم كذابون أهل حماقات ودعاوى كاذبة يدعون أنهم يوحى إليهم، وأن الوحي يوقفهم على الحق ويسمعونه','1155','133','0');
INSERT INTO [t18612] VALUES (1378,'اطراح القرائين ما افتراه الحاخاميم ونسبوه إلى التوراة','1155','133','0');
INSERT INTO [t18612] VALUES (1379,'الفرقة الثانية: الربانون وهم أصحاب القياس، وفيهم الحاخاميم الكذابون المفترون وهم أشد اليهود عداوة لغيرهم بما بث الحاخاميم في نفوسهم من الكراهية للأمم','1156','133','0');
INSERT INTO [t18612] VALUES (1380,'وإنما صنع الحاخاميم ذلك بهم لأغراض ومنافع لهم في ذلك','1156','133','0');
INSERT INTO [t18612] VALUES (1381,'كلما كان الحاخام أكثر تكلفا وأشد إصرا قالوا: هذا العالم الرباني','1156','133','0');
INSERT INTO [t18612] VALUES (1382,'من الأسباب التي دعتهم إلى التشديد والتضييق: أنهم مبددون في شرق الأرض وغربها، فإذا قدم عليهم رجل من أهل دينهم من بلاد بعيدة يظهر لهم الخشونة والمبالغة في الدين، لينال الكرامة والمنزلة عندهم','1156','133','0');
INSERT INTO [t18612] VALUES (1383,'هم أبدا يعتقدون الصواب والحق مع من يشدد ويضيق','1157','133','0');
INSERT INTO [t18612] VALUES (1384,'فصل: ومن تلاعب الشيطان بهم: أنهم يطلبون التخلص بأنواع الحيل مما يأمرهم الله به وينهاهم عنه','1157','133','0');
INSERT INTO [t18612] VALUES (1385,'إلزامهم الأخ أن يتزوج امرأة أخيه الميت عنها بلا عقب، ثم احتيالهم على الخروج من ذلك بما هو أشنع الحيل وأقبحها','1157','133','0');
INSERT INTO [t18612] VALUES (1386,'احتيالهم ومكرهم بالنبي -صلى الله عليه وسلم-، والله يحفظه ويقيه شرهم','1159','133','0');
INSERT INTO [t18612] VALUES (1387,'مكر اليهود، وخيانتهم للنبي -صلى الله عليه وسلم- ولأتباعه','1159','133','0');
INSERT INTO [t18612] VALUES (1388,'اليهود أجبن الناس وأذلهم','1161','133','0');
INSERT INTO [t18612] VALUES (1389,'تمثيلهم أنفسهم بعناقيد العنب وغيرهم بالشوك','1161','133','0');
INSERT INTO [t18612] VALUES (1390,'انتظارهم قائما يعيد لهم مجد إسرائيل من ولد داود','1161','133','0');
INSERT INTO [t18612] VALUES (1391,'هم في الحقيقة إنما ينتظرون المسيح الدجال','1162','133','0');
INSERT INTO [t18612] VALUES (1392,'الأمم الثلاثة تنتظر منتظرا يخرج في آخر الزمان، والمسلمون ينتظرون عيسى ابن مريم عليه السلام يقتل اليهود والخنزير ويكسر الصليب','1162','133','0');
INSERT INTO [t18612] VALUES (1393,'فصل: قولهم لله: كم تنام يا رب، استيقظ من رقدتك!','1162','133','0');
INSERT INTO [t18612] VALUES (1394,'نسبتهم الندم والبكاء ورمد العين إلى الله تعالى','1163','133','0');
INSERT INTO [t18612] VALUES (1395,'قولهم: إن الله استنشق رائحة قتار شواء قربان نوح فقال: لن أعاود لعنة الأرض','1163','133','0');
INSERT INTO [t18612] VALUES (1396,'قولهم: إن الله استراح بعد خلق السموات والأرض','1163','133','0');
INSERT INTO [t18612] VALUES (1397,'قولهم للنبي -صلى الله عليه وسلم- نحو ذلك وقول الله له: {فاصبر على ما يقولون}','1164','133','0');
INSERT INTO [t18612] VALUES (1398,'قولهم: إن الله فقير ونحن أغنياء، ويد الله مغلولة غلت أيديهم','1164','133','0');
INSERT INTO [t18612] VALUES (1399,'صلاتهم في العشر الأول من الشهر الأول، ويقولون فيها: لا يكون الملك لله إلا إذا عادت الدولة لبني إسرائيل','1165','133','0');
INSERT INTO [t18612] VALUES (1400,'فصل: ومن تلاعب الشيطان بهم قدحهم في الأنبياء، وأذيتهم لهم','1165','133','0');
INSERT INTO [t18612] VALUES (1401,'أذيتهم لموسى في حياته وشتمه بأنه آدر، وحديث البخاري في قصة اغتساله وعدو الحجر بثوبه حتى قام على بني إسرائيل عريانا فبرأه الله','1165','133','0');
INSERT INTO [t18612] VALUES (1402,'أذيتهم لعيسى عليه السلام ولأمه','1167','133','0');
INSERT INTO [t18612] VALUES (1403,'نسبتهم لوطا إلى شرب الخمر والزنا بابنتيه','1167','133','0');
INSERT INTO [t18612] VALUES (1404,'نسبتهم يهوذا بن يعقوب إلى الزنى بزوجة ولده','1168','133','0');
INSERT INTO [t18612] VALUES (1405,'بهتانهم بجعل أولاد المسلمين أولاد زنى','1169','133','0');
INSERT INTO [t18612] VALUES (1406,'بهتانهم بدعوى أن عبد الله بن سلام كان يعلم النبي -صلى الله عليه وسلم-','1170','133','0');
INSERT INTO [t18612] VALUES (1407,'نسبتهم إلى يوسف عليه السلام أنه حل تكة سرواله وجلس من زليخا مجلس الرجل من المرأة، حتى ظهر له يعقوب في الحائط','1170','133','0');
INSERT INTO [t18612] VALUES (1408,'زعمهم أن عيسى كان عالما أو طبيبا وإقامته الحجة عليهم في السبت','1171','133','0');
INSERT INTO [t18612] VALUES (1409,'إلزامهم أن عيسى ابن مريم هو النبي المنتظر','1171','133','0');
INSERT INTO [t18612] VALUES (1410,'فصل: لا يمكن ليهودي ولا نصراني أن يؤمن بنبيه حتى يؤمن بمحمد -صلى الله عليه وسلم-','1173','133','0');
INSERT INTO [t18612] VALUES (1411,'لم يشاهدوا شيئا من معجزات موسى ولا عيسى ولا يعرفون ذلك إلا من القرآن','1173','133','0');
INSERT INTO [t18612] VALUES (1412,'تقليد اليهود والنصارى لآبائهم تقليدا أعمى لا يفيدهم شيئا، ولا يجعل آباءهم أصدق من غيرهم، وكل منهم يكفر الآخر','1173','133','0');
INSERT INTO [t18612] VALUES (1413,'نقض ما استدلوا به من التواتر','1174','133','0');
INSERT INTO [t18612] VALUES (1414,'نبوة محمد -صلى الله عليه وسلم- هي التي تثبت نبوة موسى وعيسى','1177','133','0');
INSERT INTO [t18612] VALUES (1415,'فصل: وقد اختلف أقوال الناس في التوراة التي بأيديهم، هل هي مبدلة، أو مؤولة؟ على ثلاثة أقوال','1178','133','0');
INSERT INTO [t18612] VALUES (1416,'معنى التأويل والتحريف','1178','133','0');
INSERT INTO [t18612] VALUES (1417,'قول طائفة: إن التحريف كان بالتأويل لا في التنزيل، وأدلة ذلك','1178','133','0');
INSERT INTO [t18612] VALUES (1418,'قول الطائفة الثالثة: إن التوراة زيد فيها، وغير ألفاظ يسيرة، ولكن أكثرها باق على ما أنزل عليه، والتبديل في يسير منها جدا وهو اختيار شيخ الإسلام ابن تيمية','1180','133','0');
INSERT INTO [t18612] VALUES (1419,'التحقيق أن الذبيح إسماعيل من عشرة وجوه','1181','133','0');
INSERT INTO [t18612] VALUES (1420,'حديث: «أنا ابن الذبيحين»','1184','133','0');
INSERT INTO [t18612] VALUES (1421,'أحبار اليهود معتقدون أن ما بأيديهم ليس هو التوراة الحقيقية وأدلة ذلك','1185','133','0');
INSERT INTO [t18612] VALUES (1422,'قولهم: إن موسى منع بني إسرائيل التوراة ولم يعطها إلا لأولاد لاوي','1185','133','0');
INSERT INTO [t18612] VALUES (1423,'ضياع التوراة بقتل بختنصر للأئمة الهارونيين يوم غزا بيت المقدس','1186','133','0');
INSERT INTO [t18612] VALUES (1424,'عزير هو الذي جمع هذه التوراة من محفوظاته ومحفوظات الكهنة','1186','133','0');
INSERT INTO [t18612] VALUES (1425,'التوراة في الواقع كتاب عزير وفيها كثير من التوراة المنزلة على موسى','1186','133','0');
INSERT INTO [t18612] VALUES (1426,'لحق التوراة الزيادة والنقصان، واختلاف الترجمة، واختلاف التأويل وسياق أمثلة على ذلك','1186','133','0');
INSERT INTO [t18612] VALUES (1427,'المثال الأول: تحريفهم نص: «لحم فريسة في الصحراء. . .» إلخ','1187','1426','0');
INSERT INTO [t18612] VALUES (1428,'المثال الثاني: تحريفهم نص: «نبيا أقيم لهم .. » إلخ الذي فيه البشارة بنبوة محمد -صلى الله عليه وسلم-','1187','1426','0');
INSERT INTO [t18612] VALUES (1429,'المثال الثالث: تحريفهم نص: «جاء الله من طور سيناء وأشرق نوره من سيعير واستعلى من جبال فاران»','1189','1426','0');
INSERT INTO [t18612] VALUES (1430,'فصل: ومما يدل على غلظ أفهام هذه الأمة: أنهم يحرمون طبخ لحم الجدي بلبن أمه، لعدم فهمهم للنص','1190','133','0');
INSERT INTO [t18612] VALUES (1431,'فصل: ولا يستبعد اصطلاح كافة هذه الأمة على المحال، لأن دولتهم انقرضت، وتتابعت عليهم الغارات','1191','133','0');
INSERT INTO [t18612] VALUES (1432,'لم يلق اليهود من أمة من العدل والرحمة ما لقوا من المسلمين','1191','133','0');
INSERT INTO [t18612] VALUES (1433,'أعز ما صادفه الإسلام من هذه الأمة يهود خيبر والمدينة','1192','133','0');
INSERT INTO [t18612] VALUES (1434,'كان يهود قريظة والنضير يستفتحون بالنبي -صلى الله عليه وسلم- على العرب والأوس والخزرج','1192','133','0');
INSERT INTO [t18612] VALUES (1435,'فلما هاجر النبي -صلى الله عليه وسلم- وجاءهم ما عرفوه من آياته كفروا به وسبقهم العرب (الأوس والخزرج) إلى الإيمان به','1192','133','0');
INSERT INTO [t18612] VALUES (1436,'أشد ما كان على اليهود من ملوكهم العصاة الذين كانوا يقتلون الأنبياء ويعبدون الأصنام','1192','133','0');
INSERT INTO [t18612] VALUES (1437,'استعبد الفرس اليهود ومنعوهم عن أعمال دينهم كالختان وغيره','1192','133','0');
INSERT INTO [t18612] VALUES (1438,'منع الفرس اليهود عن الصلاة، لأنهم يدعون فيها على الأمم بالدمار والخراب','1192','133','0');
INSERT INTO [t18612] VALUES (1439,'ابتداعهم الحزانة بدل الصلاة','1193','133','0');
INSERT INTO [t18612] VALUES (1440,'الحزانة ينوحون فيها ويبكون على أنفسهم ويوقعونها على الموسيقى ويجتمعون لها جماعة يترنمون بها','1193','133','0');
