INSERT INTO [t19188] VALUES (1,'مقدمة التحقيق','4','0','0');
INSERT INTO [t19188] VALUES (2,'[١ - المقنع لابن قدامة]','4','1','0');
INSERT INTO [t19188] VALUES (3,'[٢ - الشرح الكبير لابن أبي عمر]','5','1','0');
INSERT INTO [t19188] VALUES (4,'[٣ - الإنصاف للمرداوي]','13','1','0');
INSERT INTO [t19188] VALUES (5,'[٤ - تحقيق الكتب الثلاثة]','21','1','0');
INSERT INTO [t19188] VALUES (6,'[مخطوطات المقنع]','22','5','0');
INSERT INTO [t19188] VALUES (7,'[مخطوطات الشرح الكبير]','23','5','0');
INSERT INTO [t19188] VALUES (8,'[مخطوطات الإنصاف]','32','5','0');
INSERT INTO [t19188] VALUES (9,'[منهج التحقيق]','35','5','0');
INSERT INTO [t19188] VALUES (10,'[مقدمة المصنف]','37','0','0');
INSERT INTO [t19188] VALUES (11,'كتاب الطهارة','40','0','0');
INSERT INTO [t19188] VALUES (12,'باب المياه','41','11','0');
INSERT INTO [t19188] VALUES (13,'١ - مسألة؛ قال: (وهو الباقي على أصل خلقته)','43','12','0');
INSERT INTO [t19188] VALUES (14,'٢ - مسألة؛ قال: (وما تغير بمكثه)','45','12','0');
INSERT INTO [t19188] VALUES (15,'٣ - مسألة؛ قال: (أو بطاهر لا يمكن صونه عنه كالطحلب وورق الشجر)','46','12','0');
INSERT INTO [t19188] VALUES (16,'٤ - مسألة؛ قال: (أو لا يخالطه، كالعود والكافور والدهن)','46','12','0');
INSERT INTO [t19188] VALUES (17,'٥ - مسألة؛ قال: (أو ما أصله الماء، كالملح البحري)','47','12','0');
INSERT INTO [t19188] VALUES (18,'٦ - مسألة؛ قال: (أو ما تروح بريح ميتة إلى جانبه)','48','12','0');
INSERT INTO [t19188] VALUES (19,'٧ - مسألة؛ قال: (أو بطاهر)','49','12','0');
INSERT INTO [t19188] VALUES (20,'٨ - مسألة؛ قال: (وإن سخن بنجاسة، فهل يكره استعماله، على روايتين)','50','12','0');
INSERT INTO [t19188] VALUES (21,'٩ - مسألة: (فإن غير أحد أوصافه، لونه أو طعمه أو ريحه)','56','12','0');
INSERT INTO [t19188] VALUES (22,'١٠ - مسألة، قال: (أو استعمل في رفع حدث، أو طهارة مشروعة، كالتجديد، وغسل الجمعة)','60','12','0');
INSERT INTO [t19188] VALUES (23,'١١ - مسألة؛ قال: (أو غمس فيه يده قائم من نوم الليل قبل غسلها ثلاثا، فهل يسلبه طهوريته؟ على روايتين)','66','12','0');
INSERT INTO [t19188] VALUES (24,'١٢ - مسألة؛ قال: (وإن أزيلت به النجاسة، فانفصل متغيرا، أو قبل زوالها، فهو نجس)','78','12','0');
INSERT INTO [t19188] VALUES (25,'١٣ - مسألة؛ قال: (وإن انفصل غير متغير بعد زوالها، فهو طاهر)','79','12','0');
INSERT INTO [t19188] VALUES (26,'١٤ - مسألة؛ قال: (وإن كان غير الأرض، فهو طاهر، في أصح الوجهين)','80','12','0');
INSERT INTO [t19188] VALUES (27,'١٥ - مسألة؛ قال: (وإن خلت بالطهارة منه امرأة، فهو طهور)','81','12','0');
INSERT INTO [t19188] VALUES (28,'١٦ - مسألة؛ قال: (وإن كان كثيرا، فهو طاهر)','91','12','0');
INSERT INTO [t19188] VALUES (29,'١٧ - مسألة؛ قال: (إلا أن تكون النجاسة بولا، أو عذرة مائعة، ففيه روايتان؛ إحداهما، لا ينجس)','94','12','0');
INSERT INTO [t19188] VALUES (30,'١٨ - مسألة؛ قال: (إلا أن يكون مما لا يمكن نزحه لكثرته، فلا ينجس)','96','12','0');
INSERT INTO [t19188] VALUES (31,'١٩ - مسألة؛ قال: (وإذا انضم إلى الماء النجس ماء طاهر كثير، طهره إن لم يبق فيه تغير، وإن كان الماء النجس كثيرا، فزال تغيره بنفسه، أو بنزح بقي بعده كثير، طهر)','101','12','0');
INSERT INTO [t19188] VALUES (32,'٢٠ - مسألة؛ قال: (فإن كوثر بماء يسير، أو بغير الماء كالتراب ونحوه، فأزال التغير، لم يطهر)','105','12','0');
INSERT INTO [t19188] VALUES (33,'٢١ - مسألة؛ قال: (والكثير ما بلغ قلتين، واليسير ما دونهما)','109','12','0');
INSERT INTO [t19188] VALUES (34,'٢٢ - مسألة؛ قال: (وهما خمسمائة رطل بالعراقي)','110','12','0');
INSERT INTO [t19188] VALUES (35,'٢٣ - مسألة؛ قال: (وهل ذلك تقريب أو تحديد؟ على وجهين)','112','12','0');
INSERT INTO [t19188] VALUES (36,'٢٤ - مسألة؛ قال: (وإذا شك في نجاسة الماء، أو كان نجسا فشك في طهارته، بنى على اليقين)','118','12','0');
INSERT INTO [t19188] VALUES (37,'٢٥ - مسألة؛ قال: (وإن اشتبه الماء الطاهر بالنجس، لم يتحر','119','12','0');
INSERT INTO [t19188] VALUES (38,'٢٦ - مسألة؛ قال: (وهل يشترط إراقتهما أو خلطهما؟ فيه روايتان)','124','12','0');
INSERT INTO [t19188] VALUES (39,'٢٧ - مسألة؛ قال: (وإن اشتبه طهور بطاهر، توضأ من كل واحد منهما وصلى صلاة واحدة)','126','12','0');
INSERT INTO [t19188] VALUES (40,'٢٨ - مسألة؛ قال: (وإن اشتبهت ثياب طاهرة بنجسة، صلى في كل ثوب صلاة بعدد النجس، وزاد صلاة)','128','12','0');
INSERT INTO [t19188] VALUES (41,'باب الآنية','132','11','0');
INSERT INTO [t19188] VALUES (42,'٢٩ - مسألة؛ قال: (إلا آنية الذهب والفضة، والمضبب بهما، فإنه يحرم اتخاذها واستعمالها على الرجال والنساء)','134','41','0');
INSERT INTO [t19188] VALUES (43,'٣٠ - مسألة؛ قال: (فإن توضأ منهما أو اغتسل، فهل تصح طهارته؟ على وجهين)','137','41','0');
INSERT INTO [t19188] VALUES (44,'٣١ - مسألة، قال: (إلا أن تكون الضبة يسيرة من الفضة، كتشعيب القدح، فلا بأس بها إذا لم يباشرها بالاستعمال)','139','41','0');
INSERT INTO [t19188] VALUES (45,'٣٢ - مسألة؛ قال: (وثياب الكفار وأوانيهم طاهرة مباحة الاستعمال، ما لم تعلم نجاستها)','143','41','0');
INSERT INTO [t19188] VALUES (46,'٣٣ - مسألة، قال: (ولا يطهر جلد الميتة بالدباغ)','149','41','0');
INSERT INTO [t19188] VALUES (47,'٣٤ - مسألة؛ قال: (وهل يجوز استعماله في اليابسات بعد الدبغ؟ على روايتين)','152','41','0');
INSERT INTO [t19188] VALUES (48,'٣٥ - مسألة؛ قال: (وعنه: يطهر منها جلد ما كان طاهرا حال الحياة)','154','41','0');
INSERT INTO [t19188] VALUES (49,'٣٦ - مسألة؛ قال: (ولا يطهر جلد غير المأكول بالذكاة)','159','41','0');
INSERT INTO [t19188] VALUES (50,'٣٧ - مسألة؛ قال: (وابن الميتة نجس؛ لأنه مائع في وعاء نجس فتنجس به، وكذلك إنفحتها في ظاهر المذهب)','163','41','0');
INSERT INTO [t19188] VALUES (51,'٣٨ - مسألة؛ قال: (وعظمها وقرنها وظفرها نجس)','165','41','0');
INSERT INTO [t19188] VALUES (52,'٣٩ - مسألة؛ قال: (وصوفها وشعرها وريشها طاهر)','168','41','0');
INSERT INTO [t19188] VALUES (53,'باب الاستنجاء','174','11','0');
INSERT INTO [t19188] VALUES (54,'٤٠ - مسألة؛ قال، رحمه الله: (يستحب لمن أراد دخول الخلاء، أن يقول: بسم الله)','174','53','0');
INSERT INTO [t19188] VALUES (55,'٤١ - مسألة؛ قال، رحمه الله: (ولا يدخله بشيء فيه ذكر الله','175','53','0');
INSERT INTO [t19188] VALUES (56,'٤٢ - مسألة؛ قال: (ويقدم رجله اليسرى في الدخول، واليمنى في الخروج)','177','53','0');
INSERT INTO [t19188] VALUES (57,'٤٣ - مسألة: (ويعتمد على رجله اليسرى)','178','53','0');
INSERT INTO [t19188] VALUES (58,'٤٤ - مسألة؛ قال، رحمه الله: (ولا يتكلم)','178','53','0');
INSERT INTO [t19188] VALUES (59,'٤٥ - مسألة؛ قال: (ولا يلبث فوق حاجته)','180','53','0');
INSERT INTO [t19188] VALUES (60,'٤٦ - مسألة؛ قال: (فإذا خرج قال: غفرانك، الحمد لله الذي أذهب عني الأذى وعافاني)','181','53','0');
INSERT INTO [t19188] VALUES (61,'٤٧ - مسألة؛ قال: (وإن كان في الفضاء أبعد)','182','53','0');
INSERT INTO [t19188] VALUES (62,'٤٨ - مسألة؛ قال: (واستتر وارتاد مكانا رخوا)','182','53','0');
INSERT INTO [t19188] VALUES (63,'٤٩ - مسألة؛ قال: (ولا يبول في شق، ولا سرب، ولا طريق، ولا ظل نافع، ولا تحت شجرة مثمرة)','184','53','0');
INSERT INTO [t19188] VALUES (64,'٥٠ - مسألة؛ قال: (ولا يستقبل الشمس، ولا القمر)','188','53','0');
INSERT INTO [t19188] VALUES (65,'٥١ - مسألة؛ قال: (ولا يجوز أن يستقبل القبلة في الفضاء)','189','53','0');
INSERT INTO [t19188] VALUES (66,'٥٢ - مسألة: (وفي: استدبارها فيه، واستقبالها في البنيان، روايتان)','190','53','0');
INSERT INTO [t19188] VALUES (67,'٥٣ - مسألة؛ قال: (فإذا فرغ مسح بيده اليسرى من أصل ذكره إلى رأسه، ثم ينتره ثلاثا)','193','53','0');
INSERT INTO [t19188] VALUES (68,'٥٤ - مسألة؛ قال: (ولا يمس ذكره بيمينه، ولا يستجمر بها)','194','53','0');
INSERT INTO [t19188] VALUES (69,'٥٥ - مسألة: (ثم يتحول عن موضعه؛ لئلا يتنجس بالخارج منه، ثم يستجمر، ثم يستنجي بالماء)','197','53','0');
INSERT INTO [t19188] VALUES (70,'٥٦ - مسألة؛ قال: (ويجزئه أحدهما)','198','53','0');
INSERT INTO [t19188] VALUES (71,'٥٧ - مسألة، قال: (إلا أن يعدو الخارج موضع العادة، فلا يجزئ إلا الماء)','200','53','0');
INSERT INTO [t19188] VALUES (72,'٥٨ - مسألة؛ قال: (ويجوز الاستجمار بكل طاهر ينقي، كالحجر، ونحوه الخشب والخرق)','207','53','0');
INSERT INTO [t19188] VALUES (73,'٥٩ - مسألة؛ قال: (إلا الروث والعظام والطعام، وما له حرمة، وما يتصل بحيوان)','210','53','0');
INSERT INTO [t19188] VALUES (74,'٦٠ - مسألة: (ولا يجزئ أقل من ثلاث مسحات، إما بحجر ذي شعب أو بثلاثة)','212','53','0');
INSERT INTO [t19188] VALUES (75,'٦١ - مسألة؛ قال: (فإن لم ينق بها، زاد حتى ينقى)','215','53','0');
INSERT INTO [t19188] VALUES (76,'٦٢ - مسألة؛ قال: (ويقطع على وتر)','215','53','0');
INSERT INTO [t19188] VALUES (77,'٦٣ - مسألة؛ قال: (ويجب الاستنجاء من كل خارج إلا الريح)','217','53','0');
INSERT INTO [t19188] VALUES (78,'٦٤ - مسألة؛ قال: (فإن توضأ قبله، فهل يصح وضوءه؟ على روايتين)','220','53','0');
INSERT INTO [t19188] VALUES (79,'باب السواك، وسنة الوضوء','223','11','0');
INSERT INTO [t19188] VALUES (80,'٦٥ - مسألة؛ قال: (والسواك مسنون في جميع الأوقات)','223','79','0');
INSERT INTO [t19188] VALUES (81,'٦٦ - مسألة؛ قال: (إلا للصائم بعد الزوال، فلا يستحب)','224','79','0');
INSERT INTO [t19188] VALUES (82,'٦٧ - مسألة؛ قال: (ويتأكد استحبابه في ثلاثة مواضع؛ عند الصلاة)','228','79','0');
INSERT INTO [t19188] VALUES (83,'٦٨ - مسألة: (ويستاك بعود لين ينقي الفم، ولا يجرحه، ولا يضره، ولا يتفتت فيه)','230','79','0');
INSERT INTO [t19188] VALUES (84,'٦٩ - مسألة: (فإن استاك بأصبعه، أو خرقة، فهل يصيب السنة؟ على وجهين)','231','79','0');
INSERT INTO [t19188] VALUES (85,'٧٠ - مسألة: (ويستاك عرضا، ويدهن غبا، ويكتحل وترا)','232','79','0');
INSERT INTO [t19188] VALUES (86,'٧١ - مسألة: (ويكره القزع، وهو حلق بعض الرأس)','250','79','0');
INSERT INTO [t19188] VALUES (87,'٧٢ - مسألة؛ قال: (ويجب الختان، ما لم يخفه على نفسه)','250','79','0');
INSERT INTO [t19188] VALUES (88,'٧٣ - مسألة: (ويتيامن في سواكه وطهوره وانتعاله، ودخوله المسجد)','256','79','0');
INSERT INTO [t19188] VALUES (89,'٧٤ - مسألة: (وسنن الوضوء عشر؛ السواك)','257','79','0');
INSERT INTO [t19188] VALUES (90,'٧٥ - مسألة، قال: (وغسل الكفين، إلا أن يكون قائما من نوم الليل، ففي وجوبه روايتان)','261','79','0');
INSERT INTO [t19188] VALUES (91,'٧٦ - مسألة؛ قال: (والبداية بالمضمضة والاستنشاق، والمبالغة فيهما، إلا أن يكون صائما)','264','79','0');
INSERT INTO [t19188] VALUES (92,'٧٧ - مسألة؛ قال: (وتخليل اللحية، وهو سنة)','267','79','0');
INSERT INTO [t19188] VALUES (93,'٧٨ - مسألة: (وتخليل الأصابع)','269','79','0');
INSERT INTO [t19188] VALUES (94,'٧٩ - مسألة؛ قال: (والتيامن)','270','79','0');
INSERT INTO [t19188] VALUES (95,'٨٠ - مسألة؛ قال: (وأخذ ماء جديد للأذنين)','271','79','0');
INSERT INTO [t19188] VALUES (96,'٨١ - مسألة؛ قال: (والغسلة الثانية والثالثة)','273','79','0');
INSERT INTO [t19188] VALUES (97,'باب فرض الوضوء، وصفته','275','11','0');
INSERT INTO [t19188] VALUES (98,'٨٢ - مسألة؛ قال: (والفم والأنف منه)','275','97','0');
INSERT INTO [t19188] VALUES (99,'٨٣ - مسألة؛ قال: (وغسل اليدين)','275','97','0');
INSERT INTO [t19188] VALUES (100,'٨٤ - مسألة؛ قال: (ومسح الرأس)','275','97','0');
INSERT INTO [t19188] VALUES (101,'٨٥ - مسألة؛ قال: (والترتيب على ما ذكر الله تعالى)','280','97','0');
INSERT INTO [t19188] VALUES (102,'٨٦ - مسألة؛ قال: (والموالاة على إحدى الروايتين)','284','97','0');
INSERT INTO [t19188] VALUES (103,'٨٧ - مسألة؛ قال: (وهو أن لا يؤخر غسل عضو حتى ينشف الذي قبله)','286','97','0');
INSERT INTO [t19188] VALUES (104,'٨٨ - مسألة؛ قال: (والنية شرط لطهارة الحدث كلها)','288','97','0');
INSERT INTO [t19188] VALUES (105,'٨٩ - مسألة؛ قال: (وهي أن يقصد رفع الحدث أو الطهارة لما لا يباح إلا بها)','291','97','0');
INSERT INTO [t19188] VALUES (106,'٩٠ - مسألة؛ قال: (فإن نوى ما تسن له الطهارة، أو التجديد، فهل يرتفع حدثه؟ على روايتين)','293','97','0');
INSERT INTO [t19188] VALUES (107,'٩١ - مسألة: (وإن نوى غسلا مسنونا، فهل يجزئ عن الواجب؟ على وجهين)','297','97','0');
INSERT INTO [t19188] VALUES (108,'٩٢ - مسألة: (وإن اجتمعت أحداث توجب الوضوء أو الغسل، فنوى بطهارته أحدها، فهل يرتفع سائرها؟ على وجهين)','298','97','0');
INSERT INTO [t19188] VALUES (109,'٩٣ - مسألة: (ويجب تقديم النية على أول واجبات الطهارة)','300','97','0');
INSERT INTO [t19188] VALUES (110,'٩٤ - مسألة: (واستصحاب ذكرها في جميعها، وإن استصحب حكمها أجزأه)','301','97','0');
INSERT INTO [t19188] VALUES (111,'٩٥ - مسألة؛ قال: (وهما واجبان في الطهارتين. وعنه: أن','307','97','0');
INSERT INTO [t19188] VALUES (112,'٩٦ - مسألة؛ قال: (ويغسل وجهه ثلاثا','311','97','0');
INSERT INTO [t19188] VALUES (113,'٩٧ - مسألة: (فإن كان فيه شعر خفيف يصف البشرة، وجب غسلها معه. وإن كان يسترها، أجزأه غسل ظاهره. ويستحب تخليله)','318','97','0');
INSERT INTO [t19188] VALUES (114,'٩٨ - مسألة: (ثم يغسل يديه إلى المرفقين ثلاثا، ويدخل المرفقين في الغسل)','321','97','0');
INSERT INTO [t19188] VALUES (115,'٩٩ - مسألة: (ثم يمسح رأسه)','326','97','0');
INSERT INTO [t19188] VALUES (116,'١٠٠ - مسألة: (فيبدأ بيديه من [مقدم رأسه]','327','97','0');
INSERT INTO [t19188] VALUES (117,'١٠١ - مسألة: (ويجب مسح جميعه مع الأذنين، وعنه: يجزئ مسح أكثره)','330','97','0');
INSERT INTO [t19188] VALUES (118,'١٠٢ - مسألة: (ولا يستحب تكراره، وعنه، يستحب)','340','97','0');
INSERT INTO [t19188] VALUES (119,'١٠٣ - مسألة: (ثم يغسل رجليه إلى الكعبين ثلاثا، ويدخلهما في الغسل)','343','97','0');
INSERT INTO [t19188] VALUES (120,'١٠٤ - مسألة (ويخلل أصابعهما)','344','97','0');
INSERT INTO [t19188] VALUES (121,'١٠٥ - مسألة: (فإن كان أقطع، غسل ما بقي من محل الفرض)','345','97','0');
INSERT INTO [t19188] VALUES (122,'١٠٦ - مسألة: (فإن لم يبق شيء، سقط)','345','97','0');
INSERT INTO [t19188] VALUES (123,'١٠٧ - مسألة: (ثم يرفع نظره إلى السماء، ويقول: أشهد أن لا إله إلا الله، وحده لا شريك له، وأشهد أن محمدا عبده ورسوله)','347','97','0');
INSERT INTO [t19188] VALUES (124,'١٠٨ - مسألة: (وتباح معونته)','350','97','0');
INSERT INTO [t19188] VALUES (125,'١٠٩ - مسألة؛ قال: (ويباح تنشيف أعضائه، ولا يستحب)','351','97','0');
INSERT INTO [t19188] VALUES (126,'باب المسح على الخفين','358','11','0');
INSERT INTO [t19188] VALUES (127,'١١٠ - مسألة: (يجوز المسح على الخفين)','360','126','0');
INSERT INTO [t19188] VALUES (128,'١١١ - مسألة؛ قال: (والعمامة والجبائر)','362','126','0');
INSERT INTO [t19188] VALUES (129,'١١٢ - مسألة: (وفي المسح على القلانس، وخمر النساء المدارة تحت حلوقهن، روايتان)','365','126','0');
INSERT INTO [t19188] VALUES (130,'١١٣ - مسألة؛ قال: (ومن شرطه أن يلبس الجميع بعد كمال الطهارة)','368','126','0');
INSERT INTO [t19188] VALUES (131,'١١٤ - مسألة؛ قال: (إلا الجبيرة، على إحدى الروايتين)','373','126','0');
INSERT INTO [t19188] VALUES (132,'١١٥ - مسألة؛ قال: (ويمسح المقيم يوما وليلة، والمسافر ثلاثة أيام ولياليهن)','377','126','0');
INSERT INTO [t19188] VALUES (133,'١١٦ - مسألة؛ قال: (إلا الجبيرة، فإنه يمسح عليها إلى حلها)','380','126','0');
INSERT INTO [t19188] VALUES (134,'١١٧ - مسألة: (وابتداء المدة من الحدث بعد اللبس. وعنه، من المسح بعده)','381','126','0');
INSERT INTO [t19188] VALUES (135,'١١٨ - مسألة؛ قال: (ومن مسح مسافرا، ثم أقام، أتم مسح مقيم)','382','126','0');
INSERT INTO [t19188] VALUES (136,'١١٩ - مسألة: (وإن مسح مقيما، ثم سافر أوشك في ابتدائه، أتم مسح مقيم. وعنه، يتم مسح مسافر)','383','126','0');
INSERT INTO [t19188] VALUES (137,'١٢٠ - مسألة: (وإن أحدث، ثم سافر قبل المسح، أتم مسح مسافر)','385','126','0');
INSERT INTO [t19188] VALUES (138,'١٢١ - مسألة؛ قال: (ولا يجوز المسح إلا على ما يستر محل الفرض، ويثبت بنفسه)','386','126','0');
INSERT INTO [t19188] VALUES (139,'١٢٢ - مسألة؛ قال: (ويثبت بنفسه)','387','126','0');
INSERT INTO [t19188] VALUES (140,'١٢٣ - مسألة: (فإن كان فيه خرق يبدو منه بعض القدم،','389','126','0');
INSERT INTO [t19188] VALUES (141,'١٢٤ - مسألة: (وإن لبس خفا، فلم يحدث حتى لبس عليه آخر، جاز المسح عليه)','392','126','0');
INSERT INTO [t19188] VALUES (142,'١٢٥ - مسألة: (ويمسح أعلى الخف دون أسفله وعقبه، فيضع يده على الأصابع، ثم يمسح إلى ساقه)','395','126','0');
INSERT INTO [t19188] VALUES (143,'١٢٦ - مسألة: (ويجوز المسح على العمامة المحنكة إذا كانت ساترة لجميع الرأس، إلا ما جرت العادة بكشفه)','399','126','0');
INSERT INTO [t19188] VALUES (144,'١٢٧ - مسألة؛ قال: (ولا يجوز على غير المحنكة، إلا أن تكون ذات ذؤابة، فيجوز في أحد الوجهين)','400','126','0');
INSERT INTO [t19188] VALUES (145,'١٢٨ - مسألة: (ويجزئ مسح أكثرها، وقيل: لا يجزئ إلا مسح جميعها)','403','126','0');
INSERT INTO [t19188] VALUES (146,'١٢٩ - مسألة: (ويمسح على جميع الجبيرة، إذا لم تتجاوز قدر الحاجة)','404','126','0');
INSERT INTO [t19188] VALUES (147,'١٣٠ - مسألة؛ قال: (ومتى ظهر قدم الماسح أو رأسه، أو انقضت مدة المسح، استأنف الطهارة)','406','126','0');
INSERT INTO [t19188] VALUES (148,'١٣١ - مسألة؛ قال: (ولا مدخل لحائل في الطهارة الكبرى إلا الجبيرة)','413','126','0');
INSERT INTO [t19188] VALUES (149,'باب نواقض الوضوء','418','11','0');
INSERT INTO [t19188] VALUES (150,'١٣٢ - مسألة: (الثاني، خروج النجاسات من سائر البدن، فإن','424','149','0');
INSERT INTO [t19188] VALUES (151,'١٣٣ - مسألة؛ قال: (وإن كانت غيرهما، لم ينقض إلا كثيرها، وهو ما فحش في النفس، وحكي عنه أن قليلها ينقض)','426','149','0');
INSERT INTO [t19188] VALUES (152,'١٣٤ - مسألة؛ قال: (الثالث، زوال العقل، إلا النوم اليسير','432','149','0');
INSERT INTO [t19188] VALUES (153,'١٣٥ - مسألة: (الرابع، مس الذكر بيده، ببطن كفه أو بظهره)','439','149','0');
INSERT INTO [t19188] VALUES (154,'١٣٦ - مسألة؛ قال: (ولا ينقض مسه بذراعه)','445','149','0');
INSERT INTO [t19188] VALUES (155,'١٣٧ - مسألة: (وفي مس الذكر المقطوع وجهان)','446','149','0');
INSERT INTO [t19188] VALUES (156,'١٣٨ - مسألة: (وإذا لمس قبل الخنثى المشكل وذكره، انتقض وضوءه. وإن مس أحدهما، لم ينتقض، إلا أن يمس الرجل ذكره لشهوة)','447','149','0');
INSERT INTO [t19188] VALUES (157,'١٣٩ - مسألة: (وفي مس الدبر، ومس المرأة فرجها روايتان)','450','149','0');
INSERT INTO [t19188] VALUES (158,'١٤٠ - مسألة؛ قال: (وعنه: لا ينقض مس الفرج بحال)','451','149','0');
INSERT INTO [t19188] VALUES (159,'١٤١ - مسألة: (الخامس، أن تمس بشرته بشرة أنثى لشهوة. وعنه: لا ينقض. وعنه: ينقض لمسها بكل حال)','452','149','0');
INSERT INTO [t19188] VALUES (160,'١٤٢ - مسألة: (ولا ينقض لمس الشعر والسن والظفر)','459','149','0');
INSERT INTO [t19188] VALUES (161,'١٤٣ - مسألة: (وفي نقض وضوء الملموس روايتان)','460','149','0');
INSERT INTO [t19188] VALUES (162,'١٤٤ - مسألة: (السادس، غسل الميت)','461','149','0');
INSERT INTO [t19188] VALUES (163,'١٤٥ - مسألة: (السابع، أكل لحم الجزور)','462','149','0');
INSERT INTO [t19188] VALUES (164,'١٤٦ - مسألة: (فإن شرب من لبنها، فعلى روايتين)','467','149','0');
INSERT INTO [t19188] VALUES (165,'١٤٧ - مسألة: (وإن أكل من كبدها أو طحالها، فعلى وجهين)','468','149','0');
INSERT INTO [t19188] VALUES (166,'١٤٨ - مسألة: (الثامن، الردة عن الإسلام)','471','149','0');
INSERT INTO [t19188] VALUES (167,'١٤٩ - مسألة: (ومن تيقن الطهارة، وشك في الحدث، أو تيقن الحدث وشك في الطهارة، بنى على اليقين)','476','149','0');
INSERT INTO [t19188] VALUES (168,'١٥٠ - مسألة: (فإن تيقنهما، وشك في السابق منهما، نظر في حاله قبلهما؛ فإن كان متطهرا، فهو محدث، وإن كان محدثا، فهو متطهر)','478','149','0');
INSERT INTO [t19188] VALUES (169,'١٥١ - مسألة؛ قال: (ومن أحدث حرم عليه الصلاة والطواف ومس المصحف)','480','149','0');
INSERT INTO [t19188] VALUES (170,'باب الغسل','488','11','0');
INSERT INTO [t19188] VALUES (171,'١٥٢ - مسألة: (فإن خرج لغير ذلك لم يوجب)','489','170','0');
INSERT INTO [t19188] VALUES (172,'١٥٣ - مسألة: (فإن أحس بانتقاله فأمسك ذكره، فلم يخرج، فعلى روايتين)','495','170','0');
INSERT INTO [t19188] VALUES (173,'١٥٤ - مسألة: (فإن خرج بعد الغسل)','497','170','0');
INSERT INTO [t19188] VALUES (174,'١٥٥ - مسألة: (الثاني: التقاء الختانين، وهو تغييب الحشفة في الفرج، قبلا كان أو دبرا، من آدمي أو بهيمة، حي أو ميت)','500','170','0');
INSERT INTO [t19188] VALUES (175,'١٥٦ - مسألة: (الثالث: إسلام الكافر، أصليا كان أو مرتدا. وقال أبو بكر: لا غسل عليه)','507','170','0');
INSERT INTO [t19188] VALUES (176,'١٥٧ - مسألة: (الرابع، الموت. الخامس، الحيض. السادس، النفاس)','511','170','0');
INSERT INTO [t19188] VALUES (177,'١٥٨ - مسألة؛ قال: (وفي الولادة وجهان)','512','170','0');
INSERT INTO [t19188] VALUES (178,'١٥٩ - مسألة؛ قال: (ومن لزمه الغسل حرم عليه قراءة آية فصاعدا، وفي بعض آية روايتان)','515','170','0');
INSERT INTO [t19188] VALUES (179,'١٦٠ - مسألة: (ويجوز له العبور في المسجد، ويحرم عليه اللبث فيه، إلا أن يتوضأ)','519','170','0');
INSERT INTO [t19188] VALUES (180,'فصل في صفة الغسل','534','170','0');
INSERT INTO [t19188] VALUES (181,'١٦١ - مسألة؛ قال: (ومجزئ وهو أن يغسل ما به من أذى، وينوي، ويعم بدنه بالغسل)','537','170','0');
INSERT INTO [t19188] VALUES (182,'١٦٢ - مسألة؛ قال: (ويتوضأ بالمد، ويغتسل بالصاع، فإن أسبغ بدونهما أجزأه)','550','170','0');
INSERT INTO [t19188] VALUES (183,'١٦٣ - مسألة: (وإذا اغتسل ينوي الطهارتين أجزأ عنهما. وعنه: لا يجزئه حتى يتوضأ)','556','170','0');
INSERT INTO [t19188] VALUES (184,'١٦٤ - مسألة: (ويستحب للجنب إذا أراد النوم أو الأكل أو الوطء ثانيا، أن يغسل فرجه ويتوضأ)','559','170','0');
INSERT INTO [t19188] VALUES (185,'باب التيمم','571','11','0');
INSERT INTO [t19188] VALUES (186,'١٦٥ - مسألة؛ قال: (وهو بدل، لا يجوز إلا بشرطين؛','571','185','0');
INSERT INTO [t19188] VALUES (187,'١٦٦ - مسألة؛ قال: (أو لضرر في استعماله؛ من جرح، أو برد شديد، أو مرض يخشى زيادته أو تطاوله)','578','185','0');
INSERT INTO [t19188] VALUES (188,'١٦٧ - مسألة: (أو عطش يخافه على نفسه، أو رفيقه، أو','581','185','0');
INSERT INTO [t19188] VALUES (189,'١٦٨ - مسألة؛ قال: (أو خشية على [نفسه أو]','585','185','0');
INSERT INTO [t19188] VALUES (190,'١٦٩ - مسألة؛ قال: (أو تعذره إلا بزيادة كثيرة على ثمن مثله، أو ثمن يعجز عن أدائه)','589','185','0');
INSERT INTO [t19188] VALUES (191,'١٧٠ - مسألة: (فإن كان بعض بدنه جريحا، تيمم له وغسل الباقي)','592','185','0');
INSERT INTO [t19188] VALUES (192,'١٧١ - مسألة؛ قال: (وإن وجد فاء يكفي بعض بدنه، لزمه استعماله، وتيمم للباقي إن كان جنبا. وإن كان محدثا، فهل يلزمه استعماله؟ على وجهين)','599','185','0');
INSERT INTO [t19188] VALUES (193,'١٧٢ - مسألة، قال: (ومن عدم الماء لزمه طلبه في رحله، وما','602','185','0');
INSERT INTO [t19188] VALUES (194,'١٧٣ - مسألة: (وإن نسى الماء بموضع يمكنه استعماله، وتيمم، لم يجزئه)','608','185','0');
INSERT INTO [t19188] VALUES (195,'١٧٤ - مسألة: (ويجوز التيمم لجميع الأحداث، وللنجاسة على جرح تضره إزالتها)','610','185','0');
INSERT INTO [t19188] VALUES (196,'١٧٥ - مسألة: (وإن تيمم في الحضر خوفا من البرد وصلى، ففي وجوب الإعادة روايتان)','614','185','0');
INSERT INTO [t19188] VALUES (197,'١٧٦ - مسألة: (فإن عدم الماء والتراب، صلى على حسب حاله)','616','185','0');
INSERT INTO [t19188] VALUES (198,'١٧٧ - مسألة: (ولا يجوز التيمم إلا بتراب طاهر له غبار يعلق باليد)','619','185','0');
INSERT INTO [t19188] VALUES (199,'١٧٨ - مسألة: (فإن خالطه ذو غبار لا يجوز التيمم به، كالجص ونحوه، فهو كالماء إذا خالطته الطاهرات)','625','185','0');
INSERT INTO [t19188] VALUES (200,'١٧٩ - مسألة: (ويجب تعيين النية لما يتيمم له، من حدث أو غيره)','631','185','0');
INSERT INTO [t19188] VALUES (201,'١٨٠ - مسألة: (فإن نوى جميعها، جاز)','633','185','0');
INSERT INTO [t19188] VALUES (202,'١٨١ - مسألة؛ قال: (وإن نوى أحدها، لم يجزئه عن الآخر)','633','185','0');
INSERT INTO [t19188] VALUES (203,'١٨٢ - مسألة: (وإن نوى نفلا، أو أطلق النية للصلاة، لم يصل إلا نفلا)','635','185','0');
INSERT INTO [t19188] VALUES (204,'١٨٣ - مسألة؛ قال: (وإن نوى فرضا فله فعله، والجمع بين الصلاتين، وقضاء الفوائت، والتنفل إلى آخر الوقت)','636','185','0');
INSERT INTO [t19188] VALUES (205,'١٨٤ - مسألة: (ويبطل التيمم بخروج الوقت، ووجود الماء، ومبطلات الوضوء)','642','185','0');
INSERT INTO [t19188] VALUES (206,'١٨٥ - مسألة: (فإن تيمم وعليه ما يجوز المسح عليه، ثم خلعه، لم يبطل تيممه. وقال أصحابنا: يبطل)','647','185','0');
INSERT INTO [t19188] VALUES (207,'١٨٦ - مسألة: (وإن وجد الماء بعد الصلاة، لم تجب إعادتها)','649','185','0');
INSERT INTO [t19188] VALUES (208,'١٨٧ - مسألة: (وإن وجده فيها، بطلت. وعنه، لا تبطل)','650','185','0');
INSERT INTO [t19188] VALUES (209,'١٨٨ - مسألة: (ويستحب تأخير التيمم إلى آخر الوقت، لمن يرجو وجود الماء)','655','185','0');
INSERT INTO [t19188] VALUES (210,'١٨٩ - مسألة: (فإن تيمم في أول الوقت وصلى أجزأه)','657','185','0');
INSERT INTO [t19188] VALUES (211,'١٩٠ - مسألة: (والسنة في التيمم أن ينوي ويسمي ويضرب بيديه مفرجتي الأصابع على التراب ضربة واحدة، فيمسح وجهه بباطن أصابعه وكفيه براحتيه)','658','185','0');
INSERT INTO [t19188] VALUES (212,'١٩١ - مسألة: (ومن حبس في المصر صلى بالتيمم، ولا إعادة عليه)','665','185','0');
INSERT INTO [t19188] VALUES (213,'١٩٢ - مسألة: (ولا يجوز لواجد الماء التيمم خوفا من فوات المكتوبة، ولا الجنازة. وعنه، يجوز للجنازة)','666','185','0');
INSERT INTO [t19188] VALUES (214,'١٩٣ - مسألة: (وإن اجتمع جنب وميت ومن عليها غسل حيض، فبذل ماء يكفي أحدهم، لأولاهم به، فهو للميت. وعنه، أنه للحي. وأيهما يقدم؟ فيه وجهان)','669','185','0');
INSERT INTO [t19188] VALUES (215,'باب إزالة النجاسة','675','11','0');
INSERT INTO [t19188] VALUES (216,'١٩٤ - مسألة: (ويجب غسل نجاسة الكلب والخنزير سبعا، إحداهن بالتراب)','677','215','0');
INSERT INTO [t19188] VALUES (217,'١٩٥ - مسألة: (فإن جعل مكانه أشنانا أو نحوه، فعلى وجهين)','683','215','0');
INSERT INTO [t19188] VALUES (218,'١٩٦ - مسألة: (وفي سائر النجاسات ثلاث روايات؛ إحداهن،','686','215','0');
INSERT INTO [t19188] VALUES (219,'١٩٧ - مسألة: (ولا تطهر الأرض النجسة بشمس ولا ريح)','697','215','0');
INSERT INTO [t19188] VALUES (220,'١٩٨ - مسألة: (ولا يطهر شيء من النجاسات بالاستحالة، إلا الخمرة إذا انقلبت بنفسها)','699','215','0');
INSERT INTO [t19188] VALUES (221,'١٩٩ - مسألة: (فإن خللت لم تطهر)','701','215','0');
INSERT INTO [t19188] VALUES (222,'٢٠٠ - مسألة: (ولا تطهر الأدهان النجسة)','704','215','0');
INSERT INTO [t19188] VALUES (223,'٢٠١ - مسألة: (وإذا خفيت النجاسة، لزمه غسل ما يتيقن به إزالتها)','708','215','0');
INSERT INTO [t19188] VALUES (224,'٢٠٢ - مسألة: (ويجزئ في بول الغلام الذي لم يأكل الطعام، النضح)','710','215','0');
INSERT INTO [t19188] VALUES (225,'٢٠٣ - مسألة: (وإذا تنجس أسفل الخف أو الحذاء، وجب غسله. وعنه: يجزيء دلكه بالأرض. وعنه: يغسل من البول والغائط، ويدلك من غيرهما)','712','215','0');
INSERT INTO [t19188] VALUES (226,'٢٠٤ - مسألة؛ قال: (ولا يعفى عن يسير شيء من النجاسات، إلا الدم، وما تولد منه من القيح والصديد، وأثر الاستنجاء)','717','215','0');
INSERT INTO [t19188] VALUES (227,'٢٠٥ - مسألة: (وعنه، في المذي، والقئ، وريق البغل، والحمار، وسباع البهائم، والطير، وعرقها، وبول الخفاش، والنبيذ، والمني؛ أنه كالدم. وعنه، في المذي؛ أنه يجزئ فيه النضح)','725','215','0');
INSERT INTO [t19188] VALUES (228,'٢٠٦ - مسألة: (ولا ينجس الآدمي بالموت، ولا ما لا نفس له سائلة، كالذباب وغيره)','734','215','0');
INSERT INTO [t19188] VALUES (229,'٢٠٧ - مسألة: (وبول ما يؤكل لحمه، وروثه، ومنيه طاهر. وعنه، أنه نجس)','741','215','0');
INSERT INTO [t19188] VALUES (230,'٢٠٨ - مسألة: (ومني الآدمي طاهر. وعنه، أنه نجس، ويجزئ فرك يابسه)','746','215','0');
INSERT INTO [t19188] VALUES (231,'٢٠٩ - مسألة: (وفي رطوبة فرج المرأة روايتان)','748','215','0');
INSERT INTO [t19188] VALUES (232,'٢١٠ - مسألة: (وسباع البهائم والطير، والبغل، والحمار الأهلي، نجسة. وعنه، أنها طاهرة)','750','215','0');
INSERT INTO [t19188] VALUES (233,'٢١١ - مسألة: (وسؤر الهرة وما دونها في الخلقة طاهر)','754','215','0');
INSERT INTO [t19188] VALUES (234,'باب الحيض','759','11','0');
INSERT INTO [t19188] VALUES (235,'٢١٢ - مسألة؛ قال: (وهو دم طبيعة وجبلة)','759','234','0');
INSERT INTO [t19188] VALUES (236,'٢١٣ - مسألة: (ويمنع عشرة أشياء)','761','234','0');
INSERT INTO [t19188] VALUES (237,'٢١٤ - مسألة: (ويوجب الغسل)','765','234','0');
INSERT INTO [t19188] VALUES (238,'٢١٥ - مسألة: (والنفاس مثله إلا في الاعتداد)','766','234','0');
INSERT INTO [t19188] VALUES (239,'٢١٦ - مسألة: (فإذا انقطع الدم أبيح فعل الصيام، والطلاق، ولم يبح غيرهما حتى تغتسل)','767','234','0');
INSERT INTO [t19188] VALUES (240,'٢١٧ - مسألة: (ويجوز الاستمتاع من الحائض بما دون الفرج)','769','234','0');
INSERT INTO [t19188] VALUES (241,'٢١٨ - مسألة: (فإن وطئها في الفرج، فعليه نصف دينار كفارة. وعنه، ليس عليه إلا التوبة)','772','234','0');
INSERT INTO [t19188] VALUES (242,'٢١٩ - مسألة: (وأقل سن تحيض له المرأة تسع سنين)','779','234','0');
INSERT INTO [t19188] VALUES (243,'٢٢٠ - مسألة؛ قال: (وأكثره خمسون سنة. وعنه: ستون في نساء العرب)','781','234','0');
INSERT INTO [t19188] VALUES (244,'٢٢١ - مسألة: (والحامل لا تحيض)','784','234','0');
INSERT INTO [t19188] VALUES (245,'٢٢٢ - مسألة: (وأقل الحيض يوم وليلة. وعنه: يوم. وأكثره خمسة عشر يوما. وعنه: سبعة عشر)','787','234','0');
INSERT INTO [t19188] VALUES (246,'٢٢٣ - مسألة: (وغالبه ست أو سبع)','790','234','0');
INSERT INTO [t19188] VALUES (247,'٢٢٤ - مسألة: (وأقل الطهر بين الحيضتين ثلاثة عشر يوما)','790','234','0');
INSERT INTO [t19188] VALUES (248,'٢٢٥ - مسألة: (والمبتدأة تجلس يوما وليلة ثم تغتسل وتصلي، فإن انقطع دمها لأكثره فما دون، اغتسلت عند انقطاعه، وتفعل ذلك ثلاثا. فإن كان في الثلاث على قدر واحد، صار عادة وانتقلت إليه، وأعادت ما صامته من الفرض فيه. وعنه: يصير عادة بمرتين)','792','234','0');
INSERT INTO [t19188] VALUES (249,'٢٢٦ - مسألة: (فإن جاوز أكثر الحيض، فهي مستحاضة)','798','234','0');
INSERT INTO [t19188] VALUES (250,'٢٢٧ - مسألة: (فإن كان دمها متميزا؛ بعضه أسود ثخين منتن، وبعضه رقيق أحمر، فحيضها زمن الدم الأسود، وما عداه استحاضة)','798','234','0');
INSERT INTO [t19188] VALUES (251,'٢٢٨ - مسألة: (وذكر أبو الخطاب في المبتدأة أول ما ترى الدم الروايات الأربع)','806','234','0');
INSERT INTO [t19188] VALUES (252,'٢٢٩ - مسألة: (وإن استحيضت المعتادة، رجعت إلى عادتها وإن كانت مميزة. وعنه: يقدم التمييز. وهو اختيار الخرقي)','807','234','0');
INSERT INTO [t19188] VALUES (253,'٢٣٠ - مسألة؛ قال: (وإن نسيت العادة عملت بالتمييز)','814','234','0');
INSERT INTO [t19188] VALUES (254,'٢٣١ - مسألة: (فإن لم يكن لها تمييز جلست غالب الحيض من كل شهر. وعنه: أقله. وقيل: فيها الروايات الأربع)','820','234','0');
INSERT INTO [t19188] VALUES (255,'٢٣٢ - مسألة: (وإن علمت عدد أيامها ونسيت موضعها،','824','234','0');
INSERT INTO [t19188] VALUES (256,'٢٣٣ - مسألة: (وكذلك الحكم في موضع حيض من لا عادة لها ولا تمييز)','826','234','0');
INSERT INTO [t19188] VALUES (257,'٢٣٤ - مسألة: (وإن علمت أيامها في وقت من الشهر، كنصفه الأول، جلستها فيه؛ إما من أوله، أو بالتحري، على اختلاف الوجهين)','826','234','0');
INSERT INTO [t19188] VALUES (258,'٢٣٥ - مسألة: (وإن علمت موضع حيضها ونسيت عدده، جلست فيه غالب الحيض أو أقله، على اختلاف الروايتين)','828','234','0');
INSERT INTO [t19188] VALUES (259,'٢٣٦ - مسألة: (وإن تغيرت العادة، بزيادة أو تقدم أو تأخر أو انتقال، فالمذهب أنها لا تلتفت إلى ما خرج عن العادة حتى يتكرر ثلاثا أو مرتين، على الاختلاف)','829','234','0');
INSERT INTO [t19188] VALUES (260,'٢٣٧ - مسألة: (وإن طهرت في أثناء عادتها، اغتسلت وصلت. فإن عاودها الدم في العادة، فهل تلتفت إليه؟ على روايتين)','835','234','0');
INSERT INTO [t19188] VALUES (261,'٢٣٨ - مسألة، قال: (والصفرة والكدرة في أيام الحيض من الحيض)','842','234','0');
INSERT INTO [t19188] VALUES (262,'٢٣٩ - مسألة: (ومن كانت ترى يوما دما ويوما طهرا، فإنها تضم الدم إلى الدم فيكون حيضا، والباقي طهرا، إلا أن يجاوز أكثر الحيض فتكون مستحاضة)','845','234','0');
INSERT INTO [t19188] VALUES (263,'٢٤٠ - مسألة: (وهل يباح وطء المستحاضة في الفرج من غير خوف العنت؟ على روايتين)','862','234','0');
INSERT INTO [t19188] VALUES (264,'٢٤١ - مسألة؛ قال: (ولا حد لأقله)','866','234','0');
INSERT INTO [t19188] VALUES (265,'٢٤٢ - مسألة: (أي وقت رأت الطهر، فهي طاهر، تغتسل وتصلي)','867','234','0');
INSERT INTO [t19188] VALUES (266,'٢٤٣ - مسألة: (ويستحب أن لا يقربها في الفرج حتى تتم الأربعين)','868','234','0');
INSERT INTO [t19188] VALUES (267,'٢٤٤ - مسألة: (فإن انقطع دمها في مدة الأربعين، ثم عاد فيها فهو نفاس. وعنه، أنه مشكوك فيه، تصوم وتصلي وتقضي الصوم المفروض)','869','234','0');
INSERT INTO [t19188] VALUES (268,'٢٤٥ - مسألة: (وإن ولدت توأمين، فأول النفاس من الأول، وآخره منه. وعنه، أنه من الأخير. والأول أصح)','872','234','0');
INSERT INTO [t19188] VALUES (269,'كتاب الصلاة','879','0','0');
INSERT INTO [t19188] VALUES (270,'[باب. . .]','879','269','0');
INSERT INTO [t19188] VALUES (271,'٢٤٦ - مسألة: (وهي واجبة على كل مسلم بالغ عاقل، إلا الحائض والنفساء)','881','270','0');
INSERT INTO [t19188] VALUES (272,'٢٤٧ - مسألة؛ قال: (وتجب على النائم، ومن زال عقله بسكر أو إغماء أو شرب دواء)','882','270','0');
INSERT INTO [t19188] VALUES (273,'٢٤٨ - مسألة: (ولا تجب على كافر ولا مجنون، ولا تصح منهما)','885','270','0');
INSERT INTO [t19188] VALUES (274,'٢٤٩ - مسألة: (وإذا صلى الكافر حكم بإسلامه)','890','270','0');
INSERT INTO [t19188] VALUES (275,'٢٥٠ - مسألة: (ولا تجب على صبي. وعنه، تجب على من بلغ عشرا)','893','270','0');
INSERT INTO [t19188] VALUES (276,'٢٥١ - مسألة: (ويؤمر بها لسبع، ويضرب على تركها لعشر)','895','270','0');
INSERT INTO [t19188] VALUES (277,'٢٥٢ - مسألة: (فإن بلغ في أثنائها أو بعدها في وقتها، لزمه إعادتها)','896','270','0');
INSERT INTO [t19188] VALUES (278,'٢٥٣ - مسألة: (ولا يجوز لمن وجبت عليه الصلاة تأخيرها عن وقتها، إلا لمن ينوي الجمع، أو لمشتغل بشرطها)','897','270','0');
INSERT INTO [t19188] VALUES (279,'٢٥٤ - مسألة: (ومن جحد وجوبها كفر)','900','270','0');
INSERT INTO [t19188] VALUES (280,'٢٥٥ - مسألة: (فإن تركها تهاونا لا جحودا، دعي إلى فعلها، فإن أبى حتى تضايق وقت التي بعدها، وجب قتله. وعنه: لا يجب حتى يترك ثلاثا، ويضيق وقت الرابعة)','901','270','0');
INSERT INTO [t19188] VALUES (281,'٢٥٦ - مسألة: (ولا يقتل حتى يستتاب ثلاثا، فإن تاب وإلا قتل بالسيف)','906','270','0');
INSERT INTO [t19188] VALUES (282,'٢٥٧ - مسألة: (وهل يقتل حدا أو لكفره؟ على روايتين)','907','270','0');
INSERT INTO [t19188] VALUES (283,'باب الأذان والإقامة','914','269','0');
INSERT INTO [t19188] VALUES (284,'٢٥٨ - مسألة: (وهما مشروعان للصلوات الخمس دون غيرها، للرجال دون النساء)','917','283','0');
INSERT INTO [t19188] VALUES (285,'٢٥٩ - مسألة؛ قال: (وهما فرض على الكفاية، إن اتفق أهل بلد كل تركهما قائلهم الإمام)','921','283','0');
INSERT INTO [t19188] VALUES (286,'٢٦٠ - مسألة: (ولا يجوز أخذ الأجرة عليهما في أظهر الروايتين)','928','283','0');
INSERT INTO [t19188] VALUES (287,'٢٦١ - مسألة: (فإن لم يوجد متطوع بهما رزق الإمام من بيت المال من يقوم بهما)','929','283','0');
INSERT INTO [t19188] VALUES (288,'٢٦٢ - مسألة: (وينبغي أن يكون المؤذن صيتا أمينا عالما بالأوقات)','930','283','0');
INSERT INTO [t19188] VALUES (289,'٢٦٣ - مسألة: (فإن تشاح فيه نفسان قدم أفضلهما في ذلك، ثم أفضلهما في دينه وعقله)','931','283','0');
INSERT INTO [t19188] VALUES (290,'٢٦٤ - مسألة: (والأذان خمس عشرة كلمة، لا ترجيع فيه)','933','283','0');
INSERT INTO [t19188] VALUES (291,'٢٦٥ - مسألة: (والإقامة إحدى عشرة كلمة، فإن رجع في الأذان أو ثنى في الإقامة، فلا بأس)','935','283','0');
INSERT INTO [t19188] VALUES (292,'٢٦٦ - مسألة: (ويقول في أذان الصبح: الصلاة خير من النوم. مرتين)','938','283','0');
INSERT INTO [t19188] VALUES (293,'٢٦٧ - مسألة: (ويستحب أن يترسل في الأذان، ويحدر الإقامة)','940','283','0');
INSERT INTO [t19188] VALUES (294,'٢٦٨ - مسألة: (ويؤذن قائما متطهرا على موضع عال مستقبل القبلة)','942','283','0');
INSERT INTO [t19188] VALUES (295,'٢٦٩ - مسألة: (فإذا بلغ الحيعلة التفت يمينا وشمالا، ولم يستدر)','946','283','0');
INSERT INTO [t19188] VALUES (296,'٢٧٠ - مسألة: (ويجعل إصبعيه في أذنيه)','949','283','0');
INSERT INTO [t19188] VALUES (297,'٢٧١ - مسألة: (ويتولاهما معا)','951','283','0');
INSERT INTO [t19188] VALUES (298,'٢٧٢ - مسألة: (و)','952','283','0');
INSERT INTO [t19188] VALUES (299,'٢٧٣ - مسألة: (ولا يصح الأذان إلا مرتبا مواليا، فإن نكسه، أو فرق بينه بسكوت طويل، أو كلام كثير، أو محرم، لم يعتد به)','953','283','0');
INSERT INTO [t19188] VALUES (300,'٢٧٤ - مسألة: (ولا يصح إلا بعد دخول الوقت، إلا الفجر، فإنه يؤذن لها بعد نصف الليل)','957','283','0');
INSERT INTO [t19188] VALUES (301,'٢٧٥ - مسألة: (ويستحب أن يجلس بعد أذان المغرب جلسة خفيفة، ثم يقيم)','962','283','0');
INSERT INTO [t19188] VALUES (302,'٢٧٦ - مسألة: (ومن جمع بين صلاتين، أو قضى فوائت، أذن وأقام للأولى، ثم أقام لكل صلاة بعدها)','965','283','0');
INSERT INTO [t19188] VALUES (303,'٢٧٧ - مسألة: (وهل يجزئ أذان المميز للبالغين؟ على روايتين)','969','283','0');
INSERT INTO [t19188] VALUES (304,'٢٧٨ - مسألة: (وهل [يعتد بأذان]','971','283','0');
INSERT INTO [t19188] VALUES (305,'٢٧٩ - مسألة: (ويستحب لمن سمع المؤذن أن يقول كما يقول، إلا فى الحيعلة، فإنه يقول: لا حول ولا قوة إلا بالله [العلى العظيم]','974','283','0');
INSERT INTO [t19188] VALUES (306,'٢٨٠ - مسألة: (ثم يقول بعد فراغه: اللهم رب هذه الدعوة التامة والصلاة القائمة، آت محمدا الوسيلة والفضيلة [والدرجة الرفيعة]','977','283','0');
INSERT INTO [t19188] VALUES (307,'باب شروط الصلاة','990','269','0');
INSERT INTO [t19188] VALUES (308,'٢٨١ - مسألة؛ قال: (وهى ما يجب لها قبلها، وهى ست؛ أولها دخول الوقت، والثانى، الطهارة من الحدث)','990','307','0');
INSERT INTO [t19188] VALUES (309,'٢٨٢ - مسألة؛ قال: (والصلوات المفروضات خمس)','991','307','0');
INSERT INTO [t19188] VALUES (310,'٢٨٣ - مسألة؛ قال: (الظهر، وهى الأولى، ووقتها من زوال الشمس إلى أن يصير ظل كل شئ مثله، بعد الذي زالت عليه الشمس)','993','307','0');
INSERT INTO [t19188] VALUES (311,'٢٨٤ - مسألة: (وتعجيلها أفضل، إلا في شدة الحر والغيم لمن يصلى جماعة','1000','307','0');
INSERT INTO [t19188] VALUES (312,'٢٨٥ - مسألة؛ قال: (ثم العصر، وهى الوسطى، ووقتها من خروج وقت الظهر إلى اصفرار الشمس. وعنه، إلى أن يصير ظل كل شيء مثليه. ثم يذهب وقت الاختيار، ويبقى وقت الضرورة إلى غروب الشمس)','1008','307','0');
INSERT INTO [t19188] VALUES (313,'٢٨٦ - مسألة: (وتعجيلها أفضل بكل حال)','1017','307','0');
INSERT INTO [t19188] VALUES (314,'٢٨٧ - مسألة: (ثم المغرب وهى الوتر، ووقتها من مغيب الشمس إلى مغيب الشفق الأحمر)','1019','307','0');
INSERT INTO [t19188] VALUES (315,'٢٨٨ - مسألة: (وتعجيلها أفضل، إلا ليلة جمع لمن قصدها)','1023','307','0');
INSERT INTO [t19188] VALUES (316,'٢٨٩ - مسألة: (ثم العشاء، ووقتها من مغيب الشفق الأحمر إلى ثلث الليل الأول','1025','307','0');
INSERT INTO [t19188] VALUES (317,'٢٩٠ - مسألة: (ثم يذهب وقت الاختيار، ويبقى وقت الضرورة إلى طلوع الفجر الثانى، وهر البياض المعترض في المشرق، ولا ظلمة بعده، وتأخيرها أفضل ما لم يشق)','1027','307','0');
INSERT INTO [t19188] VALUES (318,'٢٩١ - مسألة: (ثم الفجر، ووقتها من طلوع الفجر الثاني إلى طلوع الشمس)','1032','307','0');
INSERT INTO [t19188] VALUES (319,'٢٩٢ - مسألة: (وتعجيلها أفضل. وعنه، إن أسفر المأمومون، فالأفضل الإسفار)','1033','307','0');
INSERT INTO [t19188] VALUES (320,'٢٩٣ - مسألة: (ومن أدرك تكبيرة الإحرام من صلاة في وقتها فقد أدركها)','1037','307','0');
INSERT INTO [t19188] VALUES (321,'٢٩٤ - مسألة: (ومن شك في الوقت، لم يصل حتى يغلب على ظنه [دخوله)','1040','307','0');
INSERT INTO [t19188] VALUES (322,'٢٩٥ - مسألة: (فإن أخبره بذلك مخبر في يقين قبل قوله، وإن كان عن ظن يقبله)','1041','307','0');
INSERT INTO [t19188] VALUES (323,'٢٩٦ - مسألة: (ومتى اجتهد وصلى، فبان أنه وافق الوقت أو ما بعده، أجزأه)','1044','307','0');
INSERT INTO [t19188] VALUES (324,'٢٩٧ - مسألة: (ومن أدرك من الوقت قدر تكبيرة، ثم جن، أو حاضت المرأة، لزمهم القضاء)','1044','307','0');
INSERT INTO [t19188] VALUES (325,'٢٩٨ - مسألة: «وإن بلغ صبي، أو أسلم كافر، أو أفاق مجنون، أو طهرت حائض، قبل طلوع الشمس بقدر تكبيرة، لزمهم الصبح، وإن كان قبل غروب الشمس، لزمهم الظهر والعصر، وإن كان قبل طلوع الفجر، لزمهم المغرب والعشاء) وجملة ذلك، أنه متى أدرك أحد هؤلاء جزءا من آخر وقت الصلاة، لزمه قضاؤها، لأنها وجبت عليه، فلزمه القضاء كما لو أدرك وقتا يتسع لها. وهذا مذهب الشافعي، ولا نعلم فيه خلافا. قال شيخنا','1046','307','0');
INSERT INTO [t19188] VALUES (326,'٢٩٩ - مسألة: (ومن فاتته صلاة، لزمه قضاؤها على الفور مرتبا؛ قلت أو كثرت)','1049','307','0');
INSERT INTO [t19188] VALUES (327,'٣٠٠ - مسألة: (فإن خشى فوات الحاضرة، أو نسى الترتيب، سقط وجوبه)','1054','307','0');
INSERT INTO [t19188] VALUES (328,'٣٠١ - مسألة: (وإن نسى الترتيب، سقط وجوبه)','1062','307','0');
INSERT INTO [t19188] VALUES (329,'باب ستر العورة','1064','269','0');
INSERT INTO [t19188] VALUES (330,'٣٠٢ - مسألة: (وسترها عن النظر بما لا يصف البشرة واجب)','1065','329','0');
INSERT INTO [t19188] VALUES (331,'٣٠٣ - مسألة: (وعورة الرجل والأمة ما بين السرة والركبة. وعنه، أنها الفرجان)','1067','329','0');
INSERT INTO [t19188] VALUES (332,'٣٠٤ - مسألة: (والحرة كلها عورة إلا الوجه، وفى الكفين روايتان)','1073','329','0');
INSERT INTO [t19188] VALUES (333,'٣٠٥ - مسألة: (وأم الولد، والمعتق بعضها: كالأمة. وعنه، كالحرة)','1076','329','0');
INSERT INTO [t19188] VALUES (334,'٣٠٦ - مسألة: (ويستحب للرجل أن يصلى فى ثوبين)','1079','329','0');
INSERT INTO [t19188] VALUES (335,'٣٠٧ - مسألة: (فإن اقتصر على ستر العورة أجزأه، إذا كان على عاتقه شئ من اللباس)','1080','329','0');
INSERT INTO [t19188] VALUES (336,'٣٠٨ - مسألة: (ويستحب للمرأة أن تصلي في درع وخمار وملحفة، فإن اقتصرت على ستر العورة أجزأها)','1086','329','0');
INSERT INTO [t19188] VALUES (337,'٣٠٩ - مسألة: (وإذا انكشف من العورة يسير لا يفحش في النظر، لم تبطل صلاته)','1087','329','0');
INSERT INTO [t19188] VALUES (338,'٣١٠ - مسألة: (وإن فحش بطلت)','1090','329','0');
INSERT INTO [t19188] VALUES (339,'٣١١ - مسألة: (ومن صلى في ثوب حرير أو مغصوب، لم تصح صلاته. وعنه، تصح مع التحريم)','1090','329','0');
INSERT INTO [t19188] VALUES (340,'٣١٢ - مسألة: (ومن لم يجد إلا ثوبا نجسا صلى فيه)','1093','329','0');
INSERT INTO [t19188] VALUES (341,'٣١٣ - مسألة: (وتلزمه الإعادة على المنصوص)','1093','329','0');
INSERT INTO [t19188] VALUES (342,'٣١٤ - مسألة: (فإن لم يجد إلا ما يستر عورته سترها)','1097','329','0');
INSERT INTO [t19188] VALUES (343,'٣١٥ - مسألة: (فإن لم يكف جميعها، ستر الفرجين)','1098','329','0');
INSERT INTO [t19188] VALUES (344,'٣١٦ - مسألة: (فإن لم يكفهما جميعا، ستر أيهما شاء)','1099','329','0');
INSERT INTO [t19188] VALUES (345,'٣١٧ - مسألة: (وإن بذلت له سترة، لزمه قبولها، إذا كانت عارية)','1100','329','0');
INSERT INTO [t19188] VALUES (346,'٣١٨ - مسألة: (فإن عدم بكل حال صلى جالسا يومئ إيماء، وإن صلى قائما جاز. وعنه، أنه يصلي قائما ويسجد بالأرض)','1101','329','0');
INSERT INTO [t19188] VALUES (347,'٣١٩ - مسألة: (وإن وجد السترة قريبة عنه في أثناء الصلاة، ستر وبنى، وإن كانت بعيدة، ستر وابتدأ)','1105','329','0');
INSERT INTO [t19188] VALUES (348,'٣٢٠ - مسألة: (وتصلي العراة جماعة، وإمامهم في وسطهم)','1107','329','0');
INSERT INTO [t19188] VALUES (349,'٣٢١ - مسألة: (وإن كانوا رجالا ونساء، صلى كل نوع لأنفسهم)','1109','329','0');
INSERT INTO [t19188] VALUES (350,'٣٢٢ - مسألة: (ويكره في الصلاة السدل؛ وهو أن يطرح على كتفيه ثوبا ولا يرد أخد طرفيه على الكتف الأخرى)','1111','329','0');
INSERT INTO [t19188] VALUES (351,'٣٢٣ - مسألة: (و)','1113','329','0');
INSERT INTO [t19188] VALUES (352,'٣٢٤ - مسألة: (ويكره تغطية الوجه والتلثم على الفم والأنف)','1115','329','0');
INSERT INTO [t19188] VALUES (353,'٣٢٥ - مسألة: (و)','1116','329','0');
INSERT INTO [t19188] VALUES (354,'٣٢٦ - مسألة: (ويكره شد الوسط بما يشبه شد الزنار','1117','329','0');
INSERT INTO [t19188] VALUES (355,'٣٢٧ - مسألة: (و)','1118','329','0');
INSERT INTO [t19188] VALUES (356,'٣٢٨ - مسألة: (ولا يجوز للرجل لبس ثياب الحرير، ولا ما غالبه الحرير، ولا افتراشه إلا من ضرورة)','1123','329','0');
INSERT INTO [t19188] VALUES (357,'٣٢٩ - مسألة: (فإن استوى هو وما نسج معه، فعلى وجهين)','1125','329','0');
INSERT INTO [t19188] VALUES (358,'٣٣٠ - مسألة: (ويحرم لبس المنسوج بالذهب والمموه به)','1127','329','0');
INSERT INTO [t19188] VALUES (359,'٣٣١ - مسألة: (وإن لبس الحرير لمرض أو حكة، أو في الحرب، أو ألبسه الصبي، فعلى روايتين)','1129','329','0');
INSERT INTO [t19188] VALUES (360,'٣٣٢ - مسألة: (ويباح حشو الجباب والفرش به، ويحتمل أن يحرم)','1133','329','0');
INSERT INTO [t19188] VALUES (361,'٣٣٣ - مسألة: (ويباح العلم الحرير في الثوب إذا كان أربع أصابع فما دون)','1135','329','0');
INSERT INTO [t19188] VALUES (362,'٣٣٤ - مسألة: (ويكره للرجل لبس المزعفر والمعصفر)','1136','329','0');
INSERT INTO [t19188] VALUES (363,'باب اجتناب النجاسات','1143','269','0');
INSERT INTO [t19188] VALUES (364,'٣٣٥ - مسألة: (وإن طين الأرض النجسة، أو بسط عليها شيئا طاهرا، صحت الصلاة عليها مع الكراهة)','1147','363','0');
INSERT INTO [t19188] VALUES (365,'٣٣٦ - مسألة: (وإن صلى على مكان طاهر من بساط، طرفه نجس، صحت صلاته، إلا أن يكون متعلقا','1151','363','0');
INSERT INTO [t19188] VALUES (366,'٣٣٧ - مسألة: (ومتى وجد عليه نجاسة، لا يعلم هل كانت في الصلاة أولا؛ فصلاته صحيحة)','1153','363','0');
INSERT INTO [t19188] VALUES (367,'٣٣٨ - مسألة: (وإذا جبر ساقه بعظم نجس فجبر، لم يلزمه قلعه إذا خاف الضرر، وتصح صلاته)','1158','363','0');
INSERT INTO [t19188] VALUES (368,'٣٣٩ - مسألة','1159','363','0');
INSERT INTO [t19188] VALUES (369,'٣٤٠ - مسألة: (ولا تصح الصلاة في المقبرة والحمام والحش وأعطان الإبل التى تقيم فيها وتأوي إليها والموضع المغصوب. وعنه، تصح مع التحريم)','1160','363','0');
INSERT INTO [t19188] VALUES (370,'٣٤١ - مسألة: (وقال بعض أصحابنا: حكم المجزرة، والمزبلة، وقارعة الطريق وأسطحتها، كذلك)','1169','363','0');
INSERT INTO [t19188] VALUES (371,'٣٤٢ - مسألة: (وتصح الصلاة إليها، إلا المقبرة والحش، في قول ابن حامد)','1174','363','0');
INSERT INTO [t19188] VALUES (372,'٣٤٣ - مسألة: (ولا تصح الفريضة في الكعبة، ولا على ظهرها)','1177','363','0');
INSERT INTO [t19188] VALUES (373,'٣٤٤ - مسألة: (وتصح النافلة إذا كان بين يديه شيء منها)','1178','363','0');
INSERT INTO [t19188] VALUES (374,'باب استقبال القبلة','1182','269','0');
INSERT INTO [t19188] VALUES (375,'٣٤٥ - مسألة؛ قال: (إلا في حال العجز عنه،','1182','374','0');
INSERT INTO [t19188] VALUES (376,'٣٤٦ - مسألة: (وهل يجوز للماشي؟ على روايتين)','1187','374','0');
INSERT INTO [t19188] VALUES (377,'٣٤٧ - مسألة: (فإن أمكنه افتتاح الصلاة إلى القبلة، فهل يلزمه ذلك؟ على روايتين)','1190','374','0');
INSERT INTO [t19188] VALUES (378,'٣٤٨ - مسألة: (والفرض في القبلة إصابة العين لمن قرب منها، إصابة، الجهة لمن بعد عنها الناس في القبلة على ضربين؛ أحدهما، [من تلزمه]','1193','374','0');
INSERT INTO [t19188] VALUES (379,'٣٤٩ - مسألة: (فإن أمكنه ذلك بخبر ثقة عن يقين أو استدلال بمحاريب المسلمين، لزمه العمل به، وإن وجد محاريب لا يعلم؛ هل هي للمسلمين، أولا؟ لم يلتفت إليها)','1197','374','0');
INSERT INTO [t19188] VALUES (380,'٣٥٠ - مسألة: (وإن اشتبهت عليه في السفر، اجتهد في طلبها بالدلائل، وأثبتها القطب؛ إذا جعله وراء ظهره كان مستقبلا القبلة','1201','374','0');
INSERT INTO [t19188] VALUES (381,'٣٥١ - مسألة','1203','374','0');
INSERT INTO [t19188] VALUES (382,'٣٥٢ - مسألة: (والرياح الجنوب تهب مستقبلة لبطن كتف المصلي اليسرى، مارة إلى يمينه)','1205','374','0');
INSERT INTO [t19188] VALUES (383,'٣٥٣ - مسألة: (وإذا اختلف اجتهاد رجلين، لم يتبع أحدهما صاحبه. ويتبع الجاهل والأعمى أوثقهما في نفسه)','1208','374','0');
INSERT INTO [t19188] VALUES (384,'٣٥٤ - مسألة: (ويتبع الجاهل والأعمى أوثقهما في نفسه)','1211','374','0');
INSERT INTO [t19188] VALUES (385,'٣٥٥ - مسألة: (وإذا صلى البصير في حضير، فأخطأ، أو صلى الأعمى بلا دليل، أعادا','1214','374','0');
INSERT INTO [t19188] VALUES (386,'٣٥٦ - مسألة: (فإن لم يجد الأعمى من يقلده، صلى، وفي الإعادة روايتان. وقال ابن حامد: إن أخطأ، أعاد، وإن أصاب، فعلى وجهين)','1215','374','0');
INSERT INTO [t19188] VALUES (387,'٣٥٧ - مسألة: (ومن صلى بالاجتهاد [إلى جهة]','1217','374','0');
INSERT INTO [t19188] VALUES (388,'٣٥٨ - مسألة: (فإن أراد صلاة أخرى، اجتهد لها، فإن تغير اجتهاده عمل بالثاني، ولم يعد ما صلى بالأول)','1219','374','0');
INSERT INTO [t19188] VALUES (389,'باب النية','1222','269','0');
INSERT INTO [t19188] VALUES (390,'٣٥٩ - مسألة: (ويجب أن ينوي الصلاة بعينها إن كانت معينة، وإلا أجزأته نية الصلاة.)','1223','389','0');
INSERT INTO [t19188] VALUES (391,'٣٦٠ - مسألة: (وهل تشترط نية القضاء في الفائتة، ونية الفرضية في الفرض؟ على وجهين)','1224','389','0');
INSERT INTO [t19188] VALUES (392,'٣٦١ - مسألة: (ويأتي بالنية عند تكبيرة الإحرام)','1227','389','0');
INSERT INTO [t19188] VALUES (393,'٣٦٢ - مسألة: (فإن تقدمت قبل ذلك بالزمن اليسير جاز)','1228','389','0');
INSERT INTO [t19188] VALUES (394,'٣٦٣ - مسألة: (ويجب أن يستصحب حكمها إلى آخر الصلاة)','1230','389','0');
INSERT INTO [t19188] VALUES (395,'٣٦٤ - مسألة: (فإن قطعها في أثنائها، بطلت الصلاة، وإن تردد في قطعها، فعلى وجهين)','1231','389','0');
INSERT INTO [t19188] VALUES (396,'٣٦٥ - مسألة: (وإن أحرم بفرض، فبان قبل وقته، انقلب نفلا)','1234','389','0');
INSERT INTO [t19188] VALUES (397,'٣٦٦ - مسألة: (وإن أحرم به في وقته ثم قلبه نفلا، جاز، ويحتمل أن لا يجوز إلا لعذر، مثل أن يحرم منفردا، ثم','1235','389','0');
INSERT INTO [t19188] VALUES (398,'٣٦٧ - مسألة: (وإن انتقل من فرض إلى فرض بطلت الصلاتان (تبطل الأولى؛ لأنه قطع نيتها، ولا تصح الثانية؛ لأنه لم ينوها من أولها.','1236','389','0');
INSERT INTO [t19188] VALUES (399,'٣٦٨ - مسألة: (ومن شرط الجماعة أن ينوي الإمام والمأموم حالهم)','1237','389','0');
INSERT INTO [t19188] VALUES (400,'٣٦٩ - مسألة: (فإن أحرم منفردا، ثم نوى الائتمام، لم يصح، في أصح الروايتين)','1239','389','0');
INSERT INTO [t19188] VALUES (401,'٣٧٠ - مسألة: (وإن نوى الإمامة صح في النفل ولم يصح في الفرض. ويحتمل أن يصح، وهو أصح عندي)','1240','389','0');
INSERT INTO [t19188] VALUES (402,'٣٧١ - مسألة: (وإن أحرم مأموما، ثم نوى الانفراد لعذر، جاز)','1243','389','0');
INSERT INTO [t19188] VALUES (403,'٣٧٢ - مسألة: (وإن كان لغير عذر لم يجز، في إحدى الروايتين)','1245','389','0');
INSERT INTO [t19188] VALUES (404,'٣٧٣ - مسألة: (وإن نوى الإمامة لاستخلاف الإمام له إذا سبقه الحدث، صح في ظاهر المذهب)','1246','389','0');
INSERT INTO [t19188] VALUES (405,'٣٧٤ - مسألة: (وإن سبق اثنان ببعض الصلاة، فائتم أحدهما بصاحبه في قضاء ما فاتهما، فعلى وجهين)','1252','389','0');
INSERT INTO [t19188] VALUES (406,'٣٧٥ - مسألة: (وإن كان لغير عذر، لم يصح)','1253','389','0');
INSERT INTO [t19188] VALUES (407,'٣٧٦ - مسألة: (إن أحرم إماما لغيبة إمام الحي، ثم حضر في أثناء الصلاة، فأحرم بهم وبنى على صلاة خليفته، وصار الإمام مأموما، فهل يصح؟ على وجهين)','1254','389','0');
INSERT INTO [t19188] VALUES (408,'فصول في أدب المشي إلى الصلاة','1256','389','0');
INSERT INTO [t19188] VALUES (409,'باب صفة الصلاة','1262','269','0');
INSERT INTO [t19188] VALUES (410,'٣٧٧ - مسألة: (يستحب أن يقوم إلى الصلاة إذا قال المؤذن: قد قامت الصلاة)','1264','409','0');
INSERT INTO [t19188] VALUES (411,'٣٧٨ - مسألة: (ثم يسوي الإمام الصفوف)','1267','409','0');
INSERT INTO [t19188] VALUES (412,'٣٧٩ - مسألة: (ويقول: الله أكبر. لا يجزئه غيرها)','1270','409','0');
INSERT INTO [t19188] VALUES (413,'٣٨٠ - مسألة: (فإن لم يحسنها لزمه تعلمها، فإن خشي فوات الوقت كبر بلغته)','1274','409','0');
INSERT INTO [t19188] VALUES (414,'٣٨١ - مسألة: (ويجهر الإمام بالتكبير كله)','1276','409','0');
INSERT INTO [t19188] VALUES (415,'٣٨٢ - مسألة: (ويسر غيره به وبالقراءة بقدر ما يسمع نفسه)','1277','409','0');
INSERT INTO [t19188] VALUES (416,'٣٨٣ - مسألة: (ثم يرفع يديه مع ابتداء التكبير ممدودة الأصابع مضموما بعضها إلى بعض إلى حذو منكبيه أو إلى فروع أذنيه)','1280','409','0');
INSERT INTO [t19188] VALUES (417,'٣٨٤ - مسألة: (ثم يضع كف يده اليمنى على كوع اليسرى،','1284','409','0');
INSERT INTO [t19188] VALUES (418,'٣٨٥ - مسألة: (وينظر إلى موضع سجوده)','1287','409','0');
INSERT INTO [t19188] VALUES (419,'٣٨٦ - مسألة: (ثم يقول: سبحانك اللهم وبحمدك، وتبارك اسمك، وتعالى جدك، ولا إله غيرك)','1288','409','0');
INSERT INTO [t19188] VALUES (420,'٣٨٧ - مسألة: (ثم يقول: أعوذ بالله من الشيطان الرجيم)','1292','409','0');
INSERT INTO [t19188] VALUES (421,'٣٨٨ - مسألة: (ثم يقرأ: بسم الله الرحمن الرحيم)','1293','409','0');
INSERT INTO [t19188] VALUES (422,'٣٨٩ - مسألة: (وليست من الفاتحة. وعنه، أنها منها. ولا بجهر بشئ من ذلك)','1296','409','0');
INSERT INTO [t19188] VALUES (423,'٣٩٠ - مسألة: (ثم يقرأ الفاتحة، وفيها إحدى عشرة تشديدة)','1302','409','0');
INSERT INTO [t19188] VALUES (424,'٣٩١ - مسألة: (فإن ترك ترتيبها، أو تشديدة منها، أو قطعها بذكر كثير، أو سكوت طويل، لزمه استئنافها)','1307','409','0');
INSERT INTO [t19188] VALUES (425,'٣٩٢ - مسألة: (فإذا قال: {ولا الضالين}. قال: آمين)','1310','409','0');
INSERT INTO [t19188] VALUES (426,'٣٩٣ - مسألة: (يجهر بها الإمام والمأموم في صلاة الجهر)','1312','409','0');
INSERT INTO [t19188] VALUES (427,'٣٩٤ - مسألة: (فإن لم يحسن الفاتحة، وضاق الوقت عن تعلمها، قرأ قدرها في عدد الحروف، وقيل في عدد الآيات من غيرها، فإن لم يحسن إلا آية كررها بقدرها)','1313','409','0');
INSERT INTO [t19188] VALUES (428,'٣٩٥ - مسألة: (فإن لم يحسن شيئا من القرآن، لم يجز أن يترجم عنه بلغة أخرى، ولزمه أن يقول: سبحان الله، والحمد لله، ولا إله إلا الله، والله أكبر، ولا حول ولا قوة إلا بالله)','1317','409','0');
INSERT INTO [t19188] VALUES (429,'٣٩٦ - مسألة: (فإن لم يحسن إلا بعض ذلك، كرره بقدره)','1319','409','0');
INSERT INTO [t19188] VALUES (430,'٣٩٧ - مسألة: (فإن لم يحسن شيئا من الذكر، وقف بقدر القراءة)','1320','409','0');
INSERT INTO [t19188] VALUES (431,'٣٩٨ - مسألة: (ثم يقرأ [بعد الفاتحة] (٣)','1321','409','0');
INSERT INTO [t19188] VALUES (432,'٣٩٩ - مسألة: (ويجهر الإمام بالقراءة في الصبح، والأوليين من المغرب والعشاء)','1329','409','0');
INSERT INTO [t19188] VALUES (433,'٤٠٠ - مسألة: (وإن قرأ بقراءة تخرج عن مصحف عثمان، لم تصح صلاته. وعنه، تصح)','1332','409','0');
INSERT INTO [t19188] VALUES (434,'٤٠١ - مسألة: (ثم يرفع يديه، ويركع مكبرا، فيضع يديه على ركبتيه، ويمد ظهره مستويا، ويجعل رأسه حيال ظهره، ولا يرفعه ولا يخفضه)','1336','409','0');
INSERT INTO [t19188] VALUES (435,'٤٠٢ - مسألة: (ويجعل رأسه حيال ظهره، ولا يرفعه ولا يخفضه)','1342','409','0');
INSERT INTO [t19188] VALUES (436,'٤٠٣ - مسألة: (وقدر الإجزاء الانحناء، بحيث يمكنه مس ركبتيه بيديه)','1342','409','0');
INSERT INTO [t19188] VALUES (437,'٤٠٤ - مسألة: (ثم يقول: سبحان ربي العظيم. ثلاثا. وهو أدنى الكمال)','1343','409','0');
INSERT INTO [t19188] VALUES (438,'٤٠٥ - مسألة: (ثم يرفع رأسه قائلا: سمع الله لمن حمده. ويرفع يديه)','1348','409','0');
INSERT INTO [t19188] VALUES (439,'٤٠٦ - مسألة: (فإذا اعتدل قائما، قال: ربنا ولك الحمد، ملء السماء','1351','409','0');
INSERT INTO [t19188] VALUES (440,'٤٠٧ - مسألة: (فإن كان مأموما لم يزد على: ربنا ولك الحمد.','1355','409','0');
INSERT INTO [t19188] VALUES (441,'٤٠٨ - مسألة: (ثم يكبر ويخر ساجدا، ولا يرفع يديه)','1361','409','0');
INSERT INTO [t19188] VALUES (442,'٤٠٩ - مسألة: (فيضع ركبتيه، ثم يديه، ثم جبهته وأنفه، ويكون على أطراف أصابعه)','1363','409','0');
INSERT INTO [t19188] VALUES (443,'٤١٠ - مسألة: (والسجود على هذه الأعضاء واجب، إلا الأنف، على إحدى الروايتين)','1366','409','0');
INSERT INTO [t19188] VALUES (444,'٤١١ - مسألة: (ولا تجب عليه مباشرة المصلى بشئ منها، إلا الجبهة على إحدى الروايتين)','1370','409','0');
INSERT INTO [t19188] VALUES (445,'٤١٢ - مسألة: (ويجافي عضديه عن جنبيه، وبطنه عن فخذيه، ويضع يديه حذو منكبيه، ويفرق بين ركبتيه)','1375','409','0');
INSERT INTO [t19188] VALUES (446,'٤١٣ - مسألة: (ويقول: سبحان ربى الأعلى. ثلاثا)','1378','409','0');
INSERT INTO [t19188] VALUES (447,'٤١٤ - مسألة: (ثم يرفع رأسه مكبرا)','1382','409','0');
INSERT INTO [t19188] VALUES (448,'٤١٥ - مسألة، قال: «ويجلس مفترشا، يفرش','1382','409','0');
INSERT INTO [t19188] VALUES (449,'٤١٦ - مسألة: (ثم يسجد الثانية كالأولى)','1385','409','0');
INSERT INTO [t19188] VALUES (450,'٤١٧ - مسألة: (ثم يرفع رأسه مكبرا، ويقوم على صدور قدميه معتمدا على ركبتيه)','1386','409','0');
INSERT INTO [t19188] VALUES (451,'٤١٨ - مسألة: (إلا أن يشق عليه فيعتمد بالأرض)','1389','409','0');
INSERT INTO [t19188] VALUES (452,'٤١٩ - مسألة: (وعنه، أنه يجلس جلسة الاستراحة على قدميه وأليتيه)','1389','409','0');
INSERT INTO [t19188] VALUES (453,'٤٢٠ - مسألة: (ثم ينهض، ثم يصلى الثانية كذلك، إلا في تكبيرة الإحرام والاستفتاح، وفي الاستعاذة روايتان)','1392','409','0');
INSERT INTO [t19188] VALUES (454,'٤٢١ - مسألة: (ثم يجلس مفترشا، ويضع يده اليمنى على فخذه اليمنى، يقبض منها الخنصر والبنصر، ويحلق الإبهام مع الوسطى، ويشير بالسبابة في تشهده مرارا، ويبسط اليسرى على فخذه اليسرى)','1395','409','0');
INSERT INTO [t19188] VALUES (455,'٤٢٢ - مسألة: (ثم يتشهد فيقول: التحيات لله، والصلوات','1399','409','0');
INSERT INTO [t19188] VALUES (456,'٤٢٣ - مسألة؛ قال: (هذا التشهد الأول)','1403','409','0');
INSERT INTO [t19188] VALUES (457,'٤٢٤ - مسألة: (ثم يقول: اللهم صل على محمد وعلى آل محمد،','1405','409','0');
INSERT INTO [t19188] VALUES (458,'٤٢٥ - مسألة: (ويستحب أن يتعوذ، فيقول: أعوذ بالله من عذاب جهنم، ومن عذاب القبر، ومن فتنة المحيا والممات، ومن فتنة المسيح الدجال)','1415','409','0');
INSERT INTO [t19188] VALUES (459,'٤٢٦ - مسألة: (وإن دعا بما ورد في الأخبار، فلا بأس [الدعاء في الصلاة بما وردت به الأخبار جائز]','1416','409','0');
INSERT INTO [t19188] VALUES (460,'٤٢٧ - مسألة: (ثم يسلم عن يمينه: السلام عليكم ورحمة الله. وعن يساره كذلك)','1424','409','0');
INSERT INTO [t19188] VALUES (461,'٤٢٨ - مسألة: (فإن لم يقل: ورحمة الله. لم يجزئه. وقال القاضي: يجزئه. ونص عليه أحمد في صلاة الجنازة)','1429','409','0');
INSERT INTO [t19188] VALUES (462,'٤٢٩ - مسألة: (وينوي بسلامه الخروج من الصلاة، فإن لم ينو، جاز. وقال ابن حامد: تبطل صلاته)','1434','409','0');
INSERT INTO [t19188] VALUES (463,'٤٣٠ - مسألة: (وإن كانت الصلاة مغربا، أو رباعية نهض مكبرا إذا فرغ من التشهد الأول، فصلى الثالثة والرابعة مثل الثانية، إلا أنه لا يجهر ولا يقرأ شيئا بعد الفاتحة)','1441','409','0');
INSERT INTO [t19188] VALUES (464,'٤٣١ - مسألة: (ثم يجلس في التشهد الثاني متوركا، يفرش رجله اليسرى، وينصب اليمنى، ويخرجهما عن يمينه ويجعل أليتيه على الأرض)','1444','409','0');
INSERT INTO [t19188] VALUES (465,'٤٣٢ - مسألة: (والمرأة كالرجل في ذلك كله، إلا أنها تجمع نفسها في الركوع والسجود، وتجلس متربعة، أو تسدل رجليها فتجعلهما في جانب يمينها. وهل يسن لها رفع اليدين؛ على روايتين)','1449','409','0');
INSERT INTO [t19188] VALUES (466,'٤٣٣ - مسألة: (و)','1453','409','0');
INSERT INTO [t19188] VALUES (467,'٤٣٤ - مسألة؛ قال: (وافتراش الذراعين في السجود)','1453','409','0');
INSERT INTO [t19188] VALUES (468,'٤٣٥ - مسألة: (و)','1455','409','0');
INSERT INTO [t19188] VALUES (469,'٤٣٦ - مسألة: (ويكره أن يصلي وهو حاقن)','1457','409','0');
INSERT INTO [t19188] VALUES (470,'٤٣٧ - مسألة: (أو بحضرة طعام تتوق نفسه إليه)','1458','409','0');
INSERT INTO [t19188] VALUES (471,'٤٣٨ - مسألة: (ويكره العبث)','1459','409','0');
INSERT INTO [t19188] VALUES (472,'٤٣٩ - مسألة؛ قال: (والتروح، وفرقعة الأصابع، وتشبيكها)','1460','409','0');
INSERT INTO [t19188] VALUES (473,'٤٤٠ - مسألة: (وله رد المار بين يديه)','1465','409','0');
INSERT INTO [t19188] VALUES (474,'٤٤١ - مسألة: (و)','1471','409','0');
INSERT INTO [t19188] VALUES (475,'٤٤٢ - مسألة: (و)','1473','409','0');
INSERT INTO [t19188] VALUES (476,'٤٤٣ - مسألة: (وإن طال الفعل في الصلاة أبطلها، [عمدا كان أو سهوا]','1476','409','0');
INSERT INTO [t19188] VALUES (477,'٤٤٤ - مسألة: (ويكره تكرار الفاتحة)','1479','409','0');
INSERT INTO [t19188] VALUES (478,'٤٤٥ - مسألة: (و)','1480','409','0');
INSERT INTO [t19188] VALUES (479,'٤٤٦ - مسألة: (ولا تكره قراءة أو أواخر السور وأوساطها. وعنه، تكره)','1482','409','0');
INSERT INTO [t19188] VALUES (480,'٤٤٧ - مسألة: (وله أن يفتح على الإمام إذا أرتج عليه)','1485','409','0');
INSERT INTO [t19188] VALUES (481,'٤٤٨ - مسألة: (وإذا نابه شيء، مثل سهو إمامه، أو استئذان إنسان عليه، سبح إن كان','1489','409','0');
INSERT INTO [t19188] VALUES (482,'٤٤٩ - مسألة: (وإن بدره البصاق بصق في ثوبه، وإن كان في غير المسجد جاز أن يبصق عن يساره، أو تحت قدمه)','1497','409','0');
INSERT INTO [t19188] VALUES (483,'٤٥٠ - مسألة: (ويستحب أن يصلي إلى سترة مثل آخرة الرحل)','1499','409','0');
INSERT INTO [t19188] VALUES (484,'٤٥١ - مسألة: (فإن لم يجد، خط خطا)','1504','409','0');
INSERT INTO [t19188] VALUES (485,'٤٥٢ - مسألة: (فإن مر من ورائها شيء، لم يكره)','1511','409','0');
INSERT INTO [t19188] VALUES (486,'٤٥٣ - مسألة: (وإن لم تكن سترة، فمر بين يديه الكلب الأسود البهيم، بطلت صلاته، وفي المرأة والحمار روايتان)','1511','409','0');
INSERT INTO [t19188] VALUES (487,'٤٥٤ - مسألة: (ويجوز له النظر في المصحف)','1522','409','0');
INSERT INTO [t19188] VALUES (488,'٤٥٥ - مسألة: (وإذا مرت به آية رحمة أن يسألها، أو آية عذاب أن يستعيذ منها، وعنه، يكره ذلك في الفرض)','1524','409','0');
INSERT INTO [t19188] VALUES (489,'٤٥٦ - مسألة: قال رحمه الله: (أركان الصلاة اثنا عشر، القيام، وتكبيرة الإحرام، وقراءة الفاتحة، والركوع، والاعتدال عنه، والسجود، والجلوس بين السجدتين، والطمأنينة في هذه الأفعال، والتشهد الأخير، والجلوس له، والتسليمة الأولى، والترتيب، من ترك منها شيئا عمدا بطلت صلاته)','1526','409','0');
INSERT INTO [t19188] VALUES (490,'٤٥٧ - مسألة: (وواجباتها تسعة: التكبير غير تكبيرة الإحرام، والتسبيح في الركوع والسجود مرة مرة، والتسميع والتحميد في الرفع من الركوع، وسؤال المغفرة بين السجدتين مرة، والتشهد الأول، والجلوس له، والصلاة على النبي -صلى الله عليه وسلم- في موضعها','1533','409','0');
INSERT INTO [t19188] VALUES (491,'٤٥٨ - مسألة؛ قال: (ومن ترك منها شيئا عمدا بطلت صلاته،','1539','409','0');
INSERT INTO [t19188] VALUES (492,'٤٥٩ - مسألة: (وسنن الأقوال اثنا عشر، الاستفتاح، والتعوذ، وقراءة بسم الله الرحمن الرحيم، [وقول: «آمين»]','1540','409','0');
INSERT INTO [t19188] VALUES (493,'٤٦٠ - مسألة: (وما سوى هذا من سنن الأفعال لا تبطل الصلاة بتركها، ولا يشرع السجود لها)','1544','409','0');
INSERT INTO [t19188] VALUES (494,'باب سجود السهو','1549','269','0');
INSERT INTO [t19188] VALUES (495,'٤٦١ - مسألة؛ قال: (ولا يشرع في العمد)','1549','494','0');
INSERT INTO [t19188] VALUES (496,'٤٦٢ - مسألة: (ويشرع للسهو في زيادة، ونقص، وشك)','1550','494','0');
INSERT INTO [t19188] VALUES (497,'٤٦٣ - مسألة: (للنافلة والفرض)','1550','494','0');
INSERT INTO [t19188] VALUES (498,'٤٦٤ - مسألة: (فمتى زاد فعلا من جنس الصلاة، قياما، أو','1551','494','0');
INSERT INTO [t19188] VALUES (499,'٤٦٥ - مسألة: (وإن زاد ركعة، فلم يعلم حتى فرغ منها، سجد لها)','1553','494','0');
INSERT INTO [t19188] VALUES (500,'٤٦٦ - مسألة: (وإن علم فيها، جلس في الحال، فتشهد إن لم يكن تشهد، وسجد، وسلم)','1553','494','0');
INSERT INTO [t19188] VALUES (501,'٤٦٧ - مسألة: (وإن سبح به اثنان، لزمه الرجوع)','1556','494','0');
INSERT INTO [t19188] VALUES (502,'٤٦٨ - مسألة: (فإن لم يرجع، بطلت صلاته وصلاة من اتبعه عالما، وإن فارقه، أو كان جاهلا، لم تبطل)','1559','494','0');
INSERT INTO [t19188] VALUES (503,'٤٦٩ - مسألة: (والعمل المستكثر في العادة، من غير جنس الصلاة، يبطلها عمده وسهوه، ولا تبطل باليسير، ولا يشرع له سجود)','1562','494','0');
INSERT INTO [t19188] VALUES (504,'٤٧٠ - مسألة: (وإن أكل أو شرب عمدا، بطلت صلاته، قل أو كثر، وإن كان سهوا، لم تبطل إذا كان يسيرا)','1563','494','0');
INSERT INTO [t19188] VALUES (505,'٤٧١ - مسألة: (وإن أتى بقول مشروع في غير موضعه، كالقراءة في السجود والقعود، والتشهد في القيام، وقراءة السورة في الأخريين، لم تبطل الصلاة بعمده)','1566','494','0');
INSERT INTO [t19188] VALUES (506,'٤٧٢ - مسألة: (وإن سلم قبل إتمام صلاته عمدا، أبطلها)','1568','494','0');
INSERT INTO [t19188] VALUES (507,'٤٧٣ - مسألة: (وإن تكلم لمصلحتها، ففيه ثلاث روايات؛ إحداهن، لا تبطل. والثانية، تبطل. والثالثة، تبطل صلاة المأموم دون الإمام. اختارها الخرقي)','1574','494','0');
INSERT INTO [t19188] VALUES (508,'٤٧٤ - مسألة؛ قال: (وإن تكلم في صلب الصلاة بطلت. وعنه، لا تبطل إذا كان ساهيا، أو جاهلا، ويسجد له)','1576','494','0');
INSERT INTO [t19188] VALUES (509,'٤٧٥ - مسألة: (وإن قهقه، أو نفخ، أو انتحب، فبان حرفان فهو كالكلام، إلا ما كان من خشية الله تعالى. قال أصحابنا في النحنحة مثل ذلك. وقد روي عن أبي عبد الله، أنه كان يتنحنح في صلاته ولا يراها مبطلة للصلاة)','1585','494','0');
INSERT INTO [t19188] VALUES (510,'٤٧٦ - مسألة؛ قال رحمه الله: (وأما النقص، فمتى ترك ركنا، فذكره بعد شروعه في قراءة ركعة أخرى، بطلت التى تركه منها. وإن ذكره قبل ذلك','1593','494','0');
INSERT INTO [t19188] VALUES (511,'٤٧٧ - مسألة: (وإن نسي أربع سجدات من أربع ركعات،','1598','494','0');
INSERT INTO [t19188] VALUES (512,'٤٧٨ - مسألة: (وإن نسي التشهد الأول ونهض، لزمه الرجوع، ما لم ينتصب قائما، فإن استتم قائما، لم يرجع، وإن رجع، جاز. وإن شرع في القراءة، لم يجز له الرجوع، وعليه السجود لذلك كله)','1602','494','0');
INSERT INTO [t19188] VALUES (513,'٤٧٩ - مسألة (فإن استوى الأمران عنده، بنى على اليقين)','1614','494','0');
INSERT INTO [t19188] VALUES (514,'٤٨٠ - مسألة: (ومن شك في ترك ركن فهو كتركه)','1615','494','0');
INSERT INTO [t19188] VALUES (515,'٤٨١ - مسألة: (وليس على المأموم سجود سهو، إلا أن يسهو إمامه، فيسجد)','1617','494','0');
INSERT INTO [t19188] VALUES (516,'٤٨٢ - مسألة: (فإن لم يسجد الإمام، فهل يسجد المأموم؟ على روايتين)','1619','494','0');
INSERT INTO [t19188] VALUES (517,'٤٨٣ - مسألة: (ومحله قبل السلام، إلا في السلام قبل إتمام صلاته، وفيما إذا بنى الإمام على غالب ظنه. وعنه، أن الجميع قبل السلام. وعنه، ما كان من زيادة فهو بعد السلام، وما كان من نقص كان قبله)','1625','494','0');
INSERT INTO [t19188] VALUES (518,'٤٨٤ - مسألة: (وإن نسيه قبل السلام قضاه، ما لم يطل الفصل، أو يخرج من المسجد. وعنه، أنه يسجد وإن بعد)','1629','494','0');
INSERT INTO [t19188] VALUES (519,'٤٨٥ - مسألة: (ويكفي لجميع السهو سجدتان، إلا أن يختلف محلهما، ففيه وجهان)','1633','494','0');
INSERT INTO [t19188] VALUES (520,'٤٨٦ - مسألة: (ومتى سجد بعد السلام، جلس فتشهد، ثم سلم)','1637','494','0');
INSERT INTO [t19188] VALUES (521,'٤٨٧ - مسألة: (وإن ترك السجود الواجب قبل السلام عمدا، بطلت صلاته)','1639','494','0');
INSERT INTO [t19188] VALUES (522,'باب صلاة التطوع','1641','269','0');
INSERT INTO [t19188] VALUES (523,'٤٨٨ - مسألة؛ قال: (وهي أفضل تطوع البدن)','1641','522','0');
INSERT INTO [t19188] VALUES (524,'٤٨٩ - مسألة: (وآكدها صلاة الكسوف والاستسقاء)','1642','522','0');
INSERT INTO [t19188] VALUES (525,'٤٩٠ - مسألة؛ قال: (ثم الوتر، وليس بواجب، ووقته ما بين صلاة العشاء وطلوع الفجر، وأقله ركعة، وأكثره إحدى عشرة ركعة، يسلم من كل ركعتين، ويوتر بركعة)','1643','522','0');
INSERT INTO [t19188] VALUES (526,'٤٩١ - مسألة: (وإن أوتر بتسع سرد ثمانيا، وجلس فتشهد','1654','522','0');
INSERT INTO [t19188] VALUES (527,'٤٩٢ - مسألة: (وأدنى الكمال ثلاث ركعات بتسليمتين)','1658','522','0');
INSERT INTO [t19188] VALUES (528,'٤٩٣ - مسألة؛ قال: (يقرأ في الأولى بـ {سبح}، وفي الثانية {قل ياأيها الكافرون}، وفي الثالثة {قل هو الله أحد}. يستحب أن يقرأ في ركعات الوتر الثلاث بذلك. وبه قال الثوري، وإسحاق، وأصحاب الرأي. وقال الشافعي: يقرأ في الثالثة: {قل هو الله أحد} والمعوذتين. وروي نحوه عن أحمد. وهو قول مالك في الوتر. وقال في الشفع: لم يبلغني فيه شئ معلوم، لما روت عائشة، أن رسول الله الله -صلى الله عليه وسلم- كان يقرأ في الركعة الأولى بـ {سبح اسم ربك الأعلى}، وفي الثانية {قل ياأيها الكافرون}، وفي الثالثة {قل هو الله أحد} والمعوذتين. رواه ابن ماجه','1661','522','0');
INSERT INTO [t19188] VALUES (529,'٤٩٤ - مسألة؛ قال: (ويقنت فيها بعد الركوع)','1662','522','0');
INSERT INTO [t19188] VALUES (530,'٤٩٥ - مسألة: (وهل يمسح وجهه بيديه؟ على روايتين)','1669','522','0');
INSERT INTO [t19188] VALUES (531,'٤٩٦ - مسألة: (ولا يقنت في غير الوتر)','1670','522','0');
INSERT INTO [t19188] VALUES (532,'٤٩٧ - مسألة؛ قال: (إلا أن تنزل بالمسلمين نازلة، فللإمام خاصة القنوت في صلاة الفجر)','1672','522','0');
INSERT INTO [t19188] VALUES (533,'٤٩٨ - مسألة؛ قال: (ثم السنن الراتبة، وهي عشر ركعات؛ ركعتان قبل الظهر، وركعتان بعدها، وركعتان بعد المغرب، وركعتان بعد العشاء، وركعتان قبل الفجر، وهما آكدها، قال أبو الخطاب: وأربع قبل العصر)','1676','522','0');
INSERT INTO [t19188] VALUES (534,'٤٩٩ - مسألة: (ومن فاته شيء من هذه السنن، سن له قضاؤه)','1684','522','0');
INSERT INTO [t19188] VALUES (535,'٥٠٠ - مسألة: (ثم التراويح، وهي عشرون ركعة، يقوم بها في رمضان في جماعة، ويوتر بعدها في الجماعة)','1698','522','0');
INSERT INTO [t19188] VALUES (536,'٥٠١ - مسألة: (فإن أحب متابعة الإمام، فأوتر معه، قام إذا سلم الإمام فشفعها بأخرى)','1706','522','0');
INSERT INTO [t19188] VALUES (537,'٥٠٢ - مسألة: (ويكره التطوع بين التراويح. وفي التعقيب روايتان؛ وهو أن يتطوع بعد التراويح والوتر في جماعة)','1710','522','0');
INSERT INTO [t19188] VALUES (538,'٥٠٣ - مسألة: (وصلاة الليل أفضل من صلاة النهار)','1719','522','0');
INSERT INTO [t19188] VALUES (539,'٥٠٤ - مسألة: (وأفضلها وسط الليل، والنصف الأخير أفضل من الأول)','1720','522','0');
INSERT INTO [t19188] VALUES (540,'٥٠٥ - مسألة: (وصلاة الليل مثنى مثنى، فإن تطوع في النهار بأربع فلا بأس، والأفضل مثنى)','1729','522','0');
INSERT INTO [t19188] VALUES (541,'٥٠٦ - مسألة: (وصلاة القاعد على النصف من صلاة القائم، ويكون في حال القيام متربعا)','1735','522','0');
INSERT INTO [t19188] VALUES (542,'٥٠٧ - مسألة: (وأدنى صلاة الضحى ركعتان، وأكثرها ثمان، ووقتها إذا علت الشمس)','1740','522','0');
INSERT INTO [t19188] VALUES (543,'٥٠٨ - مسألة؛ قال الشيخ، رحمه الله: (وسجود التلاوة صلاة)','1745','522','0');
INSERT INTO [t19188] VALUES (544,'٥٠٩ - مسألة: (وهو سنة للقارئ والمستمع دون السامع)','1746','522','0');
INSERT INTO [t19188] VALUES (545,'٥١٠ - مسألة: (ويعتبر أن يكون القارئ يصلح إماما له)','1750','522','0');
INSERT INTO [t19188] VALUES (546,'٥١١ - مسألة: (فإن لم يسجد القارئ، لم يسجد)','1751','522','0');
INSERT INTO [t19188] VALUES (547,'٥١٢ - مسألة؛ قال: (وهو أربع عشرة سجدة)','1756','522','0');
INSERT INTO [t19188] VALUES (548,'٥١٣ - مسألة؛ قال: (في الحج منها اثنتان)','1760','522','0');
INSERT INTO [t19188] VALUES (549,'٥١٤ - مسألة؛ قال: (ويكبر إذا سجد، وإذا رفع)','1762','522','0');
INSERT INTO [t19188] VALUES (550,'٥١٥ - مسألة: (ويجلس ويسلم، ولا يتشهد)','1764','522','0');
INSERT INTO [t19188] VALUES (551,'٥١٦ - مسألة؛ قال: (وإذا سجد في الصلاة رفع يديه. نص عليه. وقال القاضي: لا يرفعهما)','1766','522','0');
INSERT INTO [t19188] VALUES (552,'٥١٧ - مسألة: (ولا يستحب للإمام السجود في صلاة لا يجهر فيها)','1768','522','0');
INSERT INTO [t19188] VALUES (553,'٥١٨ - مسألة: (فإن سجد، فالمأموم مخير بين اتباعه وتركه)','1769','522','0');
INSERT INTO [t19188] VALUES (554,'٥١٩ - مسألة: (ويستحب سجود الشكر عند تجدد','1770','522','0');
INSERT INTO [t19188] VALUES (555,'٥٢٠ - مسألة: (ولا يسجد له في الصلاة)','1771','522','0');
INSERT INTO [t19188] VALUES (556,'٥٢١ - مسألة؛ قال: (ويجوز قضاء الفرائض فيها)','1780','522','0');
INSERT INTO [t19188] VALUES (557,'٥٢٢ - مسألة: (وتجوز صلاة الجنازة، وركعتا الطواف','1783','522','0');
INSERT INTO [t19188] VALUES (558,'٥٢٣ - مسألة: (ولا يجوز التطوع بغيرها في شيء من هذه','1789','522','0');
INSERT INTO [t19188] VALUES (559,'باب صلاة الجماعة','1800','269','0');
INSERT INTO [t19188] VALUES (560,'٥٢٤ - مسألة: (وهي واجبة للصلوات الخمس على الرجال، لا شرط)','1800','559','0');
INSERT INTO [t19188] VALUES (561,'٥٢٥ - مسألة: (وله فعلها في بيته في أصح الروايتين)','1807','559','0');
INSERT INTO [t19188] VALUES (562,'٥٢٦ - مسألة: (ويستحب لأهل الثغر الاجتماع في مسجد واحد)','1809','559','0');
INSERT INTO [t19188] VALUES (563,'٥٢٧ - مسألة: (والأفضل لغيرهم الصلاة في المسجد الذي لا تقام فيه الجماعة إلى بحضوره','1809','559','0');
INSERT INTO [t19188] VALUES (564,'٥٢٨ - مسألة: (ثم ما كان أكثر جماعة، ثم في المسجد العتيق)','1810','559','0');
INSERT INTO [t19188] VALUES (565,'٥٢٩ - مسألة: (وهل الأولى قصد الأبعد أو الأقرب؟ على روايتين)','1811','559','0');
INSERT INTO [t19188] VALUES (566,'٥٣٠ - مسألة: (ولا يؤم في مسجد قبل إمامه الراتب إلا بإذنه)','1813','559','0');
INSERT INTO [t19188] VALUES (567,'٥٣١ - مسألة: (فإن لم يعلم عذره انتظر وروسل، [ما لم يخش]','1814','559','0');
INSERT INTO [t19188] VALUES (568,'٥٣٢ - مسألة: (فإن صلى، ثم أقيمت الصلاة وهو في المسجد، استحب له إعادتها إلا المغرب. وعنه','1815','559','0');
INSERT INTO [t19188] VALUES (569,'٥٣٣ - مسألة: (ولا تكره إعادة الجماعة في غير المساجد الثلاثة)','1820','559','0');
INSERT INTO [t19188] VALUES (570,'٥٣٤ - مسألة: (وإذا أقيمت الصلاة فلا صلاة إلا المكتوبة)','1823','559','0');
INSERT INTO [t19188] VALUES (571,'٥٣٥ - مسألة: (وإن أقيمت وهو في نافلة أتمها)','1824','559','0');
INSERT INTO [t19188] VALUES (572,'٥٣٦ - مسألة: (ومن كبر قبل سلام الإمام، فقد أدرك الجماعة)','1826','559','0');
INSERT INTO [t19188] VALUES (573,'٥٣٧ - مسألة؛ قال: (ومن أدرك الركوع فقد أدرك الركعة)','1827','559','0');
INSERT INTO [t19188] VALUES (574,'٥٣٨ - مسألة: (وأجزأته تكبيرة واحدة، والأفضل اثنتان)','1828','559','0');
INSERT INTO [t19188] VALUES (575,'٥٣٩ - مسألة: (وما أدرك مع الإمام فهو آخر صلاته، وما يقضيه أولها','1832','559','0');
INSERT INTO [t19188] VALUES (576,'٥٤٠ - مسألة: (ولا تجب القراءة على المأموم)','1837','559','0');
INSERT INTO [t19188] VALUES (577,'٥٤١ - مسألة: (ويستحب أن يقرأ في سكتات الإمام، وما لا يجهر فيه، أو لا يسمعه لبعده. فإن لم يسمعه لطرش، فعلى وجهين)','1841','559','0');
INSERT INTO [t19188] VALUES (578,'٥٤٢ - مسألة: (وهل يستفتح ويستعيذ فيما يجهر فيه الإمام؛ على روايتين)','1848','559','0');
INSERT INTO [t19188] VALUES (579,'٥٤٣ - مسألة: (ومن ركع أو سجد قبل إمامه، فعليه أن يرفع ليأتي به بعده','1851','559','0');
INSERT INTO [t19188] VALUES (580,'٥٤٤ - مسألة: (فإن ركع ورفع قبل ركوع إمامه عالما عمدا فهل تبطل صلاته؟ على وجهين)','1854','559','0');
INSERT INTO [t19188] VALUES (581,'٥٤٥ - مسألة: (ويستحب للإمام تخفيف الصلاة مع إتمامها)','1860','559','0');
INSERT INTO [t19188] VALUES (582,'٥٤٦ - مسألة: (و)','1862','559','0');
INSERT INTO [t19188] VALUES (583,'٥٤٧ - مسألة: (ولا يستحب انتظار داخل وهو في الركوع، في إحدى الروايتين)','1864','559','0');
INSERT INTO [t19188] VALUES (584,'٥٤٨ - مسألة: (وإذا استأذنت المرأة إلى المسجد كره منعها، وبيتها خير لها)','1867','559','0');
INSERT INTO [t19188] VALUES (585,'٥٤٩ - مسألة: (ثم أفقههم، ثم أسنهم، ثم أقدمهم هجرة، ثم أشرفهم، ثم أتقاهم، ثم من تقع له القرعة)','1873','559','0');
INSERT INTO [t19188] VALUES (586,'٥٥٠ - مسألة: (وصاحب البيت وإمام المسجد أحق بالإمامة، إلا أن يكون بعضهم ذا سلطان)','1879','559','0');
INSERT INTO [t19188] VALUES (587,'٥٥١ - مسألة: (والحر أولى من العبد، والحاضر أولى من المسافر, والبصير أولى من الأعمي، في أحد الوجهين)','1882','559','0');
INSERT INTO [t19188] VALUES (588,'٥٥٢ - مسألة: (وهل تصح إمامة الفاسق والأقلف؟ على روايتين)','1887','559','0');
INSERT INTO [t19188] VALUES (589,'٥٥٣ - مسألة: (وفي إمامة أقطع اليدين وجهان)','1899','559','0');
INSERT INTO [t19188] VALUES (590,'٥٥٤ - مسألة: (لا تصح الصلاة خلف كافر، ولا أخرس)','1901','559','0');
INSERT INTO [t19188] VALUES (591,'٥٥٥ - مسألة: (ولا)','1905','559','0');
INSERT INTO [t19188] VALUES (592,'٥٥٦ - مسألة: (ولا تصح خلف عاجز عن القيام، إلا إمام الحي المرجو زوال علته، [ويصلون وراءه جلوسا]','1908','559','0');
INSERT INTO [t19188] VALUES (593,'٥٥٧ - مسألة؛ قال: (فإن صلوا قياما صحت صلاتهم في أحد الوجهين)','1912','559','0');
INSERT INTO [t19188] VALUES (594,'٥٥٨ - مسألة: (فإن ابتدأ بهم الصلاة قائما، ثم اعتل فجلس، أتموا خلفه قياما)','1914','559','0');
INSERT INTO [t19188] VALUES (595,'٥٥٩ - مسألة: (ولا تصح إمامة المرأة والخنثى للرجال، ولا للخناثى)','1916','559','0');
INSERT INTO [t19188] VALUES (596,'٥٦٠ - مسألة؛ قال: (ولا إمامة الصبي لبالغ، إلا في النفل،','1920','559','0');
INSERT INTO [t19188] VALUES (597,'٥٦١ - مسألة: (ولا تصح إمامة محدث ولا نجس يعلم ذلك','1923','559','0');
INSERT INTO [t19188] VALUES (598,'٥٦٢ - مسألة: (ولا تصح إمامة الأمي؛ وهو من لا يحسن الفاتحة، أو يدغم حرفا [لا يدغم، أو يبدل حرفا]','1928','559','0');
INSERT INTO [t19188] VALUES (599,'٥٦٣ - مسألة: (وتكره إمامة اللحان، والفأفاء الذي يكرر الفاء، والتمتام الذي يكرر التاء','1933','559','0');
INSERT INTO [t19188] VALUES (600,'٥٦٤ - مسألة: (و)','1935','559','0');
INSERT INTO [t19188] VALUES (601,'٥٦٥ - مسألة: (و)','1936','559','0');
INSERT INTO [t19188] VALUES (602,'٥٦٦ - مسألة: (ولا بأس بإمامة ولد الزنا والجندي، إذا سلم دينهما)','1939','559','0');
INSERT INTO [t19188] VALUES (603,'٥٦٧ - مسألة: (ويصح ائتمام [من يؤدي]','1941','559','0');
INSERT INTO [t19188] VALUES (604,'٥٦٨ - مسألة: (ويصح ائتمام المفترض بالمتنفل، ومن يصلي الظهر بمن يصلي العصر، في إحدى الروايتين. والأخرى، لا يصح فيهما)','1942','559','0');
INSERT INTO [t19188] VALUES (605,'٥٦٩ - مسألة: (فإن وقفوا قدامه، لم يصح)','1950','559','0');
INSERT INTO [t19188] VALUES (606,'٥٧٠ - مسألة: (وإن وقفوا عن يمينه أو عن جانبيه، صح)','1952','559','0');
INSERT INTO [t19188] VALUES (607,'٥٧١ - مسألة: (وإن كان واحدا، وقف عن يمينه)','1952','559','0');
INSERT INTO [t19188] VALUES (608,'٥٧٢ - مسألة: (وإن وقف خلفه، أو عن يساره، لم يصح)','1953','559','0');
INSERT INTO [t19188] VALUES (609,'٥٧٣ - مسألة: (وإن أم امرأة وقفت خلفه)','1957','559','0');
INSERT INTO [t19188] VALUES (610,'٥٧٤ - مسألة: (وإن اجتمع)','1959','559','0');
INSERT INTO [t19188] VALUES (611,'٥٧٥ - مسألة: (ومن لم يقف معه إلا كافر، أو امرأة، أو محدث يعلم حدثه، فهو فذ. وكذلك الصبي، إلا في النافلة)','1962','559','0');
INSERT INTO [t19188] VALUES (612,'٥٧٦ - مسألة: (ومن جاء فوجد فرجة وقف فيها، فإن لم يجد','1967','559','0');
INSERT INTO [t19188] VALUES (613,'٥٧٧ - مسألة: (فإن صلى فذا ركعة، لم تصح)','1969','559','0');
INSERT INTO [t19188] VALUES (614,'٥٧٨ - مسألة: (وإن ركع فذا ثم دخل في الصف، أو وقف معه آخر قبل رفع الإمام صحت صلاته، وإن رفع ولم يسجد صحت. وقيل: إن علم النهى لم تصح، وإن فعله لغير عذر لم تصح)','1971','559','0');
INSERT INTO [t19188] VALUES (615,'٥٧٩ - مسألة: (وإذا كان المأموم يرى من وراء الإمام صحت صلاته، إذا اتصلت الصفوف. وإن لم ير من وراءه تصح، وعنه، تصح إذا كانا','1977','559','0');
INSERT INTO [t19188] VALUES (616,'٥٨٠ - مسألة: (ولا يكون الإمام أعلى من المأموم، فإن فعل وكان كثيرا، فهل تصح صلاته؟ على','1985','559','0');
INSERT INTO [t19188] VALUES (617,'٥٨١ - مسألة: (ويكره للإمام أن يصلي في طاق القبلة، أن يتطوع في موضع المكتوبة، إلا من حاجة)','1989','559','0');
INSERT INTO [t19188] VALUES (618,'٥٨٢ - مسألة: (ويكره للمأمومين الوقوف بين السواري إذا قطعت صفوفهم)','1991','559','0');
INSERT INTO [t19188] VALUES (619,'٥٨٣ - مسألة: (ويكره للإمام إطالة القعود بعد الصلاة، مستقبل القبلة)','1992','559','0');
INSERT INTO [t19188] VALUES (620,'٥٨٤ - مسألة: (فإن كان معه نساء، لبث قليلا لينصرف النساء)','1993','559','0');
INSERT INTO [t19188] VALUES (621,'٥٨٥ - مسألة: (وإن أمت امرأة نساء، قامت وسطهن في الصف)','1994','559','0');
INSERT INTO [t19188] VALUES (622,'٥٨٦ - مسألة: (ويعذر في ترك','1996','559','0');
INSERT INTO [t19188] VALUES (623,'٥٨٧ - مسألة: (ومن يدافع أحد الأخبثين، أو بحضرة طعام هو محتاج إليه)','1997','559','0');
INSERT INTO [t19188] VALUES (624,'٥٨٨ - مسألة: (والخائف من ضياع ماله، أو فواته، أو ضرر فيه، [أو موت قريبه، أو]','1998','559','0');
INSERT INTO [t19188] VALUES (625,'٥٨٩ - مسألة: (أو فوات رفقته','2000','559','0');
INSERT INTO [t19188] VALUES (626,'باب صلاة أهل الأعذار','2007','269','0');
INSERT INTO [t19188] VALUES (627,'٥٩٠ - مسألة: (ويصلى المريض كما قال النبى -صلى الله عليه وسلم- لعمران بن حصين: «صل قائما، فإن لم تستطع فقاعدا، فإن لم تستطع فعلى جنب»)','2007','626','0');
INSERT INTO [t19188] VALUES (628,'٥٩١ - مسألة: (فإن صلى على ظهره، ورجلاه إلى القبلة، صحت','2012','626','0');
INSERT INTO [t19188] VALUES (629,'٥٩٢ - مسألة: (ويومئ بالركوع والسجود، ويجعل سجوده أخفض من ركوعه)','2014','626','0');
INSERT INTO [t19188] VALUES (630,'٥٩٣ - مسألة: (فإن عجز عنه أومأ بطرفه، ولا تسقط الصلاة)','2016','626','0');
INSERT INTO [t19188] VALUES (631,'٥٩٤ - مسألة: (وإن قدر على القيام أو القعود فى أثنائها، انتقل إليه وأتمها)','2017','626','0');
INSERT INTO [t19188] VALUES (632,'٥٩٥ - مسألة: (وإن قدر على القيام، وعجز عن الركوع والسجود، أومأ بالركوع قائما، وبالسجود قاعدا)','2018','626','0');
INSERT INTO [t19188] VALUES (633,'٥٩٦ - مسألة: (وإذا قال ثقات من العلماء بالطب للمريض: إن','2019','626','0');
INSERT INTO [t19188] VALUES (634,'٥٩٧ - مسألة: (ولا تصح الصلاة فى السفينة قاعدا لقادر على القيام)','2021','626','0');
INSERT INTO [t19188] VALUES (635,'٥٩٨ - مسألة: (وتجوز صلاة الفرض على الراحلة خشية التأذى بالوحل','2022','626','0');
INSERT INTO [t19188] VALUES (636,'٥٩٩ - مسألة: (وهل يجوز)','2024','626','0');
INSERT INTO [t19188] VALUES (637,'فصل فى قصر الصلاة','2028','626','0');
INSERT INTO [t19188] VALUES (638,'٦٠٠ - مسألة: (ومن سافر سفرا مباحا، يبلغ ستة عشر فرسخا، فله قصر الرباعية خاصة إلى ركعتين)','2030','626','0');
INSERT INTO [t19188] VALUES (639,'٦٠١ - مسألة: (إذا جاوز بيوت قريته، أو خيام قومه)','2046','626','0');
INSERT INTO [t19188] VALUES (640,'٦٠٢ - مسألة: (وهو أفضل من الإتمام، وإن أتم جاز)','2050','626','0');
INSERT INTO [t19188] VALUES (641,'٦٠٣ - مسألة: (فإن أحرم فى الحضر ثم سافر، أو فى السفر ثم','2054','626','0');
INSERT INTO [t19188] VALUES (642,'٦٠٤ - مسألة: (ومن له طريقان؛ بعيد وقريب، فسلك البعيد، أو ذكر صلاة سفر فى آخر، فله القصر)','2066','626','0');
INSERT INTO [t19188] VALUES (643,'٦٠٥ - مسألة: (وإذا نوى الإقامة ببلد أكثر من إحدى وعشرين','2070','626','0');
INSERT INTO [t19188] VALUES (644,'٦٠٦ - مسألة: (وإن أقام لقضاء حاجة، أو حبس، ولم ينو الإقامة، قصر أبدا)','2077','626','0');
INSERT INTO [t19188] VALUES (645,'٦٠٧ - مسألة: (والملاح الذى معه أهله، وليس له نية الإقامة ببلد، ليس له الترخص)','2084','626','0');
INSERT INTO [t19188] VALUES (646,'فصل فى الجمع','2086','626','0');
INSERT INTO [t19188] VALUES (647,'٦٠٨ - مسألة: (يجوز الجمع بين الظهر والعصر، والعشاءين فى','2086','626','0');
INSERT INTO [t19188] VALUES (648,'٦٠٩ - مسألة: (والمرض الذى يلحقه بترك الجمع فيه مشقة','2090','626','0');
INSERT INTO [t19188] VALUES (649,'٦١٠ - مسألة: (والمطر الذى يبل الثياب، إلا أن جمع المطر يختص بالعشاءين، فى أصح الوجهين)','2093','626','0');
INSERT INTO [t19188] VALUES (650,'٦١١ - مسألة: (وهل يجوز ذلك لأجل الوحل، أو الريح الشديدة الباردة، أو لمن يصلى فى بيته، أو فى مسجد طريقه تحت ساباط','2096','626','0');
INSERT INTO [t19188] VALUES (651,'٦١٢ - مسألة: (ويفعل الأرفق به؛ من تأخير الأولى إلى وقت الثانية، أو تقديم الثانية إليها)','2100','626','0');
INSERT INTO [t19188] VALUES (652,'٦١٣ - مسألة: (وللجمع فى وقت الأولى ثلاثة شروط؛ نية','2104','626','0');
INSERT INTO [t19188] VALUES (653,'٦١٤ - مسألة: (وإن جمع فى وقت الثانية كفاه نية الجمع فى وقت الأولى، ما لم يضق عن فعلها، واستمرار العذر إلى دخول وقت الثانية','2111','626','0');
INSERT INTO [t19188] VALUES (654,'فصل فى صلاة الخوف','2116','626','0');
INSERT INTO [t19188] VALUES (655,'٦١٥ - مسألة؛ (قال الإمام أبو عبد الله: صح عن النبى -صلى الله عليه وسلم- صلاة الخوف من خمسة أوجه، أو ستة)','2119','626','0');
INSERT INTO [t19188] VALUES (656,'٦١٦ - مسألة: (فإن كانت الصلاة مغربا، صلى)','2131','626','0');
INSERT INTO [t19188] VALUES (657,'٦١٧ - مسألة: (وإن كانت رباعية غير مقصورة، صلى بكل طائفة ركعتين، وأتمت الأولى بـ {الحمد لله} فى كل ركعة، والأخرى تتم بـ {الحمد لله} وسورة)','2132','626','0');
INSERT INTO [t19188] VALUES (658,'٦١٨ - مسألة: (وهل تفارقه الأولى فى التشهد الأول أو فى الثالثة؟ على وجهين)','2133','626','0');
INSERT INTO [t19188] VALUES (659,'٦١٩ - مسألة: (وإن فرقهم أربعا، فصلى بكل طائفة ركعة، صحت صلاة الأوليين، وبطلت صلاة الإمام، والأخريين إن علمتا بطلان صلاته)','2135','626','0');
INSERT INTO [t19188] VALUES (660,'٦٢٠ - مسألة: (ويستحب أن يحمل معه فى الصلاة من السلاح','2144','626','0');
INSERT INTO [t19188] VALUES (661,'٦٢١ - مسألة: (وإذا اشتد الخوف، صلوا رجالا وركبانا، إلى','2147','626','0');
INSERT INTO [t19188] VALUES (662,'٦٢٢ - مسألة: (فإن أمكنهم افتتاح الصلاة إلى القبلة، فهل يلزمهم ذلك؟ على روايتين)','2150','626','0');
INSERT INTO [t19188] VALUES (663,'٦٢٣ - مسألة: (ومن هرب من عدو هربا مباحا، أو من سيل، أو سبع ونحوه، فله [أن يصلى]','2151','626','0');
INSERT INTO [t19188] VALUES (664,'٦٢٤ - مسألة: (وهل لطالب العدو الخائف فواته الصلاة كذلك؟ على روايتين)','2153','626','0');
INSERT INTO [t19188] VALUES (665,'٦٢٥ - مسألة: (ومن أمن فى الصلاة أتم صلاة آمن، وإن ابتدأها آمنا فخاف، أتم صلاة خائف)','2155','626','0');
INSERT INTO [t19188] VALUES (666,'٦٢٦ - مسألة','2157','626','0');
INSERT INTO [t19188] VALUES (667,'باب صلاة الجمعة','2159','269','0');
INSERT INTO [t19188] VALUES (668,'٦٢٧ - مسألة: (وهى واجبة على كل مسلم، مكلف، ذكر، حر، مستوطن ببناء، ليس بينه وبين موضع الجمعة أكثر من فرسخ، إذا لم يكن له عذر)','2162','667','0');
INSERT INTO [t19188] VALUES (669,'٦٢٨ - مسألة: (ولا تجب على مسافر، ولا عبد، ولا امرأة، ولا خنثى)','2170','667','0');
INSERT INTO [t19188] VALUES (670,'٦٢٩ - مسألة: (ومن حضرها منهم أجزأته، ولم تنعقد به، ولم يجز له أن يؤم فيها. وعنه، فى العبد، أنها تجب عليه)','2174','667','0');
INSERT INTO [t19188] VALUES (671,'٦٣٠ - مسألة: (ومن سقطت عنه لعذر، إذا حضرها وجبت عليه، وانعقدت به)','2176','667','0');
INSERT INTO [t19188] VALUES (672,'٦٣١ - مسألة: (ومن صلى الظهر ممن عليه حضور الجمعة قبل صلاة الإمام، لم تصح صلاته، والأفضل لمن لا تجب عليه أن لا يصلى الظهر حتى يصلى الإمام)','2177','667','0');
INSERT INTO [t19188] VALUES (673,'٦٣٢ - مسألة: (ولا يجوز لمن تلزمه الجمعة السفر فى يومها بعد الزوال)','2183','667','0');
INSERT INTO [t19188] VALUES (674,'٦٣٣ - مسألة: (ويجوز قبله. وعنه، لا يجوز. وعنه، يجوز للجهاد خاصة)','2184','667','0');
INSERT INTO [t19188] VALUES (675,'فصل','2186','667','0');
INSERT INTO [t19188] VALUES (676,'٦٣٤ - مسألة: (فإن خرج وقتها قبل فعلها، صلوا ظهرا)','2191','667','0');
INSERT INTO [t19188] VALUES (677,'٦٣٥ - مسألة: (وإن خرج وقد صلوا ركعة، أتموها جمعة، وإن خرج قبل ركعة، فهل يتمونها ظهرا، أو يستأنفونها؟ على وجهين)','2191','667','0');
INSERT INTO [t19188] VALUES (678,'٦٣٦ - مسألة: (الثانى، أن يكون بقرية يستوطنها أربعون من','2194','667','0');
INSERT INTO [t19188] VALUES (679,'٦٣٧ - مسألة: (ويجوز إقامتها فى الأبنية المتفرقة إذا شملها اسم واحد، وفيما قارب البنيان من الصحراء)','2196','667','0');
INSERT INTO [t19188] VALUES (680,'٦٣٨ - مسألة: (الثالث، حضور أربعين من أهل القرية، فى ظاهر المذهب. وعنه، تنعقد بثلاثة)','2199','667','0');
INSERT INTO [t19188] VALUES (681,'٦٣٩ - مسألة: (فإن نقصوا قبل إتمامها، استأنفوا ظهرا. ويحتمل','2202','667','0');
INSERT INTO [t19188] VALUES (682,'٦٤٠ - مسألة: (ومن أدرك مع الإمام منها ركعة أتمها جمعة)','2205','667','0');
INSERT INTO [t19188] VALUES (683,'٦٤١ - مسألة: (ومن أدرك أقل من ركعة أتمها ظهرا، إذا كان قد نوى الظهر فى قول الخرقى. وقال أبو إسحاق بن شاقلا: ينوى جمعة، ويتمها ظهرا)','2206','667','0');
INSERT INTO [t19188] VALUES (684,'٦٤٢ - مسألة: (ومن أحرم مع الإمام، ثم زحم عن السجود، سجد على ظهر إنسان أو رجله)','2210','667','0');
INSERT INTO [t19188] VALUES (685,'٦٤٣ - مسألة: (فإن لم يمكنه سجد إذا زال الزحام، إلا أن يخاف فوات الثانية، فيتابع الإمام فيها، وتصير أولاه، ويتمها جمعة)','2212','667','0');
INSERT INTO [t19188] VALUES (686,'٦٤٤ - مسألة: (فإن لم يتابعه عالما بتحريم ذلك بطلت صلاته، وإن جهل تحريمه فسجد ثم أدرك الإمام فى التشهد، أتى بركعة أخرى بعد سلام الإمام، وصحت جمعته. وعنه، يتمها ظهرا)','2216','667','0');
INSERT INTO [t19188] VALUES (687,'٦٤٥ - مسألة: (الرابع، أن يتقدمها خطبتان، ومن شرط صحتهما حمد الله تعالى، والصلاة على رسوله -صلى الله عليه وسلم-، وقراءة آية، والوصية بتقوى الله','2219','667','0');
INSERT INTO [t19188] VALUES (688,'٦٤٦ - مسألة: (وهل يشترط لهما الطهارة، وأن يتولاهما من يتولى الصلاة؟ على روايتين)','2230','667','0');
INSERT INTO [t19188] VALUES (689,'٦٤٧ - مسألة: (ومن سننهما أن يخطب على منبر، أو موضع عال)','2236','667','0');
INSERT INTO [t19188] VALUES (690,'٦٤٨ - مسألة: (ويسلم على المأمومين إذا أقبل عليهم)','2237','667','0');
INSERT INTO [t19188] VALUES (691,'٦٤٩ - مسألة: (ثم يجلس إلى فراغ الأذان، ويجلس بين الخطبتين)','2238','667','0');
INSERT INTO [t19188] VALUES (692,'٦٥٠ - مسألة: (ويخطب قائما)','2239','667','0');
INSERT INTO [t19188] VALUES (693,'٦٥١ - مسألة: (ويعتمد على سيف، أو قوس، أو عصا)','2241','667','0');
INSERT INTO [t19188] VALUES (694,'٦٥٢ - مسألة: (ويقصد تلقاء وجهه)','2241','667','0');
INSERT INTO [t19188] VALUES (695,'٦٥٣ - مسألة: (ويستحب تقصير الخطبة)','2243','667','0');
INSERT INTO [t19188] VALUES (696,'٦٥٤ - مسألة: (و)','2244','667','0');
INSERT INTO [t19188] VALUES (697,'٦٥٥ - مسألة: (ولا يشترط إذن الإمام. وعنه، يشترط)','2247','667','0');
INSERT INTO [t19188] VALUES (698,'فصل','2249','667','0');
INSERT INTO [t19188] VALUES (699,'٦٥٦ - مسألة: (ويستحب أن يقرأ فى الأولى بسورة الجمعة،','2249','667','0');
INSERT INTO [t19188] VALUES (700,'٦٥٧ - مسألة: (وتجوز إقامة الجمعة فى موضعين من البلد للحاجة، ولا يجوز مع عدمها)','2253','667','0');
INSERT INTO [t19188] VALUES (701,'٦٥٨ - مسألة: (فإن فعلوا فجمعة الإمام هى الصحيحة)','2256','667','0');
INSERT INTO [t19188] VALUES (702,'٦٥٩ - مسألة: (فإن استويا فالثانية باطلة)','2257','667','0');
INSERT INTO [t19188] VALUES (703,'٦٦٠ - مسألة: (فإن وقعتا معا، أو جهلت الأولى بطلتا معا)','2258','667','0');
INSERT INTO [t19188] VALUES (704,'٦٦١ - مسألة: (وإذا وقع العيد يوم الجمعة فاجتزئ بالعيد)','2261','667','0');
INSERT INTO [t19188] VALUES (705,'٦٦٢ - مسألة: (وأقل السنة بعد الجمعة ركعتان، وأكثرها ست ركعات)','2265','667','0');
INSERT INTO [t19188] VALUES (706,'فصل','2269','667','0');
INSERT INTO [t19188] VALUES (707,'٦٦٣ - مسألة: (ويتنظف، ويتطيب، ويلبس أحسن ثيابه)','2274','667','0');
INSERT INTO [t19188] VALUES (708,'٦٦٤ - مسألة: (وييكر إليها ماشيا، ويدنو من الإمام)','2276','667','0');
INSERT INTO [t19188] VALUES (709,'٦٦٥ - مسألة: (ويشتغل بالصلاة والذكر، ويقرأ سورة الكهف','2282','667','0');
INSERT INTO [t19188] VALUES (710,'٦٦٦ - مسألة: (ولا يتخطى رقاب الناس، إلا أن يكون إماما، أو يرى فرجة فيتخطى إليها. وعنه، يكره)','2288','667','0');
INSERT INTO [t19188] VALUES (711,'٦٦٧ - مسألة: (ولا يقيم غيره فيجلس فى مكانه، إلا من قدم صاحبا له فجلس فى موضع يحفظه له)','2291','667','0');
INSERT INTO [t19188] VALUES (712,'٦٦٨ - مسألة: (وإن وجد مصلى مفروشا، فهل له رفعه؟ على روايتين)','2295','667','0');
INSERT INTO [t19188] VALUES (713,'٦٦٩ - مسألة: (ومن قام من موضعه لعارض لحقه، ثم عاد إليه فهو أحق به)','2296','667','0');
INSERT INTO [t19188] VALUES (714,'٦٧٠ - مسألة: (ومن دخل والإمام يخطب لم يجلس حتى يركع ركعتين، يوجز فيهما)','2299','667','0');
INSERT INTO [t19188] VALUES (715,'٦٧١ - مسألة: (ولا يجوز الكلام والإمام يخطب، إلا له، أو لمن كلمه)','2302','667','0');
INSERT INTO [t19188] VALUES (716,'٦٧٢ - مسألة: (ويجوز الكلام قبل الخطبة وبعدها. وعنه، يجوز فيها)','2310','667','0');
INSERT INTO [t19188] VALUES (717,'باب صلاة العيدين','2316','269','0');
INSERT INTO [t19188] VALUES (718,'٦٧٣ - مسألة: (وهى فرض على الكفاية، إن اتفق أهل بلد على تركها قاتلهم الإمام)','2317','717','0');
INSERT INTO [t19188] VALUES (719,'٦٧٤ - مسألة: (وأول وقتها إذا ارتفعت الشمس، وآخره إذا زالت)','2319','717','0');
INSERT INTO [t19188] VALUES (720,'٦٧٥ - مسألة: (فإن لم يعلم بالعيد إلا بعد الزوال، خرج من الغد فصلى بهم)','2320','717','0');
INSERT INTO [t19188] VALUES (721,'٦٧٦ - مسألة: (ويسن تقديم الأضحى، وتأخير الفطر، والأكل فى الفطر قبل الصلاة، والإمساك فى الأضحى حتى يصلى)','2322','717','0');
INSERT INTO [t19188] VALUES (722,'٦٧٧ - مسألة: (و)','2324','717','0');
INSERT INTO [t19188] VALUES (723,'٦٧٨ - مسألة: (وإذا غدا من طريق، رجع فى أخرى)','2331','717','0');
INSERT INTO [t19188] VALUES (724,'٦٧٩ - مسألة: (وهل من شرطها الاستيطان، وإذن الإمام، والعدد المشروط للجمعة؟ على روايتين)','2334','717','0');
INSERT INTO [t19188] VALUES (725,'٦٨٠ - مسألة: (وتسن فى الصحراء، وتكره فى الجامع، إلا من عذر)','2337','717','0');
INSERT INTO [t19188] VALUES (726,'٦٨١ - مسألة: (ويبدأ بالصلاة، فيصلى ركعتين)','2340','717','0');
INSERT INTO [t19188] VALUES (727,'٦٨٢ - مسألة: (يكبر فى الأولى بعد الاستفتاح وقبل التعوذ ستا، وفى الثانية بعد القيام من السجود خمسا)','2342','717','0');
INSERT INTO [t19188] VALUES (728,'٦٨٣ - مسألة: و (يرفع يديه مع كل تكبيرة)','2346','717','0');
INSERT INTO [t19188] VALUES (729,'٦٨٤ - مسألة: (ويقول: الله أكبر كبيرا، والحمد لله كثيرا، وسبحان الله بكرة وأصيلا، وصلى الله على محمد النبى وآله وصحبه وسلم تسليما كثيرا. وإن أحب قال غير ذلك)','2346','717','0');
INSERT INTO [t19188] VALUES (730,'٦٨٥ - مسألة: (ثم يقرأ بعد الفاتحة فى الأولى بـ «سبح»','2348','717','0');
INSERT INTO [t19188] VALUES (731,'٦٨٦ - مسألة: (ويكون بعد التكبير فى الركعتين. وعنه، يوالى بين القراءتين)','2350','717','0');
INSERT INTO [t19188] VALUES (732,'٦٨٧ - مسألة: (فإذا سلم خطب خطبتين، يجلس بينهما، يفتتح الأولى بتسع تكبيرات، والثانية بسبع، يحثهم فى خطبة الفطر على الصدقة، ويبين لهم ما يخرجون، ويرغبهم فى الأضحية فى الأضحى، ويبين لهم حكم الأضحية)','2352','717','0');
INSERT INTO [t19188] VALUES (733,'٦٨٨ - مسألة: (والتكبيرات الزوائد، والذكر بينهما، والخطبتان، سنة)','2356','717','0');
INSERT INTO [t19188] VALUES (734,'٦٨٩ - مسألة: (ولا يتنفل قبل صلاة العيد ولا بعدها، فى موضع الصلاة)','2359','717','0');
INSERT INTO [t19188] VALUES (735,'٦٩٠ - مسألة: (ومن كبر قبل سلام الإمام، صلى ما فاته عك صفته)','2363','717','0');
INSERT INTO [t19188] VALUES (736,'٦٩١ - مسألة: (وإن فاتته الصلاة، استحب أن يقضيها على صفتها. وعنه، يقضيها أربعا. وعنه، أنه مخير بين ركعتين وأربع)','2365','717','0');
INSERT INTO [t19188] VALUES (737,'٦٩٢ - مسألة: (ويستحب التكبير فى ليلتى العيدين)','2367','717','0');
INSERT INTO [t19188] VALUES (738,'٦٩٣ - مسألة: (وفى الأضحى، يكبر عقيب كل فريضة فى جماعة. وعنه، يكبر، وإن كان وحده، من صلاة الفجر يوم عرفة، إلى العصر من آخر أيام التشريق)','2370','717','0');
INSERT INTO [t19188] VALUES (739,'٦٩٤ - مسألة: (وإن نسى التكبير قضاه، ما لم يحدث، أو يخرج من المسجد)','2378','717','0');
INSERT INTO [t19188] VALUES (740,'٦٩٥ - مسألة: (وفى التكبير عقيب العيد وجهان)','2380','717','0');
INSERT INTO [t19188] VALUES (741,'٦٩٦ - مسألة: (وصفة التكبير شفعا؛ الله أكبر الله أكبر، لا إله إلا الله، والله أكبر الله أكبر، ولله الحمد)','2381','717','0');
INSERT INTO [t19188] VALUES (742,'باب صلاة الكسوف','2385','269','0');
INSERT INTO [t19188] VALUES (743,'٦٩٧ - مسألة: (وإذا كسفت الشمس أو القمر، فزع الناس إلى الصلاة، جماعة وفرادى، بإذن الإمام وغير إذنه)','2385','742','0');
INSERT INTO [t19188] VALUES (744,'٦٩٨ - مسألة: (ثم يصلى ركعتين، يقرأ فى الأولى بعد الفاتحة سورة طويلة، ويجهر بالقراءة، ثم يركع ركوعا طويلا، ثم يرفع، فيسمع ويحمد، ويقرأ الفاتحة وسورة، ويطيل، وهو دون القيام الأول، ثم يركع ويطيل، وهو دون الركوع الأول، ثم يرفع، ثم يسجد سجدتين طويلتين، ثم يقوم إلى الثانية، فيفعل مثل ذلك، ثم يتشهد، ويسلم)','2389','742','0');
INSERT INTO [t19188] VALUES (745,'٦٩٩ - مسألة: (فإن تجلى الكسوف فيها أتمها خفيفة، وإن تجلى قبلها، أو غابت الشمس كاسفة، أو طلعت والقمر خاسف، لم يصل)','2398','742','0');
INSERT INTO [t19188] VALUES (746,'٧٠٠ - مسألة: (وإن أتى فى كل ركعة بثلاث ركوعات، أو أربع، فلا بأس)','2402','742','0');
INSERT INTO [t19188] VALUES (747,'٧٠١ - مسألة: (ولا يصلى لشئ من سائر الآيات، إلا الزلزلة الدائمة)','2405','742','0');
INSERT INTO [t19188] VALUES (748,'باب صلاة الاستسقاء','2409','269','0');
INSERT INTO [t19188] VALUES (749,'٧٠٢ - مسألة: (وإذا أجدبت الأرض، وقحط المطر، فزع الناس إلى الصلاة)','2409','748','0');
INSERT INTO [t19188] VALUES (750,'٧٠٣ - مسألة: (وصفتها فى موضعها وأحكامها صفة صلاة العيد)','2411','748','0');
INSERT INTO [t19188] VALUES (751,'٧٠٤ - مسألة: (وإذا أراد الإمام الخروج إليها، وعظ الناس، وأمرهم بالتوبة من المعاصى، والخروج من المظالم، والصيام، والصدقة، وترك التشاحن)','2414','748','0');
INSERT INTO [t19188] VALUES (752,'٧٠٥ - مسألة: (ويعدهم يوما يخرجون فيه)','2415','748','0');
INSERT INTO [t19188] VALUES (753,'٧٠٦ - مسألة: (ويتنظف لها)','2415','748','0');
INSERT INTO [t19188] VALUES (754,'٧٠٧ - مسألة: (ويخرج متواضعا، متخشعا، متذللا، متضرعا)','2415','748','0');
INSERT INTO [t19188] VALUES (755,'٧٠٨ - مسألة: (و)','2416','748','0');
INSERT INTO [t19188] VALUES (756,'٧٠٩ - مسألة: (ويجوز خبروج الصبيان)','2416','748','0');
INSERT INTO [t19188] VALUES (757,'٧١٠ - مسألة: (وإن خرج معهم أهل الذمة لم يمنعوا، ولم يختلطوا بالمسلمين)','2417','748','0');
INSERT INTO [t19188] VALUES (758,'٧١١ - مسألة: (فيصلى بهم، ثم يخطب خطبة واحدة، يفتتحها بالتكبير، كخطبة العيد)','2420','748','0');
INSERT INTO [t19188] VALUES (759,'٧١٢ - مسألة: (ويكثر فيها الاستغفار، وقراءة الآيات التى فيها الأمر به)','2423','748','0');
INSERT INTO [t19188] VALUES (760,'٧١٣ - مسألة: (ويرفع يديه، فيدعو بدعاء النبى -صلى الله عليه وسلم-)','2424','748','0');
INSERT INTO [t19188] VALUES (761,'٧١٤ - مسألة: (ويستقبل القبلة فى أثناء الخطبة، ويحول رداءه، ويجعل الأيسر على الأيمن، والأيمن على الأيسر، ويفعل الناس','2428','748','0');
INSERT INTO [t19188] VALUES (762,'٧١٥ - مسألة: (ويدعو سرا حال استقبال القبلة، فيقول: اللهم','2430','748','0');
INSERT INTO [t19188] VALUES (763,'٧١٦ - مسألة: (فإن سقوا، وإلا عادوا ثانيا وثالثا. وإن سقوا قبل خروجهم، شكروا الله تعالى، وسألوه المزيد من فضله)','2432','748','0');
INSERT INTO [t19188] VALUES (764,'٧١٧ - مسألة: (وينادى لها: الصلاة جامعة)','2434','748','0');
INSERT INTO [t19188] VALUES (765,'٧١٨ - مسألة: (وهل من شرطها إذن الإمام؟ على روايتين)','2434','748','0');
INSERT INTO [t19188] VALUES (766,'٧١٩ - مسألة: (ويستحب أن يقف فى أول المطر، ويخرج رحله وثيابه؛ ليصيبها)','2435','748','0');
INSERT INTO [t19188] VALUES (767,'٧٢٠ - مسألة: (وإذا زادت المياه فخيف منها، استحب له أن يقول: اللهم حوالينا ولا علينا، اللهم على الظراب، والآكام، وبطون','2437','748','0');
INSERT INTO [t19188] VALUES (768,'كتاب الجنائز','2441','0','0');
INSERT INTO [t19188] VALUES (769,'٧٢١ - مسألة: و (تستحب عيادة المريض، وتذكيره التوبة والوصية)','2443','768','0');
INSERT INTO [t19188] VALUES (770,'٧٢٢ - مسألة: (وإذا نزل به، تعاهد بل حلقه بماء أو شراب، وندى شفتيه بقطنة)','2449','768','0');
INSERT INTO [t19188] VALUES (771,'٧٢٣ - مسألة: (و)','2449','768','0');
INSERT INTO [t19188] VALUES (772,'٧٢٤ - مسألة: (ولا يزيد على ثلاث)','2450','768','0');
INSERT INTO [t19188] VALUES (773,'٧٢٥ - مسألة: ويقرأ عنده سورة يس) لما روى معقل بن يسار، قال: قال رسول الله - صلى الله عليه وسلم -: «اقرأوا يس على موتاكم». رواه أبو داود','2451','768','0');
INSERT INTO [t19188] VALUES (774,'٧٢٦ - مسألة: (ويوجهه إلى القبلة)','2452','768','0');
INSERT INTO [t19188] VALUES (775,'٧٢٧ - مسألة: (فإذا مات أغمض عينيه، وشد لحييه، ولين مفاصله، وخلع ثيابه، وسجاه بثوب يستره، وجعل على بطنه مرآة أو نحوها، ووضعه على سرير غسله، متوجها، منحدرا نحو رجليه)','2454','768','0');
INSERT INTO [t19188] VALUES (776,'٧٢٨ - مسألة: (ويسارع في قضاء دينه)','2456','768','0');
INSERT INTO [t19188] VALUES (777,'٧٢٩ - مسألة: (و)','2457','768','0');
INSERT INTO [t19188] VALUES (778,'٧٣٠ - مسألة: (و)','2457','768','0');
INSERT INTO [t19188] VALUES (779,'٧٣١ - مسألة: (غسل الميت ودفنه وتكفينه والصلاة عليه، فرض كفاية)','2461','768','0');
INSERT INTO [t19188] VALUES (780,'٧٣٢ - مسألة: (وأحق الناس به وصيه، ثم أبوه، ثم جده، ثم الأقرب فالأقرب من عصباته، ثم ذوو أرحامه، إلا الصلاة عليه، فإن الأمير أحق بها بعد وصيه)','2465','768','0');
INSERT INTO [t19188] VALUES (781,'٧٣٣ - مسألة: (وأحق الناس بغسل المرأة)','2476','768','0');
INSERT INTO [t19188] VALUES (782,'٧٣٤ - مسألة: (ولكل واحد من الزوجين غسل صاحبه فى إحدى الروايتين، كذلك السيد مع سريته)','2477','768','0');
INSERT INTO [t19188] VALUES (783,'٧٣٥ - مسألة: (وللرجل والمرأة غسل من له دون سبع سنين، وفي ابن السبع وجهان)','2485','768','0');
INSERT INTO [t19188] VALUES (784,'٧٣٦ - مسألة: (وإذا مات رجل بين نسوة، أو امرأة بين رجال، أو خنثى مشكل، يمم فى أصح الروايتين. وفى الأخرى، يصب عليه الماء من فوق القميص، ولا يمس)','2488','768','0');
INSERT INTO [t19188] VALUES (785,'٧٣٧ - مسألة: (ولا يغسل مسلم كافرا, ولا يدفنه، إلا أن لا يجد من يواريه غيره)','2490','768','0');
INSERT INTO [t19188] VALUES (786,'٧٣٨ - مسألة: (وإذا أخذ فى غسله، ستر عورته وجرده. وقال القاضى: يغسله','2492','768','0');
INSERT INTO [t19188] VALUES (787,'٧٣٩ - مسألة: (ويستر الميت عن العيون، ولا يحضره إلا من يعين فى غسله)','2495','768','0');
INSERT INTO [t19188] VALUES (788,'٧٤٠ - مسألة: (ثم يرفع رأسه برفق إلى قريب من الجلوس، ويعصر بطنه عصرا رفيقا، ويكثر صب الماء حينئذ)','2497','768','0');
INSERT INTO [t19188] VALUES (789,'٧٤١ - مسألة: (ثم يلف على يده خرقة، فينجيه. ولا يحل مس عورته، ويستحب أن لا يمس سائر بدنه إلا بخرقة)','2498','768','0');
INSERT INTO [t19188] VALUES (790,'٧٤٢ - مسألة: (ثم ينوى غسله','2499','768','0');
INSERT INTO [t19188] VALUES (791,'٧٤٣ - مسألة؛ قال: (ويدخل إصبعيه مبلولتين بالماء بين شفتيه، فيمسح أسنانه، وفى منخريه فينظفهما، ويوضئه، ولا يدخل الماء فى فيه ولا أنفه)','2501','768','0');
INSERT INTO [t19188] VALUES (792,'٧٤٤ - مسألة: (ثم يضرب السدر، فيغسل برغوته رأسه ولحيته','2502','768','0');
INSERT INTO [t19188] VALUES (793,'٧٤٥ - مسألة: (فإن لم ينق بالثلاث، [أو خرج]','2509','768','0');
INSERT INTO [t19188] VALUES (794,'٧٤٦ - مسألة: (ويجعل فى الغسلة الأخيرة كافورا)','2512','768','0');
INSERT INTO [t19188] VALUES (795,'٧٤٧ - مسألة: (والماء الحار، والخلال، والأشنان','2513','768','0');
INSERT INTO [t19188] VALUES (796,'٧٤٨ - مسألة: (ويقص شاربه، ويقلم أظفاره، ولا يسرح شعره، ولا لحيته)','2514','768','0');
INSERT INTO [t19188] VALUES (797,'٧٤٩ - مسألة: (ويضفر شعر المرأة ثلاثة قرون، ويسدل من ورائها)','2519','768','0');
INSERT INTO [t19188] VALUES (798,'٧٥٠ - مسألة: (ثم ينشفه بثوب)','2520','768','0');
INSERT INTO [t19188] VALUES (799,'٧٥١ - مسألة: (فإن خرج منه شئ بعد السبع حشاه بالقطن،','2520','768','0');
INSERT INTO [t19188] VALUES (800,'٧٥٢ - مسألة: (ثم يغسل المحل ويوضأ)','2521','768','0');
INSERT INTO [t19188] VALUES (801,'٧٥٣ - مسألة: (فإن خرج منه شئ بعد وضعه فى أكفانه، لم يعد إلى الغسل)','2522','768','0');
INSERT INTO [t19188] VALUES (802,'٧٥٤ - مسألة: (ويغسل المحرم بماء وسدر، ولا يلبس المخيط، ولا يخمر رأسه، ولا يقرب طيبا)','2523','768','0');
INSERT INTO [t19188] VALUES (803,'٧٥٥ - مسألة: (والشهيد لا يغسل، إلا أن يكون جنبا)','2526','768','0');
INSERT INTO [t19188] VALUES (804,'٧٥٦ - مسألة: (وينزع عنه السلاح والجلود، ويزمل فى ثيابه،','2529','768','0');
INSERT INTO [t19188] VALUES (805,'٧٥٧ - مسألة: (ولا يصلى عليه، فى أصح الروايتين)','2531','768','0');
INSERT INTO [t19188] VALUES (806,'٧٥٨ - مسألة: (وإن سقط من دابته، [أو وجد ميتا ولا]','2534','768','0');
INSERT INTO [t19188] VALUES (807,'٧٥٩ - مسألة: (ومن قتل مظلوما، فهل يلحق بالشهيد؟ على روايتين)','2540','768','0');
INSERT INTO [t19188] VALUES (808,'٧٦٠ - مسألة: (وإذا ولد السقط لأكثر من أربعة أشهر، غسل','2543','768','0');
INSERT INTO [t19188] VALUES (809,'٧٦١ - مسألة: (ومن تعذر غسله يمم)','2547','768','0');
INSERT INTO [t19188] VALUES (810,'٧٦٢ - مسألة: (وعلى الغاسل ستر ما رآه إن لم يكن حسنا)','2548','768','0');
INSERT INTO [t19188] VALUES (811,'٧٦٣ - مسألة: (ويجب كفن الميت فى ماله، مقدما على الدين وغيره)','2550','768','0');
INSERT INTO [t19188] VALUES (812,'٧٦٤ - مسألة: (فإن لم يكن له مال، فعلى من تلزمه نفقته، إلا الزوج لا يلزمه كفن امرأته)','2552','768','0');
INSERT INTO [t19188] VALUES (813,'٧٦٥ - مسألة: (ويستحب تكفين الرجل فى ثلاث لفائف بيض، يبسط بعضها فوق بعض بعد تجميرها)','2556','768','0');
INSERT INTO [t19188] VALUES (814,'٧٦٦ - مسألة:. (ثم يوضع عليها مستلقيا، ويجعل الحنوط','2559','768','0');
INSERT INTO [t19188] VALUES (815,'٧٦٧ - مسألة: (ثم يرد طرف اللفافة العليا على شقه الأيمن، ثم يرد طرفها الآخر على شقه الأيسر)','2561','768','0');
INSERT INTO [t19188] VALUES (816,'٧٦٨ - مسألة: (وإن كفن فى قميص ومئزر ولفافة، جاز)','2563','768','0');
INSERT INTO [t19188] VALUES (817,'٧٦٩ - مسألة: (وتكفن المرأة فى خمسة أثواب، إزار، وخمار، وقميص، ولفافتين)','2564','768','0');
INSERT INTO [t19188] VALUES (818,'٧٧٠ - مسألة: (والواجب من ذلك ثوب يستر جميعه)','2568','768','0');
INSERT INTO [t19188] VALUES (819,'٧٧١ - مسألة: (السنة أن يقوم الإمام عند رأس الرجل، ووسط المرأة)','2572','768','0');
INSERT INTO [t19188] VALUES (820,'٧٧٢ - مسألة: (ويقدم إلى الإمام أفضلهم، ويجعل وسط المرأة حذاء رأس الرجل. وقال القاضى: يسوى بين رءوسهم)','2574','768','0');
INSERT INTO [t19188] VALUES (821,'٧٧٣ - مسألة: (ثم يكبر أربع تكبيرات، يقرأ فى الأولى الفاتحة، ويصلى على النبى - صلى الله عليه وسلم - فى الثانية)','2580','768','0');
INSERT INTO [t19188] VALUES (822,'٧٧٤ - مسألة: (ويدعو فى)','2584','768','0');
INSERT INTO [t19188] VALUES (823,'٧٧٥ - مسألة: (وإن كان صبيا)','2588','768','0');
INSERT INTO [t19188] VALUES (824,'٧٧٦ - مسألة: (ثم يقف بعد الرابعة قليلا، ويسلم تسليمة واحدة عن يمينه)','2590','768','0');
INSERT INTO [t19188] VALUES (825,'٧٧٧ - مسألة: (ويرفع يديه مع كل تكبيرة)','2594','768','0');
INSERT INTO [t19188] VALUES (826,'٧٧٨ - مسألة: (والواجب من ذلك، التكبيرات، والقيام و)','2595','768','0');
INSERT INTO [t19188] VALUES (827,'٧٧٩ - مسألة: (وإن كبر الإمام خمسا كبر بتكبيره. وعنه، لا يتابع في زيادة على أربع. وعنه، يتابع إلى سبع.)','2600','768','0');
INSERT INTO [t19188] VALUES (828,'٧٨٠ - مسألة: (ومن فاته شيء من التكبير، قضاه على صفته. وقال الخرقى: يقضيه متتابعا)','2608','768','0');
INSERT INTO [t19188] VALUES (829,'٧٨١ - مسألة: (فإن سلم ولم يقضه.، فعلى روايتين)','2610','768','0');
INSERT INTO [t19188] VALUES (830,'٧٨٢ - مسألة: (ومن فاتته الصلاة على الجنازة، صلى على القبر إلى شهر)','2612','768','0');
INSERT INTO [t19188] VALUES (831,'٧٨٣ - مسألة: (ويصلى على الغائب بالنية، فإن كان في أحد جانبى البلد، لم يصل','2617','768','0');
INSERT INTO [t19188] VALUES (832,'٧٨٤ - مسألة: (ولا يصلى الإمام على الغال، ولا من قتل نفسه)','2620','768','0');
INSERT INTO [t19188] VALUES (833,'٧٨٥ - مسألة: (وإن وجد بعض الميت، غسل وصلى عليه.','2627','768','0');
INSERT INTO [t19188] VALUES (834,'٧٨٦ - مسألة: (وإن اختلط من يصلى عليه بمن لا يصلى عليه، صلى على الجميع، ينوى من يصلى عليه)','2630','768','0');
INSERT INTO [t19188] VALUES (835,'٧٨٧ - مسألة: (ولا بأس بالصلاة على الميت فى المسجد)','2631','768','0');
INSERT INTO [t19188] VALUES (836,'٧٨٨ - مسألة: (وإن لم يحضره إلا النساء صلين عليه)','2633','768','0');
INSERT INTO [t19188] VALUES (837,'٧٨٩ - مسألة: (يستحب التربيع فى حمله)','2634','768','0');
INSERT INTO [t19188] VALUES (838,'٧٩٠ - مسألة؛ قال: (وهو أن يضع قائمة السرير اليسرى المقدمة','2634','768','0');
INSERT INTO [t19188] VALUES (839,'٧٩١ - مسألة: (وإن حمل بين العمودين فحسن)','2635','768','0');
INSERT INTO [t19188] VALUES (840,'٧٩٢ - مسألة: (ويستحب الإسراع بها)','2636','768','0');
INSERT INTO [t19188] VALUES (841,'٧٩٣ - مسألة: (و)','2640','768','0');
INSERT INTO [t19188] VALUES (842,'٧٩٤ - مسألة: (ولا يجلس من تبعها حتى توضع)','2647','768','0');
INSERT INTO [t19188] VALUES (843,'٧٩٥ - مسألة: (وإن جاءت وهو جالس لم يقم لها)','2648','768','0');
INSERT INTO [t19188] VALUES (844,'٧٩٦ - مسألة: (ويدخل قبره من عند رجل القبر، إن كان أسهل عليهم)','2650','768','0');
INSERT INTO [t19188] VALUES (845,'٧٩٧ - مسألة؛ قال: (ولا يسجى القبر، إلا أن يكون لامرأة)','2654','768','0');
INSERT INTO [t19188] VALUES (846,'٧٩٨ - مسألة: (ويلحد له لحدا، وينصب عليه اللبن نصبا)','2654','768','0');
INSERT INTO [t19188] VALUES (847,'٧٩٩ - مسألة: (ولا يدخل القبر خشبا، ولا شيئا مسته النار)','2655','768','0');
INSERT INTO [t19188] VALUES (848,'٨٠٠ - مسألة: (ويقول الذى يدخله: بسم الله، وعلى ملة رسول الله)','2656','768','0');
INSERT INTO [t19188] VALUES (849,'٨٠١ - مسألة: (ويضعه فى لحده على جنبه الأيمن، مستقبل القبلة)','2658','768','0');
INSERT INTO [t19188] VALUES (850,'٨٠٢ - مسألة: (ويجثو التراب فى القبر ثلاث حثيات، ويهال عليه التراب)','2660','768','0');
INSERT INTO [t19188] VALUES (851,'٨٠٣ - مسألة: (ويرفع القبر عن الأرض قدر شبر مسنما)','2661','768','0');
INSERT INTO [t19188] VALUES (852,'٨٠٤ - مسألة: (ويرش عليه الماء)','2663','768','0');
INSERT INTO [t19188] VALUES (853,'٨٠٥ - مسألة: (ولا بأس بتطيينه)','2666','768','0');
INSERT INTO [t19188] VALUES (854,'٨٠٦ - مسألة: (ويكره تجصيصه، والبناء عليه، والجلوس، والوطء عليه، والاتكاء إليه، والكتابة عليه)','2667','768','0');
INSERT INTO [t19188] VALUES (855,'٨٠٧ - مسألة: (ولا يدفن فيه اثنان إلا لضرورة، ويقدم الأفضل إلى القبلة ويجعل بين كل اثنين حاجز من التراب)','2676','768','0');
INSERT INTO [t19188] VALUES (856,'٨٠٨ - مسألة: (وإن وقع فى القبر ماله قيمة، نبش وأخذ)','2679','768','0');
INSERT INTO [t19188] VALUES (857,'٨٠٩ - مسألة: (وإن كفن بثوب غصب، أو بلع مال غيره، غرم ذلك من تركته. وقيل: ينبش، ويؤخذ الكفن، ويشق جوفه فيخرج)','2680','768','0');
INSERT INTO [t19188] VALUES (858,'٨١٠ - مسألة: (وإن ماتت حامل لم يشق بطنها، وتسطو عليه القوابل، فيخرجنه)','2687','768','0');
INSERT INTO [t19188] VALUES (859,'٨١١ - مسألة: (وإن ماتت ذمية حامل من مسلم دفنت وحدها، ويجعل ظهرها إلى القبلة)','2689','768','0');
INSERT INTO [t19188] VALUES (860,'٨١٢ - مسألة: (ولا تكره القراءة على القبر، فى أصح الروايتين)','2690','768','0');
INSERT INTO [t19188] VALUES (861,'٨١٣ - مسألة: (وأى قربة فعلها وجعل ثوابها للميت المسلم، نفعه ذلك)','2692','768','0');
INSERT INTO [t19188] VALUES (862,'٨١٤ - مسألة: (ويستحب أن يصلح لأهل الميت طعام، يبعث إليهم، ولا يصلحون هم طعاما للناس)','2698','768','0');
INSERT INTO [t19188] VALUES (863,'٨١٥ - مسألة: (ويقول إذا زارها، أو مر بها)','2703','768','0');
INSERT INTO [t19188] VALUES (864,'٨١٦ - مسألة: (ويستحب تعزية أهل الميت)','2705','768','0');
INSERT INTO [t19188] VALUES (865,'٨١٧ - مسألة: (ويقول فى تعزية المسلم بالمسلم: أعظم','2708','768','0');
INSERT INTO [t19188] VALUES (866,'٨١٨ - مسألة: (و)','2709','768','0');
INSERT INTO [t19188] VALUES (867,'٨١٩ - مسألة: (ويجوز البكاء على الميت، وأن يجعل المصاب على رأسه ثوبا يعرف به)','2712','768','0');
INSERT INTO [t19188] VALUES (868,'٨٢٠ - مسألة: (ولا يجوز الندب، ولا النياحة، ولا شق الثياب، ولطم الخدود، وما أشبه ذلك)','2715','768','0');
INSERT INTO [t19188] VALUES (869,'كتاب الزكاة','2725','0','0');
INSERT INTO [t19188] VALUES (870,'[باب. . .]','2725','869','0');
INSERT INTO [t19188] VALUES (871,'٨٢١ - مسألة: (تجب الزكاة فى أربعة أصناف من المال؛ السائمة من بهيمة الأنعام، والخارج من الأرض، والأثمان، وعروض','2727','870','0');
INSERT INTO [t19188] VALUES (872,'٨٢٢ - مسألة: (وقال أصحابنا: تجب فى المتولد بين الوحشى والأهلى)','2729','870','0');
INSERT INTO [t19188] VALUES (873,'٨٢٣ - مسألة: (وفى بقر الوحش روايتان)','2731','870','0');
INSERT INTO [t19188] VALUES (874,'٨٢٤ - مسألة: (ولا تجب إلا بشروط خمسة؛ الإسلام،','2732','870','0');
INSERT INTO [t19188] VALUES (875,'٨٢٥ - مسألة: (فإن ملك السيد عبده مالا، وقلنا: إنه يملكه.','2736','870','0');
INSERT INTO [t19188] VALUES (876,'٨٢٦ - مسألة: (الثالث، ملك نصاب، فإن نقص عنه فلا زكاة فيه، إلا أن يكون نقصا يسيرا؛ كالحبة والحبتين)','2739','870','0');
INSERT INTO [t19188] VALUES (877,'٨٢٧ - مسألة: (وتجب فيما زاد على النصاب بالحساب، إلا في السائمة)','2742','870','0');
INSERT INTO [t19188] VALUES (878,'٨٢٨ - مسألة: الشرط (الرابع، تمام الملك، فلا زكاة في دين المكاتب)','2744','870','0');
INSERT INTO [t19188] VALUES (879,'٨٢٩ - مسألة: (ولا)','2744','870','0');
INSERT INTO [t19188] VALUES (880,'٨٣٠ - مسألة: (ومن كان له دين على ملئ من صداق أو غيره، زكاه إذا قبضه لما مضى)','2751','870','0');
INSERT INTO [t19188] VALUES (881,'٨٣١ - مسألة: (وفى الدين على غير الملئ، والمؤجل، والمجحود، والمغصوب، والضائع، روايتان)','2755','870','0');
INSERT INTO [t19188] VALUES (882,'٨٣٢ - مسألة؛ (قال الخرقي: واللقطة إذا جاء ربها زكاها للحول الذى كان الملتقط ممنوعا منها)','2766','870','0');
INSERT INTO [t19188] VALUES (883,'٨٣٣ - مسألة: (ولا زكاة في مال من عليه دين ينقص النصاب، إلا في المواشى والحبوب في إحدى الروايتين)','2768','870','0');
INSERT INTO [t19188] VALUES (884,'٨٣٤ - مسألة: (والكفارة كالدين فى أحد الوجهين)','2777','870','0');
INSERT INTO [t19188] VALUES (885,'٨٣٥ - مسألة: الشرط (الخامس، مضي الحول شرط، إلا فى الخارج من الأرض)','2780','870','0');
INSERT INTO [t19188] VALUES (886,'٨٣٦ - مسألة: (فإذا استفاد مالا، فلا زكاة فيه','2782','870','0');
INSERT INTO [t19188] VALUES (887,'٨٣٧ - مسألة: (وإن ملك نصابا صغارا، انعقد عليه الحول من حين ملكه. وعنه، لا ينعقد حتى يبلغ سنا يجزئ مثله فى الزكاة)','2788','870','0');
INSERT INTO [t19188] VALUES (888,'٨٣٨ - مسألة: (ومتى نقص النصاب فى بعض الحول، أو باعه، أو أبدله بغير جنسه، انقطع الحول)','2790','870','0');
INSERT INTO [t19188] VALUES (889,'٨٣٩ - مسألة: (إلا أن يقصد بذلك الفرار من الزكاة عند قرب وجوبها، فلا تسقط)','2792','870','0');
INSERT INTO [t19188] VALUES (890,'٨٤٠ - مسألة: (وإن أبدله بنصاب من جنسه بنى على حوله، ويتخرج أن ينقطع)','2798','870','0');
INSERT INTO [t19188] VALUES (891,'٨٤١ - مسألة: (وإذا تم الحول وجبت الزكاة فى عين المال. وعنه، تجب فى الذمة)','2801','870','0');
INSERT INTO [t19188] VALUES (892,'٨٤٢ - مسألة: (ولا يعتبر فى وجوبها إمكان','2804','870','0');
INSERT INTO [t19188] VALUES (893,'٨٤٣ - مسألة: (ولا تسقط بتلف المال. وعنه، أنها تسقط إذا لم يفرط)','2805','870','0');
INSERT INTO [t19188] VALUES (894,'٨٤٤ - مسألة: (وإذا مضى حولان على نصاب لم يؤد زكاتهما، فعليه زكاة واحدة، إن قلنا: تجب في العين. وزكاتان، إن قلنا: تجب فى الذمة. إلا ما كان زكاته الغنم من الإبل، فإن فيه لكل حول زكاة)','2809','870','0');
INSERT INTO [t19188] VALUES (895,'٨٤٥ - مسألة: (وإن كان أكثر من نصاب، فعليه زكاة جميعه لكل حول، إن قلنا: تجب فى الذمة. وإن قلنا: تجب فى العين. نقص ص من زكاته لكل حول بقدر نقصه بها','2811','870','0');
INSERT INTO [t19188] VALUES (896,'٨٤٦ - مسألة: (وإذا مات من عليه الزكاة أخذت من تركته، فإن كان عليه دين اقتسموا بالحصص)','2812','870','0');
INSERT INTO [t19188] VALUES (897,'باب زكاة بهيمة الأنعام','2814','869','0');
INSERT INTO [t19188] VALUES (898,'٨٤٧ - مسألة: (ولا تجب إلا في السائمة منها)','2814','897','0');
INSERT INTO [t19188] VALUES (899,'٨٤٨ - مسألة: (وهى التى ترعى فى أكثر الحول)','2815','897','0');
INSERT INTO [t19188] VALUES (900,'٨٤٩ - مسألة: (وهى ثلاثة أنواع؛ أحدها، الإبل، فلا زكاة فيها حتى تبلغ خمسا فتجب فيها شاة)','2818','897','0');
INSERT INTO [t19188] VALUES (901,'٨٥٠ - مسألة: (فإن أخرج بعيرا لم يجزئه)','2822','897','0');
INSERT INTO [t19188] VALUES (902,'٨٥١ - مسألة: (وفى العشر شاتان، وفى خمس عشرة ثلاث شياه، وفى العشرين أربع شياه)','2824','897','0');
INSERT INTO [t19188] VALUES (903,'٨٥٢ - مسألة: (فإذا بلغت خمسا وعشرين ففيها بنت مخاض، وهى التى لها سنة)','2824','897','0');
INSERT INTO [t19188] VALUES (904,'٨٥٣ - مسألة: (فإن عدمها أجزأه ابن لبون، وهو الذى له','2824','897','0');
INSERT INTO [t19188] VALUES (905,'٨٥٤ - مسألة: (وفى ست وثلاثين بنت لبون، وفى ست وأربعين حقة، وهى التى لها ثلاث سنين، وفى إحدى وستين جذعة، وهى التى لها أربع سنين، وفي ست وسبعين ابنتا لبون، وفى إحدى وتسعين حقتان إلى مائة وعشرين)','2828','897','0');
INSERT INTO [t19188] VALUES (906,'٨٥٥ - مسألة: (فإذا زادت)','2830','897','0');
INSERT INTO [t19188] VALUES (907,'٨٥٦ - مسألة: (فإذا بلغت مائتين اتفق الفرضان، فإن شاء أخرج أربع حقاق، وإن شاء خمس بنات لبون، والمنصوص أنه يخرج الحقاق)','2833','897','0');
INSERT INTO [t19188] VALUES (908,'٨٥٧ - مسألة:. (وليس فيما بين الفريضتين شئ)','2837','897','0');
INSERT INTO [t19188] VALUES (909,'٨٥٨ - مسألة: (ومن وجبت عليه سن فعدمها، أخرج سنا أسفل','2838','897','0');
INSERT INTO [t19188] VALUES (910,'٨٥٩ - مسألة: (فإن عدم السن التى تليها انتقل إلى الأخرى، وجبرها بأربع شياه، أو أربعين درهما. وقال أبو الخطاب: لا ينتقل إلا إلى سن تلى الواجب)','2841','897','0');
INSERT INTO [t19188] VALUES (911,'٨٦٠ - مسألة: (ولا مدخل للجبران في غير الإبل)','2844','897','0');
INSERT INTO [t19188] VALUES (912,'٨٦١ - مسألة: (ولا يجزئ الذكر في الزكاة في غير هذا، إلا ابن لبون مكان بنت مخاض إذا عدمها، إلا أن يكون النصاب كله ذكورا، فيجزئ الذكر في الغنم، وجها واحدا، وفى البقر والإبل في أحد الوجهين)','2850','897','0');
INSERT INTO [t19188] VALUES (913,'٨٦٢ - مسألة: (ويؤخذ من الصغار صغيرة، ومن المراض مريضة','2853','897','0');
INSERT INTO [t19188] VALUES (914,'٨٦٣ - مسألة: (فإن اجتمع كبار وصغار، وصحاح ومراض، وذكور وإناث، لم يؤخذ إلا أنثى كبيرة صحيحة على قدر قيمة المالين)','2858','897','0');
INSERT INTO [t19188] VALUES (915,'٨٦٤ - مسألة: (وإن كان نوعين؛ كالبخاتى والعراب','2860','897','0');
INSERT INTO [t19188] VALUES (916,'٨٦٥ - مسألة: (ولا شئ فيها حتى تبلغ أربعين، فتجب فيها شاة، إلى مائة وعشرين، فإذا زادت واحدة ففيها [شاتان إلى مائتين، فإذا زادت واحدة ففيها]','2864','897','0');
INSERT INTO [t19188] VALUES (917,'٨٦٦ - مسألة: (ويؤخذ من المعز الثنى، ومن الضأن الجذع)','2866','897','0');
INSERT INTO [t19188] VALUES (918,'٨٦٧ - مسألة: (ولا يؤخذ)','2868','897','0');
INSERT INTO [t19188] VALUES (919,'٨٦٨ - مسألة: (ولا الربى؛ وهى التى تربى ولدها، ولا الماخض، ولا كرائم المال، إلا أن يشاء ربه)','2870','897','0');
INSERT INTO [t19188] VALUES (920,'٨٦٩ - مسألة: (ولا يجوز إخراج القيمة. وعنه، فيجوز)','2872','897','0');
INSERT INTO [t19188] VALUES (921,'٨٧٠ - مسألة: (وإن أخرج سنا أعلى من الفرض من جنسه، جاز)','2876','897','0');
INSERT INTO [t19188] VALUES (922,'فصل فى الخلطة','2878','897','0');
INSERT INTO [t19188] VALUES (923,'٨٧١ - مسألة: (وإذا اختلط نفسان أو أكثر من أهل الزكاة في نصاب من الماشية حولا، لم يثبت لهما حكم الانفراد في بعضه، فحكمهما في الزكاة حكم الواحد، سواء كانت خلطة أعيان؛ بأن يكون مشاعا بينهما، أو خلطة أوصاف؛ بأن يكون مال كل واحد منهما متميزا، فخلطاه واشتركا في المراح والمسرح والمشرب والراعي والفحل)','2878','897','0');
INSERT INTO [t19188] VALUES (924,'٨٧٢ - مسألة: (فإن اختل شرط منها، أو ثبت لهما حكم الانفراد فى بعض الحول، زكيا زكاة المنفردين فيه)','2887','897','0');
INSERT INTO [t19188] VALUES (925,'٨٧٣ - مسألة: (وإن ثبت لأحدهما حكم الانفراد وحده، فعليه زكاة المنفرد، وعلى الثانى زكاة الخلطة، ثم يزكيان فيما بعد ذلك الحول زكاة الخلطة، كلما تم حول أحدهما، فعليه بقدر ماله منها)','2889','897','0');
INSERT INTO [t19188] VALUES (926,'٨٧٤ - مسألة: (ولو ملك رجل نصابا شهرا، ثم باع نصفه مشاعا، أو أعلم على بعضه وباعه مختلطا، فقال أبو بكر: ينقطع الحول، ويستأنفانه من حين البيع)','2892','897','0');
INSERT INTO [t19188] VALUES (927,'٨٧٥ - مسألة: (فإن أخرجها من المال انقطع حول المشترى؛ لنقصان النصاب)','2894','897','0');
INSERT INTO [t19188] VALUES (928,'٨٧٦ - مسألة: (وإن أخرجها من غيره، وقلنا: الزكاة فى العين. فكذلك. وإن قلنا: فى الذمة. فعليه عند تمام حوله زكاة حصته)','2894','897','0');
INSERT INTO [t19188] VALUES (929,'٨٧٧ - مسألة: (وإن أفرد بعضه وباعه ثم اختلطا، انقطع الحول)','2897','897','0');
INSERT INTO [t19188] VALUES (930,'٨٧٨ - مسألة: (وإن ملك نصابين شهرا، ثم باع أحدهما مشاعا، فعلى قياس قول أبى بكر، يثبت للبائع حكم الانفراد، وعليه عند تمام حوله زكاة المنفرد، لثبوت حكم الانفراد له. (وعلى قياس قول ابن حامد، عليه زكاة خليط)','2897','897','0');
INSERT INTO [t19188] VALUES (931,'٨٧٩ - مسألة: (ولو ملك)','2898','897','0');
INSERT INTO [t19188] VALUES (932,'٨٨٠ - مسألة: (وإن كان الثانى يتغير به الفرض؛ مثل أن يملك مائة شاة، فعليه زكاته إذا تم حوله، وجها واحدا)','2901','897','0');
INSERT INTO [t19188] VALUES (933,'٨٨١ - مسألة: (وإذا كان الثانى يتغير به الفرض ولا يبلغ نصابا، مثل أن يملك ثلاثين من البقر فى المحرم وعشرا فى صفر، فعليه فى)','2904','897','0');
INSERT INTO [t19188] VALUES (934,'٨٨٢ - مسألة: (وإن ملك ما لا يغير الفرض، كخمس، فلا شئ فيها)','2905','897','0');
INSERT INTO [t19188] VALUES (935,'٨٨٣ - مسألة: (وإذا كان لرجل ستون شاة، كل عشرين منها مختلطة بعشرين لآخر، فعلى الجميع شاة؛ نصفها على صاحب الستين،','2905','897','0');
INSERT INTO [t19188] VALUES (936,'٨٨٤ - مسألة: (وإذا كانت ماشية الرجل متفرقة فى بلدين لا تقصر','2907','897','0');
INSERT INTO [t19188] VALUES (937,'٨٨٥ - مسألة: (ولا تؤثر الخلطة فى غير السائمة. وعنه، أنها','2909','897','0');
INSERT INTO [t19188] VALUES (938,'٨٨٦ - مسألة: (ويجوز للساعى أخذ الفرض من مال أى الخليطين شاء)','2912','897','0');
INSERT INTO [t19188] VALUES (939,'٨٨٧ - مسألة: (ويرجع المأخوذ منه على خليطه بحصته من القيمة)','2913','897','0');
INSERT INTO [t19188] VALUES (940,'٨٨٨ - مسألة: (فإن اختلفا فى القيمة، فالقول قول المرجوع عليه، إذا عدمت البينة)','2913','897','0');
INSERT INTO [t19188] VALUES (941,'٨٨٩ - مسألة: (وإذا أخذ الساعى أكثر من الفرض ظلما، لم','2913','897','0');
INSERT INTO [t19188] VALUES (942,'٨٩٠ - مسألة: (وإن أخذه بقول بعض العلماء رجع عليه)','2914','897','0');
INSERT INTO [t19188] VALUES (943,'باب زكاة الخارج من الأرض','2916','869','0');
INSERT INTO [t19188] VALUES (944,'٨٩١ - مسألة: (تجب الزكاة فى الحبوب كلها، وفى كل ثمر يكال ويدخر؛ كالتمر، والزبيب، واللوز، والفستق، والبندق. ولا تجب فى سائر الثمر، ولا فى الخضر، والزهر، والبقول)','2917','943','0');
INSERT INTO [t19188] VALUES (945,'٨٩٢ - مسألة: (وقال ابن حامد: لا زكاة فى حب البقول؛ كحب الرشاد، والأبازير، كالكسفرة، والكمون، وبزر القثاء، والخيار، ونحوه)','2930','943','0');
INSERT INTO [t19188] VALUES (946,'٨٩٣ - مسألة: (ويعتبر لوجوبها شرطان؛ أحدهما، أن تبلغ نصابا، قدره بعد التصفية فى الحبوب، والجفاف فى الثمار، خمسة أوسق)','2930','943','0');
INSERT INTO [t19188] VALUES (947,'٨٩٤ - مسألة: (والوسق ستون صاعا، والصاع خمسة أرطال وثلث بالعراقى، فيكون ذلك ألفا وستمائة رطل)','2933','943','0');
INSERT INTO [t19188] VALUES (948,'٨٩٥ - مسألة؛ قال: (إلا الأرز والعلس؛ نوع من الحنطة يدخر فى قشره، فإن نصاب كل واحد منهما مع قشره عشرة أوسق)','2935','943','0');
INSERT INTO [t19188] VALUES (949,'٨٩٦ - مسألة: (وعنه، أنه يعتبر نصاب ثمرة النخل والكرم رطبا، ويؤخذ عشره يابسا)','2938','943','0');
INSERT INTO [t19188] VALUES (950,'٨٩٧ - مسألة: (وتضم ثمرة العام الواحد بعضها إلى بعض فى تكميل النصاب)','2938','943','0');
INSERT INTO [t19188] VALUES (951,'٨٩٨ - مسألة: (فإن كان له نخل يحمل فى السنة حملين، ضم','2939','943','0');
INSERT INTO [t19188] VALUES (952,'٨٩٩ - مسألة: (ولا يضم جنس إلى آخر فى تكميل النصاب.','2940','943','0');
INSERT INTO [t19188] VALUES (953,'فصل','2948','943','0');
INSERT INTO [t19188] VALUES (954,'٩٠٠ - مسألة: (فإن سقى نصف السنة بهذا، ونصفها بهذا، ففيه ثلاثة أرباع العشر)','2951','943','0');
INSERT INTO [t19188] VALUES (955,'٩٠١ - مسألة: (وإذا اشتد الحب، وبدا الصلاح فى الثمر، وجبت الزكاة)','2953','943','0');
INSERT INTO [t19188] VALUES (956,'٩٠٢ - مسألة: (ولا يستقر الوجوب إلا بجعلها فى الجرين)','2954','943','0');
INSERT INTO [t19188] VALUES (957,'٩٠٣ - مسألة: (ومتى ادعى)','2957','943','0');
INSERT INTO [t19188] VALUES (958,'٩٠٤ - مسألة: (ويجب إخراج زكاة الحب مصفى والثمر','2959','943','0');
INSERT INTO [t19188] VALUES (959,'٩٠٥ - مسألة: (فإن احتيج إلى قطع الثمرة قبل كمالها)','2961','943','0');
INSERT INTO [t19188] VALUES (960,'٩٠٦ - مسألة: (وينبغى أن يبعث الإمام ساعيا إذا بدا الصلاح فى الثمر، فيخرصه عليهم، ليتصرفوا فيه)','2966','943','0');
INSERT INTO [t19188] VALUES (961,'٩٠٧ - مسألة: (فإن كان أنواعا، خرص كل نوع وحده)','2968','943','0');
INSERT INTO [t19188] VALUES (962,'٩٠٨ - مسألة: (وإن كان نوعا واحدا، فله خرص كل شجرة وحدها)','2969','943','0');
INSERT INTO [t19188] VALUES (963,'٩٠٩ - مسألة: (ويجب أن يترك فى الخرص لرب المال الثلث أو الربع)','2971','943','0');
INSERT INTO [t19188] VALUES (964,'٩١٠ - مسألة: (فإن لم يفعل، فلرب المال الأكل بقدر ذلك، ولا يحتسب عليه)','2973','943','0');
INSERT INTO [t19188] VALUES (965,'٩١١ - مسألة: (ويخرج العشر من كل نوع على حدته، فإن شق ذلك أخذ من الوسط)','2975','943','0');
INSERT INTO [t19188] VALUES (966,'٩١٢ - مسألة: (ويجب العشر على المستأجر دون المالك)','2978','943','0');
INSERT INTO [t19188] VALUES (967,'٩١٣ - مسألة: (ويجتمع العشر والخراج فى كل أرض','2979','943','0');
INSERT INTO [t19188] VALUES (968,'٩١٤ - مسألة: (ويجوز لأهل الذمة شراء الأرض العشرية، ولا عشر عليهم. وعنه، عليهم عشران، يسقط أحدهما بالإسلام)','2983','943','0');
INSERT INTO [t19188] VALUES (969,'فصل','2987','943','0');
INSERT INTO [t19188] VALUES (970,'فصل فى المعدن','2994','943','0');
INSERT INTO [t19188] VALUES (971,'٩١٥ - مسألة: (ومن استخرج من معدن نصابا من الأثمان، أو ما قيمته نصاب من الجواهر، والقار، والصفر، والزئبق، والكحل، والزرنيخ، وسائر ما يسمى معدنا، ففيه الزكاة فى الحال، ربع العشر من قيمته، أو من عينها، إن كانت أثمانا، سواء استخرجه فى دفعة أو دفعات لم يترك العمل بينها ترك إهمال)','2994','943','0');
INSERT INTO [t19188] VALUES (972,'٩١٦ - مسألة: (ولا يجوز إخراجها إذا كانت أثمانا إلا بعد السبك','3002','943','0');
INSERT INTO [t19188] VALUES (973,'٩١٧ - مسألة: (ولا زكاة فيما يخرج من البحر؛ [من العنبر]','3004','943','0');
INSERT INTO [t19188] VALUES (974,'فصل','3007','943','0');
INSERT INTO [t19188] VALUES (975,'٩١٨ - مسألة؛ قال: (إن وجده فى موات أو أرض لا يعلم','3014','943','0');
INSERT INTO [t19188] VALUES (976,'٩١٩ - مسألة: (والركاز ما وجد من دفن الجاهليه، عليه علامتهم. فإن كان عليه علامة المسلمين أو لم تكن عليه علامة، فهو لقطة)','3022','943','0');
INSERT INTO [t19188] VALUES (977,'باب زكاة الأثمان','3026','869','0');
INSERT INTO [t19188] VALUES (978,'٩٢٠ - مسألة: (ولا شئ فى الذهب حتى يبلغ عشرين مثقالا، فيجب فيه نصف مثقال)','3027','977','0');
INSERT INTO [t19188] VALUES (979,'٩٢١ - مسألة؛ قال: (ولا فى الفضة حتى تبلغ مائتى درهم، فيجب فيها خمسة دراهم)','3028','977','0');
INSERT INTO [t19188] VALUES (980,'٩٢٢ - مسألة: (ولا زكاة فى مغشوشهما حتى يبلغ قدر ما فيه','3030','977','0');
INSERT INTO [t19188] VALUES (981,'٩٢٣ - مسألة: (فإن شك فيه، خير بين سبكه وبين الإخراج)','3031','977','0');
INSERT INTO [t19188] VALUES (982,'٩٢٤ - مسألة: (ويخرج عن الجيد الصحيح من جنسه)','3033','977','0');
INSERT INTO [t19188] VALUES (983,'٩٢٥ - مسألة: (فإن أخرج مكسرا أو بهرجا زاد','3034','977','0');
INSERT INTO [t19188] VALUES (984,'٩٢٦ - مسألة: (وهل يضم الذهب إلى الفضة فى تكميل النصاب، أو يخرج أحدهما عن الآخر؟ على روايتين)','3036','977','0');
INSERT INTO [t19188] VALUES (985,'٩٢٧ - مسألة: (ويكون الضم بالأجزاء. وقيل: بالقيمة فيما فيه الحظ للمساكين)','3041','977','0');
INSERT INTO [t19188] VALUES (986,'٩٢٨ - مسألة: (وتضم قيمة العروض إلى كل واحد منهما)','3043','977','0');
INSERT INTO [t19188] VALUES (987,'فصل','3044','977','0');
INSERT INTO [t19188] VALUES (988,'٩٢٩ - مسألة: (فأما الحلى المحرم، والآنية، وما أعد للكراء والنفقة، ففيه الزكاة إذا بلغ نصابا)','3048','977','0');
INSERT INTO [t19188] VALUES (989,'٩٣٠ - مسألة: (والاعتبار بوزنه، إلا ما كان مباح الصناعة،','3052','977','0');
INSERT INTO [t19188] VALUES (990,'٩٣١ - مسألة: (ويباح للرجال من الفضة الخاتم، وقبيعة','3055','977','0');
INSERT INTO [t19188] VALUES (991,'٩٣٢ - مسألة: (ومن الذهب قبيعة السيف، وما دعت إليه الضرورة؛ كالأنف، وما ربط به أسنانه. وقال أبو بكر: يباح يسير الذهب)','3058','977','0');
INSERT INTO [t19188] VALUES (992,'٩٣٣ - مسألة: (ويباح للنساء من الذهب والفضة وكل ما جرت عادتهن بلبسه، قل أو كثر. وقال ابن حامد: إن بلغ الف مثقال حرم، وفيه الزكاة)','3061','977','0');
INSERT INTO [t19188] VALUES (993,'باب زكاة العروض','3064','869','0');
INSERT INTO [t19188] VALUES (994,'٩٣٤ - مسألة: (تجب الزكاة فى عروض التجارة، إذا بلغت قيمتها نصابا)','3064','993','0');
INSERT INTO [t19188] VALUES (995,'٩٣٥ - مسألة: (ويؤخذ منها لا من العروض)','3068','993','0');
INSERT INTO [t19188] VALUES (996,'٩٣٦ - مسألة: (ولا تصير للتجارة إلا أن يملكها بفعله بنية التجارة','3068','993','0');
INSERT INTO [t19188] VALUES (997,'٩٣٧ - مسألة: (فإن ملكها بإرث، أو ملكها بفعله بغير نية التجارة، ثم نوى التجارة بها, لم تصر للتجارة)','3071','993','0');
INSERT INTO [t19188] VALUES (998,'٩٣٨ - مسألة: (وإن كان عنده عرض للتجارة، فنواه للقنية، ثم نواه للتجارة، لم يصر للتجارة. وعنه، أن العروض تصير للتجارة بمجرد النية)','3072','993','0');
INSERT INTO [t19188] VALUES (999,'٩٣٩ - مسألة: (وتقوم العروض عند الحول بما هو أحظ للمساكين، من عين أو ورق، ولا يعتبر ما اشتريت به)','3074','993','0');
INSERT INTO [t19188] VALUES (1000,'٩٤٠ - مسألة: (وإن اشترى عرضا بنصاب من الأثمان أو من العروض، بنى على حوله)','3077','993','0');
INSERT INTO [t19188] VALUES (1001,'٩٤١ - مسألة: (وإن اشتراه بنصاب من السائمة لم يبن على حوله)','3078','993','0');
INSERT INTO [t19188] VALUES (1002,'٩٤٢ - مسألة: (وإن ملك نصابا من السائمة للتجارة، فعليه زكاة التجارة دون السوم، فإن لم تبلع قيمته نصاب التجارة، فعليه زكاة السوم)','3078','993','0');
INSERT INTO [t19188] VALUES (1003,'٩٤٣ - مسألة: (وإن اشرى أرضا أو نخلا للتجارة، فأثمرت النخل، أو زرعت الأرض، فعليه فيهما العشر، ويزكى الأصل للتجارة)','3082','993','0');
INSERT INTO [t19188] VALUES (1004,'٩٤٤ - مسألة: (وإذا أذن كل واحد من الشريكين لصاحبه فى إخراج زكاته)','3088','993','0');
INSERT INTO [t19188] VALUES (1005,'٩٤٥ - مسألة: (فإن أخرجها أحدهما قبل الآخر، ضمن الثانى نصيب الأول، علم أو لم يعلم)','3089','993','0');
INSERT INTO [t19188] VALUES (1006,'باب زكاة الفطر','3090','869','0');
INSERT INTO [t19188] VALUES (1007,'٩٤٦ - مسألة: (وهى واجبة على كل مسلم تلزمه مؤنة نفسه، إذا فضل عنده عن قوته وقوت عياله يوم العيد وليلته صاع، وإن كان مكاتبا)','3092','1006','0');
INSERT INTO [t19188] VALUES (1008,'٩٤٧ - مسألة: (وإن فضل بعض صاع، فهل يلزمه إخراجه؟ على روايتين)','3099','1006','0');
INSERT INTO [t19188] VALUES (1009,'٩٤٨ - مسألة: (وتلزمه فطرة من يمونه من المسلمين)','3100','1006','0');
INSERT INTO [t19188] VALUES (1010,'٩٤٩ - مسألة: (فإن لم يجد ما يؤدى عن جميعهم، بدأ بنفسه،','3103','1006','0');
INSERT INTO [t19188] VALUES (1011,'٩٥٠ - مسألة: (ويستحب الإخراج عن الجنين، ولا يجب)','3107','1006','0');
INSERT INTO [t19188] VALUES (1012,'٩٥١ - مسألة: (ومن تكفل بمؤنة شخص فى شهر رمضان، لم تلزمه فطرته عند أبى الخطاب. والمنصوص أنها تلزمه)','3108','1006','0');
INSERT INTO [t19188] VALUES (1013,'٩٥٢ - مسألة: (وإذا كان العبد بين شركاء، فعليهم صاع. وعنه، على كل واحد صاع. وكذلك الحكم فى من بعضه حر)','3111','1006','0');
INSERT INTO [t19188] VALUES (1014,'٩٥٣ - مسألة: (وإن عجز زوج المرأة عن فطرتها، فعليها أو على','3114','1006','0');
INSERT INTO [t19188] VALUES (1015,'٩٥٤ - مسألة: (ومن كان له غائب أو آبق فعليه فطرته، إلا','3116','1006','0');
INSERT INTO [t19188] VALUES (1016,'٩٥٥ - مسألة: (وإن علم حياته بعد ذلك، أخرج لما مضى)','3118','1006','0');
INSERT INTO [t19188] VALUES (1017,'٩٥٦ - مسألة: (ولا تلزم الزوج فطرة الناشز. وقال أبو الخطاب: تلزمه)','3119','1006','0');
INSERT INTO [t19188] VALUES (1018,'٩٥٧ - مسألة: (ومن لزم غيره فطرته فأخرج عن نفسه بغير إذنه، فهل يجزئه؟ على وجهين)','3120','1006','0');
INSERT INTO [t19188] VALUES (1019,'٩٥٨ - مسألة: (ولا يمنع الدين وجوب الفطرة، إلا أن يكون مطالبا به)','3122','1006','0');
INSERT INTO [t19188] VALUES (1020,'٩٥٩ - مسألة: (وتجب بغروب الشمس من ليلة الفطر، فمن أسلم بعد ذلك، أو ملك عبدا أو زوجة، أو ولد له ولد، لم تلزمه فطرته، وإن وجد ذلك قبل الغروب، وجبت)','3124','1006','0');
INSERT INTO [t19188] VALUES (1021,'٩٦٠ - مسألة: (ويجوز إخراجها قبل العيد بيومين)','3126','1006','0');
INSERT INTO [t19188] VALUES (1022,'٩٦١ - مسألة: (والأفضل إخراجها يوم العيد قبل الصلاة)','3127','1006','0');
INSERT INTO [t19188] VALUES (1023,'٩٦٢ - مسألة: (ويجوز فى سائر اليوم)','3128','1006','0');
INSERT INTO [t19188] VALUES (1024,'فصل','3129','1006','0');
INSERT INTO [t19188] VALUES (1025,'٩٦٣ - مسألة: (ولا يجزئ غير ذلك، إلا أن يعدمه، فيخرج مما يقتات عند ابن حامد. وعند أبى','3139','1006','0');
INSERT INTO [t19188] VALUES (1026,'٩٦٤ - مسألة: (ولا يخرج حبا معيبا, ولا خبزا)','3141','1006','0');
INSERT INTO [t19188] VALUES (1027,'٩٦٥ - مسألة: (ويجزئ إخراج صاع من أجناس)','3142','1006','0');
INSERT INTO [t19188] VALUES (1028,'٩٦٦ - مسألة: (وأفضل المخرج التمر، ثم ما هو أنفع للفقراء بعده)','3143','1006','0');
INSERT INTO [t19188] VALUES (1029,'٩٦٧ - مسألة: (ويجوز أن يعطى الجماعة ما يلزم الواحد، والواحد ما يلزم الجماعة)','3145','1006','0');
INSERT INTO [t19188] VALUES (1030,'باب إخراج الزكاة','3149','869','0');
INSERT INTO [t19188] VALUES (1031,'٩٦٨ - مسألة: (فإن جحد وجوبها جهلا به، عرف ذلك، فإن أصر كفر وأخذت منه، واستتيب ثلاثا، فإن لم يتب قتل)','3153','1030','0');
INSERT INTO [t19188] VALUES (1032,'٩٦٩ - مسألة: (وإن منعها بخلا بها، أخذت منه وعزر. فإن غيب ماله، أو كتمه، أو قاتل دونها، وأمكن أخذها، أخذت من غير زيادة. وقال أبو بكر: يأخذها وشطر ماله)','3154','1030','0');
INSERT INTO [t19188] VALUES (1033,'٩٧٠ - مسألة: (فإن لم يمكن أخذها استتيب ثلاثا، فإن تاب، وإلا قتل وأخذت من تركته. وقال بعض أصحابنا: إن قاتل عليها كفر)','3157','1030','0');
INSERT INTO [t19188] VALUES (1034,'٩٧١ - مسألة: (وإن ادعى ما يمنع وجوب الزكاة؛ من نقصان الحول أو النصاب، أو انتقاله عنه فى بعض الحول، قبل قوله بغير يمين. نص عليه)','3159','1030','0');
INSERT INTO [t19188] VALUES (1035,'٩٧٢ - مسألة: (والصبى والمجنون يخرج عنهما وليهما)','3160','1030','0');
INSERT INTO [t19188] VALUES (1036,'٩٧٣ - مسألة: (ويستحب للإنسان تفرقة زكاته بنفسه، ويجوز دفعها إلى الساعى. وعنه، يستحب أن يدفع إليه العشر، ويتولى تفريق الباقى)','3162','1030','0');
INSERT INTO [t19188] VALUES (1037,'٩٧٤ - مسألة: (وعند أبى الخطاب، دفعها إلى الإمام العادل أفضل)','3163','1030','0');
INSERT INTO [t19188] VALUES (1038,'٩٧٥ - مسألة: (ولا يجزئ إخراجها إلا بنية، إلا أن يأخذها الإمام منه قهرا. وقال أبو الخطاب: لا تجزئه أيضا بغير نية)','3169','1030','0');
INSERT INTO [t19188] VALUES (1039,'٩٧٦ - مسألة: (وإن دفعها إلى وكيله، اعتبرت النية فى الموكل دون الوكيل)','3175','1030','0');
INSERT INTO [t19188] VALUES (1040,'٩٧٧ - مسألة: (ويستحب أن يقول عند الدفع: اللهم اجعلها مغنما، ولا تجعلها مغرما)','3178','1030','0');
INSERT INTO [t19188] VALUES (1041,'٩٧٨ - مسألة: (ويقول الآخذ: آجرك الله فيما أعطيت، وبارك لك فيما أبقيت، وجعله لك طهورا)','3178','1030','0');
INSERT INTO [t19188] VALUES (1042,'٩٧٩ - مسألة: (ولا يجوز نقلها إلى بلد تقصر إليه الصلاة، فإن فعل، فهل تجزئه؟ على روايتين)','3181','1030','0');
INSERT INTO [t19188] VALUES (1043,'٩٨٠ - مسألة: (فإن كان فى بلد، وماله فى آخر، أخرج زكاة المال فى بلده، وفطرته فى البلد الذى هو فيه)','3186','1030','0');
INSERT INTO [t19188] VALUES (1044,'٩٨١ - مسألة: (وإذا حصل عند الإمام ماشية، استحب له وسم الإبل فى أفخاذها، والغنم فى آذانها، فإن كانت زكاة كتب «لله» أو «زكاة»، وإن كانت جزية كتب «صغار» أو «جزية»)','3188','1030','0');
INSERT INTO [t19188] VALUES (1045,'فصل','3189','1030','0');
INSERT INTO [t19188] VALUES (1046,'٩٨٢ - مسألة: (وفى تعجيلها لأكثر من حول روايتان)','3192','1030','0');
INSERT INTO [t19188] VALUES (1047,'٩٨٣ - مسألة: (وإن عجلها عن النصاب وما يستفيده، أجزأ عن النصاب دون الزيادة)','3195','1030','0');
INSERT INTO [t19188] VALUES (1048,'٩٨٤ - مسألة: (وإن عجل عشر الثمرة قبل طلوع الطلع والحصرم','3200','1030','0');
INSERT INTO [t19188] VALUES (1049,'٩٨٥ - مسألة: (وإن عجل زكاة النصاب، فتم الحول وهو ناقص قدر ما عجله، جاز)','3201','1030','0');
INSERT INTO [t19188] VALUES (1050,'٩٨٦ - مسألة: (وإن عجل زكاة المائتين، فنتجت عند الحول سخلة، لزمته شاة ثالثة)','3202','1030','0');
INSERT INTO [t19188] VALUES (1051,'٩٨٧ - مسألة: (وإن عجلها، فدفعها إلى مستحقها، فمات أو ارتد أو استغنى، أجزأت عنه)','3205','1030','0');
INSERT INTO [t19188] VALUES (1052,'٩٨٨ - مسألة: (وإن دفعها إلى غنى، فافتقر عند الوجوب، لم تجزئه)','3206','1030','0');
INSERT INTO [t19188] VALUES (1053,'٩٨٩ - مسألة: (وإن عجلها ثم هلك','3207','1030','0');
INSERT INTO [t19188] VALUES (1054,'باب ذكر أهل الزكاة','3213','869','0');
INSERT INTO [t19188] VALUES (1055,'٩٩٠ - مسألة: (الفقراء؛ وهم الذين لا يجدون ما يقع موقعا من كفايتهم. الثانى، المساكين؛ وهم الذين يجدون معظم الكفاية)','3214','1054','0');
INSERT INTO [t19188] VALUES (1056,'٩٩١ - مسألة: (ومن ملك من غير الأثمان ما لا يقوم بكفايته، فليس بغنى وإن كثرت قيمته)','3220','1054','0');
INSERT INTO [t19188] VALUES (1057,'٩٩٢ - مسألة: (وإن كان من الأثمان، فكذلك فى إحدى الروايتين. والأخرى، إن ملك خمسين درهما أو قيمتها من الذهب، فهو غنى)','3223','1054','0');
INSERT INTO [t19188] VALUES (1058,'٩٩٣ - مسألة: (الثالث، العاملون عليها؛ وهم الجباة لها، والحافظون لها)','3229','1054','0');
INSERT INTO [t19188] VALUES (1059,'٩٩٤ - مسألة: (ويشترط أن يكون العامل مسلما أمينا، من غير ذوى القربى، ولا يشترط حريته ولا فقره. وقال القاضى: لا يشترط إسلامه، ولا كونه من غير ذوى القربى)','3230','1054','0');
INSERT INTO [t19188] VALUES (1060,'٩٩٥ - مسألة: (فإن تلفت الصدقة فى يده من غير تفريط، أعطى أجرته من بيت المال)','3235','1054','0');
INSERT INTO [t19188] VALUES (1061,'٩٩٦ - مسألة: (الرابع، المؤلفة قلوبهم؛ وهم السادة المطاعون فى عشائرهم ممن يرجى إسلامه، أو يخشى شره، أو يرجى بعطيته قوة إيمانه، أو إسلام نظيره، أو جباية الزكاة ممن لا يعطيها، أو الدفع عن','3237','1054','0');
INSERT INTO [t19188] VALUES (1062,'٩٩٧ - مسألة: (الخامس، الرقاب؛ وهم المكاتبون)','3242','1054','0');
INSERT INTO [t19188] VALUES (1063,'٩٩٨ - مسألة: (ويجوز أن يشترى بها أسيرا مسلما. نص عليه)','3245','1054','0');
INSERT INTO [t19188] VALUES (1064,'٩٩٩ - مسألة: (وهل يجوز أن يشترى منها رقبة يعتقها؟ على روايتين)','3246','1054','0');
INSERT INTO [t19188] VALUES (1065,'١٠٠٠ - مسألة: (السادس، الغارمون؛ وهم المدينون، وهم ضربان؛ ضرب غرم لإصلاح ذات البين، وضرب غرم لإصلاح نفسه فى مباح)','3249','1054','0');
INSERT INTO [t19188] VALUES (1066,'١٠٠١ - مسألة: (السابع، فى سبيل الله؛ وهم الغزاة الذين لا ديوان لهم)','3252','1054','0');
INSERT INTO [t19188] VALUES (1067,'١٠٠٢ - مسألة: (ولا يعطى منها فى الحج. وعنه، يعطى الفقير قدر ما يحج به الفرض أو يستعين به فيه)','3253','1054','0');
INSERT INTO [t19188] VALUES (1068,'١٠٠٣ - مسألة: (الثامن، ابن السبيل؛ وهو المسافر المنقطع به دون المنشئ للسفر من بلده)','3257','1054','0');
INSERT INTO [t19188] VALUES (1069,'١٠٠٤ - مسألة: (ويعطى الفقير والمسكين ما يغنيهما)','3260','1054','0');
INSERT INTO [t19188] VALUES (1070,'١٠٠٥ - مسألة: (و)','3262','1054','0');
INSERT INTO [t19188] VALUES (1071,'١٠٠٦ - مسألة: (والغارم والمكاتب ما يقضيان به دينهما)','3262','1054','0');
INSERT INTO [t19188] VALUES (1072,'١٠٠٧ - مسألة: (والغازى ما يحتاج إليه لغزوه وإن كثر)','3263','1054','0');
INSERT INTO [t19188] VALUES (1073,'١٠٠٨ - مسألة: (ولا يزاد أحد منهم على ذلك)','3264','1054','0');
INSERT INTO [t19188] VALUES (1074,'١٠٠٩ - مسألة: (ومن كان ذا عيال أخذ ما يكفيهم)','3264','1054','0');
INSERT INTO [t19188] VALUES (1075,'١٠١٠ - مسألة: (ولا يعطى أحد منهم مع الغنى، إلا أربعة؛ العامل، والمؤلف، والغارم لإصلاح ذات البين، والغازى)','3264','1054','0');
INSERT INTO [t19188] VALUES (1076,'١٠١١ - مسألة: (وإن فضل مع الغارم والمكاتب والغازى وابن السبيل شئ بعد حاجتهم، لزمهم رده، والباقون يأخذون أخذا مستقرا، فلا يردون شيئا. وظاهر كلام الخرقى فى الم','3269','1054','0');
INSERT INTO [t19188] VALUES (1077,'١٠١٢ - مسألة: (وإن ادعى الفقر من عرف بالغنى)','3272','1054','0');
INSERT INTO [t19188] VALUES (1078,'١٠١٣ - مسألة: (وإن ادعى أنه مكاتب أو غارم أو ابن سبيل، لم يقبل)','3273','1054','0');
INSERT INTO [t19188] VALUES (1079,'١٠١٤ - مسألة: (فإن صدق المكاتب سيده، أو الغارم غريمه، فعلى وجهين)','3274','1054','0');
INSERT INTO [t19188] VALUES (1080,'١٠١٥ - مسألة: (وإن ادعى الفقر من لم يعرف بالغنى، قبل','3275','1054','0');
INSERT INTO [t19188] VALUES (1081,'١٠١٦ - مسألة: (وإن ادعى أن له عيالا، قلد وأعطى)','3277','1054','0');
INSERT INTO [t19188] VALUES (1082,'١٠١٧ - مسألة: (ومن سافر أو غرم فى معصية، لم يدفع إليه)','3277','1054','0');
INSERT INTO [t19188] VALUES (1083,'١٠١٨ - مسألة: (ويستحب صرفها فى الأصناف كلها. فإن اقتصر على إنسان واحد أجزأه. وعنه، لا يجزئه إلا ثلاثة من كل صنف، إلا العامل، فإنه يجوز أن يكون واحدا)','3279','1054','0');
INSERT INTO [t19188] VALUES (1084,'١٠١٩ - مسألة: (ويستحب صرفها إلى أقاربه الذين لا تلزمه مؤنتهم، وتفريقها فيهم على قدر حاجتهم)','3284','1054','0');
INSERT INTO [t19188] VALUES (1085,'١٠٢٠ - مسألة: (ويجوز للسيد دفع زكاته إلى مكاتبه، وإلى غريمه)','3285','1054','0');
INSERT INTO [t19188] VALUES (1086,'فصل','3288','1054','0');
INSERT INTO [t19188] VALUES (1087,'١٠٢١ - مسألة: (ولا)','3292','1054','0');
INSERT INTO [t19188] VALUES (1088,'١٠٢٢ - مسألة؛ قال: (ولا إلى الزوجة)','3293','1054','0');
INSERT INTO [t19188] VALUES (1089,'١٠٢٣ - مسألة: (ولا لبنى هاشم، ولا مواليهم)','3294','1054','0');
INSERT INTO [t19188] VALUES (1090,'١٠٢٤ - مسألة: (ويجوز لبنى هاشم الأخذ من صدقة التطوع، ووصايا الفقراء، والنذر. وفى الكفارة وجهان)','3298','1054','0');
INSERT INTO [t19188] VALUES (1091,'١٠٢٥ - مسألة: (وهل يجوز دفعها إلى سائر من تلزمه مؤنته من أقاربه، أو إلى الزوج، أو بنى المطلب؟ على روايتين)','3304','1054','0');
INSERT INTO [t19188] VALUES (1092,'١٠٢٦ - مسألة؛ (وإن دفعها إلى من لا يستحقها وهو لا يعلم، ثم علم، لم يجزئه، إلا الغنى إذا طنه فقيرا، فى إحدى الروايتين)','3314','1054','0');
INSERT INTO [t19188] VALUES (1093,'فصل','3318','1054','0');
INSERT INTO [t19188] VALUES (1094,'١٠٢٧ - مسألة: (وأفضل ما تكون فى شهر رمضان، وأوقات الحاجات)','3319','1054','0');
INSERT INTO [t19188] VALUES (1095,'١٠٢٨ - مسألة: (وتستحب الصدقة بالفاضل عن كفايته وكفاية من يمونه)','3321','1054','0');
INSERT INTO [t19188] VALUES (1096,'١٠٢٩ - مسألة: (ومن أراد الصدقة بماله كله، وهو يعلم من نفسه','3322','1054','0');
INSERT INTO [t19188] VALUES (1097,'١٠٣٠ - مسألة: (ويكره لمن لا صبر له على الضيق أن ينقص نفسه من الكفاية التامة)','3325','1054','0');
INSERT INTO [t19188] VALUES (1098,'كتاب الصيام','3326','0','0');
INSERT INTO [t19188] VALUES (1099,'[باب. . .]','3326','1098','0');
INSERT INTO [t19188] VALUES (1100,'١٠٣١ - مسألة؛ قال: (ويجب صوم رمضان برؤية الهلال، فإن لم ير مع الصحو، أكملوا عدة شعبان ثلاثين يوما، ثم صاموا. فإن حال دون منظره غيم أو قتر ليلة الثلاثين، وجب صيامه بنية رمضان فى','3329','1099','0');
INSERT INTO [t19188] VALUES (1101,'١٠٣٢ - مسألة: (وإذا رئى الهلال نهارا، قبل الزوال أو بعده، فهو لليلة المقبلة)','3337','1099','0');
INSERT INTO [t19188] VALUES (1102,'١٠٣٣ - مسألة: (وإذا رأى الهلال أهل بلد؛ لزم الناس كلهم الصوم)','3338','1099','0');
INSERT INTO [t19188] VALUES (1103,'١٠٣٤ - مسألة (ويقبل فى هلال رمضان قول عدل واحد، ولا يقبل فى سائر الشهور إلا عدلان)','3341','1099','0');
INSERT INTO [t19188] VALUES (1104,'١٠٣٥ - مسألة: (وإذا صاموا بشهادة اثنين ثلاثين يوما فلم يروا الهلال، أفطروا)','3347','1099','0');
INSERT INTO [t19188] VALUES (1105,'١٠٣٦ - مسألة: (وإن صاموا بشهادة واحد)','3347','1099','0');
INSERT INTO [t19188] VALUES (1106,'١٠٣٧ - مسألة: (فإن صاموا لأجل الغيم، لم يفطروا)','3349','1099','0');
INSERT INTO [t19188] VALUES (1107,'١٠٣٨ - مسألة: (ومن رأى هلال رمضان وحده وردت','3349','1099','0');
INSERT INTO [t19188] VALUES (1108,'١٠٣٩ - مسألة: (وإن رأى هلال شوال وحده، لم يفطر)','3351','1099','0');
INSERT INTO [t19188] VALUES (1109,'١٠٤٠ - مسألة: (وإن اشتبهت الأشهر على الأسير، تحرى','3353','1099','0');
INSERT INTO [t19188] VALUES (1110,'١٠٤١ - مسألة: (ولا يجب الصوم إلا على المسلم البالغ العاقل القادر على الصوم، ولا يجب على كافر ولا مجنون ولا','3357','1099','0');
INSERT INTO [t19188] VALUES (1111,'١٠٤٢ - مسألة: (ويؤمر به إذا أطاقه، ويضرب عليه ليعتاده)','3360','1099','0');
INSERT INTO [t19188] VALUES (1112,'١٠٤٣ - مسألة: (وإذا قامت البينة بالرؤية فى أثناء النهار، لزمهم الإمساك والقضاء)','3361','1099','0');
INSERT INTO [t19188] VALUES (1113,'١٠٤٤ - مسألة: (وإن بلغ صبى، أو أسلم كافر، أو أفاق مجنون، فكذلك. وعنه، لا يلزمهم شئ)','3362','1099','0');
INSERT INTO [t19188] VALUES (1114,'١٠٤٥ - مسألة: (وإن بلغ الصبى صائما أتم، ولا قضاء عليه عند القاضى. وعند أبى الخطاب، عليه القضاء)','3364','1099','0');
INSERT INTO [t19188] VALUES (1115,'١٠٤٦ - مسألة: (وإن طهرت حائض، أو نفساء، أو قدم المسافر مفطرا، فعليهم القضاء. وفى الإمساك روايتان)','3365','1099','0');
INSERT INTO [t19188] VALUES (1116,'١٠٤٧ - مسألة: (ومن عجز عن الصوم لكبر، أو مرض لا يرجى برؤه، أفطر وأطعم عن كل يوم مسكينا)','3367','1099','0');
INSERT INTO [t19188] VALUES (1117,'١٠٤٨ - مسألة: (والمريض إذا خاف الضرر، والمسافر، استحب لهما الفطر، فإن صاما أجزأهما)','3370','1099','0');
INSERT INTO [t19188] VALUES (1118,'١٠٤٩ - مسألة: (ولا يجوز أن يصوما فى رمضان عن غيره)','3379','1099','0');
INSERT INTO [t19188] VALUES (1119,'١٠٥٠ - مسألة: (وإن نوى الحاضر صوم يوم، ثم سافر فى أثنائه، فله الفطر. وعنه، لا يباح)','3382','1099','0');
INSERT INTO [t19188] VALUES (1120,'١٠٥١ - مسألة: (والحامل والمرضع إذا خافتا)','3384','1099','0');
INSERT INTO [t19188] VALUES (1121,'١٠٥٢ - مسألة: (ومن نوى قبل الفجر، ثم جن، أو أغمى عليه جميع النهار، لم يصح صومه. وإن أفاق جزءا منه، صح صومه)','3389','1099','0');
INSERT INTO [t19188] VALUES (1122,'١٠٥٣ - مسألة: (وإن نام جميع النهار، صح صومه)','3391','1099','0');
INSERT INTO [t19188] VALUES (1123,'١٠٥٤ - مسألة: (ويلزم المغمى عليه القضاء دون المجنون)','3391','1099','0');
INSERT INTO [t19188] VALUES (1124,'١٠٥٥ - مسألة: (ولا يحتاج إلى نية الفرضية. وقال ابن حامد: يجب ذلك)','3401','1099','0');
INSERT INTO [t19188] VALUES (1125,'١٠٥٦ - مسألة: (ولو نوى، إن كان غدا من رمضان، فهو فرضى، وإلا فهو نفل. لم يجزئه)','3402','1099','0');
INSERT INTO [t19188] VALUES (1126,'١٠٥٧ - مسألة: (ومن نوى الإفطار، أفطر)','3403','1099','0');
INSERT INTO [t19188] VALUES (1127,'١٠٥٨ - مسألة: (ويصح صوم النفل بنية من النهار، قبل الزوال وبعده. وقال القاضي: لا يجزئ بعد الزوال)','3406','1099','0');
INSERT INTO [t19188] VALUES (1128,'باب ما يفسد الصوم ويوجب الكفارة','3411','1098','0');
INSERT INTO [t19188] VALUES (1129,'١٠٥٩ - مسألة: (أو استقاء، أو استمنى)','3416','1128','0');
INSERT INTO [t19188] VALUES (1130,'١٠٦٠ - مسألة؛ قال: (أو قبل أو لمس فأمنى أو مذى)','3418','1128','0');
INSERT INTO [t19188] VALUES (1131,'١٠٦١ - مسألة: (أو كرر النظر فأنزل)','3420','1128','0');
INSERT INTO [t19188] VALUES (1132,'١٠٦٢ - مسألة؛ قال: (أو حجم، أو احتجم)','3421','1128','0');
INSERT INTO [t19188] VALUES (1133,'١٠٦٣ - مسألة: (فإن فكر فأنزل، لم يفسد صومه)','3429','1128','0');
INSERT INTO [t19188] VALUES (1134,'١٠٦٤ - مسألة: (وإن طار إلى حلقه ذباب أو غبار، أو قطر في إحليله، أو فكر فأنزل، أو احتلم، أو ذرعه القئ، أو أصبح وفى فيه طعام فلفظه، أو اغتسل، أو تمضمض، أو استنشق فدخل الماء حلقه، لم يفسد','3432','1128','0');
INSERT INTO [t19188] VALUES (1135,'١٠٦٥ - مسألة؛ قال: (أو أصبح وفى فيه طعام فلفظه)','3433','1128','0');
INSERT INTO [t19188] VALUES (1136,'١٠٦٦ - مسألة؛ قال: (أو اغتسل، أو تمضمض، أو استنشق فدخل الماء حلقه، لم يفسد صومه)','3434','1128','0');
INSERT INTO [t19188] VALUES (1137,'١٠٦٧ - مسألة: (وإن أكل شاكا في طلوع الفجر، فلا قضاء عليه)','3439','1128','0');
INSERT INTO [t19188] VALUES (1138,'١٠٦٨ - مسألة: (وإن أكل شاكا في غروب الشمس، فعليه القضاء)','3440','1128','0');
INSERT INTO [t19188] VALUES (1139,'١٠٦٩ - مسألة: (ومن أكل معتقدا أنه ليل فبان نهارا، فعليه القضاء)','3441','1128','0');
INSERT INTO [t19188] VALUES (1140,'١٠٧٠ - مسألة: (ولا يلزم المرأة كفارة مع العذر. وهل يلزمها مع عدمه؟ على روايتين)','3450','1128','0');
INSERT INTO [t19188] VALUES (1141,'١٠٧١ - مسألة؛ قال: (وكل أمر غلب عليه الصائم فليس عليه قضاء ولا كفارة)','3452','1128','0');
INSERT INTO [t19188] VALUES (1142,'١٠٧٢ - مسألة: (وإن جامع فيما دون الفرج فأنزل، أو وطئ','3454','1128','0');
INSERT INTO [t19188] VALUES (1143,'١٠٧٣ - مسألة: (وإن جامع في يوم رأى الهلال في ليلته وردت شهادته، فعليه القضاء والكفارة)','3459','1128','0');
INSERT INTO [t19188] VALUES (1144,'١٠٧٤ - مسألة: (وإن جامع فى يومين ولم يكفر، فهل تلزمه كفارة أو كفارتان؟ على وجهين)','3460','1128','0');
INSERT INTO [t19188] VALUES (1145,'١٠٧٥ - مسألة: (وإن جامع ثم كفر ثم جامع فى يومه، فعليه كفارة ثانية. نص عليه. وكذلك كل من لزمه الإمساك، إذا جامع)','3462','1128','0');
INSERT INTO [t19188] VALUES (1146,'١٠٧٦ - مسألة: (وإن جامع وهو صحيح، ثم مرض أو جن أو سافر، لم تسقط عنه)','3464','1128','0');
INSERT INTO [t19188] VALUES (1147,'١٠٧٧ - مسألة: (وإن نوى الصوم فى سفره، ثم جامع، فلا','3466','1128','0');
INSERT INTO [t19188] VALUES (1148,'١٠٧٨ - مسألة: (ولا تجب الكفارة بغير الجماع فى نهار رمضان)','3468','1128','0');
INSERT INTO [t19188] VALUES (1149,'١٠٧٩ - مسألة: (والكفارة عتق رقبة، فإن لم يجد فصيام شهرين متتابعين، فإن لم يستطع فإطعام ستين مسكينا)','3470','1128','0');
INSERT INTO [t19188] VALUES (1150,'١٠٨٠ - مسألة: (فإن لم يجد سقطت عنه. وعنه، لا تسقط. وعنه، أن الكفارة على التخيير، فبأيها كفر أجزأه)','3474','1128','0');
INSERT INTO [t19188] VALUES (1151,'باب ما يكره وما يستحب، وحكم القضاء','3476','1098','0');
INSERT INTO [t19188] VALUES (1152,'١٠٨١ - مسألة: (ويكره للصائم أن يجمع ريقه فيبلعه، وأن يبتلع النخامة. وهل يفطر بهما؟ على وجهين)','3476','1151','0');
INSERT INTO [t19188] VALUES (1153,'١٠٨٢ - مسألة: (ويكره ذوق الطعام، وإن وجد طعمه فى','3479','1151','0');
INSERT INTO [t19188] VALUES (1154,'١٠٨٣ - مسألة: (ويكره مضغ العلك الذى لا يتحلل منه أجزاء، ولا يجوز مضغ ما يتحلل منه أجزاء إلا أن لا يبلع ريقه، وإن وجد طعمه فى حلقه، أفطر)','3481','1151','0');
INSERT INTO [t19188] VALUES (1155,'١٠٨٤ - مسألة: (وتكره القبلة، إلا أن يكون ممن لا تحرك شهوته، فى إحدى الروايتين)','3483','1151','0');
INSERT INTO [t19188] VALUES (1156,'١٠٨٥ - مسألة: (ويجب عليه اجتناب الكذب والغيبة','3486','1151','0');
INSERT INTO [t19188] VALUES (1157,'١٠٨٦ - مسألة: (ويستحب تعجيل الإفطار وتأخير السحور، وأن يفطر على التمر، فإن لم يجد فعلى الماء، وأن يقول عند فطره: اللهم لك صمت، وعلى رزقك أفطرت، سبحانك وبحمدك، اللهم تقبل منى، إنك أنت السميع العليم)','3488','1151','0');
INSERT INTO [t19188] VALUES (1158,'١٠٨٧ - مسألة: (و)','3490','1151','0');
INSERT INTO [t19188] VALUES (1159,'١٠٨٨ - مسألة: (يستحب التتابع فى قضاء رمضان، ولا يجب)','3496','1151','0');
INSERT INTO [t19188] VALUES (1160,'١٠٨٩ - مسألة: (فإن فعل، فعليه القضاء، وإطعام مسكين لكل يوم)','3500','1151','0');
INSERT INTO [t19188] VALUES (1161,'١٠٩٠ - مسألة: (وإن أخره لعذر فلا شئ عليه، وإن مات)','3501','1151','0');
INSERT INTO [t19188] VALUES (1162,'١٠٩١ - مسألة: (وإن أخره لغير عذر، فمات قبل أن أدركه رمضان آخر، أطعم عنه لكل يوم مسكين. ومن مات بعد أن أدركه رمضان آخر، فهل يطعم عنه لكل يوم مسكين أو اثنان؟ على وجهين)','3502','1151','0');
INSERT INTO [t19188] VALUES (1163,'١٠٩٢ - مسألة: (ومن مات وعليه صوم منذور أو حج أو اعتكاف، فعله عنه وليه. وإن كانت صلاة منذورة، فعلى روايتين)','3507','1151','0');
INSERT INTO [t19188] VALUES (1164,'باب صوم التطوع','3514','1098','0');
INSERT INTO [t19188] VALUES (1165,'١٠٩٣ - مسألة: (وأفضله صيام داود، عليه السلام، كان يصوم يوما، ويفطر يوما)','3514','1164','0');
INSERT INTO [t19188] VALUES (1166,'١٠٩٤ - مسألة: (ويستحب صيام أيام البيض من كل شهر، وصوم الاثنين والخميس)','3515','1164','0');
INSERT INTO [t19188] VALUES (1167,'١٠٩٥ - مسألة: (ومن صام رمضان، وأتبعه بست من شوال، فكأنما صام الدهر)','3517','1164','0');
INSERT INTO [t19188] VALUES (1168,'١٠٩٦ - مسألة: (وصيام يوم عاشوراء كفارة سنة، ويوم عرفة كفارة سنتين. ولا يستحب لمن كان بعرفة)','3520','1164','0');
INSERT INTO [t19188] VALUES (1169,'١٠٩٧ - مسألة: (ويستحب صيام عشر ذى الحجة)','3525','1164','0');
INSERT INTO [t19188] VALUES (1170,'١٠٩٨ - مسألة: (وأفضل الصيام بعد شهر رمضان شهر الله المحرم)','3526','1164','0');
INSERT INTO [t19188] VALUES (1171,'١٠٩٩ - مسألة: (ويكره إفراد رجب بالصوم)','3527','1164','0');
INSERT INTO [t19188] VALUES (1172,'١١٠٠ - مسألة: (و)','3529','1164','0');
INSERT INTO [t19188] VALUES (1173,'١١٠١ - مسألة: (ولا يجوز صوم العيدين عن فرض ولا','3540','1164','0');
INSERT INTO [t19188] VALUES (1174,'١١٠٢ - مسألة: (ولا يجوز صيام أيام التشريق تطوعا، وفي صيامها عن الفرض روايتان)','3542','1164','0');
INSERT INTO [t19188] VALUES (1175,'١١٠٣ - مسألة: (ومن شرع في صوم أو صلاة تطوعا، استحب له إتمامه، ولا يلزمه، فإن أفسده فلا قضاء عليه)','3544','1164','0');
INSERT INTO [t19188] VALUES (1176,'١١٠٤ - مسألة: (وتطلب ليلة القدر في العشر الأواخر من رمضان، وليالى الوتر آكدها)','3549','1164','0');
INSERT INTO [t19188] VALUES (1177,'١١٠٥ - مسألة: (وأرجاها ليلة سبع وعشرين)','3552','1164','0');
INSERT INTO [t19188] VALUES (1178,'١١٠٦ - مسألة: ويستحب أن يجتهد فيها في الدعاء (ويدعو فيها بما روى عن عائشة، أنها قالت: يا رسول الله، إن وافقتها بم أدعو؟ قال: «قولى: اللهم إنك عفو تحب العفو، فاعف عني»)','3559','1164','0');
INSERT INTO [t19188] VALUES (1179,'كتاب الاعتكاف','3560','0','0');
INSERT INTO [t19188] VALUES (1180,'١١٠٧ - مسألة: (وهو سنة، إلا أن ينذره، فيجب)','3561','1179','0');
INSERT INTO [t19188] VALUES (1181,'١١٠٨ - مسألة: (ويصح بغير صوم. وعنه، لا يصح. فعلى هذا، لا يصح في ليلة مفردة، ولا بعض يوم)','3565','1179','0');
INSERT INTO [t19188] VALUES (1182,'١١٠٩ - مسألة: (وليس للمرأة الاعتكاف إلا بإذن زوجها، ولا للعبد إلا بإذن سيده)','3570','1179','0');
INSERT INTO [t19188] VALUES (1183,'١١١٠ - مسألة: (فإن شرعا فيه بغير إذن، فلهما تحليلهما، وإن كان بإذن، فلهما تحليلهما إن كان تطوعا، وإلا فلا)','3571','1179','0');
INSERT INTO [t19188] VALUES (1184,'١١١١ - مسألة: (وللمكاتب أن يعتكف ويحج بغير إذن)','3572','1179','0');
INSERT INTO [t19188] VALUES (1185,'١١١٢ - مسألة: (ومن بعضه حر، إن كان بينهما مهايأة','3573','1179','0');
INSERT INTO [t19188] VALUES (1186,'١١١٣ - مسألة: (ولا يجوز الاعتكاف إلا في مسجد يجمع فيه، إلا المرأة لها الاعتكاف في كل مسجد إلا مسجد بيتها)','3574','1179','0');
INSERT INTO [t19188] VALUES (1187,'١١١٤ - مسألة: (والأفضل الاعتكاف فى الجامع إذا كانت الجمعة تتخلله)','3582','1179','0');
INSERT INTO [t19188] VALUES (1188,'١١١٥ - مسألة: (وإذا نذر الاعتكاف أو الصلاة فى مسجد،','3582','1179','0');
INSERT INTO [t19188] VALUES (1189,'١١١٦ - مسألة: (وأفضلها المسجد الحرام، ثم مسجد المدينة، ثم)','3586','1179','0');
INSERT INTO [t19188] VALUES (1190,'١١١٧ - مسألة: (فإن نذره فى الأفضل، لم)','3587','1179','0');
INSERT INTO [t19188] VALUES (1191,'١١١٨ - مسألة: (وإن نذر اعتكاف شهر بعينه، لزمه الشروع فيه قبل دخول ليلته إلى انقضائه)','3588','1179','0');
INSERT INTO [t19188] VALUES (1192,'١١١٩ - مسألة: (وإن نذر شهرا مطلقا، لزمه شهر متتابع)','3591','1179','0');
INSERT INTO [t19188] VALUES (1193,'١١٢٠ - مسألة: (وإن نذر أياما معدودة، فله تفريقها، إلا عند القاضى)','3593','1179','0');
INSERT INTO [t19188] VALUES (1194,'١١٢١ - مسألة: (وإن نذر أياما أو ليالى متتابعة، لزمه ما يتخللها من ليل أو نهار)','3594','1179','0');
INSERT INTO [t19188] VALUES (1195,'١١٢٢ - مسألة: (ولا يعود مريضا، ولا يشهد جنازة، إلا أن يشترطه، فيجوز. وعنه، له ذلك من غير شرط)','3608','1179','0');
INSERT INTO [t19188] VALUES (1196,'١١٢٣ - مسألة: (وله السؤال عن المريض فى طريقه ما لم يعرج)','3612','1179','0');
INSERT INTO [t19188] VALUES (1197,'١١٢٤ - مسألة: (فإن خرج لما لا بد منه خروجا معتادا، كحاجة','3613','1179','0');
INSERT INTO [t19188] VALUES (1198,'١١٢٥ - مسألة: (وإن خرج لغير المعتاد فى المتتابع وتطاول،','3613','1179','0');
INSERT INTO [t19188] VALUES (1199,'١١٢٦ - مسألة: (وإن خرج لما له منه بد فى المتتابع، لزمه استئنافه، وإن فعله فى معين، فعليه الكفارة، وفى الاستئناف وجهان)','3617','1179','0');
INSERT INTO [t19188] VALUES (1200,'١١٢٧ - مسألة: (وإن وطئ المعتكف فى الفرج، فسد اعتكافه، ولا كفارة عليه، إلا لترك نذره. وقال أبو بكر: عليه كفارة يمين. وقال القاضى: عليه كفارة الظهار)','3621','1179','0');
INSERT INTO [t19188] VALUES (1201,'١١٢٨ - مسألة: (وإن باشر فيما دون الفرج فأنزل، فسد اعتكافه، وإلا فلا)','3625','1179','0');
INSERT INTO [t19188] VALUES (1202,'١١٢٩ - مسألة: (ويستحب للمعتكف التشاغل بفعل القرب','3627','1179','0');
INSERT INTO [t19188] VALUES (1203,'١١٣٠ - مسألة: (ولا يستحب له إقراء القرآن والعلم، والمناظرة فيه، إلا عند [أبى الخطاب]','3632','1179','0');
INSERT INTO [t19188] VALUES (1204,'كتاب المناسك','3640','0','0');
INSERT INTO [t19188] VALUES (1205,'[باب. . .]','3640','1204','0');
INSERT INTO [t19188] VALUES (1206,'١١٣١ - مسألة: (يجب الحج والعمرة في العمر مرة واحدة، بخمسة شروط)','3640','1205','0');
INSERT INTO [t19188] VALUES (1207,'١١٣٢ - مسألة: (وإنما يجب الحج والعمرة بخمسة شروط؛ الإسلام، والعقل، والبلوغ، والحرية، والاستطاعة)','3645','1205','0');
INSERT INTO [t19188] VALUES (1208,'١١٣٣ - مسألة: (إلا أن يبلغ ويعتق في الحج قبل الخروج من عرفة، وفى [العمرة قبل]','3649','1205','0');
INSERT INTO [t19188] VALUES (1209,'١١٣٤ - مسألة: (ويحرم الصبى المميز بإذن وليه، وغير المميز يحرم عنه وليه، ويفعل عنه','3652','1205','0');
INSERT INTO [t19188] VALUES (1210,'١١٣٥ - مسألة: (ونفقة الحج وكفاراته في مال وليه. وعنه، في مال الصبى)','3659','1205','0');
INSERT INTO [t19188] VALUES (1211,'١١٣٦ - مسألة: (وليس للعبد الإحرام إلا بإذن سيده، ولا للمرأة الإحرام نفلا إلا بإذن زوجها)','3662','1205','0');
INSERT INTO [t19188] VALUES (1212,'١١٣٧ - مسألة: (وليس للرجل منع امرأته من حج الفرض، ولا تحليلها إن أحرمت به)','3671','1205','0');
INSERT INTO [t19188] VALUES (1213,'١١٣٨ - مسألة: (ولا يصير مستطيعا ببذل غيره بحال)','3684','1205','0');
INSERT INTO [t19188] VALUES (1214,'١١٣٩ - مسألة: (فمن كملت له هذه الشروط، وجب عليه الحج على الفور)','3685','1205','0');
INSERT INTO [t19188] VALUES (1215,'١١٤٠ - مسألة: (فإن عجز عنه لكبر، أو مرض لا يرجى برؤه، لزمه أن يقيم من يحج عنه، ويعتمر من بلده، وقد أجزأ عنه وإن عوفى)','3688','1205','0');
INSERT INTO [t19188] VALUES (1216,'١١٤١ - مسألة: (ومن قدر على السعى، لزمه ذلك إذا كان فى','3701','1205','0');
INSERT INTO [t19188] VALUES (1217,'١١٤٢ - مسألة: (ومن وجب عليه الحج [فتوفى قبله، أخرج]','3705','1205','0');
INSERT INTO [t19188] VALUES (1218,'١١٤٣ - مسألة: (فإن ضاق ماله عن ذلك، أو كان عليه دين، أخذ للحج بحصته، وحج به من حيث يبلغ)','3709','1205','0');
INSERT INTO [t19188] VALUES (1219,'١١٤٤ - مسألة: (فإن مات المحرم فى الطريق، مضت فى حجها، ولم تصر محصرة)','3723','1205','0');
INSERT INTO [t19188] VALUES (1220,'١١٤٥ - مسألة: (ولا يجوز لمن لم يحج عن نفسه أن يحج عن غيره، ولا نذره، ولا نافلة، فإن فعل، انصرف إلى حجة الإسلام. وعنه، يقع ما نواه)','3724','1205','0');
INSERT INTO [t19188] VALUES (1221,'١١٤٦ - مسألة: (وهل يجوز لمن يقدر على الحج بنفسه أن يستنيب في حج التطوع؟ على روايتين)','3730','1205','0');
INSERT INTO [t19188] VALUES (1222,'باب المواقيت','3732','1204','0');
INSERT INTO [t19188] VALUES (1223,'١١٤٧ - مسألة: (ميقات أهل المدينة من ذى الحليفة، وأهل الشام ومصر والمغرب الجحفة، وأهل اليمن يلملم، وأهل نجد قرن، وأهل المشرق ذات عرق)','3732','1222','0');
INSERT INTO [t19188] VALUES (1224,'١١٤٨ - مسألة: (فهذه المواقيت لأهلها، ولمن مر عليها من غيرهم)','3736','1222','0');
INSERT INTO [t19188] VALUES (1225,'١١٤٩ - مسألة: (ومن منزله دون الميقات، فميقاته من','3738','1222','0');
INSERT INTO [t19188] VALUES (1226,'١١٥٠ - مسألة: (وأهل مكة إذا أرادوا العمرة، فمن الحل، وإن','3739','1222','0');
INSERT INTO [t19188] VALUES (1227,'١١٥١ - مسألة: (ومن لم يكن طريقه على ميقات، فإذا حاذى أقرب المواقيت إليه، أحرم)','3745','1222','0');
INSERT INTO [t19188] VALUES (1228,'١١٥٢ - مسألة: (ولا يجوز لمن أراد دخول مكة تجاوز الميقات بغير إحرام، إلا لقتال مباح، أو حاجة متكررة؛ كالحطاب ونحوه،','3746','1222','0');
INSERT INTO [t19188] VALUES (1229,'١١٥٣ - مسألة: (ومن جاوزه مريدا للنسك)','3752','1222','0');
INSERT INTO [t19188] VALUES (1230,'١١٥٤ - مسألة: (والاختيار أن لا يحرم قبل ميقاته، ولا يحرم بالحج قبل أشهره، فإن فعل فهو محرم)','3756','1222','0');
INSERT INTO [t19188] VALUES (1231,'١١٥٥ - مسألة: (وأشهر الحج؛ شوال، وذو القعدة، وعشر من ذى الحجة)','3761','1222','0');
INSERT INTO [t19188] VALUES (1232,'باب الإحرام','3764','1204','0');
INSERT INTO [t19188] VALUES (1233,'١١٥٦ - مسألة: (يستحب لمن أراد الإحرام أن يغتسل، ويتنظف، ويتطيب، ويلبس ثوبين أبيضين نظيفين؛ إزارا [ورداء]','3764','1232','0');
INSERT INTO [t19188] VALUES (1234,'١١٥٧ - مسألة: (ويصلى ركعتين، ويحرم عقيبهما)','3772','1232','0');
INSERT INTO [t19188] VALUES (1235,'١١٥٨ - مسألة: (وينوى الإحرام بنسك معين، ولا ينعقد إلا بالنية)','3774','1232','0');
INSERT INTO [t19188] VALUES (1236,'١١٥٩ - مسألة: (ويشترط، فيقول: اللهم إنى أريد النسك','3776','1232','0');
INSERT INTO [t19188] VALUES (1237,'١١٦٠ - مسألة: (وهو مخير بين التمتع والإفراد والقران)','3779','1232','0');
INSERT INTO [t19188] VALUES (1238,'١١٦١ - مسألة: (وأفضلها التمتع، ثم الإفراد)','3780','1232','0');
INSERT INTO [t19188] VALUES (1239,'١١٦٢ - مسألة: (وصفة التمتع؛ أن يحرم بالعمرة فى أشهر الحج، ويفرغ منها، ويحرم بالحج من مكة أو من قريب منها فى عامه. والإفراد أن يحرم بالحج مفردا. والقران أن يحرم بهما جميعا، أو يحرم بالعمرة ثم يدخل عليها الحج. ولو أحرم بالحج ثم أدخل عليه العمرة، لم يصح إحرامه بها)','3791','1232','0');
INSERT INTO [t19188] VALUES (1240,'١١٦٣ - مسألة: (ويجب على المتمتع والقارن دم نسك، إذا لم يكونا من حاضرى المسجد الحرام؛ وهم أهل مكة، ومن كان منها دون مسافة القصر)','3797','1232','0');
INSERT INTO [t19188] VALUES (1241,'١١٦٤ - مسألة: (ومن كان مفردا أو قارنا، أحببنا له أن يفسخ إذا طاف وسعى ويجعلها عمرة؛ لأمر رسول الله - صلى الله عليه وسلم - أصحابه بذلك، إلا أن يكون معه هدى، فيكون على إحرامه)','3814','1232','0');
INSERT INTO [t19188] VALUES (1242,'١١٦٥ - مسألة: (ولو ساق المتمتع الهدى، لم يكن له أن يحل)','3821','1232','0');
INSERT INTO [t19188] VALUES (1243,'١١٦٦ - مسألة: (والمرأة إذا دخلت متمتعة','3823','1232','0');
INSERT INTO [t19188] VALUES (1244,'١١٦٧ - مسألة: (ومن أحرم مطلقا، صح، وله صرفه إلى ما شاء)','3827','1232','0');
INSERT INTO [t19188] VALUES (1245,'١١٦٨ - مسألة: (وإن أحرم بمثل ما أحرم به فلان، انعقد إحرامه بمثله)','3828','1232','0');
INSERT INTO [t19188] VALUES (1246,'١١٦٩ - مسألة: (وإن أحرم بحجتين أو عمرتين، انعقد إحرامه بإحداهما)','3830','1232','0');
INSERT INTO [t19188] VALUES (1247,'١١٧٠ - مسألة: (وإن أحرم بنسك ونسيه، جعله عمرة. وقال القاضى: يصرفه إلى ما شاء)','3831','1232','0');
INSERT INTO [t19188] VALUES (1248,'١١٧١ - مسألة: (وإن أحرم عن رجلين، وقع عن نفسه)','3834','1232','0');
INSERT INTO [t19188] VALUES (1249,'١١٧٢ - مسألة: (وإن أحرم عن أحدهما لا بعينه، وقع عن نفسه. وقال أبو الخطاب: له صرفه إلى أيهما شاء)','3834','1232','0');
INSERT INTO [t19188] VALUES (1250,'١١٧٣ - مسألة: (وإذا استوى على راحلته، لبى تلبية رسول الله','3835','1232','0');
INSERT INTO [t19188] VALUES (1251,'١١٧٤ - مسألة: (والتلبية سنة، ويستحب رفع الصوت بها، والإكثار منها، والدعاء بعدها)','3839','1232','0');
INSERT INTO [t19188] VALUES (1252,'١١٧٥ - مسألة: (ويلبى إذا علا نشزا، أو هبط واديا، وفى دبر','3844','1232','0');
INSERT INTO [t19188] VALUES (1253,'١١٧٦ - مسألة: (ولا ترفع المرأة صوتها بالتلبية، إلا بقدر ما تسمع نفسها)','3847','1232','0');
INSERT INTO [t19188] VALUES (1254,'باب محظورات الإحرام','3848','1204','0');
INSERT INTO [t19188] VALUES (1255,'١١٧٧ - مسألة: (حلق الشعر)','3848','1254','0');
INSERT INTO [t19188] VALUES (1256,'١١٧٨ - مسألة: (وتقليم الأظفار)','3849','1254','0');
INSERT INTO [t19188] VALUES (1257,'١١٧٩ - مسألة: (فمن حلق أو قلم ثلاثة، فعليه دم. وعنه، لا يجب إلا فى أربع فصاعدا)','3850','1254','0');
INSERT INTO [t19188] VALUES (1258,'١١٨٠ - مسألة: (وفيما دون ذلك فى كل واحد مد من طعام. وعنه، قبضة. وعنه، درهم)','3852','1254','0');
INSERT INTO [t19188] VALUES (1259,'١١٨١ - مسألة: (وإن حلق رأسه بإذنه، فالفدية عليه، وإن كان مكرها أو نائما، فالفدية على الحالق)','3855','1254','0');
INSERT INTO [t19188] VALUES (1260,'١١٨٢ - مسألة: (وإن حلق محرم رأس حلال، فلا فدية عليه)','3856','1254','0');
INSERT INTO [t19188] VALUES (1261,'١١٨٣ - مسألة: (وقطع الشعر ونتفه كحلقه، وشعر الرأس والبدن واحد. وعنه، لكل واحد حكم مفرد)','3857','1254','0');
INSERT INTO [t19188] VALUES (1262,'١١٨٤ - مسألة: (وإن خرج فى عينيه شعر فقلعه، أو نزل شعره فغطى عينيه فقصه، أو انكسر ظفره فقصه، أو قلع جلدا عليه شعر، فلا فدية عليه)','3859','1254','0');
INSERT INTO [t19188] VALUES (1263,'١١٨٥ - مسألة: (وإن استظل بالمحمل، ففيه روايتان)','3863','1254','0');
INSERT INTO [t19188] VALUES (1264,'١١٨٦ - مسألة: (وإن حمل على رأسه شيئا، أو نصب حياله ثوبا، أو استظل بخيمة أو شجرة أو بيت، فلا شئ عليه)','3868','1254','0');
INSERT INTO [t19188] VALUES (1265,'١١٨٧ - مسألة: (وفى تغطية الوجه روايتان)','3870','1254','0');
INSERT INTO [t19188] VALUES (1266,'١١٨٨ - مسألة: (الرابع، لبس المخيط والخفين)','3872','1254','0');
INSERT INTO [t19188] VALUES (1267,'١١٨٩ - مسألة: (إلا أن لا يجد إزارا، فليلبس','3873','1254','0');
INSERT INTO [t19188] VALUES (1268,'١١٩٠ - مسألة: (ولا يعقد عليه منطقة ولا رداء ولا غيره، إلا إزاره وهميانه الذى فيه نفقته، إذا لم يثبت إلا بالعقد)','3880','1254','0');
INSERT INTO [t19188] VALUES (1269,'١١٩١ - مسألة: (وإن طرح على كتفيه قباء، فعليه الفدية. وقال الخرقى: لا فدية عليه، إلا أن يدخل يديه فى كميه)','3884','1254','0');
INSERT INTO [t19188] VALUES (1270,'١١٩٢ - مسألة: (ويتقلد بالسيف عند الضرورة)','3885','1254','0');
INSERT INTO [t19188] VALUES (1271,'١١٩٣ - مسألة: (وشم المسك والكافور والعنبر والزعفران والورس، والتبخر','3889','1254','0');
INSERT INTO [t19188] VALUES (1272,'١١٩٤ - مسألة: (وإن مس من الطيب ما لا يعلق بيده، فلا فدية عليه)','3891','1254','0');
INSERT INTO [t19188] VALUES (1273,'١١٩٥ - مسألة: (وله شم العود والفواكه والشيح والخزامى','3891','1254','0');
INSERT INTO [t19188] VALUES (1274,'١١٩٦ - مسألة: (وفى شم الريحان والنرجس والورد والبنفسج','3892','1254','0');
INSERT INTO [t19188] VALUES (1275,'١١٩٧ - مسألة: (وإن جلس عند العطار، أو فى موضع ليشم الطيب، فشمه، فعليه الفدية، وإلا فلا)','3899','1254','0');
INSERT INTO [t19188] VALUES (1276,'١١٩٨ - مسألة: (فمن أتلفه، أو تلف في يده، أو أتلف جزءا منه، فعليه جزاؤه)','3903','1254','0');
INSERT INTO [t19188] VALUES (1277,'١١٩٩ - مسألة: (ويضمن ما دل عليه، أو أشار إليه، أو أعان','3904','1254','0');
INSERT INTO [t19188] VALUES (1278,'١٢٠٠ - مسألة: (ويحرم عليه الأكل من ذلك كله، وأكل ما صيد لأجله، ولا يحرم عليه الأكل من غير ذلك)','3912','1254','0');
INSERT INTO [t19188] VALUES (1279,'١٢٠١ - مسألة: (وإن أتلف بيض صيد، أو نقله إلى موضع آخر ففسد، فعليه ضمانه بقيمته)','3918','1254','0');
INSERT INTO [t19188] VALUES (1280,'١٢٠٢ - مسألة: (ولا يملك الصيد بغير الإرث. وقيل: لا يملكه','3921','1254','0');
INSERT INTO [t19188] VALUES (1281,'١٢٠٣ - مسألة: (وإن أمسك صيدا حتى تحلل، ثم تلف أو ذبحه، ضمنه، وكان ميتة. وقال أبو الخطاب: له أكله','3923','1254','0');
INSERT INTO [t19188] VALUES (1282,'١٢٠٤ - مسألة: (وإن أحرم وفى يده صيد، أو دخل الحرم بصيد، لزمه إزالة يده المشاهدة دون الحكمية عنه، فإن لم يفعل، فتلف، ضمنه، وإن أرسله إنسان من يده قهرا، فلا ضمان على المرسل)','3925','1254','0');
INSERT INTO [t19188] VALUES (1283,'١٢٠٥ - مسألة: (وإن قتل صيدا صائلا عليه دفعا عن نفسه، أو','3929','1254','0');
INSERT INTO [t19188] VALUES (1284,'١٢٠٦ - مسألة: (ولا تأثير للحرم ولا للإحرام في تحريم حيوان إنسى، ولا محرم الأكل، إلا القمل)','3931','1254','0');
INSERT INTO [t19188] VALUES (1285,'١٢٠٧ - مسألة: (ولا يحرم صيد البحر على المحرم، وفي إباحته في الحرم روايتان، لا يحرم صيد البحر على المحرم، بغير خلاف، لقوله تعالى: {أحل لكم صيد البحر وطعامه متاعا لكم وللسيارة}','3944','1254','0');
INSERT INTO [t19188] VALUES (1286,'١٢٠٨ - مسألة: (ويضمن الجراد بقيمته، فإن انفرش في','3946','1254','0');
INSERT INTO [t19188] VALUES (1287,'١٢٠٩ - مسألة: (ومن اضطر إلى أكل الصيد، أو احتاج','3949','1254','0');
INSERT INTO [t19188] VALUES (1288,'١٢١٠ - مسألة: (وعليهما المضي في فاسده، والقضاء على الفور من حيث أحرما أولا. ونفقة المرأة في القضاء عليها إن طاوعت، وإن أكرهت فعلى الزوج)','3963','1254','0');
INSERT INTO [t19188] VALUES (1289,'١٢١١ - مسألة: (ويتفرقان في القضاء من الموضع الذي أصابها فيه إلى أن يحلا. وهل','3966','1254','0');
INSERT INTO [t19188] VALUES (1290,'١٢١٢ - مسألة: (وإن جامع بعد التحلل الأول لم يفسد نسكه،','3972','1254','0');
INSERT INTO [t19188] VALUES (1291,'١٢١٣ - مسألة: (ولا تلبس القفازين، ولا الخلخال، ولا تكتحل بالإثمد)','3986','1254','0');
INSERT INTO [t19188] VALUES (1292,'١٢١٤ - مسألة: (ويجوز لبس المعصفر والكحلي، والخضاب بالحناء، والنظر في المرآة لهما جميعا)','3992','1254','0');
INSERT INTO [t19188] VALUES (1293,'باب الفدية','4003','1204','0');
INSERT INTO [t19188] VALUES (1294,'١٢١٥ - مسألة: النوع (الثانى، جزاء الصيد؛ يتخير فيه بين المثل وتقويمه بدراهم يشترى بها طعاما، فيطعم لكل مسكين مدا، أو يصوم عن كل مد يوما، وإن كان مما لا مثل لة، خير بين الإطعام والصيام. وعنه، أن جزاء الصيد على الترتيب، فيجب المثل، فإن لم يجد، لزمه الإطعام، فإن لم يجد، صام)','4007','1293','0');
INSERT INTO [t19188] VALUES (1295,'١٢١٦ - مسألة: (فإن لم يصم قبل يوم النحر، صام أيام منى. وعنه، لا يصومها، ويصوم بعد ذلك عشرة أيام، وعليه دم)','4020','1293','0');
INSERT INTO [t19188] VALUES (1296,'١٢١٧ - مسألة: (وقال أبو الخطاب: إن أخر الصوم أو الهدى لعذر، لم يلزمه إلا قضاؤه، وإن أخر الهدى لغير عذر، فهل يلزمه دم آخر؟ على روايتين. قال: وعندى أنه لا يلزمه مع','4023','1293','0');
INSERT INTO [t19188] VALUES (1297,'١٢١٨ - مسألة: (ومتى وجب عليه الصوم، فشرع فيه، ثم قدر على الهدى، لم يلزمه الانتقال إليه، إلا أن يشاء)','4026','1293','0');
INSERT INTO [t19188] VALUES (1298,'١٢١٩ - مسألة: (وإن وجب، ولم يشرع، فهل يلزمه الانتقال؟ على روايتين)','4027','1293','0');
INSERT INTO [t19188] VALUES (1299,'١٢٢٠ - مسألة: (النوع الثانى، المحصر، يلزمه الهدى، فإن لم يجد، صام عشرة أيام، ثم حل)','4029','1293','0');
INSERT INTO [t19188] VALUES (1300,'١٢٢١ - مسألة: (النوع الثالث، فدية الوطء، تجب به بدنة، فإن لم يجد، صام عشرة أيام؛ ثلاثة فى الحج، وسبعة إذا رجع، كدم المتعة؛ لقضاء الصحابة)','4030','1293','0');
INSERT INTO [t19188] VALUES (1301,'١٢٢٢ - مسألة: (ويجب بالوطء فى الفرج بدنة، إن كان فى الحج، وشاة، إن كان فى العمرة)','4035','1293','0');
INSERT INTO [t19188] VALUES (1302,'١٢٢٣ - مسألة: (ويجب على المرأة مثل ذلك، إن كانت مطاوعة، وإن كانت مكرهة، فلا فدية عليها. وقيل: عليها كفارة يتحملها الزوج عنها)','4036','1293','0');
INSERT INTO [t19188] VALUES (1303,'١٢٢٤ - مسألة: (ومتى أنزل بالمباشرة دون الفرج، فعليه بدنة، وإن لم ينزل، فعليه شاة. وعنه، بدنة)','4041','1293','0');
INSERT INTO [t19188] VALUES (1304,'١٢٢٥ - مسألة: (وإن كرر النظر فأنزل، أو استمنى، فعليه دم، هل هو شاة أو بدنة؟ على روايتين. وإن مذى بذلك، فعليه شاة)','4043','1293','0');
INSERT INTO [t19188] VALUES (1305,'١٢٢٦ - مسألة: (فإن فكر فأنزل، فلا شئ عليه)','4045','1293','0');
INSERT INTO [t19188] VALUES (1306,'١٢٢٧ - مسألة: (وإن قتل صيدا بعد صيد، فعليه جزاؤهما. وعنه، عليه جزاء واحد)','4049','1293','0');
INSERT INTO [t19188] VALUES (1307,'١٢٢٨ - مسألة: (كان فعل مخظورا من أجناس، فعليه لكل واحد فداء. وعنه، عليه فنية واحدة)','4050','1293','0');
INSERT INTO [t19188] VALUES (1308,'١٢٢٩ - مسألة: (كان حلق، أو قلم، أو وطئ، أو قتل صيدا عامدا أو مخطئا، فعليه الكفارة. وعنه فى الصيد، لا كفارة عليه إلا فى العمد، ويتخرج فى الحلق مثله)','4052','1293','0');
INSERT INTO [t19188] VALUES (1309,'١٢٣٠ - مسألة: (وإن لبس، أو تطيب، أو غطى رأسه ناسيا، فلا كفارة فيه. وعنه، عليه الكفارة)','4054','1293','0');
INSERT INTO [t19188] VALUES (1310,'١٢٣١ - مسألة: (ومن رفض إحرامه، ثم فعل محظورا، فعليه فداؤه)','4059','1293','0');
INSERT INTO [t19188] VALUES (1311,'١٢٣٢ - مسألة: (ومن تطيب قبل إحرامه فى بدنه، فله استدامة ذلك فى إحرامه، وليس له لبس ثوب مطيب)','4060','1293','0');
INSERT INTO [t19188] VALUES (1312,'١٢٣٣ - مسألة: (وإن أحرم وعليه قميص، خلعه ولم يشقه،','4061','1293','0');
INSERT INTO [t19188] VALUES (1313,'١٢٣٤ - مسألة: (وإن لبس ثوبا كان','4062','1293','0');
INSERT INTO [t19188] VALUES (1314,'١٢٣٥ - مسألة: (وأما الصيام، فيجزئه بكل مكان)','4071','1293','0');
INSERT INTO [t19188] VALUES (1315,'١٢٣٦ - مسألة: (وكل دم ذكرناه، يجزئ فيه شاة أو سبع بدنة. ومن وجبت عليه بدنة، أجزأته بقرة)','4071','1293','0');
INSERT INTO [t19188] VALUES (1316,'باب جزاء الصيد','4079','1204','0');
INSERT INTO [t19188] VALUES (1317,'١٢٣٧ - مسألة: (النوع الثانى، ما لم تقض فيه الصحابة، فيرجع فيه إلى قول عدلين من أهل الخبرة، ويجوز أن يكون القاتل','4089','1316','0');
INSERT INTO [t19188] VALUES (1318,'١٢٣٨ - مسألة: (ويجب فى كل واحد من الصغير والكبير، والصحيح والمعيب مثله، إلا الماخض تفدى بقيمة مثلها. وقال أبو الخطاب: يجب فيها مثلها)','4092','1316','0');
INSERT INTO [t19188] VALUES (1319,'١٢٣٩ - مسألة: (ويجوز فداء أعور من عين بأعور من أخرى، وفداء الذكر بالأنثى، وفى فدائها به وجهان)','4095','1316','0');
INSERT INTO [t19188] VALUES (1320,'١٢٤٠ - مسألة: (الضرب الثانى، ما لا مثل له؛ وهو سائر الطير فيجب؛ فيه قيمته، إلا ما كان أكبر من الحمام، فهل تجب؛ فيه قيمته أو شاة؟ على وجهين)','4096','1316','0');
INSERT INTO [t19188] VALUES (1321,'١٢٤١ - مسألة: (ومن أتلف جزءا من صيد، فعليه ما نقص من قيمته، أو قيمة مثله إن كان مثليا)','4098','1316','0');
INSERT INTO [t19188] VALUES (1322,'١٢٤٢ - مسألة: (وإن نفر صيدا، فتلف بشئ، ضمنه)','4100','1316','0');
INSERT INTO [t19188] VALUES (1323,'١٢٤٣ - مسألة: (وإن جرحه فغاب ولم يعلم خبره، فعليه ما نقصه، وكذلك إن وجده ميتا ولم يعلم موته بجنايته. وإن اندمل غير ممتنع، فعليه جزاء جميعه)','4101','1316','0');
INSERT INTO [t19188] VALUES (1324,'١٢٤٤ - مسألة: (وإن نتف ريشه فعاد، فلا شئ عليه. وقيل','4104','1316','0');
INSERT INTO [t19188] VALUES (1325,'١٢٤٥ - مسألة: (وكلما قتل صيدا حكم عليه)','4106','1316','0');
INSERT INTO [t19188] VALUES (1326,'١٢٤٦ - مسألة: (وإن اشترك جماعة فى قتل صيد، فعليهم جزاء واحد. وعنه، على كل واحد جزاء. وعنه، إن كفروا بالمال، فكفارة واحدة، وإن كفروا بالصيام، فعلى كل واحد كفارة)','4107','1316','0');
INSERT INTO [t19188] VALUES (1327,'باب صيد الحرم ونباته','4111','1204','0');
INSERT INTO [t19188] VALUES (1328,'١٢٤٧ - مسألة: (وهو حرام على الحلال والمحرم، فمن أتلف من صيده شيئا، فعليه ما على المحرم فى مثله)','4111','1327','0');
INSERT INTO [t19188] VALUES (1329,'١٢٤٨ - مسألة: (وإن رمى الحلال من الحل صيدا فى الحرم، أو أرسل كلبه عليه، أو قتل صيدا على غصن فى الحرم أصله فى الحل أو','4114','1327','0');
INSERT INTO [t19188] VALUES (1330,'١٢٤٩ - مسألة: (وإن قتل من الحرم صيدا فى الحل بسهمه، أو كلبه، أو صيدا على غصن فى الحل أصله فى الحرم، أو أمسك حمامة فى الحرم، فهلك فراخها فى الحل، لم يضمن، فى أصح الروايتين)','4116','1327','0');
INSERT INTO [t19188] VALUES (1331,'١٢٥٠ - مسألة: (وإن أرسل كلبه من الحل على صيد فى الحل،','4118','1327','0');
INSERT INTO [t19188] VALUES (1332,'١٢٥١ - مسألة: (ومن قطعه ضمن الشجرة الكبيرة ببقرة، والصغيرة بشاة، والحشيش بقيمته، والغصن بما نقص','4129','1327','0');
INSERT INTO [t19188] VALUES (1333,'١٢٥٢ - مسألة: (وإن قطع غصنا فى الحل أصله فى الحرم، ضمنه. وإن قطع غصنا فى الحرم أصله فى الحل، لم يضمنه، فى أحد','4133','1327','0');
INSERT INTO [t19188] VALUES (1334,'١٢٥٣ - مسألة: (ولا جزاء فى صيد المدينة. وعنه، جزاؤه سلب القاتل لمن أخذه)','4139','1327','0');
INSERT INTO [t19188] VALUES (1335,'١٢٥٤ - مسألة: (وحد حرمها بين ثور إلى عير. وجعل النبى - صلى الله عليه وسلم - حول المدينة اثنى عشر ميلا حمى)','4141','1327','0');
INSERT INTO [t19188] VALUES (1336,'باب ذكر دخول مكة','4146','1204','0');
INSERT INTO [t19188] VALUES (1337,'١٢٥٥ - مسألة (ويستحب أن يدخل مكة من أعلاها، من ثنية','4146','1336','0');
INSERT INTO [t19188] VALUES (1338,'١٢٥٦ - مسألة: (فإذا رأى البيت رفع يديه وكبر، وقال: اللهم أنت السلام، ومنك السلام، حينا ربنا بالسلام، اللهم زد هذا البيت تعظيما وتشريفا وتكريما ومهابة وبرا، وزد من عظمه','4148','1336','0');
INSERT INTO [t19188] VALUES (1339,'١٢٥٧ - مسألة: (ثم يبتدئ بطواف العمرة، إن كان معتمرا، وبطواف القدوم، إن كان مفردا أو قارنا)','4152','1336','0');
INSERT INTO [t19188] VALUES (1340,'١٢٥٨ - مسألة: (ويضطبع بردائه، فيجعل وسطه تحت عاتقه الأيمن، وطرفيه على عاتقه الأيسر)','4153','1336','0');
INSERT INTO [t19188] VALUES (1341,'١٢٥٩ - مسألة: (ثم يبتدئ من الحجر الأسود، فيحاذيه بجميع بدنه، ثم يستلمه، ويقبله، وإن شاء استلمه وقبل يده، وإن شاء أشار إليه، ثم يقول: الله أكبر إيمانا بك، وتصديقا بكتابك، ووفاء بعهدك، واتباعا لسنة نبيك محمد - صلى الله عليه وسلم -. كلما استلمه)','4155','1336','0');
INSERT INTO [t19188] VALUES (1342,'١٢٦٠ - مسألة: (ثم يأخذ على يمينه، ويجعل البيت على يساره)','4160','1336','0');
INSERT INTO [t19188] VALUES (1343,'١٢٦١ - مسألة: (فإذا أتى على الركن اليمانى استلمه وقبل يده)','4160','1336','0');
INSERT INTO [t19188] VALUES (1344,'١٢٦٢ - مسألة: (ويطوف سبعا، يرمل فى الثلاثة الأول منها؛ وهو إسراع المشى مع تقارب الخطى، ولا يثب وثبا، ويمشى أربعا)','4163','1336','0');
INSERT INTO [t19188] VALUES (1345,'١٢٦٣ - مسألة: (وكلما حاذى الحجر والركن اليمانى، استلمهما أو أشار إليهما. ويقول كلما حاذى الحجر: لا إله إلا الله والله أكبر)','4169','1336','0');
INSERT INTO [t19188] VALUES (1346,'١٢٦٤ - مسألة: (و)','4171','1336','0');
INSERT INTO [t19188] VALUES (1347,'١٢٦٥ - مسألة: (و)','4172','1336','0');
INSERT INTO [t19188] VALUES (1348,'١٢٦٦ - مسألة: (وليس على النساء ولا أهل مكة رمل ولا اضطباع. وليس فى غير هذا الطواف رمل ولا اضطباع)','4175','1336','0');
INSERT INTO [t19188] VALUES (1349,'١٢٦٧ - مسألة: (ومن طاف راكبا أو محمولا، أجزأه. وعنه، لا يجزئه إلا لعذر. ولا يجزئ عن الحامل)','4177','1336','0');
INSERT INTO [t19188] VALUES (1350,'١٢٦٨ - مسألة: (وإن طاف منكسا، أو على جدار الحجر، أو شاذروان الكعبة، أو ترك شيئا من طوافه، وإن قل، أو لم ينوه، لم يجزئه)','4184','1336','0');
INSERT INTO [t19188] VALUES (1351,'١٢٦٩ - مسألة: (وإن طاف محدثا، أو نجسا، أو عريانا، لم يجزئه. وعنه، يجزئه، ويجبره بدم)','4187','1336','0');
INSERT INTO [t19188] VALUES (1352,'١٢٧٠ - مسألة: (وإن أحدث فى بعض طوافه، أو قطعه بفصل طويل، ابتدأه)','4190','1336','0');
INSERT INTO [t19188] VALUES (1353,'١٢٧١ - مسألة: (ولو كان يسيرا، أو أقيمت الصلاة، أو حضرت جنازة، صلى، وبنى. ويتخرج أن الموالاة سنة)','4191','1336','0');
INSERT INTO [t19188] VALUES (1354,'١٢٧٢ - مسألة: (ثم يصلى ركعتين، والأفضل أن تكون خلف المقام، يقرأ فيهما: {قل ياأيها الكافرون}. [و {قل هو الله أحد}. بعد الفاتحة)','4193','1336','0');
INSERT INTO [t19188] VALUES (1355,'١٢٧٣ - مسألة: (ثم يعود إلى الركن فيسمتلمه)','4197','1336','0');
INSERT INTO [t19188] VALUES (1356,'١٢٧٤ - مسألة: (ثم يخرج إلى الصفا من بابه، ويسعى سبعا، يبدأ بالصفا، فيرقى عليه حتى يرى البيت فيستقبله، ويكبر ثلاثا، ويقول: الحمد لله على ما هدانا، لا إله إلا الله وحده لا شريك له، له','4198','1336','0');
INSERT INTO [t19188] VALUES (1357,'١٢٧٥ - مسألة: (ثم ينزل، فيمشى حتى يأتى العلم، فيسعى سعيا شديدا إلى العلم)','4202','1336','0');
INSERT INTO [t19188] VALUES (1358,'١٢٧٦ - مسألة: (ويستحب أن يسعى طاهرا مستترا متواليا. وعنه، أن ذلك من شرائطه)','4205','1336','0');
INSERT INTO [t19188] VALUES (1359,'١٢٧٧ - مسألة: (والمرأة لا ترمل ولا ترقى)','4208','1336','0');
INSERT INTO [t19188] VALUES (1360,'١٢٧٨ - مسألة: (فإذا فرغ من السعى، فإذا كان معتمرا، قصر من شعره، وتحلل، إلا أن يكون قد ساق)','4209','1336','0');
INSERT INTO [t19188] VALUES (1361,'١٢٧٩ - مسألة: (ومن كان متمتعا، قطع التلبية إذا وصل','4214','1336','0');
INSERT INTO [t19188] VALUES (1362,'باب صفة الحج','4217','1204','0');
INSERT INTO [t19188] VALUES (1363,'١٢٨٠ - مسألة: (يستحب للمتمتع الذى حل، وغيره من المحلين بمكة، الإحرام بالحج يوم التروية -وهو الثامن من ذى الحجة- من مكة، ومن حيث أحرم من الحرم، جاز)','4220','1362','0');
INSERT INTO [t19188] VALUES (1364,'١٢٨١ - مسألة: (ثم يخرج إلى منى، فيبيت فيها)','4224','1362','0');
INSERT INTO [t19188] VALUES (1365,'١٢٨٢ - مسألة: (فإذا طلعت الشمس، سار إلى عرفة، فأقام بنمرة حتى تزول الشمس)','4226','1362','0');
INSERT INTO [t19188] VALUES (1366,'١٢٨٣ - مسألة: (ثم يخطب الإمام خطبة يعلمهم فيها الوقوف ووقته، والدفع منه، والمبيت بمزدلفة، ثم ينزل فيصلى بهم الظهر والعصر، يجمع بينهما بأذان وإقامتين)','4227','1362','0');
INSERT INTO [t19188] VALUES (1367,'١٢٨٤ - مسألة: (ثم يروح إلى الموقف، وعرفة كلها موقف إلا','4230','1362','0');
INSERT INTO [t19188] VALUES (1368,'١٢٨٥ - مسألة: (ويستحب أن يقف عند الصخرات وجبل الرحمة راكبا. وقيل: الراجل أفضل)','4232','1362','0');
INSERT INTO [t19188] VALUES (1369,'١٢٨٦ - مسألة: (ويكثر من الدعاء، ومن قول: لا إله إلا الله وحده لا شريك له، له الملك وله الحمد، يحيى ويميت، وهو على كل شئ قدير. اللهم اجعل فى قلبى نورا، وفى بصرى نورا، وفى سمعى نورا، ويسر لى أمرى)','4234','1362','0');
INSERT INTO [t19188] VALUES (1370,'١٢٨٧ - مسألة: (ومن فاته ذلك، فاته الحج)','4242','1362','0');
INSERT INTO [t19188] VALUES (1371,'١٢٨٨ - مسألة: (ومن وقف بها نهارا، ودفع قبل غروب','4242','1362','0');
INSERT INTO [t19188] VALUES (1372,'١٢٨٩ - مسألة: (ومن وافاها ليلا فوقف بها، فلا دم عليه)','4245','1362','0');
INSERT INTO [t19188] VALUES (1373,'١٢٩٠ - مسألة: (ثم يدفع بعد غروب الشمس إلى مزدلفة، وعليه السكينة)','4246','1362','0');
INSERT INTO [t19188] VALUES (1374,'١٢٩١ - مسألة: (فإذا وصل مزدلفة، صلى المغرب والعشاء قبل حط الرحال)','4248','1362','0');
INSERT INTO [t19188] VALUES (1375,'١٢٩٢ - مسألة: (وإن صلى المغرب فى الطريق، ترك السنة، وأجزأه)','4250','1362','0');
INSERT INTO [t19188] VALUES (1376,'١٢٩٣ - مسألة: (ومن فاتته الصلاة مع الإمام بعرفة أو بمزدلفة، جمع وحده)','4251','1362','0');
INSERT INTO [t19188] VALUES (1377,'١٢٩٤ - مسألة: (ثم يبيت بها، فإن دفع قبل نصف الليل، فعليه دم، وإن دفع بعده، فلا شئ عليه، وإن وافاها بعد نصف الليل، فلا شئ عليه، وإن جاء بعد الفجر، فعليه دم. وحد المزدلفة ما بين المأزمين ووادى محسر)','4252','1362','0');
INSERT INTO [t19188] VALUES (1378,'١٢٩٥ - مسألة: (فإذا أصبح بها، صلى الصبح، ثم يأتى المشعر الحرام فيرقى عليه، أو يقف عنده، ويحمد الله تعالى، ويكبر، ويدعو)','4256','1362','0');
INSERT INTO [t19188] VALUES (1379,'١٢٩٦ - مسألة: (ثم يدفع قبل طلوع الشمس)','4257','1362','0');
INSERT INTO [t19188] VALUES (1380,'١٢٩٧ - مسألة: (فإذا بلغ محسرا، أسرع قدر رمية بحجر)','4259','1362','0');
INSERT INTO [t19188] VALUES (1381,'١٢٩٨ - مسألة: (ثم يأخذ حصى الجمار من طريقه، أو من','4259','1362','0');
INSERT INTO [t19188] VALUES (1382,'١٢٩٩ - مسألة: (وعدده سبعون حصاة)','4262','1362','0');
INSERT INTO [t19188] VALUES (1383,'١٣٠٠ - مسألة: (ويقطع التلبية مع ابتداء الرمى)','4268','1362','0');
INSERT INTO [t19188] VALUES (1384,'١٣٠١ - مسألة: (وإن رمى بذهب، أو فضة، أو غير الحصى، أو)','4270','1362','0');
INSERT INTO [t19188] VALUES (1385,'١٣٠٢ - مسألة: (ويرمى بعد طلوع الشمس، فإن رمى بعد نصف الليل، أجزأه)','4273','1362','0');
INSERT INTO [t19188] VALUES (1386,'١٣٠٣ - مسألة: (ثم ينحر هديا، إن كان معه، ويحلق أو يقصر من جميع شعره. وعنه، يجزئه بعضه، كالمسح)','4275','1362','0');
INSERT INTO [t19188] VALUES (1387,'١٣٠٤ - مسألة: (والمرأة تقصر من شعرها قدر الأنملة)','4281','1362','0');
INSERT INTO [t19188] VALUES (1388,'١٣٠٥ - مسألة: (ثم قد حل له كل شئ إلا النساء. وعنه، إلا الوطء فى الفرج)','4283','1362','0');
INSERT INTO [t19188] VALUES (1389,'١٣٠٦ - مسألة: (والحلاق','4285','1362','0');
INSERT INTO [t19188] VALUES (1390,'١٣٠٧ - مسألة: (وإن قدم الحلق على الرمى والنحر، جاهلا أو','4290','1362','0');
INSERT INTO [t19188] VALUES (1391,'١٣٠٨ - مسألة: (ثم يخطب الإمام خطبة، يعلمهم فيها النحر والإفاضة والرمى)','4295','1362','0');
INSERT INTO [t19188] VALUES (1392,'١٣٠٩ - مسألة: (ثم يفيض إلى مكة، ويطوف للزيارة، ويعينه بالنية، وهو الطواف الواجب الذى به تمام الحج)','4297','1362','0');
INSERT INTO [t19188] VALUES (1393,'١٣١٠ - مسألة: (وأول وقته بعد نصف الليل من ليلة النحر، والأفضل فعله يوم النحر، فإن أخره عنه وعن أيام منى، جاز)','4299','1362','0');
INSERT INTO [t19188] VALUES (1394,'١٣١١ - مسألة: (ثم يسعى بين الصفا والمروة، إن كان متمتعا،','4300','1362','0');
INSERT INTO [t19188] VALUES (1395,'١٣١٢ - مسألة: (ثم قد حل له كل شئ)','4302','1362','0');
INSERT INTO [t19188] VALUES (1396,'١٣١٣ - مسألة: ويستحب أن (يأتى زمزم، فيشرب من مائها لما أحب، ويتضلع','4307','1362','0');
INSERT INTO [t19188] VALUES (1397,'فصل','4308','1362','0');
INSERT INTO [t19188] VALUES (1398,'١٣١٤ - مسألة: (ويرمى الجمرات بها فى أيام التشريق بعد الزوال، كل جمرة بسبع حصيات، فيبدأ بالجمرة الأولى، وهى أبعدهن من مكة، وتلى مسجد الخيف، فيجعلها عن يساره، ويرميها','4309','1362','0');
INSERT INTO [t19188] VALUES (1399,'١٣١٥ - مسألة: (والترتيب شرط فى الرمى. وفى عدد الحصى روايتان؛ إحداهما سبع. والأخرى، يجزئه خمس)','4314','1362','0');
INSERT INTO [t19188] VALUES (1400,'١٣١٦ - مسألة: (فإن أخل بحصاة واجبة من الأولى، لم يصح رمى الثانية)','4316','1362','0');
INSERT INTO [t19188] VALUES (1401,'١٣١٧ - مسألة: (وإن أخر الرمى كله، فرماه فى آخر أيام التشريق، أجزأه، ويرتبه بنيته. وإن أخره عن أيام التشريق، أو ترك المبيت بمنى فى لياليها، فعليه دم، وفى حصاة أو ليلة واحدة ما فى حلق شعره)','4317','1362','0');
INSERT INTO [t19188] VALUES (1402,'١٣١٨ - مسألة: (وليس على أهل سقاية الحاج ولا الرعاء مبيت','4320','1362','0');
INSERT INTO [t19188] VALUES (1403,'١٣١٩ - مسألة: (ويخطب الإمام فى اليوم الثانى من أيام التشريق، خطبة يعلمهم فيها حكم التعجيل والتأخير، وتوديعهم)','4324','1362','0');
INSERT INTO [t19188] VALUES (1404,'١٣٢٠ - مسألة: (فمن أحب أن يتعجل فى يومين، خرج قبل غروب الشمس، فإن غربت الشمس، وهو بمنى لزمه المبيت','4324','1362','0');
INSERT INTO [t19188] VALUES (1405,'١٣٢١ - مسألة: (فإذا أتى مكة، لم يخرج حتى يودع البيت','4329','1362','0');
INSERT INTO [t19188] VALUES (1406,'١٣٢٢ - مسألة: (فإن ودع ثم اشتغل فى تجارة، أو أقام، أعاد الوداع)','4332','1362','0');
INSERT INTO [t19188] VALUES (1407,'١٣٢٣ - مسألة: (فإن أخر طواف الزيارة، فطافه عند الخروج، أجزأه عن طواف الوداع)','4333','1362','0');
INSERT INTO [t19188] VALUES (1408,'١٣٢٤ - مسألة: (فإن خرج قبل الوداع، رجع إليه. فإن لم يمكنه، فعليه دم، إلا الحائض والنفساء، لا وداع عليهما)','4334','1362','0');
INSERT INTO [t19188] VALUES (1409,'١٣٢٥ - مسألة: (فإذا فرغ من الوداع، وقف فى الملتزم بين الركن والباب)','4338','1362','0');
INSERT INTO [t19188] VALUES (1410,'١٣٢٦ - مسألة: (فإذا فرغ من الحج، استحب زيارة قبر النبى -صلى الله عليه وسلم- وقبر صاحبيه، رضى الله عنهما)','4345','1362','0');
INSERT INTO [t19188] VALUES (1411,'فصل فى صفة العمرة','4350','1362','0');
INSERT INTO [t19188] VALUES (1412,'١٣٢٧ - مسألة: (فإن أحرم من الحرم، لم يجز، وينعقد، وعليه دم)','4352','1362','0');
INSERT INTO [t19188] VALUES (1413,'١٣٢٨ - مسألة: (ثم يطوف ويسعى، ثم يحلق أو يقصر، ثم قد حل)','4353','1362','0');
INSERT INTO [t19188] VALUES (1414,'١٣٢٩ - مسألة: (وتجزئ عمرة القارن، والعمرة من التنعيم، عن عمرة الإسلام، فى أصح الروايتين)','4354','1362','0');
INSERT INTO [t19188] VALUES (1415,'فصل','4361','1362','0');
INSERT INTO [t19188] VALUES (1416,'١٣٣٠ - مسألة: (وواجباته سبعة؛ الإحرام من الميقات، والوقوف بعرفة إلى الليل، والمبيت بمزدلفة إلى بعد نصف الليل، والمبيت بمنى، والرمى، والحلاق','4365','1362','0');
INSERT INTO [t19188] VALUES (1417,'باب الفوات والإحصار','4370','1204','0');
INSERT INTO [t19188] VALUES (1418,'١٣٣١ - مسألة: (ومن طلع عليه الفجر يوم النحر ولم يقف بعرفة، فقد فاته الحج، ويتحلل بطواف وسعى. وعنه، أنه','4370','1417','0');
INSERT INTO [t19188] VALUES (1419,'١٣٣٢ - مسألة: (وهل يلزمه هدى؟ على روايتين؛ إحداهما،','4375','1417','0');
INSERT INTO [t19188] VALUES (1420,'١٣٣٣ - مسألة: (وإن أخطأ الناس، فوقفوا فى غير يوم عرفة، أجزأهم. وإن أخطأ بعضهم، فقد فاته الحج)','4379','1417','0');
INSERT INTO [t19188] VALUES (1421,'١٣٣٤ - مسألة: (ومن أحرم فحصره عدو، ولم يكن له طريق إلى الحج، نحر هديا فى موضعه، وحل)','4383','1417','0');
INSERT INTO [t19188] VALUES (1422,'١٣٣٥ - مسألة: (فإن لم يجد، صام عشرة أيام، ثم حل، ولو نوى التحلل قبل ذلك، لم يحل)','4390','1417','0');
INSERT INTO [t19188] VALUES (1423,'١٣٣٦ - مسألة: (وفى وجوب القضاء على المحصور روايتان)','4392','1417','0');
INSERT INTO [t19188] VALUES (1424,'١٣٣٧ - مسألة: (فإن صد عن عرفة دون البيت، تحلل بعمرة، ولا شئ عليه)','4394','1417','0');
INSERT INTO [t19188] VALUES (1425,'١٣٣٨ - مسألة','4395','1417','0');
INSERT INTO [t19188] VALUES (1426,'١٣٣٩ - مسألة: (ومن أحصر بمرض أو ذهاب نفقة، لم يكن له التحلل)','4396','1417','0');
INSERT INTO [t19188] VALUES (1427,'١٣٤٠ - مسألة: (ومن شرط فى ابتداء إحرامه؛ أن محلى حيث حبستنى، فله التحلل بجميع ذلك، ولا شئ عليه)','4399','1417','0');
INSERT INTO [t19188] VALUES (1428,'باب الهدى والأضاحى','4400','1204','0');
INSERT INTO [t19188] VALUES (1429,'١٣٤١ - مسألة: (والأفضل فيهما الإبل، ثم البقر، ثم الغنم. والذكر والأنثى سواء)','4401','1428','0');
INSERT INTO [t19188] VALUES (1430,'١٣٤٢ - مسألة: (ولا يجزئ إلا الجذع من الضأن؛ وهو ما له','4405','1428','0');
INSERT INTO [t19188] VALUES (1431,'١٣٤٣ - مسألة: (وثنى الإبل ما كمل له خمس سنين، ومن البقر','4407','1428','0');
INSERT INTO [t19188] VALUES (1432,'١٣٤٤ - مسألة: (وتجزئ الشاة عن واحد، والبدنة والبقرة عن سبعة، سواء أراد جميعهم القربة، أو بعضهم والباقون اللحم)','4409','1428','0');
INSERT INTO [t19188] VALUES (1433,'١٣٤٥ - مسألة: (ولا يجزئ فيهما','4414','1428','0');
INSERT INTO [t19188] VALUES (1434,'١٣٤٦ - مسألة: (وتجزئ الجماء والبتراء والخصى. وقال ابن حامد: لا تجزئ الجماء)','4421','1428','0');
INSERT INTO [t19188] VALUES (1435,'١٣٤٧ - مسألة: (والسنة نحر الإبل قائمة معقولة يدها اليسرى، فيطعنها بالحربة فى الوهدة التى بين أصل العنق والصدر، ويذبح البقر والغنم)','4424','1428','0');
INSERT INTO [t19188] VALUES (1436,'١٣٤٨ - مسألة: (ويقول عند ذلك: بسم الله والله أكبر، اللهم هذا منك ولك)','4426','1428','0');
INSERT INTO [t19188] VALUES (1437,'١٣٤٩ - مسألة: (ولا يستحب أن يذبحها إلا مسلم، وإن ذبحها بيده كان أفضل، فإن لم يفعل، استحب أن يشهدها)','4428','1428','0');
INSERT INTO [t19188] VALUES (1438,'١٣٥٠ - مسألة: (ووقت الذبح يوم العيد بعد الصلاة أو قدرها، إلى آخر يومين من أيام التشريق)','4430','1428','0');
INSERT INTO [t19188] VALUES (1439,'١٣٥١ - مسألة: (ولا تجزى فى ليلتيهما، فى قول الخرقى. وقال غيره: يجزئ)','4438','1428','0');
INSERT INTO [t19188] VALUES (1440,'١٣٥٢ - مسألة: (فإن فات الوقت، ذبح الواجب قضاء، وسقط التطوع)','4439','1428','0');
INSERT INTO [t19188] VALUES (1441,'١٣٥٣ - مسألة: (ويتعين الهدى بقوله: هذا هدى. أو تقليده، أو إشعاره مع النية. والأضحية بقوله: هذه أضحية. ولو نوى حال الشراء، لم تتعين بذلك)','4441','1428','0');
INSERT INTO [t19188] VALUES (1442,'١٣٥٤ - مسألة: (وإذا تعينت لم يجز بيعها ولا هبتها، إلا أن يبدلها','4443','1428','0');
INSERT INTO [t19188] VALUES (1443,'١٣٥٥ - مسألة: (وله ركوبها عند الحاجة، ما لم يضر بها)','4447','1428','0');
INSERT INTO [t19188] VALUES (1444,'١٣٥٦ - مسألة: (وإن ولدت ذبح ولدها معها، ولا يشرب من لبنها إلا ما فضل عن ولدها)','4449','1428','0');
INSERT INTO [t19188] VALUES (1445,'١٣٥٧ - مسألة: (و)','4452','1428','0');
INSERT INTO [t19188] VALUES (1446,'١٣٥٨ - مسألة: (ولا يعطى الجازر بأجرته شيئا منها)','4452','1428','0');
INSERT INTO [t19188] VALUES (1447,'١٣٥٩ - مسألة: (وله أن ينتفع بجلدها وجلها','4453','1428','0');
INSERT INTO [t19188] VALUES (1448,'١٣٦٠ - مسألة: (فإن ذبحها فسرقت، فلا شئ عليه)','4455','1428','0');
INSERT INTO [t19188] VALUES (1449,'١٣٦١ - مسألة: (وإن ذبحها ذابح فى وقتها بغير إذن، أجزأت، ولا ضمان على ذابحها)','4456','1428','0');
INSERT INTO [t19188] VALUES (1450,'١٣٦٢ - مسألة: (وإن أتلفها أجنبى، ضمنها بقيمتها، وإن أتلفها صاحبها، ضمنها بأكثر الأمرين من قيمتها أو مثلها. فإن ضمنها بمثلها وأخرج فضل القيمة، جاز، ويشترى به شاة أو سبع بدنة، فإن لم يبلغ،','4459','1428','0');
INSERT INTO [t19188] VALUES (1451,'١٣٦٣ - مسألة: (فإن تلفت بغير تفريطه)','4462','1428','0');
INSERT INTO [t19188] VALUES (1452,'١٣٦٤ - مسألة: (وإن عطب الهدى فى الطريق، نحره فى موضعه، وصبغ نعله التى فى عنقه فى دمه، وضرب بها صفحة سنامه؛ ليعرفه الفقراء، فيأخذوه. ولا يأكل منه هو، ولا أحد من أهل رفقته)','4463','1428','0');
INSERT INTO [t19188] VALUES (1453,'١٣٦٥ - مسألة: (وإن تعيبت، ذبحها، وأجزأته، إلا أن تكون واجبة فى ذمته قبل التعيين، كالفدية والمنذور فى الذمة، فعليه بدلها)','4467','1428','0');
INSERT INTO [t19188] VALUES (1454,'١٣٦٦ - مسألة: (وهل له استرجاع هذا العاطب والمعيب؟ على روايتين)','4471','1428','0');
INSERT INTO [t19188] VALUES (1455,'١٣٦٧ - مسألة: (وكذلك إن ضلت فذبح بدلها ثم وجدها)','4473','1428','0');
INSERT INTO [t19188] VALUES (1456,'فصل','4475','1428','0');
INSERT INTO [t19188] VALUES (1457,'١٣٦٨ - مسألة: (ويستحب أن يقفه بعرفة، ويجمع فيه','4475','1428','0');
INSERT INTO [t19188] VALUES (1458,'١٣٦٩ - مسألة: (ويسن إشعار البدنة','4476','1428','0');
INSERT INTO [t19188] VALUES (1459,'١٣٧٠ - مسألة: (وإذا نذر هديا مطلقا، فأقل ما يجزئه شاة، أو سبع بدنة)','4480','1428','0');
INSERT INTO [t19188] VALUES (1460,'١٣٧١ - مسألة: (ومن نذر بدنة، أجزأته بقرة)','4481','1428','0');
INSERT INTO [t19188] VALUES (1461,'١٣٧٢ - مسألة: (فإن عين بنذره، أجزأه ما عينه، صغيرا كان أو كبيرا، من الحيوان وغيره، وعليه إيصاله إلى فقراء الحرم، إلا أن يعينه بموضع سواه)','4481','1428','0');
INSERT INTO [t19188] VALUES (1462,'١٣٧٣ - مسألة: (ويستحب أن يأكل من هديه، ولا يأكل من','4483','1428','0');
INSERT INTO [t19188] VALUES (1463,'فصل','4488','1428','0');
INSERT INTO [t19188] VALUES (1464,'١٣٧٤ - مسألة: (وذبحها أفضل من الصدقة بثمنها)','4490','1428','0');
INSERT INTO [t19188] VALUES (1465,'١٣٧٥ - مسألة: (ويستحب أن يأكل ثلثها، ويهدى ثلثها، ويتصدق بثلثها، وإن أكل أكثر، جاز)','4491','1428','0');
INSERT INTO [t19188] VALUES (1466,'١٣٧٦ - مسألة: (فإن أكلها كلها، ضمن أقل ما يجزئ فى الصدقة منها)','4496','1428','0');
INSERT INTO [t19188] VALUES (1467,'١٣٧٧ - مسألة: (ومن أراد أن يضحى، فدخل العشر، فلا يأخذ من شعره ولا بشرته شيئا)','4498','1428','0');
INSERT INTO [t19188] VALUES (1468,'فصل','4501','1428','0');
INSERT INTO [t19188] VALUES (1469,'١٣٧٨ - مسألة: (عن الغلام شاتان، وعن الجارية شاة)','4504','1428','0');
INSERT INTO [t19188] VALUES (1470,'١٣٧٩ - مسألة: (وتذبح يوم سابعه، ويحلق رأسه، ويتصدق بوزنه ورقا. فإن فات، ففى أربع عشرة، فإن فات، ففى إحدى وعشرين)','4507','1428','0');
INSERT INTO [t19188] VALUES (1471,'١٣٨٠ - مسألة: (وينزعها أعضاء، ولا يكسر عظمها، وحكمها حكم الأضحية)','4512','1428','0');
INSERT INTO [t19188] VALUES (1472,'١٣٨١ - مسألة: (ولا تسن الفرعة؛ وهى ذبح أول ولد الناقة،','4516','1428','0');
INSERT INTO [t19188] VALUES (1473,'كتاب الجهاد','4521','0','0');
INSERT INTO [t19188] VALUES (1474,'[باب. . .]','4521','1473','0');
INSERT INTO [t19188] VALUES (1475,'١٣٨٢ - مسألة: (وهو فرض كفاية','4522','1474','0');
INSERT INTO [t19188] VALUES (1476,'١٣٨٣ - مسألة: (ولا يجب إلا على ذكر حر مكلف مستطيع؛','4524','1474','0');
INSERT INTO [t19188] VALUES (1477,'١٣٨٤ - مسألة: (وأقل ما يفعل مرة فى كل عام، إلا أن تدعو الحاجة إلى تأخيره)','4528','1474','0');
INSERT INTO [t19188] VALUES (1478,'١٣٨٥ - مسألة: (وأفضل ما يتطوع به الجهاد)','4532','1474','0');
INSERT INTO [t19188] VALUES (1479,'١٣٨٦ - مسألة: (وغزو البحر أفضل من غزو البر)','4535','1474','0');
INSERT INTO [t19188] VALUES (1480,'١٣٨٧ - مسألة: (ويغزى مع كل بر وفاجر)','4537','1474','0');
INSERT INTO [t19188] VALUES (1481,'١٣٨٨ - مسألة: (ويقاتل كل قوم من يليهم من العدو)','4538','1474','0');
INSERT INTO [t19188] VALUES (1482,'١٣٨٩ - مسألة: (وتمام الرباط أربعون يوما، وهو لزوم الثغر للجهاد)','4542','1474','0');
INSERT INTO [t19188] VALUES (1483,'١٣٩٠ - مسألة: (ولا يستحب نقل أهله إليه. وقال رسول الله -صلى الله عليه وسلم-: «رباط يوم فى سبيل الله، خير من ألف يوم فيما سواه من المنازل»)','4547','1474','0');
INSERT INTO [t19188] VALUES (1484,'١٣٩١ - مسألة: (وتجب. الهجرة على من يعجز عن إظهار دينه فى دار الحرب، وتستحب لمن قدر عليه)','4551','1474','0');
INSERT INTO [t19188] VALUES (1485,'١٣٩٢ - مسألة: (ولا يجاهد من عليه دين لا وفاء له، ومن أحد','4555','1474','0');
INSERT INTO [t19188] VALUES (1486,'١٣٩٣ - مسألة: (ولا يجوز للمسلمين الفرار من ضعفهم، إلا متحرفين لقتال، أو متحيزين إلى فئة، فإن زاد الكفار، فلهم الفرار، إلا أن يغلب على ظنهم الظفر)','4562','1474','0');
INSERT INTO [t19188] VALUES (1487,'١٣٩٤ - مسألة: (فإن ألقى فى مركبهم نار)','4569','1474','0');
INSERT INTO [t19188] VALUES (1488,'فصل','4570','1474','0');
INSERT INTO [t19188] VALUES (1489,'١٣٩٥ - مسألة: (ولا يجوز إحراق نحل، ولا تغريقه)','4572','1474','0');
INSERT INTO [t19188] VALUES (1490,'١٣٩٦ - مسألة: (ولا)','4573','1474','0');
INSERT INTO [t19188] VALUES (1491,'١٣٩٧ - مسألة: (وفى حرق شجرهم وزرعهم وقطعه روايتان؛ إحداهما، يجوز، إن لم يضر بالمسلمين. والأخرى، لا يجوز، إلا أن لا يقدر عليهم إلا به، أو يكونوا يفعلونه بنا، وكذلك رميهم بالنار، وفتح الماء ليغرقهم)','4578','1474','0');
INSERT INTO [t19188] VALUES (1492,'١٣٩٨ - مسألة: (وإذا ظفر بهم، لم يقتل صبى، ولا امرأة، ولا راهب، ولا شيخ فان، ولا زمن، ولا أعمى، لا رأى لهم، إلا أن يقاتلوا)','4583','1474','0');
INSERT INTO [t19188] VALUES (1493,'١٣٩٩ - مسألة: (فإن تترسوا بهم، جاز رميهم، ويقصد المقاتلة)','4591','1474','0');
INSERT INTO [t19188] VALUES (1494,'١٤٠٠ - مسألة: (وإن تترسوا بمسلمين، لم يجز رميهم، إلا أن يخاف على المسلمين، فيرميهم، ويقصد الكفار)','4592','1474','0');
INSERT INTO [t19188] VALUES (1495,'١٤٠١ - مسألة: (ومن أسر أسيرا، لم يجز له قتله حتى يأتى به الإمام، إلا أن يمتنع من السير معه ولا يمكنه إكراهه)','4593','1474','0');
INSERT INTO [t19188] VALUES (1496,'١٤٠٢ - مسألة: (ويخير الأمير فى الأسرى؛ بين القتل، والاسترقاق، والمن، والفداء بمسلم، أو بمال. وعنه، لا يجوز بمال، إلا غير الكتابى، ففى استرقاقه روايتان. ولا يجوز أن يختار إلا الأصلح للمسلمين)','4596','1474','0');
INSERT INTO [t19188] VALUES (1497,'١٤٠٣ - مسألة: (فإن أسلموا رقوا فى الحال)','4607','1474','0');
INSERT INTO [t19188] VALUES (1498,'١٤٠٤ - مسألة: (ومن سبى من أطفالهم منفردا أو مع أحد أبويه،','4608','1474','0');
INSERT INTO [t19188] VALUES (1499,'١٤٠٥ - مسألة: (ولا ينفسخ النكاح باسترقاق الزوجين، وإن سبيت المرأة وحدها، انفسخ نكاحها، وحلت لسابيها)','4611','1474','0');
INSERT INTO [t19188] VALUES (1500,'١٤٠٦ - مسألة: (وهل يجوز بيع من استرق منهم للمشركين؟ على روايتين)','4615','1474','0');
INSERT INTO [t19188] VALUES (1501,'١٤٠٧ - مسألة: (ولا يفرق فى البيع بين ذوى رحم محرم، إلا بعد البلوغ، على إحدى الروايتين)','4616','1474','0');
INSERT INTO [t19188] VALUES (1502,'١٤٠٨ - مسألة: (وإذا حصر الإمام حصنا، لزمه مصابرته، إذا رأى المصلحة فيها)','4622','1474','0');
INSERT INTO [t19188] VALUES (1503,'١٤٠٩ - مسألة: ([فإن أسلموا، أو]','4623','1474','0');
INSERT INTO [t19188] VALUES (1504,'١٤١٠ - مسألة: (وإن سألوا الموادعة بمال أو غيره، جاز، إن كانت المصلحة فيه)','4627','1474','0');
INSERT INTO [t19188] VALUES (1505,'١٤١١ - مسألة: (وإن نزلوا على حكم حاكم، جاز، إذا كان حرا، مسلما، بالغا، عاقلا، من أهل الاجتهاد)','4627','1474','0');
INSERT INTO [t19188] VALUES (1506,'١٤١٢ - مسألة: (ولا يحكم إلا بما فيه الحظ للمسلمين؛ من القتل، والسبى، والفداء، فإن حكم بالمن، لزم قبوله، فى أحد الوجهين)','4629','1474','0');
INSERT INTO [t19188] VALUES (1507,'١٤١٣ - مسألة: (وإن حكم بقتل، أو سبى، فأسلموا، عصموا دماءهم، فى استرقاقهم وجهان)','4631','1474','0');
INSERT INTO [t19188] VALUES (1508,'باب ما يلزم الإمام والجيش','4633','1473','0');
INSERT INTO [t19188] VALUES (1509,'١٤١٤ - مسألة: (يلزم الإمام عند مسير الجيش تعاهد الخيل والرجال، فما لا يصلح للحرب، يمنعه من الدخول)','4633','1508','0');
INSERT INTO [t19188] VALUES (1510,'١٤١٥ - مسألة: (ويمنع المخذل، والمرجف)','4634','1508','0');
INSERT INTO [t19188] VALUES (1511,'١٤١٦ - مسألة: (و)','4635','1508','0');
INSERT INTO [t19188] VALUES (1512,'١٤١٧ - مسألة: (ولا يستعين بمشرك، إلا عند الحاجة إليه)','4637','1508','0');
INSERT INTO [t19188] VALUES (1513,'١٤١٨ - مسألة: (ويرفق بهم فى السير)','4640','1508','0');
INSERT INTO [t19188] VALUES (1514,'١٤١٩ - مسألة: (ويجوز أن يبذل جعلا لمن يدله على طريق، أو قلعة، أو ماء. ويجب أن يكون معلوما، إلا أن يكون من مال الكفار، فيجوز)','4645','1508','0');
INSERT INTO [t19188] VALUES (1515,'١٤٢٠ - مسألة: (فإن شرط له جارية)','4646','1508','0');
INSERT INTO [t19188] VALUES (1516,'١٤٢١ - مسألة: (وإن فتحت صلحا، ولم يشترطوا الجارية، فله قيمتها)','4647','1508','0');
INSERT INTO [t19188] VALUES (1517,'١٤٢٢ - مسألة: (وله أن ينقل فى البدأة الربع بعد الخمس، وفى الرجعة الثلث بعده، وذلك','4649','1508','0');
INSERT INTO [t19188] VALUES (1518,'فصل','4660','1508','0');
INSERT INTO [t19188] VALUES (1519,'١٤٢٣ - مسألة: (ولا يجوز لأحد أن يتعلف، ولا يحتطب، ولا يبارز، ولا يخرج من المعسكر، ولا يحدث حدثا، إلا بإذن الأمير)','4660','1508','0');
INSERT INTO [t19188] VALUES (1520,'١٤٢٤ - مسألة: (فإن دعا كافر إلى البراز، استحب لمن يعلم من نفسه القوة والشجاعة أن يبارزه بإذن الأمير)','4663','1508','0');
INSERT INTO [t19188] VALUES (1521,'١٤٢٥ - مسألة: (فإن شرط الكافر أن لا يقاتله غير الخارج إليه، فله شرطه)','4665','1508','0');
INSERT INTO [t19188] VALUES (1522,'١٤٢٦ - مسألة: (فإن انهزم المسلم، أو أثخن بالجراح، جاز الدفع عنه)','4665','1508','0');
INSERT INTO [t19188] VALUES (1523,'١٤٢٧ - مسألة: (وإن قتله المسلم، فله سلبه)','4667','1508','0');
INSERT INTO [t19188] VALUES (1524,'١٤٢٨ - مسألة: (وكل من قتل قتيلا، فله سلبه غير مخموس، إذا قتله حال الحرب منهمكا على القتال، غير مثخن، وغرر بنفسه فى قتله. وعنه، لا يستحقه إلا من شرط له)','4669','1508','0');
INSERT INTO [t19188] VALUES (1525,'١٤٢٩ - مسألة: (وإن قطع أربعته، وقتله آخر، فسلبه للقاطع)','4678','1508','0');
INSERT INTO [t19188] VALUES (1526,'١٤٣٠ - مسألة: (وإن قتله اثنان، فسلبه غنيمة)','4678','1508','0');
INSERT INTO [t19188] VALUES (1527,'١٤٣١ - مسألة: (وإن أسره، وقتله الإمام، فسلبه غنيمة)','4679','1508','0');
INSERT INTO [t19188] VALUES (1528,'١٤٣٢ - مسألة: (وإن قطع يده ورجله، وقتله آخر، فسلبه غنيمة. وقيل: هو للقاتل)','4680','1508','0');
INSERT INTO [t19188] VALUES (1529,'١٤٣٣ - مسألة: (والسلب ما كان عليه؛ من ثياب، وحلى، وسلاح، والدابة بآلتها. وعنه، أن الدابة ليست من السلب. ونفقته، وخيمته، ورحله غنيمة)','4682','1508','0');
INSERT INTO [t19188] VALUES (1530,'١٤٣٤ - مسألة: (وإن دخل قوم لا منعة لهم دار الحرب بغير إذن الإمام، فغنموا)','4693','1508','0');
INSERT INTO [t19188] VALUES (1531,'١٤٣٥ - مسألة: (ومن أخذ من دار الحرب طعاما، أو علفا،','4696','1508','0');
INSERT INTO [t19188] VALUES (1532,'١٤٣٦ - مسألة: (فإن فضل معه منه شئ فأدخله البلد، رده فى الغنيمة، إلا أن يكون يسيرا، فله أكله، فى إحدى الروايتين)','4703','1508','0');
INSERT INTO [t19188] VALUES (1533,'١٤٣٧ - مسألة: (ومن أخذ سلاحا، فله أن يقاتل به حتى تنقضى الحرب، ثم يرده. وليس له ركوب الفرس، فى إحدى الروايتين)','4707','1508','0');
INSERT INTO [t19188] VALUES (1534,'باب قسمة الغنائم','4710','1473','0');
INSERT INTO [t19188] VALUES (1535,'١٤٣٨ - مسألة: (وإن أخذ منهم مال مسلم، فأدركه صاحبه قبل قسمه، فهو أحق به، وإن أدركه مقسوما، فهو أحق به بثمنه. وعنه، لا حق له','4711','1534','0');
INSERT INTO [t19188] VALUES (1536,'١٤٣٩ - مسألة: (ويملك الكفار أموال المسلمين بالقهر. ذكره القاضى. وقال أبو الخطاب: ظاهر كلام أحمد أنهم لا يملكونها)','4719','1534','0');
INSERT INTO [t19188] VALUES (1537,'١٤٤٠ - مسألة: (وما أخذ','4725','1534','0');
INSERT INTO [t19188] VALUES (1538,'١٤٤١ - مسألة: (وتملك الغنيمة بالاستيلاء عليها فى دار','4728','1534','0');
INSERT INTO [t19188] VALUES (1539,'١٤٤٢ - مسألة: (وهى لمن شهد الوقعة من أهل القتال، قاتل أو لم يقاتل، من تجار العسكر وأجرائهم، الذين يستعدون للقتال)','4731','1534','0');
INSERT INTO [t19188] VALUES (1540,'١٤٤٣ - مسألة: (فأما المريض العاجز عن القتال، والمخذل، والمرجف، والفرس الضعيف العجيف، فلا حق له)','4733','1534','0');
INSERT INTO [t19188] VALUES (1541,'١٤٤٤ - مسألة: (وإذا لحق مدد، أو هرب أسير، فأدركوا الحرب قبل تقضيها، أسهم لهم. وإن جاءوا بعد إحراز الغنيمة، فلا شئ لهم)','4734','1534','0');
INSERT INTO [t19188] VALUES (1542,'١٤٤٥ - مسألة: (وإذا أراد القسمة، بدأ بالأسلاب فدفعها إلى أهلها)','4740','1534','0');
INSERT INTO [t19188] VALUES (1543,'١٤٤٦ - مسألة: (ثم يخمس الباقى، فيقسم خمسه على خمسة','4740','1534','0');
INSERT INTO [t19188] VALUES (1544,'١٤٤٧ - مسألة: (ثم يعطى النفل بعد ذلك)','4756','1534','0');
INSERT INTO [t19188] VALUES (1545,'١٤٤٨ - مسألة: (ويرضخ لمن لا يسهم له؛ وهم العبيد، والنساء، والصبيان)','4757','1534','0');
INSERT INTO [t19188] VALUES (1546,'١٤٤٩ - مسألة: (وفى الكافر روايتان؛ إحداهما، يرضخ له. والأخرى، يسهم له)','4763','1534','0');
INSERT INTO [t19188] VALUES (1547,'١٤٥٠ - مسألة: (ولا يبلغ بالرضخ للراجل سهم راجل، ولا','4764','1534','0');
INSERT INTO [t19188] VALUES (1548,'١٤٥١ - مسألة: (فإن تغيرت حالهم قبل تقضى الحرب، أسهم لهم)','4765','1534','0');
INSERT INTO [t19188] VALUES (1549,'١٤٥٢ - مسألة: (وإن غزا العبد على فرس لسيده، قسم للفرس، ورضخ للعبد)','4766','1534','0');
INSERT INTO [t19188] VALUES (1550,'١٤٥٣ - مسألة: (ثم يقسم باقى الغنيمة؛ للراجل سهم، وللفارس ثلاثة أسهم؛ سهم له، وسهمان لفرسه)','4769','1534','0');
INSERT INTO [t19188] VALUES (1551,'١٤٥٤ - مسألة: (إلا أن يكون فرسه هجينا أو برذونا؛ فيكون له سهم. وعنه، له سهمان، كالعربى)','4772','1534','0');
INSERT INTO [t19188] VALUES (1552,'١٤٥٥ - مسألة: (ولا يسهم لأكثر من فرسين)','4776','1534','0');
INSERT INTO [t19188] VALUES (1553,'١٤٥٦ - مسألة: (ولا يسهم لغير الخيل. وقال الخرقى: من غزا على بعير لا يقدر على غيره، قسم له ولبعيره سهمان)','4777','1534','0');
INSERT INTO [t19188] VALUES (1554,'١٤٥٧ - مسألة: (ومن دخل دار الحرب راجلا، ثم ملك فرسا، أو استعاره، أو استأجره، فشهد به الوقعة، فله سهم فارس. ومن دخل فارسا، فنفق فرسه، أو شرد حتى تقضى الحرب، فله سهم راجل)','4781','1534','0');
INSERT INTO [t19188] VALUES (1555,'١٤٥٨ - مسألة: (ومن غصب فرسا فقاتل عليه، فسهم الفرس لمالكه)','4784','1534','0');
INSERT INTO [t19188] VALUES (1556,'١٤٥٩ - مسألة: (وإذا قال الإمام: من أخذ شيئا فهو له. أو فضل بعض الغانمين على بعض، لم يجز، فى إحدى الروايتين، ويجوز فى الأخرى)','4786','1534','0');
INSERT INTO [t19188] VALUES (1557,'١٤٦٠ - مسألة: (ومن استؤجر للجهاد ممن لا يلزمه من العبيد','4788','1534','0');
INSERT INTO [t19188] VALUES (1558,'١٤٦١ - مسألة: (ومن مات بعد انقضاء الحرب، فسهمه','4794','1534','0');
INSERT INTO [t19188] VALUES (1559,'١٤٦٢ - مسألة: (ويشارك الجيش سراياه فيما غنمت، ويشاركونه فيما غنم)','4796','1534','0');
INSERT INTO [t19188] VALUES (1560,'١٤٦٣ - مسألة: (وإذا قسمت الغنيمة فى أرض الحرب،','4797','1534','0');
INSERT INTO [t19188] VALUES (1561,'١٤٦٤ - مسألة: [ومن]','4802','1534','0');
INSERT INTO [t19188] VALUES (1562,'١٤٦٥ - مسألة: (ومن أعتق منهم عبدا، عتق عليه قدر حصته، وقوم عليه باقيه إن كان موسرا، وكذلك إن كان فيهم من يعتق عليه)','4807','1534','0');
INSERT INTO [t19188] VALUES (1563,'١٤٦٦ - مسألة: (والغال من الغنيمة يحرق رحله كله، إلا السلاح والمصحف والحيوان)','4809','1534','0');
INSERT INTO [t19188] VALUES (1564,'١٤٦٧ - مسألة: (وما أخذ من الفدية، أو أهداه الكفار لأمير الجيش، أو بعض قواده، فهو غنيمة)','4817','1534','0');
INSERT INTO [t19188] VALUES (1565,'باب حكم الأرضين المغنومة','4820','1473','0');
INSERT INTO [t19188] VALUES (1566,'١٤٦٨ - مسألة: الضرب (الثانى، ماجلا عنها أهلها خوفا)','4827','1565','0');
INSERT INTO [t19188] VALUES (1567,'١٤٦٩ - مسألة: (ويقرون فيها بغير جزية؛ لأنهم فى غير دار الإسلام، بخلاف التى قبلها)','4830','1565','0');
INSERT INTO [t19188] VALUES (1568,'١٤٧٠ - مسألة: (والمرجع فى الخراج والجزية إلى اجتهاد الإمام فى الزيادة والنقصان على قدر الطاقة. وعنه، يرجع إلى ما ضربه','4830','1565','0');
INSERT INTO [t19188] VALUES (1569,'١٤٧١ - مسألة: (وما لا يناله الماء مما لا يمكن زرعه، فلا خراج عليه)','4835','1565','0');
INSERT INTO [t19188] VALUES (1570,'١٤٧٢ - مسألة: (فإن أمكن زرعه عاما بعد عام، وجب نصف','4835','1565','0');
INSERT INTO [t19188] VALUES (1571,'١٤٧٣ - مسألة: (و)','4836','1565','0');
INSERT INTO [t19188] VALUES (1572,'١٤٧٤ - مسألة: (والخراج كالدين، يحبس به الموسر، وينظر به المعسر)','4837','1565','0');
INSERT INTO [t19188] VALUES (1573,'١٤٧٥ - مسألة: (ومن عجز عن عمارة أرضه، أجبر على إجارتها، أو رفع يده عنها)','4837','1565','0');
INSERT INTO [t19188] VALUES (1574,'١٤٧٦ - مسألة: (ويجوز لصاحب الأرض أن يرشو العامل ليدفع عنه الظلم فى خراجه)','4838','1565','0');
INSERT INTO [t19188] VALUES (1575,'١٤٧٧ - مسألة: (وإن رأى الإمام المصلحة فى إسقاط الخراج)','4838','1565','0');
INSERT INTO [t19188] VALUES (1576,'باب الفئ','4840','1473','0');
INSERT INTO [t19188] VALUES (1577,'١٤٧٨ - مسألة: (ولا يخمس. وقال الخرقى: يخمس؛ فيصرف خمسه إلى أهل الخمس، وباقيه للمصالح)','4844','1576','0');
INSERT INTO [t19188] VALUES (1578,'١٤٧٩ - مسألة: (وإن فضل منه فضل، قسم بين المسلمين. ويبدأ بالمهاجرين، ويقدم الأقرب فالأقرب من رسول الله، -صلى الله عليه وسلم-)','4847','1576','0');
INSERT INTO [t19188] VALUES (1579,'١٤٨٠ - مسألة: (ثم الأنصار، ثم سائر المسلمين. وهل يفاضل بينهم؟ على روايتين)','4848','1576','0');
INSERT INTO [t19188] VALUES (1580,'١٤٨١ - مسألة: (ومن مات بعد حلول وقت العطاء، دفع إلى ورثته حقه)','4852','1576','0');
INSERT INTO [t19188] VALUES (1581,'١٤٨٢ - مسألة: (ومن مات من أجناد المسلمين، دفع إلى امرأته وأولاده الصغار ما يكفيهم)','4853','1576','0');
INSERT INTO [t19188] VALUES (1582,'١٤٨٣ - مسألة: (فإذا بلغ ذكورهم، فاختاروا أن يكونوا فى','4853','1576','0');
INSERT INTO [t19188] VALUES (1583,'باب الأمان','4855','1473','0');
INSERT INTO [t19188] VALUES (1584,'١٤٨٤ - مسألة: (ويصح أمان الإمام لجميع الكفار)','4859','1583','0');
INSERT INTO [t19188] VALUES (1585,'١٤٨٥ - مسألة: (ومن قال لكافر: أنت آمن. أو: لا بأس عليك. أو: أجرتك. أو: قف. أو: ألق سلاحك. أو: مترس','4862','1583','0');
INSERT INTO [t19188] VALUES (1586,'١٤٨٦ - مسألة: (ومن جاء بمشرك، فادعى أنه أمنه، فأنكره، فالقول قوله. وعنه)','4866','1583','0');
INSERT INTO [t19188] VALUES (1587,'١٤٨٧ - مسألة: (ومن أعطى أمانا ليفتح حصنا، ففتحه، واشتبه علينا فيهم، حرم قتلهم واسترقاقهم)','4868','1583','0');
INSERT INTO [t19188] VALUES (1588,'١٤٨٨ - مسألة: (ويجوز عقد الأمان للرسول والمستأمن، ويقيمون مدة الهدنة بغير جزية. وعند أبو الخطاب: لا يقيمون سنة إلا بجزية)','4870','1583','0');
INSERT INTO [t19188] VALUES (1589,'١٤٨٩ - مسألة: (ومن دخل دار الإسلام بغير أمان، وادعى أنه رسول، أو تاجر ومعه متاع يبيعه، قبل منه)','4872','1583','0');
INSERT INTO [t19188] VALUES (1590,'١٤٩٠ - مسألة: (وإذا أودع المستأمن ماله مسلما، أو أقرضه إياه، ثم عاد إلى دار الحرب، بقى الأمان فى ماله، يبعث إليه إن طلبه)','4875','1583','0');
INSERT INTO [t19188] VALUES (1591,'١٤٩١ - مسألة: (وإذا أسر الكفار مسلما، فأطلقوه بشرط أن يقيم عندهم مدة، لزمه الوفاء لهم)','4879','1583','0');
INSERT INTO [t19188] VALUES (1592,'١٤٩٢ - مسألة: (فإن لم يشترطوا شيئا، أو شرطوا كونه رقيقا، فله أن يقتل، ويسرق، ويهرب)','4880','1583','0');
INSERT INTO [t19188] VALUES (1593,'١٤٩٣ - مسألة: (وإن أطلقوه بشرط أن يبعث إليهم مالا، وإن عجز عنه عاد إليهم، لزمه الوفاء لهم، إلا أن تكون امرأة، فلا ترجع إليهم. وقال الخرقى: لا يرجع الرجل أيضا)','4881','1583','0');
INSERT INTO [t19188] VALUES (1594,'باب الهدنة','4887','1473','0');
INSERT INTO [t19188] VALUES (1595,'١٤٩٤ - مسألة: (ولا يجوز عقد الهدنة إلا من الإمام أو نائبه)','4889','1594','0');
INSERT INTO [t19188] VALUES (1596,'١٤٩٥ - مسألة: (فمتى رأى المصلحة، جاز له عقدها مدة','4891','1594','0');
INSERT INTO [t19188] VALUES (1597,'١٤٩٦ - مسألة: (وإن هادنهم مطلقا، لم يصح)','4893','1594','0');
INSERT INTO [t19188] VALUES (1598,'١٤٩٧ - مسألة: (وإن شرط)','4894','1594','0');
INSERT INTO [t19188] VALUES (1599,'١٤٩٨ - مسألة: (وإن شرط رد من جاء من الرجال مسلما، جاز، ولا يمنعهم أخذه، ولا يجبره على ذلك، وله أن يأمره بقتالهم والفرار منهم)','4899','1594','0');
INSERT INTO [t19188] VALUES (1600,'١٤٩٩ - مسألة: (وعلى الإمام حماية من هادنه من المسلمين','4901','1594','0');
INSERT INTO [t19188] VALUES (1601,'١٥٠٠ - مسألة: (وإن خاف نقض العهد منهم، نبذ إليهم','4903','1594','0');
INSERT INTO [t19188] VALUES (1602,'باب عقد الذمة','4906','1473','0');
INSERT INTO [t19188] VALUES (1603,'١٥٠١ - مسألة: و (لا يجوز عقدها إلا لأهل الكتاب؛ وهم اليهود والنصارى، ومن يوافقهم في التدين بالتوراة والإنجيل؛ كالسامرة','4907','1602','0');
INSERT INTO [t19188] VALUES (1604,'١٥٠٢ - مسألة: (فأما الصابئ، فينظر فيه؛ فإن انتسب إلى أحد','4914','1602','0');
INSERT INTO [t19188] VALUES (1605,'١٥٠٣ - مسألة: (ومن تهود أو تنصر بعد بعث نبينا محمد -صلى الله عليه وسلم-، أو ولد بين أبوين لا تقبل الجزية من أحدهما، فعلى وجهين)','4916','1602','0');
INSERT INTO [t19188] VALUES (1606,'١٥٠٤ - مسألة: (ولا تؤخذ الجزية من نصارى بنى تغلب، وتؤخذ الزكاة من أموالهم، مثلى ما تؤخذ من أموال المسلمين)','4918','1602','0');
INSERT INTO [t19188] VALUES (1607,'١٥٠٥ - مسألة: (ويؤخذ ذلك من نسائهم وصبيانهم ومجانينهم)','4920','1602','0');
INSERT INTO [t19188] VALUES (1608,'١٥٠٦ - مسألة: (ومصرفه مصرف الجزية)','4922','1602','0');
INSERT INTO [t19188] VALUES (1609,'١٥٠٧ - مسألة: (ولا يؤخذ ذلك من كتابى غيرهم. وقال القاضى: تؤخذ من نصارى العرب ويهودهم)','4923','1602','0');
INSERT INTO [t19188] VALUES (1610,'١٥٠٨ - مسألة: (ولا جزية على صبى، ولا امرأة، ولا مجنون، ولا زمن، ولا أعمى، ولا عبد، ولا فقير يعجز عنها)','4926','1602','0');
INSERT INTO [t19188] VALUES (1611,'١٥٠٩ - مسألة: (ومن بلغ، أو أفاق، أو استغنى، فهو من أهلها بالعقد الأول، ويؤخذ منه فى آخر الحول بقدر ما أدرك)','4934','1602','0');
INSERT INTO [t19188] VALUES (1612,'١٥١٠ - مسألة: (ومن كان يجن ويفيق، لفقت إفاقته، فإذا بلغت حولا، أخذت منه. ويحتمل أن يؤخذ فى آخر كل حول بقدر إفاقته منه)','4936','1602','0');
INSERT INTO [t19188] VALUES (1613,'١٥١١ - مسألة: (وتقسم الجزية بينهم؛ فيجعل على الغنى ثمانية','4937','1602','0');
INSERT INTO [t19188] VALUES (1614,'١٥١٢ - مسألة: (والغنى منهم من عده الناس غنيا، فى ظاهر المذهب)','4941','1602','0');
INSERT INTO [t19188] VALUES (1615,'١٥١٣ - مسألة: (وإذا بذلوا الواجب عليهم، لزم قبوله، وحرم قتالهم)','4941','1602','0');
INSERT INTO [t19188] VALUES (1616,'١٥١٤ - مسألة: (ومن أسلم بعد الحول، سقطت عنه الجزية،','4943','1602','0');
INSERT INTO [t19188] VALUES (1617,'١٥١٥ - مسألة: (وإن اجتمعت عليه جزية سنين، استوفيت كلها)','4946','1602','0');
INSERT INTO [t19188] VALUES (1618,'١٥١٦ - مسألة: (وتؤخذ الجزية)','4946','1602','0');
INSERT INTO [t19188] VALUES (1619,'١٥١٧ - مسألة: (ويجوز أن يشترط عليهم ضيافة من يمر بهم من المسلمين، ويبين أيام الضيافة، وقدر الطعام والإدام والعلف، وعدد من يضاف. ولا يجب)','4949','1602','0');
INSERT INTO [t19188] VALUES (1620,'١٥١٨ - مسألة: (وإذا تولى إمام، فعرف قدر جزيتهم، وما شرط عليهم، أقرهم عليه، فإن لم يعرف، رجع إلى قولهم، فإن بان له كذبهم، رجع عليهم. وعند أبى الخطاب أنه يستأنف العقد معهم)','4954','1602','0');
INSERT INTO [t19188] VALUES (1621,'١٥١٩ - مسألة: (وإذا عقد الذمة)','4956','1602','0');
INSERT INTO [t19188] VALUES (1622,'باب أحكام الذمة','4957','1473','0');
INSERT INTO [t19188] VALUES (1623,'١٥٢٠ - مسألة: (ويلزمهم التميز عن المسلمين؛ فى شعورهم بحذف مقادم رءوسهم وترك الفرق، وكناهم فلا يتكنون بكنى المسلمين؛ كأبى القاسم، وأبى عبد الله، وركوبهم بترك الركوب على السروج، وركوبهم عرضا على الأكف، ولباسهم فيلبسون ثيابا تخالف ثيابهم، كالعسلى والأدكن، وشد الخرق فى قلانسهم وعمائمهم، وتؤمر النصارى بشد الزنار','4960','1622','0');
INSERT INTO [t19188] VALUES (1624,'١٥٢١ - مسألة: (ولا يجوز تصديرهم فى المجالس، ولا بداءتهم بالسلام، فإن سلم أحدهم، قيل له: و','4964','1622','0');
INSERT INTO [t19188] VALUES (1625,'١٥٢٢ - مسألة: (وفى تهنئتهم وتعزيتهم وعيادتهم روايتان)','4968','1622','0');
INSERT INTO [t19188] VALUES (1626,'١٥٢٣ - مسألة: (ويمنعون)','4969','1622','0');
INSERT INTO [t19188] VALUES (1627,'١٥٢٤ - مسألة: (وإن ملكوا دارا عالية من مسلم، لم يجب نقضها)','4971','1622','0');
INSERT INTO [t19188] VALUES (1628,'١٥٢٥ - مسألة: (ويمنعون من إحداث الكنائس والبيع، ولا','4971','1622','0');
INSERT INTO [t19188] VALUES (1629,'١٥٢٦ - مسألة: (ويمنعون)','4976','1622','0');
INSERT INTO [t19188] VALUES (1630,'١٥٢٧ - مسألة: (وإن صولحوا فى بلادهم على إعطاء الجزية، لم يمنعوا شيئا من ذلك)','4977','1622','0');
INSERT INTO [t19188] VALUES (1631,'١٥٢٨ - مسألة: (ويمنعون)','4978','1622','0');
INSERT INTO [t19188] VALUES (1632,'١٥٢٩ - مسألة: (فإن قدم رسول لا بد له من لقاء الإمام، خرج إليه، ولم يأذن له، فإن دخل، عزر وهدد)','4979','1622','0');
INSERT INTO [t19188] VALUES (1633,'١٥٣٠ - مسألة: (ويمنعون من الإقامة بالحجاز؛ كالمدينة واليمامة وخيبر)','4980','1622','0');
INSERT INTO [t19188] VALUES (1634,'١٥٣١ - مسألة: (فإن دخلوا لتجارة، لم يقيموا فى موضع واحد أكثر من أربعة أيام)','4982','1622','0');
INSERT INTO [t19188] VALUES (1635,'١٥٣٢ - مسألة: (فإن مرض، لم يخرج حتى يبرأ، وإن مات، دفن به)','4984','1622','0');
INSERT INTO [t19188] VALUES (1636,'١٥٣٣ - مسألة: (ولا يمنعون من تيماء وفيد','4985','1622','0');
INSERT INTO [t19188] VALUES (1637,'١٥٣٤ - مسألة: (وهل لهم دخول المساجد بإذن مسلم؟ على روايتين)','4985','1622','0');
INSERT INTO [t19188] VALUES (1638,'فصل','4988','1622','0');
INSERT INTO [t19188] VALUES (1639,'١٥٣٥ - مسألة: (وإن اتجر حربى إلينا، أخذ منه العشر، ولا','4992','1622','0');
INSERT INTO [t19188] VALUES (1640,'١٥٣٦ - مسألة: (ويؤخذ)','4999','1622','0');
INSERT INTO [t19188] VALUES (1641,'١٥٣٧ - مسألة: (وعلى الإمام حفظهم، والمنع من أذاهم، واستنقاذ من أسر منهم)','5001','1622','0');
INSERT INTO [t19188] VALUES (1642,'١٥٣٨ - مسألة: (وإن تحاكموا إلى الحاكم مع مسلم، لزمه الحكم بينهم)','5003','1622','0');
INSERT INTO [t19188] VALUES (1643,'١٥٣٩ - مسألة: (وإن تبايعوا بيوعا فاسدة، وتقابضوا، لم ينقض فعلهم)','5005','1622','0');
INSERT INTO [t19188] VALUES (1644,'١٥٤٠ - مسألة: (وإن تهود نصرانى، أو تنصر يهودى، لم يقر، ولم يقبل منه إلا الإسلام، أو الدين الذى كان عليه، ويحتمل أن لا يقبل منه إلا الإسلام، فإن أبى، هدد، ويحبس، ويحتمل أن يقتل، وعنه)','5008','1622','0');
INSERT INTO [t19188] VALUES (1645,'١٥٤١ - مسألة: (وإن انتقل إلى غير دين أهل الكتاب، أو انتقل المجوسى إلى غير دين أهل الكتاب، لم يقر، وأمر أن يسلم، فإن أبى، قتل)','5010','1622','0');
INSERT INTO [t19188] VALUES (1646,'١٥٤٢ - مسألة: (وإن انتقل غير الكتابى إلى دين أهل الكتاب، أقر، ويحتمل أن لا يقبل منه إلا الإسلام)','5012','1622','0');
INSERT INTO [t19188] VALUES (1647,'١٥٤٣ - مسألة: (وإن تمجس الوثنى، فهل يقر؟ على روايتين)','5013','1622','0');
INSERT INTO [t19188] VALUES (1648,'فصل فى نقض العهد','5014','1622','0');
INSERT INTO [t19188] VALUES (1649,'١٥٤٤ - مسألة: (وإن تعدى على مسلم؛ بقتل، أو قذف،','5015','1622','0');
INSERT INTO [t19188] VALUES (1650,'١٥٤٥ - مسألة: (وإن أظهر منكرا، أو رفع صوته بكتابه، لم','5018','1622','0');
INSERT INTO [t19188] VALUES (1651,'١٥٤٦ - مسألة: (ولا ينتقض عهد نسائه وأولاده بنقض عهده،','5020','1622','0');
INSERT INTO [t19188] VALUES (1652,'١٥٤٧ - مسألة: (وماله فئ عند الخرقى)','5023','1622','0');
INSERT INTO [t19188] VALUES (1653,'كتاب البيع','5027','0','0');
INSERT INTO [t19188] VALUES (1654,'[باب. . .]','5027','1653','0');
INSERT INTO [t19188] VALUES (1655,'١٥٤٨ - مسألة: (وله صورتان؛ إحداهما، الإيجاب والقبول)','5030','1654','0');
INSERT INTO [t19188] VALUES (1656,'١٥٤٩ - مسألة: (وإن تراخى القبول عن الإيجاب، صح ما داما','5033','1654','0');
INSERT INTO [t19188] VALUES (1657,'١٥٥٠ - مسألة: (الثانية، المعاطاة)','5034','1654','0');
INSERT INTO [t19188] VALUES (1658,'فصل','5038','1654','0');
INSERT INTO [t19188] VALUES (1659,'فصل','5040','1654','0');
INSERT INTO [t19188] VALUES (1660,'فصل','5044','1654','0');
INSERT INTO [t19188] VALUES (1661,'١٥٥١ - مسألة: (ويجوز بيع الهر، والفيل،','5049','1654','0');
INSERT INTO [t19188] VALUES (1662,'١٥٥٢ - مسألة: (ويجوز بيع العبد المرتد والمريض. وفى بيع الجانى والقاتل فى المحاربة، ولبن الآدميات وجهان)','5055','1654','0');
INSERT INTO [t19188] VALUES (1663,'١٥٥٣ - مسألة: (وفى جواز بيع المصحف وكراهة شرائه وإبداله، روايتان)','5060','1654','0');
INSERT INTO [t19188] VALUES (1664,'١٥٥٤ - مسألة: (ولا يجوز بيع الحشرات، ولا الميتة، ولا شئ منها، ولا سباع البهائم التى لا تصلح للصيد)','5063','1654','0');
INSERT INTO [t19188] VALUES (1665,'١٥٥٥ - مسألة: (ولا)','5064','1654','0');
INSERT INTO [t19188] VALUES (1666,'١٥٥٦ - مسألة: (ولا)','5069','1654','0');
INSERT INTO [t19188] VALUES (1667,'١٥٥٧ - مسألة: (ولا)','5070','1654','0');
INSERT INTO [t19188] VALUES (1668,'١٥٥٨ - مسألة: (وفى جواز الاستصباح بها روايتان. ويخرج على ذلك جواز بيعها)','5073','1654','0');
INSERT INTO [t19188] VALUES (1669,'فصل','5076','1654','0');
INSERT INTO [t19188] VALUES (1670,'١٥٥٩ - مسألة: (وإن اشترى له فى ذمته بغير إذنه، صح. فإن أجازه من اشترى له، ملكه، وإلا لزم من اشتراه)','5078','1654','0');
INSERT INTO [t19188] VALUES (1671,'١٥٦٠ - مسألة: (ولا يجوز بيع ما لا يملكه ليمضى ويشتريه ويسلمه)','5081','1654','0');
INSERT INTO [t19188] VALUES (1672,'١٥٦١ - مسألة: (ولا يجوز بيع ما فتح عنوة، ولم يقسم، كأرض الشام، والعراق، ومصر، ونحوها، إلا المساكن، وأرضا من العراق فتحت صلحا؛ وهى الحيرة وأليس','5082','1654','0');
INSERT INTO [t19188] VALUES (1673,'١٥٦٢ - مسألة: (وتجوز إجارتها)','5092','1654','0');
INSERT INTO [t19188] VALUES (1674,'١٥٦٣ - مسألة: (ولا يجوز بيع رباع مكة، ولا إجارتها. وعنه، يجوز ذلك)','5093','1654','0');
INSERT INTO [t19188] VALUES (1675,'١٥٦٤ - مسألة: (ولا يجوز بيع كل ماء عد','5098','1654','0');
INSERT INTO [t19188] VALUES (1676,'١٥٦٥ - مسألة: (إلا أنه لا يجوز له الدخول إلى ملك غيره بغير إذنه)','5101','1654','0');
INSERT INTO [t19188] VALUES (1677,'فصل','5110','1654','0');
INSERT INTO [t19188] VALUES (1678,'فصل','5115','1654','0');
INSERT INTO [t19188] VALUES (1679,'١٥٦٦ - مسألة: (وإن ذكر له من صفته ما يكفى فى السلم،','5120','1654','0');
INSERT INTO [t19188] VALUES (1680,'١٥٦٧ - مسألة: (ولا يجوز بيع الحمل فى البطن، واللبن فى الضرع، والمسك فى الفأر، والنوى فى التمر)','5127','1654','0');
INSERT INTO [t19188] VALUES (1681,'١٥٦٨ - مسألة: (ولا يجوز بيع الملامسة؛ وهو أن يقول: بعتك ثوبى هذا، على','5132','1654','0');
INSERT INTO [t19188] VALUES (1682,'١٥٦٩ - مسألة: (ولا يجوز أن يبيع عبدا غير معين، ولا عبدا','5134','1654','0');
INSERT INTO [t19188] VALUES (1683,'١٥٧٠ - مسألة: (وإن باع قفيزا','5137','1654','0');
INSERT INTO [t19188] VALUES (1684,'١٥٧١ - مسألة: (وإن باعه الصبرة إلا قفيزا، أو ثمرة الشجرة إلا صاعا، لم يصح. وعنه، يصح)','5139','1654','0');
INSERT INTO [t19188] VALUES (1685,'١٥٧٢ - مسألة: (وإن باعه أرضا إلا جريبا','5143','1654','0');
INSERT INTO [t19188] VALUES (1686,'١٥٧٣ - مسألة: (وإن باعه حيوانا مأكولا إلا رأسه، أو جلده، أو أطرافه، صح. وإن استثنى حمله، أو شحمه، لم يصح)','5145','1654','0');
INSERT INTO [t19188] VALUES (1687,'١٥٧٤ - مسألة: (ويجوز بيع ما مأكوله فى جوفه، وبيع الباقلا والجوز واللوز فى قشرته، والحب المشتد فى سنبله)','5151','1654','0');
INSERT INTO [t19188] VALUES (1688,'فصل','5152','1654','0');
INSERT INTO [t19188] VALUES (1689,'١٥٧٥ - مسألة: (وإن قال: بعتك بعشرة صحاح، أو إحدى عشرة','5155','1654','0');
INSERT INTO [t19188] VALUES (1690,'١٥٧٦ - مسألة: (وإن باعه الصبرة كل قفيز بدرهم، والثوب كل ذراع بدرهم، والقطيع كل شاة بدرهم، صح)','5157','1654','0');
INSERT INTO [t19188] VALUES (1691,'١٥٧٧ - مسألة: (وإن باعه من الصبرة، كل قفيز بدرهم، لم يصح)','5161','1654','0');
INSERT INTO [t19188] VALUES (1692,'١٥٧٨ - مسألة: (وإن باعه بمائة درهم إلا دينارا، لم يصح. ذكره القاضى. [ويجئ على قول الخرقى أنه يصح)','5171','1654','0');
INSERT INTO [t19188] VALUES (1693,'فصل فى تفريق الصفقة','5172','1654','0');
INSERT INTO [t19188] VALUES (1694,'١٥٧٩ - مسألة: (وإن باع عبده وعبد غيره بإذنه بثمن واحد، فهل يصح؟ على وجهين)','5179','1654','0');
INSERT INTO [t19188] VALUES (1695,'١٥٨٠ - مسألة: (وإن جمع بين بيع وإجارة، أو بيع وصرف، صح فيهما، ويقسط العوض عليهما، فى أحد الوجهين)','5181','1654','0');
INSERT INTO [t19188] VALUES (1696,'١٥٨١ - مسألة: (وإن جمع بين كتابة وبيع، فكاتب عبده وباعه شيئا صفقة واحدة)','5183','1654','0');
INSERT INTO [t19188] VALUES (1697,'فصل','5185','1654','0');
INSERT INTO [t19188] VALUES (1698,'١٥٨٢ - مسألة: (ويصح النكاح وسائر العقود، فى أصح الوجهين)','5188','1654','0');
INSERT INTO [t19188] VALUES (1699,'١٥٨٣ - مسألة: (ولا يصح بيع العصير لمن يتخذه خمرا، ولا بيع السلاح فى الفتنة، ولا لأهل الحرب. ويحتمل أن يصح مع التحريم)','5189','1654','0');
INSERT INTO [t19188] VALUES (1700,'١٥٨٤ - مسألة: (ولا يصح بيع العبد المسلم لكافر، إلا أن يكون ممن يعتق عليه، فيصح فى إحدى الروايتين)','5194','1654','0');
INSERT INTO [t19188] VALUES (1701,'١٥٨٥ - مسألة: (وإن أسلم عبد الذمى، أجبر على إزالة ملكه عنه)','5196','1654','0');
INSERT INTO [t19188] VALUES (1702,'١٥٨٦ - مسألة: (ولا يجوز بيع الرجل على بيع أخيه؛ وهو أن يقول لمن اشترى سلعة بعشرة: أنا أعطيك مثلها بتسعة. ولا شراؤه على شراء أخيه؛ وهو أن يقول لمن باع سلعة بتسعة: عندى فيها عشرة.','5198','1654','0');
INSERT INTO [t19188] VALUES (1703,'١٥٨٧ - مسألة: (وفى بيع الحاضر للبادى روايتان، إحداهما، يصح. والأخرى، لا يصح بخمسة شروط، أن يحضر البادى لبيع','5204','1654','0');
INSERT INTO [t19188] VALUES (1704,'١٥٨٨ - مسألة: (فأما شراؤه له، فيصح، رواية واحدة)','5209','1654','0');
INSERT INTO [t19188] VALUES (1705,'١٥٨٩ - مسألة: (ومن باع سلعة بنسيئة، لم يجز أن يشتريها بأقل مما باعها نقدا','5212','1654','0');
INSERT INTO [t19188] VALUES (1706,'١٥٩٠ - مسألة: (وإن باع ما يجرى فيه الربا نسيئة','5217','1654','0');
INSERT INTO [t19188] VALUES (1707,'باب الشروط فى البيع','5225','1653','0');
INSERT INTO [t19188] VALUES (1708,'١٥٩١ - مسألة: (فإن شرطها ثيبا كافرة، فبانت بكرا مسلمة، فلا فسخ له)','5227','1707','0');
INSERT INTO [t19188] VALUES (1709,'١٥٩٢ - مسألة: (وإن شرط الطائر مصوتا، أو أنه يجئ من مسافة معلومة، صح. وقال القاضى: لا يصح)','5231','1707','0');
INSERT INTO [t19188] VALUES (1710,'١٥٩٣ - مسألة: (وذكر الخرقى فى جز الرطبة، إن شرطه على البائع، لم يصح. فيخرج ههنا مثله)','5243','1707','0');
INSERT INTO [t19188] VALUES (1711,'١٥٩٤ - مسألة: (وإن جمع بين شرطين، لم يصح)','5246','1707','0');
INSERT INTO [t19188] VALUES (1712,'فصل','5250','1707','0');
INSERT INTO [t19188] VALUES (1713,'١٥٩٥ - مسألة: (إلا إذا شرط العتق، ففى صحته روايتان؛ إحداهما، يصح)','5257','1707','0');
INSERT INTO [t19188] VALUES (1714,'١٥٩٦ - مسألة: (وعنه فى من باع جارية، وشرط على المشترى، أنه إن باعها، فهو أحق بها بالثمن، أن البيع جائز)','5259','1707','0');
INSERT INTO [t19188] VALUES (1715,'١٥٩٧ - مسألة: (وإن شرط رهنا فاسدا)','5262','1707','0');
INSERT INTO [t19188] VALUES (1716,'١٥٩٨ - مسألة: وكذلك إذا قال','5269','1707','0');
INSERT INTO [t19188] VALUES (1717,'١٥٩٩ - مسألة: (وإن قال: بعتك على أن تنقدنى الثمن إلى ثلاث)','5274','1707','0');
INSERT INTO [t19188] VALUES (1718,'١٦٠٠ - مسألة: (وإن باعه، وشرط البراءة من كل عيب، لم يبرأ. وعنه، يبرأ، إلا أن يكون البائع علم العيب، فكتمه)','5275','1707','0');
INSERT INTO [t19188] VALUES (1719,'فصل','5278','1707','0');
INSERT INTO [t19188] VALUES (1720,'باب الخيار فى البيع','5283','1653','0');
INSERT INTO [t19188] VALUES (1721,'١٦٠١ - مسألة: (ولكل واحد من المتبايعين الخيار ما لم يتفرقا بأبدانهما)','5296','1720','0');
INSERT INTO [t19188] VALUES (1722,'١٦٠٢ - مسألة: (إلا أن يتبايعا على أن لا خيار بينهما، أو يسقطا الخيار بعده، فيسقط فى إحدى الروايتين. وإن أسقطه أحدهما، بقى خيار صاحبه)','5301','1720','0');
INSERT INTO [t19188] VALUES (1723,'فصل','5304','1720','0');
INSERT INTO [t19188] VALUES (1724,'١٦٠٣ - مسألة: (ولا يجوز مجهولا، فى ظاهر المذهب. وعنه، يجوز، وهما على خيارهما، ما لم يقطعاه أو تنتهى مدته)','5306','1720','0');
INSERT INTO [t19188] VALUES (1725,'١٦٠٤ - مسألة: (ولا يثبت إلا فى البيع، والصلح بمعناه، والإجارة فى الذمة، أو على مدة لا تلى العقد)','5309','1720','0');
INSERT INTO [t19188] VALUES (1726,'١٦٠٥ - مسألة: (وإن شرطاه إلى الغد، لم يدخل فى المدة)','5311','1720','0');
INSERT INTO [t19188] VALUES (1727,'١٦٠٦ - مسألة: (وإن شرطاه مدة، فابتداؤها من حين العقد.','5312','1720','0');
INSERT INTO [t19188] VALUES (1728,'١٦٠٧ - مسألة: (وإن شرط الخيار لغيره، جاز، وكان توكيلا له فيه)','5314','1720','0');
INSERT INTO [t19188] VALUES (1729,'١٦٠٨ - مسألة: (وإن شرطا الخيار لأحدهما دون صاحبه، جاز','5317','1720','0');
INSERT INTO [t19188] VALUES (1730,'١٦٠٩ - مسألة: (ولمن له الخيار الفسخ من غير حضور صاحبه، ولا رضاه)','5318','1720','0');
INSERT INTO [t19188] VALUES (1731,'١٦١٠ - مسألة: (وإن مضت المدة، ولم يفسخا، بطل خيارهما)','5318','1720','0');
INSERT INTO [t19188] VALUES (1732,'١٦١١ - مسألة: (وينتقل الملك إلى المشترى بنفس العقد، في أظهر الروايتين)','5322','1720','0');
INSERT INTO [t19188] VALUES (1733,'١٦١٢ - مسألة: (فما حصل من كسب، أو نماء منفصل، فهو له، أمضيا العقد، أو فسخاه)','5328','1720','0');
INSERT INTO [t19188] VALUES (1734,'١٦١٣ - مسألة: (وليس لواحد منهما التصرف في المبيع في مدة الخيار، إلا بما يحصل به تجربة المبيع)','5330','1720','0');
INSERT INTO [t19188] VALUES (1735,'١٦١٤ - مسألة: (فإن تصرفا فيه ببيع، أو هبة، أو نحوهما،','5330','1720','0');
INSERT INTO [t19188] VALUES (1736,'١٦١٥ - مسألة: (ويكون تصرف البائع فسخا للبيع، وتصرف المشتري إسقاطا لخياره، في أحد الوجهين. وفي الآخر،','5336','1720','0');
INSERT INTO [t19188] VALUES (1737,'١٦١٦ - مسألة: (وإن أعتقه المشتري، نفذ عتقه، وبطل خيارهما. وكذلك إن تلف المبيع. وعنه، لا يبطل خيار البائع، وله الفسخ والرجوع بالقيمة)','5342','1720','0');
INSERT INTO [t19188] VALUES (1738,'١٦١٧ - مسألة: (وحكم الوقف حكم البيع، في أحد الوجهين)','5348','1720','0');
INSERT INTO [t19188] VALUES (1739,'١٦١٨ - مسألة: (وإن وطئ المشتري الجارية فأحبلها، صارت أم ولد له، وولده حر ثابت النسب)','5349','1720','0');
INSERT INTO [t19188] VALUES (1740,'١٦١٩ - مسألة: (وإن وطئها البائع، وقلنا: البيع ينفسخ بوطئه. فكذلك. وإن قلنا: لا ينفسخ. فعليه المهر، وولده رقيق، إلا إذا قلنا: الملك له. ولا حد فيه على كل حال. وقال أصحابنا: عليه الحد إذا علم','5350','1720','0');
INSERT INTO [t19188] VALUES (1741,'١٦٢٠ - مسألة: (ومن مات منهما بطل خياره، ولم يورث)','5353','1720','0');
INSERT INTO [t19188] VALUES (1742,'١٦٢١ - مسألة: (الثانية، النجش؛ وهو أن يزيد في السلعة من لا','5359','1720','0');
INSERT INTO [t19188] VALUES (1743,'١٦٢٢ - مسألة: (الثالثة، المسترسل، إذا غبن الغبن المذكور)','5362','1720','0');
INSERT INTO [t19188] VALUES (1744,'١٦٢٣ - مسألة: (ويرد مع المصراة عوض اللبن صاعا من تمر.','5371','1720','0');
INSERT INTO [t19188] VALUES (1745,'١٦٢٤ - مسألة: (فإن كان اللبن بحاله لم يتغير، رده، واجزأه. ويحتمل أن لا يجزئه إلا التمر)','5375','1720','0');
INSERT INTO [t19188] VALUES (1746,'١٦٢٥ - مسألة: (ومتى علم التصرية، فله الرد. وقال القاضي: ليس له ردها إلا بعد ثلاث)','5378','1720','0');
INSERT INTO [t19188] VALUES (1747,'١٦٢٦ - مسألة: (وإن صار لبنها عادة، لم يكن له الرد في قياس قوله: إذا اشترى أمة مزوجة، فطلقها الزوج، لم يملك الرد)','5381','1720','0');
INSERT INTO [t19188] VALUES (1748,'١٦٢٧ - مسألة: (وإن كانت التصرية في غير بهيمة الأنعام)','5382','1720','0');
INSERT INTO [t19188] VALUES (1749,'١٦٢٨ - مسألة: (ولا يحل للبائع تدليس سلعته، ولا كتمان عيبها)','5384','1720','0');
INSERT INTO [t19188] VALUES (1750,'١٦٢٩ - مسألة: (فمن اشترى معيبا لم يعلم عيبه، فله الخيار بين الرد والإمساك مع الأرش، وهو قسط ما بين قيمة الصحيح والمعيب من الثمن)','5395','1720','0');
INSERT INTO [t19188] VALUES (1751,'١٦٣٠ - مسألة: (وما كسب فهو للمشتري، وكذلك نماؤه','5399','1720','0');
INSERT INTO [t19188] VALUES (1752,'١٦٣١ - مسألة: (ووطء الثيب لا يمنع الرد. وعنه، يمنع)','5404','1720','0');
INSERT INTO [t19188] VALUES (1753,'١٦٣٢ - مسألة: (وإن وطئ البكر، أو تعيبت عنده، فله الأرش. وعنه، أنه مخير بين الأرش وبين رده وأرش العيب الحادث عنده، ويأخذ الثمن)','5406','1720','0');
INSERT INTO [t19188] VALUES (1754,'١٦٣٣ - مسألة: (قال الخرقي: إلا أن يكون البائع دلس العيب، فيلزمه رد الثمن كاملا. قال القاضي: ولو تلف المبيع عنده، ثم علم أن البائع دلس العيب، رجع بالثمن كله. نص عليه في رواية حنبل)','5412','1720','0');
INSERT INTO [t19188] VALUES (1755,'١٦٣٤ - مسألة: (وإن أعتق العبد، أو تلف المبيع، رجع بأرشه. وكذلك إن باعه غير عالم بعيبه. [نص عليه]','5415','1720','0');
INSERT INTO [t19188] VALUES (1756,'١٦٣٥ - مسألة: (وإن باع بعضه فله أرش الباقي. وفي أرش المبيع الروايتان. وقال الخرقي: له رد ملكه منه بقسطه من الثمن، أو','5423','1720','0');
INSERT INTO [t19188] VALUES (1757,'١٦٣٦ - مسألة: (وإن صبغه، أو نسجه، فله الأرش)','5426','1720','0');
INSERT INTO [t19188] VALUES (1758,'١٦٣٧ - مسألة: (وإن اشترى ما مأكوله في جوفه، فكسره،','5428','1720','0');
INSERT INTO [t19188] VALUES (1759,'١٦٣٨ - مسألة: (ومن علم العيب، وأخر الرد، لم يبطل خياره، إلا أن يوجد منه ما يدل على الرضا؛ من التصرف ونحوه)','5433','1720','0');
INSERT INTO [t19188] VALUES (1760,'١٦٣٩ - مسألة: (ولا يفتقر الرد إلى رضا. ولا قضاء، ولا حضور صاحبه)','5435','1720','0');
INSERT INTO [t19188] VALUES (1761,'١٦٤٠ - مسألة: (وإن اشترى اثنان شيئا، وشرطا الخيار، أو','5435','1720','0');
INSERT INTO [t19188] VALUES (1762,'١٦٤١ - مسألة: (وإن اشترى واحد معيبين صفقة واحدة، فليس له إلا ردهما أو إمساكهما)','5439','1720','0');
INSERT INTO [t19188] VALUES (1763,'١٦٤٢ - مسألة: (وإن كان أحدهما معيبا، فله رده بقسطه)','5441','1720','0');
INSERT INTO [t19188] VALUES (1764,'١٦٤٣ - مسألة: (فإن كان المبيع مما ينقصه التفريق؛ كمصراعي باب، أو زوجى خف، أو من لا يجوز التفريق بينهما؛ كجارية وولدها، فليس له رد أحدهما)','5442','1720','0');
INSERT INTO [t19188] VALUES (1765,'١٦٤٤ - مسألة: (وإن اختلفا في العيب: هل كان عند البائع، أو حدث عند المشتري، ففي أيهما يقبل قولة؟ روايتان. إلا أن لا يحتمل إلا قول أحدهما، فالقول قوله بغير يمين)','5443','1720','0');
INSERT INTO [t19188] VALUES (1766,'١٦٤٥ - مسألة: (ومن باع عبدا تلزمه عقوبة من قصاص أو','5450','1720','0');
INSERT INTO [t19188] VALUES (1767,'١٦٤٦ - مسألة: (فإن لم يعلم حتى قتل، فله الأرش)','5451','1720','0');
INSERT INTO [t19188] VALUES (1768,'١٦٤٧ - مسألة: (وإن كانت الجناية موجبة للمال، والسيد معسر، قدم حق المجني عليه. وللمشتري الخيار)','5452','1720','0');
INSERT INTO [t19188] VALUES (1769,'١٦٤٨ - مسألة: (ومعنى التولية؛ البيع برأس المال، فيقول: وليتكه. أو: بعتكه برأس ماله. أو: بما اشتريته. أو: برقمه)','5454','1720','0');
INSERT INTO [t19188] VALUES (1770,'١٦٤٩ - مسألة: (والشركة؛ بيع بعضه بقسطه من الثمن. ويصح بقوله: شركتك في نصفه. أو: ثلثه)','5454','1720','0');
INSERT INTO [t19188] VALUES (1771,'١٦٥٠ - مسألة: (والمرابحة؛ أن يبيعه بربح، فيقول: رأس','5459','1720','0');
INSERT INTO [t19188] VALUES (1772,'١٦٥١ - مسألة: (والمواضعة؛ أن يقول: بعتكه بها، ووضيعة درهم من كل عشرة. فيلزم المشتري تسعون درهما)','5461','1720','0');
INSERT INTO [t19188] VALUES (1773,'١٦٥٢ - مسألة: (ومتى اشتراه بثمن مؤجل، أو ممن لا تقبل شهادته له، أو بأكثر من ثمنه حيلة، أو باع بعض الصفقة بقسطها من الثمن، فلم يبين ذلك للمشتري في تخبيره','5466','1720','0');
INSERT INTO [t19188] VALUES (1774,'١٦٥٣ - مسألة: (وما يزاد في الثمن أو يحط منه في مدة الخيار، أو يؤخذ أرشا للعيب أو جناية عليه، يلحق برأس المال، ويخبر به)','5471','1720','0');
INSERT INTO [t19188] VALUES (1775,'١٦٥٤ - مسألة: (وإن جنى، ففداه المشتري، أو زيد في','5474','1720','0');
INSERT INTO [t19188] VALUES (1776,'١٦٥٥ - مسألة: (وإن اشترى ثوبا بعشرة، وقصره بعشرة، أخبر بذلك على وجهه. فإن قال: تحصل علي بعشرين. فهل يجوز','5475','1720','0');
INSERT INTO [t19188] VALUES (1777,'١٦٥٦ - مسألة: (وإن اشتراه بعشرة، ثم باعه بخمسة عشر،','5477','1720','0');
INSERT INTO [t19188] VALUES (1778,'١٦٥٧ - مسألة: (فإن نكل أحدهما، لزمه ما قال صاحبه)','5486','1720','0');
INSERT INTO [t19188] VALUES (1779,'١٦٥٨ - مسألة: (فإن تحالفا','5486','1720','0');
INSERT INTO [t19188] VALUES (1780,'١٦٥٩ - مسألة: (وإن كانت السلعة تالفة، رجعا إلى قيمة مثلها. فإن اختلفا فيصفتها، فالقول قول المشتري [مع يمينه]','5488','1720','0');
INSERT INTO [t19188] VALUES (1781,'١٦٦٠ - مسألة: (وإن ماتا، فورثتهما بمنزلتهما)','5493','1720','0');
INSERT INTO [t19188] VALUES (1782,'١٦٦١ - مسألة: (ومتى فسخ المظلوم منهما، انفسخ العقد','5493','1720','0');
INSERT INTO [t19188] VALUES (1783,'١٦٦٢ - مسألة: (وإن اختلفا في صفة الثمن، تحالفا، إلا أن يكون للبلد نقد معلوم، فيرجع إليه)','5497','1720','0');
INSERT INTO [t19188] VALUES (1784,'١٦٦٣ - مسألة: (وإن اختلفا في أجل أو شرط، فالقول قول','5499','1720','0');
INSERT INTO [t19188] VALUES (1785,'١٦٦٤ - مسألة: (وإن قال: بعتني هذين. قال: بل أحدهما. فالقول قول البائع)','5502','1720','0');
INSERT INTO [t19188] VALUES (1786,'١٦٦٥ - مسألة: (وإن قال: بعتني هذا. قال: بل هذا. حلف','5503','1720','0');
INSERT INTO [t19188] VALUES (1787,'١٦٦٦ - مسألة: (وإن قال البائع: لا أسلم المبيع حتى أقبض ثمنه. وقال المشتري: لا أسلمه حتى أقبض المبيع)','5505','1720','0');
INSERT INTO [t19188] VALUES (1788,'١٦٦٧ - مسألة: (ويثبت الخيار للخلف في الصفة، وتغير ما تقدمت رؤيته. وقد ذكرناه)','5512','1720','0');
INSERT INTO [t19188] VALUES (1789,'١٦٦٨ - مسألة: (وعنه، في الصبرة المتعينة، أنه يجوز بيعها قبل قبضها، وإن تلفت فهي من ضمان المشتري)','5524','1720','0');
INSERT INTO [t19188] VALUES (1790,'١٦٦٩ - مسألة: (وما عدا المكيل والموزون يجوز التصرف فيه قبل قبضه، وإن تلف، فهو من مال المشتري. وحكى أبو الخطاب رواية أخرى، أنه كالمكيل والموزون في ذلك)','5524','1720','0');
INSERT INTO [t19188] VALUES (1791,'١٦٧٠ - مسألة: (ويحصل القبض فيما بيع بالكيل والوزن،','5532','1720','0');
INSERT INTO [t19188] VALUES (1792,'١٦٧١ - مسألة: (وفي الصبرة وما ينقل، بالنقل)','5534','1720','0');
INSERT INTO [t19188] VALUES (1793,'١٦٧٢ - مسألة: (وفيما يتناول، بالتناول، وفيما عدا ذلك، بالتخلية. وعنه، أن قبض جميع الأشياء بالتخلية مع التمييز)','5535','1720','0');
INSERT INTO [t19188] VALUES (1794,'١٦٧٣ - مسألة: (والإقالة فسخ، تجوز في المبيع قبل قبضه، ولا يستحق بها شفعة، ولا تجوز إلا بمثل الثمن. وعنه، أنها بيع، فلا يثبت فيها ذلك إلا بمثل الثمن، في أحد الوجهين)','5540','1720','0');
INSERT INTO [t19188] VALUES (1795,'باب الربا والصرف','5550','1653','0');
INSERT INTO [t19188] VALUES (1796,'١٦٧٤ - مسألة: (وهو نوعان؛ ربا الفضل، وربا النسيئة)','5551','1795','0');
INSERT INTO [t19188] VALUES (1797,'١٦٧٥ - مسألة: (فأما ربا الفضل، فيحرم في الجنس الواحد من كل مكيل أو موزون وإن كان يسيرا كتمرة بتمرتين وحبة بحبتين. وعنه، لا يحرم إلا في الجنس الواحد من الذهب والفضة وكل مطعوم.','5553','1795','0');
INSERT INTO [t19188] VALUES (1798,'١٦٧٦ - مسألة: (ولا يباع ما أصله الكيل بشيء من جنسه وزنا، ولا ما أصله الوزن كيلا)','5567','1795','0');
INSERT INTO [t19188] VALUES (1799,'١٦٧٧ - مسألة؛ قال: (فإن اختلف الجنس، جاز بيع بعضه ببعض كيلا، ووزنا، وجزافا)','5570','1795','0');
INSERT INTO [t19188] VALUES (1800,'١٦٧٨ - مسألة: (والجنس: ما له اسم خاص يشمل أنواعا، كالذهب، والفضة، والبر، والشعير، والتمر، والملح)','5573','1795','0');
INSERT INTO [t19188] VALUES (1801,'١٦٧٩ - مسألة: (وفروع الأجناس أجناس، كالأدقة، والأخباز، والأدهان)','5575','1795','0');
INSERT INTO [t19188] VALUES (1802,'١٦٨٠ - مسألة: (واللحم أجناس باختلاف أصوله. وكذلك اللبن. وعنه، جنس واحد. وعنه، في اللحم، أنه أربعة أجناس؛ لحم الأنعام، ولحم الوحش، ولحم الطير، ولحم دواب الماء)','5577','1795','0');
INSERT INTO [t19188] VALUES (1803,'١٦٨١ - مسألة: (واللحم والشحم والكبد أجناس)','5581','1795','0');
INSERT INTO [t19188] VALUES (1804,'١٦٨٢ - مسألة: (ولا يجوز بيع لحم بحيوان من جنسه. وفي بيعه بغير جنسه وجهان)','5585','1795','0');
INSERT INTO [t19188] VALUES (1805,'١٦٨٣ - مسألة: (ولا يجوز بيع حب بدقيقه ولا بسويقه؛ في أصح الروايتين)','5589','1795','0');
INSERT INTO [t19188] VALUES (1806,'١٦٨٤ - مسألة: (ولا يجوز بيع أصله بعصيره، ولا خالصه بمشوبه، ولا رطبه بيابسه، ولا نيئه بمطبوخه)','5591','1795','0');
INSERT INTO [t19188] VALUES (1807,'١٦٨٥ - مسألة: (ويجوز بيع دقيقه بدقيقه إذا استويا في النعومة، ومطبوخه بمطبوخه، وخبز؛ بخبزه إذا استويا في النشاف، وعصيره بعصيره، ورطبه برطبه)','5598','1795','0');
INSERT INTO [t19188] VALUES (1808,'١٦٨٦ - مسألة: (ولا يجوز بيع المحاقلة؛ وهو بيع الحب في سنبله بجنسه. وفي بيعه بغير جنسه وجهان)','5605','1795','0');
INSERT INTO [t19188] VALUES (1809,'١٦٨٧ - مسألة: (ولا)','5608','1795','0');
INSERT INTO [t19188] VALUES (1810,'١٦٨٨ - مسألة: (فيعطيه من التمر مثل ما يؤول إليه ما في النخل عند الجفاف. وعنه، يعطيه مثل رطبه)','5617','1795','0');
INSERT INTO [t19188] VALUES (1811,'١٦٨٩ - مسألة: (ولا يجوز في سائر الثمار، في أحد الوجهين)','5619','1795','0');
INSERT INTO [t19188] VALUES (1812,'١٦٩٠ - مسألة: (ولا يجوز بيع جنس فيه الربا بعضه ببعض ومع أحدهما أو معهما من غير جنسهما؛ كمد عجوة ودرهم بمدين، أو بدرهمين، أو بمد ودرهم. وعنه، يجوز بشرط أن يكون المفرد أكثر من الذي معه غيره، أو يكون مع كل واحد منهما من غير جنسه)','5622','1795','0');
INSERT INTO [t19188] VALUES (1813,'١٦٩١ - مسألة: (وإن باع نوعي جنس بنوع واحد منه؛','5627','1795','0');
INSERT INTO [t19188] VALUES (1814,'١٦٩٢ - مسألة: (وفي بيع النوى بتمر فيه النوى، واللبن بشاة ذات لبن، والصوف بنعجة عليها صوف، روايتان)','5630','1795','0');
INSERT INTO [t19188] VALUES (1815,'١٦٩٣ - مسألة: (ولا يجوز بيع تمر منزوع النوى بما نواه فيه)','5634','1795','0');
INSERT INTO [t19188] VALUES (1816,'١٦٩٤ - مسألة: (والمرجع في الكيل والوزن إلى عرف أهل الحجاز في زمن النبي - صلى الله عليه وسلم -. وما لا عرف لهم به','5635','1795','0');
INSERT INTO [t19188] VALUES (1817,'١٦٩٥ - مسألة: (وإن باع مكيلا بموزون)','5644','1795','0');
INSERT INTO [t19188] VALUES (1818,'١٦٩٦ - مسألة: (وما لا يدخله ربا الفضل؛ كالثياب، والحيوان، [يجوز النساء فيهما. وعنه، لا يجوز. وعنه، لا يجوز في الجنس الواحد؛ كالحيوان بالحيوان، ويجوز في الجنسين؛ كالثياب بالحيوان]','5645','1795','0');
INSERT INTO [t19188] VALUES (1819,'١٦٩٧ - مسألة: (ولا يجوز بيع الكالئ بالكالئ؛ وهو بيع الدين بالدين)','5650','1795','0');
INSERT INTO [t19188] VALUES (1820,'١٦٩٨ - مسألة: (وإن قبض البعض، ثم افترقا، بطل في الجميع، في أحد الوجهين. وفي الآخر، يبطل فيما لم يقبض)','5653','1795','0');
INSERT INTO [t19188] VALUES (1821,'١٦٩٩ - مسألة: (وإن تقابضا، ثم افترقا، فوجد أحدهما ما قبضه رديئا، فرده، بطل العقد، في إحدى الروايتين)','5655','1795','0');
INSERT INTO [t19188] VALUES (1822,'١٧٠٠ - مسألة: (والدراهم والدنانير تتعين بالتعيين في العقد، في أظهر الروايتين، فلا يجوز إبدالها، وإن خرجت مغصوبة بطل العقد)','5671','1795','0');
INSERT INTO [t19188] VALUES (1823,'١٧٠١ - مسألة: (ويحرم الربا بين المسلم والحربي، وبين','5679','1795','0');
INSERT INTO [t19188] VALUES (1824,'باب بيع الأصول والثمار','5683','1653','0');
INSERT INTO [t19188] VALUES (1825,'١٧٠٢ - مسألة: (ولا يدخل ما هو مودع فيها، من الكنز، والأحجار المدفونة)','5684','1824','0');
INSERT INTO [t19188] VALUES (1826,'١٧٠٣ - مسألة: (فأما ما كان من مصالحها)','5685','1824','0');
INSERT INTO [t19188] VALUES (1827,'١٧٠٤ - مسألة: (وإن باع أرضا بحقوقها، دخل غراسها وبناؤها في البيع، وإن لم يقل: بحقوقها. فعلى وجهين)','5688','1824','0');
INSERT INTO [t19188] VALUES (1828,'١٧٠٥ - مسألة: (وإن كان فيها زرع يجز مرة بعد أخرى؛ كالرطبة','5691','1824','0');
INSERT INTO [t19188] VALUES (1829,'١٧٠٦ - مسألة: (وإن كان فيها زرع لا يحصد إلا مرة؛ كالبر، والشعير، فهو للبائع مبقى إلي الحصاد، إلا أن يشترطه المبتاع)','5695','1824','0');
INSERT INTO [t19188] VALUES (1830,'١٧٠٧ - مسألة: (وكذلك الشجر إذا كان فيه ثمر باد؛ كالتوت، والتين، والرمان، والجوز، وما ظهر من نوره؛ كالمشمش، والتفاح، والسفرجل، واللوز، وما خرج من أكمامه؛ كالورد، والقطن. وما قبل ذلك فهو للمشتري)','5706','1824','0');
INSERT INTO [t19188] VALUES (1831,'١٧٠٨ - مسألة: (والورق للمشتري بكل حال)','5710','1824','0');
INSERT INTO [t19188] VALUES (1832,'١٧٠٩ - مسألة: (وإن ظهر بعض الثمرة، فهو للبائع، وما لم يظهر، فهو للمشتري. وقال ابن','5711','1824','0');
INSERT INTO [t19188] VALUES (1833,'١٧١٠ - مسألة: (وإن احتاج الزرع أو الثمرة إلى سقي، لم يلزم المشتري، ولم يملك منع البائع منه)','5712','1824','0');
INSERT INTO [t19188] VALUES (1834,'١٧١١ - مسألة: (ولا)','5717','1824','0');
INSERT INTO [t19188] VALUES (1835,'١٧١٢ - مسألة: (والحصاد واللقاط على المشتري)','5721','1824','0');
INSERT INTO [t19188] VALUES (1836,'١٧١٣ - مسألة: (فإن باعه مطلقا، أو بشرط التبقية، لم يصح)','5722','1824','0');
INSERT INTO [t19188] VALUES (1837,'١٧١٤ - مسألة: (فإن باعها بشرط القطع، ثم تركه المشتري','5725','1824','0');
INSERT INTO [t19188] VALUES (1838,'١٧١٥ - مسألة: (وإذا اشتد الحب وبدا الصلاح في الثمر، جاز بيعه مطلقا، وبشرط التبقية، وللمشتري تبقيته إلى الحصاد والجذاذ)','5735','1824','0');
INSERT INTO [t19188] VALUES (1839,'١٧١٦ - مسألة: (ويلزم البائع سقيه إن احتاج إلى ذلك)','5737','1824','0');
INSERT INTO [t19188] VALUES (1840,'١٧١٧ - مسألة: (وإن تلفت بجائحة من السماء، رجع على البائع. وعنه، إن أتلفت الثلث فصاعدا، ضمنه البائع، وإلا فلا)','5738','1824','0');
INSERT INTO [t19188] VALUES (1841,'١٧١٨ - مسألة: (وصلاح بعض ثمرة الشجرة صلاح لجميعها)','5747','1824','0');
INSERT INTO [t19188] VALUES (1842,'١٧١٩ - مسألة: (وبدو الصلاح في ثمر النخل أن يحمر أو يصفر، وفي العنب أن يتموه، وفي سائر الثمر أن يبدو فيه النضج، ويطيب أكله)','5751','1824','0');
INSERT INTO [t19188] VALUES (1843,'١٧٢٠ - مسألة: (ومن باع عبدا، وله مال، فماله للبائع، إلا أن يشترطه المبتاع)','5754','1824','0');
INSERT INTO [t19188] VALUES (1844,'١٧٢١ - مسألة: (فإن كان قصده المال، اشترط علمه وسائر شروط البيع، وإن لم يكن قصده المال، لم يشترط علمه)','5755','1824','0');
INSERT INTO [t19188] VALUES (1845,'١٧٢٢ - مسألة: (فإن كان عليه ثياب، فقال أحمد: ما كان للجمال، فهو للبائع، وما كان للبس المعتاد، فهو للمشتري)','5759','1824','0');
INSERT INTO [t19188] VALUES (1846,'باب السلم','5761','1653','0');
INSERT INTO [t19188] VALUES (1847,'١٧٢٣ - مسألة: (ولا يصح إلا بشروط سبعة، أحدها، أن','5762','1846','0');
INSERT INTO [t19188] VALUES (1848,'١٧٢٤ - مسألة: (فأما المعدود المختلف؛ كالحيوان، والفواكه، والبقول، والجلود، والرءوس، ونحوها، ففيه روايتان)','5764','1846','0');
INSERT INTO [t19188] VALUES (1849,'١٧٢٥ - مسألة: (وفي الأواني المختلفة الرءوس والأوساط؛ كالقماقم','5770','1846','0');
INSERT INTO [t19188] VALUES (1850,'١٧٢٦ - مسألة: (ولا يصح فيما لا ينضبط كالجواهر كلها، والحوامل من الحيوان، والمغشوش من الأثمان وغيرها، وما يجمع أخلاطا غير مميزة، كالغالية','5774','1846','0');
INSERT INTO [t19188] VALUES (1851,'١٧٢٧ - مسألة: (وإن شرط الأجود، لم يصح)','5793','1846','0');
INSERT INTO [t19188] VALUES (1852,'١٧٢٨ - مسألة: (وإن جاءه بدون ما وصف له، أو نوع آخر، فله أخذه)','5793','1846','0');
INSERT INTO [t19188] VALUES (1853,'١٧٢٩ - مسألة: (وإن جاءه بجنس آخر، لم يجز له أخذه)','5794','1846','0');
INSERT INTO [t19188] VALUES (1854,'١٧٣٠ - مسألة: (وإن جاءه بأجود منه من نوعه، لزمه قبوله)','5794','1846','0');
INSERT INTO [t19188] VALUES (1855,'١٧٣١ - مسألة: وإن جاءه بالأجود، فقال: (خذه وزدني درهما. لم يصح)','5795','1846','0');
INSERT INTO [t19188] VALUES (1856,'١٧٣٢ - مسألة: (ولابد أن يكون المكيال معلوما، فإن شرط مكيالا بعينه، أو صنجة بعينها غير معلومة، لم يصح)','5798','1846','0');
INSERT INTO [t19188] VALUES (1857,'١٧٣٣ - مسألة: (وفي المعدود المختلف غير الحيوان روايتان؛ إحداهما، يسلم فيه عددا. والأخرى، وزنا. وقيل: يسلم في الجوز والبيض عددا، وفي الفواكه والبقول وزنا)','5799','1846','0');
INSERT INTO [t19188] VALUES (1858,'١٧٣٤ - مسألة: (إلا أن يسلم في شيء يأخذ منه كل يوم أجزاء معلومة، فيصح)','5805','1846','0');
INSERT INTO [t19188] VALUES (1859,'١٧٣٥ - مسألة: (فإن أسلم في جنس إلى أجلين، أو في جنسين إلى أجل، صح)','5806','1846','0');
INSERT INTO [t19188] VALUES (1860,'١٧٣٦ - مسألة: (ولابد أن يكون الأجل مقدرا بزمن معلوم)','5806','1846','0');
INSERT INTO [t19188] VALUES (1861,'١٧٣٧ - مسألة: (فإن أسلم إلى الحصاد، أو [الجذاذ، أو]','5809','1846','0');
INSERT INTO [t19188] VALUES (1862,'١٧٣٨ - مسألة: (وإذا جاءه بالسلم قبل محله، ولا ضرر في قبضه، لزمه قبضه، وإلا فلا)','5812','1846','0');
INSERT INTO [t19188] VALUES (1863,'١٧٣٩ - مسألة: (وإن أسلم في ثمرة بستان بعينه، أو قرية صغيرة، لم يصح)','5815','1846','0');
INSERT INTO [t19188] VALUES (1864,'١٧٤٠ - مسألة: (وإن أسلم إلى محل يوجد فيه عاما، فانقطع،','5817','1846','0');
INSERT INTO [t19188] VALUES (1865,'١٧٤١ - مسألة: (وهل يشترط كونه معلوم الصفة والقدر كالمسلم فيه؟ على وجهين)','5825','1846','0');
INSERT INTO [t19188] VALUES (1866,'١٧٤٢ - مسألة: (وإن أسلم ثمنا واحدا في جنسين، لم يجز حتى يبين ثمن كل جنس)','5829','1846','0');
INSERT INTO [t19188] VALUES (1867,'١٧٤٣ - مسألة: (ولا يشترط ذكر مكان الإيفاء)','5832','1846','0');
INSERT INTO [t19188] VALUES (1868,'١٧٤٤ - مسألة: (إلا أن يكون موضع العقد لا يمكن الوفاء فيه،','5832','1846','0');
INSERT INTO [t19188] VALUES (1869,'١٧٤٥ - مسألة: (ويكون الوفاء في مكان العقد)','5833','1846','0');
INSERT INTO [t19188] VALUES (1870,'١٧٤٦ - مسألة: (فإن شرط الوفاء فيه، كان تأكيدا)','5834','1846','0');
INSERT INTO [t19188] VALUES (1871,'١٧٤٧ - مسألة: (وإن شرطه في غيره، صح)','5834','1846','0');
INSERT INTO [t19188] VALUES (1872,'١٧٤٨ - مسألة: (ولا يجوز بيع المسلم فيه قبل قبضه، ولا هبته، ولا أخذ غيره مكانه، ولا الحوالة به)','5835','1846','0');
INSERT INTO [t19188] VALUES (1873,'١٧٤٩ - مسألة: (ويجوز بيع الدين المستقر لمن هو في ذمته، بشرط أن يقبض عوضه في المجلس، ولا يجوز لغيره)','5839','1846','0');
INSERT INTO [t19188] VALUES (1874,'١٧٥٠ - مسألة: (وتجوز الإقالة في السلم، وتجوز في بعضه في [إحدى الروايتين]','5843','1846','0');
INSERT INTO [t19188] VALUES (1875,'١٧٥١ - مسألة: (وإن انفسخ العقد بإقالة أو غيرها، لم يجز أن يأخذ عن الثمن عوضا من غير جنسه)','5846','1846','0');
INSERT INTO [t19188] VALUES (1876,'١٧٥٢ - مسألة: (وإذا كان لرجل سلم، وعليه سلم من جنسه، فقال لغريمه: اقبض سلمي لنفسك. ففعل','5848','1846','0');
INSERT INTO [t19188] VALUES (1877,'١٧٥٣ - مسألة: (وإن قال: اقبضه لي، ثم اقبضه لنفسك. صح)','5849','1846','0');
INSERT INTO [t19188] VALUES (1878,'١٧٥٤ - مسألة: (وإن قال: أنا أقبضه لنفسي، وخذه','5850','1846','0');
INSERT INTO [t19188] VALUES (1879,'١٧٥٥ - مسألة: (وإن اكتاله، وتركه في المكيال، وسلمه إلى غريمه، فقبضه، صح القبض لهما)','5851','1846','0');
INSERT INTO [t19188] VALUES (1880,'١٧٥٦ - مسألة: (وإن قبض المسلم فيه جزافا، فالقول قوله في قدره)','5854','1846','0');
INSERT INTO [t19188] VALUES (1881,'١٧٥٧ - مسألة: (وإن قبضه كيلا، أو وزنا، ثم ادعى غلطا، لم يقبل قوله، في أحد الوجهين)','5855','1846','0');
INSERT INTO [t19188] VALUES (1882,'١٧٥٨ - مسألة: (وهل يجوز الرهن والكفيل بالمسلم فيه؟ على روايتين)','5856','1846','0');
INSERT INTO [t19188] VALUES (1883,'باب القرض','5864','1653','0');
INSERT INTO [t19188] VALUES (1884,'١٧٥٩ - مسألة: (وهو من المرافق المندوب إليها)','5865','1883','0');
INSERT INTO [t19188] VALUES (1885,'١٧٦٠ - مسألة: (ويصح في كل عين يجوز بيعها، إلا بني آدم، والجواهر ونحوها، مما لا يصح السلم فيه، في أحد الوجهين فيهما)','5866','1883','0');
INSERT INTO [t19188] VALUES (1886,'١٧٦١ - مسألة: (ويثبت الملك فيه بالقبض)','5871','1883','0');
INSERT INTO [t19188] VALUES (1887,'١٧٦٢ - مسألة: (فلا يملك المقرض استرجاعه)','5873','1883','0');
INSERT INTO [t19188] VALUES (1888,'١٧٦٣ - مسألة: (وله طلب بدله)','5873','1883','0');
INSERT INTO [t19188] VALUES (1889,'١٧٦٤ - مسألة: (فإن رده المقترض عليه، لزمه قبوله ما لم','5874','1883','0');
INSERT INTO [t19188] VALUES (1890,'١٧٦٥ - مسألة: (ويجب رد المثل في المكيل والموزون، والقيمة في الجواهر ونحوها. وفيما سوى ذلك وجهان)','5878','1883','0');
INSERT INTO [t19188] VALUES (1891,'١٧٦٦ - مسألة: (ويثبت العوض في الذمة حالا، وإن أجله)','5880','1883','0');
INSERT INTO [t19188] VALUES (1892,'١٧٦٧ - مسألة: (ويجوز شرط الرهن والضمين فيه)','5881','1883','0');
INSERT INTO [t19188] VALUES (1893,'١٧٦٨ - مسألة: (ولا يجوز شرط ما يجر نفعا؛ نحو أن يسكنه داره، أو يقضيه خيرا منه، أو في بلد آخر. ويحتمل جواز هذا الشرط)','5882','1883','0');
INSERT INTO [t19188] VALUES (1894,'١٧٦٩ - مسألة: (وإن فعل ذلك من غير شرط، أو قضى خيرا','5885','1883','0');
INSERT INTO [t19188] VALUES (1895,'١٧٧٠ - مسألة: (وإن فعله قبل الوفاء، لم يجز، إلا أن تكون','5888','1883','0');
INSERT INTO [t19188] VALUES (1896,'١٧٧١ - مسألة: (وإذا أقرضه أثمانا، فطالبه بها ببلد آخر، لزمته. وإن أقرضه غيرها)','5894','1883','0');
INSERT INTO [t19188] VALUES (1897,'باب الرهن','5897','1653','0');
INSERT INTO [t19188] VALUES (1898,'١٧٧٢ - مسألة: (وهو وثيقة بالحق)','5898','1897','0');
INSERT INTO [t19188] VALUES (1899,'١٧٧٣ - مسألة: وهو (لازم في حق الراهن، جائز في حق المرتهن)','5899','1897','0');
INSERT INTO [t19188] VALUES (1900,'١٧٧٤ - مسألة: (يجوز عقده مع الحق وبعده، ولا يجوز قبله، إلا عند أبي الخطاب)','5899','1897','0');
INSERT INTO [t19188] VALUES (1901,'١٧٧٥ - مسألة: (ويصح في كل عين يجوز بيعها)','5901','1897','0');
INSERT INTO [t19188] VALUES (1902,'١٧٧٦ - مسألة: (إلا المكاتب، إذا قلنا: استدامة القبض شرط. لم يجز رهنه)','5902','1897','0');
INSERT INTO [t19188] VALUES (1903,'١٧٧٧ - مسألة: (ويجوز رهن ما يسرع إليه الفساد بدين مؤجل، ويباع ويجعل ثمنه رهنا)','5904','1897','0');
INSERT INTO [t19188] VALUES (1904,'١٧٧٨ - مسألة: (ويجوز رهن المشاع)','5905','1897','0');
INSERT INTO [t19188] VALUES (1905,'١٧٧٩ - مسألة: (ويجوز رهن المبيع غير المكيل والموزون قبل قبضه إلا على ثمنه، في أحد الوجهين)','5910','1897','0');
INSERT INTO [t19188] VALUES (1906,'١٧٨٠ - مسألة: (وما لا يجوز بيعه لا يجوز رهنه، إلا الثمرة','5913','1897','0');
INSERT INTO [t19188] VALUES (1907,'١٧٨١ - مسألة: (ولا يصح رهن العبد المسلم لكافر)','5918','1897','0');
INSERT INTO [t19188] VALUES (1908,'١٧٨٢ - مسألة: (ولا يلزم الرهن إلا بالقبض، واستدامته شرط','5926','1897','0');
INSERT INTO [t19188] VALUES (1909,'١٧٨٣ - مسألة: (فإن أخرجه المرتهن إلى الراهن باختياره، زال لزوم الرهن)','5929','1897','0');
INSERT INTO [t19188] VALUES (1910,'١٧٨٤ - مسألة: (ولو رهنه عصيرا، فتخمر، زال لزومه، فإن تخلل، عاد لزومه بحكم العقد السابق)','5932','1897','0');
INSERT INTO [t19188] VALUES (1911,'١٧٨٥ - مسألة: (وعنه، أن القبض واستدامته في المتعين ليسا بشرط)','5933','1897','0');
INSERT INTO [t19188] VALUES (1912,'١٧٨٦ - مسألة: (وتصرف الراهن في الرهن لا يصح، إلا العتق، فإنه يصح، وتؤخذ منه قيمته','5947','1897','0');
INSERT INTO [t19188] VALUES (1913,'١٧٨٧ - مسألة: وليس له','5953','1897','0');
INSERT INTO [t19188] VALUES (1914,'١٧٨٨ - مسألة: (وإن وطئ الجارية، فأولدها، خرجت من الرهن، وأخذت منه قيمتها، فجعلت رهنا)','5955','1897','0');
INSERT INTO [t19188] VALUES (1915,'١٧٨٩ - مسألة: (وإن أذن له المرتهن في بيع الرهن، أو هبته، أو نحو ذلك، صح، وبطل الرهن، إلا أن يأذن له في بيعه بشرط أن يجعل','5959','1897','0');
INSERT INTO [t19188] VALUES (1916,'١٧٩٠ - مسألة: (ونماء الرهن، وكسبه، وأرش الجناية عليه، من الرهن)','5963','1897','0');
INSERT INTO [t19188] VALUES (1917,'١٧٩١ - مسألة: (ومؤنته على الراهن، وكفنه إن مات، وأجرة مخزنه إن كان مخزونا)','5967','1897','0');
INSERT INTO [t19188] VALUES (1918,'١٧٩٢ - مسألة: (وهو أمانة في يد المرتهن، إن تلف بغير تعد منه، فلا شيء عليه، ولا يسقط بهلاكه شيء من دينه)','5972','1897','0');
INSERT INTO [t19188] VALUES (1919,'١٧٩٣ - مسألة: (وإن تلف بعضه، فباقيه رهن بجميع الدين)','5975','1897','0');
INSERT INTO [t19188] VALUES (1920,'١٧٩٤ - مسألة: (ولا ينفك شيء من الرهن حتى يقضي جميع الدين)','5977','1897','0');
INSERT INTO [t19188] VALUES (1921,'١٧٩٥ - مسألة: (وإن رهنه عند رجلين، فوفى أحدهما، انفك في نصيبه)','5978','1897','0');
INSERT INTO [t19188] VALUES (1922,'١٧٩٦ - مسألة: (وإن رهنه رجلان شيئا، فوفاه أحدهما، انفك في نصيبه)','5979','1897','0');
INSERT INTO [t19188] VALUES (1923,'١٧٩٧ - مسألة: (وإذا حل الدين، وامتنع من وفائه، فإن كان الراهن أذن للمرتهن أو العدل في بيع الرهن، باع ووفى الدين، وإلا رفع الأمر إلى الحاكم، فيجبره على وفاء الدين أو بيع الرهن، فإن لم يفعل باعه الحاكم، وقضى دينه)','5981','1897','0');
INSERT INTO [t19188] VALUES (1924,'١٧٩٨ - مسألة؛ قال الشيخ، رحمه الله: (وإن شرط في الرهن جعله على يد عدل، صح، وقام قبضه مقام قبض المرتهن)','5983','1897','0');
INSERT INTO [t19188] VALUES (1925,'١٧٩٩ - مسألة: (وإن شرط جعله في يد اثنين، فليس لأحدهما الانفراد بحفظه)','5984','1897','0');
INSERT INTO [t19188] VALUES (1926,'١٨٠٠ - مسألة: (وليس للراهن ولا للمرتهن إذا لم يتفقا، ولا للحاكم نقله عن يد العدل، إلا أن يتغير حاله)','5985','1897','0');
INSERT INTO [t19188] VALUES (1927,'١٨٠١ - مسألة: (وله رده إليهما، ولا يملك رده إلى أحدهما، فإن فعل، فعليه رده إلى يده، فإن لم يفعل، ضمن حق الآخر)','5986','1897','0');
INSERT INTO [t19188] VALUES (1928,'١٨٠٢ - مسألة: (فإن أذنا له في البيع، لم يبع إلا بنقد البلد، فإن كان فيه نقود، باع بجنس الدين)','5988','1897','0');
INSERT INTO [t19188] VALUES (1929,'١٨٠٣ - مسألة: (وإن قبض الثمن، فتلف في يده، فهو من ضمان الراهن)','5992','1897','0');
INSERT INTO [t19188] VALUES (1930,'١٨٠٤ - مسألة: (وإن استحق المبيع، رجع المشتري على الراهن)','5992','1897','0');
INSERT INTO [t19188] VALUES (1931,'١٨٠٥ - مسألة: (وإن ادعى دفع الثمن إلى المرتهن، فأنكر، ولم يكن قضاه ببينة، ضمن. وعنه، لا يضمن، إلا أن يكون أمر بالإشهاد، فلم يفعل. وهكذا الحكم في الوكيل)','5994','1897','0');
INSERT INTO [t19188] VALUES (1932,'١٨٠٦ - مسألة: (وإن شرط أن يبيعه المرتهن أو العدل، صح، فإن عزلهما، صح عزله)','5998','1897','0');
INSERT INTO [t19188] VALUES (1933,'١٨٠٧ - مسألة: (فإن شرط أن لا يبيعه عند الحلول، أو إن جاءه بحقه)','6000','1897','0');
INSERT INTO [t19188] VALUES (1934,'١٨٠٨ - مسألة: (وإن أقر الراهن أنه أعتق العبد قبل رهنه)','6023','1897','0');
INSERT INTO [t19188] VALUES (1935,'١٨٠٩ - مسألة: (وإن أقر أنه كان جنى، أو أنه باعه، أو غصبه، قبل على نفسه، ولم يقبل على المرتهن، إلا أن يصدقه)','6024','1897','0');
INSERT INTO [t19188] VALUES (1936,'١٨١٠ - مسألة: (وإن أنفق على الرهن بغير إذن الراهن مع إمكانه، فهو متبرع)','6032','1897','0');
INSERT INTO [t19188] VALUES (1937,'١٨١١ - مسألة: (وإن عجز عن استئذانه، ولم يستأذن الحاكم، فعلى روايتين)','6033','1897','0');
INSERT INTO [t19188] VALUES (1938,'١٨١٢ - مسألة: (وكذلك الحكم في الوديعة، وفي نفقة الجمال إذا هرب الجمال وتركها في يد المكتري)','6035','1897','0');
INSERT INTO [t19188] VALUES (1939,'١٨١٣ - مسألة: (وإن انهدمت الدار، فعمرها المرتهن بغير إذن الراهن، لم يرجع به، رواية واحدة)','6036','1897','0');
INSERT INTO [t19188] VALUES (1940,'١٨١٤ - مسألة: (فإن فداه، فهو رهن بحاله، وإن سلمه بطل الرهن)','6040','1897','0');
INSERT INTO [t19188] VALUES (1941,'١٨١٥ - مسألة: (فإن لم يستغرق الأرش قيمته، بيع منه بقدره، وباقيه رهن. وقيل: يباع جميعه، ويكون باقي ثمنه رهنا)','6040','1897','0');
INSERT INTO [t19188] VALUES (1942,'١٨١٦ - مسألة: (فإن اختار المرتهن فداءه، ففداه بإذن الراهن، رجع به، وإن فداه بغير إذنه، فهل يرجع به؟ على روايتين)','6042','1897','0');
INSERT INTO [t19188] VALUES (1943,'١٨١٧ - مسألة: (وإن جني عليه جناية موجبة للقصاص،','6050','1897','0');
INSERT INTO [t19188] VALUES (1944,'١٨١٨ - مسألة: (وكذلك إن جنى على سيده، فاقتص منه هو أو ورثته)','6054','1897','0');
INSERT INTO [t19188] VALUES (1945,'١٨١٩ - مسألة: (وإن عفا السيد على مال، أو كانت موجبة للمال، فما قبض منه، جعل مكانه)','6055','1897','0');
INSERT INTO [t19188] VALUES (1946,'١٨٢٠ - مسألة: (وإن عفا السيد عن المال، صح في حقه، ولم يصح في حق المرتهن، فإذا انفك الرهن، رد إلى الجاني. وقال أبو الخطاب: يصح، وعليه قيمته)','6056','1897','0');
INSERT INTO [t19188] VALUES (1947,'١٨٢١ - مسألة: (وإن وطئ المرتهن الجارية)','6060','1897','0');
INSERT INTO [t19188] VALUES (1948,'١٨٢٢ - مسألة: (وإن وطئها بإذن الراهن، وادعى الجهالة، وكان مثله يجهل ذلك، فلا حد عليه، ولا مهر، وولده حر، لا تلزمه قيمته)','6062','1897','0');
INSERT INTO [t19188] VALUES (1949,'باب الضمان','6067','1653','0');
INSERT INTO [t19188] VALUES (1950,'١٨٢٣ - مسألة: (ولصاحب الحق مطالبة من شاء منهما في الحياة والموت)','6069','1949','0');
INSERT INTO [t19188] VALUES (1951,'١٨٢٤ - مسألة: (فإن برئت ذمة المضمون عنه برئ الضامن)','6073','1949','0');
INSERT INTO [t19188] VALUES (1952,'١٨٢٥ - مسألة: (وإن برئ الضامن، أو أقر ببراءته، لم يبرأ المضمون عنه)','6073','1949','0');
INSERT INTO [t19188] VALUES (1953,'١٨٢٦ - مسألة: (ولو ضمن ذمي لذمي عن ذمي خمرا، فأسلم المضمون له أو المضمون عنه، برئ هو والضامن معا)','6074','1949','0');
INSERT INTO [t19188] VALUES (1954,'١٨٢٧ - مسألة: (ولا يصح إلا من جائز التصرف)','6075','1949','0');
INSERT INTO [t19188] VALUES (1955,'١٨٢٨ - مسألة: (ولا يصح من صبي، ولا مجنون، ولا سفيه، ولا من عبد بغير إذن سيده. وعنه، يصح، ويتبع به بعد العتق. وإن ضمن بإذن سيده، صح. وهل يتعلق برقبته أو ذمة سيده؟ على روايتين)','6077','1949','0');
INSERT INTO [t19188] VALUES (1956,'١٨٢٩ - مسألة: (ولا يصح إلا برضا الضامن، ولا يعتبر رضا المضمون له، ولا المضمون عنه، ولا معرفة الضامن لهما)','6083','1949','0');
INSERT INTO [t19188] VALUES (1957,'١٨٣٠ - مسألة: (ولا)','6084','1949','0');
INSERT INTO [t19188] VALUES (1958,'١٨٣١ - مسألة: (ويصح ضمان دين الضامن)','6087','1949','0');
INSERT INTO [t19188] VALUES (1959,'١٨٣٢ - مسألة: (و)','6089','1949','0');
INSERT INTO [t19188] VALUES (1960,'١٨٣٣ - مسألة: (ويصح ضمان عهدة المبيع عن البائع للمشتري، وعن المشتري للبائع)','6091','1949','0');
INSERT INTO [t19188] VALUES (1961,'١٨٣٤ - مسألة: (ولا يصح ضمان دين الكتابة، في أصح الروايتين)','6096','1949','0');
INSERT INTO [t19188] VALUES (1962,'١٨٣٥ - مسألة: (ولا يصح ضمان الأمانات؛ كالوديعة ونحوها، إلا أن يضمن التعدي فيها)','6097','1949','0');
INSERT INTO [t19188] VALUES (1963,'١٨٣٦ - مسألة: (فأما الأعيان المضمونة؛ كالغصوب، والعواري، والمقبوض على وجه السوم، فيصح ضمانها)','6098','1949','0');
INSERT INTO [t19188] VALUES (1964,'١٨٣٧ - مسألة: (وإن قضى الضامن الدين متبرعا، لم يرجع بشيء)','6104','1949','0');
INSERT INTO [t19188] VALUES (1965,'١٨٣٨ - مسألة: (وإن نوى الرجوع، وكان الضمان والقضاء بغير إذن المضمون عنه، فهل يرجع؟ على روايتين. وإن أذن)','6104','1949','0');
INSERT INTO [t19188] VALUES (1966,'١٨٣٩ - مسألة: (وإن أنكر المضمون له القضاء، وحلف، لم يرجع الضامن على المضمون عنه، سواء صدقه أو كذبة)','6112','1949','0');
INSERT INTO [t19188] VALUES (1967,'١٨٤٠ - مسألة: (وإن اعترف)','6115','1949','0');
INSERT INTO [t19188] VALUES (1968,'١٨٤١ - مسألة: (وإن قضى المؤجل قبل أجله، لم يرجع حتى يحل)','6117','1949','0');
INSERT INTO [t19188] VALUES (1969,'١٨٤٢ - مسألة: (وإن مات الضامن أو المضمون عنه، فهل يحل الدين؟ على روايتين. وأيهما حل عليه، لم يحل على الآخر)','6117','1949','0');
INSERT INTO [t19188] VALUES (1970,'١٨٤٣ - مسألة: (ويصح ضمان الحال مؤجلا. وإن ضمن المؤجل حالا، لم يلزمه قبل أجله، في أصح الوجهين)','6119','1949','0');
INSERT INTO [t19188] VALUES (1971,'١٨٤٤ - مسألة: (وتصح ببدن من عليه دين، وبالأعيان المضمونة)','6123','1949','0');
INSERT INTO [t19188] VALUES (1972,'١٨٤٥ - مسألة: (ولا تصح ببدن من عليه حد أو قصاص)','6125','1949','0');
INSERT INTO [t19188] VALUES (1973,'١٨٤٦ - مسألة: (ولا)','6126','1949','0');
INSERT INTO [t19188] VALUES (1974,'١٨٤٧ - مسألة: (وإن كفل بجزء شائع من إنسان أو عضو، أو كفل بإنسان على أنه إن جاء به، وإلا فهو كفيل بآخر، أو ضامن ما عليه، صح، في أحد الوجهين)','6127','1949','0');
INSERT INTO [t19188] VALUES (1975,'١٨٤٨ - مسألة: (ولا يصح إلا برضا الكفيل، وفي رضا المكفول به وجهان)','6132','1949','0');
INSERT INTO [t19188] VALUES (1976,'١٨٤٩ - مسألة: (ومتى، [أحضر المكفول به]','6133','1949','0');
INSERT INTO [t19188] VALUES (1977,'١٨٥٠ - مسألة: (وإن مات المكفول به، أو تلفت العين بفعل الله تعالى، أو سلم نفسه، برئ الكفيل)','6137','1949','0');
INSERT INTO [t19188] VALUES (1978,'١٨٥١ - مسألة: (وإن تعذر إحضاره مع بقائه، لزم الكفيل الدين أو عوض العين)','6140','1949','0');
INSERT INTO [t19188] VALUES (1979,'١٨٥٢ - مسألة: (وإن غاب، أمهل الكفيل بقدر ما يمضي','6140','1949','0');
INSERT INTO [t19188] VALUES (1980,'١٨٥٣ - مسألة: (وإذا طالب الكفيل المكفول به بالحضور معه، لزمه ذلك إن كانت الكفالة بإذنه أو طالبه صاحب الحق بإحضاره، وإلا فلا)','6142','1949','0');
INSERT INTO [t19188] VALUES (1981,'١٨٥٤ - مسألة: (ولو تكفل واحد لاثنين، فأبرأه أحدهما)','6145','1949','0');
INSERT INTO [t19188] VALUES (1982,'باب الحوالة','6148','1653','0');
INSERT INTO [t19188] VALUES (1983,'١٨٥٥ - مسألة: (والحوالة تنقل الحق من ذمة المحيل إلى ذمة المحال عليه، فلا يملك المحتال الرجوع عليه بحال)','6149','1982','0');
INSERT INTO [t19188] VALUES (1984,'١٨٥٦ - مسألة: (ولا تصح إلا بشروط ثلاثة؛ أحدها، أن يحيل على دين مستقر، فإن أحال على مال الكتابة، أو السلم)','6152','1982','0');
INSERT INTO [t19188] VALUES (1985,'١٨٥٧ - مسألة: (ولا يعتبر رضا المحال عليه، ولا رضا','6161','1982','0');
INSERT INTO [t19188] VALUES (1986,'١٨٥٨ - مسألة: (وإن ظنه مليئا، فبان مفلسا، ولم يكن رضي بالحوالة، رجع عليه، وإلا فلا. ويحتمل أن يرجع)','6165','1982','0');
INSERT INTO [t19188] VALUES (1987,'١٨٥٩ - مسألة: (وإذا أحال المشتري البائع بالثمن، أو أحال البائع عليه به، فبان البيع باطلا، فالحوالة باطلة)','6166','1982','0');
INSERT INTO [t19188] VALUES (1988,'١٨٦٠ - مسألة: (فإن فسخ العقد بعيب أو إقالة، لم تبطل الحوالة)','6167','1982','0');
INSERT INTO [t19188] VALUES (1989,'١٨٦١ - مسألة: (وللبائع أن يحيل المشتري على من أحاله المشتري عليه في الصورة الأولى، وللمشتري أن يحيل المحتال عليه على البائع في الثانية)','6169','1982','0');
INSERT INTO [t19188] VALUES (1990,'١٨٦٢ - مسألة: (وإذا قال: أحلتك. قال: بل وكلتني. أو قال: وكلتك. قال: بل أحلتني. فالقول قول مدعي الوكالة)','6171','1982','0');
INSERT INTO [t19188] VALUES (1991,'١٨٦٣ - مسألة: (وإن اتفقا على أنه قال: أحلتك)','6172','1982','0');
INSERT INTO [t19188] VALUES (1992,'١٨٦٤ - مسألة: (وإن قال: أحلتك بدينك. فالقول قول مدعي الحوالة، وجها واحدا)','6176','1982','0');
INSERT INTO [t19188] VALUES (1993,'باب الصلح','6180','1653','0');
INSERT INTO [t19188] VALUES (1994,'١٨٦٥ - مسألة: و (الصلح في الأموال قسمان؛ أحدهما، صلح على الإقرار، وهو نوعان؛ أحدهما، صلح على جنس الحق، مثل أن يقر له بدين، فيضع عنه بعضه، أو بعين، فيهب له بعضها، ويأخذ','6181','1993','0');
INSERT INTO [t19188] VALUES (1995,'١٨٦٦ - مسألة: (ولا يصح ذلك ممن لا يملك التبرع؛ كالمكاتب، والمأذون له، وولي اليتيم، إلا في حال الإنكار وعدم','6186','1993','0');
INSERT INTO [t19188] VALUES (1996,'١٨٦٧ - مسألة: (وإن صالح عن المؤجل ببعضه حالا، لم يصح)','6187','1993','0');
INSERT INTO [t19188] VALUES (1997,'١٨٦٨ - مسألة: (وإن وضع بعض الحال وأجل باقيه، صح الإسقاط دون التأجيل)','6188','1993','0');
INSERT INTO [t19188] VALUES (1998,'١٨٦٩ - مسألة: (وإن صالح عن الحق بأكثر منه من جنسه، مثل أن يصالح عن دية الخطأ، أو قيمة متلف بأكثر منها من جنسها، لم يصح)','6190','1993','0');
INSERT INTO [t19188] VALUES (1999,'١٨٧٠ - مسألة: (وإن صالحه بعرض قيمته أكثر منها)','6190','1993','0');
INSERT INTO [t19188] VALUES (2000,'١٨٧١ - مسألة: (وإن صالحه عن بيت على أن يسكنه سنة، أو يبني له فوقه غرفة، لم يصح)','6191','1993','0');
INSERT INTO [t19188] VALUES (2001,'١٨٧٢ - مسألة: (ولو قال: أقر لي بديني، وأعطيك منه مائة. ففعل، صح الإقرار، ولم يصح الصلح)','6192','1993','0');
INSERT INTO [t19188] VALUES (2002,'١٨٧٣ - مسألة: (وإن صالح إنسانا ليقر له بالعبودية، أو امرأة لتقر له بالزوجية، لم يصح)','6193','1993','0');
INSERT INTO [t19188] VALUES (2003,'١٨٧٤ - مسألة: (وإن دفع المدعى عليه العبودية إلى المدعي مالا صلحا عن دعواه، صح)','6194','1993','0');
INSERT INTO [t19188] VALUES (2004,'١٨٧٥ - مسألة: (وإن صالحت المرأة بتزويج نفسها، صح. فإن كان الصلح عن عيب في مبيعها، فتبين أنه ليس بعيب، رجعت بأرشه','6197','1993','0');
INSERT INTO [t19188] VALUES (2005,'١٨٧٦ - مسألة: (وإن صالح عما في الذمة بشيء في الذمة، لم يجز التفرق قبل القبض؛ لأنه بيع دين بدين)','6200','1993','0');
INSERT INTO [t19188] VALUES (2006,'١٨٧٧ - مسألة: (ويصح الصلح عن المجهول بمعلوم، إذا كان مما لا يمكن معرفته للحاجة)','6202','1993','0');
INSERT INTO [t19188] VALUES (2007,'١٨٧٨ - مسألة: (وإن كان شقصا مشفوعا، ثبتت فيه الشفعة، ويكون إبراء في حق الآخر، فلا يرد ما صالح','6210','1993','0');
INSERT INTO [t19188] VALUES (2008,'١٨٧٩ - مسألة: (فإن كان أحدهما عالما بكذب نفسه، فالصلح باطل في حقه، وما أخذه حرام عليه)','6211','1993','0');
INSERT INTO [t19188] VALUES (2009,'١٨٨٠ - مسألة: (فإن صالح عن المنكر أجنبي بغير إذنه، صح، ولم يرجع عليه في أصح الوجهين)','6212','1993','0');
INSERT INTO [t19188] VALUES (2010,'١٨٨١ - مسألة: (وإن صالح الأجنبي لنفسه؛ لتكون المطالبة','6214','1993','0');
INSERT INTO [t19188] VALUES (2011,'١٨٨٢ - مسألة: (ولو صالح سارقا ليطلقه','6221','1993','0');
INSERT INTO [t19188] VALUES (2012,'١٨٨٣ - مسألة: (وإن صالحه على','6224','1993','0');
INSERT INTO [t19188] VALUES (2013,'١٨٨٤ - مسألة: (ويجوز أن يشتري ممرا في دار، من موضعا في حائطه يفتحه بابا، وبقعة يحفرها بئرا)','6231','1993','0');
INSERT INTO [t19188] VALUES (2014,'١٨٨٥ - مسألة: (و)','6231','1993','0');
INSERT INTO [t19188] VALUES (2015,'١٨٨٦ - مسألة: (وإن حصل في هوائه أغصان شجرة غيره، فطالبه بإزالتها، فله ذلك. فإن أبى، فله قطعها)','6233','1993','0');
INSERT INTO [t19188] VALUES (2016,'١٨٨٧ - مسألة: (فإن صالحه عن ذلك بعوض، لم يجز)','6234','1993','0');
INSERT INTO [t19188] VALUES (2017,'١٨٨٨ - مسألة: (فإن اتفقا على أن الثمرة له أو بينهما، جاز، ولم يلزم)','6236','1993','0');
INSERT INTO [t19188] VALUES (2018,'١٨٨٩ - مسألة: (ولا يجوز أن يشرع إلى طريق نافذ جناحا، ولا ساباطا','6239','1993','0');
INSERT INTO [t19188] VALUES (2019,'١٨٩٠ - مسألة: (ولا)','6245','1993','0');
INSERT INTO [t19188] VALUES (2020,'١٨٩١ - مسألة: (فإن صالح عن ذلك بعوض، جاز في أحد الوجهين)','6246','1993','0');
INSERT INTO [t19188] VALUES (2021,'١٨٩٢ - مسألة: (وإذا كان ظهر داره في درب غير نافذ، ففتح فيه بابا لغير الاستطراق، جاز)','6247','1993','0');
INSERT INTO [t19188] VALUES (2022,'١٨٩٣ - مسألة: (وإن فتحه للاستطراق، لم يجز إلا بإذنهم)','6248','1993','0');
INSERT INTO [t19188] VALUES (2023,'١٨٩٤ - مسألة: (ولو كان بابه في آخر الدرب، ملك نقله إلى أوله، ولم يملك نقله إلى داخل منه في أحد الوجهين)','6249','1993','0');
INSERT INTO [t19188] VALUES (2024,'١٨٩٥ - مسألة: (وليس له أن يفتح في حائط جاره، ولا الحائط المشترك روزنة','6254','1993','0');
INSERT INTO [t19188] VALUES (2025,'١٨٩٦ - مسألة: (وليس له وضع خشبه عليه إلا عند الضرورة، بأن لا يمكنه التسقيف إلا به)','6255','1993','0');
INSERT INTO [t19188] VALUES (2026,'١٨٩٧ - مسألة: (وعنه، ليس له وضع خشبه على جدار المسجد. وهذا تنبيه على أنه لا يضعه على جدار جاره)','6259','1993','0');
INSERT INTO [t19188] VALUES (2027,'١٨٩٨ - مسألة: (وإن كان بينهما حائط فانهدم، فطالب أحدهما صاحبه ببنائه، أجبر عليه. وعنه، لا يجبر)','6265','1993','0');
INSERT INTO [t19188] VALUES (2028,'١٨٩٩ - مسألة: (وليس له منعه من بنائه)','6268','1993','0');
INSERT INTO [t19188] VALUES (2029,'١٩٠٠ - مسألة: (فإن بناه بآلته، فهو بينهما، وإن بناه بآلة من عنده، فهو له، وليس للآخر الانتفاع به. فإن طلب الانتفاع به، خير الباني بين أخذ نصف قيمته وبين أخذ آلته)','6268','1993','0');
INSERT INTO [t19188] VALUES (2030,'١٩٠١ - مسألة: (وإن كان بينهما نهر، أو بئر، أو دولاب','6275','1993','0');
INSERT INTO [t19188] VALUES (2031,'١٩٠٢ - مسألة: (وليس لأحدهما منع صاحبه من عمارته كالحائط، فإن عمره، فالماء بينهما على الشركة)','6276','1993','0');
INSERT INTO [t19188] VALUES (2032,'كتاب الحجر','6281','0','0');
INSERT INTO [t19188] VALUES (2033,'[باب. . .]','6281','2032','0');
INSERT INTO [t19188] VALUES (2034,'١٩٠٣ - مسألة: (وهو على ضربين)','6281','2033','0');
INSERT INTO [t19188] VALUES (2035,'١٩٠٤ - مسألة: (ومن لزمه دين موجل، لم يطالب به قبل أجله)','6284','2033','0');
INSERT INTO [t19188] VALUES (2036,'١٩٠٥ - مسألة: (فإن أراد سفرا يحل الدين قبل مدته، فلغريمه منعه، إلا أن يوثقه برهن أو كفيل)','6284','2033','0');
INSERT INTO [t19188] VALUES (2037,'١٩٠٦ - مسألة: (فإن كان لا يحل)','6285','2033','0');
INSERT INTO [t19188] VALUES (2038,'١٩٠٧ - مسألة: (وإن كان حالا، وله مال يفي به، لم يحجر عليه)','6288','2033','0');
INSERT INTO [t19188] VALUES (2039,'١٩٠٨ - مسألة: (فإن أصر باعه الحاكم وقضى دينه)','6289','2033','0');
INSERT INTO [t19188] VALUES (2040,'١٩٠٩ - مسألة: (وإن ادعى الإعسار، وكان دينه عن عوض، كالبيع والقرض، أو عرف له مال سابق، حبس، إلا أن يقيم البينة على نفاد ماله وإعساره. وهل يحلف معها؟ على وجهين. وإن لم يكن كذلك، حلف وخلي سبيله)','6292','2033','0');
INSERT INTO [t19188] VALUES (2041,'١٩١٠ - مسألة: (وإن كان)','6301','2033','0');
INSERT INTO [t19188] VALUES (2042,'١٩١١ - مسألة: (ويستحب)','6302','2033','0');
INSERT INTO [t19188] VALUES (2043,'١٩١٢ - مسألة: (وإن تصرف في ذمته بشراء، أو ضمان، أو إقرار، صح. ويتبع به بعد فك الحجر عنه)','6307','2033','0');
INSERT INTO [t19188] VALUES (2044,'١٩١٣ - مسألة: (وإن جنى، شارك المجني عليه الغرماء، وإن جنى عبده، قدم المجني عليه بثمنه)','6308','2033','0');
INSERT INTO [t19188] VALUES (2045,'١٩١٤ - مسألة: (ولم يتعلق بها حق من شفعة، أو جناية، أو رهن)','6328','2033','0');
INSERT INTO [t19188] VALUES (2046,'١٩١٥ - مسألة: (ولم تزد زيادة متصلة، كالسمن، وتعلم صنعة)','6331','2033','0');
INSERT INTO [t19188] VALUES (2047,'١٩١٦ - مسألة: (فأما الزيادة المنفصلة، والنقص بهزال، أو نسيان صنعة، فلا يمنع الرجوع، والزيادة للمفلس. وعنه، للبائع)','6333','2033','0');
INSERT INTO [t19188] VALUES (2048,'١٩١٧ - مسألة: (وإن صبغ الثوب أو قصره','6339','2033','0');
INSERT INTO [t19188] VALUES (2049,'١٩١٨ - مسألة: (وإن غرس الأرض، أو بنى فيها، فله الرجوع ودفع قيمة الغراس والبناء، فيملكه، إلا أن يختار المفلس والغرماء القلع ومشاركته بالنقص)','6353','2033','0');
INSERT INTO [t19188] VALUES (2050,'١٩١٩ - مسألة: (وإن أبوا القلع، وأبى دفع القيمة، سقط الرجوع)','6356','2033','0');
INSERT INTO [t19188] VALUES (2051,'١٩٢٠ - مسألة: (ويترك له من ماله ما تدعو إليه حاجته من مسكن وخادم)','6367','2033','0');
INSERT INTO [t19188] VALUES (2052,'١٩٢١ - مسألة: (وينفق عليه بالمعروف إلى أن يفرغ من قسمه بين غرمائه)','6370','2033','0');
INSERT INTO [t19188] VALUES (2053,'١٩٢٢ - مسألة: (ويبدأ ببيع ما يسرع إليه الفساد)','6374','2033','0');
INSERT INTO [t19188] VALUES (2054,'١٩٢٣ - مسألة: (ويعطي المنادي أجرته من المال)','6374','2033','0');
INSERT INTO [t19188] VALUES (2055,'١٩٢٤ - مسألة: (ويبدأ بالمجني عليه، فيدفع إليه الأقل من الأرش أو ثمن الجاني)','6375','2033','0');
INSERT INTO [t19188] VALUES (2056,'١٩٢٥ - مسألة: (ثم بمن له رهن، فيخص بثمنه)','6375','2033','0');
INSERT INTO [t19188] VALUES (2057,'١٩٢٦ - مسألة: (ثم بمن له عين مال يأخذها)','6379','2033','0');
INSERT INTO [t19188] VALUES (2058,'١٩٢٧ - مسألة: (فإن كان فيهم من له دين مؤجل، لم يحل. وعنه، أنه يحل، فيشاركهم)','6380','2033','0');
INSERT INTO [t19188] VALUES (2059,'١٩٢٨ - مسألة: (ومن مات وعليه دين مؤجل، لم يحل إذا وثق الورثة. وعنه، أنه يحل)','6382','2033','0');
INSERT INTO [t19188] VALUES (2060,'١٩٢٩ - مسألة: (وإن ظهر غريم بعد قسم ماله، رجع على الغرماء بقسطه)','6393','2033','0');
INSERT INTO [t19188] VALUES (2061,'١٩٣٠ - مسألة: (وإن بقيت على المفلس بقية، وله صنعة، فهل يجبر على إيجار نفسه لقضائها؟ على روايتين)','6395','2033','0');
INSERT INTO [t19188] VALUES (2062,'١٩٣١ - مسألة: (ولا ينفك عنه الحجر إلا بحكم حاكم)','6399','2033','0');
INSERT INTO [t19188] VALUES (2063,'١٩٣٢ - مسألة: (ومتى فك عنه الحجر، فلزمته ديون)','6401','2033','0');
INSERT INTO [t19188] VALUES (2064,'١٩٣٣ - مسألة: (وإن كان للمفلس حق له به شاهد، فأبى أن يحلف معه، لم يكن لغرمائه أن يحلفوا)','6401','2033','0');
INSERT INTO [t19188] VALUES (2065,'١٩٣٤ - مسألة: (فلا يصح تصرفهم قبل الإذن)','6404','2033','0');
INSERT INTO [t19188] VALUES (2066,'١٩٣٥ - مسألة: (ومن دفع إليهم ماله ببيع أو قرض رجع فيه ما كان باقيا)','6404','2033','0');
INSERT INTO [t19188] VALUES (2067,'١٩٣٦ - [مسألة: (وإن جنوا فعليهم أرش الجناية)]','6406','2033','0');
INSERT INTO [t19188] VALUES (2068,'١٩٣٧ - مسألة: (ومتى عقل المجنون، وبلغ الصبي، ورشدا، انفك الحجر عنهما بغير حكم حاكم، ودفع إليهما مالهما، ولا ينفك قبل ذلك بحال)','6407','2033','0');
INSERT INTO [t19188] VALUES (2069,'١٩٣٨ - مسألة: (والبلوغ يحصل بالاحتلام، أو بلوغ خمس عشرة سنة، أو نبات الشعر الخشن حول القبل، وتزيد الجارية بالحيض والحمل)','6411','2033','0');
INSERT INTO [t19188] VALUES (2070,'١٩٣٩ - مسألة: (والرشد الصلاح في المال)','6418','2033','0');
INSERT INTO [t19188] VALUES (2071,'١٩٤٠ - مسألة: (ولا يدفع إليه ماله حتى يختبر)','6419','2033','0');
INSERT INTO [t19188] VALUES (2072,'١٩٤١ - مسألة: (وأن يحفظ ما في يده عن صرفه فيما لا فائدة فيه؛ كالغناء، والقمار، وشراء المحرمات)','6420','2033','0');
INSERT INTO [t19188] VALUES (2073,'١٩٤٢ - مسألة: (وعنه، لا يدفع إلى الجارية مالها بعد رشدها، حتى تتزوج وتلد، أو تقيم في بيت الزوج سنة)','6420','2033','0');
INSERT INTO [t19188] VALUES (2074,'١٩٤٣ - مسألة: (ووقت الاختبار قبل البلوغ)','6422','2033','0');
INSERT INTO [t19188] VALUES (2075,'١٩٤٤ - مسألة: (وليس لوليهما التصرف في مالهما، إلا على وجه الحظ لهما)','6425','2033','0');
INSERT INTO [t19188] VALUES (2076,'١٩٤٥ - مسألة: (ولا يجوز أن يشتري من مالهما شيئا لنفسه، ولا يبيعهما، إلا الأب)','6426','2033','0');
INSERT INTO [t19188] VALUES (2077,'١٩٤٦ - مسألة: (ولوليهما مكاتبة رقيقهما، وعتقه على مال)','6427','2033','0');
INSERT INTO [t19188] VALUES (2078,'١٩٤٧ - مسألة: (و)','6428','2033','0');
INSERT INTO [t19188] VALUES (2079,'١٩٤٨ - مسألة: (و)','6428','2033','0');
INSERT INTO [t19188] VALUES (2080,'١٩٤٩ - مسألة: (والربح كله لليتيم)','6431','2033','0');
INSERT INTO [t19188] VALUES (2081,'١٩٥٠ - مسألة: فأما إن دفعه إلى غيره، فللمضارب ما جعل له الولي ووافقه عليه في قولهم جميعا؛ لأن الوصي نائب عن اليتيم فيما فيه مصلحته، وهذا فيه مصلحته، فأشبه تصرف المالك في ماله','6432','2033','0');
INSERT INTO [t19188] VALUES (2082,'١٩٥١ - مسألة: (و)','6432','2033','0');
INSERT INTO [t19188] VALUES (2083,'١٩٥٢ - مسألة: (و)','6433','2033','0');
INSERT INTO [t19188] VALUES (2084,'١٩٥٣ - مسألة: (و)','6436','2033','0');
INSERT INTO [t19188] VALUES (2085,'١٩٥٤ - مسألة: (وله شراء الأضحية لليتيم الموسر. نص عليه)','6438','2033','0');
INSERT INTO [t19188] VALUES (2086,'١٩٥٥ - مسألة: (و)','6440','2033','0');
INSERT INTO [t19188] VALUES (2087,'١٩٥٦ - مسألة: (ولا يبيع عقارهم إلا لضرورة أو غبطة؛ وهو أن يزاد في ثمنه الثلث فصاعدا)','6440','2033','0');
INSERT INTO [t19188] VALUES (2088,'١٩٥٧ - مسألة: (وإن وصى لأحدهما بمن يعتق عليه ولا تلزمه نفقته، لإعسار الموصى له أو غير ذلك، وجب على الولي قبول الوصية)','6443','2033','0');
INSERT INTO [t19188] VALUES (2089,'١٩٥٨ - مسألة: (ولا ينظر في ماله إلا الحاكم)','6445','2033','0');
INSERT INTO [t19188] VALUES (2090,'١٩٥٩ - مسألة: (ولا ينفك عنه الحجر إلا بحكمه)','6445','2033','0');
INSERT INTO [t19188] VALUES (2091,'١٩٦٠ - مسألة: (ويستحب إظهار الحجر عليه)','6447','2033','0');
INSERT INTO [t19188] VALUES (2092,'١٩٦١ - مسألة: (ويصح تزويجه بإذن وليه)','6447','2033','0');
INSERT INTO [t19188] VALUES (2093,'١٩٦٢ - مسألة: (وهل يصح عتقه؟ على روايتين)','6450','2033','0');
INSERT INTO [t19188] VALUES (2094,'١٩٦٣ - مسألة: (وإن أقر بحد، أو قصاص، أو نسب، أو طلق زوجته، أخذ به)','6452','2033','0');
INSERT INTO [t19188] VALUES (2095,'١٩٦٤ - مسألة؛ قال: (وإن أقر بمال، لم يلزمه في حال حجره. ويحتمل أن لا يلزمه مطلقا)','6454','2033','0');
INSERT INTO [t19188] VALUES (2096,'١٩٦٥ - مسألة: (وحكم تصرف وليه حكم تصرف ولي الصبي والمجنون)','6456','2033','0');
INSERT INTO [t19188] VALUES (2097,'١٩٦٦ - مسألة؛ قال الشيخ، رحمه الله: (وللولي أن يأكل من مال المولى عليه بقدر عمله إذا احتاج إليه)','6457','2033','0');
INSERT INTO [t19188] VALUES (2098,'١٩٦٧ - مسألة: (وهل يلزمه عوض ذلك إذا أيسر؟ على روايتين)','6458','2033','0');
INSERT INTO [t19188] VALUES (2099,'١٩٦٨ - مسألة: (وكذلك يخرج في الناظر في الوقف)','6459','2033','0');
INSERT INTO [t19188] VALUES (2100,'١٩٦٩ - مسألة: (ومتى زال الحجر، فادعى على الولي تعديا أو ما يوجب ضمانا، فالقول قول الولي)','6461','2033','0');
INSERT INTO [t19188] VALUES (2101,'١٩٧٠ - مسألة: (وكذلك القول قوله في دفع المال إليه بعد رشده)','6462','2033','0');
INSERT INTO [t19188] VALUES (2102,'١٩٧١ - مسألة: (وهل للزوج أن يحجر على وامرأته في التبرع بما زاد على الثلث من مالها؟ على روايتين)','6463','2033','0');
INSERT INTO [t19188] VALUES (2103,'١٩٧٢ - مسألة: (ويجوز ذلك لسيد العبد)','6468','2033','0');
INSERT INTO [t19188] VALUES (2104,'١٩٧٣ - مسألة: (ولا ينفك عنهما الحجر، إلا فيما أذن لهما فيه،','6468','2033','0');
INSERT INTO [t19188] VALUES (2105,'١٩٧٤ - مسألة: (وإن أذن له في جميع أنواع التجارة، لم يجز أن يؤجر نفسه، ولا يتوكل لغيره)','6470','2033','0');
INSERT INTO [t19188] VALUES (2106,'١٩٧٥ - مسألة: (وهل له أن يوكل فيما يتولى مثله بنفسه؟ على روايتين)','6470','2033','0');
INSERT INTO [t19188] VALUES (2107,'١٩٧٦ - مسألة: (وإن رآه سيده أو وليه يتجر فلم ينهه، لم يصر مأذونا له)','6471','2033','0');
INSERT INTO [t19188] VALUES (2108,'١٩٧٧ - مسألة: (وما استدان العبد، فهو في رقبته، يفديه سيده أو يسلمه. وعنه، يتعلق بذمته، يتبع به بعد العتق، إلا المأذون له، هل يتعلق برقبته أو ذمة سيده؟ على روايتين)','6472','2033','0');
INSERT INTO [t19188] VALUES (2109,'١٩٧٨ - مسألة: (وإذا باع السيد عبده المأذون له شيئا، لم يصح','6478','2033','0');
INSERT INTO [t19188] VALUES (2110,'١٩٧٩ - مسألة: (ويصح إقرار المأذون له في قدر ما أذن له فيه)','6480','2033','0');
INSERT INTO [t19188] VALUES (2111,'١٩٨٠ - مسألة: (وإن حجر عليه وفي يدة مال، ثم أذن له فيه، فأقر به، صح)','6480','2033','0');
INSERT INTO [t19188] VALUES (2112,'١٩٨١ - مسألة: (ولا يبطل الإذن بالإباق)','6481','2033','0');
INSERT INTO [t19188] VALUES (2113,'١٩٨٢ - مسألة: (ولا يصح تبرع المأذون له بهبة الدراهم وكسوة الثياب)','6482','2033','0');
INSERT INTO [t19188] VALUES (2114,'١٩٨٣ - مسألة: (وتجوز هديته للمأكول، وإعارة دايته)','6482','2033','0');
INSERT INTO [t19188] VALUES (2115,'١٩٨٤ - مسألة: (وهل لغير المأذون له الصدقة من قوته بالرغيف ونحوه، إذا لم يضر به؟ على روايتين)','6483','2033','0');
INSERT INTO [t19188] VALUES (2116,'١٩٨٥ - مسألة: (وهل للمرأة الصدقة من بيت زوجها بغير إذنه بنحو ذلك؟ على روايتين)','6484','2033','0');
INSERT INTO [t19188] VALUES (2117,'باب الوكالة','6488','2032','0');
INSERT INTO [t19188] VALUES (2118,'١٩٨٦ - مسألة: (تصح الوكالة بكل قول. يدل على الإذن، وكل قول أو فعل يدل على القبول)','6489','2117','0');
INSERT INTO [t19188] VALUES (2119,'١٩٨٧ - مسألة: (ويجوز القبول على الفور والتراخي، نحو أن يوكله في بيع شيء، فيبيعه بعد سنة، أو يبلغه أنه وكله منذ شهر، فيقول: قبلت)','6491','2117','0');
INSERT INTO [t19188] VALUES (2120,'١٩٨٨ - مسألة: (ولا يصح التوكيل والتوكل في شيء، إلا ممن يصح تصرفه فيه)','6493','2117','0');
INSERT INTO [t19188] VALUES (2121,'١٩٨٩ - مسألة: (ويجوز التوكيل في كل حق آدمي من العقود،','6495','2117','0');
INSERT INTO [t19188] VALUES (2122,'١٩٩٠ - مسألة: (ويجوز أن يوكل من يقبل له النكاح، ومن يزوج وليته إذا كان الوكيل ممن يصح منه ذلك لنفسه وموليته)','6499','2117','0');
INSERT INTO [t19188] VALUES (2123,'١٩٩١ - مسألة: (ويجوز في كل حق لله تعالى تدخله النيابة، من العبادات والحدود، في إثباتها واستيفائها)','6503','2117','0');
INSERT INTO [t19188] VALUES (2124,'١٩٩٢ - مسألة: (ويجوز الاستيفاء في حضرة الموكل وغيبته)','6506','2117','0');
INSERT INTO [t19188] VALUES (2125,'١٩٩٣ - مسألة: (ولا يجوز للوكيل أن يوكل فيما يتولى مثله بنفسه، إلا بإذن الموكل. وعنه، يجوز. وكذلك الوصي والحاكم. وله التوكيل فيما لا يتولى مثله بنفسه، أو يعجز عنه لكثرته وجملة ذلك، أن التوكيل لا يخلو من ثلاثة أحوال، أحدها، أن ينهى الموكل وكيله عن التوكيل، فلا يجوز له ذلك بغير خلاف، لأن ما نهاه عنه غير داخل في إذنه، فلم يجز له، كما لو لم يوكله. الثاني، أن','6507','2117','0');
INSERT INTO [t19188] VALUES (2126,'١٩٩٤ - مسألة: (ويجوز توكيل عبد غيره بإذن سيده)','6514','2117','0');
INSERT INTO [t19188] VALUES (2127,'١٩٩٥ - مسألة: (فإن وكله بإذنه في شراء نفسه من سيده، فعلى روايتين)','6515','2117','0');
INSERT INTO [t19188] VALUES (2128,'١٩٩٦ - مسألة: (والوكالة عقد جائز من الطرفين، لكل واحد منهما فسخها)','6518','2117','0');
INSERT INTO [t19188] VALUES (2129,'١٩٩٧ - مسألة: (وتبطل بالموت، والجنون، والحجر للسفه. وكذلك كل عقد جائز، كالشركة والمضاربة. ولا تبطل بالسكر، والإغماء، والتعدي)','6519','2117','0');
INSERT INTO [t19188] VALUES (2130,'١٩٩٨ - مسألة: (وهل تبطل بالردة، وحرية العبد؟ على وجهين)','6524','2117','0');
INSERT INTO [t19188] VALUES (2131,'١٩٩٩ - مسألة: (وهل ينعزل الوكيل بالموت والعزل قبل علمه؟ على روايتين)','6529','2117','0');
INSERT INTO [t19188] VALUES (2132,'٢٠٠٠ - مسألة: (وإذا وكل اثنين، لم يكن لأحدهما الانفراد','6533','2117','0');
INSERT INTO [t19188] VALUES (2133,'٢٠٠١ - مسألة: (ولا يجوز للوكيل في البيع أن يبيع لنفسه)','6536','2117','0');
INSERT INTO [t19188] VALUES (2134,'٢٠٠٢ - مسألة: (وهل يجوز أن يبيعه لولده، أو والده، أو مكاتبه؟ على وجهين)','6541','2117','0');
INSERT INTO [t19188] VALUES (2135,'٢٠٠٣ - مسألة: (ولا يجوز أن يبيع نساء، ولا بغير نقد البلد. ويحتمل أن يجوز، كالمضارب)','6543','2117','0');
INSERT INTO [t19188] VALUES (2136,'٢٠٠٤ - مسألة: (وإن باع بدون ثمن المثل، أو بأنقص مما قدره له، صح، وضمن النقص. ويحتمل أن لا يصح)','6545','2117','0');
INSERT INTO [t19188] VALUES (2137,'٢٠٠٥ - مسألة: (وإن باع بأكثر)','6549','2117','0');
INSERT INTO [t19188] VALUES (2138,'٢٠٠٦ - مسألة: (وإن قال: بعه بدرهم. فباعه بدينار، صح، في أحد الوجهين)','6550','2117','0');
INSERT INTO [t19188] VALUES (2139,'٢٠٠٧ - مسألة: (وإن قال: بعه بألف نساء. فباعه بألف حالة، صح، إن كان لا يستضر بحفظ الثمن في الحال)','6552','2117','0');
INSERT INTO [t19188] VALUES (2140,'٢٠٠٨ - مسألة: (وإن وكله في الشراء، فاشترى بأكثر من ثمن المثل، أو بأكثر مما قدره له أو وكله في بيع شيء، فباع نصفه بدون ثمن الكل، لم يصح)','6554','2117','0');
INSERT INTO [t19188] VALUES (2141,'٢٠٠٩ - مسألة: وإن وكله في شراء شيء نقدا بثمن معين، فاشتراه به مؤجلا، صح. ذكره القاضي؛ لأنه زاده خيرا، فأشبه ما لو وكله في الشراء بمائة، فاشترى بدونها. ويحتمل أن ينظر في ذلك، فإن كان فيه ضرر نحو','6558','2117','0');
INSERT INTO [t19188] VALUES (2142,'٢٠١٠ - مسألة: وإن (قال: اشتر لي شاة بدينار. فاشترى)','6558','2117','0');
INSERT INTO [t19188] VALUES (2143,'٢٠١١ - مسألة: (وليس له شراء معيب، فإن وجد بما اشتراه عيبا، فله رده)','6563','2117','0');
INSERT INTO [t19188] VALUES (2144,'٢٠١٢ - مسألة: (فإن قال البائع: موكلك قد رضي بالعيب.','6564','2117','0');
INSERT INTO [t19188] VALUES (2145,'٢٠١٣ - مسألة: (فإن رده، فصدق الموكل البائع في الرضا بالعيب، فهل يصح الرد؟ على وجهين)','6565','2117','0');
INSERT INTO [t19188] VALUES (2146,'٢٠١٤ - مسألة: (وإن وكله في شراء معين، فاشتراه فوجده معيبا، فهل له رده قبل إعلام الموكل؟ على وجهين)','6567','2117','0');
INSERT INTO [t19188] VALUES (2147,'٢٠١٥ - مسألة: (فإن قال: اشتر لي بعين هذا الثمن. فاشترى له في ذمته، لم يلزم الموكل)','6569','2117','0');
INSERT INTO [t19188] VALUES (2148,'٢٠١٦ - مسألة: (فإن قال: اشتر لي في ذمتك، وانقد الثمن. فاشترى بعينه، صح)','6570','2117','0');
INSERT INTO [t19188] VALUES (2149,'٢٠١٧ - مسألة: (وإن أمره ببيعه في سوق بثمن، فباعه به في آخر، صح. وإن قال: بعه من زيد. فباعه من غيره، لم يصح)','6571','2117','0');
INSERT INTO [t19188] VALUES (2150,'٢٠١٨ - مسألة: (وإن وكله في بيع شيء، ملك تسليمه، ولم يملك قبض ثمنه إلا بقرينة. فإن تعذر قبضه، لم يلزم الوكيل شيء)','6574','2117','0');
INSERT INTO [t19188] VALUES (2151,'٢٠١٩ - مسألة: (وإن وكله في بيع فاسد، لم يصح)','6577','2117','0');
INSERT INTO [t19188] VALUES (2152,'٢٠٢٠ - مسألة: (و)','6578','2117','0');
INSERT INTO [t19188] VALUES (2153,'٢٠٢١ - مسألة: (وإن وكله في بيع ماله كله، صح)','6579','2117','0');
INSERT INTO [t19188] VALUES (2154,'٢٠٢٢ - مسألة: (وإن قال: اشتر لي ما شئت. أو: عبدا بما شئت. لم يصح)','6579','2117','0');
INSERT INTO [t19188] VALUES (2155,'٢٠٢٣ - مسألة: (وإن وكله في الخصومة، لم يكن وكيلا في القبض)','6581','2117','0');
INSERT INTO [t19188] VALUES (2156,'٢٠٢٤ - مسألة: (وإن وكله في القبض، كان وكيلا في','6582','2117','0');
INSERT INTO [t19188] VALUES (2157,'٢٠٢٥ - مسألة: (وإن وكله في قبض الحق من إنسان، لم يكن له قبضه من وارثه. وإن قال: اقبض حقي الذي قبله. فله القبض من وارثه)','6585','2117','0');
INSERT INTO [t19188] VALUES (2158,'٢٠٢٦ - مسألة: (وإن وكله في قبضه اليوم، لم يكن له قبضه غدا)','6585','2117','0');
INSERT INTO [t19188] VALUES (2159,'٢٠٢٧ - مسألة: (وإن وكله في الإيداع، فأودع ولم يشهد، لم يضمن)','6586','2117','0');
INSERT INTO [t19188] VALUES (2160,'٢٠٢٨ - مسألة: (وإن وكله في قضاء دين، فقضاه ولم يشهد، وأنكر الغريم، ضمن، إلا أن يقضيه بحضرة الموكل)','6586','2117','0');
INSERT INTO [t19188] VALUES (2161,'٢٠٢٩ - مسألة: (ولو قال: بعت الثوب، وقبضت الثمن فتلف. فالقول قوله)','6591','2117','0');
INSERT INTO [t19188] VALUES (2162,'٢٠٣٠ - مسألة: (وإن اختلفا في رده إلى الموكل، فالقول قوله إن كان متطوعا. وإن كان بجعل، فعلى وجهين)','6593','2117','0');
INSERT INTO [t19188] VALUES (2163,'٢٠٣١ - مسألة: (وكذلك يخرج في الأجير والمرتهن)','6594','2117','0');
INSERT INTO [t19188] VALUES (2164,'٢٠٣٢ - مسألة: (فإن قال: أذنت لي في البيع نساء، وفي الشراء بخمسة. فأنكره، فعلى وجهين)','6596','2117','0');
INSERT INTO [t19188] VALUES (2165,'٢٠٣٣ - مسألة: (وإن قال: أذنت لي أن أتزوج لك فلانة، ففعلت. وصدقته المرأة، فأنكر، فالقول قول المنكر بغير يمين. وهل يلزم الوكيل نصف الصداق؟ على وجهين)','6604','2117','0');
INSERT INTO [t19188] VALUES (2166,'٢٠٣٤ - مسألة: (ويجوز التوكيل بجعل وبغيره، فلو قال: بع ثوبي بعشرة فما زاد فلك. صح)','6609','2117','0');
INSERT INTO [t19188] VALUES (2167,'٢٠٣٥ - مسألة: (وإن كذبه، لم يستحلف)','6616','2117','0');
INSERT INTO [t19188] VALUES (2168,'٢٠٣٦ - مسألة: (فإن دفعه إليه، فأنكر صاحب الحق الوكالة، وحلف، رجع على الدافع وحده)','6616','2117','0');
INSERT INTO [t19188] VALUES (2169,'٢٠٣٧ - مسألة: (وإن كان المدفوع وديعة، فوجدها، أخذها، وإن تلفت، فله تضمين من شاء منهما، ولا يرجع من ضمنه على الآخر)','6618','2117','0');
INSERT INTO [t19188] VALUES (2170,'٢٠٣٨ - مسألة: (فإن كان ادعى أن صاحب الحق أحاله، ففي وجوب الدفع إليه مع التصديق، واليمين مع الإنكار وجهان)','6619','2117','0');
INSERT INTO [t19188] VALUES (2171,'٢٠٣٩ - مسألة: (وإن ادعى أنه مات، وأنا وارثه)','6620','2117','0');
INSERT INTO [t19188] VALUES (2172,'كتاب الشركة','6633','0','0');
INSERT INTO [t19188] VALUES (2173,'[باب. . .]','6633','2172','0');
INSERT INTO [t19188] VALUES (2174,'٢٠٤٠ - مسألة: (وهي على خمسمة أضرب؛ أحدها، شركة','6634','2173','0');
INSERT INTO [t19188] VALUES (2175,'٢٠٤١ - مسألة: (ولا تصح إلا بشرطين؛ أحدهما، أن يكون رأس المال دراهم أو دنانير)','6639','2173','0');
INSERT INTO [t19188] VALUES (2176,'٢٠٤٢ - مسألة: (وهل تصح بالمغشوش والفلوس؟ على وجهين)','6642','2173','0');
INSERT INTO [t19188] VALUES (2177,'٢٠٤٣ - مسألة: الشرط (الثاني، أن يشرطا لكل واحد)','6645','2173','0');
INSERT INTO [t19188] VALUES (2178,'٢٠٤٤ - مسألة: (فإن قالا: الربح بيننا. فهو بينهما نصفين)','6646','2173','0');
INSERT INTO [t19188] VALUES (2179,'٢٠٤٥ - مسألة: (فإن لم يذكرا الربح)','6646','2173','0');
INSERT INTO [t19188] VALUES (2180,'٢٠٤٦ - مسألة: وإن (شرطا لأحدهما جزءا مجهولا)','6647','2173','0');
INSERT INTO [t19188] VALUES (2181,'٢٠٤٧ - مسألة: فإن شرطا لأحدهما في الشركة أو','6647','2173','0');
INSERT INTO [t19188] VALUES (2182,'٢٠٤٨ - مسألة: (وكذلك الحكم في المساقاة والمزارعة)','6649','2173','0');
INSERT INTO [t19188] VALUES (2183,'٢٠٤٩ - مسألة: (ولا يشترط أن يخلطا المالين، ولا أن يكونا من جنس واحد)','6649','2173','0');
INSERT INTO [t19188] VALUES (2184,'٢٠٥٠ - مسألة: (وما يشتريه كل واحد منهما بعد عقد الشركة، فهو بينهما)','6652','2173','0');
INSERT INTO [t19188] VALUES (2185,'٢٠٥١ - مسألة: (وإن تلف أحد المالين، فهو من ضمانهما)','6652','2173','0');
INSERT INTO [t19188] VALUES (2186,'٢٠٥٢ - مسألة: (والوضيعة على قدر المال)','6653','2173','0');
INSERT INTO [t19188] VALUES (2187,'٢٠٥٣ - مسألة: (وليس له أن يكاتب الرقيق، ولا يزوجه، ولا يعتقه على مال)','6656','2173','0');
INSERT INTO [t19188] VALUES (2188,'٢٠٥٤ - مسألة: (ولا يضارب بالمال، ولا يأخذ به سفتجة، ولا يعطها، إلا بإذن شريكه)','6657','2173','0');
INSERT INTO [t19188] VALUES (2189,'٢٠٥٥ - مسألة: (وهل له أن يودع، أو يبيع نساء، أو يبضع، أو يوكل فيما يتولى مثله)','6659','2173','0');
INSERT INTO [t19188] VALUES (2190,'٢٠٥٦ - مسألة: (وليس له أن يستدين على)','6665','2173','0');
INSERT INTO [t19188] VALUES (2191,'٢٠٥٧ - مسألة.: (وإن أخر حقه من الدين، جاز)','6666','2173','0');
INSERT INTO [t19188] VALUES (2192,'٢٠٥٨ - مسألة: (وإن تقاسما الدين في الذمة، لم يصح)','6667','2173','0');
INSERT INTO [t19188] VALUES (2193,'٢٠٥٩ - مسألة: (وإن أبرأ من الدين، لزم في حقه دون صاحبه)','6668','2173','0');
INSERT INTO [t19188] VALUES (2194,'٢٠٦٠ - مسألة: (وكذلك إن أقر بمال)','6668','2173','0');
INSERT INTO [t19188] VALUES (2195,'٢٠٦١ - مسألة: (وعلى كل واحد منهما أن يتولى ما جرت العادة أن يتولاه؛ من نشر الثوب وطيه، وختم الكيس وإحرازه)','6670','2173','0');
INSERT INTO [t19188] VALUES (2196,'٢٠٦٢ - مسألة: (فإن فعله ليأخذ أجرته، فهل له ذلك؟ على وجهين)','6671','2173','0');
INSERT INTO [t19188] VALUES (2197,'٢٠٦٣ - مسألة: (وفاسد، مثل أن يشترط ما يعود بجهالة الربح، أو ضمان المال، أو أن عليه من الوضيعة أكثر من قدر ماله، أو أن يوليه ما يختار من السلع، أو يرتفق بها، أو)','6674','2173','0');
INSERT INTO [t19188] VALUES (2198,'٢٠٦٤ - مسألة: (وإذا فسد العقد، قسم الربح على قدر المالين)','6676','2173','0');
INSERT INTO [t19188] VALUES (2199,'٢٠٦٥ - مسألة: (فإن قال: خذه فاتجر به، والربح كله لي. فهو إبضاع)','6684','2173','0');
INSERT INTO [t19188] VALUES (2200,'٢٠٦٦ - مسألة: (وإن قال: والربح كله لك. فهو قرض)','6684','2173','0');
INSERT INTO [t19188] VALUES (2201,'٢٠٦٧ - مسألة: (وإن قال: والربح بيننا. فهو بينهما نصفين)','6684','2173','0');
INSERT INTO [t19188] VALUES (2202,'٢٠٦٨ - مسألة: (وإن قال: خذه مضاربة، والربح كله لك أو لي. لم يصح)','6685','2173','0');
INSERT INTO [t19188] VALUES (2203,'٢٠٦٩ - مسألة: (وإن قال: لك ثلث الربح. صح، والباقي لرب المال)','6686','2173','0');
INSERT INTO [t19188] VALUES (2204,'٢٠٧٠ - مسألة: (وإن قال: ولي ثلث الربح)','6686','2173','0');
INSERT INTO [t19188] VALUES (2205,'٢٠٧١ - مسألة: (وإن اختلفا)','6688','2173','0');
INSERT INTO [t19188] VALUES (2206,'٢٠٧٢ - [مسألة: (وكذلك حكم المساقاة والمزارعة) قياسا عليها]','6688','2173','0');
INSERT INTO [t19188] VALUES (2207,'٢٠٧٣ - مسألة: (وحكم المضاربة حكم الشركة فيما للعامل أن يفعله أو لا يفعله، وفيما يلزمه فعله، وفي الشروط)','6691','2173','0');
INSERT INTO [t19188] VALUES (2208,'٢٠٧٤ - مسألة: (وإذا فسدت، فالربح لرب المال، وللعامل الأجرة. وعنه، له الأقل من الأجرة أو ما شرط له من الربح)','6692','2173','0');
INSERT INTO [t19188] VALUES (2209,'٢٠٧٥ - مسألة: (وإن شرطا تأقيت المضاربة، فهل تفسد؟ على روايتين)','6694','2173','0');
INSERT INTO [t19188] VALUES (2210,'٢٠٧٦ - مسألة: (وإن قال: بع هذا العرض وضارب بثمنه. أو: اقبض وديعتي وضارب بها. أو: إذا قدم الحاج فضارب بهذا. صح)','6696','2173','0');
INSERT INTO [t19188] VALUES (2211,'٢٠٧٧ - مسألة: (وإن قال: ضارب بالدين الذي عليك. لم يصح)','6698','2173','0');
INSERT INTO [t19188] VALUES (2212,'٢٠٧٨ - مسألة: (وإن أخرج مالا ليعمل فيه هو وآخر، والربح بينهما، صح. ذكره الخرقي)','6700','2173','0');
INSERT INTO [t19188] VALUES (2213,'٢٠٧٩ - مسألة: (وإن اشترى امرأته، صح، وانفسخ نكاحهما)','6713','2173','0');
INSERT INTO [t19188] VALUES (2214,'٢٠٨٠ - مسألة: (وإن اشترى)','6715','2173','0');
INSERT INTO [t19188] VALUES (2215,'٢٠٨١ - مسألة: (وليس للمضارب أن يضارب لآخر، إذا كان فيه ضرر على الأول. فإن فعل، رد نصيبه من الربح في شركة الأول)','6723','2173','0');
INSERT INTO [t19188] VALUES (2216,'٢٠٨٢ - مسألة: (وليس لرب المال أن يشتري من مال المضاربة شيئا لنفسه. وعنه، يجوز)','6732','2173','0');
INSERT INTO [t19188] VALUES (2217,'٢٠٨٣ - مسألة: (وكذلك شراء السيد من عبده المأذون)','6732','2173','0');
INSERT INTO [t19188] VALUES (2218,'٢٠٨٤ - مسألة: (وإن اشترى أحد الشريكين نصيب شريكه، صح)','6734','2173','0');
INSERT INTO [t19188] VALUES (2219,'٢٠٨٥ - مسألة: (وإن اشترى الجميع، بطل في نصيبه)','6734','2173','0');
INSERT INTO [t19188] VALUES (2220,'٢٠٨٦ - مسألة: (وليس للمضارب نفقة إلا بشرط)','6735','2173','0');
INSERT INTO [t19188] VALUES (2221,'٢٠٨٧ - مسألة: (فإن اختلفا)','6738','2173','0');
INSERT INTO [t19188] VALUES (2222,'٢٠٨٨ - مسألة: (فإن أذن له في التسري، فاشترى جارية، ملكها، وصار ثمنها قرضا. نص عليه)','6739','2173','0');
INSERT INTO [t19188] VALUES (2223,'٢٠٨٩ - مسألة: (وليس للمضارب ربح حتى يستوفي رأس المال)','6741','2173','0');
INSERT INTO [t19188] VALUES (2224,'٢٠٩٠ - مسألة: (وإن اشتري سلعتين، فربح في إحداهما، وخسر في الأخرى، أو تلفت، جبرت الوضيعة من الربح)','6744','2173','0');
INSERT INTO [t19188] VALUES (2225,'٢٠٩١ - مسألة: (وإن تلف بعض رأس المال قبل التصرف فيه، انفسخت فيه المضاربة)','6745','2173','0');
INSERT INTO [t19188] VALUES (2226,'٢٠٩٢ - مسألة: (وإن تلف المال، ثم اشترى سلعة للمضاربة، فهي له، وثمنها عليه)','6746','2173','0');
INSERT INTO [t19188] VALUES (2227,'٢٠٩٣ - مسألة: (وإن تلف بعد الشراء، فالمضاربة بحالها، والثمن على رب المال)','6747','2173','0');
INSERT INTO [t19188] VALUES (2228,'٢٠٩٤ - مسألة: (وإذا ظهر الربح، لم يكن للعامل أخذ شيء، إلا بإذن رب المال)','6750','2173','0');
INSERT INTO [t19188] VALUES (2229,'٢٠٩٥ - مسألة: (وإن طلب العامل البيع، فأبى رب المال، أجبر إن كان فيه ربح، وإلا فلا)','6751','2173','0');
INSERT INTO [t19188] VALUES (2230,'٢٠٩٦ - مسألة: (وإن انفسخ القراض، والمال عرض، فرضي رب المال أن يأخذ بماله عرضا، أو طلب البيع، فله ذلك)','6752','2173','0');
INSERT INTO [t19188] VALUES (2231,'٢٠٩٧ - مسألة: (وإن كان دينا، لزم العامل تقاضيه)','6755','2173','0');
INSERT INTO [t19188] VALUES (2232,'٢٠٩٨ - مسألة: (وإن قارض في المرض، فالربح من رأس المال وإن زاد على أجر المثل)','6758','2173','0');
INSERT INTO [t19188] VALUES (2233,'٢٠٩٩ - مسألة: (ويقدم به على سائر الغرماء)','6759','2173','0');
INSERT INTO [t19188] VALUES (2234,'٢١٠٠ - مسألة: (وإن مات المضارب، ولم يعرف مال','6759','2173','0');
INSERT INTO [t19188] VALUES (2235,'٢١٠١ - مسألة: (والقول قول رب المال في رده إليه)','6764','2173','0');
INSERT INTO [t19188] VALUES (2236,'٢١٠٢ - مسألة: (وفي الجزء المشروط للعامل)','6765','2173','0');
INSERT INTO [t19188] VALUES (2237,'٢١٠٣ - مسألة: وإن قال: أذنت لي في البيع نساء، وفي الشراء','6766','2173','0');
INSERT INTO [t19188] VALUES (2238,'٢١٠٤ - مسألة: (وإن قال: ربحت ألفا ثم خسرتها)','6768','2173','0');
INSERT INTO [t19188] VALUES (2239,'٢١٠٥ - مسألة: (وإن قال: غلطت)','6768','2173','0');
INSERT INTO [t19188] VALUES (2240,'٢١٠٦ - مسألة: (والملك بينهما على ما شرطاه)','6780','2173','0');
INSERT INTO [t19188] VALUES (2241,'٢١٠٧ - مسألة: (وهما في التصرفات كشريكي العنان)','6782','2173','0');
INSERT INTO [t19188] VALUES (2242,'٢١٠٨ - مسألة: وتصح مع اتفاق الصنائع رواية واحدة','6785','2173','0');
INSERT INTO [t19188] VALUES (2243,'٢١٠٩ - مسألة: (وإن مرض أحدهما، فالكسب بينهما. فإن طالبه الصحيح أن يقيم مقامه، لزمه ذلك)','6788','2173','0');
INSERT INTO [t19188] VALUES (2244,'٢١١٠ - مسألة: (وإن اشتركا على أن يحملا على دابتيهما والأجرة بينهما، صح)','6789','2173','0');
INSERT INTO [t19188] VALUES (2245,'٢١١١ - مسألة: (فإذا تقبلا حمل شيء، فحملاه عليهما)','6789','2173','0');
INSERT INTO [t19188] VALUES (2246,'٢١١٢ - مسألة: (وإن أجراهما بأعيانهما، فلكل واحد منهما أجرة دابته)','6791','2173','0');
INSERT INTO [t19188] VALUES (2247,'٢١١٣ - مسألة: (وإن جمعا بين شركة العنان والأبدان والوجوه والمضاربة، صح)','6799','2173','0');
INSERT INTO [t19188] VALUES (2248,'باب المساقاة','6803','2172','0');
INSERT INTO [t19188] VALUES (2249,'٢١١٤ - مسألة: (تجوز المساقاة في النخل، وفي كل شجر له ثمر مأكول ببعض ثمرته)','6804','2248','0');
INSERT INTO [t19188] VALUES (2250,'٢١١٥ - مسألة: (وتصح بلفظ المساقاة)','6809','2248','0');
INSERT INTO [t19188] VALUES (2251,'٢١١٦ - مسألة: (وتصح بلفظ الإجارة، في أحد الوجهين)','6810','2248','0');
INSERT INTO [t19188] VALUES (2252,'٢١١٧ - مسألة: (وقد نص أحمد في رواية جماعة، في من قال','6810','2248','0');
INSERT INTO [t19188] VALUES (2253,'٢١١٨ - مسألة: (وهل تصح على ثمرة موجودة؟ على روايتين)','6812','2248','0');
INSERT INTO [t19188] VALUES (2254,'٢١١٩ - مسألة: (وإن ساقاه على شجر يغرسه ويعمل عليه حتى يثمر بجزء من الثمرة، صح)','6815','2248','0');
INSERT INTO [t19188] VALUES (2255,'٢١٢٠ - مسألة: (والمساقاة عقد جائز في ظاهر كلامه)','6821','2248','0');
INSERT INTO [t19188] VALUES (2256,'٢١٢١ - مسألة: فإن قلنا: هي عقد لازم. فلا يصح إلا على مدة معلومة','6825','2248','0');
INSERT INTO [t19188] VALUES (2257,'٢١٢٢ - مسألة: فإن شرطا مدة لا تكمل فيها، لم تصح','6826','2248','0');
INSERT INTO [t19188] VALUES (2258,'٢١٢٣ - مسألة: وإن شرطا مدة قد تكمل فيها الثمرة وقد لا تكمل','6826','2248','0');
INSERT INTO [t19188] VALUES (2259,'٢١٢٤ - مسألة: (وإن مات العامل، تمم الوارث)','6828','2248','0');
INSERT INTO [t19188] VALUES (2260,'٢١٢٥ - مسألة: (فإن فسخ بعد ظهور الثمرة، فهي بينهما، وإن فسخ قبله، فهل للعامل أجرة؟ على وجهين)','6829','2248','0');
INSERT INTO [t19188] VALUES (2261,'٢١٢٦ - مسألة: (وكذلك إن هرب العامل ولم يوجد له ما ينفق عليها)','6831','2248','0');
INSERT INTO [t19188] VALUES (2262,'٢١٢٧ - مسألة: (فإن عمل فيها رب المال بإذن حاكم أو إشهاد، رجع به، وإلا فلا)','6832','2248','0');
INSERT INTO [t19188] VALUES (2263,'٢١٢٨ - مسألة: (وعلى رب المال ما فيه حفظ الأصل من سد الحيطان، وإجراء الأنهار، وحفر البئر والدولاب وما يديره)','6835','2248','0');
INSERT INTO [t19188] VALUES (2264,'٢١٢٩ - مسألة: (وحكم العامل حكم المضارب فيما يقبل قوله فيه و)','6839','2248','0');
INSERT INTO [t19188] VALUES (2265,'٢١٣٠ - مسألة: (وإذا شرط إن سقى سيحا فله الربع، وإن سقى بكلفة فله النصف، أو إن زرعها شعيرا فله الربع، وإن زرعها حنطة فله النصف، لم يصح، في أحد الوجهين)','6845','2248','0');
INSERT INTO [t19188] VALUES (2266,'٢١٣١ - مسألة: (وإن قال: ما زرعت من شعير فلي ربعه، وما زرعت من حنطة فلي نصفه)','6847','2248','0');
INSERT INTO [t19188] VALUES (2267,'٢١٣٢ - مسألة: ولو قال: (ساقيتك هذا البستان بالنصف، على أن أساقيك الآخر بالربع. لم يصح، وجها واحدا)','6847','2248','0');
INSERT INTO [t19188] VALUES (2268,'فصل في المزارعة','6850','2248','0');
INSERT INTO [t19188] VALUES (2269,'٢١٣٣ - مسألة: (وتجوز المزارعة بجزء معلوم يجعل للعامل من الزرع)','6850','2248','0');
INSERT INTO [t19188] VALUES (2270,'٢١٣٤ - مسألة: (فإن كان في الأرض شجر، فزارعه الأرض، وساقاه على الشجر، صح)','6857','2248','0');
INSERT INTO [t19188] VALUES (2271,'٢١٣٥ - مسألة: (ولا يشترط كون البذر من رب الأرض. وظاهر المذهب اشتراطه)','6861','2248','0');
INSERT INTO [t19188] VALUES (2272,'٢١٣٦ - مسألة: (وإن شرط أن يأخذ رب الأرض مثل بذره، ويقتسما الباقي)','6866','2248','0');
INSERT INTO [t19188] VALUES (2273,'٢١٣٧ - مسألة: وكذلك لو شرطا لأحدهما (دراهم معلومة، أو زرع ناحية معينة)','6866','2248','0');
INSERT INTO [t19188] VALUES (2274,'٢١٣٨ - مسألة: (ومتى فسدت، فالزرع لصاحب البذر)','6867','2248','0');
INSERT INTO [t19188] VALUES (2275,'٢١٣٩ - مسألة: (وحكم المزارعة حكم المساقاة فيما ذكرنا)','6867','2248','0');
INSERT INTO [t19188] VALUES (2276,'٢١٤٠ - مسألة: (والحصاد على العامل. نص عليه. وكذلك الجداد. وعنه، أن الجداد عليهما)','6868','2248','0');
INSERT INTO [t19188] VALUES (2277,'٢١٤١ - مسألة: (وإن قال: أنا أزرع الأرض ببذري وعواملي، وتسقيها بمائك، والزرع بيننا)','6870','2248','0');
INSERT INTO [t19188] VALUES (2278,'٢١٤٢ - مسألة: (وإن زارع شريكه في نصيبه، صح)','6873','2248','0');
INSERT INTO [t19188] VALUES (2279,'فصل في إجارة الأرض','6875','2248','0');
INSERT INTO [t19188] VALUES (2280,'باب الإجارة','6879','2172','0');
INSERT INTO [t19188] VALUES (2281,'٢١٤٣ - مسألة: (وهي عقد على المنافع، تنعقد بلفظ الإجارة، والكراء، وما في معناهما، وفي لفظ البيع وجهان)','6882','2280','0');
INSERT INTO [t19188] VALUES (2282,'٢١٤٤ - مسألة: (ولا تصح إلا بشروط ثلاثة؛ أحدها، معرفة المنفعة، إما بالعرف، كسكنى الدار شهرا، وخدمة العبد سنة، وإما','6884','2280','0');
INSERT INTO [t19188] VALUES (2283,'٢١٤٥ - مسألة: (أو بناء حائط يذكر طوله وعرضه وسمكه وآلته، وإجارة أرض معينة لزرع كذا، أو غرس، أو بناء معلوم)','6887','2280','0');
INSERT INTO [t19188] VALUES (2284,'٢١٤٦ - مسألة: (وإن استأجر للركوب، ذكر المركوب، فرسا أو بعيرا أو نحوه)','6891','2280','0');
INSERT INTO [t19188] VALUES (2285,'٢١٤٧ - مسألة: (فإن كان للحمل، لم يحتج إلى ذكره)','6893','2280','0');
INSERT INTO [t19188] VALUES (2286,'٢١٤٨ - مسألة: (إلا أنه يجوز أن يستأجر الأجير بطعامه وكسوته، وكذلك الظئر)','6897','2280','0');
INSERT INTO [t19188] VALUES (2287,'٢١٤٩ - مسألة: (ويستحب أن تعطى عند الفطام عبدا أو وليدة، إذا كان المسترضع موسرا)','6905','2280','0');
INSERT INTO [t19188] VALUES (2288,'٢١٥٠ - مسألة: (وإن دفع ثوبه إلى خياط أو قصار ليعملاه، ولهما عادة بأجرة، صح، ولهما ذلك وإن لم يعقدا عقد إجارة. وكذلك دخول الحمام، والركوب في سفينة الملاح)','6910','2280','0');
INSERT INTO [t19188] VALUES (2289,'٢١٥١ - مسألة: (ويجوز إجارة دار بسكنى دار، وخدمة عبد، وتزويج امرأة)','6913','2280','0');
INSERT INTO [t19188] VALUES (2290,'٢١٥٢ - مسألة: (وتجوز إجارة الحلي بأجرة من جنسه.','6914','2280','0');
INSERT INTO [t19188] VALUES (2291,'٢١٥٣ - مسألة: (وإن قال: إن خطت هذا الثوب اليوم فلك درهم، وإن خطته غدا فلك نصف درهم. فهل يصح؟ على روايتين)','6917','2280','0');
INSERT INTO [t19188] VALUES (2292,'٢١٥٤ - مسألة: (وإن قال: إن خطته روميا فلك درهم، وإن خطته فارسيا فلك نصف درهم)','6918','2280','0');
INSERT INTO [t19188] VALUES (2293,'٢١٥٥ - مسألة: (وإن أكراه دابة، وقال: إن رددتها اليوم فكراؤها خمسة، وإن رددتها غدا فكراؤها عشرة. فقال أحمد: لا بأس به. وقال القاضي: تصح في اليوم الأول دون الثاني)','6920','2280','0');
INSERT INTO [t19188] VALUES (2294,'٢١٥٦ - مسألة: (وإن أكراه دابة عشرة أيام بعشرة دراهم، فما زاد فله بكل يوم درهم، فقال أحمد)','6921','2280','0');
INSERT INTO [t19188] VALUES (2295,'٢١٥٧ - مسألة: (ونص أحمد على أنه لا يجوز أن يكتري لمدة غزاته)','6923','2280','0');
INSERT INTO [t19188] VALUES (2296,'٢١٥٨ - مسألة: (وإن سمى لكل يوم شيئا معلوما، فجائز)','6924','2280','0');
INSERT INTO [t19188] VALUES (2297,'٢١٥٩ - مسألة: (وإن أكراه كل شهر بدرهم، أو كل دلو بتمرة، فالمنصوص)','6924','2280','0');
INSERT INTO [t19188] VALUES (2298,'٢١٦٠ - مسألة: (ولا يجوز الاستئجار على حمل الميتة والخمر. وعنه، يصح)','6933','2280','0');
INSERT INTO [t19188] VALUES (2299,'٢١٦١ - مسألة: (ويجوز له استئجار حائط ليضع عليه أطراف خشبه)','6938','2280','0');
INSERT INTO [t19188] VALUES (2300,'٢١٦٢ - مسألة: (و)','6938','2280','0');
INSERT INTO [t19188] VALUES (2301,'٢١٦٣ - مسألة: (و)','6940','2280','0');
INSERT INTO [t19188] VALUES (2302,'٢١٦٤ - مسألة: (و)','6942','2280','0');
INSERT INTO [t19188] VALUES (2303,'٢١٦٥ - مسألة: (فإن أطلق الإجارة)','6943','2280','0');
INSERT INTO [t19188] VALUES (2304,'٢١٦٦ - مسألة: (ويجوز استئجار ولده لخدمته، وامرأته لرضاع ولده وحضانته)','6945','2280','0');
INSERT INTO [t19188] VALUES (2305,'٢١٦٧ - مسألة؛ [قال، رضي الله عنه]','6947','2280','0');
INSERT INTO [t19188] VALUES (2306,'٢١٦٨ - مسألة: (ولا)','6948','2280','0');
INSERT INTO [t19188] VALUES (2307,'٢١٦٩ - مسألة: (إلا في الظئر ونقع','6949','2280','0');
INSERT INTO [t19188] VALUES (2308,'٢١٧٠ - مسألة: (الثاني، معرفة العين برؤية أو صفة، في أحد الوجهين، ويصح في الآخر بدونه، وللمستاجر خيار الروية)','6952','2280','0');
INSERT INTO [t19188] VALUES (2309,'٢١٧١ - مسألة: (الثالث، القدرة على التسليم، فلا يصح إجازة الآبق والشارد، ولا المغصوب)','6954','2280','0');
INSERT INTO [t19188] VALUES (2310,'٢١٧٢ - مسألة: (ولا تجوز إجارة المشاع مفردا لغير شريكه. وعنه ما يدل على الجواز)','6954','2280','0');
INSERT INTO [t19188] VALUES (2311,'٢١٧٣ - مسألة: (الرابع، اشتمال العين على المنفعة. فلا يجوز استئجار بهيمة زمنة للحمل، ولا أرض لا تنبت للزرع)','6957','2280','0');
INSERT INTO [t19188] VALUES (2312,'٢١٧٤ - مسألة: (الخامس، كون المنفعة مملوكة للمؤجر، أو مأذونا له فيها)','6958','2280','0');
INSERT INTO [t19188] VALUES (2313,'٢١٧٥ - مسألة: (يجوز للمستأجر إجارة العين لمن يقوم مقامه)','6958','2280','0');
INSERT INTO [t19188] VALUES (2314,'٢١٧٦ - مسألة: (وتجوز)','6961','2280','0');
INSERT INTO [t19188] VALUES (2315,'٢١٧٧ - مسألة: (وللمستعير إجارتها إذا أذن له المعير مدة بعينها)','6963','2280','0');
INSERT INTO [t19188] VALUES (2316,'٢١٧٨ - مسألة: (وتجوز إجارة الوقف)','6964','2280','0');
INSERT INTO [t19188] VALUES (2317,'٢١٧٩ - مسألة: (فإن مات المؤجر، فانتقل إلى من بعده، لم تنفسخ الإجارة، في أحد الوجهين، وللثاني حصته من الأجرة)','6965','2280','0');
INSERT INTO [t19188] VALUES (2318,'٢١٨٠ - مسألة: (وإن أجر الولي اليتيم)','6966','2280','0');
INSERT INTO [t19188] VALUES (2319,'٢١٨١ - مسألة: فإن أجر السيد عبده مدة ثم أعتقه في أثنائها، صح العتق، ولم يبطل عقد الإجارة، في قياس المذهب. ولا يرجع العبد على مولاه بشيء. وهذا أحد قولي الشافعي. وقال في القديم: يرجع على مولاه بأجر المثل؛ لأن المنافع تستوفى منه بسبب كان من جهة السيد، فرجع به','6969','2280','0');
INSERT INTO [t19188] VALUES (2320,'٢١٨٢ - مسألة: (ويشترط أن تكون المدة معلومة، يغلب على الظن بقاء العين فيها، وإن طالت)','6971','2280','0');
INSERT INTO [t19188] VALUES (2321,'٢١٨٣ - مسألة: (ولا يشترط أن تلي العقد، فلو أجره سنة خمس في سنة أربع، صح، سواء كانت العين مشغولة وقت العقد أو لم تكن)','6975','2280','0');
INSERT INTO [t19188] VALUES (2322,'٢١٨٤ - مسألة: (وإذا أجره في أثناء شهر سنة، استوفى شهرا بالعدد، وسائرها بالأهلة)','6978','2280','0');
INSERT INTO [t19188] VALUES (2323,'٢١٨٥ - مسألة: يجوز (استئجار رجل ليدله على طريق)','6986','2280','0');
INSERT INTO [t19188] VALUES (2324,'٢١٨٦ - مسألة: (و)','6986','2280','0');
INSERT INTO [t19188] VALUES (2325,'٢١٨٧ - مسألة: (ولا يجوز الجمع بين تقدير المدة والعمل، كقوله: استأجرتك لتخيط لي هذا الثوب في يوم. وعنه، يجوز)','6994','2280','0');
INSERT INTO [t19188] VALUES (2326,'٢١٨٨ - مسألة: (ولا تجوز الإجارة على عمل يختص فاعله أن يكون من أهل القربة؛ كالحج، والأذان، ونحوهما. وعنه، تصح)','6996','2280','0');
INSERT INTO [t19188] VALUES (2327,'٢١٨٩ - مسألة: (وإن استأجر من يحجمه، صح، ويكره للحر أكل أجرته، ويطعمه الرقيق والبهائم. وقال القاضي: لا يصح)','7005','2280','0');
INSERT INTO [t19188] VALUES (2328,'٢١٩٠ - مسألة: (وله استيفاء المنفعة وما دونها في الضرر من جنسها)','7016','2280','0');
INSERT INTO [t19188] VALUES (2329,'٢١٩١ - مسألة: فإذا استأجر أرضا (لزرع الحنطة، فله زرع الشعير ونحوه. وليس له زرع الدخن ونحوه. ولا يملك الغرس ولا البناء. وإن اكتراها لأحدهما، لم يملك الآخر. وإن اكتراها للغرس، ملك الزرع)','7016','2280','0');
INSERT INTO [t19188] VALUES (2330,'٢١٩٢ - مسألة: (وإن اكترى دابة للركوب، أو الحمل، لم يملك الآخر. وإن اكتراها لحمل الحديد، أو القطن، لم يملك حمل','7025','2280','0');
INSERT INTO [t19188] VALUES (2331,'٢١٩٣ - مسألة: (وإن فعل)','7027','2280','0');
INSERT INTO [t19188] VALUES (2332,'٢١٩٤ - مسألة: (وإن اكتراها لحمولة شيء، فزاد عليه، أو إلى','7028','2280','0');
INSERT INTO [t19188] VALUES (2333,'٢١٩٥ - مسألة: (وإن تلفت)','7035','2280','0');
INSERT INTO [t19188] VALUES (2334,'٢١٩٦ - مسألة. (فأما تفريغ البالوعة والكنيف، فيلزم المستأجر إذا تسلمها فارغة)','7044','2280','0');
INSERT INTO [t19188] VALUES (2335,'٢١٩٧ - مسألة: (وإن بدا له قبل تقضي المدة، فعليه الأجرة)','7054','2280','0');
INSERT INTO [t19188] VALUES (2336,'٢١٩٨ - مسألة: (وإن حوله المالك قبل تقضيها)','7057','2280','0');
INSERT INTO [t19188] VALUES (2337,'٢١٩٩ - مسألة: (وإن هرب الأجير حتى انقضت المدة، انفسخت الإجارة، وإن كانت على عمل، خير المستأجر بين الفسخ والصبر)','7059','2280','0');
INSERT INTO [t19188] VALUES (2338,'٢٢٠٠ - مسألة: (وإن هرب الجمال، أو مات','7060','2280','0');
INSERT INTO [t19188] VALUES (2339,'٢٢٠١ - مسألة: (وتنفسخ الإجارة بتلف العين المعقود عليها)','7064','2280','0');
INSERT INTO [t19188] VALUES (2340,'٢٢٠٢ - مسألة: (وموت الصبي المرتضع)','7066','2280','0');
INSERT INTO [t19188] VALUES (2341,'٢٢٠٣ - مسألة: (وموات الراكب، إذا لم يكن له من يقوم مقامه في استيفاء المنفعة)','7067','2280','0');
INSERT INTO [t19188] VALUES (2342,'٢٢٠٤ - مسألة: (وانقلاع الضرس الذي اكترى لقلعه، أو برئه)','7069','2280','0');
INSERT INTO [t19188] VALUES (2343,'٢٢٠٥ - مسألة: (وإن اكترى دارا فانهدمت، أو أرضا للزرع فانقطع ماؤها، انفسخت الإجارة فيما بقي من المدة، في أحد الوجهين. وفي الآخر، يثبت للمستأجر خيار الفسخ)','7069','2280','0');
INSERT INTO [t19188] VALUES (2344,'٢٢٠٦ - مسألة: (ولا تنفسخ بموت المكتري أو المكري)','7073','2280','0');
INSERT INTO [t19188] VALUES (2345,'٢٢٠٧ - مسألة: (ولا)','7074','2280','0');
INSERT INTO [t19188] VALUES (2346,'٢٢٠٨ - مسألة: (وإن غصبت العين، خير المستأجر بين الفسخ)','7075','2280','0');
INSERT INTO [t19188] VALUES (2347,'٢٢٠٩ - مسألة: (ومن استؤجر لعمل شيء فمرض، أقيم مقامه','7078','2280','0');
INSERT INTO [t19188] VALUES (2348,'٢٢١٠ - مسألة: (وإن وجد العين معيبة)','7080','2280','0');
INSERT INTO [t19188] VALUES (2349,'٢٢١١ - مسألة: (ويجوز بيع العين المستأجرة، ولا تنفسخ','7082','2280','0');
INSERT INTO [t19188] VALUES (2350,'٢٢١٢ - مسألة: (ويضمن الأجير المشترك ما جنت يده، من تخريق الثوب، وغلطه في تفصيله)','7093','2280','0');
INSERT INTO [t19188] VALUES (2351,'٢٢١٣ - مسألة: (ولا ضمان عليه فيما تلف من حرزه، أو بغير فعله، ولا أجرة له فيما عمل فيه. وعنه، يضمن)','7097','2280','0');
INSERT INTO [t19188] VALUES (2352,'٢٢١٤ - مسألة: (ولا ضمان على حجام، ولا ختان، ولا بزاغ','7100','2280','0');
INSERT INTO [t19188] VALUES (2353,'٢٢١٥ - مسألة: (ولا ضمان على الراعي إذا لم يتعد)','7103','2280','0');
INSERT INTO [t19188] VALUES (2354,'٢٢١٦ - مسألة: (وإذا حبس الصانع الثوب على أجرته، فتلف، ضمنه)','7107','2280','0');
INSERT INTO [t19188] VALUES (2355,'٢٢١٧ - مسألة: (وإن أتلف الثوب بعد عمله خير)','7107','2280','0');
INSERT INTO [t19188] VALUES (2356,'٢٢١٨ - مسألة: (وإذا ضرب المستأجر الدابة بقدر العادة، أو كبحها، أو الرائض الدابة، لم يضمن)','7112','2280','0');
INSERT INTO [t19188] VALUES (2357,'٢٢١٩ - مسألة: (وكذلك المعلم إذا ضرب الصبي)','7112','2280','0');
INSERT INTO [t19188] VALUES (2358,'٢٢٢٠ - مسألة: (وإن قال: أذنت لي في تفصيله قباء. قال: بل قميصا. فالقول قول الخياط. نص عليه)','7114','2280','0');
INSERT INTO [t19188] VALUES (2359,'٢٢٢١ - مسألة: (ولا يجب تسليم أجرة العمل في الذمة حتى يتسلمه)','7126','2280','0');
INSERT INTO [t19188] VALUES (2360,'٢٢٢٢ - مسألة: (وإذا انقضت الإجارة وفي الأرض غراس أو بناء، لم يشترط قلعه عند انقضاء)','7130','2280','0');
INSERT INTO [t19188] VALUES (2361,'٢٢٢٣ - مسألة: (وإن كان فيها زرع بقاؤه بتفريط المستأجر، فللمالك أخذه بالقيمة، وتركه بالأجرة. وإن كان بغير تفريط، لزم تركه بالأجرة)','7135','2280','0');
INSERT INTO [t19188] VALUES (2362,'٢٢٢٤ - مسألة: (وإذا تسلم العين بالإجارة الفاسدة، فعليه أجرة المثل، سكن أو لم يسكن)','7139','2280','0');
INSERT INTO [t19188] VALUES (2363,'٢٢٢٥ - مسألة: (إذا اكترى بدراهم، وأعطاه عنها دنانير، ثم انفسخ العقد، رجع المستأجر بالدراهم)','7141','2280','0');
INSERT INTO [t19188] VALUES (2364,'باب السبق','7144','2172','0');
INSERT INTO [t19188] VALUES (2365,'٢٢٢٦ - مسألة: (تجوز المسابقة على الدواب، والأقدام)','7144','2364','0');
INSERT INTO [t19188] VALUES (2366,'٢٢٢٧ - مسألة: (ولا تجوز بعوض، إلا في الخيل، والإبل، والسهام)','7147','2364','0');
INSERT INTO [t19188] VALUES (2367,'٢٢٢٨ - مسألة: ولا تصح إلا (بشروط خمسة؛ أحدها، تعيين المركوب والرماة)','7152','2364','0');
INSERT INTO [t19188] VALUES (2368,'٢٢٢٩ - مسألة: (ولا يشترط تعيين الراكبين ولا القوسين)','7152','2364','0');
INSERT INTO [t19188] VALUES (2369,'٢٢٣٠ - مسألة: (الثاني، أن يكون المركوبان والقوسان من نوع واحد، فلا يجوز بين عربي وهجين، ولا بين قوس عربية وفارسية. ويحتمل الجواز)','7153','2364','0');
INSERT INTO [t19188] VALUES (2370,'٢٢٣١ - مسألة: (الثالث، تحديد المسافة، والغاية، ومدى الرمي، بما جرت به العادة)','7156','2364','0');
INSERT INTO [t19188] VALUES (2371,'٢٢٣٢ - مسألة: (الرابع، كون العوض معلوما)','7157','2364','0');
INSERT INTO [t19188] VALUES (2372,'٢٢٣٣ - مسألة','7158','2364','0');
INSERT INTO [t19188] VALUES (2373,'٢٢٣٤ - مسألة: (فإن كان الجعل من الإمام، أو أحد غيرهما، أو أحدهما على أن من سبق أخذه، جاز)','7158','2364','0');
INSERT INTO [t19188] VALUES (2374,'٢٢٣٥ - مسألة: (فإن جاءا معا، فلا شيء لهما)','7159','2364','0');
INSERT INTO [t19188] VALUES (2375,'٢٢٣٦ - مسألة: (وإن أخرجا معا، لم يجز، إلا أن يدخلا بينهما محللا يكافئ فرسه فرسيهما، أو بعيره بعيريهما، أو رميه رمييهما، فإن سبقهما أحرز سبقهما، وإن سبقاه، أحرزا سبقيهما، ولم يأخذا منه شيئا، وإن سبق أحدهما أحرز السبقين، وإن سبق معه المحلل، فسبق الآخر بينهما)','7160','2364','0');
INSERT INTO [t19188] VALUES (2376,'٢٢٣٧ - مسألة: (وإن قال المخرج: من سبق فله عشرة، ومن صلى فله ذلك. لم يجز إذا كانا اثنين. وإن قال: من صلى فله خمسة. جاز)','7163','2364','0');
INSERT INTO [t19188] VALUES (2377,'٢٢٣٨ - مسألة: (وإن شرطا أن السابق يطعم السبق أصحابه أو غيرهم، لم يصح الشرط. وفي صحة المسابقة وجهان)','7166','2364','0');
INSERT INTO [t19188] VALUES (2378,'٢٢٣٩ - مسألة: (وتنفسخ بموت أحد المتعاقدين)','7170','2364','0');
INSERT INTO [t19188] VALUES (2379,'٢٢٤٠ - مسألة: (والسبق في الخيل بالرأس، إذا تماثلت الأعناق، وفي مختلفي العنق والإبل بالكتف)','7171','2364','0');
INSERT INTO [t19188] VALUES (2380,'٢٢٤١ - مسألة: (ولا يجوز أن يجنب أحدهما مع فرسه فرسا، يحرضه على العدو، ولا يصيح به في وقت سباقه؛ لقول النبي - صلى الله عليه وسلم -: «لا جلب ولا جنب»)','7174','2364','0');
INSERT INTO [t19188] VALUES (2381,'٢٢٤٢ - مسألة: (وإذا أطلقا الإصابة، تناولها على أي صفة كانت)','7187','2364','0');
INSERT INTO [t19188] VALUES (2382,'٢٢٤٣ - مسألة','7187','2364','0');
INSERT INTO [t19188] VALUES (2383,'٢٢٤٤ - مسألة: (فإن قالا: خواسق. وهو ما خرق الغرض وثبت فيه. أو: خوازق. وهو ما خرقه)','7188','2364','0');
INSERT INTO [t19188] VALUES (2384,'٢٢٤٥ - مسألة: (وإن شرطا إصابة موضع من الغرض، كالدائرة فيه، تقيد به)','7189','2364','0');
INSERT INTO [t19188] VALUES (2385,'٢٢٤٦ - مسألة: (وإن تشاحا في المبتدئ)','7189','2364','0');
INSERT INTO [t19188] VALUES (2386,'٢٢٤٧ - مسألة: (وإذا بدأ أحدهما في وجه، بدأ الآخر في الثاني)','7190','2364','0');
INSERT INTO [t19188] VALUES (2387,'٢٢٤٨ - مسألة: (والسنة أن يكون لهما غرضان)','7191','2364','0');
INSERT INTO [t19188] VALUES (2388,'٢٢٤٩ - مسألة: (وإذا أطارت الريح الغرض، فوقع السهم موضعه، فإن كان شرطهما خواصل، احتسب)','7193','2364','0');
INSERT INTO [t19188] VALUES (2389,'٢٢٥٠ - مسألة: (وإن كان)','7193','2364','0');
INSERT INTO [t19188] VALUES (2390,'٢٢٥١ - مسألة: (وإن عرض عارض؛ من كسر قوس، أو','7196','2364','0');
INSERT INTO [t19188] VALUES (2391,'٢٢٥٢ - مسألة: (وإن عرض مطر أو ظلمة، جاز تأخير الرمي)','7199','2364','0');
INSERT INTO [t19188] VALUES (2392,'٢٢٥٣ - مسألة','7200','2364','0');
INSERT INTO [t19188] VALUES (2393,'كتاب العارية','7201','0','0');
INSERT INTO [t19188] VALUES (2394,'٢٢٥٤ - مسألة: (وهي هبة منفعة، تجوز في كل المنافع إلا منافع البضع)','7203','2393','0');
INSERT INTO [t19188] VALUES (2395,'٢٢٥٥ - مسألة: (ولا تجوز إعارة العبد المسلم لكافر)','7206','2393','0');
INSERT INTO [t19188] VALUES (2396,'٢٢٥٦ - مسألة: (وتكره إعارة الأمة الشابة لرجل غير محرمها)','7206','2393','0');
INSERT INTO [t19188] VALUES (2397,'٢٢٥٧ - مسألة: (واستعارة والديه للخدمة)','7207','2393','0');
INSERT INTO [t19188] VALUES (2398,'٢٢٥٨ - مسألة: (وللمعير الرجوع)','7208','2393','0');
INSERT INTO [t19188] VALUES (2399,'٢٢٥٩ - مسألة: فإن أذن له في شغله بشيء يستضر المستعير برجوعه فيه، لم يجز له الرجوع؛ لما فيه من الإضرار بالمستعير (مثل أن يعيره سفينة لحمل متاعه)','7210','2393','0');
INSERT INTO [t19188] VALUES (2400,'٢٢٦٠ - مسألة: (وإن أعاره أرضا للدفن، لم يرجع حتى يبلى الميت)','7210','2393','0');
INSERT INTO [t19188] VALUES (2401,'٢٢٦١ - مسألة: (وإن أعاره حائطا ليضع عليه أطراف خشبه، لم يرجع ما دام عليه)','7211','2393','0');
INSERT INTO [t19188] VALUES (2402,'٢٢٦٢ - مسألة: (وإن سقط عنه لهدم أو غيره، لم يملك رده)','7211','2393','0');
INSERT INTO [t19188] VALUES (2403,'٢٢٦٣ - مسألة: (وإن أعاره أرضا للزرع، لم يرجع إلى الحصاد، إلا أن يكون مما يحصد قصيلا فيحصده)','7212','2393','0');
INSERT INTO [t19188] VALUES (2404,'٢٢٦٤ - مسألة: (وإن أعارها للغرس والبناء، وشرط عليه القلع','7212','2393','0');
INSERT INTO [t19188] VALUES (2405,'٢٢٦٥ - مسألة: (وإن لم يشترط القلع، لم يلزمه، إلا أن يضمن له المعير النقص)','7214','2393','0');
INSERT INTO [t19188] VALUES (2406,'٢٢٦٦ - مسألة: (فإن أبى القلع)','7215','2393','0');
INSERT INTO [t19188] VALUES (2407,'٢٢٦٧ - مسألة: فإن امتنع المعير من دفع القيمة وأرش النقص، وامتنع المستعير من القلع ودفع الأجر، لم يقلع؛ لأن العارية تقتضي الانتفاع بغير ضمان، والإذن فيما يبقى على الدوام وتضر إزالته رضا بالإبقاء، ولأن قول النبي - صلى الله عليه وسلم -: «ليس لعرق ظالم حق»','7216','2393','0');
INSERT INTO [t19188] VALUES (2408,'٢٢٦٨ - [مسألة: (فإن أبى ذلك بيعا لهما)]','7216','2393','0');
INSERT INTO [t19188] VALUES (2409,'٢٢٦٩ - مسألة: (فإن أبيا البيع، ترك بحاله)','7216','2393','0');
INSERT INTO [t19188] VALUES (2410,'٢٢٧٠ - مسألة: (وللمعير التصرف في أرضه على وجه لا يضر بالشجر)','7217','2393','0');
INSERT INTO [t19188] VALUES (2411,'٢٢٧١ - مسألة: (ولم يذكر أصحابنا عليه أجرة من حين الرجوع، [وذكروا عليه أجرة في الزرع، وهذا مثله، فيخرج فيهما وفي سائر المسائل وجهان)','7218','2393','0');
INSERT INTO [t19188] VALUES (2412,'٢٢٧٢ - مسألة: (وإن غرس أو بنى بعد الرجوع أو بعد الوقت، فهو غاصب، يأتي حكمه)','7219','2393','0');
INSERT INTO [t19188] VALUES (2413,'٢٢٧٣ - مسألة: (وإن حمل السيل بذرا إلى أرض فنبت فيها، فهو لصاحبه)','7221','2393','0');
INSERT INTO [t19188] VALUES (2414,'٢٢٧٤ - مسألة: (فإن حمل)','7223','2393','0');
INSERT INTO [t19188] VALUES (2415,'٢٢٧٥ - مسألة: (والعارية مضمونة بقيمتها يوم التلف، وإن شرط نفي ضمانها)','7226','2393','0');
INSERT INTO [t19188] VALUES (2416,'٢٢٧٦ - مسألة: (وكل ما كان أمانة لا يصير مضمونا بشرطه)','7229','2393','0');
INSERT INTO [t19188] VALUES (2417,'٢٢٧٧ - مسألة: (وإن تلفت أجزاؤها بالاستعمال؛ كخمل','7230','2393','0');
INSERT INTO [t19188] VALUES (2418,'٢٢٧٨ - مسألة: (وليس للمستعير أن يعير)','7233','2393','0');
INSERT INTO [t19188] VALUES (2419,'٢٢٧٩ - مسألة: وإن تلفت عند الثاني، فللمالك (تضمين أيهما شاء)','7236','2393','0');
INSERT INTO [t19188] VALUES (2420,'٢٢٨٠ - مسألة: (وعلى المستعير مؤنة رد العارية)','7237','2393','0');
INSERT INTO [t19188] VALUES (2421,'٢٢٨١ - مسألة: (فإن رد الدابة إلى إصطبل المالك أو غلامه، لم يبرأ من الضمان، إلا أن يردها إلى من جرت عادته بجريان ذلك على يده، كالسائس ونحوه)','7238','2393','0');
INSERT INTO [t19188] VALUES (2422,'٢٢٨٢ - مسألة: (وإن كان بعد مضي مدة لها أجرة، فالقول قول المالك فيما مضى من المدة دون ما بقي منها)','7240','2393','0');
INSERT INTO [t19188] VALUES (2423,'٢٢٨٣ - مسألة: (وهل يستحق أجرة المثل، أو المدعى إن زاد عليها؟ على وجهين)','7242','2393','0');
INSERT INTO [t19188] VALUES (2424,'٢٢٨٤ - مسألة: وإن اختلفا بعد تلف الدابة فقال المالك (أعرتك)','7243','2393','0');
INSERT INTO [t19188] VALUES (2425,'٢٢٨٥ - مسألة: (وإن قال: أجرتني. أو: أعرتني. قال: بل غصبتني. فالقول قول المالك. وقيل: قول الغاصب)','7245','2393','0');
INSERT INTO [t19188] VALUES (2426,'كتاب الغصب','7248','0','0');
INSERT INTO [t19188] VALUES (2427,'[باب. . .]','7248','2426','0');
INSERT INTO [t19188] VALUES (2428,'٢٢٨٦ - مسألة: (وتضمن أم الولد والعقار بالغصب)','7251','2427','0');
INSERT INTO [t19188] VALUES (2429,'٢٢٨٧ - مسألة: (و)','7251','2427','0');
INSERT INTO [t19188] VALUES (2430,'٢٢٨٨ - مسألة: (وإن غصب كلبا فيه نفع، أو خمر ذمي، لزمه ردهما)','7254','2427','0');
INSERT INTO [t19188] VALUES (2431,'٢٢٨٩ - مسألة: (وإن غصب جلد ميتة، فهل يجب رده؟ على','7258','2427','0');
INSERT INTO [t19188] VALUES (2432,'٢٢٩٠ - مسألة: (وإن دبغه، وقلنا بطهارته، لزمه رده)','7259','2427','0');
INSERT INTO [t19188] VALUES (2433,'٢٢٩١ - مسألة: (وإن استولى على حر، لم يضمنه بذلك)','7260','2427','0');
INSERT INTO [t19188] VALUES (2434,'٢٢٩٢ - مسألة','7261','2427','0');
INSERT INTO [t19188] VALUES (2435,'٢٢٩٣ - مسألة: (وإن استعمل الحر كرها، فعليه أجرته)','7262','2427','0');
INSERT INTO [t19188] VALUES (2436,'٢٢٩٤ - [مسألة: (وإن حبسه مدة، فهل تلزمه أجرته؟ على وجهين)]','7263','2427','0');
INSERT INTO [t19188] VALUES (2437,'٢٢٩٥ - مسألة: (وإن خلطه بما يتميز منه، لزمه تخليصه ورده)','7266','2427','0');
INSERT INTO [t19188] VALUES (2438,'٢٢٩٦ - مسألة: (وإن بنى عليه، لزمه رده، إلا أن يكون قد بلي)','7266','2427','0');
INSERT INTO [t19188] VALUES (2439,'٢٢٩٧ - مسألة: (وإن سمر بالمسامير بابا، لزمه قلعها وردها)','7267','2427','0');
INSERT INTO [t19188] VALUES (2440,'٢٢٩٨ - مسألة: (وإن زرع الأرض وردها بعد أخذ الزرع، فعليه أجرتها)','7271','2427','0');
INSERT INTO [t19188] VALUES (2441,'٢٢٩٩ - مسألة: (وإن أدركها ربها والزرع قائم، خير بين تركه','7272','2427','0');
INSERT INTO [t19188] VALUES (2442,'٢٣٠٠ - مسألة: (وإن غرس أو بنى، أخذ بقلع غرسه وبنائه، وتسوية الأرض وأرش نقصها وأجرتها)','7280','2427','0');
INSERT INTO [t19188] VALUES (2443,'٢٣٠١ - مسألة: (وإن غصب لوحا، فرفع به سفينة، لم يقلع حتى ترسو)','7289','2427','0');
INSERT INTO [t19188] VALUES (2444,'٢٣٠٢ - مسألة: (وإن غصب خيطا، فخاط به جرح حيوان، وخيف عليه من قلعه، فعليه قيمته، إلا أن يكون الحيوان مأكولا للغاصب، فهل يلزمه رده ويذبح الحيوان؟ على وجهين)','7290','2427','0');
INSERT INTO [t19188] VALUES (2445,'٢٣٠٣ - مسألة: (فإن مات الحيوان، لزمه رده، إلا أن يكون آدميا)','7293','2427','0');
INSERT INTO [t19188] VALUES (2446,'٢٣٠٤ - مسألة: (وإن غصب جارحا فصاد به أو شبكة أو شركا فأمسك شيئا، أو فرسا فصاد عليه أو غنم، فهو لمالكه)','7299','2427','0');
INSERT INTO [t19188] VALUES (2447,'٢٣٠٥ - مسألة: (وإن غصب ثوبا فقصره، أو غزلا فنسجه، أو فضة أو حديدا فضربه، أو خشبا فنجره، أو شاة فذبحها وشواها، رد ذلك بزيادته وأرش نقصه، ولا شيء له)','7302','2427','0');
INSERT INTO [t19188] VALUES (2448,'٢٣٠٦ - مسألة: (وإن غصب أرضا، فحفر فيها بئرا، ووضع','7307','2427','0');
INSERT INTO [t19188] VALUES (2449,'٢٣٠٧ - مسألة: (وإن غصب حبا فزرعه، أو نوى فصار غرسا، أو بيضا فصار فراخا، رده، ولا شيء للغاصب)','7311','2427','0');
INSERT INTO [t19188] VALUES (2450,'٢٣٠٨ - مسألة: (وإن غصبه وجنى عليه، ضمنه بأكثر الأمرين)','7315','2427','0');
INSERT INTO [t19188] VALUES (2451,'٢٣٠٩ - مسألة: (وإن جنى عليه غير الغاصب، فله تضمين الغاصب أكثر الأمرين، ويرجع الغاصب على الجاني بأرش الجناية، وله تضمين الجاني أرش الجناية، وتضمين الغاصب ما بقي من النقص)','7317','2427','0');
INSERT INTO [t19188] VALUES (2452,'٢٣١٠ - مسألة: (وإن غصب عبدا فخصاه، لزمه رده ورد قيمته)','7318','2427','0');
INSERT INTO [t19188] VALUES (2453,'٢٣١١ - مسألة: (وإن نقصت)','7321','2427','0');
INSERT INTO [t19188] VALUES (2454,'٢٣١٢ - مسألة: (وإن نقصت القيمة لمرض)','7322','2427','0');
INSERT INTO [t19188] VALUES (2455,'٢٣١٣ - [مسألة: (وإن زاد من جهة أخرى؛ مثل أن تعلم)]','7323','2427','0');
INSERT INTO [t19188] VALUES (2456,'٢٣١٤ - مسألة: (وإن زادت القيمة لسمن أو نحوه','7324','2427','0');
INSERT INTO [t19188] VALUES (2457,'٢٣١٥ - مسألة: (فإن عاد مثل الزيادة الأولى من جنسها)','7326','2427','0');
INSERT INTO [t19188] VALUES (2458,'٢٣١٦ - مسألة: (وإن كانت من غير جنس الأولى، لم يسقط ضمانها)','7328','2427','0');
INSERT INTO [t19188] VALUES (2459,'٢٣١٧ - مسألة: (وإن غصب عبدا مفرطا في السمن، فهزل فزادت قيمته)','7328','2427','0');
INSERT INTO [t19188] VALUES (2460,'٢٣١٨ - مسألة: (وإن نقص المغصوب نقصا غير مستقر، كحنطة ابتلت وعفنت)','7330','2427','0');
INSERT INTO [t19188] VALUES (2461,'٢٣١٩ - مسألة: (وإن جنى المغصوب، فعليه أرش جنايته، سواء جنى على سيده أو غيره)','7332','2427','0');
INSERT INTO [t19188] VALUES (2462,'٢٣٢٠ - مسألة: (وجنايته على الغاصب وعلى ماله هدر)','7335','2427','0');
INSERT INTO [t19188] VALUES (2463,'٢٣٢١ - مسألة: (وتضمن زوائد الغصب؛ كالولد، والثمرة إذا تلفت أو نقصت، كالأصل)','7335','2427','0');
INSERT INTO [t19188] VALUES (2464,'٢٣٢٢ - مسألة: (وإن خلط المغصوب بماله على وجه لا يتميز منه، مثل أن خلط حنطة أو زيتا بمثله، لزمه مثله منه، في أحد الوجهين. وفي الآخر، يلزمه مثله من حيث شاء)','7337','2427','0');
INSERT INTO [t19188] VALUES (2465,'٢٣٢٣ - مسألة: (وإن خلطه بدونه، أو خير منه، أو بغير جنسه)','7339','2427','0');
INSERT INTO [t19188] VALUES (2466,'٢٣٢٤ - مسألة: (وإن غصب ثوبا فصبغه، أو سويقا فلته بزيت)','7342','2427','0');
INSERT INTO [t19188] VALUES (2467,'٢٣٢٥ - مسألة: (وإن لم تنقص ولم تزد)','7343','2427','0');
INSERT INTO [t19188] VALUES (2468,'٢٣٢٦ - مسألة: وإن (زادت قيمتهما)','7343','2427','0');
INSERT INTO [t19188] VALUES (2469,'٢٣٢٧ - مسألة: (وإن أراد أحدهما قلع الصبغ، لم يجبر الآخر عليه. ويحتمل أن يجبر إذا ضمن)','7345','2427','0');
INSERT INTO [t19188] VALUES (2470,'٢٣٢٨ - مسألة: (وإن وهب)','7347','2427','0');
INSERT INTO [t19188] VALUES (2471,'٢٣٢٩ - مسألة: (وإن غصب صبغا فصبغ به ثوبا، أو زيتا فلت به سويقا، احتمل أن يكون كذلك)','7349','2427','0');
INSERT INTO [t19188] VALUES (2472,'٢٣٣٠ - مسألة: (وإن غصب ثوبا وصبغا، فصبغه به، رده وأريق نقصه، ولا شيء له في زيادته)','7350','2427','0');
INSERT INTO [t19188] VALUES (2473,'٢٣٣١ - مسألة: (وإن ولدت، فالولد رقيق للسيد)','7352','2427','0');
INSERT INTO [t19188] VALUES (2474,'٢٣٣٢ - مسألة: (ويضمن نقص الولادة)','7354','2427','0');
INSERT INTO [t19188] VALUES (2475,'٢٣٣٣ - مسألة: (وإن باعها، أو وهبها لعالم بالغصب فوطئها، فللمالك تضمين أيهما شاء نقصها ومهرها وأجرتها وقيمة ولدها إن تلف،','7356','2427','0');
INSERT INTO [t19188] VALUES (2476,'٢٣٣٤ - مسألة: (وإن لم يعلما بالغصب فضمنهما، رجعا على الغاصب)','7358','2427','0');
INSERT INTO [t19188] VALUES (2477,'٢٣٣٥ - مسألة: (وإن ولدت)','7359','2427','0');
INSERT INTO [t19188] VALUES (2478,'٢٣٣٦ - مسألة: (ويفديه بمثله في صفاته تقريبا)','7360','2427','0');
INSERT INTO [t19188] VALUES (2479,'٢٣٣٧ - مسألة: (ويرجع)','7362','2427','0');
INSERT INTO [t19188] VALUES (2480,'٢٣٣٨ - مسألة: (وإن تلفت، فعليه قيمتها)','7363','2427','0');
INSERT INTO [t19188] VALUES (2481,'٢٣٣٩ - مسألة: (وعنه، أن ما حصلت له به منفعة، كالأجرة والمهر وأرش البكارة، لا يرجع به)','7364','2427','0');
INSERT INTO [t19188] VALUES (2482,'٢٣٤٠ - مسألة: (فإن ضمن الغاصب، رجع على المشتري بما لا يرجع به عليه)','7367','2427','0');
INSERT INTO [t19188] VALUES (2483,'٢٣٤١ - مسألة: (وإن ولدت من زوج فمات الولد، ضمنه بقيمته)','7369','2427','0');
INSERT INTO [t19188] VALUES (2484,'٢٣٤٢ - مسألة: (وإن أعارها فتلفت عند المستعير، استقر ضمان قيمتها عليه، وضمان الأجرة على الغاصب)','7370','2427','0');
INSERT INTO [t19188] VALUES (2485,'٢٣٤٣ - مسألة: (وإن اشترى أرضا فغرسها أو بنى فيها، فخرجت مستحقة، وقلع غرسه وبناؤه، رجع المشتري على البائع بما غرمه. ذكره القاضي في القسمة)','7373','2427','0');
INSERT INTO [t19188] VALUES (2486,'٢٣٤٤ - مسألة: (وإن أطعم المغصوب لعالم بالغصب، استقر الضمان عليه)','7374','2427','0');
INSERT INTO [t19188] VALUES (2487,'٢٣٤٥ - مسألة: (وإن لم يعلم، وقال له الغاصب: كله، فإنه طعامي. استقر الضمان على الغاصب)','7375','2427','0');
INSERT INTO [t19188] VALUES (2488,'٢٣٤٦ - مسألة: (وإن لم يقل، ففي أيهما يستقر عليه الضمان','7375','2427','0');
INSERT INTO [t19188] VALUES (2489,'٢٣٤٧ - مسألة: (وإن أطعمه لمالكه ولم يعلم، لم يبرأ. نص عليه)','7376','2427','0');
INSERT INTO [t19188] VALUES (2490,'٢٣٤٨ - مسألة: (وإن رهنه عند مالكه، أو أودعه إياه، أو أجره، أو استأجره على قصارته أو خياطته)','7379','2427','0');
INSERT INTO [t19188] VALUES (2491,'٢٣٤٩ - مسألة: (وإن أعاره إياه، برئ، علم أو لم يعلم)','7380','2427','0');
INSERT INTO [t19188] VALUES (2492,'٢٣٥٠ - مسألة: (وإن اشترى عبدا فأعتقه، فادعى رجل أن البائع غصبه منه، فصدقه أحدهما، لم يقبل على الآخر. وإن صدقاه مع العبد،','7381','2427','0');
INSERT INTO [t19188] VALUES (2493,'٢٣٥١ - مسألة: (وإن أعوز المثل، فعليه قيمة مثله يوم إعوازه. وقال القاضي)','7386','2427','0');
INSERT INTO [t19188] VALUES (2494,'٢٣٥٢ - مسألة: (وإن لم يكن مثليا، ضمنه (بقيمته يوم تلفه في بلده من نقده)','7389','2427','0');
INSERT INTO [t19188] VALUES (2495,'٢٣٥٣ - مسألة: (فإن كان مصوغا أو تبرا تخالف قيمته وزنه، قومه بغير جنسه)','7397','2427','0');
INSERT INTO [t19188] VALUES (2496,'٢٣٥٤ - مسألة: (فإن كان محلى بالنقدين معا، قومه بما شاء منهما)','7399','2427','0');
INSERT INTO [t19188] VALUES (2497,'٢٣٥٥ - مسألة: (وإن تلف بعض المغصوب، فنقصت قيمة باقيه؛ كزوجي خف تلف أحدهما، فعليه رد الباقي وقيمة التالف وأرش النقص. وقيل: لا يلزمه أرش النقص)','7400','2427','0');
INSERT INTO [t19188] VALUES (2498,'٢٣٥٦ - مسألة: (وإن غضب عبدا فأبق، أو فرسا فشرد، أو شيئا تعذر رده مع بقائه، ضمن قيمته، فإن قدر عليه بعد، رده وأخذ القيمة)','7404','2427','0');
INSERT INTO [t19188] VALUES (2499,'٢٣٥٧ - مسألة: (وإن غصب عصيرا فتخمر، فعليه)','7406','2427','0');
INSERT INTO [t19188] VALUES (2500,'٢٣٥٨ - مسألة: (وإن تلف المغصوب، فعليه أجرته إلى وقت تلفه)','7411','2427','0');
INSERT INTO [t19188] VALUES (2501,'٢٣٥٩ - مسألة: (وإن غصب شيئا، فعجز عن رده، فأدى قيمته، فعليه أجرته إلى وقت أداء القيمة)','7411','2427','0');
INSERT INTO [t19188] VALUES (2502,'٢٣٦٠ - مسألة: (وإن اتجر بالدراهم، فالربح لمالكها)','7414','2427','0');
INSERT INTO [t19188] VALUES (2503,'٢٣٦١ - مسألة: (وإن اشترى في ذمته ثم نقدها، [فكذلك إذا اشترى في ذمته]','7415','2427','0');
INSERT INTO [t19188] VALUES (2504,'٢٣٦٢ - مسألة: (وإن اختلفا في قيمة المغصوب، أو قدره، أو صناعة فيه، فالقول قول الغاصب)','7419','2427','0');
INSERT INTO [t19188] VALUES (2505,'٢٣٦٣ - مسألة: (وإن اختلفا في رده، أو عيب، فالقول قول المالك)','7420','2427','0');
INSERT INTO [t19188] VALUES (2506,'٢٣٦٤ - مسألة: (وإن بقيت في يده غصوب لا يعلم أربابها، تصدق بها عنهم، بشرط الضمان، كاللقطة)','7421','2427','0');
INSERT INTO [t19188] VALUES (2507,'٢٣٦٥ - مسألة: (وإن فتح قفصا عن طائره)','7424','2427','0');
INSERT INTO [t19188] VALUES (2508,'٢٣٦٦ - مسألة: وإن حل (وكاء زق مائع، أو جامد فأذابته الشمس، أو بقي بعد حله قاعدا، فألقته الريح فاندفق، ضمنه)','7427','2427','0');
INSERT INTO [t19188] VALUES (2509,'٢٣٦٧ - مسألة: (وإن ربط دابة في طريق فأتلفت، أو اقتنى كلبا عقورا فعقر، أو خرق ثوبا، ضمن)','7428','2427','0');
INSERT INTO [t19188] VALUES (2510,'٢٣٦٨ - مسألة: (إلا أن يكون دخل منزله بغير إذنه)','7430','2427','0');
INSERT INTO [t19188] VALUES (2511,'٢٣٦٩ - مسألة: (وقيل: في الكلب روايتان في الجملة)','7431','2427','0');
INSERT INTO [t19188] VALUES (2512,'٢٣٧٠ - مسألة: (وإن أجج نارا في ملكه، أو سقى أرضه،','7433','2427','0');
INSERT INTO [t19188] VALUES (2513,'٢٣٧١ - مسألة: (وإن حفر في فنائه بئرا لنفسه، ضمن ما تلف بها)','7436','2427','0');
INSERT INTO [t19188] VALUES (2514,'٢٣٧٢ - مسألة: (وإن حفرها في سابلة؛ لنفع المسلمين، لم يضمن، في أصح الروايتين)','7437','2427','0');
INSERT INTO [t19188] VALUES (2515,'٢٣٧٣ - مسألة: (وإن بسط في مسجد حصيرا، أو علق فيه قنديلا)','7442','2427','0');
INSERT INTO [t19188] VALUES (2516,'٢٣٧٤ - مسألة: (وإن جلس في مسجد أو طريق واسع، فعثر به حيوان)','7444','2427','0');
INSERT INTO [t19188] VALUES (2517,'٢٣٧٥ - مسألة: (وإن أخرج جناحا أو ميزابا إلى الطريق، فسقط على شيء فأتلفه، ضمن)','7446','2427','0');
INSERT INTO [t19188] VALUES (2518,'٢٣٧٦ - مسألة: (وإن مال حائطه، فلم يهدمه حتى أتلف شيئا]','7448','2427','0');
INSERT INTO [t19188] VALUES (2519,'٢٣٧٧ - مسألة: (وما أتلفت البهيمة، فلا ضمان على صاحبها، إلا أن تكون في يد إنسان؛ كالراكب والسائق والقائد، فيضمن ما جنت يدها أو فمها دون ما جنت برجلها)','7456','2427','0');
INSERT INTO [t19188] VALUES (2520,'٢٣٧٨ - مسألة: (و)','7461','2427','0');
INSERT INTO [t19188] VALUES (2521,'٢٣٧٩ - مسألة: (ومن صال عليه آدمي أو غيره، فقتله دفعا عن نفسه، لم يضمنه)','7466','2427','0');
INSERT INTO [t19188] VALUES (2522,'٢٣٨٠ - مسألة: (وإن اصطدمت سفينتان فغرقتا، ضمن كل واحد منهما سفينة الآخر وما فيها)','7468','2427','0');
INSERT INTO [t19188] VALUES (2523,'٢٣٨١ - مسألة: (وإن كانت إحداهما منحدرة، فعلى صاحبها ضمان المصعدة، إلا أن يكون غلبه ريح، فلم يقدر على ضبطها)','7470','2427','0');
INSERT INTO [t19188] VALUES (2524,'٢٣٨٢ - مسألة: وإن كسر (مزمارا، أو طنبورا، أو صليبا)','7474','2427','0');
INSERT INTO [t19188] VALUES (2525,'٢٣٨٣ - مسألة: وإن (كسر)','7475','2427','0');
INSERT INTO [t19188] VALUES (2526,'٢٣٨٤ - مسألة: وإن كسر (إناء خمر، لم يضمنه)','7477','2427','0');
INSERT INTO [t19188] VALUES (2527,'باب الشفعة','7479','2426','0');
INSERT INTO [t19188] VALUES (2528,'٢٣٨٥ - مسألة: (ولا يحل الاحتيال)','7481','2527','0');
INSERT INTO [t19188] VALUES (2529,'٢٣٨٦ - مسألة: (ولا تثبت إلا بشروط خمسة؛ أحدها، أن يكون مبيعا، ولا شفعة فيما انتقل بغير عوض بحال)','7486','2527','0');
INSERT INTO [t19188] VALUES (2530,'٢٣٨٧ - مسألة: (ولا)','7487','2527','0');
INSERT INTO [t19188] VALUES (2531,'٢٣٨٨ - مسألة: (الثاني، أن يكون شقصا مشاعا من عقار ينقسم، فأما المقسوم المحدود فلا شفعة لجاره فيه)','7492','2527','0');
INSERT INTO [t19188] VALUES (2532,'٢٣٨٩ - مسألة: (و)','7502','2527','0');
INSERT INTO [t19188] VALUES (2533,'٢٣٩٠ - مسألة: (إلا أن يعلم وهو غائب، فيشهد على الطلب، ثم إن أخر الطلب بعد الإشهاد مع إمكانه)','7511','2527','0');
INSERT INTO [t19188] VALUES (2534,'٢٣٩١ - مسألة: (فإن ترك الطلب والإشهاد لعجزه عنهما؛ كالمريض، والمحبوس، ومن لا يجد من يشهده)','7518','2527','0');
INSERT INTO [t19188] VALUES (2535,'٢٣٩٢ - مسألة: (أو لإظهارهم زيادة في الثمن، أو نقصا في','7519','2527','0');
INSERT INTO [t19188] VALUES (2536,'٢٣٩٣ - مسألة: وإن قال الشفيع للمشتري: بعني ما اشتريت. أو: قاسمني','7524','2527','0');
INSERT INTO [t19188] VALUES (2537,'٢٣٩٤ - مسألة: (وإن دل في البيع)','7527','2527','0');
INSERT INTO [t19188] VALUES (2538,'٢٣٩٥ - مسألة: وإن (توكل)','7527','2527','0');
INSERT INTO [t19188] VALUES (2539,'٢٣٩٦ - مسألة: وإن (جعل له الخيار فاختار إمضاء البيع، فهو على شفعته)','7528','2527','0');
INSERT INTO [t19188] VALUES (2540,'٢٣٩٧ - مسألة: (وإن أسقط شفعته قبل البيع، لم تسقط. ويحتمل أن تسقط)','7529','2527','0');
INSERT INTO [t19188] VALUES (2541,'٢٣٩٨ - مسألة: (وإن ترك الولي شفعة للصبي فيها حظ، لم تسقط، وله الأخذ بها إذا كبر، وإن تركها لعدم الحظ فيها، سقطت. ذكره ابن حامد. وقال القاضي: يحتمل أن لا تسقط)','7531','2527','0');
INSERT INTO [t19188] VALUES (2542,'٢٣٩٩ - مسألة: (وإن كانا شفيعين، فالشفعة بينهما على قدر ملكيهما. وعنه، على عدد الرءوس)','7540','2527','0');
INSERT INTO [t19188] VALUES (2543,'٢٤٠٠ - مسألة: (فإن ترك أحدهما شفعته، لم يكن للآخر إلا أن يأخذ الكل أو يترك)','7543','2527','0');
INSERT INTO [t19188] VALUES (2544,'٢٤٠١ - مسألة: (وإن كان المشتري شريكا، فالشفعة بينه وبين الآخر)','7548','2527','0');
INSERT INTO [t19188] VALUES (2545,'٢٤٠٢ - مسألة: (وإن ترك)','7549','2527','0');
INSERT INTO [t19188] VALUES (2546,'٢٤٠٣ - مسألة: (وإذا كانت دار بين اثنين، فباع أحدهما نصيبه لأجنبي صفقتين، ثم علم الشريك، فله أن يأخذ بالبيعين، وله أن يأخذ بأحدهما، فإن أخذ بالثاني، شاركه المشتري في شفعته، في أحد الوجهين، وإن أخذ بالأول، لم يشاركه، وإن أخذ بهما)','7550','2527','0');
INSERT INTO [t19188] VALUES (2547,'٢٤٠٤ - مسألة: (وإن اشترى اثنان حق واحد، فللشفيع أخذ حق أحدهما)','7553','2527','0');
INSERT INTO [t19188] VALUES (2548,'٢٤٠٥ - مسألة: (وإن اشترى واحد حق اثنين، أو اشترى شقصين من دارين صفقة واحدة، فللشفيع أخذ أحدهما، على أصح الوجهين)','7557','2527','0');
INSERT INTO [t19188] VALUES (2549,'٢٤٠٦ - مسألة: (وإن باع شقصا وسيفا، فللشفيع أخذ الشقص بحصته من الثمن. ويحتمل أن لا يجوز)','7560','2527','0');
INSERT INTO [t19188] VALUES (2550,'٢٤٠٧ - مسألة: (وإن تلف بعض المبيع، فله أخذ الباقي بحصته من الثمن. وقال ابن حامد: إن كان تلفه بفعل الله تعالى، فليس له أخذه إلا بجميع الثمن)','7561','2527','0');
INSERT INTO [t19188] VALUES (2551,'٢٤٠٨ - مسألة: (فإن اشترى اثنان دارا صفقة واحدة، فلا شفعة لأحدهما على صاحبه)','7564','2527','0');
INSERT INTO [t19188] VALUES (2552,'٢٤٠٩ - مسألة: (فإن ادعى كل واحد منهما السبق، فتحالفا، أو تعارضت بينتاهما، فلا شفعة لهما)','7564','2527','0');
INSERT INTO [t19188] VALUES (2553,'٢٤١٠ - مسألة: (ولا شفعة بشركة الوقف، في أحد الوجهين)','7565','2527','0');
INSERT INTO [t19188] VALUES (2554,'٢٤١١ - مسألة: (وإن باع فللشفيع الأخذ بأي البيعين شاء، فإن أخذ بالأول، رجع الثاني على الأول)','7570','2527','0');
INSERT INTO [t19188] VALUES (2555,'٢٤١٢ - مسألة: (وإن فسخ البيع بعيب أو إقالة أو تحالف،','7571','2527','0');
INSERT INTO [t19188] VALUES (2556,'٢٤١٣ - مسألة: (وإن أجره)','7578','2527','0');
INSERT INTO [t19188] VALUES (2557,'٢٤١٤ - مسألة: (وإن استغله)','7579','2527','0');
INSERT INTO [t19188] VALUES (2558,'٢٤١٥ - مسألة: (وإن أخذه الشفيع وفيه زرع أو ثمرة ظاهرة،','7579','2527','0');
INSERT INTO [t19188] VALUES (2559,'٢٤١٦ - مسألة: (وإنا قاسم المشتري وكيل الشفيع، أو قاسم الشفيع؛ لكونه أظهر له زيادة في الثمن أو نحوه، وغرس أو بنى، فللشفيع أن يدفع إليه قيمة الغراس والبناء ويملكه، أو يقلعه ويضمن النقص. فإن اختار)','7582','2527','0');
INSERT INTO [t19188] VALUES (2560,'٢٤١٧ - مسألة: (وإن باع الشفيع ملكه قبل العلم لم تسقط شفعته، في أحد الوجهين، وللمشتري الشفعة فيما باعه الشفيع، في أصح الوجهين)','7587','2527','0');
INSERT INTO [t19188] VALUES (2561,'٢٤١٨ - مسألة: (وإن مات الشفيع بطلت الشفعة إلا أن يموت بعد طلبها، فتكون لوارثه)','7591','2527','0');
INSERT INTO [t19188] VALUES (2562,'٢٤١٩ - مسألة: (وما يزاد في الثمن أو يحط منه في مدة الخيار يلحق به، وما بعد ذلك لا يلحق به)','7602','2527','0');
INSERT INTO [t19188] VALUES (2563,'٢٤٢٠ - مسألة: (وإن كان مؤجلا، أخذه الشفيع بالأجل إن كان مليئا، وإلا أقام كفيلا مليا وأخذه به)','7603','2527','0');
INSERT INTO [t19188] VALUES (2564,'٢٤٢١ - مسألة: (وإن كان الثمن عرضا، أعطاه مثله إن كان ذا مثل، وإلا أعطاه قيمته)','7604','2527','0');
INSERT INTO [t19188] VALUES (2565,'٢٤٢٢ - مسألة: (وإن اختلفا في قدر الثمن، فالقول قول المشتري، إلا أن تكون للشفيع بينة)','7607','2527','0');
INSERT INTO [t19188] VALUES (2566,'٢٤٢٣ - مسألة: (وإن قال المشتري: اشتريته بألف. وأقام البائع بينة أنه باعه بألفين، فللشفيع أخذه بألف. وإن قال المشتري: غلطت. فهل يقبل قوله مع يمينه؟ على وجهين)','7611','2527','0');
INSERT INTO [t19188] VALUES (2567,'٢٤٢٤ - مسألة: (وإن ادعى أنك اشتريته بألف، قال: بل اتهبته. أو: ورثته. فالقول قوله مع يمينه. فإن نكل عنها، أو قامت للشفيع بينة، فله أخذه، ويقال للمشتري: إما أن تقبل الثمن، وإما أن تبرئ منه)','7615','2527','0');
INSERT INTO [t19188] VALUES (2568,'٢٤٢٥ - مسألة: (وإن كان عوضا في الخلع)','7621','2527','0');
INSERT INTO [t19188] VALUES (2569,'٢٤٢٦ - مسألة: (وإن أقر البائع بالبيع، وأنكر المشتري، فهل تجب الشفعة؟ على وجهين)','7628','2527','0');
INSERT INTO [t19188] VALUES (2570,'٢٤٢٧ - مسألة: (وعهدة الشفيع على المشتري، وعهدة المشتري على البائع)','7632','2527','0');
INSERT INTO [t19188] VALUES (2571,'٢٤٢٨ - مسألة: (وإن أبى المشتري قبض المبيع، أجبره الحاكم عليه)','7636','2527','0');
INSERT INTO [t19188] VALUES (2572,'٢٤٢٩ - مسألة: (ولو ورث اثنان شقصا عن أبيهما، فباع أحدهما نصيبه، فالشفعة بين أخيه وشريك أبيه)','7637','2527','0');
INSERT INTO [t19188] VALUES (2573,'٢٤٣٠ - مسألة: (ولا شفعة لكافر على مسلم)','7638','2527','0');
INSERT INTO [t19188] VALUES (2574,'٢٤٣١ - مسألة: (وهل تجب الشفعة للمضارب على رب المال، أو لرب المال على المضارب فيما يشتريه من مال المضاربة؟ على وجهين)','7642','2527','0');
INSERT INTO [t19188] VALUES (2575,'باب الوديعة','7656','2426','0');
INSERT INTO [t19188] VALUES (2576,'٢٤٣٢ - مسألة: (وهي أمانة لا ضمان عليه فيها، إلا أن يتعدى. وإن تلفت من بين ماله، لم يضمن، في أصح الروايتين)','7658','2575','0');
INSERT INTO [t19188] VALUES (2577,'٢٤٣٣ - مسألة: (ويلزمه حفظها في حرز مثلها)','7660','2575','0');
INSERT INTO [t19188] VALUES (2578,'٢٤٣٤ - مسألة: (وإن عين صاحبها حرزا، فجعلها في دونه، ضمن)','7661','2575','0');
INSERT INTO [t19188] VALUES (2579,'٢٤٣٥ - مسألة: (وإن أحرزها في مثله، أو فوقه، لم يضمن)','7662','2575','0');
INSERT INTO [t19188] VALUES (2580,'٢٤٣٦ - مسألة: (وإن نهاه)','7663','2575','0');
INSERT INTO [t19188] VALUES (2581,'٢٤٣٧ - مسألة: (فإن قال: لا تخرجها وإن خفت عليها. فأخرجها عند الخوف، أو تركها، لم يضمن)','7666','2575','0');
INSERT INTO [t19188] VALUES (2582,'٢٤٣٨ - مسألة: (وإن أودعه بهيمة، فلم يعلفها حتى ماتت، ضمنها، إلا أن ينهاه المالك عن علفها)','7668','2575','0');
INSERT INTO [t19188] VALUES (2583,'٢٤٣٩ - مسألة: (وإن قال: اترك الوديعة في جيبك. فتركها في كمه، ضمن)','7672','2575','0');
INSERT INTO [t19188] VALUES (2584,'٢٤٤٠ - مسألة: (وإن دفع الوديعة إلى من يحفظ ماله؛ كزوجته أو عبده، لم يضمن)','7677','2575','0');
INSERT INTO [t19188] VALUES (2585,'٢٤٤١ - مسألة: (وإن دفعها إلى أجنبي أو حاكم، ضمن، وليس للمالك مطالبة الأجنبي. وقال القاضي: له ذلك)','7678','2575','0');
INSERT INTO [t19188] VALUES (2586,'٢٤٤٢ - مسألة: (وإن أراد سفرا، أو خاف عليها عنده، ردها على مالكها)','7681','2575','0');
INSERT INTO [t19188] VALUES (2587,'٢٤٤٣ - مسألة: (فإن لم يجده، حملها معه إن كان أحفظ لها)','7682','2575','0');
INSERT INTO [t19188] VALUES (2588,'٢٤٤٤ - مسألة: فإن لم يجد صاحبها ولا وكيله، فله دفعها إلى الحاكم، سواء كان به ضرورة إلى السفر أو لم يكن','7684','2575','0');
INSERT INTO [t19188] VALUES (2589,'٢٤٤٥ - مسألة: (فإن تعذر ذلك، أودعها ثقة، أو دفنها وأعلم بها ثقة يسكن تلك الدار، فإن دفنها ولم يعلم بها أحدا، أو أعلم بها من لا يسكن الدار، ضمنها)','7685','2575','0');
INSERT INTO [t19188] VALUES (2590,'٢٤٤٦ - مسألة: (وإن تعدى فيها، فركب الدابة لغير نفعها،','7687','2575','0');
INSERT INTO [t19188] VALUES (2591,'٢٤٤٧ - مسألة: فإن (جحدها ثم أقر بها)','7688','2575','0');
INSERT INTO [t19188] VALUES (2592,'٢٤٤٨ - مسألة: فإن (كسر ختم كيسها)','7689','2575','0');
INSERT INTO [t19188] VALUES (2593,'٢٤٤٩ - مسألة: وإن (خلطها بما لا تتميز منه، ضمنها)','7689','2575','0');
INSERT INTO [t19188] VALUES (2594,'٢٤٥٠ - مسألة: (وإن خلطها بمتميز، أو ركب الدابة ليسقيها، لم يضمن)','7691','2575','0');
INSERT INTO [t19188] VALUES (2595,'٢٤٥١ - مسألة: (وإن أخذ درهما ثم رده، فضاع الكل، ضمنه وحده)','7692','2575','0');
INSERT INTO [t19188] VALUES (2596,'٢٤٥٢ - مسألة: (وإن أودعه صبي وديعة، ضمنها، ولم يبرأ إلا بالتسليم إلى وليه)','7695','2575','0');
INSERT INTO [t19188] VALUES (2597,'٢٤٥٣ - مسألة: (وإن أودع الصبي)','7696','2575','0');
INSERT INTO [t19188] VALUES (2598,'٢٤٥٤ - مسألة: (وإن أودع عبدا وديعة فأتلفها)','7698','2575','0');
INSERT INTO [t19188] VALUES (2599,'٢٤٥٥ - مسألة: (وإن قال: لم تودعني. ثم أقر بها، أو ثبتت ببينة، ثم ادعى الرد أو التلف، لم يقبل)','7705','2575','0');
INSERT INTO [t19188] VALUES (2600,'٢٤٥٦ - مسألة: (وإن قال: ما لك عندي شيء.','7707','2575','0');
INSERT INTO [t19188] VALUES (2601,'٢٤٥٧ - مسألة: (وإن مات المودع، فادعى وارثه التسليم، لم يقبل إلا ببينة)','7709','2575','0');
INSERT INTO [t19188] VALUES (2602,'٢٤٥٨ - مسألة: (فإن تلفت عنده قبل إمكان ردها، لم يضمنها)','7709','2575','0');
INSERT INTO [t19188] VALUES (2603,'٢٤٥٩ - مسألة: (وإن ادعى الوديعة اثنان، فأقر بها لأحدهما، فهي له مع يمينه)','7713','2575','0');
INSERT INTO [t19188] VALUES (2604,'٢٤٦٠ - مسألة: (وإن أقر بها لهما)','7715','2575','0');
INSERT INTO [t19188] VALUES (2605,'٢٤٦١ - مسألة: (وإن أودعه اثنان مكيلا أو موزونا، فطلب أحدهما نصيبه، سلمه إليه)','7717','2575','0');
INSERT INTO [t19188] VALUES (2606,'٢٤٦٢ - مسألة: (وإن غصبت الوديعة، فهل للمودع المطالبة بها؟ على وجهين)','7717','2575','0');
INSERT INTO [t19188] VALUES (2607,'باب إحياء الموات','7719','2426','0');
INSERT INTO [t19188] VALUES (2608,'٢٤٦٣ - مسألة: (فإن كان فيها آثار الملك ولا يعلم لها مالك، ففيه روايتان)','7721','2607','0');
INSERT INTO [t19188] VALUES (2609,'٢٤٦٤ - مسألة: (ومن أحيا أرضا ميتة فهي له)','7726','2607','0');
INSERT INTO [t19188] VALUES (2610,'٢٤٦٥ - مسألة: ويملكه (بإذن الإمام وغير إذنه)','7731','2607','0');
INSERT INTO [t19188] VALUES (2611,'٢٤٦٦ - مسألة: (إلا ما أحياه مسلم من أرض الكفار التي صولحوا عليها)','7731','2607','0');
INSERT INTO [t19188] VALUES (2612,'٢٤٦٧ - مسألة: (وما قرب من العامر وتعلق بمصالحه، لا يملك بالإحياء. فإن لم يتعلق بمصالحه، فعلى روايتين)','7732','2607','0');
INSERT INTO [t19188] VALUES (2613,'٢٤٦٨ - مسألة: (ولا تملك المعادن الظاهرة؛ كالملح، والقار، والكحل، والجص، والنفط، بالإحياء، وليس للإمام إقطاعه)','7736','2607','0');
INSERT INTO [t19188] VALUES (2614,'٢٤٦٩ - مسألة: (فإن كان بقرب الساحل موضع إذا حصل فيه الماء صار ملحا، ملك بالإحياء، وللإمام إقطاعه)','7740','2607','0');
INSERT INTO [t19188] VALUES (2615,'٢٤٧٠ - مسألة: (وإذا ملك المحيا، ملك ما فيه من المعادن الباطنة، كمعادن الذهب والفضة)','7741','2607','0');
INSERT INTO [t19188] VALUES (2616,'٢٤٧١ - مسألة: (وإن ظهر فيه عين ماء أو معدن جار أو كلأ أو شجر، فهو أحق به)','7742','2607','0');
INSERT INTO [t19188] VALUES (2617,'٢٤٧٢ - مسألة: ويلزمه بذل (ما فضل من مائه لبهائم غيره)','7743','2607','0');
INSERT INTO [t19188] VALUES (2618,'٢٤٧٣ - مسألة: (وإن حفر بئرا عادية، ملك حريمها خمسين ذراعا. وإن لم تكن عادية، فحريمها خمسة وعشرون)','7755','2607','0');
INSERT INTO [t19188] VALUES (2619,'٢٤٧٤ - مسألة: (وقيل: حريمها قدر مد رشائها من كل جانب)','7760','2607','0');
INSERT INTO [t19188] VALUES (2620,'٢٤٧٥ - مسألة: (وقيل: إحياء الأرض ما عد إحياء، وهو عمارتها بما تتهيأ به لما يراد منها)','7760','2607','0');
INSERT INTO [t19188] VALUES (2621,'٢٤٧٦ - مسألة: (ومن تحجر مواتا، لم يملكه، وهو أحق به، ووارثه من بعده، ومن ينقله إليه. وليس له بيعه. وقيل: له ذلك)','7763','2607','0');
INSERT INTO [t19188] VALUES (2622,'٢٤٧٧ - مسألة: (فإن لم يتم إحياءه، قيل له: إما أن تحييه، وإما أن تتركه)','7765','2607','0');
INSERT INTO [t19188] VALUES (2623,'٢٤٧٨ - مسألة: (فإن طلب الإمهال، أمهل)','7766','2607','0');
INSERT INTO [t19188] VALUES (2624,'٢٤٧٩ - مسألة: (وله إقطاع الجلوس في الطرق الواسعة ورحاب المساجد، ما لم يضيق على الناس)','7773','2607','0');
INSERT INTO [t19188] VALUES (2625,'٢٤٨٠ - مسألة: (فإن لم يقطعها، فلمن يسبق إليها الجلوس فيها، ويكون أحق بها ما لم ينقل قماشه عنها)','7774','2607','0');
INSERT INTO [t19188] VALUES (2626,'٢٤٨١ - مسألة: فإن طال مقامه، منع','7776','2607','0');
INSERT INTO [t19188] VALUES (2627,'٢٤٨٢ - مسألة: (وإن سبق اثنان)','7777','2607','0');
INSERT INTO [t19188] VALUES (2628,'٢٤٨٣ - مسألة: (وإن سبق إلى معدن، فهو أحق بما ينال منه)','7779','2607','0');
INSERT INTO [t19188] VALUES (2629,'٢٤٨٤ - مسألة: (وهل يمنع إذا طال مقامه)','7779','2607','0');
INSERT INTO [t19188] VALUES (2630,'٢٤٨٥ - مسألة: (ومن سبق إلى مباح؛ كصيد، أو عنبر، وحطب، وثمر)','7781','2607','0');
INSERT INTO [t19188] VALUES (2631,'٢٤٨٦ - مسألة: (وإذا كان الماء في نهر غير مملوك؛ كمياه الأمطار، فلمن في أعلاه أن يسقي ويحبس الماء حتى يصل إلى الكعب، ثم يرسل إلى من يليه)','7783','2607','0');
INSERT INTO [t19188] VALUES (2632,'٢٤٨٧ - مسألة: (فإن أراد إنسان إحياء أرض)','7787','2607','0');
INSERT INTO [t19188] VALUES (2633,'٢٤٨٨ - مسألة: (وللإمام أن)','7797','2607','0');
INSERT INTO [t19188] VALUES (2634,'٢٤٨٩ - مسألة: (وما حماه النبي - صلى الله عليه وسلم - فليس لأحد نقضه)','7800','2607','0');
INSERT INTO [t19188] VALUES (2635,'باب الجعالة','7802','2426','0');
INSERT INTO [t19188] VALUES (2636,'٢٤٩٠ - مسألة: (وهي أن يقول: من رد عبدي، أو لقطتي، أو بنى لي هذا الحائط، فله كذا)','7803','2635','0');
INSERT INTO [t19188] VALUES (2637,'٢٤٩١ - مسألة: (فمن فعله بعد أن بلغه الجعل، استحقه)','7804','2635','0');
INSERT INTO [t19188] VALUES (2638,'٢٤٩٢ - مسألة: (وإن فعله جماعة، فهو بينهم)','7805','2635','0');
INSERT INTO [t19188] VALUES (2639,'٢٤٩٣ - مسألة: (وإن فعله قبل ذلك لم يستحقه، سواء رده قبل بلوغه الجعل أو بعده)','7807','2635','0');
INSERT INTO [t19188] VALUES (2640,'٢٤٩٤ - مسألة: (وتصح على مدة مجهولة، وعمل مجهول، إذا كان العوض معلوما)','7808','2635','0');
INSERT INTO [t19188] VALUES (2641,'٢٤٩٥ - مسألة: (وهي عقد جائز، لكل واحد منهما فسخها. فمتى فسخها العامل، لم يستحق شيئا، وإن فسخها الجاعل بعد الشروع، فعليه للعامل أجرة عمله)','7812','2635','0');
INSERT INTO [t19188] VALUES (2642,'٢٤٩٦ - مسألة: (وإن اختلفا في أصل الجعل، أو قدره، فالقول قول الجاعل)','7813','2635','0');
INSERT INTO [t19188] VALUES (2643,'٢٤٩٧ - مسألة: (ومن عمل لغيره عملا بغير جعل، فلا شيء له، إلا في رد الآبق)','7814','2635','0');
INSERT INTO [t19188] VALUES (2644,'٢٤٩٨ - مسألة: فأما رد الآبق، فإنه يستحق الجعل برده وإن لم يشرط له','7816','2635','0');
INSERT INTO [t19188] VALUES (2645,'٢٤٩٩ - مسألة: (ويأخذ منه ما أنفق)','7821','2635','0');
INSERT INTO [t19188] VALUES (2646,'٢٥٠٠ - مسألة: (وإن مات السيد، استحق ذلك في تركته)','7822','2635','0');
INSERT INTO [t19188] VALUES (2647,'باب اللقطة','7825','2426','0');
INSERT INTO [t19188] VALUES (2648,'٢٥٠١ - مسألة: (وتنقسم ثلاثة أقسام؛ أحذها: ما لا تتبعه الهمة؛ كالسوط، والشسع','7827','2647','0');
INSERT INTO [t19188] VALUES (2649,'٢٥٠٢ - مسألة: (فمن لا يأمن نفسه عليها، ليس له أخذها)','7846','2647','0');
INSERT INTO [t19188] VALUES (2650,'٢٥٠٣ - مسألة: (ومن أمن نفسه عليها، وقوى على تعريفها، فله أخذها)','7846','2647','0');
INSERT INTO [t19188] VALUES (2651,'٢٥٠٤ - مسألة: (ومتى أخذها ثم ردها إلى موضعها، [أو فرط فيها، ضمنها)','7848','2647','0');
INSERT INTO [t19188] VALUES (2652,'٢٥٠٥ - مسألة: (وهي على ثلاثة أضرب؛ حيوان، فيخير بين أكله)','7857','2647','0');
INSERT INTO [t19188] VALUES (2653,'٢٥٠٦ - مسألة: (الثاني، ما يخشى فساده، فيخير بين بيعه وأكله)','7863','2647','0');
INSERT INTO [t19188] VALUES (2654,'٢٥٠٧ - مسألة: (وغرامة التجفيف منه)','7865','2647','0');
INSERT INTO [t19188] VALUES (2655,'٢٥٠٨ - مسألة: (الثالث، سائر المال، فيلزمه حفظه، ويعرف الجميع بالنداء عليه في مجامع الناس؛ كالأسواق، وأبواب المساجد','7866','2647','0');
INSERT INTO [t19188] VALUES (2656,'٢٥٠٩ - مسألة: (فإن لم تعرف، دخلت في ملكه بعد الحول حكما كالميراث. وعند أبي الخطاب، لا يملكه حتى يختار ذلك)','7876','2647','0');
INSERT INTO [t19188] VALUES (2657,'٢٥١٠ - مسألة: (وعن أحمد، لا تملك إلا الأثمان. وهو ظاهر المذهب. وهل له الصدقة بغيرها؟ على روايتين)','7879','2647','0');
INSERT INTO [t19188] VALUES (2658,'٢٥١١ - مسألة: وعن أحمد، أن (لقطة الحرم لا تملك بحال)','7885','2647','0');
INSERT INTO [t19188] VALUES (2659,'٢٥١٢ - مسألة: (فمتى جاء طالبها فوصفها، لزم دفعها إليه بنمائها المتصل، وزيادتها المنفصلة لمالكها قبل الحول، ولواجدها بعده، في أصح الوجهين)','7891','2647','0');
INSERT INTO [t19188] VALUES (2660,'٢٥١٣ - مسألة: (وإن تلفت أو نقصت قبل الحول، لم يضمنها، وبعده يضمنها)','7895','2647','0');
INSERT INTO [t19188] VALUES (2661,'٢٥١٤ - مسألة: (وإن وصفها اثنان، قسمت بينهما، في أحد الوجهين)','7899','2647','0');
INSERT INTO [t19188] VALUES (2662,'٢٥١٥ - مسألة: (فإن أقام آخر بينة أنها له)','7900','2647','0');
INSERT INTO [t19188] VALUES (2663,'٢٥١٦ - مسألة: (إلا أن يدفعها بحكم حاكم)','7901','2647','0');
INSERT INTO [t19188] VALUES (2664,'٢٥١٧ - مسألة: (وإن وجدها صبي أو سفيه، قام وليه بتعريفها، فإذا عرفها، فهي لواجدها)','7907','2647','0');
INSERT INTO [t19188] VALUES (2665,'٢٥١٨ - مسألة: (وإن)','7910','2647','0');
INSERT INTO [t19188] VALUES (2666,'٢٥١٩ - مسألة: (والمكاتب كالحر)','7914','2647','0');
INSERT INTO [t19188] VALUES (2667,'باب اللقيط','7916','2426','0');
INSERT INTO [t19188] VALUES (2668,'٢٥٢٠ - مسألة: (وهو حر)','7917','2667','0');
INSERT INTO [t19188] VALUES (2669,'٢٥٢١ - مسألة: (ينفق عليه من بيت المال إن لم)','7918','2667','0');
INSERT INTO [t19188] VALUES (2670,'٢٥٢٢ - مسألة: (ويحكم بإسلامه، إلا أن يوجد في بلد الكفار ولا مسلم فيه، فيكون كافرا. فإن كان فيه مسلم، فعلى وجهين)','7921','2667','0');
INSERT INTO [t19188] VALUES (2671,'٢٥٢٣ - مسألة: (وما وجد معه؛ من فراش تحته، أو ثياب، أو مال في جيبه أو تحت فراشه، أو حيوان مشدود بثيابه، فهو له. وإن','7923','2667','0');
INSERT INTO [t19188] VALUES (2672,'٢٥٢٤ - مسألة: (وأولى الناس بحضانته واجده إن كان أمينا)','7926','2667','0');
INSERT INTO [t19188] VALUES (2673,'٢٥٢٥ - مسألة: (وله الإنفاق. عليه مما وجد معه بغير إذن حاكم. وعنه، ما يدل على أنه لا ينفق عليه إلا بإذنه)','7927','2667','0');
INSERT INTO [t19188] VALUES (2674,'٢٥٢٦ - مسألة: (وإن كان)','7929','2667','0');
INSERT INTO [t19188] VALUES (2675,'٢٥٢٧ - مسألة: فإن كان الملتقط رقيقا، لم يقر في يده','7933','2667','0');
INSERT INTO [t19188] VALUES (2676,'٢٥٢٨ - مسألة: (أو كافرا واللقيط مسلم)','7934','2667','0');
INSERT INTO [t19188] VALUES (2677,'٢٥٢٩ - مسألة: (أو بدويا ينتقل في المواضع)','7934','2667','0');
INSERT INTO [t19188] VALUES (2678,'٢٥٣٠ - مسألة: وإن (وجده في الحضر وأراد نقله إلى البادية، لم يقر في يده)','7936','2667','0');
INSERT INTO [t19188] VALUES (2679,'٢٥٣١ - مسألة: (وإن التقطه في البادية مقيم في حلة)','7936','2667','0');
INSERT INTO [t19188] VALUES (2680,'٢٥٣٢ - مسألة: (وإن التقطه في الحضر من يريد نقله إلى بلد آخر)','7936','2667','0');
INSERT INTO [t19188] VALUES (2681,'٢٥٣٣ - مسألة: (وإن التقطه اثنان، قدم الموسر منهما على المعسر، والمقيم على المسافر)','7937','2667','0');
INSERT INTO [t19188] VALUES (2682,'٢٥٣٤ - مسألة','7940','2667','0');
INSERT INTO [t19188] VALUES (2683,'٢٥٣٥ - مسألة: (فإن اختلفا في الملتقط منهما، قدم من له بينة)','7942','2667','0');
INSERT INTO [t19188] VALUES (2684,'٢٥٣٦ - مسألة: (فإن لم يكن لهما بينة، قدم صاحب اليد)','7943','2667','0');
INSERT INTO [t19188] VALUES (2685,'٢٥٣٧ - مسألة: (فإن كان في أيديهما، أقرع بينهما)','7944','2667','0');
INSERT INTO [t19188] VALUES (2686,'٢٥٣٨ - مسألة: (فإن لم يكن لهما يد فوصفه أحدهما، قدم)','7944','2667','0');
INSERT INTO [t19188] VALUES (2687,'٢٥٣٩ - مسألة: (وإن قتل عمدا، فوليه الإمام، إن شاء اقتص، وإن شاء أخذ الدية)','7948','2667','0');
INSERT INTO [t19188] VALUES (2688,'٢٥٤٠ - مسألة: (وإن قطع طرفه عمدا، انتظر بلوغه، إلا أن يكون فقيرا أو مجنونا، فللإمام العفو على مال ينفق عليه)','7948','2667','0');
INSERT INTO [t19188] VALUES (2689,'٢٥٤١ - مسألة: (وإن ادعى الجاني عليه أو قاذفه رقه، وكذبه اللقيط بعد بلوغه، فالقول قول اللقيط)','7951','2667','0');
INSERT INTO [t19188] VALUES (2690,'٢٥٤٢ - مسألة: (وإن ادعى إنسان أنه مملوكه، لم يقبل إلا ببينة تشهد أن أمته ولدته في ملكه. ويحتمل ألا يعتبر قولها في ملكه)','7953','2667','0');
INSERT INTO [t19188] VALUES (2691,'٢٥٤٣ - مسألة: (وإن أقر بالرق بعد بلوغه، لم يقبل. وعنه، يقبل. وقال القاضي: يقبل فيما عليه، رواية واحدة، وهل يقبل في غيره؟ على روايتين)','7956','2667','0');
INSERT INTO [t19188] VALUES (2692,'٢٥٤٤ - مسألة: (وإن قال: إني كافر. لم يقبل قوله، وحكمه حكم المرتد. وقيل: يقبل، إلا أن يكون قد نطق بالإسلام وهو يعقله)','7962','2667','0');
INSERT INTO [t19188] VALUES (2693,'٢٥٤٥ - مسألة: (ولا يتبع الكافر في دينه إلا أن يقيم بينة أنه ولد على فراشه)','7966','2667','0');
INSERT INTO [t19188] VALUES (2694,'٢٥٤٦ - مسألة: (فإن ادعاه اثنان أو أكثر، لأحدهم بينة، قدم بها. فإن استووا في البينة أو عدمها، عرض معهما على القافة أو مع أقاربهما إن ماتا)','7971','2667','0');
INSERT INTO [t19188] VALUES (2695,'٢٥٤٧ - مسألة: (فإن ألحقته بأحدهما، لحق به)','7978','2667','0');
INSERT INTO [t19188] VALUES (2696,'٢٥٤٨ - مسألة: (ولا يلحق بأكثر من أم واحدة)','7980','2667','0');
INSERT INTO [t19188] VALUES (2697,'٢٥٤٩ - مسألة: (فإن ادعاه أكثر من اثنين فألحقته بهم، لحق وإن كثروا)','7983','2667','0');
INSERT INTO [t19188] VALUES (2698,'٢٥٥٠ - مسألة: (فإن نفته القافة عنهم، أو أشكل عليهم، أو لم يوجد قافة، ضاع نسبه، في أحد الوجهين. وفي الآخر، يترك حتى يبلغ فينتسب إلى من شاء. أومأ إليه أحمد)','7985','2667','0');
INSERT INTO [t19188] VALUES (2699,'٢٥٥١ - مسألة: (وكذلك الحكم إن وطئ اثنان امرأة بشبهة، أو جارية مشتركة بينهما في طهر واحد، أو وطئت زوجة رجل أو أم ولده بشبهة، وأتت بولد يمكن أن يكون منه، فادعى الزوج أنه من الواطئ، أري القافة معهما)','7989','2667','0');
INSERT INTO [t19188] VALUES (2700,'٢٥٥٢ - مسألة: (ولا يقبل قول القائف إلا أن يكون ذكرا عدلا، مجربا في الإصابة)','7990','2667','0');
INSERT INTO [t19188] VALUES (2701,'كتاب الوقف','7996','0','0');
INSERT INTO [t19188] VALUES (2702,'[باب. . .]','7996','2701','0');
INSERT INTO [t19188] VALUES (2703,'٢٥٥٣ - مسألة: (وفيه روايتان؛ إحداهما، أنه يحصل بالقول والفعل الدال عليه؛ مثل أن يبني مسجدا ويأذن للناس بالصلاة فيه، أو يجعل أرضه مقبرة ويأذن لهم في الدفن فيها، أو سقاية ويشرعها لهم)','7998','2702','0');
INSERT INTO [t19188] VALUES (2704,'٢٥٥٤ - مسألة: (وصريحه: وقفت، وسبلت، وحبست)','8001','2702','0');
INSERT INTO [t19188] VALUES (2705,'٢٥٥٥ - مسألة: (ولا يصح)','8003','2702','0');
INSERT INTO [t19188] VALUES (2706,'٢٥٥٦ - مسألة: (ويصح وقف المشاع)','8006','2702','0');
INSERT INTO [t19188] VALUES (2707,'٢٥٥٧ - مسألة: (ويصح وقف الحلي على اللبس والعارية)','8007','2702','0');
INSERT INTO [t19188] VALUES (2708,'٢٥٥٨ - مسألة؛ (ولا يصح الوقف في الذمة؛ كعبد، ودار)','8008','2702','0');
INSERT INTO [t19188] VALUES (2709,'٢٥٥٩ - مسألة: (ولا)','8009','2702','0');
INSERT INTO [t19188] VALUES (2710,'٢٥٦٠ - مسألة: (ولا)','8009','2702','0');
INSERT INTO [t19188] VALUES (2711,'٢٥٦١ - مسألة: (ولا يصح على الكنائس، وبيوت النار)','8014','2702','0');
INSERT INTO [t19188] VALUES (2712,'٢٥٦٢ - مسألة: (ولا)','8017','2702','0');
INSERT INTO [t19188] VALUES (2713,'٢٥٦٣ - مسألة: (ولا يصح على نفسه، في إحدى الروايتين)','8018','2702','0');
INSERT INTO [t19188] VALUES (2714,'٢٥٦٤ - مسألة: (وإن وقف على غيره واستثنى الأكل منه مدة','8020','2702','0');
INSERT INTO [t19188] VALUES (2715,'٢٥٦٥ - مسألة: (الثالث، أن يقفه على معين يملك. ولا يصح على مجهول؛ كرجل، ومسجد)','8025','2702','0');
INSERT INTO [t19188] VALUES (2716,'٢٥٦٦ - مسألة: (ولا)','8026','2702','0');
INSERT INTO [t19188] VALUES (2717,'٢٥٦٧ - مسألة: (الرابع، أن يقف ناجزا، فإذا علقه على شرط، لم يصح، إلا أن يقول: هو وقف بعد موتي. فيصح في قول الخرقي. وعند أبي الخطاب، لا يصح)','8029','2702','0');
INSERT INTO [t19188] VALUES (2718,'٢٥٦٨ - مسألة: (وإن وقف على جهة تنقطع ولم يذكر له مآلا، أو وقف على من يجوز ثم على من لا يجوز، أو قال: وقفت. وسكت، انصرف بعد انقراض من يجوز الوقف عليه إلى ورثة الواقف وقفا عليهم، في إحدى الروايتين. والأخرى، إلى أقرب عصبته. وهل يختص به فقراؤهم؟ على وجهين. وقال القاضي في موضع: يكون وقفا على المساكين)','8039','2702','0');
INSERT INTO [t19188] VALUES (2719,'٢٥٦٩ - مسألة: (وإن قال: وقفت داري سنة)','8048','2702','0');
INSERT INTO [t19188] VALUES (2720,'٢٥٧٠ - مسألة: (ولا يشترط إخراج الوقف عن يده، في إحدى الروايتين)','8050','2702','0');
INSERT INTO [t19188] VALUES (2721,'٢٥٧١ - مسألة: (ويملك صوفه ولبنه وثمرته ونفعه)','8053','2702','0');
INSERT INTO [t19188] VALUES (2722,'٢٥٧٢ - مسألة: (وليس له وطء الجارية، فإن فعل، فلا حد عليه ولا مهر)','8053','2702','0');
INSERT INTO [t19188] VALUES (2723,'٢٥٧٣ - مسألة: وإن ولدت، فالولد حر؛ لأنه من وطء شبهة (وعليه قيمته)','8053','2702','0');
INSERT INTO [t19188] VALUES (2724,'٢٥٧٤ - مسألة: (وإن وطئها أجنبي بشبهة، فالولد حر)','8055','2702','0');
INSERT INTO [t19188] VALUES (2725,'٢٥٧٥ - مسألة: (وإن تلفت، فعليه قيمتها، يشترى بها مثلها)','8055','2702','0');
INSERT INTO [t19188] VALUES (2726,'٢٥٧٦ - مسألة: (وله تزويج الأمة وأخذ مهرها، وولدها وقف معها. ويحتمل أن يملكه)','8057','2702','0');
INSERT INTO [t19188] VALUES (2727,'٢٥٧٧ - مسألة: (وإن جنى الوقف خطأ، فالأرش على الموقوف','8058','2702','0');
INSERT INTO [t19188] VALUES (2728,'٢٥٧٨ - مسألة: (وإذا وقف على ثلاثة ثم على المساكين، فمن مات منهم رجع نصيبه إلى الآخرين)','8063','2702','0');
INSERT INTO [t19188] VALUES (2729,'٢٥٧٩ - مسألة: (فإن لم يشرط ناظرا، فالنظر للموقوف عليه. وقيل: للحاكم, وينفق عليه من غلته)','8067','2702','0');
INSERT INTO [t19188] VALUES (2730,'٢٥٨٠ - مسألة: (وإن وقف على ولده ثم على المساكين، فهو لولده الذكور والإناث)','8073','2702','0');
INSERT INTO [t19188] VALUES (2731,'٢٥٨١ - مسألة: (ولا يدخل فيه ولد البنات، وهل يدخل فيه ولد البنين؟ على روايتين)','8074','2702','0');
INSERT INTO [t19188] VALUES (2732,'٢٥٨٢ - مسألة: (وإن وقف على عقبه، أو ولد ولده، أو ذريته)','8088','2702','0');
INSERT INTO [t19188] VALUES (2733,'٢٥٨٣ - مسألة: فإن قال: (على ولد ولدى لصلبي)','8092','2702','0');
INSERT INTO [t19188] VALUES (2734,'٢٥٨٤ - مسألة: (وإن وقف على بنيه، أو بني فلان فهو للذكور خاصة)','8098','2702','0');
INSERT INTO [t19188] VALUES (2735,'٢٥٨٥ - مسألة: (إلا أن يكونوا قبيلة، فيدخل فيه النساء دون أولادهن من غيرهم)','8099','2702','0');
INSERT INTO [t19188] VALUES (2736,'٢٥٨٦ - مسألة: (وإن وقف على قرابته، أو قرابة فلان، فهو للذكر والأنثى من أولاده وأولاد أبيه وجده وجد أبيه، لأن النبي - صلى الله عليه وسلم - لم يتجاوز بسهم ذوي القربى)','8100','2702','0');
INSERT INTO [t19188] VALUES (2737,'٢٥٨٧ - مسألة: (وأهل بيته بمنزلة قرابته. وقال الخرقي: يعطى من قبل أبيه وأمه)','8105','2702','0');
INSERT INTO [t19188] VALUES (2738,'٢٥٨٨ - مسألة: (وقومه ونسباؤه كقرابته)','8107','2702','0');
INSERT INTO [t19188] VALUES (2739,'٢٥٨٩ - مسألة: (والعترة هم العشيرة)','8109','2702','0');
INSERT INTO [t19188] VALUES (2740,'٢٥٩٠ - مسألة: (وذوو رحمه كل قرابة له من جهة الآباء والأمهات)','8110','2702','0');
INSERT INTO [t19188] VALUES (2741,'٢٥٩١ - مسألة: (والأيامى والعزاب، من لا زوج له من الرجال والنساء. ويحتمل)','8111','2702','0');
INSERT INTO [t19188] VALUES (2742,'٢٥٩٢ - مسألة: (فأما الأرامل، فهن النساء اللاتي فارقهن أزواجهن)','8112','2702','0');
INSERT INTO [t19188] VALUES (2743,'٢٥٩٣ - مسألة: (وإن وقف على أهل قريته أو قرابته، لم يدخل فيهم من يخالف دينه. وفيه وجه آخر، أن المسلم يدخل وإن كان الواقف كافرا)','8115','2702','0');
INSERT INTO [t19188] VALUES (2744,'٢٥٩٤ - مسألة: (وإن وقف على مواليه، وله موال من فوق ومن أسفل، تناول جميعهم. وقال ابن حامد: يختص الموالي من فوق)','8119','2702','0');
INSERT INTO [t19188] VALUES (2745,'٢٥٩٥ - مسألة: (وإذا وقف على جماعة يمكن حصرهم واستيعابهم، وجب تعميمهم والتسوية بينهم)','8123','2702','0');
INSERT INTO [t19188] VALUES (2746,'٢٥٩٦ - مسألة: فإن لم يمكن حصرهم؛ كالمساكين، والقبيلة الكثيرة؛ كبني هاشم، وبني تميم، صح الوقف عليهم','8124','2702','0');
INSERT INTO [t19188] VALUES (2747,'٢٥٩٧ - مسألة: ولا يعطى كل واحد أكثر من القدر الذي يعطى من الزكاة. يعني (إذا كان الوقف على صنف من أصناف الزكاة)','8126','2702','0');
INSERT INTO [t19188] VALUES (2748,'٢٥٩٨ - مسألة: (والوصية كالوقف في هذا التفصيل)','8128','2702','0');
INSERT INTO [t19188] VALUES (2749,'٢٥٩٩ - مسألة:، (ولا يجوز بيعه إلا أن تتعطل منافعه، فيباع ويصرف ثمنه في مثله. وكذلك الفرس الحبيس إذا لم يصلح للغزو، بيع واشتري بثمنه ما يصلح للجهاد. وكذلك المسجد إذا لم ينتفع به في موضعه، وعنه، لا تباع المساجد لكن تنقل آلتها إلى مسجد آخر)','8131','2702','0');
INSERT INTO [t19188] VALUES (2750,'٢٦٠٠ - مسألة: (ويجوز بيع بعض آلته وصرفها في عمارته)','8135','2702','0');
INSERT INTO [t19188] VALUES (2751,'٢٦٠١ - مسألة: (وما فضل من حصره وزيته)','8142','2702','0');
INSERT INTO [t19188] VALUES (2752,'٢٦٠٢ - مسألة: (ولا يجوز غرس شجرة في المسجد)','8144','2702','0');
INSERT INTO [t19188] VALUES (2753,'٢٦٠٣ - مسألة: (فإن كانت مغروسة، جاز الأكل منها)','8145','2702','0');
INSERT INTO [t19188] VALUES (2754,'باب الهبة والعطية','8149','2701','0');
INSERT INTO [t19188] VALUES (2755,'٢٦٠٢ - مسألة: (فإن شرط فيها عوضا معلوما، صارت بيعا. وعنه، يغلب فيها حكم الهبة)','8150','2754','0');
INSERT INTO [t19188] VALUES (2756,'٢٦٠٣ - مسألة: (وإن شرط ثوابا مجهولا، لم تصح)','8152','2754','0');
INSERT INTO [t19188] VALUES (2757,'٢٦٠٤ - مسألة: (وتحصل الهبة بما يتعارفه الناس هبة، من الإيجاب والقبول والمعاطاة المقترنة بما يدل عليها)','8155','2754','0');
INSERT INTO [t19188] VALUES (2758,'٢٦٠٥ - مسألة: (وتلزم بالقبض. وعنه، تلزم في غير المكيل والموزون بمجرد الهبة)','8158','2754','0');
INSERT INTO [t19188] VALUES (2759,'٢٦٠٦ - مسألة: (ولا يصح القبص إلا بإذن الواهب، إلا ما كان في يد المتهب، فيكفي مضي زمن يتأتى قبضه فيه. وعنه، لا يصح حتى يأذن في القبض)','8164','2754','0');
INSERT INTO [t19188] VALUES (2760,'٢٦٠٧ - مسألة: (فإن مات الواهب، قام وارثه مقامه في الإذن والرجوع)','8167','2754','0');
INSERT INTO [t19188] VALUES (2761,'٢٦٠٨ - مسألة: (وإن أبرأ الغريم غريمه من دينه، أو وهبه له، أو أحله منه، برئ وإن رد ذلك ولم يقبله)','8171','2754','0');
INSERT INTO [t19188] VALUES (2762,'٢٦٠٩ - مسألة: (وتصح هبة المشاع)','8182','2754','0');
INSERT INTO [t19188] VALUES (2763,'٢٦١٠ - مسألة: (و)','8184','2754','0');
INSERT INTO [t19188] VALUES (2764,'٢٦١١ - مسألة: (ولا تصح هبة المجهول)','8186','2754','0');
INSERT INTO [t19188] VALUES (2765,'٢٦١٢ - مسألة: (ولا يجوز تعليقها على شرط، ولا شرط ما ينافي مقتضاها، نحو أن لا يبيعها ولا يهبها)','8188','2754','0');
INSERT INTO [t19188] VALUES (2766,'٢٦١٣ - مسألة: (ولا توقيتها، كقوله: وهبتك هذا سنة)','8189','2754','0');
INSERT INTO [t19188] VALUES (2767,'٢٦١٤ - مسألة: (إلا في العمرى)','8190','2754','0');
INSERT INTO [t19188] VALUES (2768,'٢٦١٥ - مسألة: (وإن شرط رجوعها إلى المعمر عند موته، أو قال: هي لآخرنا موتا. صح الشرط. وعنه، لا يصح، وتكون للمعمر ولورثته)','8195','2754','0');
INSERT INTO [t19188] VALUES (2769,'٢٦١٦ - مسألة: (فإن خص بعضهم أو فضله، فعليه التسوية بالرجوع أو إعطاء الآخر حتى يستووا)','8206','2754','0');
INSERT INTO [t19188] VALUES (2770,'٢٦١٧ - مسألة: (وإن مات قبل ذلك، ثبت للمعطى. وعنه، لا يثبت، وللباقين الرجوع. اختاره أبو عبد الله بن بطة)','8211','2754','0');
INSERT INTO [t19188] VALUES (2771,'٢٦١٨ - مسألة','8217','2754','0');
INSERT INTO [t19188] VALUES (2772,'٢٦١٩ - مسألة: (ولا يجوز لواهب أن يرجع في هبته إلا الأب. وعنه، ليس له الرجوع. وعنه، له الرجوع إلا أن يتعلق به حق أو رغبة، نحو أن يتزوج الولد أو يفلس)','8224','2754','0');
INSERT INTO [t19188] VALUES (2773,'٢٦٢٠ - مسألة: (وإن نقصت العين، أو زادت زيادة منفصلة، لم تمنع الرجوع، والزيادة للابن. ويحتمل أنها للأب. وهل تمنع','8234','2754','0');
INSERT INTO [t19188] VALUES (2774,'٢٦٢١ - مسألة: (وإن باعه المتهب ثم رجع إليه بفسخ أو إقالة، فهل له الرجوع؟ على وجهين. وإن رجع إليه ببيع أو هبة، لم يملك الرجوع)','8239','2754','0');
INSERT INTO [t19188] VALUES (2775,'٢٦٢٢ - مسألة: (وإن وهبه المتهب لابنه، لم يملك أبوه الرجوع، إلا أن يرجع هو)','8240','2754','0');
INSERT INTO [t19188] VALUES (2776,'٢٦٢٣ - مسألة: (وإن كاتبه أو رهنه، لم يملك)','8241','2754','0');
INSERT INTO [t19188] VALUES (2777,'٢٦٢٤ - مسألة: (وعن أحمد، في المرأة تهب زوجها مهرها: إن كان سألها ذلك رده إليها، رضيت أو كرهت؛ لأنها لا تهبه له إلا مخافة غضبه أو إضرار بها بأن يتزوج عليها)','8244','2754','0');
INSERT INTO [t19188] VALUES (2778,'٢٦٢٥ - مسألة: (فإن تصرف فيه قبل تملكه؛ ببيع، أو عتق، أو إبراء من دين، لم يصح تصرفه)','8250','2754','0');
INSERT INTO [t19188] VALUES (2779,'٢٦٢٦ - مسألة: (وإن وطيء جارية ابنه فأحبلها، صارت أم ولد له، وولده حر لا تلزمه قيمته، ولا حد)','8252','2754','0');
INSERT INTO [t19188] VALUES (2780,'٢٦٢٧ - مسألة: (وليس للابن مطالبة أبيه بدين، ولا قيمة متلف، ولا أرش جناية، ولا غير ذلك)','8255','2754','0');
INSERT INTO [t19188] VALUES (2781,'٢٦٢٨ - مسألة: (والهدية والصدقة نوعان من الهبة)','8258','2754','0');
INSERT INTO [t19188] VALUES (2782,'٢٦٢٩ - مسألة: (وإن كان مرض الموت المخوف، كالبرسام)','8260','2754','0');
INSERT INTO [t19188] VALUES (2783,'٢٦٣٠ - مسألة: (فعطاياه كالوصية في أنها لا تصح لوارث، ولا','8262','2754','0');
INSERT INTO [t19188] VALUES (2784,'٢٦٣١ - مسألة: (فأما الأمراض الممتدة؛ كالجذام)','8266','2754','0');
INSERT INTO [t19188] VALUES (2785,'٢٦٣٢ - مسألة: (ومن كان بين الصفين عند التحام الحرب، أو في لجة البحر عند هيجانه، أو وقع الطاعون ببلده، أو قدم ليقتص منه، والحامل عند المخاض، فهو كالمريض)','8267','2754','0');
INSERT INTO [t19188] VALUES (2786,'٢٦٣٣ - مسألة: وإن لم يف (الثلث بالتبرعات المنجزة، بدئ بالأول فالأول)','8276','2754','0');
INSERT INTO [t19188] VALUES (2787,'٢٦٣٤ - مسألة: (وإن تساوت قسم بين الجميع بالحصص. وعنه، يقدم العتق)','8277','2754','0');
INSERT INTO [t19188] VALUES (2788,'٢٦٣٥ - مسألة: (وأما معاوضة المريض بثمن المثل، فتصح من رأس المال وإن كانت مع وارث)','8280','2754','0');
INSERT INTO [t19188] VALUES (2789,'٢٦٣٦ - مسألة: (وإن حابى وارثه، فقال القاضي: تبطل في قدر ما حاباه، وتصح فيما عداه)','8281','2754','0');
INSERT INTO [t19188] VALUES (2790,'٢٦٣٧ - مسألة: (فإن كان له شفيع، فله أخذه، فإن أخذه فلا خيار للمشتري)','8282','2754','0');
INSERT INTO [t19188] VALUES (2791,'٢٦٣٨ - مسألة: (وإن باع المريض أجنبيا وحاباه، وكان شفيعه وارثا، فله الأخذ بالشفعة؛ لأن المحاباة لغيره)','8285','2754','0');
INSERT INTO [t19188] VALUES (2792,'٢٦٣٩ - مسألة: (ويعتبر الثلث عند الموت)','8286','2754','0');
INSERT INTO [t19188] VALUES (2793,'٢٦٤٠ - مسألة: (فلو أعتق في مرضه عبدا، أو وهبه لإنسان، ثم كسب في حياة سيده شيئا، ثم مات سيده فخرج من الثلث، كان كسبه','8289','2754','0');
INSERT INTO [t19188] VALUES (2794,'٢٦٤١ - مسألة: (وإن كان موهوبا لإنسان)','8291','2754','0');
INSERT INTO [t19188] VALUES (2795,'٢٦٤٢ - مسألة: (وإن أعتق جارية)','8296','2754','0');
INSERT INTO [t19188] VALUES (2796,'٢٦٤٣ - مسألة: (وإن وهبها مريضا آخر لا مال له غيرها، ثم وهبها الثاني للأول)','8296','2754','0');
INSERT INTO [t19188] VALUES (2797,'٢٦٤٤ - مسألة: (وإن باع مريض قفيزا لا يملك غيره يساوي ثلاثين بقفيز يساوي عشرة)','8304','2754','0');
INSERT INTO [t19188] VALUES (2798,'٢٦٤٥ - مسألة: (وإن أصدق امرأة عشرة)','8306','2754','0');
INSERT INTO [t19188] VALUES (2799,'٢٦٤٦ - مسألة: (وإن مات قبلها، ورثته، وسقطت المحاباة)','8308','2754','0');
INSERT INTO [t19188] VALUES (2800,'٢٦٤٧ - مسألة: (ولو أعتق أمته وتزوجها في مرضه)','8322','2754','0');
INSERT INTO [t19188] VALUES (2801,'٢٦٤٨ - مسألة: (ولو أعتقها وقيمتها مائة، ثم تزوجها وأصدقها مائتين لا مال له سواهما، وهما مهر مثلها، ثم مات، صح العتق، ولم تستحق الصداق؛ لئلا يفضي إلى بطلان عتقها، ثم يبطل صداقها. وقال القاضي: تستحق المائتين)','8323','2754','0');
INSERT INTO [t19188] VALUES (2802,'٢٦٤٩ - مسألة: (وإن تبرع بالثلث، ثم اشترى أباه من الثلثين)','8324','2754','0');
INSERT INTO [t19188] VALUES (2803,'كتاب الوصايا','8330','0','0');
INSERT INTO [t19188] VALUES (2804,'[باب. . .]','8330','2803','0');
INSERT INTO [t19188] VALUES (2805,'٢٦٥٠ - مسألة: (وتصح من البالغ الرشيد، عدلا كان أو فاسقا، رجلا أو امرأة، مسلما أو كافرا)','8333','2804','0');
INSERT INTO [t19188] VALUES (2806,'٢٦٥١ - مسألة: (و)','8334','2804','0');
INSERT INTO [t19188] VALUES (2807,'٢٦٥٢ - مسألة: (ولا تصح من غير عاقل؛ كالطفل، والمجنون، والمبرسم. وفي السكران وجهان)','8338','2804','0');
INSERT INTO [t19188] VALUES (2808,'٢٦٥٣ - مسألة: (وتصح وصية الأخرس بالإشارة، ولا تصح ممن اعتقل لسانه بها. ويحتمل أن تصح)','8340','2804','0');
INSERT INTO [t19188] VALUES (2809,'٢٦٥٤ - مسألة: (وإن وجدت وصيته بخطه، صحت)','8342','2804','0');
INSERT INTO [t19188] VALUES (2810,'٢٦٥٥ - مسألة: (فأما من لا وارث له، فتجوز وصيته بجميع ماله. وعنه، لا يجوز إلا الثلث)','8354','2804','0');
INSERT INTO [t19188] VALUES (2811,'٢٦٥٦ - مسألة: (ولا تجوز لمن له وارث بزيادة على الثلث لأجنبي، ولا لوارثه بشيء إلا بإجازة الورثة)','8358','2804','0');
INSERT INTO [t19188] VALUES (2812,'٢٦٥٧ - مسألة: فإن وصى (لكل وارث بمعين بقدر)','8362','2804','0');
INSERT INTO [t19188] VALUES (2813,'٢٦٥٨ - مسألة: (وإن لم يف الثلث بالوصايا، تحاصوا فيه، وأدخل النقص على كل واحد بقدر وصيته. وعنه، يقدم العتق)','8363','2804','0');
INSERT INTO [t19188] VALUES (2814,'٢٦٥٩ - مسألة: (وإن أجاز الورثة الوصية، جازت)','8365','2804','0');
INSERT INTO [t19188] VALUES (2815,'٢٦٦٠ - مسألة: (ومن أوصي له وهو في الظاهر وارث، فصار عند الموت غير وارث، صحت الوصية. وإن أوصى له وهو غير وارث، فصار عند الموت وارثا، بطلت؛ لأن اعتبار الوصية بالموت)','8369','2804','0');
INSERT INTO [t19188] VALUES (2816,'٢٦٦١ - مسألة: (ولا تصح إجازتهم وردهم إلا بعد موت الموصي، وما قبل ذلك لا عبرة به)','8371','2804','0');
INSERT INTO [t19188] VALUES (2817,'٢٦٦٢ - مسألة: (ومن أجاز الوصية ثم قال: إنما أجزت لأني ظننت المال قليلا. فالقول قوله مع يمينه، وله الرجوع بما زاد على ما ظنه، في أظهر الوجهين، إلا أن تقوم به بينة)','8372','2804','0');
INSERT INTO [t19188] VALUES (2818,'٢٦٦٣ - مسألة: (وإن كان المجاز عينا)','8374','2804','0');
INSERT INTO [t19188] VALUES (2819,'٢٦٦٤ - مسألة: (ولا يثبت الملك للموصى له إلا بالقبول بعد الموت، فأما رده وقبوله قبل ذلك فلا عبرة به)','8375','2804','0');
INSERT INTO [t19188] VALUES (2820,'٢٦٦٥ - مسألة: (وإن مات الموصى له قبل موت الموصي، بطلت الوصية)','8377','2804','0');
INSERT INTO [t19188] VALUES (2821,'٢٦٦٦ - مسألة: (وإن ردها بعد موته، بطلت أيضا)','8378','2804','0');
INSERT INTO [t19188] VALUES (2822,'٢٦٦٧ - مسألة: (وإن مات بعده وقبل الرد والقبول، قام وارثه مقامه. ذكره الخرقي. وقال القاضي: تبطل على قياس قوله)','8381','2804','0');
INSERT INTO [t19188] VALUES (2823,'٢٦٦٨ - مسألة: (وإن قبلها بعد الموت، ثبت الملك حين القبول، في الصحيح)','8383','2804','0');
INSERT INTO [t19188] VALUES (2824,'٢٦٦٩ - مسألة: فما حصل من كسب أو (نماء منفصل)','8387','2804','0');
INSERT INTO [t19188] VALUES (2825,'٢٦٧٠ - مسألة: (وإن كانت الوصية بأمة فوطئها الوارث قبل القبول فأولدها، صارت أم ولد له، وولدها حر)','8388','2804','0');
INSERT INTO [t19188] VALUES (2826,'٢٦٧١ - مسألة: (وإن وصى له بزوجته فأولدها)','8389','2804','0');
INSERT INTO [t19188] VALUES (2827,'٢٦٧٢ - مسألة: (وإن وصى له بأبيه فمات قبل القبول، فقبل','8389','2804','0');
INSERT INTO [t19188] VALUES (2828,'٢٦٧٣ - مسألة: (فإذا قال: قد رجعت في وصيتي. أو: أبطلتها. أو نحو ذلك)','8393','2804','0');
INSERT INTO [t19188] VALUES (2829,'٢٦٧٤ - مسألة: وإن قال: (ما أوصيت به لفلان فهو لفلان.','8394','2804','0');
INSERT INTO [t19188] VALUES (2830,'٢٦٧٥ - مسألة: (وإن وصى به لآخر ولم يقل ذلك، فهو بينهما)','8394','2804','0');
INSERT INTO [t19188] VALUES (2831,'٢٦٧٦ - مسألة: (وإن باعه، أو وهبه، أو رهنه، كان رجوعا)','8397','2804','0');
INSERT INTO [t19188] VALUES (2832,'٢٦٧٧ - مسألة: (وإن كاتبه، أو دبره، أو جحد الوصية، فعلى وجهين)','8398','2804','0');
INSERT INTO [t19188] VALUES (2833,'٢٦٧٨ - مسألة: (وإن خلطه بغيره على وجه لا يتميز)','8399','2804','0');
INSERT INTO [t19188] VALUES (2834,'٢٦٧٩ - مسألة: (وإن وصى له بقفيز من صبرة، ثم خلط الصبرة بأخرى، لم يكن رجوعا)','8402','2804','0');
INSERT INTO [t19188] VALUES (2835,'٢٦٨٠ - مسألة: (وإن زاد في الدار عمارة، أو انهدم بعضها، فهل يستحقه الموصى له؟ على وجهين)','8404','2804','0');
INSERT INTO [t19188] VALUES (2836,'٢٦٨١ - مسألة: (وإن وصى لرجل)','8406','2804','0');
INSERT INTO [t19188] VALUES (2837,'٢٦٨٢ - مسألة: (وإن قال: أخرجوا الواجب من ثلثي)','8411','2804','0');
INSERT INTO [t19188] VALUES (2838,'باب الموصى له','8415','2803','0');
INSERT INTO [t19188] VALUES (2839,'٢٦٨٣ - مسألة: وتصح للمرتد كما تصح الهبة له. ذكره أبو الخطاب (وقال ابن أبي موسى: لا تصح)','8418','2838','0');
INSERT INTO [t19188] VALUES (2840,'٢٦٨٤ - مسألة: (وتصح لمكاتبه، ومدبره، وأم ولده)','8418','2838','0');
INSERT INTO [t19188] VALUES (2841,'٢٦٨٥ - مسألة: وتصح الوصية لمدبره؛ لأنه يصير حرا حين لزوم الوصية، فصحت الوصية له، كأم الولد','8421','2838','0');
INSERT INTO [t19188] VALUES (2842,'٢٦٨٦ - مسألة: وتصح الوصية لأم ولده؛ لأنها حرة حين لزوم الوصية. وقد روي','8421','2838','0');
INSERT INTO [t19188] VALUES (2843,'٢٦٨٧ - مسألة: (وتصح لعبد غيره)','8422','2838','0');
INSERT INTO [t19188] VALUES (2844,'٢٦٨٨ - مسألة: (وتصح لعبده بمشاع؛ كثلثه)','8424','2838','0');
INSERT INTO [t19188] VALUES (2845,'٢٦٨٩ - مسألة: (وإن وصى له بمعين)','8425','2838','0');
INSERT INTO [t19188] VALUES (2846,'٢٦٩٠ - مسألة: (وتصح)','8426','2838','0');
INSERT INTO [t19188] VALUES (2847,'٢٦٩١ - مسألة: (وإن وصى لمن تحمل هذه المرأة، لم تصح)','8432','2838','0');
INSERT INTO [t19188] VALUES (2848,'٢٦٩٢ - مسألة: (وإن قتل الوصي الموصي، بطلت الوصية، وإن جرحه، ثم أوصى له، فمات من الجرح، لم تبطل، في ظاهر كلامه. وقال أصحابنا: في الوصية للقاتل روايتان)','8434','2838','0');
INSERT INTO [t19188] VALUES (2849,'٢٦٩٣ - مسألة: (وإن وصى لصنف من أصناف الزكاة، أو لجميع الأصناف، صح)','8437','2838','0');
INSERT INTO [t19188] VALUES (2850,'٢٦٩٤ - مسألة: (وإن وصى لكتب القرآن، أو العلم، أو لمسجد، أو لفرس حبيس ينفق عليه، صح)','8440','2838','0');
INSERT INTO [t19188] VALUES (2851,'٢٦٩٥ - مسألة: (وإن وصى في أبواب البر)','8444','2838','0');
INSERT INTO [t19188] VALUES (2852,'٢٦٩٦ - مسألة: (وإن وصى أن يحج عنه بألف، صرف في حجة بعد أخرى حتى تنفد)','8447','2838','0');
INSERT INTO [t19188] VALUES (2853,'٢٦٩٧ - مسألة: وإن وصى أن يحج عنه حجة بألف (دفع الكل إلى من يحج)','8451','2838','0');
INSERT INTO [t19188] VALUES (2854,'٢٦٩٨ - مسألة: (فإن عينه في الوصية، فقال: يحج عني فلان بألف)','8452','2838','0');
INSERT INTO [t19188] VALUES (2855,'٢٦٩٩ - مسألة: فإن (أبى الحج، وقال: اصرفوا لي الفضل. لم يعطه، وبطلت الوصية في حقه)','8453','2838','0');
INSERT INTO [t19188] VALUES (2856,'٢٧٠٠ - مسألة: (وإن وصى لأهل سكته، فهو لأهل دربه)','8457','2838','0');
INSERT INTO [t19188] VALUES (2857,'٢٧٠١ - مسألة: (وإن وصى لجيرانه، تناول أربعين دارا من كل جانب)','8458','2838','0');
INSERT INTO [t19188] VALUES (2858,'٢٧٠٢ - مسألة: (وإن وصى لأقرب قرابته)','8459','2838','0');
INSERT INTO [t19188] VALUES (2859,'٢٧٠٣ - مسألة: (والأخ من الأب والأخ من الأم سواء، والأخ من الأبوين أحق منهما)','8461','2838','0');
INSERT INTO [t19188] VALUES (2860,'٢٧٠٤ - مسألة: وإن وصى (لكتب التوارة والإنجيل)','8464','2838','0');
INSERT INTO [t19188] VALUES (2861,'٢٧٠٥ - مسألة: (ولا)','8465','2838','0');
INSERT INTO [t19188] VALUES (2862,'٢٧٠٦ - مسألة: (وإن وصى لحي وميت يعلم موته، فالكل للحي. ويحتمل أن لا يكون له إلا النصف. وإن لم يعلم، فللحي نصف الموصى به)','8466','2838','0');
INSERT INTO [t19188] VALUES (2863,'٢٧٠٧ - مسألة: (فإن وصى لوارثه وأجنبي)','8469','2838','0');
INSERT INTO [t19188] VALUES (2864,'٢٧٠٨ - مسألة: (وإن وصى لهما بثلثي ماله)','8470','2838','0');
INSERT INTO [t19188] VALUES (2865,'٢٧٠٩ - مسألة: (ولو وصى بماله لابنيه وأجنبي)','8472','2838','0');
INSERT INTO [t19188] VALUES (2866,'٢٧١٠ - مسألة: (وإن وصى لزيد والفقراء والمساكين بثلثه، فلزيد التسع)','8473','2838','0');
INSERT INTO [t19188] VALUES (2867,'باب الموصى به','8476','2803','0');
INSERT INTO [t19188] VALUES (2868,'٢٧١١ - مسألة: (و)','8476','2867','0');
INSERT INTO [t19188] VALUES (2869,'٢٧١٢ - مسألة: (وتصح بما فيه نفع مباح من غير المال؛ كالكلب، والزيت النجس)','8478','2867','0');
INSERT INTO [t19188] VALUES (2870,'٢٧١٣ - مسألة: (وتصح الوصية بالمجهول؛ كعبد، وشاة)','8482','2867','0');
INSERT INTO [t19188] VALUES (2871,'٢٧١٤ - مسألة: (فإن اختلف الاسم بالحقيقة والعرف، كالشاة في العرف)','8483','2867','0');
INSERT INTO [t19188] VALUES (2872,'٢٧١٥ - مسألة: (والدابة اسم للذكر والأنثى من الخيل والبغال والحمير)','8485','2867','0');
INSERT INTO [t19188] VALUES (2873,'٢٧١٦ - مسألة: (وإن وصى له بغير معين؛ كعبد من عبيده، صح، ويعطيه الورثة ما شاءوا)','8486','2867','0');
INSERT INTO [t19188] VALUES (2874,'٢٧١٧ - مسألة: (وإن لم يكن له عبيد، لم تصح الوصية، في أحد الوجهين)','8488','2867','0');
INSERT INTO [t19188] VALUES (2875,'٢٧١٨ - مسألة: (فإن كان له عبيد فماتوا إلا واحدا، تعينت الوصية فيه)','8489','2867','0');
INSERT INTO [t19188] VALUES (2876,'٢٧١٩ - مسألة: (وإن وصى له بقوس، وله أقواس للرمي والبندق والندف','8490','2867','0');
INSERT INTO [t19188] VALUES (2877,'٢٧٢٠ - مسألة: وإن وصى له بطبل حرب، صحت الوصية به؛ لأن فيه منفعة مباحة','8493','2867','0');
INSERT INTO [t19188] VALUES (2878,'٢٧٢١ - مسألة: (وتنفذ الوصية فيما علم من ماله أو لم يعلم)','8494','2867','0');
INSERT INTO [t19188] VALUES (2879,'٢٧٢٢ - مسألة: (وإن وصى بثلثه فاستحدث مالا، دخل ثلثه في الوصية)','8495','2867','0');
INSERT INTO [t19188] VALUES (2880,'٢٧٢٣ - مسألة: (وإن قتل وأخذت ديته، فهل تدخل الدية في الوصية؟ على روايتين)','8496','2867','0');
INSERT INTO [t19188] VALUES (2881,'٢٧٢٤ - مسألة: (فإن وصى بمعين بقدر نصف الدية، فهل الدية على الورثة من الثلثين؟ على وجهين)','8498','2867','0');
INSERT INTO [t19188] VALUES (2882,'٢٧٢٥ - مسألة: (إذا أوصى)','8500','2867','0');
INSERT INTO [t19188] VALUES (2883,'٢٧٢٦ - مسألة: (ولهم ولاية تزويجها)','8501','2867','0');
INSERT INTO [t19188] VALUES (2884,'٢٧٢٧ - مسألة: ومهرها هاهنا وفي كل موضع وجب للورثة، في اختيار شيخنا (لأن منافع البضع لا تصح الوصية بها)','8502','2867','0');
INSERT INTO [t19188] VALUES (2885,'٢٧٢٨ - مسألة: (وإن وطئت بشبهة، فالولد حر)','8503','2867','0');
INSERT INTO [t19188] VALUES (2886,'٢٧٢٩ - مسألة: (وإن قتلت، فللورثة قيمتها، في أحد الوجهين)','8504','2867','0');
INSERT INTO [t19188] VALUES (2887,'٢٧٣٠ - مسألة: (وللوصي استخدامها وإجارتها وإعارتها)','8505','2867','0');
INSERT INTO [t19188] VALUES (2888,'٢٧٣١ - مسألة: (وليس لواحد منهما وطؤها)','8505','2867','0');
INSERT INTO [t19188] VALUES (2889,'٢٧٣٢ - مسألة: (وإن ولدت من زوج أو زنى، فحكمه حكمها)','8506','2867','0');
INSERT INTO [t19188] VALUES (2890,'٢٧٣٣ - مسألة: (وفي نفقتها ثلاثة أوجه؛ أحدها)','8507','2867','0');
INSERT INTO [t19188] VALUES (2891,'٢٧٣٤ - مسألة: (وفي اعتبارها من الثلث وجهان؛ أحدهما، يعتبر جميعها من الثلث)','8509','2867','0');
INSERT INTO [t19188] VALUES (2892,'٢٧٣٥ - مسألة: (وإن وصى لرجل برقبتها ولآخر بمنفعتها، صح. وصاحب الرقبة كالوارث فيما ذكرنا)','8510','2867','0');
INSERT INTO [t19188] VALUES (2893,'٢٧٣٦ - مسألة: تصح الوصية بالمكاتب، إذا قلنا: يصح بيعه','8512','2867','0');
INSERT INTO [t19188] VALUES (2894,'٢٧٣٧ - مسألة: (وإن وصى له بمال الكتابة، أو بنجم منها، صح)','8514','2867','0');
INSERT INTO [t19188] VALUES (2895,'٢٧٣٨ - مسألة: (وإن وصى لرجل برقبته ولآخر بما عليه، صح. فإن أدى)','8514','2867','0');
INSERT INTO [t19188] VALUES (2896,'٢٧٣٩ - مسألة: (وإن تلف المال كله غيره بعد موت الموصي، فهو للموصى له)','8518','2867','0');
INSERT INTO [t19188] VALUES (2897,'٢٧٤٠ - مسألة: (وإن لم يأخذه زمانا، قوم وقت الموت لا وقت الأخذ)','8518','2867','0');
INSERT INTO [t19188] VALUES (2898,'٢٧٤١ - مسألة: (فإن لم يكن له سوى المعين إلا مال غائب، أو دين في ذمة موسر أو معسر، فللموصى له ثلث الموصى به. وكلما اقتضي من الدين شيء، أو حضر من الغائب، ملك من الموصى به قدر ثلثه، حتى يملكه كله)','8520','2867','0');
INSERT INTO [t19188] VALUES (2899,'٢٧٤٢ - مسألة: (وكذلك الحكم في المدبر)','8522','2867','0');
INSERT INTO [t19188] VALUES (2900,'٢٧٤٣ - مسألة: (وإن وصى له بثلث عبد، فاستحق ثلثاه، فله الثلث الباقي. وإن وصى له بثلث ثلاثة أعبد، فاستحق اثنان منهم أو ماتا،','8525','2867','0');
INSERT INTO [t19188] VALUES (2901,'٢٧٤٤ - مسألة: (وإن وصى له بعبد لا يملك غيره، قيمته مائة، ولآخر بثلث ماله، وملكه غير العبد مائتان، فأجاز الورثة، فللموصى له بالثلث ثلث المائتين وربع العبد، وللموصى له بالعبد ثلاثة أرباعه. وإن ردوا، فقال الخرقي: للموصى له بالثلث سدس المائتين وسدس العبد، وللموصى له بالعبد نصفه)','8527','2867','0');
INSERT INTO [t19188] VALUES (2902,'٢٧٤٥ - مسألة: (وإن كانت الوصية بالنصف مكان الثلث فله)','8531','2867','0');
INSERT INTO [t19188] VALUES (2903,'٢٧٤٦ - مسألة: (وإن وصى لرجل بثلث ماله، ولآخر بمائة، ولثالث بتمام الثلث على المائة، فلم يزد الثلث على المائة)','8534','2867','0');
INSERT INTO [t19188] VALUES (2904,'باب الوصية بالأنصباء والأجزاء','8537','2803','0');
INSERT INTO [t19188] VALUES (2905,'٢٧٤٧ - مسألة: (فإذا وصى)','8538','2904','0');
INSERT INTO [t19188] VALUES (2906,'٢٧٤٨ - مسألة: (وإن وصى بنصيب ابنه، فكذلك في أحد الوجهين)','8538','2904','0');
INSERT INTO [t19188] VALUES (2907,'٢٧٤٩ - مسألة: (وإن وصى بضعف نصيب ابنه أو ضعفيه، فله مثله مرتين. وإن وصى بثلاثة أضعافه، فله ثلاثة أمثاله)','8540','2904','0');
INSERT INTO [t19188] VALUES (2908,'٢٧٥٠ - مسألة: (وإذا وصى)','8543','2904','0');
INSERT INTO [t19188] VALUES (2909,'٢٧٥١ - مسألة: (ولو وصى)','8545','2904','0');
INSERT INTO [t19188] VALUES (2910,'٢٧٥٢ - مسألة: فإن خلف أربعة بنين (فأوصى بمثل نصيب خامس لو كان إلا مثل نصيب سادس لو كان، فقد أوصى له بالخمس إلا السدس بعد الوصية، فله سهم يزاد على ثلاثين، وتصح من اثنين وستين، له سهمان، ولكل ابن خمسة عشر)','8546','2904','0');
INSERT INTO [t19188] VALUES (2911,'٢٧٥٣ - مسألة: (وإن وصى له بسهم من ماله، ففيه ثلاث روايات؛ إحداهن، له السدس بمنزلة سدس المفروض إن لم تكمل فروض المسألة، أو كانوا عصبة أعطي سدسا كاملا، وإن كملت فروضها، أعيلت به، وإن عالت أعيل معها. والثانية، له سهم مما تصح منه المسألة ما لم يزد على السدس. والثالثة، له مثل نصيب أقل الورثة ما لم يزد على السدس)','8552','2904','0');
INSERT INTO [t19188] VALUES (2912,'٢٧٥٤ - مسألة: (وإن وصى بجزء معلوم، كثلث أو ربع، أخذته من مخرجه فدفعته إليه، وقسمت الباقي على مسألة الورثة، إلا أن يزيد على الثلث ولا يجيزوا له، فتفرض له الثلث، وتقسم الثلثين عليها)','8558','2904','0');
INSERT INTO [t19188] VALUES (2913,'٢٧٥٥ - مسألة: (وإن وصى بجزأين أو أكثر، أخذتها من مخرجها، وقسمت الباقي على المسألة، فإن زادت على الثلث وردوا،','8558','2904','0');
INSERT INTO [t19188] VALUES (2914,'٢٧٥٦ - مسألة: «فإذا أوصى لرجل بثلث ماله، ولآخر بربعه، وخلف ابنين، أخذت الثلث والربع من مخرجهما سبعة من اثني عشر، يبقى للابنين خمسة إن أجازا، وإن ردا جعلت السبعة ثلث المال، فتكون المسألة من أحد وعشرين) للوصيين الثلث سبعة، ولصاحب الثلث أربعة، ولصاحب الربع ثلاثة، ولكل واحد من الابنين سبعة (فإن أجازا لأحدهما دون الآخر، أو أجاز أحدهما لهما دون الآخر، أو أجاز كل واحد)','8559','2904','0');
INSERT INTO [t19188] VALUES (2915,'٢٧٥٧ - مسألة: (وإن وصى لرجل بجميع ماله ولآخر بنصفه، وخلف ابنين، فالمال بينهما على ثلاثة إن أجيز لهما، والثلث على ثلاثة إن رد عليهما)','8567','2904','0');
INSERT INTO [t19188] VALUES (2916,'٢٧٥٨ - مسألة: (فإن أجازوا لصاحب النصف وحده)','8568','2904','0');
INSERT INTO [t19188] VALUES (2917,'٢٧٥٩ - مسألة: (فإن أجاز أحد الابنين لهما)','8569','2904','0');
INSERT INTO [t19188] VALUES (2918,'٢٧٦٠ - مسألة: (وإن كان الجزء الموصى به النصف، خرج فيها وجه ثالث، وهو أن يكون لصاحب النصيب في حال الإجازة ثلث الثلثين، وفي الرد يقسم الثلث بينهما على ثلاثة عشر سهما؛ لصاحب النصف تسعة، ولصاحب النصيب أربعة)','8571','2904','0');
INSERT INTO [t19188] VALUES (2919,'٢٧٦١ - مسألة: (إذا وصى لرجل بمثل نصيب أحد ابنيه، ولآخر بثلث باقي المال، فعلى الوجه الأول، لصاحب النصيب ثلث المال، وللآخر ثلث باقي المال تسعان، والباقي)','8574','2904','0');
INSERT INTO [t19188] VALUES (2920,'٢٧٦٢ - مسألة: (وإن كانت وصية الثاني بثلث ما يبقى من النصف)','8575','2904','0');
INSERT INTO [t19188] VALUES (2921,'٢٧٦٣ - مسألة: (وإن خلف أما وبنتا وأختا، وأوصى بمثل نصيب الأم وسبع ما بقي، ولآخر بمثل نصيب الأخت وربع ما بقي، ولآخر بمثل نصيب البنت وثلث ما بقي)','8581','2904','0');
INSERT INTO [t19188] VALUES (2922,'٢٧٦٤ - مسألة: (إذا خلف ثلاثة بنين، ووصى بمثل نصيب أحدهم إلا ربع المال، فخذ مخرج الكسر أربعة)','8584','2904','0');
INSERT INTO [t19188] VALUES (2923,'٢٧٦٥ - مسألة: (فإن قال: إلا ربع الباقي بعد الوصية. جعلت المخرج ثلاثة وزدت عليه واحدا صار أربعة، فهو النصيب، وتزيد','8585','2904','0');
INSERT INTO [t19188] VALUES (2924,'باب الموصى إليه','8597','2803','0');
INSERT INTO [t19188] VALUES (2925,'٢٧٦٦ - مسألة: (ولا تصح إلى غيرهم)','8601','2924','0');
INSERT INTO [t19188] VALUES (2926,'٢٧٦٧ - مسألة: (فإن كانوا على غير هذه الصفات، ثم وجدت عند الموت، فهل تصح؟ على وجهين)','8602','2924','0');
INSERT INTO [t19188] VALUES (2927,'٢٧٦٨ - مسألة: (وإذا أوصى إلى رجل وبعده إلى آخر، فهما وصيان، إلا أن يقول: قد أخرجت الأول)','8604','2924','0');
INSERT INTO [t19188] VALUES (2928,'٢٧٦٩ - مسألة: (وليس لأحدهما الانفراد بالتصرف إلا أن يجعل ذلك إليه)','8605','2924','0');
INSERT INTO [t19188] VALUES (2929,'٢٧٧٠ - مسألة: (وإن مات أحدهما أقام الحاكم مقامه أمينا)','8607','2924','0');
INSERT INTO [t19188] VALUES (2930,'٢٧٧١ - مسألة: (وكذلك إن فسق. وعنه، يضم إليه أمين)','8609','2924','0');
INSERT INTO [t19188] VALUES (2931,'٢٧٧٢ - مسألة: (ويصح قبوله للوصية)','8613','2924','0');
INSERT INTO [t19188] VALUES (2932,'٢٧٧٣ - مسألة: (وله عزل نفسه متى شاء)','8613','2924','0');
INSERT INTO [t19188] VALUES (2933,'٢٧٧٤ - مسألة: (وللموصي عزله متى شاء)','8614','2924','0');
INSERT INTO [t19188] VALUES (2934,'٢٧٧٥ - مسألة: (وليس للوصي أن يوصي إلا أن يجعل ذلك إليه. وعنه، له ذلك)','8614','2924','0');
INSERT INTO [t19188] VALUES (2935,'٢٧٧٦ - مسألة: (ولا تصح الوصية إلا في معلوم يملك الموصي فعله؛ كقضاء الدين، وتفريق الوصية، والنظر في أمر الأطفال)','8617','2924','0');
INSERT INTO [t19188] VALUES (2936,'٢٧٧٧ - مسألة: (وإذا أوصى إليه في شيء لم يصر وصيا في غيره)','8619','2924','0');
INSERT INTO [t19188] VALUES (2937,'٢٧٧٨ - مسألة: (وإذا أوصى إليه بتفرقة ثلثه فأبى الورثة إخراج ثلث ما في أيديهم)','8621','2924','0');
INSERT INTO [t19188] VALUES (2938,'٢٧٧٩ - مسألة: (وإن أوصاه بقضاء دين معين فأبى الورثة ذلك، قضاه بغير علمهم)','8623','2924','0');
INSERT INTO [t19188] VALUES (2939,'٢٧٨٠ - مسألة: (وتصح وصية الكافر إلى المسلم)','8626','2924','0');
INSERT INTO [t19188] VALUES (2940,'٢٧٨١ - مسألة: (إذا قال: ضع ثلثي حيث شئت. أو: أعطه من شئت. لم يجز له أخذه ولا دفعه إلى ولده)','8627','2924','0');
INSERT INTO [t19188] VALUES (2941,'٢٧٨٢ - مسألة: (وإن دعت الحاجة إلى بيع بعض العقار لقضاء دين الميت، أو حاجة الصغار، وفي بيع بعضه نقص، فله البيع على الكبار والصغار)','8629','2924','0');
INSERT INTO [t19188] VALUES (2942,'كتاب الفرائض','8634','0','0');
INSERT INTO [t19188] VALUES (2943,'[باب. . .]','8634','2942','0');
INSERT INTO [t19188] VALUES (2944,'٢٧٨٣ - مسألة: (وأسباب التوارث ثلاثة؛ رحم، ونكاح، وولاء، لا غير)','8636','2943','0');
INSERT INTO [t19188] VALUES (2945,'٢٧٨٤ - مسألة: (والمجمع على توريثهم من الذكور عشرة؛ الابن، وابنه وإن نزل، والأب، وأبوه وإن علا، والأخ من كل جهة، وابن الأخ إلا من الأم، والعم، وابنه كذلك، والزوج، ومولى النعمة. ومن النساء سبع؛ البنت، وبنت الابن، والأم، والجدة، والأخت، والمرأة، ومولاة النعمة)','8639','2943','0');
INSERT INTO [t19188] VALUES (2946,'٢٧٨٥ - مسألة: (والوراث ثلاثة؛ ذوو فرض، وعصبات، وذوو رحم)','8641','2943','0');
INSERT INTO [t19188] VALUES (2947,'باب ميراث ذوي الفروض','8642','2942','0');
INSERT INTO [t19188] VALUES (2948,'٢٧٨٦ - مسألة: (فإن لم يفضل عن الفرض إلا السدس فهو له، ويسقط من معه)','8654','2947','0');
INSERT INTO [t19188] VALUES (2949,'٢٧٨٧ - مسألة: (فإن لم يكن)','8659','2947','0');
INSERT INTO [t19188] VALUES (2950,'٢٧٨٨ - مسألة: (وولد الأب كولد الأبوين في مقاسمة الجد إذا انفردوا)','8660','2947','0');
INSERT INTO [t19188] VALUES (2951,'٢٧٨٩ - مسألة: (إلا أن يكون ولد الأبوين أختا واحدة، فتأخذ تمام النصف، وما فضل)','8662','2947','0');
INSERT INTO [t19188] VALUES (2952,'٢٧٩٠ - مسألة: (فإن كان معهم أم فلها السدس، وللجد ثلث الباقي)','8663','2947','0');
INSERT INTO [t19188] VALUES (2953,'٢٧٩١ - مسألة: (وعصبته عصبة أمه. وعنه، أنها هي عصبته)','8673','2947','0');
INSERT INTO [t19188] VALUES (2954,'٢٧٩٢ - مسألة: (فإذا خلف أما وخالا، فلأمه الثلث)','8676','2947','0');
INSERT INTO [t19188] VALUES (2955,'٢٧٩٣ - مسألة: (وإذا مات ابن ابن الملاعنة وخلف أمه','8681','2947','0');
INSERT INTO [t19188] VALUES (2956,'٢٧٩٤ - مسألة: (فإن كان بعضهن أقرب من بعض فالميراث لأقربهن. وعنه، أن القربى من جهة الأب لا تحجب البعدى من جهة الأم)','8689','2947','0');
INSERT INTO [t19188] VALUES (2957,'٢٧٩٥ - مسألة: (ولا يرث أكثر من ثلاث جدات، أم الأم، وأم الأب، وأم الجد، ومن كان من أمهاتهن وإن علت درجتهن)','8691','2947','0');
INSERT INTO [t19188] VALUES (2958,'٢٧٩٦ - مسألة: (والجدات المتحاذيات أم أم أم وأم أم أب وأم أبي أب)','8693','2947','0');
INSERT INTO [t19188] VALUES (2959,'٢٧٩٧ - مسألة: (وترث الجدة وابنها حي. وعنه، لا ترث)','8694','2947','0');
INSERT INTO [t19188] VALUES (2960,'٢٧٩٨ - مسألة: (فإن اجتمعت جدة ذات قرابتين مع أخرى)','8697','2947','0');
INSERT INTO [t19188] VALUES (2961,'٢٧٩٩ - مسألة؛ قال، رحمه الله: (وللبنت الواحدة النصف)','8699','2947','0');
INSERT INTO [t19188] VALUES (2962,'٢٨٠٠ - مسألة: (وبنات الابن بمنزلة البنات إذا لم يكن بنات)','8701','2947','0');
INSERT INTO [t19188] VALUES (2963,'٢٨٠١ - مسألة: (فإن كانت بنت وبنات ابن، فللبنت النصف، ولبنات الابن -واحدة كانت أو أكثر من ذلك- السدس تكملة الثلثين، إلا أن يكون معهن ذكر فيعصبهن فيما بقي، للذكر مثل حظ الأنثيين)','8702','2947','0');
INSERT INTO [t19188] VALUES (2964,'٢٨٠٢ - مسألة: (وإن استكمل البنات الثلثين سقط بنات الابن، إلا أن يكون معهن أو أنزل منهن ذكر فيعصبهن فيما بقي)','8704','2947','0');
INSERT INTO [t19188] VALUES (2965,'٢٨٠٣ - مسألة: (والأخوات مع البنات عصبة، يرثن ما فضل كالإخوة، وليست لهن معهن فريضة مسماة)','8708','2947','0');
INSERT INTO [t19188] VALUES (2966,'٢٨٠٤ - مسألة: (و)','8712','2947','0');
INSERT INTO [t19188] VALUES (2967,'٢٨٠٥ - مسألة: (ويسقط ولد الأم بأربعة؛ بالولد ذكرا كان أو أنثى وولد الابن، والأب، والجد)','8713','2947','0');
INSERT INTO [t19188] VALUES (2968,'باب العصبات','8717','2942','0');
INSERT INTO [t19188] VALUES (2969,'٢٨٠٦ - مسألة: (فإذا انقرض العصبة من النسب، ورث المولى المعتق، ثم عصباته)','8720','2968','0');
INSERT INTO [t19188] VALUES (2970,'٢٨٠٧ - مسألة: (وأربعة من الذكور يعصبون أخواتهم فيمنعونهن الفرض، ويقتسمون ما ورثوا للذكر مثل حظ الأنثيين، وهم','8720','2968','0');
INSERT INTO [t19188] VALUES (2971,'٢٨٠٨ - مسألة: (وابن ابن الابن يعصب من بإزائه من أخواته وبنات عمه وبنات عم أبيه)','8721','2968','0');
INSERT INTO [t19188] VALUES (2972,'٢٨٠٩ - مسألة: (ومتى كان بعض بني الأعمام زوجا أو أخا لأم، أخذ فرضه وشارك الباقين في تعصيبهم)','8723','2968','0');
INSERT INTO [t19188] VALUES (2973,'٢٨١٠ - مسألة: (وإذا اجتمع ذو فرض وعصبة، بدئ بذي الفرض فأخذ فرضه، وما بقي للعصبة)','8728','2968','0');
INSERT INTO [t19188] VALUES (2974,'٢٨١١ - مسألة: (وإن استغرقت الفروض المال فلا شيء للعصبة، كزوج وأم وإخوة لأم وإخوة لأبوين أو لأب، فللزوج النصف، وللأم السدس، وللإخوة للأم الثلث، وسقط سائرهم)','8728','2968','0');
INSERT INTO [t19188] VALUES (2975,'٢٨١٢ - مسألة: (ولو كان مكانهم أخوات لأبوين أو لأب عالت إلى عشرة، وسميت ذات الفروخ)','8733','2968','0');
INSERT INTO [t19188] VALUES (2976,'باب أصول المسائل','8738','2942','0');
INSERT INTO [t19188] VALUES (2977,'٢٨١٣ - مسألة: (الفروض ستة)','8738','2976','0');
INSERT INTO [t19188] VALUES (2978,'٢٨١٤ - مسألة؛ قال','8741','2976','0');
INSERT INTO [t19188] VALUES (2979,'٢٨١٥ - مسألة: (وإن اجتمع مع الثمن سدس أو ثلثان، فأصلها','8742','2976','0');
INSERT INTO [t19188] VALUES (2980,'٢٨١٦ - مسألة: (فإن كان المردود عليه واحدا أخذ المال كله)','8747','2976','0');
INSERT INTO [t19188] VALUES (2981,'٢٨١٧ - مسألة: (وإن اختلفت أجناسهم، فخذ عدد سهامهم من أصل ستة، فاجعله أصل مسألتهم)','8748','2976','0');
INSERT INTO [t19188] VALUES (2982,'٢٨١٨ - مسألة: (فإن انكسر على فريق منهم ضربته في عدد سهامهم؛ لأنه أصل مسألتهم)','8749','2976','0');
INSERT INTO [t19188] VALUES (2983,'٢٨١٩ - مسألة: (فإن كان معهم أحد الزوجين أعطيته فرضه من أصل مسألته، وقسمت الباقي على مسألة الرد)','8750','2976','0');
INSERT INTO [t19188] VALUES (2984,'باب تصحيح المسائل','8754','2942','0');
INSERT INTO [t19188] VALUES (2985,'٢٨٢٠ - مسألة: (وإن انكسر على فريقين أو أكثر)','8755','2984','0');
INSERT INTO [t19188] VALUES (2986,'باب المناسخات','8763','2942','0');
INSERT INTO [t19188] VALUES (2987,'٢٨٢١ - مسألة: (فإن مات ثالث جمعت سهامه مما صحت منه الأوليان، وعملت فيها عملك في مسألة الثاني مع الأول. وكذلك تصنع في الرابع ومن بعده)','8770','2986','0');
INSERT INTO [t19188] VALUES (2988,'باب قسم التركات','8772','2942','0');
INSERT INTO [t19188] VALUES (2989,'٢٨٢٢ - مسألة: (وإن شئت في مسائل المناسخات قسمت التركة على المسألة الأولى، ثم أخذت نصيب الثاني فقسمته على مسألته، وكذلك الثالث، فإن كان بين التركة والمسألة موافقة)','8774','2988','0');
INSERT INTO [t19188] VALUES (2990,'٢٨٢٣ - مسألة: (وإن أردت القسمة على قراريط الدينار فاجعل','8775','2988','0');
INSERT INTO [t19188] VALUES (2991,'٢٨٢٤ - مسألة: (وإن كانت التركة سهاما من عقار؛ كثلث','8777','2988','0');
INSERT INTO [t19188] VALUES (2992,'باب ذوي الأرحام','8782','2942','0');
INSERT INTO [t19188] VALUES (2993,'٢٨٢٥ - مسألة: (ويورثون بالتنزيل، فيجعل كل وارث بمنزلة من أدلى به، فيجعل ولد البنات والأخوات كأمهاتهم، وبنات الإخوة والأعمام وولد الإخوة من الأم كآبائهم، والأخوال والخالات وأبو الأم كالأم، والعمات والعم من الأم كالأب. وعنه، كالعم. ثم تجعل نصيب كل وارث لمن أدلى به)','8788','2992','0');
INSERT INTO [t19188] VALUES (2994,'٢٨٢٦ - مسألة: (فإن أدلى جماعة منهم بواحد، واستوت منازلهم منه، فنصيبه بينهم بالسوية، ذكرهم وأنثاهم سواء. وعنه، للذكر مثل','8791','2992','0');
INSERT INTO [t19188] VALUES (2995,'٢٨٢٧ - مسألة: (وإذا كان ابن وبنت أخت وبنت أخت أخرى، فلبنت الأخت وحدها النصف، وللأخرى وأخيها النصف بينهما)','8799','2992','0');
INSERT INTO [t19188] VALUES (2996,'٢٨٢٨ - مسألة: (وإن اختلفت منازلهم من المدلى به جعلته كالميت، وقسمت نصيبه بينهم على ذلك، كثلاث خالات مفترقات وثلاث عمات مفترقات، فالثلث بين الخالات على خمسة أسهم، والثلثان بين العمات كذلك، فاجتزئ بإحداهما، واضربها في ثلاثة تكن خمسة عشر؛ للخالة التي من قبل الأب والأم ثلاثة أسهم)','8804','2992','0');
INSERT INTO [t19188] VALUES (2997,'٢٨٢٩ - مسألة: (فإن خلف ثلاثة أخوال مفترقين، فللخال من الأم السدس، والباقي للخال من الأبوين)','8806','2992','0');
INSERT INTO [t19188] VALUES (2998,'٢٨٣٠ - مسألة: (وإن كان معهم أبو أم أسقطهم، كما يسقط الأب الإخوة،)','8806','2992','0');
INSERT INTO [t19188] VALUES (2999,'٢٨٣١ - مسألة: (وإن خلف ثلاث بنات عمومة مفترقين، فالمال لبنت العم من الأبوين وحدها)','8810','2992','0');
INSERT INTO [t19188] VALUES (3000,'٢٨٣٢ - مسألة: (فإن أدلى جماعة منهم بجماعة، قسمت المال بين المدلى بهم كأنهم أحياء، فما صار لكل وارث فهو لمن أدلى به)','8812','2992','0');
INSERT INTO [t19188] VALUES (3001,'٢٨٣٣ - مسألة: (والجهات أربع؛ الأبوة، والأمومة، والبنوة، والأخوة)','8815','2992','0');
INSERT INTO [t19188] VALUES (3002,'٢٨٣٤ - مسألة: (ومن أمت بقرابتين ورث بهما)','8821','2992','0');
INSERT INTO [t19188] VALUES (3003,'٢٨٣٥ - مسألة: (فإن اتفق معهم أحد الزوجين أعطته فرضه غير محجوب ولا معاول)','8823','2992','0');
INSERT INTO [t19188] VALUES (3004,'٢٨٣٦ - مسألة: (ولا يعول من مسائل ذوي الأرحام إلا مسألة واحدة وشبهها)','8826','2992','0');
INSERT INTO [t19188] VALUES (3005,'باب ميراث الحمل','8827','2942','0');
INSERT INTO [t19188] VALUES (3006,'٢٨٣٧ - مسألة: (فإذا وضع الحمل دفعت إليه نصيبه ورددت الباقي إلى مستحفه)','8830','3005','0');
INSERT INTO [t19188] VALUES (3007,'٢٨٣٨ - مسألة: (وإن خرج بعضه فاستهل ثم انفصل ميتا لم يرث)','8836','3005','0');
INSERT INTO [t19188] VALUES (3008,'٢٨٣٩ - مسألة: (وإن ولدت توأمين فاستهل أحدهما وأشكل، أقرع بينهما)','8837','3005','0');
INSERT INTO [t19188] VALUES (3009,'باب ميراث المفقود','8843','2942','0');
INSERT INTO [t19188] VALUES (3010,'٢٨٤٠ - مسألة: وإن مات للمفقود من يرثه قبل الحكم بوفاته','8848','3009','0');
INSERT INTO [t19188] VALUES (3011,'٢٨٤١ - مسألة: (ولباقي الورثة أن يصطلحوا على ما زاد عن نصيبه فيقسموه)','8851','3009','0');
INSERT INTO [t19188] VALUES (3012,'باب ميراث الخنثى','8856','2942','0');
INSERT INTO [t19188] VALUES (3013,'٢٨٤٢ - مسألة: (ويعتبر بمباله)','8856','3012','0');
INSERT INTO [t19188] VALUES (3014,'٢٨٤٣ - مسألة: (فإن)','8858','3012','0');
INSERT INTO [t19188] VALUES (3015,'٢٨٤٤ - مسألة: (فإذا كان ابن، وبنت)','8861','3012','0');
INSERT INTO [t19188] VALUES (3016,'٢٨٤٥ - مسألة: (فإن كانا خنثيين أو أكثر نزلتهم بعدد أحوالهم)','8866','3012','0');
INSERT INTO [t19188] VALUES (3017,'باب ميراث الغرقى ومن عمي موتهم','8870','2942','0');
INSERT INTO [t19188] VALUES (3018,'٢٨٤٦ - مسألة قال: (فلو غرق أخوان، أحدهما مولى زيد والآخر مولى عمرو)','8876','3017','0');
INSERT INTO [t19188] VALUES (3019,'باب ميراث أهل الملل','8880','2942','0');
INSERT INTO [t19188] VALUES (3020,'٢٨٤٧ - مسألة: (إلا أن يسلم قبل قسم الميراث فيرثه. وعنه، لا يرث)','8882','3019','0');
INSERT INTO [t19188] VALUES (3021,'٢٨٤٨ - مسألة: (وإن عتق عبد بعد موت موروثه وقبل القسم لم يرث، وجها واحدا)','8885','3019','0');
INSERT INTO [t19188] VALUES (3022,'٢٨٤٩ - مسألة: (ويرث أهل الذمة بعضهم بعضا إن اتفقت أديانهم)','8887','3019','0');
INSERT INTO [t19188] VALUES (3023,'٢٨٥٠ - مسألة: (وهم ثلاث ملل؛ اليهودية، والنصرانية، ودين سائرهم)','8888','3019','0');
INSERT INTO [t19188] VALUES (3024,'٢٨٥١ - مسألة: (وإن اختلفت أديانهم لم يتوارثوا)','8890','3019','0');
INSERT INTO [t19188] VALUES (3025,'٢٨٥٢ - مسألة: (ولا يرث حربي ذميا، ولا ذمي حربيا. ذكره القاضي)','8891','3019','0');
INSERT INTO [t19188] VALUES (3026,'٢٨٥٣ - مسألة: (والمرتد لا يرث أحدا، إلا أن يسلم قبل قسم الميراث)','8893','3019','0');
INSERT INTO [t19188] VALUES (3027,'٢٨٥٤ - مسألة: (وإن مات على ردته فماله فيء. وعنه، أنه لورثته من المسلمين. وعنه، أنه لورثته من أهل الدين الذي اختاره)','8894','3019','0');
INSERT INTO [t19188] VALUES (3028,'٢٨٥٥ - مسألة: (إذا خلف أمه، وهي أخته من أبيه، وعما)','8903','3019','0');
INSERT INTO [t19188] VALUES (3029,'٢٨٥٦ - مسألة: (ولا يرثون بنكاح ذوات المحارم، ولا بنكاح لا يقرون عليه لو أسلموا)','8903','3019','0');
INSERT INTO [t19188] VALUES (3030,'باب ميراث المطلقة','8913','2942','0');
INSERT INTO [t19188] VALUES (3031,'٢٨٥٧ - مسألة: (وإن كان)','8914','3030','0');
INSERT INTO [t19188] VALUES (3032,'٢٨٥٨ - مسألة: (وإن طلقها في مرض الموت المخوف طلاقا لا يتهم فيه؛ بأن سألته الطلاق، أو علق طلاقها على فعل لها منه بد ففعلته، أو علقه على شرط في الصحة فوجد في المرض، أو طلق من لا ترث كالأمة والذمية فعتقت وأسلمت، فهو كطلاق الصحيح في أصح الروايتين)','8914','3030','0');
INSERT INTO [t19188] VALUES (3033,'٢٨٥٩ - مسألة: فإن طلق الزوج المسلم امرأته الذمية أو الأمة في المرض طلاقا بائنا، ثم أسلمت الذمية وعتقت الأمة، ثم مات في عدتهما، لم ترثاه؛ لأنه لم يكن عند الطلاق فارا','8917','3030','0');
INSERT INTO [t19188] VALUES (3034,'٢٨٦٠ - مسألة: وإن قال سيد الأمة: أنت حرة غدا. فطلقها اليوم وهو يعلم بقول السيد، ورثته؛ لأنه فار، وإن لم يعلم لم ترثه؛ لعدم الفرار','8917','3030','0');
INSERT INTO [t19188] VALUES (3035,'٢٨٦١ - مسألة: (وإن كان متهما بقصد حرمانها الميراث؛ مثل أن طلقها ابتداء، أو علقه على فعل لا بد لها منه كالصلاة ونحوها ففعلته، أو قال للأمة أو الذمية: إذا أسلمت أو عتقت فأنت طالق. أو علم أن سيد الأمة قال لها: أنت حرة غدا. فطلقها اليوم، ورثته، ما دامت في العدة، ولم يرثها)','8918','3030','0');
INSERT INTO [t19188] VALUES (3036,'٢٨٦٢ - مسألة: وإن علق طلاقها على فعل لا بد لها منه','8920','3030','0');
INSERT INTO [t19188] VALUES (3037,'٢٨٦٣ - مسألة: (وهل ترثه بعد العدة، أو ترثه المطلقة قبل الدخول؟ على روايتين)','8920','3030','0');
INSERT INTO [t19188] VALUES (3038,'٢٨٦٤ - مسألة: (وإن تزوجت)','8922','3030','0');
INSERT INTO [t19188] VALUES (3039,'٢٨٦٥ - مسألة: (وإن أكره الابن امرأة أبيه في مرض أبيه على ما يفسخ نكاحها، لم يقطع ميراثها، إلا أن يكون له امرأة سواها)','8926','3030','0');
INSERT INTO [t19188] VALUES (3040,'٢٨٦٦ - مسألة: (وإن فعلت)','8929','3030','0');
INSERT INTO [t19188] VALUES (3041,'٢٨٦٧ - مسألة: (وإن خلف زوجات نكاح بعضهن فاسد أقرع بينهن، فمن أصابتها القرعة فلا ميراث لها)','8930','3030','0');
INSERT INTO [t19188] VALUES (3042,'٢٨٦٨ - مسألة: (إذا طلق أربع نسوة في مرضه فانقضت عدتهن، ثم تزوج أربعا سواهن، فالميراث للزوجات. وعنه، أنه بين الثمان)','8931','3030','0');
INSERT INTO [t19188] VALUES (3043,'٢٨٦٩ - مسألة: إذا ألحق باثنين، فمات وترك أما حرة، فلها الثلث، والباقي لهما. وإن كان لكل واحد منهما ابن سواه، أو لأحدهما ابنان، فلأمه السدس. وإن مات أحد الأبوين، وله ابن آخر، فماله بينهما نصفين، فإن مات الغلام بعد ذلك، فلأمه السدس والباقي للباقي','8943','3030','0');
INSERT INTO [t19188] VALUES (3044,'باب الإقرار بمشارك في الميراث','8949','2942','0');
INSERT INTO [t19188] VALUES (3045,'٢٨٧٠ - مسألة: (وسواء كان المقر به يحجب المقر أو لا يحجبه، كأخ يقر بابن للميت)','8953','3044','0');
INSERT INTO [t19188] VALUES (3046,'٢٨٧١ - مسألة: (وإن أقر بعضهم لم يثبت نسبه، إلا أن يشهد منهم عدلان أنه ولد على فراشه، أو أن الميت أقر به)','8955','3044','0');
INSERT INTO [t19188] VALUES (3047,'٢٨٧٢ - مسألة: (وعلى المقر أن يدفع إليه فضل ما في يده عن ميراثه)','8957','3044','0');
INSERT INTO [t19188] VALUES (3048,'٢٨٧٣ - مسألة: (فإن لم يكن في يد المقر فضل، فلا شيء للمقر به)','8961','3044','0');
INSERT INTO [t19188] VALUES (3049,'٢٨٧٤ - مسألة: (وطريق العمل)','8962','3044','0');
INSERT INTO [t19188] VALUES (3050,'٢٨٧٥ - مسألة: (وإن خلف ابنا فأقر بأخوين بكلام متصل)','8966','3044','0');
INSERT INTO [t19188] VALUES (3051,'٢٨٧٦ - مسألة: (فإن أقر بأحدهما بعد الآخر، أعطى الأول نصف ما في يده)','8967','3044','0');
INSERT INTO [t19188] VALUES (3052,'٢٨٧٧ - مسألة: (وإن أقر بعض الورثة بامرأة للميت لزمه من إرثها بقدر حصته)','8968','3044','0');
INSERT INTO [t19188] VALUES (3053,'٢٨٧٨ - مسألة: (إذا قال: مات أبي وأنت أخي. فقال: هو أبي ولست بأخي. لم يقبل إنكاره)','8975','3044','0');
INSERT INTO [t19188] VALUES (3054,'٢٨٧٩ - مسألة: (فإن قال: ماتت زوجتي وأنت أخوها. فقال: لست بزوجها. فهل يقبل إنكاره؟ على وجهين)','8976','3044','0');
INSERT INTO [t19188] VALUES (3055,'٢٨٨٠ - مسألة: (فإن كان معهم أختان من أم)','8978','3044','0');
INSERT INTO [t19188] VALUES (3056,'باب ميراث القاتل','8983','2942','0');
INSERT INTO [t19188] VALUES (3057,'٢٨٨١ - مسألة: (فأما ما لا يضمن بشيء من هذا؛ كالقتل قصاصا أو حدا أو دفعا عن نفسه، وقتل العادل الباغي، والباغي العادل، فلا يمنع. وعنه، لا يرث العادل الباغي، ولا الباغي العادل. فيخرج','8986','3056','0');
INSERT INTO [t19188] VALUES (3058,'باب ميراث المعتق بعضه','8991','2942','0');
INSERT INTO [t19188] VALUES (3059,'٢٨٨٢ - مسألة: (فأما المعتق بعضه، فما كسبه بجزئه الحر، فهو لورثته، ويرث ويحجب بقدر ما فيه من الحرية)','8996','3058','0');
INSERT INTO [t19188] VALUES (3060,'٢٨٨٣ - مسألة: (وإن كان عصبتان، نصف كل واحد منهما حر، كالأخوين، فهل تكمل الحرية فيهما؟ يحتمل وجهين. وإن كان أحدهما يحجب الآخر؛ كابن وابن ابن، فالصحيح أنها لا تكمل)','9001','3058','0');
INSERT INTO [t19188] VALUES (3061,'باب الولاء','9015','2942','0');
INSERT INTO [t19188] VALUES (3062,'٢٨٨٤ - مسألة: (كل من أعتق عبدا، أو عتق عليه برحم، أو كتابة، أو تدبير، أو استيلاد، أو وصية بعتقه، فله عليه الولاء، وعلى أولاده من زوجة معتقة أو من أمته، وعلى معتقيه ومعتقي أولاده وأولادهم','9016','3061','0');
INSERT INTO [t19188] VALUES (3063,'٢٨٨٥ - مسألة: (أو عتق عليه برحم)','9020','3061','0');
INSERT INTO [t19188] VALUES (3064,'٢٨٨٦ - مسألة: (أو استيلاد، أو وصية بعتقه)','9022','3061','0');
INSERT INTO [t19188] VALUES (3065,'٢٨٨٧ - مسألة: (ويرث به عند عدم العصبة من النسب)','9023','3061','0');
INSERT INTO [t19188] VALUES (3066,'٢٨٨٨ - مسألة: (ثم يرث به عصباته الأقرب فالأقرب)','9024','3061','0');
INSERT INTO [t19188] VALUES (3067,'٢٨٨٩ - مسألة: (وعنه في المكاتب إذا أدى إلى الورثة، أن ولاءه لهم)','9028','3061','0');
INSERT INTO [t19188] VALUES (3068,'٢٨٩٠ - مسألة: (ومن كان أحد أبويه)','9029','3061','0');
INSERT INTO [t19188] VALUES (3069,'٢٨٩١ - مسألة: (ومن أعتق سائبة، أو في زكاته، أو نذره،','9031','3061','0');
INSERT INTO [t19188] VALUES (3070,'٢٨٩٢ - مسألة: (ومن أعتق عبده عن حي بلا أمره أو عن ميت، فالولاء للمعتق)','9037','3061','0');
INSERT INTO [t19188] VALUES (3071,'٢٨٩٣ - مسألة: (وإن، أعتقه عنه بأمره، فالولاء للمعتق عنه)','9039','3061','0');
INSERT INTO [t19188] VALUES (3072,'٢٨٩٤ - مسألة: (وإذا قال: أعتق عبدك عني، وعلي ثمنه. ففعل، فالثمن عليه، والولاء للمعتق عنه)','9041','3061','0');
INSERT INTO [t19188] VALUES (3073,'٢٨٩٥ - مسألة: (ولو قال: أعتقه والثمن علي. ففعل، فالثمن عليه، والولاء للمعتق)','9042','3061','0');
INSERT INTO [t19188] VALUES (3074,'٢٨٩٦ - مسألة: (وإن قال الكافر لرجل: أعتق عبدك المسلم عني، وعلي ثمنه. ففعل، فهل يصح؟ على وجهين)','9043','3061','0');
INSERT INTO [t19188] VALUES (3075,'٢٨٩٧ - مسألة: (وإن أعتق عبدا يباينه في دينه فله ولاؤه. وهل يرث به؟ على روايتين؛ إحداهما، لا يرث، لكن إن كان له عصبة على دين المعتق ورث. فإن أسلم الكافر منهما ورث المعتق، رواية واحدة)','9044','3061','0');
INSERT INTO [t19188] VALUES (3076,'٢٨٩٨ - مسألة: فإن كان للسيد عصبة على دين المعتق، ورثه دون سيده','9046','3061','0');
INSERT INTO [t19188] VALUES (3077,'٢٨٩٩ - مسألة: (ولا يرث)','9052','3061','0');
INSERT INTO [t19188] VALUES (3078,'٢٩٠٠ - مسألة: (والجد يرث الثلث مع الإخوة إن كان أحظ له)','9053','3061','0');
INSERT INTO [t19188] VALUES (3079,'٢٩٠١ - مسألة: (والولاء لا يورث، وإنما يورث به)','9056','3061','0');
INSERT INTO [t19188] VALUES (3080,'٢٩٠٢ - مسألة: (ولا يباع ولا يوهب)','9058','3061','0');
INSERT INTO [t19188] VALUES (3081,'٢٩٠٣ - مسألة: (وهو للكبر، فإذا مات المعتق وخلف عتيقه وابنين، فمات أحد الابنين عن ابن، ثم مات)','9059','3061','0');
INSERT INTO [t19188] VALUES (3082,'٢٩٠٤ - مسألة: (وإذا اشترى رجل وأخته أباهما أو أخاهما، عتق عليهما)','9062','3061','0');
INSERT INTO [t19188] VALUES (3083,'٢٩٠٥ - مسألة: (وإن أعتق الجد لم يجر ولاءهم، في أصح الروايتين. وعنه، يجره)','9070','3061','0');
INSERT INTO [t19188] VALUES (3084,'٢٩٠٦ - مسألة: (وإن اشترى الابن أباه، عتق عليه، وله ولاؤه','9072','3061','0');
INSERT INTO [t19188] VALUES (3085,'٢٩٠٧ - مسألة: (وإن اشترى)','9073','3061','0');
INSERT INTO [t19188] VALUES (3086,'٢٩٠٨ - مسألة: وإذا ماتت امرأة وخلفت ابنها وعصبتها ومولاها، فولاؤه','9075','3061','0');
INSERT INTO [t19188] VALUES (3087,'كتاب العتق','9085','0','0');
INSERT INTO [t19188] VALUES (3088,'[باب. . .]','9085','3087','0');
INSERT INTO [t19188] VALUES (3089,'٢٩٠٩ - مسألة: (وهو من أفضل القرب)','9086','3088','0');
INSERT INTO [t19188] VALUES (3090,'٢٩١٠ - مسألة: (والمستحب عتق من له كسب)','9087','3088','0');
INSERT INTO [t19188] VALUES (3091,'٢٩١١ - مسألة: (ويحصل العتق بالقول والملك)','9090','3088','0');
INSERT INTO [t19188] VALUES (3092,'٢٩١٢ - مسألة: (وفي قوله لأمته: أنت طالق، وأنت حرام. روايتان؛ إحداهما، هي كناية. والأخرى، لا تعتق به [وإن نوى)','9096','3088','0');
INSERT INTO [t19188] VALUES (3093,'٢٩١٣ - مسألة: (وإن قال لعبده، وهو أكبر منه: أنت ابني. لم يعتق. ذكره القاضي. ويحتمل أن يعتق)','9098','3088','0');
INSERT INTO [t19188] VALUES (3094,'٢٩١٤ - مسألة: (وإن أعتق حاملا عتق جنينها، إلا أن يستثنيه)','9099','3088','0');
INSERT INTO [t19188] VALUES (3095,'٢٩١٥ - مسألة: (وإن أعتق ما في بطنها دونها، عتق وحده)','9101','3088','0');
INSERT INTO [t19188] VALUES (3096,'٢٩١٦ - مسألة: (وأما الملك، فمن ملك ذا رحم محرم عتق عليه. وعنه، لا يعتق إلا عمودا النسب)','9104','3088','0');
INSERT INTO [t19188] VALUES (3097,'٢٩١٧ - مسألة: (وإن ملك ولده من الزنى لم يعتق)','9107','3088','0');
INSERT INTO [t19188] VALUES (3098,'٢٩١٨ - مسألة: (وإن ملك سهما ممن يعتق عليه بغير الميراث وهو موسر عتق عليه كله، وعليه قيمة نصيب شريكه. وإن كان معسرا لم يعتق عليه إلا ما ملك. وإن ملكه بالميراث لم يعتق منه إلا ما ملك، موسرا كان أو معسرا. وعنه، أنه يعتق عليه نصيب الشريك إن كان موسرا)','9108','3088','0');
INSERT INTO [t19188] VALUES (3099,'٢٩١٩ - مسألة: (وإن مثل بعبده فجدع أنفه أو أذنه ونحو ذلك، عتق. نص عليه)','9115','3088','0');
INSERT INTO [t19188] VALUES (3100,'٢٩٢٠ - مسألة: (وإذا أعتق عبدا فماله لسيده)','9117','3088','0');
INSERT INTO [t19188] VALUES (3101,'٢٩٢١ - مسألة: (وإن أعتق شركا له في عبد، وهو موسر بقيمة باقيه، عتق كله، وعليه قيمة باقيه يوم العتق لشريكه)','9122','3088','0');
INSERT INTO [t19188] VALUES (3102,'٢٩٢٢ - مسألة: (فإن أعتقه الشريك بعد ذلك)','9125','3088','0');
INSERT INTO [t19188] VALUES (3103,'٢٩٢٣ - مسألة: (وإن كان معسرا لم يعتق إلا نصيبه، وبقي حق شريكه فيه. وعنه، يعتق كله، ويستسعى العبد في قيمة باقيه غير مشقوق عليه)','9130','3088','0');
INSERT INTO [t19188] VALUES (3104,'٢٩٢٤ - مسألة: (وإذا كان العبد لثلاثة؛ لأحدهم نصفه، وللآخر ثلثه، ولثالث سدسه، فأعتق صاحب النصف وصاحب السدس معا وهما موسران، عتق عليهما وضمنا حق شريكهما فيه نصفين، وصار ولاؤه بينهما أثلاثا. ويحتمل أن يضمناه على قدر ملكهما فيه)','9134','3088','0');
INSERT INTO [t19188] VALUES (3105,'٢٩٢٥ - مسألة: (وإذا أعتق الكافر نصيبه من مسلم وهو موسر، سرى إلى باقيه، في أحد الوجهين)','9137','3088','0');
INSERT INTO [t19188] VALUES (3106,'٢٩٢٦ - مسألة: (وإن ادعى كل واحد من الشريكين أن شريكه أعتق نصيبه وهما موسران، فقد صار العبد حرا باعتراف كل واحد منهما لحريته','9138','3088','0');
INSERT INTO [t19188] VALUES (3107,'٢٩٢٧ - مسألة: (وإن كانا معسرين لم يعتق على واحد منهما)','9139','3088','0');
INSERT INTO [t19188] VALUES (3108,'٢٩٢٨ - مسألة: (وإن اشترى أحدهما نصيب صاحبه عتق)','9141','3088','0');
INSERT INTO [t19188] VALUES (3109,'٢٩٢٩ - مسألة: (وإن كان أحدهما موسرا والآخر معسرا، عتق نصيب المعسر وحده)','9144','3088','0');
INSERT INTO [t19188] VALUES (3110,'٢٩٣٠ - مسألة: (وإذا قال أحد الشريكين: إذا أعتقت نصيبك فنصيبي حر. فأعتق الأول وهو موسر، عتق كله عليه)','9145','3088','0');
INSERT INTO [t19188] VALUES (3111,'٢٩٣١ - مسألة: (وإن كان معسرا)','9146','3088','0');
INSERT INTO [t19188] VALUES (3112,'٢٩٣٢ - مسألة: (وإن قال: إذا أعتقت نصيبك فنصيبي حر مع نصيبك. فأعتق نصيبه، عتق عليهما، موسرا كان أو معسرا)','9147','3088','0');
INSERT INTO [t19188] VALUES (3113,'٢٩٣٣ - مسألة: وإذا علق عتق عبده بشرط، كقوله: إن أديت إلي ألفا فأنت حر. أو: إن دخلت الدار فأنت حر. فهي صفة لازمة، ألزمها نفسه (ولا يملك إبطالها بالقول)','9153','3088','0');
INSERT INTO [t19188] VALUES (3114,'٢٩٣٤ - مسألة: (إلا أن تكون)','9156','3088','0');
INSERT INTO [t19188] VALUES (3115,'٢٩٣٥ - مسألة: (وتبطل الصفة بالموت)','9158','3088','0');
INSERT INTO [t19188] VALUES (3116,'٢٩٣٦ - مسألة: (فإن قال: إن دخلت الدار [فأنت حر بعد موتي]','9158','3088','0');
INSERT INTO [t19188] VALUES (3117,'٢٩٣٧ - مسألة: (وإن قال: إن دخلت الدار فأنت حر بعد موتي. فدخل في حياة السيد، صار مدبرا)','9163','3088','0');
INSERT INTO [t19188] VALUES (3118,'٢٩٣٨ - مسألة: (وإذا قال: إن ملكت فلانا فهو حر. أو: كل مملوك أملكه فهو حر. فهل يصح؟ على روايتين)','9164','3088','0');
INSERT INTO [t19188] VALUES (3119,'٢٩٣٩ - مسألة: فإن قال العبد ذلك ثم عتق وملك','9167','3088','0');
INSERT INTO [t19188] VALUES (3120,'٢٩٤٠ - مسألة: (فإن قال: آخر مملوك أشتريه فهو حر. فملك عبيدا)','9169','3088','0');
INSERT INTO [t19188] VALUES (3121,'٢٩٤١ - مسألة: (فإن قال لأمته: آخر ولد تلدينه فهو حر. فولدت حيا ثم ميتا، لم يعتق الأول)','9172','3088','0');
INSERT INTO [t19188] VALUES (3122,'٢٩٤٢ - مسألة: (ولا يتبع ولد المعتقة بالصفة أمه في العتق، في أصح الوجهين، إلا أن تكون حاملا به حال عتقها، أو حال تعليق عتقها)','9173','3088','0');
INSERT INTO [t19188] VALUES (3123,'٢٩٤٣ - مسألة: (وإذا قال لعبده: أنت حر وعليك ألف. أو: على ألف. عتق، ولا شيء عليه. وعنه، إن لم يقبل لم يعتق)','9174','3088','0');
INSERT INTO [t19188] VALUES (3124,'٢٩٤٤ - مسألة: (وإن قال: أنت حر على أن تخدمني سنة. فكذلك. وقيل: إن لم يقبل لم يعتق. رواية واحدة)','9177','3088','0');
INSERT INTO [t19188] VALUES (3125,'٢٩٤٥ - مسألة: (وإن قال: أحد عبدي حر. أقرع بينهما، فمن وقعت له القرعة فهو حر من حين أعتقه)','9180','3088','0');
INSERT INTO [t19188] VALUES (3126,'٢٩٤٦ - [مسألة: (فإن مات أقرع الورثة) لما ذكرنا. في المعتق]','9182','3088','0');
INSERT INTO [t19188] VALUES (3127,'٢٩٤٧ - مسألة: (وإن مات أحد العبدين أقرع بينه وبين الحي)','9183','3088','0');
INSERT INTO [t19188] VALUES (3128,'٢٩٤٨ - مسألة: (وإن أعتق عبدا وأنسيه، أخرج بالقرعة)','9184','3088','0');
INSERT INTO [t19188] VALUES (3129,'٢٩٤٩ - مسألة: (فإن علم بعد أن المعتق غيره، عتق. وهل يبطل عتق الأول؟ على وجهين)','9185','3088','0');
INSERT INTO [t19188] VALUES (3130,'٢٩٥٠ - مسألة: (وإن أعتق جزءا من عبده، أو دبره)','9187','3088','0');
INSERT INTO [t19188] VALUES (3131,'٢٩٥١ - مسألة: (وإن أعتق في مرضه شركا له في عبد، أو دبره، وثلثه يحتمل باقيه، أعطي الشريك، وكان جميعه حرا، في إحدى الروايتين. والأخرى، لا يعتق إلا ما ملك منه)','9189','3088','0');
INSERT INTO [t19188] VALUES (3132,'٢٩٥٢ - مسألة: (وإن أعتق في مرضه ستة أعبد قيمتهم سواء، وثلثه يحتملهم، ثم ظهر عليه دين يستغرقهم، بيعوا في دينه. ويحتمل أن يعتق ثلثهم)','9191','3088','0');
INSERT INTO [t19188] VALUES (3133,'٢٩٥٣ - مسألة: (وإن أعتقهم، فأعتقنا ثلثهم، ثم ظهر له مال يخرجون من ثلثه، عتق من أرق منهم)','9194','3088','0');
INSERT INTO [t19188] VALUES (3134,'٢٩٥٤ - مسألة: (وإن لم يظهر له مال جزأناهم ثلاثة أجزاء؛ كل اثنين جزءا، وأقرعنا بينهم بسهم حرية وسهمي رق، فمن خرج له سهم الحرية عتق، ورق الباقون)','9197','3088','0');
INSERT INTO [t19188] VALUES (3135,'٢٩٥٥ - مسألة: (وإن كانوا ثمانية، فإن شاء أقرع بينهم بسهمي حرية، وخمسة رق، وسهم لمن ثلثاه حر. وإن شاء جزأهم أربعة أجزاء، فأقرع بينهم بسهم حرية، وثلاثة رق، ثم أعاد القرعة لإخراج من ثلثاه حر. وإن فعل غير ذلك جاز)','9206','3088','0');
INSERT INTO [t19188] VALUES (3136,'٢٩٥٦ - مسألة: (وإن أعتق عبدين، قيمة أحدهما مائتان والآخر ثلاثمائة)','9208','3088','0');
INSERT INTO [t19188] VALUES (3137,'٢٩٥٧ - مسألة: (وإن أعتق واحدا من ثلاثة أعبد)','9209','3088','0');
INSERT INTO [t19188] VALUES (3138,'باب التدبير','9215','3087','0');
INSERT INTO [t19188] VALUES (3139,'٢٩٥٨ - مسألة: (ويعتبر من الثلث)','9216','3138','0');
INSERT INTO [t19188] VALUES (3140,'٢٩٥٩ - مسألة: (ويصح من كل من تصح وصيته)','9217','3138','0');
INSERT INTO [t19188] VALUES (3141,'٢٩٦٠ - مسألة: (وصريحه لفظ العتق والحرية المعلقين بالموت)','9219','3138','0');
INSERT INTO [t19188] VALUES (3142,'٢٩٦١ - مسألة: (ويصح مطلقا ومقيدا)','9220','3138','0');
INSERT INTO [t19188] VALUES (3143,'٢٩٦٢ - مسألة: (وإن قال: متى شئت فأنت مدبر)','9222','3138','0');
INSERT INTO [t19188] VALUES (3144,'٢٩٦٣ - مسألة: (وإن قال: إن شئت فأنت مدبر. فقياس المذهب)','9223','3138','0');
INSERT INTO [t19188] VALUES (3145,'٢٩٦٤ - مسألة: (وإذا قال: قد رجعت في تدبيري. أو: أبطلته. لم يبطل؛ لأنه تعليق للعتق بصفة. وعنه، أنه يبطل، كالوصية)','9225','3138','0');
INSERT INTO [t19188] VALUES (3146,'٢٩٦٥ - مسألة: (وله بيع المدبر وهبته. وإن عاد إليه عاد التدبير. وعنه، لا يباع إلا في الدين. وعنه، لا تباع الأمة خاصة)','9232','3138','0');
INSERT INTO [t19188] VALUES (3147,'٢٩٦٦ - مسألة: (وإن عاد إليه عاد التدبير)','9236','3138','0');
INSERT INTO [t19188] VALUES (3148,'٢٩٦٧ - مسألة: (وما ولدت المدبرة بعد تدبيرها فهو بمنزلتها، ولا يتبعها ولدها من قبل التدبير)','9237','3138','0');
INSERT INTO [t19188] VALUES (3149,'٢٩٦٨ - مسألة: (وله إصابة مدبرته، فإن أولدها بطل تدبيرها)','9244','3138','0');
INSERT INTO [t19188] VALUES (3150,'٢٩٦٩ - مسألة: (وإن كاتب المدبر، أو دبر المكاتب، جاز)','9246','3138','0');
INSERT INTO [t19188] VALUES (3151,'٢٩٧٠ - مسألة: (فإن أدى عتق)','9248','3138','0');
INSERT INTO [t19188] VALUES (3152,'٢٩٧١ - مسألة: (وإذا دبر شركا له في عبد لم يسر إلى نصيب شريكه، وإن أعتق شريكه سرى إلى المدبر، وغرم قيمته لسيده.','9249','3138','0');
INSERT INTO [t19188] VALUES (3153,'٢٩٧٢ - مسألة: (وإذا أسلم مدبر الكافر لم يقر في يده، وترك في يد عدل ينفق عليه من كسبه، وما فضل لسيده، وإن أعوز فعليه تمامه، إلا أن يرجع في التدبير، ونقول بصحة رجوعه، فيجبر على بيعه)','9253','3138','0');
INSERT INTO [t19188] VALUES (3154,'٢٩٧٣ - مسألة: (ومن أنكر التدبير، لم يحكم عليه إلا بشاهدين. وهل يحكم بشاهد وامرأتين، أو شاهد ويمين العبد؟ على روايتين)','9255','3138','0');
INSERT INTO [t19188] VALUES (3155,'٢٩٧٤ - مسألة: (وإذا قتل المدبر سيده بطل تدبيره)','9260','3138','0');
INSERT INTO [t19188] VALUES (3156,'باب الكتابة','9264','3087','0');
INSERT INTO [t19188] VALUES (3157,'٢٩٧٥ - مسألة: (وهي مستحبة لمن يعلم فيه خير، وهو الكسب والأمانة. وعنه، أنها واجبة إذا ابتغاها من سيده أجبر عليها)','9266','3156','0');
INSERT INTO [t19188] VALUES (3158,'٢٩٧٦ - مسألة: (وهل تكره كتابة من لا كسب له؟ على روايتين)','9269','3156','0');
INSERT INTO [t19188] VALUES (3159,'٢٩٧٧ - مسألة: (ولا تصح إلا من جائز التصرف)','9271','3156','0');
INSERT INTO [t19188] VALUES (3160,'٢٩٧٨ - مسألة: (وإن كاتب المميز عبده بإذن وليه، صح. ويحتمل أن لا يصح)','9271','3156','0');
INSERT INTO [t19188] VALUES (3161,'٢٩٧٩ - مسألة: (وإن كاتب السيد عبده المميز، صح)','9272','3156','0');
INSERT INTO [t19188] VALUES (3162,'٢٩٨٠ - مسألة: (ولا تصح إلا بالقول. وتنعقد بقوله: كاتبتك','9278','3156','0');
INSERT INTO [t19188] VALUES (3163,'٢٩٨١ - مسألة: ولا يفتقر إلى قوله: (وإن أديت إلي فأنت حر)','9279','3156','0');
INSERT INTO [t19188] VALUES (3164,'٢٩٨٢ - مسألة: (ولا تصح إلا على عوض معلوم منجم، نجمين فصاعدا)','9280','3156','0');
INSERT INTO [t19188] VALUES (3165,'٢٩٨٣ - مسألة: ويشترط علم ما يؤدى إليه، في كل نجم، كالثمن في البيع، ولئلا يفضي إلى التنازع','9283','3156','0');
INSERT INTO [t19188] VALUES (3166,'٢٩٨٤ - مسألة: (وتصح على مال وخدمة، سواء تقدمت الخدمة أو تأخرت)','9286','3156','0');
INSERT INTO [t19188] VALUES (3167,'٢٩٨٥ - مسألة: (وإذا أدى ما كوتب عليه، أو أبرئ منه، عتق)','9290','3156','0');
INSERT INTO [t19188] VALUES (3168,'٢٩٨٦ - مسألة: (وما فضل في يده فهو له)','9291','3156','0');
INSERT INTO [t19188] VALUES (3169,'٢٩٨٧ - مسألة: (فلو مات قبل الأداء كان ما في يده لسيده، في الصحيح عنه. وعلى الرواية الأخرى، لسيده بقية كتابته، والباقي لورثته)','9294','3156','0');
INSERT INTO [t19188] VALUES (3170,'٢٩٨٨ - مسألة: (وإذا عجلت الكتابة قبل محلها، لزم السيد','9298','3156','0');
INSERT INTO [t19188] VALUES (3171,'٢٩٨٩ - مسألة: (ولا بأس أن يعجل المكاتب لسيده، ويضع عنه بعض كتابته)','9303','3156','0');
INSERT INTO [t19188] VALUES (3172,'٢٩٩٠ - مسألة: (وإذا أدى وعتق، فوجد السيد بالعوض عيبا، فله أرشه أو قيمته، ولا يرتفع العتق)','9307','3156','0');
INSERT INTO [t19188] VALUES (3173,'٢٩٩١ - مسألة: (وإن شرط عليه ألا يسافر، ولا يأخذ الصدقة، فهل يصح الشرط؟ على وجهين)','9312','3156','0');
INSERT INTO [t19188] VALUES (3174,'٢٩٩٢ - مسألة: وله الإنفاق على نفسه وولده ورقيقه، وكل ما فيه صلاح المال','9315','3156','0');
INSERT INTO [t19188] VALUES (3175,'٢٩٩٣ - مسألة: (وليس له أن يتزوج، ولا يتسرى، ولا يتبرع، ولا يقرض، ولا يحابي، ولا يقتص عبده الجاني على بعض رقيقه، ولا يعتق ولا يكاتب إلا بإذن سيده، وولاء من يعتقه ويكاتبه لسيده)','9316','3156','0');
INSERT INTO [t19188] VALUES (3176,'٢٩٩٤ - مسألة: (وولاء من يعتقه ويكاتبه لسيده)','9326','3156','0');
INSERT INTO [t19188] VALUES (3177,'٢٩٩٥ - مسألة: (ولا يكفر بالمال. وعنه، له ذلك بإذن سيده)','9329','3156','0');
INSERT INTO [t19188] VALUES (3178,'٢٩٩٦ - مسألة: (وهل له أن يرهن أو يضارب؟ يحتمل وجهين)','9331','3156','0');
INSERT INTO [t19188] VALUES (3179,'٢٩٩٧ - مسألة: (وليس له شراء ذوي رحمه إلا بإذن سيده)','9332','3156','0');
INSERT INTO [t19188] VALUES (3180,'٢٩٩٨ - مسألة: (وله أن يقبلهم إذا وهبوا له، أو وصي له بهم)','9334','3156','0');
INSERT INTO [t19188] VALUES (3181,'٢٩٩٩ - مسألة: (وإذا ملكهم فليس له بيعهم)','9335','3156','0');
INSERT INTO [t19188] VALUES (3182,'٣٠٠٠ - مسألة: (وولد المكاتبة الذي ولدته في الكتابة يتبعها)','9337','3156','0');
INSERT INTO [t19188] VALUES (3183,'٣٠٠١ - مسألة: (وإن اشترى زوجته)','9343','3156','0');
INSERT INTO [t19188] VALUES (3184,'٣٠٠٢ - مسألة: (وإن استولد أمته، فهل تصير أم ولد يمتنع عليه','9343','3156','0');
INSERT INTO [t19188] VALUES (3185,'٣٠٠٣ - مسألة: (وإن جنى عليه، فعليه أرش جنايته)','9347','3156','0');
INSERT INTO [t19188] VALUES (3186,'٣٠٠٤ - مسألة: (وإن حبسه مدة، فعليه أرفق الأمرين به، من إنظاره مثل تلك المدة، أو أجرة مثله)','9348','3156','0');
INSERT INTO [t19188] VALUES (3187,'٣٠٠٥ - مسألة: (وليس له أن يطأ مكاتبته إلا أن يشترط)','9349','3156','0');
INSERT INTO [t19188] VALUES (3188,'٣٠٠٦ - مسألة: (وإن وطئها ولم يشترط، أو وطئ أمتها،','9351','3156','0');
INSERT INTO [t19188] VALUES (3189,'٣٠٠٧ - مسألة: فإن أولدها (صارت أم ولد له)','9354','3156','0');
INSERT INTO [t19188] VALUES (3190,'٣٠٠٨ - مسألة: (فإن أدت عتقت، وإن مات)','9356','3156','0');
INSERT INTO [t19188] VALUES (3191,'٣٠٠٩ - مسألة: (وكذلك الحكم فيما إذا أعتق المكاتب سيده)','9359','3156','0');
INSERT INTO [t19188] VALUES (3192,'٣٠١٠ - مسألة: وإن (وطئاها)','9365','3156','0');
INSERT INTO [t19188] VALUES (3193,'٣٠١١ - مسألة: (وإن ولدت من أحدهما، صارت أم ولد له، ويغرم لشريكه نصف قيمتها. وهل يغرم نصف قيمة ولدها؟ على روايتين)','9367','3156','0');
INSERT INTO [t19188] VALUES (3194,'٣٠١٢ - مسألة: (وإن أتت بولد وألحق بهما، صارت أم ولد لهما، يعتق نصفها بموت أحدهما، وباقيها بموت الآخر)','9368','3156','0');
INSERT INTO [t19188] VALUES (3195,'٣٠١٣ - مسألة: (ويجوز بيع المكاتب. ومشتريه يقوم مقام المكاتب)','9375','3156','0');
INSERT INTO [t19188] VALUES (3196,'٣٠١٤ - مسألة: (فإن أدى عتق، وولاؤه له، وإن عجز عاد قنا له، وإن لم يعلم أنه مكاتب، فله الرد أو','9379','3156','0');
INSERT INTO [t19188] VALUES (3197,'٣٠١٥ - مسألة: (وإن اشترى كل واحد من المكاتبين الآخر، صح شراء الأول، وبطل شراء الثاني، وسواء كانا لواحد أو لاثنين)','9382','3156','0');
INSERT INTO [t19188] VALUES (3198,'٣٠١٦ - مسألة: فإن لم يعلم السابق منهما (فسد البيعان)','9384','3156','0');
INSERT INTO [t19188] VALUES (3199,'٣٠١٧ - مسألة: (وإن أسر العدو المكاتب، فاشتراه رجل، فأحب سيده، أخذه بما اشتراه، وإلا فهو عند مشتريه، مبقى على ما بقي من كتابته، يعتق بالأداء، وولاؤه له)','9385','3156','0');
INSERT INTO [t19188] VALUES (3200,'٣٠١٨ - مسألة: (وعليه فداء نفسه)','9392','3156','0');
INSERT INTO [t19188] VALUES (3201,'٣٠١٩ - مسألة: (وإن عجز، فلسيده تعجيزه)','9392','3156','0');
INSERT INTO [t19188] VALUES (3202,'٣٠٢٠ - مسألة: (وإن لزمته ديون تعلقت بذمته، يتبع بها بعد العتق)','9396','3156','0');
INSERT INTO [t19188] VALUES (3203,'٣٠٢١ - مسألة: (ولا تنفسخ بموت السيد)','9406','3156','0');
INSERT INTO [t19188] VALUES (3204,'٣٠٢٢ - مسألة: (ويعتق بالأداء إلى سيده، [وإلى من يقوم مقامه من الورثة وغيرهم)','9407','3156','0');
INSERT INTO [t19188] VALUES (3205,'٣٠٢٣ - مسألة: (وإن حل نجم فلم يؤده، فللسيد الفسخ. وعنه، لا يعجز حتى يحل نجمان. وعنه، لا يعجز حتى يقول: قد عجزت)','9416','3156','0');
INSERT INTO [t19188] VALUES (3206,'٣٠٢٤ - مسألة: (وليس للعبد فسخها بحال)','9422','3156','0');
INSERT INTO [t19188] VALUES (3207,'٣٠٢٥ - مسألة: (ولو زوج ابنته من مكاتبه، ثم مات، انفسخ النكاح. ويحتمل أن لا ينفسخ حتى يعجز)','9423','3156','0');
INSERT INTO [t19188] VALUES (3208,'٣٠٢٦ - مسألة: (ويجب على السيد أن يؤتيه ربع مال الكتابة، إن شاء وضعه عنه، وإن شاء قبضه ثم دفعه إليه)','9424','3156','0');
INSERT INTO [t19188] VALUES (3209,'٣٠٢٧ - مسألة: (فإن أدى ثلاثة أرباع الكتابة وعجز عن الربع، عتق، ولم تنفسخ، الكتابة في قول القاضي وأصحابه)','9430','3156','0');
INSERT INTO [t19188] VALUES (3210,'٣٠٢٨ - [مسألة: (ويقسط العوض بينهم على قدر قيمتهم، ويكون كل واحد منهم مكاتبا بقدر حصته، يعتق بأدائها, ويعجز بالعجز عنها وحده)]','9434','3156','0');
INSERT INTO [t19188] VALUES (3211,'٣٠٢٩ - مسألة: (وإن اختلفوا بعد الأداء في قدر ما أدى كل واحد منهم، فالقول قول من يدعي أداء قدر الواجب عليه)','9439','3156','0');
INSERT INTO [t19188] VALUES (3212,'٣٠٣٠ - مسألة: (وتجوز كتابة بعض عبده، فإذا أدى عتق كله)','9444','3156','0');
INSERT INTO [t19188] VALUES (3213,'٣٠٣١ - مسألة: (وتجوز كتابة حصته من العبد المشترك بغير إذن شريكه)','9445','3156','0');
INSERT INTO [t19188] VALUES (3214,'٣٠٣٢ - مسألة: (وإذا أدى ما كوتب عليه، ومثله لسيده الآخر، عتق كله، إن كان الذي كاتبه موسرا، وعليه قيمة حصة شريكه)','9447','3156','0');
INSERT INTO [t19188] VALUES (3215,'٣٠٣٣ - مسألة: (وإن أعتق الشريك قبل أدائه، عتق عليه كله إن كان موسرا، وعليه قيمة نصيب المكاتب. وقال)','9449','3156','0');
INSERT INTO [t19188] VALUES (3216,'٣٠٣٤ - مسألة: (وإن كاتبا عبدهما جار، سواء كان على التساوي أو التفاضل. ولا يجوز أن يؤدي إليهما إلا على التساوي)','9452','3156','0');
INSERT INTO [t19188] VALUES (3217,'٣٠٣٥ - مسألة: (وإن اختلفا في قدر عوضها، فالقول قول السيد في إحدى الروايتين)','9475','3156','0');
INSERT INTO [t19188] VALUES (3218,'٣٠٣٦ - مسألة: (وإن اختلفا في وفاء مالها)','9477','3156','0');
INSERT INTO [t19188] VALUES (3219,'٣٠٣٧ - مسألة: (وإن أقام العبد شاهدا وحلف معه، أو شاهدا وامرأتين، ثبت الأداء وعتق)','9479','3156','0');
INSERT INTO [t19188] VALUES (3220,'٣٠٣٨ - مسألة: (وتنفسخ بموت السيد، وجنونه، والحجر للسفه)','9485','3156','0');
INSERT INTO [t19188] VALUES (3221,'٣٠٣٩ - مسألة: (وهل يتبع المكاتبة ولدها فيها؟ على وجهين)','9487','3156','0');
INSERT INTO [t19188] VALUES (3222,'٣٠٤٠ - مسألة: (وقال أبو بكر: لا تنفسخ)','9488','3156','0');
INSERT INTO [t19188] VALUES (3223,'باب أحكام أمهات الأولاد','9489','3087','0');
INSERT INTO [t19188] VALUES (3224,'٣٠٤١ - مسألة: (إذا حملت الأمة من سيدها، فوضعت منه ما يتبين فيه بعض خلق الإنسان، صارت له بذلك أم ولد، فإذا مات عتقت','9490','3223','0');
INSERT INTO [t19188] VALUES (3225,'٣٠٤٢ - مسألة: وتعتق بموت سيدها من رأس المال، وإن لم يملك سواها','9493','3223','0');
INSERT INTO [t19188] VALUES (3226,'٣٠٤٣ - مسألة: (وإن وضعت جسما لا تخطيط فيه، فعلى روايتين)','9496','3223','0');
INSERT INTO [t19188] VALUES (3227,'٣٠٤٤ - مسألة: (وإن أصابها في ملك غيره بنكاح أو غيره، ثم ملكها حاملا، عتق الجنين، ولم تصر أم ولد)','9499','3223','0');
INSERT INTO [t19188] VALUES (3228,'٣٠٤٥ - مسألة: (وأحكام أم الولد أحكام الأمة، في الإجارة، والاستخدام، والوطء، وسائر أمورها، إلا فيما ينقل الملك في رقبتها؛ كالبيع، والهبة، والوقف، أو ما يراد له؛ كالرهن. وعنه، ما يدل على جواز بيعها مع الكراهة. ولا عمل عليه)','9509','3223','0');
INSERT INTO [t19188] VALUES (3229,'٣٠٤٦ - مسألة: (وإن ولدت من غير سيدها، فلولدها حكمها في العتق بموت سيدها، سواء عتقت أو ماتت قبله)','9516','3223','0');
INSERT INTO [t19188] VALUES (3230,'٣٠٤٧ - مسألة: (وإن مات سيدها وهي حامل منه، فهل تستحق النفقة لمدة حملها؟ على روايتين)','9519','3223','0');
INSERT INTO [t19188] VALUES (3231,'٣٠٤٨ - مسألة: (وإذا جنت أم الولد، فداها سيدها بقيمتها أو دونها. وعنه، يفديها بأرش الجناية كله)','9520','3223','0');
INSERT INTO [t19188] VALUES (3232,'٣٠٤٩ - مسألة: (فإن عادت فجنت، فداها أيضا. وعنه، يتعلق ذلك بذمتها)','9523','3223','0');
INSERT INTO [t19188] VALUES (3233,'٣٠٥٠ - مسألة: (وإن قتلت سيدها عمدا فعليها القصاص، وإن عفوا على مال، أو كانت الجناية خطأ، فعليها قيمة نفسها، وتعتق في الموضعين)','9526','3223','0');
INSERT INTO [t19188] VALUES (3234,'٣٠٥١ - مسألة: (ولا حد على قاذفها. وعنه، عليه الحد)','9529','3223','0');
INSERT INTO [t19188] VALUES (3235,'٣٠٥٢ - مسألة: (وإذا وطئ أحد الشريكين الجارية وأولدها،','9533','3223','0');
INSERT INTO [t19188] VALUES (3236,'٣٠٥٣ - مسألة: (وعند القاضي، وأبي الخطاب، إن كان الأول معسرا لم يسر استيلاده، وتصير أم ولد لهما، يعتق نصفها بموت أحدهما)','9538','3223','0');
INSERT INTO [t19188] VALUES (3237,'كتاب النكاح','9542','0','0');
INSERT INTO [t19188] VALUES (3238,'[باب. . .]','9542','3237','0');
INSERT INTO [t19188] VALUES (3239,'٣٠٥٤ - مسألة: و (النكاح سنة)','9548','3238','0');
INSERT INTO [t19188] VALUES (3240,'٣٠٥٥ - مسألة: (والاشتغال به أفضل من التخلي لنوافل','9549','3238','0');
INSERT INTO [t19188] VALUES (3241,'٣٠٥٦ - مسألة: (وعن أحمد، أن النكاح واجب على الإطلاق)','9554','3238','0');
INSERT INTO [t19188] VALUES (3242,'٣٠٥٧ - مسألة: (ويستحب تخير ذات الدين، الولود، البكر، الحسيبة، الأجنبية)','9559','3238','0');
INSERT INTO [t19188] VALUES (3243,'٣٠٥٨ - مسألة: (ويجوز لمن أراد خطبة امرأة النظر إلى وجهها من غير خلوة بها. وعنه، له النظر إلى ما يظهر غالبا؛ كالرقبة، واليدين، والقدمين)','9563','3238','0');
INSERT INTO [t19188] VALUES (3244,'٣٠٥٩ - مسألة: (وله النظر إلى ذلك، وإلى الرأس والساقين من الأمة المستامة ومن ذوات محارمه. وعنه، لا ينظر من ذوات محارمه إلا)','9568','3238','0');
INSERT INTO [t19188] VALUES (3245,'٣٠٦٠ - مسألة: (وللعبد النظر إليهما من مولاته)','9573','3238','0');
INSERT INTO [t19188] VALUES (3246,'٣٠٦١ - مسألة: (ولغير أولي الإربة من الرجال، كالكبير والعنين ونحوهما، النظر إلى ذلك. وعنه، لا يباح)','9575','3238','0');
INSERT INTO [t19188] VALUES (3247,'٣٠٦٢ - مسألة: (وللشاهد النظر إلى وجه المشهود عليها)','9578','3238','0');
INSERT INTO [t19188] VALUES (3248,'٣٠٦٣ - مسألة: (وللطبيب النظر إلى ما تدعو الحاجة إلى نظره)','9579','3238','0');
INSERT INTO [t19188] VALUES (3249,'٣٠٦٤ - مسألة: (وللصبي المميز غير ذي الشهوة النظر)','9579','3238','0');
INSERT INTO [t19188] VALUES (3250,'٣٠٦٥ - مسألة: (فإن كان ذا شهوة، فهو كذي المحرم)','9580','3238','0');
INSERT INTO [t19188] VALUES (3251,'٣٠٦٦ - مسألة: (وللمرأة مع المرأة، والرجل مع الرجل، النظر إلى ما عدا ما بين السرة والركبة. وعنه، أن الكافرة مع المسلمة كالأجنبي)','9582','3238','0');
INSERT INTO [t19188] VALUES (3252,'٣٠٦٧ - مسألة: (ويباح للمرأة النظر من الرجل إلى غير العورة. وعنه، لا يباح)','9586','3238','0');
INSERT INTO [t19188] VALUES (3253,'٣٠٦٨ - مسألة: (ويجوز النظر إلى الغلام لغير شهوة)','9591','3238','0');
INSERT INTO [t19188] VALUES (3254,'٣٠٦٩ - مسألة: (ولا يجوز النظر إلى أحد ممن ذكرنا لشهوة)','9592','3238','0');
INSERT INTO [t19188] VALUES (3255,'٣٠٧٠ - مسألة: (ولكل واحد من الزوجين النظر إلى جميع بدن الآخر ولمسه، وكذلك السيد مع أمته)','9593','3238','0');
INSERT INTO [t19188] VALUES (3256,'٣٠٧١ - مسألة؛ قال الشيخ، رضي الله عنه: (ولا يجوز التصريح بخطبة المعتدة، ولا التعريض بخطبة الرجعية)','9601','3238','0');
INSERT INTO [t19188] VALUES (3257,'٣٠٧٢ - مسألة: فأما الرجعية، فلا يجوز لأحد التعريض بخطبتها ولا التصريح','9602','3238','0');
INSERT INTO [t19188] VALUES (3258,'٣٠٧٣ - مسألة: (ويجوز في عدة الوفاة، و)','9602','3238','0');
INSERT INTO [t19188] VALUES (3259,'٣٠٧٤ - مسألة: (وهل يجوز في عدة البائن بغير الثلاث؟ على','9602','3238','0');
INSERT INTO [t19188] VALUES (3260,'٣٠٧٥ - مسألة: (والتعريض قوله: إني في مثلك لراغب. و','9603','3238','0');
INSERT INTO [t19188] VALUES (3261,'٣٠٧٦ - مسألة: (وتجيبه)','9604','3238','0');
INSERT INTO [t19188] VALUES (3262,'٣٠٧٧ - مسألة: (ولا يجوز للرجل أن يخطب على خطبة أخيه إن أجيب، وإن رد، حل، وإن لم يعلم الحال فعلى وجهين)','9605','3238','0');
INSERT INTO [t19188] VALUES (3263,'٣٠٧٨ - مسألة: (والتعويل في الرد والإجابة عليها إن لم تكن','9610','3238','0');
INSERT INTO [t19188] VALUES (3264,'٣٠٧٩ - مسألة: (ويستحب عقد النكاح مساء يوم الجمعة)','9614','3238','0');
INSERT INTO [t19188] VALUES (3265,'٣٠٨٠ - مسألة: (و)','9614','3238','0');
INSERT INTO [t19188] VALUES (3266,'٣٠٨١ - مسألة؛ (و)','9618','3238','0');
INSERT INTO [t19188] VALUES (3267,'٣٠٨٢ - مسألة: (ويقول إذا زفت إليه: اللهم إني أسألك خيرها وخير ما جبلتها عليه، وأعوذ بك من شرها وشر ما جبلتها عليه)','9619','3238','0');
INSERT INTO [t19188] VALUES (3268,'باب أركان النكاح وشروطه','9621','3237','0');
INSERT INTO [t19188] VALUES (3269,'٣٠٨٣ - مسألة: (فإن قدر على تعلمها بالعربية، لم يلزمه)','9627','3268','0');
INSERT INTO [t19188] VALUES (3270,'٣٠٨٤ - مسألة: (والقبول أن يقول: قبلت هذا النكاح. أو ما يقوم مقامه في حق من لا يحسن)','9627','3268','0');
INSERT INTO [t19188] VALUES (3271,'٣٠٨٥ - مسألة: (فإن اقتصر على قوله: قبلت)','9628','3268','0');
INSERT INTO [t19188] VALUES (3272,'٣٠٨٦ - مسألة: (فإن تقدم القبول الإيجاب، لم يصح)','9631','3268','0');
INSERT INTO [t19188] VALUES (3273,'٣٠٨٧ - مسألة: (وإن تراخى)','9633','3268','0');
INSERT INTO [t19188] VALUES (3274,'٣٠٨٨ - مسألة: (فإن تفرقا قبله، بطل الإيجاب)','9633','3268','0');
INSERT INTO [t19188] VALUES (3275,'٣٠٨٩ - مسألة: (فلو قال: زوجتك ابنتي. وله بنات، لم يصح حتى يشير إليها، أو يسميها، أو يصفها بما تتميز به، وإن لم يكن له إلا ابنة واحدة، صح)','9636','3268','0');
INSERT INTO [t19188] VALUES (3276,'٣٠٩٠ - مسألة: (وإن قال: إن وضعت زوجتي ابنة، فقد زوجتكها. لم يصح)','9638','3268','0');
INSERT INTO [t19188] VALUES (3277,'٣٠٩١ - مسألة: (إلا الأب له تزويج أولاده الصغار والمجانين وبناته الأبكار بغير إذنهم)','9641','3268','0');
INSERT INTO [t19188] VALUES (3278,'٣٠٩٢ - مسألة: (وهل له تزويج الثيب الصغيرة؟ على وجهين)','9655','3268','0');
INSERT INTO [t19188] VALUES (3279,'٣٠٩٣ - مسألة: (وللسيد تزويج إمائه الثيب والأبكار، وعبيده','9657','3268','0');
INSERT INTO [t19188] VALUES (3280,'٣٠٩٤ - مسألة: (ولا يملك إجبار عبده الكبير)','9661','3268','0');
INSERT INTO [t19188] VALUES (3281,'٣٠٩٥ - مسألة: (وليس لسائر الأولياء تزويج كبيرة إلا بإذنها، إلا المجنونة، لهم تزويجها إذا ظهر منها الميل إلى الرجال)','9664','3268','0');
INSERT INTO [t19188] VALUES (3282,'٣٠٩٦ - مسألة: (وليس لهم تزويج صغيرة بحال)','9669','3268','0');
INSERT INTO [t19188] VALUES (3283,'٣٠٩٧ - مسألة: (وإذن الثيب الكلام، وإذن البكر الصمات)','9674','3268','0');
INSERT INTO [t19188] VALUES (3284,'٣٠٩٨ - مسألة: (ولا فرق بين الثيوبة بوطء مباح أو محرم)','9677','3268','0');
INSERT INTO [t19188] VALUES (3285,'٣٠٩٩ - مسألة: (فأما زوال البكارة بإصبع أو وثبة، فلا يغير صفة الإذن)','9678','3268','0');
INSERT INTO [t19188] VALUES (3286,'٣١٠٠ - مسألة: (وعن أحمد)','9686','3268','0');
INSERT INTO [t19188] VALUES (3287,'٣١٠١ - مسألة: (وأحق الناس بنكاح المرأة الحرة أبوها)','9689','3268','0');
INSERT INTO [t19188] VALUES (3288,'٣١٠٢ - مسألة: (ثم أبوه وإن علا)','9691','3268','0');
INSERT INTO [t19188] VALUES (3289,'٣١٠٣ - مسألة: (ثم ابنها، ثم ابنه وإن سفل)','9692','3268','0');
INSERT INTO [t19188] VALUES (3290,'٣١٠٤ - مسألة: (ثم أخوها لأبويها، ثم لأبيها)','9693','3268','0');
INSERT INTO [t19188] VALUES (3291,'٣١٠٥ - مسألة: (وعنه تقديم الابن على الجد، والتسوية بين الجد والإخوة، وبين الأخ للأبوين والأخ للأب)','9694','3268','0');
INSERT INTO [t19188] VALUES (3292,'٣١٠٦ - مسألة: (ثم بنو الإخوة وإن سفلوا، ثم العم، ثم ابنه، ثم الأقرب فالأقرب من العصبات، على ترتيب الميراث)','9695','3268','0');
INSERT INTO [t19188] VALUES (3293,'٣١٠٧ - مسألة: (ثم المولى المنعم، ثم عصباته من بعده،','9696','3268','0');
INSERT INTO [t19188] VALUES (3294,'٣١٠٨ - مسألة: (وولي الأمة سيدها)','9701','3268','0');
INSERT INTO [t19188] VALUES (3295,'٣١٠٩ - مسألة: (فإن كانت لامرأة، فوليها ولي سيدتها، ولا','9701','3268','0');
INSERT INTO [t19188] VALUES (3296,'٣١١٠ - مسألة: (ويشترط في الولي الحرية، والذكورية، واتفاق الدين، والعقل)','9706','3268','0');
INSERT INTO [t19188] VALUES (3297,'٣١١١ - مسألة: (فإن كان الأقرب طفلا أو كافرا أو عبدا، زوج الأبعد)','9712','3268','0');
INSERT INTO [t19188] VALUES (3298,'٣١١٢ - مسألة: (وإن عضل الأقرب، زوج الأبعد. وعنه، يزوج الحاكم)','9712','3268','0');
INSERT INTO [t19188] VALUES (3299,'٣١١٣ - مسألة: (وإن غاب غيبة منقطعة زوج الأبعد، وهي ما لا تقطع إلا بكلفة ومشقة، في ظاهر كلام أحمد. وقال الخرقي: ما لا يصل إليه الكتاب، أو يصل فلا يجيب عنه. وقال القاضي: ما لا تقطعه','9715','3268','0');
INSERT INTO [t19188] VALUES (3300,'٣١١٤ - مسألة: (ولا يلي كافر نكاح مسلمة بحال، إلا إذا','9720','3268','0');
INSERT INTO [t19188] VALUES (3301,'٣١١٥ - مسألة: (ولا يلي مسلم نكاح كافرة، إلا سيد الأمة أو ولي سيدتها أو السلطان)','9722','3268','0');
INSERT INTO [t19188] VALUES (3302,'٣١١٦ - مسألة: (ويلي الذمي نكاح موليته الذمية من الذمي. لقوله تعالى: {والذين كفروا بعضهم أولياء بعض} (وهل يليه من','9723','3268','0');
INSERT INTO [t19188] VALUES (3303,'٣١١٧ - مسألة: (وإذا زوج الأبعد من غير عذر للأقرب، أو زوج أجنبي، لم يصح. وعنه، يصح ويقف على إجازة الولي)','9725','3268','0');
INSERT INTO [t19188] VALUES (3304,'٣١١٨ - مسألة: (ووكيل كل واحد من هؤلاء يقوم مقامه وإن','9731','3268','0');
INSERT INTO [t19188] VALUES (3305,'٣١١٩ - مسألة: (وإذا استوى الأولياء في الدرجة)','9740','3268','0');
INSERT INTO [t19188] VALUES (3306,'٣١٢٠ - مسألة: والأولى تقديم أكبرهم وأفضلهم؛ لأن النبي - صلى الله عليه وسلم - لما تقدم إليه (١)','9741','3268','0');
INSERT INTO [t19188] VALUES (3307,'٣١٢١ - مسألة: (فإن سبق غير من وقعت له القرعة فزوج، صح)','9742','3268','0');
INSERT INTO [t19188] VALUES (3308,'٣١٢٢ - مسألة: (وإذا زوج)','9743','3268','0');
INSERT INTO [t19188] VALUES (3309,'٣١٢٣ - مسألة: (وإذا زوج السيد عبده الصغير من أمته، جاز أن يتولى طرفي العقد)','9757','3268','0');
INSERT INTO [t19188] VALUES (3310,'٣١٢٤ - مسألة: (وكذلك ولي المرأة -مثل ابن العم والمولى','9757','3268','0');
INSERT INTO [t19188] VALUES (3311,'٣١٢٥ - مسألة: (وإذا قال السيد لأمته: أعتقتك، وجعلت عتقك صداقك. صح فإن طلقها قبل الدخول، رجع عليها بنصف قيمتها)','9762','3268','0');
INSERT INTO [t19188] VALUES (3312,'٣١٢٦ - مسألة: (وعنه، لا يصح حتى يستأنف نكاحها بإذنها، فإن أبت ذلك فعليها قيمتها)','9763','3268','0');
INSERT INTO [t19188] VALUES (3313,'٣١٢٧ - مسألة: (و)','9777','3268','0');
INSERT INTO [t19188] VALUES (3314,'٣١٢٨ - مسألة: (ولا ينعقد نكاح مسلم بشهادة ذميين. ويتخرج أن ينعقد إذا كانت المرأة ذمية)','9779','3268','0');
INSERT INTO [t19188] VALUES (3315,'٣١٢٩ - مسألة: (ولا ينعقد بحضور أصمين ولا أخرسين)','9779','3268','0');
INSERT INTO [t19188] VALUES (3316,'٣١٣٠ - مسألة: وهل ينعقد بحضور عدوين، أو ابني الزوجين أو أحدهما؟ على وجهين) أحدهما، ينغقد. اختاره أبو عبد الله بن بطة؛ لعموم قوله: «وشاهدي عدل». ولأنه ينعقد بهما نكاح غير هذا الزوج، فانعقد بهما نكاحه، كسائرء العدول. والثاني، لا ينعقد؛ لأن العدو لا تقبل شهادته على عدوه، والابن لا تقبل شهادته لوالده (وعنه، أن الشهادة ليست من شروط النكاح)','9780','3268','0');
INSERT INTO [t19188] VALUES (3317,'٣١٣١ - مسألة: (لكن إن لم ترض المرأة والأولياء جميعهم، فلمن لم يرض الفسخ)','9785','3268','0');
INSERT INTO [t19188] VALUES (3318,'٣١٣٢ - مسألة: (فلو زوج الأب بغير كفء برضاها، فللإخوة الفسخ. نص عليه)','9786','3268','0');
INSERT INTO [t19188] VALUES (3319,'٣١٣٣ - مسألة: (والكفاءة؛ الدين والمنصب)','9788','3268','0');
INSERT INTO [t19188] VALUES (3320,'٣١٣٤ - مسألة: (العرب بعضهم لبعض أكفاء، وسائر الناس','9791','3268','0');
INSERT INTO [t19188] VALUES (3321,'٣١٣٥ - مسألة: (وعنه، أن الحرية والصناعة واليسار من شروط الكفاءة، فلا تزوج حرة بعبد، ولا بنت بزاز بحجام، ولا بنت تانئ','9794','3268','0');
INSERT INTO [t19188] VALUES (3322,'باب المحرمات في النكاح','9802','3237','0');
INSERT INTO [t19188] VALUES (3323,'٣١٣٦ - مسألة: (فإن متن قبل الدخول، فهل تحرم بناتهن؟ على روايتين)','9811','3322','0');
INSERT INTO [t19188] VALUES (3324,'٣١٣٧ - مسألة: (ويثبت تحريم المصاهرة بالوطء الحلال','9813','3322','0');
INSERT INTO [t19188] VALUES (3325,'٣١٣٨ - مسألة: (فإن كانت الموطوءة ميتة أو صغيرة)','9818','3322','0');
INSERT INTO [t19188] VALUES (3326,'٣١٣٩ - مسألة: (وإن باشر امرأة، أو نظر إلى فرجها، أو خلا بها لشهوة، فعلى روايتين)','9819','3322','0');
INSERT INTO [t19188] VALUES (3327,'٣١٤٠ - مسألة: (ومن تلوط بغلام، حرم على كل واحد منهما أم الآخر وابنته)','9824','3322','0');
INSERT INTO [t19188] VALUES (3328,'٣١٤١ - مسألة: (و)','9830','3322','0');
INSERT INTO [t19188] VALUES (3329,'٣١٤٢ - مسألة: (فإن جمع بينهما في عقد)','9833','3322','0');
INSERT INTO [t19188] VALUES (3330,'٣١٤٣ - مسألة: (وإن تزوجهما في عقدين، أو تزوج إحداهما في عدة الأخرى، سواء كانت بائنا أو رجعية، فنكاح الثانية باطل)','9834','3322','0');
INSERT INTO [t19188] VALUES (3331,'٣١٤٤ - مسألة: (وإن اشترى أخت امرأته، أو عمتها، أو خالتها، صح)','9837','3322','0');
INSERT INTO [t19188] VALUES (3332,'٣١٤٥ - مسألة: (وإن اشتراهن في عقد واحد، صح)','9838','3322','0');
INSERT INTO [t19188] VALUES (3333,'٣١٤٦ - مسألة: وله وطء إحداهما؛ لأن الأخرى لم تصر فراشا','9838','3322','0');
INSERT INTO [t19188] VALUES (3334,'٣١٤٧ - مسألة: (فإن وطيء إحداهما، فليس له وطء الأخرى حتى يحرم الموطوءة على نفسه بإخراج عن ملكه أو تزويج)','9840','3322','0');
INSERT INTO [t19188] VALUES (3335,'٣١٤٨ - مسألة: (فإن عادت إلى ملكه، لم يطأ واحدة منهما حتى','9843','3322','0');
INSERT INTO [t19188] VALUES (3336,'٣١٤٩ - مسألة: (وإن وطئ أمته ثم تزوج أختها، لم يصح عند أبي بكر)','9846','3322','0');
INSERT INTO [t19188] VALUES (3337,'٣١٥٠ - مسألة: (ولا يحل للحر أن يجمع بين أكثر من أربع، ولا للعبد أن يتزوج أكثر من اثنتين، فإن طلق إحداهن، لم يتزوج أخرى حتى تنقضي عدتها)','9853','3322','0');
INSERT INTO [t19188] VALUES (3338,'٣١٥١ - مسألة: (وتحرم الزانية حتى تتوب وتنقضي عدتها)','9861','3322','0');
INSERT INTO [t19188] VALUES (3339,'٣١٥٢ - مسألة: (و)','9870','3322','0');
INSERT INTO [t19188] VALUES (3340,'٣١٥٣ - مسألة: (و)','9870','3322','0');
INSERT INTO [t19188] VALUES (3341,'٣١٥٤ - مسألة: (ولا يحل لمسلمة نكاح كافر بحال)','9871','3322','0');
INSERT INTO [t19188] VALUES (3342,'٣١٥٥ - مسألة: (ولا)','9871','3322','0');
INSERT INTO [t19188] VALUES (3343,'٣١٥٦ - مسألة: (فإن كان أحد أبويها غير كتابي، أو كانت من نساء بني تغلب، فهل تحل)','9878','3322','0');
INSERT INTO [t19188] VALUES (3344,'٣١٥٧ - مسألة: (وليس للمسلم وإن كان عبدا نكاح أمة كتابية. وعنه، يجوز)','9881','3322','0');
INSERT INTO [t19188] VALUES (3345,'٣١٥٨ - مسألة: (ولا يحل لحر نكاح أمة مسلمة، إلا أن يخاف العنت، ولا يجد طولا لنكاح حرة، ولا ثمن أمة)','9883','3322','0');
INSERT INTO [t19188] VALUES (3346,'٣١٥٩ - مسألة: (وإن تزوجها وفيه الشرطان، ثم أيسر، أو نكح حرة، فهل يبطل نكاح الأمة؟ على روايتين)','9890','3322','0');
INSERT INTO [t19188] VALUES (3347,'٣١٦٠ - مسألة: (وإن تزوج حرة أو أمة فلم تعفه، ولم يجد طولا','9892','3322','0');
INSERT INTO [t19188] VALUES (3348,'٣١٦١ - مسألة: (قال الخرقي: وله أن يتزوج من الإماء أربعا، إذا كان الشرطان فيه قائمين)','9895','3322','0');
INSERT INTO [t19188] VALUES (3349,'٣١٦٢ - مسألة: (وللعبد نكاح الأمة)','9896','3322','0');
INSERT INTO [t19188] VALUES (3350,'٣١٦٣ - مسألة: (وهل له أن ينكحها على حرة؟ على روايتين)','9896','3322','0');
INSERT INTO [t19188] VALUES (3351,'٣١٦٤ - مسألة: (وإن جمع بينهما في العقد، جاز)','9897','3322','0');
INSERT INTO [t19188] VALUES (3352,'٣١٦٥ - مسألة: (وليس للعبد نكاح سيدته)','9899','3322','0');
INSERT INTO [t19188] VALUES (3353,'٣١٦٦ - مسألة: (وليس للحر أن يتزوج أمته)','9899','3322','0');
INSERT INTO [t19188] VALUES (3354,'٣١٦٧ - مسألة: (ولا)','9899','3322','0');
INSERT INTO [t19188] VALUES (3355,'٣١٦٨ - مسألة: (ويجوز للعبد نكاح أمة ابنه)','9901','3322','0');
INSERT INTO [t19188] VALUES (3356,'٣١٦٩ - مسألة: (وإن اشترى الحر زوجته)','9903','3322','0');
INSERT INTO [t19188] VALUES (3357,'٣١٧٠ - مسألة: (ومن جمع بين محللة ومحرمة في عقد واحد، فهل يصح في من تحل؟ على روايتين)','9905','3322','0');
INSERT INTO [t19188] VALUES (3358,'٣١٧١ - مسألة: (و)','9908','3322','0');
INSERT INTO [t19188] VALUES (3359,'٣١٧٢ - مسألة: (فإن تزوج امرأة ثم قال: أنا امرأة. انفسخ نكاحه)','9913','3322','0');
INSERT INTO [t19188] VALUES (3360,'٣١٧٣ - مسألة: (وإن تزوج رجلا ثم قال: أنا رجل. لم يقبل قوله في فسخ نكاحه)','9914','3322','0');
INSERT INTO [t19188] VALUES (3361,'باب الشروط في النكاح','9915','3237','0');
INSERT INTO [t19188] VALUES (3362,'٣١٧٤ - مسألة: (فإن شرط أن لا يخرجها من دارها أو بلدها، أو لا يتزوج عليها، أو لا يتسرى، فهو صحيح لازم إن وفى به، وإلا','9916','3361','0');
INSERT INTO [t19188] VALUES (3363,'٣١٧٥ - مسألة: (وإن شرط لها طلاق ضرتها، فقال أبو الخطاب: هو صحيح)','9921','3361','0');
INSERT INTO [t19188] VALUES (3364,'٣١٧٦ - مسألة؛ قال، رحمه الله: (القسم الثاني، فاسد، وهو ثلاثة أنواع؛ أحدها، ما يبطل النكاح، وهو ثلاثة أشياء؛ أحدها، نكاح الشغار، وهو أن يزوجه وليته على أن يزوجه الآخر وليته ولا مهر','9923','3361','0');
INSERT INTO [t19188] VALUES (3365,'٣١٧٧ - مسألة: (فإن نوى ذلك من غير شرط، لم يصح أيضا، في ظاهر المذهب. وقيل: يكره ويصح)','9932','3361','0');
INSERT INTO [t19188] VALUES (3366,'٣١٧٨ - مسألة: وكذلك إن شرط أن يطلقها في وقت بعينه، فلا يصح النكاح، سواء كان معلوما أو مجهولا','9944','3361','0');
INSERT INTO [t19188] VALUES (3367,'٣١٧٩ - مسألة: (وإن علق ابتداءه على شرط، كقوله: زوجتك إذا جاء رأس الشهر. أو: إن رضيت أمها. فهذا كله باطل من أصله)','9945','3361','0');
INSERT INTO [t19188] VALUES (3368,'٣١٨٠ - مسألة: (فإن شرطها كتابية فبانت مسلمة، فلا خيار له)','9952','3361','0');
INSERT INTO [t19188] VALUES (3369,'٣١٨١ - مسألة: (وإن شرطها بكرا)','9954','3361','0');
INSERT INTO [t19188] VALUES (3370,'٣١٨٢ - مسألة: (وإن تزوج أمة يظنها حرة)','9956','3361','0');
INSERT INTO [t19188] VALUES (3371,'٣١٨٣ - مسألة: (وإن كان المغرور عبدا، فولده أحرار، ويفديهم إذا عتق، ويرجع به على من غره)','9969','3361','0');
INSERT INTO [t19188] VALUES (3372,'٣١٨٤ - مسألة: (وإن تزوجت)','9973','3361','0');
INSERT INTO [t19188] VALUES (3373,'٣١٨٥ - مسألة: (وإن كان عبدا فلها الخيار في فسخ النكاح)','9978','3361','0');
INSERT INTO [t19188] VALUES (3374,'٣١٨٦ - مسألة: (ولها الفسخ)','9979','3361','0');
INSERT INTO [t19188] VALUES (3375,'٣١٨٧ - مسألة: (فإن أعتق قبل فسخها، أو أمكنته من وطئها، بطل خيارها)','9981','3361','0');
INSERT INTO [t19188] VALUES (3376,'٣١٨٨ - مسألة: (وخيار المعتقة على التراخي، ما لم يوجد منها ما يدل على الرضا)','9985','3361','0');
INSERT INTO [t19188] VALUES (3377,'٣١٨٩ - مسألة: (فإن كانت صغيرة أو مجنونة، فلها الخيار إذا بلغت وعقلت)','9986','3361','0');
INSERT INTO [t19188] VALUES (3378,'٣١٩٠ - مسألة: (فإن طلقت قبل اختيارها، وقع الطلاق)','9988','3361','0');
INSERT INTO [t19188] VALUES (3379,'٣١٩١ - مسألة: (وإن عتقت)','9989','3361','0');
INSERT INTO [t19188] VALUES (3380,'٣١٩٢ - مسألة: (فإن اختارت المقام، فهل يبطل خيارها؟ على وجهين)','9990','3361','0');
INSERT INTO [t19188] VALUES (3381,'٣١٩٣ - مسألة: (ومتى اختارت، المعتقة)','9991','3361','0');
INSERT INTO [t19188] VALUES (3382,'٣١٩٤ - مسألة: وإن اختارت الفسخ قبل الدخول، فلا مهر لها. نص عليه أحمد. وهو مذهب الشافعي. وعن أحمد، أن للسيد نصف المهر. اختاره أبو بكر؛ لأنه وجب للسيد، فلا يسقط بفعل غيره. ولنا، أن الفرقة جاءت من قبلها، فيسقط مهرها، كما لو أسلمت أو ارتدت، أو أرضعت من يفسخ نكاحها رضاعه','9992','3361','0');
INSERT INTO [t19188] VALUES (3383,'٣١٩٥ - مسألة: (وإن أعتق أحد الشريكين وهو معسر، فلا خيار لها. وقال أبو بكر: لها الخيار)','9993','3361','0');
INSERT INTO [t19188] VALUES (3384,'٣١٩٦ - مسألة: (وإن عتق الزوجان معا، فلا خيار لها. وعنه ينفسخ نكاحهما)','9997','3361','0');
INSERT INTO [t19188] VALUES (3385,'باب حكم العيوب في النكاح','10002','3237','0');
INSERT INTO [t19188] VALUES (3386,'٣١٩٧ - مسألة: (فإن اختلفا في إمكان الجماع)','10005','3385','0');
INSERT INTO [t19188] VALUES (3387,'٣١٩٨ - مسألة: فإن ادعت ذلك، أجل سنة منذ ترافعه','10008','3385','0');
INSERT INTO [t19188] VALUES (3388,'٣١٩٩ - مسألة: (فإن اعترف بذلك)','10008','3385','0');
INSERT INTO [t19188] VALUES (3389,'٣٢٠٠ - مسألة: (فإن اعترفت أنه وطئها مرة، بطل كونه عنينا)','10011','3385','0');
INSERT INTO [t19188] VALUES (3390,'٣٢٠١ - مسألة: (وإن وطئها في الدبر، أو وطئ غيرها، لم تزل العنة. ويحتمل أن تزول)','10013','3385','0');
INSERT INTO [t19188] VALUES (3391,'٣٢٠٢ - مسألة: (وإن ادعى أنه وطئها، وقالت: إنها عذراء. فشهدت بذلك امرأة ثقة، فالقول قولها، وإلا فالقول قوله)','10016','3385','0');
INSERT INTO [t19188] VALUES (3392,'٣٢٠٣ - مسألة: (وإن كانت ثيبا، فالقول قوله)','10018','3385','0');
INSERT INTO [t19188] VALUES (3393,'٣٢٠٤ - مسألة: (وإن علم بالعيب وقت العقد، أو قال: قد رضيت به معيبا)','10032','3385','0');
INSERT INTO [t19188] VALUES (3394,'٣٢٠٥ - مسألة: (ولا يجوز الفسخ إلا بحكم حاكم)','10035','3385','0');
INSERT INTO [t19188] VALUES (3395,'٣٢٠٦ - مسألة: (فإن فسخ قبل الدخول، فلا مهر، وإن فسخ بعده، فعليه المهر المسمى. وقيل)','10036','3385','0');
INSERT INTO [t19188] VALUES (3396,'٣٢٠٧ - مسألة: (ويرجع به على من غره، من المرأة والولي. وعنه، لا يرجع)','10038','3385','0');
INSERT INTO [t19188] VALUES (3397,'٣٢٠٨ - مسألة: (وليس له تزويج كبيرة بمعيب بغير رضاها)','10043','3385','0');
INSERT INTO [t19188] VALUES (3398,'٣٢٠٩ - مسألة: (فإن اختارت الكبيرة تزويج مجبوب أو عنين، لم يملك منعها)','10044','3385','0');
INSERT INTO [t19188] VALUES (3399,'٣٢١٠ - مسألة: (فأما إن علمت العيب بعد العقد، أو حدث','10046','3385','0');
INSERT INTO [t19188] VALUES (3400,'باب نكاح الكفار','10050','3237','0');
INSERT INTO [t19188] VALUES (3401,'٣٢١١ - مسألة: (ويقرون على الأنكحة المحرمة، ما اعتقدوا حلها، ولم يرتفعوا إلينا)','10052','3400','0');
INSERT INTO [t19188] VALUES (3402,'٣٢١٢ - مسألة: (وإن أسلموا، أو ترافعوا إلينا في ابتداء العقد، لم نمضه إلا على الوجه الصحيح)','10054','3400','0');
INSERT INTO [t19188] VALUES (3403,'٣٢١٣ - مسألة: لكن إن كانت المرأة في هذه الحال ممن [يجوز ابتداء نكاحها في الحال، أقرا عليه (وإن كانت ممن]','10055','3400','0');
INSERT INTO [t19188] VALUES (3404,'٣٢١٤ - مسألة: (وإن قهر حربي حربية، فوطئها، أو طاوعته، واعتقداه نكاحا)','10058','3400','0');
INSERT INTO [t19188] VALUES (3405,'٣٢١٥ - مسألة: (وإن كان المهر مسمى صحيحا، أو فاسدا قبضته، استقر، وإن كان فاسدا لم تقبضه، فرض لها مهر المثل)','10058','3400','0');
INSERT INTO [t19188] VALUES (3406,'٣٢١٦ - مسألة: (فإن أسلمت الكتابية)','10064','3400','0');
INSERT INTO [t19188] VALUES (3407,'٣٢١٧ - مسألة: (فإن كانت هي المسلمة، فلا مهر لها،','10064','3400','0');
INSERT INTO [t19188] VALUES (3408,'٣٢١٨ - مسألة: (وإن قالت: أسلمت قبلي. وأنكرها، فالقول قولها)','10068','3400','0');
INSERT INTO [t19188] VALUES (3409,'٣٢١٩ - مسألة: (وإن قال)','10069','3400','0');
INSERT INTO [t19188] VALUES (3410,'٣٢٢٠ - مسألة: (وإن أسلم أحدهما بعد الدخول، وقف الأمر على انقضاء العدة، فإن أسلم الثاني قبل انقضائها، فهما على نكاحهما، وإلا تبينا أن الفرقة وقعت)','10070','3400','0');
INSERT INTO [t19188] VALUES (3411,'٣٢٢١ - مسألة: (فإن أسلمت قبله، فلها نفقة العدة)','10076','3400','0');
INSERT INTO [t19188] VALUES (3412,'٣٢٢٢ - مسألة: (فإن اختلفا في السابق منهما)','10077','3400','0');
INSERT INTO [t19188] VALUES (3413,'٣٢٢٣ - مسألة: (وعنه، أن الفرقة تتعجل بإسلام أحدهما، كما قبل الدخول)','10078','3400','0');
INSERT INTO [t19188] VALUES (3414,'٣٢٢٤ - مسألة: (فأما الصداق، فواجب بكل حال)','10078','3400','0');
INSERT INTO [t19188] VALUES (3415,'٣٢٢٥ - مسألة: (وإن كانت الردة بعد الدخول، فهل تتعجل الفرقة أو تقف على انقضاء العدة؟ على روايتين)','10081','3400','0');
INSERT INTO [t19188] VALUES (3416,'٣٢٢٦ - مسألة: (فإن كان هو المرتد، فعليه نفقة العدة)','10083','3400','0');
INSERT INTO [t19188] VALUES (3417,'٣٢٢٧ - مسألة: (وإن انتقل أحد الكتابيين إلى دين لا يقر عليه، فهو كردته)','10086','3400','0');
INSERT INTO [t19188] VALUES (3418,'٣٢٢٨ - مسألة: (وعليه نفقتهن إلى أن يختار)','10094','3400','0');
INSERT INTO [t19188] VALUES (3419,'٣٢٢٩ - مسألة: (وإن طلق إحداهن، كان اختيارا لها)','10096','3400','0');
INSERT INTO [t19188] VALUES (3420,'٣٢٣٠ - مسألة (وإن وطئ)','10096','3400','0');
INSERT INTO [t19188] VALUES (3421,'٣٢٣١ - مسألة: (وإن طلق الجميع، أقرع بينهن، فأخرج أربع منهن بالقرعة)','10098','3400','0');
INSERT INTO [t19188] VALUES (3422,'٣٢٣٢ - مسألة: (وإن ظاهر أو آلى من إحداهن، فهل يكون اختيارا لها؟ على وجهين)','10099','3400','0');
INSERT INTO [t19188] VALUES (3423,'٣٢٣٣ - مسألة: (وإن مات فعلى الجميع عدة الوفاة)','10101','3400','0');
INSERT INTO [t19188] VALUES (3424,'٣٢٣٤ - مسألة: (والميراث لأربع منهن بالقرعة)','10104','3400','0');
INSERT INTO [t19188] VALUES (3425,'٣٢٣٥ - مسألة: (وإن أسلم وتحته أختان، اختار منهما واحدة)','10107','3400','0');
INSERT INTO [t19188] VALUES (3426,'٣٢٣٦ - مسألة: (وإن كانتا أما وبنتا)','10109','3400','0');
INSERT INTO [t19188] VALUES (3427,'٣٢٣٧ - مسألة: (فإن أسلم وهو موسر، فلم يسلمن حتى أعسر، فله الاختيار منهن)','10115','3400','0');
INSERT INTO [t19188] VALUES (3428,'٣٢٣٨ - مسألة: (وإن أسلمت إحداهن بعده، ثم عتقت، ثم أسلم البواقي، فله الاختيار منهن)','10116','3400','0');
INSERT INTO [t19188] VALUES (3429,'٣٢٣٩ - مسألة: (وإن أسلم وتحته حرة وإماء، فأسلمت الحرة في عدتها قبلهن أو بعدهن، انفسخ نكاحهن)','10119','3400','0');
INSERT INTO [t19188] VALUES (3430,'٣٢٤٠ - مسألة: (وإن أسلم عبد وتحته إماء، فأسلمن معه، ثم أعتق، فله أن يختار منهن)','10122','3400','0');
INSERT INTO [t19188] VALUES (3431,'٣٢٤١ - مسألة: (وإن أسلم وأعتق، ثم أسلمن، فحكمه حكم الحر، لا يجوز أن يختار منهن إلا بوجود الشرطين فيه)','10122','3400','0');
INSERT INTO [t19188] VALUES (3432,'كتاب الصداق','10123','0','0');
INSERT INTO [t19188] VALUES (3433,'[باب. . .]','10123','3432','0');
INSERT INTO [t19188] VALUES (3434,'٣٢٤٢ - مسألة: (ويستحب تخفيفه)','10125','3433','0');
INSERT INTO [t19188] VALUES (3435,'٣٢٤٣ - مسألة: (و)','10126','3433','0');
INSERT INTO [t19188] VALUES (3436,'٣٢٤٤ - مسألة: (و)','10127','3433','0');
INSERT INTO [t19188] VALUES (3437,'٣٢٤٥ - مسألة: (ولا يتقدر أقله ولا أكثره، بل كل ما جاز أن','10128','3433','0');
INSERT INTO [t19188] VALUES (3438,'٣٢٤٦ - مسألة: كل ما جاز أن يكون ثمنا، جاز أن يكون صداقا (من قليل وكثير، وعين ودين، ومعجل ومؤجل، ومنفعة معلومة، كرعاية غنمها مدة معلومة، وخياطة ثوب، ورد عبدها الآبق من موضع معين)','10132','3433','0');
INSERT INTO [t19188] VALUES (3439,'٣٢٤٧ - مسألة: (وإن كانت)','10133','3433','0');
INSERT INTO [t19188] VALUES (3440,'٣٢٤٨ - مسألة: (وإن تزوجها على منافعه مدة معلومة، فعلى روايتين)','10135','3433','0');
INSERT INTO [t19188] VALUES (3441,'٣٢٤٩ - مسألة: (وكل موضع لا تصح التسمية، يجب مهر المثل)','10137','3433','0');
INSERT INTO [t19188] VALUES (3442,'٣٢٥٠ - مسألة: (وإن أصدقها تعليم أبواب من الفقه أو الحديث، أو قصيدة من الشعر المباح، صح)','10138','3433','0');
INSERT INTO [t19188] VALUES (3443,'٣٢٥١ - مسألة: (وإن كان لا يحفظها, لم يصح. ويحتمل أن يصح، ويتعلمها ثم يعلمها)','10139','3433','0');
INSERT INTO [t19188] VALUES (3444,'٣٢٥٢ - مسألة: (فإن تعلمتها من غيره، فعليه أجرة تعليمها)','10141','3433','0');
INSERT INTO [t19188] VALUES (3445,'٣٢٥٣ - مسألة: (فإن طلقها قبل الدخول وقبل تعليمها)','10142','3433','0');
INSERT INTO [t19188] VALUES (3446,'٣٢٥٤ - مسألة: (وإن أصدقها تعليم شيء من القرآن معين، لم يصح. وعنه، يصح)','10143','3433','0');
INSERT INTO [t19188] VALUES (3447,'٣٢٥٥ - مسألة: (ولا يحتاج إلى ذكر قراءة من. وقال أبو الخطاب: يحتاج إلى ذلك)','10146','3433','0');
INSERT INTO [t19188] VALUES (3448,'٣٢٥٦ - مسألة: (وإن تزوج نساء بمهر واحد، أو خالعهن بعوض واحد، صح، ويقسم بينهن على قدر مهورهن، في أحد الوجهين. وفي الآخر، يقسم بينهن بالسوية)','10148','3433','0');
INSERT INTO [t19188] VALUES (3449,'٣٢٥٧ - مسألة: (وإن أصدقها عبدا من عبيده، لم يصح. ذكره أبو بكر)','10155','3433','0');
INSERT INTO [t19188] VALUES (3450,'٣٢٥٨ - مسألة: (و)','10159','3433','0');
INSERT INTO [t19188] VALUES (3451,'٣٢٥٩ - مسألة: (وإن أصدقها طلاق امرأة له أخرى، لم يصح. وعنه، يصح. فإن فات طلاقها بموتها، فلها مهرها في قياس المذهب)','10161','3433','0');
INSERT INTO [t19188] VALUES (3452,'٣٢٦٠ - مسألة: (وإن تزوجها على ألف إن كان أبوها حيا، وألفين إن كان ميتا، لم تصح)','10167','3433','0');
INSERT INTO [t19188] VALUES (3453,'٣٢٦١ - مسألة: (وإن تزوجها على ألف إن لم يكن له زوجة، وألفين إن كان له زوجة، لم تصح)','10167','3433','0');
INSERT INTO [t19188] VALUES (3454,'٣٢٦٢ - مسألة: (وإذا قال العبد لسيدته: أعتقيني على أن أتزوجك. فأعتقته على ذلك، عتق، ولم يلزمه شيء)','10170','3433','0');
INSERT INTO [t19188] VALUES (3455,'٣٢٦٣ - مسألة: (وإذا فرض الصداق مؤجلا ولم','10170','3433','0');
INSERT INTO [t19188] VALUES (3456,'٣٢٦٤ - مسألة: (وإن تزوجها على عبد فخرج حرا أو مغصوبا، أو عصير فبان خمرا، فلها قيمته)','10177','3433','0');
INSERT INTO [t19188] VALUES (3457,'٣٢٦٥ - مسألة: (وإن وجدت به عيبا، فلها الخيار بين أخذ أرشه','10180','3433','0');
INSERT INTO [t19188] VALUES (3458,'٣٢٦٦ - مسألة: (فإن طلقها قبل الدخول، رجع عليها بالألف)','10184','3433','0');
INSERT INTO [t19188] VALUES (3459,'٣٢٦٧ - مسألة: (وإن فعل ذلك غير الأب، فالكل لها دونه)','10187','3433','0');
INSERT INTO [t19188] VALUES (3460,'٣٢٦٨ - مسألة: (وللأب تزويج ابنته البكر والثيب بدون صداق','10187','3433','0');
INSERT INTO [t19188] VALUES (3461,'٣٢٦٩ - مسألة: (وإن فعل ذلك غيره بإذنها، صح، ولم يكن لغيره الاعتراض)','10190','3433','0');
INSERT INTO [t19188] VALUES (3462,'٣٢٧٠ - مسألة: (وإن زوج ابنه الصغير بأكثر من مهر المثل، صح، ولزم ذمة الابن)','10191','3433','0');
INSERT INTO [t19188] VALUES (3463,'٣٢٧١ - مسألة: (فإن كان معسرا، فهل يضمنه الأب؟ يحتمل وجهين)','10192','3433','0');
INSERT INTO [t19188] VALUES (3464,'٣٢٧٢ - مسألة: (وللأب قبض صداق ابنته الصغيرة بغير إذنها)','10194','3433','0');
INSERT INTO [t19188] VALUES (3465,'٣٢٧٣ - مسألة: (وإن تزوج بغير إذن سيده، لم يصح النكاح)','10198','3433','0');
INSERT INTO [t19188] VALUES (3466,'٣٢٧٤ - مسألة: فإن فارقها قبل الدخول، فلا شيء عليه؛ لأنه عقد باطل؛ فلا يوجب بمجرده شيئا، كالبيع الباطل، وهكذا سائر الأنكحة الفاسدة، لا توجب بمجردها شيئا. فإن أصابها، وجب لها المهر، في الصحيح من المذهب، رواه عن أحمد جماعة وروى عنه حنبل أنه لا مهر لها إذا تزوج العبد بغير إذن سيده. وهذا يمكن حمله على ما قبل الدخول، فيكون موافقا لرواية الجماعة، ويمكن حمله على عمومه في عدم الصداق. وهو قول ابن عمر. ورواه الأثرم، عن نافع، قال: كان إذا تزوج مملوك [لابن عمر]','10200','3433','0');
INSERT INTO [t19188] VALUES (3467,'٣٢٧٥ - مسألة: (والواجب مهر المثل)','10202','3433','0');
INSERT INTO [t19188] VALUES (3468,'٣٢٧٦ - مسألة: (وإن زوج السيد عبده أمته، لم يجب مهر. ذكره أبو بكر)','10206','3433','0');
INSERT INTO [t19188] VALUES (3469,'٣٢٧٧ - مسألة: (وإن زوج عبده حرة، ثم باعها)','10207','3433','0');
INSERT INTO [t19188] VALUES (3470,'٣٢٧٨ - مسألة: (فإن باعها إياه بالصداق، صح)','10210','3433','0');
INSERT INTO [t19188] VALUES (3471,'٣٢٧٩ - مسألة: (فإن كان معينا، كالعبد والدار، فلها التصرف فيه، ونماؤه لها، وزكاته ونقصه وضمانه عليها)','10212','3433','0');
INSERT INTO [t19188] VALUES (3472,'٣٢٨٠ - مسألة: (فإن كان غير معين، كقفيز من صبرة، لم يدخل في ضمانها، ولم تملك التصرف فيه، إلا بقبضه كالبيع)','10214','3433','0');
INSERT INTO [t19188] VALUES (3473,'٣٢٨١ - مسألة: (فإن قبضت صداقها ثم طلقها قبل الدخول، رجع)','10216','3433','0');
INSERT INTO [t19188] VALUES (3474,'٣٢٨٢ - مسألة: (فإن زاد زيادة منفصلة، رجع في نصف الأصل، والزيادة لها، وإن كانت متصلة، فهي مخيرة بين دفع نصفه زائدا، وبين دفع نصف قيمته يوم العقد)','10219','3433','0');
INSERT INTO [t19188] VALUES (3475,'٣٢٨٣ - مسألة: (وإن كان ناقصا، خير الزوج بين أخذ نصفه ناقصا، وبين)','10222','3433','0');
INSERT INTO [t19188] VALUES (3476,'٣٢٨٤ - مسألة: (وإن كان تالفا، أو مستحقا بدين أو شفعة، فله نصف القيمة يوم العقد، إلا أن يكون مثليا، فيرجع بنصف مثله. وقال القاضى: له القيمة أقل ما كانت من يوم العقد إلى يوم القبض)','10225','3433','0');
INSERT INTO [t19188] VALUES (3477,'٣٢٨٥ - مسألة: (وإن نقص الصداق فى يدها بعد الطلاق، فهل','10229','3433','0');
INSERT INTO [t19188] VALUES (3478,'٣٢٨٦ - مسألة: (وإن قال الزوج: نقص قبل الطلاق)','10231','3433','0');
INSERT INTO [t19188] VALUES (3479,'٣٢٨٧ - مسألة: (والزوج هو الذى، بيده عقدة النكاح، فإذا طلقها قبل الدخول، فأيهما عفا لصاحبه عهتا وجب له من المهر، وهو جائز الأمر فى ماله، برئ منه صاحبه. وعنه، أنه الأب. فله أن يعفو عن نصف صداق ابنته الصغيرة إذا طلقت قبل الدخول)','10243','3433','0');
INSERT INTO [t19188] VALUES (3480,'فصل','10252','3432','0');
INSERT INTO [t19188] VALUES (3481,'٣٢٨٨ - مسألة: (وكل فرقة جاءت من)','10261','3480','0');
INSERT INTO [t19188] VALUES (3482,'٣٢٨٩ - مسألة: (وكل فرقة جاءت من المرأة)','10263','3480','0');
INSERT INTO [t19188] VALUES (3483,'٣٢٩٠ - مسألة: (وفرقة اللعان تخرج على روايتين)','10265','3480','0');
INSERT INTO [t19188] VALUES (3484,'٣٢٩١ - مسألة: (وفى فرقة بيع الزوجة من الزوج وشرائها له وجهان)','10266','3480','0');
INSERT INTO [t19188] VALUES (3485,'٣٢٩٢ - مسألة: (وفرقة الموت يستقر بها المهر كله كالدخول)','10267','3480','0');
INSERT INTO [t19188] VALUES (3486,'فصل','10268','3432','0');
INSERT INTO [t19188] VALUES (3487,'٣٢٩٣ - مسألة: (فإن ادعى أقل منه، وادعت أكثر منه، رد إليه بلا يمين، عند القاضى فى الأحوال كلها)','10270','3486','0');
INSERT INTO [t19188] VALUES (3488,'٣٢٩٤ - مسألة: (وإن قال: تزوجتك على هذا العبد. قالت: بل على هذه الأمة. خرج على الروايتين)','10274','3486','0');
INSERT INTO [t19188] VALUES (3489,'٣٢٩٥ - مسألة: (وإن اختلفا فى قبض الصداق، فالقول قولها)','10280','3486','0');
INSERT INTO [t19188] VALUES (3490,'٣٢٩٦ - مسألة: (وإن تزوجها على صداقين سر وعلانية، أخذ بالعلانية وإن كان قد انعقد بالسر)','10280','3486','0');
INSERT INTO [t19188] VALUES (3491,'٣٢٩٧ - مسألة: (وإن قال: هو عقد واحد، أسررته ثم أظهرته. وقالت: بل هو عقدان. فالقول قولها مع يمينها)','10283','3486','0');
INSERT INTO [t19188] VALUES (3492,'فصل فى المفوضة','10291','3432','0');
INSERT INTO [t19188] VALUES (3493,'٣٢٩٨ - مسألة: (ولها المطالبة بفرضه)','10293','3492','0');
INSERT INTO [t19188] VALUES (3494,'٣٢٩٩ - مسألة: (وإن مات أحدهما قبل الإصابة، ورثة صاحبه، ولها مهر نسائها)','10300','3492','0');
INSERT INTO [t19188] VALUES (3495,'٣٣٠٠ - مسألة: (فإن طلقها قبل الدخول، لم يكن لها عليه إلا المتعة)','10303','3492','0');
INSERT INTO [t19188] VALUES (3496,'٣٣٠١ - مسألة: (على الموسع قدره وعلى المقتر قدره، فأعلاها خادم، وأدناها كسوة)','10308','3492','0');
INSERT INTO [t19188] VALUES (3497,'٣٣٠٢ - مسألة: (وعن أحمد، يرجع فى تقديرها إلى الحاكم)','10309','3492','0');
INSERT INTO [t19188] VALUES (3498,'٣٣٠٣ - مسألة: (فإن دخل بها، استقر مهر المثل)','10312','3492','0');
INSERT INTO [t19188] VALUES (3499,'فصل','10316','3432','0');
INSERT INTO [t19188] VALUES (3500,'٣٣٠٤ - مسألة: (وتعتبر المساواة فى المال، والجمال، والعقل، والأدب، والسن، والبكارة، والثيوبة، والبلد)','10318','3499','0');
INSERT INTO [t19188] VALUES (3501,'٣٣٠٥ - مسألة: (فإن لم يوجد إلا دونها، زيدت بقدر فضيلتها)','10318','3499','0');
INSERT INTO [t19188] VALUES (3502,'٣٣٠٦ - مسألة: (فإن كانت)','10319','3499','0');
INSERT INTO [t19188] VALUES (3503,'فصل','10320','3432','0');
INSERT INTO [t19188] VALUES (3504,'٣٣٠٧ - مسألة: (فإن لم يكن لها أقارب، اعتبر)','10320','3503','0');
INSERT INTO [t19188] VALUES (3505,'٣٣٠٨ - مسألة: (فإن دخل بها، استقر المسمى. [وعنه، يجب مهر المثل. وهى أصح)','10321','3503','0');
INSERT INTO [t19188] VALUES (3506,'٣٣٠٩ - مسألة: (ولا يستقر بالخلوة)','10323','3503','0');
INSERT INTO [t19188] VALUES (3507,'٣٣١٠ - مسألة: (ويجب مهر المثل للموطوءة بشبهة، والمكرهة على الزنى، ولا يجب معه أرش البكارة. ويحتمل أن يجب للمكرهة)','10325','3503','0');
INSERT INTO [t19188] VALUES (3508,'٣٣١١ - مسألة: (وإذا دفع أجنبية فأذهب عذرتها، فعليه أرش بكارتها. وقال القاضى: يجب مهر المثل)','10332','3503','0');
INSERT INTO [t19188] VALUES (3509,'٣٣١٢ - مسألة: (وإن فعل ذلك الزوج، ثم طلق قبل الدخول، لم يكن)','10335','3503','0');
INSERT INTO [t19188] VALUES (3510,'٣٣١٣ - مسألة: (وللمرأة منع نفسها حتى تقبض مهرها)','10336','3503','0');
INSERT INTO [t19188] VALUES (3511,'٣٣١٤ - مسألة: (وإن أعسر بالمهر قبل الدخول، فلها الفسخ)','10340','3503','0');
INSERT INTO [t19188] VALUES (3512,'٣٣١٥ - مسألة: (ولا يجوز الفسخ)','10343','3503','0');
INSERT INTO [t19188] VALUES (3513,'باب الوليمة','10345','3432','0');
INSERT INTO [t19188] VALUES (3514,'٣٣١٦ - مسألة: (وهى مستحبة)','10348','3513','0');
INSERT INTO [t19188] VALUES (3515,'٣٣١٧ - مسألة: (والإجابة إليها واجبة، إذا عينه الداعى المسلم فى اليوم الأول)','10351','3513','0');
INSERT INTO [t19188] VALUES (3516,'٣٣١٨ - مسألة: (فإن دعا الجفلى، كقوله: يا أيها الناس تعالوا إلى الطعام)','10354','3513','0');
INSERT INTO [t19188] VALUES (3517,'٣٣١٩ - مسألة: (أو دعاه فيما بعد اليوم الأول)','10354','3513','0');
INSERT INTO [t19188] VALUES (3518,'٣٣٢٠ - مسألة: (وسائر الدعوات والإجابة إليها مستحبة غير واجبة)','10357','3513','0');
INSERT INTO [t19188] VALUES (3519,'٣٣٢١ - مسألة: (وإذا حضر وهو صائم صوما واجبا، لم يفطر،','10359','3513','0');
INSERT INTO [t19188] VALUES (3520,'٣٣٢٢ - مسألة: (وإن دعاه اثنان، أجاب أولهما)','10363','3513','0');
INSERT INTO [t19188] VALUES (3521,'٣٣٢٣ - مسألة: (وإن علم أن فى الدعوة منكرا، كالزمر والخمر، وأمكنه الإنكار، حضر وأنكر، وإلا لم يحضر)','10365','3513','0');
INSERT INTO [t19188] VALUES (3522,'٣٣٢٤ - مسألة: (وإن علم به، ولم يره ولم يسمعه، فله الجلوس)','10367','3513','0');
INSERT INTO [t19188] VALUES (3523,'٣٣٢٥ - مسألة: (وإن شاهد ستورا معلقة فيها صور الحيوان، لم يجلس إلا أن تزال، وإن كانت مبسوطة أو على وسائد، فلا بأس)','10368','3513','0');
INSERT INTO [t19188] VALUES (3524,'٣٣٢٦ - مسألة: (فإن سترت الحيطان بستور لا صور فيها، أو فيها صور غير الحيوان، فهل تباح؟ على روايتين)','10375','3513','0');
INSERT INTO [t19188] VALUES (3525,'٣٣٢٧ - مسألة: (ولا يباح الأكل بغير إذن)','10380','3513','0');
INSERT INTO [t19188] VALUES (3526,'٣٣٢٨ - مسألة: (والنثار والتقاطه مكروه. وعنه، لا يكره)','10382','3513','0');
INSERT INTO [t19188] VALUES (3527,'٣٣٢٩ - مسألة: (ومن حصل فى حجره شئ، فهو له)','10386','3513','0');
INSERT INTO [t19188] VALUES (3528,'٣٣٣٠ - مسألة: (ويستحب إعلان النكاح والضرب عليه بالدف)','10387','3513','0');
INSERT INTO [t19188] VALUES (3529,'باب عشرة النساء','10408','3432','0');
INSERT INTO [t19188] VALUES (3530,'٣٣٣١ - مسألة: (وإذا تم العقد، وجب تسليم المرأة في بيت الزوج إذا طلبها، وكانت حرة يمكن الاستمتاع بها)','10411','3529','0');
INSERT INTO [t19188] VALUES (3531,'٣٣٣٢ - مسألة: (و)','10414','3529','0');
INSERT INTO [t19188] VALUES (3532,'٣٣٣٣ - مسألة: (فإن سألت الإنظار، انظرت مدة جرت العادة بإصلاح أمرها فيها)','10414','3529','0');
INSERT INTO [t19188] VALUES (3533,'٣٣٣٤ - مسألة: (وإن كانت أمة، لم يجب تسليمها إلا بالليل)','10415','3529','0');
INSERT INTO [t19188] VALUES (3534,'٣٣٣٥ - مسألة: (وله الاستمتاع بها ما لم يشغلها عن الفرائض، من غير إضرار بها)','10417','3529','0');
INSERT INTO [t19188] VALUES (3535,'٣٣٣٦ - مسألة: (ولا يجوز وطؤها فى الحيض)','10418','3529','0');
INSERT INTO [t19188] VALUES (3536,'٣٣٣٧ - مسألة: (ولا يعزل عن الحرة إلا بإذنها)','10422','3529','0');
INSERT INTO [t19188] VALUES (3537,'٣٣٣٨ - مسألة: (وله إجبارها على الغسل من الحيض والجنابة والنجاسة، واجتناب المحرمات، وأخذ الشعر الذى تعافه النفس، إلا الذمية، فله إجبارها على الغسل من الحيض، وفى سائر الأشياء روايتان)','10426','3529','0');
INSERT INTO [t19188] VALUES (3538,'فصل','10430','3529','0');
INSERT INTO [t19188] VALUES (3539,'٣٣٣٩ - مسألة: (وإن كانت أمة، فمن كل ثمان)','10432','3529','0');
INSERT INTO [t19188] VALUES (3540,'٣٣٤٠ - مسألة: (وله الانفراد بنفسه فيما بقى)','10433','3529','0');
INSERT INTO [t19188] VALUES (3541,'٣٣٤١ - مسألة: (وعليه أن يطأ فى كل أربعة أشهر مرة)','10434','3529','0');
INSERT INTO [t19188] VALUES (3542,'٣٣٤٢ - مسألة: (فإن سافر عنها أكثر من ستة أشهر فطلبت قدومه، لزمه ذلك إن لم يكن عذر)','10436','3529','0');
INSERT INTO [t19188] VALUES (3543,'٣٣٤٣ - مسألة: (فإن أبى شيئا من ذلك ولم يكن عذر، فطلبت','10438','3529','0');
INSERT INTO [t19188] VALUES (3544,'٣٣٤٤ - مسألة: (ويستحب أن يقول عند الجماع: بسم الله، اللهم جنبنى الشيطان، وجنب الشيطان ما رزقتنى)','10441','3529','0');
INSERT INTO [t19188] VALUES (3545,'٣٣٤٥ - مسألة: (ولا يكثر الكلام حال الوطء)','10445','3529','0');
INSERT INTO [t19188] VALUES (3546,'٣٣٤٦ - مسألة: (ولا ينزع إذا فرغ قبلها حتى تفرغ)','10446','3529','0');
INSERT INTO [t19188] VALUES (3547,'٣٣٤٧ - مسألة: (ولا بأس أن يجمع بين وطء نسائه وإمائه بغسل واحد)','10447','3529','0');
INSERT INTO [t19188] VALUES (3548,'٣٣٤٨ - مسألة: (ولا يجامع إحداهما بحيث تراه الأخرى أو غيرها)','10449','3529','0');
INSERT INTO [t19188] VALUES (3549,'٣٣٤٩ - مسألة: (وله منعها من الخروج من منزله)','10451','3529','0');
INSERT INTO [t19188] VALUES (3550,'٣٣٥٠ - مسألة: (فإن مرض بعض محارمها أو مات، استحب له أن يأذن لها فى الخروج إليه)','10452','3529','0');
INSERT INTO [t19188] VALUES (3551,'٣٣٥١ - مسألة: (ولا تملك المرأة إجارة نفسها للرضاع والخدمة بغير إذن زوجها)','10456','3529','0');
INSERT INTO [t19188] VALUES (3552,'٣٣٥٢ - مسألة: (وله أن يمنعها من رضاع ولدها، إلا أن يضطر إليها، وتخشى عليه)','10457','3529','0');
INSERT INTO [t19188] VALUES (3553,'فصل فى القسم','10460','3529','0');
INSERT INTO [t19188] VALUES (3554,'٣٣٥٣ - مسألة: (وعماد القسم الليل، إلا لمن، معيشته بالليل، كالحارس)','10461','3529','0');
INSERT INTO [t19188] VALUES (3555,'٣٣٥٤ - مسألة: (وليس له البداية بإحداهن ولا السفر بها إلا بقرعة)','10463','3529','0');
INSERT INTO [t19188] VALUES (3556,'٣٣٥٥ - مسألة: (فإذا بات عندها بقرعة أو غيرها، لزمه المبيت عند الثانية)','10464','3529','0');
INSERT INTO [t19188] VALUES (3557,'٣٣٥٦ - مسألة: (وليس عليه التسوية بينهن فى الوطء، بل يستحب)','10464','3529','0');
INSERT INTO [t19188] VALUES (3558,'٣٣٥٧ - مسألة: (ويقسم لزوجته الأمة ليلة، وللحرة ليلتين وإن كانت كتابية)','10466','3529','0');
INSERT INTO [t19188] VALUES (3559,'٣٣٥٨ - مسألة: (ويقسم للحائض، والنفساء، والمريضة،','10470','3529','0');
INSERT INTO [t19188] VALUES (3560,'٣٣٥٩ - مسألة: (فإن دخل فى ليلتها إلى غيرها، لم يجز إلا لحاجة داعية، فإن لم يلبث، لم يقض، وإن لبث، أو جامع، لزمه أن يقضى لها ذلك من حق الأخرى)','10471','3529','0');
INSERT INTO [t19188] VALUES (3561,'٣٣٦٠ - مسألة: (وإن أراد النقلة من بلد إلى بلد وأخذ إحداهن معه، والأخرى مع غيره، لم يجز إلا بقرعة)','10478','3529','0');
INSERT INTO [t19188] VALUES (3562,'٣٣٦١ - مسألة: (ومتى سافر بها بقرعة، لم يقض، وإن كان','10478','3529','0');
INSERT INTO [t19188] VALUES (3563,'٣٣٦٢ - مسألة: (وإن امتنعت من السفر معه، أو من المبيت عنده، أو سافرت بغير إذنه، سقط حقها من القسم)','10483','3529','0');
INSERT INTO [t19188] VALUES (3564,'٣٣٦٣ - مسألة: (وإن أشخصها هو، فهى على حقها من ذلك)','10483','3529','0');
INSERT INTO [t19188] VALUES (3565,'٣٣٦٤ - مسألة: (وإن سافرت لحاجتها بإذنه، فعلى وجهين)','10484','3529','0');
INSERT INTO [t19188] VALUES (3566,'٣٣٦٥ - مسألة: (وللمرأة أن تهب حقها من القسم لبعض ضرائرها باذنه، وله فيجعله لمن شاء منهن)','10485','3529','0');
INSERT INTO [t19188] VALUES (3567,'٣٣٦٦ - مسألة: (فمتى رجعت فى الهبة، عاد حقها)','10488','3529','0');
INSERT INTO [t19188] VALUES (3568,'٣٣٦٧ - مسألة: (ولا قسم عليه فى ملك اليمين، وله الاستمتاع بهن كيف شاء)','10490','3529','0');
INSERT INTO [t19188] VALUES (3569,'٣٣٦٨ - مسألة: (وتستحب التسوية بينهن)','10490','3529','0');
INSERT INTO [t19188] VALUES (3570,'فصل','10491','3529','0');
INSERT INTO [t19188] VALUES (3571,'٣٣٦٩ - مسألة: (وإن زفت إليه امرأتان، قدم السابقة منهما، ثم أقام عند الأخرى، ثم دار، فإن زفتا معا، قدم إحداهما بالقرعة، ثم أقام عند الأخرى،)','10494','3529','0');
INSERT INTO [t19188] VALUES (3572,'٣٣٧٠ - مسألة: (وإن أراد السفر فخرجت القرعة لإحداهما،','10495','3529','0');
INSERT INTO [t19188] VALUES (3573,'٣٣٧١ - مسألة: (وإن طلق إحدى نسائه فى ليلتها، أثم)','10497','3529','0');
INSERT INTO [t19188] VALUES (3574,'فصل فى النشوز','10498','3529','0');
INSERT INTO [t19188] VALUES (3575,'٣٣٧٢ - مسألة: (وله أن يخرج فى نهار ليل القسم لمعاشه وقضاء حقوق الناس)','10498','3529','0');
INSERT INTO [t19188] VALUES (3576,'٣٣٧٣ - مسألة: (فمتى ظهرت منها أمارات النشوز، بأن لا تجيبه إلى الاستمتاع، أو تجيبه متبرمة متكرهة، وعظها، فإن أصرت، هجرها فى المضجع ما شاء، وفى الكلام ما دون ثلاثة أيام، فإن أصرت، فله أن يضربها ضربا غير مبرح)','10499','3529','0');
INSERT INTO [t19188] VALUES (3577,'٣٣٧٤ - مسألة: (فإن ادعى كل واحد منهما ظلم صاحبه له، أسكنهما الحاكم إلى جانب ثقة، يشرف عليهما، ويلزمهما الإنصاف)','10506','3529','0');
INSERT INTO [t19188] VALUES (3578,'٣٣٧٥ - مسألة: (فإن خرجا إلى الشقاق والعداوة، بعث الحاكم حكمين حرين مسلمين عدلين -والأولى أن يكونا','10507','3529','0');
INSERT INTO [t19188] VALUES (3579,'٣٣٧٦ - مسألة: (فإن امتنعا من ذلك، لم يجبرا)','10509','3529','0');
INSERT INTO [t19188] VALUES (3580,'٣٣٧٧ - مسألة: (فإن غاب الزوجان أو أحدهما، لم ينقطع نظر الحكمين على الرواية الأولى، وينقطع على الثانية. وإن جنا انقطع نظرهما على)','10513','3529','0');
INSERT INTO [t19188] VALUES (3581,'كتاب الخلع','10517','0','0');
INSERT INTO [t19188] VALUES (3582,'٣٣٧٨ - مسألة: (وإذا كانت المرأة مبغضة للرجل، وتخشى أن لا تقيم حدود الله في حقه، فلا بأس أن تفتدى نفسها منه)','10517','3581','0');
INSERT INTO [t19188] VALUES (3583,'٣٣٧٩ - مسألة: (وإن خالعته لغير ذلك، كره، ووقع الخلع. وعنه، لا يجوز)','10521','3581','0');
INSERT INTO [t19188] VALUES (3584,'٣٣٨٠ - مسألة: (فأما إن عضلها لتفتدى نفسها منه، ففعلت، فالخلع باطل، والعوض مردود، والزوجية بحالها، إلا أن يكون طلاقا،','10523','3581','0');
INSERT INTO [t19188] VALUES (3585,'٣٣٨١ - مسألة-: (ويصح الخلع من كل زوج يصح طلاقه، مسلما كان أو ذميا)','10527','3581','0');
INSERT INTO [t19188] VALUES (3586,'٣٣٨٢ - مسألة: (فإن كان محجورا عليه، دفع المال إلى وليه)','10527','3581','0');
INSERT INTO [t19188] VALUES (3587,'٣٣٨٣ - مسألة: (وإن كان عبدا، دفع إلى سيده)','10528','3581','0');
INSERT INTO [t19188] VALUES (3588,'٣٣٨٤ - مسألة: (وهل للأب خلع زوجة ابنه الصغير أو طلاقها؟','10529','3581','0');
INSERT INTO [t19188] VALUES (3589,'٣٣٨٥ - مسألة: (وليس له خلع ابنته الصغيرة بشئ من مالها)','10532','3581','0');
INSERT INTO [t19188] VALUES (3590,'٣٣٨٦ - مسألة: (ويصح الخلع مع الزوجة)','10533','3581','0');
INSERT INTO [t19188] VALUES (3591,'٣٣٨٧ - مسألة: (ويصح بذل العوض فيه من كل جائز التصرف)','10535','3581','0');
INSERT INTO [t19188] VALUES (3592,'٣٣٨٨ - مسألة: (فإن خالعت الأمة على شئ معلوم بغير إذن سيدها، كان فى ذمتها، تتبع به بعد العتق)','10536','3581','0');
INSERT INTO [t19188] VALUES (3593,'٣٣٨٩ - مسألة: (وإن خالعته المحجور عليها، لم يصح الخلع، ووقع طلاقه رجعيا)','10539','3581','0');
INSERT INTO [t19188] VALUES (3594,'٣٣٩٠ - مسألة: (والخلع طلاق بائن، إلا أن يقع بلفظ الخلع أو الفسخ أو المفاداة، ولا ينوى به الطلاق، فيكون فسخا لا ينقص به عدد الطلاق، فى إحدى الروايتين. والأخرى، هو طلاق بائن بكل حال)','10541','3581','0');
INSERT INTO [t19188] VALUES (3595,'٣٣٩١ - مسألة: (ولا يقع بالمعتدة من الخلع طلاق ولو واجهها','10549','3581','0');
INSERT INTO [t19188] VALUES (3596,'٣٣٩٢ - مسألة: (وإن شرط الرجعة فى الخلع، لم يصح الشرط، فى أحد الوجهين. وفى الآخر، يصح الشرط ويبطل العوض)','10551','3581','0');
INSERT INTO [t19188] VALUES (3597,'فصل','10554','3581','0');
INSERT INTO [t19188] VALUES (3598,'٣٣٩٣ - مسألة: (ولا يستحب أن يأخذ منها أكثر مما أعطاها، فإن فعل، كره وصح. وقال أبو بكر: لا يجوز، ويرد الزيادة)','10557','3581','0');
INSERT INTO [t19188] VALUES (3599,'٣٣٩٤ - مسألة: (وإن خالعها على محرم، كالخمر والحر، فهو كالخلع بغير عوض)','10559','3581','0');
INSERT INTO [t19188] VALUES (3600,'٣٣٩٥ - مسألة: (وإن خالعها على عبد فبان حرا أو مستحقا،','10561','3581','0');
INSERT INTO [t19188] VALUES (3601,'٣٣٩٦ - مسألة: (وإن خالعها على رضاع ولده عامين، أو سكنى دار، صح، فإن مات الولد أو خربت الدار، رجع بأجرة باقى المدة)','10564','3581','0');
INSERT INTO [t19188] VALUES (3602,'٣٣٩٧ - مسألة: (وإن خالع الحامل على نفقة عدتها، صح وسقطت)','10569','3581','0');
INSERT INTO [t19188] VALUES (3603,'فصل','10571','3581','0');
INSERT INTO [t19188] VALUES (3604,'٣٣٩٨ - مسألة: (فإن خالعها على ما فى يدها من الدراهم)','10573','3581','0');
INSERT INTO [t19188] VALUES (3605,'٣٣٩٩ - مسألة: وإن خالعها على (ما فى بيتها من المتاع)','10573','3581','0');
INSERT INTO [t19188] VALUES (3606,'٣٤٠٠ - مسألة: (وإن خالعها على حمل أمتها أو ما تحمل شجرتها، فله ذلك، فإن لم يحملا، فقال أحمد: ترضيه بشئ. وقال القاضى: لا شئ له)','10574','3581','0');
INSERT INTO [t19188] VALUES (3607,'٣٤٠١ - مسألة: (وإن خالعها على عبد، فله أقل ما يسمى عبدا. وإن قال: إن أعطيتنى عبدا فأنت طالق، طلقت بأى عبد أعطته طلاقا بائنا، وملك العبد. نص عليه. وقال القاضى: يلزمها عبد وسط فيهما)','10577','3581','0');
INSERT INTO [t19188] VALUES (3608,'٣٤٠٢ - مسألة: (وإذا قال: إن أعطيتنى هذا العبد فأنت طالق. فأعطته إياه، طلقت، فإن خرج معيبا فلا شئ له)','10580','3581','0');
INSERT INTO [t19188] VALUES (3609,'٣٤٠٣ - مسألة: (وإن قال: إن أعطيتنى ثوبا هرويا فأنت طالق. فأعطته مرويا، لم تطلق)','10584','3581','0');
INSERT INTO [t19188] VALUES (3610,'فصل','10587','3581','0');
INSERT INTO [t19188] VALUES (3611,'٣٤٠٤ - مسألة: (إذا قالت: اخلعنى بألف. أو: على ألف. ففعل، بانت، واستحق الألف)','10591','3581','0');
INSERT INTO [t19188] VALUES (3612,'٣٤٠٥ - مسألة: (وإذا قالت: طلقنى واحدة بألف. فطلقها ثلاثا، استحق الألف)','10594','3581','0');
INSERT INTO [t19188] VALUES (3613,'٣٤٠٦ - مسألة: (وإن قالت: طلقنى ثلاثا بألف. فطلقها واحدة، لم يستحق شيئا. ويحتمل أن يستحق ثلث الألف)','10597','3581','0');
INSERT INTO [t19188] VALUES (3614,'٣٤٠٧ - مسألة: (وإن لم يكن بقى من طلاقها إلا واحدة ففعل، استحق الألف، علمت أو لم تعلم. ويحتمل أن لا يستحق إلا ثلثه إذا لم تعلم)','10599','3581','0');
INSERT INTO [t19188] VALUES (3615,'٣٤٠٨ - مسألة: (وإن كان له امرأتان؛ مكلفة، وغير مكلفة','10602','3581','0');
INSERT INTO [t19188] VALUES (3616,'٣٤٠٩ - مسألة: (وإن قال لامرأته: أنت طالق وعليك ألف.','10605','3581','0');
INSERT INTO [t19188] VALUES (3617,'٣٤١٠ - مسألة: (وإن قال)','10607','3581','0');
INSERT INTO [t19188] VALUES (3618,'٣٤١١ - مسألة: وإن قال: (بألف. فكذلك. ويحتمل أن لا تطلق حتى تختار، فيلزمها الألف)','10609','3581','0');
INSERT INTO [t19188] VALUES (3619,'فصل','10612','3581','0');
INSERT INTO [t19188] VALUES (3620,'٣٤١٢ - مسألة: (وإن خالعها فى مرض موته، وأوصى لها بأكثر','10613','3581','0');
INSERT INTO [t19188] VALUES (3621,'٣٤١٣ - مسألة: (وإن خالعها وحاباها، فهو من رأس المال)','10614','3581','0');
INSERT INTO [t19188] VALUES (3622,'٣٤١٤ - مسألة: (وإذا وكل الزوج فى خلع امرأته مطلقا، فخالع بمهرها فما زاد، صح، وإن نقص من المهر، رجع على الوكيل بالنقص.','10618','3581','0');
INSERT INTO [t19188] VALUES (3623,'٣٤١٥ - مسألة: (وإن وكلت المرأة فى خلعها، فخالع بمهرها فما دون، أو بما عينته فما دون، صح، وإن زاد لم يصح. ويحتمل أن يصح، وتبطل الزيادة)','10623','3581','0');
INSERT INTO [t19188] VALUES (3624,'٣٤١٦ - مسألة: (وإذا تخالعا، تراجعا بما بينهما من الحقوق. وعنه أنها تسقط)','10625','3581','0');
INSERT INTO [t19188] VALUES (3625,'فصل','10627','3581','0');
INSERT INTO [t19188] VALUES (3626,'٣٤١٧ - مسألة: (وإن اختلفا فى قدر العوض، أو عينه، أو تأجيله، فالقول قولها)','10627','3581','0');
INSERT INTO [t19188] VALUES (3627,'٣٤١٨ - مسألة: (وإن علق طلاقها بصفة، ثم خالعها فوجدت الصفة، ثم عاد فتزوجها، فوجدت الصفة، طلقت. نص عليه. ويتخرج أن لا تطلق، بناء على الرواية فى العتق. واختاره أبو الحسن التميمى. وإن لم توجد الصفة حال البينونة، عادت، رواية واحدة)','10630','3581','0');
INSERT INTO [t19188] VALUES (3628,'كتاب الطلاق','10636','0','0');
INSERT INTO [t19188] VALUES (3629,'[باب. . .]','10636','3628','0');
INSERT INTO [t19188] VALUES (3630,'٣٤١٩ - مسألة: (ويباح عند الحاجة إليه، ويكره من غير حاجة. وعنه، أنه يحرم، ويستحب إذا كان بقاء النكاح ضررا)','10637','3629','0');
INSERT INTO [t19188] VALUES (3631,'٣٤٢٠ - مسألة: (ويصح من الزوج العاقل البالغ المختار، ومن الصبى العاقل. وعنه، لا يصح حتى يبلغ)','10641','3629','0');
INSERT INTO [t19188] VALUES (3632,'٣٤٢١ - مسألة: (ومن زال عقله بسبب يعذر فيه؛ كالمجنون، والنائم، والمغمى عليه، والمبرسم، لم يقع طلاقه)','10645','3629','0');
INSERT INTO [t19188] VALUES (3633,'٣٤٢٢ - مسألة: (وإن كان بسبب لا يعذر فيه، كالسكران،','10646','3629','0');
INSERT INTO [t19188] VALUES (3634,'٣٤٢٣ - مسألة: (ومن أكره على الطلاق بغير حق، لم يقع طلاقه)','10655','3629','0');
INSERT INTO [t19188] VALUES (3635,'٣٤٢٤ - مسألة: (وإن هدده بالقتل وأخذ المال ونحوه، قادر يغلب على ظنه وقوع ما هدده به، فهو إكراه. وعنه، لا يكون مكرها حتى ينال بشئ من العذاب؛ كالضرب، والخنق، وعصر الساق.','10657','3629','0');
INSERT INTO [t19188] VALUES (3636,'٣٤٢٥ - مسألة: (ويقع الطلاق فى النكاح المختلف فيه، كالنكاح بلا ولى عند أصحابنا. واختار أبو الخطاب أنه لا يقع حتى يعتقد صحته)','10664','3629','0');
INSERT INTO [t19188] VALUES (3637,'٣٤٢٦ - مسألة: (وإذا وكل فى الطلاق من يصح توكيله، صح طلاقه)','10666','3629','0');
INSERT INTO [t19188] VALUES (3638,'٣٤٢٧ - مسألة: (وله أن يطلق متى شاء، إلا أن يحد له حدا)','10667','3629','0');
INSERT INTO [t19188] VALUES (3639,'٣٤٢٨ - مسألة: (ولا يطلق أكثر من واحدة، إلا أن يجعل)','10668','3629','0');
INSERT INTO [t19188] VALUES (3640,'٣٤٢٩ - مسألة: (فإن وكل اثنين)','10668','3629','0');
INSERT INTO [t19188] VALUES (3641,'٣٤٣٠ - مسألة: (فإن وكلهما فى ثلاث، فطلق أحدهما أكثر من الآخر)','10669','3629','0');
INSERT INTO [t19188] VALUES (3642,'٣٤٣١ - مسألة: (وإن قال لامرأته: طلقى نفسك. فلها ذلك، كالوكيل)','10670','3629','0');
INSERT INTO [t19188] VALUES (3643,'٣٤٣٢ - مسألة: (وإن قال: اختارى من ثلاث ما شئت. لم يكن لها أن تختار أكثر من اثنتين)','10673','3629','0');
INSERT INTO [t19188] VALUES (3644,'باب سنة الطلاق وبدعته','10674','3628','0');
INSERT INTO [t19188] VALUES (3645,'٣٤٣٣ - مسألة: (وإن طلق المدخول بها فى حيضها، أو طهر أصابها فيه، فهو طلاق بدعة محرم، ويقع)','10677','3644','0');
INSERT INTO [t19188] VALUES (3646,'٣٤٣٤ - مسألة: (وتستحب رجعتها. وعنه أنها واجبة)','10680','3644','0');
INSERT INTO [t19188] VALUES (3647,'٣٤٣٥ - مسألة: (وإن طلقها ثلاثا فى طهر لم يصبها فيه، كره. وفى تحريمه روايتان)','10684','3644','0');
INSERT INTO [t19188] VALUES (3648,'٣٤٣٦ - مسألة: (وإن قال لمن لها سنة وبدعة: أنت طالق للسنة. فى طهر لم يصبها فيه، طلقت فى الحال، وإن كانت حائضا، طلقت إذا طهرت، وإن كان فى طهر أصابها فيه، طلقت إذا طهرت من الحيضة المستقبلة)','10699','3644','0');
INSERT INTO [t19188] VALUES (3649,'٣٤٣٧ - مسألة: (وإن قال لها: أنت طالق للبدعة. وهى حائض','10701','3644','0');
INSERT INTO [t19188] VALUES (3650,'٣٤٣٨ - مسألة: (وإن قال لها: أنت طالق ثلاثا للسنة. طلقت ثلاثا فى طهر لم يصبها فيه، فى إحدى الروايتين. وفى الأخرى، تطلق فى الحال واحدة، وتطلق الثانية والثالثة فى طهرين في نكاحين إن أمكن)','10703','3644','0');
INSERT INTO [t19188] VALUES (3651,'٣٤٣٩ - مسألة: (وإن قال لها: أنت طالق فى كل قرء طلقة. وهى من اللائى لم يحضن، لم تطلق حتى تحيض، فتطلق فى كل حيضة طلقة)','10709','3644','0');
INSERT INTO [t19188] VALUES (3652,'٣٤٤٠ - مسألة: (وإن قال: أنت طالق أحسن الطلاق -أو- أجمله. فهو كقوله: أنت طالق للسنة)','10712','3644','0');
INSERT INTO [t19188] VALUES (3653,'٣٤٤١ - مسألة: (وإن قال: أقبحه وأسمجه)','10713','3644','0');
INSERT INTO [t19188] VALUES (3654,'٣٤٤٢ - مسألة: وإن قال: أردت أن (أحسن أحوالك أو أقبحها','10713','3644','0');
INSERT INTO [t19188] VALUES (3655,'٣٤٤٣ - مسألة: (وإن قال: أنت طالق طلقة حسنة قبيحة)','10714','3644','0');
INSERT INTO [t19188] VALUES (3656,'باب صريح الطلاق وكنايته','10716','3628','0');
INSERT INTO [t19188] VALUES (3657,'٣٤٤٤ - مسألة: (وقال الخرقى: صريحه ثلاثة ألفاظ؛ الطلاق، والفراق، والسراح، وما تصرف منهن)','10717','3656','0');
INSERT INTO [t19188] VALUES (3658,'٣٤٤٥ - مسألة: (فمتى أتى بصريح الطلاق، وقع، نواه أو لم ينوه)','10721','3656','0');
INSERT INTO [t19188] VALUES (3659,'٣٤٤٦ - مسألة: (فإن نوى بقوله: أنت طالق. من وثاق. أو أراد','10722','3656','0');
INSERT INTO [t19188] VALUES (3660,'٣٤٤٧ - مسألة: (ولو قيل له: أطلقت امرأتك؟ فقال: نعم. وأراد الكذب، طلقت. ولو قيل له: ألك امرأة؟ قال: لا. وأراد الكذب، لم تطلق)','10726','3656','0');
INSERT INTO [t19188] VALUES (3661,'٣٤٤٨ - مسألة: (وإن لطم امرأته، أو أطعمها، أو سقاها، وقال: هذا طلاقك. طلقت، إلا أن ينوى أن هذا سبب طلاقك، أو نحو ذلك)','10730','3656','0');
INSERT INTO [t19188] VALUES (3662,'٣٤٤٩ - مسألة: (وإن قال: أنت طالق لا شئ -أو- ليس بشئ -أو- لا يلزمك. طلقت)','10733','3656','0');
INSERT INTO [t19188] VALUES (3663,'٣٤٥٠ - مسألة: (وإن قال: أنت طالق أو لا؟ أو: طالق واحدة','10733','3656','0');
INSERT INTO [t19188] VALUES (3664,'٣٤٥١ - مسألة: (وإن كتب طلاق امرأته ونوى الطلاق، وقع، وإن نوى تجويد خطه، أو غم أهله، لم يقع. وهل تقبل دعواه فى الحكم؟ على روايتين)','10735','3656','0');
INSERT INTO [t19188] VALUES (3665,'٣٤٥٢ - مسألة: (وإن لم ينو شيئا)','10738','3656','0');
INSERT INTO [t19188] VALUES (3666,'٣٤٥٣ - مسألة: (وإن كتبه بشئ لا يبين)','10739','3656','0');
INSERT INTO [t19188] VALUES (3667,'٣٤٥٤ - مسألة: (وصريح الطلاق فى لسان العجم بهشتم)','10741','3656','0');
INSERT INTO [t19188] VALUES (3668,'فصل','10743','3656','0');
INSERT INTO [t19188] VALUES (3669,'٣٤٥٥ - مسألة: (والخفية نحو: اخرجى، واذهبى، وذوقى، وتجرعى، وخليتك، وأنت مخلاة، وأنت واحدة، ولست لى بامرأة، واعتدى، واستبرى، وما أشبهه)','10748','3656','0');
INSERT INTO [t19188] VALUES (3670,'٣٤٥٦ - مسألة: (واختلف فى قوله: الحقى بأهلك، و: حبلك على غاربك، و: تزوجى من شئت، و: حللت للأزواج، و: لا سبيل لى عليك، و: لا سلطان لى [عليك)','10750','3656','0');
INSERT INTO [t19188] VALUES (3671,'٣٤٥٧ - مسألة: (ومن شرط وقوع الطلاق)','10755','3656','0');
INSERT INTO [t19188] VALUES (3672,'٣٤٥٨ - مسألة: (إلا أن يأتى بها فى حال الخصومة والغضب، فعلى روايتين)','10757','3656','0');
INSERT INTO [t19188] VALUES (3673,'٣٤٥٩ - مسألة: (وإن جاءت جوابا لسؤالها الطلاق، فقال أصحابنا: يقع بها الطلاق)','10760','3656','0');
INSERT INTO [t19188] VALUES (3674,'٣٤٦٠ - مسألة: (ومتى نوى بالكناية الطلاق، وقع بالظاهرة ثلاث وإن نوى واحدة)','10762','3656','0');
INSERT INTO [t19188] VALUES (3675,'٣٤٦١ - مسألة: (وأما ما لا يدل على الطلاق، نحو: كلى، و: اشربى، و: اقعدى، و: اقربى، و: بارك الله عليك، و: أنت مليحة، أو: قبيحة)','10765','3656','0');
INSERT INTO [t19188] VALUES (3676,'٣٤٦٢ - مسألة: (وكذلك قوله: أنا طالق)','10767','3656','0');
INSERT INTO [t19188] VALUES (3677,'٣٤٦٣ - مسألة: (وإن قال: أنا منك بائن. أو: حرام. فهل هو كناية أو لا؟ على وجهين)','10768','3656','0');
INSERT INTO [t19188] VALUES (3678,'٣٤٦٤ - مسألة: (وإن قال: أنت على كظهر أمى. ينوى به الطلاق، لم يقع، وكان ظهارا)','10770','3656','0');
INSERT INTO [t19188] VALUES (3679,'٣٤٦٥ - مسألة: (وإن قال: أنت على حرام. أو: ما أحل الله','10770','3656','0');
INSERT INTO [t19188] VALUES (3680,'٣٤٦٦ - مسألة: (وإن قال: ما أحل الله على حرام، أعنى به الطلاق. فقال أحمد: تطلق امرأته ثلاثا. وإن قال: أعنى به طلاقا. طلقت واحدة)','10776','3656','0');
INSERT INTO [t19188] VALUES (3681,'٣٤٦٧ - مسألة: (وإن قال: أنت على كالميتة والدم. وقع ما نواه من الطلاق والظهار واليمين. وإن لم ينو شيئا، فهل يكون ظهارا أو يمينا؟ على وجهين)','10779','3656','0');
INSERT INTO [t19188] VALUES (3682,'٣٤٦٨ - مسألة: (وإن قال: حلفت بالطلاق. وكذب، لزمه إقراره فى الحكم، ولا يلزمه فيما بينه وبين الله تعالى)','10781','3656','0');
INSERT INTO [t19188] VALUES (3683,'فصل','10783','3656','0');
INSERT INTO [t19188] VALUES (3684,'٣٤٦٩ - مسألة: (وإن قال: اختارى نفسك. لم يكن لها أن تطلق أكثر من واحدة، إلا أن يجعل إليها أكثر من ذلك، وليس لها أن تطلق إلا ما دامت فى المجلس، ولم يتشاغلا بما يقطعه)','10786','3656','0');
INSERT INTO [t19188] VALUES (3685,'٣٤٧٠ - مسألة: وليس لها أن تطلق إلا ما داما فى المجلس، ولم يتشاغلا بما يقطعه وذلك بأن لا يخرجا من الكلام [الذى كانا فيه] (١)','10790','3656','0');
INSERT INTO [t19188] VALUES (3686,'٣٤٧١ - مسألة: (فإن جعل لها الخيار اليوم كله، أو جعل أمرها','10791','3656','0');
INSERT INTO [t19188] VALUES (3687,'٣٤٧٢ - مسألة: (ولفظة الأمر والخيار كناية فى حق الزوج، تفتقر إلى نية)','10796','3656','0');
INSERT INTO [t19188] VALUES (3688,'٣٤٧٣ - مسألة: (فإن اختلفا فى نيتها)','10798','3656','0');
INSERT INTO [t19188] VALUES (3689,'٣٤٧٤ - مسألة: (وإن قال: طلقى نفسك. فقالت: اخترت نفسى. ونوت الطلاق، وقع. ويحتمل أن لا يقع)','10801','3656','0');
INSERT INTO [t19188] VALUES (3690,'٣٤٧٥ - مسألة: (وليس لها أن تطلق أكثر من واحدة، إلا أن يجعل إليها أكثر منها)','10802','3656','0');
INSERT INTO [t19188] VALUES (3691,'٣٤٧٦ - مسألة: (وإذا قال: وهبتك لأهلك. فإن قبلوها فواحدة، وإن ردوها فلا شئ. وعنه، إن قبلوها فثلاث، وإن ردوها فواحدة. وكذلك إذا قال: وهبتك لنفسك)','10804','3656','0');
INSERT INTO [t19188] VALUES (3692,'باب ما يختلف به عدد الطلاق','10811','3628','0');
INSERT INTO [t19188] VALUES (3693,'٣٤٧٧ - مسألة: (فإذا قال: أنت الطلاق. أو: الطلاق لى لازم. ونوى الثلاث، طلقت ثلاثا)','10815','3692','0');
INSERT INTO [t19188] VALUES (3694,'٣٤٧٨ - مسألة: (فإن لم ينو شيئا)','10817','3692','0');
INSERT INTO [t19188] VALUES (3695,'٣٤٧٩ - مسألة: (وإن قال: أنت طالق. ونوى ثلاثا، ففيه روايتان؛ إحداهما، تطلق ثلاثا)','10818','3692','0');
INSERT INTO [t19188] VALUES (3696,'٣٤٨٠ - مسألة: (وإن قال: أنت طالق واحدة. ونوى ثلاثا، لم يقع إلا واحدة)','10822','3692','0');
INSERT INTO [t19188] VALUES (3697,'٣٤٨١ - مسألة: (وإن قال: أنت طالق هكذا. وأشار بأصابعه الثلاث، طلقت ثلاثا)','10823','3692','0');
INSERT INTO [t19188] VALUES (3698,'٣٤٨٢ - مسألة: (وإن قال: أنت طالق كل الطلاق، أو أكثره، أو جميعه، أو منتهاه، أو: طالق كألف، أو بعدد الحصى، أو القطر، أو الرمل، أو الريح، أو التراب. طلقت ثلاثا وإن نوى واحدة)','10825','3692','0');
INSERT INTO [t19188] VALUES (3699,'٣٤٨٣ - مسألة: (وإن قال: أشد الطلاق، أو أغلظه، أو أطوله، أو أعرضه، أو ملء الدنيا)','10827','3692','0');
INSERT INTO [t19188] VALUES (3700,'٣٤٨٤ - مسألة: (وإن قال: أنت طالق من واحدة إلى ثلاث. وقع طلقتان)','10829','3692','0');
INSERT INTO [t19188] VALUES (3701,'٣٤٨٥ - مسألة: (وإذا قال: أنت طالق طلقة فى اثنتين. ونوى طلقة مع طلقتين، وقعت الثلاث، وإن نوى موجبه عند الحساب، وهو يعرفه، طلقت طلقتين، وإن لم يعرفه، فكذلك عند ابن حامد. وعند','10830','3692','0');
INSERT INTO [t19188] VALUES (3702,'فصل','10835','3692','0');
INSERT INTO [t19188] VALUES (3703,'٣٤٨٦ - مسألة: (وإن قال: نصفى طلقتين. وقعت طلقتان)','10837','3692','0');
INSERT INTO [t19188] VALUES (3704,'٣٤٨٧ - مسألة: (وإن قال: ثلاثة أنصاف طلقة. طلقت طلقتين)','10837','3692','0');
INSERT INTO [t19188] VALUES (3705,'٣٤٨٨ - مسألة: (وإن قال: ثلاثة أنصاف طلقتين. طلقت ثلاثا. ويحتمل أن تطلق طلقتين)','10838','3692','0');
INSERT INTO [t19188] VALUES (3706,'٣٤٨٩ - مسألة: (وإن قال: نصف طلقة، ثلث طلقة، سدس طلقة، أو نصف وثلث وسدس طلقة. طلقت طلقة)','10839','3692','0');
INSERT INTO [t19188] VALUES (3707,'٣٤٩٠ - مسألة: (وإن قال: نصف طلقة وثلث طلقة وسدس طلقة. طلقت ثلاثا)','10840','3692','0');
INSERT INTO [t19188] VALUES (3708,'٣٤٩١ - مسألة: (وإذا قال لأربع)','10841','3692','0');
INSERT INTO [t19188] VALUES (3709,'٣٤٩٢ - مسألة: (وإن قال: أوقعت بينكن خمسا. وقع بكل واحدة طلقتان)','10843','3692','0');
INSERT INTO [t19188] VALUES (3710,'فصل','10845','3692','0');
INSERT INTO [t19188] VALUES (3711,'٣٤٩٣ - مسألة: (وإن قال: شعرك، أو: ظفرك، أو: سنك طالق. لم تطلق)','10847','3692','0');
INSERT INTO [t19188] VALUES (3712,'٣٤٩٤ - مسألة: (وإن أضافه إلى الريق، والحمل، والدمع، والعرق، لم تطلق)','10848','3692','0');
INSERT INTO [t19188] VALUES (3713,'٣٤٩٥ - مسألة: (وإن قال: روحك طالق. طلقت)','10849','3692','0');
INSERT INTO [t19188] VALUES (3714,'فصل فيما تخالف به المدخول بها غيرها','10852','3692','0');
INSERT INTO [t19188] VALUES (3715,'٣٤٩٦ - مسألة: (إذا قال لمدخول بها: أنت طالق، أنت طالق. طلقت طلقتين، إلا أن ينوى بالثانية التأكيد أو إفهامها)','10852','3692','0');
INSERT INTO [t19188] VALUES (3716,'٣٤٩٧ - مسألة: (وإن قال: أنت طالق فطالق. أو: ثم طالق. أو: طالق طلقة بل طلقتين. أو: بل طلقة. أو: طالق طلقة بعدها طلقة. أو: قبل طلقة. طلقت طلقتين)','10855','3692','0');
INSERT INTO [t19188] VALUES (3717,'٣٤٩٨ - مسألة: (وإن قال: أنت طالق طلقة قبلها طلقة. فكذلك عند القاضى)','10858','3692','0');
INSERT INTO [t19188] VALUES (3718,'٣٤٩٩ - مسألة: (وإن قال: أنت طالق طلقة معها طلقة. أو: مع طلقة. أو: طالق وطالق. طلقت طلقتين)','10860','3692','0');
INSERT INTO [t19188] VALUES (3719,'٣٥٠٠ - مسألة: (والمعلق كالمنجز)','10865','3692','0');
INSERT INTO [t19188] VALUES (3720,'٣٥٠١ - مسألة: (وإن قال: إن دخلت الدار فأنت طالق طلقة معها طلقة. فدخلت، طلقت طلقتين)','10866','3692','0');
INSERT INTO [t19188] VALUES (3721,'٣٥٠٢ - مسألة: وإن قال لغير مدخول بها: أنت طالق ثم طالق ثم طالق، إن دخلت الدار. أو','10867','3692','0');
INSERT INTO [t19188] VALUES (3722,'٣٥٠٣ - مسألة: (وإن قال: إن دخلت فأنت طالق، إن دخلت فأنت طالق. فدخلت، طلقت اثنتين بكل حال)','10868','3692','0');
INSERT INTO [t19188] VALUES (3723,'باب الاستثناء فى الطلاق','10870','3628','0');
INSERT INTO [t19188] VALUES (3724,'٣٥٠٤ - مسألة: (وإن قال: أنت طالق ثلاثا إلا ثلاثا. أو: ثلاثا إلا اثنتين. أو: خمسا إلا ثلاثا. أو: ثلاثا إلا ربع طلقة. طلقت ثلاثا)','10874','3723','0');
INSERT INTO [t19188] VALUES (3725,'٣٥٠٥ - مسألة: (وإن قال: أنت طالق اثنتين إلا واحدة. فعلى وجهين)','10875','3723','0');
INSERT INTO [t19188] VALUES (3726,'٣٥٠٦ - مسألة: (وإن قال: أنت طالق ثلاثا إلا اثنتين إلا واحدة. فهل تطلق ثلاثا أو اثنتين؟ على وجهين)','10876','3723','0');
INSERT INTO [t19188] VALUES (3727,'٣٥٠٧ - مسألة: (وإن قال: أنت طالق ثلاثا إلا ثلاثا إلا واحدة)','10877','3723','0');
INSERT INTO [t19188] VALUES (3728,'٣٥٠٨ - مسألة: (وإن قال: أنت طالق وطالق وطالق إلا واحدة. أو: طلقتين وواحدة إلا واحدة. أو: طلقتين ونصفا إلا واحدة. طلقت ثلاثا. ويحتمل أن تقع طلقتان)','10878','3723','0');
INSERT INTO [t19188] VALUES (3729,'٣٥٠٩ - مسألة: (وإن قال: أنت طالق ثلاثا. واستثنى بقلبه','10882','3723','0');
INSERT INTO [t19188] VALUES (3730,'باب الطلاق فى الماضى والمستقبل','10890','3628','0');
INSERT INTO [t19188] VALUES (3731,'٣٥١٠ - مسألة: (إذا قال لزوجته: أنت طالق أمس. أو: قبل أن أنكحك. ينوى الإيقاع، وقع)','10890','3730','0');
INSERT INTO [t19188] VALUES (3732,'٣٥١١ - مسألة: (وإن لم ينو، لم يقع فى ظاهر كلامه)','10891','3730','0');
INSERT INTO [t19188] VALUES (3733,'٣٥١٢ - مسألة: (وحكى عن أبى بكر)','10892','3730','0');
INSERT INTO [t19188] VALUES (3734,'٣٥١٣ - مسألة: (فإن قال: أردت أن زوجا قبلى طلقها. أو','10892','3730','0');
INSERT INTO [t19188] VALUES (3735,'٣٥١٤ - مسألة: (فإن مات أو جن أو خرس قبل العلم بمراده،','10894','3730','0');
INSERT INTO [t19188] VALUES (3736,'٣٥١٥ - مسألة: (وإن قال: أنت طالق قبل قدوم زيد بشهر. فقدم قبل مضي شهر، لم تطلق)','10895','3730','0');
INSERT INTO [t19188] VALUES (3737,'٣٥١٦ - مسألة: (وإن قدم بعد شهر وجزء يقع الطلاق فيه، تبينا وقوعه فيه)','10896','3730','0');
INSERT INTO [t19188] VALUES (3738,'٣٥١٧ - مسألة: (وإن خالعها بعد اليمين بيوم، وكان الطلاق بائنا، ثم قدم بعد الشهر بيومين، صح الخلع وبطل الطلاق)','10897','3730','0');
INSERT INTO [t19188] VALUES (3739,'٣٥١٨ - مسألة: (وإن قال: أنت طالق قبل موتي. طلقت في الحال)','10898','3730','0');
INSERT INTO [t19188] VALUES (3740,'٣٥١٩ - مسألة: (وإن قال)','10899','3730','0');
INSERT INTO [t19188] VALUES (3741,'٣٥٢٠ - مسألة: (وإن تزوج أمة أبيه، ثم قال: إذا مات أبي أو اشتريتك فأنت طالق. فمات أبوه أو اشتراها، لم يقع الطلاق)','10900','3730','0');
INSERT INTO [t19188] VALUES (3742,'٣٥٢١ - مسألة: (وإن قال: أنت طالق إن شربت ماء الكوز. ولا ماء فيه. أو: إن صعدت السماء. أو)','10905','3730','0');
INSERT INTO [t19188] VALUES (3743,'٣٥٢٢ - مسألة: (وإن قال: أنت طالق اليوم إذا جاء غد. فعلى','10907','3730','0');
INSERT INTO [t19188] VALUES (3744,'فصل في الطلاق في زمن مستقبل','10909','3730','0');
INSERT INTO [t19188] VALUES (3745,'٣٥٢٣ - مسألة: (إذا قال: أنت طالق غدا. أو: يوم السبت. أو: في رجب. طلقت بأول ذلك)','10909','3730','0');
INSERT INTO [t19188] VALUES (3746,'٣٥٢٤ - مسألة: (ولو قال: أنت طالق اليوم. أو: في هذا الشهر)','10910','3730','0');
INSERT INTO [t19188] VALUES (3747,'٣٥٢٥ - مسألة: (وإن قال: أنت طالق اليوم وغدا وبعد','10913','3730','0');
INSERT INTO [t19188] VALUES (3748,'٣٥٢٦ - مسألة: (وإن قال: أنت طالق اليوم إن لم أطلقك اليوم. طلقت في آخر جزء منه)','10915','3730','0');
INSERT INTO [t19188] VALUES (3749,'٣٥٢٧ - مسألة: (وإن قال: أنت طالق يوم يقدم زيد. فماتت','10917','3730','0');
INSERT INTO [t19188] VALUES (3750,'٣٥٢٨ - مسألة: (وإن قال: أنت طالق في غد إذا قدم زيد. فماتت قبل قدومه، لم تطلق)','10919','3730','0');
INSERT INTO [t19188] VALUES (3751,'٣٥٢٩ - مسألة: (وإن قال: أنت طالق اليوم غدا. طلقت اليوم واحدة)','10920','3730','0');
INSERT INTO [t19188] VALUES (3752,'٣٥٣٠ - مسألة: (فإن أراد: طالق اليوم وطالق غدا. فتطلق','10920','3730','0');
INSERT INTO [t19188] VALUES (3753,'٣٥٣١ - مسألة: وإن أراد (نصف طلقة اليوم ونصفها غدا)','10921','3730','0');
INSERT INTO [t19188] VALUES (3754,'٣٥٣٢ - مسألة: (وإن قال: أنت طالق إلى شهر. طلقت عند انقضائه)','10922','3730','0');
INSERT INTO [t19188] VALUES (3755,'٣٥٣٣ - مسألة: (وإن قال: أنت طالق في آخر الشهر. أو: أول آخره. طلقت)','10924','3730','0');
INSERT INTO [t19188] VALUES (3756,'٣٥٣٤ - مسألة: (وإن قال: إذا مضت سنة فأنت طالق. طلقت إذا مضى اثنا عشر شهرا بالأهلة، ويكمل الشهر الذي حلف في أثنائه بالعدد)','10926','3730','0');
INSERT INTO [t19188] VALUES (3757,'٣٥٣٥ - مسألة: (وإن قال: إذا مضت السنة فأنت طالق. طلقت بانسلاخ ذي الحجة)','10927','3730','0');
INSERT INTO [t19188] VALUES (3758,'٣٥٣٦ - مسألة: (وإذا قال: أنت طالق في كل سنة طلقة)','10928','3730','0');
INSERT INTO [t19188] VALUES (3759,'٣٥٣٧ - مسألة: (وإن قال: أردت بالسنة اثني عشر شهرا.','10929','3730','0');
INSERT INTO [t19188] VALUES (3760,'٣٥٣٨ - مسألة: (وإذا قال: أنت طالق يوم يقدم زيد. فقدم ليلا، لم تطلق، إلا أن يريد باليوم الوقت، فتطلق)','10931','3730','0');
INSERT INTO [t19188] VALUES (3761,'٣٥٣٩ - مسألة: (وإن قدم به ميتا أو مكرها، لم تطلق)','10932','3730','0');
INSERT INTO [t19188] VALUES (3762,'باب تعليق الطلاق بالشروط','10938','3628','0');
INSERT INTO [t19188] VALUES (3763,'٣٥٤٠ - مسألة: (وإن قال لأجنبية: إن قمت فأنت طالق. فتزوجها، ثم قامت، لم تطلق رواية واحدة)','10941','3762','0');
INSERT INTO [t19188] VALUES (3764,'٣٥٤١ - مسألة: (وإن علق الزوج الطلاق بشرط، لم تطلق قبل وجوده)','10941','3762','0');
INSERT INTO [t19188] VALUES (3765,'٣٥٤٢ - مسألة: (وإن قال: عجلت ما علقته. لم يتعجل)','10941','3762','0');
INSERT INTO [t19188] VALUES (3766,'٣٥٤٣ - مسألة: (وإن قال: سبق لساني بالشرط ولم أرده. وقع','10942','3762','0');
INSERT INTO [t19188] VALUES (3767,'٣٥٤٤ - مسألة: (وإن قال: أنت طالق. ثم قال: أردت إن قمت. دين)','10943','3762','0');
INSERT INTO [t19188] VALUES (3768,'٣٥٤٥ - مسألة: (وليس فيها ما يقتضي التكرار إلا «كلما»)','10945','3762','0');
INSERT INTO [t19188] VALUES (3769,'٣٥٤٦ - مسألة: (وكلها على التراخي إذا تجردت عن «لم»،','10946','3762','0');
INSERT INTO [t19188] VALUES (3770,'٣٥٤٧ - مسألة','10947','3762','0');
INSERT INTO [t19188] VALUES (3771,'٣٥٤٨ - مسألة: (وفي «إذا» وجهان)','10948','3762','0');
INSERT INTO [t19188] VALUES (3772,'٣٥٤٩ - مسألة: (فإن تكرر القيام لم يتكرر الطلاق، إلا في','10950','3762','0');
INSERT INTO [t19188] VALUES (3773,'٣٥٥٠ - مسألة: (فإذا قال: كلما أكلت رمانة فأنت طالق، وكلما أكلت نصف رمانة فأنت طالق. فأكلت رمانة، طلقت ثلاثا)','10951','3762','0');
INSERT INTO [t19188] VALUES (3774,'٣٥٥١ - مسألة: (ولو علق طلاقها على صفات ثلاث فاجتمعن في عين واحدة، نحو أن يقول: إن رأيت رجلا فأنت طالق. وإن رأيت أسود فأنت طالق، وإن رأيت فقيها فأنت طالق. فرأت رجلا أسود فقيها، طلقت ثلاثا)','10952','3762','0');
INSERT INTO [t19188] VALUES (3775,'٣٥٥٢ - مسألة:. (وإن قال: إن لم أطلقك فأنت طالق. لم تطلق إلا في آخر جزء من حياة أحدهما، إلا أن يكون له نية)','10953','3762','0');
INSERT INTO [t19188] VALUES (3776,'٣٥٥٣ - مسألة: (وإن قال: إذا لم أطلقك فأنت طالق)','10958','3762','0');
INSERT INTO [t19188] VALUES (3777,'٣٥٥٤ - مسألة: (وإن قال: كلما لم أطلقك فأنت طالق. فمضى زمن يمكن فيه طلاقها ثلاثا، ولم يطلقها، طلقت ثلاثا)','10958','3762','0');
INSERT INTO [t19188] VALUES (3778,'٣٥٥٥ - مسألة: (وإن قال العامي: أن دخلت الدار فأنت طالق. بفتح الهمزة، فهو شرط)','10959','3762','0');
INSERT INTO [t19188] VALUES (3779,'٣٥٥٦ - مسألة: (وإن قال: إن قمت وأنت طالق. طلقت في الحال)','10961','3762','0');
INSERT INTO [t19188] VALUES (3780,'٣٥٥٧ - مسألة: (وإن قال: إن قمت فقعدت فأنت طالق)','10966','3762','0');
INSERT INTO [t19188] VALUES (3781,'٣٥٥٨ - مسألة: (وإن قال: إن قمت وقعدت فأنت طالق.','10968','3762','0');
INSERT INTO [t19188] VALUES (3782,'٣٥٥٩ - مسألة: (وإن قال: إن قمت أو قعدت فأنت طالق.','10970','3762','0');
INSERT INTO [t19188] VALUES (3783,'٣٥٦٠ - مسألة: (وإذا قال لطاهر: إذا حضت حيضة فأنت طالق. لم تطلق حتى تحيض ثم تطهر)','10972','3762','0');
INSERT INTO [t19188] VALUES (3784,'٣٥٦١ - مسألة: (وإذا قال: إذا حضت نصف حيضة فأنت طالق)','10973','3762','0');
INSERT INTO [t19188] VALUES (3785,'٣٥٦٢ - مسألة: (وإن قال: إذا طهرت فأنت طالق)','10974','3762','0');
INSERT INTO [t19188] VALUES (3786,'٣٥٦٣ - مسألة: (وإذا قالت)','10976','3762','0');
INSERT INTO [t19188] VALUES (3787,'٣٥٦٤ - مسألة: (ولو قال: قد حضت. فأنكرته، طلقت بإقراره)','10978','3762','0');
INSERT INTO [t19188] VALUES (3788,'٣٥٦٥ - مسألة: (وإن قال: إن حضت فأنت وضرتك طالقتان. فقالت: قد حضت. وكذبها، طلقت)','10978','3762','0');
INSERT INTO [t19188] VALUES (3789,'٣٥٦٦ - مسألة: (وإن قال)','10979','3762','0');
INSERT INTO [t19188] VALUES (3790,'٣٥٦٧ - مسألة: (وإن قال ذلك لأربع)','10980','3762','0');
INSERT INTO [t19188] VALUES (3791,'٣٥٦٨ - مسألة: (وإن قال: كلما حاضت إحداكن فضرائرها طوالق)','10981','3762','0');
INSERT INTO [t19188] VALUES (3792,'٣٥٦٩ - مسألة: (وإن قال: إن لم تكوني حاملا فأنت طالق.','10985','3762','0');
INSERT INTO [t19188] VALUES (3793,'٣٥٧٠ - مسألة: (ويحرم وطؤها قبل استبرائها في إحدى','10986','3762','0');
INSERT INTO [t19188] VALUES (3794,'٣٥٧١ - مسألة: (وإذا قال: إن كنت حاملا بذكر فأنت طالق','10989','3762','0');
INSERT INTO [t19188] VALUES (3795,'٣٥٧٢ - مسألة: (فإن أشكل كيفية وضعهما، وقعت واحدة بيقين، ولغا ما زاد)','10994','3762','0');
INSERT INTO [t19188] VALUES (3796,'٣٥٧٣ - مسألة: (ولا فرق بين أن تلده حيا أو ميتا)','10996','3762','0');
INSERT INTO [t19188] VALUES (3797,'٣٥٧٤ - مسألة: (إذا قال: إذا طلقتك فأنت طالق. ثم قال: إن قمت فأنت طالق. فقامت، طلقت)','11000','3762','0');
INSERT INTO [t19188] VALUES (3798,'٣٥٧٥ - مسألة: (ولو قال)','11000','3762','0');
INSERT INTO [t19188] VALUES (3799,'٣٥٧٦ - مسألة: (ولو قال: إن قمت فأنت طالق. ثم قال: إن وقع عليك طلاقي فأنت طالق. فقامت، طلقت)','11001','3762','0');
INSERT INTO [t19188] VALUES (3800,'٣٥٧٧ - مسألة: (وإن قال: كلما طلقتك فأنت طالق)','11001','3762','0');
INSERT INTO [t19188] VALUES (3801,'٣٥٧٨ - مسألة: (وإن قال: كلما وقع عليك طلاقي فأنت طالق. ثم وقع عليها طلاقه بمباشرة أو سبب)','11002','3762','0');
INSERT INTO [t19188] VALUES (3802,'٣٥٧٩ - مسألة: (وإن قال: كلما وقع عليك طلاقي. أو: إن وقع عليك طلاقي، فأنت طالق قبله ثلاثا. ثم قال: أنت طالق. فلا نص فيها. وقال أبو بكر والقاضي: تطلق ثلاثا)','11005','3762','0');
INSERT INTO [t19188] VALUES (3803,'٣٥٨٠ - مسألة: (وإن قال لنسائه الأربع: أيتكن وقع عليها','11014','3762','0');
INSERT INTO [t19188] VALUES (3804,'٣٥٨١ - مسألة: (وإن قال: كلما طلقت واحدة منكن فعبد من عبيدي حر، وكلما طلقت اثنتين فعبدان حران، وكلما طلقت ثلاثا فثلاثة أحرار، وكلما طلقت أربعا فأربعة أحرار)','11015','3762','0');
INSERT INTO [t19188] VALUES (3805,'٣٥٨٢ - مسألة: (إذا قال لامرأته: إذا أتاك طلاقي فأنت طالق. ثم كتب إليها: إذا أتاك كتابي فأنت طالق. فأتاها الكتاب، طلقت طلقتين)','11020','3762','0');
INSERT INTO [t19188] VALUES (3806,'٣٥٨٣ - مسألة: (فإذا قال: إن حلفت بطلاقك فأنت طالق. ثم قال: أنت طالق إن قمت، أو: دخلت الدار)','11022','3762','0');
INSERT INTO [t19188] VALUES (3807,'٣٥٨٤ - مسألة: (وإن قال: إن حلفت بطلاقك فأنت طالق. وأعاده مرة أخرى، طلقت واحدة)','11024','3762','0');
INSERT INTO [t19188] VALUES (3808,'٣٥٨٥ - مسألة: (وإن قال: إن كلمتك فأنت طالق. وأعاده','11024','3762','0');
INSERT INTO [t19188] VALUES (3809,'٣٥٨٦ - مسألة: (وإن قال لامرأتيه: إن حلفت بطلاقكما فأنتما طالقتان)','11025','3762','0');
INSERT INTO [t19188] VALUES (3810,'٣٥٨٧ - مسألة: فإن قال لإحداهما: إذا حلفت بطلاق ضرتك فأنت طالق. ثم قال ذلك للأخرى، طلقت الأولى؛ لأن التعليق حلف، وقد علق طلاق ضرتها، فتطلق الأولى؛ لوجود شرط طلاقها، وهو تعليق طلاق ضرتها، فإن أعاده للأولى، طلقت الأخرى لذلك، وكلما أعاده لامرأة [منهما على هذا الوجه]','11028','3762','0');
INSERT INTO [t19188] VALUES (3811,'٣٥٨٨ - مسألة: (وإن قال لمدخول بها: كلما حلفت بطلاق واحدة منكما فأنتما طالقتان. وأعاده ثانيا، طلقت كل واحدة طلقتين)','11029','3762','0');
INSERT INTO [t19188] VALUES (3812,'٣٥٨٩ - مسألة: (وإن قال: كلما حلفت بطلاق واحدة منكما فهي طالق. أو: فضرتها طالق. وأعاده، طلقت كل واحدة)','11029','3762','0');
INSERT INTO [t19188] VALUES (3813,'٣٥٩٠ - مسألة: (وإن قال: إن بدأتك بالكلام فأنت طالق. فقالت: إن بدأتك به فعبدي حر. انحلت يمينه)','11034','3762','0');
INSERT INTO [t19188] VALUES (3814,'٣٥٩١ - مسألة: (وإذا قال: إن كلمت فلانا فأنت طالق. فكلمته فلم يسمع لتشاغله أو غفلته، أو كاتبته أو راسلته، حنث)','11035','3762','0');
INSERT INTO [t19188] VALUES (3815,'٣٥٩٢ - مسألة: (وإن أشارت إليه، احتمل وجهين)','11036','3762','0');
INSERT INTO [t19188] VALUES (3816,'٣٥٩٣ - مسألة: (وإن كلمته سكران أو أصم، بحيث يعلم أنها تكلمه، أو مجنونا يسمع كلامها، حنث)','11037','3762','0');
INSERT INTO [t19188] VALUES (3817,'٣٥٩٤ - مسألة. (وإن كلمته ميتا، أو غائبا، أو مغمى عليه، أو نائما، لم يحنث. وقال أبو بكر: يحنث)','11038','3762','0');
INSERT INTO [t19188] VALUES (3818,'٣٥٩٥ - مسألة: (فإن قال لامرأتيه: إن كلمتما هذين)','11041','3762','0');
INSERT INTO [t19188] VALUES (3819,'٣٥٩٦ - مسألة: (فإن قال: إن أمرتك فخالفتني فأنت طالق','11043','3762','0');
INSERT INTO [t19188] VALUES (3820,'٣٥٩٧ - مسألة: (إذا قال: إن خرجت بغير إذني. أو: إلا بإذني. أو: حتى آذن لك، فأنت طالق. ثم أذن لها، فخرجت، ثم خرجت بغير إذنه، طلقت)','11047','3762','0');
INSERT INTO [t19188] VALUES (3821,'٣٥٩٨ - مسألة: (وإن أذن لها من حيث لا تعلم، فخرجت، طلقت)','11048','3762','0');
INSERT INTO [t19188] VALUES (3822,'٣٥٩٩ - مسألة: (وإن قال: إن خرجت إلى غير الحمام بغير إذني فأنت طالق)','11049','3762','0');
INSERT INTO [t19188] VALUES (3823,'٣٦٠٠ - مسألة: (وإن خرجت تريد الحمام ثم عدلت إلى غيره)','11050','3762','0');
INSERT INTO [t19188] VALUES (3824,'٣٦٠١ - مسألة: (وإن حلف لعامل أن لا يخرج إلا بإذنه، فعزل، فهل تنحل يمينه؟ على وجهين)','11051','3762','0');
INSERT INTO [t19188] VALUES (3825,'٣٦٠٢ - مسألة: ومتى وجدت المشيئة باللسان، وقع الطلاق (سواء كان على الفور أو التراخي)','11053','3762','0');
INSERT INTO [t19188] VALUES (3826,'٣٦٠٣ - مسألة: (وإن قال: أنت طالق إن شئت. فقالت: قد شئت إن شئت. فقال: قد شئت. لم تطلق)','11055','3762','0');
INSERT INTO [t19188] VALUES (3827,'٣٦٠٤ - مسألة: (وإن قال: أنت طالق إن شئت وشاء أبوك. لم تطلق حتى يشاءا)','11056','3762','0');
INSERT INTO [t19188] VALUES (3828,'٣٦٠٥ - مسألة: (وإن قال: أنت طالق إن شاء زيد. فمات أو جن أو خرس قبل المشيئة، لم تطلق)','11057','3762','0');
INSERT INTO [t19188] VALUES (3829,'٣٦٠٦ - مسألة: (وإن قال: أنت طالق إلا أن يشاء زيد. فمات أو جن أو خرس، طلقت)','11059','3762','0');
INSERT INTO [t19188] VALUES (3830,'٣٦٠٧ - مسألة: (وإن قال: أنت طالق واحدة إلا أن يشاء زيد ثلاثا. فشاء ثلاثا)','11060','3762','0');
INSERT INTO [t19188] VALUES (3831,'٣٦٠٨ - مسألة: (وإن قال: أنت طالق إن شاء الله. طلقت. وإن قال لأمته: أنت حرة إن شاء الله. عتقت. وحكي عنه، أنه يقع العتق دون الطلاق)','11061','3762','0');
INSERT INTO [t19188] VALUES (3832,'٣٦٠٩ - مسألة: (وإن قال: أنت طالق إلا أن يشاء الله. طلقت)','11065','3762','0');
INSERT INTO [t19188] VALUES (3833,'٣٦١٠ - مسألة: (وإن قال: إن لم يشأ الله)','11065','3762','0');
INSERT INTO [t19188] VALUES (3834,'٣٦١١ - مسألة: (وإن قال: إن دخلت الدار فأنت طالق إن شاء','11066','3762','0');
INSERT INTO [t19188] VALUES (3835,'٣٦١٢ - مسألة: (وإن قال: أنت طالق لرضا زيد. أو: مشيئته. طلقت في الحال)','11069','3762','0');
INSERT INTO [t19188] VALUES (3836,'٣٦١٣ - مسألة: (وإن قال: إن كنت تحبين أن يعذبك الله بالنار فأنت طالق. أو قال: إن كنت تحبينه بقلبك. فقالت: أنا أحبه. فقد توقف أحمد عنها)','11071','3762','0');
INSERT INTO [t19188] VALUES (3837,'٣٦١٤ - مسألة: (وإن قال: من بشرتنى بقدوم أخي فهي طالق. فأخبرته امرأتاه، طلقت الأولى منهما، إلا أن تكون الثانية هي الصادقة وحدها، فتطلق وحدها)','11075','3762','0');
INSERT INTO [t19188] VALUES (3838,'٣٦١٥ - مسألة: (وإن قال: من أخبرتني بقدومه فهي طالق. فكذلك عند القاضي)','11076','3762','0');
INSERT INTO [t19188] VALUES (3839,'٣٦١٦ - مسألة: (وإن حلف لا يفعل شيئا، ففعله ناسيا، حنث في الطلاق والعتاق، ولم يحنث في اليمين المكفرة، في ظاهر المذهب)','11080','3762','0');
INSERT INTO [t19188] VALUES (3840,'٣٦١٧ - مسألة: (وإن حلف لا يدخل على فلان بيتا، أو لا يكلمه، أو لا يسلم عليه، أو لا يفارقه حتى يقضيه حقه، فدخل بيتا هو فيه ولم يعلم، أو سلم على قوم هو فيهم ولم يعلم، أو قضاه حقه ففارقه، فخرج رديا، أو أحاله به ففارقه ظنا منه أنه قد بر','11082','3762','0');
INSERT INTO [t19188] VALUES (3841,'٣٦١٨ - مسألة: (وإن حلف لا يفعل شيئا، ففعل بعضه، لم يحنث. وعنه، يحنث، إلا أن ينوي جميعه)','11085','3762','0');
INSERT INTO [t19188] VALUES (3842,'٣٦١٩ - مسألة: وإن حلف ليفعلن شيئا، أو ليدخلن الدار، لم يبرأ إلا بفعل جميعه، والدخول إلى الدار بجملته','11087','3762','0');
INSERT INTO [t19188] VALUES (3843,'٣٦٢٠ - مسألة: (وإن حلف لا يدخل دارا، فأدخلها بعض','11087','3762','0');
INSERT INTO [t19188] VALUES (3844,'٣٦٢١ - مسألة: (وإن حلف لا يشرب ماء هذا النهر، فشرب منه، حنث)','11089','3762','0');
INSERT INTO [t19188] VALUES (3845,'٣٦٢٢ - مسألة: (وإن حلف لا يلبس ثوبا اشتراه زيد أو نسجه، أو لا يأكل طعاما طبخه، فلبس ثوبا نسجه هو وغيره، أو اشترياه، أو أكل طعاما طبخاه، فعلى روايتين)','11091','3762','0');
INSERT INTO [t19188] VALUES (3846,'باب التأويل في الحلف','11096','3628','0');
INSERT INTO [t19188] VALUES (3847,'٣٦٢٣ - مسألة: (فإذا أكل تمرا','11103','3846','0');
INSERT INTO [t19188] VALUES (3848,'٣٦٢٤ - مسألة: (وإن حلف ليقعدن على بارية في بيته، ولا يدخله بارية، فإنه يدخل قصبا فينسجه فيه)','11105','3846','0');
INSERT INTO [t19188] VALUES (3849,'٣٦٢٥ - مسألة: (وان حلف ليطبخن قدرا برطل ملح، ويأكل منه فلا يجد طعم الملح، فإنه يسلق به بيضا)','11105','3846','0');
INSERT INTO [t19188] VALUES (3850,'٣٦٢٦ - مسألة: (وإن حلف لا يأكل بيضا ولا تفاحا، وليأكلن','11105','3846','0');
INSERT INTO [t19188] VALUES (3851,'٣٦٢٧ - مسألة: (وإن كان على سلم، فحلف)','11106','3846','0');
INSERT INTO [t19188] VALUES (3852,'٣٦٢٨ - مسألة: (وإن حلف لا أقمت عليه، ولا نزلت منه، ولا صعدت فيه. فإنه ينتقل)','11107','3846','0');
INSERT INTO [t19188] VALUES (3853,'٣٦٢٩ - مسألة: (وإن حلف لا أقمت في هذا الماء، ولا خرجت منه. وكان الماء جاريا، لم يحنث)','11107','3846','0');
INSERT INTO [t19188] VALUES (3854,'٣٦٣٠ - مسألة: (وإن كان)','11108','3846','0');
INSERT INTO [t19188] VALUES (3855,'٣٦٣١ - مسألة: (وإن استحلفه ظالم: ما لفلان عندك وديعة؟ وكانت له عنده وديعة، فإنه يعني بـ «ما»: الذي، ويبر في يمينه)','11108','3846','0');
INSERT INTO [t19188] VALUES (3856,'٣٦٣٢ - مسألة: (وإن حلف ما فلان ههنا. وعنى موضعا معينا، بر في يمينه)','11109','3846','0');
INSERT INTO [t19188] VALUES (3857,'٣٦٣٣ - مسألة: ولو سرقت منه امرأته شيئا، فحلف بالطلاق: لتصدقني أسرقت مني شيئا أم لا؟ وخافت أن تصدقه، فإنها تقول: سرقت منك ما سرقت منك. [وتعنى بـ «ما»: الذي سرقت منك]','11109','3846','0');
INSERT INTO [t19188] VALUES (3858,'٣٦٣٤ - مسألة: (ولو حلف على امرأته: لا سرقت مني شيئا. فخانته في وديعته، لم يحنث)','11110','3846','0');
INSERT INTO [t19188] VALUES (3859,'باب الشك في الطلاق','11114','3628','0');
INSERT INTO [t19188] VALUES (3860,'٣٦٣٥ - مسألة: (وإن شك في عدد الطلاق، بنى على اليقين)','11116','3859','0');
INSERT INTO [t19188] VALUES (3861,'٣٦٣٦ - مسألة: (وإن قال لامرأتيه: إحداكما طالق. ينوي واحدة بعينها، طلقت وحدها، فإن لم ينو، أخرجت المطلقة بالقرعة)','11121','3859','0');
INSERT INTO [t19188] VALUES (3862,'٣٦٣٧ - مسألة: (وإن طلق واحدة وأنسيها، فكذلك عند أصحابنا)','11126','3859','0');
INSERT INTO [t19188] VALUES (3863,'٣٦٣٨ - مسألة: فعلى قول أصحابنا (إن تبين أن المطلقة غير التي وقعت عليها القرعة)','11130','3859','0');
INSERT INTO [t19188] VALUES (3864,'٣٦٣٩ - مسألة: (إلا أن تكون قد تزوجت، أو تكون بحكم','11130','3859','0');
INSERT INTO [t19188] VALUES (3865,'٣٦٤٠ - مسألة: (وإن طار طائر، فقال: إن كان هذا غرابا ففلانة طالق، وإن لم يكن غرابا ففلانة طالق. ولم يعلم حاله، فهي كالمنسية)','11140','3859','0');
INSERT INTO [t19188] VALUES (3866,'٣٦٤١ - مسألة: (وإن قال: إن كان غرابا ففلانة طالق، وإن كان حماما ففلانة طالق)','11141','3859','0');
INSERT INTO [t19188] VALUES (3867,'٣٦٤٢ - مسألة: (فإن قال)','11143','3859','0');
INSERT INTO [t19188] VALUES (3868,'٣٦٤٣ - مسألة: (إذا قال لامرأته وأجنبية: إحداكما طالق)','11147','3859','0');
INSERT INTO [t19188] VALUES (3869,'٣٦٤٤ - مسألة: (فإن نادى امرأته، فأجابته امرأة له أخرى، فقال: أنت طالق. يظنها المناداة، طلقتا في إحدى الروايتين)','11150','3859','0');
INSERT INTO [t19188] VALUES (3870,'٣٦٤٥ - مسألة: (وإن لقي أجنبية ظنها زوجته، فقال: فلانة، أنت طالق)','11152','3859','0');
INSERT INTO [t19188] VALUES (3871,'كتاب الرجعة','11155','0','0');
INSERT INTO [t19188] VALUES (3872,'٣٦٤٦ - مسألة: (إذا طلق الحر امرأته بعد دخوله بها أقل من ثلاث، أو العبد واحدة بغير عوض)','11156','3871','0');
INSERT INTO [t19188] VALUES (3873,'٣٦٤٧ - مسألة: (وألفاظ الرجعة: راجعت امرأتي. أو: رجعتها. أو: ارتجعتها. أو: رددتها. أو: أمسكتها)','11157','3871','0');
INSERT INTO [t19188] VALUES (3874,'٣٦٤٨ - مسألة: (فإن قال: نكحتها. أو: تزوجتها)','11158','3871','0');
INSERT INTO [t19188] VALUES (3875,'٣٦٤٩ - مسألة: (وهل من شرطها الإشهاد؟ على روايتين)','11160','3871','0');
INSERT INTO [t19188] VALUES (3876,'٣٦٥٠ - مسألة: (والرجعية زوجة يلحقها الطلاق والظهار والإيلاء)','11162','3871','0');
INSERT INTO [t19188] VALUES (3877,'٣٦٥١ - مسألة: (ويباح لزوجها وطؤها، والخلوة والسفر بها، ولها أن تتزين)','11163','3871','0');
INSERT INTO [t19188] VALUES (3878,'٣٦٥٢ - مسألة: (ولا تحصل بمباشرتها، والنظر إلى فرجها، والخلوة بها لشهوة. نص عليه)','11168','3871','0');
INSERT INTO [t19188] VALUES (3879,'٣٦٥٣ - مسألة: (ولا يصح تعليق الرجعة على شرط)','11170','3871','0');
INSERT INTO [t19188] VALUES (3880,'٣٦٥٤ - مسألة: (وإن طهرت من الحيضة الثالثة ولم تغتسل، فهل له رجعتها؟ على روايتين)','11172','3871','0');
INSERT INTO [t19188] VALUES (3881,'٣٦٥٥ - مسألة: (وإن انقضت عدتها ولم يرتجعها، بانت،','11176','3871','0');
INSERT INTO [t19188] VALUES (3882,'٣٦٥٦ - مسألة: (وتعود على ما بقي من طلاقها، سواء رجعت بعد نكاح زوج غيره أو قبله. وعنه)','11177','3871','0');
INSERT INTO [t19188] VALUES (3883,'٣٦٥٧ - مسألة: (وإن ارتجعها في عدتها، وأشهد على رجعتها من حيث لا تعلم، فاعتدت، ثم تزوجت من أصابها، ردت إليه، ولا يطؤها حتى تنقضي عدتها)','11179','3871','0');
INSERT INTO [t19188] VALUES (3884,'٣٦٥٨ - مسألة: (وإن لم يكن للمدعي بينة بالرجعة)','11181','3871','0');
INSERT INTO [t19188] VALUES (3885,'٣٦٥٩ - مسألة: (وإذا ادعت المرأة انقضاء عدتها، قبل قولها إذا كان ممكنا، إلا أن تدعيه بالحيض في شهر، فلا يقبل إلا ببينة)','11183','3871','0');
INSERT INTO [t19188] VALUES (3886,'٣٦٦٠ - مسألة: فإن ادعت انقضاء عدتها بالقروء في [أقل من]','11187','3871','0');
INSERT INTO [t19188] VALUES (3887,'٣٦٦١ - مسألة: (إذا قالت: انقضت عدتي. فقال: قد كنت راجعتك. فالقول قولها)','11191','3871','0');
INSERT INTO [t19188] VALUES (3888,'٣٦٦٢ - مسألة: (وإن سبق فقال: ارتجعتك. فقالت: قد انقضت عدتي قبل رجعتك)','11192','3871','0');
INSERT INTO [t19188] VALUES (3889,'٣٦٦٣ - مسألة: (وإن تداعيا معا، قدم قولها)','11193','3871','0');
INSERT INTO [t19188] VALUES (3890,'٣٦٦٤ - مسألة: (فإن كان مجبوبا)','11201','3871','0');
INSERT INTO [t19188] VALUES (3891,'٣٦٦٥ - مسألة: فإن كانت ذمية، فوطئها زوجها الذمي، أحلها لمطلقها المسلم','11202','3871','0');
INSERT INTO [t19188] VALUES (3892,'٣٦٦٦ - مسألة: (وإن وطئها في الدبر، أو وطئت بشبهة، أو بملك يمين، لم تحل)','11205','3871','0');
INSERT INTO [t19188] VALUES (3893,'٣٦٦٧ - مسألة: (وإن وطئها زوجها في حيض، أو نفاس، أو إحرام، أحلها. وقال أصحابنا: لا يحلها)','11206','3871','0');
INSERT INTO [t19188] VALUES (3894,'٣٦٦٨ - مسألة: (ولو كانت أمة فاشتراها مطلقها, لم يحل)','11207','3871','0');
INSERT INTO [t19188] VALUES (3895,'٣٦٦٩ - مسألة: (وإن طلق العبد زوجته اثنتين، لم تحل له حتى تنكح زوجا غيره، سواء عتقا أو بقيا على الرق)','11208','3871','0');
INSERT INTO [t19188] VALUES (3896,'٣٦٧٠ - مسألة: (وإذا غاب عن مطلقته، فأتته فذكرت أنها نكحت من أصابها وانقضت عدتها)','11211','3871','0');
INSERT INTO [t19188] VALUES (3897,'كتاب الإيلاء','11214','0','0');
INSERT INTO [t19188] VALUES (3898,'٣٦٧١ - مسألة: (وهو الحلف على ترك الوطء)','11214','3897','0');
INSERT INTO [t19188] VALUES (3899,'٣٦٧٢ - مسألة: (ويشترط له أربعة شروط؛ أحدها، الحلف على ترك الوطء في القبل)','11215','3897','0');
INSERT INTO [t19188] VALUES (3900,'٣٦٧٣ - مسألة: (فإن تركه مضرا بها من غير عذر، فهل تضرب له مدة الإيلاء، ويحكم عليه بحكمه؟ على روايتين)','11216','3897','0');
INSERT INTO [t19188] VALUES (3901,'٣٦٧٤ - مسألة: (وإن حلف على ترك الوطء في الدبر، أو دون الفرج، لم يكن موليا)','11218','3897','0');
INSERT INTO [t19188] VALUES (3902,'٣٦٧٥ - مسألة: (وإن حلف أن لا يجامعها إلا جماع سوء، يريد جماعا ضعيفا، لا يزيد على التقاء الختانين، لم يكن موليا)','11218','3897','0');
INSERT INTO [t19188] VALUES (3903,'٣٦٧٦ - مسألة: (وإذا حلف على ترك الوطء بلفظ لا يحتمل غيره، كلفظه الصريح، وقوله: لا أدخلت ذكري في فرجك. وللبكر خاصة: لا افتضضتك','11219','3897','0');
INSERT INTO [t19188] VALUES (3904,'٣٦٧٧ - مسألة: (فإن حلف بنذر، أو عتق، أو طلاق، لم يصر موليا في الظاهر عنه. وعنه، يكون موليا)','11224','3897','0');
INSERT INTO [t19188] VALUES (3905,'٣٦٧٨ - مسألة: (وإن قال: إن وطئتك فأنت زانية. أو: فلله علي صوم هذا الشهر. لم يكن موليا)','11228','3897','0');
INSERT INTO [t19188] VALUES (3906,'٣٦٧٩ - مسألة: (أو يعلقه على شرط يغلب على الظن أنه لا يوجد في أقل)','11233','3897','0');
INSERT INTO [t19188] VALUES (3907,'٣٦٨٠ - مسألة: وإن قال: والله لا وطئتك (حتى تحبلي)','11234','3897','0');
INSERT INTO [t19188] VALUES (3908,'٣٦٨١ - مسألة: (وإن قال: والله لا وطئتك مدة. أو: ليطولن تركي لجماعك. لم يكن موليا حتى ينوي)','11236','3897','0');
INSERT INTO [t19188] VALUES (3909,'٣٦٨٢ - مسألة: (وإن حلف على ترك الوطء حتى يقدم زيد، أو نحوه مما لا يغلب على الظن عدمه في أربعة أشهر. أو: لا وطئتك','11236','3897','0');
INSERT INTO [t19188] VALUES (3910,'٣٦٨٣ - مسألة: (وإن قال: إن وطئتك فو الله لا وطئتك. أو: إن دخلت الدار فوالله لا وطئتك. لم يكن موليا)','11239','3897','0');
INSERT INTO [t19188] VALUES (3911,'٣٦٨٤ - مسألة: (وإن قال: والله لا وطئتك في السنة إلا مرة. لم يصر موليا)','11240','3897','0');
INSERT INTO [t19188] VALUES (3912,'٣٦٨٥ - مسألة: (وإن قال)','11241','3897','0');
INSERT INTO [t19188] VALUES (3913,'٣٦٨٦ - مسألة: (فإن قال: والله لا وطئتك أربعة أشهر، فإذا مضت فو الله لا وطئتك أربعة أشهر)','11244','3897','0');
INSERT INTO [t19188] VALUES (3914,'٣٦٨٧ - مسألة: (وإن قال: والله لا وطئتك إن شئت. فشاءت، صار موليا)','11245','3897','0');
INSERT INTO [t19188] VALUES (3915,'٣٦٨٨ - مسألة: (وإن قال: إلا أن تشائي. أو: إلا باختيارك.','11246','3897','0');
INSERT INTO [t19188] VALUES (3916,'٣٦٨٩ - مسألة: (وإن قال لنسائه)','11248','3897','0');
INSERT INTO [t19188] VALUES (3917,'٣٦٩٠ - مسألة: (وإن قال: والله لا وطئت كل واحدة منكن. كان موليا من جميعهن)','11250','3897','0');
INSERT INTO [t19188] VALUES (3918,'٣٦٩١ - مسألة: (وإن قال: والله لا أطؤكن. فهي كالتي قبلها في أحد الوجهين)','11251','3897','0');
INSERT INTO [t19188] VALUES (3919,'٣٦٩٢ - مسألة: (وإن آلى من واحدة، وقال للأخرى: شركتك معها. لم يصر موليا من الثانية)','11255','3897','0');
INSERT INTO [t19188] VALUES (3920,'٣٦٩٣ - مسألة: ويصح إيلاء الذمي، ويلزمه ما يلزم المسلم إذا تقاضوا إلينا','11260','3897','0');
INSERT INTO [t19188] VALUES (3921,'٣٦٩٤ - مسألة: (ولا يصح إيلاء الصبي والمجنون)','11262','3897','0');
INSERT INTO [t19188] VALUES (3922,'٣٦٩٥ - مسألة: (وفي إيلاء السكران وجهان)','11263','3897','0');
INSERT INTO [t19188] VALUES (3923,'٣٦٩٦ - مسألة: (ومدة الإيلاء في الأحرار والرقيق سواء. وعنه، أنها في العبد على النصف)','11264','3897','0');
INSERT INTO [t19188] VALUES (3924,'٣٦٩٧ - مسألة: (ولا حق لسيد الأمة في طلب الفيئة والعفو عنها، وإنما ذلك إليها)','11266','3897','0');
INSERT INTO [t19188] VALUES (3925,'٣٦٩٨ - مسألة: (فإن كان بالرجل عذر يمنع الوطء، احتسب عليه بمدته، وإن كان ذلك بها، لم يحتسب عليه، وإن طرأ بها، استؤنفت المدة عند زواله)','11269','3897','0');
INSERT INTO [t19188] VALUES (3926,'٣٦٩٩ - مسألة: (إلا الحيض فإنه يحتسب عليه بمدته. وفي النفاس وجهان)','11270','3897','0');
INSERT INTO [t19188] VALUES (3927,'٣٧٠٠ - مسألة: (وإن طلقها في أثناء المدة، انقطعت)','11272','3897','0');
INSERT INTO [t19188] VALUES (3928,'٣٧٠١ - مسألة: (وإن انقضت المدة وبها عذر)','11274','3897','0');
INSERT INTO [t19188] VALUES (3929,'٣٧٠٢ - مسألة: (وإن كان العذر به، وهو مما يعجز به عن الوطء)','11275','3897','0');
INSERT INTO [t19188] VALUES (3930,'٣٧٠٣ - مسألة: ومتى قدر على الفيئة، وهي الجماع، طولب به؛ لأنه تأخر للعذر، فإذا زال العذر طولب به، كالدين الحال، فإن لم يفعل أمر بالطلاق. وهذا قول كل من يقول: يوقف المولي. لأن الله تعالى قال: {فإمساك بمعروف أو تسريح بإحسان}','11277','3897','0');
INSERT INTO [t19188] VALUES (3931,'٣٧٠٤ - مسألة: (وإن كان مظاهرا، فقال: أمهلوني حتى أطلب رقبة أعتقها عن ظهاري. أمهل ثلاثة أيام)','11279','3897','0');
INSERT INTO [t19188] VALUES (3932,'٣٧٠٥ - مسألة: (وإن قال: أمهلوني حتى أقضي صلاتي. أو: أتغدى)','11281','3897','0');
INSERT INTO [t19188] VALUES (3933,'٣٧٠٦ - مسألة: (فإذا لم يبق له عذر، وطلبت الفيئة -وهي الجماع)','11282','3897','0');
INSERT INTO [t19188] VALUES (3934,'٣٧٠٧ - مسألة: (فإذا جامع، انحلت يمينه، وعليه كفارتها)','11283','3897','0');
INSERT INTO [t19188] VALUES (3935,'٣٧٠٨ - مسألة: (وأدنى ما يكفي)','11285','3897','0');
INSERT INTO [t19188] VALUES (3936,'٣٧٠٩ - مسألة: (وإن وطئها في الفرج وطأ محرما، مثل أن يطأ في الحيض، أو النفاس، أو الإحرام، أو صيام فرض من أحدهما)','11286','3897','0');
INSERT INTO [t19188] VALUES (3937,'٣٧١٠ - مسألة: (وإن لم يفئ وأعفته المرأة، سقط حقها. ويحتمل أن لا يسقط، ولها المطالبة بعد)','11291','3897','0');
INSERT INTO [t19188] VALUES (3938,'٣٧١١ - مسألة: (وإن لم تعفه، أمر بالطلاق)','11292','3897','0');
INSERT INTO [t19188] VALUES (3939,'٣٧١٢ - مسألة: (فإن طلق واحدة، فله رجعتها. وعنه، أنها','11292','3897','0');
INSERT INTO [t19188] VALUES (3940,'٣٧١٣ - مسألة: (فإن لم يطلق، حبس وضيق عليه حتى يطلق، في إحدى الروايتين. والأخرى، يطلق الحاكم عليه)','11294','3897','0');
INSERT INTO [t19188] VALUES (3941,'٣٧١٤ - مسألة: (فإن طلق واحدة، فهو كطلاق المولي)','11296','3897','0');
INSERT INTO [t19188] VALUES (3942,'٣٧١٥ - مسألةـ: (وإن ادعى أن المدة ما انقضت)','11297','3897','0');
INSERT INTO [t19188] VALUES (3943,'٣٧١٦ - مسألة: (فإن ادعى أنه وطئها)','11298','3897','0');
INSERT INTO [t19188] VALUES (3944,'كتاب الظهار','11301','0','0');
INSERT INTO [t19188] VALUES (3945,'٣٧١٧ - مسألة: (والظهار أن يشبه امرأته أو عضوا منها، بظهر من تحرم عليه على التأبيد، أو بها أو بعضو منها، فيقول: أنت علي كظهر أمي. أو: كيد أختي. أو: كوجه حماتي. أو)','11304','3944','0');
INSERT INTO [t19188] VALUES (3946,'٣٧١٨ - مسألة: (وإن قال: أنت علي كأمي. كان مظاهرا. فإن قال: أردت كأمي في الكرامة، أو نحوه. دين. وهل يقبل في الحكم؟ يخرج على روايتين)','11309','3944','0');
INSERT INTO [t19188] VALUES (3947,'٣٧١٩ - مسألة: (وإن قال: أنت كأمي. أو: مثل أمي)','11312','3944','0');
INSERT INTO [t19188] VALUES (3948,'٣٧٢٠ - مسألة: (وإن قال: أنت علي كظهر أبي)','11313','3944','0');
INSERT INTO [t19188] VALUES (3949,'٣٧٢١ - مسألة: (وإن قال: أنت علي كظهر أجنبية. أو: أخت زوجتي. أو: عمتها. أو: خالتها. فعلى روايتين)','11314','3944','0');
INSERT INTO [t19188] VALUES (3950,'٣٧٢٢ - مسألة: (وإن قال: أنت علي كظهر البهيمة. لم يكن مظاهرا لأنه ليس بمحل للاستمتاع. وفيه وجه آخر، أنه يكون مظاهرا، كما لو شبهها بظهر أبيه.','11316','3944','0');
INSERT INTO [t19188] VALUES (3951,'٣٧٢٣ - مسألة: (وإن قال: أنت علي حرام. فهو ظهار، إلا أن ينوي طلاقا أو يمينا، فهل يكون ظهارا أو ما نواه؟ على روايتين)','11316','3944','0');
INSERT INTO [t19188] VALUES (3952,'٣٧٢٤ - مسألة: (ويصح من كل زوجة)','11325','3944','0');
INSERT INTO [t19188] VALUES (3953,'٣٧٢٥ - مسألة: (فإن ظاهر من أمته، أو أم ولده، لم يصح، وعليه كفارة يمين. ويحتمل أن تلزمه كفارة ظهار)','11326','3944','0');
INSERT INTO [t19188] VALUES (3954,'٣٧٢٦ - مسألة: (وإن قالت المرأة لزوجها: أنت علي كظهر أبي. لم تكن مظاهرة)','11328','3944','0');
INSERT INTO [t19188] VALUES (3955,'٣٧٢٧ - مسألة: (وعليها تمكين)','11331','3944','0');
INSERT INTO [t19188] VALUES (3956,'٣٧٢٨ - مسألة: (وإن قال لأجنبية: أنت علي كظهر أمي. لم يطأها إن تزوجها حتى يكفر)','11333','3944','0');
INSERT INTO [t19188] VALUES (3957,'٣٧٢٩ - مسألة: (وإن قال)','11336','3944','0');
INSERT INTO [t19188] VALUES (3958,'٣٧٣٠ - مسألة: (ويصح الظهار معجلا ومعلقا بشرط، ومطلقا ومؤقتا، نحو)','11337','3944','0');
INSERT INTO [t19188] VALUES (3959,'٣٧٣١ - مسألة: (وهل يحرم الاستمتاع منها بما دون الفرج؟ على روايتين)','11343','3944','0');
INSERT INTO [t19188] VALUES (3960,'٣٧٣٢ - مسألة: (وتجب الكفارة بالعود، وهو الوطء. نص عليه أحمد، وأنكر قول مالك أنه العزم على الوطء. [وقال القاضي، وأبو الخطاب: هو العزم)','11344','3944','0');
INSERT INTO [t19188] VALUES (3961,'٣٧٣٣ - مسألة: (فإن مات أحدهما، أو طلقها قبل الوطء، فلا كفارة عليه، فإن عاد فتزوجها، لم يطأها حتى يكفر)','11347','3944','0');
INSERT INTO [t19188] VALUES (3962,'٣٧٣٤ - مسألة: (وإن وطئ قبل التكفير، أثم، واستقرت عليه الكفارة)','11349','3944','0');
INSERT INTO [t19188] VALUES (3963,'٣٧٣٥ - مسألة: (وتجزئه كفارة واحدة)','11350','3944','0');
INSERT INTO [t19188] VALUES (3964,'٣٧٣٦ - مسألة: (وإن ظاهر من امرأته الأمة ثم اشتراها، لم تحل له حتى يكفر. وقال [أبو بكر]','11351','3944','0');
INSERT INTO [t19188] VALUES (3965,'٣٧٣٧ - مسألة: (وإن كرر الظهار قبل التكفير فكفارة واحدة)','11353','3944','0');
INSERT INTO [t19188] VALUES (3966,'٣٧٣٨ - مسألة: (وإن ظاهر من نسائه بكلمة واحدة، فكفارة واحدة، وإن كان بكلمات، فلكل واحدة كفارة)','11355','3944','0');
INSERT INTO [t19188] VALUES (3967,'فصل في كفارة الظهار وما في معناها','11358','3944','0');
INSERT INTO [t19188] VALUES (3968,'٣٧٣٩ - مسألة: (كفارة الظهار على الترتيب، فيجب عليه تحرير رقبة، فإن لم يجد فصيام شهرين متتابعين، فإن لم يستطع فإطعام ستين مسكينا)','11358','3944','0');
INSERT INTO [t19188] VALUES (3969,'٣٧٤٠ - مسألة: (وكفارة القتل مثلهما)','11359','3944','0');
INSERT INTO [t19188] VALUES (3970,'٣٧٤١ - مسألة: (والاعتبار في الكفارة بحال الوجوب في إحدى الروايتين)','11360','3944','0');
INSERT INTO [t19188] VALUES (3971,'٣٧٤٢ - مسألة: (فإن شرع في الصوم)','11365','3944','0');
INSERT INTO [t19188] VALUES (3972,'٣٧٤٣ - مسألة: فإن كانت له رقبة يحتاج إلى خدمتها؛ لكبر، أو مرض، أو زمن','11368','3944','0');
INSERT INTO [t19188] VALUES (3973,'٣٧٤٤ - مسألة: [وكذلك إن]','11369','3944','0');
INSERT INTO [t19188] VALUES (3974,'٣٧٤٥ - مسألة: وإن وجد رقبة بثمن مثلها، لزمه شراؤها','11370','3944','0');
INSERT INTO [t19188] VALUES (3975,'٣٧٤٦ - مسألة: (وإن وهبت له رقبة، لم يلزمه قبولها)','11371','3944','0');
INSERT INTO [t19188] VALUES (3976,'٣٧٤٧ - مسألة: (وإن كان ماله غائبا وأمكنه شراؤها بنسيئة)','11372','3944','0');
INSERT INTO [t19188] VALUES (3977,'٣٧٤٨ - مسألة: (ولا تجزئ في كفارة القتل إلا رقبة مؤمنة)','11374','3944','0');
INSERT INTO [t19188] VALUES (3978,'٣٧٤٩ - مسألة: (ولا يجزئ إلا رقبة سليمة من العيوب المضرة بالعمل ضررا بينا)','11376','3944','0');
INSERT INTO [t19188] VALUES (3979,'٣٧٥٠ - مسألة: ولا يجزيء مقطوع اليد، أو الرجل','11377','3944','0');
INSERT INTO [t19188] VALUES (3980,'٣٧٥١ - مسألة: (ولا يجزئ المريض المأيوس من برئه)','11379','3944','0');
INSERT INTO [t19188] VALUES (3981,'٣٧٥٢ - مسألة: (ولا)','11380','3944','0');
INSERT INTO [t19188] VALUES (3982,'٣٧٥٣ - مسألة: (ولا)','11381','3944','0');
INSERT INTO [t19188] VALUES (3983,'٣٧٥٤ - مسألة: ولا يجزئ الأصم','11381','3944','0');
INSERT INTO [t19188] VALUES (3984,'٣٧٥٥ - مسألة: (ولا)','11383','3944','0');
INSERT INTO [t19188] VALUES (3985,'٣٧٥٦ - مسألة: (ولا)','11383','3944','0');
INSERT INTO [t19188] VALUES (3986,'٣٧٥٧ - مسألة: (ولا)','11384','3944','0');
INSERT INTO [t19188] VALUES (3987,'٣٧٥٨ - مسألة: (ولا أم ولد، في الصحيح عنه)','11385','3944','0');
INSERT INTO [t19188] VALUES (3988,'٣٧٥٩ - مسألة: (ولا)','11386','3944','0');
INSERT INTO [t19188] VALUES (3989,'٣٧٦٠ - مسألة: (ويجزيء الأعرج يسيرا)','11389','3944','0');
INSERT INTO [t19188] VALUES (3990,'٣٧٦١ - مسألة: (و)','11392','3944','0');
INSERT INTO [t19188] VALUES (3991,'٣٧٦٢ - مسألة: (و)','11393','3944','0');
INSERT INTO [t19188] VALUES (3992,'٣٧٦٣ - مسألة: (و)','11394','3944','0');
INSERT INTO [t19188] VALUES (3993,'٣٧٦٤ - مسألة: ولو ملك نصف عبد، فأعتقه عن كفارته (ثم اشترى باقيه فأعتقه، أجزأه)','11398','3944','0');
INSERT INTO [t19188] VALUES (3994,'٣٧٦٥ - مسألة: (فإن أعتقه)','11398','3944','0');
INSERT INTO [t19188] VALUES (3995,'٣٧٦٦ - مسألة: ولو أعتق نصفي عبدين، أو نصفي أمتين، أو نصف عبد ونصف أمة، أجزأ عنه','11400','3944','0');
INSERT INTO [t19188] VALUES (3996,'٣٧٦٧ - مسألة: (فإن تخلل صومها صوم شهر رمضان، أو فطر واجب كفطر العيد، أو الفطر لحيض أو نفاس، لم ينقطع التتابع)','11404','3944','0');
INSERT INTO [t19188] VALUES (3997,'٣٧٦٨ - مسألة: فإن أفطر (لمرض مخوف، أو جنون)','11406','3944','0');
INSERT INTO [t19188] VALUES (3998,'٣٧٦٩ - مسألة: وكذلك (فطر الحامل، والمرضع؛ لخوفهما على أنفسهما)','11407','3944','0');
INSERT INTO [t19188] VALUES (3999,'٣٧٧٠ - مسألة: (فإن خافتا على ولديهما)','11408','3944','0');
INSERT INTO [t19188] VALUES (4000,'٣٧٧١ - مسألة: (وإن أفطر لغير عذر، أو صام تطوعا، أو قضاء، أو عن نذر أو كفارة أخرى، لزمه الاستئناف)','11409','3944','0');
INSERT INTO [t19188] VALUES (4001,'٣٧٧٢ - مسألة: (وإن أفطر لعذر يبيح الفطر، كالسفر، والمرض غير المخوف، فعلى وجهين)','11410','3944','0');
INSERT INTO [t19188] VALUES (4002,'٣٧٧٣ - مسألة: (وإن أصاب المظاهر منها ليلا أو نهارا، انقطع التتابع)','11414','3944','0');
INSERT INTO [t19188] VALUES (4003,'٣٧٧٤ - مسألة: (وإن وطئ غيرها ليلا، لم ينقطع)','11416','3944','0');
INSERT INTO [t19188] VALUES (4004,'٣٧٧٥ - مسألة: ولا يجوز دفعها إلى كافر. وقد ذكرناه (ولا إلى من تلزمه مؤنته)','11421','3944','0');
INSERT INTO [t19188] VALUES (4005,'٣٧٧٦ - مسألة: ويجوز دفع الكفارة إلى من ظاهره الفقر','11422','3944','0');
INSERT INTO [t19188] VALUES (4006,'٣٧٧٧ - مسألة: (وإن رددها على مسكين واحد ستين يوما، لم يجزئه، إلا أن لا يجد غيره، فيجزئه في ظاهر المذهب. وعنه، لا يجزئه. وعنه، يجزئ وإن وجد غيره)','11422','3944','0');
INSERT INTO [t19188] VALUES (4007,'٣٧٧٨ - مسألة: (وإن دفع إلى مسكين في يوم واحد من كفارتين، أجزأه)','11424','3944','0');
INSERT INTO [t19188] VALUES (4008,'٣٧٧٩ - مسألة: (والمخرج في الكفارة ما يجزئ في الفطرة)','11425','3944','0');
INSERT INTO [t19188] VALUES (4009,'٣٧٨٠ - مسألة: (وفي الخبز روايتان)','11425','3944','0');
INSERT INTO [t19188] VALUES (4010,'٣٧٨١ - مسألة: (فإن كان قوت بلده غير ذلك)','11427','3944','0');
INSERT INTO [t19188] VALUES (4011,'٣٧٨٢ - مسألة: (ولا يجزئ من البر أقل من مد، ولا من غيره أقل من مدين)','11429','3944','0');
INSERT INTO [t19188] VALUES (4012,'٣٧٨٣ - مسألة: (ولا)','11433','3944','0');
INSERT INTO [t19188] VALUES (4013,'٣٧٨٤ - مسألة: (فإن أخرج القيمة، أو غدى المساكين أو عشاهم، لم يجزئه)','11434','3944','0');
INSERT INTO [t19188] VALUES (4014,'٣٧٨٥ - مسألة: (وإن غدى المساكين أو عشاهم، لم يجزئه. وعنه، يجزئه)','11435','3944','0');
INSERT INTO [t19188] VALUES (4015,'٣٧٨٦ - مسألة: (فإن كانت عليه كفارة واحدة، فنوى عن كفارتي، أجزأه)','11438','3944','0');
INSERT INTO [t19188] VALUES (4016,'٣٧٨٧ - مسألة: (وإن كانت من أجناس)','11440','3944','0');
INSERT INTO [t19188] VALUES (4017,'كتاب اللعان','11445','0','0');
INSERT INTO [t19188] VALUES (4018,'٣٧٨٨ - مسألة: (إذا قذف الرجل زوجته بالزنى، فله إسقاط الحد)','11448','4017','0');
INSERT INTO [t19188] VALUES (4019,'٣٧٨٩ - مسألة: (وصفة اللعان أن يبدأ الزوج فيقول: أشهد بالله إني لمن الصادقين فيما رميت به امرأتي هذه من الزنى. ويشير إليها)','11450','4017','0');
INSERT INTO [t19188] VALUES (4020,'٣٧٩٠ - مسألة: (فإن نقص أحدهما من الألفاظ الخمسة شيئا، أو بدأت باللعان قبله، أو تلاعنا بغير حضرة الحاكم أو نائبه، لم يعتد به)','11452','4017','0');
INSERT INTO [t19188] VALUES (4021,'٣٧٩١ - مسألة: (وإن أبدل لفظة: أشهد، بـ: أقسم، أو أحلف، أو لفظة اللعنة بالإبعاد، أو الغضب بالسخط. فعلى وجهين)','11453','4017','0');
INSERT INTO [t19188] VALUES (4022,'٣٧٩٢ - مسألة: (ومن قدر على اللعان بالعربية، لم يصح منه','11455','4017','0');
INSERT INTO [t19188] VALUES (4023,'٣٧٩٣ - مسألة: (وإذا فهمت إشارة الأخرس أو كتابته، صح لعانه بها، وإلا فلا)','11457','4017','0');
INSERT INTO [t19188] VALUES (4024,'٣٧٩٤ - مسألة: (وهل يصح لعان من اعتقل لسانه وأيس من نطقه','11458','4017','0');
INSERT INTO [t19188] VALUES (4025,'٣٧٩٥ - مسألة: (وإذا بلغ كل واحد منهما الخامسة، أمر الحاكم','11463','4017','0');
INSERT INTO [t19188] VALUES (4026,'٣٧٩٦ - مسألة: (وأن يكون ذلك بحضرة الحاكم)','11464','4017','0');
INSERT INTO [t19188] VALUES (4027,'٣٧٩٧ - مسألة: (وإن كانت المرأة خفرة','11466','4017','0');
INSERT INTO [t19188] VALUES (4028,'٣٧٩٨ - مسألة: (وإذا قذف رجل نساءه، فعليه أن يفرد كل واحدة بلعان. وعنه؛ يجزئه لعان واحد)','11466','4017','0');
INSERT INTO [t19188] VALUES (4029,'٣٧٩٩ - مسألة: (وإن قذف أجنبية)','11472','4017','0');
INSERT INTO [t19188] VALUES (4030,'٣٨٠٠ - مسألة: (وإن أبان زوجته، ثم قذفها بزنى)','11474','4017','0');
INSERT INTO [t19188] VALUES (4031,'٣٨٠١ - مسألة: (وإن قذفها في نكاح فاسد)','11476','4017','0');
INSERT INTO [t19188] VALUES (4032,'٣٨٠٢ - مسألة: (وإن أبان امرأته بعد قذفها، فله أن يلاعن، سواء كان بينهما ولد أو لم يكن)','11477','4017','0');
INSERT INTO [t19188] VALUES (4033,'٣٨٠٣ - مسألة: (وإن قذف زوجته الصغيرة أو المجنونة، عزر، ولا لعان بينهما)','11479','4017','0');
INSERT INTO [t19188] VALUES (4034,'٣٨٠٤ - مسألة: (فإن قال: وطئت بشبهة، أو مكرهة. فلا','11484','4017','0');
INSERT INTO [t19188] VALUES (4035,'٣٨٠٥ - مسألة: (وإن قال: لم تزن ولكن ليس هذا الولد مني. فهو ولده في الحكم)','11486','4017','0');
INSERT INTO [t19188] VALUES (4036,'٣٨٠٦ - مسألة: (وإن قال ذلك بعد أن أبانها، فشهدت امرأة مرضية أنه ولد على فراشه، لحقه نسبه)','11489','4017','0');
INSERT INTO [t19188] VALUES (4037,'٣٨٠٧ - مسألة: (وإن ولدت توأمين، فأقر بأحدهما ونفى الآخر، لحقه نسبهما، ويلاعن لنفي الحد)','11490','4017','0');
INSERT INTO [t19188] VALUES (4038,'٣٨٠٨ - مسألة: (وإن مات أحدهما قبل اللعان، ورثه صاحبه، ولحقه نسب الولد، ولا لعان)','11495','4017','0');
INSERT INTO [t19188] VALUES (4039,'٣٨٠٩ - مسألة: (وإن مات الولد، فله لعانها ونفيه)','11497','4017','0');
INSERT INTO [t19188] VALUES (4040,'٣٨١٠ - مسألة: (وإن لاعن ونكلت الزوجة عن اللعان، خلي سبيلها، ولحقه الولد. ذكره الخرقي. وعن أحمد أنها تحبس حتى تقر أو تلاعن)','11502','4017','0');
INSERT INTO [t19188] VALUES (4041,'٣٨١١ - مسألة: (ولا يعرض للزوج حتى تطالبه زوجته. فإن أراد اللعان من غير طلبها، فإن كان بينهما ولد يريد نفيه، فله ذلك، وإلا فلا)','11506','4017','0');
INSERT INTO [t19188] VALUES (4042,'٣٨١٢ - مسألة: (فإذا تم اللعان بينهما، ثبت أربعة أحكام؛ أحدها، سقوط الحد عنه أو التعزير. ولو قذفها برجل بعينه، سقط الحد عنه لهما)','11508','4017','0');
INSERT INTO [t19188] VALUES (4043,'٣٨١٣ - مسألة: (الثاني، الفرقة بينهما وعنه، لا تحصل حتى يفرق الحاكم بينهما)','11511','4017','0');
INSERT INTO [t19188] VALUES (4044,'٣٨١٤ - مسألة: (الثالث، التحريم المؤبد. وعنه، أنه إن أكذب نفسه، حلت له)','11518','4017','0');
INSERT INTO [t19188] VALUES (4045,'٣٨١٥ - مسألة','11521','4017','0');
INSERT INTO [t19188] VALUES (4046,'٣٨١٦ - مسألة: (وإذا قلنا: تحل له بإكذاب نفسه. فإن لم يكن وجد منه طلاق، فهي باقية على النكاح)','11522','4017','0');
INSERT INTO [t19188] VALUES (4047,'٣٨١٧ - مسألة: (الرابع، انتفاء الولد عنه بمجرد اللعان. ذكره أبو بكر. وينتفي عنه حملها وإن لم يذكره. وقال الخرقي: لا ينتفي حتى يذكره في اللعان)','11522','4017','0');
INSERT INTO [t19188] VALUES (4048,'٣٨١٨ - مسألة: (وإن نفى الحمل في التعانه، لم ينتف حتى ينفيه عند وضعفا له ويلاعن)','11528','4017','0');
INSERT INTO [t19188] VALUES (4049,'٣٨١٩ - مسألة: (وإن هنيء به فسكت)','11532','4017','0');
INSERT INTO [t19188] VALUES (4050,'٣٨٢٠ - مسألة: (فإن أمن على الدعاء)','11532','4017','0');
INSERT INTO [t19188] VALUES (4051,'٣٨٢١ - مسألة: (وإن أخر نفيه مع إمكانه، لزمه نسبه)','11532','4017','0');
INSERT INTO [t19188] VALUES (4052,'٣٨٢٢ - مسألة: (فإن قال: أخرته رجاء موته. لم يعذر بذلك)','11534','4017','0');
INSERT INTO [t19188] VALUES (4053,'٣٨٢٣ - مسألة: (وإن قال: لم أعلم به. أو: لم أعلم أن لي نفيه. أو: لم أعلم أن ذلك على الفور. وأمكن صدقه، قبل منه)','11534','4017','0');
INSERT INTO [t19188] VALUES (4054,'٣٨٢٤ - مسألة: (وإن أخره لمرض، أو غيبة، أو شيء يمنعه ذلك، لم يسقط نفيه)','11535','4017','0');
INSERT INTO [t19188] VALUES (4055,'٣٨٢٥ - مسألة: (ومتى أكذب نفسه بعد نفيه، لحقه نسبه، ولزمه الحد إن كانت المرأة محصنة، أو التعزير إن لم تكن محصنة)','11537','4017','0');
INSERT INTO [t19188] VALUES (4056,'٣٨٢٦ - مسألة: ويلزمه الحد إذا أكذب نفسه، سواء أكذبها قبل لعانها أو بعده. وهذا قول الشافعي، وأبي ثور، وأصحاب الرأي، ولا نعلم لهم مخالفا؛ لأن اللعان أقيم مقام البينة في حق الزوج، فإذا أكذب نفسه بان أن لعانه كذب، وزيادة في هتكها، وتكرار لقذفها، فلا أقل من أن يجب الحد الذي كان واجبا بالقذف المجرد. فإن عاد عن إكذاب نفسه، وقال: لي بينة أقيمها','11540','4017','0');
INSERT INTO [t19188] VALUES (4057,'٣٨٢٧ - مسألة: (فأما إن أتت به لدون ستة أشهر منذ تزوجها، أو لأكثر من أربع سنين منذ أبانها)','11543','4017','0');
INSERT INTO [t19188] VALUES (4058,'٣٨٢٨ - مسألة: (وإن أقرت بانقضاء عدتها بالقروء، ثم أتت به لأكثر من ستة أشهر)','11543','4017','0');
INSERT INTO [t19188] VALUES (4059,'٣٨٢٩ - مسألة: فإن طلقها وهي حامل، فولدت، ثم ولدت آخر قبل مضي ستة أشهر، فهو من الزوج؛ لأنا نعلم أيهما حمل واحد، فإذا كان أحدهما منه، فالآخر منه. وإن كان بينهما أكثر من ستة أشهر، لم يلحق بالزوج، وانتفى عنه من غير لعان؛ لأنه لا','11545','4017','0');
INSERT INTO [t19188] VALUES (4060,'٣٨٣٠ - مسألة: (أو مع العلم بأنه لم يجتمع بها، كالذي يتزوجها بحضرة الحاكم، ويطلقها في المجلس)','11545','4017','0');
INSERT INTO [t19188] VALUES (4061,'٣٨٣١ - مسألة: (أو صبي له دون عشر سنين، أو مقطوع الذكر والأنثيين)','11547','4017','0');
INSERT INTO [t19188] VALUES (4062,'٣٨٣٢ - مسألة: (وإن طلقها طلاقا رجعيا، فولدت لأكثر من أربع سنين منذ طلقها، ولأقل من أربع سنين منذ انقضت عدتها، ففيه وجهان)','11550','4017','0');
INSERT INTO [t19188] VALUES (4063,'٣٨٣٣ - مسألة: (وإن أعتقها أو باعها بعد اعترافه بوطئها، فأتت بولد لدون ستة أشهر)','11560','4017','0');
INSERT INTO [t19188] VALUES (4064,'٣٨٣٤ - مسألة: (وكذلك إن لم يستبرئها فأتت به لأكثر من ستة أشهر، فادعى المشتري أنه منه، سواء ادعاه البائع أو لم يدعه)','11561','4017','0');
INSERT INTO [t19188] VALUES (4065,'٣٨٣٥ - مسألة: (وإن استبرئت ثم أتت بولد لأكثر من ستة أشهر، لم يلحقه نسبه)','11562','4017','0');
INSERT INTO [t19188] VALUES (4066,'٣٨٣٦ - مسألة: (فأما إن لم يكن البائع أقر بوطئها قبل بيعها، لم يلحقه الولد بحال)','11562','4017','0');
INSERT INTO [t19188] VALUES (4067,'٣٨٣٧ - مسألة: (وإن ادعاة البائع، فهو عبد للمشتري)','11563','4017','0');
INSERT INTO [t19188] VALUES (4068,'٣٨٣٨ - مسألة: (وإن وطئ المجنون من لا ملك له عليها ولا شبهة ملك، فأتت بولد، لم يلحقه نسبه)','11564','4017','0');
INSERT INTO [t19188] VALUES (4069,'كتاب العدد','11567','0','0');
INSERT INTO [t19188] VALUES (4070,'[باب. . .]','11567','4069','0');
INSERT INTO [t19188] VALUES (4071,'٣٨٣٩ - مسألة. (كل امرأة فارقها زوجها في الحياة قبل المسيس والخلوة)','11568','4070','0');
INSERT INTO [t19188] VALUES (4072,'٣٨٤٠ - مسألة: (وإن خلا بها وهي مطاوعة، فعليها العدة، سواء كان بهما أو بأحدهما مانع من الوطء، كالإحرام والصيام والحيض والنفاس والمرض والجب والعنة، أو لم يكن، إلا أن لا يعلم بها، كالأعمى والطفل، فلا عدة عليها)','11569','4070','0');
INSERT INTO [t19188] VALUES (4073,'٣٨٤١ - مسألة: (إلا أن لا يعلم بها، كالأعمى والطفل، فلا عدة عليها)','11572','4070','0');
INSERT INTO [t19188] VALUES (4074,'٣٨٤٢ - مسألة: (والحمل الذي تنقضي به العدة، ما يتبين فيه شيء من خلق الإنسان، فإن وضعت مضغة لا يتبين فيها شيء من ذلك، فذكر ثقات من النساء أنه مبتدأ خلق آدمي، فهل تنقضي به العدة؟ على روايتين)','11577','4070','0');
INSERT INTO [t19188] VALUES (4075,'٣٨٤٣ - مسألة: (وإن أتت بولد لا يلحقه نسبه، كامرأة الطفل، لم تنقض به العدة. وعنه، تنقضي. وفيه بعد)','11581','4070','0');
INSERT INTO [t19188] VALUES (4076,'٣٨٤٤ - مسألة: (وأقل مدة الحمل ستة أشهر، وغالبها تسعة،','11584','4070','0');
INSERT INTO [t19188] VALUES (4077,'٣٨٤٥ - مسألة: (وأقل ما يتبين به الولد أحد وثمانون يوما)','11588','4070','0');
INSERT INTO [t19188] VALUES (4078,'٣٨٤٦ - مسألة: (وإن مات زوج الرجعية)','11592','4070','0');
INSERT INTO [t19188] VALUES (4079,'٣٨٤٧ - مسألة: (وإن طلقها في الصحة طلاقا بائنا، ثم مات في عدتها، لم تنتقل عن عدتها)','11593','4070','0');
INSERT INTO [t19188] VALUES (4080,'٣٨٤٨ - مسألة: (وإن كان الطلاق في مرض موته، اعتدت أطول الأجلين؛ من عدة الطلاق وعدة الوفاة)','11594','4070','0');
INSERT INTO [t19188] VALUES (4081,'٣٨٤٩ - مسألة: (وإن ارتابت المتوفى عنها لظهور أمارات الحمل، من الحركة، وانتفاخ البطن، وانقطاع الحيض قبل أن تنكح، لم تزل في عدتها حتى تزول الريبة، وإن تزوجت قبل زوالها لم يصح النكاح، وإن ظهر بها ذلك بعد نكاحها، لم يفسد به، لكن إن أتت بولد لأقل من ستة أشهر منذ نكحها، فهو باطل، وإلا فلا)','11597','4070','0');
INSERT INTO [t19188] VALUES (4082,'٣٨٥٠ - مسألة: (وإذا مات عن امرأة نكاحها فاسد، فقال القاضي: عليها عدة الوفاة. نص عليه. وقال ابن حامد: لا عدة عليها','11600','4070','0');
INSERT INTO [t19188] VALUES (4083,'٣٨٥١ - مسألة؛ قال، رضي الله عنه: (الثالث، ذات القروء التي فارقها في الحياة بعد دخوله بها، عدتها ثلاثة قروء إن كانت حرة، وقرءان إن كانت أمة)','11602','4070','0');
INSERT INTO [t19188] VALUES (4084,'٣٨٥٢ - مسألة: (والقروء الحيض، في أصح الروايتين)','11604','4070','0');
INSERT INTO [t19188] VALUES (4085,'٣٨٥٣ - مسألة: (ولا تعتد بالحيضة التي طلقها فيها حتى تأتي بثلاث كاملة بعدها)','11609','4070','0');
INSERT INTO [t19188] VALUES (4086,'٣٨٥٤ - مسألة','11609','4070','0');
INSERT INTO [t19188] VALUES (4087,'٣٨٥٥ - مسألة: (والرواية الثانية، القروء الأطهار، وتعتد بالطهر الذي طلقها فيه قرءا، فإذا طعنت في الحيضة، الثالثة، حلت)','11614','4070','0');
INSERT INTO [t19188] VALUES (4088,'٣٨٥٦ - مسألة: (وعدة أم الولد عدة الأمة)','11621','4070','0');
INSERT INTO [t19188] VALUES (4089,'٣٨٥٧ - مسألة: (وحد الإياس خمسون سنة. وعنه، أن ذلك حده في نساء العجم، وحده في نساء العرب ستون سنة)','11622','4070','0');
INSERT INTO [t19188] VALUES (4090,'٣٨٥٨ - مسألة: (وإن حاضت الصغيرة في عدتها، انتقلت إلى القروء، ويلزمها إكمالها)','11625','4070','0');
INSERT INTO [t19188] VALUES (4091,'٣٨٥٩ - مسألة: (وإن يئست ذات القروء في عدتها، انتقلت إلى عدة الآيسات)','11627','4070','0');
INSERT INTO [t19188] VALUES (4092,'٣٨٦٠ - مسألة: (وإن عتقت الأمة الرجعية في عدتها، بنت على','11627','4070','0');
INSERT INTO [t19188] VALUES (4093,'٣٨٦١ - مسألة: (وإن كانت أمة، اعتدت أحد عشر شهرا)','11632','4070','0');
INSERT INTO [t19188] VALUES (4094,'٣٨٦٢ - مسألة: (وعدة الجارية التي أدركت فلم تحض، والمستحاضة الناسية، ثلاثة أشهر. وعنه، سنة)','11635','4070','0');
INSERT INTO [t19188] VALUES (4095,'٣٨٦٣ - مسألة: وهكذا حكم المستحاضة الناسية. وجملة القول في عدة (١)','11636','4070','0');
INSERT INTO [t19188] VALUES (4096,'٣٨٦٤ - مسألة: (فأما التي عرفت ما رفع الحيض؛ من مرض أو رضاع ونحوه، فلا تزال في عدة حتى يعود الحيض، فتعتد به)','11638','4070','0');
INSERT INTO [t19188] VALUES (4097,'٣٨٦٥ - مسألة: (وهل تفتقر إلى رفع الأمر إلى الحاكم ليحكم','11645','4070','0');
INSERT INTO [t19188] VALUES (4098,'٣٨٦٦ - مسألة: (وإذا حكم الحاكم بالفرقة، نفذ حكمه في الظاهر دون الباطن، فلو طلق الأول، صح طلاقه)','11647','4070','0');
INSERT INTO [t19188] VALUES (4099,'٣٨٦٧ - مسألة: (فإذا فعلت ذلك)','11648','4070','0');
INSERT INTO [t19188] VALUES (4100,'٣٨٦٨ - مسألة: وإن قدم بعد دخول الثاني بها (خير الأول بين أخذها)','11650','4070','0');
INSERT INTO [t19188] VALUES (4101,'٣٨٦٩ - مسألة: (ويأخذ منه صداقها)','11652','4070','0');
INSERT INTO [t19188] VALUES (4102,'٣٨٧٠ - مسألة: (ومن مات عنها زوجها، أو طلقها وهو غائب، فعدتها من يوم مات أو طلق، وإن لم تجتنب ما تجتنبه المعتدات. وعنه، إن ثبت ذلك ببينة فكذلك، وإلا فعدتها من يوم بلغها الخبر)','11660','4070','0');
INSERT INTO [t19188] VALUES (4103,'٣٨٧١ - مسألة: (وعدة المؤطوءة بشبهة عدة المطلقة، وكذلك المزني بها. وعنه، أنها تستبرأ بحيضة)','11662','4070','0');
INSERT INTO [t19188] VALUES (4104,'٣٨٧٢ - مسألة (وإن كانت بائنا فأصابها المطلق عمدا، فكذلك)','11672','4070','0');
INSERT INTO [t19188] VALUES (4105,'٣٨٧٣ - مسألة: وكل معتدة من غير النكاح الصحيح؛ كالزانية، والموطوءة بشبهة، أو في نكاح فاسد، فقياس المذهب تحريم نكاحها على الواطئ وغيره. قال شيخنا','11674','4070','0');
INSERT INTO [t19188] VALUES (4106,'٣٨٧٤ - مسألة: (وإن تزوجت في عدتها، لم تنقطع عدتها حتى يدخل بها، فتنقطع حينئذ)','11675','4070','0');
INSERT INTO [t19188] VALUES (4107,'٣٨٧٥ - مسألة: (ثم إذا فارقها، بنت على عدة الأول، ثم استأنفت العدة من الثاني)','11676','4070','0');
INSERT INTO [t19188] VALUES (4108,'٣٨٧٦ - مسألة: (وإن أتت بولد من أحدهما، انقضت عدتها به منه، ثم اعتدت للآخر أيهما كان)','11678','4070','0');
INSERT INTO [t19188] VALUES (4109,'٣٨٧٧ - مسألة: (وإن أمكن أن يكون منهما)','11678','4070','0');
INSERT INTO [t19188] VALUES (4110,'٣٨٧٨ - مسألة: (وللثاني أن ينكحها بعد انقضاء العدتين.','11680','4070','0');
INSERT INTO [t19188] VALUES (4111,'٣٨٧٩ - مسألة: (وإن وطيء رجلان امرأة، فعليها عدتان لهما)','11683','4070','0');
INSERT INTO [t19188] VALUES (4112,'٣٨٨٠ - مسألة: (وإن راجعها، ثم طلقها بعد دخوله بها، استأنفت العدة)','11684','4070','0');
INSERT INTO [t19188] VALUES (4113,'٣٨٨١ - مسألة: (وإن طلقها قبل دخوله بها، فهل تبني أو تستأنف؟ على روايتين)','11684','4070','0');
INSERT INTO [t19188] VALUES (4114,'٣٨٨٢ - مسألة: (وإن طلقها طلاقا بائنا، ثم نكحها في عدتها، ثم طلقها قبل دخوله بها، فعلى روايتين)','11686','4070','0');
INSERT INTO [t19188] VALUES (4115,'٣٨٨٣ - مسألة: (وهل يجب على البائن؟ على روايتين)','11690','4070','0');
INSERT INTO [t19188] VALUES (4116,'٣٨٨٤ - مسألة: ولا إحداد على الرجعية','11692','4070','0');
INSERT INTO [t19188] VALUES (4117,'٣٨٨٥ - مسألة: ويستوي في وجوبه الحرة والأمة','11693','4070','0');
INSERT INTO [t19188] VALUES (4118,'٣٨٨٦ - مسألة: (والإحداد اجتناب الزينة والطيب والتحسين، كلبس الحلي والملون من الثياب للتحسين)','11694','4070','0');
INSERT INTO [t19188] VALUES (4119,'٣٨٨٧ - مسألة: (ولا يحرم عليها الأبيض من الثياب وإن كان حسنا)','11702','4070','0');
INSERT INTO [t19188] VALUES (4120,'٣٨٨٨ - مسألة: (إلا أن تدعو ضرورة إلى خروجها منه، بأن يحولها مالكه، أو تخشى على نفسها، فتنتقل)','11707','4070','0');
INSERT INTO [t19188] VALUES (4121,'٣٨٨٩ - مسألة: (ولا تخرج ليلا، ولها الخروج نهارا لحوائجها)','11716','4070','0');
INSERT INTO [t19188] VALUES (4122,'٣٨٩٠ - مسألة: (وإن أذن لها زوجها في النقلة إلى بلد للسكنى فيه، فمات قبل مفارقة البنيان، لزمها العود إلى منزلها، وإن مات بعده، فلها الخيار بين البلدين)','11719','4070','0');
INSERT INTO [t19188] VALUES (4123,'٣٨٩١ - مسألة: (وإن سافر بها فمات في الطريق وهي قريبة، لزمها العود)','11721','4070','0');
INSERT INTO [t19188] VALUES (4124,'٣٨٩٢ - مسألة: (وإن أذن لها في الحج فأحرمت به، ثم مات، فخشيت فوات الحج، مضت في سفرها، وإن لم تخش، وهي في بلدها أو قريبة يمكنها العود، أقامت لتقضي العدة في منزلها، وإلا مضت في سفرها، وإن لم تكن أحرمت به، أو أحرمت بعد موته، فحكمها حكم من لم تخش الفوات)','11723','4070','0');
INSERT INTO [t19188] VALUES (4125,'٣٨٩٣ - مسألة: (وأما المبتوتة فلا تجب عليها العدة في منزله، وتعتد حيث شاءت. نص عليه)','11727','4070','0');
INSERT INTO [t19188] VALUES (4126,'باب في استبراء الإماء','11730','4069','0');
INSERT INTO [t19188] VALUES (4127,'٣٨٩٤ - مسألة: ويحرم الاستمتاع منها','11733','4126','0');
INSERT INTO [t19188] VALUES (4128,'٣٨٩٥ - مسألة: (وسواء ملكها من صغير أو كبير، أو رجل أو امرأة)','11735','4126','0');
INSERT INTO [t19188] VALUES (4129,'٣٨٩٦ - مسألة: (وإن أعتقها قبل استبرائها، لم يحل له نكاحها','11735','4126','0');
INSERT INTO [t19188] VALUES (4130,'٣٨٩٧ - مسألة: (والصغيرة التي لا يوطأ مثلها، هل يجب استبراؤها؟ على وجهين)','11738','4126','0');
INSERT INTO [t19188] VALUES (4131,'٣٨٩٨ - مسألة: (وإن اشترى زوجته)','11740','4126','0');
INSERT INTO [t19188] VALUES (4132,'٣٨٩٩ - مسألة: (أو عجزت مكاتبته)','11740','4126','0');
INSERT INTO [t19188] VALUES (4133,'٣٩٠٠ - مسألة: (أو أسلمت)','11741','4126','0');
INSERT INTO [t19188] VALUES (4134,'٣٩٠١ - مسألة: (أو اشترى مكاتبه ذوات رحمه، فحضن','11741','4126','0');
INSERT INTO [t19188] VALUES (4135,'٣٩٠٢ - مسألة: (وإن وجد الاستبراء في يد البائع قبل القبض، أجزأ. ويحتمل أن لا يجزئ)','11744','4126','0');
INSERT INTO [t19188] VALUES (4136,'٣٩٠٣ - مسألة: (وإن باع أمته، ثم عادت إليه بفسخ أو غيره بعد القبض، وجب استبراؤها، وإن كان قبله، فعلى روايتين)','11746','4126','0');
INSERT INTO [t19188] VALUES (4137,'٣٩٠٤ - مسألة: (وإذا اشترى أمة مزوجة، فطلقها الزوج قبل الدخول، وجب استبراؤها)','11747','4126','0');
INSERT INTO [t19188] VALUES (4138,'٣٩٠٥ - مسألة: (وإن كان بعده، لم يجب في أحد الوجهين)','11747','4126','0');
INSERT INTO [t19188] VALUES (4139,'٣٩٠٦ - مسألة: (وإن مات زوجها وسيدها، ولم يعلم السابق منهما، وبين موتهما أقل من شهرين وخمسة أيام، لزمها بعد موت الآخر منهما عدة حرة من الوفاة حسب)','11757','4126','0');
INSERT INTO [t19188] VALUES (4140,'٣٩٠٧ - مسألة: (وإن كان بينهما أكثر من ذلك، أو جهلت المدة)','11757','4126','0');
INSERT INTO [t19188] VALUES (4141,'٣٩٠٨ - مسألة: (وإن اشترك رجلان في وطء أمة، لزمها استبراءان)','11761','4126','0');
INSERT INTO [t19188] VALUES (4142,'٣٩٠٩ - مسألة: (أو بحيضة إن كانت ممن تحيض)','11762','4126','0');
INSERT INTO [t19188] VALUES (4143,'٣٩١٠ - مسألة: (أو بمضي شهر إن كانت آيسة أو صغيرة. وعنه، بثلاثة أشهر. اختارها الخرقي)','11767','4126','0');
INSERT INTO [t19188] VALUES (4144,'٣٩١١ - مسألة: (وإن ارتفع حيضها ما تدري ما رفعه)','11769','4126','0');
INSERT INTO [t19188] VALUES (4145,'كتاب الرضاع','11771','0','0');
INSERT INTO [t19188] VALUES (4146,'٣٩١٢ - مسألة: (إذا حملت المرأة من رجل يثبت نسسب ولدها منه، فثاب لها لبن، فأرضعت به طفلا، صار ولدا لهما في تحريم النكاح، وإباحة النظر والخلوة، وثبوت المحرمية، وأولاده وإن سفلوا','11772','4145','0');
INSERT INTO [t19188] VALUES (4147,'٣٩١٣ - مسألة: (ولا تنتشر إلى من في درجته من إخوته وأخواته، ولا من هو أعلى منه من آبائه وأمهاته وأعمامه وعماته وأخواله وخالاته، فلا تحرم المرضعة على أبي المرتضع، ولا أخيه، ولا تحرم أم المرتضع ولا أخته على أبيه من الرضاع ولا أخيه)','11775','4145','0');
INSERT INTO [t19188] VALUES (4148,'٣٩١٤ - مسألة: (وإن أرضعت بلبن ولدها من الزنى طفلا، صار ولدا لها، وحرم على الزاني تحريم المصاهرة، ولم تثبت حرمة الرضاع في حقه، في ظاهر قول الخرقي. وقال أبو بكر: تثبت. قال أبو الخطاب: وكذلك الولد المنفي باللعان)','11776','4145','0');
INSERT INTO [t19188] VALUES (4149,'٣٩١٥ - مسألة (وإن وطئ رجلان امرأة بشبهة، فأتت بولد، فأرضعت بلبنه طفلا، صار ابنا لمن ثبت نسب المولود منه)','11779','4145','0');
INSERT INTO [t19188] VALUES (4150,'٣٩١٦ - مسألة: (وإن ثاب لامرأة لبن من غير حمل تقدم، لم ينشر الحرمة. نص عليه في لبن البكر. وعنه، ينشرها. ذكرها ابن أبي موسى)','11781','4145','0');
INSERT INTO [t19188] VALUES (4151,'٣٩١٧ - مسألة: (ولا ينشر الحرمة غير لبن المرأة، فلو ارتضع طفلان بن رجل أو بهيمة أو خنثى مشكل، لم ينشر الحرمة. وقال ابن حامد: يوقف أمر الخنثى حتى يتبين أمره)','11782','4145','0');
INSERT INTO [t19188] VALUES (4152,'٣٩١٨ - مسألة: (فلو ارتضع بعدهما بلحظة، لم يثبت)','11788','4145','0');
INSERT INTO [t19188] VALUES (4153,'٣٩١٩ - مسألة: (ومتى أخذ الثدي، فامتص منه ثم تركه، أو قطع عليه، فهي رضعة، فإن عاد)','11792','4145','0');
INSERT INTO [t19188] VALUES (4154,'٣٩٢٠ - مسألة: (والسعوط والوجور كالرضاع، في إحدى الروايتين)','11794','4145','0');
INSERT INTO [t19188] VALUES (4155,'٣٩٢١ - مسألة: (ويحرم لبن الميتة واللبن المشوب. ذكره الخرقي. وقال أبو بكر: لا يثبت التحريم بهما)','11797','4145','0');
INSERT INTO [t19188] VALUES (4156,'٣٩٢٢ - مسألة: (ويحرم اللبن المشوب)','11799','4145','0');
INSERT INTO [t19188] VALUES (4157,'٣٩٢٣ - مسألة: (والحقنة لا تنشر الحرمة. نص عليه. وقال ابن حامد: تنشرها)','11801','4145','0');
INSERT INTO [t19188] VALUES (4158,'٣٩٢٤ - مسألة: (وإن أرضعت اثنتين منفردتين، انفسخ نكاحهما، على الرواية الأولى، وعلى الثانية، ينفسخ نكاح الأولى، ويثبت نكاح الثانية)','11804','4145','0');
INSERT INTO [t19188] VALUES (4159,'٣٩٢٥ - مسألة: (وإن أرضعت الثلاث متفرقات، انفسخ نكاح','11805','4145','0');
INSERT INTO [t19188] VALUES (4160,'٣٩٢٦ - مسألة: (وإن أرضعت إحداهن منفردة، واثنتين بعدها، انفسخ نكاح الجميع، على الروايتين)','11806','4145','0');
INSERT INTO [t19188] VALUES (4161,'٣٩٢٧ - مسألة: (وله أن يتزوج من شاء من الأصاغر)','11807','4145','0');
INSERT INTO [t19188] VALUES (4162,'٣٩٢٨ - مسألة: (وكل امرأة تحرم ابنتها عليه؛ كأمه، وأخته، وجدته، وربيبته، إذا أرضعت طفلة، حرمتها عليه)','11807','4145','0');
INSERT INTO [t19188] VALUES (4163,'٣٩٢٩ - مسألة: (وإن أفسدت نكاحها)','11809','4145','0');
INSERT INTO [t19188] VALUES (4164,'٣٩٣٠ - مسألة: وإن أفسدت نكاح نفسها بعد الدخول','11811','4145','0');
INSERT INTO [t19188] VALUES (4165,'٣٩٣١ - مسألة: (وإن أفسدت نكاح نفسها)','11813','4145','0');
INSERT INTO [t19188] VALUES (4166,'٣٩٣٢ - مسألة: (فإذا أرضعت امرأته الكبرى الصغرى، فانفسخ نكاحهما، فعليه نصف مهر الصغرى، يرجع به على الكبرى، ولا مهر للكبرى إن كان قبل الدخول)','11814','4145','0');
INSERT INTO [t19188] VALUES (4167,'٣٩٣٣ - مسألة: فلو دبت الصغرى إلى الكبرى وهي نائمة','11814','4145','0');
INSERT INTO [t19188] VALUES (4168,'٣٩٣٤ - مسألة: (وإذا كان لرجل خمس أمهات أولاد، لهن منه لبن، فأرضعن امرأة له صغرى. كل واحدة منهن رضعة، حرمت عليه، في أحد الوجهين)','11817','4145','0');
INSERT INTO [t19188] VALUES (4169,'٣٩٣٥ - مسألة: (ولو كان له ثلاث نسوة، لهن لبن منه، فأرضعن امرأة له صغرى، كل واحدة رضعتين، لم تحرم المرضعات)','11819','4145','0');
INSERT INTO [t19188] VALUES (4170,'٣٩٣٦ - مسألة: (فإن كان لرجل ثلاث بنات امرأة لهن لبن، فأرضعن ثلاث نسوة له صغارا، حرمت الكبيرة)','11821','4145','0');
INSERT INTO [t19188] VALUES (4171,'٣٩٣٧ - مسألة: (وإن أرضعن واحدة، كل واحدة منهن رضعتين، فهل تحرم الكبرى بذلك؟ على وجهين)','11822','4145','0');
INSERT INTO [t19188] VALUES (4172,'٣٩٣٨ - مسألة: (ولو تزوجت الصبي أولا، ثم فسخت نكاحه لعيب، ثم تزوجت كبيرا، فصار لها منه لبن، فأرضعت به الصبي، حرمت عليهما علي الأبد)','11824','4145','0');
INSERT INTO [t19188] VALUES (4173,'٣٩٣٩ - مسألة: (وإن شهد به امرأة مرضية، ثبت بشهادتها. وعنه، أنها إن كانت مرضية استحلفت، فإن كانت كاذبة، لم يحل الحول حتى يبيض ثدياها، وذهب في ذلك إلى قول ابن عباس)','11829','4145','0');
INSERT INTO [t19188] VALUES (4174,'٣٩٤٠ - مسألة: (وإن تزوج امرأة، ثم قال قبل الدخول: هي أختي من الرضاع. انفسخ النكاح، فإن صدقته، فلا مهر)','11832','4145','0');
INSERT INTO [t19188] VALUES (4175,'٣٩٤١ - مسألة: (وإن قال ذلك بعد الدخول، انفسخ النكاح)','11833','4145','0');
INSERT INTO [t19188] VALUES (4176,'٣٩٤٢ - مسألة: (وإن كانت هي التي قالت: هو أخي من الرضاع. فأكذبها، فهي زوجته في الحكم)','11835','4145','0');
INSERT INTO [t19188] VALUES (4177,'٣٩٤٣ - مسألة: (ولو قال الزوج: هي ابنتي من الرضاع. وهي في سنه أو أكبر منه، لم تحرم؛ لتحققنا كذبه)','11837','4145','0');
INSERT INTO [t19188] VALUES (4178,'٣٩٤٤ - مسألة: (ولو تزوج رجل امرأة لها لبن من زوج قبله، فحملت منه ولم يزد لبنها، فهو للأول، وإن زاد لبنها فأرضعت به طفلا، صار ابنا لهما. وإن انقطع لبن الأول، ثم ثاب بحملها من الثاني، فكذلك عند أبي بكر. وعند أبي الخطاب، هو ابن الثاني وحده)','11838','4145','0');
INSERT INTO [t19188] VALUES (4179,'كتاب النفقات','11843','0','0');
INSERT INTO [t19188] VALUES (4180,'[باب. . .]','11843','4179','0');
INSERT INTO [t19188] VALUES (4181,'٣٩٤٥ - مسألة: (وليس ذلك مقدرا، لكنه معتبر بحال الزوجين)','11845','4180','0');
INSERT INTO [t19188] VALUES (4182,'٣٩٤٦ - مسألة: (فإن تنازعا فيها، رجع الأمر إلى الحاكم)','11849','4180','0');
INSERT INTO [t19188] VALUES (4183,'٣٩٤٧ - مسألة: ويجب عليه كسوتها، بإجماع أهل العلم','11852','4180','0');
INSERT INTO [t19188] VALUES (4184,'٣٩٤٨ - مسألة: (وعليه ما يعود بنظافة المرأة، من الدهن، والسدر، وثمن الماء)','11855','4180','0');
INSERT INTO [t19188] VALUES (4185,'٣٩٤٩ - مسألة: (فأما الطيب والخضاب والحناء ونحوه، فلا يلزمه، إلا أن يريد منها التزين به)','11856','4180','0');
INSERT INTO [t19188] VALUES (4186,'٣٩٥٠ - مسألة: (وإن احتاجت إلى من يخدمها؛ لكون مثلها لا تخدم نفسها، أو لمرضها، لزمه ذلك)','11858','4180','0');
INSERT INTO [t19188] VALUES (4187,'٣٩٥١ - مسألة: (فإن كان لها خادم، وإلا أقام لها خادما، إما بشراء أو كراء أو عارية)','11858','4180','0');
INSERT INTO [t19188] VALUES (4188,'٣٩٥٢ - مسألة: (وعليه نفقته بقدر نفقة الفقيرين، إلا في النظافة)','11860','4180','0');
INSERT INTO [t19188] VALUES (4189,'٣٩٥٣ - مسألة: (ولا يلزمه أكثر من نفقة خادم واحد)','11861','4180','0');
INSERT INTO [t19188] VALUES (4190,'٣٩٥٤ - مسألة: (فإن قالت: أنا أخدم نفسى، وآخذ ما يلزمك لخادمى. لم يكن لها ذلك)','11862','4180','0');
INSERT INTO [t19188] VALUES (4191,'٣٩٥٥ - مسألة: (وإن قال)','11862','4180','0');
INSERT INTO [t19188] VALUES (4192,'فصل','11863','4180','0');
INSERT INTO [t19188] VALUES (4193,'٣٩٥٦ - مسألة: (وأما البائن بفسخ أو طلاق، فإن كانت حاملا، فلها النفقة والسكنى، وإلا فلا شئ لها. وعنه، لها السكنى)','11863','4180','0');
INSERT INTO [t19188] VALUES (4194,'٣٩٥٧ - مسألة: (فإن)','11871','4180','0');
INSERT INTO [t19188] VALUES (4195,'٣٩٥٨ - مسألة: (وإن أنفق عليها يظنها حاملا وبانت حائلا)','11871','4180','0');
INSERT INTO [t19188] VALUES (4196,'٣٩٥٩ - مسألة: (وهل تجب النفقة للحامل لحملها، أو لها من أجله؟ على روايتين)','11874','4180','0');
INSERT INTO [t19188] VALUES (4197,'٣٩٦٠ - مسألة: (وأما المتوفى عنها)','11879','4180','0');
INSERT INTO [t19188] VALUES (4198,'فصل','11886','4180','0');
INSERT INTO [t19188] VALUES (4199,'٣٩٦١ - مسألة: (فإن طلب أحدهما دفع القيمة، لم يلزم الآخر)','11888','4180','0');
INSERT INTO [t19188] VALUES (4200,'٣٩٦٢ - مسألة: (وعليه كسوتها فى كل عام)','11888','4180','0');
INSERT INTO [t19188] VALUES (4201,'٣٩٦٣ - مسألة: (فإذا قبضتها فسرقت أو تلفت، لم يلزمه عوضها)','11889','4180','0');
INSERT INTO [t19188] VALUES (4202,'٣٩٦٤ - مسألة: (وإن انقضت السنة وهى صحيحة، فعليه كسوة السنة الأخرى، ويحتمل أن لا يلزمه)','11890','4180','0');
INSERT INTO [t19188] VALUES (4203,'٣٩٦٥ - مسألة: (وإن ماتت أو طلقها قبل مضى السنة، فهل يرجع عليها بقسط بقية السنة؟ على وجهين)','11891','4180','0');
INSERT INTO [t19188] VALUES (4204,'٣٩٦٦ - مسألة: (وإذا قبضت النفقة، فلها التصرف فيها على وجه لا يضر بها، ولا ينهك بدنها)','11892','4180','0');
INSERT INTO [t19188] VALUES (4205,'٣٩٦٧ - مسألة: (وإن غاب مدة ولم ينفق، فعليه نفقة ما مضى)','11893','4180','0');
INSERT INTO [t19188] VALUES (4206,'فصل','11895','4180','0');
INSERT INTO [t19188] VALUES (4207,'٣٩٦٨ - مسألة: وإن سلمت نفسها، وهى ممن يتعذر وطؤها، لرتق، أو حيض، أو نفاس، أو لكونها نضوة الخلق لا يمكنه وطؤها لذلك، أو لمرضها، لزمته نفقتها أيضا','11897','4180','0');
INSERT INTO [t19188] VALUES (4208,'٣٩٦٩ - مسألة: وإن أسلمت نفسها [وهو صغير]','11898','4180','0');
INSERT INTO [t19188] VALUES (4209,'٣٩٧٠ - مسألة: (فإن كانت صغيرة لا يمكن وطؤها، لم تجب نفقتها، ولا تسليمها إليه إذا طلبها)','11899','4180','0');
INSERT INTO [t19188] VALUES (4210,'٣٩٧١ - مسألة: (فإن بذلته والزوج غائب، لم يفرض لها حتى يراسله الحاكم، ويمضى زمن يمكن أن يقدم فى مثله)','11900','4180','0');
INSERT INTO [t19188] VALUES (4211,'٣٩٧٢ - مسألة: (وإن منعت نفسها، أو منعها أهلها، فلا نفقة','11901','4180','0');
INSERT INTO [t19188] VALUES (4212,'٣٩٧٣ - مسألة: (إلا أن تمنع نفسها قبل الدخول حتى تقبض صداقها الحال، فلها ذلك، وتجب نفقتها)','11903','4180','0');
INSERT INTO [t19188] VALUES (4213,'٣٩٧٤ - مسألة: (وإن كان بعد الدخول)','11904','4180','0');
INSERT INTO [t19188] VALUES (4214,'٣٩٧٥ - مسألة: فأما الصداق الآجل (١)','11905','4180','0');
INSERT INTO [t19188] VALUES (4215,'٣٩٧٦ - مسألة: (وإن سلمت الأمة نفسها ليلا ونهارا، فهى','11905','4180','0');
INSERT INTO [t19188] VALUES (4216,'٣٩٧٧ - مسألة: (وإن كانت تأوى إليه ليلا، وعند السيد نهارا، فعلى كل واحد منهما النفقة بقدر مقامها عنده)','11907','4180','0');
INSERT INTO [t19188] VALUES (4217,'٣٩٧٨ - مسألة: (وإذا نشزت المرأة، أو سافرت بغير إذنه، أو تطوعت بحج أو صوم، أو أحرمت بحج منذور فى الذمة)','11910','4180','0');
INSERT INTO [t19188] VALUES (4218,'٣٩٧٩ - مسألة: (وإن بعثها فى حاجته)','11913','4180','0');
INSERT INTO [t19188] VALUES (4219,'٣٩٨٠ - مسألة: (وإن أحرمت بمنذور معين فى وقته، فعلى وجهين)','11914','4180','0');
INSERT INTO [t19188] VALUES (4220,'٣٩٨١ - مسألة: (وإن اختلفا فى نشوزها)','11916','4180','0');
INSERT INTO [t19188] VALUES (4221,'٣٩٨٢ - مسألة: وكذلك إن ادعى (تسليم النفقة إليها)','11916','4180','0');
INSERT INTO [t19188] VALUES (4222,'٣٩٨٣ - مسألة: (وإن اختلفا فى بذل التسليم)','11916','4180','0');
INSERT INTO [t19188] VALUES (4223,'فصل','11917','4180','0');
INSERT INTO [t19188] VALUES (4224,'٣٩٨٤ - مسألة: وإن رضيت بالمقام معه مع عسرته، وترك المطالبة، جاز؛ لأن الحق لها، وتكون النفقة دينا فى ذمته (ثم)','11922','4180','0');
INSERT INTO [t19188] VALUES (4225,'٣٩٨٥ - مسألة: (وإن أعسر بنفقة الخادم، أو النفقة الماضية،','11925','4180','0');
INSERT INTO [t19188] VALUES (4226,'٣٩٨٦ - مسألة: ويثبت ذلك','11927','4180','0');
INSERT INTO [t19188] VALUES (4227,'٣٩٨٧ - مسألة: (وإن أعسر بالسكنى أو المهر، فهل لها الفسخ؟ يحتمل وجهين)','11927','4180','0');
INSERT INTO [t19188] VALUES (4228,'٣٩٨٨ - مسألة: (وإن أعسر زوج الأمة فرضيت، لم يكن)','11930','4180','0');
INSERT INTO [t19188] VALUES (4229,'٣٩٨٩ - مسألة: وإن أعسر (زوج الصغيرة أو المجنونة)','11931','4180','0');
INSERT INTO [t19188] VALUES (4230,'فصل','11933','4180','0');
INSERT INTO [t19188] VALUES (4231,'٣٩٩٠ - مسألة: (فإن لم تقدر، أجبره الحاكم وحبسه)','11935','4180','0');
INSERT INTO [t19188] VALUES (4232,'٣٩٩١ - مسألة: (فإن غيب ماله، وصبر على الحبس، فلها الفسخ)','11936','4180','0');
INSERT INTO [t19188] VALUES (4233,'٣٩٩٢ - مسألة: (وإن غاب)','11938','4180','0');
INSERT INTO [t19188] VALUES (4234,'٣٩٩٣ - مسألة: (ولا يجوز الفسخ فى ذلك كله إلا بحكم','11939','4180','0');
INSERT INTO [t19188] VALUES (4235,'باب نفقة الأقارب والمماليك','11941','4179','0');
INSERT INTO [t19188] VALUES (4236,'٣٩٩٤ - مسألة: (ويلزمه نفقة سائر آبائه وإن علوا، وأولاده وإن سفلوا)','11943','4235','0');
INSERT INTO [t19188] VALUES (4237,'٣٩٩٥ - مسألة: (وتلزمه نفقة كل من يرثه بفرض أو تعصيب ممن سواهم، سواء ورثه الآخر أو لا، كعمته، وعتيقه. وحكى عنه، إن لم يرثه الآخر، فلا نفقة له)','11947','4235','0');
INSERT INTO [t19188] VALUES (4238,'٣٩٩٦ - مسألة: (فأما ذوو الأرحام، فلا نفقة عليهم، رواية واحدة. ذكره القاضى. وقال أبو الخطاب، يخرج فى وجوبها عليهم روايتان)','11952','4235','0');
INSERT INTO [t19188] VALUES (4239,'٣٩٩٧ - مسألة: (وإن كان للفقير وراث، فنفقته عليهم على قدر إرثهم منه)','11954','4235','0');
INSERT INTO [t19188] VALUES (4240,'٣٩٩٨ - مسألة: (فإذا كان له أم وجد، فعلى الأم الثلث، والباقى على الجد)','11954','4235','0');
INSERT INTO [t19188] VALUES (4241,'٣٩٩٩ - مسألة: (وإن)','11955','4235','0');
INSERT INTO [t19188] VALUES (4242,'٤٠٠٠ - مسألة: (إلا أن يكون له أب، فتكون النفقة عليه وحده)','11958','4235','0');
INSERT INTO [t19188] VALUES (4243,'٤٠٠١ - مسألة: (ومن له ابن فقير وأخ موسر، فلا نفقة له عليهما)','11959','4235','0');
INSERT INTO [t19188] VALUES (4244,'٤٠٠٢ - مسألة: (ومن له أم فقيرة وجدة موسرة، فالنفقة عليها)','11959','4235','0');
INSERT INTO [t19188] VALUES (4245,'٤٠٠٣ - مسألة: (ومن كان صحيحا مكلفا لا حرفة له سوى','11961','4235','0');
INSERT INTO [t19188] VALUES (4246,'٤٠٠٤ - مسألة: (ومن لم يفضل عنده إلا نفقة واحد، بدأ','11963','4235','0');
INSERT INTO [t19188] VALUES (4247,'٤٠٠٥ - مسألة: (فإن كان له أبوان، فهو بينهما)','11964','4235','0');
INSERT INTO [t19188] VALUES (4248,'٤٠٠٦ - مسألة: (وإن كان معهما ابن)','11965','4235','0');
INSERT INTO [t19188] VALUES (4249,'٤٠٠٧ - مسألة: (وإن كان له أب وجد، أو ابن وابن ابن، فالأب والابن أحق)','11966','4235','0');
INSERT INTO [t19188] VALUES (4250,'٤٠٠٨ - مسألة: (ولا تجب النفقة مع اختلاف الدين. وقيل','11968','4235','0');
INSERT INTO [t19188] VALUES (4251,'٤٠٠٩ - مسألة: (وإن ترك الإنفاق الواجب مدة، لم يلزمه عوضه)','11970','4235','0');
INSERT INTO [t19188] VALUES (4252,'٤٠١٠ - مسألة: (ومن لزمته نفقة رجل، فهل تلزمه نفقة امرأته؟ على روايتين)','11973','4235','0');
INSERT INTO [t19188] VALUES (4253,'فصل','11978','4235','0');
INSERT INTO [t19188] VALUES (4254,'٤٠١١ - مسألة: (وليس له منع المرأة من رضاع ولدها إذا طلبت','11978','4235','0');
INSERT INTO [t19188] VALUES (4255,'٤٠١٢ - مسألة: (وإن امتنعت من رضاعه لم تجبر، إلا أن يضطر إليها، ويخشى عليه)','11983','4235','0');
INSERT INTO [t19188] VALUES (4256,'٤٠١٣ - مسألة: (ولا تجب عليه أجرة الظئر لما زاد على الحولين)','11985','4235','0');
INSERT INTO [t19188] VALUES (4257,'٤٠١٤ - مسألة: (وإن تزوجت المرأة، فلزوجها منعها من رضاع ولدها، إلا أن يضطر إليها)','11985','4235','0');
INSERT INTO [t19188] VALUES (4258,'فصل','11988','4235','0');
INSERT INTO [t19188] VALUES (4259,'٤٠١٥ - مسألة: (و)','11991','4235','0');
INSERT INTO [t19188] VALUES (4260,'٤٠١٦ - مسألة: (إلا الأمة إذا كان يستمتع بها)','11992','4235','0');
INSERT INTO [t19188] VALUES (4261,'٤٠١٧ - مسألة: (ولا يكلفهم من العمل ما لا يطيقون)','11992','4235','0');
INSERT INTO [t19188] VALUES (4262,'٤٠١٨ - مسألة: (ويريحهم وقت القيلولة والنوم وأوقات','11992','4235','0');
INSERT INTO [t19188] VALUES (4263,'٤٠١٩ - مسألة: (ويداويهم إذا مرضوا)','11994','4235','0');
INSERT INTO [t19188] VALUES (4264,'٤٠٢٠ - [مسألة: (ويركبهم عقبة إذا سافر بهم) لئلا يكلفهم ما لا يطيقون]','11995','4235','0');
INSERT INTO [t19188] VALUES (4265,'٤٠٢١ - مسألة: (وإذا ولى أحدهم طعامه، أطعمه معه، فإن أبى، أطعمه منه)','11995','4235','0');
INSERT INTO [t19188] VALUES (4266,'٤٠٢٢ - مسألة: (ولا يسترضع الأمة لغير ولدها، إلا أن يكون فيها فضل عن ريه)','11996','4235','0');
INSERT INTO [t19188] VALUES (4267,'٤٠٢٣ - مسألة: (ولا يجبر العبد على المخارجة، وإن اتفقا عليها، جاز)','11996','4235','0');
INSERT INTO [t19188] VALUES (4268,'٤٠٢٤ - مسألة','11998','4235','0');
INSERT INTO [t19188] VALUES (4269,'٤٠٢٥ - مسألة: (وله تأديب رقيقه بما يؤدب به ولده وامرأته)','12000','4235','0');
INSERT INTO [t19188] VALUES (4270,'٤٠٢٦ - مسألة: (وللعبد التسرى بإذن سيده، ولو ملكه سيده','12001','4235','0');
INSERT INTO [t19188] VALUES (4271,'فصل','12006','4235','0');
INSERT INTO [t19188] VALUES (4272,'٤٠٢٧ - مسألة: (ولا يحملها ما لا تطيق)','12007','4235','0');
INSERT INTO [t19188] VALUES (4273,'باب الحضانة','12009','4179','0');
INSERT INTO [t19188] VALUES (4274,'٤٠٢٨ - مسألة: و (أحق الناس بحضانة الطفل والمعتوه أمه، ثم أمهاتها الأقرب فالأقرب)','12010','4273','0');
INSERT INTO [t19188] VALUES (4275,'٤٠٢٩ - مسألة: وأولى الناس بعد الأم أمها (ثم أمهاتها الأقرب','12011','4273','0');
INSERT INTO [t19188] VALUES (4276,'٤٠٣٠ - مسألة: (ثم الأخت للأبوين، ثم الأخت للأب، ثم الأخت للأم، ثم الخالة، ثم العمة، فى الصحيح عنه)','12012','4273','0');
INSERT INTO [t19188] VALUES (4277,'٤٠٣١ - مسألة: (قال الخرقى: وخالة الأب أحق من خالة الأم)','12016','4273','0');
INSERT INTO [t19188] VALUES (4278,'٤٠٣٢ - مسألة: (إلا أن الجارية ليس لابن عمها حضانتها)','12019','4273','0');
INSERT INTO [t19188] VALUES (4279,'٤٠٣٣ - مسألة: (فإن امتنعت الأم من حضانتها)','12020','4273','0');
INSERT INTO [t19188] VALUES (4280,'٤٠٣٤ - مسألة: (فإن عدم هؤلاء كلهم، فهل للرجال من ذوى الأرحام حضانة؟ على وجهين؛ أحدهما، لهم)','12021','4273','0');
INSERT INTO [t19188] VALUES (4281,'٤٠٣٥ - مسألة: (ولا حضانة لرقيق ولا فاسق، ولا كافر على مسلم)','12023','4273','0');
INSERT INTO [t19188] VALUES (4282,'٤٠٣٦ - مسألة: (ولا)','12026','4273','0');
INSERT INTO [t19188] VALUES (4283,'٤٠٣٧ - مسألة: (ومتى زالت الموانع منهم)','12030','4273','0');
INSERT INTO [t19188] VALUES (4284,'٤٠٣٨ - مسألة: (ومتى أراد أحد الأبوين النقلة إلى بلد بعيد آمن ليسكنه، فالأب أحق. وعنه، الأم أحق. فإن اختل شرط منها، فالمقيم منهما أحق)','12033','4273','0');
INSERT INTO [t19188] VALUES (4285,'فصل','12037','4273','0');
INSERT INTO [t19188] VALUES (4286,'٤٠٣٩ - مسألة: (فإن اختار أباه، كان عنده ليلا ونهارا، وإن اختار أمه، كان عندها ليلا، وعند أبيه نهارا؛ ليعلمه الصناعة والكتابة ويؤدبه)','12040','4273','0');
INSERT INTO [t19188] VALUES (4287,'٤٠٤٠ - مسألة: (فإن عاد فاختار الآخر، نقل إليه، فإن عاد فاختار الأول، رد إليه)','12041','4273','0');
INSERT INTO [t19188] VALUES (4288,'٤٠٤١ - مسألة: (وإن لم يختر أحدهما، أقرع بينهما)','12042','4273','0');
INSERT INTO [t19188] VALUES (4289,'٤٠٤٢ - مسألة: (وإذا استوى اثنان فى الحضانة، كالأختين، قدم أحدهما بالقرعة)','12042','4273','0');
INSERT INTO [t19188] VALUES (4290,'٤٠٤٣ - مسألة: (وإذا بلغت الجارية سبع)','12044','4273','0');
INSERT INTO [t19188] VALUES (4291,'كتاب الجنايات','12049','0','0');
INSERT INTO [t19188] VALUES (4292,'[باب. . .]','12049','4291','0');
INSERT INTO [t19188] VALUES (4293,'٤٠٤٣ - مسألة: و (القتل على أربعة أضرب؛ عمد، وشبه عمد، وخطأ، وما أجرى مجرى الخطأ)','12052','4292','0');
INSERT INTO [t19188] VALUES (4294,'٤٠٤٤ - مسألة: (فالعمد أن يقتله بما يغلب على الظن موته به، عالما بكونه آدميا معصوما، وهو تسعة أقسام؛ أحدها، أن يجرحه بما له مور','12054','4292','0');
INSERT INTO [t19188] VALUES (4295,'٤٠٤٥ - مسألة: (فإن بقى من ذلك ضمنا حتى مات، أو كان الغرز بها فى مقتل، كالفؤاد والخصيتين فهو عمد محض)','12057','4292','0');
INSERT INTO [t19188] VALUES (4296,'٤٠٤٦ - مسألة: (وإن قطع سلعة','12058','4292','0');
INSERT INTO [t19188] VALUES (4297,'٤٠٤٧ - مسألة: (فإن خلط السم بطعام نفسه، فدخل إنسان منزله فأكله، فلا ضمان عليه)','12072','4292','0');
INSERT INTO [t19188] VALUES (4298,'٤٠٤٨ - مسألة: (فإن ادعى القاتل بالسم: إننى لم أعلم أنه سم','12072','4292','0');
INSERT INTO [t19188] VALUES (4299,'فصل','12079','4292','0');
INSERT INTO [t19188] VALUES (4300,'فصل','12082','4292','0');
INSERT INTO [t19188] VALUES (4301,'٤٠٤٩ - مسألة: (والذى أجرى مجرى الخطأ، كالنائم ينقلب على إنسان فيقتله، أو يقتل بالسبب، مثل أن يحفر بئرا، أو ينصب سكينا أو حجرا، فيئول إلى إتلاف إنسان، وعمد الصبى والمجنون، فهذا كله لا قصاص فيه، والدية على العاقلة، وعليه الكفارة فى ماله)','12085','4292','0');
INSERT INTO [t19188] VALUES (4302,'فصل','12086','4292','0');
INSERT INTO [t19188] VALUES (4303,'٤٠٥٠ - مسألة: (وإن جرحه أحدهما جرحا والآخر مائة، فهما','12088','4292','0');
INSERT INTO [t19188] VALUES (4304,'٤٠٥١ - مسألة: (وإن قطع أحدهما)','12091','4292','0');
INSERT INTO [t19188] VALUES (4305,'٤٠٥٢ - مسألة: (وإن فعل أحدهما فعلا لا تبقى معه الحياة، كقطع حشوته، أو مريئه، أو ودجيه، ثم ضرب عنقه آخر، فالقاتل','12093','4292','0');
INSERT INTO [t19188] VALUES (4306,'٤٠٥٣ - مسألة: (فإن رماه من شاهق، فتلقاه آخر بسيف فقده)','12097','4292','0');
INSERT INTO [t19188] VALUES (4307,'٤٠٥٤ - مسألة: (وإن ألقاه فى لجة، فالتقمه حوت، فالقود على الرامى فى أحد الوجهين)','12097','4292','0');
INSERT INTO [t19188] VALUES (4308,'٤٠٥٥ - مسألة: (وإن أكره إنسانا على القتل فقتل، فالقصاص عليهما)','12098','4292','0');
INSERT INTO [t19188] VALUES (4309,'٤٠٥٦ - مسألة: (وإن أمر من لا يميز، أو مجنونا، أو عبده الذى لا يعلم أن القتل محرم، بالقتل، فقتل، فالقصاص على الآمر)','12100','4292','0');
INSERT INTO [t19188] VALUES (4310,'٤٠٥٧ - مسألة: (وإن أمر كبيرا عاقلا عالما بتحريم القتل به، فقتل، فالقصاص على القاتل)','12103','4292','0');
INSERT INTO [t19188] VALUES (4311,'٤٠٥٨ - مسألة: (وإن أمر السلطان بقتل إنسان بغير حق من يعلم ذلك، فالقصاص على القاتل، وإن لم يعلم فعلى الآمر)','12103','4292','0');
INSERT INTO [t19188] VALUES (4312,'٤٠٥٩ - مسألة: (وإن أمسك إنسانا لآخر ليقتله، فقتله، قتل القاتل، وحبس الممسك حتى يموت، فى إحدى الروايتين)','12106','4292','0');
INSERT INTO [t19188] VALUES (4313,'٤٠٦٠ - مسألة: (وإن كتفه وطرحه فى أرض مسبعة، أو ذات','12109','4292','0');
INSERT INTO [t19188] VALUES (4314,'فصل','12110','4292','0');
INSERT INTO [t19188] VALUES (4315,'٤٠٦١ - مسألة: (وفى شريك السبع وشريك نفسه وجهان)','12116','4292','0');
INSERT INTO [t19188] VALUES (4316,'٤٠٦٢ - مسألة: (ولو جرحه إنشان عمدا، فداوى جرحه بسم، أو خاطه فى اللحم، أو فعل ذلك وليه، أو الإمام، فمات، ففى وجوب القصاص على الجارح وجهان)','12118','4292','0');
INSERT INTO [t19188] VALUES (4317,'باب شروط القصاص','12121','4291','0');
INSERT INTO [t19188] VALUES (4318,'٤٠٦٣ - مسألة: (وفى السكران وشبهه روايتان؛ أصحهما،','12122','4317','0');
INSERT INTO [t19188] VALUES (4319,'فصل','12124','4317','0');
INSERT INTO [t19188] VALUES (4320,'٤٠٦٤ - مسألة: وكذلك المرتد لا يجب بقتله قصاص ولا دية ولا كفارة، وإن قتله ذمى','12124','4317','0');
INSERT INTO [t19188] VALUES (4321,'٤٠٦٥ - مسألة: (وإن قطع مسلم أو ذمى يد مرتد أو حربى، فأسلم، ثم مات)','12126','4317','0');
INSERT INTO [t19188] VALUES (4322,'٤٠٦٦ - مسألة: (وإن رمى حربيا، فأسلم قبل أن يقع به السهم،','12126','4317','0');
INSERT INTO [t19188] VALUES (4323,'٤٠٦٧ - مسألة: (ولو قطع يد مسلم، فارتد، ثم مات، فلا شئ على القاطع، فى أحد الوجهين)','12128','4317','0');
INSERT INTO [t19188] VALUES (4324,'٤٠٦٨ - مسألة: (وإن عاد إلى الإسلام، ثم مات، وجب القصاص)','12130','4317','0');
INSERT INTO [t19188] VALUES (4325,'فصل','12133','4317','0');
INSERT INTO [t19188] VALUES (4326,'٤٠٦٩ - مسألة: ويقتل العبد المسلم بالعبد المسلم، تساوت قيمتهما أو اختلفت. هذا قول أكثر أهل العلم. روى ذلك عن عمر ابن عبد العزيز، وسالم، والنخعى، والشعبى، والزهرى، وقتادة، والثورى، ومالك، والشافعى، وأبى حنيفة. وعن أحمد رواية أخرى، أن من شرط القصاص تساوى قيمتهم، وإن اختلفت قيمتهم لم يجر بينهم قصاص. وينبغى أن يختص هذا بما إذا كانت قيمة القاتل أكثر، فإن كانت أقل فلا. وهذا قول عطاء. وقال ابن عباس: ليس فى','12134','4317','0');
INSERT INTO [t19188] VALUES (4327,'٤٠٧٠ - مسألة: (ويقتل الذكر بالأنثى، والأنثى بالذكر)','12138','4317','0');
INSERT INTO [t19188] VALUES (4328,'٤٠٧١ - مسألة: (وعن أحمد، لا يقتل العبد بالعبد إلا أن تستوى قيمتهما. ولا عمل عليه)','12140','4317','0');
INSERT INTO [t19188] VALUES (4329,'٤٠٧٢ - مسألة: (ويقتل الكافر بالمسلم)','12140','4317','0');
INSERT INTO [t19188] VALUES (4330,'٤٠٧٣ - مسألة: (ولا يقتل مسلم بكافر)','12142','4317','0');
INSERT INTO [t19188] VALUES (4331,'٤٠٧٤ - مسألة: (ولا)','12145','4317','0');
INSERT INTO [t19188] VALUES (4332,'٤٠٧٥ - مسألة: (إلا أن يجرحه وهو مثله، أو يقتله ثم يسلم القاتل أو الجارح، أو يعتق فيموت المجروح، فإنه يقتل به)','12146','4317','0');
INSERT INTO [t19188] VALUES (4333,'٤٠٧٦ - مسألة: وإن جرح مسلم كافرا، فأسلم المجروح، ثم مات مسلما بسراية الجرح','12150','4317','0');
INSERT INTO [t19188] VALUES (4334,'٤٠٧٧ - مسألة: (وإن رمى مسلم ذميا عبدا، فلم يقع السهم به حتى عتق وأسلم، فلا قود، وعليه دية حر مسلم إذا مات من الرمية)','12153','4317','0');
INSERT INTO [t19188] VALUES (4335,'٤٠٧٨ - مسألة: (ولو قتل من يعرفه ذميا عبدا، فبان أنه قد عتق وأسلم، فعليه القصاص)','12162','4317','0');
INSERT INTO [t19188] VALUES (4336,'٤٠٧٩ - مسألة: (وإن كان يعرفه مرتدا، فكذلك عند أبى بكر)','12162','4317','0');
INSERT INTO [t19188] VALUES (4337,'فصل','12163','4317','0');
INSERT INTO [t19188] VALUES (4338,'٤٠٨٠ - مسألة: (ويقتل الولد بكل واحد منهما، فى أظهر الروايتين)','12168','4317','0');
INSERT INTO [t19188] VALUES (4339,'٤٠٨١ - مسألة: (متى ورث ولده القصاص أو شيئا منه، أو ورث القاتل شيئا من دمه، سقط القصاص)','12169','4317','0');
INSERT INTO [t19188] VALUES (4340,'٤٠٨٢ - مسألة: (ولو قتل رجل أخا زوجته، فورثته، ثم ماتت، فورثها ولده، سقط عنه القصاص)','12170','4317','0');
INSERT INTO [t19188] VALUES (4341,'٤٠٨٣ - مسألة: (ولو قتل أباه أو أخاه، فورثه أخواه، ثم قتل أحدهما صاحبه، سقط القصاص عن الأول؛ لأنه ورث بعض دم نفسه)','12171','4317','0');
INSERT INTO [t19188] VALUES (4342,'٤٠٨٤ - مسألة: (وإن قتل أحد الابنين أباه، والآخر أمه، وهى','12171','4317','0');
INSERT INTO [t19188] VALUES (4343,'٤٠٨٥ - مسألة: (وإن قتل من لا يعرف، وادعى كفره)','12175','4317','0');
INSERT INTO [t19188] VALUES (4344,'٤٠٨٦ - مسألة: (وإن قتل رجلا فى داره، وادعى أنه دخل','12175','4317','0');
INSERT INTO [t19188] VALUES (4345,'٤٠٨٧ - مسألة: (وإن تجارح اثنان، وادعى كل واحد)','12178','4317','0');
INSERT INTO [t19188] VALUES (4346,'باب استيفاء القصاص','12185','4291','0');
INSERT INTO [t19188] VALUES (4347,'٤٠٨٨ - مسألة: (فإن كانا محتاجين إلى النفقة، فهل لوليهما العفو إلى الدية؟ يحتمل وجهين)','12188','4346','0');
INSERT INTO [t19188] VALUES (4348,'٤٠٨٩ - مسألة: (فإن قتلا قاتل أبيهما، أو قطعا قاطعهما قهرا، احتمل أن يسقط حقهما، واحتمل أن تجب دية أبيهما لهما فى مال الجانى)','12189','4346','0');
INSERT INTO [t19188] VALUES (4349,'فصل','12190','4346','0');
INSERT INTO [t19188] VALUES (4350,'٤٠٩٠ - مسألة: (وإن اقتصا ممن لا تحمل ديته العاقلة)','12190','4346','0');
INSERT INTO [t19188] VALUES (4351,'٤٠٩١ - مسألة: (فإن فعل، فلا قصاص عليه)','12191','4346','0');
INSERT INTO [t19188] VALUES (4352,'٤٠٩٢ - مسألة: (وعليه لشركائه حقهم من الدية، وتسقط عن الجانى فى أحد الوجهين. وفى الآخر، لهم ذلك فى تركة الجانى، ويرجع ورثة الجانى على قاتله)','12191','4346','0');
INSERT INTO [t19188] VALUES (4353,'٤٠٩٣ - مسألة: (وإن عفا بعضهم، سقط القصاص وإن كان','12193','4346','0');
INSERT INTO [t19188] VALUES (4354,'٤٠٩٤ - مسألة: (وإن قتله الباقون عالمين بالعفو وسقوط القصاص به، فعليهم القود، وإلا فلا قود، وعليهم ديته)','12197','4346','0');
INSERT INTO [t19188] VALUES (4355,'٤٠٩٥ - مسألة: (وسواء كان الجميع حاضرين أو بعضهم غائبا)','12199','4346','0');
INSERT INTO [t19188] VALUES (4356,'٤٠٩٦ - مسألة: (وإن كان بعضهم صغيرا أو مجنونا، فليس للبالغ العاقل الاستيفاء حتى يصيرا مكلفين، فى المشهور. وعنه، له ذلك)','12200','4346','0');
INSERT INTO [t19188] VALUES (4357,'٤٠٩٧ - مسألة: (وكل من ورث المال ورث القصاص، على','12202','4346','0');
INSERT INTO [t19188] VALUES (4358,'٤٠٩٨ - مسألة: (ومن لا وارث له وليه الإمام، إن شاء اقتص، وإن شاء عفا)','12203','4346','0');
INSERT INTO [t19188] VALUES (4359,'فصل','12205','4346','0');
INSERT INTO [t19188] VALUES (4360,'٤٠٩٩ - مسألة: (وحكم الحد فى ذلك حكم القصاص)','12208','4346','0');
INSERT INTO [t19188] VALUES (4361,'٤١٠٠ - مسألة: (وإن ادعت الحمل)','12208','4346','0');
INSERT INTO [t19188] VALUES (4362,'٤١٠١ - مسألة: (وإن اقتص من حامل، وجب ضمان جنينها','12209','4346','0');
INSERT INTO [t19188] VALUES (4363,'فصل','12212','4346','0');
INSERT INTO [t19188] VALUES (4364,'٤١٠٢ - مسألة: (وعليه تفقد الآلة، فإن كانت كالة','12214','4346','0');
INSERT INTO [t19188] VALUES (4365,'٤١٠٣ - مسألة: (وينظر)','12214','4346','0');
INSERT INTO [t19188] VALUES (4366,'٤١٠٤ - مسألة: (وإن)','12215','4346','0');
INSERT INTO [t19188] VALUES (4367,'٤١٠٥ - مسألة: (فإن احتاج)','12216','4346','0');
INSERT INTO [t19188] VALUES (4368,'٤١٠٦ - مسألة: (والولى مخير بين الاستيفاء بنفسه إن كان يحسن، وبين التوكيل)','12217','4346','0');
INSERT INTO [t19188] VALUES (4369,'٤١٠٧ - مسألة: (وإن تشاح أولياء المقتول فى الاستيفاء، قدم أحدهم بالقرعة)','12218','4346','0');
INSERT INTO [t19188] VALUES (4370,'فصل','12220','4346','0');
INSERT INTO [t19188] VALUES (4371,'٤١٠٨ - مسألة: (فإن قتله بمحرم فى نفسه؛ كتجريع الخمر، واللواط، ونحوه، قتل بالسيف، رواية واحدة)','12229','4346','0');
INSERT INTO [t19188] VALUES (4372,'٤١٠٩ - مسألة: (ولا تجوز الزيادة على ما أتى به، رواية واحدة، ولا قطع شئ من أطرافه، فإن فعل، فلا قصاص فيه، وتجب فيه ديته، سواء عفا عنه أو قتله)','12230','4346','0');
INSERT INTO [t19188] VALUES (4373,'فصل','12235','4346','0');
INSERT INTO [t19188] VALUES (4374,'٤١١٠ - مسألة: (وإن تشاحوا فى من يقتله منهم على الكمال، أقيد للأول)','12237','4346','0');
INSERT INTO [t19188] VALUES (4375,'٤١١١ - مسألة: (وإن قتل وقطع طرفا، قطع طرفه)','12238','4346','0');
INSERT INTO [t19188] VALUES (4376,'٤١١٢ - مسألة: (وإن قطع أيدى جماعة، فحكمه حكم القتل)','12241','4346','0');
INSERT INTO [t19188] VALUES (4377,'باب العفو عن القصاص','12242','4291','0');
INSERT INTO [t19188] VALUES (4378,'٤١١٣ - مسألة: (والواجب بقتل العمد أحد شيئين؛ القصاص أو الآية، فى ظاهر المذهب، والخيرة فى ذلك إلى الولى، إن شاء اقتص، وإن شاء أخذ الدية، وإن شاء عفا إلى غير شئ، والعفو أفضل)','12243','4377','0');
INSERT INTO [t19188] VALUES (4379,'٤١١٤ - مسألة: (وإن مات القاتل، وجبت الدية فى تركته)','12251','4377','0');
INSERT INTO [t19188] VALUES (4380,'٤١١٥ - مسألة: (وإن قطع إصبعا عمدا، فعفا عنه، ثم سرت إلى الكف أو النفس، وكان العفو على مال، فله تمام الدية، وإن عفا على غير مال، فلا شئ له، على ظاهر كلامه. ويحتمل أن له تمام الدية. وإن عفا مطلقا، انبنى على الروايتين فى موجب العمد)','12252','4377','0');
INSERT INTO [t19188] VALUES (4381,'٤١١٦ - مسألة: (وإن قال الجانى: عفوت مطلقا. أو: عفوت عنها وعن سرايتها. قال: بل عفوت إلى مال. أو: عفوت عنها دون سرايتها. فالقول قول المجنى عليه)','12256','4377','0');
INSERT INTO [t19188] VALUES (4382,'٤١١٧ - مسألة: (وإن قتل الجانى العافى)','12257','4377','0');
INSERT INTO [t19188] VALUES (4383,'٤١١٨ - مسألة: (وإذا وكل رجلا فى القصاص ثم عفا، ولم يعلم الوكيل حتى اقتص، فلا شئ عليه. وهل يضمن العافى؟ يحتمل وجهين. ويتخرج أن يضمن الوكيل، ويرجع به على الموكل فى أحد','12258','4377','0');
INSERT INTO [t19188] VALUES (4384,'٤١١٩ - مسألة: (وإن عفا عن قاتله بعد الجرح، صح)','12263','4377','0');
INSERT INTO [t19188] VALUES (4385,'٤١٢٠ - مسألة: (وإن أبرأه من الدية أو وصى له بها، فهى وصية لقاتل، هل تصح؟ على روايتين؛ إحداهما، تصح)','12265','4377','0');
INSERT INTO [t19188] VALUES (4386,'٤١٢١ - مسألة: (ويحتمل أن لا يصح عفوه عن المال، ولا وصيته','12265','4377','0');
INSERT INTO [t19188] VALUES (4387,'٤١٢٢ - مسألة: (وإن أبرأ القاتل من الدية الواجبة على عاقلته، أو العبد من الجناية المتعلق أرشها برقبته، لم يصح)','12266','4377','0');
INSERT INTO [t19188] VALUES (4388,'٤١٢٣ - مسألة: (وإن وجب لعبد قصاص)','12267','4377','0');
INSERT INTO [t19188] VALUES (4389,'باب ما يوجب القصاص فيما دون النفس','12268','4291','0');
INSERT INTO [t19188] VALUES (4390,'٤١٢٤ - مسألة: (ولا يجب إلا بمثل الموجب فى النفس، وهو العمد المحض)','12270','4389','0');
INSERT INTO [t19188] VALUES (4391,'٤١٢٥ - مسألة: (وهو نوعان؛ أحدهما، الأطراف، فتؤخذ العين بالعين، والأنف بالأنف، والأذن بالأذن، والسن بالسن، والجفن بالجفن، والشفة بالشفة، واليد باليد، والرجل بالرجل)','12271','4389','0');
INSERT INTO [t19188] VALUES (4392,'٤١٢٦ - مسألة: وتقلع العين بالعين','12271','4389','0');
INSERT INTO [t19188] VALUES (4393,'٤١٢٧ - مسألة: (و)','12274','4389','0');
INSERT INTO [t19188] VALUES (4394,'٤١٢٨ - مسألة: (و)','12277','4389','0');
INSERT INTO [t19188] VALUES (4395,'٤١٢٩ - مسألة: (و)','12277','4389','0');
INSERT INTO [t19188] VALUES (4396,'٤١٣٠ - مسألة: (و)','12277','4389','0');
INSERT INTO [t19188] VALUES (4397,'٤١٣١ - مسألة: (ويؤخذ كل واحد من الأصابع والكف والمرفق والذكر والأنثيين بمثله)','12281','4389','0');
INSERT INTO [t19188] VALUES (4398,'٤١٣٢ - مسألة: ويجرى القصاص فى الأنثيين','12282','4389','0');
INSERT INTO [t19188] VALUES (4399,'٤١٣٣ - مسألة: (وهل يجرى القصاص فى الألية والشفر؟ على وجهين)','12282','4389','0');
INSERT INTO [t19188] VALUES (4400,'٤١٣٤ - مسألة: وفى القصاص فى شفرى المرأة وجهان','12283','4389','0');
INSERT INTO [t19188] VALUES (4401,'فصل','12284','4389','0');
INSERT INTO [t19188] VALUES (4402,'٤١٣٥ - مسألة: (ويشترط للقصاص فى الطرف ثلاثة شروط؛ أحدها، أن يكون القطع من مفصل، أو له حد ينتهى إليه، كمارن الأنف، وهو ما لان منه، فإن قطع القصبة، أو قطع من نصف الساعد أو الساق، فلا قصاص فى أحد الوجهين، وفى الآخر، يقتص من حد المارن، ومن الكوع والكعب. وهل يجب له أرش الباقى؟ على وجهين)','12284','4389','0');
INSERT INTO [t19188] VALUES (4403,'٤١٣٦ - مسألة: (وإذا أوضح إنسانا، فذهب ضوء عينه، أو سمعه، أو شمه، فإنه يوضحه)','12291','4389','0');
INSERT INTO [t19188] VALUES (4404,'فصل','12294','4389','0');
INSERT INTO [t19188] VALUES (4405,'٤١٣٧ - مسألة: (و)','12294','4389','0');
INSERT INTO [t19188] VALUES (4406,'٤١٣٨ - مسألة: (فلو قطع أنملة رجل العليا، وقطع الوسطى من آخر ليس له عليا، فصاحب الوسطى مخير بين أخذ عقل أنملته، وبين أن يصبر حتى يقطع العليا، ثم يقتص من الوسطى)','12295','4389','0');
INSERT INTO [t19188] VALUES (4407,'٤١٣٩ - مسألة: (ولا تؤخذ أصلية بزائدة، ولا زائدة بأصلية)','12297','4389','0');
INSERT INTO [t19188] VALUES (4408,'٤١٤٠ - مسألة: فلو تراضيا على قطع إحدى اليدين بدلا عن الأخرى، فقطعها المقتص، سقط القود، لأن القود سقط فى الأولى بإسقاط صاحبها، وفى الثانية بإذن صاحبها فى قطعها، وديتهما','12298','4389','0');
INSERT INTO [t19188] VALUES (4409,'٤١٤١ - مسألة: وإن (قال)','12299','4389','0');
INSERT INTO [t19188] VALUES (4410,'٤١٤٢ - مسألة: (وإن أخرجها دهشة، أو ظنا)','12300','4389','0');
INSERT INTO [t19188] VALUES (4411,'٤١٤٣ - مسألة: (وإن كان من عليه القصاص مجنونا)','12302','4389','0');
INSERT INTO [t19188] VALUES (4412,'٤١٤٤ - مسألة: وإن كان من له القصاص مجنونا، ومن عليه القصاص عاقلا','12302','4389','0');
INSERT INTO [t19188] VALUES (4413,'فصل','12303','4389','0');
INSERT INTO [t19188] VALUES (4414,'٤١٤٥ - مسألة: (ولا)','12306','4389','0');
INSERT INTO [t19188] VALUES (4415,'٤١٤٦ - مسألة: (ولا)','12306','4389','0');
INSERT INTO [t19188] VALUES (4416,'٤١٤٧ - مسألة: (إلا مارن الأشم الصحيح)','12308','4389','0');
INSERT INTO [t19188] VALUES (4417,'٤١٤٨ - مسألة: (وأذن السميع بأذن الأصم)','12309','4389','0');
INSERT INTO [t19188] VALUES (4418,'٤١٤٩ - مسألة: (ويؤخذ المعيب من ذلك كله بالصحيح وبمثله، إذا أمن من قطع الشلاء التلف)','12310','4389','0');
INSERT INTO [t19188] VALUES (4419,'٤١٥٠ - مسألة: وتؤخذ الناقصة بالناقصة، إذا تساوتا فيه، بأن يكون المقطوع من يد الجانى كالمقطوع من يد المجنى عليه؛ لأنهما تساوتا فى الذات والصفة. فإن اختلفا، فكان المقطوع من يد أحدهما الإبهام، ومن الأخرى','12312','4389','0');
INSERT INTO [t19188] VALUES (4420,'٤١٥١ - مسألة: وتؤخذ الناقصة بالكاملة؛ لأنها دون حقه. وهل له أخذ دية الأصابع الناقصة؟','12312','4389','0');
INSERT INTO [t19188] VALUES (4421,'فصل','12316','4389','0');
INSERT INTO [t19188] VALUES (4422,'٤١٥٢ - مسألة: (وإن اختلفا فى شلل العضو وصحته)','12316','4389','0');
INSERT INTO [t19188] VALUES (4423,'٤١٥٣ - مسألة: (وإن كسر بعض سنه، برد من سن الجانى متله، إذا أمن قلعها)','12317','4389','0');
INSERT INTO [t19188] VALUES (4424,'٤١٥٤ - مسألة: (ولا يقتص من السن حتى ييأس من عودها)','12320','4389','0');
INSERT INTO [t19188] VALUES (4425,'٤١٥٥ - مسألة: فإن عاد بدل السن على صفتها فى موضعها، فلا شئ على الجانى','12320','4389','0');
INSERT INTO [t19188] VALUES (4426,'٤١٥٦ - مسألة: (فإن مات)','12320','4389','0');
INSERT INTO [t19188] VALUES (4427,'٤١٥٧ - مسألة: فإن قلع','12321','4389','0');
INSERT INTO [t19188] VALUES (4428,'٤١٥٨ - مسألة: (وإن اقتص من سن فعادت، غرم سن الجانى)','12321','4389','0');
INSERT INTO [t19188] VALUES (4429,'٤١٥٩ - مسألة: (وإن عادت سن المجنى عليه قصيرة أو معيبة، فعلى الجانى أرش نقصها)','12322','4389','0');
INSERT INTO [t19188] VALUES (4430,'فصل','12323','4389','0');
INSERT INTO [t19188] VALUES (4431,'٤١٦٠ - مسألة: (ولا يجب)','12325','4389','0');
INSERT INTO [t19188] VALUES (4432,'٤١٦١ - مسألة: (إلا أن يكون أعظم من الموضحة، كالهاشمة والمنقلة والمأمومة، فله أن يقتص موضحة)','12327','4389','0');
INSERT INTO [t19188] VALUES (4433,'٤١٦٢ - مسألة: (ولا شئ له)','12327','4389','0');
INSERT INTO [t19188] VALUES (4434,'٤١٦٣ - مسألة: (ويعتبر قدر الجرح بالمساحة، فلو أوضح إنسانا فى بعض رأسه، مقدار ذلك البعض جميع رأس الشاج وزيادة، كان له أن يوضحه فى جميع رأسه، وفى الأرش للزائد وجهان)','12328','4389','0');
INSERT INTO [t19188] VALUES (4435,'فصل','12333','4389','0');
INSERT INTO [t19188] VALUES (4436,'٤١٦٤ - مسألة: (وإن تفرقت أفعالهم، أو قطع كل واحد من جانب، فلا قصاص)','12337','4389','0');
INSERT INTO [t19188] VALUES (4437,'٤١٦٥ - مسألة: (وسراية الجناية مضمونة بالقصاص أو الدية)','12338','4389','0');
INSERT INTO [t19188] VALUES (4438,'٤١٦٦ - مسألة: (وإن شل، ففيه ديته)','12339','4389','0');
INSERT INTO [t19188] VALUES (4439,'٤١٦٧ - مسألة: (وسراية القود غير مضمونة، فلو قطع اليد','12340','4389','0');
INSERT INTO [t19188] VALUES (4440,'٤١٦٨ - مسألة: (ولا يقتص من الطرف إلا بعد برئه)','12342','4389','0');
INSERT INTO [t19188] VALUES (4441,'٤١٦٩ - مسألة: فإن فعل ذلك، سقط حقه من سرايته (فلو سرى إلى نفسه، كان هدرا، ولو سرى القصاص إلى نفس الجانى، كان هدرا أيضا)','12344','4389','0');
INSERT INTO [t19188] VALUES (4442,'كتاب الديات','12348','0','0');
INSERT INTO [t19188] VALUES (4443,'[باب. . .]','12348','4442','0');
INSERT INTO [t19188] VALUES (4444,'٤١٧٠ - مسألة: (كل من أتلف إنسانا أو جزءا منه، بمباشرة أو سبب، فعليه ديته)','12349','4443','0');
INSERT INTO [t19188] VALUES (4445,'٤١٧١ - مسألة: (فإن كان)','12349','4443','0');
INSERT INTO [t19188] VALUES (4446,'٤١٧٢ - مسألة: (وإن كان شبه عمد، أو خطأ، أو ما جرى مجراه، فعلى عاقلته)','12351','4443','0');
INSERT INTO [t19188] VALUES (4447,'٤١٧٣ - مسألة: (ولو ألقى على إنسان أفعى، أو ألقاه عليها،','12354','4443','0');
INSERT INTO [t19188] VALUES (4448,'٤١٧٤ - مسألة: فإن طلب إنسانا بسيف مشهور','12355','4443','0');
INSERT INTO [t19188] VALUES (4449,'٤١٧٥ - مسألة: وإن حفر فى فنائه بئرا لنفسه، أو فى طريق لغير مصلحة المسلمين، أو فى ملك غيره بغير إذنه، أو وضع فى ذلك حجرا، أو صب فيه ماء، أو رمى قشر بطيخ فهلك به إنسان، ضمنه؛ لأنه تلف بعدوانه. وروى عن شريح أنه ضمن رجلا حفر بئرا، فوقع فيها رجل فمات. وروى ذلك (١)','12357','4443','0');
INSERT INTO [t19188] VALUES (4450,'٤١٧٦ - مسألة: وإن بالت فيها دابته، فزلق به حيوان','12357','4443','0');
INSERT INTO [t19188] VALUES (4451,'٤١٧٧ - مسألة: (وإن حفر بئرا، ووضع آخر حجرا)','12358','4443','0');
INSERT INTO [t19188] VALUES (4452,'٤١٧٨ - مسألة: (وإن غصب صغيرا، فنهشته حية، أو أصابته صاعقة، ففيه الدية)','12363','4443','0');
INSERT INTO [t19188] VALUES (4453,'٤١٧٩ - مسألة: (وإن اصطدم نفسان، فماتا، فعلى عاقلة كل واحد منهما دية الآخر)','12364','4443','0');
INSERT INTO [t19188] VALUES (4454,'٤١٨٠ - مسألة: (وإن كانا راكبين، فماتت الدابتان، فعلى كل','12365','4443','0');
INSERT INTO [t19188] VALUES (4455,'٤١٨١ - مسألة: (إلا أن يكون أحدهما يسير، والآخر واقفا، فعلى السائر ضمان الواقف ودابته)','12367','4443','0');
INSERT INTO [t19188] VALUES (4456,'٤١٨٢ - مسألة: (إلا أن يكون فى طريق ضيق، قاعدا أو واقفا، فلا ضمان فيه، وعليه ضمان ما تلف به)','12367','4443','0');
INSERT INTO [t19188] VALUES (4457,'٤١٨٣ - مسألة: (وإن أركب صبيين لا ولاية له عليهما، فاصطدما، فماتا، فعلى عاقلته ديتهما)','12369','4443','0');
INSERT INTO [t19188] VALUES (4458,'٤١٨٤ - مسألة: (وإن رمى ثلاثة بمنجنيق، فقتل الحجر','12370','4443','0');
INSERT INTO [t19188] VALUES (4459,'٤١٨٥ - مسألة: (وإن كانوا أكثر من ثلاثة، فالدية حالة فى أموالهم)','12375','4443','0');
INSERT INTO [t19188] VALUES (4460,'٤١٨٦ - مسألة: (وإن جنى إنسان على نفسه أو طرفه خطأ، فلا شئ له. وعنه، على عاقلته ديته لورثته، ودية طرفه لنفسه)','12377','4443','0');
INSERT INTO [t19188] VALUES (4461,'٤١٨٧ - مسألة: (وإن نزل رجل فى بئر، فخر عليه آخر، فمات الأول من سقطته، فعلى عاقلته ديته)','12379','4443','0');
INSERT INTO [t19188] VALUES (4462,'٤١٨٨ - مسألة: (وإن وقع)','12381','4443','0');
INSERT INTO [t19188] VALUES (4463,'٤١٨٩ - مسألة: (وإن كان الأول جذب الثانى، وجذب الثانى الثالث، فلا شئ على الثالث)','12382','4443','0');
INSERT INTO [t19188] VALUES (4464,'٤١٩٠ - مسألة: (وإن خر رجل فى زبية أسد، فجذب آخر،','12386','4443','0');
INSERT INTO [t19188] VALUES (4465,'٤١٩١ - مسألة: (ومن اضطر إلى طعام إنسان أو شرابه، وليس به مثل ضرورته، فمنعه حتى مات، ضمنه. نص عليه)','12391','4443','0');
INSERT INTO [t19188] VALUES (4466,'٤١٩٢ - مسألة: (وإن أفزع إنسانا، فأحدث بغائط، فعليه ثلث ديته. وعنه، لا شئ عليه)','12394','4443','0');
INSERT INTO [t19188] VALUES (4467,'فصل','12398','4443','0');
INSERT INTO [t19188] VALUES (4468,'٤١٩٣ - مسألة: (ويتخرج وجوب الضمان، على ما قاله فيما','12398','4443','0');
INSERT INTO [t19188] VALUES (4469,'٤١٩٤ - مسألة: (وإن سلم ولده إلى السابح ليعلمه، فغرق، لم يضمنه. ويحتمل أن تضمنه العاقلة)','12402','4443','0');
INSERT INTO [t19188] VALUES (4470,'٤١٩٥ - مسألة: (وإن أمر إنسانا)','12403','4443','0');
INSERT INTO [t19188] VALUES (4471,'٤١٩٦ - مسألة: (وإن وضع جرة على سطحه)','12404','4443','0');
INSERT INTO [t19188] VALUES (4472,'٤١٩٧ - مسألة: وإن أخرج جناحا إلى الطريق أو ميزابا، فسقط على إنسان فأتلفه، ضمنه؛ لأن إخراج الجناح إلى الطريق غير جائز؛ لأنه تصرف فى غير ملكه، إذا كان الطريق نافذا، أو غير نافذ ولم يأذن فيه أصحابه. إذا سقط على شئ فأتلفه، ضمنه؛ لأنه تلف بعدوانه، فضمنه، كما لو وضع البناء على أرض الطريق. وكذلك الحكم فى الميزاب. وفى ذلك اختلاف وتفصيل ذكرناه فى الغصب','12404','4443','0');
INSERT INTO [t19188] VALUES (4473,'باب مقادير ديات النفس','12405','4442','0');
INSERT INTO [t19188] VALUES (4474,'٤١٩٨ - مسألة: (وفى الحلل روايتان، إحداهما، ليست أصلا)','12408','4473','0');
INSERT INTO [t19188] VALUES (4475,'٤١٩٩ - مسألة: (وعن أحمد)','12409','4473','0');
INSERT INTO [t19188] VALUES (4476,'٤٢٠٠ - مسألة: وإذا قلنا: إن الإبل هى الأصل خاصة. فعلى من عليه الدية تسليمها إلى مستحقها سليمة من العيوب، وأيهما أراد العدول عنها إلى غيرها، فللآخر منعه، لأن الحق متعين فيها، فاستحقت، كالمثل فى المثليات','12411','4473','0');
INSERT INTO [t19188] VALUES (4477,'٤٢٠١ - مسألة: (فإن كان القتل عمدا، أو شبه عمد، وجبت أرباعا؛ خمس وعشرون بنت مخاض، وخمس وعشرون بنت لبون، وخمس وعشرون حقة، وخمس وعشرون جذعة. وعنه، أنها ثلاثون حقة، وثلاثون جذعة، وأربعون خلفة فى بطونها أولادها)','12412','4473','0');
INSERT INTO [t19188] VALUES (4478,'٤٢٠٢ - مسألة: (وإن كان)','12416','4473','0');
INSERT INTO [t19188] VALUES (4479,'٤٢٠٣ - مسألة: (ويؤخذ فى البقر النصف مسنات، والنصف أتبعة، وفى الغنم النصف ثنايا، والنصف أجذعة)','12418','4473','0');
INSERT INTO [t19188] VALUES (4480,'٤٢٠٤ - مسألة: (ولا تعتبر القيمة فى شئ من ذلك إذا كان سليما من العيوب. وقال أبو الخطاب: يعتبر أن يكون قيمة كل بعير مائة وعشرين درهما. فظاهر هذا أنه يعتبر فى الأصول كلها أن تبلغ','12419','4473','0');
INSERT INTO [t19188] VALUES (4481,'٤٢٠٥ - مسألة: (ويؤخذ فى الحلل المتعارف)','12424','4473','0');
INSERT INTO [t19188] VALUES (4482,'فصل','12426','4473','0');
INSERT INTO [t19188] VALUES (4483,'٤٢٠٦ - مسألة: (ويساوى جراح المرأة جراح الرجل إلى ثلث الدية، فإذا زادت، صارت على النصف)','12427','4473','0');
INSERT INTO [t19188] VALUES (4484,'٤٢٠٧ - مسألة: (ودية الخنثى المشكل نصف دية ذكر','12430','4473','0');
INSERT INTO [t19188] VALUES (4485,'فصل','12431','4473','0');
INSERT INTO [t19188] VALUES (4486,'٤٢٠٨ - مسألة: (وجراحاتهم)','12435','4473','0');
INSERT INTO [t19188] VALUES (4487,'٤٢٠٩ - مسألة: (ونساؤهم على النصف من دياتهم)','12435','4473','0');
INSERT INTO [t19188] VALUES (4488,'٤٢١٠ - مسألة: (ودية المجوسى والوثنى ثمانمائة درهم)','12436','4473','0');
INSERT INTO [t19188] VALUES (4489,'٤٢١١ - مسألة: فأما عبدة الأوثان، وسائر من ليس له كتاب، كالترك، ومن عبد ما استحسن، فلا ذمة','12437','4473','0');
INSERT INTO [t19188] VALUES (4490,'٤٢١٢ - مسألة: (ومن لم تبلغه الدعوة، فلا ضمان فيه)','12438','4473','0');
INSERT INTO [t19188] VALUES (4491,'فصل','12439','4473','0');
INSERT INTO [t19188] VALUES (4492,'٤٢١٣ - مسألة: (وفى جراحه إن لم يكن مقدرا فى الحر، ما نقصه)','12442','4473','0');
INSERT INTO [t19188] VALUES (4493,'٤٢١٤ - مسألة: (ومن نصفه حر، ففيه نصف دية حر ونصف قيمته، وكذلك فى جراحه)','12446','4473','0');
INSERT INTO [t19188] VALUES (4494,'٤٢١٥ - مسألة: (وإذا قطع خصيتى عبد، أو أنفه، أو أذنيه، لزمته قيمته للسيد، ولم يزل ملك السيد عنه، وإن قطع ذكره، ثم خصاه، لزمته قيمته لقطع الذكر، وقيمته مقطوع الذكر، وملك سيده باق عليه)','12447','4473','0');
INSERT INTO [t19188] VALUES (4495,'فصل','12448','4473','0');
INSERT INTO [t19188] VALUES (4496,'٤٢١٦ - مسألة: (ولا يقبل فى الغرة خنثى، ولا معيب، ولا من له دون سبع سنين)','12457','4473','0');
INSERT INTO [t19188] VALUES (4497,'٤٢١٧ - مسألة: (وإن كان الجنين مملوكا، ففيه عشر قيمة أمه، ذكرا كان أو أنثى)','12459','4473','0');
INSERT INTO [t19188] VALUES (4498,'٤٢١٨ - مسألة: (وإن ضرب بطن أمة، فعتقت، ثم أسقطت الجنين، ففيه غرة)','12466','4473','0');
INSERT INTO [t19188] VALUES (4499,'٤٢١٩ - مسألة: (وإن كان الجنين محكوما بكفره، ففيه عشر دية أمه)','12469','4473','0');
INSERT INTO [t19188] VALUES (4500,'٤٢٢٠ - مسألة: (وإن كان أحد أبويه كتابيا، والآخر مجوسيا، اعتبر أكثرهما)','12469','4473','0');
INSERT INTO [t19188] VALUES (4501,'٤٢٢١ - مسألة: (وإن سقط الجنين حيا، ثم مات، ففيه دية حر إن كان حرا، أو قيمته إن كان مملوكا، إذا كان سقوطه لوقت يعيش','12470','4473','0');
INSERT INTO [t19188] VALUES (4502,'٤٢٢٢ - مسألة: (وإن اختلفا فى حياته، ولا بينة)','12474','4473','0');
INSERT INTO [t19188] VALUES (4503,'فصل','12481','4473','0');
INSERT INTO [t19188] VALUES (4504,'٤٢٢٣ - مسألة: (وظاهر كلام الخرقى أن الدية لا تغلظ لشئ من ذلك)','12485','4473','0');
INSERT INTO [t19188] VALUES (4505,'٤٢٢٤ - مسألة: (وإن قتل المسلم كافرا عمدا، أضعفت الدية)','12488','4473','0');
INSERT INTO [t19188] VALUES (4506,'فصل','12490','4473','0');
INSERT INTO [t19188] VALUES (4507,'٤٢٢٥ - مسألة: وان كانت الجناية أكثر من قيمته','12492','4473','0');
INSERT INTO [t19188] VALUES (4508,'٤٢٢٦ - مسألة: (وإن سلمه)','12493','4473','0');
INSERT INTO [t19188] VALUES (4509,'٤٢٢٧ - مسألة: (وإن جنى عمدا، فعفا الولى عن القصاص على رقبته، فهل يملكه بغير رضا السيد؟ على روايتين)','12494','4473','0');
INSERT INTO [t19188] VALUES (4510,'٤٢٢٨ - مسألة: (وإن جنى على اثنين خطأ، اشتركا فيه بالحصص)','12495','4473','0');
INSERT INTO [t19188] VALUES (4511,'٤٢٢٩ - مسألة: (فإن عفا أحدهما، أو مات المجنى عليه، فعفا بعض ورثته، فهل يتعلق حق الباقين بجميع العبد أو بحصتهم منه؟ على وجهين)','12496','4473','0');
INSERT INTO [t19188] VALUES (4512,'٤٢٣٠ - مسألة: (وإن جرح)','12497','4473','0');
INSERT INTO [t19188] VALUES (4513,'باب ديات الأعضاء ومنافعها','12501','4442','0');
INSERT INTO [t19188] VALUES (4514,'٤٢٣١ - مسألة: (وما فيه منه شيئان، ففيهما الدية، وفى أحدهما نصفها؛ كالعينين، والأذنين، والشفتين، واللحيين، وثديى المرأة، وثندوتى الرجل، واليدين، والرجلين، والخصيتين، والأليتين)','12502','4513','0');
INSERT INTO [t19188] VALUES (4515,'٤٢٣٢ - مسألة: وفى الثديين الدية. ولا نعلم خلافا فى أن فى ثديى المرأة الدية، وفى الواحد منهما نصف الدية. قال ابن المنذر','12504','4513','0');
INSERT INTO [t19188] VALUES (4516,'٤٢٣٣ - مسألة: وفى قطع حلمتى الثديين ديتهما','12504','4513','0');
INSERT INTO [t19188] VALUES (4517,'٤٢٣٤ - مسألة: وفى ثديى الرجل -وهما الثندوتان- الدية. وبه قال إسحاق. وحكى ذلك قولا للشافعى. [وقال النخعى، ومالك، وأصحاب الرأى، وابن المنذر: فيهما حكومة. وهو ظاهر مذهب الشافعى]','12505','4513','0');
INSERT INTO [t19188] VALUES (4518,'٤٢٣٥ - مسألة: وفى العينين الدية. أجمع أهل العلم على ذلك، وعلى أن فى العين الواحدة نصفها؛ لقول النبى -صلى الله عليه وسلم-: «وفى العينين الدية»','12506','4513','0');
INSERT INTO [t19188] VALUES (4519,'٤٢٣٦ - مسألة: وفى الأذنين الدية','12507','4513','0');
INSERT INTO [t19188] VALUES (4520,'٤٢٣٧ - مسألة: وفى اللحيين الدية','12509','4513','0');
INSERT INTO [t19188] VALUES (4521,'٤٢٣٨ - مسألة: وفى الأليتين الدية. قال ابن المنذر','12509','4513','0');
INSERT INTO [t19188] VALUES (4522,'٤٢٣٩ - مسألة: وفى الأنثيين الدية. لا نعلم فى هذا خلافا. وفى كتاب النبى -صلى الله عليه وسلم- لعمرو بن حزم: «وفى البيضتين الدية»','12510','4513','0');
INSERT INTO [t19188] VALUES (4523,'٤٢٤٠ - مسألة: (وفى إسكتى المرأة)','12511','4513','0');
INSERT INTO [t19188] VALUES (4524,'٤٢٤١ - مسألة: وفى اللسان الدية إذا كان ناطقا','12512','4513','0');
INSERT INTO [t19188] VALUES (4525,'٤٢٤٢ - مسألة: (وفى المنخرين ثلثا الدية، وفى الحاجز ثلثها. وعنه، فى المنخرين الدية، وفى الحاجز حكومة)','12514','4513','0');
INSERT INTO [t19188] VALUES (4526,'٤٢٤٣ - مسألة: (وفى الأجفان الأربعة الدية، وفى كل واحد ربعها)','12516','4513','0');
INSERT INTO [t19188] VALUES (4527,'٤٢٤٤ - مسألة: (وفى أصابع اليدين الدية، وكذلك أصابع الرجلين، وفى كل إصبع عشرها)','12516','4513','0');
INSERT INTO [t19188] VALUES (4528,'٤٢٤٥ - مسألة: (وفى كل أنملة ثلث عقلها)','12516','4513','0');
INSERT INTO [t19188] VALUES (4529,'٤٢٤٦ - مسألة: (وفى الظفر خمس دية الإصبع)','12517','4513','0');
INSERT INTO [t19188] VALUES (4530,'٤٢٤٧ - مسألة: (وفى كل سن خمس من الإبل، إذا قلعت ممن قد أثغر)','12517','4513','0');
INSERT INTO [t19188] VALUES (4531,'٤٢٤٨ - مسألة: (إذا قلعت ممن قد ثغر)','12521','4513','0');
INSERT INTO [t19188] VALUES (4532,'٤٢٤٩ - مسألة: (وتجب دية اليد والرجل فى قطعهما من','12524','4513','0');
INSERT INTO [t19188] VALUES (4533,'٤٢٥٠ - مسألة: (وفى مارن الأنف، وحشفة الذكر، وحلمتى الثديين، دية العضو كاملة)','12529','4513','0');
INSERT INTO [t19188] VALUES (4534,'٤٢٥١ - مسألة: وفى الذكر الدية. أجمع أهل العلم على ذلك؛ لأن فى كتاب النبى -صلى الله عليه وسلم- فى لعمرو بن حزم: «وفى الذكر الدية»','12530','4513','0');
INSERT INTO [t19188] VALUES (4535,'٤٢٥٢ - مسألة: (وفى كسر ظاهر السن ديتها)','12530','4513','0');
INSERT INTO [t19188] VALUES (4536,'٤٢٥٣ - مسألة: (ويحتمل أن يلزم من استوعب الأنف جدعا دية، وحكومة فى القصبة)','12532','4513','0');
INSERT INTO [t19188] VALUES (4537,'٤٢٥٤ - مسألة: (وفى قطع بعض المارن، والأذن، والحلمة، واللسان، والشفة، والحشفة، والأنملة، وشق الحشفة طولا، بالحساب من ديته، يقدر بالأجزاء)','12533','4513','0');
INSERT INTO [t19188] VALUES (4538,'٤٢٥٥ - مسألة: (وفى شلل العضو وإذهاب نفعه، والجناية على الشفتين، بحيث لا ينطبقان على الأسنان)','12534','4513','0');
INSERT INTO [t19188] VALUES (4539,'٤٢٥٦ - مسألة: (و)','12535','4513','0');
INSERT INTO [t19188] VALUES (4540,'٤٢٥٧ - مسألة: (وفى العضو الأشل من اليد، والرجل، والذكر، والثدى، ولسان الأخرس، والعين القائمة،','12537','4513','0');
INSERT INTO [t19188] VALUES (4541,'٤٢٥٨ - مسألة: (وعنه فى ذكر الخصى والعنين كمال ديته)','12543','4513','0');
INSERT INTO [t19188] VALUES (4542,'٤٢٥٩ - مسألة: فإذا قلنا: لا تكمل الدية فى قطع ذكر الخصى. (إن قطع الذكر والأنثيين دفعة واحدة، أو قطع الذكر ثم قطع الأنثيين، لزمته ديتان، وإن قطع الأنثيين ثم قطع الذكر، لزمته دية واحدة للأنثيين،','12544','4513','0');
INSERT INTO [t19188] VALUES (4543,'٤٢٦٠ - مسألة: (وإن أشل الأنف، أو الأذن، أو عوجهما، ففيه حكومة. وفى قطع الأشل منهما كمال الدية)','12545','4513','0');
INSERT INTO [t19188] VALUES (4544,'٤٢٦١ - مسألة: (وتجب الدية فى أنف الأخشم والمخزوم)','12547','4513','0');
INSERT INTO [t19188] VALUES (4545,'٤٢٦٢ - مسألة: (وإن قطع أنفه، فذهب شمه، وجبت ديتان)','12548','4513','0');
INSERT INTO [t19188] VALUES (4546,'٤٢٦٣ - مسألة: (وسائر الأعضاء إذا أذهبها بمنفعتها، لم تجب إلا دية واحدة)','12548','4513','0');
INSERT INTO [t19188] VALUES (4547,'فصل فى دية المنافع','12550','4513','0');
INSERT INTO [t19188] VALUES (4548,'٤٢٦٤ - مسألة: وفى البصر الدية؛ لأن كل عضوين وجبت الدية بذهابهما، وجبت بذهاب نفعهما، كاليدين إذا أشلهما، وفى ذهاب بصر','12551','4513','0');
INSERT INTO [t19188] VALUES (4549,'٤٢٦٥ - مسألة: وفى الشم الدية؛ لأنه حاسة تختص بمنفعة، فكان فى ذهابها الدية، كسائر الحواس، ولا نعلم فى هذا خلافا. قال القاضى: فى كتاب عمرو بن حزم عن النبى -صلى الله عليه وسلم- أنه قال: «وفى المشام الدية»','12552','4513','0');
INSERT INTO [t19188] VALUES (4550,'٤٢٦٦ - مسألة: (وكذلك تجب فى الكلام، والعقل، والمشى، والأكل، والنكاح)','12553','4513','0');
INSERT INTO [t19188] VALUES (4551,'٤٢٦٧ - مسألة: وفى ذهاب العقل الدية. ولا نعلم فيه خلافا. روى ذلك','12553','4513','0');
INSERT INTO [t19188] VALUES (4552,'٤٢٦٨ - مسألة: وفى ذهاب المشى الدية؛ لأنها منفعة مقصودة، فوجبت فيها الدية، كالكلام','12555','4513','0');
INSERT INTO [t19188] VALUES (4553,'٤٢٦٩ - مسألة: وفى ذهاب الأكل الدية؛ لأنها منفعة مقصودة، فوجبت فيها الدية، كالشم والنكاح','12556','4513','0');
INSERT INTO [t19188] VALUES (4554,'٤٢٧٠ - مسألة: فإن كسر صلبه، فذهب نكاحه، ففيه الدية أيضا','12556','4513','0');
INSERT INTO [t19188] VALUES (4555,'٤٢٧١ - مسألة: (وتجب فى الحدب، والصعر، وهو أن','12557','4513','0');
INSERT INTO [t19188] VALUES (4556,'٤٢٧٢ - مسألة: وفى الصعر الدية، وهو أن يضربه فيصير الوجه إلى جانب. وأصل الصعر داء يأخذ البعير [فى عنقه]','12558','4513','0');
INSERT INTO [t19188] VALUES (4557,'٤٢٧٣ - مسألة: (وفى تسويد الوجه إذا لم يزل)','12559','4513','0');
INSERT INTO [t19188] VALUES (4558,'٤٢٧٤ - مسألة: (وإذا لم يستمسك الغائط أو البول، ففى كل واحد من ذلك دية كاملة)','12560','4513','0');
INSERT INTO [t19188] VALUES (4559,'٤٢٧٥ - مسألة: (وفى نقص شئ من ذلك إن علم بقدره، مثل نقص العقل، بأن يجن يوما ويفيق يوما، أو ذهاب بصر إحدى العينين، أو سمع إحدى الأذنين)','12562','4513','0');
INSERT INTO [t19188] VALUES (4560,'٤٢٧٦ - مسألة: (وفى بعض الكلام بالحساب، يقسم على ثمانية وعشرين حرفا)','12562','4513','0');
INSERT INTO [t19188] VALUES (4561,'٤٢٧٧ - مسألة: (وإن لم يعلم قدره، مثل أن صار مدهوشا)','12565','4513','0');
INSERT INTO [t19188] VALUES (4562,'٤٢٧٨ - مسألة: (فإن نقص سمعه، أو بصره، أو شمه، أو حصل فى كلامه تمتمة أو عجلة)','12565','4513','0');
INSERT INTO [t19188] VALUES (4563,'٤٢٧٩ - مسألة: (وإن نقص مشيه أو انحنى قليلا، أو تقلصت شفته بعض التقلص، أو تحركت سنه، أو ذهب اللبن من ثدى المرأة، ونحو ذلك، ففيه حكومة)','12566','4513','0');
INSERT INTO [t19188] VALUES (4564,'٤٢٨٠ - مسألة: (وإن قطع بعض اللسان فذهب بعض الكلام، اعتبر أكثرهما؛ فلو ذهب ربع اللسان ونصف الكلام، أو ربع الكلام ونصف اللسان، وجب نصف الدية)','12567','4513','0');
INSERT INTO [t19188] VALUES (4565,'٤٢٨١ - مسألة: (وإن قطع ربع اللسان فذهب نصف الكلام، ثم قطع الآخر بقيته)','12568','4513','0');
INSERT INTO [t19188] VALUES (4566,'٤٢٨٢ - مسألة: (وإن قطع لسانه، فذهب نطقه وذوقه، لم يجب إلا دية، وإن ذهبا مع بقاء اللسان، وجبت ديتان)','12571','4513','0');
INSERT INTO [t19188] VALUES (4567,'٤٢٨٣ - مسألة: (وإن كسر صلبه فذهب مشيه ونكاحه، ففيه ديتان)','12573','4513','0');
INSERT INTO [t19188] VALUES (4568,'٤٢٨٤ - مسألة: (وإن اختلفا فى نقص سمعه وبصره، فالقول قول المجنى عليه)','12573','4513','0');
INSERT INTO [t19188] VALUES (4569,'٤٢٨٥ - مسألة: (وإن اختلفا فى ذهاب بصره، أرى أهل الخبرة)','12576','4513','0');
INSERT INTO [t19188] VALUES (4570,'٤٢٨٦ - مسألة: (وإن اختلفا فى ذهاب سمعه)','12577','4513','0');
INSERT INTO [t19188] VALUES (4571,'٤٢٨٧ - مسألة: وإن ادعى ذهاب شمه','12578','4513','0');
INSERT INTO [t19188] VALUES (4572,'فصل','12579','4513','0');
INSERT INTO [t19188] VALUES (4573,'٤٢٨٨ - مسألة: (وإن اختلفا فى ذهاب ذوقه، أطعم الأشياء المرة)','12579','4513','0');
INSERT INTO [t19188] VALUES (4574,'٤٢٨٩ - مسألة: (ولا)','12580','4513','0');
INSERT INTO [t19188] VALUES (4575,'٤٢٩٠ - مسألة: (فلو قلع سن كبير أو ظفرا ثم نبت، أو رده فالتحم)','12580','4513','0');
INSERT INTO [t19188] VALUES (4576,'٤٢٩١ - مسألة: (وإن ذهب سمعه، أو بصره، أو شمه، أو ذوقه، أو عقله، ثم عاد، سقطت ديته)','12583','4513','0');
INSERT INTO [t19188] VALUES (4577,'٤٢٩٢ - مسألة: (وإن عاد ناقصا، أو عادت السن أو الظفر قصيرا','12583','4513','0');
INSERT INTO [t19188] VALUES (4578,'٤٢٩٣ - مسألة: (وعنه فى الظفر إذا نبت على صفته، خمسة دنانير، وإن نبت أسود)','12584','4513','0');
INSERT INTO [t19188] VALUES (4579,'٤٢٩٤ - مسألة: (وإن قلع سن صغير ويئس من عودها، وجبت ديتها)','12584','4513','0');
INSERT INTO [t19188] VALUES (4580,'٤٢٩٥ - مسألة: (وإن مات المجنى عليه وادعى الجانى عود ما أذهبه، فأنكر الولى، فالقول قوله)','12585','4513','0');
INSERT INTO [t19188] VALUES (4581,'فصل','12586','4513','0');
INSERT INTO [t19188] VALUES (4582,'٤٢٩٦ - مسألة: (وفى كل حاجب نصفها، وفى كل هدب ربعها)','12587','4513','0');
INSERT INTO [t19188] VALUES (4583,'٤٢٩٧ - مسألة: (وفى بعض ذلك بقسطه من الدية)','12587','4513','0');
INSERT INTO [t19188] VALUES (4584,'٤٢٩٨ - مسألة: (وإنما تجب ديته إذا أزاله على وجه لا يعود)','12588','4513','0');
INSERT INTO [t19188] VALUES (4585,'٤٢٩٩ - مسألة: (فإن عاد، سقطت الدية)','12588','4513','0');
INSERT INTO [t19188] VALUES (4586,'٤٣٠٠ - مسألة: (وإن بقى من لحيته ما لا جمال فيه)','12588','4513','0');
INSERT INTO [t19188] VALUES (4587,'٤٣٠١ - مسألة: (وإن قلع الجفن بهدبه، لم يجب إلا دية الجفن)','12589','4513','0');
INSERT INTO [t19188] VALUES (4588,'٤٣٠٢ - مسألة: (وإن قلع اللحيين بما عليهما من الأسنان، وجبت ديتهما ودية الأسنان)','12589','4513','0');
INSERT INTO [t19188] VALUES (4589,'٤٣٠٣ - مسألة: (وإن قطع كفا بأصابعه، لم يجب إلا دية الأصابع)','12590','4513','0');
INSERT INTO [t19188] VALUES (4590,'٤٣٠٤ - مسألة: (وإن قطع كفا عليه بعض الأصابع، دخل ما حاذى الأصابع فى ديتها، وعليه أرش باقى الكف)','12590','4513','0');
INSERT INTO [t19188] VALUES (4591,'فصل','12591','4513','0');
INSERT INTO [t19188] VALUES (4592,'٤٣٠٥ - مسألة: (وإن قطع أنملة بظفرها، فليس عليه إلا ديتها)','12591','4513','0');
INSERT INTO [t19188] VALUES (4593,'٤٣٠٦ - مسألة: (وإن قلع الأعور عين صحيح مماثلة لعينه الصحيحة عمدا، فلا قصاص، وعليه دية كاملة)','12593','4513','0');
INSERT INTO [t19188] VALUES (4594,'٤٣٠٧ - مسألة: (وإن قلع عينى صحيح عمدا، خير بين قلع عينه ولا شئ له غيرها، وبين الدية)','12595','4513','0');
INSERT INTO [t19188] VALUES (4595,'٤٣٠٨ - مسألة: (وفى يد الأقطع نصف الدية، وكذلك فى رجله. وعنه، فيها دية كاملة)','12596','4513','0');
INSERT INTO [t19188] VALUES (4596,'باب الشجاج وكسر العظام','12600','4442','0');
INSERT INTO [t19188] VALUES (4597,'فصل','12605','4596','0');
INSERT INTO [t19188] VALUES (4598,'٤٣٠٩ - مسألة: (فإن عمت الرأس ونزلت إلى الوجه، فهل هى موضحة أو موضحتان؟ على وجهين)','12609','4596','0');
INSERT INTO [t19188] VALUES (4599,'٤٣١٠ - مسألة: (وإن أوضحه موضحتين بينهما حاجز، فعليه','12610','4596','0');
INSERT INTO [t19188] VALUES (4600,'٤٣١١ - مسألة: فإن خرقه أجنبى','12611','4596','0');
INSERT INTO [t19188] VALUES (4601,'٤٣١٢ - مسألة: (وإن اختلفا فى من خرقه، فالقول قول المجنى عليه)','12612','4596','0');
INSERT INTO [t19188] VALUES (4602,'٤٣١٣ - مسألة: (وإن خرق ما بينهما فى الباطن)','12613','4596','0');
INSERT INTO [t19188] VALUES (4603,'٤٣١٤ - مسألة: (وإن شج جميع رأسه سمحاقا إلا موضعا منه أوضحه، فعليه أرش موضحة)','12614','4596','0');
INSERT INTO [t19188] VALUES (4604,'٤٣١٥ - مسألة: (ثم الهاشمة؛ وهى التى توضح العظم وتهشمه، ففيها عشر من الإبل)','12614','4596','0');
INSERT INTO [t19188] VALUES (4605,'٤٣١٦ - مسألة: (فإن ضربه بمثقل فهشمه من غير أن يوضحه، ففيه حكومة)','12616','4596','0');
INSERT INTO [t19188] VALUES (4606,'٤٣١٧ - مسألة: (ثم المنقلة؛ وهى التى توضح وتهشم وتنقل عظامها، ففيها خمس عشرة من الإبل)','12617','4596','0');
INSERT INTO [t19188] VALUES (4607,'٤٣١٨ - مسألة: (ثم المأمومة؛ وهى التى تصل إلى جلدة الدماغ، وتسمى أم الدماغ. وتسمى المأمومة آمة)','12618','4596','0');
INSERT INTO [t19188] VALUES (4608,'فصل','12619','4596','0');
INSERT INTO [t19188] VALUES (4609,'٤٣١٩ - مسألة: (فإن خرقه من جانب فخرج من الجانب الآخر، فهى جائفتان)','12621','4596','0');
INSERT INTO [t19188] VALUES (4610,'٤٣٢٠ - مسألة: (وإن طعنه فى خده فوصل إلى فيه، ففيه حكومة)','12623','4596','0');
INSERT INTO [t19188] VALUES (4611,'٤٣٢١ - مسألة: (وإن جرحه فى وركه فوصل الجرح إلى جوفه، أو أوضحه فوصل الجرح إلى قفاه، فعليه دية جائفة وموضحة، وحكومة لجرح القفا والورك)','12624','4596','0');
INSERT INTO [t19188] VALUES (4612,'٤٣٢٢ - مسألة: (وإن أجافه، ووسع آخر الجرح، فهى جائفتان)','12625','4596','0');
INSERT INTO [t19188] VALUES (4613,'٤٣٢٣ - مسألة: (وإن وسع ظاهره دون باطنه، أو باطنه دون ظاهره، فعليه حكومة)','12625','4596','0');
INSERT INTO [t19188] VALUES (4614,'٤٣٢٤ - مسألة: (وإن التحمت الجائفة ففتحها آخر، فهى جائفة أخرى)','12626','4596','0');
INSERT INTO [t19188] VALUES (4615,'فصل','12631','4596','0');
INSERT INTO [t19188] VALUES (4616,'٤٣٢٥ - مسألة: (وفى كل واحد من الذراع، والزند، والعضد، والساق، بعيران)','12634','4596','0');
INSERT INTO [t19188] VALUES (4617,'٤٣٢٦ - مسألة: (وما عدا ما ذكرنا من الجروح وكسر','12636','4596','0');
INSERT INTO [t19188] VALUES (4618,'٤٣٢٧ - مسألة: (والحكومة أن يقوم المجنى عليه كأنه عبد لا جناية به، ثم يقوم وهى به قد برأت، فما نقص، فله مثله من الدية، فإن كان قيمته وهو صحيح عشرين، وقيمته وبه الجناية تسعة عشر، ففيه نصف عشر ديته)','12637','4596','0');
INSERT INTO [t19188] VALUES (4619,'٤٣٢٨ - مسألة: (إلا أن تكون فى شئ فيه مقدر، فلا يبلغ به أرش المقدر، فإن كانت فى الشجاج التى دون الموضحة، لم يبلغ بها أرش الموضحة)','12639','4596','0');
INSERT INTO [t19188] VALUES (4620,'٤٣٢٩ - مسألة: (فإت كانت)','12642','4596','0');
INSERT INTO [t19188] VALUES (4621,'٤٣٣٠ - مسألة: (فإن لم ينقص فى تلك الحال)','12644','4596','0');
INSERT INTO [t19188] VALUES (4622,'باب العاقلة وما تحمله','12646','4442','0');
INSERT INTO [t19188] VALUES (4623,'٤٣٣١ - مسألة: (وليس على فقير، ولا صبى، ولا زائل العقل، ولا امرأة، ولا خنثى مشكل، ولا رقيق، ولا مخالف لدين الجانى، حمل شئ)','12652','4622','0');
INSERT INTO [t19188] VALUES (4624,'٤٣٣٢ - مسألة: (ويحمل الغائب كما يحمل الحاضر)','12654','4622','0');
INSERT INTO [t19188] VALUES (4625,'٤٣٣٣ - مسألة: (وخطأ الإمام والحاكم فى أحكامه فى بيت المال. وعنه، على عاقلته)','12655','4622','0');
INSERT INTO [t19188] VALUES (4626,'٤٣٣٤ - مسألة: (وهل يتعاقل أهل الذمة؟ على روايتين)','12656','4622','0');
INSERT INTO [t19188] VALUES (4627,'٤٣٣٥ - مسألة: (ولا يعقل حربى عن ذمى، ولا ذمى عن حربى)','12657','4622','0');
INSERT INTO [t19188] VALUES (4628,'٤٣٣٦ - مسألة: (ومن لا عاقلة له، أو لم تكن له عاقلة تحمل الجميع، فالدية أو باقيها عليه إن كان ذميا)','12658','4622','0');
INSERT INTO [t19188] VALUES (4629,'فصل','12665','4622','0');
INSERT INTO [t19188] VALUES (4630,'٤٣٣٧ - مسألة: (ولا تحمل العاقلة عمدا، ولا عبدا، ولا صلحا، ولا اعترافا، ولا ما دون ثلث الدية، ويكون ذلك فى مال','12665','4622','0');
INSERT INTO [t19188] VALUES (4631,'٤٣٣٨ - مسألة: وتحمل غرة الجنين إذا مات مع أمه، فإن العاملة تحملها مع دية أمه. نص عليه؛ لأن ديتهما','12673','4622','0');
INSERT INTO [t19188] VALUES (4632,'٤٣٣٩ - مسألة: (وتحمل جناية الخطأ عن الحر إذا بلغت الثلث)','12673','4622','0');
INSERT INTO [t19188] VALUES (4633,'٤٣٤٠ - مسألة: (قال أبو بكر: ولا تحمل)','12673','4622','0');
INSERT INTO [t19188] VALUES (4634,'٤٣٤١ - مسألة: (وما يحمله كل واحد من العاقلة غير مقدر، لكن يرجع فيه إلى اجتهاد الحاكم، فيحمل كل إنسان منهم ما يسهل ولا يشق)','12676','4622','0');
INSERT INTO [t19188] VALUES (4635,'٤٣٤٢ - مسألة: واختلف القائلون بالتقدير بنصف دينار وربعه','12678','4622','0');
INSERT INTO [t19188] VALUES (4636,'٤٣٤٣ - مسألة: (ويبدأ بالأقرب فالأقرب، فمتى اتسعت أموال الأقربين لها، لم يتجاوزهم، وإلا انتقل إلى من يليهم)','12680','4622','0');
INSERT INTO [t19188] VALUES (4637,'٤٣٤٤ - مسألة: (وإن تساوى جماعة فى القرب، وزع القدر الذى يلزمهم بينهم)','12682','4622','0');
INSERT INTO [t19188] VALUES (4638,'فصل','12683','4622','0');
INSERT INTO [t19188] VALUES (4639,'٤٣٤٥ - مسألة: (وما تحمله العاقلة يجب مؤجلا فى ثلاث سنين، فى كل سنة ثلثه إن كان دية كاملة)','12683','4622','0');
INSERT INTO [t19188] VALUES (4640,'٤٣٤٦ - مسألة: (وإن كان الواجب ثلث الدية، وجب فى رأس','12684','4622','0');
INSERT INTO [t19188] VALUES (4641,'٤٣٤٧ - مسألة: فإن كانت الدية ناقصة، كدية المرأة والكتابى، ففيها وجهان؛ أحدهما، تقسم فى ثلاث سنين؛ لأنه بدل','12687','4622','0');
INSERT INTO [t19188] VALUES (4642,'٤٣٤٨ - مسألة: (وابتداء الحول فى الجرح من حين الاندمال، وفى القتل من حين الموت. وقال القاضى: إن لم يسر الجرح إلى شئ، فحوله من حين القطع)','12688','4622','0');
INSERT INTO [t19188] VALUES (4643,'٤٣٤٩ - مسألة: (ومن مات من العاقلة قبل الحول أو افتقر، سقط ما عليه، وإن مات بعد الحول، لم يسقط ما عليه)','12689','4622','0');
INSERT INTO [t19188] VALUES (4644,'٤٣٥٠ - مسألة: (وعمد الصبى والمجنون خطأ تحمله العاقلة)','12690','4622','0');
INSERT INTO [t19188] VALUES (4645,'باب كفارة القتل','12692','4442','0');
INSERT INTO [t19188] VALUES (4646,'٤٣٥١ - مسألة: ومن شارك فى قتل يوجب الكفارة، لزمته كفارة، ويلزم كل واحد من شركائه كفاره. هذا قول أكثر أهل العلم؛ منهم الحسن، وعكرمة، والنخعى، والثورى، ومالك، والشافعى، وأصحاب الرأى. (وعن أحمد، أن على المشتركين كفارة واحدة)','12693','4645','0');
INSERT INTO [t19188] VALUES (4647,'٤٣٥٢ - مسألة: (ولو ضرب بطن امرأة، فألقت جنينا ميتا، أو حيا ثم مات، فعليه الكفارة)','12694','4645','0');
INSERT INTO [t19188] VALUES (4648,'٤٣٥٣ - مسألة: (مسلما كان المقتول أو كافرا، حرا أو عبدا)','12695','4645','0');
INSERT INTO [t19188] VALUES (4649,'٤٣٥٤ - مسألة: وتجب الكفارة بقتل العبد','12695','4645','0');
INSERT INTO [t19188] VALUES (4650,'٤٣٥٥ - مسألة: (وسواء كان القاتل كبيرا عاقلا، أو صبيا أو مجنونا، حرا أو عبدا)','12696','4645','0');
INSERT INTO [t19188] VALUES (4651,'٤٣٥٦ - مسألة: (ويكفر العبد بالصيام)','12697','4645','0');
INSERT INTO [t19188] VALUES (4652,'٤٣٥٧ - مسألة: (فأما القتل المباح، كالقصاص، والحد، وقتل الباغى والصائل، فلا كفارة فيه)','12697','4645','0');
INSERT INTO [t19188] VALUES (4653,'٤٣٥٨ - مسألة: (وفى العمد وشبه العمد روايتان؛ إحداهما، لا كفارة فيه. اختارها أبو بكر والقاضى. والأخرى، فيه الكفارة)','12699','4645','0');
INSERT INTO [t19188] VALUES (4654,'باب القسامة','12704','4442','0');
INSERT INTO [t19188] VALUES (4655,'٤٣٥٩ - مسألة: (ولا تثبت إلا بشروط أربعة؛ أحدها، دعوى القتل، ذكرا كان المقتول أو أنثى، حرا أو عبدا، مسلما أو ذميا. وأما الجراح فلا قسامة فيه)','12705','4654','0');
INSERT INTO [t19188] VALUES (4656,'٤٣٦٠ - مسألة: (وسواء كان المقتول ذكرا أو أنثى، حرا أو عبدا، مسلما أو ذميا)','12709','4654','0');
INSERT INTO [t19188] VALUES (4657,'٤٣٦١ - مسألة: (فأما الجراح فلا قسامة فيه)','12713','4654','0');
INSERT INTO [t19188] VALUES (4658,'٤٣٦٢ - مسألة: (فأما قول القتيل: فلان قتلنى. فليس بلوث)','12721','4654','0');
INSERT INTO [t19188] VALUES (4659,'٤٣٦٣ - مسألة: (ومتى ادعى القتل مع عدم اللوث عمدا، فقال الخرقى: لا يحكم له بيمين ولا غيرها. وعن أحمد، أنه يحلف يمينا واحدة. وهى الأولى. وإن كان خطأ حلف يمينا واحدة)','12722','4654','0');
INSERT INTO [t19188] VALUES (4660,'٤٣٦٤ - مسألة: (وذكر الخرقى من شروط القسامة أن تكون الدعوى عمدا، توجب القصاص إذا ثبت القتل، وأن تكون الدعوى على واحد)','12739','4654','0');
INSERT INTO [t19188] VALUES (4661,'فصل','12743','4654','0');
INSERT INTO [t19188] VALUES (4662,'٤٣٦٥ - مسألة: (وعن أحمد، يحلف من العصبة الوارث منهم وغير الوارث، خمسون رجلا، كل واحد يمينا)','12752','4654','0');
INSERT INTO [t19188] VALUES (4663,'٤٣٦٦ - مسألة: (فإن لم يحلف المدعون، حلف المدعى عليه خمسين يمينا، وبرئ)','12755','4654','0');
INSERT INTO [t19188] VALUES (4664,'٤٣٦٧ - مسألة: (فإن لم يحلف المدعون، ولم يرضوا بيمين المدعى عليه، فداه الإمام من بيت المال)','12758','4654','0');
INSERT INTO [t19188] VALUES (4665,'٤٣٦٨ - مسألة: (وإن طلبو أيمانهم فنكلوا، لم يحبسوا. وهل تلزمهم الدية أو تكون فى بيت المال؟ على روايتين)','12759','4654','0');
INSERT INTO [t19188] VALUES (4666,'كتاب الحدود','12762','0','0');
INSERT INTO [t19188] VALUES (4667,'[باب. . .]','12762','4666','0');
INSERT INTO [t19188] VALUES (4668,'٤٣٦٩ - مسألة: (ولا يجب الحد إلا على بالغ عاقل عالم بالتحريم)','12762','4667','0');
INSERT INTO [t19188] VALUES (4669,'٤٣٧٠ - مسألة: ولا يجب الحد إلا على عالم بالتحريم','12764','4667','0');
INSERT INTO [t19188] VALUES (4670,'٤٣٧١ - مسألة: (ولا يجوز أن يقيم الحد إلا الإمام أو نائبه)','12765','4667','0');
INSERT INTO [t19188] VALUES (4671,'٤٣٧٢ - مسألة: (إلا السيد، فإن له إقامة الحد بالجلد خاصة على رقيقه القن. وهل له القتل فى الردة، والقطع فى السرقة؟ على روايتين)','12766','4667','0');
INSERT INTO [t19188] VALUES (4672,'٤٣٧٣ - مسألة: (ولا)','12772','4667','0');
INSERT INTO [t19188] VALUES (4673,'٤٣٧٤ - مسألة: (وإن كان السيد فاسقا أو امرأة، فله إقامته فى ظاهر كلامه. ويحتمل أن لا يملكه)','12774','4667','0');
INSERT INTO [t19188] VALUES (4674,'٤٣٧٥ - مسألة: (ولا يملكه المكاتب)','12775','4667','0');
INSERT INTO [t19188] VALUES (4675,'٤٣٧٦ - مسألة: (وسواء ثبت ببينة أو إقرار)','12775','4667','0');
INSERT INTO [t19188] VALUES (4676,'٤٣٧٧ - مسألة: (وإن ثبت بعلمه، فله إقامته. نص عليه.','12776','4667','0');
INSERT INTO [t19188] VALUES (4677,'٤٣٧٨ - مسألة: (ولا يقيم الإمام الحد بعلمه)','12777','4667','0');
INSERT INTO [t19188] VALUES (4678,'٤٣٧٩ - مسألة: (ولا تقام الحدود فى المساجد)','12778','4667','0');
INSERT INTO [t19188] VALUES (4679,'٤٣٨٠ - مسألة: (ويضرب الرجل قائما)','12779','4667','0');
INSERT INTO [t19188] VALUES (4680,'٤٣٨١ - مسألة: (ولا يمد، ولا يربط، ولا يجرد)','12782','4667','0');
INSERT INTO [t19188] VALUES (4681,'٤٣٨٢ - مسألة: (ولا يبالغ فى ضربه بحيث يشق الجلد)','12783','4667','0');
INSERT INTO [t19188] VALUES (4682,'٤٣٨٣ - مسألة: (والمرأة كذلك)','12784','4667','0');
INSERT INTO [t19188] VALUES (4683,'٤٣٨٤ - مسألة: (والجلد فى الزنى أشد الجلد، ثم جلد القذف، ثم الشرب، ثم التعزير)','12785','4667','0');
INSERT INTO [t19188] VALUES (4684,'٤٣٨٥ - مسألة: (وإن رأى الإمام الجلد فى حد الخمر بالجريد والنعال، فله ذلك)','12786','4667','0');
INSERT INTO [t19188] VALUES (4685,'٤٣٨٦ - مسألة: (قال أصحابنا: ولا يؤخر الحد للمرض، فإن كان جلدا، وخشى عليه من السوط، أقيم بأطراف الثياب والعثكول','12787','4667','0');
INSERT INTO [t19188] VALUES (4686,'٤٣٨٧ - مسألة: (وإذا مات المحدود فى الجلد، فالحق قتله)','12794','4667','0');
INSERT INTO [t19188] VALUES (4687,'٤٣٨٨ - مسألة: (وإن زاد)','12795','4667','0');
INSERT INTO [t19188] VALUES (4688,'٤٣٨٩ - مسألة: (وإذا كان الحد رجما، لم يحفر له، رجلا كان أو امرأة، فى أحد الوجهين)','12798','4667','0');
INSERT INTO [t19188] VALUES (4689,'٤٣٩٠ - مسألة: (وأما المرأة، فإن كان ثبت بإقرارها، لم يحفر لها، وإن ثبت ببينة، حفر لها إلى الصدر)','12799','4667','0');
INSERT INTO [t19188] VALUES (4690,'٤٣٩١ - مسألة: (ويستحب أن يبدأ الشهود بالرجم. وإن ثبت بالإقرار، استحب أن يبدأ الإمام)','12801','4667','0');
INSERT INTO [t19188] VALUES (4691,'٤٣٩٢ - مسألة: (ومتى رجع المقر بالحد عن إقراره، قبل منه،','12802','4667','0');
INSERT INTO [t19188] VALUES (4692,'٤٣٩٣ - مسألة: (وإن رجم ببينة فهرب، لم يترك، وإن كان بإقرار، ترك)','12805','4667','0');
INSERT INTO [t19188] VALUES (4693,'فصل','12806','4666','0');
INSERT INTO [t19188] VALUES (4694,'٤٣٩٤ - مسألة: (وأما حقوق الآدميين، فتستوفى كلها، سواء كان فيها قتل أو لم يكن. ويبدأ بغير القتل)','12810','4693','0');
INSERT INTO [t19188] VALUES (4695,'٤٣٩٥ - مسألة: (فإن اجتمعت مع حدود الله تعالى، بدئ بها)','12811','4693','0');
INSERT INTO [t19188] VALUES (4696,'فصل','12816','4666','0');
INSERT INTO [t19188] VALUES (4697,'٤٣٩٦ - مسألة: (وإن فعل ذلك فى الحرم، استوفى منه فيه)','12822','4696','0');
INSERT INTO [t19188] VALUES (4698,'٤٣٩٧ - مسألة: (وإن أتى حدا فى الغزو، لم يستوف منه فى أرض العدو حتى يرجع إلى دار الإسلام، فيقام عليه)','12824','4696','0');
INSERT INTO [t19188] VALUES (4699,'باب حد الزنى','12829','4666','0');
INSERT INTO [t19188] VALUES (4700,'٤٣٩٨ - مسألة: (إذا زنى الحر المحصن، فحده الرجم حتى يموت. وهل يجلد قبل الرجم؟ على روايتين)','12831','4699','0');
INSERT INTO [t19188] VALUES (4701,'٤٣٩٩ - مسألة: (والمحصن من وطئ امرأته فى قبلها، فى نكاح صحيح، وهما بالغان عاقلان حران، فإن اختل شرط منها، فلا إحصان لواحد منهما. ولا يثبت الإحصان بالوطء بملك اليمين، ولا فى نكاح فاسد)','12837','4699','0');
INSERT INTO [t19188] VALUES (4702,'٤٤٠٠ - مسألة: (ويثبت الإحصان للذميين. وهل تحصن الذمية مسلما؟ على روايتين)','12842','4699','0');
INSERT INTO [t19188] VALUES (4703,'٤٤٠١ - مسألة: (وإن كان لرجل ولد من امرأته فقال: ما وطئتها. لم يثبت إحصانه)','12845','4699','0');
INSERT INTO [t19188] VALUES (4704,'٤٤٠٢ - مسألة: (وإن زنى الحر غير المحصن، جلد مائة، وغرب عاما إلى مسافة القصر)','12848','4699','0');
INSERT INTO [t19188] VALUES (4705,'٤٤٠٣ - مسألة: (وعنه، أن المرأة تنفى إلى دون مسافة القصر)','12851','4699','0');
INSERT INTO [t19188] VALUES (4706,'٤٤٠٤ - مسألة: (ويخرج مع المرأة محرمها)','12853','4699','0');
INSERT INTO [t19188] VALUES (4707,'٤٤٠٥ - مسألة: (وإن كان الزانى رقيقا، فحده خمسون جلدة بكل حال، ولا يغرب)','12858','4699','0');
INSERT INTO [t19188] VALUES (4708,'٤٤٠٦ - مسألة: (وإن كان نصفه حرا، فحده خمس وسبعون جلدة، وتغريب نصف عام. ويحتمل أن لا يغرب)','12863','4699','0');
INSERT INTO [t19188] VALUES (4709,'٤٤٠٧ - مسألة: (وحد اللوطى كحد الزانى سواء. وعنه، حده الرجم بكل حال)','12865','4699','0');
INSERT INTO [t19188] VALUES (4710,'٤٤٠٨ - مسألة: (ومن أتى بهيمة، فعليه حد اللوطى عند القاضى. واختار الخرقى وأبو بكر، أنه يعزر. وتقتل البهيمة)','12869','4699','0');
INSERT INTO [t19188] VALUES (4711,'٤٤٠٩ - مسألة: (وكره احمد أكل لحمها. وهل يحرم؟ على وجهين)','12873','4699','0');
INSERT INTO [t19188] VALUES (4712,'فصل','12875','4699','0');
INSERT INTO [t19188] VALUES (4713,'٤٤١٠ - مسألة: (وأقل ذلك تغييب الحشفة فى الفرج)','12876','4699','0');
INSERT INTO [t19188] VALUES (4714,'٤٤١١ - مسألة: (فإن وطئ دون الفرج)','12876','4699','0');
INSERT INTO [t19188] VALUES (4715,'٤٤١٢ - مسألة: (وإن أتت المرأة المرأة، فلا حد عليهما)','12877','4699','0');
INSERT INTO [t19188] VALUES (4716,'فصل','12878','4699','0');
INSERT INTO [t19188] VALUES (4717,'٤٤١٣ - مسألة: (أو وجد امرأة)','12879','4699','0');
INSERT INTO [t19188] VALUES (4718,'٤٤١٤ - مسألة: (أو وطئ فى نكاح مختلف فى صحته، أو وطئ امرأته فى دبرها، أو حيضها، أو نفاسها)','12881','4699','0');
INSERT INTO [t19188] VALUES (4719,'٤٤١٥ - مسألة: ولا حد على من لم يعلم بتحريم الزنى. قال عمر، وعلى، وعثمان: لا حد إلا على من علمه','12882','4699','0');
INSERT INTO [t19188] VALUES (4720,'٤٤١٦ - مسألة: (أو أكره على الزنى، فلا حد فيه. وقال أصحابنا: إن أكره الرجل فزنى، حد)','12883','4699','0');
INSERT INTO [t19188] VALUES (4721,'٤٤١٧ - مسألة: (وإن وطئ ميتة، أو ملك أمه أو أخته من الرضاع، فوطئها، فهل يحد أو يعزر؟ على وجهين)','12886','4699','0');
INSERT INTO [t19188] VALUES (4722,'٤٤١٨ - مسألة: (وإن وطئ فى نكاح مجمع على بطلانه، كنكاح المزوجة، والمعتدة، والخامسة، وذوات المحارم من النسب والرضاع)','12888','4699','0');
INSERT INTO [t19188] VALUES (4723,'٤٤١٩ - مسألة: (أو استأجر امرأة للزنى، أو لغيره، وزنى بها، أو زنى بامرأة له عليها القصاص، أو بصغيرة، أو مجنونة، أو زنى بامرأة ثم تزوجها، أو بأمة ثم اشتراها، أو أمكنت العاقلة)','12893','4699','0');
INSERT INTO [t19188] VALUES (4724,'فصل','12896','4699','0');
INSERT INTO [t19188] VALUES (4725,'٤٤٢٠ - مسألة: ومتى رجع المقر بالحد عن إقراره قبل منه، وقد ذكرنا الخلاف فيه','12907','4699','0');
INSERT INTO [t19188] VALUES (4726,'٤٤٢١ - مسألة: (فإن جاء بعضهم بعد أن قام الحاكم، أو شهد','12913','4699','0');
INSERT INTO [t19188] VALUES (4727,'٤٤٢٢ - مسألة: (وإن كانوا فساقا، أو عميانا، أو بعضهم، فعليهم الحد. وعنه، لا حد عليهم)','12916','4699','0');
INSERT INTO [t19188] VALUES (4728,'٤٤٢٣ - مسألة: (وإن كان أحدهم زوجا، حد الثلاثة، ولاعن الزوج إن شاء)','12917','4699','0');
INSERT INTO [t19188] VALUES (4729,'٤٤٢٤ - مسألة: (وإن شهد اثنان أنه زنى بها فى بيت أو بلد، واثنان أنه زنى بها فى بيت أو بلد آخر، فهم قذفة، وعليهم الحد. وعنه،','12918','4699','0');
INSERT INTO [t19188] VALUES (4730,'٤٤٢٥ - مسألة: (وإن شهد اثنان أنه زنى بها فى زاوية بيت، وشهد اثنان أنه زنى بها فى زاوية أخرى)','12921','4699','0');
INSERT INTO [t19188] VALUES (4731,'٤٤٢٦ - مسألة: (وإن شهدا أنه زنى بها فى قميص أبيض، وشهد آخران أنه زنى بها فى قميص أحمر، كملت شهادتهم. ويحتمل أن لا تكمل)','12922','4699','0');
INSERT INTO [t19188] VALUES (4732,'٤٤٢٧ - مسألة: (وإن شهدا أنه زنى بها مطاوعة، وشهد آخران أنه زنى بها مكرهة)','12923','4699','0');
INSERT INTO [t19188] VALUES (4733,'٤٤٢٨ - مسألة: (وهل يحد الجميع أو شاهدا المطاوعة؟ على وجهين)','12925','4699','0');
INSERT INTO [t19188] VALUES (4734,'٤٤٢٩ - مسألة: (وإن شهد أربعة فرجع أحدهم، فلا شئ على الراجع، ويحد الثلاثة، وإن كان رجوعه بعد الحد، فلا حد على الثلاثة، ويغرم الراجع ربع ما أتلفوه)','12927','4699','0');
INSERT INTO [t19188] VALUES (4735,'٤٤٣٠ - مسألة: (وإن شهد أربعة بالزنى بامرأة، فشهد ثقات من النساء أنها عذراء، فلا حد عليها ولا على الشهود. نص عليه)','12931','4699','0');
INSERT INTO [t19188] VALUES (4736,'٤٤٣١ - مسألة: (وإن شهد أربعة أنه زنى بامرأة، فشهد أربعة آخرون أنهم هم الزناة بها، لم يحد المشهود عليه. وهل يحد الشهود','12932','4699','0');
INSERT INTO [t19188] VALUES (4737,'٤٤٣٢ - مسألة: (وإن حملت امرأة لا زوج لها ولا سيد، لم تحد بذلك بمجرده)','12935','4699','0');
INSERT INTO [t19188] VALUES (4738,'باب حد القذف','12940','4666','0');
INSERT INTO [t19188] VALUES (4739,'٤٤٣٣ - مسألة: (ومن قذف حرا محصنا، فعليه جلد ثمانين جلدة إن كان القاذف حرا، أو أربعين إن كان عبدا. وقذف غير المحصن يوجب التعزير)','12941','4738','0');
INSERT INTO [t19188] VALUES (4740,'٤٤٣٤ - مسألة: (والمحصن هو الحر المسلم العاقل العفيف الذى يجامع مثله. وهل يشترط البلوغ؟ على روايتين)','12943','4738','0');
INSERT INTO [t19188] VALUES (4741,'٤٤٣٥ - مسألة: (وقذف غير المحصن يوجب التعزير)','12948','4738','0');
INSERT INTO [t19188] VALUES (4742,'٤٤٣٦ - مسألة: (وإن قال: زنيت وأنت صغيرة. وفسره بصغر عن تسع سنين، لم يحد، وإلا خرج على الروايتين)','12953','4738','0');
INSERT INTO [t19188] VALUES (4743,'٤٤٣٧ - مسألة: (وإن قال لحرة مسلمة: زنيت وأنت نصرانية. أو: أمة. ولم تكن كذلك، فعليه الحد)','12954','4738','0');
INSERT INTO [t19188] VALUES (4744,'٤٤٣٨ - مسألة: (ومن قذف محصنا، فزال إحصانه قبل إقامة الحد)','12958','4738','0');
INSERT INTO [t19188] VALUES (4745,'فصل','12961','4738','0');
INSERT INTO [t19188] VALUES (4746,'٤٤٣٩ - مسألة: (وإن أتت بولد يخالف لونه لونهما، لم يبح نفيه بذلك. وقال أبو الخطاب: ظاهر كلامه إباحته)','12964','4738','0');
INSERT INTO [t19188] VALUES (4747,'فصل','12966','4738','0');
INSERT INTO [t19188] VALUES (4748,'٤٤٤٠ - مسألة: (وإن قال: يا لوطى. أو: يا معفوج','12966','4738','0');
INSERT INTO [t19188] VALUES (4749,'٤٤٤١ - مسألة: (فإن قال: أردت)','12968','4738','0');
INSERT INTO [t19188] VALUES (4750,'٤٤٤٢ - مسألة: (فإن قال: أردت أنك تعمل عمل قوم لوط غير إتيان الرجال. احتمل وجهين)','12969','4738','0');
INSERT INTO [t19188] VALUES (4751,'٤٤٤٣ - مسألة: (وإن قال: لست بولد فلان. فقد قذف أمه)','12971','4738','0');
INSERT INTO [t19188] VALUES (4752,'٤٤٤٤ - مسألة: (وإن قال: لست بولدى. فعلى وجهين)','12972','4738','0');
INSERT INTO [t19188] VALUES (4753,'٤٤٤٥ - مسألة: (وإن قال: أنت أزنى الناس، أو أزنى من فلانة)','12973','4738','0');
INSERT INTO [t19188] VALUES (4754,'٤٤٤٦ - مسألة: (وإن قال لرجل: يا زانية. أو لامرأة: يا زانى. أو قال: زنت يداك ورجلاك. فهو صريح فى القذف، فى قول أبى بكر، وليس بصريح عند ابن حامد)','12974','4738','0');
INSERT INTO [t19188] VALUES (4755,'٤٤٤٧ - مسألة: (وإن قال: زنأت فى الجبل. مهموزا، فهو صريح عند أبى بكر. وقال ابن حامد: إن كان يعرف العربية، فليس','12976','4738','0');
INSERT INTO [t19188] VALUES (4756,'٤٤٤٨ - مسألة: (والكنايات نحو قوله لامرأته: قد فضحته، وغطيت، أو: نكست رأسه، وجعلت له قرونا، وعلقت عليه أولادا من غيره، وأفسدت فراشه. أو يقول لمن يخاصمه: يا حلال ابن الحلال، ما يعرفك الناس بالزنى يا عفيف، أو: يا فاجرة، يا قحبة، يا خبيثة. أو يقول لعربى: يا نبطى، يا فارسى، يا رومى. أو يسمع رجلا يقذف رجلا، فيقول: صدقت، أو: أخبرنى فلان أنك زنيت. وكذبه الآخر. فهذا كناية، إن فسره بما يحتمله غير القذف، قبل قوله فى أحد الوجهين. وفى الآخر، هذا كله صريح)','12980','4738','0');
INSERT INTO [t19188] VALUES (4757,'٤٤٤٩ - مسألة: (أو يسمع رجلا يقذف رجلا، فيقول: صدقت. أو: أخبرنى فلان أنك زنيت. وكذبه الآخر، فهو كناية،','12984','4738','0');
INSERT INTO [t19188] VALUES (4758,'٤٤٥٠ - مسألة: (وإن قذف أهل بلد أو جماعة لا يتصور الزنى من جميعهم، عزر، ولم يحد)','12987','4738','0');
INSERT INTO [t19188] VALUES (4759,'٤٤٥١ - مسألة: (وإن قال لرجل: اقذفنى. فقذفه. فهل يحد)','12988','4738','0');
INSERT INTO [t19188] VALUES (4760,'٤٤٥٢ - مسألة: (وإن قال لامرأته: يا زانية. قالت: بك زنيت. لم تكن قاذفة)','12988','4738','0');
INSERT INTO [t19188] VALUES (4761,'٤٤٥٣ - مسألة: (وإذا قذفت المرأة، لم يكن لولدها المطالبة، إذا كانت الأم فى الحياة، وإن قذفت وهى ميتة، مسلمة كانت أو كافرة، حرة أو أمة، حد القاذف إذا طالب الابن، وكان حرا مسلما. ذكره الخرقى. وقال أبو بكر: لا يجب الحد بقذف ميتة)','12989','4738','0');
INSERT INTO [t19188] VALUES (4762,'٤٤٥٤ - مسألة: (وإن مات المقذوف سقط الحد)','12993','4738','0');
INSERT INTO [t19188] VALUES (4763,'٤٤٥٥ - مسألة: (ومن قذف أم النبى -صلى الله عليه وسلم- قتل، مسلما كان أو كافرا)','12995','4738','0');
INSERT INTO [t19188] VALUES (4764,'٤٤٥٦ - مسألة: (وإن قذف الجماعة بكلمة واحدة، فحد واحد إذا طالبوا أو واحد منهم. وعنه، إن طالبوا متفرقين، حد لكل واحد حدا)','12997','4738','0');
INSERT INTO [t19188] VALUES (4765,'٤٤٥٧ - مسألة: (وإن قذفهم بكلمات، حد لكل واحد حدا)','12999','4738','0');
INSERT INTO [t19188] VALUES (4766,'٤٤٥٨ - مسألة: (وإن حد للقذف، فأعاده، لم يعد عليه الحد)','13001','4738','0');
INSERT INTO [t19188] VALUES (4767,'باب حد المسكر','13005','4666','0');
INSERT INTO [t19188] VALUES (4768,'٤٤٥٩ - مسألة: (كل شراب أسكر كثيره، فقليله حرام، من أى شئ كان، ويسمى خمرا)','13008','4767','0');
INSERT INTO [t19188] VALUES (4769,'٤٤٦٠ - مسألة: (ولا يجوز شربه للذة، ولا للتداوى، ولا','13011','4767','0');
INSERT INTO [t19188] VALUES (4770,'٤٤٦١ - مسألة: (ومن شربه مختارا عالما أن كثيره يسكر، قليلا','13013','4767','0');
INSERT INTO [t19188] VALUES (4771,'٤٤٦٢ - مسألة: (والرقيق على النصف من ذلك)','13020','4767','0');
INSERT INTO [t19188] VALUES (4772,'٤٤٦٣ - مسألة: (والذمى لا يحد بشربه، فى الصحيح)','13020','4767','0');
INSERT INTO [t19188] VALUES (4773,'٤٤٦٤ - مسألة: (وهل يجب الحد بوجود الرائحة؟ على','13021','4767','0');
INSERT INTO [t19188] VALUES (4774,'٤٤٦٥ - مسألة: (والعصير إذا أتت عليه ثلاثة أيام، حرم، إلا أن يغلى قبل ذلك فيحرم، نص عليه)','13026','4767','0');
INSERT INTO [t19188] VALUES (4775,'٤٤٦٦ - مسألة: وقال أبو الخطاب: عندى أن كلام أحمد فى ذلك محمول على عصير الغالب أنه يتخمر فى ثلاثة أيام','13028','4767','0');
INSERT INTO [t19188] VALUES (4776,'٤٤٦٧ - مسألة: (ولا يكره أن يترك فى الماء تمرا أو زبيبا ونحوه؛ ليأخذ ملوحته، ما لم يشتد، أو يأتى عليه ثلاث)','13029','4767','0');
INSERT INTO [t19188] VALUES (4777,'٤٤٦٨ - مسألة: (ولا يكره الانتباذ فى الدباء، والحنتم،','13029','4767','0');
INSERT INTO [t19188] VALUES (4778,'٤٤٦٩ - مسألة: (ويكره الخليطان، وهو أن ينتبذ شيئين،','13031','4767','0');
INSERT INTO [t19188] VALUES (4779,'٤٤٧٠ - مسألة: (ولا بأس بالفقاع','13034','4767','0');
INSERT INTO [t19188] VALUES (4780,'باب التعزير','13038','4666','0');
INSERT INTO [t19188] VALUES (4781,'٤٤٧١ - مسألة: (ومن وطئ أمة امرأته، فعليه الحد، إلا أن تكون)','13041','4780','0');
INSERT INTO [t19188] VALUES (4782,'٤٤٧٢ - مسألة: (وهل يلحقه نسب ولدها)','13042','4780','0');
INSERT INTO [t19188] VALUES (4783,'٤٤٧٣ - مسألة: (ولا يسقط الحد بالإباحة فى غير هذا الموضع)','13043','4780','0');
INSERT INTO [t19188] VALUES (4784,'٤٤٧٤ - مسألة: (ولا يزاد فى التعزير على عشر جلدات، فى غير هذا الموضع. وعنه، ما كان سببه الوطء، كوطء جاريته المشتركة والمزوجة ونحوه، ضرب مائة، ويسقط عنه)','13044','4780','0');
INSERT INTO [t19188] VALUES (4785,'٤٤٧٥ - مسألة: (وإن استمنى بيده لغير حاجة عزر)','13055','4780','0');
INSERT INTO [t19188] VALUES (4786,'باب القطع فى السرقة','13056','4666','0');
INSERT INTO [t19188] VALUES (4787,'٤٤٧٦ - مسألة: (ولا يجب إلا بسبعة شروط؛ أحدها، السرقة؛ وهى أخذ المال على وجه الاختفاء)','13057','4786','0');
INSERT INTO [t19188] VALUES (4788,'٤٤٧٧ - مسألة: (ولا قطع على منتهب، ولا مختلس، ولا','13057','4786','0');
INSERT INTO [t19188] VALUES (4789,'٤٤٧٨ - مسألة: (ويقطع الطرار، وهو الذى يبط الجيب وغيره، ويأخذ منه. وعنه، لا يقطع)','13061','4786','0');
INSERT INTO [t19188] VALUES (4790,'فصل','13062','4786','0');
INSERT INTO [t19188] VALUES (4791,'٤٤٧٩ - مسألة: (ويقطع بسرقة العبد الصغير)','13066','4786','0');
INSERT INTO [t19188] VALUES (4792,'٤٤٨٠ - مسألة: (ولا يقطع بسرقة حر وإن كان صغيرا. وعنه،','13068','4786','0');
INSERT INTO [t19188] VALUES (4793,'٤٤٨١ - مسألة: فإن كان عليه حلى أو ثياب تبلغ نصابا، لم يقطع','13069','4786','0');
INSERT INTO [t19188] VALUES (4794,'٤٤٨٢ - مسألة: (ولا يقطع بسرقة مصحف. وعند أبى الخطاب، يقطع)','13071','4786','0');
INSERT INTO [t19188] VALUES (4795,'٤٤٨٣ - مسألة: (ويقطع بسرقة سائر كتب العلم)','13073','4786','0');
INSERT INTO [t19188] VALUES (4796,'٤٤٨٤ - مسألة: (ولا يقطع بسرقة آلة لهو، ولا محرم، كالخمر)','13073','4786','0');
INSERT INTO [t19188] VALUES (4797,'٤٤٨٥ - مسألة: (وإن سرق آنية فيها الخمر، أو صليبا، أو صنم ذهب، لم يقطع. وعند أبى الخطاب، يقطع)','13075','4786','0');
INSERT INTO [t19188] VALUES (4798,'فصل','13077','4786','0');
INSERT INTO [t19188] VALUES (4799,'٤٤٨٦ - مسألة: (وإن سرق نصابا، ثم نقصت قيمته، أو ملكه ببيع أو هبة، أو غيرهما، لم يسقط القطع)','13085','4786','0');
INSERT INTO [t19188] VALUES (4800,'٤٤٨٧ - مسألة: وإن ملك العين المسروقة بهبة أو بيع أو غير ذلك من أسباب الملك، وكان ملكها قبل رفعه إلى الحاكم، والمطالبة بها عنده، لم يجب القطع. وبهذا قال مالك، والشافعى، وإسحاق، وأصحاب الرأى، ولا نعلم فيه خلافا. وإن ملكها بعده، لم يسقط القطع عند مالك، والشافعى، وإسحاق. وقال أصحاب الرأى: يسقط؛ لأنها صارت ملكه، فلا يقطع فى عين [هى ملكه، كما لو ملكها قبل المطالبة بها، ولأن المطالبة شرط، والشروط يعتبر دوامها، ولم يبق لهذه العين مطالب]','13087','4786','0');
INSERT INTO [t19188] VALUES (4801,'٤٤٨٨ - مسألة: (وإن دخل الحرز، فذبح شاة قيمتها نصاب، فنقصت عن النصاب، ثم أخرجها، لم يقطع)','13089','4786','0');
INSERT INTO [t19188] VALUES (4802,'٤٤٨٩ - مسألة: (وإن سرق فرد خف، قيمته منفردا درهمان، وقيمته مع الآخر أربعة، لم يقطع)','13089','4786','0');
INSERT INTO [t19188] VALUES (4803,'٤٤٩٠ - مسألة: (وإن اشترك جماعة فى سرقة نصاب، قطعوا، سواء أخرجوه جملة، أو أخرج كل واحد جزءا)','13090','4786','0');
INSERT INTO [t19188] VALUES (4804,'٤٤٩١ - مسألة: (وإن هتك اثنان حرزا، ودخلاه، فأخرج أحدهما نصابا وحده، أو دخل أحدهما فقدمه إلى باب النقب، وأدخل الآخر يده فأخرجه، قطعا)','13093','4786','0');
INSERT INTO [t19188] VALUES (4805,'٤٤٩٢ - مسألة: فإن نقبا حرزا، فدخل أحدهما فقرب المتاع من النقب، وأدخل الخارج','13094','4786','0');
INSERT INTO [t19188] VALUES (4806,'٤٤٩٣ - مسألة: (وإن رماه الداخل إلى خارج، فأخذه الآخر، فالقطع على الداخل وحده)','13095','4786','0');
INSERT INTO [t19188] VALUES (4807,'٤٤٩٤ - مسألة: (وإن نقب أحدهما، ودخل الآخر فأخرجه، فلا قطع عليهما. ويحتمل أن يقطعا)','13095','4786','0');
INSERT INTO [t19188] VALUES (4808,'٤٤٩٥ - مسألة: (إلا أن ينقب)','13096','4786','0');
INSERT INTO [t19188] VALUES (4809,'فصل','13097','4786','0');
INSERT INTO [t19188] VALUES (4810,'٤٤٩٦ - مسألة: (فإن سرق من غير حرز)','13098','4786','0');
INSERT INTO [t19188] VALUES (4811,'٤٤٩٧ - مسألة: (فإن دخل الحرز، فأتلف فيه)','13098','4786','0');
INSERT INTO [t19188] VALUES (4812,'٤٤٩٨ - مسألة: (وإن ابتلع جوهرا أو ذهبا فخرج به، أو نقب ودخل، فترك المتاع على بهيمة، فخرجت به، أو فى ماء جار،','13098','4786','0');
INSERT INTO [t19188] VALUES (4813,'٤٤٩٩ - مسألة: (والحرز ما جرت العادة بحفظ المال فيه، ويختلف باختلاف الأموال، والبلدان، وعدل السلطان وجوره، وقوته وضعفه)','13103','4786','0');
INSERT INTO [t19188] VALUES (4814,'٤٥٠٠ - مسألة: إذا ثبت ذلك (فخرز الأثمان والجواهر والقماش فى الدور، والدكاكين العمران، وراء الأبواب والأغلاق الوثيقة)','13104','4786','0');
INSERT INTO [t19188] VALUES (4815,'٤٥٠١ - مسألة: (وحرز البقل، والباقلاء، ونحوه، وقدوره','13106','4786','0');
INSERT INTO [t19188] VALUES (4816,'٤٥٠٢ - مسألة: (وحرز الخشب والحطب الحظائر)','13107','4786','0');
INSERT INTO [t19188] VALUES (4817,'٤٥٠٣ - مسألة: (وحرز المواشى الصير','13107','4786','0');
INSERT INTO [t19188] VALUES (4818,'٤٥٠٤ - مسألة: (وحرز حمولة الإبل بتقطيرها، وقائدها وسائقها، إذا كان يراها)','13108','4786','0');
INSERT INTO [t19188] VALUES (4819,'٤٥٠٥ - مسألة: (وحرز الثياب فى الحمام بالحافظ)','13110','4786','0');
INSERT INTO [t19188] VALUES (4820,'٤٥٠٦ - مسألة: (وحرز الكفن فى القبر على الميت، فلو نبش','13112','4786','0');
INSERT INTO [t19188] VALUES (4821,'٤٥٠٧ - مسألة: (وحرز الباب تركيبه فى موضعه)','13116','4786','0');
INSERT INTO [t19188] VALUES (4822,'٤٥٠٨ - مسألة: (فلو سرق رتاج الكعبة، أو باب مسجد، أو تأزيره','13117','4786','0');
INSERT INTO [t19188] VALUES (4823,'٤٥٠٩ - مسألة: (وإن سرق قناديل المسجد، أو حصره، فعلى وجهين)','13119','4786','0');
INSERT INTO [t19188] VALUES (4824,'٤٥١٠ - مسألة: (وإن نام إنسان على ردائه فى المسجد، فسرقه سارق، قطع)','13120','4786','0');
INSERT INTO [t19188] VALUES (4825,'٤٥١١ - مسألة: (وإن سرق من السوق غزلا، وثم حافظ، قطع)','13120','4786','0');
INSERT INTO [t19188] VALUES (4826,'٤٥١٢ - مسألة: (ومن سرق من النخل أو الشجر من غير حرز، فلا قطع عليه، ويضمن عوضها مرتين)','13121','4786','0');
INSERT INTO [t19188] VALUES (4827,'٤٥١٣ - مسألة: (وقال أبو بكر: ما كان حرزا لمال، فهو حرز لمال آخر)','13125','4786','0');
INSERT INTO [t19188] VALUES (4828,'فصل','13126','4786','0');
INSERT INTO [t19188] VALUES (4829,'٤٥١٤ - مسألة: (ولا يقطع العبد بالسرقة من مال سيده)','13128','4786','0');
INSERT INTO [t19188] VALUES (4830,'٤٥١٥ - مسألة: (ولا)','13130','4786','0');
INSERT INTO [t19188] VALUES (4831,'٤٥١٦ - مسألة: (ولا)','13131','4786','0');
INSERT INTO [t19188] VALUES (4832,'٤٥١٧ - مسألة: (ومن سرق من الغنيمة ممن له فيها حق، أو لولده، أو لسيده، لم يقطع)','13132','4786','0');
INSERT INTO [t19188] VALUES (4833,'٤٥١٨ - مسألة: (وهل يقطع أحد الزوجين بالسرقة من مال الآخر المحرز عنه؟ على روايتين)','13133','4786','0');
INSERT INTO [t19188] VALUES (4834,'٤٥١٩ - مسألة: (ويقطع سائر الأقارب بالسرقة من مال أقاربهم)','13134','4786','0');
INSERT INTO [t19188] VALUES (4835,'٤٥٢٠ - مسألة: (ويقطع المسلم بالسرقة من مال الذمى والمستأمن، ويقطعان بسرقة ماله)','13135','4786','0');
INSERT INTO [t19188] VALUES (4836,'٤٥٢١ - مسألة: (ومن سرق عينا، وادعى أنها ملكه، لم يقطع. وعنه، يقطع. وعنه، لا يقطع، إلا أن يكون معروفا بالسرقة)','13136','4786','0');
INSERT INTO [t19188] VALUES (4837,'٤٥٢٢ - مسألة: (وإذا سرق المسروق منه مال السارق، أو المغصوب منه مال الغاصب من الحرز الذى فيه العين المسروقة أو المغصوبة، لم يقطع، وإن سرق من غير ذلك، أو سرق من مال من له عليه دين، قطع، إلا أن يعجز عن أخذه منه، فيسرق قدر حقه، فلا','13138','4786','0');
INSERT INTO [t19188] VALUES (4838,'٤٥٢٣ - مسألة: (ومن أجر داره، أو أعارها، ثم سرق منها مال المستعير أو المستأجر، قطع)','13142','4786','0');
INSERT INTO [t19188] VALUES (4839,'٤٥٢٤ - مسألة: وإن استعار دارا فنقبها المعير، وسرق مال المستعير منها، قطع أيضا','13142','4786','0');
INSERT INTO [t19188] VALUES (4840,'فصل','13144','4786','0');
INSERT INTO [t19188] VALUES (4841,'٤٥٢٥ - مسألة: (ولا ينزع عن إقراره حتى يقطع)','13149','4786','0');
INSERT INTO [t19188] VALUES (4842,'فصل','13154','4786','0');
INSERT INTO [t19188] VALUES (4843,'٤٥٢٦ - مسألة: (وإذا وجب القطع، قطعت يده اليمنى من مفصل الكف، وحسمت؛ وهو أن تغمس فى زيت مغلى، فإن عاد،','13154','4786','0');
INSERT INTO [t19188] VALUES (4844,'٤٥٢٧ - مسألة: (فإن عاد، حبس، ولم يقطع. وعنه، أنه تقطع يده اليسرى فى الثالثة والرجل اليمنى فى الرابعة)','13159','4786','0');
INSERT INTO [t19188] VALUES (4845,'٤٥٢٨ - مسألة: (ومن سرق وليس له يد يمنى، قطعت رجله','13163','4786','0');
INSERT INTO [t19188] VALUES (4846,'٤٥٢٩ - مسألة: (وإن سرق وله يمنى، فذهبت، سقط القطع)','13165','4786','0');
INSERT INTO [t19188] VALUES (4847,'٤٥٣٠ - مسألة: (وإن ذهبت يده اليسرى)','13166','4786','0');
INSERT INTO [t19188] VALUES (4848,'٤٥٣١ - مسألة: إن وجب قطع يمناه، فقطع القاطع يسراه، بدلا عن يمينه، أجزأت، ولا شئ على القاطع إلا الأدب. وهو قول الشعبى، وأصحاب الرأى؛ لأن قطع يمنى السارق يفضى إلى تفويت منفعة الجنس، وقطع يديه بسرقة واحدة، فلا يشرع، فإذا انتفى قطع يمينه، حصل قطع يساره مجزئا عن القطع الواجب، فلا يجب على فاعله قصاص. وقال أصحابنا: (فى)','13168','4786','0');
INSERT INTO [t19188] VALUES (4849,'٤٥٣٢ - مسألة: (ويجتمع القطع والضمان، فترد العين','13169','4786','0');
INSERT INTO [t19188] VALUES (4850,'٤٥٣٣ - مسألة: (وهل يجب الزيت الذى يحسم به من بيت المال أو من مال السارق؟ على وجهين)','13174','4786','0');
INSERT INTO [t19188] VALUES (4851,'باب حد المحاربين','13179','4666','0');
INSERT INTO [t19188] VALUES (4852,'٤٥٣٤ - مسألة: (وهم الذين يعرضون للناس بالسلاح فى الصحراء، فيغصبونهم المال مجاهرة، فأما من يأخذه على وجه السرقة فليس بمحارب)','13181','4851','0');
INSERT INTO [t19188] VALUES (4853,'٤٥٣٥ - مسألة: (وإن فعلوا ذلك فى البنيان، لم يكونوا محاربين فى قول الخرقى)','13182','4851','0');
INSERT INTO [t19188] VALUES (4854,'٤٥٣٦ - مسألة: (فإذا قدر عليهم، فمن كان منهم قد قتل من يكافئه وأخذ المال، قتل حتما، وصلب حتى يشتهر. وقال أبو بكر: يصلب قدر ما يقع عليه اسم الصلب. وعن أحمد، أنه يقطع مع ذلك)','13184','4851','0');
INSERT INTO [t19188] VALUES (4855,'٤٥٣٧ - مسألة: (وإن قتل من لا يكافئه، فهل يقتل؟ على روايتين)','13188','4851','0');
INSERT INTO [t19188] VALUES (4856,'٤٥٣٨ - مسألة: (وإن جنى جناية توجب القصاص فيما دون النفس، فهل يتحتم استيفاؤه؟ على روايتين)','13191','4851','0');
INSERT INTO [t19188] VALUES (4857,'٤٥٣٩ - مسألة: (وحكم الردء','13193','4851','0');
INSERT INTO [t19188] VALUES (4858,'٤٥٤٠ - مسألة: (ومن قتل ولم يأخذ المال، قتل. وهل يصلب؟ على روايتين)','13196','4851','0');
INSERT INTO [t19188] VALUES (4859,'٤٥٤١ - مسألة: (ومن أخذ المال ولم يقتل، قطعت يده اليمنى ورجله اليسرى فى مقام واحد، وحسمتا، وخلى)','13197','4851','0');
INSERT INTO [t19188] VALUES (4860,'٤٥٤٢ - مسألة: (ولا يقطع منهم إلا من أخذ ما يقطع السارق فى مثله)','13198','4851','0');
INSERT INTO [t19188] VALUES (4861,'٤٥٤٣ - مسألة: (فإن كانت يمينه مقطوعة، أو مستحقة فى قصاص، أو شلاء، قطعت رجله اليسرى، وهل تقطع يسرى يديه؟ ينبنى على الروايتين فى قطع يسرى السارق، فى المرة الثالثة)','13199','4851','0');
INSERT INTO [t19188] VALUES (4862,'٤٥٤٤ - مسألة: (ومن لم يقتل ولا أخذ المال، نفى وشرد، فلا','13200','4851','0');
INSERT INTO [t19188] VALUES (4863,'٤٥٤٥ - مسألة: (ومن تاب قبل القدرة عليه، سقطت عنه حدود الله تعالى؛ من الصلب والقطع والنفى وانحتام القتل، وأخذ بحقوق الآدميين؛ من الأنفس، والجراح، والأموال، إلا أن يعفى له عنها)','13203','4851','0');
INSERT INTO [t19188] VALUES (4864,'٤٥٤٦ - مسألة: (ومن وجب عليه حد لله تعالى سوى ذلك، فتاب قبل إقامته، لم يسقط)','13205','4851','0');
INSERT INTO [t19188] VALUES (4865,'فصل','13210','4851','0');
INSERT INTO [t19188] VALUES (4866,'٤٥٤٧ - مسألة: (ومن مات وعليه حد، سقط عنه)','13210','4851','0');
INSERT INTO [t19188] VALUES (4867,'٤٥٤٨ - مسألة: فإن أردت نفسه، لم يلزمه الدفع؛ لأن النبي - صلى الله عليه وسلم - قال في الفتنة: «اجلس في بيتك فإن خفت أن يبهرك شعاع السيف، فغط وجهك»','13216','4851','0');
INSERT INTO [t19188] VALUES (4868,'٤٥٤٩ - مسألة: (وإن عض إنسان إنسانا، فانتزع يده من فيه، فسقطت ثناياه، ذهبت هدرا)','13222','4851','0');
INSERT INTO [t19188] VALUES (4869,'٤٥٥٠ - مسألة: (وإن نظر في بيته من خصاص الباب، أو نحوه،','13224','4851','0');
INSERT INTO [t19188] VALUES (4870,'باب قتال أهل البغي','13229','4666','0');
INSERT INTO [t19188] VALUES (4871,'٤٥٥١ - مسألة: (وهم القوم الذين يخرجون على الإمام بتأويل سائغ، ولهم منعة وشوكة)','13232','4870','0');
INSERT INTO [t19188] VALUES (4872,'٤٥٥٢ - مسألة: (وعلى الإمام أن يراسلهم، ويسألهم ما ينقمون منه، ويزيل ما يذكرونه من مظلمة، ويكشف ما يدعونه من شبهة،','13239','4870','0');
INSERT INTO [t19188] VALUES (4873,'٤٥٥٣ - مسألة: (وعلى رعيته معونته على حربهم)','13242','4870','0');
INSERT INTO [t19188] VALUES (4874,'٤٥٥٤ - مسألة: (فإن استنظروه مدة، رجاء رجوعهم فيها،','13242','4870','0');
INSERT INTO [t19188] VALUES (4875,'٤٥٥٥ - مسألة: (وإن ظن أنها مكيدة، لم ينظرهم، وقاتلهم)','13243','4870','0');
INSERT INTO [t19188] VALUES (4876,'٤٥٥٦ - مسألة: (ولا يقاتلهم بما يعم إتلافه، كالمنجنيق، والنار، إلا لضرورة)','13246','4870','0');
INSERT INTO [t19188] VALUES (4877,'٤٥٥٧ - مسألة: (ولا يستعين في حربهم بكافر)','13247','4870','0');
INSERT INTO [t19188] VALUES (4878,'٤٥٥٨ - مسألة: (وهل يجوز أن يستعين عليهم بسلاحهم، وكراعهم','13248','4870','0');
INSERT INTO [t19188] VALUES (4879,'٤٥٥٩ - مسألة: وذكر القاضي، أن أحمد أومأ إلى جواز الانتفاع به حال التحام','13248','4870','0');
INSERT INTO [t19188] VALUES (4880,'٤٥٦٠ - مسألة: (ولا يتبع لهم مدبر، ولا يجاز على جريح)','13249','4870','0');
INSERT INTO [t19188] VALUES (4881,'٤٥٦١ - مسألة: (ولا يغنم لهم مال، ولا تسبى لهم ذرية)','13251','4870','0');
INSERT INTO [t19188] VALUES (4882,'٤٥٦٢ - مسألة: (ومن أسر من رجالهم، حبس حتى تنقضي الحرب، ثم يرسل)','13253','4870','0');
INSERT INTO [t19188] VALUES (4883,'٤٥٦٣ - مسألة: (وإن أسر صبي أو امرأة، فهل يفعل به ذلك،','13253','4870','0');
INSERT INTO [t19188] VALUES (4884,'٤٥٦٤ - مسألة: (وإذا انقضى الحرب، فمن وجد منهم ماله في يد إنسان أخذه)','13255','4870','0');
INSERT INTO [t19188] VALUES (4885,'٤٥٦٥ - مسألة: (ولا يضمن أهل العدل ما أتلفوه عليهم حال الحرب، من نفس أو مال. وهل يضمن البغاة ما أتلفوه على أهل العدل في الحرب؟ على روايتين)','13255','4870','0');
INSERT INTO [t19188] VALUES (4886,'٤٥٦٦ - مسألة: (ومن أتلف في غير حال الحرب شيئا، ضمنه)','13258','4870','0');
INSERT INTO [t19188] VALUES (4887,'٤٥٦٧ - مسألة: (وما أخذوا في حال امتناعهم من زكاة، أو خراج، أو جزية لم يعد عليهم، ولا على صاحبه)','13263','4870','0');
INSERT INTO [t19188] VALUES (4888,'٤٥٦٨ - مسألة: (ومن ادعى دفع زكاته إليهم، قبل بغير يمين)','13264','4870','0');
INSERT INTO [t19188] VALUES (4889,'٤٥٦٩ - مسألة: (وإن ادعى ذمي دفع جزيته إليهم، لم يقبل إلا ببينة)','13264','4870','0');
INSERT INTO [t19188] VALUES (4890,'٤٥٧٠ - مسألة: (وإن ادعى دفع خراجه إليهم، فهل يقبل بغير بينة؟ على وجهين)','13265','4870','0');
INSERT INTO [t19188] VALUES (4891,'٤٥٧١ - مسألة: (وتجوز شهادتهم)','13266','4870','0');
INSERT INTO [t19188] VALUES (4892,'٤٥٧٢ - مسألة: (ولا ينقض من حكم حاكمهم، إلا ما ينقض من حكم غيره)','13266','4870','0');
INSERT INTO [t19188] VALUES (4893,'٤٥٧٣ - مسألة: (وإن استعانوا بأهل الذمة، فأعانوهم، انتقض عهدهم، إلا أن يدعوا أنهم ظنوا أنه يجب عليهم معونة من استعان بهم من المسلمين، ونحو ذلك، فلا ينتقض عهدهم)','13269','4870','0');
INSERT INTO [t19188] VALUES (4894,'٤٥٧٤ - مسألة: (وإن استعانوا بأهل الحرب وأمنوهم، لم يصح أمانهم، وأبيح قتلهم)','13271','4870','0');
INSERT INTO [t19188] VALUES (4895,'٤٥٧٥ - مسألة: (وإن أظهر قوم رأي الخوارج، ولم يجتمعوا لحرب، لم يتعرض لهم)','13272','4870','0');
INSERT INTO [t19188] VALUES (4896,'٤٥٧٦ - مسألة: (فإن سبوا الإمام، عزرهم)','13274','4870','0');
INSERT INTO [t19188] VALUES (4897,'٤٥٧٧ - مسألة: (وإن جنوا جناية، أو أتوا حدا، أقامه عليهم)','13279','4870','0');
INSERT INTO [t19188] VALUES (4898,'٤٥٧٨ - مسألة: (وإن اقتتلت طائفتان لعصبية، أو طلب رياسة، فهما ظالمتان، وتضمن كل واحدة)','13280','4870','0');
INSERT INTO [t19188] VALUES (4899,'باب حكم المرتد','13281','4666','0');
INSERT INTO [t19188] VALUES (4900,'٤٥٧٩ - مسألة: (فمن أشرك بالله تعالى، أو جحد ربوبيته، أو','13281','4899','0');
INSERT INTO [t19188] VALUES (4901,'٤٥٨٠ - مسألة: (فإن جحد وجوب العبادات الخمس، أو شيئا منها، أو أحل الزنى، أو الخمر، أو شيئا من المحرمات الظاهرة المجمع)','13283','4899','0');
INSERT INTO [t19188] VALUES (4902,'٤٥٨١ - مسألة: (ومن ترك شيئا من العبادات الخمس تهاونا،','13287','4899','0');
INSERT INTO [t19188] VALUES (4903,'٤٥٨٢ - مسألة: (ومن ارتد عن الإسلام من الرجال والنساء،','13288','4899','0');
INSERT INTO [t19188] VALUES (4904,'٤٥٨٣ - مسألة: (ويقتل بالسيف)','13295','4899','0');
INSERT INTO [t19188] VALUES (4905,'٤٥٨٤ - مسألة: (ولا يقتله إلا الإمام أو نائبه)','13296','4899','0');
INSERT INTO [t19188] VALUES (4906,'٤٥٨٥ - مسألة: (فإن قتله غيره بغير إذنه، أساء، وعزر)','13296','4899','0');
INSERT INTO [t19188] VALUES (4907,'٤٥٨٦ - مسألة: (وإن عقل الصبي الإسلام، صح إسلامه وردته. وعنه، يصح إسلامه دون ردته. وعنه، لا يصح شيء منهما حتى يبلغ. والمذهب الأول)','13297','4899','0');
INSERT INTO [t19188] VALUES (4908,'٤٥٨٧ - مسألة: (وإن أسلم، ثم قال: لم أدر ما قلت. لم يلتفت','13301','4899','0');
INSERT INTO [t19188] VALUES (4909,'٤٥٨٨ - مسألة: (ولا يقتل حتى يبلغ، ويجاوز ثلاثة أيام من وقت بلوغه، فإن ثبت على كفره قتل)','13303','4899','0');
INSERT INTO [t19188] VALUES (4910,'٤٥٨٩ - مسألة: (ومن ارتد وهو سكران، لم يقتل حتى يصحو، وتتم له ثلاثة أيام من وقت ردته، فإن مات في سكره، مات كافرا. وعنه، لا تصح ردته)','13304','4899','0');
INSERT INTO [t19188] VALUES (4911,'٤٥٩٠ - مسألة: (وهل تقبل توبة الزنديق، ومن تكررت ردته، أو من يسب الله تعالى أو رسوله، أو الساحر؟ على روايتين؛ إحداهما، لا تقبل توبته، ويقتل بكل حال. والأخرى، تقبل توبته كغيره)','13307','4899','0');
INSERT INTO [t19188] VALUES (4912,'٤٥٩١ - مسألة: (وتوبة المرتد إسلامه، وهو أن يشهد أن لا إله إلا الله، وأن محمدا عبده ورسوله. إلا أن تكون ردته بإنكار فرض، أو إحلال محرم، أو جحد نبي أو كتاب، أو إلى دين من يعتقد أن محمدا','13315','4899','0');
INSERT INTO [t19188] VALUES (4913,'٤٥٩٢ - مسألة: وإذا أتى الكافر بالشهادتين، ثم قال: لم أرد الإسلام. صار بذلك مرتدا، ويجبر على الإسلام. نص عليه أحمد، في رواية جماعة، ونقل عن أحمد، أنه يقبل منه، ولا يجبر على الإسلام؛ لأنه يحتمل الصدق، فلا يراق دمه بالشبهة','13321','4899','0');
INSERT INTO [t19188] VALUES (4914,'٤٥٩٣ - مسألة: (وإذا مات المرتد، فأقام وارثه بينة أنه صلى بعد الردة، حكم بإسلامه)','13321','4899','0');
INSERT INTO [t19188] VALUES (4915,'٤٥٩٤ - مسألة: (ولا يبطل إحصان المسلم بردته، ولا عباداته التي فعلها في إسلامه، إذا عاد إلى الإسلام)','13323','4899','0');
INSERT INTO [t19188] VALUES (4916,'٤٥٩٥ - مسألة: (وتقضى ديونه وأروش جناياته، وينفق على من تلزمه مؤنته)','13329','4899','0');
INSERT INTO [t19188] VALUES (4917,'٤٥٩٦ - مسألة: (وما أتلف من شيء، ضمنه، ويتخرج في الجماعة الممتنعة أن لا تضمن ما أتلفته)','13331','4899','0');
INSERT INTO [t19188] VALUES (4918,'٤٥٩٧ - مسألة: (وإذا أسلم، فهل يلزمه قضاء ما ترك من العبادات؟ على روايتين)','13333','4899','0');
INSERT INTO [t19188] VALUES (4919,'٤٥٩٨ - مسألة: (وإذا ارتد الزوجان، ولحقا بدار الحرب، ثم قدر عليهما، لم يجز استرقاقهما، ولا استرقاق أولادهما الذين ولدوا في الإسلام، ومن لم يسلم منهم قتل. ويجوز استرقاق من ولد بعد الردة، وهل يقرون على كفرهم؟ على روايتين)','13335','4899','0');
INSERT INTO [t19188] VALUES (4920,'٤٥٩٩ - مسألة: (فأما الذي يسحر بالأدوية، والتدخين، وسقي شيء يضر، فلا يكفر ولا يقتل)','13362','4899','0');
INSERT INTO [t19188] VALUES (4921,'٤٦٠٠ - مسألة: (وأما الذي يعزم على الجن، ويزعم أنه يجمعها فتطيعه، فلا يكفر ولا يقتل. وذكره أبو الخطاب في السحرة الذين يقتلون)','13363','4899','0');
INSERT INTO [t19188] VALUES (4922,'كتاب الأطعمة','13368','0','0');
INSERT INTO [t19188] VALUES (4923,'[باب. . .]','13368','4922','0');
INSERT INTO [t19188] VALUES (4924,'٤٦٠١ - مسألة: (فيحل كل طعام طاهر لا مضرة فيه من الحبوب','13368','4923','0');
INSERT INTO [t19188] VALUES (4925,'٤٦٠٢ - مسألة: (والحيوانات مباحة)','13370','4923','0');
INSERT INTO [t19188] VALUES (4926,'٤٦٠٣ - مسألة: (وما له ناب يفرس به؛ كالأسد، والنمر، والذئب، والفهد، والكلب، والخنزير، وابن آوى، والسنور، وابن عرس، والنمس، والقرد، إلا الضبع)','13372','4923','0');
INSERT INTO [t19188] VALUES (4927,'٤٦٠٤ - مسألة: (وما له مخلب من الطير يصيد به، كالعقاب والبازي، والصقر، والشاهين، والحدأة، والبومة)','13375','4923','0');
INSERT INTO [t19188] VALUES (4928,'٤٦٠٥ - مسألة: (وما يأكل الجيف؛ كالنسر، والرخم','13377','4923','0');
INSERT INTO [t19188] VALUES (4929,'٤٦٠٦ - مسألة: (وما يستخبث؛ كالقنفذ، والفأر، والحيات، والحشرات كلها)','13379','4923','0');
INSERT INTO [t19188] VALUES (4930,'٤٦٠٧ - مسألة: (وما تولد من مأكول وغيره؛ كالبغل، والسمع، والعسبار، والسمع ولد الضبع من الذئب. وقيل: يسمى العسبار. والعسبار ولد الذئب من الذيخ)','13383','4923','0');
INSERT INTO [t19188] VALUES (4931,'٤٦٠٨ - مسألة: (وفي الثعلب، والوبر','13384','4923','0');
INSERT INTO [t19188] VALUES (4932,'٤٦٠٩ - مسألة: (وما عدا هذا فمباح؛ كبهيمة الأنعام، والخيل، والدجاج)','13389','4923','0');
INSERT INTO [t19188] VALUES (4933,'٤٦١٠ - مسألة: (والوحشي من البقر، والظباء، والحمر)','13391','4923','0');
INSERT INTO [t19188] VALUES (4934,'٤٦١١ - مسألة: (والأرنب)','13392','4923','0');
INSERT INTO [t19188] VALUES (4935,'٤٦١٢ - مسألة: (وسائر الوحش)','13394','4923','0');
INSERT INTO [t19188] VALUES (4936,'٤٦١٣ - مسألة: (والزاغ','13398','4923','0');
INSERT INTO [t19188] VALUES (4937,'٤٦١٤ - مسألة: (وجميع حيوان البحر)','13399','4923','0');
INSERT INTO [t19188] VALUES (4938,'٤٦١٥ - مسألة: (وتحرم الجلالة التي أكثر علفها النجاسة، وبيضها، ولبنها. وعنه، تكره ولا تحرم)','13403','4923','0');
INSERT INTO [t19188] VALUES (4939,'٤٦١٦ - مسألة: (حتى تحبس)','13405','4923','0');
INSERT INTO [t19188] VALUES (4940,'٤٦١٧ - مسألة: (وما سقي بالماء النجس من الزرع والثمار محرم)','13407','4923','0');
INSERT INTO [t19188] VALUES (4941,'٤٦١٨ - مسألة: (فإن وجد طعاما لا يعرف مالكه، وميتة، أو صيدا، وهو محرم، فقال أصحابنا: يأكل الميتة. ويحتمل أن يحل له الطعام والصيد إذا لم تقبل نفسه الميتة)','13416','4923','0');
INSERT INTO [t19188] VALUES (4942,'٤٦١٩ - مسألة: (وإن لم يجد إلا طعاما لم يبذله مالكه؛ فإن كان صاحبه مضطرا إليه، فهو أحق به، وإلا لزمه بذله، فإن أبى، فللمضطر أخذه قهرا، ويعطيه قيمته، فإن منعه، فله قتاله على ما يسد رمقه، أو قدر شبعه، على اختلاف الروايتين. فإن قتل صاحب الطعام، لم يجب ضمانه، وإن قتل المضطر، فعليه ضمانه)','13420','4923','0');
INSERT INTO [t19188] VALUES (4943,'٤٦٢٠ - مسألة: (فإن لم يجد إلا آدميا مباح الدم، كالمرتد، والزاني المحصن، حل)','13424','4923','0');
INSERT INTO [t19188] VALUES (4944,'٤٦٢١ - مسألة: (وفي الزرع وشرب لبن الماشية روايتان)','13432','4923','0');
INSERT INTO [t19188] VALUES (4945,'٤٦٢٢ - مسألة: (ويجب على المسلم ضيافة المسلم المجتاز به يوما وليلة، فإن أبى فللضيف طلبه به عند الحاكم)','13437','4923','0');
INSERT INTO [t19188] VALUES (4946,'٤٦٢٣ - مسألة: (وتستحب ضيافته ثلاثة أيام، فما زاد فهو صدقة)','13442','4923','0');
INSERT INTO [t19188] VALUES (4947,'٤٦٢٤ - مسألة: (وليس عليه إنزال الضيف في بيته)','13443','4923','0');
INSERT INTO [t19188] VALUES (4948,'باب الذكاة','13451','4922','0');
INSERT INTO [t19188] VALUES (4949,'٤٦٢٥ - مسألة: (ولا يباح شيء من الحيوان المقدور عليه بغير ذكاة إلا الجراد وشبهه، والسمك، وسائر ما لا يعيش إلا في الماء، فلا','13451','4948','0');
INSERT INTO [t19188] VALUES (4950,'٤٦٢٦ - مسألة: (وعنه، في السرطان وسائر البحري، أنه يحل بلا ذكاة)','13454','4948','0');
INSERT INTO [t19188] VALUES (4951,'٤٦٢٧ - مسألة: (وعنه، في الجراد، لا يؤكل إلا أن يموت بسبب، ككبسه وتغريقه)','13456','4948','0');
INSERT INTO [t19188] VALUES (4952,'٤٦٢٨ - مسألة: (ويشترط للذكاة شروط أربعة؛ أحدها، أهلية الذابح، وهو أن يكون عاقلا، مسلما أو كتابيا، فتباح ذبيحته، ذكرا كان أو أنثى. وعنه، لا تباح ذبيحة نصارى بني تغلب، ولا من أحد أبويه غير كتابي)','13459','4948','0');
INSERT INTO [t19188] VALUES (4953,'٤٦٢٩ - مسألة: (ولا تباح ذكاة مجنون، ولا سكران، ولا طفل غير مميز، ولا مجوسي، ولا وثني، ولا مرتد)','13464','4948','0');
INSERT INTO [t19188] VALUES (4954,'٤٦٣٠ - مسألة: (فإن ذبح بآلة مغصوبة، حل في أصح الوجهين)','13471','4948','0');
INSERT INTO [t19188] VALUES (4955,'٤٦٣١ - مسألة: (وإن نحره، أجزأ، وهو أن يطعنه بمحدد في لبته. ويستحب أن ينحر البعير، ويذبح ما سواه)','13476','4948','0');
INSERT INTO [t19188] VALUES (4956,'٤٦٣٢ - مسألة: (فإن عجز عن ذلك)','13479','4948','0');
INSERT INTO [t19188] VALUES (4957,'٤٦٣٣ - مسألة: (وإن ذبحها من قفاها وهو مخطئ، فأتت','13481','4948','0');
INSERT INTO [t19188] VALUES (4958,'٤٦٣٤ - مسألة: (وكل ما وجد فيه سبب الموت؛ كالمنخنقة)','13485','4948','0');
INSERT INTO [t19188] VALUES (4959,'٤٦٣٥ - مسألة: (إلا الأخرس، فإنه يومئ برأسه إلى السماء)','13492','4948','0');
INSERT INTO [t19188] VALUES (4960,'٤٦٣٦ - مسألة: (فإن ترك التسمية عمدا، لم تبح، وإن تركها ساهيا، أبيحت. وعنه، تباح في الحالين. وعنه، لا تباح فيهما)','13494','4948','0');
INSERT INTO [t19188] VALUES (4961,'٤٦٣٧ - مسألة: (وتحصل ذكاة الجنين بذكاة أمه إذا خرج ميتا، أو متحركا كحركة المذبوح، وإن كانت فيه حياة مستقرة، لم يبح إلا بذبحه، وسواء أشعر أو لم يشعر)','13499','4948','0');
INSERT INTO [t19188] VALUES (4962,'٤٦٣٨ - مسألة: (و)','13503','4948','0');
INSERT INTO [t19188] VALUES (4963,'٤٦٣٩ - مسألة: (فإن فعل، أساء، وأكلت)','13504','4948','0');
INSERT INTO [t19188] VALUES (4964,'٤٦٤٠ - مسألة: (وإذا ذبح الحيوان، ثم غرق في ماء، أو وطئ عليه شيء يقتله مثله، فهل يحل؟ على روايتين)','13505','4948','0');
INSERT INTO [t19188] VALUES (4965,'٤٦٤١ - مسألة: (وإذا ذبح الكتابي ما يحرم عليه، كذي الظفر، لم يحرم علينا)','13506','4948','0');
INSERT INTO [t19188] VALUES (4966,'٤٦٤٢ - مسألة: (وإن ذبح لعيده، أو ليتقرب به إلى شيء مما يعظمونه، لم يحرم. نص عليه)','13510','4948','0');
INSERT INTO [t19188] VALUES (4967,'٤٦٤٣ - مسألة: (ومن ذبح حيوانا، فوجد في بطنه جرادا، أو','13513','4948','0');
INSERT INTO [t19188] VALUES (4968,'كتاب الصيد','13515','0','0');
INSERT INTO [t19188] VALUES (4969,'٤٦٤٤ - مسألة: (ومن صاد صيدا، فأدركه حيا حياة مستقرة، لم يحل إلا بالذكاة)','13517','4968','0');
INSERT INTO [t19188] VALUES (4970,'٤٦٤٥ - مسألة: (فإن لم يجد ما يذكيه به، أرسل الصائد له عليه حتى يقتله، في إحدى الروايتين. واختاره الخرقي)','13519','4968','0');
INSERT INTO [t19188] VALUES (4971,'٤٦٤٦ - مسألة: (فإن لم يفعل وتركه حتى مات، لم يحل. وقال القاضي: يحل. والأخرى، لا يحل إلا أن يذكيه)','13520','4968','0');
INSERT INTO [t19188] VALUES (4972,'٤٦٤٧ - مسألة: (وإن رمى صيدا فأثبته، ثم رماه آخر فقتله،','13521','4968','0');
INSERT INTO [t19188] VALUES (4973,'٤٦٤٨ - مسألة: (وإن أدرك الصيد متحركا كحركة المذبوح،','13528','4968','0');
INSERT INTO [t19188] VALUES (4974,'٤٦٤٩ - مسألة: (فإن رمى مسلم ومجوسي صيدا، أو أرسلا عليه جارحا، أو شارك كلب المجوسي كلب المسلم في قتله، لم يحل، وإن أصاب سهم أحدهما المقتل دون الآخر، حل. ويحتمل أن لا يحل)','13530','4968','0');
INSERT INTO [t19188] VALUES (4975,'٤٦٥٠ - مسألة: (وإن رد كلب المجوسي الصيد على كلب المسلم، فقتله، حل)','13534','4968','0');
INSERT INTO [t19188] VALUES (4976,'٤٦٥١ - مسألة: (وإن صاد المسلم بكلب المجوسي، حل)','13534','4968','0');
INSERT INTO [t19188] VALUES (4977,'٤٦٥٢ - مسألة: (وإن صاد المجوسي بكلب المسلم، لم يحل)','13535','4968','0');
INSERT INTO [t19188] VALUES (4978,'٤٦٥٣ - مسألة: (وإن أرسل المسلم كلبا، فزجره المجوسي، حل صيده)','13536','4968','0');
INSERT INTO [t19188] VALUES (4979,'٤٦٥٤ - مسألة: (وإن صاد بالمعراض، أكل ما قتل بحده دون عرضه)','13537','4968','0');
INSERT INTO [t19188] VALUES (4980,'٤٦٥٥ - مسألة: (وإن نصب مناجل أو سكاكين، وسمى عند نصبها، فقتلت صيدا، أبيح)','13539','4968','0');
INSERT INTO [t19188] VALUES (4981,'٤٦٥٦ - مسألة: (وإذا قتل بسهم مسموم، لم يبح، إذا غلب على الظن أن السم أعان على قتله)','13540','4968','0');
INSERT INTO [t19188] VALUES (4982,'٤٦٥٧ - مسألة: (وإن رماه فوقع في ماء، أو تردى من جبل، أو وطئ عليه شيء فقتله، لم يبح، إلا أن تكون الجراحة موحية كالذكاة، فهل يحل؟ على روايتين)','13542','4968','0');
INSERT INTO [t19188] VALUES (4983,'٤٦٥٨ - مسألة: (فإن رماه في الهواء، فوقع على الأرض، فمات، حل)','13545','4968','0');
INSERT INTO [t19188] VALUES (4984,'٤٦٥٩ - مسألة: (وإن رمى صيدا، فغاب، ثم وجده ميتا لا أثر به غير سهمه، حل. وعنه، إن كانت الجراح موحية، حل، وإلا فلا. وعنه، إن وجده في يومه، حل، وإلا فلا. وإن وجد به غير أثر سهمه مما يحتمل أنه أعان على قتله، لم يبح)','13546','4968','0');
INSERT INTO [t19188] VALUES (4985,'٤٦٦٠ - مسألة: (وإن ضربه، فأبان منه عضوا وبقيت فيه حياة مستقرة، لم يبح ما أبان منه، وإن بقي معلقا بجلده، حل، وإن أبانه ومات في الحال، حل الجميع. وعنه، لا يباح ما أبان منه)','13551','4968','0');
INSERT INTO [t19188] VALUES (4986,'٤٦٦١ - مسألة: (وإن أخذ قطعة من حوت، وأفلت حيا، أبيح ما أخذ منه)','13554','4968','0');
INSERT INTO [t19188] VALUES (4987,'٤٦٦٢ - مسألة: (وأما ما ليس بمحدد؛ كالبندق والعصا والحجر والشبكة والفخ، فلا يباح ما قتل به؛ لأنه وقيذ)','13554','4968','0');
INSERT INTO [t19188] VALUES (4988,'٤٦٦٣ - مسألة: (والجوارح نوعان؛ ما يصيد بنابه، كالكلب والفهد، فتعليمه بثلاثة أشياء؛ أن يسترسل إذا أرسل، وينزجر إذا زجر، وإذا أمسك لم يأكل)','13559','4968','0');
INSERT INTO [t19188] VALUES (4989,'٤٦٦٤ - مسألة: (ولا يعتبر تكرر ذلك منه)','13560','4968','0');
INSERT INTO [t19188] VALUES (4990,'٤٦٦٥ - مسألة: (فإن أكل بعد تعلمه، لم يحرم ما تقدم من صيده، ولم يبح ما أكل منه في إحدى الروايتين. والأخرى، يحل)','13562','4968','0');
INSERT INTO [t19188] VALUES (4991,'٤٦٦٦ - مسألة: (ولابد أن يجرح الصيد، فإن قتله بصدمته،','13568','4968','0');
INSERT INTO [t19188] VALUES (4992,'٤٦٦٧ - مسألة: (وما أصابه فم الكلب، هل يجب غسله؟ على وجهين)','13569','4968','0');
INSERT INTO [t19188] VALUES (4993,'٤٦٦٨ - مسألة: (وإن أرسل كلبه إلى هدف فقتل صيدا، أو أرسله يريد الصيد ولا يرى صيدا، لم يحل صيده إذا قتله)','13572','4968','0');
INSERT INTO [t19188] VALUES (4994,'٤٦٦٩ - مسألة؛ (فإن رمى حجرا يظنه صيدا، فأصاب صيدا، لم يحل. ويحتمل أن يحل)','13573','4968','0');
INSERT INTO [t19188] VALUES (4995,'٤٦٧٠ - مسألة: (وإن رمى صيدا، فقتل غيره، أو رمى صيدا، فقتل جماعة، حل)','13574','4968','0');
INSERT INTO [t19188] VALUES (4996,'٤٦٧١ - مسألة: (وإن أرسل سهمه على صيد، فأعانته الريح فقتلته، ولولاها ما وصل، حل)','13576','4968','0');
INSERT INTO [t19188] VALUES (4997,'٤٦٧٢ - مسألة: (وإن رمى صيدا فأثبته، ملكه، فإن تحامل فأخذه غيره، لزمه رده)','13576','4968','0');
INSERT INTO [t19188] VALUES (4998,'٤٦٧٣ - مسألة: (وإن لم يثبته، فدخل خيمة إنسان فأخذه، فهو لآخذه)','13576','4968','0');
INSERT INTO [t19188] VALUES (4999,'٤٦٧٤ - مسألة: (ولو وقع صيد في شبكة إنسان، فخرقها','13577','4968','0');
INSERT INTO [t19188] VALUES (5000,'٤٦٧٥ - مسألة: (ومن كان في سفينة، فوثبت سمكة فوقعت في حجره، فهي له دون صاحب السفينة)','13579','4968','0');
INSERT INTO [t19188] VALUES (5001,'٤٦٧٦ - مسألة. (وإن صنع بركة ليصيد بها السمك، فما حصل','13580','4968','0');
INSERT INTO [t19188] VALUES (5002,'٤٦٧٧ - مسألة: (ويكره صيد السمك بالنجاسة)','13582','4968','0');
INSERT INTO [t19188] VALUES (5003,'٤٦٧٨ - مسألة: (و) يكره (صيد الطير بالشباش)','13584','4968','0');
INSERT INTO [t19188] VALUES (5004,'٤٦٧٩ - مسألة: (وإن أرسل صيدا، وقال: أعتقتك. لم يزل ملكه عنه. ويحتمل أن يزول)','13584','4968','0');
INSERT INTO [t19188] VALUES (5005,'كتاب الأيمان','13590','0','0');
INSERT INTO [t19188] VALUES (5006,'[باب. . .]','13590','5005','0');
INSERT INTO [t19188] VALUES (5007,'٤٦٨٠ - مسألة: (واليمين التي تجب بها الكفارة، هي اليمين بالله تعالى، أو صفة من صفاته)','13598','5006','0');
INSERT INTO [t19188] VALUES (5008,'٤٦٨١ - مسألة: (وأسماء الله تعالى قسمان؛ أحدهما، ما لا يسمى به غيره، نحو: والله، والقديم الأزلي، والأول الذي ليس قبله شيء، والآخر الذي ليس بعده شيء، وخالق الخلق، ورازق العالمين. فهذا القسم به يمين بكل حال)','13599','5006','0');
INSERT INTO [t19188] VALUES (5009,'٤٦٨٢ - مسألة: (فهذا إن نوى بالقسم به اسم الله تعالى، أو أطلق، كان يمينا)','13600','5006','0');
INSERT INTO [t19188] VALUES (5010,'٤٦٨٣ - مسألة: (وأما ما لا يعد من أسمائه، كالشيء والموجود)','13602','5006','0');
INSERT INTO [t19188] VALUES (5011,'٤٦٨٤ - مسألة: (وإن قال: وحق الله، وعهد الله، وايم الله، وأمانة الله، وميثاقه، وقدرته، وعظمته، وكبريائه، وجلاله، وعزته، ونحو ذلك، فهو يمين. وإن قال: والعهد، والميثاق، وسائر ذلك، ولم يضفه إلى الله تعالى، لم يكن يمينا، إلا أن ينوي صفة الله تعالى. وعنه، يكون يمينا)','13603','5006','0');
INSERT INTO [t19188] VALUES (5012,'٤٦٨٥ - مسألة: وإن قال: وايم الله، أو: وايمن الله','13605','5006','0');
INSERT INTO [t19188] VALUES (5013,'٤٦٨٦ - مسألة: وإن قال: وأمانة الله. فقال القاضي: لا يختلف المذهب في أن الحلف بأمانة الله يمين مكفرة. وبه قال أبو حنيفة. وقال الشافعي: لا تنعقد اليمين بها، إلا أن ينوي الحلف بصفة الله؛ لأن الأمانة تطلق على الفرائض والودائع والحقوق، قال الله تعالى: {إنا عرضنا الأمانة على السماوات والأرض والجبال فأبين أن يحملنها وأشفقن منها وحملها الإنسان}','13606','5006','0');
INSERT INTO [t19188] VALUES (5014,'٤٦٨٧ - مسألة: (وإن قال: والعهد، والميثاق، وسائر ذلك، ولم يضفه إلى الله تعالى، لم يكن يمينا، إلا أن ينوي صفة الله تعالى. وعنه، يكون يمينا)','13609','5006','0');
INSERT INTO [t19188] VALUES (5015,'٤٦٨٨ - مسألة: (وإن قال، لعمر الله. كان يمينا. وقال أبو','13610','5006','0');
INSERT INTO [t19188] VALUES (5016,'٤٦٨٩ - مسألة: (وإن حلف بكلام الله، أو بالمصحف، أو بالقرآن، فهي يمين فيها كفارة واحدة. وعنه، عليه بكل آية كفارة)','13613','5006','0');
INSERT INTO [t19188] VALUES (5017,'٤٦٩٠ - مسألة: (وإن قال: احلف بالله أو: أشهد بالله. أو: أقسم بالله. أو: أعزم بالله. كان يمينا. وإن لم يذكر اسم الله، لم يكن','13617','5006','0');
INSERT INTO [t19188] VALUES (5018,'٤٦٩١ - مسألة: (ويجوز القسم بغير حرف القسم، فيقول: الله لأفعلن. بالجر والنصب، فإن قال: الله لأفعلن. مرفوعا، كان يمينا، إلا أن يكون من أهل العربية، ولا ينوي اليمين)','13627','5006','0');
INSERT INTO [t19188] VALUES (5019,'٤٦٩٢ - مسألة: (ويكره الحلف بغير الله تعالى. ويحتمل أن يكون محرما)','13631','5006','0');
INSERT INTO [t19188] VALUES (5020,'٤٦٩٣ - مسألة: (ولا تجب الكفارة باليمين به، سواء أضافه إلى الله تعالى، مثل قوله: ومعلوم الله، وخلقه، ورزقه، وبيته. أو لم يضفه، مثل: والكعبة، وأبى)','13634','5006','0');
INSERT INTO [t19188] VALUES (5021,'٤٦٩٤ - مسألة: (وقال أصحابنا: تجب الكفارة بالحلف برسول الله - صلى الله عليه وسلم - خاصة)','13635','5006','0');
INSERT INTO [t19188] VALUES (5022,'٤٦٩٥ - مسألة: (فأما اليمين على الماضي، فليست منعقدة، وهي نوعان، يمين الغموس، وهي التي يحلف بها كاذبا عالما بكذبه.','13638','5006','0');
INSERT INTO [t19188] VALUES (5023,'٤٦٩٦ - مسألة: (وإن سبقت اليمين على لسانه من غير قصد إليها، كقوله: لا والله. وبلى والله. في عرض حديثه، فلا كفارة عليه)','13649','5006','0');
INSERT INTO [t19188] VALUES (5024,'٤٦٩٧ - مسألة: (فإن حلف فقال: إن شاء الله. لم يحنث، فعل أو ترك، إذا كان متصلا بيمينه)','13657','5006','0');
INSERT INTO [t19188] VALUES (5025,'٤٦٩٨ - مسألة: (وإذا حلف ليفعلن شيئا، ونوى وقتا بعينه، تقيد به، وإن لم ينو، لم يحنث حتى ييأس من فعله، إما بتلف المحلوف عليه، أو موت الحالف)','13665','5006','0');
INSERT INTO [t19188] VALUES (5026,'٤٦٩٩ - مسألة: (وإذا حلف على يمين فرأى غيرها خيرا منها، استحب له الحنث والتكفير)','13666','5006','0');
INSERT INTO [t19188] VALUES (5027,'٤٧٠٠ - مسألة: (ولا يستحب تكرار الحلف)','13667','5006','0');
INSERT INTO [t19188] VALUES (5028,'٤٧٠١ - مسألة: (فإن دعي إلى الحلف عند الحاكم وهو محق،','13669','5006','0');
INSERT INTO [t19188] VALUES (5029,'٤٧٠٢ - مسألة: (وإن قال: هو يهودي)','13677','5006','0');
INSERT INTO [t19188] VALUES (5030,'٤٧٠٣ - مسألة: (وعليه كفارة إن فعل، في إحدى الروايتين)','13678','5006','0');
INSERT INTO [t19188] VALUES (5031,'٤٧٠٤ - مسألة: (وإن قال: «أنا أستحل الزنى. أو نحوه، فعلى وجهين)','13680','5006','0');
INSERT INTO [t19188] VALUES (5032,'٤٧٠٥ - مسألة: (وإن قال: عصيت الله. أو: أنا أعصي الله في كل ما أمرني به. أو: محوت المصحف إن فعلت)','13681','5006','0');
INSERT INTO [t19188] VALUES (5033,'٤٧٠٦ - مسألة: (وإن قال: عبد فلان حر لأفعلن. فليس بشيء. وعنه، عليه كفارة إن حنث)','13683','5006','0');
INSERT INTO [t19188] VALUES (5034,'٤٧٠٧ - مسألة: (وإن قال: أيمان البيعة تلزمني. فهي يمين، رتبها الحجاج، تشتمل على اليمين بالله تعالى، والطلاق، والعتاق،','13684','5006','0');
INSERT INTO [t19188] VALUES (5035,'٤٧٠٨ - مسألة: (وإن قال: علي نذر، أو يمين إن فعلت كذا. وفعله، فقال أصحابنا: عليه كفارة يمين)','13689','5006','0');
INSERT INTO [t19188] VALUES (5036,'٤٧٠٩ - مسألة: (وهي تجمع تخييرا وترتيبا، فيخير بين ثلاثة','13691','5006','0');
INSERT INTO [t19188] VALUES (5037,'٤٧١٠ - مسألة: (فإن لم يجد، فصيام ثلاثة أيام متتابعة، إن شاء قبل الحنث، وإن شاء بعده، ولا يجوز تقديمها على اليمين)','13695','5006','0');
INSERT INTO [t19188] VALUES (5038,'٤٧١١ - مسألة: وهو مخير في التكفير، إن شاء قبل الحنث، وإن شاء بعده، سواء كان صوما أو غيره، فيما سوى الظهار','13697','5006','0');
INSERT INTO [t19188] VALUES (5039,'٤٧١٢ - مسألة: (ومن كرر أيمانا قبل التكفير، فعليه كفارة واحدة. وعنه، لكل يمين كفارة)','13702','5006','0');
INSERT INTO [t19188] VALUES (5040,'٤٧١٣ - مسألة: (والظاهر)','13704','5006','0');
INSERT INTO [t19188] VALUES (5041,'٤٧١٤ - مسألة: (وإن كانت الأيمان مختلفة الكفارة، كالظهار واليمين بالله تعالى، فلكل يمين كفارتها)','13707','5006','0');
INSERT INTO [t19188] VALUES (5042,'٤٧١٥ - مسألة: (وكفارة العبد الصيام، وليس لسيده منعه منه. ومن بعضه حر فحكمه في الكفارة حكم الأحرار)','13708','5006','0');
INSERT INTO [t19188] VALUES (5043,'٤٧١٦ - مسألة: وليس للسيد منع العبد','13713','5006','0');
INSERT INTO [t19188] VALUES (5044,'باب جامع الأيمان','13722','5005','0');
INSERT INTO [t19188] VALUES (5045,'٤٧١٧ - مسألة: (ويرجع في الأيمان إلى النية، فإن لم تكن له نية، رجع إلى سبب اليمين وما هيجها)','13722','5044','0');
INSERT INTO [t19188] VALUES (5046,'٤٧١٨ - مسألة: (فإن لم تكن له نية، رجع إلى سبب اليمين وما هيجها)','13728','5044','0');
INSERT INTO [t19188] VALUES (5047,'٤٧١٩ - مسألة: (وإن حلف)','13730','5044','0');
INSERT INTO [t19188] VALUES (5048,'٤٧٢٠ - مسألة: (وإن حلف لا يدخل دارا، ونوى اليوم، لم يحنث بالدخول في غيره)','13731','5044','0');
INSERT INTO [t19188] VALUES (5049,'٤٧٢١ - مسألة: (وإن دعي إلى غداء، فحلف لا يتغدى، اختصت يمينه به إذا قصده)','13732','5044','0');
INSERT INTO [t19188] VALUES (5050,'٤٧٢٢ - مسألة: (وإن حلف لا يشرب له الماء من العطش، يقصد قطع منته، حنث بأكل خبزه، واستعارة دابته، وكل ما فيه المنة)','13732','5044','0');
INSERT INTO [t19188] VALUES (5051,'٤٧٢٣ - مسألة: (وإن حلف لا يلبس ثوبا من غزلها، يقصد قطع منتها، فباعه واشترى بثمنه ثوبا فلبسه، حنث، وكذلك إن انتفع بثمنه)','13733','5044','0');
INSERT INTO [t19188] VALUES (5052,'٤٧٢٤ - مسألة: (وإن حلف لا يأوي معها في دار، يريد جفاءها، ولم يكن للدار سبب هيج يمينه، فأوى معها في غيرها، حنث)','13735','5044','0');
INSERT INTO [t19188] VALUES (5053,'٤٧٢٥ - مسألة: (وإن حلف لعامل لا يخرج إلا بإذنه فعزل، أو على زوجته فطلقها، أو على عبده فأعتقه، ونحوه، يريد ما دام كذلك، انحلت يمينه. وإن لم تكن له نية، انحلت)','13738','5044','0');
INSERT INTO [t19188] VALUES (5054,'٤٧٢٦ - مسألة: (وذكر القاضي في من قال لامرأته: إن سرقت مني شيئا وبعتنيه، فأنت طالق. ففعلت، لم تطلق)','13756','5044','0');
INSERT INTO [t19188] VALUES (5055,'٤٧٢٧ - مسألة.: (إذا حلف لا يصوم، لم يحنث حتى يصوم يوما)','13759','5044','0');
INSERT INTO [t19188] VALUES (5056,'٤٧٢٨ - مسألة: (وإن حلف لا يصلي، لم يحنث)','13760','5044','0');
INSERT INTO [t19188] VALUES (5057,'٤٧٢٩ - مسألة: (وإن حلف لا يتصدق عليه، فوهبه، لم يحنث)','13764','5044','0');
INSERT INTO [t19188] VALUES (5058,'٤٧٣٠ - مسألة: (وإن حلف لا يهبه، فتصدق عليه، حنث)','13764','5044','0');
INSERT INTO [t19188] VALUES (5059,'٤٧٣١ - مسألة: (وإن أعاره لم يحنث إلا عند أبي الخطاب)','13766','5044','0');
INSERT INTO [t19188] VALUES (5060,'٤٧٣٢ - مسألة: (وإن وقف عليه، حنث)','13767','5044','0');
INSERT INTO [t19188] VALUES (5061,'٤٧٣٣ - مسألة: (وإن وصى له، لم يحنث)','13767','5044','0');
INSERT INTO [t19188] VALUES (5062,'٤٧٣٤ - مسألة: (وإن باعه وحاباه، حنث)','13767','5044','0');
INSERT INTO [t19188] VALUES (5063,'٤٧٣٥ - مسألة: (وإن أكل المرق، لم يحنث. وقد قال أحمد: لا يعجبني. قال أبو الخطاب: هذا على سبيل الورع)','13772','5044','0');
INSERT INTO [t19188] VALUES (5064,'٤٧٣٦ - مسألة: (وإن حلف لا يأكل الشحم، فأكل شحم الظهر، حنث)','13774','5044','0');
INSERT INTO [t19188] VALUES (5065,'٤٧٣٧ - مسألة: (وإن حلف لا يأكل لبنا، فأكل زبدا، أو سمنا، أو كشكا، أو مصلا، أو جبنا، لم يحنث. وإن حلف على الزبد','13776','5044','0');
INSERT INTO [t19188] VALUES (5066,'٤٧٣٨ - مسألة: (وإن حلف على الفاكهة، فأكل من ثمر الشجر؛ كالجوز، واللوز، والتمر، والرمان، حنث، وإن أكل البطيخ، حنث. ويحتمل أن لا يحنث)','13779','5044','0');
INSERT INTO [t19188] VALUES (5067,'٤٧٣٩ - مسألة: (ولا يحنث بأكل القثاء والخيار)','13782','5044','0');
INSERT INTO [t19188] VALUES (5068,'٤٧٤٠ - مسألة: (وإن حلف لا يأكل رطبا، فأكل مذنبا، حنث)','13783','5044','0');
INSERT INTO [t19188] VALUES (5069,'٤٧٤١ - مسألة: (وإن أكل تمرا أو بسرا)','13784','5044','0');
INSERT INTO [t19188] VALUES (5070,'٤٧٤٢ - مسألة: (وإن حلف لا يأكل أدما، حنث بأكل البيض، والشواء، والجبن، والملح، والزيتون، واللبن، وسائر ما يصطبغ به. وفي التمر وجهان)','13785','5044','0');
INSERT INTO [t19188] VALUES (5071,'٤٧٤٣ - مسألة: (وإن حلف لا يلبس شيئا، فلبس ثوبا، أو درعا، أو جوشنا، أو خفا، أو نعلا، حنث)','13791','5044','0');
INSERT INTO [t19188] VALUES (5072,'٤٧٤٤ - مسألة: (وإن حلف لا يلبس حليا، فلبس حلية ذهب أو فضة أو جوهر، حنث، وإن لبس عقيقا أو سبجا','13793','5044','0');
INSERT INTO [t19188] VALUES (5073,'٤٧٤٥ - مسألة: (وإن حلف لا يركب دابة فلان، ولا يلبس ثوبه، ولا يدخل داره، فركب دابة عبده، ولبس ثوبه، ودخل داره، أو فعل ذلك فيما استأجره فلان، حنث، وإن ركب دابة استعارها، لم يحنث)','13796','5044','0');
INSERT INTO [t19188] VALUES (5074,'٤٧٤٦ - مسألة: (وإن حلف لا يركب دابة فلان، فركب دابة استعارها، لم يحنث، وإن ركب دابة استأجرها، حنث)','13798','5044','0');
INSERT INTO [t19188] VALUES (5075,'٤٧٤٧ - مسألة: (وإن حلف لا يركب دابة عبده، فركب دابة جعلت برسمه، حنث)','13799','5044','0');
INSERT INTO [t19188] VALUES (5076,'٤٧٤٨ - مسألة: (وإن حلف لا يدخل دارا، فدخل سطحها، حنث، وإن دخل طاق الباب، احتمل وجهين)','13799','5044','0');
INSERT INTO [t19188] VALUES (5077,'٤٧٤٩ - مسألة: (وإن حلف لا يكلم إنسانا، حنث بكلام كل إنسان)','13803','5044','0');
INSERT INTO [t19188] VALUES (5078,'٤٧٥٠ - مسألة: (وإن حلف لا يبتدئه بكلام، فتكلما معا، حنث)','13805','5044','0');
INSERT INTO [t19188] VALUES (5079,'٤٧٥١ - مسألة: (وإن حلف لا يكلمه حينا، فذلك ستة أشهر. نص عليه)','13809','5044','0');
INSERT INTO [t19188] VALUES (5080,'٤٧٥٢ - مسألة: (وإن قال: زمنا، أو: دهرا، أو: بعيدا، أو: مليا،','13810','5044','0');
INSERT INTO [t19188] VALUES (5081,'٤٧٥٣ - مسألة: (وإن قال: عمرا. احتمل أن يكون كذلك)','13812','5044','0');
INSERT INTO [t19188] VALUES (5082,'٤٧٥٤ - مسألة: (وإن قال: الأبد، والدهر. فذلك على الزمان كله)','13813','5044','0');
INSERT INTO [t19188] VALUES (5083,'٤٧٥٥ - مسألة: (والحقب ثمانون سنة)','13813','5044','0');
INSERT INTO [t19188] VALUES (5084,'٤٧٥٦ - مسألة: (والشهور اثنا عشر عند القاضي. وعند أبي الخطاب ثلاثة، كالأشهر)','13815','5044','0');
INSERT INTO [t19188] VALUES (5085,'٤٧٥٧ - مسألة: (والأيام ثلاثة)','13815','5044','0');
INSERT INTO [t19188] VALUES (5086,'٤٧٥٨ - مسألة: (وإن حلف لا يدخل باب هذه الدار، فحول ودخله، حنث)','13817','5044','0');
INSERT INTO [t19188] VALUES (5087,'٤٧٥٩ - مسألة: (وإن حلف لا يكلمه إلى حين الحصاد، انتهت يمينه بأوله)','13818','5044','0');
INSERT INTO [t19188] VALUES (5088,'٤٧٦٠ - مسألة: (وإن حلف لا مال له، وله مال غير زكوي، أو دين على الناس، حنث)','13819','5044','0');
INSERT INTO [t19188] VALUES (5089,'٤٧٦١ - مسألة: (وإن حلف لا يفعل شيئا، فوكل من يفعله، حنث، إلا أن ينوي)','13822','5044','0');
INSERT INTO [t19188] VALUES (5090,'٤٧٦٢ - مسألة.: (وإن حلف على وطء امرأة، تعلقت يمينه بجماعها)','13824','5044','0');
INSERT INTO [t19188] VALUES (5091,'٤٧٦٣ - مسألة: (وإن حلف على وطء دار، تعلقت)','13825','5044','0');
INSERT INTO [t19188] VALUES (5092,'٤٧٦٤ - مسألة: (وإن حلف لا يشم الريحان، فشم الورد والبنفسج والياسمين، أو لا يشم الورد والبنفسج، فشم دهنهما، أو ماء الورد، فالقياس أنه لا يحنث. وقال بعض أصحابنا: يحنث)','13825','5044','0');
INSERT INTO [t19188] VALUES (5093,'٤٧٦٥ - مسألة: (وإن حلف لا يأكل لحما، فأكل يسمكا، حنث عند الخرقي، ولم يحنث عند ابن أبي موسى)','13827','5044','0');
INSERT INTO [t19188] VALUES (5094,'٤٧٦٦ - مسألة: (وإن حلف لا يأكل رأسا ولا بيضا، حنث بأكل رءوس الطير والسمك، وبيض السمك والجراد، عند القاضي. وعند أبي الخطاب، لا يحنث إلا بأكل رأس جرت العادة بأكله منفردا، أو بيض يزايل بائضه حال الحياة)','13829','5044','0');
INSERT INTO [t19188] VALUES (5095,'٤٧٦٧ - مسألة: (وإن حلف لا يدخل بيتا، فدخل مسجدا، أو حماما، أو بيت شعر، أو أدم، أو لا يركب، فركب سفينة، حنث عند أصحابنا. ويحتمل أن لا يحنث)','13831','5044','0');
INSERT INTO [t19188] VALUES (5096,'٤٧٦٨ - مسألة: (وإن حلف لا يتكلم، فقرأ، أو سبح، أو ذكر الله تعالى، لم يحنث)','13833','5044','0');
INSERT INTO [t19188] VALUES (5097,'٤٧٦٩ - مسألة: (وإن خلف لا يضرب امرأته، فخنقها، أو نتف شعرها، أو عضها، حنث)','13836','5044','0');
INSERT INTO [t19188] VALUES (5098,'٤٧٧٠ - مسألة: (وإن حلف ليضربنه مائة سوط، فجمعها، فضربه بها ضربة واحدة، لم يبر في يمينه)','13836','5044','0');
INSERT INTO [t19188] VALUES (5099,'٤٧٧١ - مسألة: (وإن حلف لا يأكل شحما، فأكل اللحم الأحمر، فقال الخرقي: يحنث)','13841','5044','0');
INSERT INTO [t19188] VALUES (5100,'٤٧٧٢ - مسألة: (فإن حلف لا يطعم شيئا، حنث بأكله وشربه)','13847','5044','0');
INSERT INTO [t19188] VALUES (5101,'٤٧٧٣ - مسألة: (وإن ذاقه ولم يبتلعه، لم يحنث)','13847','5044','0');
INSERT INTO [t19188] VALUES (5102,'٤٧٧٤ - مسألة: (وإن حلف لا يأكل مائعا، فأكله بالخبز، حنث)','13848','5044','0');
INSERT INTO [t19188] VALUES (5103,'٤٧٧٥ - مسألة: (وإن حلف لا يركب ولا يلبس، فاستدام ذلك، حنث)','13849','5044','0');
INSERT INTO [t19188] VALUES (5104,'٤٧٧٦ - مسألة: (وإن حلف لا يدخل دارا هو داخلها، فأقام فيها، حنث عند القاضي. ولم يحنث عند أبي الخطاب)','13851','5044','0');
INSERT INTO [t19188] VALUES (5105,'٤٧٧٧ - مسألة: (وإن حلف لا يدخل على فلان بيتا، فدخل فلان عليه، فأقام معه، فعلى الوجهين)','13853','5044','0');
INSERT INTO [t19188] VALUES (5106,'٤٧٧٨ - مسألة: (وإن حلف لا يسكن دارا، ولا يساكن فلانا وهما متساكنان، ولم يخرج في الحال، حنث، إلا أن يقيم لنقل متاعه، أو يخشى على نفسه الخروج فيقيم إلى أن يمكنه)','13853','5044','0');
INSERT INTO [t19188] VALUES (5107,'٤٧٧٩ - مسألة: فإن أقام لنقل متاعه وأهله، لم يحنث','13854','5044','0');
INSERT INTO [t19188] VALUES (5108,'٤٧٨٠ - مسألة: (وإن خرج دون أهله ومتاعه، حنث)','13855','5044','0');
INSERT INTO [t19188] VALUES (5109,'٤٧٨١ - مسألة: (وإن حلف لا يساكن فلانا، فبنيا بينهما حائطا وهما متساكنان، حنث، وإن كان في الدار حجرتان، كل حجرة تختص ببابها ومرافقها، فسكن كل واحد حجرة، لم يحنث)','13858','5044','0');
INSERT INTO [t19188] VALUES (5110,'٤٧٨٢ - مسألة: (وإن حلف ليخرجن من هذه البلدة، فخرج دون أهله)','13861','5044','0');
INSERT INTO [t19188] VALUES (5111,'٤٧٨٣ - مسألة: (وإن حلف ليخرجن من هذه البلدة، أو ليرحلن عن هذه الدار، ففعل، فهل له العود إليها؟ على روايتين)','13862','5044','0');
INSERT INTO [t19188] VALUES (5112,'٤٧٨٤ - مسألة: (وإن حلف ليشربن)','13866','5044','0');
INSERT INTO [t19188] VALUES (5113,'٤٧٨٥ - مسألة: وإن قال: والله لأشربن ماء هذا الكوز غدا. فاندفق اليوم. أو: لآكلن هذا الخبز غدا. فتلف، فهو على نحو ما ذكرنا في العبد. قال صالح: سألت أبي عن الرجل يحلف أن يشرب هذا','13871','5044','0');
INSERT INTO [t19188] VALUES (5114,'٤٧٨٦ - مسألة: (وإن حلف ليقضينه حقه، فأبرأه، فهل يحنث؟ على وجهين)','13872','5044','0');
INSERT INTO [t19188] VALUES (5115,'٤٧٨٧ - مسألة: (وإن مات المستحق فقضى ورثته، لم يحنث)','13873','5044','0');
INSERT INTO [t19188] VALUES (5116,'٤٧٨٨ - مسألة: (وإن باعه بحقه عرضا، لم يحنث عند ابن حامد)','13874','5044','0');
INSERT INTO [t19188] VALUES (5117,'٤٧٨٩ - مسألة: (وإن حلف ليقضينه حقه عند رأس الهلال)','13875','5044','0');
INSERT INTO [t19188] VALUES (5118,'٤٧٩٠ - مسألة: (وإن حلف: لا فارقتك حتى أستوفي حقي)','13877','5044','0');
INSERT INTO [t19188] VALUES (5119,'٤٧٩١ - مسألة: (فإن حلف: لا افترقنا. فهرب منه، حنث)','13882','5044','0');
INSERT INTO [t19188] VALUES (5120,'٤٧٩٢ - مسألة: (وقدر الفراق ما عده الناس فراقا، كفرقة البيع)','13883','5044','0');
INSERT INTO [t19188] VALUES (5121,'باب النذر','13884','5005','0');
INSERT INTO [t19188] VALUES (5122,'٤٧٩٣ - مسألة: (وهو أن يلزم نفسه لله تعالى شيئا)','13885','5121','0');
INSERT INTO [t19188] VALUES (5123,'٤٧٩٤ - مسألة: (ولا يصح إلا بالقول، فإن نواه من غير قول، لم يصح)','13887','5121','0');
INSERT INTO [t19188] VALUES (5124,'٤٧٩٥ - مسألة: (ولا يصح في محال ولا واجب، فلو قال: لله علي صوم أمس. أو: صوم رمضان. لم ينعقد)','13888','5121','0');
INSERT INTO [t19188] VALUES (5125,'٤٧٩٦ - مسألة: (والنذر المنعقد على خمسة أقسام؛ أحدها، النذر المطلق، وهو أن يقول: لله علي نذر. فتجب)','13890','5121','0');
INSERT INTO [t19188] VALUES (5126,'٤٧٩٧ - مسألة: (فإن نذر مكروها، كالطلاق)','13896','5121','0');
INSERT INTO [t19188] VALUES (5127,'٤٧٩٨ - مسألة: (إلا أن ينذر ذبح ولده، ففيه روايتان؛ إحداهما، أنه كذلك. والثانية، يلزمه ذبح كبش)','13898','5121','0');
INSERT INTO [t19188] VALUES (5128,'٤٧٩٩ - مسألة: (ويحتمل أن لا ينعقد نذر المباح ولا المعصية، ولا تجب به كفارة، ولهذا قال أصحابنا: من نذر الاعتكاف','13903','5121','0');
INSERT INTO [t19188] VALUES (5129,'٤٨٠٠ - مسألة: (ولو نذر الصدقة بكل ماله، فله الصدقة بثلثه، ولا كفارة عليه)','13905','5121','0');
INSERT INTO [t19188] VALUES (5130,'٤٨٠١ - مسألة: (وإن نذر الصدقة بألف، لزمه جميعه. وعنه، يجزئه ثلثه)','13909','5121','0');
INSERT INTO [t19188] VALUES (5131,'٤٨٠٢ - مسألة: (وإن نذر صوم سنة، لم يدخل في نذره رمضان ويوما العيدين. وفي أيام التشريق روايتان. وعنه ما يدل على أنه يقضي يومي العيدين وأيام التشريق)','13915','5121','0');
INSERT INTO [t19188] VALUES (5132,'٤٨٠٣ - مسألة: (وإن نذر صوم يوم الخميس، فوافق يوم عيد أو حيض، أفطر، وقضى وكفر)','13918','5121','0');
INSERT INTO [t19188] VALUES (5133,'٤٨٠٤ - مسألة: (ونقل عنه ما يدل على أنه إن صام يوم العيد، صح صومه)','13919','5121','0');
INSERT INTO [t19188] VALUES (5134,'٤٨٠٥ - مسألة: (وإن وافق أيام التشريق، فهل يصومها؟ على روايتين)','13920','5121','0');
INSERT INTO [t19188] VALUES (5135,'٤٨٠٦ - مسألة: (وإن نذر صوم يوم يقدم فلان، فقدم ليلا، فلا شيء عليه، وإن قدم نهارا، فعنه ما يدل على أنه لا ينعقد نذره، ولا يلزمه إلا صيام ذلك اليوم إن لم يكن أفطر. وعنه، أنه يقضي ويكفر، سواء قدم وهو مفطر أو صائم. وإن وافق قدومه يوما من رمضان، فقال الخرقي: يجزئه صيامه لرمضان ونذره. وقال غيره: عليه القضاء. وفي الكفارة روايتان)','13921','5121','0');
INSERT INTO [t19188] VALUES (5136,'٤٨٠٧ - مسألة: (وإن وافق يوم نذره وهو مجنون، فلا قضاء عليه ولا كفارة)','13927','5121','0');
INSERT INTO [t19188] VALUES (5137,'٤٨٠٨ - مسألة: (وإن نذر صوم شهر معين، فلم يصمه لغير عذر، فعليه القضاء وكفارة يمين، [وإن لم يصمه لعذر فعليه القضاء، وفي الكفارة روايتان)','13928','5121','0');
INSERT INTO [t19188] VALUES (5138,'٤٨٠٩ - مسألة: (وإن صام قبله، لم يجزئه)','13930','5121','0');
INSERT INTO [t19188] VALUES (5139,'٤٨١٠ - مسألة: (وإن أفطر في أثنائه لغير عذر، لزمه استئنافه، ويكفر. ويحتمل أن يتم باقيه، ويقضي ويكفر)','13930','5121','0');
INSERT INTO [t19188] VALUES (5140,'٤٨١١ - مسألة: (وإن نذر صوم شهر، لزمه التتابع)','13934','5121','0');
INSERT INTO [t19188] VALUES (5141,'٤٨١٢ - مسألة: (وإن نذر أياما معدودة، لم يلزمه التتابع، إلا أن يشترطه)','13935','5121','0');
INSERT INTO [t19188] VALUES (5142,'٤٨١٣ - مسألة: (وإن نذر صياما متتابعا، فأفطر لمرض أو حيض، قضى لا غير، وإن أفطر لغير عذر، لزمه الاستئناف، وإن أفطر لسفر أو ما يبيح الفطر، فعلى وجهين)','13937','5121','0');
INSERT INTO [t19188] VALUES (5143,'٤٨١٤ - مسألة: (وإن نذر صياما، فعجز عنه لكبر، أو مرض','13942','5121','0');
INSERT INTO [t19188] VALUES (5144,'٤٨١٥ - مسألة: (وإن نذر المشي إلى بيت الله الحرام، أو موضع من الحرم، لم يجزئه إلا أن يمشي في حج أو عمرة، فإن ترك المشي لعجز أو غيره، فعليه كفارة يمين. وعنه، عليه دم)','13948','5121','0');
INSERT INTO [t19188] VALUES (5145,'٤٨١٦ - مسألة: (فإن نذر الركوب، فمشى، فعلى الروايتين)','13953','5121','0');
INSERT INTO [t19188] VALUES (5146,'٤٨١٧ - مسألة: (فإن نذر رقبة، فهي التي تجزئ عن الواجب،','13959','5121','0');
INSERT INTO [t19188] VALUES (5147,'٤٨١٨ - مسألة: (وإن نذر أن يطوف على أربع، طاف طوافين. نص عليه)','13965','5121','0');
INSERT INTO [t19188] VALUES (5148,'كتاب القضاء','13968','0','0');
INSERT INTO [t19188] VALUES (5149,'[باب. . .]','13968','5148','0');
INSERT INTO [t19188] VALUES (5150,'٤٨١٩ - مسألة: (وهو فرض كفاية)','13969','5149','0');
INSERT INTO [t19188] VALUES (5151,'٤٨٢٠ - مسألة: (فيجب على الإمام أن ينصب في كل إقليم قاضيا، ويختار لذلك أفضل من يجد وأورعهم)','13972','5149','0');
INSERT INTO [t19188] VALUES (5152,'٤٨٢١ - مسألة: (ويختار لذلك أفضل من يجد وأورعهم، ويأمره بتقوى الله، وإيثار طاعته في سره وعلانيته، وتحري العدل، والاجتهاد في إقامة الحق)','13973','5149','0');
INSERT INTO [t19188] VALUES (5153,'٤٨٢٢ - مسألة: (ويجب على من يصلح له، إذا طلب ولم يوجد غيره، الدخول فيه. وعنه، أنه سئل: هل يأثم القاضي إذا لم يوجد غيره ممن يوثق به؟ قال: لا يأثم. وهذا يدل على أنه ليس بواجب)','13974','5149','0');
INSERT INTO [t19188] VALUES (5154,'٤٨٢٣ - مسألة: (فإن وجد غيره، كره له طلبه، بغير خلاف في المذهب)','13977','5149','0');
INSERT INTO [t19188] VALUES (5155,'٤٨٢٤ - مسألة: (وإن طلب، فالأفضل أن لا يجيب إليه، في ظاهر كلام أحمد. وقال ابن حامد: الأفضل الإجابة إليه إذا أمن نفسه)','13978','5149','0');
INSERT INTO [t19188] VALUES (5156,'٤٨٢٥ - مسألة؛ (ولا تثبت ولاية القضاء إلا بتولية الإمام أو نائبه)','13980','5149','0');
INSERT INTO [t19188] VALUES (5157,'٤٨٢٦ - مسألة: (ومن شرط صحتها معرفة المولي كون المولى على صفة تصلح للقضاء)','13980','5149','0');
INSERT INTO [t19188] VALUES (5158,'٤٨٢٧ - مسألة: (وتعيين ما يوليه الحكم فيه من الأعمال والبلدان، ومشافهته بالولاية أو مكاتبته بها، وإشهاد شاهدين على توليته. وقال القاضي: تثبت بالاستفاضة، إذا كان بلده قريبا يستفيض فيه أخبار بلد الإمام)','13981','5149','0');
INSERT INTO [t19188] VALUES (5159,'٤٨٢٨ - مسألة: (وهل تشترط عدالة المولي؟ على روايتين)','13984','5149','0');
INSERT INTO [t19188] VALUES (5160,'٤٨٢٩ - مسألة: (وألفاظ التولية الصريحة سبعة: وليتك الحكم، وقلدتك، واستنبتك، واستخلفتك، ورددت إليك، وفوضت إليك، [وجعلت لك الحكم]','13985','5149','0');
INSERT INTO [t19188] VALUES (5161,'٤٨٣٠ - مسألة: (وله طلب الرزق لنفسه وأمنائه وخلفائه مع الحاجة. فأما مع عدمها، فعلى وجهين)','13989','5149','0');
INSERT INTO [t19188] VALUES (5162,'٤٨٣١ - مسألة','13993','5149','0');
INSERT INTO [t19188] VALUES (5163,'٤٨٣٢ - مسألة: (فإن جعل إليهما عملا واحدا، جاز. وعند أبي الخطاب لا يجوز)','13994','5149','0');
INSERT INTO [t19188] VALUES (5164,'٤٨٣٣ - مسألة: (إذا مات المولي، أو عزل المولى مع صلاحيته، لم تبطل ولايته في أحد الوجهين، وتبطل في الآخر)','13997','5149','0');
INSERT INTO [t19188] VALUES (5165,'٤٨٣٤ - مسألة: (وهل ينعزل قبل العلم بالعزل؟ على روايتين،','14001','5149','0');
INSERT INTO [t19188] VALUES (5166,'٤٨٣٥ - مسألة: (وإذا قال المولي: من نظر في الحكم في البلد الفلاني من فلان وفلان، فهو خليفتي، أو: قد وليته. لم تنعقد الولاية لمن ينظر)','14004','5149','0');
INSERT INTO [t19188] VALUES (5167,'٤٨٣٦ - مسألة: (وإن قال: وليت فلانا وفلانا، فمن نظر منهما، فهو خليفتي. انعقدت الولاية)','14005','5149','0');
INSERT INTO [t19188] VALUES (5168,'٤٨٣٧ - مسألة: وليس من شرط الحاكم أن يكون كاتبا. وفيه وجه آخر، أنه يشترط ذلك؛ ليعلم ما يكتبه كاتبه، ولا يتمكن من إخفائه عنه. ولنا، أن رسول الله - صلى الله عليه وسلم - كان أميا، وهو سيد الحكام، وليس من ضرورة الحكم الكتابة، فلا تعتبر شرطا','14012','5149','0');
INSERT INTO [t19188] VALUES (5169,'٤٨٣٨ - مسألة: (والمجتهد من يعرف من كتاب الله تعالى، وسنة رسوله - صلى الله عليه وسلم - الحقيقة والمجاز، والأمر والنهي، والمجمل والمبين، والمحكم والمتشابه، والخاص والعام، والمطلق والمقيد، والناسخ والمنسوخ، والمستثنى والمستثنى منه، ويعرف من السنة صحيحها من سقيمها، وتواترها من آحادها، ومرسلها ومتصلها، ومسندها ومنقطعها، مما له تعلق بالأحكام خاصة)','14014','5149','0');
INSERT INTO [t19188] VALUES (5170,'٤٨٣٩ - مسألة: (ويعرف ما أجمع عليه مما اختلف فيه، والقياس وحدوده وشروطه وكيفية استنباطه)','14016','5149','0');
INSERT INTO [t19188] VALUES (5171,'باب أدب القاضي','14027','5148','0');
INSERT INTO [t19188] VALUES (5172,'٤٨٤٠ - مسألة: (وإذا ولي في غير بلده، سأل عمن فيه من الفقهاء والفضلاء والعدول، وينفذ عند مسيره من يعلمهم يوم دخوله ليتلقوه)','14030','5171','0');
INSERT INTO [t19188] VALUES (5173,'٤٨٤١ - مسألة: (و)','14030','5171','0');
INSERT INTO [t19188] VALUES (5174,'٤٨٤٢ - مسألة: (فإذا اجتمع الناس أمر بعهده فقريء عليهم)','14032','5171','0');
INSERT INTO [t19188] VALUES (5175,'٤٨٤٣ - مسألة: (ثم يخرج في اليوم الذي وعد بالجلوس فيه، على أعدل أحواله، غير غضبان، ولا جائع، ولا شبعان، ولا حاقن، ولا مهموم بأمر يشغله عن الفهم)','14033','5171','0');
INSERT INTO [t19188] VALUES (5176,'٤٨٤٤ - مسألة: (ويستعين بالله ويتوكل عليه.، ويدعوه سرا أن يعصمه من الزلل، ويوفقه للصواب، ولما يرضيه من القول والعمل، ويجعل مجلسه في مكان فسيح؛ كالجامع، والفضاء)','14035','5171','0');
INSERT INTO [t19188] VALUES (5177,'٤٨٤٥ - مسألة: (ولا يتخذ حاجبا ولا بوابا)','14037','5171','0');
INSERT INTO [t19188] VALUES (5178,'٤٨٤٦ - مسألة: (ويعرض القصص، فيبدأ بالأول فالأول)','14038','5171','0');
INSERT INTO [t19188] VALUES (5179,'٤٨٤٧ - مسألة: (ويعدل بين الخصمين في لحظه، ولفظه، ومجلسه والدخول عليه، إلا أن يكون أحدهما كافرا، فيقدم المسلم عليه في الدخول، ويرفعه في الجلوس)','14039','5171','0');
INSERT INTO [t19188] VALUES (5180,'٤٨٤٨ - مسألة: (ولا يعلمه كيف يدعي، في أحد الوجهين)','14042','5171','0');
INSERT INTO [t19188] VALUES (5181,'٤٨٤٩ - مسألة: (وينبغي أن يحضر مجلسه الفقهاء من كل مذهب)','14043','5171','0');
INSERT INTO [t19188] VALUES (5182,'٤٨٥٠ - مسألة: (فإن اتضح له)','14046','5171','0');
INSERT INTO [t19188] VALUES (5183,'٤٨٥١ - مسألة: (ولا يقضي وهو غضبان، ولا حاقن، ولا في','14047','5171','0');
INSERT INTO [t19188] VALUES (5184,'٤٨٥٢ - مسألة: (ولا يحل له أن يرتشي، ولا يقبل الهدية إلا ممن كان يهدي إليه قبل وكشه، بشرط أن لا يكون له حكومة)','14051','5171','0');
INSERT INTO [t19188] VALUES (5185,'٤٨٥٣ - مسألة: (ويكره أن يتولى البيع والشراء بنفسه، ويستحب أن يوكل في ذلك من لا يعرف أنه وكيله)','14057','5171','0');
INSERT INTO [t19188] VALUES (5186,'٤٨٥٤ - مسألة: (ويستحب له عيادة المرضى، وشهود الجنائز، ما لم يشغله عن الحكم)','14059','5171','0');
INSERT INTO [t19188] VALUES (5187,'٤٨٥٥ - مسألة: (وله حضور الولائم)','14060','5171','0');
INSERT INTO [t19188] VALUES (5188,'٤٨٥٦ - مسألة: (ويوصي الوكلاء والأعوان على بابه بالرفق بالخصوم، وقلة الطمع، ويجتهد أن يكونوا شيوخا أو كهولا، من أهل الدين والعفة والصيانة)','14061','5171','0');
INSERT INTO [t19188] VALUES (5189,'٤٨٥٧ - مسألة: (ويتخذ كاتبا مسلما، مكلفا، عدلا، حافظا، عالما، يجلسه بحيث يشاهد ما يكتبه، ويجعل القمطر','14061','5171','0');
INSERT INTO [t19188] VALUES (5190,'٤٨٥٨ - مسألة: (ويستحب أن لا يحكم إلا بحضرة الشهود)','14064','5171','0');
INSERT INTO [t19188] VALUES (5191,'٤٨٥٩ - مسألة: (ولا يحكم لنفسه، ولا لمن لا تقبل شهادته له، ويحكم بينهم بعض خلفائه. [وقال أبو بكو: يجوز ذلك)','14064','5171','0');
INSERT INTO [t19188] VALUES (5192,'٤٨٦٠ - مسألة: (وإن كان حبس في تهمة، أو افتيات على القاضي','14070','5171','0');
INSERT INTO [t19188] VALUES (5193,'٤٨٦١ - مسألة: (وإن لم يحضر له خصم، وقال: حبست ظلما، ولا حق علي، ولا خصم لي. نادى بذلك ثلاثا، فإن حضر له خصم، وإلا أحلفه، وخلى سبيله)','14071','5171','0');
INSERT INTO [t19188] VALUES (5194,'٤٨٦٢ - مسألة: (ثم ينظر في أمر اليتامى والمجانين والوقوف)','14072','5171','0');
INSERT INTO [t19188] VALUES (5195,'٤٨٦٣ - مسألة: (ثم)','14074','5171','0');
INSERT INTO [t19188] VALUES (5196,'٤٨٦٤ - مسألة: (وإن كان ممن لا يصلح، نقض أحكامه وإن وافقت الصحيح. ويحتمل أن لا ينقض الصواب منها)','14080','5171','0');
INSERT INTO [t19188] VALUES (5197,'٤٨٦٥ - مسألة: (وإن استعداه)','14083','5171','0');
INSERT INTO [t19188] VALUES (5198,'٤٨٦٦ - مسألة: (وإن استعداه على القاضي قبله، سأله عما يدعيه، فإن قال: لي عليه دين من معاملة، أو رشوة. راسله)','14086','5171','0');
INSERT INTO [t19188] VALUES (5199,'٤٨٦٧ - مسألة: (وإن قال: حكم علي بشهادة فاسقين. فالقول قوله بغير يمين)','14088','5171','0');
INSERT INTO [t19188] VALUES (5200,'٤٨٦٨ - مسألة: (وإن قال الحاكم المعزول: كنت حكمت في','14089','5171','0');
INSERT INTO [t19188] VALUES (5201,'٤٨٦٩ - مسألة: (فإن ادعى على امرأة غير برزة، لم يحضرها، وأمرها بالتوكيل، فإن وجبت عليها اليمين، أرسل إليها من يحلفها)','14095','5171','0');
INSERT INTO [t19188] VALUES (5202,'٤٨٧٠ - مسألة: (وإن ادعى على غائب عن البلد في موضع لا حاكم فيه، كتب إلى ثقات من أهل ذلك البلد، ليتوسطوا بينهما، فإن لم يقبلوا، قيل للخصم: حقق ما تدعيه. ثم يحضره وإن بعدت المسافة)','14097','5171','0');
INSERT INTO [t19188] VALUES (5203,'باب طريق الحكم وصفته','14100','5148','0');
INSERT INTO [t19188] VALUES (5204,'٤٨٧١ - مسألة: (ثم يقول للخصم: ما تقول فيما ادعاه؟)','14104','5203','0');
INSERT INTO [t19188] VALUES (5205,'٤٨٧٢ - مسألة: (فإن أقر، لم يحكم له حتى يطالبه المدعي بالحكم)','14105','5203','0');
INSERT INTO [t19188] VALUES (5206,'٤٨٧٣ - مسألة: (وإن أنكر، مثل أن يقول المدعي: أقرضته ألفا. أو: بعته. فيقول: ما أقرضني ولا باعني. أو: ما يستحق علي','14106','5203','0');
INSERT INTO [t19188] VALUES (5207,'٤٨٧٤ - مسألة: (وللمدعي أن يقول: لي بينة)','14108','5203','0');
INSERT INTO [t19188] VALUES (5208,'٤٨٧٥ - مسألة: فإذا سمع الحاكم الشهادة، وكانت صحيحة (حكم بها إذا سأله المدعي)','14110','5203','0');
INSERT INTO [t19188] VALUES (5209,'٤٨٧٦ - مسألة: (ولا خلاف في أنه يجوز له الحكم بالإقرار والبينة في مجلسه، إذا سمعه معه شاهدان، فإن لم يسمعه معه أحد، أو سمعه معه شاهد واحد، فله الحكم. نص عليه)','14112','5203','0');
INSERT INTO [t19188] VALUES (5210,'٤٨٧٧ - مسألة: (وليس له الحكم بعلمه فيما رآه أو سمعه)','14113','5203','0');
INSERT INTO [t19188] VALUES (5211,'٤٨٧٨ - مسألة: (وإن قال المدعي: ما لي بينة. فالقول قول المنكر مع يمينه، فيعلمه أن له اليمين على خصمه، فإن سأله إحلافه، أحلفه)','14118','5203','0');
INSERT INTO [t19188] VALUES (5212,'٤٨٧٩ - مسألة: (وإن أحلفه، أو حلف من غير سؤال المدعي، لم يعتد بيمينه)','14120','5203','0');
INSERT INTO [t19188] VALUES (5213,'٤٨٨٠ - مسألة: (وإن نكل، قضى عليه بالنكول. نص عليه. واختاره عامة شيوخنا. فيقول له: إن حلفت، وإلا قضيت عليك.','14121','5203','0');
INSERT INTO [t19188] VALUES (5214,'٤٨٨١ - مسألة: (وإن نكل أيضا صرفهما)','14126','5203','0');
INSERT INTO [t19188] VALUES (5215,'٤٨٨٢ - مسألة: (وإن قال المدعي: لي بينة. بعد قوله: ما لي بينة. لم تسمع. ذكره الخرقي)','14128','5203','0');
INSERT INTO [t19188] VALUES (5216,'٤٨٨٣ - مسألة: (وإن قال: لا أعلم لي بينة. ثم قال: قد علمت لي بينة. سمعت)','14130','5203','0');
INSERT INTO [t19188] VALUES (5217,'٤٨٨٤ - مسألة: (وإن قال شاهدان: نحن نشهد لك. فقال: هذان بينتي. سمعت)','14130','5203','0');
INSERT INTO [t19188] VALUES (5218,'٤٨٨٥ - مسألة: (وإن قال: ما أريد أن تشهدا لي. لم يكلف إقامة البينة)','14130','5203','0');
INSERT INTO [t19188] VALUES (5219,'٤٨٨٦ - مسألة: (وإن قال: لي بينة وأريد يمينه. فإن كانت غائبة، فله إحلافه، وإن كانت حاضرة، فهل له ذلك؟ على وجهين)','14131','5203','0');
INSERT INTO [t19188] VALUES (5220,'٤٨٨٧ - مسألة: (فإن سكت)','14134','5203','0');
INSERT INTO [t19188] VALUES (5221,'٤٨٨٨ - مسألة: (وإن حلف المنكر، ثم أحضر المدعي بينته، حكم بها، ولم تكن اليمين مزيلة للحق)','14135','5203','0');
INSERT INTO [t19188] VALUES (5222,'٤٨٨٩ - مسألة: (فإن قال: لي مخرج مما ادعاه. لم يكن مجيبا)','14137','5203','0');
INSERT INTO [t19188] VALUES (5223,'٤٨٩٠ - مسألة: (وإن قال: لي حساب أريد أن أنظر فيه. لم يلزم المدعي إنظاره)','14137','5203','0');
INSERT INTO [t19188] VALUES (5224,'٤٨٩١ - مسألة: (وإن قال: قضيته. أو: أبرأني، ولي بينة بالقضاء. أو: الإبراء. وسأل الإنظار، أنظر ثلاثا)','14138','5203','0');
INSERT INTO [t19188] VALUES (5225,'٤٨٩٢ - مسألة: (فإن ادعى عليه عينا في يده، فأقر بها لغيره، خعل الخصم فيها، وهل يحلف المدعى عليه؟ على وجهين. فإن كان المقر له حاضرا مكلفا، سئل، فإن ادعاها لنفسه ولم تكن بينة، حلف وأخذها، وإن أقر بها للمدعي، سلمت إليه، وإن قال: ليست لي، ولا أعلم لمن هي. سلمت إلى المدعي، في أحد الوجهين. وفي الآخر، لا تسلم إليه إلا ببينة. ويجعلها الحاكم عند أمين. وإن أقر بها لغائب، أو صبي، أو مجنون، سقطت عنه الدعوى. ثم إن كان للمدعي بينة،','14140','5203','0');
INSERT INTO [t19188] VALUES (5226,'٤٨٩٣ - مسألة: وإن أقر بها لغائب، أو لغير مكلف معين، كالصبي والمجنون، صارت الدعوى عليه. فإن لم تكن للمدعي بينة، لم يقض له بها؛ لأن الحاضر يعترف أنها ليست له، ولايقضى على الغائب بمجرد الدعوى، ويقف الأمر حتى يقدم الغائب، ويصير غير المكلف مكلفا','14144','5203','0');
INSERT INTO [t19188] VALUES (5227,'٤٨٩٤ - مسألة: (ؤإن أقر بها لمجهول، قيل: إما أن تعرفه، وإما أن نجعلك ناكلا)','14147','5203','0');
INSERT INTO [t19188] VALUES (5228,'٤٨٩٥ - مسألة: (فإن كان المدعى عينا حاضرة، عينها)','14150','5203','0');
INSERT INTO [t19188] VALUES (5229,'٤٨٩٦ - مسألة: (وإن كانت تالفة من ذوات الأمثال، ذكر قدرها وجنسها وصفتها)','14151','5203','0');
INSERT INTO [t19188] VALUES (5230,'٤٨٩٧ - مسألة: (وإن ادعى نكاحا، فلابد من ذكر المرأة بعينها إن حضرت، وإلا ذكر اسمها ونسبها، وذكر شروط النكاح، وأنه تزوجها بولي مرشد وشاهدي عدل، ورضاها، في الصحيح من المذهب)','14153','5203','0');
INSERT INTO [t19188] VALUES (5231,'٤٨٩٨ - مسألة: (وإن ادعى بيعا، أو عقدا سواه، فهل يشترط','14155','5203','0');
INSERT INTO [t19188] VALUES (5232,'٤٨٩٩ - مسألة: (وإن ادعت المرأة نكاحا على رجل، وادعت معه نفقة أو مهرا، سمعت دعواها، وإن لم تدع سوى النكاح، فهل تسمع دعواها؟ على وجهين)','14157','5203','0');
INSERT INTO [t19188] VALUES (5233,'٤٩٠٠ - مسألة: (وإن ادعى قتل موروثه، ذكر القاتل، وأنه انفرد به، أو شاركه)','14160','5203','0');
INSERT INTO [t19188] VALUES (5234,'٤٩٠١ - مسألة: (وإن ادعى الإرث، ذكر سببه)','14160','5203','0');
INSERT INTO [t19188] VALUES (5235,'٤٩٠٢ - مسألة: (وإن ادعى شيئا محلى)','14161','5203','0');
INSERT INTO [t19188] VALUES (5236,'٤٩٠٣ - مسألة: (وإذا علم الحاكم عدالتهما، عمل بعلمه، وحكم بشهادتهما)','14171','5203','0');
INSERT INTO [t19188] VALUES (5237,'٤٩٠٤ - مسألة: (إلا أن يرتاب بهما، فيفرقهما، ويسأل كل واحد)','14172','5203','0');
INSERT INTO [t19188] VALUES (5238,'٤٩٠٥ - مسألة: وإن اتفقوا، وعظهم، وخوفهم، كما روى عن شريح، أنه كان يقول للشاهدين إذا حضرا: يا هذان، ألا تريان؟ إنى لم أدعكما، ولست أمنعكما أن ترجعا، وإنما يقضى على هذا أنتما، وأنا متق','14174','5203','0');
INSERT INTO [t19188] VALUES (5239,'٤٩٠٦ - مسألة: (فإن ثبتا، حكم)','14176','5203','0');
INSERT INTO [t19188] VALUES (5240,'٤٩٠٧ - مسألة: (وإن جرحهما المشهود عليه، كلف البينة بالجرح، فإن سأل الإنظار، أنظر ثلاثا)','14178','5203','0');
INSERT INTO [t19188] VALUES (5241,'٤٩٠٨ - مسألة: (وللمدعى ملازمته)','14178','5203','0');
INSERT INTO [t19188] VALUES (5242,'٤٩٠٩ - مسألة: (ولا يسمع الجرح إلا مفسرا بما يقدح فى العدالة)','14179','5203','0');
INSERT INTO [t19188] VALUES (5243,'٤٩١٠ - مسألة: (وإن شهد عنده فاسق يعرف حاله، قال للمدعى: زدنى شهودا)','14183','5203','0');
INSERT INTO [t19188] VALUES (5244,'٤٩١١ - مسألة: (وإن جهل حاله، طالب المدعى بتزكيته)','14184','5203','0');
INSERT INTO [t19188] VALUES (5245,'٤٩١٢ - مسألة: (ويكفى فى التزكية شاهدان يشهدان أنه عدل رضا، ولا يحتاج أن يقول)','14184','5203','0');
INSERT INTO [t19188] VALUES (5246,'٤٩١٣ - مسألة: (وإن عدله اثنان، وجرحه اثنان، فالجرح أولى)','14188','5203','0');
INSERT INTO [t19188] VALUES (5247,'٤٩١٤ - مسألة: (وإن سأل المدعى حبس المشهود عليه حتى يزكى شهوده، فهل يحبس؟ على وجهين)','14189','5203','0');
INSERT INTO [t19188] VALUES (5248,'٤٩١٥ - مسألة: (وإن أقام شاهدا، وسأل حبسه حتى يقيم الآخر، حبسه إن كان فى المال)','14190','5203','0');
INSERT INTO [t19188] VALUES (5249,'٤٩١٦ - مسألة: (وإن حاكم إليه من لا يعرف لسانه، ترجم','14192','5203','0');
INSERT INTO [t19188] VALUES (5250,'٤٩١٧ - مسألة: (ولا يقبل فى الترجمة، والجرح، والتعديل، والتعريف، والرسالة، إلا قول عدلين. [وعنه، يقبل قول)','14193','5203','0');
INSERT INTO [t19188] VALUES (5251,'٤٩١٨ - مسألة: (ومن ثبتت عدالته مرة، فهل يحتاج إلى تجديد البحث عن عدالته مرة أخرى؟ على وجهين)','14197','5203','0');
INSERT INTO [t19188] VALUES (5252,'٤٩١٩ - مسألة: والميت المدعى عليه كالغائب، بل أولى؛ لأن الغائب قد يحضر، بخلاف الميت. قال الشاعر','14202','5203','0');
INSERT INTO [t19188] VALUES (5253,'٤٩٢٠ - مسألة: (وهل يحلف المدعى أنه لم يبرأ إليه منه، ولا من شئ منه؟ على روايتين)','14202','5203','0');
INSERT INTO [t19188] VALUES (5254,'٤٩٢١ - مسألة: (ثم إذا قدم الغائب، أو بلغ الصبى، أو أفاق المجنون، فهو على حجته)','14204','5203','0');
INSERT INTO [t19188] VALUES (5255,'٤٩٢٢ - مسألة: (وإن كان الخصم فى البلد غائبا عن المجلس، لم تسمع البينة حتى يحضر، فإن امتنع من الحضور، سمعت البينة','14205','5203','0');
INSERT INTO [t19188] VALUES (5256,'٤٩٢٣ - مسألة: (وإن ادعى أن أباه مات عنه وعن أخ له غائب،','14208','5203','0');
INSERT INTO [t19188] VALUES (5257,'٤٩٢٤ - مسألة: (وإن ادعى إنسان أن الحاكم حكم له بحق، فصدقه، قبل قول الحاكم وحده، وإن لم يذكر الحاكم ذلك، فشهد عدلان أنه حكم له به، قبل شهادتهما، وأمضى القضاء. وكذلك إن شهدا أن فلانا وفلانا شهدا عندك بكذا، قبل شهادتهما)','14215','5203','0');
INSERT INTO [t19188] VALUES (5258,'٤٩٢٥ - مسألة: (وإن لم يشهد به أحد، لكن وجده فى قمطره، فى صحيفة تحت ختمه بخطه، فهل ينفذه؟ على روايتين)','14217','5203','0');
INSERT INTO [t19188] VALUES (5259,'٤٩٢٦ - مسألة: (وكذلك الشاهد إذا وجد خطه بشهادة فى كتاب، ولم يذكرها، فهل له أن يشهد بها؟ على روايتين)','14219','5203','0');
INSERT INTO [t19188] VALUES (5260,'٤٩٢٧ - مسألة: - (وحكم الحاكم لا يزيل الشئ عن صفته فى الباطن. وذكر ابن أبى موسى عنه رواية)','14228','5203','0');
INSERT INTO [t19188] VALUES (5261,'باب حكم كتاب القاضى إلى القاضى','14239','5148','0');
INSERT INTO [t19188] VALUES (5262,'٤٩٢٨ - مسألة: (يقبل كتاب القاضى إلى القاضى فى المال، وما','14240','5261','0');
INSERT INTO [t19188] VALUES (5263,'٤٩٢٩ - مسألة: (ويجوز كتاب القاضى فيما حكم به لينفذه فى المسافة القريبة ومسافة القصر، ويجوز فيما ثبت عنده ليحكم به فى المسافة','14245','5261','0');
INSERT INTO [t19188] VALUES (5264,'٤٩٣٠ - مسألة: (ويجوز أن يكتب إلى قاض معين، وإلى من يصل إليه كتابى هذا من قضاة المسلمين وحكامهم)','14248','5261','0');
INSERT INTO [t19188] VALUES (5265,'٤٩٣١ - مسألة: (ولا يقبل الكتاب إلا أن يشهد به شاهدان، يحضرهما القاضى الكاتب، فيقرؤه عليهما، ثم يقول: اشهد كما أن هذا','14248','5261','0');
INSERT INTO [t19188] VALUES (5266,'٤٩٣٢ - مسألة: (ولو أدرج الكتاب وختمه، وقال: هذا خطى، اشهدا على بما فيه)','14254','5261','0');
INSERT INTO [t19188] VALUES (5267,'٤٩٣٣ - مسألة: (فإذا وصل الكتاب، فأحضر المكتوب إليه الخصم المحكوم عليه فى الكتاب، فقال: لست فلان بن فلان. فالقول قوله مع يمينه، إلا أن تقوم به بينة، فإن ثبت أنه فلان بن فلان ببينة أو','14258','5261','0');
INSERT INTO [t19188] VALUES (5268,'٤٩٣٤ - مسألة: (وإن تغيرت حال القاضى الكاتب بعزل أو موت، لم يقدح فى كتابه، وإن تغيرت بفسق، لم يقدح فيما حكم به، وبطل فيما ثبت عنده ليحكم به، وإن تغيرت حال المكتوب إليه، فلمن قام مقامه قبول الكتاب والعمل به)','14262','5261','0');
INSERT INTO [t19188] VALUES (5269,'٤٩٣٥ - مسألة: (وكل من ثبت له عند حاكم حق، أو ثبتت براءته، مثل أن أنكر وحلفه الحاكم، فسأل الحاكم أن يكتب له محضرا','14266','5261','0');
INSERT INTO [t19188] VALUES (5270,'٤٩٣٦ - مسألة: (وإن سأل من ثبت محضره عند الحاكم، أن يسجل به، فعل ذلك، وجعله نسختين؛ نسخة يدفعها إليه، ونسخة يحبسها عنده، والورق من بيت المال، فإن لم يكن، فمن مال المكتوب له)','14269','5261','0');
INSERT INTO [t19188] VALUES (5271,'٤٩٣٧ - مسألة: (وأما السجل، فهو لإنفاذ ما ثبت عنده،','14272','5261','0');
INSERT INTO [t19188] VALUES (5272,'باب القسمة','14278','5148','0');
INSERT INTO [t19188] VALUES (5273,'٤٩٣٨ - مسألة: (وهى نوعان؛ قسمة تراض','14278','5272','0');
INSERT INTO [t19188] VALUES (5274,'٤٩٣٩ - مسألة: (وهذه)','14281','5272','0');
INSERT INTO [t19188] VALUES (5275,'٤٩٤٠ - مسألة: (والضرر المانع من القسمة، هو نقص القيمة','14282','5272','0');
INSERT INTO [t19188] VALUES (5276,'٤٩٤١ - مسألة: (فإن كان الضرر على أحدهما دون الآخر، كرجلين لأحدهما الثلثان، وللآخر الثلث، ينتفع صاحب الثلثين','14284','5272','0');
INSERT INTO [t19188] VALUES (5277,'٤٩٤٢ - مسألة: (وإن كان بينهما عبيد، أو بهائم، أو ثياب،','14288','5272','0');
INSERT INTO [t19188] VALUES (5278,'٤٩٤٣ - مسألة: (وإن كان بينهما حائط، لم يجبر الممتنع من قسمته، وإن استهدم، لم يجبر على قسم عرصته. وقال أصحابنا: إن','14291','5272','0');
INSERT INTO [t19188] VALUES (5279,'٤٩٤٤ - مسألة: (وإن كان بينهما دار لها علو وسفل، فطلب أحدهما قسمها، لأحدهما العلو وللآخر السفل، أو كان بينهما منافع، لم يجبر الممتنع من قسمها، وإن تراضيا على قسمها كذلك، أو على قسم المنافع بالمهايأة، جاز)','14294','5272','0');
INSERT INTO [t19188] VALUES (5280,'٤٩٤٥ - مسألة: (وإن كان بينهما أرض ذات زرع، فطلب أحدهما قسمها دون الزرع، قسمت)','14298','5272','0');
INSERT INTO [t19188] VALUES (5281,'٤٩٤٦ - مسألة: (وإن طلب قسمتها مع الزرع، لم يجبر الآخر)','14299','5272','0');
INSERT INTO [t19188] VALUES (5282,'٤٩٤٧ - مسألة: (وإن تراضوا عليه والزرع قصيل أو قطن،','14300','5272','0');
INSERT INTO [t19188] VALUES (5283,'٤٩٤٨ - مسألة: (وإن كان بينهما نهر، أو قناة، أو عين ينبع ماؤها، فالماء بينهما على ما اشترطا عند استخراج ذلك)','14302','5272','0');
INSERT INTO [t19188] VALUES (5284,'٤٩٤٩ - مسألة: (وإن أرادا قسم ذلك بنصب خشبة، أو حجر في مصدم الماء، فيه ثقبان على قدر حق كل واحد منهما جاز)','14302','5272','0');
INSERT INTO [t19188] VALUES (5285,'٤٩٥٠ - مسألة: (وهذه القسمة إفراز حق أحدهما من الآخر، وليست بيعا)','14309','5272','0');
INSERT INTO [t19188] VALUES (5286,'٤٩٥١ - مسألة: (فمتى عدلت السهام وأخرجت القرعة، لزمت القسمة)','14314','5272','0');
INSERT INTO [t19188] VALUES (5287,'٤٩٥٢ - مسألة: (وإذا كان في القسمة تقويم، لم يجز أقل من قاسمين)','14315','5272','0');
INSERT INTO [t19188] VALUES (5288,'٤٩٥٣ - مسألة: (وإذا سألوا الحاكم قسمة عقار لم يثبت عنده أنه لهم، قسمه، وذكر في كتاب القسمة أن قسمه بمجرد دعواهم، لا عن بينة شهدت لهم بملكهم)','14316','5272','0');
INSERT INTO [t19188] VALUES (5289,'٤٩٥٤ - مسألة: (وإن تقاسموا ثم استحق من حصة أحدهما شئ معين، بطلت)','14333','5272','0');
INSERT INTO [t19188] VALUES (5290,'٤٩٥٥ - مسألة: (وإن اقتسما دارين قسمة تراض، فبنى أحدهما فى نصيبه، ثم خرجت الدار مستحقة، فقلع بناؤه، رجع بنصف قيمته على شريكه)','14336','5272','0');
INSERT INTO [t19188] VALUES (5291,'٤٩٥٦ - مسألة: (وإن خرج فى نصيب أحدهما عيب، فله فسخ القسمة)','14338','5272','0');
INSERT INTO [t19188] VALUES (5292,'٤٩٥٧ - مسألة: (وإذا اقتسم الورثة العقار، ثم ظهر على الميت دين، فإن قلنا: هى إفراز حق. لم تبطل القسمة. وإن قلنا: هى بيع.','14339','5272','0');
INSERT INTO [t19188] VALUES (5293,'٤٩٥٨ - مسألة: (وإن اقتسما فحصلت الطريق فى نصيب أحدهما، ولا منفذ للآخر، بطلت القسمة)','14346','5272','0');
INSERT INTO [t19188] VALUES (5294,'٤٩٥٩ - مسألة: (ويجوز للأب والوصى قسم مال المولى عليه مع شريكه)','14348','5272','0');
INSERT INTO [t19188] VALUES (5295,'باب الدعاوى والبينات','14349','5148','0');
INSERT INTO [t19188] VALUES (5296,'٤٩٦٠ - مسألة: (والمدعى من إذا سكت ترك، والمنكر من إذا سكت لم يترك)','14350','5295','0');
INSERT INTO [t19188] VALUES (5297,'٤٩٦١ - مسألة: (ولا تصح الدعوى والإنكار إلا من جائز التصرف)','14352','5295','0');
INSERT INTO [t19188] VALUES (5298,'٤٩٦٢ - مسألة: (وإن تداعيا عينا، لم تخل من ثلاثة أقسام؛ أحدها، أن تكون في يد أحدهما، فهي له مع يمينه أنها له، لا حق للآخر فيها، إذا لم تكن بينة)','14352','5295','0');
INSERT INTO [t19188] VALUES (5299,'٤٩٦٣ - مسألة: (ولو تنازعا دابة، أحدهما راكبها، أو له عليها','14353','5295','0');
INSERT INTO [t19188] VALUES (5300,'٤٩٦٤ - مسألة: (وإن تنازعا قميصا، أحدهما لابسه، والآخر آخذ بكمه، فهو للابسه)','14355','5295','0');
INSERT INTO [t19188] VALUES (5301,'٤٩٦٥ - مسألة: (وإن تنازع صاحب الدار والخياط الإبرة والمقص، فهما للخياط)','14356','5295','0');
INSERT INTO [t19188] VALUES (5302,'٤٩٦٦ - مسألة: (وإن تنازع هو والقراب القربة، فهي للقراب)','14356','5295','0');
INSERT INTO [t19188] VALUES (5303,'٤٩٦٧ - مسألة: (وإن تنازعا عرصة فيها شجر، أو بناء لأحدهما، فهي له)','14357','5295','0');
INSERT INTO [t19188] VALUES (5304,'٤٩٦٨ - مسألة: (وإن تنازعا حائطا معقودا ببناء أحدهما، وحده، أو متصلا به اتصالا لا يمكن إحداثه، أو له عليه أزج، فهو له، وإن كان محلولا من بنائهما، أو معقودا بهما، فهو بينهما)','14357','5295','0');
INSERT INTO [t19188] VALUES (5305,'٤٩٦٩ - مسألة: (ولا ترجح الدعوى بوضع خشب أحدهما عليه، ولا بوجوه الآجر، والتزويق، والتجصيص، ومعاقد القمط','14361','5295','0');
INSERT INTO [t19188] VALUES (5306,'٤٩٧٠ - مسألة: (وإن تنازع صاحب العلو والسفل في السلم المنصوب أو الدرجة، فهي لصاحب العلو، إلا أن يكون تحت الدرجة مسكن لصاحب السفل، فيكون بينهما. وإن تنازعا في السقف الذي بينهما، فهو بينهما)','14365','5295','0');
INSERT INTO [t19188] VALUES (5307,'٤٩٧١ - مسألة: (وإن تنازع المؤجر والمستأجر في رف مقلوع، أو مصراع له شكل منصوب في الدار، فهو لصاحبها، وإلا','14367','5295','0');
INSERT INTO [t19188] VALUES (5308,'٤٩٧٢ - مسألة: (وإن تنازعا دارا في أيديهما، فادعاها أحدهما،','14369','5295','0');
INSERT INTO [t19188] VALUES (5309,'٤٩٧٣ - مسألة: (وإن تنازع الزوجان أو ورثتهما في قماش البيت، فما كان يصلح للرجال فهو للرجل، وما)','14376','5295','0');
INSERT INTO [t19188] VALUES (5310,'٤٩٧٤ - مسألة: (وإن اختلف صانعان في قماش دكان لهما، حكم بآلة كل صناعة لصاحبها، في ظاهر كلام أحمد والخرقى)','14378','5295','0');
INSERT INTO [t19188] VALUES (5311,'٤٩٧٥ - مسألة: (وكل من قلنا: هو له. فهو مع يمينه، إذا لم تكن بينة)','14381','5295','0');
INSERT INTO [t19188] VALUES (5312,'٤٩٧٦ - مسألة: (وإن كان لأحدهما بينة، حكم له بها)','14382','5295','0');
INSERT INTO [t19188] VALUES (5313,'٤٩٧٧ - مسألة: (وإن كان لكل واحد منهما بينة، حكم بها للمدعى، في ظاهر المذهب. وعنه، إن شهدت بينة المدعى عليه أنها','14384','5295','0');
INSERT INTO [t19188] VALUES (5314,'٤٩٧٨ - مسألة: (وإن أقام الداخل بينة أنه اشتراها من الخارج، وأقام الخارج بينة أنه اشتراها من الداخل، فقال القاضى: تقدم بينة الداخل)','14388','5295','0');
INSERT INTO [t19188] VALUES (5315,'٤٩٧٩ - مسألة: (وإن تنازعا مسناة','14395','5295','0');
INSERT INTO [t19188] VALUES (5316,'٤٩٨٠ - مسألة: (وإن تنازعا صبيا في يديهما، فكذلك)','14395','5295','0');
INSERT INTO [t19188] VALUES (5317,'٤٩٨١ - مسألة: (وإن كان لأحدهما بينة، حكم له بها)','14398','5295','0');
INSERT INTO [t19188] VALUES (5318,'٤٩٨٢ - مسألة: وإن كانت إحداهما متقدمة التاريخ، قدمت','14399','5295','0');
INSERT INTO [t19188] VALUES (5319,'٤٩٨٣ - مسألة: فإن وقتت إحداهما وأطلقت الأخرى','14400','5295','0');
INSERT INTO [t19188] VALUES (5320,'٤٩٨٤ - مسألة: (وإن شهدت إحداهما بالملك، والأخرى بالملك والنتاج، أو سبب من أسباب الملك، فهل ترجح بذلك؟ على وجهين)','14401','5295','0');
INSERT INTO [t19188] VALUES (5321,'٤٩٨٥ - مسألة: (ولا تقدم إحداهما بكثرة العدد، ولا اشتهار العدالة، ولا الرجلان على الرجل والمرأتين، ويقدم الشاهدان على الشاهد واليمين، في أحد الوجهين)','14402','5295','0');
INSERT INTO [t19188] VALUES (5322,'٤٩٨٦ - مسألة: (وإذا تساوتا، تعارضتا، وقسمت العين بينهما بغير يمين. وعنه، أنهما يتحالفان، كمن لا بينة لهما. وعنه، أنه يقرع بينهما، فمن قرع صاحبه، حلف وأخذها)','14405','5295','0');
INSERT INTO [t19188] VALUES (5323,'٤٩٨٧ - مسألة: (فإن ادعى أحدهما أنه اشتراها من زيد، لم تسمع البينة على ذلك حتى يقول: وهي ملكه. وتشهد البينة به)','14409','5295','0');
INSERT INTO [t19188] VALUES (5324,'٤٩٨٨ - مسألة: (وإن أقام أحدهما بينة أنها ملكه، وأقام الآخر بينة أنه اشتراها منه، أو أعتقه، قدمت بينه الثاني)','14411','5295','0');
INSERT INTO [t19188] VALUES (5325,'٤٩٨٩ - مسألة: (ولو أقام رجل بينة أن هذه الدار لأبى، خلفها','14411','5295','0');
INSERT INTO [t19188] VALUES (5326,'٤٩٩٠ - مسألة: (فإن كان المدعى عبدا، فأقر لأحدهما، لم يرجح بإقراره)','14415','5295','0');
INSERT INTO [t19188] VALUES (5327,'٤٩٩١ - مسألة: (وإن كانت لكل واحد)','14415','5295','0');
INSERT INTO [t19188] VALUES (5328,'٤٩٩٢ - مسألة: (فإن أقر صاحب اليد لأحدهما، لم يرجح)','14418','5295','0');
INSERT INTO [t19188] VALUES (5329,'٤٩٩٣ - مسألة: (وإن ادعاها صاحب اليد لنفسه)','14419','5295','0');
INSERT INTO [t19188] VALUES (5330,'٤٩٩٤ - مسألة: (وإن كان فى يد رجل عبد، فادعى أنه اشتراه من زيد، وادعى العبد أن زيدا أعتقه، وأقام كل واحد بينه، انبنى على بينة الداخل والخارج، وإن كان العبد فى يد زيد، فالحكم فيه كالحكم','14424','5295','0');
INSERT INTO [t19188] VALUES (5331,'٤٩٩٥ - مسألة: (وإن كان فى يده عبد، فادعى عليه رجلان، كل واحد منهما أنه اشتراه منه بثمن سماه، فصدقهما، لزمه الثمن لكل واحد منهما، وإن أنكرهما، حلف لهما، وبرئ، وإن صدق أحدهما، لزمه ما ادعاه، وحلف للآخر، وإن كان لأحدهما بينة، فله الثمن، ويحلف للآخر، وإن كان لكل واحد منهما بينة، وأمكن صدقهما؛ لاختلاف تاريخهما، أو إطلاقها، أو إطلاق إحداهما وتأريخ الأخرى، عمل بهما، وإن اتفق تاريخهما، تعارضتا، والحكم على ما','14427','5295','0');
INSERT INTO [t19188] VALUES (5332,'٤٩٩٦ - مسألة: (وإن ادعى كل واحد منهما أنه باعنى إياه بألف، وأقام بينة، قدم أسبقهما تاريخا)','14430','5295','0');
INSERT INTO [t19188] VALUES (5333,'٤٩٩٧ - مسألة: (وإن قال أحدهما: غصبنى إياه. وقال الآخر: ملكنيه. أو: أقر لى به. وأقام كل واحد)','14431','5295','0');
INSERT INTO [t19188] VALUES (5334,'باب فى تعارض البينتين','14434','5148','0');
INSERT INTO [t19188] VALUES (5335,'٤٩٩٨ - مسألة: (وإن قال: إن مت فى المحرم، فسالم حر، وإن مت في صفر، فغانم حر. وأقام كل واحد منهما بينة بموجب عتقه، قدمت بينة سالم)','14435','5334','0');
INSERT INTO [t19188] VALUES (5336,'٤٩٩٩ - مسألة: (وإن قال: إن مت من مرضى هذا، فسالم حر. وإن برئت، فغانم حر. فأقاما بينتين، تعارضتا، وبقيا على الرق.','14436','5334','0');
INSERT INTO [t19188] VALUES (5337,'٥٠٠٠ - مسألة: (وإن أتلف ثوبا، فشهدت بينة أن قيمته عشرون، وشهدت أخرى أن قيمته ثلاثون، لزمته أقل','14439','5334','0');
INSERT INTO [t19188] VALUES (5338,'٥٠٠١ - مسألة: (ولو ماتت امرأة وابنها، فقال زوجها: ماتت فورثناها، ثم مات ابنى فورثته. وقال أخوها: بل مات ابنها فورثته، ثم ماتت فورثناها. حلف كل واحد منهما على إبطال دعوى صاحبه، وكان ميراث الابن لأبيه، وميراث المرأة لأخيها وزوجها نصفين. وإن','14441','5334','0');
INSERT INTO [t19188] VALUES (5339,'٥٠٠٢ - مسألة: (وإن شهدت بينة سالم أنه رجع عن عتق غانم، عتق سالم وحده، سواء كانت)','14448','5334','0');
INSERT INTO [t19188] VALUES (5340,'٥٠٠٣ - مسألة: (وإن كانت قيمة غانم سدس المال، وبينته أجنبية، قبلت)','14448','5334','0');
INSERT INTO [t19188] VALUES (5341,'٥٠٠٤ - مسألة: (وإن شهدت بينة أنه أعتق سالما فى مرضه، وشهدت أخرى أنه وصى بعتق غانم، وكل واحد منهما ثلث المال، عتق سالم وحده)','14451','5334','0');
INSERT INTO [t19188] VALUES (5342,'٥٠٠٥ - مسألة: (وإن شهدت بينة غانم أنه أعتقه فى مرضه أيضا، عتق أقدمهما تاريخا، فإن جهل السابق، عتق أحدهما بالقرعة)','14451','5334','0');
INSERT INTO [t19188] VALUES (5343,'٥٠٠٦ - مسألة: (فإن كانت بينة أحدهما وارثة، ولم تكذب الأجنبية، فكذلك. وإن قالت: ما أعتق سالما، إنما أعتق غانما. عتق غانم كله. وحكم سالم كحكمه لو لم يطعن فى بينته، فى أنه يعتق إن تقدم تاريخ عتقه، أو خرجت له القرعة، وإلا فلا. وإن كانت الوارثة فاسقة، ولم تطعن فى بينة سالم، عتق سالم كله، وينظر فى)','14452','5334','0');
INSERT INTO [t19188] VALUES (5344,'٥٠٠٧ - مسألة: (وإن أقام كل واحد بينة أنه مات على دينه، تعارضتا، وإن قال شاهدان: نعرفه مسلما. وقال شاهدان: نعرفه كافرا. فالميراث للمسلم، إذا لم يؤرخ الشهود معرفتهم)','14466','5334','0');
INSERT INTO [t19188] VALUES (5345,'٥٠٠٨ - مسألة: (وإن خلف أبوين كافرين وابنين مسلمين، فاختلفوا فى دينه، فالقول قول الأبوين. ويحتمل أن القول قول الابنين)','14470','5334','0');
INSERT INTO [t19188] VALUES (5346,'٥٠٠٩ - مسألة: (وإن خلف ابنا كافرا وأخا وامرأة مسلمين، فاختلفوا فى دينه، فالقول قول الابن، على قول الخرقى)','14471','5334','0');
INSERT INTO [t19188] VALUES (5347,'٥٠١٠ - مسألة: (ولو مات مسلم، وخلف ولدين؛ مسلما وكافرا، فأسلم الكافر، وقال: أسلمت قبل موت أبى. وقال أخوه','14473','5334','0');
INSERT INTO [t19188] VALUES (5348,'٥٠١١ - مسألة: (وإن قال: أسلمت فى المحرم، ومات أبى فى صفر)','14474','5334','0');
INSERT INTO [t19188] VALUES (5349,'كتاب الشهادات','14476','0','0');
INSERT INTO [t19188] VALUES (5350,'[باب. . .]','14476','5349','0');
INSERT INTO [t19188] VALUES (5351,'٥٠١٢ - مسألة: (تحمل الشهادة وأداؤها فرض على الكفاية، إذا قام بها من يكفى، سقطت عن الباقين، وإن لم يقف بها)','14478','5350','0');
INSERT INTO [t19188] VALUES (5352,'٥٠١٣ - مسألة: (قال الخرقى: ومن لزمته الشهادة، فعليه أن يقوم بها على القريب والبعيد، لا يسعه التخلف عن إقامتها وهو قادر على ذلك)','14481','5350','0');
INSERT INTO [t19188] VALUES (5353,'٥٠١٤ - مسألة: (ولا يجوز لمن تعينت عليه أخذ الأجرة عليها، ولا يجوز ذلك لمن لم تتعين عليه، في أصح الوجهين)','14483','5350','0');
INSERT INTO [t19188] VALUES (5354,'٥٠١٥ - مسألة: (ومن كانت عنده شهادة فى حد لله تعالى، أبيح إقامتها، ولم يستحب، وللحاكم أن يعرض له بالوقوف عنها، فى أحد الوجهين)','14485','5350','0');
INSERT INTO [t19188] VALUES (5355,'٥٠١٦ - مسألة (ومن كانت عنده شهادة لآدمى يعلمها، لم يقمها حتى يسأله، فإن لم يعلفها، استحب له إعلامه بها، وله إقامتها قبل ذلك)','14488','5350','0');
INSERT INTO [t19188] VALUES (5356,'٥٠١٧ - مسألة: (ولا يجوز أن يشهد إلا بما يعلمه بروية أو سماع)','14489','5350','0');
INSERT INTO [t19188] VALUES (5357,'٥٠١٨ - مسألة: (والرؤية تختص بالأفعال؛ كالقتل، والغصب، والسرقة، وشرب الخمر، والرضاع، والولادة، وغيرها)','14491','5350','0');
INSERT INTO [t19188] VALUES (5358,'٥٠١٩ - مسألة: (والسماع على ضربين؛ سماع من المشهود عليه، نحو الإقرار، والعقود، والطلاق)','14491','5350','0');
INSERT INTO [t19188] VALUES (5359,'٥٠٢٠ - مسألة: الضرب الثانى (سماع من جهة الاستفاضة، فيما يتعذر علمه فى الغالب إلا بذلك؛ كالنسب، والموت، والملك، والنكاح، والخلع، والوقف، ومصرفه، والعتق، والولاء، والولاية، والعزل، وما أشبة ذلك)','14495','5350','0');
INSERT INTO [t19188] VALUES (5360,'٥٠٢١ - مسألة: (ولا تقبل الاستفاضة إلا من عدد يقع العلم بخبرهم، فى ظاهر كلام أحمد والخرقى. وقال القاضى: تسمع من عدلين فصاعدا [ظاهر كلام أحمد والخرقى، أنه لا يشهد بالاستفاضة، حتى يسمع من عدد كثير، يحصل العلم بخبرهم. وذكر القاضى فى «المجرد» أنه يكفى أن يسمع من اثنين عدلين، يسكن قلبه إلى خبرهما]','14499','5350','0');
INSERT INTO [t19188] VALUES (5361,'٥٠٢٢ - مسألة: (وإن سمع إنسانا يقر بنسب أب، أو ابن، فصدقه المقر له، جاز أن يشهد)','14501','5350','0');
INSERT INTO [t19188] VALUES (5362,'٥٠٢٣ - مسألة: (وإن رأى شيئا فى يد إنسان، يتصرف فيه تصرف الملاك؛ من النقض، والبناء، والإجارة، والإعارة، ونحوها، جاز أن يشهد له بالملك)','14503','5350','0');
INSERT INTO [t19188] VALUES (5363,'٥٠٢٤ - مسألة: (وإن شهد بالرضاع، فلا بد من ذكر عدد','14506','5350','0');
INSERT INTO [t19188] VALUES (5364,'٥٠٢٥ - مسألة: (وإن شهد بالقتل، احتاج أن يقول: ضربه بالسيف. أو: جرحه فقتله. أو: مات من ذلك. فإن قال: جرحه فماب لم يحكم به)','14507','5350','0');
INSERT INTO [t19188] VALUES (5365,'٥٠٢٦ - مسألة: (وإن شهد بالزنى، فلا بد أن يذكر بمن زنى، واين زنى، وأنه رأى ذكره فى فرجها)','14508','5350','0');
INSERT INTO [t19188] VALUES (5366,'٥٠٢٧ - مسألة: (ومن شهد بالسرقة، فلا بد من ذكر المسروق منه، والنصاب، والحزز، وصفة السرقة)','14509','5350','0');
INSERT INTO [t19188] VALUES (5367,'٥٠٢٨ - مسألة: (وإن شهد بالقذف، فلا بد من ذكر المقذوف، وصفة القذف)','14509','5350','0');
INSERT INTO [t19188] VALUES (5368,'٥٠٢٩ - مسألة: (وإن شهدا أن هذا العبد ابن أمة فلان، لم يحكم له به حتى يقولا: ولدته فى ملكه)','14509','5350','0');
INSERT INTO [t19188] VALUES (5369,'٥٠٣٠ - مسألة: (وإن شهدت أنه اشتراها من فلان، أو وقفها عليه، أو أعتقها، لم يحكم بها حتى يقولا: وهى في ملكه)','14511','5350','0');
INSERT INTO [t19188] VALUES (5370,'٥٠٣١ - مسألة: (وإن شهدا','14511','5350','0');
INSERT INTO [t19188] VALUES (5371,'٥٠٣٢ - مسألة','14512','5350','0');
INSERT INTO [t19188] VALUES (5372,'٥٠٣٣ - مسألة: (وتجوز شهادة المستخفى)','14516','5350','0');
INSERT INTO [t19188] VALUES (5373,'٥٠٣٤ - مسألة: (ومن سمع رجلا يقر بحق، أو يشهد شاهدا بحق، أو سمع حاكما يحكم، أو يشهد على حكمه وإنفاذه)','14517','5350','0');
INSERT INTO [t19188] VALUES (5374,'٥٠٣٥ - مسألة: (وإن شهد أحدهما أنه أقر له بألف أمس، وشهد آخر أنه أقر له بألف اليوم، أو شهد أحدهما أنه باعه داره أمس،','14528','5350','0');
INSERT INTO [t19188] VALUES (5375,'٥٠٣٦ - مسألة: (وكذلك القذف)','14530','5350','0');
INSERT INTO [t19188] VALUES (5376,'٥٠٣٧ - مسألة: (وإن شهد شاهد أنه أقر له بألفين، وشهد آخر أنه أقر له بألف، ثبت الألف، ويحلف على الآخر مع شاهده، إن أحب)','14534','5350','0');
INSERT INTO [t19188] VALUES (5377,'٥٠٣٨ - مسألة: (وإن شهد أحدهما أن له)','14536','5350','0');
INSERT INTO [t19188] VALUES (5378,'٥٠٣٩ - مسألة: (وإن شهد أحدهما أن له عليه ألفا من قرض، وشهد آخر أن له عليه ألفا من ثمن مبيع، لم تكمل البينة)','14536','5350','0');
INSERT INTO [t19188] VALUES (5379,'٥٠٤٠ - مسألة: (وإن شهد شاهدان أن له عليه ألفا، وقال أحدهما: قضاه بعضه. بطلت شهادته. نص عليه. وإن شهدا','14537','5350','0');
INSERT INTO [t19188] VALUES (5380,'٥٠٤١ - مسألة: (وإن كانت له بينة بألف، فقال: أريد أن تشهدا لى بخمسمائة. لم يجز. وعند أبي الخطاب، يجوز)','14540','5350','0');
INSERT INTO [t19188] VALUES (5381,'باب شروط من تقبل شهادته','14544','5349','0');
INSERT INTO [t19188] VALUES (5382,'٥٠٤٢ - مسألة: (ولا تقبل شهادة فاسق)','14565','5381','0');
INSERT INTO [t19188] VALUES (5383,'٥٠٤٣ - مسألة: (ويتخرج على قبول شهادة أهل الذمة، قبول شهادة الفاسق من جهة الاعتقاد المتدين به، إذا لم يتدين بالشهادة لموافقيه على مخالفيه)','14568','5381','0');
INSERT INTO [t19188] VALUES (5384,'٥٠٤٤ - مسألة: (فأما من فعل شيئا من الفروع المختلف فيها، فتزوج بغير ولى، أو شرب من النبيذ ما لا يسكره، أو أخر الحج الواجب','14569','5381','0');
INSERT INTO [t19188] VALUES (5385,'٥٠٤٥ - مسألة: (الثانى، استعمال المروءة، وهو فعل ما يجمله ويزينه، وترك ما يدنسه ويشينه، فلا تقبل شهادة المصافع','14572','5381','0');
INSERT INTO [t19188] VALUES (5386,'٥٠٤٦ - مسألة: (فأما الشين فى الصناعة)','14583','5381','0');
INSERT INTO [t19188] VALUES (5387,'٥٠٤٧ - مسألة: (ولا يعتبر إصلاح العمل. وعنه، يعتبر فى التائب إصلاح العمل سنة)','14608','5381','0');
INSERT INTO [t19188] VALUES (5388,'٥٠٤٨ - مسألة: (ولا تقبل شهادة القاذف حتى يتوب)','14612','5381','0');
INSERT INTO [t19188] VALUES (5389,'٥٠٤٩ - مسألة: (وتوبته أن يكذب نفسه. وقيل: إن علم صدق نفسه، فتوبته أن يقول: قد ندمت على ما قلت، ولا أعود إلى مثله، وأنا تائب إلى الله تعالى منه)','14616','5381','0');
INSERT INTO [t19188] VALUES (5390,'٥٠٥٠ - مسألة: (وتجوز شهادة الأعمى في المسموعات، إذا تيقن الصوت، وبالاستفاضة)','14623','5381','0');
INSERT INTO [t19188] VALUES (5391,'٥٠٥١ - مسألة: (وتجوز في المرئيات التى تحملها قبل العمى، إذا عرف الفاعل باسمه ونسبه وما يتميز به)','14625','5381','0');
INSERT INTO [t19188] VALUES (5392,'٥٠٥٢ - مسألة: (فإن لم يعرف المشهود عليه)','14626','5381','0');
INSERT INTO [t19188] VALUES (5393,'٥٠٥٣ - مسألة: (وإن شهد عند الحاكم، ثم عمى، قبلت شهادته، وجها واحدا)','14627','5381','0');
INSERT INTO [t19188] VALUES (5394,'٥٠٥٤ - مسألة: (وشهادة ولد الزنى جائزة، فى الزنى وغيره)','14627','5381','0');
INSERT INTO [t19188] VALUES (5395,'٥٠٥٥ - مسألة: (وتقبل شهادة الإنسان على فعل نفسه؛ كالمرضعة على الرضاع، والقاسم على القسمة، والحاكم على حكمه بعد العزل)','14629','5381','0');
INSERT INTO [t19188] VALUES (5396,'٥٠٥٦ - مسألة: (وتقبل شهادة البدوى على القروى، والقروى على البدوى)','14631','5381','0');
INSERT INTO [t19188] VALUES (5397,'باب موانع الشهادة','14634','5349','0');
INSERT INTO [t19188] VALUES (5398,'٥٠٥٧ - مسألة: (وتقبل شهادة بعضهم على بعض، في أصح الروايتين)','14637','5397','0');
INSERT INTO [t19188] VALUES (5399,'٥٠٥٨ - مسألة: (ولا تقبل شهادة أحد الزوجين لصاحبه، في إحدى الروايتين)','14639','5397','0');
INSERT INTO [t19188] VALUES (5400,'٥٠٥٩ - مسألة: (ولا تقبل شهادة السيد لعبده، ولا العبد لسيده)','14641','5397','0');
INSERT INTO [t19188] VALUES (5401,'٥٠٦٠ - مسألة: (وتقبل شهادة الأخ لأخيه، وسائر الأقارب، والصديق لصديقه، والمولى لعتيقه)','14643','5397','0');
INSERT INTO [t19188] VALUES (5402,'٥٠٦١ - مسألة: (و)','14648','5397','0');
INSERT INTO [t19188] VALUES (5403,'٥٠٦٢ - مسألة: (ولو لم يشهد بها عند الحاكم حتى صار عدلا، قبلت)','14658','5397','0');
INSERT INTO [t19188] VALUES (5404,'٥٠٦٣ - مسألة: (ولو شهد)','14658','5397','0');
INSERT INTO [t19188] VALUES (5405,'٥٠٦٤ - مسألة: (وإن شهد لمكاتبه، أو لموروثه بجرح قبل برئه، فردت، ثم أعادها بعد عتق المكاتب وبرء الجرح، ففى ردها وجهان)','14660','5397','0');
INSERT INTO [t19188] VALUES (5406,'٥٠٦٥ - مسألة: (وإن شهد الشريك بعفو شريكه عن الشفعة، ثم عفا الشاهد عن شفعته، وأعاد تلك الشهادة، لم تقبل. ذكره القاضي)','14661','5397','0');
INSERT INTO [t19188] VALUES (5407,'باب أقسام المشهود به','14664','5349','0');
INSERT INTO [t19188] VALUES (5408,'٥٠٦٦ - مسألة: (وهل يثبت الإقرار بالزنى بشاهدين، أو لا يثبت إلا بأربعة؟ على روايتين)','14665','5407','0');
INSERT INTO [t19188] VALUES (5409,'٥٠٦٧ - مسألة: (وإن ادعى رجل الخلع، قبل فيه رجل وامرأتان)','14696','5407','0');
INSERT INTO [t19188] VALUES (5410,'٥٠٦٨ - مسألة: (وإذا شهد رجل وامرأتان لرجل بجارية أنها أم ولده، وولدها منه، قضى له بالجارية أم ولد. وهل تثبت حرية الولد ونسبه من مدعيه؟ على روايتين)','14697','5407','0');
INSERT INTO [t19188] VALUES (5411,'باب الشهادة على الشهادة والرجوع عن الشهادة','14700','5349','0');
INSERT INTO [t19188] VALUES (5412,'٥٠٦٩ - مسألة: (ولا تقبل إلا أن تتعذر شهادة شهود الأصل؛ بموت، أو مرض، أو غيبة إلى مسافة القصر)','14703','5411','0');
INSERT INTO [t19188] VALUES (5413,'٥٠٧٠ - مسألة: (ولا يجوز لشاهد الفرع أن يشهد حتى يسترعيه شاهد الأصل، فيقول: اشهد على شهادتى أنى أشهد أن فلان ابن فلان، وقد عرفته بعييه، واسمه، ونسبه، أقر عندى، وأشهدنى على نفسه طوعا بكذا. أو: شهدت عليه. أو: أقر عندى بكذا. وإن سمعه يقول: أشهد على فلان بكذا. لم يجز أن يشهد، إلا أن يسمعه يشهد عند الحاكم، أو يشهد بحق يعزيه إلى سبب؛ من بيع، أو إجارة، أو قرض، فهل يشهد به؟ على وجهين)','14706','5411','0');
INSERT INTO [t19188] VALUES (5414,'٥٠٧١ - مسألة: (وتثبت شهادة شاهدى الأصل بشهادة شاهدين، يشهدان عليهما، سواء شهدا على كل واحد منهما، أو شهد','14712','5411','0');
INSERT INTO [t19188] VALUES (5415,'٥٠٧٢ - مسألة: (ولا مدخل للنساء فى شهادة الفرع. وعنه،','14716','5411','0');
INSERT INTO [t19188] VALUES (5416,'٥٠٧٣ - مسألة: (قال القاضى: لا تجوز شهادة رجلين على)','14719','5411','0');
INSERT INTO [t19188] VALUES (5417,'٥٠٧٤ - مسألة: (ولا يجوز للحاكم أن يحكم بشهادة شاهدى الفرع، حتى تثبت عنده عدالتهما، وعدالة شاهدى الأصل)','14720','5411','0');
INSERT INTO [t19188] VALUES (5418,'٥٠٧٥ - مسألة: (وإن شهدا عنده، فلم يحكم حتى حضر شهود الأصل، وقف الحكم على سماع شهادتهم)','14721','5411','0');
INSERT INTO [t19188] VALUES (5419,'٥٠٧٦ - مسألة: (وإن حدث منهم ما يمنع قبول الشهادة، لم يجز الحكم)','14722','5411','0');
INSERT INTO [t19188] VALUES (5420,'٥٠٧٧ - مسألة: (فإن حكم بشهادتهما، ثم رجع شهود الفرع، لزمهم الضمان)','14722','5411','0');
INSERT INTO [t19188] VALUES (5421,'٥٠٧٨ - مسألة: (فإن رجع شهود الأصل، لم يضمنوا)','14722','5411','0');
INSERT INTO [t19188] VALUES (5422,'٥٠٧٩ - مسألة: (وإن رجع شهود العتق، غرموا القيمة)','14726','5411','0');
INSERT INTO [t19188] VALUES (5423,'٥٠٨٠ - مسألة: (وإن رجع شهود الطلاق قبل الدخول، غرموا نصف المسمى، وإن كان بعده، لم يغرموا شيئا)','14727','5411','0');
INSERT INTO [t19188] VALUES (5424,'٥٠٨١ - مسألة: (وإن رجع شهود القصاص أو الحد قبل الاستيفاء، لم يستوف، وإن كان بعده، وقالوا: أخطأنا. فعليهم دية ما تلف، ويقسط الغرم على عددهم، فإن رجع أحدهم، غرم بقسطه)','14729','5411','0');
INSERT INTO [t19188] VALUES (5425,'٥٠٨٢ - مسألة: فإذا شهد ستة بالزنى على محصن','14735','5411','0');
INSERT INTO [t19188] VALUES (5426,'٥٠٨٣ - مسألة: (وإن شهد أربعة بالزنى، واثنان بالإحصان، ثم رجع الجميع، لزمتهم الدية أسداسا، فى أحد الوجهين. وفى الآخر، على شهود الزنى النصف، وعلى شهود الإحصان النصف. فإن شهد أربعة بالزنى، وشهد اثنان منهم بالإحصان، صحت الشهادة، فإن رجم ثم','14738','5411','0');
INSERT INTO [t19188] VALUES (5427,'٥٠٨٤ - مسألة: (وإذا حكم)','14743','5411','0');
INSERT INTO [t19188] VALUES (5428,'٥٠٨٥ - مسألة: (وإن بان بعد الحكم أن الشاهدين كانا','14745','5411','0');
INSERT INTO [t19188] VALUES (5429,'٥٠٨٦ - مسألة: (وإن شهدوا عند الحاكم بحق، ثم ماتوا،','14752','5411','0');
INSERT INTO [t19188] VALUES (5430,'٥٠٨٧ - مسألة: (وإذا علم الحاكم بشاهد الزور، عزره، وطاف به فى المواضع التى يشتهر فيها، فيقال: إنا وجدنا هذا شاهد زور، فاجتنبوه)','14753','5411','0');
INSERT INTO [t19188] VALUES (5431,'٥٠٨٨ - مسألة: (ولا تقبل الشهادة إلا بلفظ الشهادة، فإن قال: أعلم. أو: أحق. لم يحكم به)','14758','5411','0');
INSERT INTO [t19188] VALUES (5432,'باب اليمين فى الدعاوى','14761','5349','0');
INSERT INTO [t19188] VALUES (5433,'٥٠٨٩ - مسألة: (ولا يستحلف فى حقوق الله سبحانه)','14768','5432','0');
INSERT INTO [t19188] VALUES (5434,'٥٠٩٠ - مسألة: (ويجوز الحكم فى المال، وما يقصد به المال بشاهد ويمين المدعى)','14770','5432','0');
INSERT INTO [t19188] VALUES (5435,'٥٠٩١ - مسألة: (وهل يثبت العتق بشاهد ويمين؟ على روايتين)','14771','5432','0');
INSERT INTO [t19188] VALUES (5436,'٥٠٩٢ - مسألة: (ولا يقبل فى النكاح، والرجعة، وسائر ما لا','14771','5432','0');
INSERT INTO [t19188] VALUES (5437,'٥٠٩٣ - مسألة: (ومن حلف على فعل نفسه، أو دعوى عليه)','14772','5432','0');
INSERT INTO [t19188] VALUES (5438,'٥٠٩٤ - مسألة: (ومن توجهت عليه يمين لجماعة، فقال: أحلف يمينا واحدة. فرضوا، جاز، وإن أبوا، حلف لكل واحد يمينا)','14777','5432','0');
INSERT INTO [t19188] VALUES (5439,'فصل','14778','5432','0');
INSERT INTO [t19188] VALUES (5440,'٥٠٩٥ - مسألة: (وإن رأى الحاكم تغليظها بلفظ، أو زمن، أو مكان، جاز، ففى اللفظ يقول: والله الذى لا إله إلا هو، عالم الغيب والشهادة، الرحمن الرحيم، الطالب الغالب، الضار النافع، الذى يعلم خائنة الأعين وما تخفى الصدور. واليهودى يقول: والله الذى أنزل التوراة على موسى، وفلق له البحر، وأنجاه من فرعون وملئه. والنصرانى يقول: والله الذى أنزل الإنجيل على عيسى، وجعله يحيى الموتى، ويبرئ الأكمه والأبرص. والمجوسى يقول: والله الذى','14781','5432','0');
INSERT INTO [t19188] VALUES (5441,'٥٠٩٦ - مسألة: (ولا تغلظ اليمين إلا فيما له خطر، كالجنايات، والعتاق، والطلاق، وما تجب فيه الزكاة من المال)','14790','5432','0');
INSERT INTO [t19188] VALUES (5442,'٥٠٩٧ - مسألة: (وإن رأى الحاكم ترك التغليظ فتركه، كان','14791','5432','0');
INSERT INTO [t19188] VALUES (5443,'كتاب الإقرار','14799','0','0');
INSERT INTO [t19188] VALUES (5444,'[باب. . .]','14799','5443','0');
INSERT INTO [t19188] VALUES (5445,'٥٠٩٨ - مسألة: و (يصح الإقرار من كل مكلف مختار غير','14800','5444','0');
INSERT INTO [t19188] VALUES (5446,'٥٠٩٩ - مسألة: (وكذلك العبد المأذون له فى التجارة)','14806','5444','0');
INSERT INTO [t19188] VALUES (5447,'٥١٠٠ - مسألة: (ولا يصح إقرار السكران. وتتخرج صحته، بناء على طلاقه)','14806','5444','0');
INSERT INTO [t19188] VALUES (5448,'٥١٠١ - مسألة: (ولا يصح إقرار المكره، إلا أن يقر بغير ما','14807','5444','0');
INSERT INTO [t19188] VALUES (5449,'٥١٠٢ - مسألة: (وإن أكره على وزن ثمن، فباع داره فى ذلك، صح)','14809','5444','0');
INSERT INTO [t19188] VALUES (5450,'٥١٠٣ - مسألة: (وأما المريض مرض الموت المخوف، فيصح إقراره بغير المال)','14810','5444','0');
INSERT INTO [t19188] VALUES (5451,'٥١٠٤ - مسألة: (وإن أقر بمال لمن لا يرثه، صح، فى أصح الروايتين)','14810','5444','0');
INSERT INTO [t19188] VALUES (5452,'٥١٠٥ - مسألة: (ولا يحاص المقر له غرماء الصحة. وقال أبو الحسن التميمى، والقاضى: يحاصهم)','14811','5444','0');
INSERT INTO [t19188] VALUES (5453,'٥١٠٦ - مسألة: (وإن أقر لوارث، لم يقبل إلا ببينة)','14813','5444','0');
INSERT INTO [t19188] VALUES (5454,'٥١٠٧ - مسألة: (إلا أن يقر لزوجته بمهر مثلها)','14815','5444','0');
INSERT INTO [t19188] VALUES (5455,'٥١٠٨ - مسألة: (وإن أقر لوارث وأجنبى، [فهل يصح فى حق الأجنبى؟ على وجهين)','14817','5444','0');
INSERT INTO [t19188] VALUES (5456,'٥١٠٩ - مسألة: (وإن أقر لوارث، فصار عند الموت غير وارث، لم يصح. وإن أقر لغير وارث، صح وإن صار وارثا. نص عليه. وقيل','14818','5444','0');
INSERT INTO [t19188] VALUES (5457,'٥١١٠ - مسألة: (وإن أقر لامرأته بدين، ثم أبانها، ثم تزوجها، لم يصح إقراره)','14821','5444','0');
INSERT INTO [t19188] VALUES (5458,'٥١١١ - مسألة: (وإن أقر المريض بوارث','14821','5444','0');
INSERT INTO [t19188] VALUES (5459,'٥١١٢ - مسألة: (وإن أقر بطلاق امرأته فى صحته، لم يسقط ميراثها)','14822','5444','0');
INSERT INTO [t19188] VALUES (5460,'فصل','14823','5444','0');
INSERT INTO [t19188] VALUES (5461,'٥١١٣ - مسألة: (وإن أقر العبد غير المأذون له بمال، لم يقبل فى الحال، ويتبع به بعد العتق)','14827','5444','0');
INSERT INTO [t19188] VALUES (5462,'٥١١٤ - مسألة: (وإن أقر السيد عليه بمال، أو بما','14828','5444','0');
INSERT INTO [t19188] VALUES (5463,'٥١١٥ - مسألة: (وإن أقر العبد بسرقة مال فى يده، وكذبه السيد، قبل إقراره فى القطع دون المال)','14828','5444','0');
INSERT INTO [t19188] VALUES (5464,'٥١١٦ - مسألة: (وإن أقر السيد لعبده بمال، لم يصح)','14830','5444','0');
INSERT INTO [t19188] VALUES (5465,'٥١١٧ - مسألة: (وإن أقر أنه باع عبده من نفسه بألف، وأقر العبد به، ثبت)','14831','5444','0');
INSERT INTO [t19188] VALUES (5466,'٥١١٨ - مسألة: (وإن أقر لعبد غيره بمال، صح، وكان لمالكه)','14831','5444','0');
INSERT INTO [t19188] VALUES (5467,'٥١١٩ - مسألة: (وإن أقر لبهيمة، لم يصح)','14832','5444','0');
INSERT INTO [t19188] VALUES (5468,'٥١٢٠ - مسألة: (وإن تزوج مجهولة النسب، فأقرت بالرق، لم يقبل إقرارها)','14834','5444','0');
INSERT INTO [t19188] VALUES (5469,'فصل','14836','5444','0');
INSERT INTO [t19188] VALUES (5470,'٥١٢١ - مسألة: (فإن كان)','14837','5444','0');
INSERT INTO [t19188] VALUES (5471,'٥١٢٢ - مسألة: (ومن ثبت نسبه، فجاءت أمه بعد موت المقر فادعت الزوجية، لم يثبت بذلك)','14840','5444','0');
INSERT INTO [t19188] VALUES (5472,'٥١٢٣ - مسألة: (وإن أقر بنسب أخ أو عم، فى حياة أبيه أو جده، لم يقبل، وإن كان بعد موتهما وهو الوارث وحده، صح إقراره، وثبت','14844','5444','0');
INSERT INTO [t19188] VALUES (5473,'٥١٢٤ - مسألة: (وإن أقر من عليه الولاء بنسب وارث، لم يقبل','14846','5444','0');
INSERT INTO [t19188] VALUES (5474,'٥١٢٥ - مسألة: (وإن أقرت امرأة بنكاح على نفسها، فهل يقبل؟ على روايتين)','14847','5444','0');
INSERT INTO [t19188] VALUES (5475,'٥١٢٦ - مسألة: (فإن أقر الولى عليها به، قبل إن كانت مجبرة)','14848','5444','0');
INSERT INTO [t19188] VALUES (5476,'٥١٢٧ - مسألة: (وإن أقر أن فلانة امرأته، أو أقرت أن فلانا زوجها، فلم يصدق المقر له المقر إلا بعد موت المقر، صح وورثه)','14849','5444','0');
INSERT INTO [t19188] VALUES (5477,'٥١٢٨ - مسألة: (وإن أقر الورثة على موروثهم بدين، لزمهم قضاؤه من التركة، فإن أقر بعضهم، لزمه بقدر ميراثه، فإن لم تكن له تركة، لم يلزمهم شئ)','14850','5444','0');
INSERT INTO [t19188] VALUES (5478,'فصل','14853','5444','0');
INSERT INTO [t19188] VALUES (5479,'٥١٢٩ - مسألة: (وإن أقر لكبير عاقل، فلم يصدقه، بطل إقراره، فى أحد الوجهين)','14856','5444','0');
INSERT INTO [t19188] VALUES (5480,'باب ما يحصل به الإقرار','14857','5443','0');
INSERT INTO [t19188] VALUES (5481,'٥١٣٠ - مسألة: (وإن قال: أنا أقر. أو: لا أنكر. أو: يجوز أن تكون محقا. أو: عسى. أو: لعل. أو: أحسب. أو: أظن. أو: أقدر. أو: خذ. أو: اتزن. أو: افتح كمك. لم يكن مقرا)','14858','5480','0');
INSERT INTO [t19188] VALUES (5482,'٥١٣١ - مسألة: (وإن قال: أنا مقر. أو: خذها. أو: اتزنها. أو: اقبضها. أو: أحرزها. أو: هى صحاح. فهل يكون مقرا؟ يحتمل وجهين)','14859','5480','0');
INSERT INTO [t19188] VALUES (5483,'٥١٣٢ - مسألة: (وإن قال: له على ألف إن شاء الله. أو: فى علمى. أو: فيما أعلم. أو قال: اقضنى دينى عليك ألفا. أو: أسلم إلى ثوبى هذا. أو: فرسى هذه. فقال: نعم. فقد أقر بها)','14861','5480','0');
INSERT INTO [t19188] VALUES (5484,'٥١٣٣ - مسألة: (وإن قال)','14863','5480','0');
INSERT INTO [t19188] VALUES (5485,'٥١٣٤ - مسألة: (وإن قال: إن قدم فلان فله على ألف. لم يكن مقرا)','14864','5480','0');
INSERT INTO [t19188] VALUES (5486,'٥١٣٥ - مسألة: (وإن قال: له على ألف إذا جاء رأس الشهر. كان إقرارا. وإن قال: إذا جاء رأس الشهر فله على ألف. فعلى وجهين)','14865','5480','0');
INSERT INTO [t19188] VALUES (5487,'٥١٣٦ - مسألة: (وإن قال: له على ألف إن شهد به فلان. أو','14866','5480','0');
INSERT INTO [t19188] VALUES (5488,'٥١٣٧ - مسألة: (وإن قال: إن شهد به فلان فهو صادق. احتمل وجهين)','14867','5480','0');
INSERT INTO [t19188] VALUES (5489,'٥١٣٨ - مسألة: (وإن أقر العربى بالعجمية، أو العجمى بالعربية، وقال: لم أدر','14868','5480','0');
INSERT INTO [t19188] VALUES (5490,'باب الحكم فيما إذا وصل بإقراره ما يغيره','14869','5443','0');
INSERT INTO [t19188] VALUES (5491,'٥١٣٩ - مسألة: (وإن قال)','14871','5490','0');
INSERT INTO [t19188] VALUES (5492,'٥١٤٠ - مسألة: (وإن قال: كان له عندى ألف وقضيته. أو','14871','5490','0');
INSERT INTO [t19188] VALUES (5493,'فصل','14877','5490','0');
INSERT INTO [t19188] VALUES (5494,'٥١٤١ - مسألة: (فإذا قال: له على هؤلاء العبيد العشرة إلا واحدا. لزمه تسليم تسعة)','14883','5490','0');
INSERT INTO [t19188] VALUES (5495,'٥١٤٢ - مسألة: (فإن ماتوا إلا واحدا، فقال: هو المستثنى. فهل يقبل؟ على وجهين)','14883','5490','0');
INSERT INTO [t19188] VALUES (5496,'٥١٤٣ - مسألة: (وإن قال: له هذه الدار إلا هذا البيت. أو: هذه الدار له وهذا البيت لى. قبل منه)','14886','5490','0');
INSERT INTO [t19188] VALUES (5497,'٥١٤٤ - مسألة: (وإن قال: له على درهمان وثلاثة إلا درهمين. فهل يصح؟ على وجهين)','14887','5490','0');
INSERT INTO [t19188] VALUES (5498,'٥١٤٥ - مسألة: (وإن قال: له على درهم ودرهم إلا درهما)','14887','5490','0');
INSERT INTO [t19188] VALUES (5499,'٥١٤٦ - مسألة: (وإن قال: له على خمسة إلا درهمين ودرهما. لزمته الخمسة، فى أحد الوجهين)','14890','5490','0');
INSERT INTO [t19188] VALUES (5500,'٥١٤٧ - مسألة: (ويصح الاستثناء من الاستثناء)','14891','5490','0');
INSERT INTO [t19188] VALUES (5501,'٥١٤٨ - مسألة: (وإن قال: له على عشرة إلا خمسة إلا ثلاثة إلا درهمين إلا درهما، لزمه عشرة)','14892','5490','0');
INSERT INTO [t19188] VALUES (5502,'٥١٤٩ - مسألة: (ولا يصح الاستثناء من غير الجنس. نص عليه)','14897','5490','0');
INSERT INTO [t19188] VALUES (5503,'٥١٥٠ - مسألة: فعلى هذا (متى قال: له على مائة درهم إلا ثوبا. لزمته المائة)','14900','5490','0');
INSERT INTO [t19188] VALUES (5504,'٥١٥١ - مسألة: (إلا أن يستثنى عينا من ورق، أو ورقا من عين، فيصح. ذكره الخرقى. وقال أبو بكر: لا يصح. فإذا قال: له على مائة درهم إلا دينارا. فهل يصح؟ على وجهين)','14901','5490','0');
INSERT INTO [t19188] VALUES (5505,'فصل','14904','5490','0');
INSERT INTO [t19188] VALUES (5506,'٥١٥٢ - مسألة: (إلا أن يكون فى بلد أوزانهم ناقصة، أو مغشوشة، فهل يلزمه من دراهم البلد، أو من غيرها؟ على وجهين)','14905','5490','0');
INSERT INTO [t19188] VALUES (5507,'٥١٥٣ - مسألة: (وإن قال: له على ألف إلى شهر. لزمه مؤجلا. ويحتمل أن يلزمه حالا)','14907','5490','0');
INSERT INTO [t19188] VALUES (5508,'٥١٥٤ - مسألة: (وإن قال: له على ألف زيوف. وفسره بما لا فضة فيه، لم يقبل. وإن فسره بمغشوشة، قبل)','14909','5490','0');
INSERT INTO [t19188] VALUES (5509,'٥١٥٥ - مسألة: (وإن قال: له على دراهم ناقصة. لزمته ناقصة)','14910','5490','0');
INSERT INTO [t19188] VALUES (5510,'٥١٥٦ - مسألة: (وإن قال: له عندى رهن. وقال المالك)','14913','5490','0');
INSERT INTO [t19188] VALUES (5511,'٥١٥٧ - مسألة: (وإن قال: له على ألف من ثمن مبيع لم أقبضه. وقال المقر له: بل هو دين فى ذمتك. فعلى وجهين)','14914','5490','0');
INSERT INTO [t19188] VALUES (5512,'٥١٥٨ - مسألة: (ولو قال: له عندى ألف. وفسره بدين أو وديعة، قبل منه)','14915','5490','0');
INSERT INTO [t19188] VALUES (5513,'٥١٥٩ - مسألة: (وإن قال: له على ألف. وفسره بوديعة، لم يقبل)','14917','5490','0');
INSERT INTO [t19188] VALUES (5514,'٥١٦٠ - مسألة: (وإن قال: له فى هذا المال ألف. لزمه تسليمه)','14921','5490','0');
INSERT INTO [t19188] VALUES (5515,'٥١٦١ - مسألة: (وإن قال: له من مالى. أو: فى مالى. أو: فى ميراثى من أبى ألف. أو: نصف دارى هذه. وفسره بالهبة، وقال: بدا لى من تقبيضه. قبل)','14922','5490','0');
INSERT INTO [t19188] VALUES (5516,'٥١٦٢ - مسألة: (وإن قال: له فى ميراث أبى ألف. فهو دين على التركة)','14925','5490','0');
INSERT INTO [t19188] VALUES (5517,'٥١٦٣ - مسألة: (وإن قال: له','14926','5490','0');
INSERT INTO [t19188] VALUES (5518,'٥١٦٤ - مسألة: (وإن قال: له هذه الدار عارية. ثبت لها حكم العارية)','14926','5490','0');
INSERT INTO [t19188] VALUES (5519,'٥١٦٥ - مسألة: (وإن أقر أنه وهب، أو رهن [وأقبض]','14927','5490','0');
INSERT INTO [t19188] VALUES (5520,'٥١٦٦ - مسألة: (وإن باع شيئا ثم أقر أن المبيع لغيره، لم يقبل قوله على المشترى)','14929','5490','0');
INSERT INTO [t19188] VALUES (5521,'٥١٦٧ - مسألة: (وإن قال: لم يكن ملكى ثم ملكته بعد. لم','14929','5490','0');
INSERT INTO [t19188] VALUES (5522,'فصل','14936','5490','0');
INSERT INTO [t19188] VALUES (5523,'٥١٦٨ - مسألة: وإن قال: ملكته','14938','5490','0');
INSERT INTO [t19188] VALUES (5524,'٥١٦٩ - مسألة: (وإن قال: غصبته من أحدهما)','14939','5490','0');
INSERT INTO [t19188] VALUES (5525,'٥١٧٠ - مسألة: (وإن أقر بألف فى وقتين، لزمه ألف واحد)','14941','5490','0');
INSERT INTO [t19188] VALUES (5526,'٥١٧١ - مسألة: (وإن أقر بألف من ثمن عبد، ثم أقر بألف من ثمن فرس، أو قرض، لزمه ألفان)','14942','5490','0');
INSERT INTO [t19188] VALUES (5527,'٥١٧٢ - مسألة: (وإن ادعى رجلان دارا فى يد غيرهما شركة','14942','5490','0');
INSERT INTO [t19188] VALUES (5528,'٥١٧٣ - مسألة: (وإن قال فى مرض موته: هذا الألف لقطة، فتصدقوا به. لزم الورثة الصدقة بثلثه)','14944','5490','0');
INSERT INTO [t19188] VALUES (5529,'فصل','14945','5490','0');
INSERT INTO [t19188] VALUES (5530,'٥١٧٤ - مسألة: (وإن أقر بها لهما معا، فهى بينهما)','14946','5490','0');
INSERT INTO [t19188] VALUES (5531,'٥١٧٥ - مسألة: (وإن أقر لأحدهما وحده، فهى له، ويحلف للآخر)','14946','5490','0');
INSERT INTO [t19188] VALUES (5532,'٥١٧٦ - مسألة: (وإن ادعى رجل على الميت مائة دينا، فأقر له، ثم ادعى آخر مثل ذلك، فأقر له؛ فإن كان فى مجلس واحد، فهى بينهما، وإن كان فى مجلسين، فهى للأول، ولا شئ للثاني)','14947','5490','0');
INSERT INTO [t19188] VALUES (5533,'٥١٧٧ - مسألة: (وإن خلف ابنين ومائتين فادعى رجل مائة دينا على الميت، فصدقه أحد الابنين، وأنكر الآخر، لزم المقر نصفها)','14949','5490','0');
INSERT INTO [t19188] VALUES (5534,'٥١٧٨ - مسألة: (إلا أن يكون عدلا، فيحلف الغريم مع شهادته، ويأخذ مائة، وتكون المائة الباقية بين الابنين)','14950','5490','0');
INSERT INTO [t19188] VALUES (5535,'٥١٧٩ - مسألة: (وإن خلف ابنين وعبدين متساويى القيمة، لا يملك غيرهما، فقال أحد الابنين: أبى أعتق هذا. وقال الآخر: بل أعتق هذا الآخر. عتق من كل واحد ثلثه، وصار لكل ابن سدس)','14950','5490','0');
INSERT INTO [t19188] VALUES (5536,'باب الإقرار بالمجمل','14954','5443','0');
INSERT INTO [t19188] VALUES (5537,'٥١٨٠ - مسألة]','14954','5536','0');
INSERT INTO [t19188] VALUES (5538,'٥١٨١ - مسألة: (فإن فسره بحق شفعة أو مال، قبل وإن قل، وإن فسره بما ليس بمال، كقشر جوزة، أو ميتة، أو خمر، لم يقبل، وإن فسره بكلب، أو حد قذف، فعلى وجهين)','14958','5536','0');
INSERT INTO [t19188] VALUES (5539,'٥١٨٢ - مسألة: (وإن قال: غصبت منه شيئا. ثم فسره بنفسه أو ولده، لم يقبل)','14962','5536','0');
INSERT INTO [t19188] VALUES (5540,'٥١٨٣ - مسألة: (وإن قال: له على مال عظيم، أو: خطير، أو: كثير، أو: جليل. قبل تفسيره بالكثير والقليل)','14964','5536','0');
INSERT INTO [t19188] VALUES (5541,'٥١٨٤ - مسألة: (وإن قال: له على دراهم كثيرة. قبل تفسيره بثلاثة فصاعدا)','14967','5536','0');
INSERT INTO [t19188] VALUES (5542,'٥١٨٥ - مسألة: (وإن قال: له على كذا درهم. أو: كذا وكذا)','14969','5536','0');
INSERT INTO [t19188] VALUES (5543,'٥١٨٦ - مسألة: (وإن قال: كذا درهما. بالنصب، لزمه درهم)','14970','5536','0');
INSERT INTO [t19188] VALUES (5544,'٥١٨٧ - مسألة: (وإن قال: كذا وكذا درهما. بالنصب، فقال ابن حامد)','14970','5536','0');
INSERT INTO [t19188] VALUES (5545,'٥١٨٨ - مسألة: (وإن قال: له على ألف. رجع فى تفسيره إليه، فإن فسره بأجناس، قبل منه)','14974','5536','0');
INSERT INTO [t19188] VALUES (5546,'٥١٨٩ - مسألة: (وإن قال: له على ألف ودرهم. أو: ألف','14974','5536','0');
INSERT INTO [t19188] VALUES (5547,'٥١٩٠ - مسألة: (وإن قال: له على ألف وخمسون درهما. أو: خمسون وألف درهم. فالجميع دراهم. ويحتمل على قول التميمى أن يرجع فى تفسير الألف إليه)','14978','5536','0');
INSERT INTO [t19188] VALUES (5548,'٥١٩١ - مسألة: [وإن قال: له على ألف إلا درهما. فالجميع دراهم) لأن العرب لا تستثنى فى الإثبات إلا من الجنس]','14980','5536','0');
INSERT INTO [t19188] VALUES (5549,'٥١٩٢ - مسألة: (وإن قال: له فى هذا العبد شرك. أو: هو شريكى فيه. أو: هو شركة بيننا. رجع فى تفسير نصيب الشريك إليه)','14982','5536','0');
INSERT INTO [t19188] VALUES (5550,'٥١٩٣ - مسألة: (وإن قال: له على أكثر من مال فلان. قيل له: فسر. فإن فسره بأكثر منه قدرا، قبل. وإن قال: أردت أكثر بقاء ونفعا؛ لأن الحلال أنفع من الحرام. قبل)','14983','5536','0');
INSERT INTO [t19188] VALUES (5551,'٥١٩٤ - مسألة: (ولو ادعى عليه دينا، فقال: لفلان على أكثر مما لك. وقال: أردت التهزؤ. لزمه حق لهما، يرجع فى تفسيره إليه، فى أحد الوجهين. وفى الآخر، لا يلزمه شئ)','14986','5536','0');
INSERT INTO [t19188] VALUES (5552,'فصل','14987','5536','0');
INSERT INTO [t19188] VALUES (5553,'٥١٩٥ - مسألة: (وإن قال: له على درهم فوق درهم. أو: تحت درهم. أو: فوقه. أو: تحته. أو: قبله. أو: بعده. أو: معه درهم. أو: درهم ودرهم. أو: درهم، بل درهمان. أو','14990','5536','0');
INSERT INTO [t19188] VALUES (5554,'٥١٩٦ - مسألة: وإن قال: له على درهم ودرهم. أو: درهم فدرهم. أو: درهم ثم درهم. لزمه درهمان. وبهذا قال أبو حنيفة وأصحابه. وذكر القاضى وجها، فيما إذا قال: درهم فدرهم. وقال: أردت: درهم فدرهم لازم لى. أنه يقبل منه. وهو قول الشافعى؛ لأنه يحتمل الصفة. ولنا، أن «الفاء» أحد حروف العطف الثلاثة، فأشبهت «الواو» و','14994','5536','0');
INSERT INTO [t19188] VALUES (5555,'٥١٩٧ - مسألة: فإن قال: له على درهم بل درهمان. أو: درهم لكن درهمان. لزمه درهمان','14995','5536','0');
INSERT INTO [t19188] VALUES (5556,'٥١٩٨ - مسألة','14996','5536','0');
INSERT INTO [t19188] VALUES (5557,'٥١٩٩ - مسألة: (وإن قال: له)','14996','5536','0');
INSERT INTO [t19188] VALUES (5558,'٥٢٠٠ - مسألة: (ولو قال: له على هذا الدرهم، بل هذان الدرهمان. لزمته الثلاثة)','14998','5536','0');
INSERT INTO [t19188] VALUES (5559,'٥٢٠١ - مسألة: (وإن قال: درهم فى دينار. لزمه درهم. وإن قال)','14999','5536','0');
INSERT INTO [t19188] VALUES (5560,'٥٢٠٢ - مسألة: (وإن قال: له عندى تمر فى جراب. أو: سكين فى قراب. أو: ثوب فى منديل. أو: عبد عليه عمامة. أو: دابة عليها سرج. فهل يكون مقرا بالظرف والعمامة والسرج؟ يحتمل وجهين)','15001','5536','0');
INSERT INTO [t19188] VALUES (5561,'٥٢٠٣ - مسألة: (وإن قال: له عندى خاتم فيه فص. فهو مقر بهما)','15004','5536','0');
INSERT INTO [t19188] VALUES (5562,'٥٢٠٤ - مسألة: (وإن قال: له على درهم أو دينار. لزمه أحدهما، يرجع فى تفسيره إليه)','15006','5536','0');
