INSERT INTO [t20555] VALUES (22,'القسم الثاني (السؤالات)','106','0','0');
INSERT INTO [t20555] VALUES (23,'السؤال الأول: قوله تعالى: {وما أكل السبع إلا ما ذكيتم}، ما قول أئمة الفروع من المالكية في قوله: {إلا ما ذكيتم}؟','107','22','0');
INSERT INTO [t20555] VALUES (24,'السؤال الثاني: ظاهر قوله تعالى: {وطعام الذين أوتوا الكتاب حل لكم} إباحة صيد الكتابي مطلقا، فهل الآية التي استدل بها مالك على تحريم صيده تخصص هذا العموم، أم لا وهي قوله تعالى: {أيديكم ورماحكم}؟','110','22','0');
INSERT INTO [t20555] VALUES (25,'السؤال الثالث: لما كانت زكاة العروض للمدير عند مالك لا يلزم صاحبها أن يزكيها إلا إذا لم يبق له دينار أو درهم، فهل إذا كانت الدراهم والدنانير تباع بالأسواق، وتوجد فيها الأرباح الكثيرة، ولم يشترها المدير يعد ذلك فرارا من الزكاة، ويعامل بنقيض قصده أم','111','22','0');
INSERT INTO [t20555] VALUES (26,'السؤال الرابع: ما عندكم في لزوم الصوم، أو وجوب الفطر لخبر الرجل مع اتحاد القطر؟ وما عندكم في الاستماع لقراءة القرآن [في الإذاعة]؟','118','22','0');
INSERT INTO [t20555] VALUES (27,'السؤال الخامس: ما هو حد البدعة التي من ارتكبها يعد مخالفا للسنة؟ وما عندكم فيما يفعله بعض متصوفة زماننا من حركات، والكلمات التي لا تعلق لها بالصلاة، كاستدبار القبلة، والرقص والكلام بنحو: "مر، مر" مع الجزم بأن هذا كله لا يؤثر خللا في صلاتهم زاع','123','22','0');
INSERT INTO [t20555] VALUES (28,'السؤال السادس: ما عندكم في أداء الصلاة في الطائرة الجوية إذا تيقن عدم النزول إلا بعد خروج الوقت؟','124','22','0');
INSERT INTO [t20555] VALUES (29,'السؤال السابع: عن حكمة جعل الطلاق بيد الرجل؟','126','22','0');
INSERT INTO [t20555] VALUES (30,'السؤال الثامن: لو قيل إن عقد الزوجية تعاقد بين طرفين: الزوج والزوجة، والزوج دافع الصداق، فلو جعل من الناحية العقلية أن الطلاق بيد المرأة لضيعت على زوجها حقه، فيكون هذا من الناحية العقلية كذلك؟','131','22','0');
INSERT INTO [t20555] VALUES (31,'السؤال التاسع: أريد أن يبين الشيخ للناس أن المرأة ليست لعبة بين يدي الرجل؛ لأن يكون الرجل عادلا مع المرأة ويعطيها حقها، ولا يجعلها لعبة أي: إذا زال الغرض فيلقيها عنه فهذا ليس من القسط وليس من الإنسانية ...','132','22','0');
INSERT INTO [t20555] VALUES (32,'السؤال العاشر: ما هو الدليل القطعي على وجوب إثبات البسملة في غير سورة النمل أو حذفها، وعلى الأول هل يجهر بها أو يسر؟','136','22','0');
INSERT INTO [t20555] VALUES (33,'السؤال الحادي عشر: ما هو الأظهر عندكم في الأقوال المختلفة في معنى قوله -صلى الله عليه وسلم-: "أنزل القرآن على سبعة أحرف"؟','138','22','0');
INSERT INTO [t20555] VALUES (34,'السؤال الثاني عشر: ما هي الحكمة في تقديم (به) في البقرة في قوله تعالى: {أهل به لغير الله} وتأخيرها في غيرها؟','139','22','0');
INSERT INTO [t20555] VALUES (35,'السؤال الثالث عشر: ما هو التوفيق بين الحصرين في قوله تعالى: {إن تأتيهم سنة الأولين} وقوله: {إلا أن قالوا أبعث الله بشرا رسولا (٩٤)}؟','139','22','0');
INSERT INTO [t20555] VALUES (36,'السؤال الرابع عشر: ما معنى قوله تعالى في سورة النمل: {بل ادارك علمهم في الآخرة}؟','141','22','0');
INSERT INTO [t20555] VALUES (37,'السؤال الخامس عشر: هل يمكن عندكم الآن تصحيح ما لم يصحح من الأحاديث كما هو مذهب النووي، أم لا يمكن كما هو مذهب ابن الصلاح؟','142','22','0');
INSERT INTO [t20555] VALUES (38,'السؤال السادس عشر: هل ما أسنده الشيخان ترجح فيه قول الأول أم قول الثاني؟','142','22','0');
INSERT INTO [t20555] VALUES (39,'السؤال السابع عشر: هل الخلع طلاق أو فسخ؟ وما هو رأيكم في تعدد الزوجات؟','144','22','0');
INSERT INTO [t20555] VALUES (40,'السؤال الثامن عشر: ما تقولون في التزام شخص مذهبا معينا؟','147','22','0');
INSERT INTO [t20555] VALUES (41,'السؤال التاسع عشر: قال تعالى في كتابه العزيز: {والمحصنات من الذين أوتوا الكتاب من قبلكم ... } [المائدة: ٥] إلى آخر الآية الكريمة، فأباح تزوج الكتابية بنص القرآن العزيز، فكيف ساغ لخليل أن يقول: "إلا الكتابية بكره"؟','149','22','0');
INSERT INTO [t20555] VALUES (42,'السؤال العشرون: قال مالك في الموطأ: باب جواز جمع الأختين بملك اليمين، والمرأة وابنتها؟','151','22','0');
