INSERT INTO [t20766] VALUES (159,'قوله تعالى: {الذين ينفقون أموالهم في سبيل الله ثم لا يتبعون ما أنفقوا منا ولا أذى لهم أجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون (٢٦٢)}','299','39','0');
