INSERT INTO [t20766] VALUES (171,'قوله تعالى: {وما يعلم تأويله إلا الله}','345','39','0');
INSERT INTO [t20766] VALUES (172,'قوله تعالى: {والراسخون في العلم يقولون آمنا به}','348','39','0');
INSERT INTO [t20766] VALUES (173,'قوله تعالى: {إن الذين كفروا لن تغني عنهم أموالهم ولا أولادهم من الله شيئا وأولئك هم وقود النار (١٠)}','356','39','0');
INSERT INTO [t20766] VALUES (174,'قوله تعالى: {كدأب آل فرعون والذين من قبلهم كذبوا بآياتنا فأخذهم الله بذنوبهم}','357','39','0');
INSERT INTO [t20766] VALUES (175,'قوله تعالى: {قد كان لكم آية في فئتين التقتا}','357','39','0');
INSERT INTO [t20766] VALUES (176,'قوله تعالى: {والخيل المسومة والأنعام والحرث}','358','39','0');
INSERT INTO [t20766] VALUES (177,'قوله تعالى: {قل إن كنتم تحبون الله فاتبعوني يحببكم الله}','359','39','0');
INSERT INTO [t20766] VALUES (178,'قوله تعالى: {قال رب أنى يكون لي غلام وقد بلغني الكبر}','359','39','0');
INSERT INTO [t20766] VALUES (179,'قوله تعالى: {قال آيتك ألا تكلم الناس ثلاثة أيام إلا رمزا}','360','39','0');
