INSERT INTO [t20766] VALUES (182,'قوله تعالى: {قالت أنى يكون لي غلام ولم يمسسني بشر}','361','39','0');
INSERT INTO [t20766] VALUES (183,'قوله تعالى: {فلما أحس عيسى منهم الكفر قال من أنصاري إلى الله قال الحواريون نحن أنصار الله}','361','39','0');
INSERT INTO [t20766] VALUES (184,'قوله تعالى: {ومكروا ومكر الله والله خير الماكرين (٥٤)}','361','39','0');
