INSERT INTO [t20766] VALUES (191,'قوله تعالى: {واذكروا نعمت الله عليكم إذ كنتم أعداء فألف بين قلوبكم فأصبحتم بنعمته إخوانا}','367','39','0');
INSERT INTO [t20766] VALUES (192,'قوله تعالى: {وتسود وجوه}','367','39','0');
INSERT INTO [t20766] VALUES (193,'قوله تعالى: {من أهل الكتاب أمة قائمة يتلون آيات الله آناء الليل وهم يسجدون (١١٣)}','368','39','0');
