INSERT INTO [t20766] VALUES (197,'قوله تعالى: {أم حسبتم أن تدخلوا الجنة ولما يعلم الله الذين جاهدوا منكم ويعلم الصابرين (١٤٢)}','373','39','0');
INSERT INTO [t20766] VALUES (198,'قوله تعالى: {وكأين من نبي قاتل معه ربيون كثير}','374','39','0');
INSERT INTO [t20766] VALUES (199,'قوله تعالى: {يا أيها الذين آمنوا لا تكونوا كالذين كفروا وقالوا لإخوانهم إذا ضربوا في الأرض أو كانوا غزى لو كانوا عندنا ما ماتوا وما قتلوا}','380','39','0');
INSERT INTO [t20766] VALUES (200,'قوله تعالى: {ولئن قتلتم في سبيل الله أو متم لمغفرة من الله ورحمة خير مما يجمعون (١٥٧)}','380','39','0');
INSERT INTO [t20766] VALUES (201,'قوله تعالى: {فاعف عنهم واستغفر لهم}','381','39','0');
INSERT INTO [t20766] VALUES (202,'قوله تعالى: {أفمن اتبع رضوان الله كمن باء بسخط من الله}','382','39','0');
INSERT INTO [t20766] VALUES (203,'قوله تعالى: {أولما أصابتكم مصيبة قد أصبتم مثليها قلتم أنى هذا قل هو من عند أنفسكم}','382','39','0');
INSERT INTO [t20766] VALUES (204,'قوله تعالى: {ولا تحسبن الذين قتلوا في سبيل الله أمواتا}','384','39','0');
INSERT INTO [t20766] VALUES (205,'قوله تعالى: {الذين قال لهم الناس إن الناس قد جمعوا لكم}','384','39','0');
INSERT INTO [t20766] VALUES (206,'قوله تعالى: {ولا يحسبن الذين كفروا أنما نملي لهم خير لأنفسهم إنما نملي لهم ليزدادوا إثما ولهم عذاب مهين (١٧٨)}','384','39','0');
INSERT INTO [t20766] VALUES (207,'قوله تعالى: {وما عند الله خير للأبرار (١٩٨)}','387','39','0');
INSERT INTO [t20766] VALUES (208,'سورة النساء','388','0','0');
INSERT INTO [t20766] VALUES (209,'قوله تعالى: {ولا تأكلوا أموالهم إلى أموالكم إنه كان حوبا كبيرا (٢)}','389','208','0');
INSERT INTO [t20766] VALUES (210,'قوله تعالى: {وإن خفتم ألا تقسطوا في اليتامى فانكحوا ما طاب لكم من النساء}','390','208','0');
INSERT INTO [t20766] VALUES (211,'قوله تعالى: {للرجال نصيب مما ترك الوالدان والأقربون وللنساء نصيب مما ترك الوالدان والأقربون مما قل منه أو كثر نصيبا مفروضا (٧)}','394','208','0');
INSERT INTO [t20766] VALUES (212,'قوله تعالى: {يوصيكم الله في أولادكم للذكر مثل حظ الأنثيين}','394','208','0');
INSERT INTO [t20766] VALUES (213,'قوله تعالى: {فإن كن نساء فوق اثنتين فلهن ثلثا ما ترك وإن كانت واحدة فلها النصف}','395','208','0');
INSERT INTO [t20766] VALUES (214,'قوله تعالى: {وإن كان رجل يورث كلالة أو امرأة وله أخ أو أخت فلكل واحد منهما السدس فإن كانوا أكثر من ذلك فهم شركاء في الثلث}','400','208','0');
INSERT INTO [t20766] VALUES (215,'قوله تعالى: {فإن شهدوا فأمسكوهن في البيوت حتى يتوفاهن الموت أو يجعل الله لهن سبيلا (١٥)}','402','208','0');
INSERT INTO [t20766] VALUES (216,'قوله تعالى: {ولا تنكحوا ما نكح آباؤكم من النساء}','403','208','0');
INSERT INTO [t20766] VALUES (217,'قوله تعالى: {وحلائل أبنائكم الذين من أصلابكم}','406','208','0');
INSERT INTO [t20766] VALUES (218,'قوله تعالى: {فما استمتعتم به منهن فآتوهن أجورهن}','412','208','0');
INSERT INTO [t20766] VALUES (219,'قوله تعالى: {ومن لم يستطع منكم طولا أن ينكح المحصنات المؤمنات فمن ما ملكت أيمانكم من فتياتكم المؤمنات}','415','208','0');
INSERT INTO [t20766] VALUES (220,'قوله تعالى: {فإذا أحصن فإن أتين بفاحشة فعليهن نصف ما على المحصنات من العذاب}','417','208','0');
