INSERT INTO [t20766] VALUES (224,'قوله تعالى: {ولا يكتمون الله حديثا (٤٢)}','420','208','0');
INSERT INTO [t20766] VALUES (225,'وقوله تعالى: {يا أيها الذين آمنوا لا تقربوا الصلاة وأنتم سكارى حتى تعلموا ما تقولون}','421','208','0');
INSERT INTO [t20766] VALUES (226,'قوله تعالى: {ألم تر إلى الذين أوتوا نصيبا من الكتاب يشترون الضلالة ويريدون أن تضلوا السبيل (٤٤)}','421','208','0');
INSERT INTO [t20766] VALUES (227,'وقوله تعالى: {أو نلعنهم كما لعنا أصحاب السبت}','421','208','0');
INSERT INTO [t20766] VALUES (228,'قوله تعالى: {إن الله لا يغفر أن يشرك به ويغفر ما دون ذلك لمن يشاء ومن يشرك بالله فقد افترى إثما عظيما (٤٨)}','422','208','0');
INSERT INTO [t20766] VALUES (229,'قوله تعالى: {ألم تر إلى الذين يزكون أنفسهم بل الله يزكي من يشاء}','423','208','0');
INSERT INTO [t20766] VALUES (230,'قوله تعالى: {وندخلهم ظلا ظليلا (٥٧)}','424','208','0');
INSERT INTO [t20766] VALUES (231,'قوله تعالى: {فإن تنازعتم في شيء فردوه إلى الله}','424','208','0');
INSERT INTO [t20766] VALUES (232,'قوله تعالى: {وإذا قيل لهم تعالوا إلى ما أنزل الله وإلى الرسول رأيت المنافقين يصدون عنك صدودا (٦١)}','426','208','0');
INSERT INTO [t20766] VALUES (233,'قوله تعالى: {فلا وربك لا يؤمنون حتى يحكموك فيما شجر بينهم ثم لا يجدوا في أنفسهم حرجا مما قضيت ويسلموا تسليما (٦٥)}','426','208','0');
INSERT INTO [t20766] VALUES (234,'قوله تعالى: {فإن أصابتكم مصيبة قال قد أنعم الله علي إذ لم أكن معهم شهيدا (٧٢)}','426','208','0');
INSERT INTO [t20766] VALUES (235,'قوله تعالى: {ولئن أصابكم فضل من الله ليقولن كأن لم تكن بينكم وبينه مودة ياليتني كنت معهم فأفوز فوزا عظيما (٧٣)}','427','208','0');
INSERT INTO [t20766] VALUES (236,'قوله تعالى: {ومن يقاتل في سبيل الله فيقتل أو يغلب}','427','208','0');
INSERT INTO [t20766] VALUES (237,'قوله تعالى: {وحرض المؤمنين}','427','208','0');
INSERT INTO [t20766] VALUES (238,'قوله تعالى: {أتريدون أن تهدوا من أضل الله ومن يضلل الله فلن تجد له سبيلا (٨٨)}','428','208','0');
INSERT INTO [t20766] VALUES (239,'قوله تعالى: {لا يستوي القاعدون من المؤمنين غير أولي الضرر والمجاهدون في سبيل الله بأموالهم وأنفسهم فضل الله المجاهدين بأموالهم وأنفسهم على القاعدين درجة وكلا وعد الله الحسنى وفضل الله المجاهدين على القاعدين أجرا عظيما (٩٥)}','428','208','0');
INSERT INTO [t20766] VALUES (240,'قوله تعالى: {وإذا ضربتم في الأرض فليس عليكم جناح أن تقصروا من الصلاة إن خفتم أن يفتنكم الذين كفروا}','429','208','0');
INSERT INTO [t20766] VALUES (241,'قوله تعالى: {إن الصلاة كانت على المؤمنين كتابا موقوتا}','477','208','0');
INSERT INTO [t20766] VALUES (242,'قوله تعالى: {ولا تهنوا في ابتغاء القوم إن تكونوا تألمون فإنهم يألمون كما تألمون وترجون من الله ما لا يرجون وكان الله عليما حكيما (١٠٤)}','518','208','0');
INSERT INTO [t20766] VALUES (243,'قوله تعالى: {ومن يكسب إثما فإنما يكسبه على نفسه}','518','208','0');
INSERT INTO [t20766] VALUES (244,'قوله تعالى: {وعلمك ما لم تكن تعلم}','518','208','0');
INSERT INTO [t20766] VALUES (245,'قوله تعالى: {لا خير في كثير من نجواهم}','519','208','0');
INSERT INTO [t20766] VALUES (246,'قوله تعالى: {وإن يدعون إلا شيطانا مريدا}','520','208','0');
INSERT INTO [t20766] VALUES (247,'قوله تعالى: {وقال لأتخذن من عبادك نصيبا مفروضا (١١٨)}','520','208','0');
INSERT INTO [t20766] VALUES (248,'قوله تعالى: {ولآمرنهم فليغيرن خلق الله}','522','208','0');
INSERT INTO [t20766] VALUES (249,'قوله تعالى: {ليس بأمانيكم ولا أماني أهل الكتاب}','527','208','0');
INSERT INTO [t20766] VALUES (250,'قوله تعالى: {وما يتلى عليكم في الكتاب في يتامى النساء}','528','208','0');
INSERT INTO [t20766] VALUES (251,'قوله تعالى: {وأن تقوموا لليتامى بالقسط}','533','208','0');
INSERT INTO [t20766] VALUES (252,'قوله تعالى: {وأحضرت الأنفس الشح}','533','208','0');
INSERT INTO [t20766] VALUES (253,'قوله تعالى: {ولن تستطيعوا أن تعدلوا بين النساء ولو حرصتم}','534','208','0');
INSERT INTO [t20766] VALUES (254,'قوله تعالى: {وإن يتفرقا يغن الله كلا من سعته}','535','208','0');
INSERT INTO [t20766] VALUES (255,'قوله تعالى: {إن يشأ يذهبكم أيها الناس ويأت بآخرين}','536','208','0');
INSERT INTO [t20766] VALUES (256,'قوله تعالى: {أيبتغون عندهم العزة فإن العزة لله جميعا (١٣٩)}','536','208','0');
INSERT INTO [t20766] VALUES (257,'قوله تعالى: {وقد نزل عليكم في الكتاب أن إذا سمعتم آيات الله يكفر بها ويستهزأ بها فلا تقعدوا معهم حتى يخوضوا في حديث غيره إنكم إذا مثلهم}','537','208','0');
INSERT INTO [t20766] VALUES (258,'قوله تعالى: {ولن يجعل الله للكافرين على المؤمنين سبيلا (١٤١)}','537','208','0');
INSERT INTO [t20766] VALUES (259,'قوله تعالى: {وإذا قاموا إلى الصلاة قاموا كسالى يراءون الناس ولا يذكرون الله إلا قليلا (١٤٢)}','539','208','0');
INSERT INTO [t20766] VALUES (260,'قوله تعالى: {إن المنافقين في الدرك الأسفل من النار}','539','208','0');
INSERT INTO [t20766] VALUES (261,'قوله تعالى: {ثم اتخذوا العجل من بعد ما جاءتهم البينات فعفونا عن ذلك}','540','208','0');
INSERT INTO [t20766] VALUES (262,'قوله تعالى: {وقلنا لهم لا تعدوا في السبت}','540','208','0');
INSERT INTO [t20766] VALUES (263,'قوله تعالى: {وبكفرهم وقولهم على مريم بهتانا عظيما (١٥٦)}','540','208','0');
